DROP DATABASE IF EXISTS savour_test;
CREATE DATABASE savour_test;
\c savour_test





CREATE TABLE authors (
    id SERIAL PRIMARY KEY,
    full_name VARCHAR (121) UNIQUE NOT NULL
);











CREATE TABLE units (
  id SERIAL PRIMARY KEY,
  unit VARCHAR (30) UNIQUE NOT NULL
);












CREATE TABLE ingredients (
  id SERIAL PRIMARY KEY,
  ingredient TEXT UNIQUE NOT NULL
);














CREATE TABLE main_category (
    id SERIAL PRIMARY KEY,
    main_cat_name VARCHAR (121) UNIQUE NOT NULL
);








CREATE TABLE sub_category (
    id SERIAL PRIMARY KEY,
    sub_cat_name VARCHAR (121) UNIQUE NOT NULL
);














CREATE TABLE difficulty (
    id SERIAL PRIMARY KEY,
    level VARCHAR (30) UNIQUE NOT NULL
);























CREATE TABLE recipes (
  id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  url TEXT,
  image TEXT,
  description TEXT NOT NULL,
  author_id INTEGER REFERENCES authors (id),
  serves INTEGER NOT NULL,
  difficulty_id INTEGER REFERENCES difficulty (id),
  main_category_id INTEGER REFERENCES main_category (id),
  sub_category_id INTEGER REFERENCES sub_category (id),
  steps TEXT NOT NULL,
  prep_time TEXT,
  cook_time TEXT
);

    id, name, url, image, description, author_id,
    serves, difficulty_id, main_category_id,
    sub_category_id, steps, prep_time, cook_time
  )
VALUES
(1,'smoked salmon, quinoa & dill lunch pot','https://www.bbcgoodfood.com/recipes/smoked-salmon-quinoa-dill-lunch-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoked-salmon-quinoa-dill-lunch-pot-0393a04.jpg','This easy packed lunch is as delicious as it is nutritious, with crunchy cucumber and radishes and a herby, creamy dressing',1,2,1,1,1,'First, make the dressing. Mix the soured cream and lemon juice together in a bowl, then add most of the dill, reserving a quarter for serving. In another bowl, combine the quinoa with the cucumber and radishes, and stir through half the dressing. Season and top with the salmon and the rest of the dill. Put the other half of the dressing in a small pot and drizzle over the quinoa just before serving.','15 mins','No Time'),
(2,'air fryer chicken breasts','https://www.bbcgoodfood.com/recipes/air-fryer-chicken-breasts','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Air-Fryer-Chicken-Breast-3121e78.jpg','Use an air fryer to create this tempting dish of tender chicken breasts coated in garlic, sweet paprika and herbs. Mix up the spices for interest or keep it plain if you prefer',2,4,1,1,1,'Coat the chicken in the oil and set aside. In a bowl, combine the salt, garlic, paprika and mixed herbs with a good grinding of black pepper, then scatter onto a plate. Roll each oiled chicken breast in the seasoning and put in your air fryer basket. Cook at 180C for 18-20 mins, turning after 10 mins. To check it is cooked, pierce the chicken with a knife at the thickest part to see if the juices run clear. Or insert a temperature thermometer and it should read 70C. Serve with rice and greens or a salad.','5 mins','18 mins - 20 mins'),
(3,'panuozzo sandwich','https://www.bbcgoodfood.com/recipes/panuozzo-sandwich','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Panuozzo-sandwich-4b19929.jpg','Make your own baguettes and pesto to make these pizza-inspired sandwiches for the whole family. They''re surprisingly quick and easy to make and are great for lunch or a light dinner',3,4,1,1,1,'Put the flour in a large bowl, then stir in the yeast and a large pinch of salt. Make a well in the centre, pour in 175ml warm water and the oil and bring together with a wooden spoon to make a soft, fairly wet dough. Turn out onto a lightly floured surface and knead for 5-8 mins until smooth. Divide the dough into four balls and arrange on a floured tray, well spaced apart. Cover with a tea towel and set aside for about 40 mins-1 hr until doubled in size. Heat the oven to 240C/220C fan/gas 8 and put a baking tray in the oven to heat up. Roll the dough balls out on a floured surface into long baguette sticks about 22-25cm long, place on the hot tray and bake for 10-12 mins until puffed up and golden, then leave to cool. Can be made a few hours ahead of baking. To assemble, split the bread down one side and neatly layer in the tomato, ham, mozzarella and rocket, if using. Mix the pesto with the olive oil, drizzle over, halve and serve.','20 mins','12 mins'),
(4,'bulgur & quinoa lunch bowls','https://www.bbcgoodfood.com/recipes/bulgur-quinoa-lunch-bowls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-bulgur-quinoa-lunch-bowls-dea6596.jpg','These meal prep grain bowls use one base and two deliciously different toppings. Choose from avocado, olives and rocket or chickpeas, beetroot and orange',4,4,1,1,1,'Tip the onion and bulgur mix into a pan, pour over 600ml water and stir in the thyme and bouillon. Cook, covered, over a low heat for 15 mins, then leave to stand for 10 mins. All the liquid should now be absorbed. When cool, remove the thyme and divide the bulgur between four bowls or plastic containers. For the avocado topping, toss all the ingredients together except for the rocket. Pile onto two portions of the bulgur and top with the rocket. For the beetroot topping, first pile the chickpeas on top, then toss the beetroot with the tomato, mint, cumin, a good pinch of cinnamon, the oil and vinegar. Toss well, add the orange, then pile onto the remaining portions of bulghur, scatter with the pine nuts and sprinkle with extra cinnamon. Chill in the fridge until needed.','5 mins','15 mins'),
(5,'falafel burgers','https://www.bbcgoodfood.com/recipes/falafel-burgers-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-326597_11-b7385b9.jpg','A healthy burger that''s filling too. These are great for anyone after a satisfying bite low in calories.',5,4,1,1,1,'Drain the chickpeas and pat dry with kitchen paper. Tip into a food processor along with the onion, garlic, parsley, cumin, coriander, harissa paste, flour and a little salt. Blend until fairly smooth, then shape into four patties with your hands. Heat the sunflower oil in a non-stick frying pan, and fry the burgers for 3 mins on each side until lightly golden. Serve with the toasted pitta bread, tomato salsa and green salad.','10 mins','6 mins'),
(237,'breakfast smoothie','https://www.bbcgoodfood.com/recipes/breakfast-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201467_11-c0694ef.jpg','Make the most of the berry season with a glass of fruity goodness',5,1,1,1,2,'Slice the banana into your blender or food processor and add the berries of your choice. Whizz until smooth. With the blades whirring, pour in juice or water to make the consistency you like. Toss a few extra fruits on top, drizzle with honey and serve.','5 mins','N/A'),
(6,'egg & avocado open sandwich','https://www.bbcgoodfood.com/recipes/egg-avocado-open-sandwich','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egg-avocado-open-sandwich-3b6ef94.jpg','Give your lunch box a moreish makeover - take the ingredients separately and assemble for a fresh and healthy midday meal',1,1,1,1,1,'Bring a medium pan of water to the boil. Add the eggs and cook for 8-9 mins until hard-boiled. Meanwhile, halve the avocado and scoop the flesh into a bowl. Add the lime juice, season well and mash with a fork. When the eggs are cooked, run under cold water for 2 mins before removing the shells. Spread the avocado on the rye bread. Slice the eggs into thin rounds and place on top of the avocado. Drizzle some chilli sauce over the eggs, scatter the cress on top and add a good grinding of black pepper.','10 mins','10 mins'),
(7,'turkey, bacon & avocado mini bagels','https://www.bbcgoodfood.com/recipes/turkey-bacon-avocado-mini-bagels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/turkey-bacon-avocado-mini-bagels-5d7ddcb.png','Pack up a hearty lunch to look forward to - fill bagels with leftover turkey or chicken, crispy bacon and creamy mashed avocado',6,1,1,1,1,'Mash the avocado with the lemon juice and a pinch of salt. Spread 2 bagel halves with cranberry sauce and 2 with avocado. Top the avocado halves with the turkey, bacon and leaves, then sandwich together with the cranberry halves. Wrap in cling film and chill until lunchtime.','10 mins','No Time'),
(8,'spicy chicken & avocado wraps','https://www.bbcgoodfood.com/recipes/spicy-chicken-avocado-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spicychickenavocadowraps_5865-4035909.jpg','Pan-fry lean chicken breast with lime, chilli and garlic, then pile onto seeded tortilla wraps. Cool before assembling if packing for lunch',4,2,1,1,1,'Mix the chicken with the lime juice, chilli powder and garlic. Heat the oil in a non-stick frying pan then fry the chicken for a couple of mins – it will cook very quickly so keep an eye on it. Meanwhile, warm the wraps following the pack instructions or, if you have a gas hob, heat them over the flame to slightly char them. Do not let them dry out or they are difficult to roll. Squash half an avocado onto each wrap, add the peppers to the pan to warm them through then pile onto the wraps with the chicken, and sprinkle over the coriander. Roll up, cut in half and eat with your fingers.','5 mins','8 mins'),
(9,'chorizo & chickpea soup','https://www.bbcgoodfood.com/recipes/chorizo-chickpea-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338571_12-66e09c7.jpg','Sizzling Chorizo & chickpea soup',5,2,1,1,1,'Put a medium pan on the heat and tip in the tomatoes, followed by a can of water. While the tomatoes are heating, quickly chop the chorizo into chunky pieces (removing any skin) and shred the cabbage. Pile the chorizo and cabbage into the pan with the chilli flakes and chickpeas, then crumble in the stock cube. Stir well, cover and leave to bubble over a high heat for 6 mins or until the cabbage is just tender. Ladle into bowls and eat with crusty or garlic bread.','5 mins','10 mins'),
(10,'prawn & mango salad','https://www.bbcgoodfood.com/recipes/prawn-mango-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/prawn-mango-salad-b147add.jpg','Kids will love this colourful salad with avocado and mint - add cold cooked pasta for a more filling packed lunch',7,2,1,1,1,'Mix the avocado with the lemon juice, then toss with the prawns, mango, tomatoes, cucumber, spinach and mint. Pack into a lunchbox and drizzle over the sweet chilli sauce, then chill until ready to eat.','10 mins','No Time'),
(11,'egg & rocket pizzas','https://www.bbcgoodfood.com/recipes/egg-rocket-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/eggrocketpizzas_5729-ebc7e0d.jpg','Use seeded tortillas as pizza bases for a quick and healthy lunch - crack an egg in the centre and bake to boost protein intake',4,2,1,1,1,'Heat oven to 200C/180C fan/gas 6. Lay the tortillas on two baking sheets, brush sparingly with the oil then bake for 3 mins. Meanwhile chop the pepper and tomatoes and mix with the tomato purée, seasoning and herbs. Turn the tortillas over and spread with the tomato mixture, leaving the centre free from any large pieces of pepper or tomato. Break an egg into the centre then return to the oven for 10 mins or until the egg is just set and the tortilla is crispy round the edges. Serve scattered with the rocket and onion.','10 mins','15 mins - 20 mins'),
(12,'orecchiette with butter beans, parsley, chilli & lemon','https://www.bbcgoodfood.com/recipes/orecchiette-with-butter-beans-parsley-chilli-lemon','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/Orecchiette-with-butter-beans-0872a01.jpg','Make this simple pasta dish using whatever you’ve got to hand, including different pasta shapes, beans or chopped tomatoes. You can leave out the chilli if you prefer',2,4,1,1,1,'Cook the orecchiette following pack instructions until al dente, about 10 mins. Drain, reserving the cooking water. Meanwhile, heat the olive oil in a large frying pan over a medium heat and fry the chillies and garlic for 2 mins until just softened. Mix in the butter beans, tomatoes, lemon zest and juice. Cook for 5 mins more, then scatter in the parsley and cook for another 2 mins. The tomatoes should have reduced slightly and the beans should be warmed through. Tip the orecchiette into the frying pan with the butter bean mixture. Pour in a little of the reserved pasta cooking water if you prefer a looser sauce. Spoon into bowls and sprinkle with the cheese before serving, if you like.','5 mins','10 mins'),
(13,'chicken pesto wrap','https://www.bbcgoodfood.com/recipes/chicken-pesto-wrap','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-pesto-wrap-b147add.jpg','Swap soggy sandwiches for tortilla wraps filled with chicken, cheese and veggies in this tasty packed lunch recipe that kids will love',7,2,1,1,1,'Mix together the shredded chicken, soured cream, yogurt or mayonnaise with the pesto. Season. Lay a slice of cheese on each wrap, then divide the chicken mixture between them. Sprinkle with red pepper or sweetcorn, then top with the lettuce leaves. Be careful not to overfill or it will be tricky to contain all the filling. Wrap and roll each one, then pack in a lunchbox or tightly wrap in foil.','10 mins','No Time'),
(14,'broccoli pasta salad with eggs & sunflower seeds','https://www.bbcgoodfood.com/recipes/broccoli-pasta-salad-eggs-sunflower-seeds','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/broccoli-pasta-salad-with-eggs-sunflower-seeds-758d30d.jpg','Pack in protein, carbohydrate and vitamins with this broccoli, pasta and eggs dish, a healthy vegetarian lunch to keep you going on a busy afternoon',5,2,1,1,1,'Hard-boil the eggs for 8 mins, then shell and halve. Meanwhile, boil the pasta for 5 mins, add the broccoli and beans, and cook 5 mins more or until everything is tender. Drain, reserving the water, then tip the pasta and veg into a bowl and stir in the miso, ginger, oil and 4 tbsp pasta water. Serve topped with the eggs and seeds.','10 mins','10 mins'),
(15,'healthy beef chow mein','https://www.bbcgoodfood.com/recipes/healthy-beef-chow-mein','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/Beef-chow-mein-e16180c.jpg','Swap your weekly takeaway for this healthy beef chow mein – it''s quick to cook and low in fat and calories',4,2,1,1,1,'Cook the noodles following pack instructions. Meanwhile, heat half the oil in a wok and stir-fry the beef over a high heat for 30 seconds until browned. Tip onto a plate, set aside, then add the remaining oil to the wok. Stir-fry the onion and ginger until softened. Add a drop of water to prevent burning, if needed and cook covered for 2-3 mins. Stir in the mushrooms and garlic, and fry until they start to soften (around 3 mins), then add the beansprouts and cook for a minute or two more until piping hot. Mix the tamari and vinegar together in a small bowl with 2 tbsp water. Add the drained noodles to the wok, toss well, then add the tamari mixture, spring onions and the beef. Stir-fry for a few minutes until heated through. Season with plenty of black pepper and serve.','10 mins','10 mins'),
(16,'storecupboard pasta salad','https://www.bbcgoodfood.com/recipes/storecupboard-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-663451_11-43ad8a6.jpg','This pasta salad makes a quick and healthy lunch, or is perfect prepared ahead for a picnic or lunchbox',8,2,1,1,1,'Mix the onion, capers, pesto and oil. Flake the tuna into a bowl with the pasta and tomatoes, then stir in the pesto mix.','5 mins','N/A'),
(17,'ultimate chorizo ciabatta','https://www.bbcgoodfood.com/recipes/ultimate-chorizo-ciabatta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ultimate-chorizo-ciabatta-c3f006e.jpg','Crusty ciabatta, spicy chorizo, a generous dollop of pesto and sweet roasted peppers make a moreish combination. Serve warm for a quick supper you''ll really enjoy',9,2,1,1,1,'Heat oven to 180C/160C fan/gas 4 and put the ciabatta in to warm up. Put a griddle pan over a medium heat and cook the chorizo for 5 mins each side or until charred and cooked through. Open up the warmed ciabatta and spread the pesto on the bottom. Layer with the red peppers , then the warm chorizo. Scatter over the rocket, sandwich the ciabatta together, cut in two and serve.','5 mins','10 mins'),
(18,'veggie noodle pot','https://www.bbcgoodfood.com/recipes/veggie-noodle-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-noodle-pot-dce5b50.jpg','This noodle salad with sugar snap peas and baby corn makes a great packed lunch for kids - an easy way to get 2 of their 5 a day',7,2,1,1,1,'To make the omelette, heat the olive oil in a small non-stick frying pan. Add a splash of milk to the beaten eggs, then tip into the pan. Stir once and allow to cook over a gentle heat until almost set. Flip (using a plate if necessary) and cook on the other side until cooked through. Tip onto a board and cut into strips. (You can roll the omelette up and cut slices to give you spirals, if you like.) Cook the noodles following pack instructions. Drain and rinse under cold water, then set aside. Meanwhile, mix the dressing ingredients together. Blanch the peas and sugar snap peas, then drain and run under cold water to stop them cooking any further. To assemble the salad, mix the noodles with the baby corn, spring onion, red pepper and green veg, then toss with the dressing and top with strips of omelette.','10 mins','10 mins'),
(238,'tropical breakfast smoothie','https://www.bbcgoodfood.com/recipes/tropical-breakfast-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-484466_11-fac2879.jpg','Start your day with this fruity blend of passion fruits, mango, orange and banana. It''s a good source of vitamin C and provides 1 of your 5-a-day, too',10,2,1,1,2,'Scoop the pulp of the passion fruits into a blender and add the banana, mango and orange juice. Purée until smooth and drink immediately, topped with ice cubes.','5 mins','N/A'),
(19,'chicken kiev quesadilla','https://www.bbcgoodfood.com/recipes/chicken-kiev-quesadilla','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quesadilla-a667fd0.jpg','A seriously gourmet quesadilla. This easy, chicken-filled snack will curb your cheese cravings – serve warm and golden brown with your favourite dipping sauce',11,2,1,1,1,'Heat a non-stick frying pan over a medium-high heat. Spread half the wrap with 2 tbsp soft cheese and put in the pan. Cover the other half with the cheddar, then top with the spring onion, lemon zest, parsley and chicken. Scatter over the panko and dot the remaining soft cheese over. Fold the soft-cheese-spread side of the wrap over to close the quesadilla. Cook for 3-4 mins until the cheese has melted and the top of the quesadilla is warm, so you know the chicken is heated through. Slice and serve.','5 mins','10 mins'),
(20,'italian sub','https://www.bbcgoodfood.com/recipes/italian-sub','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/italian-sub-6d78d0a.jpg','Torn mozzarella, salami, sundried tomatoes, balsamic and basil in ciabatta are the ultimate lunch combo',1,2,1,1,1,'Halve the ciabatta rolls and butter the bases, if you like. Lay the salami slices, mozzarella, basil and sundried tomatoes on top, and drizzle over the balsamic glaze. Close to serve.','5 mins','No Time'),
(21,'caesar pitta','https://www.bbcgoodfood.com/recipes/caesar-pitta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/caesar-pitta-ca63652.jpg','Pack a pitta with chicken, salad and homemade caesar dressing for an easy lunch. It takes just 10 minutes from prep to plate and it''s healthy too',12,1,1,1,1,'Whisk the lemon juice, garlic, yogurt, mayonnaise, mustard powder, anchovy, parmesan and 2-3 tbsp water together with a good grinding of black pepper. Toss in the chicken, cucumber, tomatoes and lettuce, until everything is well coated. Toast the pitta and split, then stuff with the salad and chicken mixture.','10 mins','No Time'),
(22,'fish finger wraps with pea purée','https://www.bbcgoodfood.com/recipes/fish-finger-wraps-pea-puree','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-fingers-0af3382.jpg','Fish fingers make a quick and easy filling for tortilla wraps. Spruce them up with pea purée for colour and tangy cornichons for crunch',4,2,1,1,1,'Grill or bake the fish fingers following pack instructions. Meanwhile, boil the peas for 3 mins, then drain, add the oil and lemon juice, and blitz with a hand blender (or in a food processor) until smooth. Season, adding a little more lemon juice if you like. Warm the tortillas. Spread with the pea purée, scatter with the carrots, then top with the fish fingers and cornichons. Roll up and eat while still hot, with a good squeeze of lemon.','5 mins','10 mins'),
(23,'vegan chickpea curry jacket potatoes','https://www.bbcgoodfood.com/recipes/vegan-chickpea-curry-jacket-potato','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweet-potato-curry-46f4bc8.jpg','Get some protein into a vegan diet with this tasty chickpea curry jacket. It''s an easy midweek meal, or filling lunch that packs a lot of flavour.',13,4,1,1,1,'Heat oven to 200C/180C fan/gas 6. Prick the sweet potatoes all over with a fork, then put on a baking tray and roast in the oven for 45 mins or until tender when pierced with a knife. Meanwhile, melt the coconut oil in a large saucepan over medium heat. Add the cumin seeds and fry for 1 min until fragrant, then add the onion and fry for 7-10 mins until softened. Put the garlic, ginger and green chilli into the pan, and cook for 2-3 mins. Add the spices and tikka masala paste and cook for a further 2 mins until fragrant, then tip in the tomatoes. Bring to a simmer, then tip in the chickpeas and cook for a further 20 mins until thickened. Season. Put the roasted sweet potatoes on four plates and cut open lengthways. Spoon over the chickpea curry and squeeze over the lemon wedges. Season, then scatter with coriander before serving.','15 mins','45 mins'),
(24,'salmon salad with sesame dressing','https://www.bbcgoodfood.com/recipes/salmon-salad-sesame-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-salad-440-400-67a0abd.jpg','Get three of your 5-a-day, plus healthy omega-3 fatty acids, vitamin C, folate and fibre in this quick, easy and low-calorie salad',4,2,1,1,1,'Steam the potatoes and beans in a steamer basket set over a pan of boiling water for 8 mins. Arrange the salmon fillets on top and steam for a further 6-8 mins, or until the salmon flakes easily when tested with a fork. Meanwhile, mix the dressing ingredients together along with the clementine juice. If eating straightaway, divide the salad leaves between two plates and top with the warm potatoes and beans and the clementine slices. Arrange the salmon fillets on top, scatter over the herbs and spoon over the dressing. If taking to work, prepare the potatoes, beans and salmon the night before, then pack into a rigid airtight container with the salad leaves kept separate. Put the salad elements together and dress just before eating to prevent the leaves from wilting.','7 mins','16 mins'),
(1228,'grapefruit, agave & pistachio salad','https://www.bbcgoodfood.com/recipes/grapefruit-agave-pistachio-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201667_11-3f88784.jpg','Fruit salads are quick to whip up and one of the healthiest ways to start the day - sweeten with a sugar alternative',5,2,1,2,3,'Segment grapefruits, removing as much of the pith as possible. Divide the segments between two bowls and top with with agave and pistachios.','5 mins','No Time'),
(25,'smoky tomato, chipotle & charred corn soup','https://www.bbcgoodfood.com/recipes/smoky-tomato-chipotle-charred-corn-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoky-tomato-chipotle-charred-corn-soup-4d9d7cf.jpg','Dig out the chipotle paste and make this punchy smoky tomato, chipotle and charred sweetcorn soup. An easy midweek meal, you can freeze any leftovers ',14,4,1,1,1,'Heat the oil in a casserole dish and fry the onion for 10 mins until beginning to soften. Add the garlic, ground coriander and coriander stalks, and cook for 1 min. Stir though the tomatoes, stock and chipotle and bring to a simmer. Cook, covered, over a low heat for 20 mins, stirring occasionally. Meanwhile, bring a pan of water to the boil and cook the corn for 4 mins. Drain and leave to cool a little. Cut the kernels off the cob with a sharp knife. Heat a non-stick frying pan over a high heat. Add the corn and fry for 5-7 mins or until charred, stirring now and again. Ladle the soup into bowls. Top with the feta, yogurt, charred corn and coriander leaves.','10 mins','30 mins'),
(26,'lemon & spinach rice with feta','https://www.bbcgoodfood.com/recipes/lemon-spinach-rice-with-feta','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Greek-spinach-and-rice-d16d518.jpg','Pack in spinach, feta and walnuts into this rice dish. It''s bursting with nutrients, including vitamin K, which is important for our skin, hair and bones',4,4,1,1,1,'Heat the oil in a large pan over a medium heat and fry the onions and garlic for 10 mins, stirring often until softened. Tip in the rice, then the stock, frozen spinach and half each of the dill and lemon zest. Cover, reduce the heat to low and cook for 25 mins until the rice is tender. Scatter over the remaining dill and lemon zest, the nuts and feta, then gently toss together along with the lemon juice. Serve half of the rice straightaway with two lemon wedges on the side for squeezing over. Leave the rest of the rice to cool completely and keep chilled for up to three days. Reheat in the microwave until piping hot, then serve with the remaining lemon wedges.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','40 mins'),
(27,'healthy pesto eggs on toast','https://www.bbcgoodfood.com/recipes/healthy-pesto-eggs-on-toast','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/Healthy-pesto-eggs-on-toast-2e7feac.jpg','Try a speedy, low-fat pesto as a delicious alternative to oil for frying eggs - it adds great flavour to the dish, too, to make a perfect brunch or lunch',4,2,1,1,1,'To make the pesto, peel the garlic clove and put in a small food processor along with the basil, pine nuts, oil and 2 tbsp water. Blitz until smooth, then stir in the cheese. Or, blitz using a hand blender.  Toast the bread and divide between two plates. Cook the pesto in a frying pan over a medium heat for 30 seconds, stirring. Crack the eggs into one side of the pan, put the tomatoes in the other, and fry in the pesto until the eggs are cooked to your liking. Lift out the eggs and put each one on a slice of toast. Add the spinach to the pan with the tomatoes, turn the heat up to high and cook until wilted, about 2-3 mins. The tomatoes should be soft. Spoon the veg onto the other toast slice and sprinkle with the chilli flakes, if you like.','5 mins','15 mins'),
(28,'cavolo nero orecchiette','https://www.bbcgoodfood.com/recipes/cavolo-nero-orecchiette','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Cavolo-nero-orecchiette-369e36f.jpg','Enjoy this easy-to-make, quick pasta. It’s full of goodness from the iron-rich cavolo nero, which is in season over the winter',15,2,1,1,1,'Cook the orecchiette following pack instructions, then drain, reserving a mugful of the water. Finely chop the cavolo nero stems and leaves, keeping them separate. Heat the olive oil in a wide frying pan and cook the stems, garlic, chilli and shallot over a low heat for 5 mins until softened, then stir in the cavolo nero leaves. Fry until just wilted, then tip in the pasta and toss to combine. Stir in the lemon zest and juice, and the ricotta along with a splash of the reserved pasta water to loosen, and season. Sprinkle the almonds over the top to serve.','10 mins','20 mins'),
(29,'chicken satay salad','https://www.bbcgoodfood.com/recipes/chicken-satay-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-satay-salad-8f5b068.jpg','Try this no-fuss, midweek meal that''s high in protein and big on flavour. Marinate chicken breasts, then drizzle with a punchy peanut satay sauce',4,2,1,1,1,'Pour the tamari into a large dish and stir in the curry powder, cumin, garlic and honey. Mix well. Slice the chicken breasts in half horizontally to make 4 fillets in total, then add to the marinade and mix well to coat. Set aside in the fridge for at least 1 hr, or overnight, to allow the flavours to penetrate the chicken. Meanwhile, mix the peanut butter with the chilli sauce, lime juice, and 1 tbsp water to make a spoonable sauce. When ready to cook the chicken, wipe a large non-stick frying pan with a little oil. Add the chicken and cook, covered with a lid, for 5-6 mins on a medium heat, turning the fillets over for the last min, until cooked but still moist. Set aside, covered, to rest for a few mins. While the chicken rests, toss the lettuce wedges with the cucumber, shallot, coriander and pomegranate, and pile onto plates. Spoon over a little sauce. Slice the chicken, pile on top of the salad and spoon over the remaining sauce. Eat while the chicken is still warm.','15 mins','5 mins - 10 mins'),
(30,'veggie olive wraps with mustard vinaigrette','https://www.bbcgoodfood.com/recipes/veggie-olive-wraps-mustard-vinaigrette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/olive-wrap-c866064.jpg','Eat the rainbow with our simple, healthy, veggie wrap. This olive and veg sandwich makes an easy vegan, low-calorie lunch option to eat al-desko',4,1,1,1,1,'Mix all the ingredients except for the tortilla and toss well. Put the tortilla on a sheet of foil and pile the filling along one side of the wrap – it will almost look like too much mixture, but once you start to roll it firmly it will compact. Roll the tortilla from the filling side, folding in the sides as you go. Fold the foil in at the ends to keep stuff inside the wrap. Cut in half and eat straight away. If taking to work, leave whole and wrap up like a cracker in baking parchment.','10 mins','No Time'),
(31,'bulgur & aubergine pilaf','https://www.bbcgoodfood.com/recipes/bulgur-aubergine-pilaf','https://images.immediate.co.uk/production/volatile/sites/30/2023/03/Bulgur-and-aubergine-pilaf-1efa5fc.jpg','Swap rice for quick-cooking bulgur wheat and chickpeas in this gently spiced pilaf, peppered with fried aubergine, sticky onions and crumbled feta',4,2,1,1,1,'Heat 2 tsp of the the oil in a large non-stick frying pan over a medium heat and fry the onions and ginger for 10 mins, stirring frequently until softened and golden. Set aside on a plate. Meanwhile, tip the bulgur, bouillon and cumin into a pan and cover with 400ml water. Stir well, then bring to the boil, cover and cook over a low heat for 10-15 mins, or until the liquid has completely absorbed. Tip the chickpeas into the pan, then cover and set aside. Meanwhile, heat the remaining oil in the pan you used to fry the onion over a medium-high heat, and fry the aubergine slices for 8-10 mins, keeping them in a single layer as much as possible. Flip them over halfway through the cooking time. After this time, cover and cook for a few minutes more until soft. Toss the cooked aubergine into the bulgur mixture along with the onions and coriander, then pile onto a platter and scatter over the feta and almonds before serving.','10 mins','35 mins'),
(32,'sweet potato toasts with curried chickpeas','https://www.bbcgoodfood.com/recipes/sweet-potato-toasts-with-curried-chickpeas','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Sweet-potato-toasts-7cd74e0.jpg','Make this healthier – and maybe even tastier – version of beans on toast to achieve four of your five-a-day',4,4,1,1,1,'Heat the oven to 200C/180C fan/gas 6. Brush the sweet potato slices with a little oil, arrange in a single layer on a baking tray, then bake for 20-25 mins. Check towards the end to make sure they are not catching – move around if the slices near the edges of the tray are browning faster than the ones in the middle. For the chickpeas, heat the oil in a large pan over a medium heat and fry the celery for 5 mins, stirring frequently. Add the garlic, curry powder and cumin seeds, stir briefly, then tip in the tomatoes, tomato purée and bouillon. Stir in the chickpeas and frozen spinach, then cover and simmer for 15 mins. Stir in the coriander. Divide half the chickpeas between six pieces of the sweet potato toast, and serve three toasts per person. Cool and chill the rest to eat another day. To serve, reheat the chickpeas in a pan over a low heat with a splash of water until piping hot. Briefly heat the sweet potato slices in a dry frying pan over a low heat, watching carefully so they don''t burn. Will keep chilled for three days. This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','45 mins'),
(33,'quick chicken hummus bowl','https://www.bbcgoodfood.com/recipes/quick-chicken-hummus-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quick-chicken-and-hummus-bowl-3863168.jpg','Make our healthy chicken and hummus salad bowl for a delicious budget lunch option. It can be thrown together in just 10 minutes and delivers three of your 5-a-day',6,2,1,1,1,'Mix 2 tbsp of the hummus with half the lemon juice, the lemon zest and enough water to make a drizzly dressing. Squeeze the grain pouch to separate the grains, then divide between two shallow bowls and toss through the dressing. Top each bowl with a handful of the spinach. Squeeze the remaining lemon juice over the avocado halves, then add one half to each bowl. Divide the chicken, pomegranate seeds, onion, almonds and remaining hummus between the two bowls and gently mix everything together just before eating.','10 mins','N/A'),
(38,'feta & kale loaded sweet potato','https://www.bbcgoodfood.com/recipes/feta-and-kale-loaded-sweet-potato','https://images.immediate.co.uk/production/volatile/sites/30/2018/02/feta-kale-sweet-potato-92ade52.jpg','Serve up this sweet potato packed with feta, chickpeas and kale for a healthy lunch. Topped with pumpkin seeds, it delivers three of your 5-a-day',16,2,1,1,1,'Heat oven to 200C/180C fan/gas 6. Prick the sweet potatoes all over with a fork, then put them in a roasting tin and roast for 40 mins. Add the chickpeas to the tray, then roast for 10 mins more, until the potatoes are completely tender and the chickpeas have crisped a little. Meanwhile, mix the onion with the vinegar and a pinch of sugar and salt, and set aside to quick pickle. In another bowl, marinate the feta with the oil and chilli flakes. When the potatoes are nearly cooked, cook the kale in a pan with 50ml water for 3 mins until wilted, then season to taste. Halve the potatoes, divide between two plates and top each with the kale, chickpeas, red onion (reserving the vinegar), marinated feta and pumpkin seeds. Toss the rocket with the reserved vinegar, then serve on the side.','10 mins','50 mins'),
(34,'spicy red lentil chilli with guacamole & rice','https://www.bbcgoodfood.com/recipes/spicy-red-lentil-chilli-with-guacamole-rice','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Lentil-chilli-2158471.jpg','Serve vegan chilli made with red lentils and plenty of veg – enjoy it with homemade guacamole and brown rice for a filling and healthy dinner',4,4,1,1,1,'Heat the oil in a large pan over a medium heat and fry the peppers for 5 mins, stirring frequently. Add the garlic and spices, stir briefly, then tip in the passata and stock. Stir in the lentils and thyme, then cover and simmer for 15-20 mins until pulpy. Tip in the beans along with the liquid from the can, then re-cover and simmer for another 10-15 mins. Cook the rice following pack instructions. Combine half of the guacamole ingredients. Serve half the rice and chilli between two people, topped with the guacamole. Cool and chill the rest to eat another day. To serve, reheat the chilli in a pan over a low heat with a splash of water until piping hot. Reheat the rice in the microwave. Combine the rest of the guacamole ingredients to make a fresh batch, and serve on top of the chilli and rice. Will keep chilled for up to four days.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','55 mins'),
(35,'bombay potato frittata','https://www.bbcgoodfood.com/recipes/bombay-potato-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bombay-potato-frittata-f0d52f5.jpg','Pack in the flavour with this Indian-inspired frittata. Perfect for a healthy lunch, it''s low in calories and fat and full of nutrients ',14,2,1,1,1,'Cook the potatoes in a pan of boiling water for 6 mins, or until tender. Drain and leave to steam-dry. Meanwhile, put the spinach in a heatproof bowl with 1 tbsp water. Cover and microwave for 3 mins on high, or until wilted. Heat the rapeseed oil in a medium non-stick frying pan. Add the onion and cook over a medium heat for 10 mins until golden and sticky. Stir in the garlic, ground coriander, ground cumin, mustard seeds and turmeric, and cook for 1 min more. Add the tomatoes and wilted spinach and cook for another 3 mins, then add the potatoes. Heat the grill to medium. Lightly beat the eggs with the chilli and most of the fresh coriander and pour over the potato mixture. Grill for 4-5 mins, or until golden and just set, with a very slight wobble in the middle. Leave to cool, then slice into wedges. Mix the mango chutney, yogurt and remaining fresh coriander together. Serve with the frittata wedges.','15 mins','35 mins'),
(36,'roasted cauli-broc bowl with tahini hummus','https://www.bbcgoodfood.com/recipes/roasted-cauli-broc-bowl-tahini-houmous','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roasted-cauli-bowl-a7bb279.jpg','A simple quinoa bowl you can put together in 10 minutes and enjoy al-desko. It''s vegan, healthy and gluten-free',1,2,1,1,1,'The night before, heat oven to 200C/180C fan/gas 6. Put the cauliflower and broccoli in a large roasting tin with the oil and a sprinkle of flaky sea salt. Roast for 25-30 mins until browned and cooked. Leave to cool completely. Build each bowl by putting half the quinoa in each. Lay the slices of beetroot on top, followed by the spinach, cauliflower, broccoli and walnuts. Combine the tahini, hummus, lemon juice and 1 tbsp water in a small pot. Before eating, coat in the dressing. Serve with the lemon wedges.','10 mins','30 mins'),
(37,'paneer jalfrezi with cumin rice','https://www.bbcgoodfood.com/recipes/paneer-jalfrezi-cumin-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paneer-jalfrezi-440-400-e63da48.jpg','Spice up your week with this satisfying, healthy, low-calorie veggie curry. It provides calcium, iron, vitamin C and fibre, along with three of your 5-a-day',4,4,1,1,1,'Heat 1 tsp oil a large non-stick frying pan and fry the chopped onions, garlic and half the ginger for 5 mins until softened. Add the ground coriander and cumin seeds and cook for 1 min more, then tip in the tomatoes, half a can of water and the bouillon. Blitz everything together with a stick blender until very smooth, then bring to a simmer. Cover and cook for 15 mins. Meanwhile, cook the rice and cumin seeds in a pan of boiling water for 25 mins, or until tender. Heat the remaining oil in a non-stick wok and fry the paneer until lightly coloured. Remove from the pan and set aside. Add the peppers, onion wedges and chilli to the pan and stir-fry until the veg is tender, but still retains some bite. Mix the stir-fried veg and paneer into the sauce with the chopped coriander, then serve with the rice. If you''re following our Healthy Diet Plan, eat two portions of the curry and rice, then chill the rest for another day. Will keep for up to three days, covered, in the fridge. To serve on the second night, reheat the leftover portions in the microwave until piping hot.','20 mins','30 mins'),
(66,'herby quinoa, feta & pomegranate salad','https://www.bbcgoodfood.com/recipes/herby-quinoa-feta-pomegranate-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201639_11-e0a1ec1.jpg','A Middle Eastern-style meze bowl with healthy grains, parsley, mint and coriander',17,4,1,1,1,'Cook the quinoa following pack instructions – it should be tender but with a little bite. Drain well and spread over a platter or wide, shallow bowl to cool quickly and steam dry. When the quinoa is just about cool stir through all of the remaining ingredients with plenty of seasoning.','10 mins','15 mins'),
(39,'lentil & tuna salad','https://www.bbcgoodfood.com/recipes/lentil-tuna-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/Lentil-and-tuna-salad-76f39e2.jpg','Throw together this easy, no-cook lentil and tuna salad for a speedy and healthy lunch or supper. It takes just 15 minutes to make – ideal for busy days',18,4,1,1,1,'Whisk the vinegar, mustard and garlic together in a small bowl. Slowly drizzle in the oil, whisking as you go, until emulsified, then season to taste. Add the lentils, tuna, tomatoes, peppers and herbs to a large bowl and toss together. Pour over the dressing and toss again. Divide between four bowls and garnish with the remaining chives.','15 mins','No Time'),
(40,'healthy tomato soup','https://www.bbcgoodfood.com/recipes/5-veg-creamy-tomato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-soup-with-cheese-spinach-pinwheels-91ea033.jpg','Enjoy this vegetable-packed tomato soup for a lovely light family meal, with leftovers for the next day. Serve with bread or our cheese pinwheels',6,6,1,1,1,'Heat the oil in a large pan and cook the onion for 8-10 mins until softened and starting to take on a little colour. Add the carrots, garlic, peppers and sweet potato. Cook for a further 10 mins, stirring often and taking care not to burn the garlic. Add the tomatoes, stock, herbs and 1 litre of water. Bring to a simmer. Cover and cook for 20 mins until the sweet potato is soft. Use a hand blender to whizz the soup until smooth. Stir in the cream and season to taste. Can be chilled for three days or frozen for up to three months.','20 mins','40 mins'),
(41,'cumin roast veg with tahini dressing','https://www.bbcgoodfood.com/recipes/cumin-roast-veg-tahini-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cumin-roast-vegetables-with-eggs-440-400-d4181ed.jpg','Get all five of your 5-a-day in one super-healthy packed lunch, bursting with nutrients including calcium, folate, fibre, vitamin C and iron',4,4,1,1,1,'Heat oven to 200C/180C fan/gas 6. Tip all of the vegetables into a large roasting tin. Add the cumin seeds, oil and balsamic vinegar, then toss together. Roast for 45-50 mins until the veg is tender and starting to char. Meanwhile, mix the tahini and peanut butter with the lemon juice, coriander, garlic and about 4-5 tbsp water to make a dressing. When the veg is ready, leave to cool a little. Add the mint, coriander, lemon zest and chickpeas, then toss well. If you''re following our Healthy Diet Plan, serve two portions now with the eggs, some dressing and half the spinach, then serve the remainder on another day without the eggs.','10 mins','45 mins - 50 mins'),
(42,'burrito bowl with chipotle black beans','https://www.bbcgoodfood.com/recipes/burrito-bowl-chipotle-black-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/burrito-bowl-3629880.jpg','This healthy burrito bowl is chock full of veggies and greens, perfect for a filling lunch. This is one vegetarian meal that tastes just as good as it looks',10,2,1,1,1,'Cook the rice following pack instructions, then drain and return to the pan to keep warm. In a frying pan, heat the oil, add the garlic and fry for 2 mins or until golden. Add the beans, vinegar, honey and chipotle. Season and warm through for 2 mins. Boil the kale for 1 min, then drain, squeezing out any excess water. Divide the rice between big shallow bowls and top with the beans, kale, avocado, tomato and onion. Serve with hot sauce, coriander and lime wedges, if you like.','15 mins','15 mins'),
(43,'rainbow pizzas','https://www.bbcgoodfood.com/recipes/rainbow-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rainbow-pizzas-fac9f95.jpg','Forget takeaway pizzas, encourage kids to eat a rainbow every day with this colourful recipe full of fresh flavours – guaranteed to please even fussy eaters',19,4,1,1,1,'Heat the oven to 180C/160C fan/gas 4. Put each pizza base on a baking sheet and spread each with half of the passata. Arrange the tomatoes on the top in rings or wedges of colour and add the broccoli and the olives, if using. Squish the mozzarella cherries (bocconcini) a little before dotting them over the pizzas, then drizzle 1 tbsp pesto over each. Bake for 15-20 mins or until the top is bubbling and just starting to brown a little. Scatter over the basil leaves before serving.','20 mins','20 mins'),
(44,'mexican-style stuffed peppers','https://www.bbcgoodfood.com/recipes/mexican-style-stuffed-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mexican-style-stuffed-peppers-23ce35d.jpg','Stuff peppers with rice and black beans, then top with chilli cheese and fresh guacamole for an easy vegetarian family meal. If the kids don''t like spice, use a mild cheese instead',6,2,1,1,1,'Heat the oven to 220C/200C fan/gas 7. Remove the seeds and any white pith from the peppers and arrange, cut-side up, in a roasting tin. Drizzle with oil and season, then bake for 20 mins. Combine the rice and beans. Remove the peppers from the oven and fill with the rice mixture. Top each with a slice of cheese and bake for 10-15 mins more, until the rice has melted and the filling is hot. Top with spoonfuls of guacamole.','15 mins','35 mins'),
(45,'15-minute chicken & halloumi burgers','https://www.bbcgoodfood.com/recipes/15-minute-chicken-halloumi-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/15-minute-chicken-halloumi-burgers-ef95d1d.jpg','Struggle to find dishes to cook that take very little time but the whole family will love? Look no further than these chicken and halloumi burgers',12,4,1,1,1,'Put the chicken breasts between two pieces of baking parchment and bash with a rolling pin until 1cm thick. Cut each into two even pieces.  Heat two frying pans over a medium-high heat, heating the oil in one. Fry the chicken in the oiled pan for 3-4 mins on each side until cooked through. Season. Reduce the heat, drizzle in the chilli sauce and half the lemon juice and cook for 1-2 mins more until the sauce is reduced. Remove from the heat. While the chicken cooks, toast the buns in the dry frying pan for 30 secs. Transfer to a plate, then fry the halloumi in the pan (add a bit of oil if the pan is not non-stick) for 30 secs on each side until golden. Toss the cabbage with the mayo and remaining lemon juice. Spoon the hummus (or dip of your choice) into the toasted buns, then top with the rocket, chilli chicken, halloumi and peppers. Drizzle with a little more chilli sauce, spoon over the cabbage, season with black pepper, and top with the bun lids. Serve with any extra cabbage on the side, or a green salad.','5 mins','10 mins'),
(46,'flatbreads with brunch-style eggs','https://www.bbcgoodfood.com/recipes/flatbreads-brunch-style-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/flatbreads-with-brunch-style-eggs-54092f0.jpg','Make our homemade flatbreads and serve alongside brunch-style eggs with cheese and tomatoes. This easy recipe is great for getting kids involved in the kitchen',19,6,1,1,1,'Sift the flours and 1 tsp salt into a large bowl. Add 1 tbsp of the oil and 150ml warm water. Bring together into a soft but not too sticky dough (you may need up to 175ml water). If it feels too wet, add some flour. If it’s too dry, add water.  Tip onto a floured surface and knead for 4-5 mins, or until smooth. Put the dough in an oiled bowl, cover and leave for 30 mins. Tip onto a floured surface. Divide into six balls and roll each out into a thin, 18-20cm wide circle using a rolling pin. If you prefer, you can divide again into twelve balls to make smaller flatbreads. Brush a heavy-based pan with oil and cook one flatbread over a high heat for 1-2 mins on each side, or until golden and starting to puff. Put on a plate and brush with butter. Repeat with the rest of the dough.  Meanwhile, for the eggs, heat the oil in a small non-stick pan and cook the tomatoes briefly until just softened. Season. Crack the eggs into the pan, add the cheese and cream, cover and cook for 2 mins. Remove the lid. Cook until the egg whites are set, then serve from the pan with the flatbreads, making sure the pan has cooled a little first.','20 mins','15 mins'),
(47,'cheese, marmite & spinach pinwheels','https://www.bbcgoodfood.com/recipes/cheese-marmite-spinach-pinwheels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-soup-with-cheese-spinach-pinwheels_0-001bdf6.jpg','Make these easy pinwheels with cheddar, Marmite and spinach flavours to serve with soup or as part of a family picnic. Freeze before baking, if you prefer',6,12,1,1,1,'Heat the oven to 200C/180C fan/ gas 6. Unroll the pastry with the longer side directly in front of you. Spread the Marmite over the surface. Tear the spinach over the Marmite and scatter with the cheese, making sure it’s evenly spread. Tightly roll the pastry away from you (starting from the longer side) to create a long sausage shape, then cut into 12 pieces. Arrange over two baking trays lined with baking parchment, spiral side up, and squash down a little with your hand. Will keep covered and frozen at this stage. Bake for 15-20 mins (or 25 mins if cooking from frozen), until puffed and golden brown.','5 mins','20 mins'),
(48,'puff pastry pizzas','https://www.bbcgoodfood.com/recipes/puff-pastry-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/puff-pastry-pizzas-06d1019.jpg','Have fun preparing these puff pastry pizzas with kids. Chop up any veg you have into small pieces and let everyone choose their own toppings',6,4,1,1,1,'Heat the oven to 200C/180C fan/gas 6. Unroll the pastry, cut into six squares and arrange over two baking trays lined with baking parchment. Use a cutlery knife to score a 1cm border around the edge of each pastry square. Bake for 15 mins, until puffed up but not cooked through. While the pastry cooks, make the sauce and prepare your toppings. Mix the tomato purée, tomato ketchup, oregano and 1 tbsp water. Grate the cheese and chop any veg or herbs you want to put on top into small pieces. Set aside. Remove the pastry from the oven and squash down the middles with the back of a spoon. Divide the sauce between the pastry squares and spread it out to the puffed-up edges. Sprinkle with the cheese, then add your toppings. Bake for another 5-8 mins and serve.','20 mins','25 mins'),
(49,'sesame, edamame & chicken noodle salad','https://www.bbcgoodfood.com/recipes/sesame-edamame-chicken-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sesame-edamame-chicken-noodle-salad-a434625.jpg','Pack a picnic for the family with this healthy wholewheat noodle lunch with chicken, edamame, broccoli and sesame seeds. Make it in just 20 minutes',6,3,1,1,1,'Boil the kettle. Remove the chicken skin, then shred the meat and discard the bones. Set aside. In a large pan, toast the sesame seeds for 1 min until golden, then tip into a large bowl (big enough to hold the noodles). Fill the pan with water from the kettle and bring back to the boil. Add the noodles and cook following pack instructions. Tip in the beans and broccoli for the last 2 mins. Meanwhile, mix the tahini, sesame oil, honey, soy sauce and vinegar into the sesame seeds. Drain the noodles, reserving a cup of the cooking water, and run under cold water to cool. Drain again and toss through the sauce along with the chicken. Add a splash of the cooking water to make the sauce thin enough to coat the noodles (it will thicken as it cools). Transfer to containers and chill until ready to eat, if not eating straight away.','15 mins','5 mins'),
(50,'pick & mix pesto pasta salad bar','https://www.bbcgoodfood.com/recipes/pick-mix-pesto-pasta-salad-bar','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pick-mix-pesto-pasta-salad-bar-5c2fb81.jpg','Put out all the ingredients for classic pasta salad with pesto dressing and let the kids pick whatever they like to stir in – it''s great for fussy eaters ',7,6,1,1,1,'Cook the pasta following pack instructions, then drain and toss in 1 tbsp oil. Transfer to a large bowl and set aside to cool, tossing occasionally so the pasta doesn’t stick. Mix the remaining olive oil with the pesto and set aside. Both can be prepared up to two days ahead, then covered and chilled. Put all the ingredients into individual bowls and serve with a large empty bowl and wooden spoon for mixing individual portions of pasta salad with your choice of ingredients.','10 mins','15 mins'),
(51,'easy egg muffins','https://www.bbcgoodfood.com/recipes/easy-egg-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egg-muffins-5cf2ffe.jpg','Make these mini egg muffins for an easy breakfast or lunch with the kids. Add chopped ham, bacon or smoked salmon, if you fancy',14,8,1,1,1,'Heat the oven to 200C/180C fan/gas 4. Brush half the oil in an 8-hole muffin tin. Heat the remaining oil in a frying pan and add the broccoli, pepper and spring onions. Fry for 5 mins. Set aside to cool.  Whisk the eggs with the milk, smoked paprika and half the cheese in a bowl. Add the cooked veg. Pour the egg mixture into the muffin holes and top each with the remaining cheese and a few chives, if you like. Bake for 15-17 mins or until golden brown and cooked through. ','15 mins','25 mins'),
(52,'layered rainbow salad pots','https://www.bbcgoodfood.com/recipes/layered-rainbow-salad-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/layered-rainbow-salad-pots-dd0b73f.jpg','Give kids two of their five-a-day and a dose of vitamin C with these colourful rainbow pots of tuna salad, ideal for a lunchbox, or summer picnic',19,4,1,1,1,'Cook the pasta until it is still a little al dente (2 mins less than the pack instructions) and drain well. Cook the green beans in simmering water for 2 mins, then rinse in cold water and drain well. Mix the tuna with the mayonnaise and yogurt. Add the chives, if using. Tip the pasta into a large glass bowl or four small ones, or four wide-necked jars (useful for taking on picnics). Spoon the tuna dressing over the top of the pasta. Add a layer of green beans, followed by a layer of cherry tomatoes, then the pepper and sweetcorn. Cover and chill until you’re ready to eat.','25 mins','12 mins'),
(53,'sausage & pesto pizza','https://www.bbcgoodfood.com/recipes/sausage-pesto-pizza','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-and-pesto-pizza-dbf1a0e.jpg','Make these cheesy sausage and pesto pizzas with just a handful of ingredients for an easy, takeaway-style dinner. If you prefer, make these veggie-friendly using our tip below',14,4,1,1,1,'Remove the pizza dough from the freezer 1-2 hrs before cooking to defrost. Heat the oven to 220C/200C fan/gas 7. Put a large baking sheet in the oven to heat. Tip the chopped tomatoes into a saucepan, simmer uncovered for 15 mins until reduced and saucy, then season to taste. Squeeze the sausagemeat out of the skins and into a bowl. Divide into 10 small balls. Pour the oil into a non-stick frying pan and fry the meatballs over a medium heat for 5 mins until golden brown. Roll the dough out into two oval-shaped pizzas about 20cm long. Top with a layer of the tomato sauce, then swirl in the pesto. Tear the mozzarella and scatter that over along with the sausage meatballs. Slide the pizzas onto the hot baking sheet and cook for 18-20 mins or until crisp and charred at the edges. Scatter over a few basil leaves to serve, if you like.','20 mins','40 mins'),
(54,'loaded potato skins','https://www.bbcgoodfood.com/recipes/loaded-potato-skins','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Loaded-potato-skins-b7ca3c5.jpg','Enjoy these potato skins as a relaxed starter or snack. Use chives instead of spring onion for a milder flavour, or omit the bacon for a veggie version',20,4,1,1,1,'Heat the oven to 220C/200C fan/gas 7. Put the baking potatoes on a baking tray. Prick all over with a fork and rub with a little oil. Cook potatoes in oven for 1 hr until golden and cooked all the way through. Remove from oven. Allow potatoes to cool for 15 mins. While potatoes are cooking, heat 1 tsp oil in a medium frying pan and fry the bacon for 5-8 mins until crisp. Leave to cool, then transfer to a board and finely chop. Cut the slightly cooled potatoes in half and scoop the flesh from the skin into a large bowl, leaving a little around the edges. Put the skins back on the baking tray, scooped side up. Mash the potato flesh and soured cream together using a fork or masher. Stir in the cooled bacon, most of the spring onion or chives and half of the cheddar. Season to taste. Divide the potato mixture evenly between the cavities of the potato skins. Sprinkle the remaining cheddar over each filled potato half. Return the potatoes to the oven for 20 mins until golden and bubbling. Sprinkle over the remaining spring onion or chives and serve hot.','10 mins','1 hr and 20 mins'),
(239,'green breakfast smoothie','https://www.bbcgoodfood.com/recipes/green-breakfast-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-breakfast-smoothie-60e5e6a.jpg','Blitz healthy ingredients for an energy-boosting breakfast. Using unsweetened brown rice milk fortified with calcium and vitamins makes it more nutritious',21,2,1,1,2,'Whizz 300ml water and the ingredients in a blender until smooth.','10 mins','No Time'),
(55,'easy cheesy frittata','https://www.bbcgoodfood.com/recipes/easy-cheesy-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-231460_10-81626ab.jpg','This simple lunch dish is a great way of getting children aged 5-9 involved in the kitchen',5,4,1,1,1,'Ask a grown-up helper to switch the oven on to 180C/ 160C fan/gas 4. Snip or cut up the spring onions and put them in a bowl. Add the peas to the bowl. Grate the courgette using your rotary grater, one half at a time, then add it to the bowl. Cut the ham into pieces with your scissors, if you do this over the bowl it will fall straight in. Break the feta into the bowl by crumbling it with your hands. Crack the eggs into a bowl and, if any bits of shell fall in, scoop them out with a spoon. Whisk the eggs until the yolks are mixed into the white. Pour the eggs into the other bowl and stir. Brush a round ovenproof dish, about 16cm across, with oil. Tip everything into the dish. Ask a helper to put the dish in the oven for 30 minutes or until the egg is set. Serve with salad and crusty bread.','5 mins - 15 mins','30 mins'),
(56,'real tomato soup','https://www.bbcgoodfood.com/recipes/real-tomato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560455_11-4f8e0cc.jpg','Creamy tomato soup is the stuff of childhood, and it''s easy to make yourself. Emma Lewis shows you how',8,4,1,1,1,'Heat the olive oil in a large pan, then tip in the onion and garlic. Cook over a moderate heat until the onion has softened, about 5 mins. Stir in the tomato purée, then pour in the chopped tomatoes and basil leaves, and bring up to the boil. Turn the heat down and leave to simmer for about 15 mins until thick and full of flavour. If you like a smooth soup, whizz the mixture at this point to form a smooth sauce. Can now be cooled and frozen for up to 1 month. To finish the soup, tip the tomato mixture into a pan. Spoon the baking soda into a small bowl and pour over 1 tbsp or so of the milk. Mix together until there are no lumps, then tip into the tomato mix and pour over the milk. Bring up to a boil (the mixture will froth, but don’t worry – it will go away). Gently simmer for about 5 mins until ready to serve.','5 mins','25 mins'),
(57,'foolproof slow roast chicken','https://www.bbcgoodfood.com/recipes/foolproof-slow-roast-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52090_11-61667fe.jpg','Slow-roasting is a great way to keep chicken nice and moist. Adding the potatoes to the roasting tin infuses them with plenty of flavour, too',5,4,1,1,1,'Heat oven to 160C/fan 140C/gas 3. Brush a large roasting tin all over with butter and smear some over the skin of the chicken. Place the chicken in the tin and arrange the potatoes around it. Put the halved garlic heads in the tin, pour over the white wine and stock, then cover with foil and place in the oven. Cook for 1 hr then remove the foil and give the potatoes a shake. Add the herbs and lemon wedges, then cook uncovered for 50 mins. Turn the heat up to 220C/fan 200C/gas 7. Cook for 30 mins more, then remove the chicken and potatoes from the pan. Cover the chicken loosely with foil and leave to rest on a plate for at least 10 mins before carving. Keep the potatoes warm. Serve with any pan juices.','15 mins','2 hrs and 20 mins'),
(58,'tuna melt pizza baguettes','https://www.bbcgoodfood.com/recipes/tuna-melt-pizza-baguettes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-695517_11-c375813.jpg','Quick and easy pizza breads that little ones will love',5,4,1,1,1,'Heat oven to 190C/170C fan/gas 5. Halve the 2 part-baked baguettes lengthways and cook directly on the oven shelf for 8 mins. Meanwhile mix the diced peppers, sweetcorn, tuna, and 75g of the grated cheddar. Transfer the baguettes to a baking tray, spread each with 1 tbsp tomato purée, divide the tuna mix over, then sprinkle with the remaining grated cheddar. Bake for 12 mins until melting and golden.','10 mins','20 mins'),
(59,'chicken enchiladas','https://www.bbcgoodfood.com/recipes/chicken-and-bean-enchiladas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken_enchiladas-ce0a2dd.jpg','Mildly spicy and satisfying, these easy enchiladas stuffed with refried beans, onion and red pepper will be popular with the whole family',17,10,1,1,1,'Heat 2 tbsp of the oil in your largest pan, then fry the onions, peppers, chopped chilli and coriander stalks with half the garlic for 10 mins until soft. Stir in 2 tsp ground coriander and 2 tsp cumin seeds, then fry for 1 min more. Meanwhile, in another frying pan, fry the chicken in the remaining oil, in batches, until browned – add it to the pan of veg as it is done. Stir the beans, sweetcorn, coriander leaves and 150ml of the passata into the veg and chicken. In a bowl, mix the rest of the passata with the other crushed garlic clove, the remaining spices and the sugar, then set aside. To assemble, lay the tortillas onto a board and divide the chicken mixture between them, folding over the ends and rolling up to seal. Divide the passata sauce into the dishes you are using, then top with the enchiladas. Dot over the soured cream, sprinkle with grated cheese and scatter with the sliced chilli. Cool and freeze (see freezing tips, below) or, if eating straight away, heat oven to 200C/180C fan/gas 6, then bake for 30 mins, scattering with more coriander leaves to serve, if you like.','20 mins','50 mins'),
(60,'chicken with spanish-style butter beans','https://www.bbcgoodfood.com/recipes/chicken-spanish-style-butter-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-with-spanish-style-butter-beans-64f1d34.jpg','Stuck for lunchbox inspiration? Try this healthy chicken and butterbean salad, which will fill you up for the afternoon and deliver four of your 5-a-day',16,2,1,1,1,'Squeeze the garlic out of the skins and whisk together with the oil, lemon zest, juice, smoked paprika and some seasoning in a large bowl. Add the butter beans, peppers, tomatoes and rocket, and give everything a good mix. Divide between lunchboxes. Top with the chicken on one side and crumble over the feta.','5 mins','No Time'),
(61,'giant couscous salad with charred veg & tangy pesto','https://www.bbcgoodfood.com/recipes/giant-couscous-salad-with-charred-veg-tangy-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Giant-Couscous-Salad-With-Charred-Veg-Tangy-Pesto-aea3737.jpg','The beetroot here is rich in folate, iron, manganese and potassium and a chemical called betalain, which has antioxidant and anti-inflammatory properties. Beetroot is also rich in nitrates, which may help lower blood pressure and improve athletic performance',5,4,1,1,1,'Heat the oven to 200C/180C fan/gas 6. In a bowl, toss the beetroot, onions and peppers together with the oil, then spread out on a large roasting tray lined with baking paper and roast for 35 mins. Scatter over the cherry tomatoes, then return to the oven for 10 mins more until the tomatoes have softened and the vegetables are tender. Meanwhile, cook the couscous following pack instructions, then rinse and drain. To make the pesto, put the coriander and half the parsley in a bowl with the garlic, chilli, cumin, vinegar, oil and 25g of the pine nuts. Add 2 tbsp water, then blitz with a hand blender until smooth or use a small food processor. Toss the roasted veg and chopped parsley through the couscous and pile on the pesto, then scatter with the remaining pine nuts. If you''re following the Healthy Diet Plan, serve half of the salad immediately then chill the rest for another day.','25 mins','50 mins'),
(62,'roasted veg & couscous salad','https://www.bbcgoodfood.com/recipes/roasted-veg-couscous-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-320580_11-2fff560.jpg','Throw together a filling and fragrant couscous salad for a vegetarian lunchbox meal, or double it for a buffet',5,4,1,1,1,'Heat oven to 200C/180C fan/gas 6. Cut peppers and squash into bite-size pieces (leave skin on the squash). Tip all the veg into a baking tray, add garlic, 2 tbsp oil and seasoning, then mix and roast for 20 mins. Add onion, cumin, harissa and almonds. Roast for another 20 mins, then cool. Put couscous into a large bowl, pour over the stock, cover, then set aside for 10 mins. Fluff up with a fork. In a bowl, mix zest, juice and remaining oil. Squeeze garlic pulp from skins into the bowl, mash well and fold in the mint. Pour over the veg, then toss with the couscous.','10 mins','40 mins'),
(63,'vegan kale pesto pasta','https://www.bbcgoodfood.com/recipes/vegan-kale-pesto-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/Kale-Pesto-df3faaf.jpg','Whizz up kale, pumpkin seeds, basil and garlic to make this easy kale pesto. It''s perfect stirred through wholemeal spaghetti for a healthy vegan meal',22,4,1,1,1,'Bring a pan of water to the boil. Cook the kale for 30 secs, drain and transfer to a bowl of ice-cold water for 5 mins. Drain again and pat dry with kitchen paper. Put the basil, garlic, seeds, oil, nutritional yeast, lemon juice and zest, and drained kale in a food processor. Blitz until smooth, then season. Loosen with a splash of water, if it’s too thick. Cook the pasta following pack instructions, then toss with the pesto and serve.','10 mins','10 mins'),
(64,'lime prawn cocktail pitta salad','https://www.bbcgoodfood.com/recipes/lime-prawn-cocktail-pitta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lime-prawn-cocktail-pitta-salad-8864325.jpg','Make this speedy lime prawn cocktail salad as a filling lunch that packs a flavour-punch. It''s tasty, healthy and low in calories',14,2,1,1,1,'Heat the oven to 200C/180C fan/gas 6. Slice the pitta into triangles, put on a baking sheet and drizzle over the oil. Bake for 10-15 mins until golden and crisp. Mix together the Tabasco, ketchup, mayo, yogurt and lime zest and juice. Toss the prawns in the dressing. Layer the lettuce, cucumber, tomatoes and dressed prawns in a lunchbox or jar. Season, top with the pitta chips and serve with lime wedges.','10 mins','15 mins'),
(65,'lentil salad with tahini dressing','https://www.bbcgoodfood.com/recipes/lentil-salad-tahini-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentil-salad-tahini-dressing-440-400-6248505.jpg','Pack four of your 5-a-day into each portion of this healthy lentil and sweet potato salad, which provides calcium, iron, folate, vitamin C and fibre.',4,4,1,1,1,'Heat the oil in a large non-stick wok. Add the sweet potato and fry for 5 mins, stirring frequently until it starts to soften. If it starts to brown too quickly, put a lid on the pan. Add the carrot, courgette, onion, cumin and ginger, then cook over a high heat, stirring frequently, until the veg is tender and a little charred. Stir in the seeds towards the end so they cook for a couple of mins. Remove from the heat and add the lentils, bouillon powder, lemon zest, mint and parsley. Meanwhile, stir the tahini with the garlic, yogurt and 1 tbsp water to make a dressing. Spoon the lentil salad into bowls and top with the dressing and paprika, if using. If you''re following our Healthy Diet Plan, save two portions stored in containers and chill until ready to eat.','15 mins','20 mins'),
(67,'sesame chicken noodles','https://www.bbcgoodfood.com/recipes/sesame-chicken-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sesame-chicken-noodles-1337dba.jpg','Forget soggy lunchbox sandwiches and whip up a tasty and healthy chicken and noodle salad for lunch using our pre-made roast chicken recipe',16,2,1,1,1,'Whisk together the tahini, lime juice, soy sauce, flesh from the roasted garlic, sesame oil and chilli flakes in a large bowl, adding enough water to make a creamy dressing. Add the noodles, leftover roast chicken, aubergine and carrot and toss everything to combine, then gently fold through the cucumber and mint. Divide between two containers, then sprinkle over a few extra chilli flakes to serve.','10 mins','No Time'),
(68,'coronation chicken salad','https://www.bbcgoodfood.com/recipes/coronation-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/coronation-chicken-salad-6de7ca3.jpg','Rustle up this healthy and filling coronation chicken salad for a packed lunch in just five minutes using our roast chicken cooked beforehand to save time',16,2,1,1,1,'Mix together the yogurt, curry powder, mango chutney and lemon zest with some seasoning in a large bowl. Add the grains, breaking them up with your fingers as you go, then add the chicken, onions, courgette and most of the coriander. Give everything a good stir and divide between two containers. Sprinkle over the remaining coriander, flaked almonds and nestle a lemon wedge into each lunchbox.','5 mins','No Time'),
(69,'healthy chicken salad','https://www.bbcgoodfood.com/recipes/asian-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488617_11-b39a07d.jpg','This easy-to-prepare healthy chicken salad is filled with zingy flavours - the simplified version is great for kids'' lunchboxes too',8,2,1,1,1,'Cover the chicken with cold water, bring to the boil, then cook for 10 mins. Remove from the pan and tear into shreds. Stir together the fish sauce, lime zest, juice and sugar until sugar dissolves. Place the leaves and coriander in a container, then top with the chicken, onion, chilli and cucumber. Place the dressing in a separate container and toss through the salad when ready to eat.','10 mins','10 mins'),
(70,'butternut squash salad','https://www.bbcgoodfood.com/recipes/butternut-squash-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201544_11-73656c5.jpg','Get a whopping 4 of your 5-a-day with this superhealthy salad, why not make extra for your lunchbox too',23,4,1,1,1,'Heat oven to 200C/180C fan/gas 6. Spread the squash over a large baking sheet, drizzle over the oil and bake for 30 mins or until tender. Meanwhile, cook the rice and lentils in boiling salted water for 20 mins, adding the broccoli for the final 4 mins of cooking. Drain well, then stir in the cranberries and pumpkin seeds with some seasoning. Add the squash, pour over the lemon juice and serve.','15 mins','50 mins'),
(71,'lemony tuna & asparagus salad box','https://www.bbcgoodfood.com/recipes/lemony-tuna-asparagus-salad-box','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tuna_0-bf83c5a.jpg','A new take on the classic salad Niçoise. This lean salad is bulked out by storecupboard cannellini beans and flavoured with dill and lemon',4,2,1,1,1,'Put a pan of water on to boil with a steamer above. When boiling, lower the eggs into the water and steam the asparagus above for 8 mins. Meanwhile, gently toss all the other ingredients together and arrange on plates or in rigid containers. Plunge the eggs into cold water to cool them a little, then peel and quarter. Add the eggs and asparagus to the tuna salad. Will keep in the fridge for 2 days.','5 mins','8 mins'),
(72,'chicken shawarma salad','https://www.bbcgoodfood.com/recipes/chicken-shawarma-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Chicken-shawarma-salad--2e6dcda.jpg','Serve a bright salad to feed a crowd. It can also be made with leftover cold chicken, and is robust enough to transport without wilting – perfect for picnics',7,6,1,1,1,'Mix the garlic, olive oil and lemon juice together in a reusable plastic container or bowl. Stir in all the spices, oregano, a large pinch of salt and the yogurt, then mix to make a paste. Tip in the chicken, tossing to coat, then cover and marinate for at least 1 hr, or chill overnight. To make the dressing, mix all the ingredients together in a small bowl, season, cover and chill until needed. Can be made up to two days ahead. Toss all the salad ingredients, except the lemon juice and olive oil, together. The salad can be assembled 1-2 hrs ahead and kept chilled. Light the barbecue or heat a griddle pan over a high heat. Cook the marinated chicken for 5 mins on each side, or until lightly charred and cooked through. Lift onto a board and set aside. Drizzle the salad with the lemon juice and olive oil, then season with salt and toss everything together. Scatter the salad over a large platter or tip into a large serving bowl. Slice the chicken, then arrange it over the salad (or serve it on the side if you’re also cooking for vegetarians). Serve the salad with the tahini dressing on the side for drizzling over, along with toasted flatbreads or pittas for stuffing the chicken into and pickled chillies, if you like.','30 mins','10 mins'),
(73,'sunshine cobb salad','https://www.bbcgoodfood.com/recipes/sunshine-cobb-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sunshine-cobb-salad-c76e756.png','Pack 4 of your 5-a-day into your lunchbox with this healthy black bean, egg and prawn salad with a sweet chilli and lime dressing',6,2,1,1,1,'Bring a small pan of water to the boil. Add the egg and cook for 8 mins. Drain, then run under cold water to cool. To assemble the salad, arrange the remaining ingredients in 2 containers. When the egg is cool, peel and quarter it and divide between the containers too. Mix together the dressing ingredients in a small bowl and transfer to 2 mini jars or containers to take with the salads to work. Dress and toss together just before eating.','20 mins','10 mins'),
(74,'chicken taco salad','https://www.bbcgoodfood.com/recipes/chicken-taco-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-taco-salad-814ac79.jpg','A crunchy chicken salad that''s zesty, healthy and 4 of your 5-a-day. Try this for variety in your teenager''s lunchbox or take it to work',1,1,1,1,1,'Make the dressing by combining the oil, soured cream and vinegar. Keep in the fridge. Put the lettuce, sweetcorn, tomatoes and chicken in a lunchbox. Stir the lime juice into the avocado and put on top. Pack the dressing on the side. Scatter the taco over the salad to serve.','10 mins','No Time'),
(75,'lemon & coriander hummus','https://www.bbcgoodfood.com/recipes/lemon-coriander-hummus','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424659_11-8a855ee.jpg','Hummus is so easy to make and beats shop-bought varieties every time. Try it with vegetable crudités in your packed lunch',5,6,1,1,1,'Put everything but the coriander into a food processor, then whizz to a fairly smooth mix. Scrape down the sides of the processor if you need to. Season the hummus generously, then add the coriander and pulse until roughly chopped. Spoon into a serving bowl, drizzle with olive oil, then serve.','5 mins','N/A'),
(76,'hearty mushroom soup','https://www.bbcgoodfood.com/recipes/hearty-mushroom-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52512_11-ace9a1e.jpg','A satisfying and low-fat vegetarian soup that''s a good source of folic acid',5,4,2,1,1,'Put the porcini in a bowl with 250ml boiling water and leave to soak for 25 mins. Heat the oil in a pan and add the onion, carrot, garlic, rosemary and seasoning. Fry for 5 mins on a medium heat until softened. Drain the porcini, saving the liquid, and finely chop. Tip into the pan with the fresh mushrooms. Fry for another 5 mins, then add the stock, marsala or sherry, tomato purée, barley and strained porcini liquid. Cook for 30 mins or until barley is soft, adding more liquid if it becomes too thick. Serve in bowls with parmesan sprinkled over, if desired.','30 mins','30 mins'),
(77,'healthy turkish meatloaf','https://www.bbcgoodfood.com/recipes/healthy-turkish-meatloaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/healthy-turkish-meatloaf-d0e9a9e.jpg','Serve this lamb meatloaf and salad at a summer picnic or packed up for lunch. It''s tasty and healthy too, delivering all five of your five-a-day',4,6,1,1,1,'Heat oven to 180C/160C fan/gas 4. Line a 900g loaf tin with baking parchment and lightly oil it. Put the onion and aubergine in a large bowl with the rice, tomato purée, bouillon, dill, spices and lamb. Mix and squash together with your hands then add the egg and mix again. Tip the can of tomatoes into the base of the loaf tin then pack the mince mixture on top and press down lightly to compact it. Cover the tin with foil and bake in the oven for 1 hr 40 mins, taking off the foil for the last 15 mins. Once the meatloaf has been cooking for 1 hr, tip the peppers, courgettes and red onion into a roasting tin and toss with the oil. Roast in the oven with the meatloaf for 40 mins until the vegetables are tender and a little charred. Take the meatloaf and roasted veg from the oven, leave the loaf to settle for 5 mins, then tip out onto a platter or board, remove the baking parchment and scatter with dill if using. Toss the roasted veg with the vinegar, garlic and mint and serve them with the meat loaf.','15 mins','1 hr and 30 mins'),
(78,'black bean tortilla with salsa','https://www.bbcgoodfood.com/recipes/black-bean-tortilla-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/black-bean-tortilla-with-salsa-440-400-d6dc0f5.jpg','Make this frittata for a quick, easy and healthy lunch option, making the most of storecupboard ingredients and packing in three of your 5-a-day',4,4,1,1,1,'Tip the tomatoes into a pan and stir in the onion, ½ the chilli and the smoked paprika. Cook over a low heat for 10 mins. Tip ¾ into a bowl, then stir in 2 tbsp of the coriander and the olives and lemon juice. Meanwhile, heat the grill. Tip the beans into a bowl and stir in the remaining chilli, the garlic, cumin and coriander. Beat in the eggs, then add the reserved ¼ of the salsa and the remaining fresh coriander with a little salt to taste. Blitz a little using a hand blender or mash some of the beans with a potato masher. Heat a 24cm non-stick pan with the oil. Pour in the bean mixture and leave to cook gently for 5-7 mins until the base is set, then grill for 5 mins. Tip out and cut into 4 wedges. If you''re following our Healthy Diet Plan, serve two wedges with half of the remaining salsa and the rocket. Chill the rest for the following day.','10 mins','20 mins'),
(79,'coriander roast chicken thighs with puy lentil salad','https://www.bbcgoodfood.com/recipes/coriander-roast-chicken-thighs-with-puy-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Coriander-roast-chicken-thighs-995f4ec.jpg','Rustle up this coriander chicken with puy lentil salad after a workout – a clever balance of protein and nutrient-dense carbs makes it an ideal post-exercise meal',4,4,1,1,1,'Heat the oven to 220C/200C fan/ gas 7. Boil the lentils for 35-40 mins over a medium heat until tender. Meanwhile, put the ginger, fresh coriander, garam masala, the ground coriander, ground cumin and the 2 whole garlic cloves in a large bowl with half the lemon juice and 3 tbsp of the yogurt. Blitz using a hand blender until smooth. Use 4 tbsp of the mixture to coat the chicken thighs in a large bowl. Arrange the chicken on a baking tray in a single layer. Add the remaining yogurt to the remaining spice and herb mixture, along with the turmeric, 1 tsp oil, the grated garlic, 1 tbsp water and remaining lemon juice to taste. Set aside. Tip the onions, peppers and cauliflower into the bowl used for the chicken, and toss with 1 tbsp oil to coat in some of the spice mix. Spread the veg out on a baking tray, then put in the oven with the chicken for 30-35 mins until the chicken is cooked through. Remove the chicken and wrap in foil to keep it warm. Scatter the cumin seeds over the veg and return to the oven for 5 mins until golden. To serve, drain the lentils and put in a serving bowl with the roasted veg and the remaining turmeric yogurt. Gently toss together. Serve with the chicken (taking the meat off the bones), and scatter with the extra coriander. If you''re following the Healthy Diet Plan, eat half and pack up the rest to eat cold another day. Will keep chilled for up to three days or frozen for up to a month.','20 mins','45 mins'),
(80,'healthy tikka masala','https://www.bbcgoodfood.com/recipes/healthy-tikka-masala','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/healthy-tikka-masala-bc7288a.jpg','Enjoy the spicy, rich flavours of tikka masala with this family-friendly lighter version that''s both healthy and gluten-free. It''s a guaranteed crowd-pleaser',14,4,1,1,1,'Put the onion, garlic and ginger in a food processor and whizz to a smooth paste. Heat 1 tbsp of the oil in a flameproof casserole dish over a medium heat. Add the onion mixture and fry for 15 mins. Tip into a bowl and wipe out the pan. Add the remaining oil and the chicken and fry for 5-7 mins, or until lightly brown. Stir in the tikka spice and cayenne and fry for a further minute. Tip the onion mixture back into the pan, along with the tomatoes and 1 can full of water. Bring to the boil, then reduce to a simmer and cook, uncovered, for 15 mins. Stir in the almonds and spinach and cook for a further 10 mins. Season, then stir though the yogurt and coriander. Serve with brown rice.','10 mins','55 mins'),
(81,'goan-style vegetable curry with kitchari','https://www.bbcgoodfood.com/recipes/goan-style-vegetable-curry-kitchari','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/goan-vegetable-curry-with-kitchery-1-440-400-7e597a6.jpg','Eat all 5 of your 5-a-day in one delicious curry, served with brown rice and lentils. It''s healthy, vegan, gluten-free and provides vitamin C and iron, too.',4,4,1,1,1,'Boil the rice in a pan of water for 25 mins until tender, then drain. Meanwhile, make the curry. Heat the oil in a large pan and fry the onions, chillies and ginger for 8-10 mins until softened. Add the spices and garlic, stir briefly, then pour in the bouillon and stir in the cauliflower. Cover and simmer for 5 mins. Stir in the tamarind with the beans, tomatoes and courgettes, then cover the pan and cook for 10-15 mins more until the veg are tender, but still retain a little bite. Remove the lid for the last 5 mins to reduce the sauce a little. Remove from the heat and stir in the yogurt and half the fresh coriander. Meanwhile, for the kitchari, heat the oil in a non-stick pan and add the ground coriander. Warm briefly, then tip in the rice and drained lentils. Warm through for 1 min, then stir through the remaining fresh coriander. If you''re following our Healthy Diet Plan, serve half the curry and rice now, then chill the rest to eat another night (cool the rice quickly on a wide tray, then chill immediately). Reheat the rice and curry in the microwave or on the hob. You may need to add a drop of water to the rice to stop it sticking.','10 mins','40 mins'),
(82,'vegan lentil stew','https://www.bbcgoodfood.com/recipes/hearty-lentil-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hearty-lentil-one-pot-440-400-20ecfad.jpg','Providing an impressive five of your 5-a-day in each portion, this filling vegan stew is low calorie and low fat, and also provides fibre, vitamin C, iron and calcium',4,4,1,1,1,'Cover the mushrooms in boiling water and leave to soak for 10 mins. Boil the lentils in a pan with plenty of water for 10 mins. Drain and rinse, then tip into a pan with the dried mushrooms and soaking water (don’t add the last bit of the liquid as it can contain some grit), rosemary and 2 litres water. Season, cover and simmer for 20 mins. Meanwhile, heat the oil in a large pan and fry the onions for 5 mins. Stir in the fresh mushrooms and garlic and fry for 5 mins more. Stir in the lentil mixture and bouillon powder, then add the carrots, celery and potatoes. Cover and cook for 20 mins, stirring often, until the veg and lentils are tender, topping up the water level if needed. Remove any tough stalks from the cavolo nero, then add to the pan and cover and cook for 5 mins more. If you''re following our Healthy Diet Plan, serve half in bowls, then chill the rest to eat another day. Will keep in the fridge for two to three days. Reheat in a pan until hot.','10 mins','1 hr'),
(83,'whole roast chicken with braised roots & peas','https://www.bbcgoodfood.com/recipes/whole-roast-chicken-with-braised-roots-peas','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Whole-roast-chicken-d28d2dc.jpg','Layer swede, potato and leeks to roast alongside a chicken for a healthy dinner. A whole chicken is budget-friendly as it can provide leftovers for the week',4,4,1,1,1,'Heat the oven to 200C/180C fan/gas 6. Mix the potatoes, leeks, thyme, garlic and swede in a deep, non-stick roasting tin. Shake into an even layer. Pour in the stock and brush the veg with the oil. Roast for 20 mins. Put the thyme stalks inside the chicken cavity. After 20 mins, sit the chicken on top of the veg and roast for 1 hr until cooked through. After 30 mins, check the potatoes – add 150ml water if they start to catch. Remove the chicken to a board to rest for 10 mins. Meanwhile, cook half the peas following pack instructions. Carve off the chicken legs and remove their skin, then serve with half the roast veg and the peas. Leave the remaining chicken and veg to cool, then keep chilled for up to three days. To prepare the leftovers, remove the skin from one of the breasts, and reheat it with the veg in the microwave until piping hot. Cook the remaining peas following pack instructions. Slice the breast and serve with the veg. Use the final breast for these spiced chicken egg wraps.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','50 mins'),
(84,'broccoli pesto & pancetta pasta','https://www.bbcgoodfood.com/recipes/broccoli-pesto-pancetta-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/broccoli-pesto-pancetta-pasta-75e3452.jpg','Serve this healthy bowl of pasta topped with cherry tomatoes for a quick and easy midweek meal. It''s tossed with broccoli pesto, and takes just 25 minutes to make',14,4,1,1,1,'Bring a pan of lightly salted water to the boil. Add the broccoli and boil for 5 mins. Scoop out with a slotted spoon and set aside. Put the pasta in the same pan and cook following pack instructions. Meanwhile, tip the broccoli into a food processor with the pine nuts, basil, garlic, parmesan and oil, and blitz until smooth. Season with black pepper and a little salt (the pancetta is very salty). Set a frying pan over a medium heat and cook the pancetta for 2 mins. Add the tomatoes and cook for 3 mins, or until softened. Toss the pasta with the broccoli pesto, tomatoes and pancetta, and loosen with a splash of pasta water, if needed. Spoon into bowls and serve.','10 mins','15 mins'),
(85,'sweetcorn fritters with eggs & black bean salsa','https://www.bbcgoodfood.com/recipes/sweetcorn-fritters-eggs-black-bean-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweetcorn-fritters-with-eggs-and-black-bean-salsa-1-440-400-363ffd1.jpg','Get 4 of your 5-a-day in this healthy brunch dish. This recipe makes four servings – save half for another day if you''re following our Healthy Diet Plan',4,4,1,1,1,'Heat the oven to 200C/180C fan/gas 6 and line a large baking tray with baking parchment. Heat the oil in a small pan and fry the onion and pepper for 5 mins until softened. Meanwhile, mix the flour, spices and baking powder in a bowl. Add the onions, pepper, corn and 2 of the eggs, then mix together well. Spoon eight mounds of the mixture onto the baking tray, well spaced apart, then flatten slightly with the back of the spoon. Bake for 20 mins until set and golden. Meanwhile, mix together the salsa ingredients and poach 2 of the remaining eggs to your liking. If you''re following our Healthy Diet Plan, serve four fritters on the day you make them, topped with half the salsa and the poached eggs. Chill the remaining fritters for another day. Reheat them in a pan or microwave and serve with 2 more poached eggs and the remaining salsa.','10 mins','20 mins'),
(86,'healthy chicken pasta bake','https://www.bbcgoodfood.com/recipes/healthy-chicken-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Healthy-chicken-pasta-bake-header-97650e3.jpg','Try this healthy chicken pasta bake with peppers, courgette and ricotta for a nutritious midweek dinner. It’s sure to be a hit with everyone',4,4,1,1,1,'Cook the pasta following pack instructions. Heat the oven to 180C/160C fan/gas 4. Meanwhile, heat the oil in a large pan over a medium heat and fry the peppers for 8 mins, stirring until they start to soften. Add the courgette and garlic, stir for a few minutes more, then tip in the passata and stir in the smoked paprika, bouillon, chilli, if using, and olives. Simmer for 3-4 mins. Drain the pasta, reserving the pasta water. Tip the pasta into the passata mixture along with the chicken, half a mug of the pasta water and the basil. Stir together well, then tip into baking dish. Mix the ricotta in a bowl with 5 tbsp of the pasta water and spread across the top of the pasta. Sprinkle over the cheddar, then bake for 35 mins. Scatter with a few extra basil leaves, if you like, and serve.','10 mins','50 mins'),
(87,'pomegranate chicken with almond couscous','https://www.bbcgoodfood.com/recipes/pomegranate-chicken-almond-couscous','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pomegranate-chicken-24d0745.jpg','Jazz up chicken breasts in this fruity, sweetly spiced sauce with pomegranate seeds, toasted almonds and tagine paste',6,4,1,1,1,'Boil the kettle and heat the oil in a large frying pan. Put the couscous in a bowl with some seasoning and crumble in half the stock cube. Add the onion to the pan and fry for a few mins to soften. Pour boiling water over the couscous to just cover, then cover the bowl with a tea towel and set aside. Push the onion to one side of the pan, add the chicken fillets and brown on all sides. Stir in the tagine paste or harissa and the pomegranate juice, then crumble in the rest of the stock cube and season well. Simmer, uncovered, for 10 mins until the sauce has thickened and the chicken is cooked through. Stir through the pomegranate seeds, saving a few to scatter over before serving. After 5 mins, fluff up the couscous with a fork and stir through the almonds and mint. Serve the chicken on the couscous with the sauce spooned over.','5 mins','15 mins'),
(88,'healthy pasta primavera','https://www.bbcgoodfood.com/recipes/healthy-pasta-primavera','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/healthy-pasta-primaver-35cbc26.jpg','A healthy spaghetti dish full of broad beans, leeks and asparagus tips. Make the most of spring greens with this vibrant, filling pasta recipe',5,4,1,1,1,'Bring a pan of salted water to the boil and put a steamer (or colander) over the water. Steam the beans, asparagus and peas until just tender, then set aside. Boil the pasta following pack instructions. Meanwhile, fry the leeks gently in the oil and butter for 5 mins or until soft. Add the fromage frais to the leeks and very gently warm through, stirring constantly to ensure it doesn’t split. Add the herbs and steamed vegetables with a splash of pasta water to loosen. Drain the pasta and stir into the sauce. Adjust the seasoning, then serve scattered with the cheese and drizzled with a little extra olive oil.','10 mins','20 mins'),
(89,'ricotta, tomato & spinach frittata','https://www.bbcgoodfood.com/recipes/ricotta-tomato-spinach-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-589465_11-e8b46c3.jpg','Healthy veggie bites that are packed with flavour - a midweek must',5,4,1,1,1,'Heat oven to 200C/180C fan/gas 6. Heat oil in a large non-stick frying pan and cook the onion for 5-6 mins until softened and lightly golden. Add the tomatoes and toss for 1 min to soften. Remove from the heat, add the spinach leaves and basil, and toss together to wilt a little. Transfer all the ingredients to a greased 30cm x 20cm rectangular baking tin. Take small scoops of the ricotta and dot over the vegetables. Season the eggs and beat well, then pour over the vegetables and cheese. Cook in the oven for 20-25 mins until pale golden and set. Serve with salad.','10 mins','35 mins'),
(90,'curried cod','https://www.bbcgoodfood.com/recipes/curried-cod','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/curried-cod-d8ee619.jpg','An easy-to-prepare midweek one-pot with cod fillet, chickpeas, ginger and spices - it''s healthy, low calorie and packed with iron too',24,4,1,1,1,'Heat the oil in a large, lidded frying pan. Cook the onion over a high heat for a few mins, then stir in the curry powder, ginger and garlic. Cook for another 1-2 mins until fragrant, then stir in the tomatoes, chickpeas and some seasoning. Cook for 8-10 mins until thickened slightly, then top with the cod. Cover and cook for another 5-10 mins until the fish is cooked through. Scatter over the lemon zest and coriander, then serve with the lemon wedges to squeeze over.','10 mins','25 mins'),
(91,'chicken & chorizo jambalaya','https://www.bbcgoodfood.com/recipes/chicken-chorizo-jambalaya','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1274503_8-05ae02b.jpg','A healthy Cajun-inspired rice pot recipe that''s bursting with spicy Spanish sausage, sweet peppers and tomatoes',5,4,1,1,1,'Heat 1 tbsp olive oil in a large frying pan with a lid and brown 2 chopped chicken breasts for 5-8 mins until golden. Remove and set aside. Tip in the 1 diced onion and cook for 3-4 mins until soft. Add 1 thinly sliced red pepper, 2 crushed garlic cloves, 75g sliced chorizo and 1 tbsp Cajun seasoning, and cook for 5 mins more. Stir the chicken back in with 250g long grain rice, add the 400g can of tomatoes and 350ml chicken stock. Cover and simmer for 20-25 mins until the rice is tender.','10 mins','45 mins'),
(92,'healthy chilli con carne','https://www.bbcgoodfood.com/recipes/chunky-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-f125174.jpg','Use chunks of stewing beef in this healthy chilli con carne for a robust and filling dish. Serve with rice and coriander',24,4,1,1,1,'Heat the oil in a large pan and cook the beef pieces for a few mins on each side until browned all over. Remove from the pan with a slotted spoon and set aside. Add the onion to the pan, with extra oil if needed, and cook until softened. Stir in the garlic, cumin and chipotle paste, and cook for 1 min. Sieve the kidney beans, reserving the sauce. Add this sauce, along with the chopped tomatoes and a can full of water, to the pan. Stir well, then return the meat to the pan. Bring to a simmer, then cook, covered, for 2 hrs or until the beef is tender (or bake in the oven for 3 hrs at 160C/140C fan/gas 3). Add the reserved kidney beans and lime zest, season and warm through. Serve with a scattering of coriander leaves, the lime wedges to squeeze over, and rice, if you like.','5 mins','2 hrs and 15 mins'),
(93,'smashed chicken with corn slaw','https://www.bbcgoodfood.com/recipes/smashed-chicken-corn-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-smashed-chicken-with-corn-slaw-2c3579a.jpg','Add a pop of colour to your plate with this vibrant turmeric marinade. This quick dinner is healthy, low-calorie and provides 3 of your 5-a-day',4,4,1,1,1,'Cut the chicken breasts in half, then put them between two sheets of baking parchment and bash with a rolling pin to flatten. Mix the lime zest and juice with the yogurt, thyme, turmeric, coriander and garlic in a large bowl. Add the chicken and stir until well coated. Leave to marinate while you make the slaw. Mash the avocado with the lime juice and zest, 2 tbsp yogurt and the coriander. Stir in the corn, red pepper, onion and cabbage. Heat a large non-stick frying pan or griddle pan, then cook the chicken in batches for a few mins each side – they’ll cook quickly as they’re thin. Serve the hot chicken with the slaw and the new potatoes. If you''re cooking for two, chill half the chicken and slaw for lunch another day (eat within two days).','10 mins','5 mins'),
(125,'air fryer chicken thighs','https://www.bbcgoodfood.com/recipes/air-fryer-chicken-thighs','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Air-Fryer-Chicken-Thighs-d4575b2.jpg','Cooking chicken thighs in an air fryer is a speedy way to achieve succulent meat with a delicious crispy skin. A spicy coating makes the perfect finishing touch',2,4,1,1,4,'Combine the paprika in a bowl with the herbs and garlic granules, if using, together with ½ tsp salt and ½ tsp ground black pepper. Scatter onto a plate. Rub the chicken thighs with the oil, then coat in the spice mix. Put in the basket of your air fryer and cook, skin-side down, for 10 mins at 180C. Turn over and cook for a further 10-15 mins until cooked through and the skin is crispy. To check they are cooked, pierce the thickest part of the thigh with a knife to see if the juices run clear. Remove from the air fryer immediately to stop the skin from softening.','2 mins','25 mins'),
(94,'paneer chilli con carne','https://www.bbcgoodfood.com/recipes/chilli-paneer','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Chilli-paneer-2c44359.jpg','Try this twist on chilli con carne, which combines beans and warming spices with Indian cheese. It''s also served on quinoa instead of rice for extra protein',4,4,1,1,1,'Toss the paneer with 1/2 tsp of the paprika. Heat the oil in a large non-stick pan over a medium heat, then fry the paneer, turning until golden. Remove from the pan and set aside on a plate, then add the peppers, garlic and celery, remaining cumin, paprika and chilli flakes, and briefly stir over the heat. Tip in the tomatoes and beans along with their juice, the bouillon powder and oregano. Use a wooden spoon to break up the tomatoes if needed, then leave to simmer, uncovered, for 20 mins until the vegetables are tender, stirring occasionally. Meanwhile, cook the quinoa following pack instructions, drain and set aside for 10 mins. Stir the coriander and paneer into the beans, and sprinkle over extra coriander just before serving. Serve half the chilli with half the quinoa. The remainder will keep chilled for up to three days. Reheat in the microwave or on the hob, covered, over a medium heat with a drop of water to loosen until piping hot.','10 mins','25 mins'),
(95,'moroccan harira','https://www.bbcgoodfood.com/recipes/moroccan-haira','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/moroccan-harira-e029bd0.jpg','This is a healthy vegetarian version of the classic Moroccan soup with plenty of cumin, turmeric and cinnamon, each offering different health benefits, plus it''s low in fat and calories too',5,4,1,1,1,'Heat the oil in a large non-stick sauté pan over a medium heat and fry the onions and garlic until starting to soften. Tip in the spices and chilli, stir briefly, then pour in the passata and stock. Add the lentils, carrots, sweet potato and celery, and bring to the boil. Cover the pan and leave to simmer for 30 mins, then cook uncovered for a further 5-10 mins until the vegetables and lentils are tender. Stir in the chopped coriander and serve in bowls with lemon wedges for squeezing over, and the reserved coriander sprinkled over.','15 mins','40 mins'),
(96,'cumin-spiced halloumi with corn & tomato slaw','https://www.bbcgoodfood.com/recipes/cumin-spiced-halloumi-corn-tomato-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-cumin-spiced-halloumi-with-corn-and-tomato-slaw-d7170ef.jpg','Tuck into a super-healthy veggie dinner that''s packed with flavour and freshness. We''ve topped a creamy, zesty coleslaw with pan-fried golden halloumi slices',4,4,1,1,1,'Mix the lime zest and juice with the oil, thyme, turmeric, cumin, coriander and garlic together in a bowl. Add the halloumi and carefully turn it until coated – take care as it breaks easily. To make the slaw, mix the lime juice and zest, yogurt, coriander and chilli together, then stir in the corn, red pepper, beans, tomatoes, onion and cabbage. Heat a large non-stick frying pan or griddle pan and fry the cheese in batches for 1 min each side. Serve the slaw on plates with the halloumi slices on top. If you''re cooking for two people, serve half of the halloumi and slaw and chill the rest for lunch another day.','10 mins','5 mins'),
(97,'lemony chicken stew with giant couscous','https://www.bbcgoodfood.com/recipes/lemony-chicken-stew-giant-couscous','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lemony-chicken-stew-with-giant-couscous-e50fd3b.jpg','This healthy Moroccan-style one-pot can be slow-cooked and dished up at different times - ideal for busy households',6,4,1,1,1,'Heat the oil in a large flameproof casserole dish with a lid. Add the onions and cook for 10 mins until starting to caramelise. Push the onions to one side of the dish and add the chicken. Cook over a high heat for 5 mins or so until the chicken is browning. Add the tagine paste, tomatoes, oregano, preserved lemons and honey, and crumble in the stock cube. Fill one of the tomato cans halfway with water and pour this into the dish. Season with a little salt and plenty of black pepper. Give everything a good stir, then cover with a lid and simmer for 40 mins, on a gentle bubble, or for up to 4 hrs over a very low heat if you’re eating at different times. Add the couscous 10 mins before you’re ready to serve, cover and simmer for 10 mins or until cooked. If you’re eating at different times, scoop your portion into a pan, add 50g couscous and cook in the same way. Stir in some parsley just before serving.','25 mins','1 hr'),
(126,'prawn & coconut soup','https://www.bbcgoodfood.com/recipes/cheats-laksa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheats-laksa-a7487d8.jpg','A super quick version of this flavoursome Thai green curry with just 4 ingredients. Our simple prawn and coconut soup is ideal for a midweek meal',16,2,1,1,4,'Heat 1 tsp flavourless oil in a frying pan over a medium heat. Add the curry paste and cook for 1 min. Pour in the coconut milk, then leave to bubble away for a few mins before adding the prawns and courgetti. Cook for 1 min more to warm through, then divide between bowls.','2 mins','N/A'),
(98,'sesame & ginger sushi bowls','https://www.bbcgoodfood.com/recipes/sesame-ginger-sushi-bowls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sesame-and-ginger-sushi-bowls-440-400-ed852a0.jpg','This vegan packed lunch is full of goodness, using fibre-rich wholegrain rice and protein-rich edamame beans with a tangy tamari and sesame dressing',4,4,1,1,1,'Cut the ginger as thinly as you can to make wafer thin slices or finely shred. Put in a bowl with the 4 tbsp vinegar and 1 tbsp water and massage briefly to soften. Set aside. Put the rice in a medium pan and add 600ml water. Bring to the boil, cover and simmer on a low heat for 15 mins. Tip in the edamame beans, but don’t stir them in, then cover and cook for 7 mins more. The rice should be tender and have absorbed all the water. Skim the beans off the rice and set aside. Tip the 1 tbsp of vinegar and ½ tbsp tamari into the rice, and stir well. Spoon onto a plate and spread out to cool quickly. Once cool, spoon into four bowls or lunchboxes. Top with the avocado, carrot, beans, tomatoes and cucumber. Spoon the ginger from the vinegar and scatter on top. Add the remaining tamari and sesame oil to the vinegar. Spoon over the sushi bowl and scatter with the seeds. Will keep in the fridge for two days.','10 mins','22 mins'),
(99,'spicy fish stew','https://www.bbcgoodfood.com/recipes/spicy-fish-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jamaican-fish-stew-440-400-9ba6282.jpg','Full of omega-3 fats and providing four of your 5-a-day, this healthy dinner is packed with flavourful ingredients including garlic, chilli, salmon fillets and herbs',4,4,1,1,1,'Heat the oil in a large non-stick pan and fry the onions for 8-10 mins until softened and golden. Add the spring onions, garlic, chilli and thyme. Cook, stirring, for 1 min. Pour in the tomatoes and bouillon, then stir in the peppers. Cover and leave to simmer for 15 mins. Meanwhile, cook the rice according to pack instructions. Stir in the beans with the coriander and parsley, then leave to cook gently for another 10 mins until the peppers are tender. Add the salmon and lime zest and cook for 4-5 mins until cooked through. Ladle into bowls and scatter with the coriander sprigs.','10 mins','40 mins'),
(100,'next level chilli con carne','https://www.bbcgoodfood.com/recipes/next-level-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/the-ultimate-chilli-edb1c8c.jpg','Reinvent this classic comfort food with our one-pan version that is enrichened with peanut butter, espresso powder and dark chocolate. You won''t look back',7,8,2,1,4,'Heat oven to 140C/120C fan/gas 1. Over a high heat, toast the whole chillies on all sides until you can smell them cooking, then remove and set aside. Keep the pan on the heat and toast the peppercorns, cumin seeds and coriander seeds until they just start to change colour, then grind to a powder using a pestle and mortar or spice grinder. Mix with the smoked paprika and oregano (this is a standard tex-mex seasoning), then set aside. Return the casserole to the heat, add half the oil and heat until shimmering. Fry the beef in batches, adding more oil if you need to, until it’s browned on all sides, then set aside. Fry the onions in the pan over a low heat for about 8 mins until soft and golden, then add the garlic and cook for 1 min more. Working fast, add the meat and juices, the spice mix, tomato purée, peanut butter and coffee to the pan and cook for 2-3 mins, stirring to coat the meat until everything is thick and gloopy, then pour in the vinegar and stock. Add the toasted chillies back into the casserole, along with the bay leaves, cinnamon and some salt. Bring to a simmer and stir well, then cover with the lid and cook in the oven for 3hrs, stirring occasionally, until the meat is very tender but not falling apart. Take the casserole out of the oven, put back on the stove and remove the lid. Simmer the sauce for 5 mins, then stir in the semolina flour and simmer for 2-3 mins more. Finely grate over the chocolate, stir through with the beans and simmer for a minute more to heat through. Fish out the chillies, then leave everything to rest for at least 15 mins. Bring the pan to the table. Serve with bowls of accompaniments and the chilli paste (see tip below) to add heat.','25 mins','3 hrs'),
(101,'chilli con carne recipe','https://www.bbcgoodfood.com/recipes/chilli-con-carne-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1001451_6-c8d72b8.jpg','This great chilli recipe has to be one of the best dishes to serve to friends for a casual get-together. An easy sharing favourite that uses up storecupboard ingredients.',5,4,1,1,4,'Prepare your vegetables. Chop 1 large onion into small dice, about 5mm square. The easiest way to do this is to cut the onion in half from root to tip, peel it and slice each half into thick matchsticks lengthways, not quite cutting all the way to the root end so they are still held together. Slice across the matchsticks into neat dice. Cut 1 red pepper in half lengthways, remove stalk and wash the seeds away, then chop. Peel and finely chop 2 garlic cloves. Start cooking. Put your pan on the hob over a medium heat. Add 1 tbsp oil and leave it for 1-2 minutes until hot (a little longer for an electric hob). Add the onion and cook, stirring fairly frequently, for about 5 minutes, or until the onion is soft, squidgy and slightly translucent. Tip in the garlic, red pepper, 1 heaped tsp hot chilli powder or 1 level tbsp mild chilli powder, 1 tsp paprika and 1 tsp ground cumin. Give it a good stir, then leave it to cook for another 5 minutes, stirring occasionally. Brown 500g lean minced beef. Turn the heat up a bit, add the meat to the pan and break it up with your spoon or spatula. The mix should sizzle a bit when you add the mince. Keep stirring and prodding for at least 5 minutes, until all the mince is in uniform, mince-sized lumps and there are no more pink bits. Make sure you keep the heat hot enough for the meat to fry and become brown, rather than just stew. Make the sauce. Crumble 1 beef stock cube into 300ml hot water. Pour this into the pan with the mince mixture. Add a 400g can of chopped tomatoes. Tip in ½ tsp dried marjoram, 1 tsp sugar and add a good shake of salt and pepper. Squirt in about 2 tbsp tomato purée and stir the sauce well. Simmer it gently. Bring the whole thing to the boil, give it a good stir and put a lid on the pan. Turn down the heat until it is gently bubbling and leave it for 20 minutes. Check on the pan occasionally to stir it and make sure the sauce doesn’t catch on the bottom of the pan or isn’t drying out. If it is, add a couple of tablespoons of water and make sure that the heat really is low enough. After simmering gently, the saucy mince mixture should look thick, moist and juicy. Drain and rinse a 410g can of red kidney beans in a sieve and stir them into the chilli pot. Bring to the boil again, and gently bubble without the lid for another 10 minutes, adding a little more water if it looks too dry. Taste a bit of the chilli and season. It will probably take a lot more seasoning than you think. Now replace the lid, turn off the heat and leave your chilli to stand for 10 minutes before serving. This is really important as it allows the flavours to mingle. Serve with soured cream and plain boiled long grain rice.','10 mins','1 hr'),
(102,'smoky chilli con carne','https://www.bbcgoodfood.com/recipes/smoky-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Beef-chilli-con-carne-47ba85d.jpg','Add chipotle and brown the mince first for an out of this world chilli con carne. With bags of flavour, this will soon become your go-to chilli recipe...',7,4,1,1,4,'Heat the oil in a casserole or shallow saucepan over a medium heat, then fry the beef for about 10 mins until any fat has been released and the beef has started to brown. Add the onion and fry for another 8-10 mins until the onions have softened and started to brown slightly. Add the garlic and stir for another minute. Stir in the cumin seeds, ground coriander, oregano and a good grinding of black pepper, and continue to fry the mixture for a few minutes until fragrant. Add the vinegar, then the chipotle and tomato pastes, and stir everything together. Tip in the tomatoes and a can of water, then add the yeast extract and crumble in the stock cube. Stir well and bring the mixture to a simmer. Cook for 20 mins, stirring occasionally. When the chilli has thickened, tip in the beans and half the liquid from the can. Simmer for 20 mins more, taste for seasoning, then serve with rice, and top with the soured cream, jalapeños, coriander leaves and onion.','10 mins','1 hr'),
(244,'breakfast super-shake','https://www.bbcgoodfood.com/recipes/breakfast-super-shake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/breakfast-super-shake-e63774c.jpg','This smoothie is high in natural fats and sugar - ideal if you need some fuel for intense exercise',16,1,1,1,2,'Put the ingredients in a blender and blitz until smooth. Pour into a glass and enjoy!','5 mins','No Time'),
(103,'easy chilli con carne','https://www.bbcgoodfood.com/recipes/saras-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-con-carne-4744c1d.jpg','Ideal for entertaining families with kids, this easy homemade classic chilli dish is quick, hassle-free and great to have in the freezer',4,4,1,1,4,'Heat oven to 150C/fan 130C/gas 3. Heat the oil, preferably in a large flameproof casserole, and fry the onions for 8 mins. Add the garlic, spices and oregano and cook for 1 min, then gradually add the mince, stirring well until browned. Stir in the tomatoes, add half a can of water, then crumble in the stock and season. Cover and cook in the oven for 30 mins. Stir in the peppers and sundried tomatoes, then cook for 30 mins more until the peppers are tender. Stir in the beans. To serve, reheat on the hob until bubbling. Serve with avocado or a big salad with avocado in it, some basmati rice or tortilla chips and a bowl of soured cream. If you want to use a slow cooker, fry your onions in a pan for 8 mins, then add your garlic, spices and oregano and cook for a minute. Gradually add the mince until it''s brown. Tip into your slow cooker with the tomatoes, peppers, sundried tomatoes and beans, crumble in the stock cubes and season to taste. Cook on Low for 8-10 hours, then serve as above.','20 mins','1 hr'),
(104,'quick chilli','https://www.bbcgoodfood.com/recipes/quick-bean-chorizo-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513575_11-23525bd.jpg','This fast and filling chilli is great served with rice or piled high onto a baked potato and dolloped with soured cream',5,2,1,1,4,'Dry-fry the chorizo for a few mins in a non-stick frying pan until crisp. Carefully pour off any fat from the pan, then tip in the sauce and beans with 100ml water. Bring to a simmer, cover, then lower the heat and bubble for 10 mins. Spoon over rice and serve with yogurt.','5 mins','15 mins'),
(105,'chilli marrakech','https://www.bbcgoodfood.com/recipes/chilli-marrakech','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1222541_10-1e3bd0c.jpg','If you love spicy, aromatic dishes like tagine, this one-pot will become an instant favourite - it uses lean lamb mince but beef also works',4,10,1,1,4,'Heat your largest non-stick wok or pan, tip in the cumin seeds and toast for a few secs. Remove. Add the oil to the pan and fry the onions for 5 mins until starting to colour. Add the mince, ginger and garlic, and cook, breaking up the mince with your wooden spoon, until no longer pink. Drain any excess liquid or fat from the pan. Stir in the tomatoes, toasted cumin, remaining spices and harissa – add more spice if you like an extra kick. Add the peppers, chickpeas, three-quarters of the chopped coriander and the stock. Cover and cook for 40 mins, stirring occasionally, until the sauce is slightly thickened. Remove from the heat. Cool, then stir in the remaining chopped coriander. Can be served or frozen at this point. Pack into freezer bags and smooth the mince through the bag to flatten. Use within 3 months. To serve, remove from the bags and heat from frozen in a pan on the hob with a little water until bubbling hot, then scatter with coriander.','30 mins','No Time'),
(106,'john''s chilli con carne','https://www.bbcgoodfood.com/recipes/johns-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/johns-chilli-con-carne-202804e.jpg','A warm, comforting chilli is the ultimate family meal. This dish is great for all seasons and has a mix of sweet (cascabel) and smoky (ancho) chillis to give it depth',25,6,2,1,4,'Heat oven to 160C/140C fan/ gas 3. Heat a large cast-iron pot or saucepan over a high flame, pour in the oil, then drop in the onions, chillies, paprika and thyme. Cook for 5 mins until the onions soften. Add the celery, give it a stir and crumble in the stock cube. Stir again and cook for another 5 mins Add the beef, stir well, then colour for a few mins to coat the meat with the spices. Add the flour and coat everything with a more few stirs, then add the remaining ingredients, along with 1 tsp salt. Let it come to the boil, then put a lid on, place in the oven, reduce the temperature to 140C/120C fan/ gas 1 and cook for 4 1 /2 hrs. Take the pot out of the oven and remove the carrots, chop them into small chunks, then put them back in. I like plenty of sauce, but you may want to remove some if you don’t want it too saucy – be sure to keep it, it’s great on toast when cold. Remember to remove the chillies before you serve. Finally, scatter with coriander, if you like','30 mins','4 hrs and 30 mins - 5 hrs'),
(107,'ultimate chilli con carne','https://www.bbcgoodfood.com/recipes/lindseys-ultimate-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lindseys-ultimate-chilli-con-carne-327f8e1.jpg','For a more authentic chilli with a depth of flavour, use diced belly pork, beef steak and pancetta and slowly simmer',26,6,1,1,4,'The day before, cut all the meat into small chunks – this will take at least 30 mins. In a spacious, heavy-based pan, fry the pancetta in the oil over a medium heat until it is crisp and the fat has melted. Stir the onions and garlic into the crisping pancetta and cook, stirring often, for 10-15 mins until soft. Cut the lemon into quarters lengthways, then remove the pithy core and seeds. Chop into small pieces (each with a bit of peel). Remove the pan from the heat and scoop the contents into a sieve, letting any fat drain back into the pan – and give some encouragement with a wooden spoon. Return the pan to the heat and brown the pork in the hot fat, then stir in the beef and brown that too. Add the chilli powder and cumin, and cook over a low heat for 2 mins, stirring constantly. Add the flour and cook for a further 2 mins. Add the bay leaves, 1/2 tsp salt, the lemon, oregano, tomato purée, red wine and stock. Return the onions to the pan. Bring to a simmer, stirring often, then reduce the heat and cook, uncovered, and stirring occasionally to avoid sticking, for 2 hrs until the sauce is thick and the liquid has reduced. Add salt to taste. Cool, cover and chill overnight. Can be frozen at this stage for up to 1 month – defrost overnight in the fridge before continuing with the recipe. Add the beans to the chilli and reheat. Serve with rice or couscous, a dollop of soured cream, slices of avocado tossed in lime juice, a splash of chilli sauce, sprinkling of coriander, and lime wedges, if you like.','1 hr and 15 mins','2 hrs'),
(108,'chilli con carne jackets','https://www.bbcgoodfood.com/recipes/chilli-con-carne-jackets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-con-carne-jackets-872228d.jpg','Turn your baked potato into a satisfying midweek meal with this simple-to-make pork and chipotle chilli filling',1,4,1,1,4,'Heat oven to 200C/180C fan/gas 6 and prick the potatoes all over with a fork. Use 1 tbsp of the oil to rub over the potatoes and place on a baking sheet in the oven for about 1 hr until cooked through. Meanwhile, make the chilli. Put the remaining oil in a deep frying pan over a medium heat. Add the onion and cook for about 10 mins until softened. Add the mince, breaking up with a wooden spoon and stirring until turning pale. Stir through the chipotle paste, cook for 1 min, then tip in the tomatoes, crumble over the stock cube and season well. Cover and simmer over a gentle heat with the lid on for about 40 mins, stirring occasionally. Remove the lid from the chilli and cook for 10 mins more. Once the potatoes are cooked, halve them and top with the chilli, a dollop of soured cream and a sprinkling of chives. Serve with mixed leaves, if you like.','10 mins','1 hr'),
(109,'burnt aubergine veggie chilli','https://www.bbcgoodfood.com/recipes/burnt-aubergine-veggie-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/burnt-aubergine-chilli-afd2f29.jpg','This warming aubergine chilli is low fat and four of your five-a-day. Serve up this smoky spiced vegetarian supper with brown rice and all your favourite trimmings',27,4,1,1,4,'If you have a gas hob, put the aubergine directly onto a lit ring to char completely, turning occasionally with kitchen tongs, until burnt all over. Alternatively, use a barbecue or heat the grill to its highest setting and cook, turning occasionally, until completely blackened (the grill won’t give you the same smoky flavour). Set aside to cool on a plate, then peel off the charred skin and remove the stem. Roughly chop the flesh and set aside. In a large pan, heat the oil, add the onion and carrots with a pinch of salt, and fry over a low-medium heat for 15-20 mins until the carrots have softened. Add the aubergine, both types of lentils, the kidney beans with the liquid from the can, soy sauce, tomatoes, chocolate, chilli powder, oregano and the spices. Stir to combine, then pour in the stock. Bring to the boil, then turn down the heat to very low. Cover with a lid and cook for 1½ hrs, checking and stirring every 15-20 mins to prevent it from burning. Remove the lid and let the mixture simmer over a low-medium heat, stirring occasionally, for about 15 mins until you get a thick sauce. Stir in the lime juice and taste for seasoning – add more salt if needed. Serve hot over rice with whichever accompaniments you want!','25 mins','2 hrs'),
(110,'feed-a-crowd chunky beef chilli','https://www.bbcgoodfood.com/recipes/feed-crowd-chunky-beef-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/feed-a-crowd-chunky-beef-chilli-b09babe.jpg','The ultimate make-ahead dish, this chilli con carne can be frozen for up to 2 months - simply add more chillies to turn up the heat',24,8,1,1,4,'Drain the chillies and discard the seeds and stalks. Roughly chop and put in the small bowl of a food processor with the garlic, 2 tbsp of the oil, the red wine vinegar, cumin and oregano. Whizz to a paste. Put the meat in a bowl or resealable food bag, pour on the paste and rub all over the meat. Leave to marinate overnight (or for at least 4 hrs). Heat oven to 160C/140C fan/gas 3. Heat the remaining oil in a large, lidded flameproof casserole dish and cook the onions for a few mins until beginning to soften. Add the meat and all the paste to the dish and cook for about 5 mins, turning the meat until browned all over. Pour in the tomatoes and add the cinnamon. Stir everything together, then add 100ml water and bring to the boil. Put on the lid, transfer to the oven and cook for 1 hr 45 mins. Remove the lid and stir in the red peppers, chocolate, sugar and all the beans. Return to the oven for another 20 mins. To serve, stir in the lime zest and juice, season to taste, and scatter over the coriander.','30 mins','2 hrs and 30 mins'),
(111,'beef & bean chilli bowl with chipotle yogurt','https://www.bbcgoodfood.com/recipes/beef-bean-chilli-bowl-chipotle-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beans_2-0b12c0c.jpg','Chilli con carne is padded out with healthy mixed beans flavoured with smoky chilli paste',28,4,1,1,4,'Heat the oil in a frying pan and cook the onion on a medium heat until softened. Add the beef with some black pepper and a little salt, breaking up any lumps with a wooden spoon, and cook until browned. Add the chipotle and Cajun seasoning. Give it a good stir and cook for 1 min more. Tip in the beans and tomatoes, then crumble over the stock cube. Add a can of water and simmer for about 20-30 mins, until thickened. Add the chocolate, and stir until melted, then add most of the coriander. Serve the chilli in bowls on top of the rice sprinkled with the rest of the coriander, with a dollop of low-fat yogurt, and an extra drizzle of chipotle paste on top.','5 mins','45 mins'),
(112,'vegetable & bean chilli','https://www.bbcgoodfood.com/recipes/vegetable-bean-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-801605_11-69251ae.jpg','This healthy veggie chilli makes for a quick and satisfying supper, crammed with pulses and colourful fresh vegetables',5,4,1,1,4,'Heat the oil in a large pan. Cook the garlic, ginger, onion, courgettes and peppers for about 5 mins until starting to soften. Add the chilli powder and cook for 1 min more. Stir in the lentils, tomato purée, tomatoes and 250ml water. Bring to the boil and cook for 15-20 mins. Add the sweetcorn and beans, and cook for a further 10 mins.','10 mins','30 mins - 35 mins'),
(253,'kiwi fruit smoothie','https://www.bbcgoodfood.com/recipes/kiwi-fruit-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kiwi-smoothie-3086931.jpg','This simple kiwi, mango and pineapple fruit smoothie makes an ideal morning treat. It''s an easy way to pack in the vitamins and give yourself a boost',29,2,1,1,2,'Put all of the ingredients in a blender and blitz until smooth then pour into 2 tall glasses.','5 mins','No Time'),
(113,'chilli beef with black beans and avocado salad','https://www.bbcgoodfood.com/recipes/chilli-beef-black-beans-and-avocado-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-beef-ef5ca13.jpg','Bulk out red meat with black beans to keep things a little lighter. This one-pot treat is still packed with plenty of flavour',4,6,1,1,4,'Tip the spices into a large pan and warm for a few seconds to toast them. Add the mince and garlic, and stir, breaking down the beef as it browns. Pour in the tomatoes and a can of water, crumble in the stock cube, then stir in the tomato purée, peppers and coriander stalks. Cover the pan and cook for 15 mins. Stir in the black beans and their juice, and cook for 10 mins more until everything is tender. Meanwhile, heat the rice following pack instructions and toss the salad ingredients together. Stir the coriander leaves into the chilli and serve.','20 mins','35 mins'),
(114,'pork tacos','https://www.bbcgoodfood.com/recipes/smoky-pork-black-bean-tacos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1287459_7-25d6263.jpg','Mexican pork chilli with a smoky barbecued flavour, served in crispy tortilla shells with ripe avocado. These easy pork tacos make a speedy family meal, ready in under 30 mins',6,4,1,1,4,'Heat the oil in a large frying pan, add the onion and cook for 5 mins until softened. Sprinkle over the spices and cook for 1 min more. Add the mince, breaking it up with the back of a wooden spoon, and stir until cooked through. Stir the passata and barbecue sauce into the pan along with 4 tbsp water. Increase the heat and allow the sauce to bubble and reduce until it clings to the meat. Add the beans, season and cook for a further 2 mins, then stir in the coriander. Heat the tacos following pack instructions. Use the pork and bean mix to fill the tacos, top with slices of avocado, shredded iceberg lettuce and a dollop of soured cream, if you like.','10 mins','15 mins'),
(115,'chicken mole with coriander rice','https://www.bbcgoodfood.com/recipes/chicken-mole-coriander-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1252472_7-e4bc21f.jpg','If you''re a fan of chilli con carne, you''ll love this dark, sweet and spicy South American stew with ancho, ground nuts and chocolate',6,6,1,1,4,'Put the chillies in a bowl and add enough boiling water to just cover. Leave to soften for 20 mins. If you can’t find anchos, grill red peppers until they’re really blackened and soft. Cool them, then peel and use as needed in the recipe, adding 1 tsp smoked paprika and a little extra chipotle to re-create the sweet, smoky flavour. Meanwhile, heat the oil in a flameproof casserole dish, season the chicken, then brown on all sides. You may have to do this in batches so you don’t overcrowd the dish. Remove to a plate. Add the onions to the dish and cook for 5 mins until softened. Add the spices and cook for 1 min until aromatic. Remove the chillies from their soaking liquid, reserving the liquid, and discard the stalks and seeds. Put in a food processor with 4 tbsp of the soaking liquid, the garlic and raisins. Whizz to a paste, then tip into the dish. Add the peanut butter, chipotle paste, tomatoes and 400ml water (fill up the tomato can and swirl to get all the tomato bits out). Return the chicken to the dish and season. Cover with a lid and simmer, stirring occasionally, for 1 hr. Remove the chicken pieces to a plate. Using 2 forks, shred the meat and discard the bones. Return the chicken to the sauce, add the chocolate and continue cooking, uncovered, for 30 mins more. If the sauce looks like it’s getting a little too thick, add some of the chilli soaking liquid or some water. Cook the rice following pack instructions. Meanwhile, put the red onion in a small bowl. Add the lime juice and a pinch of salt. Leave to pickle until ready to serve. When the rice is cooked, add the coriander and lime zest and juice, and fluff up with a fork. Remove the mole from the heat, scatter with the pickled red onion and serve alongside the rice, with soured cream and lime wedges, if you like.','30 mins','2 hrs'),
(116,'veggie chilli','https://www.bbcgoodfood.com/recipes/veggie-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-chilli-4a57c04.jpg','The easiest chilli you''ll ever make, with ready-to-eat grains, kidney beans in chilli sauce and summer veggies - it''s 4 of your 5-a-day too!',1,2,1,1,4,'Heat oven to 200C/180C fan/ gas 6. Cook the vegetables in a casserole dish for 15 mins. Tip in the beans and tomatoes, season, and cook for another 10-15 mins until piping hot. Heat the pouch in the microwave on High for 1 min and serve with the chilli.','2 mins','30 mins'),
(117,'mexican bean chilli','https://www.bbcgoodfood.com/recipes/mexican-bean-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1283503_8-0debc5f.jpg','The secret ingredient in this chipotle-spiced, beef chilli con carne is a storecupboard favourite - baked beans',5,4,1,1,4,'Fry the onion and red pepper in olive oil over a medium heat for 10-15 mins or until softened. Increase the heat, add the chilli powder and cook for a few minutes before adding the mince. Cook until browned and all the liquid has evaporated. Tip in the baked beans, beef stock and chipotle paste. Simmer over a low heat for 15-20 mins. Season, scatter with coriander leaves and serve with rice and yogurt.','5 mins','45 mins'),
(118,'mexican beef & potato pie','https://www.bbcgoodfood.com/recipes/mexican-beef-potato-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1308488_3-520bbbf.jpg','A shepherd''s pie with a real twist - top a chilli con carne style filling with cheesy mash and tortilla chips',4,4,1,1,4,'Heat oven to 180C/160C fan/gas 4. Warm the cumin seeds in a large pan and toast for a few secs. Tip the salsa into the pan, then stir in the mince, breaking it up with a wooden spoon. Crumble in the stock cube, tip in the beans with their juice and add a little less than a can of water. Season, then part-cover and simmer for 40 mins, stirring occasionally. If there is still a lot of liquid after 30 mins, remove the lid completely and allow it to reduce a little until thickened. Meanwhile, boil the potatoes until tender, then drain and mash with the butter and seasoning. Spoon the mince into an ovenproof dish and top with the potato, smoothing to cover the meat completely. Mix the cheese with the tortillas, then scatter on top of the pie and bake for 10 mins to melt the cheese – take care that the tortillas don’t burn.','20 mins','1 hr'),
(254,'super berry smoothie','https://www.bbcgoodfood.com/recipes/super-berry-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-901773_11-afec2d8.jpg','Frozen berries are a thrifty way of creating a healthy smoothie - pad it out with oats to make it extra filling',5,4,1,1,2,'Whizz the berries, yogurt and milk together with a stick blender until smooth. Stir through the porridge oats, then pour into 4 glasses and serve with a drizzle of honey, if you like.','10 mins','No Time'),
(119,'quick chilli with creamy chive crushed potatoes','https://www.bbcgoodfood.com/recipes/quick-chilli-creamy-chive-crushed-potatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513567_11-10f5f06.jpg','Make comfort in a pot with this tasty and warming recipe, perfect for rainy evenings',5,4,1,1,4,'Squeeze 3 balls of meat from each sausage, then fry in the oil for 5 mins until golden. While they cook, put the potatoes on to boil for 10 mins until tender. Add the peppers to the pan, then fry for 4 mins more. Tip in the garlic and spices, fry for 1 min, then add the beans, tomatoes and sugar. Simmer for 5 mins until saucy and the meatballs are cooked. Drain the potatoes, then crush with a masher. Fold through the yogurt and chives, loosen with a splash of water, then serve with the chilli. Top with a dollop more yogurt and sprinkling of chives, if you like.','5 mins','15 mins'),
(120,'double bean & roasted pepper chilli','https://www.bbcgoodfood.com/recipes/double-bean-roasted-pepper-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-16e065a.jpg','This warming vegetarian chilli is a low-fat, healthy option that packs in the veggies and flavour. Serve with Tabasco sauce, soured cream or yoghurt.',17,8,1,1,4,'Put the onions, celery and chopped peppers with the oil in your largest flameproof casserole dish or heavy-based saucepan, and fry gently over a low heat until soft but not coloured. Drain both jars of peppers over a bowl to catch the juices. Put a quarter of the peppers into a food processor with the chipotle paste, vinegar, cocoa, dried spices and herbs. Whizz to a purée, then stir into the softened veg and cook for a few mins. Add the tomatoes and refried beans with 1 can water and the reserved pepper juice. Simmer for 1 hr until thickened, smoky and the tomato chunks have broken down to a smoother sauce. At this stage you can cool and chill the sauce if making ahead. Otherwise add the kidney and black beans, and the remaining roasted peppers, cut into bite-sized pieces, then reheat. (This makes a large batch, so once the sauce is ready it might be easier to split it between two pans when you add the beans and peppers.) Once bubbling and the beans are hot, season to taste and serve.','30 mins','1 hr and 15 mins'),
(121,'coconut & squash dhansak','https://www.bbcgoodfood.com/recipes/coconut-squash-dhansak','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/coconut-squash-dhansak-a3a9133.jpg','This quick and easy vegetarian curry is perfect for a healthy weeknight dinner – with butternut squash, coconut milk, lentils and spinach',6,4,1,1,4,'Heat the oil in a large pan. Put the squash in a bowl with a splash of water. Cover with cling film and microwave on High for 10 mins or until tender. Meanwhile, add the onions to the hot oil and cook for a few mins until soft. Add the curry paste, tomatoes and coconut milk, and simmer for 10 mins until thickened to a rich sauce. Warm the naan breads in a low oven or in the toaster. Drain any liquid from the squash, then add to the sauce with the lentils, spinach and some seasoning. Simmer for a further 2-3 mins to wilt the spinach, then stir in the coconut yogurt. Serve with the warm naan and a dollop of extra yogurt.','5 mins','15 mins'),
(122,'easy huevos rancheros','https://www.bbcgoodfood.com/recipes/easy-huevos-rancheros','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-huevos-rancheros-38e94de.jpg','An easy Mexican breakfast that''ll keep you going all morning, this huevos rancheros recipe (aka ''ranch eggs'') is packed with vibrant colours and flavours.',6,1,1,1,4,'Heat the oil in a frying pan over a high heat. Add the tortilla and fry for 1-2 mins on each side until crisping at the edges. Transfer to a plate. Crack the egg into the pan and cook to your liking. Meanwhile, tip the beans into a bowl, season and add a squeeze of lime, then lightly mash with a fork. Spread the beans over the tortilla, top with the egg, avocado, feta and chilli sauce. Squeeze over a little more lime juice just before eating.','3 mins','7 mins'),
(123,'minestrone in minutes','https://www.bbcgoodfood.com/recipes/minestrone-minutes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1202595_11-36e9761.jpg','Whip up a hearty bowl of soup in next to no time, perfect for a quick lunch or warming supper',5,4,1,1,4,'Bring the stock to the boil with the tomatoes, then add the spaghetti and cook for 6 mins or until done. A few minutes before the pasta is ready, add the vegetables and bring back to the boil. Simmer for 2 mins until everything is cooked. Serve in bowls drizzled with pesto and oil, sprinkled with parmesan.','2 mins','8 mins'),
(124,'air fryer salmon','https://www.bbcgoodfood.com/recipes/air-fryer-salmon','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Air-Fryer-Salmon-bc768be.jpg','Salmon fillets cook beautifully in an air fryer and make a delicious meal when coated in a herb seasoning and served with greens and grains',2,4,1,1,4,'Combine the salt, pepper, mixed herbs and garlic granules, if using, in a bowl, then scatter onto a plate. Rub each salmon fillet with a little olive oil and roll in the seasoning to coat. Put in the air fryer basket in one layer and cook at 180C for 8-10 mins, until cooked through. If you have larger salmon fillets they will need to be cooked for longer – keep checking after 10 mins and cook in 1-2 min blasts, until ready. Serve with greens and quinoa or rice, if you like.','5 mins','8 mins - 10 mins'),
(127,'easy teriyaki chicken','https://www.bbcgoodfood.com/recipes/easy-teriyaki-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-teriyaki-c845724.jpg','Try this easy, sticky Asian-style teriyaki chicken for a speedy weeknight supper – it takes just 20 minutes to make! Serve it with sticky rice and steamed greens',14,4,1,1,4,'Heat the oil in a non-stick pan over a medium heat. Add the chicken and fry for 7 mins, or until golden. Add the garlic and ginger and fry for 2 mins. Stir in the honey, soy sauce, vinegar and 100ml water. Bring to the boil and cook for 2 - 5 mins over a medium heat until the chicken is sticky and coated in a thick sauce. Scatter over the spring onions and sesame seeds, then serve the chicken with the rice and steamed veg.','5 mins','15 mins'),
(128,'air fryer sausages','https://www.bbcgoodfood.com/recipes/air-fryer-sausages','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Air-Fryer-Sausages-66ca4d0.jpg','Cook sausages quickly and easily using an air fryer and without the need for additional oil',2,6,1,1,4,'Pierce the sausages a few times all over using a sharp knife (this is optional, but will help release more fat). Arrange the sausages in a single layer in an air fryer basket. Set the air fryer to 180C and cook for 10-15 mins, turning every 5 mins, until the sausages are cooked through. If you have a meat thermometer, check they have reached 75C in the middle. Serve as a side for breakfast or in buns.','2 mins','10 mins - 15 mins'),
(129,'quick & easy tiramisu','https://www.bbcgoodfood.com/recipes/quick-easy-tiramisu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quick-and-easy-tiramisu_0-608834a.jpg','Condensed milk is the secret to this super snappy Italian dessert. Coffee and chocolate are a classic combo, simply layer them up and enjoy',1,2,1,1,4,'Mix the coffee granules with 2 tbsp boiling water in a large jug and stir to combine. Add the coffee liqueur and 75ml cold water. Pour into a shallow dish and set aside. Make the cream layer by beating the mascarpone, condensed milk and vanilla extract with an electric whisk until thick and smooth. Break the sponge fingers into two or three pieces and soak in the coffee mixture for a few secs. Put a few bits of the sponge in the bottom of two wine or sundae glasses and top with the cream. Sift over the cocoa and chill for at least 1 hr before serving.','15 mins','No Time'),
(130,'easy thai prawn curry','https://www.bbcgoodfood.com/recipes/easy-thai-prawn-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338576_12-d97fbf5.jpg','Try our easy Thai prawn curry on a busy weeknight. This quick dinner is ready in under 20 minutes and flavoured with warming red Thai curry paste',5,4,1,1,4,'Heat the oil in a medium saucepan. Tip in the onion and ginger, then cook for a few mins until softened. Stir in the curry paste, then cook for 1 min more. Pour over the chopped tomatoes and coconut cream. Bring to the boil, then leave to simmer for 5 mins, adding a little boiling water if the mixture gets too thick. Tip in the prawns, then cook for 5-10 mins more, depending on how large they are. Serve alongside some plain rice and sprinkle with a little chopped coriander, if you like.','5 mins','15 mins'),
(131,'basil pesto','https://www.bbcgoodfood.com/recipes/easy-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/perfect-pesto-feb1d6d.jpg','Blend together basil, pine nuts or cashews, parmesan, garlic and olive oil to make your own pesto. It''s perfect for pizzas, pasta and sandwiches',3,8,1,1,4,'Toast the nuts in a pan over a low heat. Tip into a mini chopper with the remaining ingredients, or use a large pestle and mortar, then blitz or pound into a rough sauce. Will keep for three days in the fridge.','15 mins','5 mins'),
(132,'spicy kimchi pancake (kimchi jeon)','https://www.bbcgoodfood.com/recipes/spicy-kimchi-pancake-kimchi-jeon','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/KimchiPancakes-e591d25.jpg','Make these Korean-style spicy pancakes made with gut-friendly kimchi (sour cabbage) and gochujang (Korean red pepper paste). They''re packed with flavour',30,2,1,1,4,'Put the flours in a bowl and pour in 200ml ice-cold water and the reserved kimchi liquid. Whisk until a smooth batter is formed. Mix in the kimchi, gochujang and spring onions – the batter should be a slightly thick pancake batter, similar to a drop scone consistency. You can add a tablespoon of flour if you need to thicken it a little. Place a large, non-stick pan over a high heat with 2 tbsp vegetable oil. Ladle the pancake mix into the pan. Use the back of the ladle to smooth the mixture out to create a thin, even layer. Reduce the heat to medium high. Fry for 2-3 mins, until air bubbles start to pop on the surface and the sides start to crisp. Flip the pancake then slightly lift the side of the pancake with a spatula and add another tablespoon of oil underneath – shake the pan a little to make sure the oil coats the bottom of the pancake. Use a spatula to press on top of the pancake to ensure the pancake is crispy. Fry for a further 2 mins. Flip one last time, pressing down on the pancake again and fry for 30 seconds. Drain on kitchen paper and keep warm in a low oven while you cook the second pancake using the same method. Serve (optionally cut into bite-sized pieces).','10 mins','10 mins'),
(240,'strawberry green goddess smoothie','https://www.bbcgoodfood.com/recipes/strawberry-green-goddess-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-strawberry-green-goddess-smoothie-5c7a02a.jpg','Try this tasty breakfast smoothie to start your day, pairing sweet fruit with nutritious spinach and avocado for a dose of calcium, vitamin C, folate and fibre',4,2,1,1,2,'Put all the ingredients in a blender and whizz until completely smooth. If it’s a little thick, add a drop of chilled water then blitz again. Pour into glasses and drink straight away.','5 mins','No Time'),
(133,'tteokbokki (spicy rice cakes)','https://www.bbcgoodfood.com/recipes/tteokbokki-spicy-rice-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/TteokbokkiSpicyRiceCakes-b78e346.jpg','Try these tteokbokki (Korean-style spicy rice cakes) as a snack or side. A popular street food, they make a great addition to a Korean feast',30,4,1,1,4,'If using refrigerated rice cakes, soak in warm water for 15 mins. Place a large frying pan over high heat and pour in the stock (or water). Bring to a boil, then add the gochujang, soy sauce, sugar and corn syrup. Stir and reduce to a medium heat. Tip the rice cakes and fish cakes into the pan of sauce. Simmer for 10 mins until the rice cakes are soft and the sauce has thickened and clings to the rice cakes. Stir in the spring onions and serve immediately.','5 mins','15 mins'),
(134,'quick sausage bolognese','https://www.bbcgoodfood.com/recipes/quick-sausage-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-519470_12-c273e5c.jpg','Pasta, sauce, and sausages, what could be a better combination? The sausage meat adds great flavour to a simple dish',5,4,1,1,4,'Heat a large, wide frying pan, then crumble in the sausage meat and fennel seeds (there’s no need to add any oil). Fry for a few mins until golden and the fat is released, stirring well to break up the meat. Add the mushrooms and fry for a few mins until beginning to soften. Stir in the wine now, if using, bubble for 1 min, then add the tomato sauce and heat through until bubbling. Meanwhile, boil the penne. When ready, drain and tip into the sauce. Mix well until completely coated, then divide between four plates, finishing with a little parmesan.','5 mins','15 mins'),
(135,'easy stuffed peppers','https://www.bbcgoodfood.com/recipes/easy-stuffed-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338663_11-7a36645.jpg','A 15-minute vegetarian supper that''s bursting with Mediterranean flavours and calls for just five ingredients',5,4,1,1,4,'Use a small knife to cut the top out of 4 red peppers, then scoop out the seeds. Sit the peppers on a plate, cut-side up, and cook in the microwave on high for 5-6 mins until they have wilted and softened. While the peppers are cooking, mix two 250g pouches cooked tomato rice together with 2 tbsp pesto and a handful of chopped pitted black olives and 140g of the sliced goat''s cheese. Scoop the rice, pesto, olives and goat''s cheese mix into the peppers, top with the remaining 60g sliced goat’s cheese and continue to cook for 8-10 mins.','5 mins','10 mins'),
(136,'malabar prawns','https://www.bbcgoodfood.com/recipes/malabar-prawns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/malabar-prawns-2b20cf2.jpg','Make one of Kerala’s staple dishes – malabar prawns, a speciality of the South Indian coast. They''re quick and easy to prepare and full of distinct flavours',31,4,1,1,4,'Rinse the prawns in cold water and pat dry. Toss them with the turmeric, chilli powder, lemon juice and grated ginger and set aside. Heat the oil in a pan and add the curry leaves, chilli, sliced ginger and onion. Cook until translucent, about 10 mins, then add the black pepper.  Toss the prawns in with any marinade, and stir-fry until cooked, about 2 mins. Season if required and add a squeeze of lemon juice. Serve sprinkled with the coconut and coriander leaves.','15 mins','12 mins'),
(137,'ravioli with walnuts, goat’s cheese & cavolo nero sauce','https://www.bbcgoodfood.com/recipes/rapid-ravioli-walnuts-goats-cheese-cavolo-nero-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ravioliwithcavolo-recipe-image_1-452d080.jpg','Make this pasta dish using lasagne sheets and a homemade goat''s cheese paste. If you have time, it''s worth using freshly shelled walnuts for a sweeter flavour',32,4,1,1,4,'Bring a large pan of lightly salted water to the boil, then add the cavolo nero. Cook for 1-2 mins until the kale turns bright green and softens. Lift out with tongs into a colander, keeping the cooking water to boil the pasta. Run cold water over the kale, squeeze out any excess water, then transfer to a food processor. Add the oil, garlic, lemon juice and 1 tsp salt, then blitz to a fine pesto. Season to taste and transfer to a small pan. Gently warm over a medium-low heat. Clean the food processor. For the goat’s cheese paste, tip in the nuts, sage, goat’s cheese, a good squeeze of lemon juice and the oil, then blitz to a paste. Season, then warm in another small pan over a medium-low heat, stirring occasionally. Bring the pan of cavolo water to the boil again, adding more water if needed. Add the pasta sheets and simmer following pack instructions. Drain, keeping back a cup of the pasta water to add to the sauce (about 400ml). Cut the pasta sheets in half and toss in a little olive oil. Stir the reserved pasta water through the cavolo sauce and keep on a low heat. If the sauce looks thick, thin down with a little more water, or increase the heat a little if too thin; it should be pourable like double cream. Spoon a pool of sauce onto each plate. Layer the lasagne sheets on top with spoonfuls of the goat’s cheese paste in-between each layer. Top with the last sheet of pasta and ladle over a little more sauce, then scatter over the cheese. Recipe adapted from Home Cook by Thomasina Miers (£25, Guardian Books)','35 mins','10 mins'),
(148,'bacon & mushroom pasta','https://www.bbcgoodfood.com/recipes/bacon-mushroom-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338805_11-7d3f970.jpg','A simple one-pan pasta dish with bacon, mushrooms and pesto - ready in under 30 minutes',33,4,1,1,4,'Cook the pasta in boiling water in a large non-stick saucepan according to pack instructions. Meanwhile, slice the mushrooms and snip the bacon into bite-size pieces with scissors or a sharp knife. Reserve a few drops of the cooking water in a cup or bowl, then drain the pasta and set aside. Fry the bacon and mushrooms in the same pan until golden, about 5 mins. Keep the heat high so the mushrooms fry in the bacon fat, rather than sweat. Tip the pasta and reserved water back into the pan and stir over the heat for 1 min. Take the pan off the heat, spoon in the pesto and crème fraîche and most of the basil and stir to combine. Sprinkle with the remaining basil to serve.','5 mins','20 mins'),
(138,'easy rocky road','https://www.bbcgoodfood.com/recipes/easy-rocky-road','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-rocky-road-13bd3fb.jpg','Great for a bake sale, a gift, or simply an afternoon treat to enjoy with a cuppa, this rocky road is quick to make and uses mainly storecupboard ingredients.',34,12,1,1,4,'Grease and line an 18cm square brownie tin with baking paper. Place 200g digestive biscuits in a freezer bag and bash with a rolling pin or just the side of your fist until they''re broken into a mixture of everything between dust and 50p-sized lumps. Set aside. In a large saucepan melt 135g butter or margarine, 200g dark chocolate and 2-3 tbsp golden syrup over a gentle heat stirring constantly until there are no or almost no more lumps of chocolate visible, then remove from the heat. Leave to cool. Take the biscuits, 100g mini marshmallows and up to 100g of additional ingredients (dried fruit, nuts, popcorn, honeycomb), if you like, and stir into the chocolate mixture until everything is completely covered. Tip the mixture into the lined baking tin, and spread it out to the corners. Chill for at least 2 hrs then dust with icing sugar and cut into 12 fingers.','15 mins','5 mins'),
(139,'quick chicken chasseur','https://www.bbcgoodfood.com/recipes/quick-chicken-chasseur','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513564_10-759d8b1.jpg','A classic in a flash, you can also use beef. Either way - it''s a winner',5,4,1,1,4,'Heat a shallow saucepan and sizzle the bacon for about 2 mins until starting to brown. Throw in the chicken, then fry for 3-4 mins until it has changed colour. Turn up the heat and throw in the mushrooms. Cook for a few mins, stir in the flour, then cook until a paste forms. Tip in the tomatoes, stir, then crumble in the stock cube. Bubble everything for 10 mins, splash in the Worcestershire sauce, stir through the parsley, then serve with mash or boiled rice.','5 mins','20 mins'),
(140,'easy chicken fajitas','https://www.bbcgoodfood.com/recipes/easy-chicken-fajitas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-fajitas-2-d7172f8.jpg','Need a simple, vibrant midweek meal the family will love? Put together these easy chicken fajitas and people can create their own masterpieces at the table',35,4,1,1,4,'Heat oven to 200C/180C fan/gas 6 and wrap 8 medium tortillas in foil. Mix 1 heaped tbsp smoked paprika, 1 tbsp ground coriander, a pinch of ground cumin, 2 crushed garlic cloves, 4 tbsp olive oil, the juice of 1 lime and 4-5 drops Tabasco together in a bowl with a big pinch each of salt and pepper. Stir 2 finely sliced chicken breasts, 1 finely sliced red onion, 1 sliced red pepper and 1 finely sliced red chilli, if using, into the marinade. Heat a griddle pan until smoking hot and add the chicken and marinade to the pan. Keep everything moving over a high heat for about 5 mins using tongs until you get a nice charred effect. If your griddle pan is small you may need to do this in two batches. To check the chicken is cooked, find the thickest part and tear in half – if any part is still raw cook until done. Put the tortillas in the oven to heat up and serve with the cooked chicken, a bag of mixed salad and one 230g tub of fresh salsa.','15 mins','10 mins'),
(141,'easy spaghetti bolognese','https://www.bbcgoodfood.com/recipes/classic-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/classic-bolognese-65a0ab3.jpg','Mix pork and beef mince in this easy spaghetti bolognese that also has a splash of red wine and plenty of garlic for a filling, flavourful family supper',36,4,1,1,4,'Put 1 tbsp of the oil in a large saucepan over a medium-high heat, add the beef and fry until well browned. Tip out into a dish and repeat with 1 tbsp oil and the pork. Tip the pork into the dish with the beef and put the pan back on the heat with the remaining oil. Turn the heat down and cook the shallots for 8-10 mins or until very soft, then add the garlic. Tip the meat back into the pan and add the passata, purée, wine and oregano. Stir everything together, cover and simmer over a low heat, stirring occasionally, for 45 mins. Cook the spaghetti following pack instructions, then stir half the parmesan into the Bolognese. Put a spoonful of the pasta water into the sauce to loosen it if it looks too thick, then drain the spaghetti. For a better flavour, tip the pasta onto the sauce, toss everything together to coat, and season well (or serve with the sauce on top). Add the remaining parmesan and a few basil leaves.','10 mins','1 hr and 10 mins'),
(142,'ultimate spaghetti carbonara recipe','https://www.bbcgoodfood.com/recipes/ultimate-spaghetti-carbonara-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1001491_11-2e0fa5c.jpg','Discover how to make superb spaghetti carbonara. This cheesy pasta dish is an Italian favourite and with the right technique, you can make it perfect every time',37,4,1,1,4,'Put a large saucepan of water on to boil. Finely chop the 100g pancetta, having first removed any rind. Finely grate 50g pecorino cheese and 50g parmesan and mix them together. Beat the 3 large eggs in a medium bowl and season with a little freshly grated black pepper. Set everything aside. Add 1 tsp salt to the boiling water, add 350g spaghetti and when the water comes back to the boil, cook at a constant simmer, covered, for 10 minutes or until al dente (just cooked). Squash 2 peeled plump garlic cloves with the blade of a knife, just to bruise it. While the spaghetti is cooking, fry the pancetta with the garlic. Drop 50g unsalted butter into a large frying pan or wok and, as soon as the butter has melted, tip in the pancetta and garlic. Leave to cook on a medium heat for about 5 minutes, stirring often, until the pancetta is golden and crisp. The garlic has now imparted its flavour, so take it out with a slotted spoon and discard. Keep the heat under the pancetta on low. When the pasta is ready, lift it from the water with a pasta fork or tongs and put it in the frying pan with the pancetta. Don’t worry if a little water drops in the pan as well (you want this to happen) and don’t throw the pasta water away yet. Mix most of the cheese in with the eggs, keeping a small handful back for sprinkling over later. Take the pan of spaghetti and pancetta off the heat. Now quickly pour in the eggs and cheese. Using the tongs or a long fork, lift up the spaghetti so it mixes easily with the egg mixture, which thickens but doesn’t scramble, and everything is coated. Add extra pasta cooking water to keep it saucy (several tablespoons should do it). You don’t want it wet, just moist. Season with a little salt, if needed. Use a long-pronged fork to twist the pasta on to the serving plate or bowl. Serve immediately with a little sprinkling of the remaining cheese and a grating of black pepper. If the dish does get a little dry before serving, splash in some more hot pasta water and the glossy sauciness will be revived.','15 mins - 20 mins','15 mins'),
(159,'tuna pasta bake','https://www.bbcgoodfood.com/recipes/tuna-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-51616_12-796faab.jpg','Whip up this cheap treat using storecupboard ingredients, tinned tuna and sweetcorn',5,6,1,1,4,'Heat oven to 180C/fan 160C/gas 4. Boil 600g rigatoni for 2 mins less time than stated on the pack. To make the sauce, melt 50g butter in a saucepan and stir in 50g plain flour. Cook for 1 min, then gradually stir in 600ml milk to make a thick white sauce. Remove from the heat and stir in all but a handful of the 250g grated cheddar. Drain the pasta, mix with the white sauce, two 160g drained cans tuna, one 330g drained can sweetcorn and a large handful of chopped parsley, then season. Transfer to a baking dish and top with the rest of the grated cheddar. Bake for 15-20 mins until the cheese on top is golden and starting to brown.','10 mins','40 mins'),
(143,'easy pesto lasagne','https://www.bbcgoodfood.com/recipes/easy-pesto-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pesto-6a5e8ab.jpg','This vegetarian bake from reader Lucy Nanor is packed with spinach and peas along with creamy pesto and mascarpone sauce',5,4,1,1,4,'Heat oven to 180C/160C fan/gas 4. Place the pesto, half the mascarpone and 250ml water (or vegetable stock if you have some) in a pan. Heat and mix until smooth and bubbling. Add the spinach and peas and cook for a few more mins until the spinach has wilted and the peas thawed. Add the herbs and season. Place a third of the pesto mixture into a baking dish roughly 18 x 25cm. Top with 4 lasagne sheets, then repeat with 2 more layers of sauce and lasagne sheets, finishing with a layer of pasta. Mix enough milk into the remaining mascarpone to make a white sauce consistency, season, then pour over the top. Sprinkle with the Parmesan and pine nuts. Bake for 35-40 mins until golden brown on top and bubbling around the edges. Scatter over the reserved basil leaves and serve with a green salad, if you like.','10 mins','45 mins'),
(144,'chicken pasta bake','https://www.bbcgoodfood.com/recipes/chicken-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken_pasta_bake-06fe2d6.jpg','Enjoy this gooey cheese and chicken pasta bake for the ultimate weekday family dinner. Serve straight from the dish with a dressed green salad ',14,6,1,1,4,'Heat 2 tbsp of the oil in a pan over a medium heat and fry the onion gently for 10-12 mins. Add the garlic and chilli flakes and cook for 1 min. Tip in the tomatoes and sugar and season to taste. Simmer uncovered for 20 mins or until thickened, then stir through the mascarpone. Heat 1 tbsp of oil in a non-stick frying pan. Season the chicken and fry for 5-7 mins or until the chicken is cooked through. Heat the oven to 220C/200C fan/gas 7. Cook the penne following pack instructions. Drain and toss with the remaining oil. Tip the pasta into a medium sized ovenproof dish. Stir in the chicken and pour over the sauce. Top with the cheddar, mozzarella and parsley. Bake for 20 mins or until golden brown and bubbling.','30 mins','45 mins'),
(145,'sausage pasta','https://www.bbcgoodfood.com/recipes/saucy-sausage-pasta-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560464_10-a602e6d.jpg','Tomato, onion and garlic pasta sauce with sausage chunks for a bit of substance – brilliant for students (and as a hangover cure)',5,4,1,1,4,'Put a large pan of water on to boil. Heat 1 tbsp olive oil in a large frying pan and fry chunky pieces of 8 pork sausages on a fairly high heat until they are golden brown all over. Now turn the heat down and add 1 large chopped onion and 2 crushed garlic cloves, cooking them until they have softened. Stir in 1 tsp chilli powder and 400g chopped tomatoes with the sausages, bring the sauce to the boil then turn the heat down and let it bubble for about 10 minutes while you cook the pasta. Drop 300g pasta into the pan of boiling water and cook according to the pack instructions. Drain the pasta, then tip it into the frying pan with the sausage sauce, mixing well to coat. Dish up immediately with crusty bread.','5 mins','25 mins'),
(146,'caponata pasta','https://www.bbcgoodfood.com/recipes/caponata-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/caponata-pasta-a0027c4.jpg','Whip up our easy vegetarian caponata pasta in just 20 minutes. It''s simple to make, budget-friendly and packs three of your five-a-day into one delicious meal',6,4,1,1,4,'Heat the oil in a large pan and cook the onion for 8-10 mins until starting to caramelise (or for longer if you have time – the sweeter the better). Add the garlic for the final 2 mins of cooking time. Tip in the mixed veg, tomatoes, capers and raisins. Season well and simmer, uncovered, for 10 mins, or until you have a rich sauce. Meanwhile, boil the kettle. Pour the kettleful of water into a large pan with a little salt and bring back to the boil. Add the pasta and cook until tender with a little bite, then drain, reserving some of the pasta water. Tip the pasta into the sauce, adding a splash of pasta water if it needs loosening. Scatter with the basil leaves and parmesan, if you like, and serve straight from the pan','2 mins','18 mins'),
(147,'chunky sausage & tomato pasta','https://www.bbcgoodfood.com/recipes/chunky-sausage-tomato-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338950_11-30acd87.jpg','Jazz up sausages with this spicy tomato pasta',38,4,1,1,4,'Heat the olive oil in a heavy-based pan (preferably not non-stick) and add the sausages. Fry for about 8 mins until golden and cooked through. Tip in the garlic and fry for 1 min. Pour in the white wine and boil until it has reduced by half. Stir in the tomato purée and tomatoes, and season to taste. Simmer for 15 mins until the sauce is rich and thick. While the sauce cooks, boil the pasta according to pack instructions and drain. Stir in the basil if using, and cooked pasta into the sauce, then serve in bowls with grated or shaved Parmesan.','5 mins','20 mins'),
(241,'two-minute breakfast smoothie','https://www.bbcgoodfood.com/recipes/two-minute-breakfast-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/two-minute-breakfast-smoothie-4a4722d.jpg','Whizz up a low fat breakfast smoothie in no time. Use banana with other soft fruit, plus honey for a little sweetness and oats for slow-release fuel',39,2,1,1,2,'Put all the ingredients in a blender and whizz for 1 min until smooth. Pour the banana oat smoothie into two glasses to serve.','2 mins','No Time'),
(149,'cacio e pepe','https://www.bbcgoodfood.com/recipes/cacio-e-pepe','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Cacio-e-Pepe-e44b9f8.jpg','Whip up a simple cacio e pepe for a speedy lunch. With four simple ingredients – spaghetti, pepper, parmesan and butter – this is a storecupboard favourite',5,2,1,1,4,'Cook the pasta for 2 mins less than pack instructions state, in salted boiling water. Meanwhile, melt the butter in a medium frying pan over a low heat, then add the ground black pepper and toast for a few minutes. Drain the pasta, keeping 200ml of the pasta water. Tip the pasta and 100ml of the pasta water into the pan with the butter and pepper. Toss briefly, then scatter over the parmesan evenly, but don’t stir – wait for the cheese to melt for 30 seconds, then once melted, toss everything well, and stir together. This prevents the cheese from clumping or going stringy and makes a smooth, shiny sauce. Add a splash more pasta water if you need to, to loosen the sauce and coat the pasta. Serve immediately with a good grating of black pepper.','5 mins','10 mins'),
(150,'italian broccoli & salmon bake','https://www.bbcgoodfood.com/recipes/italian-broccoli-salmon-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-227467_12-0d8623c.jpg','Take a fresh look at broccoli with this creamy pasta bake',5,4,1,1,4,'Preheat the oven to 190C/gas 5/fan 170C and get out an ovenproof dish (measuring 20 by 30cm, and about 5cm deep). Meanwhile, put a large pan of water on to boil for the pasta. When it is boiling rapidly, tip in the pasta with a generous sprinkling of salt. Give it a stir, return to the boil and cook for 6 minutes. Add the broccoli, then return the water to the boil and cook for 4 minutes more, until the broccoli is on the firm side of just tender. Drain well. While the pasta is cooking, put the butter, flour and milk in a large pan and heat, whisking or stirring continuously, until it thickens to make a smooth sauce. Remove from the heat and stir in the mascarpone, sun-dried tomatoes, capers (if using), anchovies (if using) and basil, then add the pasta and broccoli and season well. Halve the salmon fillets widthways (you will see that there is an obvious divide on each fillet) then place the pieces in a single layer on the base of the ovenproof dish. Spoon the broccoli mixture on top, then scatter with the grated cheddar. (You can chill this for up to 4 hours if you want to get ahead.) Bake for 30 minutes until the mixture is just starting to bubble round the edges and the mixture is pale golden – don’t let it go too dark or the fish will overcook.','25 mins - 35 mins','30 mins'),
(151,'macaroni cheese in 4 easy steps','https://www.bbcgoodfood.com/recipes/macaroni-cheese-4-easy-steps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52317_12-e4fd4a3.jpg','A simple step-by-step guide to making a classic supper dish. An old favourite, made with plenty of cheese and a little mustard',5,4,1,1,4,'Prepare the pasta: In a small pan, warm the milk, onion, garlic and bay leaf until almost boiling. Remove from the heat, leave covered to infuse for 10 mins, then strain. Cook the macaroni according to pack instructions until just soft, but still with a little bite (this is called al dente in Italian). This will take about 10 mins. Drain in a colander, then run under the tap and stir to stop the pasta sticking together. Make a roux: (A roux is simply flour and fat, cooked together, then used to thicken sauces.) Heat oven to 190C/ fan 170C/gas 5 and butter a 25 x 18cm ovenproof dish. Melt the butter in the medium pan. When foaming, add the flour, then cook, stirring constantly, for 1 min on a low heat. Finish the sauce: Slowly stir the warm infused milk into the roux until smooth. Simmer for 3-4 mins, stirring often, until the sauce has thickened and has a coating consistency (run your finger through the sauce on a spoon – it should leave a trail). Remove the pan from the heat, then add the cheddar and mustard powder. Season, then stir until the cheese has melted. Assemble and bake: Mix the cheesesauce through the macaroni to coat it well, then tip into the prepared dish. Sprinkle the Parmesan and breadcrumbs over the top, then bake for 15-20 mins until golden brown and bubbling. Serve piping hot on its own or with a green salad. EQUIPMENT: 1 small saucepan with lid, 1 large saucepan, 1 medium saucepan, colander, chopping board, sharp knife, grater, wooden spoon, ovenproof dish','15 mins','30 mins'),
(152,'the best spaghetti bolognese recipe','https://www.bbcgoodfood.com/recipes/best-spaghetti-bolognese-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/the-best-spaghetti-bolognese-7e83155.jpg','Our best ever spaghetti bolognese is super easy and a true Italian classic with a meaty, chilli sauce. This pasta bolognese recipe is sure to become a family favourite.',40,6,1,1,4,'Put a large saucepan on a medium heat and add 1 tbsp olive oil. Add 4 finely chopped bacon rashers and fry for 10 mins until golden and crisp. Reduce the heat and add the 2 onions, 2 carrots, 2 celery sticks, 2 garlic cloves and the leaves from 2-3 sprigs rosemary, all finely chopped, then fry for 10 mins. Stir the veg often until it softens. Increase the heat to medium-high, add 500g beef mince and cook stirring for 3-4 mins until the meat is browned all over. Add 2 tins plum tomatoes, the finely chopped leaves from ¾ small pack basil, 1 tsp dried oregano, 2 bay leaves, 2 tbsp tomato purée, 1 beef stock cube, 1 deseeded and finely chopped red chilli (if using), 125ml red wine and 6 halved cherry tomatoes. Stir with a wooden spoon, breaking up the plum tomatoes. Bring to the boil, reduce to a gentle simmer and cover with a lid. Cook for 1 hr 15 mins stirring occasionally, until you have a rich, thick sauce. Add the 75g grated parmesan, check the seasoning and stir. When the bolognese is nearly finished, cook 400g spaghetti following the pack instructions. Drain the spaghetti and either stir into the bolognese sauce, or serve the sauce on top. Serve with more grated parmesan, the remaining basil leaves and crusty bread, if you like.','25 mins','1 hr and 50 mins'),
(153,'creamy courgette lasagne','https://www.bbcgoodfood.com/recipes/creamy-courgette-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Creamy-courgette-lasagne-e63aa0c.jpg','Serve this quick, creamy courgette & ricotta lasagne for a last-minute dinner party to impress vegetarian friends. It''s a great way to use courgettes when they''re in season',5,4,1,1,4,'Heat oven to 220C/fan 200C/gas 7. Put a pan of water on to boil, then cook the lasagne sheets for about 5 mins until softened, but not cooked through. Rinse in cold water, then drizzle with a little oil to stop them sticking together. Meanwhile, heat the oil in a large frying pan, then fry the onion. After 3 mins, add the courgettes and garlic and continue to fry until the courgette has softened and turned bright green. Stir in 2/3 of both the ricotta and the cheddar, then season to taste. Heat the tomato sauce in the microwave for 2 mins on High until hot. In a large baking dish, layer up the lasagne, starting with half the courgette mix, then pasta, then tomato sauce. Repeat, top with blobs of the remaining ricotta, then scatter with the rest of the cheddar. Bake on the top shelf for about 10 mins until the pasta is tender and the cheese is golden.','10 mins','20 mins'),
(160,'creamy ham, leek & mushroom spaghetti','https://www.bbcgoodfood.com/recipes/creamy-ham-leek-mushroom-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327851_12-fe9f808.jpg','This healthier pasta recipe uses low-fat soft cheese to create a creamy sauce to coat the spaghetti',5,4,1,1,4,'Heat the oil in a large saucepan. Stir in the leeks, 2 tbsp water and some seasoning, cover, then cook for 5 mins over a medium heat until softened. Add the mushrooms and garlic, then cook for 3 mins more. Meanwhile, cook the spaghetti according to pack instructions, then drain, reserving a little of the cooking liquid. Stir the soft cheese into the leek and mushroom mixture, adding enough of the reserved pasta liquid to give a sauce-like consistency. Add the ham, basil leaves and Parmesan, then toss in the spaghetti.','15 mins','15 mins'),
(154,'fennel spaghetti','https://www.bbcgoodfood.com/recipes/fennel-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fennel-spaghetti-96b0208.jpg','Low fat and low calorie, this fennel spaghetti is a healthy vegetarian meal for two. Make it as an easy midweek meal and garnish with parsley and parmesan',41,2,1,1,4,'Heat the oil in a frying pan over a medium heat and cook the fennel seeds until they pop. Sizzle the garlic for 1 min, then add the lemon zest and half the fennel slices. Cook for 10-12 mins or until the fennel has softened. Meanwhile, bring a pan of salted water to the boil and cook the pasta for 1 min less than pack instructions. Use tongs to transfer the pasta to the frying pan along with a good splash of pasta water. Increase the heat to high and toss well. Stir through the remaining fennel slices, the parsley and lemon juice, season generously, then tip straight into two bowls to serve. Top with the fennel fronds, extra olive oil and parmesan shavings, if you like.','15 mins','30 mins'),
(155,'creamy courgette & bacon pasta','https://www.bbcgoodfood.com/recipes/creamy-courgette-bacon-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-991468_11-2f6016f.jpg','A quick and creamy carbonara-style tagliatelle that showcases delicious courgettes contrasted with cream and pancetta',7,4,1,1,4,'Heat the olive oil in a large frying pan and sizzle the pancetta or bacon for about 5 mins until starting to crisp. Turn up the heat and add the grated courgette to the pan. Cook for 5 mins or until soft and starting to brown then add the garlic and cook for a minute longer. Season and set aside. Cook the tagliatelle according to the pack instructions and scoop out a cupful of cooking water. Drain the tagliatelle and tip into the frying pan with the bacon and courgette. Over a low heat toss everything together with the crème fraiche and half the Parmesan adding a splash of pasta water too if you need to loosen the sauce. Season to taste and serve twirled into bowls with the remaining Parmesan scattered over.','10 mins','20 mins'),
(156,'creamy mushroom pasta','https://www.bbcgoodfood.com/recipes/creamy-mushroom-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy_mushroom_pasta-fc7ab67.jpg','Make this creamy mushroom pasta dish on days when you need a big bowl of comfort. Cream, parmesan, white wine, lemon zest and parsley make this a rich and flavourful dinner ',14,4,1,1,4,'Heat the oil and butter in a medium saucepan. Fry the onion over a low heat for 10 mins or until softened and translucent. Add the mushrooms and cook for 10 mins over a medium heat. Add the garlic and cook for 2 mins. Add the wine and bring to a simmer, reduce the liquid by half. Add the double cream and bring to a simmer, then add the lemon zest and parmesan. Season with salt and plenty of black pepper. Meanwhile, cook the pasta following pack instructions. Reserve 100ml of the pasta water. Toss the pasta in the pan with the creamy sauce and enough of the reserved water to loosen. Stir through the parsley, divide into bowls and top with extra cheese, if you like.','15 mins','25 mins'),
(157,'beef stroganoff with herby pasta','https://www.bbcgoodfood.com/recipes/beef-stroganoff-herby-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52759_11-bf250b1.jpg','If you usually serve beef stroganoff with rice, try it with pappardelle pasta instead - it works beautifully with the creamy sauce',10,4,1,1,4,'Freeze the beef for 45 mins before you begin, slice as thinly as you can, then season. In a large, non-stick frying pan, melt half the butter with half the oil. Increase the heat, then quickly sear the beef in batches until browned on both sides. Remove the meat and set aside. Repeat with the mushrooms, then set aside with the beef. Boil the pasta. Add the remaining butter and oil to the pan and soften the shallots for a few mins. Stir in the flour for 1 min, then gradually stir in the stock. Bubble for 5 mins until thickened, then stir in the mustard, purée, crème fraîche and seasoning. Bubble for 1 min more, then return the beef and mushrooms to the pan. Drain the pasta, toss with half the parsley, season, then serve with the creamy stroganoff, sprinkled with the remaining parsley.','10 mins','20 mins'),
(158,'chicken & broccoli pasta bake','https://www.bbcgoodfood.com/recipes/chicken-broccoli-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52035_12-5c3c6ff.jpg','This dish can be assembled ready for baking a good few hours ahead - and it''s all cooked in one pan',5,4,1,1,4,'Heat the oven to 190C/ fan170C/ gas 5. Bring a large pan of salted water to the boil. Throw in 350g pasta shells or quills, stir well and return to the boil. Cook for 6 minutes, then add 200g broccoli, cut into very small florets and stems thinly sliced, and cook for 5-6 minutes more until the pasta is just cooked. Drain well, then return to the pan. Heat 2 tbsp olive oil in a wide pan, add 350g thinly sliced boneless, skinless chicken breasts and fry until lightly browned. Tip in 175g quartered chestnut mushrooms and stir fry for 1 minute, then stir in 4 tbsp sundried tomato paste, 80g soft cheese with garlic and herbs and a 284ml carton single cream. Gently simmer, stirring, until the cheese has melted to thicken the sauce. Season with salt and pepper. Pour the sauce over the pasta, stirring gently until coated, then tip into a shallow ovenproof dish (about 1.7 litre capacity) and level the top. Mix a bunch of finely sliced spring onions, 85g grated mature cheddar, 1 finely chopped garlic clove and 50g flaked almonds for the topping and sprinkle over the pasta. Bake for 20 minutes until golden.','5 mins','35 mins'),
(161,'pearl barley, bacon & leek casserole','https://www.bbcgoodfood.com/recipes/pearl-barley-bacon-leek-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pearl-862ab8c.jpg','This storecupboard grain makes a great alternative to rice or couscous. Team with bacon and veggies in this risotto-like stew',42,4,1,1,4,'Heat a large pan over a medium heat. Add the olive oil and cook the leeks for a few mins, then add the garlic and cook for just 1 min more. Add the pearl barley, carrots and mustard, then pour over the chicken stock. Season with plenty of ground black pepper and simmer for 20 mins, stirring occasionally. Add the cabbage with the bacon, and cook for 5-10 mins until cabbage is wilted and tender. Serve with extra Dijon mustard on the side.','10 mins','40 mins - 45 mins'),
(162,'herby spring chicken pot pie','https://www.bbcgoodfood.com/recipes/herby-spring-chicken-pot-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/filopie-3972d72.jpg','Minimise your washing-up with this delicious one-pan chicken pie with spinach and herbs and topped with crispy filo pastry. It''s ideal for busy weeknights',14,4,1,1,4,'Heat oven to 200C/180C fan/gas 6. Heat the oil in a large, shallow casserole dish on a medium heat. Add the spring onions and fry for 3 mins, then stir through the frozen spinach and cook for 2 mins or until it’s starting to wilt. Remove the skin from the chicken and discard. Shred the chicken off the bone and into the pan, and discard the bones. Stir through the stock and mustard. Bring to a simmer and cook, uncovered, for 5-10 mins. Stir in the peas, crème fraîche and herbs, then remove from the heat. Scrunch the filo pastry sheets over the mixture, brush with a little oil and bake for 15-20 mins or until golden brown.','10 mins','30 mins'),
(163,'prawn jambalaya','https://www.bbcgoodfood.com/recipes/prawn-jambalaya','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/prawn-jambalaya-ffe597b.jpg','This nourishing jambalaya is a winning recipe. It''s low-fat, low-calorie, and provides 4 of your 5-a-day - plus it''s all made in one pan so less washing up!',4,2,1,1,4,'Heat the oil in a large, deep frying pan. Add the onion and celery, and fry for 5 mins to soften. Add the rice and spices, and pour in the tomatoes with just under 1 can of water. Stir in the bouillon powder, pepper, garlic and thyme. Cover the pan with a lid and simmer for 30 mins until the rice is tender and almost all the liquid has been absorbed. Stir in the prawns and parsley, cook briefly to heat through, then serve.','10 mins','35 mins'),
(164,'one-pan smoky beans & sausage meatballs','https://www.bbcgoodfood.com/recipes/one-pan-smoky-beans-sausage-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/One-pan-smoky-beans-and-sausage-meatballs-b4bade1.jpg','Cook our smoky beans and sausage meatballs dish in one pan – it''s ideal camping food. This recipe mostly uses storecupboard ingredients, and is easily scaled up, too',7,4,1,1,4,'Squeeze the sausagemeat from one of the sausages, then roll into four balls. Repeat with the rest of the sausages. Heat half the oil in a flameproof casserole, shallow saucepan or deep frying pan over a medium heat and fry the sausage balls for about 8 mins, stirring occasionally until browned all over. Scatter in the onion, drizzle in the remaining oil and cook for 5 mins until the onions are golden. Add the garlic and cook for 2 mins more. Scatter in 1 tbsp sugar and the fajita spice mix, cooking for a minute or two until the mixture has thickened. Splash in the vinegar, tip in the tomatoes and a can of water, then add the beans. Season, stir, bring to a simmer and bubble gently for 15-20 mins until the sauce is thick and rich. Remove from the heat and leave to cool slightly. Can be prepared up to three days ahead, then cooled completely and chilled for up to three days or frozen for three months. Defrost completely in the fridge overnight and reheat in a pan until piping hot. Scatter with parsley or coriander and serve with crusty bread or jacket potatoes, if you like.','15 mins','40 mins'),
(165,'chipotle bean chilli with baked eggs','https://www.bbcgoodfood.com/recipes/chipotle-bean-chilli-baked-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1249455_8-4a7f81e.jpg','Black beans make a great foundation for this storecupboard, Mexican one-pot with smoky hot sauce, coriander and tortillas',28,4,1,1,4,'Heat the oil in a deep frying pan and cook the onion for about 5 mins until soft. Add the chipotle paste, beans, tomatoes and sugar, and simmer for about 15-20 mins until thickened. Season to taste. Make 4 holes and crack an egg into each one. Cover and simmer over a low heat for 8-10 mins until the eggs are cooked to your liking. Sprinkle with coriander leaves and serve with a bowl of soured cream and some warm flour tortillas.','5 mins','30 mins'),
(242,'sunshine smoothie','https://www.bbcgoodfood.com/recipes/sunshine-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sunshine-smoothie-0250bef.jpg','Start your day off with a little ray of sunshine - a low-fat smoothie that''s one of your five-a-day and tasty too!',6,3,1,1,2,'Put the ingredients in a blender and whizz until smooth. Drink straight away or pour into a bottle to drink on the go. Will keep in the fridge for a day.','5 mins','No Time'),
(166,'saucy bean baked eggs','https://www.bbcgoodfood.com/recipes/saucy-bean-baked-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/saucy-bean-baked-eggs-d582e18.jpg','Make these five-ingredient baked eggs with tomatoes and beans using storecupboard staples. It''s a quick, easy, tasty and healthy dish',14,2,1,1,4,'Tip the tomatoes and bean salad into an ovenproof frying pan or shallow flameproof casserole dish. Simmer for 10 mins, or until reduced. Stir in the spinach and cook for 5 mins more until wilted.  Heat the grill to medium. Make four indentations in the mixture using the back of a spoon, then crack one egg in each. Nestle the ham in the mixture, then grill for 4-5 mins, or until the whites are set and the yolks runny. Serve with rye bread, if you like.','5 mins','20 mins'),
(167,'one-pan coconut dhal','https://www.bbcgoodfood.com/recipes/one-pan-coconut-dhal','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Coconut-dhal-e7c1fe2.jpg','Rustle up a versatile coconut dhal. It’s the most comforting vegan meal, all made in one pan, and it can also be used as a base to make other curries.',7,4,1,1,4,'Rinse the lentils until the water runs clear, then drain. Heat the oil in a large, shallow pan and cook the onions over a low heat for 10 mins until starting to brown, adding more oil if the pan looks dry. Add the garlic and ginger, and cook for another 30 seconds. Scatter in the curry leaves, if using, and all the spices and cook for a few minutes more, then stir in the tomato purée and cook for a minute longer. Stir in the lentils, ensuring they’re well coated in the spices, then pour over the coconut milk and 850ml water. Bring to the boil, stirring occasionally, then simmer for about 30 mins until the lentils are soft and soupy. Scoop out the whole chilli and discard, then season generously with salt. The dhal can now be left to cool, then frozen in portions, or as one batch in a large plastic container. Defrost completely before reheating in a pan over a low heat, adding more water to loosen if needed. Serve with a sprinkling of the coriander, sliced ginger and chilli, and drizzle with a bit more oil.','10 mins','45 mins'),
(168,'mushroom & rice one-pot','https://www.bbcgoodfood.com/recipes/mushroom-rice-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513601_11-cfabc8f.jpg','Transform a handful of simple storecupboard ingredients into this hearty, comforting supper',43,4,1,1,4,'Heat oven to 190C/fan 170C/gas 5. Tip the rice into a sieve, rinse under cold running water, then leave to drain. Heat the oil in a flameproof casserole, add the onion, then fry until softened, about 5 mins. Stir in the rosemary and mushrooms, then fry briefly. Add the rice, stir to coat in the oil, then add the peppers, tomatoes, stock and some freshly ground pepper. Bring to the boil, give it a stir, cover tightly with a lid, then bake for 20-25 mins until the rice is tender. Scatter over the parsley and serve.','20 mins','30 mins'),
(169,'chicken & pesto pasta','https://www.bbcgoodfood.com/recipes/creamy-chicken-green-bean-pesto-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1034476_11-341df91.jpg','Grab some Italian pesto and greens from your storecupboard and rustle up this creamy chicken and green bean pesto pasta – penne or fusilli work well',5,4,1,1,4,'Cook pasta following pack instructions, adding green beans for the final 6 mins of cooking time. Drain and reserve a few tbsps of the cooking water. Meanwhile, heat olive oil in a large frying pan. Add spring onions and cook for 1-2 mins until soft, then set aside. Add the shredded chicken in the pan and heat through. Stir through pesto and cream. Pop pasta and beans in with the chicken mix and stir to coat, adding a little of the cooking water. Season and sprinkle with Parmesan.','10 mins','10 mins'),
(170,'chorizo & pea risotto','https://www.bbcgoodfood.com/recipes/chorizo-pea-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-pea-risotto-974507b.jpg','Using simple storecupboard and freezer ingredients, this pea and chorizo risotto makes an easy midweek meal. Garnish with crisped chorizo and grated parmesan',16,4,1,1,4,'Heat the oil in a large frying pan, tip in the chorizo and fry until it is crisp and all the oil has been released. Remove a quarter of the chorizo with a slotted spoon and set aside. Tip the rice into the pan, stir to coat it in the oil and toast for a min or so, then pour in the vinegar. Once the vinegar has evaporated, add a ladleful of the stock, stir then, once absorbed, add a bit more. Continue doing this for about 20 mins until most of the stock has been absorbed and the rice has swollen but still has a slight bite. Tip in the peas, parmesan and the remaining stock. Give everything a good stir. Once the cheese has melted, season with black pepper, then divide between bowls, topping each portion with the remaining crisp chorizo and extra grated parmesan.','5 mins','30 mins'),
(202,'breakfast pancake platter','https://www.bbcgoodfood.com/recipes/breakfast-pancake-platter','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/pancake-platter-78dc92d.jpg','Serve a base of wholemeal American-style pancakes alongside a range of toppings. Go sweet with fruit and yogurt, or savoury with soft cheese and salmon',4,4,1,1,2,'Tip the flour into a bowl, then whisk in the egg yolks and half the milk until smooth. Slowly whisk in the rest of the milk (the batter will be runny). Whisk the egg whites to soft peaks in a separate bowl, then gently fold this into the batter. Heat a large non-stick pan over a medium heat and wipe a drop of oil around the base using a kitchen towel. Spoon in 2 tbsp of batter per pancake (you should be able to make three at a time, well- spaced apart) and cook for 2-3 mins until bubbles appear on the surface and the bottom is set. Flip using a palette knife and cook for 2-3 mins more. Arrange the pancakes in a single layer over a large serving platter, then top one side with the salmon, soft cheese, cucumber slices, lemon wedges and onion slices. Top the other side with the yogurt, banana slices and berries. If you like, scatter the savoury pancakes with the capers, dill and spring onions, if using, and top the sweet pancakes with walnuts, seeds, cinnamon and maple syrup. Or, serve these on the side for everyone to help themselves.','10 mins','20 mins'),
(171,'easy paella','https://www.bbcgoodfood.com/recipes/easy-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-800500_11-6e543cc.jpg','Whip up this easy version of the traditional Spanish seafood dish using storecupboard staples. Add extras to paella rice such as chorizo and peas if you like',33,4,1,1,4,'Heat the olive oil in a large frying pan or wok. Add the onion and soften for 5 mins. Add the smoked paprika, thyme and paella rice, stir for 1 min, then splash in the sherry, if using. Once evaporated, stir in the chopped tomatoes and chicken stock. Season and cook, uncovered, for about 15 mins, stirring now and again until the rice is almost tender and still surrounded with some liquid. Stir in the seafood mix and cover with a lid. Simmer for 5 mins, or until the seafood is cooked through and the rice is tender. Squeeze over the lemon juice, scatter over the parsley and serve with the lemon wedges.','10 mins','30 mins'),
(172,'home-style lamb curry','https://www.bbcgoodfood.com/recipes/home-style-lamb-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/home-style-lamb-curry-6a92ec4.jpg','Storecupboard spices along with puréed onions, garlic and ginger create an authentic flavour in this tender lamb curry, that''s rich in iron and two of your 5-a-day',4,4,1,1,4,'Put ½ the thumb-sized piece of ginger, 2 quartered onions and 4 garlic cloves in a food processor with 300ml water. Blitz to a smooth purée. Scrape down the sides with a spoon and blitz again to make it as smooth as you can. Tip into a deep sauté pan, cover with a lid and simmer for 15 mins. Remove the lid and cook for 5 mins more, stirring occasionally. By now the liquid should be all gone. If not, cook a little longer. Add 2 tbsp rapeseed oil to the pan with the remaining piece of ginger, cut into matchsticks. Turn up the heat and fry, stirring, for 3-5 mins until it starts to colour. Stir in 1 cinnamon stick, 1 tbsp ground coriander, 1 tsp ground cumin, 1 tsp ground turmeric and ½ tsp fennel seeds, then add 750g leg of lamb. Stir-fry until the lamb changes colour. Tip in 400g can chopped tomatoes with a can of water and 1 deseeded and sliced red or green chilli, season well, cover and leave to simmer for 1 hr. Stir in the finely chopped stalks from a small bunch coriander, re-cover and cook for a final 30 mins until the lamb is tender. Add a splash of water if necessary to loosen the consistency as it cooks. Stir in the roughly chopped coriander leaves and serve with basmati rice and mango chutney or raita.','15 mins','1 hr and 50 mins'),
(173,'lentil & bacon soup','https://www.bbcgoodfood.com/recipes/lentil-bacon-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-946456_11-afbfcba.jpg','Blend your storecupboard pulses with cumin, turmeric and garlic to create this robust soup with crispy pancetta topping',44,3,1,1,4,'Heat the olive oil in a large saucepan. Add the onion, 1 pack of pancetta and the carrot. Cook on a low to medium heat for 10 minutes until the onions are soft. Add the cumin, turmeric, garlic and chilli and cook for a further 1 – 2 minutes until the aromas are released. Pour in 1.25 litres of boiling water, crumble in the stock cubes and add the lentils. Bring to a simmer and cook for 20 mins, stirring occasionally to ensure the lentils aren’t sticking. Meanwhile, fry the remaining lardons in a small frying pan for about 10 minutes until crispy. You don’t need to add any oil as plenty will run from the pancetta. Serve the soup with a sprinkle of crispy lardons on top.','5 mins','30 mins'),
(174,'one-pot chicken pilaf','https://www.bbcgoodfood.com/recipes/one-pot-chicken-pilaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52183_11-a8757c2.jpg','Keep the washing-up down to an absolute minimum with this one-pot supper - you can even eat it out of the cooking dish!',5,1,1,1,4,'Heat the oil in a frying pan, then fry the onion for 5-6 mins until softened. Add the chicken pieces, fry for a further couple of mins just to colour the outside, then stir in curry paste and rice. Cook for another min. Pour in the chicken stock and throw in any larger bits of frozen veg. Bring to the boil, lower the heat, then cover the pan with a lid. Cook for 10 mins, then stir in the remaining veg. Scatter over the spinach, cover, then cook for 10 mins more until all the stock is absorbed and the rice is tender. Give everything a good stir, season to taste, then tuck in.','5 mins','20 mins'),
(175,'chorizo & chickpea stew','https://www.bbcgoodfood.com/recipes/chorizo-chickpea-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1308507_1-c03a979.jpg','A casual storecupboard Spanish-inspired casserole that can be served in a tapas spread or simply with some toasted bread',10,2,1,1,4,'In a frying pan, fry the chorizo, onion and garlic for 5 mins or until the chorizo is browning on the outside. Drain the excess oil, then stir in the spices. Pour in the tomatoes, lemon juice, chickpeas and seasoning. Bubble for 5 mins more to thicken slightly, then sprinkle over the parsley. Serve with toasted sourdough.','5 mins','10 mins'),
(176,'satay cauliflower & chickpea curry with storecupboard flatbreads','https://www.bbcgoodfood.com/recipes/satay-cauliflower-chickpea-curry-storecupboard-flatbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/satay-cauliflower-chickpea-curry-with-storecupboard-flatbreads-727b881-scaled.jpg','An easy vegetarian curry that can be cooked in one pan with a rich and creamy sauce - serve with simple, homemade flatbreads',6,2,1,1,4,'Heat the oil in a large pan. Add the onion and sizzle for 5-10 mins until softened. Add the garlic, stir for 30 secs, then stir in the curry paste and peanut butter. Stir to combine, then add the cauliflower, chickpeas, coconut milk and 100ml water. Season well, cover with a lid and simmer for 15 mins until the cauliflower has thawed, then uncover and cook for 5 mins more until the sauce is thick. Meanwhile, make the flatbreads. Heat a large griddle pan. In a bowl, combine the flour, baking powder and 1/4 tsp salt. Add the coconut milk and mix to form a dough. Break off golf-ball-sized pieces of dough and roll out as thinly as you can on a lightly floured surface. Place in the pan and cook for 1-2 mins each side until charred dark brown and puffed up. Serve the curry with the flatbreads, scattered with coriander, if you like.','20 mins','40 mins'),
(177,'rösti lamb hotpot','https://www.bbcgoodfood.com/recipes/rosti-lamb-hotpot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rosti-lamb-hotpot-34e6c45-scaled.jpg','Top lamb mince and vegetables with a grated potato and cheese topping and serve in individual dishes for a delicious dinner for two',6,2,1,1,4,'Heat the oil in a large pan. Add the onion and sizzle for a few mins to soften. Add the lamb mince, carrot and Worcestershire sauce. Season well, and sizzle for 10 mins. Drain off any excess fat, then add the flour and crumble in the stock cube. Stir into the mince, then pour in 300ml water and bubble, uncovered, for 15-20 mins until thickened. Divide between 2 individual baking dishes or mini pie dishes. Heat oven to 200C/180C fan/gas 6. Grate the potatoes into a bowl, add the thyme and season well. Sprinkle the grated potato over the lamb mince and scatter over the cheese. Bake for 25 mins until golden brown, blasting under the grill for 1 min or so to brown at the end if the pie needs a little colour. Serve with some green veg.','20 mins','40 mins'),
(178,'one-pan spicy rice','https://www.bbcgoodfood.com/recipes/one-pan-spicy-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338635_12-cba1c91.jpg','This simple vegetarian rice dish is a useful weeknight backup – it needs practically no preparation and uses storecupboard ingredients to make a nourishing, filling meal',5,4,1,1,4,'Heat the oil in a large non-stick pan that has a lid, then fry the garlic and curry paste over a medium heat for 1 minute, until it''s fragrant. Tip the rice into the pan with the stock, chickpeas and raisins and stir with a fork to stop the rice from clumping. Season with salt and pepper, then cover and bring to the boil. Reduce to a medium heat and cook for 12-15 minutes or until all the liquid has been absorbed and the rice is tender. Squeeze the excess water from the spinach with your hands. Tip it into the pan along with 2 tbsp of hot water and fluff up the rice with a fork, making sure the spinach is mixed in well. Toss in the cashews. Serve drizzled with natural yogurt if you like.','5 mins','15 mins'),
(179,'chorizo & rosemary pearl barley risotto','https://www.bbcgoodfood.com/recipes/chorizo-rosemary-pearl-barley-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-risotto-5b27a77.jpg','Replace the usual rice with filling, storecupboard barley. Spicy Spanish sausage provides a rich flavour and vibrant colour',24,4,1,1,4,'Heat a non-stick frying pan over a medium heat and fry the chorizo for 3-4 mins until it turns golden and releases its oil. Remove with a slotted spoon and set aside. Add the onion and garlic to the pan, and cook for 3-4 mins, until they begin to soften. Add the pearl barley and stir well to coat in the oil, before tipping in the chopped tomatoes and the chicken stock. Bring just to the boil, then turn down to a simmer and cook for 30 mins, stirring occasionally, until all the liquid has been absorbed and the pearl barley is tender. You may need to top up with a little extra water as you go. Season and stir in the rosemary and chorizo to serve.','15 mins','40 mins'),
(180,'bean & pepper chilli','https://www.bbcgoodfood.com/recipes/bean-pepper-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1071519_10-f64dc0d.jpg','Use your choice of storecupboard pulses to make this healthy vegan chilli. Swapping meat for beans keep this one-pot recipe cheap to make and filling, too',5,4,1,1,4,'Heat the oil in a large pan. Add the onion and peppers, and cook for 8 mins until softened. Tip in the spices and cook for 1 min. Tip in the beans and tomatoes, bring to the boil and simmer for 15 mins or until the chilli is thickened. Season and serve with rice, if you like.','15 mins','30 mins'),
(181,'kidney bean curry','https://www.bbcgoodfood.com/recipes/kidney-bean-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kidney-bean-curry-f8e0b17.jpg','A rescue recipe for when there''s nothing in the fridge or when you fancy something cheap, this curry is delicious and filling. It''s healthy and vegan-friendly, too.',16,2,1,1,4,'Heat the oil in a large frying pan over a low-medium heat. Add the onion and a pinch of salt and cook slowly, stirring occasionally, until softened and just starting to colour. Add the garlic, ginger and coriander stalks and cook for a further 2 mins, until fragrant. Add the spices to the pan and cook for another 1 min, by which point everything should smell aromatic. Tip in the chopped tomatoes and kidney beans in their water, then bring to the boil. Turn down the heat and simmer for 15 mins until the curry is nice and thick. Season to taste, then serve with the basmati rice and the coriander leaves.','5 mins','30 mins'),
(208,'tofu brekkie pancakes','https://www.bbcgoodfood.com/recipes/tofu-brekkie-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tofu-brekkie-pancakes-cf7a861.jpg','Silken tofu is the secret to making a stack of fluffy, thick, American-style vegan pancakes without gluten, eggs or dairy',33,4,1,1,2,'Heat oven to 180C/160C fan/gas 4. Scatter the nuts over a baking tray and cook for 5 mins until toasty and golden. Leave to cool, then chop. Turn the oven down low if you want to keep the whole batch of pancakes warm, although I think they are best enjoyed straight from the pan. Put the tofu, vanilla, lemon juice and 200ml of the milk into a deep jug or bowl. Using a stick blender, blend together until liquid, then keep going until it turns thick and smooth, like yogurt. Stir in the oil and the rest of the milk to loosen the mixture. Put the dry ingredients and 1 tsp salt in a large bowl and whisk to combine and aerate. If there are any lumps in the sugar, squish them with your fingers. Make a well in the centre, pour in the tofu mix and bring together to make a thick batter. Heat a large (ideally non-stick) frying pan and swirl around 1 tsp oil. For golden pancakes that don’t stick, the pan and oil should be hot enough to get an enthusiastic sizzle on contact with the batter, but not so hot that it scorches it. Test a drop. Using a ladle or large serving spoon, drop in 3 spoonfuls of batter, easing it out gently in the pan to make pancakes that are about 12cm across. Cook for 2 mins on the first side or until bubbles pop over most of the surface. Loosen with a palette knife, then flip over the pancakes and cook for 1 min more or until puffed up and firm. Transfer to the oven to keep warm, if you need to, but don’t stack the pancakes too closely. Cook the rest of the batter, using a little more oil each time. Serve warm with sliced banana, berries, toasted nuts and a good drizzle of maple syrup or honey.','10 mins','10 mins'),
(182,'chicken with mustard lentils','https://www.bbcgoodfood.com/recipes/chicken-mustard-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1173677_11-a7016e0.jpg','Chicken thighs and drumsticks go a long way when teamed with healthy pulses flavoured with Dijon, cream and parsley',6,4,1,1,4,'Heat the oil in a large flameproof casserole. Season the chicken pieces, then brown in the hot oil for 3 mins each side, until golden on all sides. Remove and set aside. Pour away all but 1 tbsp oil. Add the onion to the pan and cook for 5 mins, then add the garlic and cook for 1 min more. Add the lentils and stock and stir well. Put the chicken on top, put the lid on and leave to simmer over a medium heat for 30 mins. Remove the lid and increase the heat. Bubble for another 20 mins until the lentils are tender, most of the stock has been absorbed, and the chicken is cooked through. Stir in the crème fraîche, lemon zest and juice, mustard, parsley and seasoning. Serve with green veg, if you like.','15 mins','1 hr and 15 mins'),
(183,'herby bean sausage stew','https://www.bbcgoodfood.com/recipes/herby-bean-sausage-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1065483_11-f6ecca0.jpg','A simple, filling storecupboard chipolata stew that can be whipped up in minutes or slow cooked for hours',5,4,1,1,4,'Heat a large non-stick frying pan, then brown the sausages for 3-5 mins over a high heat. Drain the beans, then add to the pan with the chopped tomatoes, herbs and sugar. Season well and bring to the boil. Simmer for 10 mins until the sausages are cooked through and the sauce has thickened, coating the beans.','5 mins','N/A'),
(184,'moroccan tomato & chickpea soup with couscous','https://www.bbcgoodfood.com/recipes/moroccan-tomato-chickpea-soup-couscous','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-997451_11-9888302.jpg','This filling soup is healthy and packed with the flavours of North Africa: harissa, ginger, lemon and coriander',7,4,1,1,4,'Tip the couscous into a bowl, season with salt and pepper and stir through 1 tbsp of the oil. Pour over enough hot stock just to cover and cover the bowl with cling film and set aside. Heat the rest of the oil in a saucepan and cook the onion and carrot gently for 8 mins until softened. Add the garlic and ginger and cook for 2 mins more then stir in the ras el hanout and harissa and cook for another minute. Pour in the tomatoes and stock and give everything a good stir. Season, add the chickpeas and simmer everything gently for 20 mins until thickened slightly then squeeze over the lemon. Uncover the couscous and fluff up with a fork. Spoon the soup into bowls, top each with a mound of couscous, scatter with coriander and serve with extra harissa for those who want it.','20 mins','45 mins'),
(185,'banana & blueberry muffins','https://www.bbcgoodfood.com/recipes/banana-blueberry-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192590_11-6d00010.jpg','A low-fat muffin that tastes as good as it looks - they''ll keep in an airtight container for up to 3 days, or freeze for up to a month',33,12,1,1,2,'Heat oven to 180C/fan 160C/gas 4 and line a 12-hole muffin tin with paper muffin cases. Tip the flour and bicarbonate of soda into a large bowl. Hold back 1 tbsp of the sugar, then mix the remainder with the flour and 50g oats. Make a well in the centre. In a separate bowl, mash the bananas until nearly smooth. Stir the buttermilk, oil and egg whites into the mashed banana until evenly combined. Pour the liquid mixture into the well and stir quickly and sparingly with a wooden spoon. The mix will look lumpy and may have the odd fleck of flour still visible, but don’t be tempted to over-mix. Tip in the blueberries and give it just one more stir. Divide the mix between the muffin cases – they will be quite full – then sprinkle the tops with the final tbsp of the oats and the rest of he sugar. Bake for 18-20 mins until risen and dark golden. Cool for 5 mins in the tray before lifting out onto a rack to cool completely.','10 mins','20 mins'),
(186,'breakfast muffins','https://www.bbcgoodfood.com/recipes/breakfast-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/muffins-d49653c.jpg','Make muffins healthier with mashed banana and apple sauce for natural sweetness, plus blueberries and seeds for an extra nutritious hit',6,12,1,1,2,'Heat oven to 180C/160C fan/gas 4. Line a 12-hole muffin tin with 12 large muffin cases. In a jug, mix the eggs, yogurt, oil, apple sauce, banana, honey and vanilla. Tip the remaining ingredients, except the seeds, into a large bowl, add a pinch of salt and mix to combine. Pour the wet ingredients into the dry and mix briefly until you have a smooth batter – don’t overmix as this will make the muffins heavy. Divide the batter between the cases. Sprinkle the muffins with the extra oats and the seeds. Bake for 25-30 mins until golden and well risen, and a skewer inserted into the centre of a muffin comes out clean. Remove from the oven, transfer to a wire rack and leave to cool. Can be stored in a sealed container for up to 3 days.','15 mins','30 mins'),
(243,'kale smoothie','https://www.bbcgoodfood.com/recipes/kale-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kale-smoothie-b96d533.jpg','Give yourself a dose of vitamin C in the morning with this vegan green smoothie. Along with kale and avocado, there''s a hit of zesty lime and pineapple',16,2,1,1,2,'Put all of the ingredients into a bullet or smoothie maker, add a large splash of water and blitz. Add more water until you have the desired consistency.','5 mins','No Time'),
(187,'healthy banana muffins','https://www.bbcgoodfood.com/recipes/get-and-go-breakfast-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1243649_8-7dfe2be.jpg','With apple, blueberry, banana and seeds, this breakfast-on-the-go tastes great, is low in calories and uses honey instead of sugar',6,12,1,1,2,'Heat oven to 180C/160C fan/gas 4. Line a 12-hole muffin tray with 12 large muffin cases. In a jug, mix the eggs, yogurt, oil, apple sauce, banana, honey and vanilla. Tip the remaining ingredients, except the seeds, into a large bowl, add a pinch of salt and mix to combine. Pour the wet ingredients into the dry, mix briefly until you have a smooth batter, don’t over mix as this will make the muffins heavy. Spoon the batter between the cases. Sprinkle the muffins with the extra oats and the seeds. Bake for 25-30 mins until golden and well risen, and a skewer inserted to the centre of a muffin comes out clean. Remove from the oven, transfer to a wire rack and leave to cool. Store in a sealed container for up to 3 days.','45 mins','No Time'),
(188,'feel-good muffins','https://www.bbcgoodfood.com/recipes/feel-good-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-191560_11-d1fb320.jpg','This simple cinnamon-spiced muffin recipe contains fibre-packed prunes and oats – perfect for breakfast on-the-go or as an afternoon pick-me-up',43,6,1,1,2,'Heat the oven to 200C/gas 6/fan 180C. Butter 6-8 muffin tins or line them with muffin cases. Put the flour, oats, sugar, cinnamon and bicarbonate of soda in a large bowl, then rub everything through your fingers, as if making pastry, to ensure the ingredients are evenly blended. Beat the egg, then stir in the buttermilk, vanilla and oil. Lightly stir the egg mix into the flour. Fold the prunes and nuts into the mixture. Divide between the tins, filling the cases to the brim, then bake for 20-25 minutes until risen and golden. Serve warm or cold.','10 mins','25 mins'),
(189,'easy banana muffins','https://www.bbcgoodfood.com/recipes/banana-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banana-muffins-943f3dc.jpg','Make these moreish banana muffins for an easy breakfast or a snack on the go. They''re also great for lunchboxes and will prove popular with the whole family.',41,12,1,1,2,'Heat the oven to 190C/170C Fan/gas 5. Line a 12-hole muffin tin with paper cases. Sift together the flour, baking powder, bicarbonate of soda and caster sugar with a big pinch of salt. In a separate bowl mix the melted butter, vanilla extract, eggs, mashed bananas and buttermilk. Make a well in the centre of the dry ingredients and pour the wet ingredients in. Roughly mix together with a fork, being careful not to over-mix. Scatter in the chopped pecans, if using, then spoon the mixture into the muffin cases. Top with pecan halves, then bake for 20-25 mins, until golden brown. Cool on a wire rack.','15 mins','25 mins'),
(190,'easy blueberry muffins','https://www.bbcgoodfood.com/recipes/blueberry-muffin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberry-muffin-ee95fd4.jpg','Make these super easy blueberry muffins for a fun weekend bake. You''re just a few simple steps away from bite-sized blueberry treats',6,12,1,1,2,'Heat oven to 200C/180C fan/gas 6 and line a 12-hole muffin tin with paper cases. Beat the butter and caster sugar together until pale and fluffy. Add the eggs and beat in for 1 min, then mix in the yogurt, vanilla extract and milk. Combine the flour, baking powder and bicarb in a bowl with ¼ tsp fine salt, then tip this into the wet ingredients and stir in. Finally, fold in the blueberries and divide the mixture between the muffin cases. Bake for 5 mins, then reduce oven to 180C/160C fan/gas 4 and bake for 15-18 mins more until risen and golden, and a cocktail stick inserted into the centre comes out clean. Cool in the tin for 10 mins, then carefully lift out onto a wire rack to finish cooling. Will keep for 3-4 days in an airtight container – after a day or two, pop them in the microwave for 10-15 secs on high to freshen up.','20 mins','20 mins'),
(191,'the ultimate makeover: blueberry muffins','https://www.bbcgoodfood.com/recipes/ultimate-makeover-blueberry-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-337557_11-11b58e1.jpg','We''ve created a deliciously light version of this classic muffin that contains virtually no saturated fat',37,12,1,1,2,'Heat oven to 200C/180C fan/gas 6. Use 1 tsp oil to lightly oil a 12-hole muffin tin (or use paper cases). Mix both flours with the baking powder and lemon zest. Reserve 1 tbsp caster sugar, then stir the rest into the flour with the muscovado. Mash the banana well. In another bowl beat the egg, then stir in the banana, buttermilk and oil. Using a large metal spoon, very lightly stir into the flour mix, just to combine. Over-mixing will make the muffins tough. Toss in the blueberries and give just a few turns of the spoon to carefully stir them in without crushing. Spoon the mixture into the tin – each hole should be very full. Bake for 20-25 mins until risen and golden. Mix the reserved caster sugar with the lemon juice. When the muffins are done, remove from the oven, then brush with the sugar and lemon mixture while they are still hot. Gently loosen the edges of each muffin with a knife, then leave in the tin for 15 mins to cool a little as they’re very delicate while hot. Remove to a wire rack. Best eaten the day of making, but will keep for up to 2 days.','25 mins','25 mins'),
(192,'basic muffin recipe','https://www.bbcgoodfood.com/recipes/basic-muffin-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/basic-muffin-recipe-8cde647.jpg','Good Food reader Charlotte Hilsdon shares her simple, chocolate chip muffin recipe, to which you can add fruit, chocolate or nuts',5,20,1,1,2,'Heat oven to 200C/180C fan/gas 6. Line 2 muffin trays with paper muffin cases. In a large bowl beat 2 medium eggs lightly with a handheld electric mixer for 1 min. Add 125ml vegetable oil and 250ml semi-skimmed milk and beat until just combined then add 250g golden caster sugar and whisk until you have a smooth batter. Sift in 400g self-raising flour and 1 tsp salt (or 400g plain flour and 3 tsp baking powder if using) then mix until just smooth. Be careful not to over-mix the batter as this will make the muffins tough. Stir in 100g chocolate chips or dried fruit if using. Fill muffin cases two-thirds full and bake for 20-25 mins, until risen, firm to the touch and a skewer inserted in the middle comes out clean. If the trays will not fit on 1 shelf, swap the shelves around after 15 mins of cooking. Leave the muffins in the tin to cool for a few mins and transfer to a wire rack to cool completely.','20 mins','25 mins'),
(193,'apple & sultana muffins','https://www.bbcgoodfood.com/recipes/apple-sultana-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-748499_11-dd6e1ab.jpg','These fruity muffins make a healthy lunchbox snack, scroll down for two other great lunchtime fillers',5,12,1,1,2,'Heat oven to 180C/160C fan/gas 4. In a large bowl mix the self-raising flour, baking powder, cinnamon, wholemeal flour and golden caster sugar. In another bowl, mix the eggs, semi-skimmed milk and sunflower oil. Pour the wet ingredients into the dry and mix well, then stir in the grated apples and sultanas. Divide the mix between 12 muffin cases and bake for 25 mins. Cool on a wire rack, then pack in a container for lunch.','15 mins','25 mins'),
(194,'spiced carrot & apple muffins','https://www.bbcgoodfood.com/recipes/spiced-carrot-apple-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-apple-muffins-228c562.jpg','These fruity muffins are great for breakfast on-the-go or a mid-morning treat. Add dried fruit, nuts or seeds for texture, if you like',5,9,1,1,2,'Heat oven to 180C/160C fan/gas 4 and line a muffin tin with nine cases (alternatively, use squares of baking parchment). Mix the oil, yogurt, honey, eggs and vanilla in a jug. In a bowl, combine the flour, bicarb, mixed spice and ¼ tsp salt. Pour the yogurt mixture into the flour mixture and add the apple and carrot, as well as any extra ingredients you’re using. Mix with a spatula until well combined, then spoon the mixture into the muffin cases. Bake for 20-22 mins, or until a skewer inserted into the middle of a muffin comes out clean. Leave to cool for at least 5 mins. Can be stored in a tin for three days or frozen for two months; defrost overnight before serving. ','10 mins','20 mins'),
(195,'pear & toffee muffins','https://www.bbcgoodfood.com/recipes/pear-toffee-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363685_12-a40fe7c.jpg','These gooey muffins are delicious warm when the toffee is still melty - you can reheat them in the microwave',7,12,1,1,2,'Heat oven to 200C/fan 180C/gas 6. Line a 12-hole deep muffin tin with paper cases. Tip the flour, baking powder, cinnamon and a pinch of salt into a large bowl, then stir in the sugar. Mix together the milk, eggs and melted butter in a large jug and pour into the dry mix all at once, along with the pears and a third of the toffee pieces. Stir briefly until just beginning to combine – the batter should still be quite lumpy and have streaks of flour. Divide the mixture between the cases, then sprinkle with the remaining toffee and the flaked almonds. Bake for 25-30 mins until the muffins are risen, golden and feel firm when pressed (the molten toffee will be extremely hot so be careful not to touch it). Remove from the tin to a wire rack to cool.','10 mins','30 mins'),
(196,'blueberry, peach & soured cream muffins','https://www.bbcgoodfood.com/recipes/blueberry-peach-soured-cream-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363973_10-bb44170.jpg','It''s the fizzy water in these delicious muffins that makes them so light - you can also heat them up in the microwave the next day',5,8,1,1,2,'Heat the oven to 200C/fan 180C/gas 6 and put 8 muffin cases in a muffin tray. Mix the flour, baking powder and sugar in a large bowl, add the melted butter, soured cream, eggs and lemon zest and beat just long enough to give a thick, smooth mixture – don’t over-beat. Carefully stir in the fruit and finally the sparkling water, until just evenly combined. Divide the mixture between the muffin cases – they should be nice and full. Sprinkle generously with the sugar and bake for 25-30 mins or until risen and golden. The muffins are best served just warm. Will keep fresh in an airtight container in a cool place for 1-2 days.','15 mins','30 mins'),
(227,'pear, nut & blackberry bircher','https://www.bbcgoodfood.com/recipes/pear-nut-blackberry-bircher','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/per-nut-blackberry-bircher-9773d66.png','Creamy overnight oats with yogurt, macadamia nuts and cinnamon - pop this Swiss-style muesli in a tupperware for an on-the-go breakfast',6,1,1,1,2,'Grate the pear (skin, pips and all) and scoop the pulp and any juice on the chopping board into a small sealable container or jam jar with a tight-fitting lid. Add the oats, cinnamon and yogurt. Stir well. Toast the macadamia nuts in a dry frying pan for 2 mins. Chop the nuts, then sprinkle over the Bircher with a small handful of blackberries and pomegranate seeds. Place in the fridge for at least 1 hr to chill, or overnight, before eating.','10 mins','2 mins'),
(197,'sweet potato, avocado & feta muffins','https://www.bbcgoodfood.com/recipes/sweet-potato-avocado-feta-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-muffins-931c9a4.jpg','Use super-ripe avocados in this savoury bake, topped with crumbly cheese, sweet potato chunks and a scattering of mixed seeds',6,9,1,1,2,'Place the sweet potato in a heatproof bowl, cover with cling film and microwave on High for 8 mins, or until really soft. Leave to cool completely. Grease 9 holes of a muffin tin with a little oil (or line each hole with a square of baking parchment.) Chop 1/4 of the sweet potato into even smaller pieces and set aside. Place the sweet potato, avocado, almonds, polenta, maple syrup, eggs, baking powder, bicarb, milk and 1/4 tsp salt in the bowl of a food processor. Blend until completely smooth. Divide the mixture evenly between the muffin tin holes, then top with the reserved chopped sweet potato, feta, seeds and a dusting of paprika. Bake for 22 mins at 180C/160 fan/ gas mark 4, or until risen, browning on top and cooked through – check by inserting a skewer to the centre, it should come out dry. Cool in the tin for 5 mins, then transfer to a wire rack and cool completely. Store in a sealed container for up to 3 days.','20 mins','20 mins'),
(198,'chocolate chip muffins','https://www.bbcgoodfood.com/recipes/chocolate-chip-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-chip-muffins-ccc8450.jpg','Make these easy chocolate chip muffins with kids over the weekend or school holidays. They take just 15 minutes prep and are a guaranteed family favourite',19,12,1,1,2,'Heat oven to 200C/180C fan/gas 6 and line a 12-hole muffin tin with paper cases. Sift the flour and bicarbonate of soda into a large bowl, then stir in the chocolate chips and sugar. Add the beaten eggs, yogurt and butter, and stir to combine. It doesn’t matter if the mixture looks a bit lumpy, it’s more important not to overmix or the muffins will turn out tough. Fill the paper cases and bake for 20-25 mins until risen and golden brown. Transfer to a rack to cool – or eat slightly warm. Will keep for three days in an airtight container.','15 mins','25 mins'),
(199,'choc-cherry muffins','https://www.bbcgoodfood.com/recipes/choc-cherry-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363944_11-c2569cf.jpg','If you’ve got a spare half-an-hour, whip up these easy muffins and freeze them for when you need them',23,12,1,1,2,'Heat oven to 200C/fan 180C/gas 6 and line a 12-hole muffin tin with some paper cases. Sift the flour, and bicarbonate of soda into a large bowl, then stir in the cherries, chocolate and sugar. Add the beaten eggs, yogurt and then the butter and stir to combine. It doesn’t matter if the mixture looks a bit lumpy, it’s more important not to overmix or the muffins will turn out tough. Fill the paper cases and bake for 20-25 mins until risen and golden brown. Transfer to a rack to cool. They are especially delicious eaten slightly warm.','40 mins','No Time'),
(200,'easy banana pancakes','https://www.bbcgoodfood.com/recipes/easy-banana-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banana_pancakes-8f350ac.jpg','Turn overripe, blackened bananas into sweet and fluffy American-style pancakes. Serve with syrup and crunchy, toasted pecan nuts as a delicious brunch treat',14,12,1,1,2,'Sieve the flour, baking powder and a generous pinch of salt into a large bowl. In a separate mixing bowl, mash the very ripe bananas with a fork until smooth, then whisk in the eggs, vanilla extract and milk. Make a well in the centre of the dry ingredients, tip in the wet ingredients and swiftly whisk together to create a smooth, silky batter. Heat a little knob of butter in a large non-stick pan over a medium heat. Add 2-3 tbsp of the batter to the pan and cook for several minutes, or until small bubbles start appearing on the surface. Flip the pancake over and cook for 1-2 mins on the other side. Repeat with the remaining batter, keeping the pancakes warm in a low oven. Stack the pancakes on plates and top with the banana slices, a glug of sticky maple syrup and a handful of pecan nuts, if you like.','5 mins','10 mins'),
(201,'cinnamon crêpes with nut butter, sliced banana & raspberries','https://www.bbcgoodfood.com/recipes/cinnamon-crepes-nut-butter-sliced-banana-raspberries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crepes-6e90cfb.jpg','Use gluten-free flour in these thin breakfast pancakes served with almond butter, fruit and lemon',45,2,1,1,2,'Tip the flour into a large mixing bowl with the cinnamon. Add the egg and milk, and whisk vigorously until you have a smooth pouring consistency. Place a non-stick frying pan over a medium heat and add a little of the oil. When the oil starts to heat, wipe most of it away with kitchen paper. Once the pan is hot, pour a small amount of the batter into the centre of the pan and swirl it to the sides of the pan in a thin layer. Leave to cook, untouched, for about 2 mins. When it is brown underneath, turn over and cook for 1 min more. Transfer to a warm plate and cover with foil to keep warm. Repeat with the remaining batter. Divide the warm pancakes between 2 plates and serve with the nut butter, banana, raspberries and lemon to assemble at the table.','5 mins','10 mins'),
(236,'raspberry and apple smoothie','https://www.bbcgoodfood.com/recipes/raspberry-and-apple-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raspberry-smoothie-e687525.jpg','Whip up a breakfast smoothie with raspberries, apples, yogurt and oats. It’s a great way to start the day and up your fruit intake',46,4,1,1,2,'Tip all ingredients into a blender or smoothie maker and blitz until smooth, adding 50ml water or milk if it’s too thick.','5 mins','N/A'),
(203,'fluffy american pancakes','https://www.bbcgoodfood.com/recipes/fluffy-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fluffy-pancakes-8ebc2cf.jpg','Fluffy American-style pancakes are a blank canvas for toppings like blueberries and bacon – great for breakfast in bed. Why not get the kids to help?',7,6,1,1,2,'If serving with pancetta, heat oven to 200C/180C fan/gas 6. Line a baking tray with baking parchment and lay on the pancetta in a single layer. Put another piece of parchment on top, followed by a second baking tray, and bake for 12-15 mins until crisp. To make the pancakes, get a little helper to weigh out and tip the flour, baking powder and sugar into a large bowl with a small pinch of salt. Crack in the eggs and whisk until smooth. Add the maple syrup and milk while whisking. Heat a splash of oil and a small knob of butter in a non-stick frying pan until sizzling. Add spoonfuls of batter to make pancakes the size you like, we made 20cm pancakes for a serving size of one per person, or if you are very hungry, two per person. Cook until bubbles start to form on the surface, then flip and cook the other side. Eat straight away or keep warm in a low oven while you cook another batch. Serve pancakes with pancetta or blueberries, drizzled with extra maple syrup.','10 mins','20 mins'),
(204,'spinach pancakes with harissa yogurt & poached eggs','https://www.bbcgoodfood.com/recipes/spinach-pancakes-with-harissa-yogurt-poached-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Spinach-pancakes-with-harissa-yogurt-and-poached-eggs-e5b0343.jpg','Make these spiced green pancakes with poached eggs for brunch – they not only taste great, but boast the added benefits of iron and vitamin K',2,2,1,1,2,'Cook the spinach in a non-stick frying pan over a medium heat for a few minutes until wilted. Transfer to a food processor, then return the pan to the heat and melt the butter. Cool the butter for a minute, then pour into the food processor with the spinach. Crack in one egg and blitz until the spinach is finely chopped (a few green flecks are fine). Mix the flour, nutmeg and baking powder together, then add to the food processor and blitz to combine. Pour in 250ml milk and blitz for another 1-2 mins until a batter forms. If you prefer thinner pancakes, add another 50ml milk. Pour the batter into a jug or bowl. Mix the yogurt with the harissa in a small bowl to taste. Set aside. Return the pan to a medium-low heat. When the residual butter in the pan is sizzling, pour in a ladleful of batter and cook for 2 mins until the top of the pancake is nearly set. Flip and cook for another 1 min until cooked and lightly golden. Transfer to a baking tray inside a low oven to keep warm. Repeat with the rest of the batter, making four to six pancakes. Crack the remaining eggs into ramekins. Bring a pan of water to a simmer, add a drop of vinegar and stir to create a gentle whirlpool. Tip in one egg and cook for 3-4 mins until the white is set. Lift onto kitchen paper to drain using a slotted spoon. Repeat with the remaining egg. Top the pancakes with the harissa yogurt, poached eggs and a sprinkling of nigella seeds, if you like.','15 mins','12 mins - 18 mins'),
(205,'vegan banana pancakes','https://www.bbcgoodfood.com/recipes/vegan-banana-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banana_pancakes_0-8be4928.jpg','Make these foolproof vegan banana pancakes. A guaranteed crowd pleaser with a fluffy, light texture, you''d never know they’re dairy and egg-free',14,12,1,1,2,'Mash the banana in a mixing bowl. Stir in the sugar, salt and oil. Add the flour and baking powder and mix thoroughly. Make a well in the centre and gradually whisk in the milk. The batter should be a thick, droppable consistency. Heat a little oil in a frying pan over a medium heat. Add 2 tbsp of the batter to make American-style pancakes. You will be able to make about 4-5 at a time. Fry on each side for 2-3 mins until golden. Serve with syrup, sliced banana and berries, if you like.','10 mins','12 mins'),
(206,'quick crêpes suzette','https://www.bbcgoodfood.com/recipes/quick-crepes-suzette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-8731_12-811cb26.jpg','A simple way to celebrate Pancake Day without piling on the pounds',5,4,1,1,2,'Tip the sugar into a microwaveable bowl and stir in 3 tbsp orange juice to dampen. Microwave on High for 3-4 mins until you have a bubbling caramel. Remove the bowl from the microwave (watch out – it will be very hot indeed), then add the rest of juice, zest and butter. Return to the microwave and blast for 1 min, stir, then cook for 1 more min until the caramel has dissolved into the juice and you have a syrupy sauce. Set aside. Heat pancakes in the microwave according to pack instructions. Serve each folded into quarters with sauce poured over, a spoonful of crème fraîche and orange wedges, if you like.','5 mins','15 mins'),
(207,'buckwheat galettes','https://www.bbcgoodfood.com/recipes/buckwheat-crepe-madames','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/buckwheat-crepe-madames-32a1c0f.jpg','Enjoy these buckwheat pancakes with eggs, cheese and ham for breakfast or brunch. They''re good for Pancake Day or something different at the weekend',6,4,1,1,2,'Mix the flour, 1 egg, the milk and a pinch of salt in a jug or bowl. Set aside for 30 mins, or up to 3 hrs. Mash together the mustard, cream and cheese in another bowl. Heat the oven to 200C/180C fan/gas 6, and line two baking trays with baking parchment or foil. Melt the butter in a large frying pan, then once foaming, add enough batter to just cover the pan, swirling it to cover the surface in a thin layer (pour any excess back into the batter bowl). Cook until the surface is set and the underside is browning, carefully flip and cook for another minute or 2, then take off the heat. Spoon a quarter of the cheese mixture onto the middle of the pancake, using the spoon to create space in the centre to hold an egg. Crack one into the space and lay a few pieces of ham around the edges. Fold each side of the pancake in towards the centre to make a square. Cook in the pan for another 30 secs-1 min, then transfer to a baking tray. Repeat with the rest of the pancakes, then bake for 6-7 mins until the egg whites are set. Serve with fried mushrooms or wilted spinach, if you like.','20 mins','30 mins'),
(209,'eggs benedict pancakes','https://www.bbcgoodfood.com/recipes/eggs-benedict-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/eggs-benedict-pancakes-bebe955.jpg','Try an indulgent twist on a classic breakfast dish with our eggs benedict pancakes. This easy but impressive stack makes a filling brunch for a crowd',12,4,1,1,2,'Whisk all of the pancake ingredients together with some seasoning until smooth. Put a small knob of butter in a large non-stick frying pan over a medium-low heat and cook until melted and foaming. Pour 2 tbsp of the mixture into the pan and use the back of the spoon to shape it into a 8-9cm round disc. Depending on the size of your pan, you may be able to get 2 or 3 pancakes to cook at the same time. Cook for 2-3 mins on the first side, then flip over and cook for another 1 min. Heat oven to its lowest setting. Stack up the cooked pancakes on a baking tray and keep them warm in the oven while you cook the rest. Bring a large pan of water to the boil. Crack each egg into a ramekin, then carefully lower into the water. Poach for 2-3 mins until the whites are set and the yolks are still runny. Drain and repeat with the other eggs. Warm the hollandaise following pack instructions. To serve, stack the pancakes with a layer of hollandaise, the ham and spinach leaves in between them, then top with a poached egg. Spoon over more hollandaise and top with chives and black pepper.','10 mins','20 mins'),
(210,'almond crêpes with avocado & nectarines','https://www.bbcgoodfood.com/recipes/almond-crepes-avocado-nectarines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/almond-crepes-with-avocado-nectarines-a5cab2e.jpg','These simple, fruit-topped pancakes will keep you going all morning yet they''re gluten-free and low in carbs ',5,2,1,1,2,'Beat one egg and 1 1 /2 tbsp of the almonds in a small bowl with 1 tbsp water. Heat 1 tsp oil in a large non-stick frying pan over a medium heat and pour in the egg mixture, swirling the pan to evenly cover the base. Cook until the mixture sets and turns golden on the underside, about 2 mins. (There is no need to flip it over.) Turn it out onto a plate and make another one with 1 tbsp water, the remaining egg, oil and almonds. Top each crêpe with the avocado, nectarines and pomegranate, and squeeze over the lime at the table.','10 mins','5 mins'),
(211,'easy pancakes','https://www.bbcgoodfood.com/recipes/easy-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273477_8-ad36e3b.jpg','Learn a skill for life with our foolproof easy crêpe recipe that ensures perfect pancakes every time – elaborate flip optional',6,12,1,1,2,'Put 100g plain flour, 2 large eggs, 300ml milk, 1 tbsp sunflower or vegetable oil and a pinch of salt into a bowl or large jug, then whisk to a smooth batter. Set aside for 30 mins to rest if you have time, or start cooking straight away. Set a medium frying pan or crêpe pan over a medium heat and carefully wipe it with some oiled kitchen paper. When hot, cook your pancakes for 1 min on each side until golden, keeping them warm in a low oven as you go. Serve with lemon wedges and caster sugar, or your favourite filling. Once cold, you can layer the pancakes between baking parchment, then wrap in cling film and freeze for up to 2 months.','10 mins','20 mins'),
(212,'chocolate pancakes','https://www.bbcgoodfood.com/recipes/chocolate-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-pancake-0774dff.jpg','Start your morning with these indulgent chocolate pancakes. Top with cream and berries, if you like – they''re also great for dessert',46,16,1,1,2,'Tip the flour, cocoa powder, baking powder and sugar into a large bowl with a small pinch of salt. Add the eggs and whisk until smooth. Gradually whisk in the milk until you have a smooth batter, then mix in the chocolate chips. Heat a splash of the oil and a little of the butter in a non-stick frying pan until sizzling. Cook the pancakes, using 2 tbsp batter for each, for 2 mins until bubbles start to form on the surface. Flip and cook for 2 mins more (you''ll need to do this in batches). Keep the pancakes warm in a low oven while you cook.  To serve, whip the cream with the icing sugar and vanilla until thick. Top the pancakes with the whipped cream, berries and maple syrup, if using.','10 mins','20 mins'),
(213,'banana & cinnamon pancakes with blueberry compote','https://www.bbcgoodfood.com/recipes/banana-cinnamon-pancakes-blueberry-compote','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-banana-cinnamon-pancakes-with-blueberry-compote-da3e263.jpg','Make a batch of these pancakes for a delicious low-cal breakfast. With wholemeal flour to make them more filling, they''re topped with an easy blueberry sauce',4,4,1,1,2,'Tip the flour and cinnamon into a bowl, then break in the whole eggs, pour in the milk and whisk together until smooth. Stir in the banana. In a separate bowl, whisk the egg whites until light and fluffy, but not completely stiff, then fold into the pancake mix until evenly incorporated. Heat a small amount of oil in a large non-stick frying pan, then add a quarter of the pancake mix, swirl to cover the base of the pan and cook until set and golden. Carefully turn the pancake over with a palette knife and cook the other side. Transfer to a plate, then carry on with the rest of the batter until you have four. To make the compote, tip the berries in a non-stick pan and heat gently until the berries just burst but hold their shape. Serve two warm pancakes with half the berries, then scatter with the mint leaves and sprinkle with a little cinnamon. Chill the remaining pancakes and compote and serve the next day. You can reheat them in the microwave or in a pan.','10 mins','15 mins'),
(214,'classic pancakes','https://www.bbcgoodfood.com/recipes/classic-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-881465_10-7cc4719.jpg','You can use this foolproof classic batter recipe to create a base for sweet or savoury pancakes',43,8,1,1,2,'Sift the flour and a pinch of salt into a bowl. Make a well in the centre with the back of a spoon then break in the egg and pour in half the milk. Whisk together, gradually incorporating the flour to make a smooth thick batter. Beat thoroughly to remove any lumps, then stir in the rest of the milk. Heat a little oil or butter in a medium frying pan, then tip off the excess into a bowl. Pour about 2 tablespoons of batter into the pan, tilting the pan as you pour, until the batter thinly coats the base. Cook over a moderate heat for 30 seconds to one minute until golden brown on the underside. Flip over the pancake with a palette knife, and cook the other side until it is golden brown. Slide the pancake out of the pan on to a plate, heat a little more oil or butter and cook the remaining pancakes one at a time in the same way. If preparing in advance, cook and stack the pancakes. Reheat in the microwave on Medium (750W) for about 2 minutes. Alternatively, heat the oven to 180C/gas 4/fan 160C. Wrap the pancakes in foil and warm them through in the oven for 10 minutes.','30 mins','No Time'),
(215,'scotch pancakes','https://www.bbcgoodfood.com/recipes/scotch-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scotch-pancakes_1-373843d.jpg','Our simple Scotch pancakes make a quick and easy breakfast that the whole family will love. Serve with butter and maple syrup for the perfect brunch treat',47,10,1,1,2,'Add the dry ingredients to a mixing bowl. Beat the egg and milk into the mixture with a handheld or electric whisk. Heat a non-stick frying pan over a medium heat and add some oil. Spoon in a small ladleful of batter to make a pancake about 10cm across. Fry for about 1 min until the edges of the pancake are firm and bubbles have started to appear on the surface. Turn over and fry for another 30 secs till golden on both sides. Serve with your favourite toppings.','10 mins','15 mins'),
(216,'sour cream crêpes with seville orange marmalade & amaretto sauce','https://www.bbcgoodfood.com/recipes/sour-cream-crepes-seville-orange-marmalade-amaretto-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sour-cream-crepes-b9b23f8.jpg','Enjoy moreish sour cream pancakes with our Seville orange, vanilla & cardamom marmalade for a lovely, bittersweet flavour. An indulgent breakfast or dessert',11,4,1,1,2,'To make the pancakes, sift the flour into a bowl with a small pinch of salt and sugar and make a well in the middle. Crack the eggs into the well and whisk them into the flour. Mix the sour cream with 150ml water and pour this in too, whisking to incorporate until you have a smooth batter the consistency of double cream – add a splash more water or milk if needed. Leave to rest for 30 mins. For the sauce, whisk the cornflour with the orange juice until smooth. Warm it in a pan, stirring to thicken, for around 2 mins. Add the marmalade, amaretto and lemon juice and cook for a few mins more. Gradually add the butter, whisking until you have a glossy sauce. Stir the melted butter into the batter, then grease a non-stick pan and place over a high heat. Stir the batter, then ladle some into the pan. Swirl it around the pan and cook until it slips away from the pan when you shake it (a couple of mins). Use a spatula to flip the pancake and cook the other side until it comes away and is golden and caramelised in patches. Remove to a plate and keep warm, either covered in foil or in a low oven. Repeat with the rest of the batter until you have a pile of pancakes. Dust each one with a little caster sugar and top with the marmalade amaretto sauce. Fold into four, top with a bit more sauce, scatter over the toasted almonds and serve.','15 mins','15 mins'),
(217,'perfect pancakes recipe','https://www.bbcgoodfood.com/recipes/perfect-pancakes-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/perfect-pancakes-ad7a753.jpg','An easy pancake batter recipe with tips on how to make the best pancakes every time with sweet or savoury toppings',5,8,1,1,2,'Put 100g plain flour and a pinch of salt into a large mixing bowl. Make a well in the centre and crack 2 eggs into the middle. Pour in about 50ml from the 300ml of semi-skimmed milk and 1 tbsp sunflower oil then start whisking from the centre, gradually drawing the flour into the eggs, milk and oil. Once all the flour is incorporated, beat until you have a smooth, thick paste. Add a little more milk if it is too stiff to beat. Add a good splash of milk and whisk to loosen the thick batter. While still whisking, pour in a steady stream of the remaining milk. Continue pouring and whisking until you have a batter that is the consistency of slightly thick single cream. Heat the pan over a moderate heat, then wipe it with oiled kitchen paper. Ladle some batter into the pan, tilting the pan to move the mixture around for a thin and even layer. Quickly pour any excess batter into the mixing bowl, return the pan to the heat. Leave to cook, undisturbed, for about 30 secs. If the pan is the right temperature, the pancake should turn golden underneath after about 30 secs and will be ready to turn. Hold the pan handle, ease a palette knife under the pancake, then quickly lift and flip it over. Make sure the pancake is lying flat against the base of the pan with no folds, then cook for another 30 secs before turning out onto a warm plate. Continue with the rest of the batter, serving them as you cook or stack onto a plate. You can freeze the pancakes for 1 month, wrapped in cling film or make them up to a day ahead.','5 mins','25 mins'),
(218,'american-style pineapple & banana pancakes','https://www.bbcgoodfood.com/recipes/american-style-pineapple-banana-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273459_8-9cfc2c2.jpg','These American-style pancakes are easier to make than traditional ones',43,4,1,1,2,'Roughly chop the pineapple and slice the banana. Tip the flour, baking powder, cinnamon and sugar into a bowl and mix well. Make a well into the centre and crack in the egg, then gradually mix the egg into the flour, adding the milk gradually to make a soft batter. Stir in the pineapple and banana. Heat a little oil in a non-stick frying pan, add the batter in heaped tablespoonsful, well apart to allow them to spread. When bubbles appear on the surface, flip the pancakes over and cook until light golden. Cook all the pancakes and keep them warm. Serve 2-3 pancakes with a spoonful of yogurt and a little maple syrup poured over.','20 mins','No Time'),
(219,'gluten-free pancakes','https://www.bbcgoodfood.com/recipes/gluten-free-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273481_8-134247e.jpg','Use special flour to make these quick and easy crêpes and safely cater for those on a gluten-free diet. Perfect for both sweet and savoury toppings',44,6,1,1,2,'Put the flour in a bowl and make a well in the centre. Crack the egg in the middle and pour in a quarter of the milk. Use an electric or balloon whisk to thoroughly combine the mixture. Once you have a paste, mix in another quarter and once lump free, mix in the remaining milk. Leave to rest for 20 mins. Stir again before using. Heat a small non-stick frying pan with a knob of butter. When the butter starts to foam, pour a small amount of the mixture into the pan and swirl around to coat the base – you want a thin layer. Cook for a few mins until golden brown on the bottom, then turn over and cook until golden on the the other side. Repeat until you have used all the mixture, stirring the mixture between pancakes and adding more butter for frying as necessary. Serve with agave syrup and a squeeze of orange juice or your pancake filling of choice.','5 mins','25 mins'),
(220,'carrot cake overnight oats','https://www.bbcgoodfood.com/recipes/carrot-cake-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-cake-oats-85f0f96.jpg','Give overnight oats a tasty boost with our indulgent carrot cake version. This simple, healthy breakfast recipe takes just 10 minutes to prepare',16,1,1,1,2,'Mix the grated carrot, mixed spice and cinnamon with the oats, 150ml water and a pinch of salt, then cover and chill in the fridge overnight. The next day, stir in the honey and sultanas then top with Greek yogurt.','10 mins','No Time'),
(221,'overnight oats','https://www.bbcgoodfood.com/recipes/overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/overnight-oats-32a2747.jpg','Adapt this recipe for easy overnight oats to suit your tastes. You can add dried fruit, seeds and nuts, grated apple or pear or chopped tropical fruits for the perfect healthy breakfast',16,1,1,1,2,'The night before serving, stir the cinnamon and 100ml water (or milk) into your oats with a pinch of salt. The next day, loosen with a little more water (or milk) if needed. Top with the yogurt, berries, a drizzle of honey and the nut butter.','10 mins','N/A'),
(222,'banana overnight oats','https://www.bbcgoodfood.com/recipes/banana-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Banana-Overnight-Oats-16f2208.jpg','Rustle up these easy overnight oats made with bananas, nut butter and almonds for a tasty breakfast. Prepped the night before, they''re ideal for busy mornings',12,2,1,1,2,'Mash 1 banana in a bowl with a fork until smooth. Stir in the oats, cinnamon, maple syrup, milk and peanut butter. Mix well, then cover and chill overnight. The next morning, stir the porridge, adding another splash of milk if the mixture is quite stiff. Divide between two bowls. Slice the remaining banana and scatter this over the porridge, drizzle with more nut butter and sprinkle over the almonds. Top with spoonfuls of yogurt, if using, and sprinkle with a pinch more cinnamon before serving.','5 mins','No Time'),
(223,'peanut butter overnight oats','https://www.bbcgoodfood.com/recipes/pbj-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pbj-oats-ddaadaa.jpg','Make a bowl of overnight oats that''s indulgent yet healthy with creamy peanut butter and tangy raspberries. Or make it in a jar and take it to work',16,1,1,1,2,'Stir the frozen raspberries into your oats with 150ml water and a pinch of salt, then cover and chill in the fridge overnight. The next day, mix in the maple syrup, then top the oats with the peanut butter.','5 mins','No Time'),
(224,'tropical overnight oats','https://www.bbcgoodfood.com/recipes/tropical-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tropical-overnight-oats-186ecc3.jpg','Give your breakfast a tropical twist with our overnight oats. This healthy, low-fat recipe takes minutes to prepare and can be made in a jar to take to work',16,1,1,1,2,'Soak the oats in 150ml water with a pinch of salt, then cover and chill in the fridge overnight. The next day spoon half the oats into a bowl. Layer with the coconut yogurt, sliced kiwis, the pulp of the passion fruit and the rest of the oats, then top with the toasted mixed seeds.','10 mins','No Time'),
(225,'raspberry kefir overnight oats','https://www.bbcgoodfood.com/recipes/raspberry-kefir-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Raspberry-kefir-overnight-oats-0e63a9b.jpg','Wake up to these healthy, gut-friendly breakfast oats. Prep in just 10 minutes the night before, then top with a layer of berries, dark chocolate and seeds',4,2,1,1,2,'Tip the oats, 1 tbsp of the hemp seeds and the chia seeds into a bowl, then mix well. Stir in the kefir yogurt and milk. Cover and chill overnight. The mixture will thicken overnight as it soaks. The next morning, spoon into two glass tumblers and top with the raspberries, remaining hemp seeds, the pumpkin seeds and chocolate.','10 mins','No Time'),
(226,'vanilla-almond chia breakfast bowl','https://www.bbcgoodfood.com/recipes/vanilla-almond-chia-breakfast-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/porridge-chia-seeds-b83eb49.jpg','This fibre-packed oat pot is similar to a bircher. The combination of porridge oats, blueberries, yogurt, seeds and nuts will keep you fuller for longer',45,2,1,1,2,'Mix all the porridge ingredients in a bowl and leave to soak for at least 20 mins. Once the oats have softened, stir through half the blueberries. If the porridge is too dry, add a little water. Divide the mixture between 2 bowls and top each with the remaining berries, almonds and honey.','5 mins','No Time'),
(228,'black forest bircher','https://www.bbcgoodfood.com/recipes/black-forest-bircher','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/black-forest-bircher-f1db3e8.jpg','A clever twist on your standard Bircher that seems like pure indulgence but counts for one of your five-a-day and is rich in fibre too',6,4,1,1,2,'Combine the pears, oats, cacao, yogurt, milk and maple syrup in a bowl. Divide between four bowls (or containers if you’re taking it to work). Top each serving with some cherries, 1 tbsp yogurt and a little extra maple syrup, if you like. Finely grate the chocolate over the Bircher, giving each serving a light dusting. Eat straight away or chill in the fridge for up to 2 days.','10 mins','No Time'),
(229,'orange & blueberry bircher','https://www.bbcgoodfood.com/recipes/orange-blueberry-bircher','https://images.immediate.co.uk/production/volatile/sites/30/2016/05/orange-blueberry-bircher-0b11aea.jpg','Soaking oats and seeds overnight makes them easily digestible. Add in the fruit and you''ve got a nutritious start to the day with the right kind of fats, fibre, vitamins and minerals',5,2,1,1,2,'Mix 70g oats and 2 tbsp golden linseeds with the zest of 1 /2 an orange. Pour over 300ml boiling water and leave overnight. The next day, stir in three-quarters of a 175g tub of yogurt, spoon into glasses or bowls, top with 2 peeled and chopped oranges, the remaining yogurt and 4 handfuls blueberries from a 150g pack.','5 mins','No Time'),
(230,'berry bircher','https://www.bbcgoodfood.com/recipes/berry-bircher','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/berry-bircher-4b8067d.jpg','Overnight oats that are low in fat and take 5 minutes to prepare. Pack your breakfast with frozen raspberries, bio yogurt and golden linseeds for a delicious and healthy start to the day',5,2,1,1,2,'Tip the oats and seeds into a bowl, and pour over 200ml boiling water and stir well. Add the bananas and three-quarters of the raspberries (chill the remainder), mash together, then cover and chill overnight. The next day, layer the raspberry oats in two tumblers or bowls with the yogurt, top with the reserved raspberries and serve.','5 mins','No Time'),
(231,'bircher muesli with apple & banana','https://www.bbcgoodfood.com/recipes/bircher-muesli-apple-banana','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bircher-museli-with-apple-banana-9244ccc.jpg','Soaking oats and seeds overnight makes them easier to digest, and the muesli will be extra creamy. Great for a quick breakfast straight from the fridge',48,2,1,1,2,'Put the grated apple in a bowl and add the oats, seeds, half the nuts and the cinnamon. Toss together well. Stir in the yogurt and 100ml cold water, cover and chill for several hours or overnight. Spoon the muesli into two bowls and top with the sliced banana, sultanas and remaining nuts.','5 mins','No Time'),
(232,'blueberry bircher pots','https://www.bbcgoodfood.com/recipes/blueberry-bircher-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-133483_12-3e4fb42.jpg','Avoid the lure of the muffin and latte. These snacks help beat off the mid-morning munchies',5,1,1,1,2,'Grate 1 small apple and mix with 2 tbsp whole oats and 2 tbsp low-fat natural yogurt. Layer in a pot with some blueberries.','10 mins','No Time'),
(233,'pistachio nut & spiced apple bircher muesli','https://www.bbcgoodfood.com/recipes/pistachio-nut-spiced-apple-bircher-muesli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bircher-16f2da2.jpg','A balanced, filling breakfast bowl with oats and apple in low-fat yogurt, cinnamon and nutmeg and a nutty pomegranate topping',45,2,1,1,2,'Mix all the base ingredients, except the yogurt, with 150ml water and leave to soak for at least 20 mins or overnight, if possible. Once the oats have softened, stir through the yogurt, then divide the mixture between 2 bowls. Sprinkle half of the topping over each bowl and serve.','10 mins','No Time'),
(234,'pear & blueberry breakfast bowl','https://www.bbcgoodfood.com/recipes/pear-blueberry-breakfast-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/breakfast-bowl-c3f5d1b.jpg','A brunch-friendly oat and fruit bowl with yogurt and crunchy seeds - pack in 2 of your 5-a-day in the morning',5,1,1,1,2,'Grate the pear into a bowl and add the oats, half the yogurt, the milk and most of the seeds. Leave for 5-10 mins, then check the consistency and dilute with a little more milk or water if it is too thick. Spoon on the remaining yogurt, pile on the berries and remaining seeds, then serve.','10 mins - 15 mins','No Time'),
(235,'weaning recipe: baby bircher','https://www.bbcgoodfood.com/recipes/weaning-recipe-baby-bircher','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bircher-f8bbf99.jpg','Once your baby is used to purées, move them onto a more textured breakfast. This weaning Bircher combines delicious fruit, oats, chia seeds and milk',44,2,1,1,2,'Mix together the fruit, oats, chia seeds and milk. If baby is used to soft foods and purées, you’ll need to steam the fruit (see tip below). If baby is used to more texture then you can peel and finely grate the apple or pear.  Soak for an hour, covered in the fridge – the Bircher is ready to serve when the chia seeds are completely soft and gel-like. For babies, this should be eaten on the day of making, for toddlers, you can leave it in the fridge overnight and serve for breakfast if you like. ','5 mins','No Time'),
(245,'turmeric smoothie bowl','https://www.bbcgoodfood.com/recipes/turmeric-smoothie-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/turmeric-smoothie-4a1d6e9.jpg','Full of warming and nourishing ingredients, this creamy breakfast bowl can be ready in just 10 minutes',21,2,1,1,2,'Peel the turmeric root, if using, and grate. Put all ingredients in a blender with 600ml water and blend until smooth. Serve in a bowl with chia seeds or some chopped nuts sprinkled over.','10 mins','No Time'),
(246,'green goddess smoothie bowl','https://www.bbcgoodfood.com/recipes/green-goddess-smoothie-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-goddess-smoothie-bowl-3a11855.jpg','Add a splash of colour to your morning with this vibrant breakfast bowl of blended avocado, spinach and nut butter, topped with fresh fruit and seeds',6,2,1,1,2,'Slice the bananas and arrange over a small baking tray lined with parchment. Freeze for 2 hrs until solid. (You can now transfer the banana slices to a freezer bag and freeze for 3 months, or continue with the recipe.) For the seed mix, heat oven to 180C/160C fan/gas 4 and line a baking tray with parchment. Tip the seeds, coconut and almonds into a bowl, add the cinnamon and drizzle over the honey, agave or maple syrup. Toss until everything is well coated, then scatter over the baking tray in an even layer. Bake for 10-15 mins, stirring every 5 mins or so, until the seeds are lightly toasted. Leave to cool. Will keep in an airtight container for up to 1 month. Put the avocado, mango, spinach, milk, nut butter, frozen banana slices and honey (if using) in a blender and whizz to a thick smoothie consistency – you may have to scrape down the sides with a spoon a few times. Divide between two bowls and arrange the fruit on top. Scatter 1-2 tbsp of the seed mix over each bowl and eat straight away.','15 mins','10 mins - 15 mins'),
(247,'green rainbow smoothie bowl','https://www.bbcgoodfood.com/recipes/green-rainbow-smoothie-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-rainbow-smoothie-bowl-6b74cc0.jpg','Start as you mean to go on with this wholesome breakfast bowl of berries and avocado',49,2,1,1,2,'Put the spinach, avocado, mango, apple and almond milk in a blender, and blitz until smooth and thick. Divide between two bowls and top with the dragon fruit and berries.','20 mins','No Time'),
(248,'tropical smoothie bowl','https://www.bbcgoodfood.com/recipes/tropical-smoothie-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tropical-smoothie-bowl-37e6a75.jpg','Add a taste of the tropical to your breakfast with our easy vegan, mango and pineapple smoothie bowl',4,2,1,1,2,'Put the mango, pineapple, bananas, yogurt and coconut milk in a blender, and blitz until smooth and thick. Pour into two bowls and decorate with the passion fruit, blueberries, coconut flakes and mint leaves. Will keep in the fridge for 1 day. Add the toppings just before serving.','20 mins','No Time'),
(249,'cranberry & raspberry smoothie','https://www.bbcgoodfood.com/recipes/cranberry-raspberry-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201507_12-908c372.jpg','A low fat, vitamin C-packed smoothie to start your day',50,4,1,1,2,'Place all the ingredients into a blender and pulse until smooth. Pour into glasses and serve topped with fresh mint.','10 mins','N/A'),
(250,'banana, clementine & mango smoothie','https://www.bbcgoodfood.com/recipes/banana-clementine-mango-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201463_12-6bca4fd.jpg','Kick-start your day with this deliciously refreshing drink, made with fresh fruit and yogurt. It''s rich in vitamin C and counts as one of your 5-a-day',51,6,1,1,2,'Halve the clementines and squeeze out the juice – you should have about 600ml/1 pint. (This can be done the night before.) Peel the mangoes, slice the fruit away from the stone in the centre, then chop the flesh into rough pieces. Peel and slice the bananas. Put the clementine juice, mango flesh, bananas, yogurt and ice cubes into a liquidiser and blend until smooth. Pour into six glasses and serve. (You might need to make this in two batches, depending on the size of your liquidiser.) If you don’t add ice cubes, chill in the fridge until ready to serve.','15 mins - 25 mins','N/A'),
(251,'strawberry & banana almond smoothie','https://www.bbcgoodfood.com/recipes/strawberry-banana-almond-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoothie-b065afd.png','Kick-start your day with this healthy fruit smoothie, made extra filling with ground almonds and calcium-packed dairy',4,1,1,1,2,'Slice the banana into the bowl of a food processor, or a jug if using a hand blender. Add the strawberries, yogurt, milk and ground almonds, and blitz until completely smooth. Pour into a glass and enjoy.','5 mins','No Time'),
(252,'avocado & strawberry smoothie','https://www.bbcgoodfood.com/recipes/avocado-strawberry-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/berry-smoothie-5543fc2.jpg','A creamy breakfast-friendly blend that''s high in calcium and low in calories',45,2,1,1,2,'Put all the ingredients in a blender and whizz until smooth. If the consistency is too thick, add a little water.','5 mins','No Time'),
(255,'banana, honey & hazelnut smoothie','https://www.bbcgoodfood.com/recipes/banana-honey-hazelnut-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1505_11-1a01aab.jpg','A delicious, dairy-free smoothie that makes a satisfying snack or filling breakfast drink, made with soya milk, fruit, warming nutmeg and chopped nuts',5,2,1,1,2,'Blend the banana with soya milk, honey and a little grated nutmeg until smooth. Pour into two large glasses and top with the toasted, chopped hazelnuts to serve.','5 mins','No Time'),
(256,'black bean chilli','https://www.bbcgoodfood.com/recipes/black-bean-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-281701_11-5a22034.jpg','This flavoursome vegetarian chilli is great for casual entertaining - just lay everything out and let people add their own toppings',5,4,1,1,5,'In a large pot, heat the olive oil and fry the garlic and onions for 5 mins until almost softened. Add the pimenton and cumin, cook for a few mins, then add the vinegar, sugar, tomatoes and some seasoning. Cook for 10 mins. Pour in the beans and cook for another 10 mins. Serve with rice and the accompaniments of your choice in small bowls.','10 mins','30 mins'),
(257,'black bean chimichurri salad','https://www.bbcgoodfood.com/recipes/black-bean-chimichurri-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blackbean-fef8ffa.jpg','This simple lunchbox-friendly salad of storecupboard pulses is dressed with chimichurri- a South American parsley, coriander and chilli sauce',24,1,1,1,5,'Whizz all the chimichurri ingredients together in a blender or the small bowl of a food processor, until combined. Season to taste and set aside. In a bowl or plastic container toss together all the salad ingredients. Drizzle over the dressing, or keep it separate in a small jar to stir through the salad just before eating.','15 mins','No Time'),
(258,'pork & black bean tacos','https://www.bbcgoodfood.com/recipes/pork-black-bean-tacos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-black-bean-tacos-7dedb5f.jpg','If you''ve got leftover pork from a Sunday roast, give these tasty tacos a try. Serve topped with black beans, avocado and a squeeze of lime',11,2,1,1,5,'Heat oven to 160C/140C fan/gas 3. Take the pork out of the fridge and allow to come to room temperature. Then put in a roasting tin and roast for 15 mins or until completely warmed through. Heat a non-stick frying pan over a medium heat. Brush the lime halves with 1 /2 tbsp oil and put them, cut-side down, in the pan. Leave to caramelise for a few mins, then set aside. Add the olive oil to the pan, followed by the cumin seeds and ground cumin, and allow to sizzle. Add half the onion, the garlic and coriander stalks, and season. Cook for 5-8 mins until soft and fragrant. Add the black beans and a splash of juice from their can, and cook, stirring, for about 8 mins until thickened. Squeeze in the juice from half a caramelised lime and stir to combine. Meanwhile, slice the avocado, squeeze over the juice of half a caramelised lime and season. Char the tortillas in a dry frying pan, or wrap in foil and put in the oven for a few mins to warm through. Serve them topped with the black beans, pork, onion, avocado and coriander leaves, with lime wedges on the side for squeezing over.','15 mins','20 mins'),
(259,'taco salad','https://www.bbcgoodfood.com/recipes/taco-salad','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Taco-salad-462c67b.jpg','Enjoy all the flavours of a taco night in this colourful Tex-Mex salad. Just like a taco, you can make it your own and add your favourite toppings',7,4,1,1,5,'Mix the red onion with half the lime juice and a pinch of salt in a small bowl, then set aside. Heat the oil in a frying pan over a medium-low heat, and crumble in the mince. Cook for 15-20 mins – the mince will release some liquid, but once this has bubbled away, it will sizzle in its own fat. At this point, stir or toss with a wooden spoon so the mince browns and caramelises all over. If the pan becomes too dry, add a drizzle more oil. When the mince is well toasted, sprinkle over the taco seasoning and cook for 2 mins, then stir through the tomato purée and pour over the rest of the lime juice and let it sizzle for another minute. Stir through the black beans and turn up the heat to medium, continuing to cook until everything is hot. Taste for seasoning, then set aside. To make the dressing, mix the lime juice with the soured cream and chilli sauce, if using, season, then set aside. Scatter a handful of tortilla chips into a bowl, then add a layer of lettuce, then the mince and black beans. Top with the avocado slices, chopped tomato, pickled red onions, along with any juices, and the feta. Drizzle with the dressing, then finally scatter over the coriander. Tuck more tortilla chips around the sides of the bowl, then serve.','20 mins','30 mins'),
(260,'black bean potato nachos','https://www.bbcgoodfood.com/recipes/black-bean-potato-nachos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/black-bean-f676175.jpg','These addictive black bean nachos make a great veggie sharing dish for a party or movie night with crispy potatoes, a fresh red pepper salsa and melty cheese',16,3,1,1,5,'Heat oven to 220C/200C fan/ gas 8. Line two large baking sheets with baking parchment. Brush the potato slices with oil, season and sprinkle over the paprika. Bake for 10-15 mins until completely crisp. Reduce oven to 200C/180C fan/gas 6. Transfer half the crisps to an ovenproof dish, layer with half the beans and cheese, then repeat. Return to the oven for 10 mins until the cheese melts. Meanwhile, mix all the salsa ingredients and season. Top the nachos with salsa and coriander.','15 mins','30 mins'),
(261,'spicy black bean tacos','https://www.bbcgoodfood.com/recipes/spicy-black-bean-tacos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spicy-black-bean-tacos-6ac283a.jpg','These vegetarian bean tacos, lightly spiced with cumin and smoked paprika, are topped with fresh guacamole and a pomegranate salsa.',10,4,1,1,5,'In a large frying pan, heat the oil and add the garlic. Fry until golden, then add the beans. Pour in the cider vinegar, honey and spices along with 1 tsp or more of salt, to taste. Cook until warmed through, crushing gently with the back of your wooden spoon, then set aside. The best way to make the guacamole is with a large stone pestle and mortar, but you can use a medium bowl and a flat-ended rolling pin instead. Crush the garlic, coriander and chilli into a rough paste. Scoop in the avocado with a little salt and crush roughly – you want it chunky, not smooth. Squeeze in the lime juice and set aside. Mix the salsa ingredients in a small bowl. Heat a griddle pan or steamer and quickly griddle the tortillas or steam a stack of them to warm up. Reheat the bean mixture. To serve, put 1-2 heaped tbsp of beans on a tortilla. Top with a big spoonful of guacamole and some salsa, hot sauce and a dollop of soured cream or yogurt.','15 mins','10 mins'),
(262,'spicy meatballs with chilli black beans','https://www.bbcgoodfood.com/recipes/spicy-meatballs-chilli-black-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spicy-meatballs-with-chilli-black-beans-9363d4f.jpg','Give your favourite meatballs a healthy makeover with this low fat, low calorie, low GI recipe with turkey mince, black beans and avocado',4,4,2,1,5,'First make the meatballs. Tip the mince into a bowl, add the oats, spring onions, spices and the coriander stalks, then lightly knead the ingredients together until well mixed. Shape into 12 ping-pong- sized balls. Heat the oil in a non-stick frying pan, add the meatballs and cook, turning them frequently, until golden. Remove from the pan. Tip the onion and garlic into the pan with the pepper and stir-fry until softened. Stir in the cumin and chilli paste, then pour in the stock. Return the meatballs to the pan and cook, covered, over a low heat for 10 mins. Stir in the tomatoes and beans, and cook, uncovered, for a few mins more. Toss the avocado chunks in the lime juice and serve the meatballs topped with the avocado and coriander leaves.','20 mins','25 mins'),
(263,'black bean tostadas with avocado salsa','https://www.bbcgoodfood.com/recipes/black-bean-tostadas-avocado-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-851472_11-b01f343.jpg','For a quick veggie health-boost make this speedy supper using flavourful corn tortillas, which make a really nice change from flour tortillas',5,4,1,1,5,'Heat oven to 200C/180C fan/gas 6. Brush the tortillas with a little of the oil and place in a single layer on baking sheets. Cook for 8 mins until crisp. In a large frying pan, heat the remaining oil. Add the onion and garlic, and cook for 5 mins. Add the spices, vinegar and honey. Cook for 2 mins more. Add the beans and seasoning, and heat through. Remove from the heat and mash the beans gently with the back of your spoon to a chunky purée. Spread some beans over the crispy corn tortillas, scatter with your choice of toppings and add a spoonful of crème fraîche to cool down, or a splash of chipotle Tabasco to spice it up.','10 mins','15 mins'),
(264,'smoky sausage & black bean chilli','https://www.bbcgoodfood.com/recipes/smoky-sausage-black-bean-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Smoky-sausage-black-bean-chilli-18eaec2.jpg','Gather round the campfire and serve this chilli one-pot with tortilla chips for scooping up the smoky beans and sausages. Or, enjoy with a jacket potato or rice',6,4,1,1,5,'Heat half the oil in a large pan and cook the sausages for 12-15 mins over a medium heat until brown all over and cooked through. Remove to a plate. Drizzle the rest of the oil into the pan and cook the onion and peppers for 10 mins, over a low heat stirring often until all the veg has softened. Stir in the garlic, all the spices and the chipotle paste, if using, and cook for another minute. Add the tomatoes, a canful of water, the ketchup and some seasoning. Bring to a simmer and cook for 20 mins half-covered with a lid or loosely covered with foil. Stir occasionally, topping up with water if the mixture starts to look too thick. Slice the cooked sausages into thick coins and return to the pan, then tip in the beans. Cook for another few minutes until heated through, then spoon into bowls and scatter with coriander leaves, if using, some grated cheese and a dollop of yogurt or soured cream. Serve with tortilla chips for scooping, as a filling for jacket potatoes, or over rice.','10 mins','1 hr'),
(457,'french apple tart','https://www.bbcgoodfood.com/recipes/french-apple-tart','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/French-apple-tart-ffb6034.jpg','Looking for a showstopper dessert for a dinner party? If you have the time, it’s well worth making a French apple tart, a classic shop-window patisserie.',7,8,2,1,6,'For the pastry, rub the butter into the flour, sugar and a pinch of salt in a bowl until crumbly. Mix in the egg until it forms a dough, then form into a puck shape. Cover and chill for at least 30 mins. Will keep chilled for two days. Heat the oven to 200C/180C fan/ gas 6. Roll the pastry out on a lightly floured surface to roughly the thickness of a £1 coin, and use to line a 23cm fluted tart tin, leaving some overhanging. Line with a disc of baking parchment big enough to cover the edges, and fill with some baking beans to weigh it down (use dried rice or lentils if you don’t have baking beans). Bake for 15 mins, then remove the parchment and beans and bake for 10-15 mins more until the pastry is biscuity. Trim away any overhanging pastry with a serrated knife. Set aside to cool. Meanwhile, set aside four of the apples, then peel, core and roughly chop the rest. Put them in a shallow saucepan with 2 tbsp water, all but 1 tbsp of the sugar and the alcohol, if using. Cover and cook over a low heat for 25-30 mins, stirring occasionally and adding more water if needed, until the apples have collapsed into a purée. Taste the mixture and sweeten with more sugar, if needed. Turn the oven up to 210C/190C fan/gas 8. Peel, core and halve the reserved apples, then cut into even-sized slices. Spread the apple purée over the base of the tart case, then arrange the apple slices in neat, concentric circles, starting from the outside. Brush the apples with butter, then scatter over the reserved sugar and bake for another 20-25 mins until golden. Mix the jam with 1 tbsp hot water from a freshly boiled kettle. When the tart has finished baking, glaze generously with the jam, then leave to cool a little. Serve warm or cold, dusted with icing sugar, if you like.','40 mins','1 hr'),
(265,'jerk sweet potato & black bean curry','https://www.bbcgoodfood.com/recipes/jerk-sweet-potato-black-bean-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1173503_12-677f8cf.jpg','Serve your vegetable curry Caribbean style, flavoured with thyme, jerk seasoning and red peppers – great with rice and peas',17,10,1,1,5,'Gently soften the diced onion in the sunflower oil in a big pan or casserole. Meanwhile, whizz together the roughly chopped onion, ginger, coriander stalks and jerk seasoning with a hand-held blender. Add to the softened onion and fry until fragrant. Stir in the thyme, chopped tomatoes, vinegar, sugar and stock cubes with 600ml water and bring to a simmer. Simmer for 10 mins, then drop in the sweet potatoes and simmer for 10 mins more. Stir in the beans, peppers and some seasoning, and simmer for another 5 mins until the potatoes are almost tender. Cool and chill for up to 2 days. To serve, gently heat through on the hob. Roughly chop most of the coriander leaves and stir in, then serve scattered with the remaining leaves.','50 mins','45 mins'),
(266,'spiced black bean & chicken soup with kale','https://www.bbcgoodfood.com/recipes/spiced-black-bean-chicken-soup-kale','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-black-bean-chicken-soup-with-kale-2fc2ea4.jpg','Use up leftover roast or ready-cooked chicken in this healthy and warming soup, spiced with cumin and chilli',33,4,1,1,5,'Heat the oil in a large saucepan, add the garlic, coriander stalks and lime zest, then fry for 2 mins until fragrant. Stir in the cumin and chilli flakes, fry for 1 min more, then tip in the tomatoes, beans and stock. Bring to the boil, then crush the beans against the bottom of the pan a few times using a potato masher. This will thicken the soup a little. Stir the kale into the soup, simmer for 5 mins or until tender, then tear in the chicken and let it heat through. Season to taste with salt, pepper and juice from half the lime, then serve in shallow bowls, scattered with the feta and a few coriander leaves. Serve the remaining lime in wedges for the table, with the toasted tortillas on the side. The longer you leave the chicken in the pan, the thicker the soup will become, so add a splash more stock if you can’t serve the soup straight away.','10 mins','15 mins'),
(267,'black bean, tofu & avocado rice bowl','https://www.bbcgoodfood.com/recipes/black-bean-tofu-avocado-rice-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/black-bean-tofu-avocado-rice-bowl-67d0fb0.jpg','Mix up your midweek meals with this vegetarian Mexican brown rice and grilled tofu dinner - swap for chunks of chorizo to make it meaty',6,4,1,1,5,'Heat the grill to High. Heat 1 tbsp oil in a frying pan, add the onion and cook, stirring, for 5 mins or so until soft. Add the garlic and sizzle for 30 secs more, then stir in the cumin and black beans. Cook for 5 mins until the beans start to pop and are hot through. Stir through the lime zest and juice, and season. While the beans cook, put the tofu in a bowl and gently toss through the remaining oil, the paprika and some seasoning. Line a baking tray with foil and arrange the tofu on top. Cook under the grill for 5 mins each side until charred all over. Heat the rice following pack instructions, then divide between bowls. Top with the beans, tofu, avocado, coriander and a wedge of lime. Add a few slices of chilli too, if you like it spicy.','20 mins','25 mins'),
(268,'jerk-seasoned pork & pineapple skewers with black beans & rice','https://www.bbcgoodfood.com/recipes/jerk-pork-pineapple-skewers-black-beans-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1011488_11-84671e7.jpg','Pop these healthy kebabs on a griddle or the BBQ – they''re packed with Creole spices and are a lean treat',10,4,1,1,5,'Mix together the pork, jerk seasoning, allspice, chilli sauce, if using, lime zest and juice, and some seasoning. Thread the pork and pineapple onto metal skewers (or pre-soaked wooden skewers) and brush with the oil. Cook rice following pack instructions. Drain well, then put back in the saucepan with the beans, stir and keep warm. Meanwhile, heat a griddle pan until very hot. Cook the skewers for 3 mins on each side until nicely charred and the pork is cooked through. Serve skewers with the beans and rice, extra chilli sauce, if you like, and lime wedges for squeezing over.','10 mins','10 mins'),
(269,'creamy black dhal with crispy onions','https://www.bbcgoodfood.com/recipes/creamy-black-dhal-crispy-onions','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-black-dhal-with-crispy-onions-e649d2b.jpg','This super-satisfying, slow cooker curry is packed with iron and fibre. Serve with a choice of tasty garnishes to turn your simple supper into a warming feast',6,4,1,1,5,'Soak the beans in cold water for 4 hrs (or overnight, if you like). Melt the butter or ghee in a large pan, then add the onions, garlic and ginger, and cook slowly for 10-15 mins until the onions are starting to caramelise. Stir in the spices, coriander stalks and 100ml water. Pour into the slow cooker (or leave in the pan if cooking on the hob). Add the passata and whole red chilli. Drain the beans and add these too, then top up with 400ml water. Season well, set the slow cooker to Low and cook for 5-6 hrs (or cover and cook for 2 hrs over a very low heat on the hob). Once cooked, the dhal should be very thick and the beans tender. Stir in the cream, check the seasoning and serve in bowls with naan bread, rice or in a jacket potato, with your choice of toppings. To freeze the dhal, cool completely, then divide into containers or sandwich bags. Freeze for up to 2 months, defrost and heat thoroughly before eating.','35 mins','6 hrs and 35 mins'),
(270,'mexican rice with chipotle pork & avocado salsa','https://www.bbcgoodfood.com/recipes/mexican-rice-chipotle-pork-avocado-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1096491_11-a76ca40.jpg','Ditch the takeaway and rustle up this smoky, spicy pork stir-fry with black beans, coriander and avocado',10,2,1,1,5,'Heat oven to 180C/160C fan/gas 4. Toss the pork with 1 tbsp of the chipotle paste, cumin, paprika, sugar and some seasoning. Spread on a baking tray, drizzle with the vegetable oil and bake for 20 mins until tender. Meanwhile, cook the rice following pack instructions until just cooked, then drain. Put back in the pan, add the beans and keep warm with a lid on. In a small bowl, toss the avocado with the red onion, most of the coriander, remaining chipotle paste and lime juice, then season. Serve the rice and beans with the pork, avocado salsa, jalapeño, lime wedges and remaining coriander.','10 mins','20 mins'),
(271,'cacio e pepe with runner beans','https://www.bbcgoodfood.com/recipes/cacio-e-pepe-runner-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cacio-e-pepe-with-runner-beans-e523207.jpg','Cacio e pepe or "cheese and pepper" is a Roman classic – an easy pasta sauce that uses pasta water for silky texture. Runner beans add freshness and bite',11,2,1,1,5,'Boil a pan of salted water, add the pasta and cook for 4 mins. Add the runner beans and cook together for 2 mins, then drain, reserving the water. Gently heat the oil and ¾ of the butter in a large, high-sided frying pan over a medium heat. Stir in the pepper with a wooden spoon and cook for 1 min, then add in a ladleful of the pasta water and whisk vigorously until you have the beginnings of a smooth sauce. Add in the rest of the butter, the pasta and the beans and half the cheeses, and stir, shaking the pan as you go to mix everything together and release starch from the pasta to make it extra creamy. Add in the rest of the cheese and shake some more until it melts and you have a silky smooth sauce. Ladle in more pasta water to loosen it if necessary, and make sure the pasta is cooked. Season with salt and serve straightaway with extra pecorino and pepper.','10 mins','15 mins'),
(272,'runner beans, chorizo & almonds','https://www.bbcgoodfood.com/recipes/runner-beans-chorizo-almonds','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Runner-beans-chorizo-and-almonds-cad7eae.jpg','Growing runner beans in the garden? Enjoy them at their very best in the late summer in this stunning light lunch or starter with chorizo, eggs and almonds',14,4,1,1,5,'Cook the beans in a pan of boiling salted water for 3-4 mins or until tender. Drain and set aside. Whisk the olive oil, vinegar and anchovies together until smooth. Season with black pepper and toss with the warm beans. Heat a dry frying pan over a medium heat and fry the chorizo for 2-3 mins until golden brown. Cook the eggs in a pan of simmering water for 6 mins 30 seconds. Drain, then rinse under cold running water to cool. Peel and halve. Spoon the beans onto plates and top with the chorizo and eggs. Sprinkle with the paprika, almonds and parsley, then serve.','10 mins','15 mins'),
(273,'runner bean tortilla with tomato salad','https://www.bbcgoodfood.com/recipes/runner-bean-tortilla-tomato-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/runner-bean-tortilla-with-tomato-salsa-e24130c.jpg','Swap potatoes for runner beans to make a fresh-tasting, healthy tortilla and serve with a tomato, red onion and basil salad to get three of your five-a-day',4,2,1,1,5,'Bring a pan of water to the boil, add the beans and cook for 5 mins or until just tender, then drain. Beat the eggs in a bowl with the lemon zest, garlic, olives and some pepper, then stir in the beans. Heat the oil in a 23cm non-stick frying pan. Pour in the egg mixture and gently stir for the first couple of mins so that you see the egg cooking and the raw egg flowing into the base of the pan as you move it. Leave to firm up for a few mins, then turn off the heat, cover and leave for 5 mins so that any raw egg on top cooks through. Put the lemon juice in a bowl and add the onion. Toss well, then add the tomatoes, basil and rocket (if using). Serve with wedges of tortilla, dusted with some parmesan if you like.','10 mins','15 mins'),
(274,'quick-fried runner beans with cheddar & hazelnuts','https://www.bbcgoodfood.com/recipes/quick-fried-runner-beans-cheddar-hazelnuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quick-fried-runner-beans-with-cheddar-hazelnuts-24e1c24.jpg','Looking to spruce up your side with minimum fuss? These crunchy runner beans tossed with cheese and nuts fit the bill',52,2,1,1,5,'In a hot wok or wide-based frying pan, heat the vegetable oil. Once smoking, add the banana shallots and runner beans, and stir-fry for 2 mins. Add the hazelnut oil and hazelnuts, and fry for a further 30 secs, then remove from the heat. Add the lemon zest, a good twist of black pepper and the cheddar, and toss together. Serve as a tasty side.','5 mins','3 mins'),
(333,'halloumi wraps with crunchy za''atar chips','https://www.bbcgoodfood.com/recipes/halloumi-wraps-with-crunchy-zaatar-chips','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Halloumi-wraps-4d13065.jpg','Dust chips with za’atar spices and wrap them in warm flatbreads with halloumi and spiced tzatziki for an easy, crowd-pleasing veggie dinner.',6,4,1,1,7,'Cut the potatoes into 1cm-thick chips – leave the skin on, or peel first, if you prefer. Tip into a pan of cold salted water and set aside while you make the flatbreads. Mix the yogurt and flour with a pinch of salt to make a soft dough. If it feels too sticky, add a little more flour. Knead gently for a minute or two until smooth and elastic, then divide into four pieces. Cover with a clean tea towel and set aside. Heat the oven to 200C/180C fan/gas 6. Bring the pan of chips to the boil, reduce the heat and simmer for 8-10 mins until the potatoes start to soften – you should be able to easily break a chip in half. Drain and leave to steam-dry in the colander for 5 mins. Drizzle a large baking tray with the veg oil and put in the oven for a few minutes to heat up. Carefully tip the chips into the tray of hot oil, season with salt and scatter with the za’atar and oregano. Use a fish slice to carefully turn the chips in the oil until well-coated, then bake for 25-30 mins, shaking the tray every 10 mins until crisp. Meanwhile, mix the ingredients for the tzatziki together, and chill until ready to serve. Will keep covered and chilled for a day. Flour a work surface and roll out each piece of dough into a roughly 25cm round. Heat a frying pan over a medium heat and cook each for 2-3 mins on each side until puffed up and golden in spots. Wrap in foil and keep warm in a low oven. Tip the chopped tomatoes and cucumber triangles into individual bowls and set aside. Squeeze half the lemon over the red onions and season with a little salt, scrunch together and set aside. Cut the remaining lemon half into wedges. Slice each halloumi block into eight pieces. Heat the olive oil in a frying pan over a medium-high heat and cook the halloumi for 1-2 mins on each side until soft and golden. Generously spread each flatbread with the tzatziki, then top with a handful of the chips, tomatoes, cucumbers, red onions and halloumi. Squeeze over a lemon wedge, then wrap up and serve with extra chips and tzatziki on the side.','25 mins','45 mins'),
(275,'three bean salad with mozzarella','https://www.bbcgoodfood.com/recipes/three-bean-salad-with-mozzarella','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Three-bean-salad-with-mozzarella-deb0114.jpg','This substantial salad features a delicious mix of cannellini, kidney and green beans, and is ideal for a summer picnic',4,4,1,1,5,'Boil or steam the green beans and carrots for 8-10 mins until just tender. Put the sliced onions in a bowl and pour over boiling water until just covered. Meanwhile, make the dressing. Mix all the ingredients together in a large bowl. Tip the cooked beans and carrots into the dressing along with the drained onions, canned beans and tomatoes, toss well, then add the basil and toss again. Serve scattered with the mozzarella and a grinding of black pepper, if you like. Will keep chilled for up to three days.','20 mins','10 mins'),
(276,'runner bean & prawn paella','https://www.bbcgoodfood.com/recipes/runner-bean-prawn-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paella_0-a1c619f.jpg','The prawns in this delicious paella may not be essential but they do make it extra special for a Saturday night supper with friends',4,4,1,1,5,'Heat the oil in a paella pan or large frying pan, add the onions and garlic, and fry for 5 mins. Push the onions over to one side, add the chicken and cook for 10 mins, turning occasionally, until the chicken is nicely coloured. Stir in the rice and paprika, then pour in the wine. Let it sizzle a bit, then stir in the saffron stock. Add the beans and pepper, and cook, stirring occasionally, for 15 mins until the rice is tender and has absorbed most of the stock. If it starts to look dry, add a splash more stock. Add the prawns for the final few mins, if using, until they turn from grey to pink. Season generously and allow to stand for 1-2 mins before serving.','10 mins','30 mins'),
(277,'runner beans with rocket & parmesan','https://www.bbcgoodfood.com/recipes/runner-beans-rocket-parmesan','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salad-52d235d.jpg','This light green salad with walnuts and Italian cheese makes a versatile side dish or starter. Add prosciutto for extra wow factor. ',7,4,1,1,5,'Mix the olive oil with the lemon juice, season and set aside. Boil a large pan of salted water, then cook the beans for about 8 mins until tender. Drain well and leave to cool a little. Toss the beans with half the dressing, the walnuts and rocket. Place in a serving dish and scatter with the Parmesan, then drizzle over the remaining dressing.','10 mins','5 mins'),
(278,'green bean casserole','https://www.bbcgoodfood.com/recipes/green-bean-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Green-bean-casserole-813ef09.jpg','Make this easy green bean and mushroom casserole as a veggie side to accompany a main course. You could add cheese to the white sauce if you fancy',12,6,1,1,5,'Heat 1 tbsp of the butter and the oil in a large pan and fry the mushrooms for 10-15 mins until golden. Stir in the green beans and cook for another 3-4 mins until the beans turn bright green. Scoop out the veg onto a plate. Heat the oven to 200C/180C fan/gas 6. Melt the remaining butter in the pan until foaming. Remove from the heat briefly and sprinkle over the flour, stirring into the butter to make a golden brown paste. Return to the heat and add the milk, a splash at a time, continually stirring until all the milk has been added and you get a smooth, thick white sauce. Season well. Stir the beans and mushrooms into the white sauce, and spoon into a medium ovenproof dish. Scatter over the crispy onions, then bake for 20-30 mins until bubbling and golden, covering if the onions brown too quickly.','10 mins','1 hr'),
(279,'thai shredded chicken & runner bean salad','https://www.bbcgoodfood.com/recipes/thai-shredded-chicken-runner-bean-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-295464_11-b8b4a0a.jpg','This low-fat yet satisfying salad is full of authentic Asian flavour and will add plenty of zing to al fresco dining',19,4,2,1,5,'Run a potato peeler down either side of the beans to remove any stringy bits. Cut into strips using a bean slicer, or on the diagonal into 2cm pieces. Cook the beans in simmering salted water for 4 mins or until tender but still bright green. Drain and put in a bowl with the chilli, shallots, lemongrass and ginger. Pull the chicken breasts into shreds using your fingers and add to the bowl. Make the dressing. Mix the coconut with the garlic, fish sauce, sugar, lime and chilli. Tear the mint and Thai basil over the chicken and toss everything together. Pile onto a plate and pour over the dressing. Serve with the lime to squeeze over and jasmine rice.','30 mins','5 mins'),
(280,'runner bean & prosciutto pasta','https://www.bbcgoodfood.com/recipes/runner-bean-prosciutto-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-163511_12-22ea0be.jpg','With just five ingredients, this is the perfect pasta dish to have up your sleeve on a lazy day',5,2,1,1,5,'Cook the tagliatelle in a large pan of boiling, salted water according to pack instructions. Four mins before the end of the cooking time, add the runner beans. Meanwhile, cut each slice of prosciutto into 5-6 pieces. Heat the oil in a frying pan, add the ham, then fry quickly until crisp. Drain the pasta and return to the pan with the crème fraîche, prosciutto, pan juices and plenty of black pepper, tossing everything together until the pasta is well coated.','10 mins','12 mins'),
(281,'charred courgettes, runner beans & ricotta','https://www.bbcgoodfood.com/recipes/charred-courgettes-runner-beans-ricotta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/charred-courgettes-runner-beans-ricotta-7203f1a.jpg','This vegetarian side dish of charred courgettes and runner beans sings summer and also has a small bite of hot chilli. It goes down well at barbecues',16,8,1,1,5,'Toast the seeds in a dry pan until they start to pop, then stir in the honey, chilli and a good pinch of sea salt. Once all the seeds are sticky and forming clusters, tip out onto a piece of baking parchment and leave to cool. Mix the lemon zest and some seasoning into the ricotta and set aside. Toss the courgettes and runner beans in the oil, then season. Fire up the barbecue or get a griddle pan searing hot on the hob. Working in batches, cook the veg until charred and softened but still a little crunchy. Spread the ricotta along the bottom of a large sharing platter and top with the veg. Squeeze over the lemon juice, then scatter over the honeyed seeds. Finish with a drizzle of olive oil and a scattering of sea salt, then dot with edible flowers for extra wow factor, if you like.','10 mins','20 mins'),
(282,'green bean mac ’n’ cheese','https://www.bbcgoodfood.com/recipes/green-bean-mac-n-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-bean-mac-n-cheese-e00d86c-scaled.jpg','Runner and green beans make a delicious addition to a comforting pasta bake - top with pumpkin seeds for added crunch',29,4,1,1,5,'Put the oil in a medium saucepan over a medium heat, add the flour and stir well. Still stirring, slowly pour in a little of the milk and mix well until smooth. Add a little more milk, repeating until all the milk has been added and you have a smooth sauce. Stir in the nutmeg, season, then take off the heat while you cook the pasta. Heat oven to 220C/200C fan/gas 7. Bring a large pan of water to the boil, add the macaroni and boil for 8 mins. Add the runner beans and green beans, and boil for a further 3 mins. Drain the pasta and the beans, and tip into a large gratin dish. Pour over the prepared sauce and tuck the chopped cheese in among the pasta, then top with the grated cheese and pumpkin seeds. Bake for 10-15 mins or until the top is bubbling and golden. Serve with a peppery salad, if you like.','5 mins','30 mins'),
(283,'runner beans & charred leeks with vinaigrette','https://www.bbcgoodfood.com/recipes/runner-beans-charred-leeks-vinaigrette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/runner-beans-charred-leeks-with-vinaigrette-72ba35d.jpg','An easy veggie side dish with plenty of crunch, coated in a mustard, chilli and anchovy dressing to give an umami hit',11,6,1,1,5,'Bring a frying pan of salted boiling water to the boil, reduce to a simmer, add the leeks and cover with a cartouche (a piece of baking parchment with a hole in the middle). Cook for 4-5 mins until tender and a deeper green, then remove from the water with a slotted spoon (keeping the water at a simmer) and dry in a clean tea towel. Add the runner beans to the water, cook for 2-3 mins until tender, then drain and set aside. Heat a griddle pan over a high heat and toss the leeks in 1/ 2 tbsp olive oil and some seasoning. Griddle for a couple of mins each side until charred. Transfer to a chopping board and slice into lengths the same size as the runner beans. To make the vinaigrette, pound the anchovy using a pestle and mortar (or whizz in a blender), add the chilli flakes, Dijon, red wine vinegar and sugar and combine. Whisk in the remaining olive oil, season, then stir in the herbs. Put the runner beans and leeks in a large bowl. Pour over the vinaigrette, toss in the spring onions, then scatter over the seeds.','15 mins','15 mins'),
(284,'sri lankan runner bean curry','https://www.bbcgoodfood.com/recipes/sri-lankan-runner-bean-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sri-lankan-runner-bean-curry-15-08-2016-july-2013-c126a3d.jpg','This vegan spice pot is cooled down with coconut milk- ginger, cinnamon, cloves, mustard seeds and garam masala are all key flavours',7,4,1,1,5,'In a blender, combine the onion, turmeric, ginger, garlic and 1 tbsp of the oil together with a large pinch of salt. Heat the remaining oil in a shallow pan. Add the mustard seeds and curry leaves, and cook until they crackle, then add the onion paste and cook until sticky. Stir through the curry powder, then pour in the coconut milk. Add the cloves, cinnamon and the chilli, and bring to a simmer. Tip in the beans and simmer for 15 mins or until the beans are tender. Squeeze in the lime juice, add the garam masala, take the pan off the heat and stir through the coriander. Serve with rice and rotis, if you like.','10 mins','20 mins'),
(285,'runner beans with shallot butter','https://www.bbcgoodfood.com/recipes/runner-beans-shallot-butter','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560712_12-2ab0762.jpg','A special twist to the under-rated fresh flavoured runner bean - serve with ''A good steak and kidney pie''',53,6,1,1,5,'Top, tail and remove the strings from the runner beans, then cut them diagonally into elongated diamond shapes. Melt the butter with the oil in a medium-sized frying pan over a low to medium heat. Tip in the shallots and fry them gently for about 8 minutes, or until soft and very slightly coloured. Stir occasionally. Meanwhile, bring a saucepan of salted water to the boil and cook the beans for 3- 4 minutes until just tender. Drain well. Turn up the heat under the frying pan and add the sugar and vinegar to the shallots. Stir well and then leave to bubble for 1 minute. Toss the beans and the buttery shallots together, either in the frying pan or saucepan, depending on size. Serve in a warmed dish.','20 mins','No Time'),
(286,'prawn, sweetcorn & runner bean pasta','https://www.bbcgoodfood.com/recipes/prawn-sweetcorn-runner-bean-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4165_12-d330545.jpg','A quick and easy low-fat supper, ready in just 15 minutes',5,4,1,1,5,'Cook the pasta according to pack instructions. Tip in the runner beans and sweetcorn (if using fresh or frozen) for the final 4 mins of the pasta cooking time. Drain and set aside. Mix the fromage frais and pesto together, then pour into the same saucepan, along with the prawns. (If you’re using canned sweetcorn, add it now.) Warm through briefly over a low heat, then tip in the pasta and veg. Take off the heat, toss together and sprinkle with coriander, if using.','5 mins','10 mins'),
(287,'runner beans with bacon & hazelnuts','https://www.bbcgoodfood.com/recipes/runner-beans-bacon-hazelnuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-53432_11-95c6fa7.jpg','A vibrant seasonal side dish to perk up a bowl of beans',7,4,1,1,5,'Bring a large pan of salted water to the boil and cook the beans for 4-5 mins until cooked, but still vibrant. Drain the beans and tip straight into a bowl of ice-cold water. Drain again and set aside. Heat a frying pan, tip in the bacon and sizzle for 4-5 mins until it starts to crisp. Throw in the hazelnuts and cook for another few mins until the nuts start to brown. Use a slotted spoon to scoop out the bacon and nuts, but leave behind any juices and residue. Place the pan back on a low heat, slosh in the vinegar and sizzle for a moment, then stir in the cream. Return the beans, bacon and hazelnuts to the pan to warm through and toss them in the dressing. Tip into a bowl and mix in the tarragon just before serving.','10 mins','10 mins'),
(288,'runner bean samosas','https://www.bbcgoodfood.com/recipes/runner-bean-samosas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/runner-bean-samosas-8b8b94c.jpg','Runner beans work really well with spices and these curried filo triangles are the perfect showcase - a great starter or nibble for 6 people',52,6,2,1,5,'Put the potato in a small saucepan, cover with cold water and bring to the boil. Simmer for 15 mins until completely cooked through, then mash and set aside. Meanwhile, make the filling for the samosas. Heat a frying pan over a medium heat with 2 tsp of the oil and fry the onion for about 5 mins until starting to brown. Add the garlic, curry powder and beans, fry for 2 mins until they start to soften, then remove from the heat. Stir in the chutney, mashed potato and coriander, add a little seasoning and chill in the fridge for 15 mins. Heat oven to 190C/170C fan/gas 5. To make the samosas, lay one filo sheet on a clean work surface. Brush the filo with a thin layer of oil, then fold in half lengthways with the short edge towards you. Keep the remaining pastry covered with a damp cloth so it doesn’t dry out. Spoon a sixth of the cooled filling at one end of the strip, and leave a 2cm border. Take the right corner and fold diagonally to the left, enclosing the filling and forming a triangle. Fold again along the upper crease of the triangle. Keep folding in this way until you reach the end of the strip. Place on a baking tray lined with baking parchment. Repeat the process until all of the mix is used up. Brush all the samosas with oil, then dust with icing sugar, curry powder and the onion seeds. Bake in the oven for 20 mins until deep golden brown, then serve with the flavoured yogurt and some chutney.','30 mins','35 mins'),
(289,'runner bean vinaigrette','https://www.bbcgoodfood.com/recipes/runner-bean-vinaigrette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-14221_12-28d6122.jpg','If you''re not mad on anchovies, grilled bacon or lardons can add the required salty bite',33,4,1,1,5,'Put the eggs into a pan of cold water, bring to a boil, then simmer for 8 mins. Put the beans into a steamer or heatproof colander, cover and steam over the egg pan for 4-5 mins or until just tender and very bright green. Meanwhile, whisk the dressing ingredients together and season with salt and pepper. Cool the eggs under cold water, then peel. Heat a frying pan and add the oil. When hot, tip in the breadcrumbs and whole garlic clove. Stir for about 3 mins until toasty and golden, and just infused with the garlic. Chop the eggs, chives and anchovies and mix together. Toss the beans with the dressing while still warm, then scoop onto a serving dish. Top with the anchovy mix and the crumbs, then spoon over any dressing left in the bowl.','30 mins','15 mins'),
(290,'garlicky green beans','https://www.bbcgoodfood.com/recipes/garlicky-green-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1159516_10-8a766b1.jpg','A simple sauté of fresh beans with lightly fried garlic and a touch of olive oil makes a healthy, versatile side',17,6,1,1,5,'Cook the green beans in boiling water for 2-3 mins until just tender, then drain well and steam-dry for a min. Meanwhile, gently fry the garlic in the oil until pale golden – no further. Toss with the beans, season and serve.','5 mins','5 mins'),
(301,'mexican bean salad','https://www.bbcgoodfood.com/recipes/mexican-bean-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7835_12-401eb52.jpg','Not one to leave your tummy rumbling, this filling salad really hits the spot and has a spicy kick',5,4,1,1,5,'Lower the eggs into boiling water and boil for 6½ mins, then put into a bowl of cold water to cool. Slice the avocados and place in a large bowl with the beans, onion, coriander and tomatoes. Measure 3 tbsp of the dressing into a small bowl, then mix in the chilli and cumin. Once the eggs have cooled but are still warm, peel off the shells and cut into quarters. Toss the salad with the dressing and nestle in the eggs. Serve straight away – delicious with toasted tortillas.','10 mins','10 mins'),
(291,'spicy bean burgers with lime yogurt & salsa','https://www.bbcgoodfood.com/recipes/mexican-bean-burgers-lime-yogurt-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-195529_11-2001af4.jpg','These beany veggie burgers are easy to make and the toppings make them wonderfully moist - cook from frozen to save time',5,6,1,1,5,'Heat grill to high. Tip the beans into a large bowl, then roughly crush with a potato masher. Add the breadcrumbs, chilli powder, coriander stalks and ½ the leaves, egg and 2 tbsp salsa, season to taste, then mix together well with a fork. Divide the mixture into 6, then wet your hands and shape into burgers. The burgers can now be frozen. Place on a non-stick baking tray, then grill for 4-5 mins on each side until golden and crisp. To cook from frozen, bake at 200C/fan 180C/gas 6 for 20-30 mins until hot through. While the burgers are cooking, mix the remaining coriander leaves with the yogurt, lime juice and a good grind of black pepper. Split the buns in half and spread the bases with some of the yogurt. Top each with leaves, avocado, onion, a burger, another dollop of the lime yogurt and some salsa, then serve.','10 mins','10 mins'),
(292,'turbo beans & cheese on toast','https://www.bbcgoodfood.com/recipes/turbo-beans-cheese-on-toast','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/220413_TURBO_BEANS_AND_CHEESE_ON_TOAST_0049_2-3edc6ea.jpg','Make an elevated version of beans on toast using your favourite beans, plus tomatoes, garlic and chilli – they''re great topped with cheese',54,4,1,1,5,'Heat the oil in a heavy-based frying pan over a medium heat and fry the onion for 8-10 mins until softened and slightly caramelised. Add the garlic and spices, the chipotle chilli paste or chilli flakes, if using, and some seasoning. Fry for a few minutes more until the mixture is fragrant. Add the tomatoes, then swirl out the pan using 100ml water and add this too. Simmer for 10-15 mins until the mixture has thickened. Blitz the sauce using a hand blender, then taste for seasoning. Tip in the beans and mix well to coat. Warm through over a medium heat for 5 mins until the beans are piping hot. Heat the grill to high and toast the bread slices for 3-5 mins. Top with the beans and grated cheese, and pop under the grill until the cheese is melted and bubbling. Sprinkle with a little paprika and serve immediately.','7 mins','35 mins'),
(293,'mild chilli & bean pasta bake','https://www.bbcgoodfood.com/recipes/mild-chilli-bean-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1118_11-dea172c.jpg','Whip up a healthy chilli and bean pasta meal that''s full of fibre and vitamin B',55,4,1,1,5,'Heat a splash of oil in a frying pan, add the onion and celery, season with pepper and a little salt, then cook until soft. Remove vegetables and set aside. Brown the mince in the pan, a handful at a time, tossing with a fork as you go. Only add a splash of oil if the mixture begins to stick too much. Once browned, tip into a sieve to remove any excess fat. Return mince and cooked veg to the frying pan, add the chilli powder, tomatoes, tomato purée, beans and stock, bring to the boil and simmer for 15 mins. Heat oven to 200C/fan 180C/gas 6. Cook the pasta according to pack instructions. Drain, stir into the mince and spoon into a large lasagne dish. Mix the yogurt, eggs, cheese and garlic together and season lightly. Spoon over the top of the pasta and bake for 20-25 mins or until lightly browned.','10 mins','45 mins'),
(294,'chilli chicken one-pot','https://www.bbcgoodfood.com/recipes/chilli-chicken-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513733_11-57004d3.jpg','A prepare-ahead one pot that will please all ages and is easily made for a crowd, just reheat and add some garlic bread and a salad',4,8,1,1,5,'Heat oven to 180C/fan 160C/gas 4. Fry the onions in the oil for 5 mins until they become soft and start to colour. Add the chorizo and fry for a few mins more. Stir in the peppers, then pour in the tomatoes, followed by a can of water, the stock cubes, chilli and oregano. Arrange the chicken thighs on top of the sauce, pushing them under the liquid. Bring to a simmer, cover, then cook in the oven for 40 mins. Add the beans, stir, then cook for 20 mins more. You can make this up to 2 days ahead and keep chilled. To serve, reheat on the top of the stove or in the oven at 190C/fan 170C/gas 5 for 1 hr 10 mins until piping hot. Stir in most of the coriander, toss the rest with the avocado, lime and a little salt, then pile this on top. Serve with Garlic & oregano bread (below) and a bag of green salad tossed with olives, cherry tomatoes and finely sliced red onion.','20 mins','1 hr and 5 mins'),
(295,'spicy chicken & bean stew','https://www.bbcgoodfood.com/recipes/spicy-chicken-bean-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1274508_9-7ff180e.jpg','This hearty, satisfying one-pot is low fat and easy on the washing up',5,6,1,1,5,'Pull the skin off the chicken and discard. Heat the oil in a large casserole dish, brown the chicken all over, then remove with a slotted spoon. Tip in the onions, garlic and chillies, then fry for 5 mins until starting to soften and turn golden. Add the peppers, tomatoes, beans and hot stock. Put the chicken back on top, half-cover with a pan lid and cook for 50 mins, until the chicken is cooked through and tender. Stir through the coriander and serve with soured cream and crusty bread.','15 mins','1 hr and 20 mins'),
(296,'refried bean quesadillas','https://www.bbcgoodfood.com/recipes/refried-bean-quesadillas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-365499_11-dc77cfd.jpg','This casual meat-free main is a cheap and cheerful way to fill up your friends. Serve these vegetarian quesadillas in slices, so they''re easy to eat',5,4,1,1,5,'Heat the oil in a large frying pan and cook the onion and garlic for 2 mins. Add the cumin and cook for 1 min more. Tip in the beans, paprika and a splash of water. Using a potato masher, break the beans down as they warm through to make a rough purée. Season generously. Spread the refried beans onto 4 of the tortillas and scatter over the cheese and coriander. Spoon over the salsa, then top with the remaining tortillas to make 4 sandwiches. Wipe the frying pan with kitchen paper and return to the heat or heat a griddle pan. Cook each sandwich for 1-2 mins on each side until the tortillas are crisp and golden and the cheese is melting. Serve warm, cut into wedges, with extra salsa and soured cream for dipping.','10 mins','20 mins'),
(297,'salsa chicken peppers','https://www.bbcgoodfood.com/recipes/salsa-chicken-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-9654_12-dfa5bef.jpg','A low-fat filler that can be warmed up easily for lunch the next day. Experiment with your favourite fillings',5,4,1,1,5,'Boil the rice for 25 mins until just tender. Meanwhile, heat oven to 220C/fan 200C/gas 7. Slice the tops off the peppers and cut out the seeds. Lightly oil, then bake (with the lids) in a roasting tin for 12 mins. Drain rice and mix with the salsa, chicken, beans, cheddar and coriander. Season to taste. Take the peppers out of the oven, then fill with the rice mixture. Put on the lids, then bake for 10 mins. Squeeze over lime wedges and serve with an avocado salad.','30 mins','N/A'),
(298,'chilli beef shepherd''s pie','https://www.bbcgoodfood.com/recipes/chilli-beef-shepherds-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-13629_12-4620776.jpg','The family favourite with a spicy kick and parsnip mash topping',5,4,1,1,5,'Heat the oil in a large frying pan, then soften the onion with 1 tsp cumin for a few mins. Push the onion to the edge of the pan, add the beef, then brown well, breaking up the lumps as you go. Add the garlic, kidney beans, chopped tomatoes and half a can of water, season and cover, then simmer for 20 mins. Meanwhile, cook the parsnips in boiling salted water until tender, drain well, then mash with some freshly milled black pepper and a little salt. Heat the grill to high. Tip the chilli mince into a large ovenproof dish, spoon over the mash, scatter with the remaining cumin seeds, then grill for 5 mins until golden.','5 mins','30 mins'),
(299,'cottage chilli hotpot','https://www.bbcgoodfood.com/recipes/cottage-chilli-hotpot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424635_11-10ec02d.jpg','The ultimate make-ahead comfort food dish - if you''re spending a weekend in the country this dish will defrost as you travel',33,6,1,1,5,'Toss the beef in flour, then brown half in a pan, using 1 tbsp oil. Once browned, spoon into a bowl. Add 100ml wine to the pan and scrape up any bits. Reduce a little, then pour into the bowl with the cooked beef. Wipe the pan and repeat with the remaining beef and another 100ml wine. Meanwhile, whizz the onions, carrots and garlic in a food processor until finely chopped. Add 2 tbsp oil to the pan and tip in the chopped veg, peppers, fresh chilli, thyme sprigs and bay. Fry for 10 mins. Tip in the spices, cook for 1 min, then add the rest of the wine and reduce by half. Add the tomatoes, sugar, beef, winey juices and stock, season, then simmer for 1½-2 hrs until the meat is meltingly tender. Stir in the beans and allow to cool. Peel and slice the potatoes about 5mm thick, then boil for 5 mins. Drain, then tip back into pan. Add the butter and thyme leaves, season, then toss to coat. Tip the chilli mix into a 35 x 25cm dish, then layer the potatoes on top. Dot with butter. Cover with cling film and cool completely if freezing. Will freeze for 1 month – defrost before cooking. Heat oven to 200C/180C fan/gas 6 and bake for 50 mins-1 hr, until the sauce is bubbling and potatoes golden. Serve with soured cream and green veg.','20 mins','3 hrs'),
(300,'vegan chilli','https://www.bbcgoodfood.com/recipes/vegan-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-chilli-be48585.jpg','Our healthy vegan chilli recipe packs in plenty of vegetables and doesn’t fall short on the flavour front. Serve it with rice or in jacket potatoes for a filling supper',7,4,1,1,5,'Heat the oven to 200C/180C fan/gas 6. Put the sweet potato in a roasting tin and drizzle over 1½ tbsp oil, 1 tsp smoked paprika and 1 tsp ground cumin. Give everything a good mix so that all the chunks are coated in spices, season with salt and pepper, then roast for 25 mins until cooked. Meanwhile, heat the remaining oil in a large saucepan over a medium heat. Add the onion, carrot and celery. Cook for 8-10 mins, stirring occasionally until soft, then crush in the garlic and cook for 1 min more. Add the remaining dried spices and tomato purée. Give everything a good mix and cook for 1 min more. Add the red pepper, chopped tomatoes and 200ml water. Bring the chilli to a boil, then simmer for 20 mins. Tip in the beans and cook for another 10 mins before adding the sweet potato. Season to taste then serve with lime wedges, guacamole, rice and coriander. Will keep, in an airtight container in the freezer, for up to three months.To make in a slow cookerHeat the oil in a large frying pan over a medium heat. Add the onion, carrot and celery. Cook for 8-10 mins, stirring occasionally until soft, then crush in the garlic, tip in the sweet potato chunks and cook for 1 min more. Add all the dried spices, oregano and tomato purée, cook for 1 min, then tip into a slow cooker.Add the red pepper and chopped tomatoes. Give everything a good stir then cook on low for 5 hrs. Stir in the beans and cook for another 30 mins to 1 hr. Season to taste and serve with lime wedges, guacamole, rice and coriander.','15 mins','45 mins'),
(302,'spicy sausage & bean one-pot','https://www.bbcgoodfood.com/recipes/spicy-sausage-bean-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-casserole-5644b25.jpg','A saucy, warming casserole with Cumberland sausages, chilli gravy and kidney beans. It''s budget-friendly and ready in less than 30 minutes too',24,4,1,1,5,'Heat the oil in a large frying pan. Cook the onion and sausages over a fairly high heat for 8-10 mins, turning the sausages often so they brown all over. Add the garlic to the pan with the kidney beans and their sauce. Half-fill one of the cans with water, swirl and then add this to the pan. Stir everything together and bring to the boil. Turn down to simmer and cook for 10 mins, or until the sausages are cooked through. Season and sprinkle with the parsley.','5 mins','20 mins'),
(303,'chicken quesadillas','https://www.bbcgoodfood.com/recipes/chicken-quesadillas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-19637_11-59fc4d2.jpg','These easy, budget-friendly chicken quesadillas make a quick lunch for two or a simple party snack and are a great way to use up leftover roast chicken',4,2,1,1,5,'Spread 2 tbsp salsa onto each tortilla, then evenly top one of them with the beans, spring onion, chicken and cheddar. Scatter with coriander, if you have it. Sandwich with the other tortilla, then brush with oil. Heat a large non-stick frying pan, then cook the tortilla, oil-side down, for 4 mins. Carefully turn over with a palette knife (or by turning it out onto a plate, sliding it back into the pan), then cook for 2 mins on the other side until golden. Serve cut into wedges.','10 mins','6 mins'),
(304,'cheesy bean potatoes','https://www.bbcgoodfood.com/recipes/cheesy-bean-potatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-12837_12-28d6122.jpg','Give baked potatoes a bit of oomph with this easily reheated side dish - great with sausages and salad',4,6,1,1,5,'Heat oven to 200C/fan 220C/gas 7. Oil and season the potatoes, then bake for 1 hr until a knife slides in easily and the skins are crispy. Cool for 10 mins. Halve the potatoes lengthways and scoop out the cooked potato into a bowl, leaving a thin layer behind so that the skins still hold their shape. Mash the potato with the butter, mustard and seasoning, then stir in the spring onions, 175g cheese and kidney beans. Pile the mixture into the potato skins and scatter with the remaining cheese. Can be made to this stage a day ahead and chilled. Bake for 30-40 mins until golden. Serve with the sausages and salad (see ''Goes well with'').','30 mins','1 hr - 1 hr and 40 mins'),
(305,'fruity caribbean curry','https://www.bbcgoodfood.com/recipes/fruity-caribbean-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fruity-caribbean-chicken-curry-de9973d.jpg','Kidney beans and veg make chicken drumsticks go further with this sweet and hot curry - pack in 4 of your 5 a day in a filling family meal',6,4,1,1,5,'Heat the oil in a large frying pan. Add the chicken and brown well on all sides, then transfer to a plate. Add the onions and peppers to the pan, and cook for 5 mins until the veg starts to soften. Return the chicken to the pan and sprinkle in the curry powder, then add the pineapple with its juice, and the coconut milk. Season and simmer, uncovered, for 40 mins until the chicken is tender and the sauce has reduced and thickened a little. Add the beans and pepper sauce to the pan. Simmer for another 2-3 mins until the beans are warmed through, then scatter with coriander and serve with cooked rice.','10 mins','50 mins'),
(306,'cajun turkey steaks with pineapple salsa','https://www.bbcgoodfood.com/recipes/cajun-turkey-steaks-pineapple-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-265506_11-74f3c08.jpg','Turkey is the ultimate healthy, feelgood food - low in fat but high in protein. Jazz it up with a spicy salsa ',5,4,1,1,5,'Reserve 2 tbsp of the onion for the salsa. Heat the oil in a saucepan and cook the remaining onion and half the pepper for 4 mins or until softened and coloured. Stir in the rice, then pour in the stock. Add the kidney beans and a pinch of salt. Bring to the boil, stir once, cover the pan, then reduce the heat to a gentle simmer. Cook for 15 mins until the rice is tender and the liquid absorbed. Dust the turkey steaks with the Cajun spice and griddle or fry in a non-stick pan for about 4-6 mins on each side until cooked through. Cut the pineapple into small pieces and mix together with the reserved red onion, pepper, green chilli and lime juice. Spoon some salsa over each steak and serve with the rice.','10 mins','30 mins'),
(307,'spicy chicken fried rice','https://www.bbcgoodfood.com/recipes/spicy-chicken-fried-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-21683_12-c7efb74.jpg','A clever mix of basmati and wild rice gives this superhealthy chicken dish a wonderfully nutty bite',5,4,1,1,5,'Put the rice into a saucepan, cover with water, crumble in the stock cube, then bring to the boil. Stir the rice once, then simmer for about 20 mins until tender. Drain well. Meanwhile, toss the chicken in the spices and coriander. Heat a pan, add the oil, tip in the peppers, then stir-fry for 3 mins until starting to soften. Add the chicken, then fry until golden, about 5 mins. Add the rice, beans and spring onions, then warm through.','5 mins','25 mins'),
(308,'simple coconut & bean soup','https://www.bbcgoodfood.com/recipes/simple-coconut-bean-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-47743_10-1c4be35.jpg','This vegetarian meal in a bowl shows how something delicious can be made from mostly storecupboard ingredients',56,4,1,1,5,'Heat the oil in a large saucepan. Sizzle the spring onion whites, pepper, chilli paste and garlic for 5-8 mins until soft and fragrant. Add the thyme, curry powder and spices, then cook for 1 min more, Stir in the tomatoes, then cook for 2 mins to soften slightly. Crumble in the stock cube, then tip in all the beans and the coconut milk. Simmer for 10 mins. Turn off the heat and stir in most of the spring onion greens, the lime juice and some seasoning. Ladle into bowls and scatter with remaining spring onions just before serving.','10 mins','20 mins'),
(309,'broad bean, feta & watercress salad','https://www.bbcgoodfood.com/recipes/broad-bean-feta-watercress-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/broad-bean-feta-watercress-salad-6a6d577.jpg','Peppery watercress makes a bold base for this simple side dish, finished with crumbled cheese and crunchy pine nuts',1,4,1,1,5,'Put the broad beans in a bowl, pour over boiling water from the kettle, then drain and transfer to a bowl of cold water. Remove the skins from the broad beans by splitting them with your fingernail and slipping out the bean. Make the dressing by combining the mint and olive oil in a small bowl. Season generously. Toss the watercress with the broad beans, feta and the dressing. Scatter with the pine nuts and serve.','15 mins','No Time'),
(310,'tomato, burrata & broad bean salad','https://www.bbcgoodfood.com/recipes/tomato-burrata-broad-bean-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-burrata-broad-bean-salad-85f67a7.jpg','Chop up tomatoes, toss with salt, top with creamy burrata and slather with a broad bean-flecked salsa verde to make this simple yet super-tasty salad',11,4,1,1,5,'Chop the tomatoes into a mixture of slices and wedges, then put them in a bowl. Toss with 1/2 tsp salt and the sugar, then leave to marinate for 30 mins. Meanwhile, bring a pan of water to the boil and add the broad beans. Boil for 2 mins, then drain and allow to cool for a couple of mins. Peel them out of their shells. This is a bit fiddly but well worth it – I use my thumbnail to pierce the skin, then gently squeeze out the shiny green beans. Finely chop the herbs, then tip into a bowl and whisk in the olive oil, mustard, fennel seeds, most of the lemon zest, and red wine vinegar. Season, then stir in the broad beans. Tip the tomatoes onto a serving platter. Top with the burrata, then spoon over the broad bean salsa. Scatter over the hazelnuts and remaining lemon zest to serve.','25 mins','2 mins'),
(311,'pea & broad bean shakshuka','https://www.bbcgoodfood.com/recipes/pea-broad-bean-shakshuka','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pea-shakshuka-02f8597.jpg','We’ve turned a classic brunch shakshuka dish into more of a sharing main course by adding seasonal spring vegetables like peas, broad beans and asparagus',19,4,1,1,5,'Trim or snap the woody ends of the asparagus and finely slice the spears, leaving the tips and about 2cm at the top intact. Finely slice the broccoli in the same way, leaving the heads and about 2cm of stalk intact. Heat the oil in a frying pan. Add the spring onions, sliced asparagus and sliced broccoli, and fry gently until the veg softens a little, then add the cumin seeds, cayenne, tomatoes (with their juices), parsley and plenty of seasoning, and stir. Cover with a lid and cook for 5 mins to make a base sauce, then add the asparagus spears, broccoli heads, peas and broad beans, cover again and cook for 2 mins. Make 4 dips in the mixture. Break an egg into each dip, arrange half the pea shoots around the eggs, season well, cover with a lid and cook until the egg whites are just set. Serve with the rest of the pea shoots, a spoonful of yogurt and some flatbreads, and sprinkle over another pinch of cayenne, if you like.','20 mins','30 mins'),
(312,'pork chops with broad bean & minted jersey smash','https://www.bbcgoodfood.com/recipes/pork-chops-broad-bean-minted-jersey-smash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-chops-with-broad-bean-minted-jersey-smash-5ed519c.jpg','Make the most of seasonal summer ingredients with pan-fried pork chops served with colourful broad beans, spring onions, mint and Jersey Royal potatoes',11,2,1,1,5,'Take the pork chops out of the fridge 1 hr before cooking. Put the potatoes in a pan and cover with cold water and 2 tsp of salt. Throw in the mint, cover and cook for 12-15 mins until tender. Remove with a slotted spoon and set aside. Cook the beans in the same water for 4-8 mins, depending on size, then drain and rinse under cold water and peel. Crush half with the potatoes, season and set aside. Heat the oil in a large non-stick frying pan. Pat the chops dry with a kitchen towel and season with sea salt on both sides, then place in the pan and do not move until they are caramelised and come away from the pan – around 3-4 mins. Turn the chops, then add the garlic and lemon, cut-side down. Cook for another 2-3 mins. Remove the lemon and set aside. Add the butter to the pan, shaking to distribute it, then toss in the thyme. Baste the chops continuously with the butter, garlic and thyme for a couple of mins more, turning to coat both sides. Remove from the pan to rest. Add the spring onions and chilli flakes and cook for 5-8 mins until tender. Tip in the potato mix, tossing with the spring onions, and cook for a few mins, allowing crusty bits to form on the Jerseys. Turn off the heat, toss in the herbs, then divide between plates and top with the remaining beans. Serve with the chops, and squeeze over the caramelised lemon.','20 mins','40 mins'),
(313,'pea, broad bean & rocket risotto with chicken','https://www.bbcgoodfood.com/recipes/pea-broad-bean-rocket-risotto-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/li-3807989gfcover_95_gree.p-9f67c12.jpg','This fresh, spring risotto has great depth as it’s made from a stock of the pea pods, asparagus ends, and parsley stalks. There''s less waste, more flavour',7,4,1,1,5,'To make the base, bring the stock to the boil, then add the pea pods and asparagus ends. Simmer for 2 mins, then add the parsley stalks and take off the heat. Use a powerful stick blender to blitz everything to make a green stock, then pass through a sieve into a jug. Set aside. Heat 2 tbsp of the oil and half the butter in a heavy, wide pan. Tip in the onion and sizzle gently for 5 mins until soft. Turn up the heat slightly, tip in the rice and stir for a few mins. Pour in the wine and cook down until it has been absorbed into the rice. Add ladles of stock to the rice, stirring, adding another ladleful after it’s been absorbed. Once the rice is starting to soften but is still chalky (about 20 mins), stir in the peas and asparagus. Keep adding stock until it’s all been used and the rice is just cooked, then stir through the broad beans. While the rice is cooking, heat the remaining oil in a frying pan. Season the chicken and pan-fry for 3-4 mins each side until lightly browned and just cooked through. Turn the heat off but leave the chicken in the pan to keep warm. Once the risotto is cooked, take off the heat and scatter over the parmesan, the rest of the butter and the parsley leaves. Cover the pan and leave to sit for a few mins. Carve the chicken into thick slices, give the risotto a good stir, season well and scatter over the rocket. Serve the risotto topped with the sliced chicken.','20 mins','50 mins'),
(314,'tomato salad with ricotta, broad beans & salsa verde','https://www.bbcgoodfood.com/recipes/tomato-salad-ricotta-broad-beans-salsa-verde','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/heritage-tomato-salad-with-ricotta-broad-beans-salsa-verde-d1cb82d.jpg','Serve tricolore salad with a twist using heritage tomatoes, ricotta and salsa verde. A lovely summery side dish for a picnic, barbecue, or light lunch',11,4,1,1,5,'Toss the sliced tomatoes and onion in a bowl with a bit of salt and set aside. Finely chop the herbs for the salsa verde. Pound the garlic, Dijon mustard and capers using a pestle and mortar and add to the herbs. Add seasoning and the oil. Add the vinegar, little by little, tasting as you go, until the sauce has the right amount of acidity – it needs to be punchy without losing the grassy, fresh flavour of the herbs. Bring a pan of water to the boil and plunge the broad beans in for a couple of mins, then drain, cool in cold water and peel away the shells. Divide the tomatoes and onions between plates or spread out on a platter. Dot the ricotta around and spoon over the salsa verde. Scatter with broad beans and serve.','25 mins','No Time'),
(315,'burrata bruschetta','https://www.bbcgoodfood.com/recipes/burrata-bruschetta-summer-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/burrata-bruschetta-with-summer-beans-cf96d0f.jpg','Try a new twist on bruschetta, topped with burrata, broad beans, sugar snap peas, radish, mint and chilli. It makes a fab lunch or starter for a dinner party',57,4,1,1,5,'Mix the sugar snap peas, peas, broad beans and radish with the chillies and herbs in a bowl. Add the chardonnay vinegar and olive oil, and toss to coat. Heat grill to high. Brush the bread with oil and sprinkle with sea salt. Grill for 2 mins each side until lightly charred. Put each piece of toast on a small plate, then halve burratas and gently spread over the toast. Divide the summer beans, pile on top and season to serve.','10 mins','10 mins'),
(316,'broad bean pasta','https://www.bbcgoodfood.com/recipes/broad-bean-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/Broad-bean-pasta-5eb8507.jpg','Make the most of broad beans – at their peak in summer months – in this light pasta recipe. Peas work well too, though broad beans are just as sweet when peeled',2,2,1,1,5,'Bring a large pan of water to the boil and cook the beans for 3 mins until just tender. Remove to a bowl of ice-cold water using a slotted spoon to halt the cooking process. Set the pan back on the heat, tip in the pasta and cook following pack instructions. Meanwhile, pop the beans out of their skins so you’re left with the bright green beans, and set aside. (This is optional, but the colour, texture and flavour is improved. Discard the skins.) Heat a small, dry frying pan over a medium heat and fry the pancetta for 5-7 mins until crisp and golden. Drain the broad beans and add to the pancetta along with the lemon zest and mint. Squeeze over one of the lemon halves and season well. Cook for 1-2 mins to warm through, then remove from the heat. Drain the pasta, reserving a mugful of the water, then return to the pan along with the pancetta and broad bean mixture, the ricotta and a few tablespoons of the reserved pasta cooking water. Mix together, adding more of the reserved water if needed to create a slightly glossy sauce. Divide the pasta between bowls, squeeze over the remaining lemon half and scatter with the parmesan, if you like.','25 mins','22 mins'),
(317,'pods & sods with watercress','https://www.bbcgoodfood.com/recipes/pods-sods-watercress','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pods-sods-with-watercress-5a326bc.jpg','Soya beans, asparagus, broad beans and peas tossed in a vinaigrette make a delicious springtime accompaniment to roast lamb',25,6,1,1,5,'Bring a large pan of salted water to the boil. Drop in the asparagus, bring back to the boil, then add all the beans, the peas and spring onions. Bring to the boil again, then drain and cool quickly by running under cold water. To make the dressing, mix the mustard with the oil, vinegar and some seasoning. On a large platter, mix all the vegetables with the mint, watercress and a splash of dressing.','5 mins','5 mins'),
(318,'greek lamb with smoked aubergine & minty broad beans','https://www.bbcgoodfood.com/recipes/greek-lamb-smoked-aubergine-minty-broad-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/greek-lamb-with-smoked-aubergine-minty-broad-beans-3b7231a.jpg','Lean red meat delivers iron and B vitamins in this protein-packed dinner, with aubergine purée as a delicious alternative to mash',4,2,1,1,5,'Turn on your hob’s largest gas flame and cook the aubergine directly over it for 7-8 mins, using tongs to turn it every 2 mins, until it is soft and the skin has charred. You can do this on the barbecue or under a hot grill if you don’t have a gas hob. Allow to cool a little. Mix the lemon zest, half the garlic, the oregano, some black pepper and 1/2 tsp oil, then use to coat the lamb steaks. Boil the broad beans for 4 mins. Put the aubergine on a large plate and carefully remove and discard the skin. Use a knife and fork to finely chop the flesh, which should now be soft and pulpy. Tip into a bowl with the remaining garlic, the yogurt, tahini and some seasoning, and stir well. Heat the griddle or barbecue and cook the lamb for 5 mins, turning once. Meanwhile, mix the beans with the lemon juice, remaining olive oil and the mint leaves. Spoon the aubergine purée onto plates and scatter round the minty beans. Top with the lamb and serve with lemon wedges.','20 mins','15 mins - 20 mins'),
(319,'crispy lamb breast with broad beans, chilli & marjoram','https://www.bbcgoodfood.com/recipes/crispy-lamb-breast-broad-beans-chilli-marjoram','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crispy-lamb-breast-with-broad-beans-chilli-marjoram-b2abb9b.jpg','Lamb, pan-fried with polenta to give it an irresistibly crisp finish, served with tender broad beans, shallots and herbs',52,6,3,1,5,'Heat oven to 140C/120C fan/gas 1. Put the lamb on a chopping board. In a small bowl, mix the garlic, 1 tbsp flaky salt, rosemary and mustard into a paste. Rub on the inside of each lamb breast. Next, stack the breasts on top of each other and roll up the longest side into a sausage shape. Use the butchers string to tie knots along the length of the lamb. In a large, round casserole dish, heat the duck fat. When it reaches 100C on a cooking thermometer, add the lamb (you may need to curl it into the dish). Put the lid on, then cook in the oven for 4 hrs until tender. Remove the dish and leave to cool. Once the fat is cool enough, remove the lamb using two slotted spoons. Reserve the cooled fat as you will need 1 tbsp tomorrow, and save the rest to make roasties. Lay the lamb roll on some cling film, then roll as tight as you can. Chill on a baking tray in the fridge overnight. The next day, heat oven to 180C/ 160C fan/gas 4. Remove the cling film and string, and slice into 6-8 equal portions. On a plate, mix the flour and polenta, and season well. Dip each portion into the mixture. Meanwhile, heat 1 tbsp of duck fat in an ovenproof frying pan. When the pan is hot, add the lamb. Seal each side for 2-3 mins until you get a golden, crispy crust. Sear the sides, then transfer the pan to the oven for 8 mins or until it’s hot in the middle. Remove from the oven and put the lamb on a plate loosely covered with foil. Carefully put the pan back on the hob – you should have a good layer of lamb fat. Add your butter then, once foaming, add the shallots, broad beans and chilli. Stir-fry for 1 min, then add the vinegar and marjoram leaves. Season to taste, then remove from the heat. Serve immediately with the crispy lamb breast and reduced lamb stock.','45 mins','4 hrs and 30 mins'),
(320,'lemony broad beans with goat''s cheese, peas & mint','https://www.bbcgoodfood.com/recipes/lemony-broad-beans-with-goats-cheese-peas-mint','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Lemony-broad-beans-with-goats-cheese-peas-and-mint-b96b63b.jpg','Use goat''s cheese with a rind here so it will slice well and have a lovely firm, creamy texture. It pairs beautifully with lemony broad beans, peas and mint',14,4,1,1,5,'Heat the oven to 200C/180C fan/gas 6. Tip the hazelnuts onto a baking tray and roast for 12-15 mins until golden. Leave to cool. Cook the broad beans and peas in a pan of boiling water for 2 mins, then drain. Heat half the oil in a small frying pan over a low-medium heat and fry the shallots with a pinch of salt for 12 mins until softened. Remove from the heat and leave to cool. Roughly chop the cooled hazelnuts, then toss with the broad beans and peas. Add the mint, parsley, remaining olive oil, the lemon zest and juice and the cooled shallots. Season with black pepper and ½ tsp sea salt flakes. Spread the mixture over a large serving plate and top with the sliced goat’s cheese. Drizzle with a little extra olive oil and scatter with torn mint leaves, then serve with chunks of crusty bread.','15 mins','20 mins'),
(321,'lamb lollipops with smashed minty broad beans','https://www.bbcgoodfood.com/recipes/lamb-lollipops-smashed-minty-broad-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb-lollipops-b042e23.jpg','These griddled cutlets should be eaten like drumsticks, making this bright, fresh dish ideal for a sharing platter',7,4,1,1,5,'Mix the garlic, lemon, chilli and olive oil together, put the lamb cutlets in a dish and pour over the marinade. Cover and leave to marinate for 1 hr in the fridge, if you have time. Tip the broad beans into a food processor with half the olive oil, plenty of salt and pepper, and the lemon juice. Blitz to a chunky purée, then tip into a saucepan but leave off the heat. Heat a griddle pan and cook the lamb for 2-3 mins on each side for thinner chops, 3-4 mins on each side for thicker chops. While the chops cook, gently heat the purée. Then stir through the mint and the rest of the olive oil. Check for seasoning. Serve the lamb in a pile with a bowl of the smashed broad beans and plenty of bread to mop up the juices.','15 mins','10 mins'),
(322,'pappardelle broad bean carbonara','https://www.bbcgoodfood.com/recipes/pappardelle-broad-bean-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carbonara-782cf4a.jpg','A creamy pancetta, broad bean and egg sauce makes an ideal accompaniment to ribbon pasta',7,2,1,1,5,'Bring a large pan of salted water to the boil. While the water boils, heat a frying pan and sizzle the pancetta for 8 mins until crisp, then throw the broad beans into the pan with the pancetta fat. In a small bowl, beat the egg yolks with the cream and mustard, then season with lots of black pepper. Cook the pasta following pack instructions. Drain the pasta, saving some of the water, and toss through the pancetta in the frying pan. Tip in the egg and cream mix, and stir to coat, adding a splash of the reserved water, if needed. Toss half of the grated Parmesan through, so the sauce clings to the pasta, then scatter with the remaining Parmesan.','10 mins','20 mins'),
(323,'broad bean & dill pilaf','https://www.bbcgoodfood.com/recipes/broad-bean-dill-pilaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pilaf_2-0dc9363.jpg','This fragrant basmati rice dish with dill and broad beans makes a great vegetarian main course or versatile side dish for lamb and chicken',7,4,1,1,5,'Rinse the rice until the water runs clear, then soak in warm water for 5 mins and drain well. Heat the butter in a saucepan and add the onion and garlic. Sizzle everything for 8-10 mins until softened, but not coloured. Stir though the rice and pour over the stock. Cover, bring to the boil, then turn down heat to a minimum and cook the rice very slowly for 10 mins. Lift the lid, and quickly scatter over the podded beans, then replace the lid. Turn up the heat and simmer for 5 mins until all the liquid is absorbed. Add the dill, give the rice a good stir and serve with extra butter melting through.','5 mins','25 mins'),
(324,'simple salmon with spring pasta','https://www.bbcgoodfood.com/recipes/simple-salmon-spring-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/may-salmon-72d5b67.jpg','Asparagus, peas and broad beans are all natural partners for fish, make them more indulgent with creamy tarragon pasta',33,2,1,1,5,'Heat oven to 220C/200C fan/gas 7. Rub the oil over the bottom of a large roasting tin or baking dish. Season the salmon with black pepper and a little salt, then wrap a piece of prosciutto around the middle of each fillet. Roast for 10 mins, then toss the asparagus into the oily tin and roast for 5 mins more. Meanwhile, boil the pasta following pack instructions, adding the peas and beans 2 mins before the end of cooking. Once the vegetables and pasta are just tender, reserve a cup of the cooking water, then drain. Remove the salmon from the tin, then add the mascarpone, pasta, 3 tbsp of the cooking water, the lemon zest and juice, and tarragon. Season to taste and toss until the pasta is covered with creamy sauce. Serve with the fish and a lemon wedge.','5 mins','15 mins'),
(325,'broad bean, barley & mint salad','https://www.bbcgoodfood.com/recipes/broad-bean-barley-mint-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1284472_9-d63497c.jpg','These little green beans go really well with grains like pearl barley - for a light touch, serve cold with goat''s cheese, mint and nuts',7,4,1,1,5,'Heat the stock in a medium saucepan until boiling, then add the broad beans and cook for 3 mins until they float. Using a slotted spoon, transfer to a bowl and run under cold water to stop them cooking further. Squeeze the beans from their skins, if desired. Add the pearl barley to the stock and simmer over a low heat for 40-45 mins until tender. Drain the barley, stir through the mint leaves while still warm, then leave to cool. For the dressing, whisk all the ingredients together, season generously and mix into the cooled barley. Serve on a large platter or individual plates, scattered with the radishes, hazelnuts and goat’s cheese.','20 mins','50 mins'),
(326,'broad bean & lancashire cheese tart','https://www.bbcgoodfood.com/recipes/broad-bean-lancashire-cheese-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-24413_10-0e47a9b.jpg','A beautifully British tart to make a picnic special',58,4,2,1,5,'Make the pastry. Tip the flour, butter and salt into a food processor and process briefly, add the egg and process until it forms small clumps. Remove and form into a ball, then wrap in cling film and chill for about 30 minutes. Roll out to fit a 4cm deep, 23cm round loose-bottomed flan tin. Prick the base and chill for 30 minutes. Preheat the oven to 200C/gas 6/fan 180C. Cook the broad beans in boiling salted water for 3-4 minutes until just tender, drain and refresh in cold water. Pat dry. Bake the pastry case blind for 15 minutes. Remove from the foil and beans and bake for a further 5 minutes. Cool slightly. Reduce the oven to 190C/gas 5/fan170C. Scatter the beans and ham in the pastry case and crumble the cheese on top. Whisk together the eggs, crème fraîche and milk and season with salt and pepper. Pour over the beans and bake for 30-40 minutes until golden brown and puffed. Serve warm, with salad.','1 hr - 1 hr and 50 mins','No Time'),
(327,'broad bean pilau','https://www.bbcgoodfood.com/recipes/broad-bean-pilau','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7138_11-cedf838.jpg','This broad bean dish will add a Middle Eastern twist to any summer occasion',7,4,1,1,5,'Wash the rice in several changes of cold water until the water runs clear, then soak in warm water with a pinch of salt for 30 mins. Meanwhile, cook the broad beans in boiling unsalted water for just 2 mins. Drain the beans, then tip into ice-cold water and leave to cool. Remove the beans from their skins and set aside. Heat the butter in a saucepan, then sizzle the spring onions, pine nuts, allspice and half the dill for a few mins until pine nuts are toasted. Drain rice, then add to the pan and stir to coat in the butter. Cover the rice with about 1cm of water, then cover the pan and bring to a hard boil. Stir once, then reduce heat to a minimum, cover again and leave for 5 mins. Turn off the heat and leave rice undisturbed for 10 mins. Finally, stir beans through the rice, pile onto a serving platter, scraping any golden crispy bits from the pan, and scatter with the remaining dill.','30 mins','15 mins'),
(328,'broad bean, yogurt & mint soup','https://www.bbcgoodfood.com/recipes/broad-bean-yogurt-mint-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2832_12-d644077.jpg','Delectable smooth soup, high in fibre and folic acid',59,4,1,1,5,'Fry the onion and celery gently in the butter in a covered saucepan over a low heat for about 10 minutes until very tender. Tip in the rice, add the savory or thyme and cook for another minute, uncovered. Now add the broad beans, pour in 1 litre/13⁄4 pints stock and season with salt and pepper. Bring to the boil, simmer for about 5 minutes, then tip in the peas and cook for a further 5 minutes, by which time the rice will be tender. Remove the herb sprig and purée the soup in batches in a blender or food processor, then return to the rinsed-out pan. (If you’ve got a hand blender, use it in the pan.) Shortly before serving, reheat the soup thoroughly and add more stock if the consistency is too thick for your liking. Take the pan off the heat and stir in 1 tbsp of the yogurt and the chopped mint. Continue to stir in the yogurt, 1 tbsp at a time, until it’s all incorporated. Taste for seasoning, then serve in warmed bowls with an extra spoonful of yogurt stirred into each and a grinding of black pepper on top.','45 mins - 1 hr','N/A'),
(329,'crushed broad bean pesto','https://www.bbcgoodfood.com/recipes/crushed-broad-bean-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1284514_8-a888a42.jpg','This green sauce is delicious stirred through pasta, spread on toast with crumbly cheese, or as a dip',7,4,1,1,5,'Cook the broad beans in a pan of boiling salted water for 3 mins until they float. Drain and quickly run under ice-cold water to stop them cooking. Squeeze the small green beans from their skins and discard the skins. Fry the garlic and anchovies in a small pan for a few mins until golden, then stir through the broad beans. Transfer to a bowl (or use a pestle and mortar) and crush the broad beans with the Parmesan, lemon juice and zest, and oil. Will keep in the fridge for 3 days.','20 mins','5 mins'),
(330,'beef, cheese & broad bean quesadillas','https://www.bbcgoodfood.com/recipes/beef-cheese-broad-bean-quesadillas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-216452_11-abce4d8.jpg','The whole family will enjoy this hand-held supper for a quick TV dinner',5,4,1,1,5,'Half-fill a ridged pan or frying pan with water, bring to the boil, then cook the beans for 1 min before draining. Dry the pan. Brush steaks with oil and season. Fry in the pan over a high heat for 2-3 mins on each side, then thinly slice. Divide the cheese over one half of each tortilla, then top with the steak, broad beans, jalapeños, tomato and coriander. Fold over the other half of the tortilla to make 8 half-moon shapes and press down to seal. Brush the tops with a little more oil. Heat the frying pan over a high heat and cook the quesadillas, oiled-side down, in batches for 1-2 mins until crisp. Brush the uncooked sides with remaining oil, then carefully flip over and cook for 1-2 mins more. Cut into wedges and serve with salsa, if you like.','15 mins - 20 mins','20 mins'),
(331,'halloumi flatbreads','https://www.bbcgoodfood.com/recipes/halloumi-flatbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-flatbreads-2f38141.jpg','Try these simple veggie halloumi wraps with a crunchy slaw and a generous helping of hummus. A great cheesy budget option for a midweek meal',16,6,1,1,7,'Toast the pumpkin seeds with the cumin seeds in a large frying pan until they begin to pop and smell fragrant, then transfer to a large bowl. Add the cabbage, peppers and vinegar, and season well. Mix thoroughly and set aside. Heat oven to low and put the bread in to warm through. Using the pan you used to toast the seeds, fry the halloumi with the olive oil in batches for 3 mins each side until crispy and golden. Transfer to the oven to keep warm. Spread a layer of hummus on each flatbread and top with a handful of slaw, halloumi and rocket to serve.','15 mins','25 mins'),
(332,'grilled courgette & halloumi salad with caper & lemon dressing','https://www.bbcgoodfood.com/recipes/grilled-courgette-halloumi-salad-with-caper-lemon-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Grilled-courgette-halloumi-salad-with-caper-and-lemon-dressing-08c8e2a.jpg','Dress this courgette and halloumi salad while everything is still fairly warm, then the courgettes will soak up the flavours of the caper and lemon dressing',60,4,1,1,7,'Put all the dressing ingredients in a bowl with 1 tbsp cold water and season with a pinch of salt. Mix together and set aside. Put the courgettes in a large bowl and drizzle with the oil. Add a pinch of salt, then toss. Heat a griddle pan to high and add the courgettes, cut-side down (it’s best to do this in batches). Cook for 4-5 mins until char marks appear and the flesh softens, then flip over and cook for another 3 mins. Remove to a shallow bowl and cover with foil to keep warm. Meanwhile, heat a separate large non-stick frying pan and add the halloumi. Cook for a few minutes until golden on both sides. Arrange the courgettes and halloumi on a large plate or platter, then spoon over the dressing.','10 mins','20 mins'),
(1229,'hot chocolate espresso sauce','https://www.bbcgoodfood.com/recipes/hot-chocolate-espresso-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Hot-chocolate-espresso-sauce-9d6d89b.jpg','Enjoy this smooth coffee- and booze-laced chocolate sauce with your favourite cake, such as our burnt honey and almond cakes',7,2,1,2,3,'Warm the cream in a small pan over a low heat for a few minutes. Remove from the heat and stir in the remaining ingredients until you have a smooth sauce. Will keep chilled for up to two days.','10 mins','5 mins'),
(334,'halloumi & chipotle loaf','https://www.bbcgoodfood.com/recipes/halloumi-chipotle-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Halloumi-and-chipotle-loaf-cbd751c.jpg','Enjoy a loaf filled with delicious textures and kicking flavours, with creamy halloumi and the heat of smoky, sweet chipotle. Enjoy with lashings of butter',61,10,1,1,7,'Weigh the flour out into a large bowl. Rub in the softened butter using your fingers until the mixture resembles fine crumbs. Add the yeast and sugar to one side of the bowl, and the salt on the other. Using a spatula, mix everything together to combine. Make a well in the centre and pour in 300ml warm water. Using the spatula, mix until you have a rough dough. Tip the dough out onto the work surface and bring together into a large mound. Knead the dough by hand for 10-15 mins or in a stand mixer fitted with the dough hook for 6-8 mins on high speed until smooth and elastic. Oil a large bowl and drop in the dough. Cover and leave to prove somewhere warm for 1 hr until doubled in size. Once the dough has doubled, tip it out onto the work surface and flatten it to remove any air pockets. Sprinkle over the halloumi in an even layer, then the chipotle flakes. Fold the dough over and knead in the cheese and chilli until evenly distributed. Line a large baking tray with baking parchment. Tuck the edges of the dough into the centre until you have a neat dough ball. Transfer this to the tray, seam-side down, cover with a clean tea towel and leave to prove for another hour until doubled in size again. You will know the dough is ready when you poke it with your finger – if it springs back slowly and a small indent remains, it’s ready. Heat the oven to 240C/220C fan/gas 7 with a roasting tin on the bottom shelf. Uncover the dough and lightly sieve some flour over it. Cut a cross into the top using the tip of a sharp serrated knife. Put the loaf on its tray into the roasting tin, then pour some hot water into the tin around the tray to create steam while baking. This will give you a crisp crust. Bake for 20-25 mins until the loaf is golden and sounds hollow when you tap the base. Remove from the oven and and leave to cool on a wire rack. Cut into slices and enjoy with lashings of butter.','35 mins','25 mins'),
(335,'halloumi fajitas','https://www.bbcgoodfood.com/recipes/halloumi-fajitas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-fajitas-ac81223.jpg','Try these halloumi tortilla wraps with avocado for a quick and easy after-school meal. Serve with chilli sauce or soured cream',6,4,1,1,7,'Split each halloumi block through the middle lengthways (they should have a natural seam). Cut each half into four strips. Heat half the oil in a frying pan over a medium heat, and fry the halloumi strips for a few minutes on each side until golden. Transfer to a plate and set aside. Heat the remaining oil in the pan over a high heat, and fry the onion and peppers for 5 mins until just soft and starting to char. Add the garlic, paprika and coriander with some seasoning and cook for 1 min more. Pour in the lime juice and fried halloumi, and stir very gently so the halloumi doesn’t break up. Will keep at room temperature for a few hours. Reheat over a low heat with a little water. Warm the wraps in a low oven wrapped in foil. Alternatively, warm two at a time for 10 seconds in the microwave (not wrapped in foil), or by holding them directly over a gas flame with a pair of tongs (this will give you flavourful charred edges).  Pile the halloumi filling into the warm wraps, with a few slices of avocado each, some coriander leaves, and spoonfuls of chilli sauce or soured cream, if you like.','15 mins','25 mins'),
(336,'halloumi fries','https://www.bbcgoodfood.com/recipes/halloumi-fries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-fries-221b9b7.jpg','Crisp and slightly salty shallow-fried halloumi fries make perfect party food. Great with a sprinkling of za''atar and a spicy yogurt for dipping',16,6,1,1,7,'Mix the yogurt with the lemon zest and some seasoning, then swirl through the harissa so that you have pockets of hot and cool in the dip. On a plate, stir the za’atar into the flour, then roll the halloumi in the mixture so that it’s evenly coated. Heat the oil in a shallow, heavy-bottomed pan or casserole dish until 180C on a cooking thermometer, or a piece of bread browns in 20 secs. Working in batches, carefully lower the halloumi into the oil and cook for 2 mins until crisp and golden, then drain on kitchen paper. Sprinkle over the mint and za’atar, and serve with the lemon wedges and the spicy yogurt for dipping.','10 mins','10 mins'),
(337,'miso mushroom & halloumi burgers','https://www.bbcgoodfood.com/recipes/miso-mushroom-halloumi-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Miso-mushroom-and-halloumi-burgers-a1f5a05.jpg','Elevate mushrooms with miso butter to enhance their umami flavour, then stack them with halloumi for a satisfying veggie burger at your next barbecue',52,4,1,1,7,'For the miso butter, beat all the ingredients together in a small bowl, then set aside. Light the barbecue. When the coals are ashen, tip more coals into one side so you have two heat zones. Brush the mushrooms and onion with some of the miso butter, then put on the grill and cook for 10 mins, basting and turning occasionally. (When you turn the onions, use a spatula so they don’t separate into rings.) When the veg is charred and has softened, move it to the edge of the cooler side of the barbecue to keep warm. Continue to baste with the butter occasionally. Cook the halloumi directly on the grill until browned and lightly charred. When the halloumi is done, brush the cut sides of the buns with the remaining miso butter and toast on the grill. Fill the buns with the mushrooms, onions, halloumi, tomatoes, rocket and burger sauce.','20 mins','30 mins'),
(353,'courgette & goat’s cheese ciabatta','https://www.bbcgoodfood.com/recipes/courgette-goats-cheese-ciabatta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/couegette-578ad76.jpg','Simple sandwiches to whip up as a speedy vegetarian supper or lunch',5,2,1,1,7,'Drizzle the courgette slices with a little olive oil. Cook on a hot griddle pan (or in a frying pan) for about 2 mins on each side until tender. Toast the ciabatta and gently rub with the cut garlic clove. Whisk together the lemon juice with the remaining oil and some seasoning. Spread the ciabatta with the soft goat’s cheese and top with the courgette slices, a little dressing, the lemon zest and basil. Serve with a salad, if you like.','10 mins','5 mins'),
(338,'spiced halloumi & pineapple burger with zingy slaw','https://www.bbcgoodfood.com/recipes/spiced-halloumi-pineapple-burger-zingy-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-burger-76608d7.jpg','Pack four of your 5-a-day into these tasty veggie burgers with barbecued halloumi. Wrap in lettuce cups instead of buns for a healthy, low-calorie option',16,2,1,1,7,'Heat the barbecue. Put the cabbage, carrot, radish and coriander in a bowl. Pour over the lime juice, add ½ tbsp oil and the chilli flakes, then season with salt and pepper. Give everything a good mix with your hands. This can be done a few hours before and kept in the fridge. Mix the remaining oil with the chipotle paste then coat the halloumi slices in the mixture. Put the halloumi slices on a sheet of foil and put on the barbecue with the pineapple (or use a searing hot griddle pan if cooking inside). Cook for 2 mins on each side until the cheese is golden, and the pineapple is beginning to caramelise. Brush the buns with the remaining chipotle oil, then put your burger buns, if using, cut-side down, on the barbecue for the last 30 seconds of cooking to toast. Assemble your burgers with the lettuce or buns. Start with a handful of the slaw, then add halloumi and pineapple. Serve with the remaining slaw.','20 mins','5 mins'),
(339,'halloumi & beetroot open sandwich','https://www.bbcgoodfood.com/recipes/halloumi-beetroot-open-sandwich','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-and-beetroot-sandwich-2c4f255.jpg','This simple veggie sandwich has a spoonful of hummus, slices of grilled halloumi and fresh beetroot. A filling, vibrant lunch to perk up your plate',62,1,1,1,7,'Bring a saucepan of water to the boil, add the broad beans and cook for 1-2 mins. Drain and run under cold water, then peel off the outer skins. Put the beans to one side. Heat grill to its highest setting. Put the halloumi on a baking tray and grill for 3 mins, turning halfway through to brown on both sides. Toast the bread and transfer to a serving plate. Spread the hummus on the bread, then top with the beetroot and grilled halloumi. Scatter over the broad beans and lemon juice to serve.','15 mins','7 mins'),
(340,'squash & halloumi flatbreads with sweet chilli beetroot jam','https://www.bbcgoodfood.com/recipes/squash-halloumi-flatbreads-with-sweet-chilli-beetroot-jam','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Squash-and-halloumi-flatbreads-b6efd2c.jpg','Do something different for a family dinner with squash and halloumi flatbreads with sweet chilli beetroot jam. Other squash or sweet potato work well, too',63,4,1,1,7,'Heat the oven to 200C/180C fan/gas 6. Prepare the squash by halving it, scooping out the seeds, then cutting into 1cm-thick slices. Put in a large bowl with the olive oil, smoked paprika and a pinch of salt. Toss well to coat the squash, then lay on a baking tray and bake at 180C/160C fan/gas 4 for about 40 mins until soft and starting to brown on the surface. For the chilli jam, grate the beetroot (wear gloves to prevent staining your hands) and put in a saucepan with the sugar, vinegar, chilli flakes, cumin seeds and salt to taste. Cook for a few minutes over medium heat until the sugar has dissolved. Remove from the heat and set aside. To make the flatbreads, tip the flour into a bowl and add 150g yogurt, or up to 200g if the dough looks too dry, then mix using a cutlery knife until it comes together into a ball. Tip the dough onto a work surface and knead for 2 mins until smooth. Split into four balls, then dust the work surface with flour and roll each into a large flatbread (or make eight mini ones). Dry-fry the flatbreads in a frying pan on medium heat for a couple of minutes on each side. Just before serving, cut the halloumi into 8 thin slices. Cook in a frying pan for a couple of minutes on each side. Layer the spinach, squash and halloumi on the flatbreads, then drizzle with the beetroot chilli jam to serve.','20 mins','45 mins'),
(341,'tomato & halloumi slice','https://www.bbcgoodfood.com/recipes/tomato-halloumi-slice','https://images.immediate.co.uk/production/volatile/sites/30/2020/11/Tomato-and-halloumi-slice-39f7fe1.jpg','Top some puff pastry with cherry tomatoes, peppers and halloumi to make an easy family lunch or supper. Scatter over some basil leaves to serve',64,4,1,1,7,'Heat the oven to 160C/140C fan/gas 3. Arrange the tomatoes and peppers on a baking tray in a single layer. Drizzle with half the oil, season with salt and roast for 25 mins. Meanwhile, heat a griddle pan over a medium heat. Lightly brush the halloumi slices with the remaining oil and griddle for 6 mins. Flip and cook for 3 mins more. Remove from the heat and set aside. Roll the puff pastry out on a lightly dusted surface to roughly 32 x 22cm, and about 1cm thick. Transfer to a large baking tray lined with baking parchment and score a 2cm border around the edges. Combine the soft cheese, 2 tsp milk and the garlic, and spread inside the border. Turn the oven to 200C/180C fan/gas 4. Tip away any liquid from the tray of tomatoes and peppers, then arrange the roasted veg over the soft cheese mix. Lay the halloumi on top. Combine the egg yolk and remaining milk, then brush this over the border. Bake for 25 mins until the pastry is golden and risen. Scatter over the basil and serve immediately.','15 mins','50 mins'),
(342,'halloumi hash','https://www.bbcgoodfood.com/recipes/halloumi-hash','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Halloumi-hash-91968c3.jpg','Whip up this substantial halloumi hash for brunch. You only need a handful of ingredients and a frying pan, making it the perfect food for camping',6,2,1,1,7,'Heat half the oil in a large frying pan and cook the potato over a low-medium heat for 10-15 mins, stirring often until softened in the middle and crisp on the outside (it will take some time for the potato to cook through). Add the white parts of the spring onion and the paprika. Cook for another minute, stirring. Push the potatoes to one side of the pan, then add the rest of the oil to the other side and fry the chunks of halloumi until slightly golden. Mix the potatoes in with the halloumi and some seasoning. Divide the hash between plates and keep warm. Crack the eggs into the pan and fry until cooked to your liking. Top the hash with the eggs, then drizzle with chilli sauce and scatter over the green parts of the spring onions.','10 mins','30 mins'),
(343,'bean & halloumi stew','https://www.bbcgoodfood.com/recipes/bean-halloumi-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bean-halloumi-stew-d5940b2.jpg','Use a can of mixed beans to make this tomato-based veggie stew with halloumi. Make it in just 25 minutes for a quick and easy midweek supper',14,2,1,1,7,'Heat 2 tbsp of oil in a frying pan over a medium heat. Add the onion and pepper, along with a pinch of salt and fry for 10 mins or until softened. Add the garlic, pesto and ground coriander, and cook for 1 min. Tip in the beans and tomatoes along with ½ can of water, then bring to a simmer and cook uncovered for 10 mins. Add the remaining oil to a separate frying pan over a medium heat. Fry the halloumi for 2 mins on each side or until golden brown. Taste the beans for seasoning, then spoon into deep bowls. Top with the halloumi and scatter over the chopped coriander. Serve with garlic bread, if you like.','5 mins','20 mins'),
(344,'halloumi & quinoa fattoush','https://www.bbcgoodfood.com/recipes/halloumi-quinoa-fattoush','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-quinoa-fattoush-a7d564f.jpg','With griddled halloumi, toasted pitta pieces, grains, tomatoes, dill and mint, this Middle Eastern-inspired salad makes a speedy and satisfying lunch for four',14,4,1,1,7,'Heat the grill to high. Toss the pitta pieces with 1 tbsp oil and spread out onto a baking sheet. Grill for 3-4 mins, turning halfway, until golden and crisp. Set aside to cool. Meanwhile, whisk together the remaining oil with the lemon juice and garlic, then season. Heat a large griddle pan or non-stick frying pan over a high heat and cook the halloumi for 1-2 mins on each side or until lightly charred. Cook the quinoa following pack instructions, leave to cool, then toss with the tomatoes, cucumber, spring onion, most of the fresh herbs and the dressing. Season to taste. Tip onto a serving plate and top with the halloumi, pitta and remaining herbs.','7 mins','10 mins'),
(345,'roasted vegetable quinoa salad with griddled halloumi','https://www.bbcgoodfood.com/recipes/roasted-vegetable-quinoa-salad-griddled-halloumi','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roasted-halloumi-77a74bc.jpg','A cheese and grain salad with vibrant roast beetroot, squash and red onion. It tastes as good as it looks, plus it''s 3 of your 5-a-day',65,4,1,1,7,'Heat oven to 220C/200C fan/gas 7. Put the beetroot, squash and red onion in a large roasting tin. Cut the top off the garlic bulb (the garlic is for the dressing) and drizzle with a little of the olive oil before wrapping in foil and adding to the tin. Season the vegetables and pour the remaining oil over them. Roast for 40-45 mins, turning the vegetables halfway through. Meanwhile, put the quinoa and stock in a medium saucepan over a high heat. Bring to the boil, then cover with a lid and simmer for 15 mins. Drain and return to the pan, off the heat. When the vegetables are roasted, set the garlic bulb aside and stir the remaining vegetables through the quinoa. To make the dressing, carefully squeeze the roasted garlic from the bulb into a small bowl. Add the lemon juice, oil, honey and seasoning and mix. Put a griddle pan over a high heat. When it’s really hot, add the halloumi and griddle for 45-60 secs either side (you may need to do this in batches). Spoon the quinoa and vegetables onto plates and top with the halloumi and dressing.','15 mins','45 mins'),
(346,'oregano halloumi with orzo salad','https://www.bbcgoodfood.com/recipes/oregano-halloumi-orzo-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/oregano-halloumi-with-orzo-salad-5adba3e.jpg','This affordable dish featuring slightly salty, herby halloumi is a great meat free meal. Any leftovers make a great take-to-work lunch for the next day',1,4,1,1,7,'Bring a large pan of salted water to the boil and cook the orzo following pack instructions. Meanwhile, mix the chopped oregano in a small bowl with the oil and brush some over the halloumi. Heat a large, non-stick frying pan and cook the halloumi for a few mins each side until golden and soft. Drain the cooked orzo and mix with the tomatoes, olives and pesto. Season to taste. Spoon onto a serving plate and top with the halloumi. Drizzle over any remaining oregano oil and scatter over the leaves.','10 mins','8 mins'),
(347,'halloumi with tomatoes & pomegranate molasses','https://www.bbcgoodfood.com/recipes/halloumi-tomatoes-pomegranate-molasses','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-with-tomatoes-pomegranate-molasses_0-2255eb6.jpg','Salty halloumi cheese, sweet pomegranate molasses and fresh mint make a delicious combination in this quick Lebanese meze dish',66,2,1,1,7,'Pour the olive oil into a medium bowl, add the za’atar and stir to combine. Add the halloumi and toss in the mixture until well coated. Heat a large griddle pan. Place the halloumi in the pan and cook for 1-2 mins, then turn over and cook for a further 1-2 mins until golden brown on both sides. After turning the halloumi, add the cherry tomatoes and move them around the pan quickly so they cook all over. Transfer the halloumi and tomatoes to a plate, then drizzle over the pomegranate molasses and serve with the mint leaves and pomegranate seeds scattered over.','5 mins','8 mins'),
(348,'halloumi, carrot & orange salad','https://www.bbcgoodfood.com/recipes/halloumi-carrot-orange-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-carrot-orange-salad-64b2d8b.jpg','Serve this halloumi, carrot, orange and watercress salad with a mustard and honey dressing for an easy lunch or starter. It takes just 20 minutes to make and delivers one of your 5-a-day',5,4,1,1,7,'Cut the peel and pith away from the oranges. Use a small serrated knife to segment the orange, catching any juices in a bowl, then squeeze any excess juice from the off-cut pith into the bowl as well. Add the mustard, honey, vinegar, oil and some seasoning to the bowl and mix well. Using a vegetable peeler, peel carrot ribbons into the dressing bowl and toss gently. Heat a drizzle of oil in a frying pan and cook the halloumi for a few mins until golden on both sides. Toss the watercress through the dressed carrots. Arrange the watercress mixture on plates and top with the halloumi and oranges.','5 mins','15 mins'),
(349,'tamarind squash & halloumi skewers','https://www.bbcgoodfood.com/recipes/tamarind-squash-halloumi-skewers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tamarind-squash-halloumi-skewers-224ba6e.jpg','Do something different with barbecued halloumi. Marinated in spices, these cheese and butternut squash skewers are an irresistible mix of sweet and savoury',67,4,1,1,7,'Bring a large pan of salted water to the boil. Add the squash and cook for 10-15 mins or until slightly tender (if you’re going to cook the squash in a griddle pan, boil for an extra few mins as it’s not as fierce as a barbecue). Drain and set aside. To make the marinade, whisk the tamarind with 2 tbsp boiling water. Once dissolved, whisk together with the soy sauce, sesame oil, garlic, lime zest and juice, chilli and brown sugar. Keep whisking until the sugar has dissolved, then season to taste. In a large bowl, toss together the squash, halloumi, marinade and herbs. Allow to marinate for at least 1 hr. Once marinated, alternately thread the squash and halloumi onto the skewers. Drizzle with a little of the remaining marinade. If barbecuing, put the skewers directly on the BBQ and cook for 3-4 mins on each side or until lightly charred, brushing with the marinade as they cook. Alternatively, heat a griddle pan until really hot and cook the skewers for 5 mins on either side. Serve immediately.','15 mins','25 mins'),
(350,'baked ratatouille & goat’s cheese','https://www.bbcgoodfood.com/recipes/baked-ratatouille-goats-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ratatouille_1-39936da.jpg','Transform ratatouille into a bake with a layer of cheese sauce and you won''t look back. It makes a great late summer dish and delivers three of your 5-a-day',68,6,1,1,7,'Heat 1 tbsp olive oil in a heavy frying pan and gently cook the red onion and garlic for 5 mins until just starting to brown. Scatter over the aubergine and red pepper and sizzle for another 4 mins, adding more oil if you need to. Season well and stir through the paprika, then splash in the vinegar and soy sauce. Pour over the passata, then simmer for 5 mins until glossy and thick. Turn off the heat. Can be made up to two days ahead and kept in the fridge. To make the cheese sauce, heat the milk in a heavy saucepan until simmering. In a separate pan, melt the butter and add the flour. Stir to make a paste. Slowly whisk in the hot milk and simmer gently for 2-3 mins, stirring continually until thickened slightly. Stir in the parmesan and season. Tip the ratatouille into an ovenproof dish, then completely cover it with the cheese sauce. Crumble over the goat’s cheese and arrange the courgette slices on top in concentric circles, alternating between green and yellow if you have them. Can be assembled several hours before cooking. Heat oven to 220C/200C fan/gas 7. Brush or drizzle the remaining oil over the courgette slices and season with sea salt. Bake in the oven for 25-30 mins until the courgettes are cooked through and starting to brown. Remove from the oven and allow to rest for 20 mins before serving.','35 mins','45 mins'),
(351,'goat’s cheese & caramelised onion frittata with a lemony green salad','https://www.bbcgoodfood.com/recipes/goats-cheese-caramelised-onion-frittata-lemony-green-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/frittata-c9d5e3d.jpg','A grilled omelette with sweet onion base, dotted with creamy cheese and served with salad - this recipes make enough for lunch the next day',45,4,1,1,7,'Heat the grill to high. Put the oil and onions in a medium-to-large ovenproof non-stick frying pan and sweat on a low-medium heat with the lid on for about 10 mins, or until they begin to soften and brown a little at the edges. Reduce heat to low, add the honey, stir well, then leave to bubble for 1 min or so. Meanwhile, crack the eggs into a bowl and beat with a fork, then add some black pepper. Pour the eggs into the pan and cook for 5-6 mins until almost set. Break the goat’s cheese into large chunks and dot over the top. Place the frittata under the hot grill for 3 mins, then shake to check that the egg is set firm and the cheese is soft and bubbling. Pop it back under the grill for 1 min or so more if needed. Mix the salad leaves and beetroot in a large bowl. Dress with the lemon juice and toss. Cut the frittata into quarters – cool then chill half for Frittata and salad (see ''goes well with''). Serve the rest warm with the salad on the side.','5 mins','20 mins'),
(352,'blue cheese gnocchi','https://www.bbcgoodfood.com/recipes/gnocchi-mushrooms-blue-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gnocchi-with-mushrooms-ef4cba5.jpg','Soft, creamy goat''s cheese or a deliciously strong blue cheese both work well in this easy veggie supper that''s on the table in just 20 minutes',24,4,1,1,7,'Bring a large pan of water to the boil and cook the gnocchi following pack instructions. When they float to the top of the pan, they are ready. Drain and set aside. Meanwhile, heat the oil and butter in a large lidded frying pan. Add the onion and mushrooms, cook for 1 min over a high heat, then turn down the heat to medium, put the lid on and cook for 5 mins, stirring a few times. Remove the lid and add the garlic, cook for 1-2 mins, then stir the gnocchi into the pan. Scatter over blobs of cheese and the parsley.','10 mins','10 mins'),
(354,'goat''s cheese, potato & onion tart','https://www.bbcgoodfood.com/recipes/goats-cheese-potato-onion-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1487_11-2e20510.jpg','This tart is easy to make, looks stunning and tastes wonderful. Best of all, you can make it ahead and reheat from frozen',23,6,2,1,7,'Heat oven to 190C/fan 170C/gas 5. Roll out the pastry so it is large enough to fit a deep 23cm loose-bottomed tart tin, then line the tin with the pastry. Chill for 30 mins. Line the pastry with greaseproof paper, fill with baking beans and bake for 15 mins. Remove the paper and beans and return the tin to the oven for 5 mins until golden, then remove and set aside. Reduce oven to 180C/fan 160C/gas 4. Heat the butter and oil in a large frying pan, add the onions and potato and cook over a very gentle heat for 10-15 mins, turning occasionally, until golden and tender. Season if you want to, then add the garlic and thyme leaves, and cook for a further 3 mins. Tip into the pastry case along with the goat’s cheese and spread out evenly. Whisk the eggs and crème fraîche together, season if you want to, then pour into the pastry case and bake for 30-35 mins until lightly golden brown and just set in the centre. Serve warm, with rocket leaves scattered over if using.','1 hr','No Time'),
(355,'pesto & goat''s cheese risotto','https://www.bbcgoodfood.com/recipes/pesto-goats-cheese-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pesto-goats-cheese-risotto-cb2c34b.jpg','A risotto that''s bursting with fresh Italian flavours - simple to make, it''s the perfect no-fuss midweek meal for two',14,2,1,1,7,'Pour a glug of olive oil into a large saucepan. Tip in the rice and fry for 1 min. Add half the stock and cook until absorbed. Add the remaining stock, a ladle at a time, and cook until the rice is al dente, stirring continually, for 20-25 mins. Stir through the pesto and half the goat’s cheese. Serve topped with the remaining cheese.','2 mins','30 mins'),
(356,'goat’s cheese, pear & walnut tartines','https://www.bbcgoodfood.com/recipes/goats-cheese-pear-walnut-tartines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/goats-cheese-pear-walnut-tartines--18fc014.jpg','Top toasted sourdough with goat''s cheese, honey-roasted pears and walnuts to make these tasty tartines. They take just two minutes to prep and deliver one of your 5-a-day',5,4,1,1,7,'Heat a griddle pan. Cut the pears into quarters or eighths, brush with the oil and season. Cook for a few mins until charred on all sides, then drizzle with the honey. Meanwhile, toast the bread. Spread with the goat’s cheese and top with the sticky pears, chives and walnuts. Drizzle over the balsamic vinegar and serve with some dressed salad leaves.','2 mins','15 mins'),
(357,'roasted beetroot & goat''s cheese salad','https://www.bbcgoodfood.com/recipes/roasted-beetroot-goats-cheese-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roasted-beetroot-goats-cheese-salad-f5615c2.jpg','Tangy balsamic vinegar, sweet beetroot and creamy goat''s cheese combine beautifully in this easy vegetarian salad that''s sure to impress, containing four of your five-a-day',1,2,1,1,7,'Heat oven to 200C/180C fan/gas 6 and put the beetroot in a roasting tin with 1 tbsp oil, 1 tbsp vinegar and plenty of seasoning. Roast for 8-10 mins until sticky. Meanwhile, bring a pan of salted water to the boil, add the green beans, cook for 1 min, then drain. Toss in with the beetroot and roast for a further 5 mins. Meanwhile, make the dressing by combining the remaining oil and vinegar in a small bowl, and season well. Put the mixed leaves and cucumber in a bowl and toss together with a little dressing. Dip both cheese halves in the chopped walnuts so the tops are covered, then pop in the roasting tin with the beetroot and green beans for a few mins, to soften and toast the nuts. Pile the salad onto plates and serve with the remaining dressing drizzled over the top.','15 mins','20 mins'),
(358,'chicken stuffed with goat’s cheese & tarragon','https://www.bbcgoodfood.com/recipes/chicken-stuffed-goats-cheese-tarragon','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken_1-3a2a725.jpg','Versatile chicken breasts work perfectly when stuffed with cheese, tarragon and chilli and wrapped in crispy proscuitto',50,4,1,1,7,'Heat oven to 190C/170C fan/gas 5. Beat together the goat’s cheese, lemon zest, chilli, tarragon and some seasoning. Cut a slit in the side of each chicken breast, ensuring that you don’t pierce through the other side. Using your fingers, make a pocket and stuff the cheese mix inside. Season the chicken breasts, then wrap 2 slices of prosciutto around each, covering the pocket tightly. Put on a baking tray, drizzle with olive oil and cook for 18-20 mins until cooked through but still moist, and the juices run clear.','15 mins','20 mins'),
(364,'broccoli & goat’s cheese pizzettes','https://www.bbcgoodfood.com/recipes/broccoli-goats-cheese-pizzettes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/broccoli-goats-cheese-pizzettes-cc79d3d.jpg','Create something different for dinner during the week with the help of shop-bought pizza dough, broccoli, goat''s cheese, courgette and red chilli',14,2,1,1,7,'Heat the oven to 240C/220C fan/gas 9. Put a baking sheet in the oven to heat up. Bring a medium pan of water to the boil, add the broccoli and boil for 1 min. Drain and pat dry with kitchen paper. Lightly dust your work surface with a little flour. Split the dough in half and roll into two oval shapes. Mix the mascarpone and lemon zest together in a bowl. Remove the hot tray from the oven and use a palette knife to transfer the bases onto the baking tray. Spread with the mascarpone and top with the broccoli and courgette, then season with black pepper. Put back in the oven to bake for 10-12 mins or until crisp and golden around the edges.  Scatter with the crumbled goat’s cheese and red chilli, and drizzle over a little olive oil to finish.','15 mins','15 mins'),
(359,'goat''s cheese & watercress quiche','https://www.bbcgoodfood.com/recipes/goats-cheese-watercress-quiche','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3570_11-0e63d52.jpg','A goat''s cheese and watercress quiche that''s gluten-free and truly flavoursome',5,4,1,1,7,'Put the flour and a pinch of salt in a large bowl. Rub in the butter until the mixture looks like breadcrumbs. Stir in the egg and about 1-2 tbsp water to form a soft dough. Knead well then wrap and chill for at least 30 minutes. Preheat the oven to 200C/gas 6/fan 180C. Knead the pastry again and roll it out to line a 20cm loose-bottomed flan tin (the pastry won’t shrink, so no need to chill). Patch holes with spare pastry and brush the base and sides with egg. For the filling, fry the onion in the oil until just soft, add the watercress and cook until just wilted. Beat the eggs and milk and stir in the watercress mixture and goat''s cheese. Season and if you like, add a pinch of nutmeg. Put the flan tin on a bakingsheet and bake the pastry blind for 5 minutes. Add the filling and bake for 15 minutes. Reduce heat to 180C/gas 4/ fan 160C and cook for 25-30 minutes or until lightly set.','1 hr','No Time'),
(360,'tomato tarts with roasted garlic & goat’s cheese','https://www.bbcgoodfood.com/recipes/tomato-tarts-roasted-garlic-goats-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomatotarts-cf9ef6e.jpg','Use up cherry tomatoes in these little puff pastry tartlets - get ahead by roasting the three whole garlic bulbs the night before',5,4,1,1,7,'Heat oven to 190C/170C fan/gas 5. Slice the tops off the garlic bulbs and sit on a sheet of foil. Drizzle over 1 tbsp of the oil, season and wrap into a parcel. Roast for 50 mins or until soft, then leave to cool – this can be done the day before. Use a sharp knife to cut the pastry into quarters. Roll out each piece and cut into a 14cm-diameter circle. Use a saucer as a guide if you don’t have a cutter. Pop the circles on a baking tray and score a 1cm border around the edge of each, being careful not to cut all the way through. Avoiding the border, prick the pastry all over with a fork, then chill. Increase oven to 200C/180 fan/gas 6. When the garlic is cool enough to handle, squeeze the flesh from the bulbs into a bowl. Add the mustard, remaining oil and some seasoning, and use a fork to mash to a paste. Spread this over the pastry, leaving the border clear, then top with the tomato halves, skin-side down, and season well. Brush the edges of the pastry with egg, crumble over the goat’s cheese and bake for 25-30 mins until the pastry is golden and crisp, and the tomatoes are starting to shrivel. Remove from the tray and allow to cool to room temperature. Scatter over the basil before serving.','2 mins','1 hr and 20 mins'),
(361,'goat''s cheese & red pepper tart','https://www.bbcgoodfood.com/recipes/goats-cheese-red-pepper-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259757_8-73307ba.jpg','There is a little work involved in this, but there is so much satisfaction in bringing it to the table to share with family or friends',43,4,2,1,7,'Cook the onions in the oil for 6-7 mins, until they are softened and lightly coloured, then add the balsamic vinegar and 1 tbsp water. Cook for a further 2-3 mins. Roughly chop the olives and stir in. Leave to cool. Halve the peppers, remove the seeds and core. Put under a hot grill, skin side up, until the skins are charred. Remove and put into a food bag until cool enough to handle, then peel off the skins. Heat oven to 200C/fan 180C/gas 6. Roll out the pastry and line a 23-24cm deep flan tin. Line with baking paper and beans, then bake for 10 mins. Remove the paper and beans, then bake for 5 mins more, until the pastry is golden and crisp. Spread the onion mix over the base of the pastry case. Put the halved peppers on top, cut side up with tapered ends towards the centre. Slice the goat’s cheese and put a few slices in each pepper half. Tuck the tomatoes in the gaps between the peppers. Bake for 20-25 mins until the cheese is lightly tinged brown and the tomatoes have burst. Scatter over the herb leaves and serve warm or at room temperature.','25 mins','40 mins'),
(362,'goat''s cheese & thyme stuffed chicken','https://www.bbcgoodfood.com/recipes/goats-cheese-thyme-stuffed-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259708_7-eda6d4a.jpg','The chicken, goat’s cheese and bacon combo is totally delicious, and the veg adds a welcome sweetness. Serve with a few steamed Jersey Royals',43,2,1,1,7,'Heat oven to 190C/fan 170C/gas 5. Split the chicken breasts almost in half from one long side and open them out like a book. Bat them out a little with a rolling pin to flatten them. Season on all sides. Put half the goat’s cheese on each piece of chicken and sprinkle with the thyme leaves. Fold the chicken over to enclose the cheese, then wrap each one in two slices of the bacon. Lightly oil a shallow gratin dish, then arrange overlapping rows of courgettes and tomatoes over the base. Drizzle with olive oil and sprinkle with salt, pepper and thyme sprigs. Sit the chicken on top. Bake for 40-45 mins until the bacon is crisp and golden and the courgettes are tender. Serve straight from the dish with new potatoes or good bread.','40 mins','45 mins'),
(363,'goat''s cheese, spring onion & parsley soufflés','https://www.bbcgoodfood.com/recipes/goats-cheese-spring-onion-parsley-souffles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259683_8-2ab6a74.jpg','These soufflés may take a while to perfect but once mastered, they''ll be a starter you return to time and again',43,4,3,1,7,'Heat oven to 190C/fan 170C/gas 5. Butter four 200ml ramekins or ovenproof dishes, or six 150ml ones. Set the dishes on a baking tray to make them easier to remove from the oven. Melt the butter in a heavy-based pan, stir in the flour and cook, stirring, for 1 min. Slowly mix in the milk to make a thick sauce. Simmer for 2 mins to cook the flour. Remove from the heat and stir in the parsley, spring onions, goat’s cheese, egg yolks, threequarters of the parmesan and seasoning. Whisk the egg whites until they are stiff, then fold into the sauce in three batches, cutting through the mixture each time to incorporate the egg white without losing too much air from the mix. Divide between the prepared dishes and sprinkle with the remaining parmesan. Bake for 20 mins for the small dishes and 25 mins for the larger ones until risen and golden. Serve without delay.','20 mins','25 mins'),
(365,'courgette, leek & goat’s cheese soup','https://www.bbcgoodfood.com/recipes/courgette-leek-goats-cheese-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-courgette-leek-goats-cheese-soup-9d16efd.jpg','This super-healthy vegetarian soup is low in calories and full of flavour. It''s packed with three of your 5-a-day, plus folate, fibre, vitamin C and iron',4,4,1,1,7,'Heat the oil in a large pan and fry the leeks for a few mins to soften. Add the courgettes, then cover the pan and cook for 5 mins more. Pour in the stock, cover and cook for about 7 mins. Add the spinach, then cover the pan and cook for 5 mins so that it wilts. Take off the heat and blitz until really smooth with a hand blender. Add the goat’s cheese and basil, then blitz again. If you''re making this recipe as part of our two-person Summer Healthy Diet Plan, spoon half the soup into two bowls or large flasks, then cool and chill the remainder for another day. Reheat in a pan or microwave to serve. If serving in bowls, scatter with some extra basil leaves and the seeds, and eat with the rye bread.','8 mins','17 mins'),
(366,'shallot tarte tatin with goat''s cheese','https://www.bbcgoodfood.com/recipes/shallot-tarte-tatin-goats-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-553458_11-831457d.jpg','James Martin''s easy, rustic cheese and onion tart is sure to become a weekend favourite',50,4,1,1,7,'Heat oven to 200C/180C fan/gas 6. Put the shallots into a heatproof bowl and pour over boiling water to cover. Leave for 10 mins, then drain and peel. Heat the oil and butter in a frying pan. Add the shallots and fry gently for 10-15 mins until softened and lightly browned. Stir in the vinegar and sugar with leaves from 2 thyme sprigs for a few mins until caramelised. Turn off heat and season. Tear remaining thyme sprigs into a few pieces and scatter over the base of a 22cm ovenproof shallow pan, cake or tart tin without a loose base. Tip in the shallots with all their sticky juices. Roll out the pastry until it’s big enough to cut out a roughly 26cm circle. Lift the pastry circle onto the shallots, then tuck the edges down the inside of the pan. Bake for 25-30 mins until pastry is golden. Leave tart for 5 mins to settle, then turn out of the tin. Dot with rounds of goat’s cheese and slice into wedges.','40 mins','45 mins'),
(367,'goat''s cheese & beetroot salad','https://www.bbcgoodfood.com/recipes/goats-cheese-beetroot-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259676_8-cd2bf78.jpg','Show off with minimal effort, this delicious salad is simple to prepare but looks so pretty on the plate',43,4,1,1,7,'Thinly slice the beetroot, either with a very sharp knife or on a mandolin. Arrange overlapping slices over four dinner plates. Put all the dressing ingredients into a jar, replace the lid, then shake well to mix. Drizzle a little over the beetroot. Slice the goat’s cheese into 12 and put 3 slices on top of each serving of beetroot. Toss the salad leaves in a little dressing, then put a small pile of leaves on top of each serving of cheese. Drizzle with the remaining dressing.','15 mins','No Time'),
(368,'caramelised onion & goat''s cheese pizza','https://www.bbcgoodfood.com/recipes/caramelised-onion-goats-cheese-pizza','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/goats-cheese-pizza-c23fce6.jpg','Make a veggie pizza that''s healthy and low-fat instead of ordering a takeaway. This easy recipe uses goat''s cheese for tang while the onions give it sweetness',4,2,1,1,7,'Heat oven to 220C/200C fan/gas 7. Tip the flour into a mixer with a dough hook, or a bowl. Add the yeast, salt, oil and just under 100ml warm water then mix to a soft dough. Knead in the food mixer for about 5 mins, but if making this by hand, tip onto a work surface and knead for about 10 mins. The dough is sticky, but try not to add too much extra flour. Leave in the bowl and cover with a tea towel while you make the topping. There is no need to let the dough prove for a specific time – just let it sit while you get on with the next step. Tip the onions into a non-stick wok and add the oil, 4 tbsp water and balsamic vinegar. Cover with a saucepan lid that sits inside the pan to help the onions soften, then cook for 15 mins, stirring about 3 times and replacing the lid quickly so as not to lose too much moisture. After the time is up, the onions should be golden and all the liquid gone. Tip onto a plate. Add the spinach and garlic to the pan and stir-fry until the spinach has wilted. Take the dough from the bowl and cut in half with an oiled knife, then press each piece into a 25-15 cm oval on a large greased baking sheet with oiled hands. Don’t knead the dough first otherwise it will be too elastic and it will keep shrinking back. Spread with the spinach followed by the onions, then dot with the cheese and scatter with the olives, thyme and sunflower seeds. Bake for 15 mins until golden and the base is cooked through.','20 mins','30 mins'),
(369,'three-cheese soufflés','https://www.bbcgoodfood.com/recipes/three-cheese-souffles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1256463_9-93c0d0c.jpg','With a double layer of goat''s cheese and a coating of cream, these bistro-style bakes are authentically rich',7,4,2,1,7,'Heat oven to 200C/180C fan/ gas 6 and butter 4 small (about 200ml) ramekins. Sprinkle the parmesan into the ramekins, turning until all sides are covered. Place the milk and bay leaves in a large saucepan over a gentle heat and bring to the boil. Turn off the heat and leave to infuse for 15 mins. Discard the bay leaves, add the butter and flour, and return to a low heat. Very gently simmer, stirring continuously with a balloon whisk, for about 6 mins until you get a smooth, thick white sauce. Make sure that you get right into the corners of the pan to stop the sauce from catching or becoming lumpy. Once thickened, transfer the sauce to a large bowl and stir in the mustard powder, cayenne pepper, gruyère and egg yolks until fully combined. In a spotlessly clean bowl and with a clean whisk, beat the egg whites just until peaks begin to form. Carefully fold the egg whites into the cheese sauce in three stages making sure you fold, rather than stir, to keep the egg whites light and airy. Fill the prepared ramekins with the soufflé mix. Top each soufflé with a slice of goat’s cheese, then place on a baking tray. Bake for 20-25 mins or until springy and well risen but cooked through. Leave to cool, then run a knife around the edge of each dish and remove the soufflés. If preparing in advance, place soufflés upside down (for neat presentation), on a tray. Cover tray in cling film. Chill for a few days or freeze for up to 1 month. When ready to re-bake, heat oven to 200C/180C fan/gas 6. Place the upside-down soufflés in a shallow baking dish, top with the remaining goat’s cheese slices and pour over the cream (this stops them from drying out when baked for the second time). Cook for 8-10 mins until golden. Serve immediately alongside some simply dressed salad.','20 mins','45 mins'),
(422,'bakewell trifles','https://www.bbcgoodfood.com/recipes/bakewell-trifles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/trifles-240adad.jpg','These cherry and almond desserts will really impress your guests. The amaretti biscuits give it a special touch',17,8,1,1,6,'Crumble the biscuits and cake, roughly mix and divide between 8 small glasses. Halve the cherries and divide between the trifles, then top with compote or jam. Beat together the custard and cream, then dollop on top. Scatter over the almonds, add a cherry half to each trifle and chill until ready to serve.','15 mins','No Time'),
(370,'onion & goat''s cheese tarte tatins','https://www.bbcgoodfood.com/recipes/onion-goats-cheese-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/onion-goats-cheese-tart-7fa38a6.jpg','Individual tarte Tatin-style bakes with puff pastry, caramelised onions, thyme and ash goat''s cheese make a sumptuous vegetarian lunch or dinner party starter',52,6,2,1,7,'Line a baking tray with baking parchment. Put the sugar in a heavy-based saucepan and melt over a medium heat. Cook to a dark caramel, occasionally swirling the pan gently to help melt any remaining sugar and get an even colour. Add the vinegar, stir to combine, and pour into the tray, spreading it out. Leave to cool. Heat oven to 180C/160C fan/gas 4. Add a splash of oil to an ovenproof frying pan over a medium-high heat. Lay the onions cut-side down and fry until blackened and just starting to burn, about 5 mins. Add the thyme sprigs, then transfer to the oven and bake for 25 mins. Once the onions are tender, remove from the pan. Space them out evenly, cut-side down, on the caramel and leave to cool. Once the onions are cool, cut the pastry into six equal discs, each large enough to cover an onion half. Cover the onions with the pastry, tucking it under slightly. Put the tray in the fridge for 1 hr so that the pastry firms up a bit. Heat oven to 200C/180C fan/gas 6. Bake the onion tarts for 20 mins, then reduce heat to 180C/160C fan/gas 4 and bake for 10 mins more. Remove from the oven and leave to cool. Once cool enough to handle, turn the tarts so the onions are now facing up. Lay a slice of goat’s cheese on top of each and bake for a further 10 mins. Remove from the oven and sprinkle over a few thyme leaves to serve.','25 mins','1 hr and 20 mins'),
(371,'aubergine & goat’s cheese salad with mint-chilli dressing','https://www.bbcgoodfood.com/recipes/aubergine-goats-cheese-salad-mint-chilli-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1255481_9-a9993b5.jpg','Packed with punchy flavours, this vegetarian main course salad contrasts earthy goat''s cheese with tangy dressing and roasted vegetables',10,4,1,1,7,'Heat oven to 200C/180C fan/ gas 6. Cut the aubergine into 3cm chunks. Drizzle with olive oil and season. Arrange on a baking tray and roast for 25 mins or until browned. Rip the bread into pieces and put on a baking tray. Just as the aubergine is nearly ready, pop the bread into the oven and bake for 8 mins. To make the mint-chilli dressing, mix all the ingredients together in a small bowl and season. Toss about one-third of the dressing with the aubergine and save the rest to pour over the salad. Arrange the tomatoes, salad leaves, shallots, dressed aubergine and crisp bread on a large platter. Drizzle the reserved dressing over and scatter the goat’s cheese on top.','15 mins','25 mins'),
(372,'spinach & goat’s cheese puff','https://www.bbcgoodfood.com/recipes/spinach-goats-cheese-puff','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1233476_9-f88c290.jpg','Top shop-bought puff pastry with wilted greens, flavoured with nutmeg and some salty cheese - a great-value supper or lunchbox option',4,4,1,1,7,'Heat oven to 220C/200C fan/gas 7. Unroll the pastry and put in a large Swiss roll tin. Trim any excess pastry at either end if necessary. Prick all over with a fork, then bake for 10 mins. Use a hand blender to blitz the spinach with the eggs, milk, half the goat’s cheese and nutmeg, until the spinach is chopped but not puréed. Take the pastry from the oven and press down the centre, as it will have puffed up. Spoon on the filling, scatter with the rest of the cheese, then bake for 15 mins until the filling is set.','10 mins','25 mins'),
(373,'harissa veg, goat’s cheese & couscous salad','https://www.bbcgoodfood.com/recipes/harissa-veg-goats-cheese-couscous-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1214459_11-4eccdc4.jpg','Ditch your boring sandwich and make an exciting lunch from your leftover roasted vegetables - squash, peppers and aubergine work well',6,2,1,1,7,'Put the couscous in a bowl, season and add the lemon juice. Pour over 100ml boiling water from the kettle, cover with a tea towel and leave for a few mins until the liquid has been completely absorbed. Fluff up the couscous with a fork. Toss the leftover roasted veg with the harissa and any oil from the roasting tin. Add to the couscous with the cheese and rocket. Pack into tubs and chill until lunchtime, or eat straight away.','10 mins','No Time'),
(374,'cheese & caramelised onion tart','https://www.bbcgoodfood.com/recipes/cheese-caramelised-onion-tart','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Cheese-and-caramelised-onion-tart-03933ce.jpg','Entertain friends and family with Tom Kerridge’s cheese and onion tart that guarantees the sweetest caramelised onions and perfectly crisp pastry',52,8,2,1,7,'First, make the pastry. Tip the flour and butter into a bowl, then rub together with your fingertips until the mixture resembles coarse breadcrumbs. Mix in the egg, then bring everything together using your hands until just combined. Form into a disc. Will keep wrapped and chilled for up to two days or frozen for up to a month. Roll the pastry out on a lightly floured surface into a disc that’s roughly 28cm in diameter. Use the rolling pin to lift it over a 23cm tart tin, then drape it over the tin. Use a small ball of pastry scraps to push the pastry into the base and up the side of the tin, leaving the excess to overhang. Chill in the fridge for 20 mins. The tin can be lined and chilled up to a day ahead. Meanwhile, melt the butter in a pan over a low-medium heat and cook the onions for 45 mins, seasoning them generously with salt and stirring occasionally until they become sticky and golden. Pour in the cream, then add the nutmeg and stir. Bring to a simmer, then remove from the heat. Heat the oven to 200C/180C fan/gas 6. Lightly prick the base of the tart all over with a fork, then line with a large circle of baking parchment and fill with baking beans, dried rice or pulses. Bake for 20 mins, then remove the beans and parchment, and bake for 10-15 mins more until light brown. Break off a little of the the overhanging pastry to check that it’s done – it should be cooked through and crumbly. Meanwhile, stir the egg yolks into the onion and cream mixture, and stir in two-thirds of the cheese. Tip into the pastry case, then sprinkle over the rest of the cheese and bake for 20-25 mins, or until just set and golden. Leave to cool completely in the tin, trim the overhang using a serrated knife, then remove from the tin. Sprinkle with chives and more cheese, if you like. Any leftovers will keep chilled for up to two days. Reheat in a low oven before serving.','40 mins','2 hrs'),
(379,'tomato & harissa stew with cheddar dumplings','https://www.bbcgoodfood.com/recipes/tomato-harissa-stew-cheddar-dumplings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1237457_7-b9f7f9e.jpg','Jazz up a vegetarian casserole with some spicy North African flavours and hearty cheese dumplings',4,4,1,1,7,'Heat the oil in a large wide-topped casserole dish with a lid, then fry the onion and celery for 5 mins until softening and starting to colour. Tip in the tomatoes and a can of water, then stir in the harissa, courgettes and chickpeas, and crumble in the stock cube. Cover and simmer for 18 mins until the veg is almost tender. Heat oven to 200C/180C fan/gas 6. Meanwhile, rub the butter into the flour and baking powder with a good pinch of salt, then mix in the cheese with a round-bladed knife. Two mins before the stew is ready, pour the milk into the dumpling mix and stir with the knife to make a dough. Turn out onto your work surface (no need to flour it), lightly shape into a sausage and cut into 8 equal pieces. Put the dumplings on top of the stew and bake in the oven for 15-20 mins until golden and cooked through.','10 mins','45 mins'),
(375,'cheddar cheese & shallot pie with fennel seed pastry','https://www.bbcgoodfood.com/recipes/cheddar-cheese-shallot-pie-fennel-seed-pastry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheddar-cheese-shallot-pie-with-fennel-seed-pastry-234630e.jpg','A hearty pie with a mustard and fennel crust, this makes a stunning veggie centrepiece for everyday or entertaining',67,6,2,1,7,'Heat oven to 180C/160C fan/gas 4. First, make the filling. Boil the potatoes in a large pan of salted water for 10-15 mins until a knife is easily inserted but the potatoes still hold their shape. Melt the butter in a medium saucepan over a low heat. Slowly sweat the shallots and spring onions with a pinch of salt until softened, stirring occasionally, about 10-12 mins. Meanwhile, whisk together the eggs, cream, thyme, cheese and some seasoning. Set aside. To make the pastry, melt the butter with 100ml water in a small saucepan and bring to a simmer. Tip the rest of the pastry ingredients and 1 / 2 tsp salt into a large bowl. Make a well in the centre and pour in the butter. Stir with a wooden spoon, then bring together with your hands, kneading lightly for a few mins. Grease a large 20-26cm pie dish. On a lightly dusted work surface, roll out two-thirds of the pastry big enough to fill the base and sides of your dish. Use the remaining pastry to roll out a lid. Add the potato and shallot mixture to the beaten egg filling, and spoon into your pastry case. Cover with the pastry lid and crimp the edges decoratively. Finally, beat the egg yolk with the milk and glaze the pie. Poke a couple of small steam holes in the top, chill for 10 mins, then bake in the oven for 40-45 mins.','40 mins','1 hr and 5 mins'),
(376,'cheese & marmite scones','https://www.bbcgoodfood.com/recipes/cheese-marmite-scones','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheese-marmite-scones-52b4ba1.jpg','Yeast extract gives a deep savoury flavour to these tasty pinwheel cheddar cheese scones - perfect for afternoon tea or a simple snack',4,8,1,1,7,'Heat oven to 220C/200C fan/gas 7 and dust a baking tray with a little flour. Mix the flour and baking powder in a large bowl. Put 1 tsp yeast extract in a jug, add the egg and make up to 300ml with the milk. Stir in the oil, then beat really well to dissolve the yeast extract. Tip 85g of the cheddar into a bowl and mix with the cream cheese and remaining yeast extract to make a spread. Toss the rest of the cheese through the flour mixture, then pour in the milk mixture and stir with the blade of a knife until it comes together. (You need to work quickly once you’ve added the liquid, as it activates the baking powder.) Tip onto a lightly floured work surface and gently knead the mix, taking care not to overwork it, as it will make the scones heavy. Press or lightly roll the dough into an oblong about 20 x 25cm. Spread with the yeast extract mixture and roll up from the longest side to create a tight, fat cylinder. Pat the ends of the cylinder to straighten them, then slice into 8 pinwheels and put on the baking tray, patting them to make flattish rounds. Bake for 12-15 mins until golden and cooked. Leave for a few mins on the tray to allow the cheese centre to harden a little, then transfer to a wire rack to cool. Eat warm or cold. Best eaten on the day they are made.','20 mins','12 mins - 15 mins'),
(377,'beetroot, bacon & cheddar bread scrolls','https://www.bbcgoodfood.com/recipes/beetroot-bacon-cheddar-bread-scrolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scrolls-0743326.jpg','These rustic homemade bread rolls are best served warm with a steaming bowl of soup',6,8,2,1,7,'To make the dough, tip the flours, yeast, oil, sugar, rosemary, 1½ tsp salt and 325ml warm water into a large mixing bowl. Mix with a wooden spoon, then use your hands to bring the mixture together, adding a little extra flour if it feels too sticky or a drizzle of extra water if too dry. Tip onto your work surface and knead the dough for 10 mins, or until it feels soft and springy. Put the dough in a clean, lightly oiled bowl and cover with oiled cling film. Leave somewhere warm to rise for 2 hrs, or until doubled in size. Meanwhile, prepare the filling. Heat the oil in a frying pan and add the bacon. Cook until crisp, then tip onto a plate lined with kitchen paper to drain and cool. Line 8 holes of a muffin tin with squares of baking parchment, or lightly grease with oil. Set aside. Tip the dough out of the bowl onto a lightly floured surface. Knead to knock out the air bubbles, then roll to a large rectangle, roughly 35 x 45cm. Sprinkle half the cheese over the surface and top with the bacon bits, beetroot, then the remaining cheese. Starting from one of the long edges, tightly roll the bread into a long cylinder, then cut into 8 pieces. Pop the bread scrolls, cut-side up, into the muffin tin holes. Cover the whole tin with oiled cling film and leave to rise for 30 mins or until doubled in size. Heat oven to 200C/180C fan/gas 6. Uncover scrolls and brush with a little extra oil. Bake for 30 mins until golden brown. Leave to cool on a wire rack for at least 20 mins, then serve warm or eat within 1 day.','45 mins','35 mins'),
(378,'cheddar & sage scones','https://www.bbcgoodfood.com/recipes/cheddar-sage-scones','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scones_0-ec33a65.jpg','Make sure you use a punchy mature cheese to shine through these savoury scones made with mustard and buttermilk',50,8,1,1,7,'Heat oven to 220C/200C fan/gas 7. Mix the flour, mustard powder, ½ tsp salt and a grinding of black pepper in a large bowl. Rub in the butter until the mixture resembles fine crumbs. Stir in half of the cheese and the sage. Mix together the egg and buttermilk in a separate bowl. Make a well in the centre of the flour mix and pour in all but ½ tbsp of the buttermilk mix. Working quickly, stir until the mixture forms a soft, spongy dough. Tip onto a lightly floured surface and knead briefly until smooth. Roll out to a 3cm-thick square. Cut into quarters, then half each quarter diagonally, so you have 8 triangles. Place the scones on a floured baking tray, brush with the remaining buttermilk, sprinkle over the remaining cheese and top each with a sage leaf. Bake for 12-14 mins until they are well risen, golden and sound hollow when tapped on the bottom. Eat while still warm, spread with butter.','10 mins','15 mins'),
(414,'quick mango pudding','https://www.bbcgoodfood.com/recipes/quick-mango-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-659451_11-2aeee80.jpg','If you''ve taken time to make a special main course, why not take the pressure off and serve this simple but delicious pud',19,6,1,1,6,'Peel and dice a large ripe mango; whizz to a purée in a food processor. Add 6 scoops of vanilla ice cream and 2 tbsp thick cream and whizz again. Spoon into bowls and scoop ½ passion fruit over each.','5 mins','No Time'),
(380,'crusty cheddar pies','https://www.bbcgoodfood.com/recipes/crusty-cheddar-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1227451_8-a4f73b7.jpg','This recipe has been designed to be made ahead and frozen - a family-friendly batch pie, with cheesy mashed potato atop hidden broccoli, leeks and celery',4,8,1,1,7,'Bring a large pan of salted water to the boil. Put the leeks, broccoli and celery in a large steamer. Add the potatoes to the water and cook for 20 mins, with the vegetables steaming on top, until all are tender. Drain the potatoes, then mash with plenty of seasoning, 25g of the butter and all the yogurt. While the veg cooks, pour the milk into a pan, add the flour, both mustards and remaining butter, and cook over a medium heat, whisking all the time, until smooth and thickened. Stir in half the cheese and season. Remove from heat. Divide the steamed veg and peas between 8 individual pie dishes. Pour over the sauce and top with the mash, then sprinkle over the remaining cheese. Pack into freezer bags and use within 3 months. To serve, unwrap and put the dishes on a baking tray in the cold oven, then set to 200C/180C fan/ gas 6. Bake for 50-55 mins until bubbling and hot all the way through.','20 mins','1 hr and 25 mins'),
(381,'courgette, potato & cheddar soup','https://www.bbcgoodfood.com/recipes/courgette-potato-cheddar-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-740509_11-32e6d17.jpg','This healthy low-calorie soup is a delicious way to use up courgettes when they''re in season. Plus it''s freezable, so you can make it in summer and enjoy all year',5,8,1,1,7,'Put the potatoes in a large pan with just enough water to cover them and crumble in the stock cubes. Bring to the boil, then cover and cook for 5 mins. Add the courgettes, put the lid back on and cook for 5 mins more. Throw in the spring onions, cover and cook for a final 5 mins. Take off the heat, then stir in the cheese and season with the nutmeg, salt and pepper. Whizz to a thick soup, adding more hot water until you get the consistency you like. Serve scattered with extra grated cheddar, spring onions and nutmeg or pepper. Or cool and freeze in freezer bags or containers with good lids for up to 3 months.','15 mins','15 mins'),
(382,'chunky cheddar & celeriac soup','https://www.bbcgoodfood.com/recipes/chunky-cheddar-celeriac-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-651459_11-1bfac69.jpg','A meal in a bowl - enjoy it with a gutsy watercress salad and fresh brown bread to make the perfect lunch',5,4,1,1,7,'In a large pan, melt the tbsp of butter. Add the onions and cook for 5 mins until softened, but not coloured. Add the potato, celeriac, the litre of stock, 4 sage leaves and 2 strips of lemon peel. Bring to the boil and simmer gently for about 30 mins, until the celeriac is tender and the potato is collapsing. Remove and discard the lemon zest and sage leaves. Stir so the potato thickens the soup a little. (At this point you can cool and keep covered in the fridge for up to two days or freeze for up to 3 months.) When you’re ready to eat, reheat the soup to just simmering. Stir in the 200g of cheese. Serve with crisp sage leaves for a special touch.','15 mins','40 mins'),
(383,'new potato, spring onion & montgomery cheddar quiche','https://www.bbcgoodfood.com/recipes/new-potato-spring-onion-montgomery-cheddar-quiche','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-624457_11-62e507c.jpg','Chef Tom Kerridge shares a larger version of a dainty tartlet he serves on his lunch menu at the Hand & Flowers in Marlow',52,8,2,1,7,'Sift the flour and a pinch of salt into a large bowl. Add the butter and rub in until it resembles breadcrumbs. Stir in the icing sugar, egg yolk and 3 tbsp cold water, then quickly bring together to form a firm dough. Wrap well in cling film and allow to rest for 20 mins in the fridge. Heat oven to 200C/180C fan/gas 6. Roll out the pastry on a lightly floured surface and line a deep 23cm tart tin. Cover with baking parchment and fill with baking beans. Cook for 20 mins, then remove the paper and beans and cook for 5-10 mins more until the tart case is pale and sandy in texture (see p14 for tips on making and baking pastry). Remove and turn the oven down to 160C/140C fan/gas 2. Spread the base of the tart with the potatoes, onions and cheddar. Whisk together the eggs, milk and cream, then season. Pour into the tart case and cook for 40-45 mins or until just set. Remove, cool on a wire rack then serve.','20 mins','1 hr and 15 mins'),
(384,'somerset stew with cheddar & parsley mash','https://www.bbcgoodfood.com/recipes/somerset-stew-cheddar-parsley-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513713_11-c8c4c6b.jpg','This warming crowd-pleaser is full of flavour and goodness. Use just one type of bean if you don''t have two, and substituting cider with extra stock will also work well',5,4,1,1,7,'Heat oil in a large pan and fry the onion, garlic, carrot and leek until soft but not coloured. Add the tomato purée, chopped tomatoes, butter beans, flageolet beans, cider (if using), stock and thyme and simmer for 15-20 mins until the stew has thickened and the veg are tender. Meanwhile, if you’re adding sausages to any portion, snip each sausage into 3-4 chunks. Heat the olive oil in a frying pan and brown the chunks for 5 mins, then stir in appropriate portions of the stew and gently simmer for the remaining cooking time. Serve with the mash.','15 mins','35 mins'),
(385,'cheddar & sweetcorn scones','https://www.bbcgoodfood.com/recipes/cheddar-sweetcorn-scones','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-376547_11-a408aa7.jpg','Savoury scones make a great meal with a bowl of soup, or a tasty addition to afternoon tea',33,10,1,1,7,'Bring a pan of salted water to the boil. Hold a corn cob at a right angle to the chopping board, then run a sharp knife down the length of the cob, as close to the core as you can get, slicing away the rows of corn. Boil the kernels for 4 mins or until just tender, then drain well. Heat oven to 220C/200C fan/gas 7. Mix the flour, baking powder, mustard, cayenne, 1 tsp salt and 1 tsp thyme leaves in a large bowl, then rub in the butter until the mix looks like fine crumbs. Tip in most of the cheese and all of the corn. Mix the milk with the lemon juice, then stir into the bowl to make a slightly sticky dough. Don’t over-work the dough. Tip the dough onto the floured work surface, knead 2-3 times to smooth a little, then divide into 10 balls. Shape each roughly with your hands and put onto a floured baking sheet. Brush each with a little milk, then scatter with a little cheese, cayenne and a few thyme leaves. Bake for 10-12 mins or until the scones are risen, golden and sound hollow when tapped on the bottom. Cool on a rack.','15 mins','12 mins'),
(386,'double-baked cheddar soufflés','https://www.bbcgoodfood.com/recipes/double-baked-cheddar-souffles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5367_11-918f775.jpg','You can cook these several hours in advance, then re-bake just before serving',69,8,2,1,7,'Heat oven to 180C/fan 160C/gas 4. Put milk, onion, nutmeg and bay leaves into a pan. Bring to the boil, then set side to infuse for 30 mins. Butter 8 x 150ml ramekins. Melt the butter in a second saucepan. Stir in the flour and cook gently for 1 min, stirring. Take off the heat and gradually whisk in the milk until smooth. Discard onion and bay leaf. Return to the hob and stir until the sauce boils and thickens. Off the heat, stir in the mustard, egg yolks and three-quarters of the cheese, then season. Scrape mixture into a large mixing bowl. In another big bowl, whisk the egg whites until stiff. Boil a full kettle. Carefully fold the whites into the cheese mix and fill ramekins twothirds full. Transfer to a roasting tin, then pour the boiling water into the tin until it reaches halfway up the ramekins. Bake for 15-20 mins or until risen, just set and still a little wobbly. Take out of the tin and leave to cool. To serve, heat oven to 220C/fan 200C/gas 7(or leave it at 190C/fan 170C/gas 5 if you are cooking the lamb). Loosen soufflés around the edges with a knife, then turn out, upside down, into a shallow ovenproof dish. Sprinkle with rest of cheese, then spoon 1 tbsp cream over each soufflé. Bake for 10-12 mins till puffed and golden on the top (or 12-15 mins at the lower temperature). Serve at once with Crunchy celery & apple salad, below.','40 mins','45 mins'),
(387,'honey & mustard ham hock pie with flaky cheese pastry','https://www.bbcgoodfood.com/recipes/honey-mustard-ham-hock-pie-flaky-cheese-pastry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/honey-mustard-ham-hock-pie-93425a6.jpg','Slow-cooked tender ham, potatoes and shallots are enclosed in cheesy cheddar pastry - invest in a pie plate or tin for a crisp base',5,6,2,1,7,'For the pastry, wrap the butter in foil and place in the freezer the night before you need it. Put the ham hocks, bay leaves and peppercorns in a large pan. Cover with water and a lid, and simmer for 3 hrs or until really tender – you may need to top up the water level during cooking. (Alternatively, cook in a slow cooker for 6-8 hrs.) Leave to cool in the liquid, then chill overnight if you have time. To make the pastry, tip the flour into a big bowl with 1/2 tsp salt and the mustard powder. Hold the butter block in the foil (peeling back a little at a time as you need to) and coarsely grate straight into the bowl of flour, dipping the end of the butter into the flour every so often – this helps to stop all the butter clumping together. Grate in the cheese too. Use a cutlery knife to lightly stir together, then stir in 175-200ml cold water to bring the dough together. Wrap in cling film and chill for 30 mins. Meanwhile, cook the potatoes in a pan of boiling water for 8 mins or until just cooked, then drain. Melt the butter in another large pan and add the shallots. Cook gently for 10-15 mins until tender and golden, shaking the pan now and then to roll them around in the butter. While they cook, drain the ham hocks, reserving the cooking liquid – the meat should be nice and tender. Shred with two forks, discarding the fat and bones. When the shallots are tender, scoop them out of the pan and add to the ham. Add the flour and mustard powder to the butter, stirring around the pan to make a paste. Cook for 1-2 mins, then splash in a little of the milk, stirring until smooth. Continue adding the milk, bit by bit, stirring well after each addition, until you have a very thick, smooth sauce. Add enough of the ham’s cooking liquid to thin the sauce to a double cream consistency, about 300ml. Taste and season with pepper – it should be salty enough from the ham stock, but add a little extra if it needs it. Stir in the ham, potatoes, shallots, wholegrain mustard, honey and parsley, then leave to cool. Heat oven to 200C/180C fan/gas 6 and place a baking sheet on the middle shelf to heat up. Remove the pastry from the fridge. Divide into 2 balls, one a little larger than the other – keep the smaller one wrapped until later. On a lightly floured surface, roll out the large ball of dough to the thickness of a £1 coin. Use this to line the base of a 23cm metal pie plate or shallow cake tin. Fill with the pie filling, then brush the edges with a little beaten egg. Roll out the remaining pastry, lay over the top, then trim the excess. Crimp the sides of the pie, if you like. Brush with more egg wash and poke 2 steam holes in the top with a sharp knife. Place the pie on the hot baking sheet and bake for 45-50 mins until crisp and golden. Serve piping hot with green veg and gravy, if you like.','50 mins','4 hrs and 10 mins'),
(388,'cheesy sausage rolls','https://www.bbcgoodfood.com/recipes/cheesy-sausage-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1119465_11-4aebb21.jpg','Bake these comforting bread rolls in a muffin tin - they''re filled with cheddar, bangers, spring onions and garlic butter',17,12,1,1,7,'Make up the bread mix following pack instructions. Set aside somewhere warm-ish to rise, in a bowl covered with oiled cling film, while you get everything else ready. Slice the cooked sausages into just under 1cm slices. Roll out the dough on a lightly floured surface to a rectangle roughly 50 x 30cm. Brush with half the garlic butter and scatter over the spring onions, cheese and sausages. Roll up the dough tightly from one of the longest sides, like a Swiss roll. Use a sharp, floured knife to cut the roll into 12 pieces. Wipe the holes of a 12-hole muffin tin with a little more melted butter. Push a roll of bread, cut-side up, into each hole. Dab the remaining butter over the tops, and cover loosely with oiled cling film. Set aside somewhere warm-ish for 30 mins to puff a little. Heat oven to 200C/180C fan/gas 6. Bake the rolls for 20-25 mins until golden and risen. Cool in the tins for 10 mins, then lift out to eat warm, or cool completely.','1 hr and 20 mins','45 mins - 50 mins'),
(389,'cauliflower cheese soup','https://www.bbcgoodfood.com/recipes/cauliflower-cheese-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1202495_11-3fdf46d.jpg','Throw a new spin on this comforting classic by blending into a smooth and creamy soup',5,6,1,1,7,'Heat the butter in a large saucepan. Tip in the onion and cook until softened, about 5 mins, stirring often. Add the cauliflower, potato, stock, milk and seasoning. Bring to the boil, then reduce the heat and leave to simmer for about 30 mins until the cauliflower is soft and the potato almost collapsing. Whizz in a food processor or crush with a potato masher until you get a creamy, thick soup. Top up with more milk to thin a little if serving in mugs. You can make ahead up to 2 days in advance, cool, cover and leave in the fridge until needed, or freeze for up to 1 month. When ready to serve, warm through, ladle into mugs or bowls, top with the cheese pieces, then stir through before eating.','10 mins','N/A'),
(390,'potato, cheddar & red onion pasties','https://www.bbcgoodfood.com/recipes/potato-cheddar-red-onion-pasties','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1100452_11-eee33bb.jpg','Cram shortcrust pastry parcels with oozing cheese and sweet onion chutney - versatile bites with a professional finish',10,4,1,1,7,'Heat oven to 190C/170C fan/gas 5. Line a baking tray with baking parchment. Boil the potato and carrots in salted water for 5 mins until tender. Drain well and tip into a mixing bowl. Add the chutney, thyme, mustard, cheese and some seasoning, and gently mix to combine. Put in the fridge to cool. Roll out the pastry to the thickness of a £1 coin, then cut out 8 x 13cm squares. Divide the filling between the squares and brush the pastry edges with the egg. Bring together the 4 corners of each square in the middle, then press all the seams together like a square envelope. Brush with more egg and sprinkle over the poppy seeds. Put the pasties on the baking tray and bake for 30 mins until golden. Serve with a salad, if you like.','30 mins','40 mins'),
(391,'ham & blue cheese tart','https://www.bbcgoodfood.com/recipes/ham-blue-cheese-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-13638_11-fefc4cd.jpg','There''s no need to make pastry for this tart, just buy a packet of filo. The leftovers are great for packed lunches too',5,4,1,1,7,'Heat oven to 180C/fan 160C/gas 4. Brush each filo sheet with a little oil and layer into a 20cm springform or loose-bottomed tin, overlapping each sheet at a different angle. Mix together the ham, spring onions, eggs, soft cheese and rocket, then pour into the filo case. Sprinkle with blue cheese and pine nuts, then bake for 30 mins until just set. Cool slightly, slice and serve with a salad.','10 mins','30 mins'),
(392,'blue cheese pasta','https://www.bbcgoodfood.com/recipes/blue-cheese-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4327_10-c878fb3.jpg','A great pasta dish that uses up your Christmas storecupboard faves',5,4,1,1,7,'Boil the pasta according to pack instructions. In a saucepan, melt the butter, then gently fry the onion until golden. Add the garlic and sage, fry for a further 2 mins, then remove the pan from the heat. Drain the pasta and reserve some cooking water. Stir through the buttery onions, stilton and 2 tbsp cooking water, then sprinkle with the toasted walnuts to serve.','5 mins','15 mins'),
(393,'portobello & blue cheese melts','https://www.bbcgoodfood.com/recipes/portobello-blue-cheese-melts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-683467_11-cc0bb48.jpg','A veggie treat that is so tasty everyone will want one!',5,1,1,1,7,'Heat oven to 220C/200C fan/gas 7. Mix the onion with the oil and vinegar, spread on a baking tray, then put the mushrooms on top, stem-side up, and scatter over the thyme and some seasoning. Cook in the oven for 15 mins, until starting to soften and caramelise. Crumble the cheese into the cavity of the mushrooms and cook for 5 mins more until the cheese is melted and bubbling. Split the ciabatta roll in half and lightly toast. Top with a handful of rocket, the sticky onions and the cheese-topped mushrooms. Serve with oven fries, if you like.','5 mins','20 mins'),
(394,'blue cheese gnocchi','https://www.bbcgoodfood.com/recipes/blue-cheese-gnocchi','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-249_10-ddee579.jpg','Creamy and soothing, this warming bake makes a great standby for friends and family',25,4,1,1,7,'Cook the gnocchi in a large pan of boiling water according to pack instructions. Plunge the spinach into the same pan, then immediately drain through a colander and shake off any excess water. Put the crème fraîche in a small ovenproof dish with the grated parmesan. Add the hot, drained gnocchi and spinach and give everything a good stir. Crumble the blue cheese over and season with ground pepper. Slide the dish under a hot grill until the cheese is bubbling and golden. Serve immediately.','5 mins','10 mins'),
(395,'spinach & blue cheese pizza','https://www.bbcgoodfood.com/recipes/spinach-blue-cheese-pizza','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spinach-blue-cheese-pizza-f08bba3.jpg','This vegetarian pizza is super-quick to make because the base is yeast free. Packed with spinach, walnuts, mushrooms and blue cheese, it''s super-tasty too',5,2,1,1,7,'Heat oven to 200C/180C fan/gas 6 and place a baking sheet inside to get hot. Heat the oil in a large non-stick frying pan and cook the mushrooms and garlic, stirring frequently, until softened. Add the spinach, a handful at a time, and cook until just wilted but not soggy. Add the onion and stir it through, then turn off the heat. For the base, tip the flour and baking powder into a bowl, and stir in the yogurt mixture with the blade of a cutlery knife to make a ball of soft dough. Dust a sheet of baking parchment with flour, put the dough on top and press with floured hands to make a flat 20cm round. Top with the spinach mixture, scatter over the cheese and walnuts, then bake on the hot baking sheet for 12 mins or until the dough is cooked through and golden.','5 mins','17 mins'),
(430,'banoffee splits','https://www.bbcgoodfood.com/recipes/banoffee-splits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1124519_11-99b0ec7.jpg','A simple pudding with a creamy and indulgent chocolate sauce, ready in just 10 minutes',7,2,1,1,6,'Tip all the sauce ingredients into a small saucepan and heat gently, stirring until you have a smooth sauce. Peel and split the bananas. Put 2 halves on each of 2 serving plates, add 2 scoops of ice cream to each plate, drizzle over the warm sauce and serve straight away.','5 mins','5 mins'),
(396,'fig & blue cheese tart','https://www.bbcgoodfood.com/recipes/fig-blue-cheese-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-68486_12-5e53c25.jpg','This extra special tart with crumbly walnut pastry makes a fabulous vegetarian dinner',17,6,1,1,7,'First make the pastry. Tip the flours into a food processor with ½ tsp salt and the diced butter. Pulse until you can’t feel any lumps, then tip in the walnuts. Mix the egg yolks with 3 tbsp cold water, then dribble this into the machine while you pulse again until the pastry comes together. Tip the pastry out onto a floured surface, lightly bring it together into a ball, then roll out and line a deep 20-23cm tart tin with overhang. The pastry may crack, but just patch it back together, then cover and chill for 1 hr. To make the filling, melt the remaining butter in a large pan, then add the shallots and soften for 10-15 mins, until golden and squishy. Stir in the thyme for 1 min, then remove from the heat. Beat the eggs in a jug with the crème fraîche and cream. Crumble in the cheese and season with pepper and a small amount of salt. Heat oven to 200C/180C fan/gas 6. Blind bake the pastry for 20 mins, remove the baking beans and paper, then bake for a further 15-20 mins until golden and sandy. Reduce the oven temp to 180C/160C fan/gas 4. Add the cooled onions to the cream mixture and pour into the case. Sit the fig halves on top, cut side up, sprinkle with some more thyme and bake on the middle shelf for 1 hr-1hr 10 mins until the tart is browning and has a slight wobble – the cheese middle will firm up on sitting. Cool for about 15-20 mins, then remove from tin and serve with a green salad.','40 mins','1 hr and 55 mins'),
(397,'steak & blue cheese pie','https://www.bbcgoodfood.com/recipes/steak-blue-cheese-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/steak-and-blue-cheese-pie-5bc3085.jpg','Bake a comforting steak pie with tender meat, a deeply savoury gravy and molten blue cheese for an extra hit of flavour. The ideal winter warmer',6,6,2,1,7,'First, make the pastry. Put the flour, mustard powder, ½ tsp salt, the lard and butter in a food processor and pulse until combined. Add 1 egg and pulse again. Add 1 tbsp cold water, if needed, until it forms a short pastry. Knead quickly into a ball, then wrap and chill in the fridge for at least 30 mins. Can be made three days ahead or frozen for two months. Season the steak generously and toss with the flour and mustard powder. Heat half the oil in a large pan. Brown the meat in a few batches, adding more oil as you need to and making sure you don’t overcrowd the pan. Turn the meat only when the underside has a nice dark crust. Transfer the chunks to a plate as you go. Add the bacon to the pan and fry until beginning to crisp. Tip in the carrots, onions and bay leaves and cook for another few mins until the veg is soft. Stir in the Worcestershire sauce and tomato purée for 1 min, scraping the tasty bits from the bottom of the pan, then add the stock, thyme and browned beef. Season, cover the pan and simmer gently for 2½ hrs, stirring now and again to prevent the sauce from sticking, until the beef is tender. Remove the lid for the last 30 mins to thicken the sauce. Alternatively, braise in the oven at 160C/140C fan/gas 3 for the same amount of time. Leave to cool. Can be chilled for up to three days, or frozen for two months. Transfer the beef to a pie dish and crumble the cheese over the top. Place the marrowbone in the centre, nestling it into the meat. Alternatively, use a pie funnel or piping nozzle. Roll out the pastry on a floured worktop until large enough to cover the pie and about ½cm thick. Brush the remaining beaten egg over the edges of the pie dish, drape the pastry over, trim the excess and pinch around the edge to seal. Cut a hole for the marrowbone or pie funnel to poke through, and brush the top all over with beaten egg. Use the pastry trimmings to decorate the pie however you like. Chill for about 30 mins while you heat the oven to 200C/180C fan/gas 6. If you have any beaten egg left, give the pie a final brush for extra glossiness. Bake for 45-50 mins until the pastry is deep golden.','35 mins','3 hrs and 30 mins'),
(398,'peach & blue cheese salad','https://www.bbcgoodfood.com/recipes/peach-blue-cheese-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-561111_11-99f99a7.jpg','Try this creamy and fruity salad - perfect for lunch or a light summer supper',5,4,1,1,7,'Toast the ciabatta slices and drizzle with 1 tbsp oil. Make the dressing by whisking together the remaining oil, honey and lemon juice, then season to taste. Mix all of the other ingredients together in a large bowl and toss with the dressing before serving.','15 mins','No Time'),
(399,'squash & blue cheese wellington','https://www.bbcgoodfood.com/recipes/squash-blue-cheese-wellington','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/strudel-42aa771.jpg','Having one of these vegetarian pastry rolls in the freezer is ideal for entertaining at the last minute, or as a vegetarian Christmas main',4,6,1,1,7,'Heat the oven to 200C/180C fan/gas 6. Cut the squash lengthways into eight long chunky pieces and put in a large roasting tin with the shallots. Toss in a little oil, season and roast for 20 mins. Add the pecans and roast for 10 mins more, or until the squash is cooked but still firm. Leave the squash to cool completely, but tip the nuts and shallots into a non-stick pan. Add the maple syrup, balsamic vinegar and plenty of seasoning, and stir over the heat until the shallots start to caramelise. Leave to cool. Roll out the pastry on a lightly floured surface to a rectangle about 32 x 38cm. Trim 2cm from one of the long edges and set aside. Transfer the pastry to a baking tray. Place 4 of the pieces of squash down the longest length in the middle to make a rectangular block, keeping a generous border of pastry all the way round. Place another 2 pieces of squash on top. Trim the remaining 2 pieces and put crossways at the end. Scatter over the sage and blue cheese, then top with the shallots and pecans. Lightly press to compact everything together. Brush round the pastry edges with the egg, then draw the two long edges up to meet and pinch together to seal – as you would a Cornish pasty. Tuck under the pastry at both ends, then brush all over with egg. Cut leaf shapes from the pastry trimmings and use to decorate the top of the Wellington. Glaze again and make a couple of small air holes with the point of a knife. To freeze: Open-freeze on a baking tray until solid, then wrap in cling film then foil. It will keep for 2 months. To serve, heat the oven to 200C/180C fan/gas 6 and put a baking tray in to heat up. Unwrap the Wellington and place on the hot tray. Bake for 30 mins. Cover lightly with foil and bake for 35-40 mins more, taking off the foil for the final 10 mins. Leave to settle for 10 mins before thickly slicing, or the cheese will be too melty and run out. (If cooking from fresh, chill the Wellington for at least 30 mins before cooking – it then needs only 30-40 mins in the oven.)','45 mins','40 mins'),
(400,'nettle & blue cheese rarebit','https://www.bbcgoodfood.com/recipes/nettle-blue-cheese-rarebit','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1286499_8-0596bb1.jpg','No nettles? This simple rustic dish can also be made using spinach',5,4,1,1,7,'Heat the olive oil in a frying pan. Add the nettles and cook for 1 min until wilted. Allow to cool for 1 min, then roughly chop. Add to a bowl with the crème fraÎche, mustard and half the blue cheese. Stir in seasoning and set aside. Heat the grill and lightly toast the bread on both sides. Divide the nettle and cheese mix between the toasts then pile on the remaining cheese and grill until golden and bubbling. Serve immediately.','5 mins','15 mins'),
(401,'broccoli, walnut and blue cheese pasta','https://www.bbcgoodfood.com/recipes/broccoli-walnut-and-blue-cheese-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-160463_10-f672424.jpg','Add colour, crunch, and a dollop of cheesy indulgence to a straightforward pasta dish',43,2,1,1,7,'Cook penne; 4 mins before the end of cooking, throw in broccoli florets. Meanwhile, heat olive oil, add a handful chopped walnuts and fry gently for 1 min. Drain the pasta, adding 4 tbsp cooking water to the walnuts. Return the pasta to the pan, add the walnuts and creamy blue cheese. Gently stir on the heat, just to melt the cheese. Squeeze over lemon juice to serve.','3 mins','12 mins'),
(402,'sticky sausage burgers with blue cheese','https://www.bbcgoodfood.com/recipes/sticky-sausage-burgers-blue-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-9990_12-aeb8094.jpg','A great burger that kids and adults alike will love',5,4,1,1,7,'Heat oven to 200C/fan 180C/gas 6. Using scissors, snip open the sausage skins and squeeze the meat into a bowl. Add the breadcrumbs, 2 tbsp chutney and the sage, then mix well with your hands. Divide the mix into 4, then shape into burgers, pushing the chunks of cheese into the middle of each patty. Make sure the cheese is completely sealed in or it will leak out during cooking. Place the burgers on a baking tray, then cook for 25 mins until browned and cooked through to the middle. Split open the buns, stuff with your favourite salad, then top with a burger and some of the remaining chutney.','10 mins','25 mins'),
(403,'buffalo wings with blue cheese dip','https://www.bbcgoodfood.com/recipes/buffalo-wings-blue-cheese-dip','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-193709_11-adb3a57.jpg','These American-style crisp chicken pieces are the perfect laid-back food to share, great for a movie night',7,6,1,1,7,'Slowly melt the butter, hot sauce and vinegar in a medium saucepan, then pour into a large bowl. Mix all the ingredients for the blue cheese dip together with a pinch of salt. Cover and chill until needed. Trim the end off each chicken wing, then cut each in half through the joint. Tip the wings into a bowl. Add the celery salt (if using), seasoning and enough flour so they’re all evenly coated. Heat 4 tbsp oil in a large, deep frying pan and add the wings (do this in 2 batches if your pan isn’t big enough). Fry them, stirring frequently, until they are cooked through and browned all over, about 10 mins for each batch. As soon as they are cooked, transfer them to the bowl of butter sauce and stir well. Repeat with the second batch. Serve the wings on a platter with any extra buttery sauce poured over and the blue cheese dip and celery sticks on the side','30 mins','30 mins'),
(404,'pear, chicory & blue cheese salad','https://www.bbcgoodfood.com/recipes/pear-chicory-blue-cheese-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-407468_11-7fb134b.jpg','Creamy blue cheese, ripe pears and walnuts make a winning combination in this elegant salad',5,4,1,1,7,'Discard the outer leaves from the chicory and slice in half lengthways. Cut out the base root, then slice into long, thin slivers and rinse. Pat dry and put in a bowl. Cut each pear quarter into 3 and toss with a little lemon juice to prevent discolouring. Add to the bowl with the parsley and walnut halves. Make a dressing by mixing the walnut oil with the remaining lemon juice and seasoning, then add to the bowl. Toss everything together, then plate up, crumbling over the cheese. Serve immediately.','15 mins','N/A'),
(405,'baked blue cheese with figs & walnuts','https://www.bbcgoodfood.com/recipes/baked-blue-cheese-figs-walnuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cropped_blue_cheese_and_figs3-4385f8a.jpg','Melted cheese pairs beautifully with baked figs and walnut in this easy baked dish. It''s great as a cheese course or sharing starter',17,6,1,1,7,'Heat oven to 200C/180C fan/gas 6. Cut a cross in the tops of the figs nearly, but not quite, all the way down to the bottom, and pinch the bases. Sit in a baking dish or roasting tin (big enough so the figs can sit in a single layer with the whole cheese) cut-side up, but don’t put the cheese in yet. Mix together the vinegar and wine or grape juice with some seasoning, and pour all over the figs. Scatter over the walnuts and thyme. Finally, drizzle over the honey. Bake for 15-20 mins until the figs start to become soft and sticky. Tuck the whole cheese in among the figs and put back in the oven for a final 5 mins. Lift the dish straight to the table and serve with lots of crusty bread to scoop up the molten cheese.','10 mins','20 mins'),
(406,'tartines with blue cheese, fig & ham','https://www.bbcgoodfood.com/recipes/tartines-blue-cheese-fig-ham','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327593_11-619e728.jpg','Put together a casual French table for lunch with friends, with charcuterie and cheese from the deli and homemade snacks',5,6,1,1,7,'Heat oven to 200C/180C fan/gas 6. Brush the baguette slices with olive oil and season. Arrange on baking trays, then bake for 5 mins until crisp. Cool before spreading over some cheese and topping each tartine with a piece of ham and a fig quarter. Add a little ground black pepper, if you like. Serve with a couple of crusty baguettes and some nice French cheeses and meats, arranged on boards and platters with tapenade, cornichons and hard-boiled quails eggs. Add a bowl of salad and Kir Royale to drink (see recipes below).','20 mins','5 mins'),
(407,'steak & blue cheese en croûte','https://www.bbcgoodfood.com/recipes/steak-blue-cheese-en-croute','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/steak-blue-cheese-cheese-en-croute-735ffd4.jpg','This impressive take on beef Wellington sees lean fillet steak spead with Stilton and wrapped in pastry - perfect to wow dinner party guests',5,6,3,1,7,'Heat the oil in a large frying pan and fry the steak for 8-10 mins, turning to brown it all over. Remove from the pan and leave to cool. To make the gravy, add the shallot and garlic to the pan juices and cook, stirring, until softened and golden. Pour in the wine, add the herbs and leave to bubble for 2 mins. Pour in the stock and stir in the flour paste, stirring all the time until the gravy has thickened. Cover and cook for 5 mins over a low heat, then strain into a bowl or small freezer container, pushing through as much liquid left in the shallots as you can. Wash the pan, then melt the butter and cook the shallot and garlic until softened. Add the mushrooms and thyme, and fry for 10 mins, stirring frequently, to drive off as much moisture as possible. Season with black pepper and sparingly with salt. Cool. Lay a couple of sheets of cling film on the work surface, side by side and slightly overlapping, then arrange the prosciutto down the centre in two overlapping rows. Spread the mushroom mixture on top, then scatter evenly with the cheese and walnuts. Place the steak on top, then tightly wrap the prosciutto round it, using the cling film to help you. Finally, tightly wrap in the cling film and chill for 30 mins. Roll out the pastry on a lightly floured surface until you think it is large enough to completely wrap round the steak roll. Unwrap the meat, place on top of the pastry and brush round it with the egg mixture. Tightly roll up in the pastry, trimming the excess as you bring the pastry together down the length and ends, sealing well. If you want to add decoration, cut out leaf shapes from the trimmings and use the egg mixture to stick them to the pastry. Chill until ready to cook. Will keep for 2 days. (If freezing, wrap in new cling film, then foil, and freeze along with the gravy and egg glaze. The day before, take everything out of the freezer and put in the fridge, unwrapping the steak roll and covering it loosely with kitchen paper.) Heat oven to 220C/200C fan/gas 7 with a baking sheet inside. Place the (completely thawed) meat on a piece of baking parchment and brush with the egg glaze. Place on the hot baking sheet and cook for 45-50 mins. Insert a meat thermometer into the centre of the beef: for rare it should be 50C, for medium-rare 55C, and for medium 60C. Cover loosely with foil and leave to rest for 15-20 mins. Trim off the pastry end, then thickly slice and serve with the gravy, heated in a pan, and seasonal vegetables.','40 mins','1 hr and 20 mins'),
(415,'easy strawberry mousse','https://www.bbcgoodfood.com/recipes/easy-strawberry-mousse','https://images.immediate.co.uk/production/volatile/sites/30/2022/06/Strawberry-mousse-6621dcb.jpg','Make this strawberry mousse using just three ingredients. With a purée at the base, it''s topped with a creamy mousse for a stunning yet simple summer dessert',7,4,1,1,6,'Choose four of the best-looking strawberries and set these aside to finish the mousse, then hull and roughly chop the rest. Tip these into a blender or food processor along with the sugar and blitz until smooth. Whisk the cream to stiff peaks. Reserve roughly 4 tbsp of the strawberry purée in a small dish, then fold the rest through the whipped cream until it turns an even shade of pink. Divide the reserved purée between four small serving glasses or ramekins. Spoon the mousse over the top, then chill for at least 1 hr. Will keep chilled for up to a day. Just before serving, halve the reserved strawberries and use these to decorate the mousse.','15 mins','No Time'),
(408,'warm new potato salad with bacon & blue cheese','https://www.bbcgoodfood.com/recipes/warm-new-potato-salad-bacon-blue-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-19505_10-4cfc3a2.jpg','This French-style main meal salad with roasted new potatoes makes a fabulous lunch',5,4,1,1,7,'Heat oven to 220C/fan 200C/gas 7. Place the potatoes in a roasting tin, then rub with 1 tbsp oil and a sprinkling of salt. Roast for 20 mins, then add the onion wedges to the tin, giving everything a good shake. Roast for 20 mins more until the potatoes have turned a deep golden brown and the onions have caramelised and softened. Leave to cool slightly. Heat a non-stick frying pan. Dry-fry the bacon until crisp. Add the sliced mushrooms, then fry for 5 mins more until they have softened. Meanwhile, make the dressing. Whisk the mustard, vinegar and remaining 1 tbsp oil with a splash of water. Place potatoes, onions, bacon and mushrooms in a large bowl with the salad leaves, pour over the dressing, then toss well. Divide between 4 plates, then crumble over the blue cheese.','10 mins','40 mins'),
(409,'chestnut, spinach & blue cheese en croûte','https://www.bbcgoodfood.com/recipes/chestnut-spinach-blue-cheese-en-croute','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chestnut-spinach-blue-cheese-en-croute-72f0fdc.jpg','This vegetarian main course is packed with festive flavours and wrapped in flaky butter puff pastry - a real Christmas treat',4,6,2,1,7,'Melt the butter in a large frying pan. Add the leeks and garlic, stir well, cover and cook for 10 mins until the leeks are soft, stirring a few times to check that they don’t catch. Tip into a large bowl. Put the spinach in the pan and allow it to wilt. Leave to cool and, when cold, squeeze out as much liquid from it as you possibly can. Tip the chestnut purée into the bowl with the leeks and add the 3 eggs, the nutmeg, chestnuts, spinach, breadcrumbs, cheese and seasoning, and stir until well mixed. Chill for at least 1 hr until the mixture firms up. Heat oven to 220C/200C fan/gas 7. On a lightly floured work surface, roll out the pastry to a rectangle large enough to completely enclose the filling. Carefully lift onto a large, long baking tray that has been lined with baking parchment, then brush round all the edges of the pastry with the remaining egg. Spoon the filling down the centre of the length of the pastry, leaving the ends clear. Tuck the ends over the filling, then firmly lift up the sides to wrap them round, trimming away any excess pastry as you go. Brush with more egg to glaze, then make a few holes in the top so steam can escape as it cooks. Bake for 40 mins until golden and the filling is firm. Remove from the oven, brush with more glaze and bake for 10 mins more. To make the sauce, heat the stock in a medium pan, add the leeks, boil for 5 mins, then take off the heat and scoop out 2 tbsp of the leeks. Blitz the rest in the pan with the cornflour using a hand blender, then cook, stirring, until thickened. Pour in the cream and reserved leeks and warm through. Can be made 2 days ahead and chilled. Serve the pastry in thick slices with the sauce.','1 hr and 20 mins','55 mins'),
(410,'blue cheese, butternut & barley salad with maple walnuts','https://www.bbcgoodfood.com/recipes/blue-cheese-butternut-barley-salad-maple-walnuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bluecheese-8f17fac.jpg','Salads don''t get much more robust than this – team roasted onions and squash, deep-flavoured cheese, crunchy nuts and healthier grains',50,6,1,1,7,'Heat oven to 200C/180C fan/gas 6. Toss the squash, onions and garlic with 2 tbsp of the oil and some seasoning. Roast for 35-45 mins or until the vegetables crisp up in places – turning halfway through cooking and adding the thyme leaves. Remove from the oven and allow to cool, discarding the garlic. Cook the barley, following pack instructions, until al dente. Drain and cool. Meanwhile, rinse the squash seeds, removing any flesh, and dry thoroughly. Put the walnuts and squash seeds in a frying pan and toast until golden brown. Add ½ tbsp oil, the maple syrup, sugar, chilli flakes and some salt. Let it bubble for a few mins, until the sugar has caramelised. Tip onto a tray, allow to cool, then bash into pieces. Make a dressing by mixing the balsamic, mustard, remaining oil and some seasoning. Toss the squash and onions through the barley, followed by the spinach and dressing. Scatter over the cheese, nuts and some extra thyme sprigs to serve.','10 mins','45 mins'),
(411,'leek, fennel & potato soup with cashel blue cheese','https://www.bbcgoodfood.com/recipes/leek-fennel-potato-soup-cashel-blue-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/leek-fennel-potato-soup-dc08be0.jpg','Combine classic leek & potato soup with blue cheese and aromatic fennel to make this decadent soup. We''ve used cashel blue but you could try other blue cheeses ',70,6,1,1,7,'Quarter the fennel and discard the tough outer leaves and the hard core from each piece. Slice the rest of the flesh, including any little fronds. Heat the butter in a saucepan and add the fennel, leeks and potato. Cook over a medium heat for about 5 mins, turning the vegetables over in the butter. The vegetables shouldn’t colour. Add a splash of water, cover the pan and cook the vegetables for 20 mins, stirring every so often. Add the stock or water and garlic, season, bring to the boil, then turn the heat down low and cover the pan with a lid or foil. Cook for about 10-15 mins, until everything is completely tender. Stir in the cream and leave to cool. Purée in a blender until completely smooth. Check the seasoning and return the mixture to the saucepan – you can reheat it quickly just before serving. Ladle into bowls and scatter each one with the walnuts and cheese.','10 mins','45 mins'),
(412,'quick peach melba crumbles','https://www.bbcgoodfood.com/recipes/quick-peach-melba-crumbles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quick-peach-melba-crumbles-e76488a.jpg','Raspberries, peaches and a crunchy granola topping make for an easy 10-minute dessert that''s perfect for summer',1,4,1,1,6,'Pour the can of peaches, with the syrup, into a small saucepan over a medium heat. Add the butter and simmer until the peaches are hot. Once simmering, add the raspberries and cook for 1-2 mins more until hot. Meanwhile, put the cream in a large bowl with the icing sugar and whisk until lightly whipped. Using a slotted spoon, put the peach Melba mixture into four serving dishes and top each with a sprinkling of granola and a dollop of cream.','5 mins','5 mins'),
(413,'quick sticky toffee puddings','https://www.bbcgoodfood.com/recipes/quick-sticky-toffee-puddings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-365510_12-419139a.jpg','Discover a shameless shortcut to this family favourite pud',5,4,1,1,6,'Heat oven to 200C/180C fan/gas 6. Mix the muffins with the sultanas. Divide between 4 buttered ramekins or one baking dish. Cover with foil and bake for 8 mins until just warmed through. Meanwhile, place the sugar, butter and cream in a small pan and gently heat together, stirring until the sugar dissolves. Pour the sauce over the muffin mixture and serve warm with ice cream.','5 mins','10 mins'),
(416,'easy microwave brownies','https://www.bbcgoodfood.com/recipes/easy-microwave-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Brownies-4cdf63b.jpg','Make these easy, fudgy brownies in under 15 minutes in the microwave. They''re ideal when you need a quick and indulgent dessert.',71,8,1,1,6,'Cut the butter into small pieces. Put in a 20 x 20cm microwavable glass or ceramic dish and cook in 20-second bursts in the microwave until melted. Remove, swirl it around the dish to coat the sides, then tip it into a mixing bowl. Add the sugar to the bowl and, using a hand or electric whisk, whisk vigorously until the mixture is creamy and fully incorporated into the melted butter. In a jug, stir the vanilla extract into the milk along with the beaten eggs. Whisk this, a little at a time, into the sugar and butter mixture, ensuring it’s thoroughly incorporated before adding more. Sift the flour and cocoa powder together, then fold gently into the cake batter. Finally, stir in the milk chocolate chips. Tip the whole mix into the buttered dish and smooth using a spatula to fill the dish. Cook for 5 mins or until the brownies are well-risen and slightly firm to touch. Don’t worry if they’re still a bit soft in the centre; they will continue to cook for several minutes once out of the microwave. Put the hot dish onto a cooling rack and leave for at least 20 mins to firm up. Cut into 8 squares. Will keep in an airtight container for three days.','10 mins','5 mins'),
(417,'microwave mug cake','https://www.bbcgoodfood.com/recipes/microwave-mug-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mug-cake-a012dbb.jpg','Need a quick chocolate fix? Try making this easy microwave mug cake with storecupboard ingredients. It''s even more delicious with a scoop of vanilla ice cream',72,1,1,1,6,'Add 4 tbsp self-raising flour, 4 tbsp caster sugar and 2 tbsp cocoa powder to the largest mug you have (to stop it overflowing in the microwave) and mix. Add 1 medium egg and mix in as much as you can, but don''t worry if there''s still dry mix left. Add the 3 tbsp milk, 3 tbsp vegetable or sunflower oil and a few drops of vanilla essence and mix until smooth, before adding 2 tbsp chocolate chips, nuts, or raisins, if using, and mix again. Centre your mug in the middle of the microwave oven and cook on High for 1½ -2 mins, or until it has stopped rising and is firm to the touch.','5 mins','2 mins'),
(418,'microwave banana pudding','https://www.bbcgoodfood.com/recipes/microwave-banana-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424720_12-b8ffc96.jpg','This pud will be a hit with all the family, and it only takes 20 minutes to make',5,4,1,1,6,'Put the butter in a 1-litre baking dish and microwave on High for 30 secs-1 min until melted. Add 1½ bananas, mash into the melted butter, then add the sugar, flour, cinnamon, eggs and milk. Mix together well. Slice the remaining banana over the top, then return to the microwave and cook on High for 8 mins until cooked through and risen. Serve warm, dusted with icing sugar, if you like, with a drizzle of toffee sauce and a scoop of ice cream.','10 mins','10 mins'),
(419,'frozen tropical fruit yogurt','https://www.bbcgoodfood.com/recipes/frozen-tropical-fruit-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/frozen-tropical-fruit-yogurt-0080ef3.jpg','Try this genius recipe – as if by magic, the frozen tropical fruit freezes the yogurt when blended together. Use a food processor rather than a blender',52,6,1,1,6,'Put everything except the fresh fruit in a food processor and blend. Scoop straight into bowls, or tip into a container and freeze if you want to serve it later. Serve with the fresh tropical fruit and spoon over the passion fruit seeds.','15 mins','N/A'),
(420,'5-minute mocha pots','https://www.bbcgoodfood.com/recipes/five-minute-mocha-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/five-minute-mocha-pots-633eb34.jpg','This dreamy dessert is ready in under 10 minutes. Use milk chocolate if you''re feeding young children',1,4,1,1,6,'Melt the chocolate in the microwave for 2 mins, stirring halfway through, or over a pan of gently simmering water. Leave to cool a little. Using an electric whisk, whip the double cream with the vanilla in a bowl until lightly whipped. Fold in the cooled, melted chocolate until fully combined. Split the mixture between four small bowls or ramekins and serve topped with a dollop of crème fraîche. If you aren’t serving straight away, chill in the fridge and then add the crème fraîche just before bringing to the table.','5 mins','2 mins'),
(421,'white forest rocky road','https://www.bbcgoodfood.com/recipes/white-forest-rocky-road','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/White-rocky-road-d815aa1.jpg','Enjoy our decadent white chocolate version of rocky road, with mini marshmallows, dried cherries, coconut, almonds and your favourite biscuits',12,20,1,1,6,'Line a 20cm square baking tin with baking parchment. Melt the butter and chocolate in a heatproof bowl set over a pan of simmering water, ensuring the bowl doesn’t touch the water, or do this in 20-second bursts in the microwave. Cut five or six triangles from the biscuits with a sharp knife to make trees. Put the off-cuts in a bowl. Bash into rubble. Tip the almonds and most of the cherries and marshmallows into the bowl with the bashed biscuits, pour over the chocolate mixture and fold in until coated. Tip into the tin and smooth the top. Press a row or two of the biscuit trees into one end of the rocky road. Sprinkle the rest with the reserved toppings and scatter over the coconut to resemble snow. Transfer the tin to the fridge and chill for 2 hrs until solid. Cut into squares to serve.','15 mins','No Time'),
(423,'vegan mug cake','https://www.bbcgoodfood.com/recipes/vegan-mug-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-mug-cake-c4c64e4.jpg','Bake a simple vegan mug cake in the microwave – you can have it ready in under 10 minutes to satisfy a craving. Serve with a scoop of dairy-free ice cream',29,1,1,1,6,'Put the milk in a microwave-safe mug, add the lemon zest and juice and leave to sit for 2-3 mins. It should start to look a bit grainy, as if it has split. Stir in the sunflower oil, flour, sugar and bicarbonate of soda. Mix really well with a fork until smooth. Drop in the raspberries then microwave on high for 1 min 30 secs, or until puffed up and cooked through. Serve with a drizzle of coconut cream, or a scoop of dairy-free ice cream if you like.','5 mins','2 mins'),
(424,'chocolate ganache with sea salt, olive oil & toasts','https://www.bbcgoodfood.com/recipes/chocolate-ganache-sea-salt-olive-oil-toasts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/choc-ganache-and-toasts-30e1e11.jpg','Silky smooth chocolate ganache, with crisp ciabatta toasts and a sprinkle of sea salt makes for a quick, chic, ultra-indulgent dinner party dessert',70,6,1,1,6,'Heat the cream in a heavy-bottomed pan until it is warm but not boiling (you don’t want to overheat the chocolate, so be careful not to overdo it). Take the pan off the heat, then add the chocolate to the pan with the cream and leave to melt for a while, then beat with a wooden spoon. At first it looks like it won’t go smooth, but it will. Leave to cool and set. You can also chill it, but take it out of the fridge before serving so that it isn’t too firm or ‘fridge cold’. Cut the bread into fine slices and toast on each side (the best way is under the grill, but watch it like a hawk). Serve the ganache in glasses in small scoops. Drizzle generously with olive oil and sprinkle with sea salt. Brush the toast with more olive oil and serve with the ganache.','10 mins','10 mins'),
(425,'quick cheat''s strawberry mousse','https://www.bbcgoodfood.com/recipes/quick-cheats-strawberry-mousse','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Strawberry-mousse-611bb80.jpg','Make the most of strawberries in this quick dessert, with a silky mascarpone layer complemented by a tangy compote, crunchy ginger biscuits and pistachios',46,4,1,1,6,'Put the strawberries, lemon juice, sugar and 2 tbsp water in a large pan over a medium heat and cook for 5 mins until the fruit has softened. Scrape into a bowl leave to cool. Tip 300g of the mixture into a blender (reserve the rest for later), blitz until smooth and sieve into a jug. Whisk the mascarpone in a large bowl to soften, then add the double cream, vanilla bean paste and lemon zest, and whisk again until just starting to thicken. Fold in the blitzed, sieved strawberry purée to combine – it will thicken a bit more. Alternatively, swirl it through so you have layers of compote going through the mousse.Will keep chilled for up to a day. Spoon or pipe the mousse into glass tumblers and top with the reserved strawberry compote, the gingernut crumbs, pistachios and dried strawberries, if you like.','20 mins','5 mins'),
(426,'vanilla, apple & ginger biscuit fool','https://www.bbcgoodfood.com/recipes/vanilla-apple-ginger-biscuit-fool','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/220413_VANILLA_APPLE_AND_GINGER_BISCUIT_FOOL_0251_111-e380a0a.jpg','Impress guests with these apple and ginger fools for dessert. Made using just four simple ingredients, they''re quick and easy',54,4,1,1,6,'Pour the cream into a bowl and whip to soft peaks using a whisk. Fold in the yogurt, apple sauce and crushed ginger biscuits, then spoon the mixture into serving glasses or bowls. Alternatively, layer the whipped cream, yogurt, apple sauce and crushed biscuits up separately in serving glasses or one large bowl. Chill in the fridge for up to a day before serving, or serve straightaway. Just before serving, sprinkle over some extra ginger biscuit crumbs, if you like.','10 mins','No Time'),
(427,'raspberry fool with whisky & toasted oats','https://www.bbcgoodfood.com/recipes/raspberry-fool-whisky-toasted-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raspberry-fool-with-whisky-toasted-oats-3f969f5.jpg','Fresh, pink raspberries with whipped cream is the perfect summer dessert. Add a splash of whisky and toasted oats for a subtle nutty flavour',52,6,1,1,6,'Whisk 300ml double cream with 40g demerara sugar until soft peaks form, then fold in 25ml whisky. Layer the whisky cream in glasses with 40g toasted oats and 200g raspberries, halved. Top with a good grating of white chocolate.','20 mins','No Time'),
(428,'apple crumble sundae','https://www.bbcgoodfood.com/recipes/apple-crumble-sundae','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apple-crumble-sundae-831ecb4.jpg','Ready in no time, this simple budget sundae will satisfy any midweek sweet cravings. If you''re looking for a pudding you can whip up in a flash, this is it',1,4,1,1,6,'In a small saucepan, melt the butter over a gentle heat and add the apples, cinnamon and sugar. Cook for 10 mins or until the apples have softened but still hold their shape. Split the mixture between four sundae glasses or bowls. Sit 2 scoops of ice cream on top of each, followed by the crushed biscuits. Serve while the apple mix is still warm.','10 mins','10 mins'),
(429,'lemon curd & yogurt fool','https://www.bbcgoodfood.com/recipes/lemon-curd-yogurt-fool','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424468_12-22dc763.jpg','Treat yourself to a delightfully easy family dessert - ready in just five minutes',5,4,1,1,6,'Put the lemon curd and yogurt into a bowl. Fold together for a rippled effect. Divide the mixture between four glasses and chill. Mix raspberries and icing sugar together and gently crush, then spoon with their juices over the chilled mix and serve.','5 mins','No Time'),
(431,'after-dinner mint cream','https://www.bbcgoodfood.com/recipes/after-dinner-mint-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-837520_11-3877c52.jpg','This creamy syllabub is a spin on classic mint choc chip and a great way to use up leftover chocolates',73,6,1,1,6,'Put the mascarpone, double cream, caster sugar and sherry in a bowl and whisk until thick enough to hold its shape. Break 200g of the after-dinner mints into small pieces, then beat these into the cream mixture. Spoon into tiny glasses or bowls and chill until needed. Add 1 more thin after-dinner mint to each dessert before serving','10 mins','No Time'),
(432,'baileys banana trifles','https://www.bbcgoodfood.com/recipes/baileys-banana-trifles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-464624_11-5823ab4.jpg','A quick and impressive make-ahead dessert with Irish cream for grown-ups, or use extra toffee sauce for kids',5,6,1,1,6,'Mix the cream with 1 tbsp Irish cream, and set aside. Divide the brownie pieces between 6 glasses, then drizzle each with 1 tbsp Irish cream. Top with the sliced bananas, custard and boozy cream, dividing equally, then drizzle with toffee sauce and finish with grated chocolate. Can be made a few hours ahead.','10 mins','N/A'),
(433,'strawberry frozen yogurt','https://www.bbcgoodfood.com/recipes/frozen-strawberry-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-666556_11-ac0ea3b.jpg','Our easy strawberry frozen yogurt is intensely fruity and really creamy, perfect for a simple summer sweet treat',17,5,1,1,6,'Roughly chop half the strawberries and whizz the rest in a food processor or with a stick blender to a purée. In a big bowl, stir the condensed milk into the puréed strawberries then gently stir in the yogurt until well mixed. Fold through the chopped strawberries. Scrape the mixture into a loaf tin or container, pop on the lid or wrap well in cling film and freeze overnight, until solid. Remove from the freezer about 10-15 mins before you want to serve the frozen yogurt. Can be frozen for up to 1 month.','10 mins','No Time'),
(434,'chocolate & berry mousse pots','https://www.bbcgoodfood.com/recipes/chocolate-berry-mousse-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-693589_11-70a79e9.jpg','Dessert doesn''t have to be devilish as this good-for-you pud proves',5,4,1,1,6,'Melt the chocolate in a heatproof bowl over a pan of simmering water, making sure the bowl doesn’t directly touch the water. Once melted, allow it to cool for 5-10 mins, then stir in the yogurt. Whisk the egg whites until stiff, then whisk in the sugar and beat until stiff again. Fold the whites into the chocolate mix – loosen the mixture first with a spoonful of egg white, then carefully fold in the rest, keeping as much air as possible. Put berries into small glasses or ramekins, then divide mousse on top. Chill in the fridge until set.','15 mins','5 mins'),
(435,'healthy chocolate brownies','https://www.bbcgoodfood.com/recipes/healthy-chocolate-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/Brownies-89bf294.jpg','Enjoy these lighter brownies, which are low in fat, with no added sugar and gluten-free. Great for those with special diets',4,16,1,1,6,'Heat the oven to 190C/170C fan/gas 5 and line a shallow 23cm square baking tin with baking parchment. Tip the beans, cocoa, coffee granules, xylitol, vanilla, eggs and yogurt into a large bowl. Blitz using a hand blender until smooth. Or, do this in a food processor. Add the baking powder, then blitz again. Quickly tip the mixture into the tin and bake for 25-30 mins until set. Leave to cool in the tin for a few minutes, then lift out onto a wire rack to cool completely. Remove the baking parchment, then cut into 16 squares. Will keep chilled for up to three days.','10 mins','30 mins'),
(436,'lemon & violet drizzle cake','https://www.bbcgoodfood.com/recipes/lemon-violet-drizzle-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-193515_11-27b78d2.jpg','A simple and sweet all-in-one cake - ideal for Mothering Sunday',43,15,1,1,6,'Preheat the oven to 180C/Gas 4/fan oven 160C. Butter and line the base of a shallow oblong tin (about 18x28cm/7x11in) with baking parchment. Tip all the cake ingredients into a large mixing bowl and beat for 2-3 minutes, until the mixture drops easily off the spoon. Spoon the mixture into the tin and smooth the surface with the back of a spoon. Bake for 30-40 minutes, until golden and firm to the touch. Meanwhile, make the icing: beat together the lemon juice and sugar, pour the mixture evenly over the cake while it is still hot, then leave to cool. Cut the cake into 15 squares. Top each one with a crystallised violet and mimosa ball.','20 mins - 25 mins','40 mins'),
(437,'apricot & raspberry tart','https://www.bbcgoodfood.com/recipes/apricot-raspberry-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-320524_10-d1e01f5.jpg','Delicious served hot or cold, this tart uses filo pastry to keep the calories down',5,4,1,1,6,'Let the filo come to room temperature for about 10 mins before use. Put a baking tray into the oven and heat oven to 200C/180C fan/gas 6. Brush each large sheet of filo with melted butter, layer on top of each other, then fold in half so you have a smaller rectangle 6 layers thick. If using small sheets just stack on top of each other. Fold in the edges of the pastry base to make a 2cm border, then spread the apricot conserve inside the border. Carefully slide the pastry base on to the hot baking tray and bake for 5 mins. Remove from oven, arrange apricots over the tart and brush with any leftover melted butter. Bake for another 10 mins, then scatter on raspberries and sprinkle with sugar. Bake for a final 10 mins until the pastry is golden brown and crisp.','15 mins','25 mins'),
(438,'healthy apple crunch','https://www.bbcgoodfood.com/recipes/healthy-apple-crunch','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Healthy-apple-crunch-b15b94a.jpg','Try our healthy, sugar-free twist on apple crumble for dessert – the soya yogurt base is topped with stewed apples and crunchy cinnamon-oat clusters',4,4,1,1,6,'Heat the oven to 180C/160C fan/gas 4. Tip all the apples into a small pan with 6 tbsp water, then cover and cook over a medium heat for 6 mins, stirring halfway through, until the apples are just cooked. The cooking apples should be pulpy, but the dessert apples will retain their shape. Tip the oats, cinnamon and vanilla into a bowl and add 3 tbsp of the apple mixture, the oil and 100g of the yogurt. Mix everything together well using your hands, until the mixture has formed into chunky clusters similar to granola. Spread the clusters out over a large baking sheet lined with baking parchment, and bake for 10 mins. Shake the tray to turn the clusters over and bake for 10 mins more until firm. Leave to cool for 5 mins so the clusters can firm up slightly more. Meanwhile, spoon the remaining yogurt into a small, shallow ovenproof dish, then spoon the rest of the apples on top. Scatter over the oat clusters, and bake for 10 mins until the topping is firm and golden and the apple mixture has warmed through.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','45 mins - 50 mins'),
(439,'instant frozen berry yogurt','https://www.bbcgoodfood.com/recipes/instant-frozen-berry-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1029452_10-563fda8.jpg','Three ingredients and two minutes is all you need to whip up this low-fat, low-calorie frozen yogurt treat, which is ideal for eating after exercise or as a quick dessert',6,4,1,1,6,'Blend berries, yogurt and honey or agave syrup in a food processor for 20 seconds, until it comes together to a smooth ice-cream texture. Scoop into bowls and serve.','2 mins','No Time'),
(440,'slow cooker rice pudding','https://www.bbcgoodfood.com/recipes/slow-cooker-rice-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ricepudding-e9878d1.jpg','Enjoy a nostalgic pudding, or a gluten-free alternative to porridge for breakfast. Add natural sweetness with fruit or almonds and a dusting of cinnamon',5,6,1,1,6,'Butter the slow cooker all over the base and half way up the sides. Heat the milk to simmering point. Mix the pudding rice with the milk and pour it into the slow cooker. Add a grating of nutmeg or cinnamon. Cook for 2½ hours on High and stir once or twice if you can. Serve with honey, or flaked almonds and fruit if you like.','15 mins','2 hrs and 30 mins'),
(441,'the ultimate makeover: chocolate mousse','https://www.bbcgoodfood.com/recipes/ultimate-makeover-chocolate-mousse','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-337565_11-c2569cf.jpg','This sophisticated dessert is great for a dinner party, or for a lighter family pud use ordinary dark chocolate with less cocoa solids',37,4,1,1,6,'Chop the chocolate very finely and put it into a large bowl that will fit over a pan of simmering water. Mix the cocoa, coffee and vanilla with 2 tbsp cold water, and pour over the chocolate. Place the bowl over the gently simmering water, give it all a stir, then remove from the heat. Leave with the bowl of chocolate still over the water, stirring occasionally to check when melted. Stir the melted chocolate, it will be quite thick. Stir in 2 tbsp boiling water and the chocolate will immediately thin down and become silky smooth. Leave to cool slightly. Whisk the egg whites to fairly soft peaks, then whisk in the sugar until thick and glossy. Beat the yogurt into the cooled chocolate. Fold about one-third of the egg whites into the chocolate mix using a large metal spoon, then very gently fold in the rest of the whites until they are evenly mixed in – being careful not to over-mix or you will lose the volume of the mousse. Spoon into 4 small cups or (125-150ml) ramekins and chill for a couple of hours, or overnight. Place each mousse on a saucer or small plate. Top with a few raspberries, then dust with a little cocoa powder. Will keep for up to 2 days in the fridge.','20 mins','N/A'),
(442,'juicy lucy pudding','https://www.bbcgoodfood.com/recipes/juicy-lucy-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192616_11-414560f.jpg','Look no further for a fruity dessert that''s both delicious and nourishing',5,4,1,1,6,'Preheat the oven to 190C/gas 5/ fan 170C. Mix the fruits of the forest in a large bowl with the sugar and jam, then add the pears and toss to mix. Tip into a deep baking dish measuring about 18x28cm, cover with foil and roast in the oven for 20 minutes. Pierce a pear or two to see if they are really tender; if not, return dish to the oven for another 5 minutes or until they feel soft. Mix breadcrumbs with the butter and scatter over the fruit. Bake uncovered in the oven for 10-15 minutes or until golden and crispy. Serve hot.','15 mins - 20 mins','35 mins'),
(443,'spiced roasted apples & blackberries','https://www.bbcgoodfood.com/recipes/spiced-roasted-apples-blackberries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-193504_11-93a879d.jpg','This guilt-free pudding tastes wonderful served with low-fat fromage frais, mixed with a little honey',74,4,1,1,6,'Preheat the oven to 180C/gas 4/fan 160C. Core the apples so you have a hole the size of a pound coin in each one. Make a cut just into the skin around the middle of each apple. Stand the apples in a shallow baking dish large enough to take all four. Mix together the honey, cinnamon and orange zest, put an equal amount into the cavity of each apple, then pour the orange juice into the dish. Roast the apples for about 40 minutes, spooning the juices over them occasionally. Then, when the apples are almost ready, spoon the blackberries around and over the top of each apple. Return to the oven for 10 minutes or until the juices start to run. Spoon the blackberries and juices over the apples to serve.','10 mins','50 mins'),
(444,'chocolate chia pudding','https://www.bbcgoodfood.com/recipes/chocolate-chia-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-chia-pudding-440-400-ed68a5f.jpg','Make a tasty, healthy chocolate pudding in just five minutes. It''s low-calorie and vegan, too, and the chia seeds are a great source of omega-3 fatty acids',16,4,1,1,6,'Put all the ingredients in a large bowl with a generous pinch of sea salt and whisk to combine. Cover with cling film then leave to thicken in the fridge for at least 4 hours, or overnight. Spoon the pudding into four glasses, then top with the frozen berries and cacao nibs.','5 mins','No Time'),
(445,'healthy spiced rice pudding','https://www.bbcgoodfood.com/recipes/healthy-spiced-rice-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced_rice_pudding-e1690af.jpg','Make a rice pudding infused with hot cross bun flavours for a special (and healthy) Easter treat for the family. Use semi-skimmed milk for 2-5 year olds',5,6,1,1,6,'Butter the slow cooker all over the base and half way up the sides. Heat the milk to a simmer. Mix the pudding rice and the milk and pour into the slow cooker. Add the orange zest, and spices. Cook for 4 hours on low and stir once or twice if you can. If after 3 hours the rice pudding is beginning to stick to the base of the cook, add a splash of water. The rice will be tender when cooked. Add 100ml milk to loosen the pudding, if necessary. Serve topped with orange segments and honey, fruit and raisins, if you like. ','45 mins','4 hrs'),
(446,'fromage frais mousse with strawberry sauce','https://www.bbcgoodfood.com/recipes/fromage-frais-mousse-strawberry-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424460_12-7b273b3.jpg','This low-fat dessert has a wonderfully light and airy texture, and it''s suitable for vegetarians too',43,6,2,1,6,'Put the egg white into a heatproof bowl with the icing sugar. Set the bowl over a large pan of simmering water and, using a hand electric whisk, whisk for 5 mins until the mixture is light, fluffy and holds peaks when the blades are lifted. Remove from heat, whisk in the lemon zest, then whisk for a further 2 mins to cool it down. Fold in the fromage frais, then transfer to six glasses or small bowls and chill. Roughly chop half the strawberries and put in the food processor with the 2 tbsp icing sugar and the lemon juice. Whizz to a purée, then press through a sieve to remove the seeds. Chop the remaining strawberries. Spoon the chopped strawberries over the mousses, then spoon a little purée over each. Chill until ready to serve.','20 mins','5 mins'),
(447,'blood orange & dark chocolate madeleines','https://www.bbcgoodfood.com/recipes/blood-orange-dark-chocolate-madeleines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blood-orange-madeleines-6d37a58.jpg','Best enjoyed fresh from the oven, these delicate madeleines dipped in dark chocolate and dusted with blood orange powder make a delectable dessert',75,15,2,1,6,'Put the butter in a small saucepan over a medium heat until it foams and turns a nutty brown colour – watch the pan carefully, as butter can burn quickly. Pour into a bowl and set aside to cool. Beat the eggs, sugar and zest in a large bowl with an electric hand whisk for about 5 mins until thick and pale. Add the juice and mix to combine. Mix the flour and baking powder together, then sift over the batter mixture, a third at a time, and fold in. Add a large spoonful of the batter to the butter, mixing together until smooth and combined. Pour this into the batter and gently fold together until fully combined. Press a sheet of cling film onto the surface of the mixture and chill overnight. About 30 mins before you want to bake the madeleines, grease a 12-hole madeleine tin with a little extra butter and dust with a little flour. Heat oven to 200C/180C fan/gas 6 and put the tin in the freezer to chill for 30 mins. Remove the batter from the fridge and add about 1 heaped tbsp to each madeleine hole – be careful not to overfill as they will bake over the edge and won’t form the characteristic hump. Depending on the size of your tin, you will have some batter left – either bake in batches or put the rest back in the fridge and bake within three days. Bake in the oven for 10-12 mins or until golden brown around the edges. Immediately turn out the madeleines onto a wire rack and leave to cool fully. Put the chocolate in a microwaveable bowl and heat in the microwave on High in 30 sec bursts until about three-quarters of the chocolate has melted. Remove from the microwave and stir vigorously until the remaining chocolate has melted (this is a simple method of tempering and just ensures the chocolate will set properly at room temperature). Dip each madeleine halfway into the melted chocolate, allowing the excess to drip off, then place on a sheet of baking parchment. As the chocolate starts to set, but while still a little tacky, sprinkle with some of the Blood orange dust (below), if using, and allow to cool.','50 mins','30 mins'),
(448,'slow cooker spiced apples with barley','https://www.bbcgoodfood.com/recipes/spiced-apples-barley','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spicedroastapples-86face5.jpg','Choose eating apples if you want the sliced rings to stay intact in the slow cooker. Make as a low-fat yet tasty pudding, or lazy weekend breakfast dish',4,4,1,1,6,'Heat the slow cooker if necessary. Put the barley and 750ml boiling water into the slow cooker. Peel and core the apples so you have a hole the size of a pound coin in each one. Cut each apple in half. Stand the apples skin side down on the barley. Mix the cinnamon, nutmeg and orange zest, and sprinkle them over the apples. Cook on Low for 2 hours. Serve with natural yogurt.','15 mins','2 hrs'),
(449,'chocolate-dipped strawberries','https://www.bbcgoodfood.com/recipes/chocolate-dipped-strawberries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-strawberries-48f9511.jpg','Indulge in chocolate-dipped strawberries for dessert or as part of a romantic picnic. Drizzle over milk and white chocolate for the wow factor',46,5,1,1,6,'Cut a strip of baking parchment and leave to one side. To melt the dark chocolate, fill a small pan with 2cm water and bring to a simmer. Put the chocolate in a heatproof bowl and rest it on top of the pan, ensuring the bowl isn’t touching the water. Stir the chocolate to gradually melt it. Or, melt the chocolate in 20 second blasts in the microwave, stirring after each blast until melted. Put the melted chocolate in a small deep bowl.  Gently hold the strawberries by the leafy top and dip into the chocolate. Tap off any excess chocolate and put on the parchment to set. If you have any leftover chocolate, pour it onto another strip of parchment and leave it to set, it can be used again.  If you''d like to decorate the strawberries, melt the white and milk chocolate and drizzle lightly over the fruit.','20 mins','4 mins'),
(450,'sticky cinnamon figs','https://www.bbcgoodfood.com/recipes/sticky-cinnamon-figs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-339086_11-8e6b423.jpg','A simple and stylish nutty fig pudding ready in just 10 minutes',33,4,1,1,6,'Heat grill to medium high. Cut a deep cross in the top of each fig then ease the top apart like a flower. Sit the figs in a baking dish and drop a small piece of the butter into the centre of each fruit. Drizzle the honey over the figs, then sprinkle with the nuts and spice. Grill for 5 mins until figs are softened and the honey and butter make a sticky sauce in the bottom of the dish. Serve warm, with dollops of mascarpone or yogurt.','5 mins','5 mins'),
(451,'effortless raspberry iced mousse','https://www.bbcgoodfood.com/recipes/effortless-raspberry-iced-mousse','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-585_11-2ee055f.jpg','Use raspberries straight from the freezer for a mousse that will unfreeze your tastebuds',69,4,1,1,6,'Tip the quark and sugar into a large bowl, squeeze in a few drops of lemon juice and beat with a wooden spoon until smooth and creamy (you can do this an hour or two ahead). Gently stir in raspberries until they begin to break up and mixture is streaked pink; taste and add a little more lemon juice if liked. Spoon into glasses and serve.','5 mins','N/A'),
(452,'sweet & fruity yorkshire pudding','https://www.bbcgoodfood.com/recipes/sweet-fruity-yorkshire-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-15397_11-2e65a7c.jpg','Not just an accompaniment to your Sunday dinner, you can also use Yorkshire pudding as a base for a delicious dessert. We love this fruity version',5,4,1,1,6,'Heat oven to 220C/fan 200C/gas 7. Spread the fruit out in a medium baking dish. Make the batter according to pack instructions and add the caster sugar. Pour the batter over the fruit and bake for 20-25 mins until golden and set. Dust with icing sugar, if you like, and serve hot or warm.','5 mins','25 mins'),
(453,'tarte tatin','https://www.bbcgoodfood.com/recipes/tarte-tatin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tarte-tatin-502b8b9.jpg','Dark and sticky caramel, sweet apples and crisp pastry combine to make this heavenly French dessert. And with Raymond Blanc''s recipe, you can make it perfectly every time',76,6,3,1,6,'Roll the pastry to a 3mm-thick round on a lightly floured surface and cut a 24cm circle, using a plate as a guide. Lightly prick all over with a fork, place on a baking sheet, then cover and freeze while preparing the apples. Heat oven to 180C/160C fan/gas 4. Peel, quarter and core the apples. Put the sugar in a flameproof 20cm ceramic tatin dish or a 20cm ovenproof heavy-based frying pan and place over a medium-high heat. Cook the sugar for 5-7 mins to a dark amber caramel syrup that’s starting to smoke, then turn off the heat and stir in the 60g diced chilled butter. To assemble the tarte tatin, arrange the apple quarters very tightly in a circle around the edge of the dish first, rounded-side down, then fill in the middle in a similar fashion. Gently press with your hands to ensure there are no gaps. Brush the fruit with the melted butter. Bake in the oven for 30 mins, then remove and place the disc of frozen puff pastry on top – it will quickly defrost. Tuck the edges down the inside of the dish and, with a knife, prick a few holes in the pastry to allow steam to escape. Bake for a further 40-45 mins until the pastry is golden brown and crisp. Allow to cool to room temperature for 1 hr before running a knife around the edge of the dish and inverting it onto a large serving plate that is deep enough to contain the juices. Serve with crème fraîche or vanilla ice cream.','20 mins','1 hr and 15 mins'),
(454,'spiced apple & blackberry hand pies','https://www.bbcgoodfood.com/recipes/spiced-apple-blackberry-hand-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-apple-blackberry-hand-pies-15f720a.jpg','Bring autumn into your kitchen with these delicious apple and blackberry pies. Sprinkle with sugar and serve with custard for the ultimate comfort food',29,6,1,1,6,'Put the apples and golden caster sugar in a saucepan with 2 tbsp water and cook, covered, for 6 mins or until the apples are soft, stirring once or twice. Tip the mixture into a shallow dish and spread out to cool slightly, then tip in the blackberries and stir. In a separate bowl, mix the cornflour with the honey and cinnamon, then stir it into the fruit mixture. Unroll the pastry sheets and, using a 10cm round cutter, cut out 12 circles. Spoon the fruit filling onto half of the pastry circles, leaving a border of about 1cm around the edge. Brush the beaten egg around the edges and place a plain pastry disc on top to encase the filling, then make a little dent in the top and bottom to form an apple shape. Seal the edges all the way around using the prongs of a fork, then brush with egg. Use the pastry off-cuts to cut out little leaf shapes and stalks, then stick them on with beaten egg. Poke a steam hole in the top with a skewer. Repeat with the remaining pastry and filling. Arrange the pies on a baking sheet lined with baking parchment and chill for at least 30 mins, or up to 24 hrs. Can be frozen unbaked for up to 2 months. Heat oven to 200C/180C fan/gas 6. Bake the pies for 20 mins, or until the pastry is golden and the filling is bubbling through the hole. If cooking from frozen, bake for an extra 5 mins. Sprinkle with the sugar, then leave to cool for at least 20 mins before serving with custard.','30 mins','30 mins'),
(455,'the best apple crumble','https://www.bbcgoodfood.com/recipes/best-apple-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/the-best-apple-crumble-1fb6036-scaled.jpg','You can''t beat a traditional apple filling topped with crispy, buttery crumble - classic comfort food at its best',77,4,1,1,6,'Heat the oven to 190C/170 fan/gas 5. Toss 575g peeled, cored and sliced Bramley apples with 2 tbsp golden caster sugar and put in a 23cm round baking dish at least 5cm deep, or a 20cm square dish. Flatten down with your hand to prevent too much crumble falling through. Put 175g plain flour and 110g golden caster sugar in a bowl with a good pinch of salt. Slice in 110g cold butter and rub it in with your fingertips until the mixture looks like moist breadcrumbs. Shake the bowl and any big bits will come to the surface – rub them in. Alternatively, pulse in a processor until sandy (don’t over-process). Pour the crumb mix over the apples to form a pile in the centre, then use a fork to even out. Gently press the surface with the back of the fork so the crumble holds together and goes crisp, then lightly drag the fork over the top for a decorative finish. Sprinkle 1 tbsp rolled oats and 1 tbsp demerara sugar over evenly, if you wish. Set on a baking tray and put in the preheated oven for 35-40 minutes, until the top is golden and the apples feel very soft when you insert a small, sharp knife. Leave to cool for 10 minutes before serving.','15 mins','40 mins'),
(456,'apple crisp','https://www.bbcgoodfood.com/recipes/apple-crisp','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Apple-crisp-54ecf7e.jpg','Try this moreish apple crisp. ‘Crisp’ is the American version of a crumble, with the topping a mix between a nutty flapjack and a traditional British crumble.',7,6,1,1,6,'Put all the topping ingredients and a pinch of sea salt in a bowl and rub together with your fingers until combined into a crumb-like texture – this can also be done in a food processor. Chill until needed. Will keep chilled for up to two days. Toss the apples with the sugar and lemon zest and juice until evenly coated, and tip into a 23cm round baking dish that’s at least 5cm deep, or a 20cm square dish. Flatten down with your hand and scatter over the topping. Will keep covered and chilled for a day. Heat the oven to 190C/170C fan/ gas 5. Sit the crisp on a baking tray and bake for 40 mins until the topping is a deep golden and the fruit is bubbling at the edges. Leave to cool for 10 mins, then serve with ice cream or custard.','20 mins','45 mins'),
(458,'somerset pomona, apple & almond cake','https://www.bbcgoodfood.com/recipes/somerset-pomona-apple-almond-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4146_12-d34ca29.jpg','This simple cake makes a great dinner treat or serve with cream for a filling dessert',5,8,2,1,6,'Heat oven to 180C/fan 160C/gas 4. Butter and line the base and sides of a 20cm round cake tin. Peel, core and slice the apples to about the thickness of a £1 coin. Melt the knob of butter in a large frying pan then tip in the apples. Cook over a medium heat, stirring every so often till softened, but not soggy, about 5 mins. Splash in the Pomona and leave to cool. Tip onto a plate to cool more quickly if you like. Using an electric whisk or wooden spoon, beat the softened butter and sugar until creamy and light. Add the eggs a little at a time, beating after each addition, then fold in the flour and almonds. Finally, fold in half of the cooled apples. Spoon the mix into the prepared tin, smooth the top and scatter with the rest of the apples, pressing down lightly into the batter. Bake for 1 hr, until golden and a skewer inserted into the middle of the cake comes out clean. Check the cake after 45 mins, cover with foil if it’s browning too quickly. To make the glaze, warm the jam and Pomona in a small pan until the jam melts. Once the cake is out of the oven, prick it all over with a skewer and drizzle over a little more Pomona. Cool for 10 mins, then turn out of the tin and transfer to a wire rack. Brush the glaze over the apples and serve sliced, with dollops of Pomona cream. Best eaten on the day it’s made, or warmed through the next.','25 mins','1 hr'),
(459,'toffee apple turnover puff pie','https://www.bbcgoodfood.com/recipes/toffee-apple-turnover-puff-pie','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Toffee-apple-turnover-puff-pie-4fd08c3.jpg','Think apple turnover made into one big pie in this super-simple pudding with a caramelised apple centre. Serve with a scoop of ice cream or double cream',52,6,1,1,6,'Chop the apples into small chunks. Heat the butter in a pan over a medium-high heat until sizzling, then scatter in most of the sugar, reserving about 1 tsp. Bubble until the sugar has dissolved and the mixture is sticky. Tip in the apples and stir to coat. Season with a small pinch of sea salt flakes and cook for 5-6 mins until the apples have softened and started to caramelise and any juices they have released have evaporated. Remove from the heat and set aside to cool completely. Once cooled, will keep chilled for up to a day. Heat the oven to 200C/180C fan/gas 6 with a baking tray inside on the middle shelf. Unravel the pastry on its baking parchment. Spoon the apple mixture over the right side of the pastry, leaving about a 1-2cm border around the edge, then brush the border all around the pastry with some of the beaten egg yolk. Fold the left side of the pastry over the apple filling to enclose it. Crimp or pinch the edge, or use a fork to seal. Use the back of a knife to score six triangles over the top of the pie, and make a few small slits in the centre of each. Brush all over with the rest of the egg yolk, then scatter with the reserved 1 tsp sugar. Carefully lift the pie, still on its parchment, onto the hot baking tray in the oven. Bake for 25-30 mins until puffed up and deeply golden. Leave to cool for 10 mins, then cut into wedges using the marked-out triangles as a guide.','20 mins','40 mins'),
(460,'apple flapjack crumble','https://www.bbcgoodfood.com/recipes/apple-flapjack-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1069547_10-f1dcd02.jpg','Sweetening the apples with apricot jam and orange juice makes it twice as fruity and adding a little syrup to the oaty crumble makes great little chewy clusters',43,6,1,1,6,'Heat oven to 190C/fan 170C/gas 5. Peel, core and thinly slice the apples and mix with the jam and orange juice. Spread evenly over a buttered 1.5-litre ovenproof dish, not too deep. Mix the oats, flour and cinnamon in a large bowl. Add the butter in small chunks and rub in gently. Stir in the sugar and rub in again. Drizzle over the syrup, mixing with a knife so it forms small clumps. Sprinkle evenly over the apples and bake for 30-35 mins until the juices from the apples start to bubble up. Cool for 10 mins, then serve with custard, cream or ice cream.','20 mins','35 mins'),
(461,'flat apple & vanilla tart','https://www.bbcgoodfood.com/recipes/flat-apple-vanilla-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-22870_12-c85bd34.jpg','A gorgeous looking tart that is fantastic for any occasion - from morning tea to decadent dessert',43,6,1,1,6,'Heat oven to 220C/fan 200C/gas 7. Roll out the pastry and trim to a round about 35cm across. Transfer to a baking sheet lined with parchment paper. Peel, core and thinly slice the apples and toss in the lemon juice. Spread over the pastry to within 2cm of the edges. Curl up the edges slightly to stop the juices running off. Dot the top with the butter and sprinkle with vanilla and caster sugar. Bake for 15-20 mins until the apples are tender and the pastry crisp. Warm the conserve and brush over the apples and pastry edge. Serve hot with vanilla ice cream or crème fraîche.','10 mins','20 mins'),
(462,'eve''s pudding','https://www.bbcgoodfood.com/recipes/eves-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2010/01/eves-pudding-a08d328.jpg','This delicious classic British apple pudding is easy to make and great for feeding the family, serve with custard',78,6,1,1,6,'Heat oven to 180C/160C fan/gas 4. Peel, core and slice the apples. Grease a large serving dish, about 2.5 litre/3 litre with 25g/1oz of the butter and tip in the apple. Scatter the raisins over the top. In a separate dish beat the remaining butter and sugar together for 10 mins until the butter turns pale and the sugar starts to dissolve. Beat in the eggs, then carefully fold in the flour with a large spoon. Spread the batter over the apples, then bake for 45 mins until golden. Serve with the custard, below.','30 mins','45 mins'),
(523,'domino potato, cod, prawn & chorizo pie','https://www.bbcgoodfood.com/recipes/domino-potato-cod-prawn-chorizo-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/domino-potato-199a650.jpg','Fish pie is a universal comfort food, we''ve given it a Spanish twist and a domino effect potato topping. The saffron and chorizo add a real depth of flavour',6,6,2,1,8,'Peel and thinly slice the potatoes, tip into a pan of cold water and bring to a simmer. Turn off the heat, leave the potatoes in the water for 1 min, then drain and leave to cool in a colander. The potatoes should still feel firm and hold their shape. Put the cod in a wide, deep pan and pour over the milk. Add 2 bay leaves and saffron and bring to a gentle simmer, cover with a lid, then lower the heat and cook for 2 mins. Turn off the heat and leave the fish in the pan to continue cooking for 5 mins more. Heat the oil and butter in another large pan, add the onion and fennel and cook for 10 mins until starting to caramelise. Add the garlic and chorizo, stir until the oils are released, then stir in the flour. Remove the fish from the milk and set aside the bay leaves. Add the milk to the chorizo pan bit by bit, stirring between additions, until you have a smooth, thick sauce. Stir in the parsley, season and remove from the heat. Heat oven to 200C/180C fan/gas 6. Pour half of the sauce into a large casserole dish. Flake the fish into large chunks and scatter over the sauce. Add the prawns and spoon over the rest of the sauce. Stack 5-6 potato slices together and trim off the edges to create a rectangle– don’t worry if it’s not perfect. Continue making stacks of rectangular potatoes until they’re all used up. Scatter the trimmings over the sauce, then arrange the slices on top in a domino pattern, fanning them out to cover the surface. Tuck the reserved bay leaves and four others in among the potatoes, season and drizzle with oil. Bake for 45 mins until the potatoes are tender and golden and the sauce is bubbling up around the edges. Serve with green veg or salad.','45 mins','1 hr and 10 mins'),
(463,'sticky toffee apple pudding','https://www.bbcgoodfood.com/recipes/sticky-toffee-apple-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5028_11-8399454.jpg','Try a fruity version of the classic sticky toffee pudding for a rich after-dinner treat. Serve with cream, custard or vanilla ice cream',8,6,1,1,6,'Heat oven to 180C/fan 160C/gas 4. Grease a 2-litre/3½-pint ovenproof dish lightly with butter. Tip the flour, sugar and baking powder, along with a pinch of salt, into a large bowl. Mix together the milk, butter, egg and vanilla extract and stir into the dry ingredients until you get a smooth batter. Arrange the apples in the dish, spoon the batter on top and smooth with a knife until the apples are covered. For the topping, pour 250ml boiling water over the sugar and stir together until smooth. Pour the liquid over the pudding mixture, then scatter over the pecans. Bake for about 40 mins until the pudding has risen and is golden. Use a big spoon to serve the pudding, making sure you get some of the gooey caramel sauce covering the bottom of the dish. Serve with pouring cream, warm custard or vanilla ice cream.','20 mins','40 mins'),
(464,'apple hand pies','https://www.bbcgoodfood.com/recipes/apple-hand-pies','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Apple-hand-pies-7b6be6d.jpg','Make a batch of apple hand pies and freeze them, uncooked, for a later date. Spiced with cinnamon and nutmeg, they''re encased in a golden shortcrust pastry',46,5,1,1,6,'Tip the apples, brown sugar, cinnamon, nutmeg and butter into a saucepan set over a medium heat and simmer, stirring occasionally for 6-8 mins until the apples are soft. Tip the mixture into a bowl and leave to cool. Meanwhile, unroll the pastry with a short side facing you. Cut in half lengthways so you have two long rectangles, then cut each into five smaller rectangles, so you have 10 in total (they should be 12 x 7cm). Once the spiced apple mixture has cooled, toss with the cornflour. Spoon into the middle of half the pastry rectangles, leaving the remaining five empty. Brush some of the beaten egg over the exposed pastry around the apples, then carefully place the plain pastry rectangles on top of the filling. Press down around the sides so the filling is encased. Crimp the edges using a fork to seal, then use the tip of a knife to lightly cut three lines across the top of the pies so that steam can escape during cooking. Arrange the pies on a baking sheet lined with baking parchment and brush all over with more of the beaten egg. Chill for at least 30 mins in the fridge or 10 mins in the freezer. At this stage, the pies can be chilled for up to 24 hrs or individually covered and kept frozen for two months. Heat the oven to 200C/180C fan/gas 6. Brush with more of the beaten egg, sprinkle with the demerara sugar and bake for 15-20 mins, or until the pastry is golden (add another 10 mins if baking from frozen). Once baked and cooled, will keep in an airtight container at room temperature for up to three days.','20 mins','25 mins'),
(465,'spiced toffee apple cake','https://www.bbcgoodfood.com/recipes/spiced-toffee-apple-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-803452_11-03d44c6.jpg','Try a twist on traditional toffee apples - sugared, spiced and very very nice!',17,15,1,1,6,'Heat oven to 180C/160C fan/gas 4. Grease and line a 20 x 30cm baking tray with baking parchment. Put the dates and milk in a small pan and bring to a simmer. Remove from the heat and set aside for 15 mins to cool. Whizz the date mixture to a smooth purée in a food processor or blender, then scrape into a large mixing bowl. Tip in the butter, flour, brown sugar, baking powder, eggs, mixed spice and vanilla, and set aside while you prepare the apples. Quarter and core the apples, then slice quite thinly, tossing in a little lemon juice as you go. Quickly beat together the cake ingredients with an electric whisk until smooth, then scrape into the baking tray. Arrange the apple slices, overlapping in rows, on top of the cake – you should be able to get 3 rows lengthways down the tray. Bake for 45-50 mins until a skewer poked into the centre of the cake comes out clean. Cool in the tray. Put the toffees in a small pan with a good splash of milk and gently melt, stirring, until runny. To finish, dust with a little icing sugar, then drizzle sauce all over the cake. Cut into squares or slices to serve.','45 mins','1 hr'),
(466,'cinnamon apple pecan pudding','https://www.bbcgoodfood.com/recipes/cinnamon-apple-pecan-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cinnamon-apple-pecan-pudding-8e0399f.jpg','It may taste indulgent, but this low-sugar fruity bake is sweetened with xylitol and topped with crunchy pecans',4,6,1,1,6,'Heat oven to 180C/160C fan/gas 4 and lightly grease a 1-litre (20 x 16cm) pie or oven dish. Tip the butter and xylitol into a bowl with the flour, oats, cinnamon and baking powder. Break in the eggs, add the milk, then beat with an electric hand whisk until evenly mixed and smooth. Stir in all the apple, then scrape into the dish, level the top and scatter with the pecans. Bake for 35-45 mins until risen and golden and a skewer inserted into the centre comes out clean. Serve with Greek yogurt or cream.','10 mins','45 mins'),
(472,'easy chocolate fudge cake','https://www.bbcgoodfood.com/recipes/naughty-chocolate-fudge-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-fudge-cake-91de17a.jpg','Need a guaranteed crowd-pleasing cake that''s easy to make? This super-squidgy chocolate fudge cake with smooth icing is an instant baking win',79,8,1,1,6,'Heat the oven to 180C/160C fan/gas 4. Oil and line the base of two 18cm sandwich tins. Sieve the flour, cocoa powder and bicarbonate of soda into a bowl. Add the caster sugar and mix well. Make a well in the centre and add the golden syrup, eggs, sunflower oil and milk. Beat well with an electric whisk until smooth. Pour the mixture into the two tins and bake for 25-30 mins until risen and firm to the touch. Remove from oven, leave to cool for 10 mins before turning out onto a cooling rack. To make the icing, beat the unsalted butter in a bowl until soft. Gradually sieve and beat in the icing sugar and cocoa powder, then add enough of the milk to make the icing fluffy and spreadable. Sandwich the two cakes together with the butter icing and cover the sides and the top of the cake with more icing.','25 mins','30 mins'),
(467,'apple & blackberry crumble','https://www.bbcgoodfood.com/recipes/apple-blackberry-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-393505_11-1dd0b92.jpg','Make this seasonal apple and blackberry crumble for a comforting family pud. Pre-cooking the topping will make it extra-crispy, while also retaining the texture of the fruit',76,4,1,1,6,'Heat oven to 190C/170C fan/gas 5. Tip 120g plain flour and 60g caster sugar into a large bowl. Add 60g unsalted butter, then rub into the flour using your fingertips to make a light breadcrumb texture. Do not overwork it or the crumble will become heavy. Sprinkle the mixture evenly over a baking sheet and bake for 15 mins or until lightly coloured. Meanwhile, for the compote, peel, core and cut 300g Braeburn apples into 2cm dice. Put 30g unsalted butter and 30g demerara sugar in a medium saucepan and melt together over a medium heat. Cook for 3 mins until the mixture turns to a light caramel. Stir in the apples and cook for 3 mins. Add 115g blackberries and ¼ tsp ground cinnamon, and cook for 3 mins more. Cover, remove from the heat, then leave for 2-3 mins to continue cooking in the warmth of the pan. To serve, spoon the warm fruit into an ovenproof gratin dish, top with the crumble mix, then reheat in the oven for 5-10 mins. Serve with vanilla ice cream.','10 mins','25 mins'),
(468,'apple, pear & walnut crumble','https://www.bbcgoodfood.com/recipes/apple-pear-walnut-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-817454_10-a07734b.jpg','Gary Rhodes''s traditional fruit and nut pudding can be oven baked in an hour for a warming family treat',80,4,1,1,6,'Heat oven to 190C/fan 170C/gas 5. Put the chopped apples, lemon zest, rosemary, if using, and sugar in a saucepan. Add the lemon juice and cook for 3-4 mins. Tip in the pears and cook 2-3 mins more, until the fruits are just beginning to soften. Spoon the fruits and juices into a 1.7 litre pudding dish and flatten slightly. For the crumble, finely chop half the walnuts in a food processor until reduced to the consistency of ground almonds. Add the flour, butter and sugar and continue to blitz to a coarse or fine crumble mix, whichever you prefer. Roughly break the remaining walnuts with your fingers and stir into the crumble. Scatter the crumble mix on top of the fruits and bake for 20-25 mins until golden brown. Serve warm with cream or custard.','30 mins - 35 mins','20 mins - 25 mins'),
(469,'apple charlotte','https://www.bbcgoodfood.com/recipes/apple-charlotte','https://images.immediate.co.uk/production/volatile/sites/30/2021/05/Apple-Charlotte-b4e51e5.jpg','Rustle up a classic apple Charlotte for dessert. With a soft centre of caramelised apples and a bread and butter casing, it''s delicious with custard or ice cream',46,6,1,1,6,'Heat the oven to 200C/180C fan/gas 6. Butter a 1-litre pudding basin, then dust with sugar, making sure you cover the whole inside and tipping out any excess sugar. Put a strip of parchment parchment across it (so it’s easier to remove the pudding later). Cut the crusts off the bread and flatten the slices with your hands. Dip in the melted butter, covering both sides. Use around 4½ slices of the bread to line the base and sides of the basin, overlapping them to make sure there aren’t any gaps. Transfer the mould to the fridge while you make the filling. To make the filling, put the sugar in a large saucepan over a medium heat for a few mins until it turns a dark caramel colour – avoid stirring it. Add the butter and stir to combine, being careful as it will steam a little. Add the apples, orange juice, zest and cinnamon, and stir to combine. Cook over a high heat for 5-8 mins until the apple has softened and the liquid has reduced slightly, stirring occasionally. Remove the pudding basin from the fridge and pour the apple filling into it. Cover the top of the filling with the remaining bread and press down the edges of the bread to seal it together. Put the pudding on a baking tray and bake for 35-40 mins, or until golden brown. Leave to cool for 5 mins, then carefully tip upside down onto a serving plate and remove from the basin. Serve with custard or ice cream.','15 mins','55 mins'),
(470,'baked apple & toffee crumble','https://www.bbcgoodfood.com/recipes/baked-apple-toffee-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apple-crumble-0e64371.jpg','This comforting bake has it all – circles of apple, buttery brown sugar, plump fruit and spice, all baked under a nutty oat topping',33,6,1,1,6,'Heat oven to 190C/170C fan/gas 5. Put everything for the apple layer, except the lemon juice and apples, into a bowl and microwave on High for 1½ mins until the butter has melted and the sugar is syrupy. Toss in the apples and lemon juice, then spoon into a medium baking dish, making sure the dried fruit is evenly distributed. Rub the flour and butter together, first into fine crumbs, then keep going until the mix forms bigger clumps, a bit like a rough biscuit dough. Stir in the sugar, oats and almonds. Scatter over the fruit, then cover with foil and bake for 1 hr, removing the foil for the final 30 mins, until golden and bubbling. Rest for 10 mins, then serve with custard or ice cream.','20 mins','1 hr'),
(471,'baked apples','https://www.bbcgoodfood.com/recipes/baked-apples','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-374527_11-40394c8.jpg','Little hands aged from 3-6 can help make these sweet treats, best served with vanilla ice cream',5,6,1,1,6,'Ask a grown-up to turn the oven on to 200C/180C fan/ gas 6. Sit each apple on the worktop and push the apple corer into the centre of each one (or ask a grown-up to do this for you). Mix the sultanas, muscovado sugar and cinnamon together in a bowl. Stand up the apples, side by side, in a baking dish. Using your fingers, push a little bit of the sultana mixture into each apple, using up all the mixture between them. Add a blob of butter to the top of each and sprinkle over the demerara sugar. Ask a grown-up to put the dish in the oven for 20 mins or until the apples are cooked through.','25 mins','20 mins'),
(473,'espresso, chocolate & chilli cake with coffee cream','https://www.bbcgoodfood.com/recipes/espresso-chocolate-chilli-cake-coffee-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/espresso-chocolate-chilli-cake-with-coffee-cream-0cf5e15.jpg','The warmth of coffee and chilli combine beautifully with chocolate in this impressive dinner party dessert, finish with double cream',10,8,1,1,6,'Heat oven to 180C/160C fan/gas 4. Grease a 22cm springform cake tin with soft butter and line the bottom with baking parchment. Shake a little flour around the buttered edges. Melt the chocolate and butter together over a pan of simmering water, or in a microwave. Once melted, whisk together until smooth and add the espresso powder and chillies and chilli powder, if using. Whisk the egg yolks with the brown sugar and caster sugar, either in an electric mixer or with an electric hand whisk, for 5-10 mins until thick and pale. In a separate bowl, using a clean whisk, beat the egg whites and cream of tartar until soft rounded peaks form. Pour the chocolate mixture onto the beaten yolks and sugar, and gently mix until smooth. Add the almonds and flour and mix again. Spoon the egg whites into the chocolate mixture and gently fold together. Pour the batter into the prepared tin and bake for 40-45 mins until the cake is set but with a slight jiggle in the centre. (If the cake is starting to brown too quickly, cover with a piece of baking parchment.) Leave to cool in the tin – it will sink a little as it cools. Run a knife around the inside of the cake tin to loosen. Remove the baking parchment and place on a cake stand. Whisk the cream and espresso powder until soft peaks form. Serve on the side or spoon on top of the cake dusted with a little cocoa powder.','20 mins','40 mins - 45 mins'),
(474,'chocolate orange tart','https://www.bbcgoodfood.com/recipes/chocolate-orange-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-orange-tart-6cf6dac.jpg','This clever no-bake tart from Elle Young uses raw cacao and coconut oil, sweetened naturally with dates, oranges and honey',5,12,1,1,6,'Put the dates in a bowl, cover with boiling water and leave to soak for 20 mins. Meanwhile, whizz the ingredients for the crust in a food processor until combined. Tip the mixture into a 23cm loose- bottomed fluted tart tin, using your fingers to spread it evenly across the base and push into the fluted sides. Cover with cling film and chill while you make the filling. Drain the dates and put in a blender with the zest and juice of 1 orange, the coconut oil, honey and cacao. Whizz together until mostly smooth, then scrape into the chilled crust and smooth with the back of a spoon. Chill for at least 1 hr. Remove from the fridge 10 mins before serving and sprinkle with the remaining orange zest and extra cacao.','15 mins','No Time'),
(475,'chocolate & marmalade steamed pudding with marmalade cream','https://www.bbcgoodfood.com/recipes/chocolate-marmalade-steamed-pudding-marmalade-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-marmalade-steamed-pudding-with-marmalade-cream-9c8e619.jpg','Pair chocolate and marmalade for a dessert made in heaven. The cream – which works with lots of other desserts, too – takes it up another notch',70,8,2,1,6,'Butter a 1.2-litre pudding basin and put a circle of buttered baking parchment in the base. Sift together the cocoa, flour, bicarb, baking powder and a pinch of salt. Beat the butter and sugar together in an electric mixer until pale and fluffy, then add the orange zest. Gradually add the eggs, a little at a time, beating well after each addition. Stir in the marmalade. Fold in the sifted ingredients followed by the dark chocolate chunks. Scrape the batter into the prepared pudding basin. Put a piece of baking parchment on top of a sheet of foil (both should be large enough to cover the basin and overhang the edges). Fold a pleat along the middle, then place this, parchment side down, onto the pudding. The pleat should run across the centre. Tie to the basin firmly with string, using it to make a handle. Trim off the excess parchment and foil. Put the pudding in a large saucepan on a trivet or upturned saucer. Add enough boiling water to come one-third of the way up the sides of the basin. Bring the water to the boil, turn down to a simmer, cover the pan and steam the pudding for 2 hrs-2 hrs 15 mins. Keep topping up with hot water. Lift the pudding out of the pan, leave to sit for 10 mins, then run a knife between the basin and the edge of the pudding. Invert onto a plate – the pudding should slide out. Remove the parchment. Whip the cream and icing sugar to soft peaks, then stir in the marmalade. Serve the pudding with a dollop of the cream and some marmalade on top.','25 mins','2 hrs and 15 mins'),
(476,'squidgy chocolate pear pudding','https://www.bbcgoodfood.com/recipes/squidgy-chocolate-pear-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/squidgy-chocolate-pear-pudding-37c536a.jpg','This hot, gooey chocolate pud, with dark chocolate and canned pears, can be made ahead and frozen – a perfect no-fuss dinner party dessert',48,8,1,1,6,'Heat oven to 190C/170C fan/gas 5. Lightly grease a roughly 20 x 30cm shallow ovenproof dish. Put the butter in a large saucepan and place over a low heat until just melted. Remove the butter from the heat and stir in the sugar until well combined. Whisk the eggs together in a large bowl. Gradually add the eggs to the butter and sugar, beating well with a wooden spoon in between each addition. Sift the flour and cocoa powder on top of the egg mixture, then beat hard with a wooden spoon until thoroughly combined. Pour into the prepared tin or dish and nestle the pears into the chocolate batter. Put the chocolate on a board and cut into chunky pieces roughly 1.5cm with a large knife. Scatter the chocolate pieces over the batter and sprinkle with almonds, if you like. Can be frozen at this stage. Bake in the centre of the oven for 30 mins or until the mixture is crusty on the surface and lightly cooked inside. Do not allow to overcook, as the cake will become spongy rather than gooey in the centre. Serve warm with cream or ice cream','20 mins','35 mins'),
(586,'speedy tuna pasta salad','https://www.bbcgoodfood.com/recipes/speedy-tuna-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338894_11-b7a16e5.jpg','A quick pasta using leftovers from the night before. Makes a great packed lunch',8,4,1,1,9,'Microwave the cooked penne according to pack instructions. Tip into a bowl. Mix through the remaining ingredients (the warm pasta will really soak up the flavours). If using chilled leftover pasta, simply mix everything together – it will still taste great. Can be kept in the fridge for up to 3 days.','5 mins','No Time'),
(477,'chocolate ice cream','https://www.bbcgoodfood.com/recipes/chocolate-ice-cream','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Next-Level_Ice-Cream-3db3bd9.jpg','Forget the faff of ice cream makers and custard – this fudgy chocolate ice cream not only tastes divine, it''s easy to make at home too',7,6,1,1,6,'Melt the chocolate in a heatproof bowl set over a pan of just-simmering water, ensuring the bowl doesn’t touch the water, or do this in the microwave in 20-second bursts until smooth and glossy. Leave to cool slightly. Pour the cream, condensed milk and vanilla into a second bowl and beat for 3 mins using an electric whisk until slightly thickened and the beaters leave a trail in the mixture when lifted across the bowl. Be careful not to overwhip, as you don’t want the mixture to be too stiff – as soon as the beaters leave a trail, it’s ready. Gently fold the melted chocolate and cocoa powder into the cream mixture until thoroughly combined, ensuring the spatula is reaching the bottom of the bowl so all the chocolate gets mixed in. Fold in most of the chocolate chips, reserving a small handful. Scrape the mixture into a 1-litre freezerproof container, then scatter over the reserved chocolate chips. Cover and freeze for at least 6 hrs until firm but scoopable. Will keep frozen for up to a month. To serve, scoop the ice cream into chilled bowls or ice cream cones and sprinkle with a pinch of sea salt, if you like.','20 mins','No Time'),
(478,'chocolate torta della nonna','https://www.bbcgoodfood.com/recipes/torta-del-nonna','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/torta-del-nonna-b0cbe14.jpg','Give this Tuscan tart a delicious twist by adding dark chocolate to the traditional custard filling and delicate sweet pastry',81,6,2,1,6,'To make the pastry, put the flour, butter and icing sugar in a food processor and pulse until it resembles breadcrumbs. With the motor running, slowly add the egg yolks. If the mixture looks too dry, drizzle in 1-2 tbsp cold water. Tip it out onto your work surface and gently knead together. Flatten the pastry into a disc, wrap in cling film and chill for 20 mins. Meanwhile, make the filling. Pour the milk into a saucepan. Scrape the vanilla seeds from the split pod into the milk and add the pod as well. Heat the milk over a medium heat until it just starts to bubble around the edges. Remove from the heat and leave to cool a little. Beat the egg yolks, sugar and flour together with an electric whisk for 5-10 mins until pale and mousse-like. Remove the vanilla pod from the cooled milk and steadily pour over the beaten egg yolks, whisking constantly. Pour the custard mixture into a clean saucepan. Heat over a medium-low heat, whisking all of the time. Cook the custard for 10 mins, still whisking, until it is very thick. Remove from the heat and push through a sieve into a wide dish. Cover the surface with cling film to prevent the custard from forming a skin. Set aside to cool while you prepare the pastry case. Roll out the pastry between 2 pieces of baking parchment to line a 24cm loose bottomed, fluted tart tin. Cover with cling film and chill for 30 mins. Heat oven to 200C/180C fan/gas 6. Line the tart case with foil or baking parchment and fill with baking beans. Bake in the oven for 15 mins. Carefully remove the baking beans and foil, then return to the oven for 5-10 mins until the pastry is golden brown. Leave to cool in the tin on a wire rack while you finish the filling. Reduce oven to 160C/140C fan/gas 3. Gently beat the custard with the ricotta, orange zest and grated chocolate until just combined, and spread evenly in the tart shell. Sprinkle over the pine nuts and bake for 35-40 mins – the filling will still be quite wobbly but it will firm up as it cools. Chill for at least 3 hrs, or overnight. Dust with icing sugar to serve.','40 mins','1 hr and 15 mins'),
(479,'hot mocha puddings','https://www.bbcgoodfood.com/recipes/hot-mocha-puddings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hot-mocha-puddings-5ef399c.jpg','It only takes a few minutes to mix, microwave and top these hot chocolate puds with ice cream and liqueur for a rich, indulgent dessert',5,4,1,1,6,'Measure the milk in a large jug, then blend with the prunes and coffee in a food processor until smooth. Add the butter, sugar, egg, almonds, cocoa, flour and a pinch of salt, then blitz again to make a thick batter. Scrape evenly and cleanly (don’t take the mixture up the sides) into 4 x 175ml ramekins (don’t use any with metal on them!). Cook in the microwave, spaced round the edge of the turntable, on Medium for 6 mins. The puddings will still look a bit moist, but that’s fine. Stand for 5 mins, then serve topped with a scoop of ice cream and a little liqueur, if you like.','5 mins','5 mins'),
(480,'sweetheart choc & hazelnut sandwiches','https://www.bbcgoodfood.com/recipes/sweetheart-choc-hazelnut-sandwiches','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1213523_11-5612da6.jpg','A loving spoonful in the form of delicious heart-shaped French toasts with a sweet chocolatey filling',6,2,1,1,6,'Thickly spread 2 of the brioche slices with the chocolate hazelnut spread, then top with the remaining slices to make 2 sandwiches. Use a large heart-shaped cutter to cut the sandwiches into hearts. Whisk the egg, milk, vanilla and caster sugar in a wide, shallow bowl. Tip the hazelnuts onto a plate. Dip the sandwiches into the egg mixture, leave for 1-2 mins each side to allow the brioche to soak up the liquid, then dip one side of each sandwich into the nuts. The sandwiches can be chilled like this, covered loosely with cling film, for up to 1 hour. Heat the butter in a wide frying pan until melted and foaming. Add the sandwiches and cook for 1-2 mins each side until golden brown and puffed up. Dust with a little icing sugar and serve with ice cream and raspberries.','5 mins','5 mins'),
(481,'chocolate & banana french toast','https://www.bbcgoodfood.com/recipes/chocolate-banana-french-toast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-931471_11-08404b4.jpg','An indulgent treat for when you''re in need of a sweet fix - chocolate and banana sandwiched in fried bread',5,2,1,1,6,'Slice the banana into a small bowl and mash using a fork. Add the chopped chocolate and mix to combine. Lay 2 slices of bread on the work surface. Divide the chocolate and banana mixture between them, spreading it almost to the edges. Top each with a second slice of bread and press together to make a sandwich. In a shallow dish, whisk together the eggs, double cream, maple syrup and vanilla extract. Melt the butter in a large frying pan over a medium heat. Lay one sandwich into the egg mixture to coat one side, then carefully flip it over so that both sides of the sandwich are soaked in the egg. Lower the sandwich into the hot frying pan and cook for about 1 min on each side, until golden brown and the chocolate has started to melt. Repeat with the second sandwich. Cut the sandwiches in half to serve and sprinkle with a little ground cinnamon and either icing sugar or extra maple syrup if you’re feeling super-indulgent.','10 mins','8 mins'),
(579,'scandi beetroot & salmon pasta','https://www.bbcgoodfood.com/recipes/scandi-beetroot-salmon-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scandi-beetroot-salmon-pasta-c1efdcb.jpg','Eat well at work with this easy-to-prepare Scandinavian-inspired beetroot and salmon fusilli salad. It''ll keep you going through the afternoon',14,1,1,1,9,'Cook the fusilli following pack instructions, then drain and leave to cool. Toss the pasta with the beetroot, avocado, cucumber, dill, yogurt and horseradish, then season generously with black pepper. Gently mix the salmon and rocket through the pasta, then tip into your lunchbox or an airtight container.','10 mins','15 mins'),
(482,'chocolate coconut banoffee pie','https://www.bbcgoodfood.com/recipes/chocolate-coconut-banoffee-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1173515_12-b3483b2.jpg','A dazzling dessert with bourbon biscuit base, caramel layer, creamy coconut topping and banana toffee',17,10,2,1,6,'To make the base, gently melt the creamed coconut in a pan, stirring frequently. Meanwhile, whizz the biscuits in a food processor (if you don’t have one, bash to the finest crumbs you can). Mix with the melted coconut and a pinch of salt. Press over the base and up the sides of a 22cm round loose-bottomed fluted tin. Chill. To make the coconut cream, bring the coconut milk to almost a simmer. Meanwhile, whisk together the egg yolks, caster sugar and flours. Pour the hot coconut milk over while whisking continuously. Tip back into the pan and gently heat, stirring constantly, until thickened, smooth and glossy. (At first it will be lumpy, but keep beating and it will become smooth.) Simmer for 2 mins, then take off the heat and stir in the Malibu. Cool with a sheet of cling film laid directly on the surface so a skin doesn’t form. Scrape the caramel into a pan with the chocolate, broken into chunks, and gently melt together. Peel and slice the bananas, then arrange the slices over the base of the pie. Pour over the chocolate caramel and chill again to set – 1 hr at least, or up to 24 hrs. When the coconut cream has gone cold, scrape into a bowl with the double cream and beat until just thick enough to hold a peak. Gently spoon into a plastic food bag (easy to transport) and chill until ready to serve. To serve, snip off the end of the cream bag and pipe all over the pie. Dust with a little cocoa to finish, and eat with any extra coconut cream mixture served in a bowl.','1 hr and 10 mins','20 mins'),
(483,'easy chocolate molten cakes','https://www.bbcgoodfood.com/recipes/easy-chocolate-molten-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-chocolate-molten-cakes-37a25eb.jpg','Bake an impressive dinner party dessert with minimum fuss – these chocolate puddings, also known as chocolate fondant or lava cake, have a lovely gooey centre',82,6,1,1,6,'Heat oven to 200C/180C fan/gas 6. Butter 6 dariole moulds or basins well and place on a baking tray. Put 100g butter and 100g chopped dark chocolate in a heatproof bowl and set over a pan of hot water (or alternatively put in the microwave and melt in 30 second bursts on a low setting) and stir until smooth. Set aside to cool slightly for 15 mins. Using an electric hand whisk, mix in 150g light brown soft sugar, then 3 large eggs, one at a time, followed by ½ tsp vanilla extract and finally 50g plain flour. Divide the mixture among the darioles or basins. You can now either put the mixture in the fridge, or freezer until you''re ready to bake them. Can be cooked straight from frozen for 16 mins, or bake now for 10-12 mins until the tops are firm to the touch but the middles still feel squidgy. Carefully run a knife around the edge of each pudding, then turn out onto serving plates and serve with single cream.','15 mins','20 mins'),
(484,'chocolate, pistachio & nougat semifreddo','https://www.bbcgoodfood.com/recipes/chocolate-pistachio-nougat-semifreddo','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-931464_10-af3cf9f.jpg','This traditional Italian ice cream bar is the perfect solution for an effortless frozen, creamy dessert',5,10,1,1,6,'Butter and line a 900g/2lb loaf tin with cling film. Bring a pan of water to a gentle simmer. Put the sugar and eggs in a heatproof bowl, then place over the simmering water and whisk until pale, thick and doubled in volume. Remove from the heat, plunge the bottom of the bowl into cold water and continue whisking until the mixture is cool. Melt the chocolate in the microwave on Low, stir, then fold into the egg mixture. Whip the cream to soft peaks and fold into the chocolate egg mixture with the nougat and pistachios. Pour the mixture into the loaf tin, smooth the surface with a spoon, then cover and freeze until firm. This can be done up to 2 weeks ahead. To serve, turn out onto a serving dish, remove the cling film and leave in the fridge for 15 mins to soften slightly before slicing.','25 mins','5 mins'),
(485,'double-the-love chocolate cake','https://www.bbcgoodfood.com/recipes/double-love-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-617498_10-2ef597e.jpg','A simple white chocolate buttercream looksgreat when it’s swirled over this gorgeous wedding cake – howeverroughly you do it',17,25,1,1,6,'Heat oven to 180C/160C fan/gas 4. Grease and line the base and sides of deep, round 20cm and 15cm cake tins with baking parchment – making sure the paper comes a few cms above the sides. Boil the kettle. Put half the butter and chocolate in a pan and gently melt, stirring. Mix together half of the flour, sugar, cocoa and bicarb with a pinch of salt. Whisk 2 of the eggs and 200ml buttermilk together. Scrape the melted chocolate mixture and egg mixture into the dry ingredients. Add 150ml boiling water and whizz everything together with an electric whisk until lump-free. Divide between the tins and bake for 40-45 mins, swapping round after 30 mins if they’re on different shelves (the 15cm one should be done after 40 mins). To test they’re cooked, push in a skewer and check it comes out clean. Cool the cakes in their tins. Repeat steps 1 and 2 again, so you end up with 2 x 20cm cakes and 2 x 15cm cakes. Melt the white chocolate in a bowl over a pan of barely simmering water. Beat together the butter and icing sugar, then beat in the white chocolate. When the cakes are cool, split each one in half. Use the icing to sandwich back together so you end up with 2 x four-layered cakes. Sit the 20cm cake on your serving plate and spread some more icing over the top. Sit the smaller cake on top of that, and completely cover with icing. Decorate with the chocolate buttons. If you want to add some flowers choose something edible, like roses. Make a posy, wrapping the stems in a little damp kitchen paper then some cling film, add a ribbon and add to the cake on the day of the wedding.','2 hrs','1 hr and 50 mins'),
(486,'chocolate & almond puds with boozy hot chocolate sauce','https://www.bbcgoodfood.com/recipes/chocolate-almond-puds-boozy-hot-chocolate-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-517527_11-17ab321.jpg','These make-ahead puds are easy, but look glamorous',50,2,1,1,6,'Heat oven to 180C/160C fan/gas 4. Butter 2 x 150ml dariole moulds, scatter in the flaked almonds so they stick to the base and sides and put on a small baking tray. Beat the butter, sugar, egg, flour, ground almonds and cocoa powder together, then divide between the moulds. Bake for 20-25 mins until a skewer comes out clean. Meanwhile, for the sauce, melt all the ingredients in a small pan and keep warm. Carefully turn the cakes out of their moulds and sit on 2 plates (the same way up as they baked). Serve with vanilla ice cream, the hot chocolate sauce, espressos and a splash more amaretto.','10 mins','25 mins'),
(487,'chocolate macarons','https://www.bbcgoodfood.com/recipes/chocolate-macaroons','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-143620_11-fb4db66.jpg','These luxurious little macarons are the perfect light hit when only chocolate will do',43,12,2,1,6,'Heat oven to 180C/ 160C fan/gas 4. Line a large baking sheet with baking paper. Sift the icing sugar and cocoa into a bowl, then stir in the ground almonds. Whisk the egg whites until stiff, then fold them into the dry ingredients. Fill an icing bag fitted with a plain nozzle with the mixture (or put in a large food bag and snip off the corner). Pipe 24 small blobs, about 3cm across, onto the baking sheet, leaving a little space between each. Smooth the surface with a wet finger, then leave for 15 mins to dry out. Bake for 15-20 mins until macaroons feel firm to the touch and peel easily off the paper. Cool on the paper, then peel off and store in a tin for up to 1 week. To make the filling, put chocolate into a heatproof bowl and gently melt over a pan of barely simmering water. Stir in the warm milk until smooth. Leave to cool and thicken a little, then use to sandwich the macarons together','25 mins','20 mins'),
(488,'choc-peanut fondants with chocolate soil & cherries','https://www.bbcgoodfood.com/recipes/choc-peanut-fondants-chocolate-soil-cherries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1250510_8-28d9b7e.jpg','Chocolate soil is a big restaurant trend at the moment - serve alongside your melting-middle pud for serious wow factor',17,6,2,1,6,'Make the soil first. Heat oven to 180C/160C fan/gas 4. Thinly slice the chocolate cake and lay on baking trays lined with baking parchment. Bake for 30 mins, turning the slices halfway, until crisp to the touch. Put in a food processor and whizz to crunchy crumbs. Will keep well for up to a week in an airtight tin. Make the fondants by brushing 6 dariole moulds well with melted butter. Chill for 1 min, then brush again. Put a spoon of cocoa powder in one and shake about so the whole inside is coated, then tip out the rest into the next and tap the bottom of the dariole to get out any excess. Repeat with all the darioles. Beat together the softened butter, peanut butter, sugar and vanilla to cream them, then beat in the eggs, followed by the flour. Stir in the melted chocolate and divide between the darioles. Chill for up to 24 hrs. To serve, heat oven to 200C/180C fan/ gas 6. Bake the fondants for 10-12 mins – the top will be set, but you should feel a soft middle when you prod it. Add a small pile of soil to each plate and top with a cherry – or put the soil in a mini bucket or flowerpot. Add a spoon of cherry compote to each plate and top with a scoop of vanilla ice cream. Finally, carefully turn each chocolate fondant out onto the plate – they should come out easily. Serve immediately.','40 mins','50 mins'),
(489,'no-bake chocolate tart','https://www.bbcgoodfood.com/recipes/no-cook-chocolate-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-21900_11-bd59661.jpg','You can make this indulgent chocolate tart up to two days ahead. Any leftovers will make a welcome treat with a cup of coffee the next day',43,8,2,1,6,'Crush the biscuits by putting them in a large, strong, plastic food bag and bashing with a rolling pin. Melt the butter with the syrup or honey in the microwave, then stir in the biscuits. Press onto the base and up the sides of a 12 x 36cm rectangular tin (or 23-25cm round flan tin) and chill while you make the filling. Break up the two chocolate bars and put in a large bowl. Melt in the microwave for 2-3 mins on Medium, stirring halfway through. Stir in the vanilla extract, then sift in the icing sugar. Whip the cream until it just holds its shape, then fold into the melted chocolate. Pour into the prepared tin and smooth the top. Chill for at least 2 hrs or for up to 2 days. Just before serving, remove the tart from the tin and slide it onto a flat plate (loosen edges first with the tip of a small pointed knife). You will find it easier to remove the tart if you leave it at room temperature for 30 mins. Put a few tsps of crème fraîche along the centre of the tart (or around the edges if the tart is circular) and top each with a raspberry, then dust lightly with icing sugar. Cut into thin slices and serve with extra raspberries on the side.','25 mins','No Time'),
(490,'chocolate ganache & salted caramel brittle','https://www.bbcgoodfood.com/recipes/chocolate-ganache-salted-caramel-brittle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ganache-6b7a52a.jpg','This restaurant-quality dessert is sure to dazzle your guests. The toffee shard also contains hazelnuts',50,6,2,1,6,'For the ganache, put the chocolate into a large bowl. Heat the cream in a pan until just boiling, then pour it over the chocolate. Leave for 5 mins, then stir until smooth. Put the ganache to one side to thicken and cool, then chill until needed. For the caramel sauce, heat the sugar and butter in a pan until the sugar has dissolved and the butter has melted. Add the cream, bring to the boil, then remove from the heat and leave to cool. For the brittle, line a baking tray with baking parchment, then put the sugar and 4 tbsp of water in a heavy-based frying pan and heat, without stirring, until golden brown. Stir through the nuts, and pour (in a thin layer) over the parchment. Sprinkle with ½ tsp flaky sea salt and leave to set. To serve, drizzle each plate with sauce, heat a large spoon in a cup of hot water, then scoop out a curl of chocolate ganache for each plate. Break the brittle into shards and push a piece into each scoop. Sprinkle with crumbled brittle and add a pinch of sea salt.','15 mins','20 mins'),
(505,'fish mappas','https://www.bbcgoodfood.com/recipes/fish-mappas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-mappas-a84eed1.jpg','A coconut fish curry, popular in the southern Indian state of Kerala - use pollock or any other sustainably sourced white fish fillets',5,4,1,1,8,'Put a large saucepan of water on to boil and cook the rice following pack instructions. Meanwhile, heat the oil in a large, wide saucepan over a medium heat and add the onions. Cook for 5-10 mins until softened and starting to colour. Add the garlic and tomatoes, and fry for 2 mins. Add the curry paste, fry for 2 mins more, then pour in the coconut milk and bring to the boil. Add the fish to the pan and simmer gently for 5-8 mins until just cooked through. Turn off the heat. Sprinkle the coriander over the curry and serve with the rice.','15 mins','20 mins'),
(491,'persimmon & white chocolate tart with pistachios','https://www.bbcgoodfood.com/recipes/persimmon-white-chocolate-tart-pistachios','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/persimmon-cdb768f.jpg','Poach fragrant, honeyed persimmons in vanilla syrup and serve in this impressive white chocolate and cocoa pastry tart',10,6,2,1,6,'Heat oven to 200C/180C fan/gas 6. Put all the pastry ingredients except the egg in a food processor. Add a pinch of salt and pulse until it has the consistency of chunky crumbs. Turn the motor back on and crack the egg in. Let it run until the mixture forms a ball, then remove. Roll the pastry out on a floured surface and use to line 6 x 8cm individual tart tins. Put in the freezer for 5 mins until hard. Line with baking parchment and baking beans. Place on a baking sheet, bake for 15 mins, then remove the parchment and baking beans. Bake for another 5 mins, then remove. Reduce the oven to 160C/140C fan/gas 3. To make the filling, put the cream in a heatproof bowl and microwave on High for 1 min, or heat over a pan of water. When hot, add the chocolate and stir until melted, then add the vanilla and eggs, whisk well, then pour into the tart cases. Bake for 10 mins, then cool at room temperature. Meanwhile, make the sugar syrup for the fruit, put the sugar, vanilla pod and 75ml water in a small saucepan. Heat until boiling, then simmer for 5 mins. When it’s syrupy, remove from the heat and add the persimmon. Put back on the heat for 1 min, then remove and cool to room temperature. Just before serving, add a slice of persimmon and a sprinkle of nuts to each tart.','20 mins','35 mins'),
(492,'white fish with spicy beans and chorizo','https://www.bbcgoodfood.com/recipes/white-fish-spicy-beans-and-chorizo','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513643_11-94bdc8c.jpg','Mop up the juices of this low-fat supper dish with crusty bread',5,4,1,1,8,'Heat the oil in a large frying pan, then soften the onion for 5 mins. Add the rosemary, chorizo and garlic, then fry for 2 mins more until the chorizo is starting to crisp. Tip in the passata, beans, cabbage and sugar, season, then simmer for 5 mins. Add the fish to the pan, leaving the tops of the fillets peeking out of the sauce, then cover with a lid and leave to cook for 3-5 mins or until the flesh flakes easily. Delicious served with crusty bread.','5 mins','15 mins'),
(493,'greek-style roast fish','https://www.bbcgoodfood.com/recipes/greek-style-roast-fish','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/greek-style-roast-fish-efd6bb8.jpg','Oven-bake white fish fillets with potatoes, tomatoes and herbs for a healthy and gluten-free weeknight dinner',4,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Tip the potatoes, onion, garlic, oregano and olive oil into a roasting tin, season, then mix together with your hands to coat everything in the oil. Roast for 15 mins, turn everything over and bake for 15 mins more. Add the lemon and tomatoes, and roast for 10 mins, then top with the fish fillets and cook for 10 mins more. Serve with parsley scattered over.','10 mins','50 mins'),
(494,'white fish with sesame noodles','https://www.bbcgoodfood.com/recipes/white-fish-sesame-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/white-fish-with-sesame-noodles-90a7e31.jpg','Whip up this tasty seabass served with spinach, noodles and sesame seeds in just 20 minutes. It makes a great choice if you''re looking for a speedy supper',41,2,1,1,8,'Use a spice grinder or pestle and mortar to crush the sesame seeds, then stir in the soy sauce, oil, 1 tbsp of water and a splash of rice vinegar, to make a creamy dressing, season and set aside. Bring a pan of salted water to the boil, add the noodles and cook following pack instructions, then drain and set aside. Using the same pan, tip in all the spinach and cook until reduced down and dark green. Tip in the noodles, along with the sesame dressing and a splash of water and toss well to heat through. Heat the oil in a non-stick frying pan over a medium to high heat. Season the skin of the seabass, then place in the pan skin-side down, fry until the skin has crisped up and the flesh has nearly all turned opaque, around 3 mins. Flip over and fry for 30 seconds further, until the fish is flaking and cooked through. Divide the noodles and greens between two bowls and place the fish on top. Scatter over the toasted sesame seeds and serve.','10 mins','10 mins'),
(495,'fish finger hot dogs','https://www.bbcgoodfood.com/recipes/fish-finger-hot-dogs','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Fish-finger-hot-dogs-a6ae660.jpg','A twist on a classic fish finger sandwich the whole family will love. Try Paul AInsworth''s trick of lightly salting the cod to firm it up before cooking – it''s a game-changer',3,4,1,1,8,'If you have time, lightly season the fish with sea salt flakes up to an hour before coating them, then cover and chill to firm it up. Put the flour mixed with some salt and pepper in a shallow bowl, then the beaten egg and breadcrumbs in another two bowls. Working in batches, coat the fish in the seasoned flour first, then shake off any excess and dip in the egg, followed by the breadcrumbs, then put the coated fish fingers on a plate. Can be done a day ahead and kept chilled overnight. Pour enough oil into a large frying pan so that it covers the base. Heat the oil over a medium-high heat, then frythe fish fingers in batches until golden on each side and cooked through (approx 3-4 mins each side). Drain on kitchen paper, season with sea salt, then transfer to a low oven to keep warm while you make the sauce. Put the gherkins in a bowl and mix with the mayonnaise and lemon juice. Split the hot dog buns, toast the cut-side under the grill, then spread each roll with 1 tbsp of the mayonnaise mixture, add a handful of lettuce, then a hot fish finger on top. Serve with the lemon wedges for squeezing over.','30 mins','15 mins'),
(496,'fish tacos','https://www.bbcgoodfood.com/recipes/fish-tacos-2','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Fish-Tacos-1337495.jpg','Pack in the flavour and lots of veg in this easy fish tacos recipe. It''s an easy way to get your family eating more fish, and one portion counts as three of their 5-a-day',6,4,1,1,8,'Combine the cumin, coriander, paprika and a generous pinch of salt in a large bowl, add the juice from 1 lime and mix well. Toss the fish fillets in the spiced lime paste and set aside while you prepare the salad. Finely slice the cabbage – you can do this by hand or in a food processor if you want it really fine – squeeze over the juice from half a lime and season with a little salt, scrunch the salt and lime into the cabbage and set aside. Chop the tomatoes into small pieces. Stone the avocado, scoop out the soft inside and slice, or if it’s a very ripe avocado you may want to mash it in a bowl with a little lime and salt. Keep all the salad ingredients separate on a board. Heat the grill to high. Line a baking tray with foil and brush with a little oil, place the fish fillets on the tray, pour over any paste from the bowl and brush with a little more oil. Cook the fish close to the grill for 8-10 mins until the fish is cooked and starting to scorch in places, to check its cooked, gently push one of the fillets, it should easily flake when cooked. If you have a gas hob, warm the tortillas directly over the flames with a pair of tongs for a charred finish. Alternatively, wrap in foil and warm in the oven on the shelf beneath the fish. To serve, spread a little soured cream over each warm tortilla, top with a handful of cabbage, some tomatoes and a few slices of avocado. Flake the fish and add a few big chunks to each tortilla then top with coriander, chilli and chilli sauce.','20 mins','10 mins'),
(497,'fish pie - in four steps','https://www.bbcgoodfood.com/recipes/fish-pie-four-steps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1001520_10-b8ac3a0.jpg','Delicious and easy - a fish pie anyone can make. You''ll learn how to poach fish and make a white sauce too',5,4,1,1,8,'Poach 400g skinless white fish fillets and 400g skinless smoked haddock fillets. Put the fish in the frying pan and pour over 500ml of the full-fat milk. Quarter 1 small onion and stud each quarter with a clove, then add to the milk, with 2 bay leaves. Bring the milk just to the boil – you will see a few small bubbles. Reduce the heat and simmer for 8 mins. Lift the fish onto a plate and strain the milk into a jug to cool. Flake the fish into large pieces in the baking dish. Hard-boil 4 eggs. Bring a small pan of water to a gentle boil, then carefully lower the eggs in with a slotted spoon. Bring the water back to a gentle boil, with just a couple of bubbles rising to the surface. Set the timer for 8 mins, cook, then drain and cool in a bowl of cold water. Peel, slice into quarters and arrange on top of the fish, then scatter over the chopped leaves of a small bunch of parsley. Make the sauce. Melt 50g butter in a pan, stir in 50g plain flour and cook for 1 min over moderate heat. Take off the heat, pour in a little of the cold poaching milk, then stir until blended. Continue to add the milk gradually, mixing well until you have a smooth sauce. Return to the heat, bring to the boil and cook for 5 mins, stirring continually, until it coats the back of a spoon. Remove from the heat, season with salt, pepper and a pinch of freshly grated nutmeg, then pour over the fish. Assemble and bake. Heat oven to 200C/fan 180C/gas 6. Boil 1kg floury potatoes, cut into even-sized chunks, for 20 mins. Drain, season and mash with the remaining 50g butter and 100ml full-fat milk. Use to top the pie, starting at the edge of the dish and working your way in – push the mash right to the edges to seal. Fluff the top with a fork, sprinkle over 50g grated cheddar, then bake for 30 mins. Make up to a day ahead, chill, then bake for 40 mins.','45 mins','30 mins'),
(498,'coconut fish curry & rice','https://www.bbcgoodfood.com/recipes/coconut-fish-curry-rice','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/220413_COCONUT_FISH_CURRY_RICE__0185_111-9bc4186.jpg','Cook this flavour-packed coconut fish curry in just 30 minutes for a speedy meal. For a dose of sour use lime juice or tamarind paste if you have it to hand',54,4,1,1,8,'Tip the fish into a large bowl with add ½ tsp each of salt and plenty of freshly ground black pepper, half the lime juice and half the turmeric. Stir gently to combine. Set aside. Heat the oil in a frying pan over a medium heat and cook the onion until softened, about 8-10 mins. Add the ginger, garlic, ½ tsp salt, the spices and remaining turmeric, and cook for 2 mins more until fragrant. Stir in the tomato purée and cook for a minute more, stirring. Tip the butternut squash and sweet potato mix into a heatproof bowl with a splash of water, cover and microwave on high for 3 mins until the veg is fork-tender. Tip the mixture into the pan with the onion and spices, and stir to combine. Add the coconut milk, 180ml water, the tamarind paste, if using, and a pinch each of salt and black pepper, and simmer over a medium heat for 10-12 mins until thickened and fragrant. Add the marinated fish to the hot coconut sauce in the pan and cook for 4-6 mins until the fish is opaque and cooked through. Break it into chunky flakes with a wooden spoon as it cooks. Remove the curry from the heat, stir in the remaining lime juice and scatter with the fresh coriander, then serve immediately with rice.','10 mins','20 mins'),
(499,'tomato & thyme cod','https://www.bbcgoodfood.com/recipes/tomato-thyme-cod','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192662_12-669095c.jpg','Ready in 20 minutes and low fat, this fish dish is great for a Monday night when you''d rather be on the sofa than in the kitchen',5,4,1,1,8,'Heat 1 tbsp olive oil in a frying pan, add 1 chopped onion, then fry for 5-8 mins until lightly browned. Stir in a 400g can chopped tomatoes, 1 heaped tsp light soft brown sugar, the leaves from a few sprigs of thyme and 1 tbsp soy sauce, then bring to the boil. Simmer 5 mins, then slip 4 cod fillets into the sauce. Cover and gently cook for 8-10 mins until the cod flakes easily. Serve with baked or steamed potatoes.','5 mins','15 mins'),
(517,'creamy leek, potato, cheddar & chive fish pie','https://www.bbcgoodfood.com/recipes/creamy-leek-potato-cheddar-chive-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Creamy-leek-cheddar-and-potato-fish-pie-a65c633.jpg','Use fresh fish to make this creamy, pastry-topped pie. The prawn, haddock and salmon filling and crumbly topping make this pie extremely moreish',14,5,2,1,8,'Tip the flour, salt, thyme and mustard powder into a food processor and pulse briefly. Add the butter and pulse again to a fine breadcrumb texture. Add half the egg and 1 tbsp cold water and pulse until it comes together into a dough (you can add up to 1-2 tbsp more water if it’s dry). Tip the dough onto a work surface and knead briefly. Wrap and chill for 30 mins. To make the filling, cook the potato in a pan of boiling water for 8-10 mins. Drain and leave to steam-dry. Heat the oil and butter in a large frying pan over a medium heat, and fry the leeks for 5 mins. Stir in the flour and cook for 2 mins, then add the milk, a little at a time, stirring between each addition to combine. Add the cream, cheese and mustard, and cook for 1-2 mins more until the cheese has melted. Stir in the chives and season. Remove from the heat and leave to cool for 15 mins. Heat the oven to 220C/200C fan/ gas 8. Gently fold the salmon, potato, haddock and prawns into the creamy leek mixture, then spoon into a 27cm round pie dish. Roll the pastry out on a lightly floured surface to the thickness of a £1 coin. Drape the pastry over the pie, then trim the excess. Crimp the edges, then shape the off-cuts to decorate the top, if you like. Brush with the remaining egg, then bake in the centre of the oven for 35-40 mins until golden. Serve with steamed greens.','5 mins','1 hr'),
(500,'chorizo, new potato & haddock one-pot','https://www.bbcgoodfood.com/recipes/chorizo-new-potato-haddock-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513528_11-91d8f56.jpg','Get fantastic results with minimum effort in this easy-to-make fish dish',33,2,1,1,8,'Heat a large lidded frying pan, then add the oil. Tip in the chorizo, fry for 2 mins until it starts to release its oils, then tip in the potatoes and some seasoning. Splash over 3 tbsp sherry, cover the pan tightly, then leave to cook for 10-15 mins until the potatoes are just tender. Move them around the pan a bit halfway through. Season the fish well. Give the potatoes another stir, add the cherry tomatoes and most of the chopped parsley to the pan, then lay the fish on top. Splash over 1 tbsp sherry, put the lid on again, then leave to cook for 5 mins, or until the fish has turned white and is flaky when prodded in the middle. Scatter the whole dish with a little more parsley and drizzle with more extra virgin oil. Serve straight away with crusty bread.','10 mins','20 mins'),
(501,'zesty haddock with crushed potatoes & peas','https://www.bbcgoodfood.com/recipes/zesty-haddock-crushed-potatoes-peas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-516487_12-72fb12b.jpg','Being pushed for time doesn''t mean you have to eat unhealthily, as this quick and low-fat fish supper proves',5,4,1,1,8,'Cover the potatoes in cold water, bring to the boil, then turn to a simmer. Cook for 10 mins until tender, adding peas for the final min of cooking. Drain and roughly crush together, adding plenty of seasoning and 1 tbsp oil. Keep warm. Meanwhile, for the dressing, mix 1 tbsp oil, the lemon juice and zest, capers and chives with some seasoning. Dust the fish in the flour, tapping off any excess and season. Heat remaining oil in a non-stick frying pan. Fry the fish for 2-3 mins on each side until cooked, then add the dressing and warm through. Serve with the crush and broccoli.','15 mins','20 mins'),
(502,'one-pot cabbage & beans with white fish','https://www.bbcgoodfood.com/recipes/one-pot-cabbage-beans-white-fish','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513819_11-dec2b45.jpg','This cabbage stew is based on a peasantdish from south-west France, calledgarbure, which is served with everythingfrom duck to fish',7,4,1,1,8,'Heat the butter in a large sauté pan until starting to sizzle, add the bacon, then fry for a few mins. Add the onion, celery and carrots, then gently cook for 8-10 mins until softening, but not brown. Stir in the thyme and cabbage, then cook for a few mins until the cabbage starts to wilt. Pour in the wine, simmer until evaporated, then add the stock and beans. Season, cover the pan, then simmer gently for 10 mins until the cabbage is soft but still vibrant. When the cabbage is done, cook the fish. Season each fillet, then dust the skin with flour. Heat the oil in a frying pan. Fry the fish, skin-side down, for 4 mins until crisp, then flip over and finish on the flesh side until cooked through. Serve each fish fillet on top of a pile of cabbage with a few small potatoes, if you like.','20 mins','30 mins'),
(503,'super-quick fish curry','https://www.bbcgoodfood.com/recipes/super-quick-fish-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513547_11-afc4279.jpg','A low fat fish curry that''s ready in a flash, make sure you buy your fish from a sustainable source.',5,4,1,1,8,'Heat the oil in a deep pan and gently fry the onion and garlic for about 5 mins until soft. Add the curry paste and stir-fry for 1-2 mins, then tip in the tomatoes and stock. Bring to a simmer, then add the fish. Gently cook for 4-5 mins until the fish flakes easily. Serve immediately with rice or naan','5 mins','10 mins'),
(504,'fish gratin with vegetables & prawns (gratíneraður fiskur með grænmeti og rækjum)','https://www.bbcgoodfood.com/recipes/fish-gratin-with-vegetables-prawns-gratineradur-fiskur-med-graenmeti-og-raekjum','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Fish-gratin-4158210.jpg','Make this comforting Iceland-inspired fish gratin by Good Food reader Terrie Hillier. During the summer months, it''s lovely served with a fresh salad.',83,4,2,1,8,'Heat the oven to 180C/160C fan/ gas 4 and butter an ovenproof baking dish (ours was 4cm deep). Cook the rice in a pan of lightly salted boiling water for 12 mins until tender, then drain and tip into the base of the prepared dish. Cook the carrots in a pan of boiling water for 10-12 mins until just tender, or tip into a heatproof bowl with a splash of water and microwave for 2-3 mins. (If you’re using canned or frozen carrots, you can skip this.) Mix the carrots with the leeks and spoon this over the rice, spreading it out to cover. To prepare the fish, remove the skin and any pin bones, then cut it into pieces. Arrange the fish over the vegetables, then scatter over the prawns. Mix the egg yolks, mayo, crème fraîche and cheese together, and season. Tip the egg whites into a clean bowl and beat with an electric whisk to stiff peaks. Gently fold the whipped egg whites into the mayonnaise mixture, then spoon this over the layer of fish and prawns in the dish. Spread it out with a spatula or the back of the spoon so it completely covers the fish. Bake for 20-25 mins, or until the topping is lightly golden brown. Leave to stand for a few minutes, then serve straight from the dish.','20 mins','20 mins - 25 mins'),
(506,'pan-fried sea bass with citrus-dressed broccoli','https://www.bbcgoodfood.com/recipes/pan-fried-sea-bass-citrus-dressed-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-191507_11-ec71ee0.jpg','A restaurant-quality fish supper, ready in just half an hour',84,2,2,1,8,'Before you start cooking, get everything prepared. Trim each sea bass fillet so they are both the same shape, then score the skin, cutting into the flesh slightly, 5 or 6 times at about 1cm intervals. Set aside. Segment the orange – slice off the top and bottom, then cut away the skin and pith. Cut away each segment, then squeeze out the juice from the rest of the orange into a bowl. Cut the broccoli into medium-size florets. To make the warm broccoli salad, cook the florets in a pan of boiling salted water for 1 min until just cooked. While the broccoli is cooking, put a frying pan on to heat. As soon as the broccoli is cooked, drain it, then tip straight into the hot frying pan to ‘scorch’ out all the moisture. Turn off the heat, then scatter the orange segments over the broccoli. Toss for a few moments just to heat through, then tip into a bowl and dress with the orange juice and 2 tbsp olive oil. Season with pepper and a small sprinkling of sea salt, then set aside. Wipe out the pan. Season the fish with a little salt and pepper just before cooking. Heat the frying pan until very hot, then add 2 tbsp oil. Lay the fish fillets in the pan, skin-side down. As soon as it goes in, press each fillet down with your fingers or a fish slice to stop it from curling up. Reduce the heat to medium, then leave the fish to cook for 3-4 mins, undisturbed, until you can see that the flesh has cooked two-thirds of the way up and the skin is crisp and brown. Flip the fillets over, then fry on the flesh side for about 2 mins until just done, basting the skin with the oil in the pan as it cooks. Leave to rest on a warm plate, skin-side up, and baste with the hot oil and juices from the pan. Pour 2 tbsp olive oil into the pan and place it back on a high heat. Scatter in the capers and anchovies, then cook until they start to crisp. Grate over the lemon zest and squeeze in the juice of ½ the lemon. If there isn’t enough juices in the pan to drizzle over both plates, add a splash more oil. You are now ready to plate up.','15 mins','15 mins'),
(507,'prosciutto & pesto fish gratin','https://www.bbcgoodfood.com/recipes/prosciutto-pesto-fish-gratin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513584_11-65e9f2a.jpg','Give white fish fillets a creamy makeover with this quick and flavour-packed recipe',5,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Season the fish all over, then wrap each fillet in a slice of ham. Put into a large baking dish. Dot the crème fraîche between the fillets and over the exposed ends of the fish. Dot the pesto around the fish, too. Scatter with the cheese. Bake the fish for 15-20 mins, adding the pine nuts halfway through, until the crème fraîche has made a sauce around the fish, and the cheese and ham are turning golden. Serve with plenty of crusty bread to mop up the sauce.','5 mins','15 mins'),
(508,'friday night fish pie','https://www.bbcgoodfood.com/recipes/friday-night-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-133464_12-8433b65.jpg','Everyone loves a fish pie. Lulu Grimes shares a delicious, easy recipe to pop in the freezer for those busy nights',19,2,1,1,8,'Simmer the potatoes for about 20 mins until tender, then drain and mash with most of the butter, plenty of seasoning and a little milk. Season and set aside. Meanwhile, cut the fish into 2cm cubes. Bring the milk just to a simmer in a large frying pan – when you see a few small bubbles, add the fish, onion and bay leaves, then cover and cook for 6-8 mins. Lift the fish out onto a plate and strain the milk into a jug. Melt the butter in a pan, stir in the flour and cook for 1 min over a medium heat. Take off the heat, pour in a little poaching milk, then stir until blended. Continue to add the milk gradually, mixing well until you have a smooth sauce. Return to the heat, bring to the boil, then simmer for 5 mins, stirring continuously, until it coats the back of a spoon. Remove from the heat, season with salt, pepper and nutmeg, then mix in the parsley. Heat oven to 200C/180C fan/gas 6. Divide the fish between 2 ovenproof dishes, about 1.3 litres each and strain any juices into the sauce. Scatter over the prawns, then add the eggs to both dishes if eating straightaway, or to just one dish if freezing the other. Pour the sauce over both. Spoon half of the mash over one dish so it covers everything, making sure it reaches the sides. Rough up the top with a fork and dot with remaining butter. Bake for 30 mins until the top is golden. Freeze the remaining fish and mash (see tips, below).','40 mins','55 mins'),
(509,'spiced bulgur pilaf with fish','https://www.bbcgoodfood.com/recipes/spiced-bulgur-pilaf-fish','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1125472_10-edff0cd.jpg','Use sustainable white fish in this North African-inspired rice one-pot with harissa and apricot - full of iron, fibre and 2 of your 5 -a-day',5,4,1,1,8,'Heat the oil in a lidded flameproof casserole dish. Tip in the onions and cook for 10 mins until soft and golden. Add the carrots and cumin, and cook for 2 mins more. Stir through the harissa, bulgur and apricots, pour over the stock and bring to the boil. Cover and simmer for 7 mins. Add the spinach and stir through until just wilted. Arrange the fish fillets on top, pop a slice of lemon on each and season. Replace the lid and cook for 8 mins, keeping over a low-ish heat. Turn heat to low and cook for 7-8 mins more until the fish is cooked through and the bulgur is tender. Season with pepper and serve.','10 mins','35 mins'),
(510,'pollock, beetroot & potato traybake with lemony crème fraîche','https://www.bbcgoodfood.com/recipes/pollock-beetroot-potato-traybake-lemony-creme-fraiche','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1287475_8-de1fa8e.jpg','This white fish is sustainable and cheaper than cod - serve it with fresh lemon dipping sauce and root veg',6,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Put the potatoes in a large baking tray and toss with the olive oil and fennel seeds. Season, arrange in a single layer, then bake for 20 mins until softened and starting to crisp. Turn the potatoes over and add the beetroot, season and return to the oven for 15 mins. Place the fish in the centre, season well and rub over a little oil from the tray. Return to the oven for 10 mins more. Meanwhile, sprinkle the lemon zest over the crème fraîche with a good grind of black pepper. To serve, scatter the fish with basil and dollop with some of the lemony crème fraîche.','10 mins','45 mins'),
(511,'fish with spiced lentils','https://www.bbcgoodfood.com/recipes/fish-spiced-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4167_11-80f40c7.jpg','A healthy supper, full of flavour, in less than half an hour',5,4,1,1,8,'Finely chop one of the lime quarters, including skin. Heat 2 tbsp oil in a medium saucepan then fry onions over a medium heat for 5 mins until softened and starting to colour. Add the curry powder and tomato purée then fry for another min. Tip in 200ml water, lentils, mango chutney and the chopped lime then bring to the boil. Simmer for 5 mins or until thickened. Heat remaining oil in a frying pan. Season the fish with salt and pepper to taste then fry for 5 mins, turning halfway, until golden and cooked through. Add a squeeze of lime to the pan and to the lentils then serve together with naan bread or chapatis on the side.','10 mins','15 mins'),
(512,'lemon-scented fish & chips','https://www.bbcgoodfood.com/recipes/lemon-scented-fish-chips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1034468_10-fc1d02a.jpg','Oven-bake white fish with carrots and potatoes and serve with a pea, broccoli and spinach puree - packed with goodness!',5,4,1,1,8,'Heat oven to 220C/200C fan/gas 7. Toss the carrots, potatoes, thyme and lemon slices in a large non-stick shallow roasting tray, with oil and seasoning. Cook for 25 mins, shaking the pan once or twice. Put the broccoli in a pan of boiling water and cook for about 5 mins or until tender. Stir through the peas and spinach. When all the spinach has wilted and the peas are tender, drain thoroughly. Blitz in a food processor to a smooth purée. Stir through the crème fraîche, a pinch of the lemon zest and some seasoning. Lay the fish on top of the roasted roots and cook for 15 mins or so more, until the fish is just cooked through. Make sure green veg purée is still hot and serve alongside the fish and roots.','10 mins','40 mins'),
(513,'easy fish soup','https://www.bbcgoodfood.com/recipes/spicy-fish-stew-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1202599_11-5c327b6.jpg','Perfect with some crusty bread, this simple healthy fish soup will soon become a favourite - you can also make it with chicken',5,4,1,1,8,'Heat oil in a saucepan. Tip in the garlic, cumin and paprika and cook for 1 min. Add 100ml water and the tomatoes. Bring to the boil, then turn down the heat. Add the pepper, simmer for 5 mins. Add the fish, simmer for 5 mins. Serve with coriander and a wedge of lemon.','5 mins','15 mins'),
(514,'baked haddock & cabbage risotto','https://www.bbcgoodfood.com/recipes/baked-haddock-cabbage-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327824_11-a478696.jpg','A must for fish lovers and a great meal-in-one - use whatever fish you have',5,4,1,1,8,'Heat oven to 200C/fan 180C/gas 6. Heat oil in a 2-litre casserole dish, then soften the onion over a medium heat for about 5 mins. Tip in the rice and cook for 2 mins, stirring well. Pour in the stock, bring to the boil, then add the cabbage. Cover and bake for 20 mins. Remove the pan from the oven and give the rice a stir. Place the fish on top of the rice, replace the lid, then bake for 5 mins. Flake the fish into large chunks and stir into the rice with the crème fraîche and half the Parmesan. Season with freshly ground pepper, then sprinkle with the remaining parmesan to serve.','5 mins','N/A'),
(515,'next level fish pie','https://www.bbcgoodfood.com/recipes/next-level-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/next-level-fish-pie-d1ddc84.jpg','Generous chunks of fish in a creamy sauce made with a few secret ingredients, topped with buttery mash and a crispy garnish. Take fish pie to the next level',7,6,2,1,8,'Heat 25g of the butter in a shallow pan and sizzle the shallot for 2 mins. Add the prawn heads, shells and smoked haddock skin and trimmings, turn up the heat and cook for 5 mins until they start to brown. Throw in the bay and star anise, then splash in the pernod if using and then the wine. Boil everything down until practically evaporated, then pour over 1 litre of milk and sprinkle in the vegetable bouillon. Simmer everything for 15 mins, turn off the heat, crush the shells with a potato masher and strain into a jug. Clean out the pan and melt 60g of butter, stir in the flour so you have a sandy paste and cook for 2 mins on a low heat. Gradually stir in the flavoured milk, then the crème fraîche, and simmer gently to make a very thick sauce. Season to taste. Turn off the heat and gently fold through all the fish and prawns, capers and lemon juice. Scrape into a large buttered baking dish. Heat oven to 220C/200C fan/gas 8. Bring the potatoes to the boil in a large pan of water, add the eggs and simmer for 8 mins. Scoop out the eggs, put in a bowl of cold water to cool, then peel, halve and push into the sauce. Drain the potatoes and mash with the remaining milk and butter. Spread or pipe the mash over the pie. Sprinkle with gruyère and/or crush over crisps. Put the pie on a baking tray and cook for 35 mins until golden and just bubbling over. Leave to sit for at least 10 mins before eating with buttered spinach, peas or watercress.','40 mins','1 hr'),
(516,'easy-to-scale cheesy fish pie with kale','https://www.bbcgoodfood.com/recipes/easy-scale-cheesy-fish-pie-kale','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish_pie-850c098.jpg','Double the ingredients in this easy fish pie if you need to feed four, or quadruple for eight. This comforting dinner is perfect for midweek, and adaptable for any situation',12,2,1,1,8,'Tip the potatoes into a pan, cover with hot water from the kettle and add a pinch of salt. Simmer for 15-20 mins until tender, then drain and leave to steam-dry. Meanwhile, warm the butter in a heavy-based pan over a low heat. Add the flour and stir until you have a paste. Gradually whisk in 200ml milk until you have a thick white sauce. Simmer for 3-4 mins, stirring. Season, then stir in the mustard and half the cheese, and continue to stir until the cheese has melted. Fold in the fish, peas and kale, and cook for 8 mins more, or until the kale wilts, the fish is just cooked and the prawns are starting to turn pink. Heat the grill to medium-high. Mash the potatoes with the spring onion and 1 tbsp milk. Fold in the remaining cheese and season well.  Tip the filling into a heatproof dish and spoon the cheesy mash on top, swirling it with a fork until the filling is completely covered. Sprinkle over the parmesan and grill for 10-15 mins until the pie is golden and bubbling. Serve with a green salad, if you like. Uncooked pie, well covered in the dish, will keep in the freezer for up to three months.','10 mins','45 mins'),
(518,'tuna pie','https://www.bbcgoodfood.com/recipes/tuna-pie','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Tuna-pie-45a4396.jpg','Enjoy this tuna pie with peppers and paprika encased in homemade pastry. It can be served hot or cold making it perfect for a buffet for a big gathering.',85,6,1,1,8,'For the pastry, pulse the flour and lard in a food processor until the mixture resembles breadcrumbs. Dissolve 1 tsp salt in 180ml boiled water, then add this and pulse until you have a smooth dough. Wrap and chill for 1 hr. To prep ahead, add 1 tsp white vinegar with the water. This way, it will keep chilled for two days. Heat the sunflower oil in a frying pan over a medium heat and cook the onions and peppers until the onions are translucent and the peppers are soft, about 8-10 mins. Drain 1 can of tuna, then add all the tuna along with the oil from the three cans to the onions and peppers. Sprinkle in the paprika and a pinch of salt, and cook, stirring, for 5 mins. Remove from the heat and leave to cool slightly. Will keep chilled for up to a day. Leave to cool first. To assemble, heat the oven to 200C/180C fan/gas 6. Divide the pastry in half and roll each half out on a lightly floured surface to a 30cm disc that’s roughly ½cm thick. Put one disc on a baking tray lined with baking parchment, then spoon the tuna filling into the middle, leaving a small border around the edge. Lay the second on top and crimp the edge to seal. Brush with the egg and bake on the middle shelf of the oven for 25-30 mins, or until golden and cooked through. Can be served hot or cold. Will keep chilled for up to two days.','15 mins','3 hrs'),
(519,'fish pies with smoked salmon, prawns & dill','https://www.bbcgoodfood.com/recipes/fish-pies-smoked-salmon-prawns-dill','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-17440_11-0beade5.jpg','These pies can be prepared the previous day - perfect for entertaining',4,8,1,1,8,'Boil the potatoes in salted water for 20 mins until tender. Drain, then mash with the green from the onion, the butter, milk and plenty of seasoning until smooth and lump-free. Pour the milk into a pan and add the butter and flour. Cook over a medium heat, whisking until the sauce is bubbling and thickened. Stir in the white parts of the onion, then bubble again for a min. Cool for about 5 mins, stirring frequently, then add the egg yolks, dill and smoked salmon. Season to taste. Divide the pollock between 8 x 300ml pie dishes or ramekins. Scatter over the prawns, stir then spoon over the smoked salmon sauce. Top each pie with mash, then cover and chill until ready to cook. They will keep in the fridge overnight. Heat oven to 190C/fan 170C/gas 5. Put the pies on baking sheets, then bake for 30 mins until bubbling and starting to turn golden. Serve with the broccoli (below).','40 mins','30 mins'),
(520,'fish & chip pie','https://www.bbcgoodfood.com/recipes/fish-chip-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-and-chip-pie-5c17e79.jpg','Hearty homemade pie meets a traditional British takeaway for a doubly satisfying family supper for those evenings when only comfort food will do',6,6,1,1,8,'Boil the eggs for 7 mins, then plunge straight into cold water and set aside to cool. Melt the butter in a large saucepan and add the oil and onions. Cook for 5 mins or until soft. Stir in the flour for 1 min to make a paste, then add the milk bit by bit, stirring as you go, to make a smooth sauce the consistency of double cream. Add the parsley, cornichons, capers and lemon juice, and season to taste. Heat oven to 200C/180C fan/gas 6. Peel the potatoes, cut into chips, put in a pan of water. Bring to a simmer, cook for 2 mins, then drain – the potatoes should still hold their shape. Leave to steam-dry for 5 mins. Spread half the sauce over the base of a large shallow casserole dish (ours was 30cm). Scatter the fish on top, peel and quarter the eggs, and add these too. Top with the remaining sauce. In a bowl, toss the chips with the vinegar, a drizzle of oil and some seasoning. Scatter over the pie and bake for 40 mins until the potatoes are golden. Meanwhile, cook the peas in a pan of boiling water for 2-3 mins, then drain and mash lightly with a knob of butter and the lemon zest. Serve alongside the pie.','45 mins','1 hr and 5 mins'),
(521,'fish pie with pea & dill mash','https://www.bbcgoodfood.com/recipes/fish-pie-pea-dill-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fishpie-bf5d3eb.jpg','Make this fish pie as a healthy midweek main. The peas, dill and lemon in the mash add plenty of flavour and contribute to your 5-a-day',4,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Cook the potatoes in a pan of boiling water for 10 mins, with the leeks in a covered steamer over the pan. Remove the steamer, add the peas to the potatoes and cook for 10 mins more. Drain the peas and potatoes, then mash with ½ tbsp crème fraîche, the lemon zest and juice, dill and bouillon. Arrange the leeks in a shallow ovenproof pie dish (about 18 x 24cm). Add the tomatoes, cod and prawns, then dot over the rest of the crème fraîche. Spoon over the mash, then spread it lightly to the edges with a fork. Bake for 30-35 mins until bubbling round the sides of the dish. Serve with veg, if you like. If you make this ahead and are cooking from cold, bake for about 10 mins longer.','10 mins','55 mins'),
(522,'luxe fish pie','https://www.bbcgoodfood.com/recipes/luxe-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/luxe-fish-pie-f016e12.jpg','Salmon and smoked haddock are perfect partners in this creamy, cheesy fish dish - freeze ahead and thaw for a make-ahead dinner party main',48,8,1,1,8,'Place the fish fillets in a large, wide saucepan and pour over the milk. Add the onion wedges and bay leaves, tucking them in around the fish. Bring to a very gentle simmer, then cover with a lid and remove from the heat immediately. Leave to stand and infuse for 10 mins or until the fish is just cooked. Drain the fish in a colander over a large jug to reserve the infused milk, then tip the fish into a bowl. Set aside. Meanwhile, make the topping. Put the potatoes in a large saucepan of cold water, bring to the boil, then simmer for 15 mins or until the potatoes are soft but not falling apart. Drain well, return to the pan and mash with the butter, half the cheese and the crème fraîche until smooth. Season to taste and set aside. To finish the filling, melt the butter in a medium saucepan and stir in the flour. Cook for a few secs, then gradually add the infused milk, stirring over a medium heat for 3 mins until the sauce is smooth and thick. Stir in the spinach and wine or vermouth, if using, and cook for 2 mins more. Remove from the heat and stir in the dill. Season to taste. Heat oven to 200C/180C fan/gas 6. Spoon a third of the sauce into the base of a 3-litre shallow rectangular dish. Scatter half the fish fillets over the sauce, breaking them into chunky pieces and discarding the skin, any stray bones, onion and bay leaves as you go. Pour over another third of the sauce, then top with more fish. Continue the layers once more, finishing with sauce. Spoon the potato over the fish mixture, starting at the edges. Swirl the potato with the back of a spoon and sprinkle over the remaining cheese. Place the dish on a baking tray and bake in the centre of the oven for 45 mins or until the potato is golden and the filling is bubbling.','45 mins','1 hr and 10 mins'),
(524,'fish pie with shoestring potato topping','https://www.bbcgoodfood.com/recipes/fish-pie-shoestring-potato-topping','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-pie-with-shoestring-potato-7fce3a6.jpg','Using spiralized potato instead of mash gives the classic fish pie a crispy topping. An ideal, filling family supper packed full of king prawns',16,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Bring a large pan of salted water to the boil. Put the peas in a small sieve that will fit the pan. Lower and cook for 30 seconds, then lift out, run under cold water and leave to drain. Drop the potatoes into the boiling water and cook for 1 min to soften partially, then drain. Pat dry with kitchen paper and transfer to a separate bowl. To make the white sauce, melt the butter in a large saucepan. Add the flour and cook for 1 min. Remove from the heat and slowly whisk in the milk until smooth then return to the heat, slowly bring up to the boil, whisking, then cook for 5–6 mins until you have a very thick sauce. Take off the heat and stir in the lemon zest and juice, mustard and chives. Gently stir in the peas, asparagus and fish. Transfer the filling to an ovenproof dish. Toss the potato with the olive oil, some flaky sea salt and black pepper. Top the fish pie mix with the potato spirals and bake in the oven for 20–25 mins until hot through. For a crispy topping, put the pie under the grill for 5 mins until the potato is golden.','16 mins','29 mins'),
(525,'puffed salmon & spinach fish pie','https://www.bbcgoodfood.com/recipes/puffed-salmon-spinach-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1222485_11-1161047.jpg','Adding greens to your mashed potato topping boosts the health benefits of this classic comfort food bake',33,6,2,1,8,'Boil the potatoes for 20 mins until tender. Meanwhile, put the fish in a frying pan, skin-side down. Stud the onion with the cloves and put into the pan with the bay, milk and stock. Bring to the boil, then cover and simmer for 2 mins. Remove the cod, which should flake easily, then re-cover and cook for 3 mins more until the salmon is just cooked through. Lift out the fish and set aside. Strain the cooking liquid. In another pan, melt the butter with the oil, stir in the flour and cook for 2 mins until it smells toasty. Take off the heat and slowly whisk in the fish cooking liquid. Return to the heat, whisk until the sauce boils and thickens, then stir in the mustard. Heat oven to 180C/160C fan/gas 4. Drain and mash the potatoes until smooth, then beat in the egg yolks, spinach and most of the cheese. Season generously with salt, pepper and nutmeg. Whisk the egg whites to soft peaks, then fold into the potato in 2 batches. Flake the fish into a large baking dish, pour over the sauce and top with the spinachy potato. Scatter with the rest of the cheese and bake for 35-40 mins until golden and risen.','15 mins','1 hr'),
(526,'fish pie mac ’n’ cheese','https://www.bbcgoodfood.com/recipes/fish-pie-mac-n-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-pie-mac-cheese-5ece214.jpg','Combine fish pie with mac ''n'' cheese to make this crowd-pleasing dinner. The dish freezes well, so you can make ahead if you like for busy weeknights',6,6,1,1,8,'Pour the milk into a large pan and add the flour and butter. Set over a medium heat and whisk continuously until you have a smooth, thick white sauce. Remove from the heat, add the mustard, most of the cheese (save a handful for the top), peas and parsley. Meanwhile, boil the pasta in a large pan of water following pack instructions until just cooked. Drain. Heat the oven to 200C/180C fan/gas 6. Tip the pasta into the sauce and add half the fish, stir everything together then tip into a large baking dish. Top with the rest of the fish, pushing it into the pasta a little, then scatter with the remaining cheese. Bake for 30 mins until golden, then serve with salad, if you like. Can be chilled and eaten within three days or frozen for up to a month. Defrost in the fridge, then reheat in a microwave or oven until piping hot.','10 mins','40 mins'),
(527,'summer fish pie','https://www.bbcgoodfood.com/recipes/summer-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-656503_11-ed46841.jpg','A great freeze-ahead fish pie recipe, packed with seafood and topped with crushed new potatoes. It feeds 10 so keep one in the freezer for last-minute entertaining',5,10,1,1,8,'Heat the stock in a small pan and add the salmon and pollock. Simmer gently for 5 mins, then turn off the heat and leave to sit for 5 mins more. Remove the fish, keeping the stock, and flake into large chunks, removing bones as you go. Spread onto the base of 1 large or 2 smaller overnproof dishes with the prawns, peas, dill and green beans. Pour the wine, reserved stock and cream into a pan and simmer to reduce a little. Mix the cornflour to a paste with 1 tbsp of the sauce and whisk back into the sauce, simmering until thickened. Pour over the fish and veg and leave to cool a little while you make the topping. Boil the potatoes in salted water until tender, about 10-15 mins. Drain and steam-dry for a few mins, then return to the pan with the olive oil, spring onions and half the cheese and chives. Season, crush lightly with a fork or potato masher, then spoon over the fish. Sprinkle on the remaining cheese and chives. To eat straight away, heat oven to 220C/200C fan/gas 7. Cook for 25-30 mins until the topping is golden and filling bubbling. To freeze, allow pies to cool completely, then wrap well and freeze. Defrost fully overnight and cook as above, just adding 10 mins to the cooking time. Or cook from frozen: cover with foil and cook at 180C/160C fan/gas 4 for 1 hr 45 mins, then remove foil and turn up to 220C/200C fan/gas 7 for 45 mins more, ensuring it is piping hot before serving.','30 mins','1 hr and 10 mins'),
(594,'hot pasta salad','https://www.bbcgoodfood.com/recipes/hot-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5287_12-a470fff.jpg','This a good source of vitamin C and packed full of veg. Take some to work',5,4,1,1,9,'Cook pasta according to pack instructions. Meanwhile, tip the mayonnaise, lemon juice and 1 tbsp of the tuna oil into a large bowl and mix. Drain the rest of the oil from the tuna, then flake the fish into the bowl and mix well. Drain the pasta and toss it with the mayonnaise mixture, peppers and onions. Scatter over the rocket just before serving.','5 mins','15 mins'),
(528,'smoked trout fish pies','https://www.bbcgoodfood.com/recipes/smoked-trout-fish-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-423533_12-8943392.jpg','Make a comforting fish pie in a flash, with this super-speedy recipe',5,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Melt the butter in a saucepan, then stir in the flour for 1 min. Gradually stir in the milk and horseradish, if using. Bubble the sauce to thicken for a couple of mins, then add the dill, three-quarters of the onions, the lemon zest, fish and some seasoning. Pour into 4 individual pie dishes or 1 large pie dish. Mix the mashed potato with the remaining spring onion. Spoon over the fish mixture, then bake for 15-20 mins until golden and bubbling.','20 mins','15 mins'),
(529,'spring fish pie','https://www.bbcgoodfood.com/recipes/spring-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2502_12-26298da.jpg','A special after-work supper treat – easily doubled if you''ve got friends over',5,2,1,1,8,'Heat oven to 220C/fan 200C /gas 7. Tip the spinach into a colander sitting in the sink and tip the potatoes into a saucepan. Bring a kettle full of water to the boil and pour enough over the potatoes to cover and slowly pour the rest over the spinach to wilt it. Bring the potatoes to the boil and cook for 8-10 mins until tender, then drain and roughly mash. Leave the spinach to cool, then squeeze out excess water with your hands. Scatter the spinach over the bottom of 2 individual or 1 small ovenproof dish leaving two gaps for the eggs. Crack the eggs into the gaps, then season with salt and pepper. Distribute the fish over the spinach and eggs. Spread over the crème fraîche and drizzle with the lemon juice. Loosely spoon over the potatoes, drizzle over the olive oil, then bake for 20-25 mins until the top is crispy and golden and the sauce is bubbling at the sides. Leave to stand for a few mins, then serve straight from the dish.','5 mins','35 mins'),
(530,'quick fish pie','https://www.bbcgoodfood.com/recipes/fastest-ever-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338701_11-99182a8.jpg','Our clever quick fish pie is the perfect midweek meal. This speedy supper is family-friendly, uses just a handful of ingredients and takes just 20 minutes',5,4,1,1,8,'Heat the grill to high. Heat the oil in a large pan, then gently fry the onion for 5 mins until soft. Add the tomatoes and stock, bring to the boil, then simmer for 5 mins. Season. Flake the smoked mackerel into the pan, stir, then tip the mixture into a shallow dish and sprinkle over the breadcrumbs and parmesan. Grill for 5 mins or until bubbling and golden.','5 mins','15 mins'),
(531,'fish pie with saffron mash','https://www.bbcgoodfood.com/recipes/fish-pie-saffron-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560911_12-e1657a9.jpg','A great dinner party dish. This can be made ahead and frozen so there''s always something ready to entertain guests',7,6,2,1,8,'Tip the shallots and vinegar into a saucepan and place on the heat so the vinegar evaporates quickly. Splash in the white wine and reduce until practically dry. Add cream, bring to the boil, then simmer gently until reduced by two-thirds and very thick. Leave to cool. Cut the fish into bite-size chunks and set aside with the prawns. Tip spinach into a large colander and pour over boiling water from a kettle until all the spinach is wilted (you may need to do this more than once). Cool under the cold tap, then squeeze the spinach tightly to get rid of any excess liquid. To make the topping, mash the saffron, garlic and chilli together using a pestle and mortar. Tip into a saucepan with the milk, bring to the boil, then remove from the heat to infuse. Bring the potatoes to the boil and simmer for 15 mins until soft. Drain, then mash well with the infused milk, olive oil, lemon juice and zest. Heat oven to 200C/fan 180C/gas 6. To assemble the pie, mix the fish and prawns into the cold cream until completely coated. Tip the fish into the bottom of a large gratin dish and scatter over clumps of crabmeat. Unravel the spinach and lay it over the fish, then top with the mash. Cook the pie for about 30 mins until golden and starting to brown and bubble around the edges. Serve with green veg if you like.','30 mins','1 hr and 15 mins'),
(532,'rosti-topped fish pie','https://www.bbcgoodfood.com/recipes/rosti-topped-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-189517_11-f0f0316.jpg','Fish pie without the fuss - a good recipe for frozen white fish such as coley',5,2,1,1,8,'Cook the potatoes in boiling salted water for 5-7 mins, until almost tender but firm enough to grate. Drain and refresh under cold running water. Put the coley in a shallow saucepan and pour over the milk. Bring to a simmer and cook for 5 mins. Drain the fish, reserving the milk, then set aside. Heat half the butter in a medium saucepan, add the leek, then cook for 5-6 mins until softened. Stir in the flour for 1 min, then remove from the heat. Gradually add the milk, stirring well between each addition. Return to the heat and stir until the sauce comes to the boil. Simmer for 2 mins, then stir in the parsley and mustard. Heat the grill to high. Flake the fish into large chunks, fold into the sauce, then place in a small ovenproof dish. Coarsely grate the potatoes. Melt the remaining butter, toss with the potatoes, season and scatter over the dish. Place under a medium grill for 5-10 mins until the potatoes are golden and tender.','15 mins','25 mins'),
(533,'next level paella','https://www.bbcgoodfood.com/recipes/next-level-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paella-308c905.jpg','Choose the freshest ingredients for a world-class paella with our ultimate recipe. Serve this classic Spanish seafood dish in the pan to impress your guests',7,4,2,1,8,'Heat 1 tbsp of the oil in a wide, shallow pan. Add the prawn heads and parsley stalks and sizzle until the heads turn pink, then mash with a potato masher. Pour over the sherry or wine and 300ml water, season with salt and simmer for 10 mins to make a stock, mashing the prawn heads as they cook. Scatter the mussels into the pan, cover the pan loosely with a lid or tea towel, then put over a high heat for 3-4 mins until the mussels just open. Stir to release the mussel juices, then pour the contents of the pan into a colander set over a large bowl containing the saffron. Let the saffron steep in the stock – you will need 700ml in total, so top up with water if needed and give everything a good stir. Pick the mussels out from the colander, then set aside. Wipe out the pan and add the rest of the olive oil. Sizzle the chorizo until it releases its oil, then add the onion and garlic and cook until softened. Add the squid and turn over until it turns white. Add the tomatoes and cook down for a minute, then pour over most of the stock, give everything a good stir and bring to the boil. Scatter the rice over the stock, stir well once, then boil vigorously for 5 mins. Reduce the heat to the lowest setting and slowly simmer for 10 mins without stirring until the rice has absorbed most of the liquid. Tuck the prawn tails into the rice and simmer for 5 mins, turning them over until cooked through. Stir through the mussels and broad beans or peas. Taste the rice – if it is still a little raw but the pan is dry, add a splash more stock and continue to cook; if it’s too soupy, then increase the heat to cook off the last of the stock. Once the rice is just cooked, turn off the heat and cover with a tea towel for a few minutes. Scatter over the parsley leaves and lemon zest, then season with smoked salt if you like. Stir everything once, then serve straight from the pan, with lemon wedges on the side.','40 mins','50 mins'),
(1230,'chocolate fondue & toasted marshmallow','https://www.bbcgoodfood.com/recipes/chocolate-fondue-toasted-marshmallow','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-17845_11-fccb753.jpg','There''s no getting away from it, toasted marshmallows are a messy business - but they''re all the better for it!',69,8,1,2,3,'Put the chocolate, butter, cream and milk into a saucepan, then heat gently, stirring occasionally, until the chocolate is melted and the sauce is smooth. Thread marshmallows onto skewers, then carefully toast on the fire, or leave until cold. Dip into the fondue and eat straight away.','5 mins','N/A'),
(534,'vegan paella','https://www.bbcgoodfood.com/recipes/vegan-paella','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Vegan-paella-e57539d.jpg','Achieve four of your 5-a-day with this vegan paella – it makes a satisfying yet low-calorie supper. We’ve included broad beans, a valuable source of plant protein',4,4,1,1,8,'Put the kettle on to boil. Tip the saffron, tomato purée and bouillon powder into a large heatproof bowl, then pour over 1 litre boiling water and set aside. Heat the oil in a large paella pan or frying pan over a medium heat and fry the onion for 5 mins, stirring often until starting to soften. Add the peppers, garlic and thyme, and cook for a few minutes more. Tip in the rice and paprika, and continue to cook, stirring for about a minute. Pour in the saffron stock, then cover and simmer for 10 mins. Uncover and gently stir, then add the frozen beans and courgettes. Return to a simmer, then cover and cook for another 10 mins until the rice is tender and has absorbed the stock. Leave to stand for 5 mins, then stir in the parsley. Serve half the paella straightaway with lemon wedges for squeezing over. The other half will keep chilled for up to three days. To enjoy on another day, reheat in the microwave until piping hot throughout.','20 mins','30 mins'),
(535,'easiest ever seafood rice','https://www.bbcgoodfood.com/recipes/easiest-ever-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259716_8-50cfcd8.jpg','Using pre-prepared mixed seafood makes this healthy rice dish very easy to put together. Plus, using one-pot means you save on washing up',5,4,1,1,8,'Heat the oil in a deep frying pan, then soften the leek for 5 mins without browning. Add the chorizo and fry until it releases its oils. Stir in the turmeric and rice until coated by the oils, then pour in the stock. Bring to the boil, then simmer for 15 mins, stirring occasionally. Tip in the peas and cook for 5 mins, then stir in the seafood to heat through for a final 1-2 mins cooking or until rice is cooked. Check for seasoning and serve immediately with lemon wedges.','5 mins','25 mins'),
(536,'paella in the oven','https://www.bbcgoodfood.com/recipes/paella-oven','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paella-ab5dea5.jpg','Try this foolproof way of cooking paella in the oven. You can adapt it according to what you have in the fridge – in this case, chicken, chorizo and prawns',77,4,1,1,8,'Heat oven to 220C/200C fan/gas 8. Put the chopped tomatoes (including their juice), stock, paprika and saffron in a large heatproof jug or bowl and microwave for about 5 mins on high until steaming hot. Tip the onion and garlic into a generous roasting tin or ovenproof dish, drizzle over the oil and mix to coat. Cook in the oven for 20 mins until beginning to brown. Stir in the rice, chicken, chorizo and hot stock mixture, season well and return to the oven for 20 mins (don’t cover). Stir in the peeled prawns and peas, dot any shell-on prawns on top, and return to the oven for 5-10 mins until the rice, chicken and prawns are cooked through. If serving with lemon, a nice touch is to pop the lemon slices on top of the paella for the last 5 mins of cooking, to make them hot and juicy. Check the seasoning and serve at once.','20 mins','45 mins'),
(537,'seafood paella','https://www.bbcgoodfood.com/recipes/seafood-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/seafood-paella-d41fe46.jpg','This impressive Spanish one pot, with monkfish, king prawns and mussels, is perfect for feeding a crowd at a dinner party',24,8,2,1,8,'Peel and de-vein the prawns, reserving the heads and shells. Return the prawns to the fridge. To make the stock, heat the oil in a large pan over a medium-high heat and add the onion, tomatoes, garlic, and reserved prawn shells and heads. Cook for 3-4 mins, then pour in 2 litres of water and add the stock cube and star anise. Bring to a boil, then simmer for 30 mins. Leave to cool slightly, then whizz in batches in a blender or food processor. Strain through a fine sieve. Heat the oil in a large paella pan or an extra-large frying pan. Brown the monkfish for a few mins each side, then remove and set aside. Add the onion and fry for 4-5 mins until softened. Stir in the rice and cook for 30 secs to toast. Add the garlic, paprika, cayenne (if using) and saffron, cook for another 30 secs, then stir in the tomatoes and 1.5 litres of the fish stock. Bring to the boil, then turn down to a simmer and cook, stirring, for about 10 mins (the rice should still be al dente). Return the monkfish to the pan with the prawns, mussels, peas and broad beans. Cover the pan with a large baking tray, or foil, and cook on a low heat for another 10-15 mins until the mussels are open and the prawns are cooked through. Scatter over the parsley before serving.','40 mins','1 hr and 10 mins'),
(538,'paella fried rice','https://www.bbcgoodfood.com/recipes/paella-fried-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-9696_11-b9e2a97.jpg','A great dish when you fancy a taste of the med in an instant. This cheat''s paella is as authentic as you can be in 10 minutes!',5,4,1,1,8,'Heat oil in a frying pan. Tip in the chorizo, onion and garlic, then cook for a couple of mins until softened. Stir through turmeric, followed by the rice, prawns and peas and 150ml boiling water. Keep stirring until everything is warmed through and the water has been absorbed. Serve with lemon wedges.','5 mins','5 mins'),
(539,'chicken & chorizo paella','https://www.bbcgoodfood.com/recipes/chicken-chorizo-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-chorizo-paella-c1aa390.jpg','Try swapping traditional seafood paella for a chicken and chorizo version – a hearty family supper for four',1,4,1,1,8,'Heat the olive oil in a deep frying pan over a high heat. Brown the chicken all over – don’t cook completely. Once browned, transfer to a plate. Reduce the heat to low, add the onions and cook slowly until softened, about 10 mins. Add the garlic, stir for 1 min, then toss in the chorizo and fry until it releases its oils. Stir in the spices, then tip in the rice. Stir to coat the rice in the oils and spices for about 2 mins, then pour in the stock. Bring to the boil, return the chicken to the pan and simmer for about 20 mins, stirring occasionally. Add the peas to the pan and simmer for a further 5 mins until the rice is cooked and the chicken is tender. Season well and serve with the lemon and parsley.','10 mins','40 mins'),
(540,'spicy clam & pork paella','https://www.bbcgoodfood.com/recipes/spicy-clam-pork-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-411594_11-da3025b.jpg','A must for chorizo lovers, this paella is a great party pleaser and will add a taste of the Med to any occasion',8,6,1,1,8,'Heat half the olive oil in a paella pan or a large frying pan. Tip in the pork and cook for about 5 mins until lightly browned, but not cooked through. Remove from the pan, then set aside on a plate. Pour the remaining olive oil into the pan, then add in the onion, garlic and chorizo. Cook for about 5 mins until the onion is softened and the chorizo is starting to crisp and give off its oil. Add the rice and paprika to the pan, then stir around for 1-2 mins until all the grains of rice are well coated. Can be prepared up to this point 1 day in advance and stored in the fridge. Pour the tomatoes and any juices into the pan along with the pork and 900ml of the vegetable stock. Bring to the boil, reduce the heat, then let it gently simmer for about 20 mins until the rice is nearly soft. Place the spinach in a sieve, then pour over a kettle of boiling water so the leaves are just wilted. Arrange the spinach leaves in the pan along with the clams. Pour over the remaining 100ml vegetable stock, then cook for another 10 mins. By this time the clams should have opened (discard any that haven’t) and the rice should be tender. Serve straight away from the pan.','20 mins','40 mins'),
(541,'easy turkey paella','https://www.bbcgoodfood.com/recipes/easy-turkey-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-turkey-paella-50f7f4e.jpg','All you need is a pack of sliced turkey and some veggies and you have the makings of an easy paella. A low fat supper you can whip up in 20 minutes',86,4,1,1,8,'Heat 1 tbsp olive oil in a large sauté pan. Fry the paella paste for 1-2 mins, then tip in the rice and cook for 2 mins more. Add 800ml boiling water, simmer for 12-15 mins or until the liquid has been absorbed, then season to taste. In another pan, fry the veg in 1 tbsp olive oil on a high heat for 1-2 mins. Add 50ml water, cover and steam for 2-3 mins. Tear the turkey into bite-sized pieces and stir through the rice with the veg.','18 mins - 24 mins','N/A'),
(542,'hey presto paella','https://www.bbcgoodfood.com/recipes/hey-presto-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3916_11-abca91d.jpg','Paella in a rush with all of the flavour',4,6,1,1,8,'This is really a construction job and a very clever cheat, as nearly everything is pre-cooked, from the rice to the chicken, peppers and seafood. Bring 400ml/14fl oz water to the boil in a large pan with the saffron, then add the peas and simmer for 2-3 minutes until they are just cooked and the saffron is infused. Tip in the rice and gently stir round the pan until it turns yellow, then stir in the chicken, seafood, peppers and olives. Cover and cook for a minute or so stirring occasionally over a medium heat until everything has heated through completely, then toss in the parsley. Arrange and serve. Pile on to a large warm platter and serve with lemon wedges round the sides.','15 mins','No Time'),
(543,'spring chicken paella','https://www.bbcgoodfood.com/recipes/spring-chicken-paella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paella-e684e0e.jpg','Throw a new spin on the classic Spanish rice one-pot by adding fresh, green broad beans, dill, mint and parsley',17,4,1,1,8,'Heat oven to 180C/160C fan/gas 4. Season the chicken thighs well and then dust all over with the flour. Heat 1 tbsp of the oil in a paella pan or large, deep frying pan. Fry the thighs until golden brown all over, then transfer to a shallow roasting tin and finish in the oven for 30-40 mins. Add the remaining oil, onions and garlic, and fry very gently until soft, about 10-15 mins. Stir in the rice, paprika, saffron and lemon zest, then add the stock and simmer, stirring occasionally, for about 20 mins on a medium heat until the rice is nearly cooked. Add the peas, broad beans and juice of 1 lemon until the rice is just cooked through, along with the veg. Stir through the herbs and as much of the remaining lemon juice as it needs, along with some seasoning. Tuck the chicken thighs back in and cover for 5 mins to let everything settle before serving.','20 mins','55 mins'),
(544,'smoked paprika paella with cod & peas','https://www.bbcgoodfood.com/recipes/smoked-paprika-paella-cod-peas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoked-paprika-paella-with-cod-peas-ed11502.jpg','Cod is a great source of stress-busting B vitamins and magnesium, so why not enjoy this easy peasy paella that''s also packed with three of your five-a-day, iron, folate and fibre',5,2,1,1,8,'Heat the oil in a non-stick frying pan over a medium-high heat and fry the onion and garlic for a couple of mins to soften. Add the rice and spices, stir briefly, then pour in the bouillon and add the pepper. Cover the pan, reduce the heat and leave to simmer for 20 mins. Stir in the courgette, cover and cook for 10 mins more. Add the peas and cod, cover the pan and cook for 10 mins more until the rice is cooked and the liquid has been absorbed. Toss with the parsley and serve with lemon wedges.','15 mins','40 mins'),
(545,'paella arancini bites','https://www.bbcgoodfood.com/recipes/paella-arancini-bites','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/paella-bites-3d39cb3.jpg','These crispy paella bites make the perfect canapé for a Spanish-style party. We predict they’re the new deep-fried mac ''n'' cheese, watch this space...',6,12,1,1,8,'With wet hands divide and shape the paella into 12 balls. If the paella is too loose, mix in a few tbsp breadcrumbs until it’s thick enough to roll. Roll each ball in flour, dust off the excess, then dip in egg. Mix the breadcrumbs with the paprika and roll the balls in the crumbs, until well coated. Chill until you’re ready to fry and serve, or for at least 30 mins to allow the rice balls to firm up. Fill a deep saucepan 3/4 full with oil. Bring up to 180C, or hot enough to brown a chunk of bread in 40 secs. Use a slotted spoon to carefully lower the arancini into the hot oil and cook for 3-4 mins until golden brown, then drain on kitchen paper. Don’t overcrowd the pan or the temperature of the oil will drop. To serve, skewer each arancini with a folded slice of chorizo and an olive.','25 mins','15 mins'),
(546,'roast cod with paella & saffron olive oil','https://www.bbcgoodfood.com/recipes/roast-cod-paella-saffron-olive-oil','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-954467_11-c5ea4e4.jpg','Impress your friends with Jason Atherton''s luxurious shellfish rice, topped with juicy cod and drizzled with saffron',87,6,2,1,8,'Heat oven to 180C/160C fan/gas 4. To make the paella, put the olive oil, chorizo, shallots and garlic in a large pan and fry for 7-8 mins until the chorizo releases its oil and the shallots have softened. Add the paprika and cook, stirring, for 5 mins, then add the rice, mix well and cook for a few mins more. Pour in 600ml of the stock, season and mix well to combine. Cover and put in the oven for 15 mins. Uncover, give everything a good stir, then return to the oven for 10 mins. When almost cooked, remove from the oven and spread onto a tray to cool. Chill and reserve for later. You can make this a day ahead. To make the saffron oil, whisk the olive oil and lemon juice together in a small saucepan. Add the saffron and heat gently for 5 mins, then remove from the heat and leave to infuse and cool. You can make this a day ahead. Heat oven to 200C/180C fan/gas 6. For the cod, put the olive oil and butter in an ovenproof frying pan over a high heat, but don’t let it burn. Season the cod with sea salt and black pepper, then cook, skin-side down, in the hot pan, for 4 mins, then carefully turn the fish over and put the pan in the oven for a further 3 mins, finishing with a little more sea salt and a squeeze of lemon juice. Meanwhile, put the paella back in the pan, stir in the remaining fish stock and cook for 2 mins until completely heated through. Stir through a knob of butter,the prawns and shelled mussels. Taste, season with lemon juice, salt and pepper if necessary, and finish with the chopped parsley. Serve with the roast cod, mussels in their shells and steamed purple broccoli or a green vegetable, if you like. Finally, drizzle everything with the saffron dressing.','20 mins','1 hr'),
(547,'spanish seafood rice','https://www.bbcgoodfood.com/recipes/spanish-seafood-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-218528_10-9549ea2.jpg','This quick version of paella is superhealthy, plus it''s all cooked in one pan so there''s minimal washing-up',5,4,1,1,8,'Heat the oil in a large saucepan and soften the onion for 6-7 mins. Add the pepper and garlic, cook for 2 mins more, then stir in the paella rice and cook for 1 min, stirring to coat. Pour in the stock, add the saffron and bring to the boil. Cook, uncovered, at a gentle bubble, for 20 mins, stirring occasionally until the rice is tender. Stir in the seafood and lemon juice and cook for 2 mins or until piping hot and completely cooked through. Serve in warm bowls scattered with the parsley.','10 mins','35 mins'),
(548,'smoky paprika seafood rice','https://www.bbcgoodfood.com/recipes/smoky-paprika-seafood-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoky-paprika-seafood-rice-4800594.jpg','Savoury smoked paprika gives a real lift to this healthy yet impressive paella-style dish with prawns, baby squid and mussels',88,4,1,1,8,'Heat the stock in a large saucepan. Add the saffron to infuse, if you like. Take off the heat and set aside. In a large deep-sided frying pan or paella pan, heat 1 tbsp of the oil. Smash the whole garlic clove and add to the oil. Throw in the prawns and cook for 2 mins until just turning pink but not cooked through. Push to one side of the pan and add the squid to the garlicky oil for 1 min or so, again just to colour. Remove the seafood to a plate. Add the remaining oil to the pan, and cook the onion and celery slowly for 15 mins until very soft and beginning to caramelise. Add the finely chopped garlic, the fennel seeds, tomato purée and paprika, and cook for 5 mins more. Meanwhile, bring the stock to a simmer. Add the rice to the pan with the onion mixture, give everything a good stir, then add the sherry and 1 litre of the hot stock. Bring to the boil and simmer gently for 15 mins, shaking the pan from time to time. When the rice is almost cooked but still has a little bite, dot over the prawns, squid and the mussels. Add the cooking juices and the rest of the stock. Cover and cook for 5 mins until the seafood is cooked through, the mussels have opened and the rice is just tender. (You may have to add a splash more water if the rice looks dry.) Discard any mussels that haven’t opened. Sprinkle over some chopped parsley and serve with lemon wedges to squeeze over.','15 mins','35 mins'),
(1046,'carrot cake monkey bread','https://www.bbcgoodfood.com/recipes/carrot-cake-monkey-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/monkey-bread-36ccf02.jpg','Put a twist on the classic American monkey bread with Easter flavours. It’s made to be pulled apart and eaten with your hands – like a monkey would!',46,12,2,2,6,'Gently heat the milk and 85g of the butter in a medium pan until the butter has melted and the milk has started to simmer. Leave to cool to room temperature. Tip the flour, orange zest, mixed spice, carrots, yeast, caster sugar and 1 tsp salt into a large bowl with the eggs, then add the cooled milk mixture and bring together into a sticky dough using your hands. Knead until combined, then tip onto a floured work surface and knead again for 5-10 mins until smooth and springy. Transfer the dough to a lightly oiled, large bowl, cover and leave to prove for 1 hr, or until doubled in size. Generously butter a 25cm bundt tin. Melt the remaining butter and leave to cool slightly. Mix the cinnamon, walnuts and brown sugar together in a medium bowl. Separate the proved dough into 40-50 x 25g pieces and roll into balls on a lightly floured surface. Roll the marzipan into small, pea-sized balls and set aside. Dunk the dough balls in the melted butter, then roll in the walnut and sugar mixture to coat. Drop the dough balls into the prepared tin, dotting around some of the marzipan balls as you go. Repeat until you’ve used up all the dough balls and marzipan. Cover and prove again for 45 mins until the dough has risen slightly. Heat the oven to 180C/160C fan/gas 4. Uncover the tin and bake for 40-45 mins, or until well risen and golden. Carefully loosen the bread from the edges using a skewer or butter knife, and leave to cool in the tin for 20 mins. Invert onto a serving plate, keeping the tin in place, and leave for 5-10 mins (the bread will naturally shrink away from the sides), then remove the tin. Gradually whisk the orange juice into the icing sugar to make a thick icing, then drizzle over the bread, letting it drip down the sides. Scatter over the orange zest and serve warm.','1 hr and 20 mins','45 mins'),
(549,'spanish rice & prawn one-pot','https://www.bbcgoodfood.com/recipes/spanish-rice-prawn-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513592_11-ee6a645.jpg','This quick and healthy one-pot, similar to paella, is filled with Mediterranean holiday flavours. Enjoy for a wholesome dinner any night of the week',5,4,1,1,8,'Boil the kettle. In a non-stick frying or shallow pan with a lid, fry the onion, peppers, chorizo and garlic in the oil over a high heat for 3 mins. Stir in the rice and chopped tomatoes with 500ml boiling water, cover, then cook over a high heat for 12 mins. Uncover, then stir – the rice should be almost tender. Stir in the prawns, with a splash more water if the rice is looking dry, then cook for another min until the prawns are just pink and rice tender.','4 mins','16 mins'),
(550,'spanish rice with squid, prawn & fennel','https://www.bbcgoodfood.com/recipes/spanish-rice-squid-prawn-fennel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spanish-rice-with-squid-prawn-fennel-e8153a5.jpg','Make this Spanish paella-style dish with squid ink for dramatic black rice – but if you can''t find it, use saffron instead for the more familiar yellow look',52,4,2,1,8,'Slice the squid into rings and tip into a mixing bowl with the tentacles, prawns, 4 tbsp olive oil, fennel seeds, the lemon juice, half the garlic and some seasoning, then set aside. Heat oven to 190C/170C fan/gas 6. Heat the remaining oil in a shallow, wide ovenproof pan. Fry the onions and chopped fennel for about 10 mins until soft and starting to turn golden, add the remaining garlic and cook for another 5 mins. Scatter in the rice, stir and cook until it just starts to crackle. Pour in the wine, ink and stock and bring up to a simmer, giving everything a gentle stir to incorporate the ink. Stir through most of the sliced squid, keeping a few rings and the tentacles back. Transfer the pan to the oven, cook for 25 mins, then place the prawns and remaining squid on top and drizzle everything with the oil from the marinade. Put back in the oven for another 10 mins or until all the liquid has been absorbed and the rice and seafood are just cooked. Scatter over the sliced fennel and fronds, lemon zest, and the chopped parsley. Place in the middle of the table and let everyone help themselves.','20 mins','45 mins'),
(551,'lemony prawn & chorizo rice pot','https://www.bbcgoodfood.com/recipes/lemony-prawn-chorizo-rice-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-872546_11-ffd7f9c.jpg','Spanish classic paella is given a healthy makeover and a good kick of heat - diet-friendly and fresh',17,4,1,1,8,'Boil the kettle. Heat the oil in a shallow pan with a lid, add the onion, peppers, chorizo, garlic and chilli, then fry over a high heat for 3 mins. Add the turmeric and rice, stirring to ensure the rice is coated. Pour in 500ml boiling water, cover, then cook for 12 mins. Uncover, then stir – the rice should be almost tender. Stir in the prawns and peas, with a splash more water if the rice is looking dry, then cook for 1 min more until the prawns are just pink and the rice tender. Stir in the lemon zest and juice with seasoning and serve with extra lemon wedges on the side.','15 mins','25 mins'),
(552,'baked rabbit & chorizo rice','https://www.bbcgoodfood.com/recipes/baked-rabbit-chorizo-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1176455_12-a8594ad.jpg','Use rich and tasty rabbit in this paella-like dish - it''s baked for ease, and is spiced with chorizo, paprika and saffron',7,6,1,1,8,'Cut the rabbit into about 20 small pieces (leave the bone in – ask your butcher to do this). Marinate in the wine, olive oil and thyme for 1 hr, or overnight if you can. Heat oven to 180C/160C fan/gas 4. Drain the rabbit from the marinade, reserving the liquid. Heat some oil in a shallow, flameproof casserole and sizzle the chorizo until crisp. Remove the chorizo and drain off the excess oil from the dish. Brown the rabbit pieces in 2 batches and set aside. Add the onion and garlic, cook until soft, then stir in the paprika and tomato purée. Stir in the rice, then pour over the reserved marinade, the stock and saffron. Nestle the rabbit into the rice with the chorizo, cover, bring to the boil, then bake for 40-50 mins, or until the rice and rabbit are tender and all the liquid has been absorbed. Stir through the frozen peas to defrost, then serve scattered with parsley.','20 mins','1 hr'),
(553,'steamed trout with mint & dill dressing','https://www.bbcgoodfood.com/recipes/steamed-trout-mint-dill-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-steamed-trout-with-mint-dill-dressing-f6dad6a.jpg','Steam these trout fillets and green veg for a delicious dinner that''s packed with nutrients, including omega-3 fats, calcium, folate, fibre, vitamin C and iron',5,2,1,1,8,'Put the new potatoes on to simmer in a pan of boiling water until tender. Cut the asparagus in half to shorten the spears and slice the ends without the tips. Tip the bouillon into a wide non-stick pan. Add the asparagus and beans, then cover and cook for 5 mins. Add the peas to the pan, then top with the trout and lemon slices. Cover again and cook for 5 mins more until the fish flakes really easily, but is still juicy. Meanwhile, mix the yogurt with the vinegar, mustard powder, mint and dill. Stir in 2-3 tbsp of the fish cooking juices. Put the veg and any remaining pan juices in bowls, top with the fish and herb dressing, then serve with the potatoes.','10 mins','25 mins'),
(554,'sardine tomato pasta with gremolata','https://www.bbcgoodfood.com/recipes/sardine-tomato-pasta-with-gremolata','https://images.immediate.co.uk/production/volatile/sites/30/2023/02/Sardine-tomato-pasta-with-gremolata-5a4d85b.jpg','Enjoy our crowd-pleasing pasta with sardines and gremolata. With added nutrients thanks to the sardines, it''s a healthy dish that''s low on calories and fat',15,1,1,1,8,'Cook the pasta following pack instructions in a large pan of boiling salted water. Heat 1 tbsp oil from the can of sardines in a non-stick frying pan over a medium heat and sizzle the capers and half the garlic for 1-2 mins until fragrant. Tip in the tomatoes and fry for 4-5 mins more until softened and bursting. Stir in the sardines and rocket, tossing a few times to break up the fish and wilt the leaves. Season. For the gremolata, combine the lemon zest, parsley and remaining garlic in a small bowl, and season. Drain the pasta and top with the sardine sauce and gremolata.','5 mins','15 mins'),
(555,'zesty salmon with roasted beets & spinach','https://www.bbcgoodfood.com/recipes/zesty-salmon-roasted-beets-spinach','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-main-9842afa.jpg','This fresh and filling salmon supper is packed full of heart healthy ingredients, is rich in calcium, iron and vitamin C and makes up 4 of your 5 a day',4,2,1,1,8,'Heat oven to 180C, 160C fan, gas 4. Trim the stems of the beetroot and reserve any tender leaves that are suitable for eating in the salad. Cut the beetroots into quarters then toss with 1/2 tbsp oil, the coriander seeds, and some seasoning then pile into the centre of a large sheet of foil and wrap up like a parcel. Bake for 45 mins or until the beetroots are tender then top with the salmon, scatter over half the orange zest and return to the oven for 15 mins. If you want to toast the pumpkin seeds, put them in the oven for 10 mins. Meanwhile cut the peel and pith from 2 oranges then cut out the segments with a sharp knife working over a bowl to catch the juices. Finely grate the garlic and leave for 10 mins to allow the enzymes to activate. Stir the garlic into the orange juice and remaining oil with seasoning to make a dressing. Remove the parcel from the oven and carefully lift off the fish. Tip the beetroot into a bowl with the red onion, remaining orange zest, pumpkin seeds and spinach leaves and toss well. Gently toss through the orange segments and avocado with any beet leaves then pile onto plates and top with the warm salmon. Drizzle over the dressing and serve while still warm.','10 mins','1 hr'),
(556,'mediterranean fish gratins','https://www.bbcgoodfood.com/recipes/mediterranean-fish-gratins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mediterranean-fish-gratins-c17b32c.jpg','These individual portions of fish in tomato sauce, topped with herby breadcrumbs, freeze beautifully - perfect for no-fuss entertaining',48,6,1,1,8,'Heat the oil in a large, wide non-stick saucepan or sauté pan and gently fry the onion, fennel, garlic and coriander seeds for 15 mins, stirring regularly until the vegetables are softened and lightly coloured. Pour the wine into the pan and add the tomatoes, tomato purée, saffron and bay leaf. Season and bring to a gentle simmer. Cook for about 15 mins, stirring occasionally, until thick. Heat oven to 220C/200C fan/gas 7. Stir the lemon juice and most of the parsley into the tomato mixture, pop the raw fish pieces and prawns on top and stir well. Cover tightly with a lid and simmer gently over a medium heat for 4-5 mins or until the fish is almost cooked. Stir a couple of times as the fish cooks, taking care not to let it break up. Ladle the hot tomato and fish mixture into 6 individual pie dishes – they will each need to hold around 350ml. Mix the cheese, breadcrumbs, remaining parsley and a little ground black pepper together and sprinkle over the top. Bake on a baking tray for 20 mins or until the pies are golden brown and bubbling. Serve with green salad, if you like.','25 mins','55 mins'),
(557,'cod & anchovy bake','https://www.bbcgoodfood.com/recipes/cod-anchovy-bake','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Cod-and-Anchovy-Bake-9044624.jpg','Get your weekly portion of white fish with this healthy all-in-one recipe made with anchovies, tomatoes, spinach and red pepper. It delivers four of your five-a-day',4,2,1,1,8,'Cook the potatoes and onions in boiling water for 5-7 mins. Drain, then gently mix with the thyme, anchovies and some black pepper. Meanwhile, heat the oil in a pan and cook the pepper, covered, for 5 mins. Add the tomatoes, vinegar, bouillon and garlic, and cook for 8-10 mins more, uncovered, until the tomatoes have softened. You don’t want this to be too wet, so continue to cook until any excess liquid has evaporated, if necessary. Heat the oven to 200C/180C fan/gas 6. Lay the cod loins in a shallow ovenproof dish and spoon over the tomato mixture, then the potato and onion mixture, in even layers. Sprinkle over the cheese. Bake for 30 mins until the cheese is golden and the fish is flaking but moist. Serve with the spinach on the side.','10 mins','45 mins'),
(558,'cod & olive tagine with brown rice','https://www.bbcgoodfood.com/recipes/cod-olive-tagine-with-brown-rice','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Cod-tagine-6950186.jpg','Make the most of cod in this delicately spiced, aromatic tagine that''s low in fat and calories. Like other seafood, cod is a useful source of iodine',4,4,1,1,8,'Heat the oil in a large pan over a medium heat and fry the onions, swede, carrots, lemon peel and garlic for 10 mins, stirring frequently. Add the spices and stir briefly. Stir in the tomatoes, tomato purée, stock and olives. Cover and cook for 25 mins. Boil the rice following pack instructions. Stir the tomato mixture well, then nestle in the frozen cod fillets. Cover and cook for 20 mins until the fish is cooked through. Scatter with the coriander. Serve half with half the cooked rice. Leave the rest of the tagine and rice to cool, then keep chilled for up to three days. Reheat the tagine in a pan over a low heat until piping hot, and reheat the rice in the microwave until completely heated through.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','55 mins'),
(559,'cod & smashed celeriac','https://www.bbcgoodfood.com/recipes/cod-smashed-celeriac','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cod-smashed-celeriac-1362cb5.jpg','If you need a healthy, low-calorie midweek meal after a busy day, try this cod served with smashed celeriac and courgette. It''s two of your five-a-day',29,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Put the celeriac into a roasting tin, drizzle with 2 tbsp oil, then sprinkle over the fennel seeds. Season and bake in the oven for 45 mins, stirring halfway through, until lightly charred. While the celeriac cooks, put the spring onions in a dry pan and cook over a high heat for 3-4 mins, turning occasionally, until starting to char. Spread two large sheets of foil out on your work surface. Divide the spring onions between the foil sheets, then top with the grated courgette, garlic and fish. Season and drizzle over the lemon juice and the remaining 1 tbsp olive oil. Scrunch up the edges of the foil to seal and create two parcels. Put the parcels on a baking sheet and bake in the oven for 10-12 mins. When the celeriac is cooked, mash the pieces with a potato masher or a fork and season well. Pile the smashed celeriac onto plates, then carefully open the fish parcels – the contents will be hot – and gently slide onto the plates next to the celeriac. Garnish with parsley and serve with rocket leaves and lemon wedges.','10 mins','45 mins'),
(560,'prawn & salmon burgers with spicy mayo','https://www.bbcgoodfood.com/recipes/prawn-salmon-burgers-spicy-mayo','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/prawn-salmon-burgers-with-spicy-mayo-2318455.jpg','These prawn and salmon burgers are not only fabulously tasty, they''re healthy too, being rich in omega-3. Make them for the family in just 25 minutes',41,4,1,1,8,'Briefly blitz half the prawns, half the salmon, the spring onions, lemon zest and half the coriander in a food processor until it forms a coarse paste. Tip into a bowl, stir in the rest of the prawns and salmon, season well and shape into four burgers. Chill for 10 mins. Mix the mayo and chilli sauce together in a small bowl, season and add some lemon juice to taste. Mix the lettuce with the cucumber, dress with a little of the remaining lemon juice and 1 tsp olive oil, then set aside. Heat the remaining oil in a large frying pan and fry the burgers for 3-4 mins each side or until they have a nice crust and the fish is cooked through. Serve with the salad on the side or in toasted burger buns, if you like, with a good dollop of the spicy mayo.','15 mins','10 mins'),
(561,'roast side of salmon with chermoula','https://www.bbcgoodfood.com/recipes/roast-side-salmon-chermoula','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roast-side-of-salmon-with-chermoula-d5ea340.jpg','Elevate roast salmon with a herby chermoula dressing for a dish that''s rich in omega 3 and perfect for sharing with friends. Pair with a light salad',16,6,1,1,8,'Heat oven to 200C/180C fan/gas 6. Line the bottom of a roasting tin that’s large enough to fit the salmon in with baking parchment. Brush ½ tbsp oil over the skin and season all over. Place skin-side down in the tin and roast for 15-20 mins until the salmon is just cooked through. Meanwhile, toast the coriander and cumin seeds in a dry frying pan. Tip into a blender and add all the remaining ingredients. Blitz to a smooth green sauce, adding enough water to get a drizzling consistency. Season to taste. The chermoula can be made in the morning and kept chilled in the fridge. Put the salmon on a serving platter and drizzle over a little chermoula, with the rest on the side to serve.','15 mins','15 mins - 20 mins'),
(562,'whole baked fish with watercress & chilli salsa','https://www.bbcgoodfood.com/recipes/whole-baked-fish-with-watercress-chilli-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Baked-seabass-ec17e28.jpg','Celebrate spring with a fabulous whole baked fish on a bed of new potatoes and cherry tomatoes. It comes alive with a punchy watercress and chilli salsa',89,6,1,1,8,'Heat the oven to 220C/200C fan/gas 7. Simmer the potatoes in a large pan of boiling salted water for 5 mins until just tender. Drain and set aside. Slash the flesh of the fish three times in a criss-cross pattern on both sides. Rub with the olive oil, then season with sea salt and black pepper. Pop the bashed garlic cloves and lemon slices into the cavity of the fish. Lay the potatoes and all the tomatoes in the bottom of a baking dish and season. Lay the fish on top and cook for 20-25 mins, or until the thickest part of the fish is cooked through (the flesh should come away from the bone easily and look opaque). Remove the fish, cover to keep warm and cook the potatoes and tomatoes for 10 mins more. To make the salsa, tip the watercress, herbs, chilli and capers into a food processor and pulse to finely chop, drizzling in a bit of the extra virgin olive oil as you do. Mix in the remaining oil, then the garlic and lemon juice, and season to taste. Put the veg on a serving platter, sit the fish on top and drizzle with the salsa to serve.','15 mins','30 mins'),
(563,'fish stew with roast garlic & saffron','https://www.bbcgoodfood.com/recipes/fish-stew-roast-garlic-saffron','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish-stew-roast-garlic-saffron-280d53f.jpg','Try this simpler, lighter version of bouillabaisse, a classic fish soup - serve with toasted slices of baguette to mop up the tomato based sauce',52,6,2,1,8,'Heat oven to 160C/140C fan/gas 3. To make the saffron and garlic paste, cut the whole heads of garlic in half horizontally. Place the garlic heads, cut-side up, on a small baking tray. Drizzle over a little olive oil and season with salt. Cover with foil and bake for 40-50 mins until softened. Remove the foil and return to the oven for a further 15-20 mins until the garlic has turned golden brown. Meanwhile, make the soup base. Chop the fennel, celery and onion into small dice and thinly slice the red chilli. Heat the oil in a large saucepan and add the chopped vegetables. Grate the garlic into the pan and add the fennel seeds. Stir well, then cover and sweat for 10 mins. Add the saffron and bay leaves, and cook for a further 10 mins until the vegetables start to soften. Pour in the Pernod and wine, bring to the boil, then add the passata and 400ml water. Bring to the boil, then turn the heat down to a simmer. Cover and cook for 45 mins until all of the flavours have started to work together, then remove the pan from the heat and leave to cool a little. Transfer the soup to a jug blender and blend until smooth, then pass through a fine sieve into a clean saucepan – you may need to do this in batches. If the soup needs thickening, reduce over a medium heat. When it is the consistency of pouring cream, remove from the heat and set aside, or leave to cool and chill in the fridge until needed. The base of the soup can be made 3 days ahead. Meanwhile, for the paste, pour the Pernod into a small saucepan and add the saffron. Bring to the boil, add the olive oil and warm gently. Take off the heat and leave to cool. When the garlic is golden brown, remove the tray from the oven and squeeze the cloves out into the bowl of a pestle and mortar. Add a large pinch of flaky sea salt and pound to a paste. Slowly add the saffron-infused olive oil, stirring constantly. Transfer to a clean container and chill until needed. Bring the soup base to the boil in a large pan, then turn down to a light simmer and taste for seasoning. Add the chunks of prepared fish and squid, and gently poach the fish in the soup for 10 mins, or until it is just cooked. Remove from the heat and stir in the lemon juice and parsley. Ladle the soup into bowls, piling up the fish in the centre. Spoon on the saffron and garlic paste and sprinkle over some more parsley. Serve with toasted slices of baguette and the extra saffron and garlic paste on the side.','1 hr','1 hr and 45 mins'),
(564,'fresh salmon with thai noodle salad','https://www.bbcgoodfood.com/recipes/fresh-salmon-thai-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fresh-salmon-with-thai-noodle-salad-6977df3.jpg','Ideal for a quick midweek meal, whip up this nutritious salmon and noodle salad in just 20 minutes. The balance of protein and carbs make it super satisfying',4,2,1,1,8,'Put a pan of water on to boil. Line a steamer with baking parchment, add the salmon fillets and scatter with a little of the orange zest. When the water is boiling, add the beans to the pan, put the salmon in the steamer on top and cook for 5 mins. Take the salmon off, and if it is cooked, set aside but add the peas and mange tout to the pan and cook for 1 min more, or if not quite cooked leave on top for the extra min. Drain the veg, but return the boiling water to the pan, add the noodles and leave to soak for 5 mins. Put the curry paste and fish sauce in a salad bowl with the orange juice and a little of the remaining zest and the spring onions. Drain the noodles when they are ready and add to the salad bowl, toss well, then add the chopped orange with the basil or coriander and the cooked vegetables. Tip in the juice from the fish, then toss well and serve in bowls with the salmon on top.','15 mins','5 mins'),
(565,'bouillabaisse','https://www.bbcgoodfood.com/recipes/bouillabaisse','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bouillebaisse-f4b8079.jpg','Make this classic French fish soup at a dinner party for friends and family. It''s a challenge, but will make an impressive starter or main course',7,6,3,1,8,'To make the croutons heat oven to 200C/180C fan/gas 6. Lay the slices of bread on a flat baking tray in a single layer, drizzle with olive oil and bake for 15 mins until golden and crisp. Set aside – can be made a day ahead and kept in an airtight container. Use a layer of the green part of the leek to wrap around and make a herb bundle with the thyme, bay, parsley stalks, orange peel and chilli. Tie everything together with kitchen string and set aside. Heat the oil in a very large casserole dish or stock pot and throw in the onion, sliced leek and fennel and cook for about 10 mins until softened. Stir through the garlic and cook for 2 mins more, then add the herb bundle, tomato purée, star anise, Pernod if using, chopped tomatoes and saffron. Simmer and stir for a minute or two then pour over the fish stock. Season with salt and pepper, bring to a simmer, then add the piece of potato. Bubble everything gently for 30 mins until you have a thin tomatoey soup. When that piece of potato is on the brink of collapse, fish it out and set aside to make the rouille. While the broth is simmering make the rouille by crushing the garlic, chilli and saffron with a pinch of salt in a mortar with a pestle. Mash in the cooked potato to make a sticky paste then whisk in the egg yolk and, very gradually, the olive oil until you make a mayonnaise-like sauce. Stir in the lemon juice and set aside. Once the chunky tomato broth has cooked you have two options: for a rustic bouillabaisse, simply poach your fish in it along with the mussels, if you''re using (just until they open) and serve. For a refined version, remove the herb bundle and star anise. Using a handheld or table-top blender, blitz the soup until smooth. Pass the soup through a sieve into a large, clean pan and bring to a gentle simmer. Starting with the densest fish, add the chunks to the broth and cook for 1 min before adding the next type. With the fish we used, the order was: monkfish, John Dory, grey mullet, snapper. When all the fish is in, scatter over the mussels, if using, and simmer everything for about 5 mins until just cooked and the mussels have opened. Use a slotted spoon to carefully scoop the fish and mussels out onto a warmed serving platter, moisten with just a little broth and scatter over the chopped parsley. Bring everything to the table. Some people eat it as two courses, serving the broth with croutons and rouille first, then the fish spooned into the same bowl. Others simply serve it as a fish stew. Whichever way you choose the rouille is there to be stirred into the broth to thicken and give it a kick.','1 hr','1 hr'),
(566,'seafood tagine','https://www.bbcgoodfood.com/recipes/seafood-tagine','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/seafood-tagine-9d76bb6.jpg','Use a frozen mixture of fish and shellfish to make this stew, served over a zesty almond couscous',24,4,1,1,8,'Heat the oil in a large pan and cook the onion and red pepper for about 5 mins until softened. Stir in the garlic, harissa and chopped tomatoes, and cook for about 10 mins until thickened. Add the frozen seafood and cook through, about 10 mins more. Put the couscous in a serving bowl, pour over boiling water to cover, then cover with cling film. Leave to soak while the tagine cooks, then stir in most of the coriander, the almonds, lemon zest and juice, and seasoning. Serve the tagine with the couscous and cut the remaining lemon half into wedges to squeeze over. Sprinkle over the remaining coriander and serve.','5 mins','15 mins'),
(567,'thai prawn & ginger noodles','https://www.bbcgoodfood.com/recipes/thai-prawn-ginger-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/thai-prawn-ginger-noodles-6ffc110.jpg','This seafood stir-fry, with fiery ginger and crunchy veg, is as healthy as it is delicious - a low-fat and low-calorie weeknight dinner',4,2,1,1,8,'Put the noodles in a bowl and pour over boiling water to cover them. Set aside to soak for 10 mins. Stir together the orange juice and zest, curry paste, fish sauce, sugar and 3 tbsp water to make a sauce. Heat the oil in a large wok and add half the ginger and the garlic. Cook, stirring, for 1 min. Add the pepper and stir-fry for 3 mins more. Toss in the sugar snaps, cook briefly, then pour in the curry sauce. Add the beansprouts and prawns, and continue cooking until the prawns just turn pink. Drain the noodles, then toss these into the pan with the herbs and remaining ginger. Mix until the noodles are well coated in the sauce, then serve.','15 mins','15 mins'),
(568,'cod puttanesca with spinach & spaghetti','https://www.bbcgoodfood.com/recipes/cod-puttanesca-spinach-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-cod-puttanesca-with-spinach-spaghetti-69452e6.jpg','Tuck into a healthy seafood pasta dish in under half an hour. We''ve combined spaghetti with low-fat, high-protein white fish and an easy puttanesca sauce',4,2,1,1,8,'Boil the spaghetti for 10 mins until al dente, adding the spinach for the last 2 mins. Meanwhile, fry the onion in the oil in a large non-stick frying pan with a lid until tender and turning golden. Stir in the chilli and garlic, then add the tomatoes. Add the vinegar, capers, olives and paprika with a ladleful of the pasta water. Put the cod fillets on top, then cover the pan and cook for 5-7 mins until the fish just flakes. Drain the pasta and wilted spinach and pile on to plates, then top with the fish and sauce. Sprinkle over some parsley to serve.','10 mins','17 mins'),
(569,'whole stuffed roast fish with fennel','https://www.bbcgoodfood.com/recipes/whole-stuffed-roast-fish-fennel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/whole-bake-fish-6f64456.jpg','This roast fish dish was inspired by Sicily, where the aromatic spices and dried fruit of Arabian cuisine meet with the fresh fish and veg of the Med',5,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Put the fennel and onion in a large roasting tin and toss with 2 tbsp oil, 1 tbsp lemon juice and plenty of seasoning. Roast for 20 mins until almost tender and starting to caramelise. Meanwhile, put the couscous in a medium bowl. Mix the saffron and hot stock, pour over the couscous and cover with cling film. Rinse the fish with cold water inside and out, then pat dry. Slash deeply on both sides and season with salt, pepper and a pinch of the lemon zest. After 10 mins soaking, fluff up the couscous with a fork and add 2 tbsp lemon juice, the remaining zest, the dill, 1 tbsp pine nuts and seasoning to taste. Stuff the couscous into the cavity of each fish, reserving any leftover couscous for later. Stir the tomatoes and currants into the tin. Sit the fish on top and roast for about 30 mins, depending on its size. Look for juices running from the slashes in the flesh. Tug gently on the back fin – if it comes away easily, the fish is cooked. 4 Scatter with the rest of the pine nuts, then serve from the tin, with lemon wedges for squeezing over. Serve one stuffed fish per person, or if you have cooked large fish, gently lift the fillets from the top of the fish first, then turn over and repeat. Share out the couscous stuffing and leftover couscous.','15 mins','50 mins'),
(570,'budget kedgeree','https://www.bbcgoodfood.com/recipes/budget-kedgeree','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Kedgeree-a8d6beb.jpg','Try canned sardines for an inexpensive yet nourishing source of omega-3. Here we use them to make a budget kedgeree, which is healthy and full of flavour',4,4,1,1,8,'Boil the eggs for 8 mins, then leave them to cool enough so you can comfortably peel them. Meanwhile heat the reserved oil from the canned sardines in a large pan and fry the onions, ginger and chilli for 10 mins, stirring frequently until softened and golden. Tip in the rice, curry powder and cumin seeds, then pour in the stock. Stir well, then drop in the frozen spinach blocks, spaced apart, on top. Don’t stir at this stage, just cover and leave to cook over a low heat for 15 mins. Add the sardines, then cover and cook for 5 mins more until the rice is tender. Gently toss everything together with the coriander. Cut the eggs into wedges, then serve on top along with the lemon wedges for squeezing over. Serve half, then leave the remainder to cool before chilling. Will keep chilled for up to three days. Reheat in a pan or in the microwave until piping hot (without the lemons which are best served cold).This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','20 mins','40 mins'),
(571,'teriyaki salmon parcels','https://www.bbcgoodfood.com/recipes/teriyaki-salmon-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon_9-8ec47b9.jpg','These easy steamed fish parcels with Japanese seasoning are an ideal way to get kids in the kitchen',7,4,1,1,8,'KIDS: The writing in bold is for you. ADULTS: The rest is for you. Make the sauce and marinade. In a small bowl, whisk together the soy, honey, garlic and mirin and set aside. Cut out some squares of foil. Using scissors, cut out 4 squares of foil, each about 30cm square. Brush each piece of foil with a little oil and bring the edges of the foil up a little. Fill your parcels. Place a couple of broccoli stems on each one, then sit a salmon fillet on top and scatter over the ginger. Spoon over the sauce. Spoon the sauce over each salmon fillet and drizzle with a little sesame oil, if you like. Close the parcels. Fold over the edges of the foil together to seal and place the parcels on a baking sheet. Can be prepared up to 1 day ahead. Cook the parcels. Heat oven to 200C/180C fan/gas 6. Get your child to place the parcels in the oven for 15-20 mins, but ensure an adult removes them, then leave to stand for a few mins. Serve each parcel on a plate and let each person open it themselves. Serve with spring onions and sesame seeds for scattering over, and some rice on the side.','15 mins','20 mins'),
(572,'fish parcels with romesco sauce & veg','https://www.bbcgoodfood.com/recipes/fish-parcels-romesco-sauce-veg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fish_1-8ae46b5.jpg','Romesco sauce is made from peppers, almonds, chilli and garlic. Team with white fish and a cauliflower and spring onion mix',5,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Put each fish fillet on a large piece of baking parchment and top each with a few slices of lemon and a sprig of thyme. Drizzle with 1 tsp of the oil and season. Fold over the top edges of each piece of parchment to make a seal, then scrunch up the ends like a sweet wrapper. Put the parcels on a baking sheet and bake in oven for 15 mins. Meanwhile, steam the cauliflower for 4 mins, then add the spring onions for 3 mins more or until tender. Pat dry, then toss the veg in 2 tsp olive oil and season. In a large frying pan, toast the bread pieces in the remaining oil, then add the chilli and garlic, and cook for 1 min. Tip into a food processor with the nuts, peppers, tomato and vinegar, and pulse until you have a rough paste. Season and put to one side. Wipe then heat the frying pan. Add the cauliflower wedges and spring onions, and cook for a few mins each side to brown. Serve alongside the fish and sauce.','15 mins','15 mins'),
(573,'baked fish with tomatoes, basil & crispy crumbs','https://www.bbcgoodfood.com/recipes/baked-fish-tomatoes-basil-crispy-crumbs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/baked-fish-8185523.jpg','A low-fat traybake supper that''s full of flavour. Combine lean white fish with cherry tomatoes, a pesto crumb, green beans and broccoli',28,4,1,1,8,'Heat oven to 200C/180C fan/gas 6. Tip the tomatoes into a roasting tin, and stir in the vinegar and 1 tbsp of the pesto. Season, mix, then bake in the oven for 10 mins. Mix together the remaining pesto and the breadcrumbs, then press onto each fish fillet. Add to the roasting tin and return to the oven for 12-15 mins until the fish flakes easily and the topping is slightly crisp. Meanwhile, boil the green beans and broccoli, then drain. Serve the bake scattered with the vegetables.','5 mins','25 mins'),
(574,'salmon & purple sprouting broccoli grain bowl','https://www.bbcgoodfood.com/recipes/salmon-and-purple-sprouting-broccoli-grain-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-and-purple-sprouting-broccoli-grain-bowl-df82338.jpg','Fill up at lunchtime with this healthy salmon and broccoli mixed grain bowl. Full of omega-3, fibre and vitamin C, it will nourish body and mind',16,2,1,1,8,'Mix 2 tbsp oil with the honey, mustard, lemon juice and some seasoning. Bring a pan of water to the boil. Add the broccoli, cook for 3-4 mins until tender but with a slight bite, then drain. Heat the remaining oil in a frying pan. Add the garlic, sizzle for a min, then tip in the mixed grain pouch, using the back of your spoon to separate the grains. Add the broccoli, mustard dressing, herbs and radishes. Give everything a mix to combine, season to taste, then gently stir through the salmon. Serve warm or cold.','10 mins','10 mins'),
(575,'sticky seeded salmon with satsuma salad','https://www.bbcgoodfood.com/recipes/sticky-seeded-salmon-satsuma-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon_3-de131ca.jpg','A multi-textured, multi-coloured fish dish with nutritious seeds and a vibrant citrus watercress salad',28,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Brush each salmon fillet with half the agave syrup or honey, sprinkle on some black pepper, then press on the seeds. Transfer to a baking tray and bake for about 15 mins, depending on the thickness of the fish, until the fish is cooked through and the seeds crunchy. Meanwhile, cut the peel from the satsumas and cut into segments, reserving all the juice that comes out for the dressing. Whisk the juice with the olive oil, vinegar and some seasoning in a small bowl. Toss together the watercress, beetroot and satsuma segments with the dressing and divide between plates. Add a piece of salmon to each one.','10 mins','15 mins'),
(576,'piri-piri fish & chips with spicy peas','https://www.bbcgoodfood.com/recipes/piri-piri-fish-chips-spicy-peas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/periperi-cc28b02.jpg','Give a traditional staple a spicy kick with some chilli marinade and minty peas. Use any white fish- we used sea bass',24,2,1,1,8,'Heat oven to 200C/180C fan/gas 6. Toss the chips with half the olive oil and half the lemon juice, arrange in a layer on a baking tray, then season. Cook for 25 mins. Stir the remaining lemon juice into the marinade. Meanwhile, bring a pan of water to the boil and cook the peas for 2-3 mins, then drain. Return the peas to the empty pan and crush with the chilli, garlic, mint and the remaining oil. Season and keep warm. After 25 mins, turn the potatoes and move to the edges of the tray. Add the fish, skin-side up, and spoon over the marinade. Cook for another 10-12 mins until the fish is cooked through. Serve with the smashed peas.','10 mins','35 mins'),
(577,'quick and easy pasta salad','https://www.bbcgoodfood.com/recipes/deli-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338583_11-1796dd4.jpg','Make this simple storecupboard pasta salad with sunny Mediterranean-style flavours like sundried tomatoes and basil. Pack it up for lunch the next day.',5,4,1,1,9,'COOK THE PASTA: Boil the pasta in salted water for 8 minutes then add the peas, return the water to the boil and cook for 2 minutes more until the pasta and peas are tender. Tip into a colander over the sink, cool the pasta and peas under the cold tap then drain really well. MAKE THE DRESSING: While the pasta is boiling roughly chop the tomato and put in a food processor with half the sun-dried tomatoes, the olive oil, vinegar, garlic and about 8 basil leaves. Season with plenty of salt and freshly ground pepper then whizz until smooth. Tip into a large salad bowl. TOSS AND SERVE: Add the pasta and peas to the dressing, roughly slice the rest of the sun-dried tomatoes and add to the pasta with the remaining basil leaves. Tear in the prosciutto or salami and toss everything together. Pile into bowls and, if possible, eat outside.','30 mins','No Time'),
(578,'kids'' pasta salad','https://www.bbcgoodfood.com/recipes/easy-greek-style-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Greek-pasta-salad-abbef79.jpg','Make our Greek pasta salad with the kids. The recipe is specially designed for younger cooks, and we''ve noted all the equipment they''ll need to make it',6,4,1,1,9,'Fill a medium saucepan with hot water from the tap until it’s three-quarters full. Set the pan on the hob. Season the water with a pinch of salt, then turn the heat to medium-high – the water should start to boil after a minute or so. Slowly tip the pasta into the boiling water – do this carefully, as any splashes could burn your skin. Holding the handle of the saucepan with one hand (use a cloth if it’s hot), carefully stir the pasta with a long wooden spoon in the other hand – this will prevent the pasta from sticking together. Set a timer for 10 mins. Stir the pasta every minute or so, and keep checking the heat to make sure the water is gently boiling while the timer counts down. While the pasta cooks, measure 3 tbsp extra virgin olive oil, 1 tbsp red wine vinegar and 1 tsp dried oregano into a mixing bowl. Season with some salt and black pepper, and mix well – this will make a zingy dressing for the pasta. When the timer beeps, it’s time to check if the pasta is cooked. To do this, scoop one piece of pasta out of the boiling water using the wooden spoon, tip it onto a plate and leave it to cool slightly. Taste the pasta: if it’s soft, it’s cooked through, but if it’s still a little hard in the middle, it needs to cook for a bit longer. Set the timer for another 2 mins, then check again. Place a large colander or sieve in the sink. When the pasta is cooked, turn off the heat and, using two hands, carefully carry the saucepan over to the sink and tip the pasta into the colander to drain away the hot water. Leave to cool for a few minutes in the colander, then tip the pasta into the mixing bowl with the dressing. Mix together until the pasta is coated in the dressing. Put a chopping board on a work surface. Cut the cherry tomatoes in half by pinching each one between your thumb and index finger, and carefully slicing through the middle of the tomato with a small serrated knife. Slice the pitted olives in the same way, then tip all the tomatoes and olives into the pasta. Put the cucumber on the chopping board and cut it in half lengthways. Lay the two halves on the board cut-side down, so they don’t roll around. Cut each piece in half again, so you have four long, chunky pieces. Cut across the cucumber pieces in the other direction so you end up with small cucumber triangles. Tip these into the bowl with the pasta, tomatoes and olives. Put the onion on the board and hold it securely. Cut off the pointy end, then cut the onion in half through the root end. Peel away the papery outer layers until you’re left with just the shiny onion underneath. Lay each onion half on the board, cut-side down so it doesn’t roll around. Holding onto the root end, carefully slice the halves into half-moon shapes, as thinly as you can. Stop when you get to the root. Tip the onions into the bowl. Crumble the feta cheese into small pieces over the bowl, then gently mix everything together until well combined. Eat straightaway, or chill for up to a day before eating.','20 mins','10 mins'),
(580,'build your own pesto pasta salad','https://www.bbcgoodfood.com/recipes/build-your-own-pesto-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/uild-your-own-pesto-pasta-salad-5d9dc92.jpg','Rustle up this easy pesto pasta salad for the family. Customise it by adding your kid''s favourite veg',3,6,1,1,9,'Cook the pasta following pack instructions, then drain and toss in 1 tbsp of the oil. Transfer to a large bowl and set aside to cool, tossing occasionally so it doesn’t stick. Toast the pine nuts in a dry frying pan over a low heat, constantly moving them for 4-5 mins until browned. When the pasta has completely cooled, toss through the pesto, toasted pine nuts, remaining olive oil, basil, lemon zest and juice, and the tomatoes, sweetcorn, peas and rocket, if using. Season, then scatter over the parmesan and serve.','10 mins','15 mins'),
(581,'tortellini with pesto & broccoli','https://www.bbcgoodfood.com/recipes/tortellini-pesto-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1046467_11-388017f.jpg','A speedy pasta salad with Mediterranean flavours - perfect for packed lunches',10,2,1,1,9,'Bring a large pan of water to the boil. Add the broccoli, cook for 2 mins, then add the tortellini and cook for 2 mins, or according to pack instructions. Drain everything, gently rinse under cold water until cool, then tip into a bowl. Toss with the pesto, pine nuts and balsamic vinegar. Add the tomatoes, pack into containers and chill. Let the salad get to room temperature during the morning to get the most flavour from the tomatoes and pesto.','5 mins','5 mins'),
(582,'greek pasta salad','https://www.bbcgoodfood.com/recipes/greek-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2681_11-461b11d.jpg','For a fresh idea with pasta, try Greek salad',5,4,1,1,9,'Tip the fusilli into a large pan of boiling salted water and boil for 10 minutes. Throw in the spinach, stir well and boil for another 2 minutes. Drain into a colander or sieve and leave to drip-dry. Tip the tomatoes, olives and feta into a big bowl, grind lots of black pepper over and then drizzle with the olive oil. Toss in the drained pasta and spinach, and let everyone help themselves.','35 mins','N/A'),
(583,'pasta salad with pesto & prawns','https://www.bbcgoodfood.com/recipes/pasta-salad-pesto-prawns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-996479_10-15bdc5b.jpg','This is a wonderful, zingy dish, perfect for a lunch out in the garden - use our recipe for homemade pesto to make it extra special',5,4,1,1,9,'Plunge the pasta into a large pan of salted boiling water and cook according to packet instructions until al dente. Drain the pasta into a colander and hold it under cold running water to cool it down quickly. Shake off the excess water and tip the pasta into a large bowl. Toss in 1 tbsp olive oil to coat thoroughly (this helps stop the pasta from sticking together), then add the prawns. Mix the pesto, mayonnaise and lemon or lime juice with some salt and pepper to taste. If you need to slacken the mixture a bit, stir in a little more olive oil. Pour the pesto dressing over the pasta and prawns and give everything a good stir. Add the parsley and basil leaves and stir again, then pile into your serving dish and serve scattered with extra shredded basil leaves, plus lemon wedges for squeezing over the salad.','40 mins','No Time'),
(584,'beetroot & feta pasta','https://www.bbcgoodfood.com/recipes/beetroot-feta-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Beetroot-and-feta-pasta-1addeb7.jpg','Bring colour to your dinner table with our creamy beetroot and feta pasta. Packed with nutrients, it''s a satisfying veggie lunch or supper for the family',46,4,1,1,9,'Put a large pan of salted water on a high heat, bring to the boil and add the beetroot chunks. Boil for 5 mins, then transfer the beetroot to a plate using a slotted spoon. Set the pan of cooking water aside. Meanwhile, put the olive oil in a large frying pan set over a medium heat, then add the onions, celery and carrots, and cook for 8 mins until softened. Finely chop the basil stalks and celery tops, then add to the pan along with the garlic, and fry for another 30 seconds. Tip the pasta into the pan of beetroot water and simmer for 13-15 mins (depending on what pasta you’re using) until just cooked but retaining a little bite. Drain, reserving 200ml of the pasta water and tip the pasta back into the pan. Meanwhile, put the beetroot with half of the fried veg into the bowl of a food processor with most of the basil leaves and the crème fraîche, then blitz until smooth. Gradually add the reserved pasta water to loosen until you have a thick mixture. Stir this sauce through the pasta along with the remaining fried veg and some seasoning. Divide between bowls, crumble over the feta, then scatter with the remaining basil leaves. Grind over some black pepper, if you like.','20 mins','25 mins'),
(585,'pesto pasta salad','https://www.bbcgoodfood.com/recipes/pasta-pesto-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pasta-pesto-salad-237d912.jpg','Enjoy this veggie pesto pasta salad with cucumber, peas, cherry tomatoes and basil for an easy family meal. It''s great for a packed lunch or summery picnic',19,6,1,1,9,'Cook the pasta for 10 mins in salted boiling water until al dente, drain, then tip into a bowl. Stir in the crème fraîche followed by the pesto, then leave to cool. When the pasta is cool, stir in the cucumber, tomatoes and peas followed by the basil leaves. Season if it needs it, and tip into a container to transport it.','10 mins','12 mins'),
(587,'prawn & avocado pasta salad','https://www.bbcgoodfood.com/recipes/prawn-avocado-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2389_12-59b51a2.jpg','A tasty and superhealthy summer salad ',5,4,1,1,9,'Cook the pasta in boiling water for 10-11 mins. Just before the end, add prawns and cook through. Drain, cool under cold water, drain and transfer to a big bowl. Put the oil, lemon juice and zest, garlic, avocado, tomatoes and spring onions in a separate bowl, then mix well – don’t mash the avocado. Season. Gently toss the mix into the pasta, with the rocket and basil.','10 mins','12 mins'),
(588,'creamy pesto & chicken pasta salad','https://www.bbcgoodfood.com/recipes/creamy-pesto-chicken-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-872594_11-6e4db8c.jpg','If you need a quick boost of carbohydrates, try this filling and nutritionally-balanced pasta salad',5,1,1,1,9,'Cook the pasta following pack instructions. Meanwhile, mix the red pepper with the cherry tomatoes and chicken. Mix the pesto and crème fraîche. Drain the pasta, then stir it through. Toss through the veg and chicken and eat immediately, or pack into a container for lunch.','15 mins','15 mins'),
(589,'chicken pasta salad','https://www.bbcgoodfood.com/recipes/chicken-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-693601_11-e3734a8.jpg','For a speedy, simple supper this chicken pasta salad dish is just the thing - save leftovers for lunch the next day',5,4,1,1,9,'Heat oven to 220C/200C fan/gas 7. Mix the pepper and onion with 1 tsp oil and roast for 20 mins. Cook the pasta following pack instructions. Drain and set aside. Meanwhile put the chicken breasts between two sheets of cling film and bash with a rolling pin until they’re about 1cm thick. Mix remaining oil, herbs, chilli and garlic then rub all over the chicken. Heat a griddle or barbecue and cook for 3-4 mins on each side. Slice the chicken on a board, scrape into the pasta with any juices, plus the roasted onion and pepper, cherry tomatoes, rocket, vinegar and seasoning. Toss together and eat warm or cold.','10 mins','20 mins'),
(590,'prawn, dill & cucumber pasta','https://www.bbcgoodfood.com/recipes/prawn-dill-cucumber-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-684539_11-bf2a0f9.jpg','Supper can be ready in 25 minutes with just six fresh ingredients and a bag of pasta',5,4,1,1,9,'Cook pasta shapes following pack instructions, drain and cool under running water. Tip into a bowl and toss with the dill, lemon, soured cream, prawns, cucumber and fennel. Season','10 mins','12 mins'),
(591,'favourite pasta salad','https://www.bbcgoodfood.com/recipes/favourite-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-374505_11-6d24795.jpg','A low-fat, zesty salad, perfect for a summer lunchbox - vary with your favourite ingredients',17,4,1,1,9,'Cook the pasta, adding the peas for final 2 mins cooking time. Drain, rinse with cold water to cool, then drain again. Tip into a bowl with the chopped herbs, lemon zest and juice, olive oil and plenty of seasoning and mix well. Cover and chill, spooning out portions as and when. If you''re making this for one person, by days 3 and 4 you''ll need to stir in a little olive oil or something creamy to loosen the pasta.','10 mins','12 mins'),
(592,'vegan pasta salad','https://www.bbcgoodfood.com/recipes/superfood-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488626_11-feedcaa.jpg','This vegan pasta salad is packed with flavour thanks to the zesty soy and ginger dressing. Serve for a lighter summer lunch or as part of a picnic spread',33,4,1,1,9,'Boil the pasta, adding the soya beans and green beans 3 mins before the end of cooking. Drain, tip into a colander, then cool quickly under running water. Whisk together the oil, soy sauce, ginger and lime juice in a large bowl, then tip in the pasta, cooked beans, sprouts, carrots and coriander. Toss together, then serve.','10 mins','10 mins'),
(593,'orzo & mozzarella salad','https://www.bbcgoodfood.com/recipes/orzo-mozzarella-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-320575_11-4a87072.jpg','This Mediterranean-inspired pasta salad makes a fresh and vibrant addition to any picnic, barbecue or buffet',5,4,1,1,9,'Boil the orzo for 8 mins until tender, then drain and cool under cold water. Drain again, then tip into a large bowl. Meanwhile, tear the basil, stalks and all, into a food processor. Add the oil, Parmesan, garlic and half the pine nuts, then whizz to a thick pesto-like dressing. Stir the dressing through the orzo, then season. The dressing will seem quite thick, but keep stirring and it will eventually coat all the grains. Add the bocconcini, tomatoes and a handful of the rocket. Scatter with the remaining pine nuts and a little more Parmesan, if you like. Top with more rocket, then serve straight away.','10 mins','8 mins'),
(595,'bacon & avocado warm pasta salad','https://www.bbcgoodfood.com/recipes/bacon-avocado-warm-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bacon-avocado-warm-pasta-salad-4b0e74a.jpg','Stir crispy fried bacon and pesto into orzo pasta for a quick dinner or easy packed lunch',24,4,1,1,9,'Bring a large pan of water to the boil. Cook the orzo following pack instructions, drain and set aside. Meanwhile, heat the oil in a frying pan and fry the bacon for 4-5 mins until crispy. Stir in the tomatoes and cook for a couple mins more. Stir in the orzo, pesto and avocado, and heat for 2 mins more. Spoon onto a serving plate, then scatter with rocket leaves and Grana Padano.','10 mins','10 mins'),
(596,'warm sausage & broccoli pasta salad','https://www.bbcgoodfood.com/recipes/warm-sausage-broccoli-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-pasta_0-e514bde.jpg','A simple midweek supper of short pasta flavoured with chilli and garlic, served with pork chipolatas',42,4,1,1,9,'Heat the grill, and cook the sausages for 10-12 mins or until cooked through and golden brown. Meanwhile, heat the oil in a small frying pan, cook the onion for 6-8 mins until soft, then add the garlic and cook for 1 min. Set aside. Bring a pan of water to the boil and cook the broccoli for 3-4 mins until tender but still with a bite. Lift out using a slotted spoon and set aside. Add the pasta to the water you have just cooked the broccoli in and cook following pack instructions. Drain well and return to the pan. Chop the sausages into 2cm pieces and toss with the pasta, then chop the broccoli roughly and add to the pasta along with the cooked onions, garlic, chilli flakes, and lemon zest and juice. Toss everything together well with the rocket and season.','10 mins','20 mins - 25 mins'),
(597,'pasta salad with tuna, capers & balsamic dressing','https://www.bbcgoodfood.com/recipes/pasta-salad-tuna-capers-balsamic-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1071480_11-69e9831.jpg','Update tuna pasta salad by skipping the mayo and adding balsamic vinegar, olive oil, colourful tomatoes and celery',10,4,1,1,9,'Cook the pasta following pack instructions, then drain and rinse in cold water. After draining again, transfer to a large bowl. Add the remaining ingredients except the basil, season well, and toss to combine. Scatter with basil and serve.','10 mins','10 mins'),
(598,'stuffed pasta bake bolognese','https://www.bbcgoodfood.com/recipes/stuffed-pasta-bake-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/stuffed-pasta-bake-bolognese-6995b2b.jpg','Combine two favourites – bolognese and stuffed pasta shells – to make the ultimate pasta bake. Serve bubbling from the oven for true comfort food',5,6,1,1,9,'To make the bolognese, heat the oil in a wide ovenproof pan over a medium heat. Fry the onion, celery and carrot with some seasoning for 20 mins until soft and lightly golden. Tip in the beef mince, turn up the heat and fry, stirring regularly, for another 10 mins until the mince browns. Add the garlic, fry for a minute, then pour in the wine and bubble for a few mins more, scraping the pan to release any bits. Stir through the purée, bay leaves and tomatoes, with a canful of water. Simmer for 1½ hrs, covered for the first hour, then remove the lid and bubble until the tomatoes have broken down. Season with salt, pepper, the sugar and balsamic vinegar. Tip the pasta shells into a large heatproof bowl with a pinch of salt, and pour over a kettle of boiled water. Cover and leave to soften for 15-20 mins – they need to retain some firmness so you can fill them.  Heat the oven to 200C/180C fan/gas 4 and drain the pasta. Use a fork to mash the mascarpone with the mozzarella, chopped basil, garlic and some black pepper in a bowl. The bolognese should be a little wetter than you’d like as the pasta shells will absorb some of the liquid as it bakes. Remove from the heat, giving it a final stir. Using a teaspoon, fill the pasta shells with some of the mascarpone mixture, pressing the mix in with your index finger. Nestle the shells into the sauce, filled-side up. Bake for 20-30 mins until the top is golden and bubbling, and the pasta is tender. Grate over some parmesan and finish with a few basil leaves.','30 mins','2 hrs'),
(599,'rigatoni sausage bake','https://www.bbcgoodfood.com/recipes/rigatoni-sausage-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-418_11-dfd66c8.jpg','A great way to get kids to eat spinach, this comforting sausage supper dish is also perfect for a hungry crowd',5,6,2,1,9,'Slit the sausages and remove them from their skins, then chop them into small pieces. Heat the oil in a pan, add the onion and fry for 5 minutes, until softened and lightly browned. Stir in the sausages and fry until lightly coloured. Add the carrot, then stir in the wine, stock, tomato purée, and season. Bring to the boil, then simmer uncovered for about 15 minutes until thickened. Taste and season. Set aside. Put the butter, flour and milk in a pan. Gently heat, whisking, until thickened and smooth. Add a sprinkle of freshly grated nutmeg, season, then simmer for 2 minutes. Preheat the oven to 190C/Gas 5/fan 170C. Bring a large pan of salted water to the boil. Add the pasta, stir well, then cook, uncovered, for 10-12 minutes, until tender. Remove from the heat, stir in the spinach and, when just wilted, drain well. Tip half the pasta into a shallow ovenproof dish, about 2.2 litre/4 pint, and level. Spoon over the sausage sauce, then cover with the remaining pasta. Pour the white sauce evenly over the top and sprinkle with the cheddar. Bake for 20-25 minutes until golden brown. Leave for 5 minutes before serving.','20 mins','45 mins - 50 mins'),
(600,'cheesy broccoli pasta bake','https://www.bbcgoodfood.com/recipes/cheesy-broccoli-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-197477_10-8d45e07.jpg','This creamy broccoli pasta is a speedy, satisfying and affordable meal which you can make in just 30 minutes. Use the grill instead of the oven to save time',5,4,1,1,9,'Cook the pasta, adding the broccoli for the final 4-5 mins and cooking until tender. Drain well, then heat the grill. Heat the butter in a saucepan and stir in the flour. Cook for 1 min, then gradually add the milk, stirring well between each addition. Bring to the boil, stirring, then simmer for 2 mins, before stirring in the mustard, half the cheese and seasoning. Mix the pasta and broccoli into the sauce and spoon into an ovenproof dish. Scatter over the remaining cheese and place under a hot grill for 3-4 mins until golden and bubbling.','10 mins','20 mins'),
(601,'creamy ham & mushroom pasta bake','https://www.bbcgoodfood.com/recipes/creamy-ham-mushroom-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-55452_12-ce19de6.jpg','Use up the leftovers in your fridge with this hearty family supper dish',5,5,1,1,9,'Cook the pasta according to pack instructions, then drain. Heat oven to 200C/fan 180C/gas 6, then melt a little butter in a large saucepan. Fry the mushrooms for a couple of mins, then scoop out and set aside. Use some kitchen paper to wipe out the pan. Melt the remaining butter in the pan, then add most of the onions and soften for 1 min. Stir in the flour for another min, then gradually stir in the milk until you have a lump-free sauce. Increase the heat and bubble the sauce, stirring for a few mins to thicken. Turn off the heat, stir in the ham and most of the cheese, then season to taste. Tip the pasta and mushrooms into a large ovenproof dish, then pour over the sauce and mix well in the dish. Scatter over the remaining cheese and spring onions, then bake for 10 mins until golden.','10 mins','20 mins'),
(602,'chorizo & mozzarella gnocchi bake','https://www.bbcgoodfood.com/recipes/chorizo-mozzarella-gnocchi-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gnocchi-1d16725.jpg','Upgrade cheesy tomato pasta with gnocchi, chorizo and mozzarella for a comforting bake that makes an excellent midweek meal',90,6,1,1,9,'Heat the oil in a medium pan over a medium heat. Fry the onion and garlic for 8-10 mins until soft. Add the chorizo and fry for 5 mins more. Tip in the tomatoes and sugar, and season. Bring to a simmer, then add the gnocchi and cook for 8 mins, stirring often, until soft. Heat the grill to high. Stir ¾ of the mozzarella and most of the basil through the gnocchi. Divide the mixture between six ovenproof ramekins, or put in one baking dish. Top with the remaining mozzarella, then grill for 3 mins, or until the cheese is melted and golden. Season, scatter over the remaining basil and serve with green salad.','10 mins','25 mins'),
(603,'turkey pasta bake','https://www.bbcgoodfood.com/recipes/turkey-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Turkey-Pasta-Bake-c5ac47a.jpg','Make the most of leftover Christmas turkey (or chicken) and use it in a low-cost, easy pasta bake. A few leftover pigs in blankets make for a nice surprise, too',6,6,1,1,9,'Heat the oil in a pan over a low-medium heat and fry the onion for 8-10 mins until softened and starting to caramelise. Stir in the garlic and tomato purée, and cook for 1-2 mins more. Tip in the chopped tomatoes, vinegar, oregano, chilli and 2 tsp sugar, then season well and bring to a simmer. Swill out the tomato cans with a splash of water and stir this into the sauce, then partially cover the pan with a lid and cook for 15-20 mins until the tomatoes have reduced to a rich sauce. Crush any large pieces of tomato with a vegetable masher, if you like. Stir through the turkey or chicken and any other cooked meat you want to use up. Meanwhile, boil the kettle. Pour the hot water into a large saucepan, season with salt and bring back to the boil. Cook the pasta for a minute less than pack instructions or until al dente. Drain, reserving a mugful of the pasta cooking water. Tip the pasta into the sauce and stir to combine, adding some of the reserved water to loosen, if needed. Transfer to a baking dish, then tuck in the pigs in blankets or put on top. Scatter over the cheeses. Once cool, will keep covered and chilled for a day, or frozen for a month. Defrost in the fridge overnight before cooking. If cooking imnmediately after prep, heat the grill to high and cook for 8-10 mins until the cheese is bubbling and golden. If cooking from chilled or defrosted, heat the oven to 200C/180C fan/gas 6 and cook for 25-30 mins until piping hot and the cheese has melted.','10 mins','30 mins'),
(604,'greek lamb & macaroni bake','https://www.bbcgoodfood.com/recipes/greek-lamb-macaroni-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1178454_11-ae921b2.jpg','A cross between moussaka and macaroni cheese, this thrifty Mediterranean pasta bake makes for a hearty meal',6,6,1,1,9,'Heat the oil in a large pan, add the onion and cook for 10 mins until soft. Add the garlic and cook for 1 min more, then tip in the lamb mince, turn up the heat and brown, breaking up the mince with a wooden spoon. Once the lamb is browned, add the cinnamon, crumbled stock cube, tomatoes and oregano. Stir well and simmer for 15 mins, covered, then for 15 mins, uncovered, until the sauce has thickened and the tomatoes have cooked down. Meanwhile, cook the macaroni following pack instructions. Drain well. Heat oven to 200C/180C fan/gas 6. Mix together the ricotta, most of the parmesan and the milk with plenty of seasoning, and stir into the macaroni. Tip the mince into a large baking dish, top with the macaroni mixture, and scatter over the remaining parmesan. Bake for 30 mins until golden and bubbling. Serve hot with garlic bread, if you like.','20 mins','1 hr and 15 mins'),
(605,'truffled mac ''n'' cheese','https://www.bbcgoodfood.com/recipes/truffled-mac-n-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/truffled-mac-n-cheese-3fe395a.jpg','Indulge in Tom Kerridge''s ultimate truffle mac ''n'' cheese, an amalgamation of all his favourite versions of this classic American dish ',52,8,2,1,9,'Cook the macaroni following pack instructions until al dente. Drain, rinse with cold water and set aside to cool. Tip the milk and bay leaves into a pan and bring to a simmer. Remove from the heat. Melt the butter in a large non-stick saucepan, then stir in the flour to make a thick paste, continuing to stir for 3-4 mins. Remove and discard the bay leaves from the milk, then gradually pour it into the flour and butter mix, continuously stirring to keep the sauce smooth. Add the mustard, thyme, truffle paste and porcini powder, then stir in the taleggio cheese and mozzarella until melted slightly. Remove from the heat, tip in the macaroni and stir to coat. Scrape into an ovenproof dish. Will keep chilled for up to 24 hrs. Heat the oven to 200C/180C fan/gas 6. Sprinkle over the cheddar, breadcrumbs and parmesan, and bake for 20 mins until golden. For a crispy top, grill for 5 mins or until just golden. Serve straightaway.','30 mins','40 mins'),
(606,'leek & greens lasagne','https://www.bbcgoodfood.com/recipes/leek-greens-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/leek-and-greens-lasagne-d22239d.jpg','Merge cheesy leeks and a classic lasagne to make our mouth-watering family-sized pasta dish. This is an easy and impressive bubbling masterpiece',11,4,1,1,9,'Heat oven to 200C/180C fan/ gas 6. Heat 1 tbsp oil with the butter over a medium heat. Add the bay leaf, rosemary and finely sliced leek, season and cook for a few mins until the leek has softened. Add the flour and cook for 1 min more until the roux smells nutty. Take off the heat, slowly pour in the milk and whisk out any lumps. Return to the heat and whisk slowly until thick and smooth, about 5 mins. Season well with grated nutmeg, then add 3/4 of the cheddar and parmesan, cooking until they are melted. Set aside. In another pan, heat 2 tbsp oil over a medium heat and fry the garlic and chilli for a couple of mins until aromatic. Add the rest of the leeks, season and fry until softened, then add the mixed greens and season a little more. Cook, stirring, until the greens have wilted, about 4 mins, then pour in the wine and cook until it evaporates. Oil a medium roasting tin (about 20cm square) and tip half the greens into the tin. Dot half the walnuts over the top, and nestle in a third of the artichoke hearts. Dot over half the ricotta. Remove the bay leaf from the leek bechamel and pour over a third of it. Top with three lasagne sheets to cover everything in a at layer. Repeat the process, then cover the top layer of lasagne sheets with the remaining bechamel and artichokes and scatter over the remaining cheese. Bake in the oven for 30-40 mins, until golden and bubbling. Rest for 10 mins, then serve with a sharply dressed grated carrot and beetroot salad.','35 mins','1 hr'),
(607,'tuna & broccoli pasta bake','https://www.bbcgoodfood.com/recipes/tuna-broccoli-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-899451_11-ffa993c.jpg','A family favourite, this filling and warming supper is ready in under half an hour - and uses lots of storecupboard ingredients',5,4,1,1,9,'Heat the reserved oil from the tuna in a very large saucepan. Tip in the tomatoes plus 200ml water and simmer while you cook the pasta, following pack instructions. Add the broccoli for the final 3 mins, then drain, reserving some of the cooking water. Heat the grill. Stir the soft cheese into the tomato sauce until melted, then mix with the pasta, broccoli and drained tuna, along with a little of the reserved cooking water if the sauce looks a bit thick. Season with salt and pepper. Tip into an ovenproof dish and sprinkle on the cheddar and crumbs. Grill, not too close to the heat, for about 4-6 mins until golden and bubbling. Serve with a big side salad, if you like.','5 mins','18 mins'),
(608,'vegan mac and cheese','https://www.bbcgoodfood.com/recipes/vegan-mac-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-mac-n-cheese-03f7ed3.jpg','Make the ultimate comfort dish, macaroni cheese, but with vegan credentials. The recipe is quick and easy to make, so a great midweek meal for the family',41,6,1,1,9,'The night before, soak the cashew nuts in water and leave overnight. Heat the oven to 180C/160C fan/gas 4. Steam the carrots and potatoes together for 5 mins, until completely softened. Transfer to a food processor. Drain the cashews and add these with 60ml of the oil, then blitz to break down the nuts. Tip in the other ingredients – apart from the macaroni, breadcrumbs and the remaining oil – then blitz again until the mixture is smooth and season well. Add a splash of water and just a drizzle of olive oil if it looks too stiff, then set aside. Cook the macaroni in a large pan of salted water for 1 min less than packet instructions, drain then stir through the sauce. Transfer the mix to an ovenproof dish, stir the breadcrumbs with the remaining oil and some seasoning. Scatter over the top of the macaroni and bake for 20-25 mins until piping hot and crisp.','15 mins','20 mins'),
(609,'chicken lasagne','https://www.bbcgoodfood.com/recipes/chicken-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Chicken-Lasagne-29e08c9.jpg','This pesto chicken lasagne recipe stretches a pack of chicken thighs into a big sharing pasta bake with a difference. It’s also a great way to use leftover chicken',6,6,1,1,9,'To make the chicken sauce, heat the oil in a large saucepan over a medium heat and tip in the chicken. Cook for about 5 mins on each side until golden, then lift onto a plate. Tip the onions into the pan and fry for 10 mins until golden. Add the garlic and herbs. Tip the chicken back in, along with the tomatoes and ketchup, then swill a little water around the cans and pour this in, too. Season and bring to a simmer. Cook for 35 mins until the chicken is pull-apart tender and the sauce is thick. Use two forks to shred the chicken into the sauce. Set aside. Meanwhile, make the white sauce. Put everything in a saucepan with some seasoning, then bring to a simmer, whisking continuously. Simmer for 5 mins, still whisking until smooth and thick, then remove from the heat. If not assembling the lasagne straightaway, cover and set aside to cool before chilling. Will keep covered and chilled for two days. Spread a thin layer of the chicken sauce in an ovenproof baking dish (ours was 20 x 30cm), drizzle with some of the white sauce and dot over a third of the pesto using a teaspoon. Scatter with a third of the mozzarella, then add a layer of four or five lasagne sheets (you may need to break one of the sheets to fill the gaps), then repeat the layers twice, finishing with a thick layer of the white sauce. Scatter the parmesan over the top. Will keep chilled for a day or frozen for three months. Leave to cool first. Defrost in the fridge overnight before cooking. Heat the oven to 200C/180C fan/gas 6. Cook for 30-35 mins until browned and crisp around the edges. Leave to stand for 10 mins. Scatter with the basil leaves, grind over some black pepper, then cut into squares. Serve with a crisp green salad, if you like.','30 mins','1 hr and 35 mins'),
(853,'linguine with avocado, tomato & lime','https://www.bbcgoodfood.com/recipes/linguine-avocado-tomato-lime','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/linguine-with-avocado-tomato-lime-9638698.jpg','Use guacamole ingredients to make this low-calorie vegan linguine which can also be served cold as a pasta salad. It delivers on flavour and it''s healthy.',4,2,1,1,10,'Cook the pasta according to pack instructions – about 10 mins. Meanwhile, put the lime juice and zest in a medium bowl with the avocado, tomatoes, coriander, onion and chilli, if using, and mix well. Drain the pasta, toss into the bowl and mix well. Serve straight away while still warm, or cold.','20 mins','10 mins'),
(610,'next level mac ''n'' cheese','https://www.bbcgoodfood.com/recipes/macaroni-cheese-marmite','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/macaroni-cheese-251d55c.jpg','Make this granddaddy of pasta bakes with the salty tang of yeast extract, plus mustard and evaporated milk. This is macaroni cheese with added oomph',7,8,1,1,9,'Heat oven to 220C/200C fan/gas 8. Cook the macaroni in salted water for 2 mins less than the stated cooking time, so it still has plenty of bite. Drain well and toss with a small knob of the butter to stop it from sticking together. In a heavy-bottomed saucepan, heat the remaining butter until frothy and nut-brown. Pour off 1 tbsp into a small bowl and place the pan back on the heat, then scatter over and whisk in the flour and cook for 3 mins until you have a sandy paste. Add the milk, a splash at a time, simmering and whisking until smooth between each addition. Once all the milk has been added, drizzle in the evaporated milk, then add the grated cheddar, a handful at a time, until you have a thick, cheesy sauce. Stir through the mustard powder, cayenne, yeast extract and vinegar and taste for seasoning, adding salt and pepper as needed. Remove from heat, stir in the macaroni, then stir through the chunks of cheese. Tip the contents of the saucepan into a large, buttered baking dish. In a small bowl, mix together all the breadcrumbs, the parmesan, another small pinch of cayenne and some crunchy sea salt if you want. Scatter the crumbs over the macaroni, drizzle with the reserved brown butter and bake for 30-35 mins until lightly browned, crunchy all over and bubbling around the edges. Leave to stand for 10 mins before serving straight from the dish.','20 mins','35 mins'),
(611,'easy tomato pasta','https://www.bbcgoodfood.com/recipes/frankie-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52021_11-2e08f7b.jpg','Make this simple tomato and cheese pasta dish for a delicious, filling and budget-friendly supper. It is a great way to use up storecupboard ingredients',69,2,1,1,9,'Heat the oil in a medium pan and fry the onion for 5 mins until softened and lightly golden. Stir in the garlic and cook for a further min. Mix in the tomatoes and sugar, bring to the boil and simmer for 20 mins. Add a few dashes of Worcestershire sauce and season to taste. This basic sauce can now be chilled or frozen. Meanwhile, cook the pasta according to pack instructions. Heat the grill to high. Mix together the cheese and breadcrumbs. Drain the pasta and stir into the sauce. Tip in the frankfurters (if using) and spoon into a heatproof dish. Scatter over the cheesy crumbs and grill for 3-5 mins or until bubbling hot and golden. Serve with a simple salad.','10 mins','30 mins'),
(612,'sausage pasta bake','https://www.bbcgoodfood.com/recipes/sausage-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Sausage-pasta-bake-f71108a.jpg','An Italian-American classic, this freezable sausage pasta bake is an ideal make-ahead family dish for busy days',7,4,1,1,9,'To make the ragu, heat the oil in a large shallow pan or flameproof casserole. Squeeze the sausagemeat from its skins in small balls straight into the pan. Sizzle for 10 mins until browned (don’t worry if the meat breaks up). Add the garlic and sizzle for another minute until starting to turn golden, then stir in the chilli flakes, if using, the tomato purée and vinegar. Tip in the tomatoes and bring to a simmer. Reduce the heat to low and bubble for 30 mins. Meanwhile, make the white sauce. Melt the butter in a saucepan and stir in the flour to make a loose paste. Sizzle for a minute, then gradually whisk in the milk. Simmer gently for 10 mins, whisking occasionally to ensure the sauce stays smooth. Season and whisk in the parmesan. Remove from the heat and set aside. Cook the pasta in a large saucepan of boiling water for 9 mins, or a minute less than pack instructions. Drain and return to the pan. Scrape most of the white sauce into the pasta and stir to coat. Tip in most of the ragu and most of the mozzarella and stir until just combined. Pour the pasta mixture into a large baking dish and spoon over the remaining white sauce and ragu. Dot with the rest of the mozzarella and sprinkle with extra parmesan. Leave to cool completely, then wrap and chill for up to a day, or freeze for up to two months. Defrost in the fridge for 24 hrs before baking. Heat the oven to 190C/170C fan/gas 5. Bake for 25-30 mins until the top is slightly crisp at the edges and the cheese is melted. Leave to rest for 5-10 mins, then take the baking dish straight to the table for people to scoop onto their plates.','30 mins','1 hr and 30 mins'),
(613,'baked salmon & aubergine cannelloni','https://www.bbcgoodfood.com/recipes/baked-salmon-aubergine-cannelloni','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-830452_11-d2380d9.jpg','Oven-cooked pasta dishes are perfect for an informal meal with friends, especially when you can freeze it ahead',5,6,2,1,9,'Heat the oil in a large pan and fry the onions for 10 mins, until starting to turn golden. Stir in the aubergines, then fry 5 mins more to soften them. Pour in the passata and wine, then stir in the capers, sugar, 1 tsp salt and some black pepper. Cover and cook for 20 mins. Meanwhile, spoon 250g mascarpone into a bowl with the milk, pesto and eggs, and beat until smooth. Halve each salmon fillet to make 2 long pieces. Spread 1 tbsp of the remaining mascarpone over each pasta sheet, add a piece of salmon, then season and roll up. Wash and wilt the spinach in a large pan (you don’t need to add oil), then squeeze as much liquid as you can from it – this is important as otherwise the pasta bake will be too wet. Spread the spinach on the base of a large lasagne dish or roasting tin, measuring approx 27 x 35cm. Arrange the salmon and lasagne parcels on the spinach, then pour over the tomato and aubergine sauce. Now carefully pour over the pesto and mascarpone layer. If freezing, cover the surface of the lasagne with cling film, then overwrap the dish with cling film and foil. To defrost, thaw overnight in the fridge. To cook and serve, heat oven to 190C/170C fan/gas 5, scatter the Parmesan over the mascarpone and bake for 40 mins until bubbling. Allow to settle in the dish for 5-10 mins before serving scattered with basil leaves.','40 mins','1 hr and 15 mins'),
(623,'bacon & broccoli pasta','https://www.bbcgoodfood.com/recipes/bacon-broccoli-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-529464_11-96bedc9.jpg','A quick and tasty pasta dish that goes from pot to plate in just 20 minutes making it an ideal midweek meal',5,4,1,1,9,'Bring a large pan of water to the boil and cook the pasta according to pack instructions. About 3 mins before the pasta is cooked, throw in the broccoli. When everything is done, drain, reserving about 100ml of the cooking water. While the pasta is cooking, grill the bacon for 6-7 mins until crisp, then cut into bite-size pieces. Tip everything into the pasta pan, toss together well and loosen with a little of the cooking water, if needed. Sprinkle over the Parmesan and serve.','5 mins','15 mins'),
(614,'sausageball pasta bake','https://www.bbcgoodfood.com/recipes/sausageball-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-836616_11-dcee4a9.jpg','Using leftover sausagemeat to make meatballs is a thrifty way to jazz up an oven-cooked pasta pot',73,4,1,1,9,'Put the sausagemeat, breadcrumbs and thyme or rosemary in a bowl and mix to combine. Shape into 20 small balls. Heat oil in a wide pan and cook the sausageballs for 10 mins until nicely browned. Add passata and tomato paste, cover and simmer gently for 20 mins. Meanwhile, cook pasta following pack instructions, adding the cabbage for the final 5 mins of cooking time. Drain cabbage and pasta, toss with the sauce and spoon into a heatproof dish. Heat oven to 200C/180C fan/ gas 6. Push mozzarella cubes into the top of the pasta. Sprinkle with Parmesan and bake for 15-20 mins until bubbling and crusty.','15 mins','50 mins'),
(615,'truffled macaroni & smoked haddock bake','https://www.bbcgoodfood.com/recipes/truffled-macaroni-smoked-haddock-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/posh-pasta-bake-2ded207.jpg','Take pasta bake to new heights with this moreish macaroni and smoked haddock dish. It can be assembled in advance and finished in the oven the next day',25,4,1,1,9,'Blend together all the ingredients for the breadcrumbs in a food processor until roughly chopped, season, then set aside. Boil the macaroni 1 min less than the time on the pack, drain, toss in the olive oil and set aside. Tip the onion, milk and the smoked haddock into a pan and season. Simmer gently for 10 mins, then remove the haddock. Flake the flesh and drizzle with the truffle oil and set aside. Mix the butter and flour together to make a paste. Over a low heat, whisk the paste into the hot milk and bring to the boil, stirring all the time. Remove from the heat and add half the grated cheese and stir well. Take the pan off the heat, tip the pasta into the sauce and mix well, then fold through the haddock and season with ground black pepper. The sauce may seem a little thin, but it’ll thicken as it cooks. Butter a large ovenproof dish and tip in all the macaroni (I like it piled high), then finish by scattering over the rest of the cheese and most of the breadcrumbs. This can be prepared a day ahead, covered and kept in the fridge. To cook, heat oven to 200C/180C fan/gas 6. Bake for 20 mins until bubbling and sprinkle with the rest of the breadcrumbs. Bake for 5 mins more, then drizzle a little more truffle oil on top, and scatter some extra herbs, if you like.','25 mins','1 hr'),
(616,'baked conchiglioni with sausage, sage & butternut squash','https://www.bbcgoodfood.com/recipes/baked-conchiglioni-sausage-sage-butternut-squash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/butternut-74b3c9d.jpg','Melting fontina cheese, rich truffle oil and a creamy squash and Parmesan sauce turn these stuffed pasta shells into an indulgent dinner',6,4,1,1,9,'Place the squash in a microwavable bowl with 1 tbsp water. Cook in the microwave on High for 10 mins or until soft. Meanwhile, heat the oil in a frying pan, add the sage leaves and sizzle for 1 min. Remove the sausages from their skins and divide into small balls about the size of unshelled hazelnuts. Add to the pan and fry until browned. Add half the squash and set aside. Mash the remaining squash with any liquid from the bowl, the nutmeg and seasoning. Heat oven to 220C/200C fan/gas 7. Cook the pasta following pack instructions. Meanwhile, melt the butter in a saucepan, then add the flour and stir to a paste. Cook for 1-2 mins, stirring the whole time, then gradually mix in the milk until you have a smooth sauce. Add the mashed squash, Parmesan and plenty of seasoning. Add 3-4 tbsp of the sauce to the sausage mix to moisten it a little. Spread the remaining squash sauce over the base of a large, shallow baking dish. Drain the pasta, but keep a little liquid in the pan to stop the shells from sticking together. Fish any broken bits of pasta out of the pan and scatter these over the sauce. Line up the shells on top, pushing them down into the sauce so only the tops of the shells are poking out. Fill each shell with the sausage mixture. Scatter with fontina or mozzarella, grate over some extra Parmesan and drizzle with a little truffle oil, if you like. Bake for 30 mins or until golden brown.','30 mins','1 hr and 10 mins'),
(617,'bucatini with mushrooms & sausage','https://www.bbcgoodfood.com/recipes/bucatini-with-mushrooms-sausage','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Bucatini_Mushrooms_Sausage-a0d4a07.jpg','Pay homage to some of the delicious pasta dishes in Umbria, where mushrooms and sausages are king – this recipe highlights the power of dried porcini stock.',91,4,1,1,9,'Tip the dried porcini mushrooms into a large pan with 600ml boiling water from the kettle. Set over a low heat and simmer for 10-15 mins until slightly reduced to make an intense mushroom stock. Heat the the oil in a large pan over a medium heat. Squeeze the sausagemeat from the skins into the pan and fry for 5-8 mins, breaking it up with a wooden spoon as it cooks, until lightly browned. Meanwhile, bring a large pan of salted water to the boil. Drop in the bucatini and move it around using tongs to prevent it sticking together. Cook for 9 mins. Meanwhile, add the garlic and chilli flakes to the sausagemeat and fry for 1 min before pouring in the wine. Cook for 5 mins more until the wine has reduced, then tip in the rehydrated mushrooms and the stock (you should have about 350ml). Season with salt and black pepper, then bring to a simmer. After roughly 9 mins, use the tongs to move the bucatini directly from its cooking water into the mushroom sauce. The mixture will seem quite watery at first, but the trick is to finish cooking the bucatini by simmering it in the broth – it will soak it all up and take on the earthy flavour. Keep moving the pasta in the broth using the tongs for 3 mins, or until most of the water has been absorbed. Stir in the cream, a grating of nutmeg and the parsley. Stir well, taste for seasoning and serve topped with lots of parmesan.','5 mins','22 mins'),
(630,'creamy linguine with ham, lemon & basil','https://www.bbcgoodfood.com/recipes/creamy-linguine-ham-lemon-basil','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-358635_10-ef2e9d8.jpg','Keep the kids happy with this easy, low-fat recipe that''s ready in under half an hour',8,6,1,1,9,'Cook the linguine. Meanwhile, tear the ham into small pieces and fry in the olive oil until golden and crisp. Drain the pasta, reserving a little of the cooking water, then return to the pan. Tip in the cooked ham. Mix together the lemon juice, egg yolks and crème fraîche, then add this to the pan along with the basil and Parmesan. Mix in with tongs, adding a little of the cooking water, if needed, to make a creamy sauce that coats the pasta. Serve with extra Parmesan grated over the top, if you like.','10 mins','15 mins'),
(618,'speedy sausage stroganoff tagliatelle','https://www.bbcgoodfood.com/recipes/speedy-sausage-stroganoff-tagliatelle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/speedy-sausage-stroganoff-tagliatelle-a4653e7.jpg','Make our budget-friendly quick sausage stroganoff tagliatelle for a simple family dinner. This filling bowl takes just five minutes to prepare',14,4,1,1,9,'Heat the butter and oil in a frying pan over a medium-high heat until foaming. Squeeze large chunks of the sausagemeat out of the skins and into the pan. Cook for 5-8 mins or until golden brown. Add the mushrooms and cook for a further 5 mins until starting to turn brown. Stir through the paprika and cook for 1 min before stirring in the soured cream, mustard and stock. Bring to a simmer and season to taste. Meanwhile, cook the pasta in a large pan of salted water according to pack instructions, then add to the sauce with half the parsley. Serve in deep bowls with the remaining parsley sprinkled on top.','5 mins','15 mins'),
(619,'pea & ham pasta','https://www.bbcgoodfood.com/recipes/pea-ham-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Pea-and-ham-pasta-3af5250.jpg','Celebrate peas being in season with this classic combination. This quick and easy pasta dish takes less than 20 minutes to make',2,2,1,1,9,'Bring a large pan of salted water to the boil, then cook the pasta following pack instructions, about 9 mins. Around 3 mins before the pasta is cooked, add the peas. Meanwhile, heat the olive oil in a frying pan over a medium heat and fry the onion for 5 mins until soft but not golden. Add the ham, double cream, lemon juice and parmesan, then season and mix well. Remove from the heat. Drain the pasta and peas (keeping a mugful of the cooking water) and return them to the pan. Tip in the creamy ham mixture and stir everything together to combine. If the mixture seems a little dry, add some of the reserved cooking water to loosen it a little. Serve with extra parmesan on top, if you like.','9 mins','10 mins'),
(620,'mushroom & sausage pasta','https://www.bbcgoodfood.com/recipes/mushroom-sausage-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-625457_11-62e507c.jpg','An easy pasta dish which will see you through many a midweek meal',5,4,1,1,9,'Dry-fry the sausage meat in a non-stick pan for 8-10 mins until browned, breaking it up with a wooden spoon, then remove and set aside. Fry bacon and mushrooms for 5-8 mins until golden, return the sausage meat and keep warm. Cook the pasta following pack instructions. Make the sauce by beating together the Parmesan, egg yolks, most of the parsley and the crème fraîche. When the pasta is cooked, drain, reserving a ladle of the cooking water. Combine the pasta and meat mixture over a low heat and add the sauce. Season and quickly mix, adding a splash of cooking water if it’s a little thick. Spoon into bowls and eat with the extra Parmesan shavings and remaining parsley on top.','10 mins','20 mins'),
(621,'lemon & greens pesto pasta','https://www.bbcgoodfood.com/recipes/lemon-greens-pesto-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Lemon-and-greens-pesto-pasta-5b71498.jpg','Prep, cook and serve this quick and easy lemon and green veg pasta in just 15 minutes. It''s perfect for busy days, and will be loved by the whole family',89,4,1,1,9,'Cook the pasta following pack instructions. Meanwhile, cook the broccoli for 4 mins in boiling salted water. Drain, then put in a food processor along with the basil, spinach and pine nuts. Pulse until combined. Stir in the garlic, lemon zest and juice, olive oil, peas, parmesan and soft cheese, and season to taste. Drain the pasta, reserving a little of the cooking water. Return the pasta to the pan and add the pesto along with a splash of water to loosen. Toss together, check the seasoning and serve.','5 mins','10 mins'),
(622,'creamy carbonara','https://www.bbcgoodfood.com/recipes/learn-to-make-spaghetti-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338497_12-05fada5.jpg','Learn how to make this spaghetti dish featuring pancetta, eggs and a touch of double cream. Ready in under half an hour, it''s a midweek meal saviour.',5,4,1,1,9,'Bring a large, deep pan of salted water to the boil. Plunge the spaghetti into the water, stir well and cook, following pack instructions. Aim for ''al dente'': cooked, but retaining some bite in the middle. Take a few strands out of the water with a spaghetti spoon or tongs 1-2 mins before the end of the cooking time and bite into it. Repeat until you think it is properly cooked. Meanwhile, heat the olive oil in a frying pan. When hot, tip in the pancetta. Fry over a medium heat until the fat in the meat has melted down into the pan and the meat has turned lightly golden. Be careful not to crisp the pancetta too much; it will be cooked a little bit more later on in the recipe. Remove from the heat and set aside. Crack two of the eggs into a mixing bowl. Separate the yolk from the third egg: gently crack the side of the shell and, using both hands, slowly open it into two halves over a separate bowl - you want to keep the yolk in one of the halves. Tip the yolk from one shell half to the other and repeat until all the egg white has fallen into the bowl below, taking care not to crack the yolk on any rough shell edges. Tip the yolk in with the other eggs and beat together, along with the double cream, parmesan and some seasoning. Add the garlic to the pancetta and return the frying pan to hob. Fry over high heat for 1 min or until garlic is cooked and pancetta warmed through. Meanwhile, drain the spaghetti. Tip back into the hot saucepan off the heat. Pour egg mixture over pasta, followed by hot pancetta, garlic, any fat and oils. Toss quickly and thoroughly with spaghetti spoon or tongs. Mix until it has thickened to a smooth, creamy sauce. Serve with extra cheese and freshly ground pepper.','5 mins','20 mins'),
(1231,'homemade hot chocolate','https://www.bbcgoodfood.com/recipes/homemade-hot-chocolate','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Classic-Homemade-Hot-Chocolate-7d8353b.jpg','Indulge in a homemade hot chocolate. Perfect as the cold nights draw in, you can add a pinch of chilli, cinnamon or a slug of rum if you like',12,1,1,2,3,'Heat the milk, cocoa, sugar and chocolate in a small pan over a medium heat until steaming and the chocolate has melted. Whisk to dissolve the cocoa. Pour into a mug, then add the cream to float on top. Grate over a little more chocolate to serve.','5 mins','5 mins'),
(624,'sausage, leek & fennel pasta','https://www.bbcgoodfood.com/recipes/sausage-leek-fennel-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-leek-fennel-pasta-d7dcbf8.jpg','This quick and easy pasta dish is sure to be a family favourite and it''s ready in 30 minutes',92,4,1,1,9,'Heat the oil in a large frying pan. Squeeze the sausages from their skins into the pan and break up into small pieces. Cook over a medium-high heat for 8-10 mins until browned and crispy in places. Remove using a slotted spoon and set aside. Boil a full kettle. Add the leeks to the pan and season. Cook over a low heat for 10-12 mins until softened and just starting to caramelise. Meanwhile, fill a large pan with the boiled water and some salt, and cook the pasta following pack instructions. Drain, reserving 150ml of the cooking water. Add the garlic and fennel seeds to the leeks and cook for 2 mins. Stir through the mustard and sausages, then pour in 100ml of the pasta water and let it bubble for 1 min. Tip in the pasta, sprinkle over the lemon zest and toss everything together, adding a little more pasta water if needed. Divide between plates and top with Parmesan and black pepper before serving.','5 mins','25 mins'),
(625,'spaghetti puttanesca with red beans & spinach','https://www.bbcgoodfood.com/recipes/spaghetti-puttanesca-red-beans-spinach','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-spaghetti-puttanesca-with-red-beans-spinach-eedfae9.jpg','Get four of your 5-a-day with this healthy vegan puttanesca. With spaghetti, kidney beans, tomatoes, olives and spinach, it''s quick, easy and full of nutrients',4,2,1,1,9,'Cook the spaghetti in simmering water for 10-12 mins until al dente. Meanwhile, fry the onion in the oil in a large non-stick frying pan with a lid until tender and turning golden. Stir in the chilli, garlic and cherry tomatoes. Add the vinegar, capers, olives and paprika with a ladleful of pasta water. Stir in the beans and cook until warmed through. Add the spinach to the pasta water to wilt, then drain well. Toss with the tomato and bean mixture and the parsley and basil, then pile onto plates or in shallow bowls to serve.','10 mins','17 mins'),
(626,'pea fusilli with ham & mustard sauce','https://www.bbcgoodfood.com/recipes/pea-fusilli-ham-mustard-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/dinner-dash-pasta-bfca6cc.jpg','Cook this quick pasta dish with the flavours of pea, ham and mustard when you need dinner, fast. It''s a filling, gluten-free meal that uses just four ingredients',29,2,1,1,9,'Bring a pan of salted water to the boil, add the pasta and boil for 6 mins. Meanwhile, warm the crème fraîche, mustard and ham hock in a pan, bring to a simmer then, once the pasta is cooked, drain it and tip into the ham & mustard sauce. Toss together and season well before serving.','5 mins','10 mins'),
(627,'asparagus, chilli & feta farfalle','https://www.bbcgoodfood.com/recipes/asparagus-chilli-feta-farfalle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/asparagus-chilli-feta-farfalle-1bfba62.jpg','A simple, budget pasta dish with a touch of chilli, squeeze of lemon and fresh basil. Add a drizzle of oil and sprinkling of feta cheese to serve',16,2,1,1,9,'Bring a large pan of salted water to the boil. Tip in the pasta and cook for 1 min less than pack instructions. Meanwhile, heat the oil in a frying pan over a medium heat. Stir in the garlic, chilli and a pinch of salt, and fry for 1 min until fragrant. Add the asparagus and lemon zest, and cook for 4-5 mins until the asparagus is just tender. Drain the pasta and tip into the asparagus pan. Add the lemon juice, stir through the basil, grind over some black pepper, then divide between bowls. Top with the pine nuts, feta and a drizzle of oil to serve.','5 mins','15 mins'),
(628,'chorizo & chilli pepper pasta','https://www.bbcgoodfood.com/recipes/chorizo-chilli-pepper-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-and-chilli-pasta-0c27224.jpg','Pickled chilli peppers, spicy chorizo and sweet cherry tomatoes make up this simple and quick pasta dish. Ideal for an easy midweek meal for two',16,2,1,1,9,'Bring a large pan of salted water to the boil. Add the pasta, give it a good stir, then cook for 1 min less than pack instructions, about 9-10 mins. Heat the oil in a sauté pan over a high heat. Add the chorizo and cook for a couple of mins until the oils have been released, then add the tomatoes, a pinch of sugar and some seasoning. Bubble away for 5 mins, then drain the pasta, toss it through the sauce and stir in the peppers and basil. Serve with a generous grating of parmesan scattered over.','5 mins','10 mins'),
(629,'two-step carbonara','https://www.bbcgoodfood.com/recipes/two-step-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-12145_11-b37c27a.jpg','The classic quick, creamy sauce - but made without cream',5,4,1,1,9,'Boil the pasta. Meanwhile, fry pancetta in oil in a frying pan for a few mins until golden and crisp. Add garlic, fry for 1 min, then turn off the heat. Briefly whisk egg and yolks with most of the Parmesan and some seasoning. Drain pasta, reserving a little of the cooking water. Add eggs and a tbsp of cooking water, then mix until pasta is coated and creamy. The heat from the pasta will gently cook the sauce. Stir in the pancetta and garlic then serve, topped with the remaining Parmesan.','2 mins','12 mins'),
(631,'green spaghetti & meatballs','https://www.bbcgoodfood.com/recipes/green-spaghetti-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-spaghetti-da9d037.jpg','Feed three adults or six kids with this spaghetti dinner that packs in two of your 5-a-day. Just the thing for children who are wary of greens',6,3,1,1,9,'Mix the mince, apple and fennel seeds in a bowl. Divide and roll into 24 cherry tomato-sized balls. Heat the oil in a large frying pan. Cook the meatballs until golden brown and cooked through. Heat a large pan of water and cook the pasta following pack instructions. Meanwhile, make the sauce. Set aside a handful of spinach, 4 tbsp peas and half the avocado for later. Pour hot water over the rest of the peas to defrost them, then drain well. Whizz the defrosted peas, spinach, avocado, basil and parmesan in a blender with 100ml pasta water, adding more if needed, to make sauce. Season well. Add the reserved frozen peas to the pasta for the last min of cooking. Drain, saving some of the water, and tip the pasta into the meatball pan. Add the sauce and cook for a minute or 2 until hot, adding more seasoning or pasta water if you need to. Serve a salad of spinach and avocado on the side and top the pasta with extra parmesan for adults, if you like.','15 mins','25 mins'),
(632,'spicy spaghetti with garlic mushrooms','https://www.bbcgoodfood.com/recipes/spicy-spaghetti-garlic-mushrooms','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201764_10-83ccae9.jpg','A low fat, fail-safe pasta dish that proves that it''s just as easy to whip up a meal as it is to turn on the microwave',5,4,1,1,9,'Heat 1 tbsp oil in a pan, add the mushrooms, then fry over a high heat for 3 mins until golden and softened. Add the garlic, fry for 1 min more, then tip into a bowl with the parsley. Add the onion and celery to the pan with the rest of the oil, then fry for 5 mins until lightly coloured. Stir in the tomatoes, chilli and a little salt, then bring to the boil. Reduce the heat and simmer, uncovered, for 10 mins until thickened. Meanwhile, boil the spaghetti, then drain. Toss with the sauce, top with the garlicky mushrooms, then serve.','10 mins','15 mins'),
(633,'nutty chicken & lemon spaghetti','https://www.bbcgoodfood.com/recipes/nutty-chicken-lemon-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-and-walnut-spaghetti-b72a59c.jpg','Enjoy spaghetti with chicken and a walnut, garlic and lemon sauce to make this quick and easy midweek meal. It takes 30 minutes from prep to plate',14,4,1,1,9,'Heat the grill to medium. Put the chicken on a baking tray, drizzle with ½ tbsp oil and season. Grill for 8-10 mins on each side or until cooked through. Shred the meat with two forks. Meanwhile, soak the bread in the milk for 5 mins, then tip into a food processor with the walnuts, remaining oil, garlic, lemon zest and juice. Pulse to a smooth creamy texture. Season. Bring a large pan of salted water to the boil. Add the spaghetti and cook according to pack instructions.  Drain the spaghetti, reserving a mugful of cooking water (about 200ml). Return the spaghetti to the pan and toss with the walnut sauce, shredded chicken, most of the pasta water and the parsley. Toss to coat, and add a little more pasta water to loosen, if you need to. Check for seasoning and serve in bowls topped with the parmesan.','10 mins','20 mins'),
(634,'spaghetti alle vongole','https://www.bbcgoodfood.com/recipes/spaghetti-alle-vongole-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-23176_12-f2c69cd.jpg','This light dish makes a perfect midweek supper treat',43,2,1,1,9,'Put the water for the spaghetti on to boil. Rinse the clams in several changes of cold water. Discard any that are open or damaged. Cover the tomatoes with boiling water, leave for 1 min, then drain and slip off the skins, Remove the seeds and chop the flesh. Cook spaghetti according to pack instructions. Meanwhile, heat the oil in a large pan, add the garlic and chilli, then fry gently for a few seconds. Stir in the tomatoes, then add the clams and a splash of wine, salt and pepper and bring to the boil. Cover the pan and cook for 3-4 mins, until the clams are open. Drain the pasta, then tip into the pan with the parsley and toss together. Serve in bowls with bread for mopping up the juices.','15 mins','12 mins'),
(635,'spaghetti with sardines','https://www.bbcgoodfood.com/recipes/spaghetti-sardines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-922495_11-955ddcf.jpg','This storecupboard supper is super quick and superhealthy to boot',5,4,1,1,9,'Cook the spaghetti in a large pan of boiling salted water according to pack instructions. Meanwhile, make the sauce. Heat the oil in a medium pan and cook the garlic for 1 min. Add the chilli flakes, tomatoes and sardines, breaking up roughly with a wooden spoon. Heat for 2-3 mins, then stir in the olives, capers and most of the parsley. Mix well to combine. Drain the pasta, reserving a couple of tbsp of the water. Add the pasta to the sauce and mix well, adding the reserved water if the sauce is a little thick. Divide between 4 bowls and sprinkle with the remaining parsley.','5 mins','15 mins'),
(1078,'little jam tarts','https://www.bbcgoodfood.com/recipes/little-jam-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-592455_11-e664fce.jpg','These simple favourites take just 30 minutes to make - get the kids to help',5,20,1,2,11,'Roll out the shortcrust pastry on a lightly floured surface to just under the thickness of £1 coin. Stamp out 20 x 5cm circles using a pastry cutter and line 2 mini muffin tins (or make in 2 batches). Prick with a fork and spoon 1 tsp jam into each (we used apricot, blackcurrant and strawberry). Stamp out shapes from the leftover pastry to decorate the tarts, if you like. Bake at 200C/180C fan/gas 6 for 12-15 mins, until the pastry is golden.','15 mins','15 mins'),
(636,'pepper & lemon spaghetti with basil & pine nuts','https://www.bbcgoodfood.com/recipes/pepper-lemon-spaghetti-basil-pine-nuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-pepper-lemon-spaghetti-with-basil-pine-nuts-ac2b524.jpg','Try this zesty pasta dish for a quick and healthy veggie lunch option. Low in calories, it provides fibre, vitamin C and iron and counts as two of your 5-a-day',4,2,1,1,9,'Heat the oil in a large non-stick frying pan. Add the pepper and cook for 5 mins. Meanwhile, cook the pasta for 10-12 mins until tender. Add the courgette and garlic to the pepper and cook, stirring very frequently, for 10-15 mins until the courgette is really soft. Stir in the lemon zest and juice, basil and spaghetti (reserve some pasta water) and toss together, adding a little of the pasta water until nicely coated. Add the pine nuts, then spoon into bowls and serve topped with the parmesan, if using.','7 mins','25 mins'),
(637,'next level spaghetti carbonara','https://www.bbcgoodfood.com/recipes/next-level-spaghetti-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spaghetti-carbonara-366e331.jpg','Make the ultimate spaghetti carbonara with a creamy hollandaise-style sauce and crisp pancetta or guanciale. You can also mix in an egg yolk at the end',7,2,2,1,9,'First, warm your serving dishes in a low oven or in the microwave. Heat the oil gently in a large, shallow pan. Fry the guanciale or pancetta and garlic for 10 mins, or until all the fat has rendered off and the meat is golden and crisp. Remove and discard the garlic clove, then turn off the heat. Bring a large pan of salted water to a simmer and cook the pasta until it’s al dente (about a minute less than the pack instructions). Meanwhile, whisk 2 egg yolks in a small bowl with a pinch of salt. Using kitchen tongs, lift the pasta from the water into the pancetta pan along with any dripping water. Use a wooden spoon to stir it into the rendered fat. If the pan looks dry, add a small ladleful of pasta water and mix it in. Keep adding until you see a little pasta water at the bottom of the pan – you’ll be surprised how much will be absorbed. Working quickly, tip the beaten yolks in with the pasta and stir vigorously. Rinse the yolk bowl out with a little more pasta water and pour that in too. Add most of the parmesan and beat again. If at any time it’s becoming claggy or starting to scramble, pour in a little more water. If you’ve added too much, stir the pasta over the lowest heat for a few moments. You’re aiming for a smooth sauce that is the consistency of double cream. Season with a couple of pinches of ground black pepper and taste for salt. Transfer the pasta to the warmed serving dishes, scatter over the remaining parmesan and nestle the last two egg yolks on top, if using. Season them with some salt and pepper, and drizzle over the truffle oil, if you have it, just before serving.','10 mins','15 mins'),
(638,'spaghetti genovese','https://www.bbcgoodfood.com/recipes/spaghetti-genovese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1845_10-e6b81e8.jpg','This vegetarian pasta dish is the perfect solution when you want something quick and tasty - and everything''s cooked in one pan',5,4,1,1,9,'Pour a kettle of boiling water into a very large pan until half full. Return to the boil, then add the potatoes and spaghetti, and a little salt. Cook for 10 minutes until the potatoes and pasta are almost tender. Tip in the green beans and cook for 5 minutes more. Drain well, reserving 4 tbsp of the cooking liquid. Return the potatoes, pasta and beans to the pan, then stir in the fresh pesto and reserved cooking liquid. Season to taste, divide between four serving plates and drizzle with a little olive oil.','25 mins','No Time'),
(639,'how to make spaghetti and meatballs','https://www.bbcgoodfood.com/recipes/meatballs-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/01/recipe-image-legacy-id-591456_11-0f62a3f.jpg','Cookery and wine writer, Fiona Beckett, shares one of her family''s favourite recipes',5,6,1,1,9,'Cut through the skin of each sausage and pull them off. Put the sausage meat into a large bowl with the mince. Add half the garlic, half the parsley, the breadcrumbs and egg, and mix well. Season generously with pepper and a little salt, and mix again. Sprinkle a chopping board with flour. Scoop out level dessertspoons of the mix, dip them in the flour and roll them into balls. Heat 3 tbsp of the oil in a large frying pan or wok, then fry the meatballs in batches, browning them on all sides. Set aside on a plate. When you’ve fried all the meatballs, pour away any excess fat. Rinse and dry the pan. Pour the remaining oil into the pan, heat for 1 min then add the remaining garlic and fry for a few seconds. Tip in the tomatoes and break them down with a fork or wooden spoon. Cook over a moderately high heat for 5 mins until jammy. Season with salt, pepper and a little sugar. Tip in the meatballs and turn them over in the sauce, ensuring they’re all covered. Cover the pan and cook the meatballs on a low heat for about 30 mins, spoon over the sauce occasionally and add a little water if it’s becoming too dry. 15 mins before the end of the cooking time, cook the spaghetti. To serve, stir most of the remaining parsley into the sauce then spoon the meatballs and sauce over the spaghetti. Scatter with last of the parsley and serve Parmesan on the side.','30 mins','45 mins'),
(640,'classic spaghetti','https://www.bbcgoodfood.com/recipes/classic-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3791_11-856e688.jpg','Whether you are a novice cook or simply want to improve your skills, follow our step-by-step recipefor a delicious pasta dish that works every time',5,4,1,1,9,'Using a sharp knife, peel the onion, slice in half through the root and trim off the top. Place the onion, cut-side down, on a chopping board. With your knife parallel to the board, cut 3 slices through the onion, keeping the root intact. With knife point at root end, cut down through the onion, from top to bottom 4 times. Holding the root end, finely slice across the onion (see pic above), to give you small diced pieces. Set a large frying pan over medium heat and when hot, tip in the oil then onion. Cook for 5 mins until starting to soften, stirring occasionally. Add the garlic, cook 2 mins more until the onions start to turn golden. When the onion has softened, tip in the tomatoes and half the basil. Leave to gently bubble for 15 mins until the sauce has thickened and looks pulpy. Stir occasionally and break up any large clumps of tomato with the back of your spoon. Pour water ¾ of the way up your largest pan. Heat over a high heat and add plenty of salt. When water has reached a rolling boil, place the spaghetti into the pan. As it softens, use your hands to push all of it into the water. Give it a stir to separate the pasta, then cook according to pack instructions, about 10 mins. Taste a strand of pasta. It should be just soft, but not mushy. Scoop out a cup of water before draining in a colander (this will help to loosen your sauce). Finishing touches: Tip the drained pasta back into its cooking pan, then pour over the tomato sauce. Give everything a good stir. The sauce should just coat the pasta, if it is thick and looks dry, stir in a few spoons of the pasta water. If it is watery, cook over a low heat until evaporated, stirring often. Use your hands to break the mozzarella into chunky pieces and stir through the pasta along with the remaining basil leaves. Serve straight away.','30 mins','N/A'),
(641,'sunblush spaghetti','https://www.bbcgoodfood.com/recipes/sunblush-spaghetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1473_11-985bbce.jpg','Pep up spaghetti with this Mediterranean-style recipe',5,4,1,1,9,'Boil the pasta according to pack instructions. Meanwhile, roughly chop the tomatoes. Drain the spaghetti, reserving a cupful of the cooking water in the bottom of the pan. With the pan still on the heat, stir the tomatoes, tomato purée, olives, oil and half the cheese into the water and bring to the boil. Simmer for a few mins to make a loose sauce, then season to taste. Toss the pasta and pine nuts through the sauce and serve sprinkled with the rest of the cheese.','5 mins','20 mins'),
(642,'spaghetti omelette','https://www.bbcgoodfood.com/recipes/spaghetti-omelette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-320470_10-3a6a26b.jpg','This speedy spaghetti omelette is a great way of using up leftover pasta, and it''s bound to be a hit with kids',5,4,1,1,9,'Heat half the oil in a medium non-stick frying pan and add the garlic and onion. Cook for 2-3 mins, until softened. Using your hands, squeeze out as much moisture from the courgettes as possible. Add them to the pan and cook for a further 3-4 mins, until tender. Spoon the vegetables into a large bowl. Stir in the parsley, cheese sauce and spaghetti and season. Beat the eggs and stir into the mixture. Heat remaining oil in the frying pan and tip in the spaghetti mixture. Level it out with the back of a spoon and cook over a medium heat for 3-5 mins, until almost set. Transfer to a hot grill for 2-3 mins, until golden. Leave to cool in the pan for 5 mins, then cut into wedges and serve.','15 mins','15 mins'),
(643,'lemon spaghetti with tuna & broccoli','https://www.bbcgoodfood.com/recipes/lemon-spaghetti-tuna-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-925484_10-cab6e79.jpg','15 minutes is all you need to whip up this fresh, light and healthy fish pasta with olives, capers and greens ',4,4,1,1,9,'Boil the spaghetti in salted water for 6 mins. Add the broccoli and boil for 4 mins more or until both are just tender. Meanwhile, mix the shallots, olives, capers, tuna and lemon zest and juice in a roomy serving bowl. Drain the pasta and broccoli, add to the bowl and toss really well with the olive oil and lots of black pepper. Serve with a little extra olive oil drizzled over.','5 mins','10 mins'),
(644,'tomato & mozzarella spaghetti bake','https://www.bbcgoodfood.com/recipes/tomato-mozzarella-spaghetti-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-328027_11-e97a30a.jpg','This pasta bake is simple yet satisfying, great for a weeknight supper',5,4,1,1,9,'Heat oven to 200C/180C fan/gas 6. Heat the oil in large pan, add the garlic and fry for 1 min. Add the tomatoes, chilli and seasoning, bring to the boil and simmer for 10 mins. Meanwhile, cook the spaghetti, then drain. Mix the spaghetti with the tomato sauce and basil, then spoon half into a large ovenproof dish. Arrange half the mozzarella over the top and sprinkle with half the Parmesan. Repeat the layers, then bake for 15-20 mins until lightly browned on top. Scatter with extra basil and serve with a big green salad.','15 mins','35 mins'),
(645,'next level spaghetti bolognese','https://www.bbcgoodfood.com/recipes/next-level-spaghetti-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spaghetti-bolognese-1130fa2.jpg','Ramp up your usual spaghetti Bolognese with this recipe which throws in a few unorthodox methods and ingredients to deliver the ultimate pasta dish',7,8,1,1,9,'Heat a drizzle of olive oil in a large non-stick frying pan and crumble in the mince and sausagemeat. Spend a good 30 mins cooking the mince – it will release lots of liquid, which you want to evaporate, and then eventually it will end up sizzling in its own fat. If the pan becomes too dry, drizzle in a little more olive oil. Towards the end, continue stirring the mince until it becomes speckled with crisp brown bits. Heat oven to 140C/120C fan/gas 2. While the mince is browning, heat a drizzle more oil in a casserole dish and sizzle the pancetta for 5 mins until it starts to brown and release its fat, then throw in the vegetables and herbs, and finely crumble over the dried porcini. Cook gently for 5 mins until soft and starting to brown. Sprinkle over the sugar, then stir in the tomato purée and splash in the fish sauce and vinegar. Simmer down until gloopy, then stir through the meat and pour in the milk and tomatoes. Rinse out the tomato tins with the wine and stir into the pan. Season, then nestle in the parmesan rind. Bring to a simmer, then cover and cook in the oven for 3 hrs. When cooked, tip as many portions of Bolognese as you need into a sauté pan. Cook the pasta until very al dente, then tong into the pan with a bit of the water and finish cooking with the sauce for 2 mins. Stir through the parmesan and a drizzle more olive oil. Twirl the pasta and Bolognese into bowls, scatter with basil and serve with more parmesan.','20 mins','4 hrs'),
(732,'zesty lamb chops with crushed kidney beans','https://www.bbcgoodfood.com/recipes/zesty-lamb-chops-crushed-kidney-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/zesty-lamb-chops-with-crushed-kiney-beans-db9235b.jpg','Perk up lamb chops with lemon zest, then serve with mashed red beans with chilli and garlic for a speedy weeknight supper',86,4,1,1,12,'Heat a griddle pan over a high heat. Brush the lamb chops with 1 tbsp oil, then rub all over with the lemon zest and some seasoning. Griddle for 3-4 mins each side for slightly pink meat, or a little longer for well done. You may need to do this in 2 batches. Remove from the pan, wrap in foil and leave to rest for 10 mins. Gently fry the remaining oil, the chillies and garlic in a saucepan for 2-3 mins. Add the kidney beans and gently crush them with a potato masher, then continue to cook for 3 mins until the beans are warm. Stir in the lemon juice, mint and seasoning, and serve with the chops and mixed leaves.','15 mins','20 mins'),
(646,'crunchy garlic chicken','https://www.bbcgoodfood.com/recipes/crunchy-garlic-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crunchy-garlic-chicken-60b790a.jpg','Think chicken breasts are a bit boring? Try this garlic chicken that has the crunch and flavour of chicken kiev, but with a fraction of the prep',77,2,1,1,13,'Heat oven to 200C/180C fan/gas 6. Make a production line of a board, two wide, shallow bowls or dishes, and a baking tray that will comfortably fit the chicken, lined with baking parchment or oiled foil. In the first dish, mix the parmesan, mayo, and flavour shaker with a good pinch of seasoning to form a paste. Tip the crumbs into the second dish and shake so they cover the base. Put the chicken on the board and, if necessary, trim and discard any fat. Blot well with kitchen paper – this will help the coating stick. Slide the first breast into the mayo mix and using a spoon, make sure it is coated all over. Pick it up by the pointy end and place on the crumbs. Use a spoon to cover completely with crumbs and press them on gently until covered all over. Pick up once again by the ‘tail’ and place on the prepared baking tray. Repeat with the second breast, then discard any leftover mayo and crumbs. Bake for 25-30 mins or until golden and cooked through (check the centre of the chicken with the point of a knife). Transfer to a board and slice into three or four thick slices, scatter over the chives and serve with the lemon wedges, roasted potatoes and tomatoes, if you like.','15 mins','25 mins'),
(647,'harissa-spiced chicken with bulgur wheat','https://www.bbcgoodfood.com/recipes/harissa-spiced-chicken-bulgur-wheat','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5308_12-97d7cdd.jpg','This spicy harissa dish will bring vibrant flavours to your table',5,4,1,1,13,'Rub 1 tbsp harissa paste over the chicken. Heat the oil in a deep non-stick pan, then fry the chicken for about 3 mins on each side, until just golden (it won’t be cooked through at this stage). Remove and set aside. Add the onions, then gently fry for 5 mins until soft. Tip in the pine nuts and continue cooking for another few mins until toasted. Tip in the apricots, bulgur and stock, then season and cover. Cook for about 10 mins until the stock is almost absorbed. Return the chicken to the pan, re-cover and cook for 5 mins on a low heat until the liquid has been absorbed and the chicken is cooked through. Fluff up the bulgur with a fork and scatter with the coriander to serve.','10 mins','20 mins'),
(648,'chicken stroganoff','https://www.bbcgoodfood.com/recipes/chicken-stroganoff','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Chicken-Stroganoff-06462c0.jpg','Use chicken thigh fillets if you prefer in this chicken stroganoff, and use half-fat soured cream for a lighter version. Enjoy with pasta, mash or rice.',12,4,1,1,13,'Heat half the oil in a frying pan and fry the chicken pieces over a medium high heat, stirring frequently. until golden brown. Season and transfer to a plate. Heat the remaining oil and lower the heat to medium. Fry the onion for 6-8 mins until softened but not golden, then stir in the garlic and paprika. Fry for a minute until fragrant, then pour in the stock, and add the Dijon and Worcestershire sauce. Tip in the seared chicken with any resting juices, and simmer for 5-6 mins until reduced slightly and the chicken is cooked through. Over a low heat, stir in the soured cream until just combined to create a creamy sauce, and it’s just started to simmer. Scatter over the parsley and serve with rice, mash or pasta, if you like.','15 mins','30 mins'),
(649,'chicken milanese','https://www.bbcgoodfood.com/recipes/chicken-milanese','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Chicken-Milanese-1e4af69.jpg','Make our breaded chicken Milanese - the cheese is optional, but it takes the coating to the next level. You can also make it with turkey, pork or aubergine',7,4,2,1,13,'Cut each chicken breast in half lengthways so you have four fillets. Put the fillets on a board, cover with a sheet of baking parchment and bash with a rolling pin until they are the same thickness all over. Put the flour in a shallow dish and season with a little salt. Tip the egg into another dish, then the breadcrumbs and parmesan, if using, into a third dish, stirring to combine. Working with one chicken fillet at a time, coat in the flour, then dip into the beaten egg, and finally coat in the cheesy breadcrumb mixture. Transfer the fillets to a plate as you go. Will keep covered and chilled for a day, or frozen, stacked between sheets of baking parchment, for three months. To cook, heat the sunflower oil in a large frying pan over a medium heat and cook the breadcrumbed chicken fillets for 3-4 mins on each side (add an extra 1 min each side if cooking from frozen) in batches if needed, until goldenand crisp. Lift onto kitchen paper to drain, then serve with lemon wedges on the side, if you like.','30 mins','20 mins'),
(654,'chicken & bean enchiladas','https://www.bbcgoodfood.com/recipes/chicken-bean-enchiladas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chickenbean-enchiladas-1044331.jpg','A family dinner that delivers four of your 5-a-day - tender chicken, red pepper and black beans, wrapped in tortilla, baked in the oven with passata and cheese',19,4,1,1,13,'Heat oven to 200C/180C fan/gas 6. Heat the oil in a large frying pan and add the onions, peppers and half the garlic. Cook for 15 mins, stirring now and then until the veg has softened. Stir in the chipotle paste, then fry for 1 min more. Add the chicken strips, turning them over in the mixture so they cook through, about 5-10 mins. Stir in the beans, coriander and 150ml of the passata, then take the pan off the heat. Mix the rest of the passata with the remaining crushed garlic and the ketchup. Divide the mixture between the tortillas, then fold the sides in and roll them up. Pour half the passata sauce into a 22cm square baking dish, then place the enchiladas on top, side by side. Dot over the remaining sauce and sprinkle over the grated cheese, then bake for 25-30 mins until golden brown.','10 mins','55 mins'),
(650,'cajun chicken pasta','https://www.bbcgoodfood.com/recipes/cajun-chicken-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Cajun-chicken-one-pot-3f68774.jpg','Make a crowd-pleasing pasta dish for dinner. We’ve taken inspiration from creamy alfredo sauce with Cajun-style seasoning to add a bit of punch.',7,6,1,1,13,'Heat the olive oil in a large, shallow saucepan or deep frying pan over a medium heat, then add the chicken, season lightly and fry for 6-8 mins, stirring occasionally until just golden all over – no need to worry about it being cooked through at this stage. Stir in the garlic and cook for 2 mins more, then scatter over the Cajun-style seasoning and stir so the chicken pieces are evenly coated. Tip in the tomatoes, a quarter of a can of water and the stock cube. Stir, then bring to a simmer and cook for 5 mins. Meanwhile, bring a large pan of salted water to the boil and cook the pasta for a minute less than pack instructions. Stir the cream into the chicken and continue to simmer gently. When the pasta is cooked, drain well and stir into chicken mixture. Finish cooking the pasta in the sauce over a low heat for 2 mins, then stir in the parmesan and lemon juice, and cook for 1 min more. Serve the pasta straight from the pan, or tip into a large serving bowl. Scatter with parsley, if you like, and extra parmesan.','10 mins','30 mins'),
(651,'chicken skewers with broad bean hummus','https://www.bbcgoodfood.com/recipes/chicken-skewers-with-broad-bean-hummus','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Chicken-skewers-with-broad-bean-hummus-8dce2e1.jpg','You can swap chickpeas for broad beans to make the hummus, if you prefer. The skewers can also be barbecued if the sun is shining',4,4,1,1,13,'Mix the oil, oregano, paprika, lemon juice and garlic in a bowl with some black pepper and a pinch of salt, then add the chicken and toss well. Thread the chicken onto four metal skewers and set aside. For the salad, mix the onions and lemon juice in a bowl and set aside. To make the hummus, boil the broad beans in 300ml water for 5 mins, then scoop out of the pan, reserving the water. Tip the beans into a bowl with the rest of the ingredients, and blitz using a hand blender with up to 150ml of the cooking water until smooth. Heat the grill to high and cook the chicken skewers for 4-5 mins on each side, or until cooked through. Meanwhile, finish preparing the salad. Toss the tomatoes and sliced cucumber with the lemon-soaked onions. Serve halve the hummus with two skewers of chicken and half the salad. The remainder will keep in the fridge for up to two days.','25 mins','15 mins'),
(652,'chicken & bacon pasta','https://www.bbcgoodfood.com/recipes/chicken-bacon-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Chicken-and-bacon-pasta-acac96f.jpg','Toss chicken and bacon with ribbons of tagliatelle in a deliciously creamy sauce to make this easy pasta dish. Serve with a green salad for an easy supper',46,4,1,1,13,'Heat the oil and butter in a medium saucepan over a low heat and fry the onion for 10 mins, or until softened and translucent. Add the garlic and cook for 2 mins more. Add the cream, mascarpone, parmesan and stock cube. Give it a stir and add the cooked chicken and bacon to heat through. Meanwhile, cook the pasta following pack instructions. Reserve 100ml of the pasta water. Toss the pasta in the creamy sauce and enough of the reserved water to loosen. Season with black pepper. Top with the parsley and serve with a green salad.','20 mins','20 mins'),
(653,'creamy kale & chicken traybake','https://www.bbcgoodfood.com/recipes/creamy-kale-chicken-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-kale-chicken-traybake-95058bf.jpg','A one-pan supper with bacon-wrapped, succulent chicken breasts and a garlicky, mascarpone stuffing. This everyday recipe is 3 of your 5-a-day and rich in fibre, folate and vitamin C',93,4,1,1,13,'Heat oven to 220C/200C fan/gas 7. Bring a pan of water to the boil and cook the kale for 4 mins until tender. Rinse under cold water, squeeze dry with your hands, then whizz in a food processor with the lemon zest, mascarpone, garlic and seasoning. Cut a long slit in the side of each chicken breast, open it up and stuff with the creamy kale. Gently stretch each rasher of bacon and wrap three around each chicken breast. Toss the vegetables with the oil on a baking tray. Nestle the chicken on top, season and bake for 20-25 mins (depending on the size of the breasts), giving the veg a stir halfway through. Check the chicken is cooked, then place on a plate to rest, covered with foil. Cook the veg for 10 mins more, then serve with the chicken and lemon wedges.','20 mins','35 mins - 40 mins'),
(655,'chinese chicken curry','https://www.bbcgoodfood.com/recipes/chinese-chicken-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-curry-9117113.jpg','Cook our easy, healthy chicken curry in just 15 minutes. Serve this replica of your favourite takeaway dish with fluffy rice for a wholesome family meal',19,4,1,1,13,'Toss the chicken pieces in the cornflour and season well. Set them aside. Fry the onion in half of the oil in a wok on a low to medium heat, until it softens – about 5-6 minutes – then add the garlic and cook for a minute. Stir in the spices and sugar and cook for another minute, then add the stock and soy sauce, bring to a simmer and cook for 20 minutes. Tip everything into a blender and blitz until smooth. Wipe out the pan and fry the chicken in the remaining oil until it is browned all over. Tip the sauce back into the pan and bring everything to a simmer, stir in the peas and cook for 5 minutes. Add a little water if you need to thin the sauce. Serve with rice.','15 mins','40 mins'),
(656,'soy & chilli chicken with peppers & peanuts','https://www.bbcgoodfood.com/recipes/soy-chilli-chicken-with-peppers-peanuts','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Soy-and-chilli-chicken-with-peppers-and-peanuts-a28ee71.jpg','Try our takeaway-style soy and chilli chicken stir-fry with rice on the side or noodles. Ready in just 30 minutes, it''s a perfect Friday night treat',7,4,1,1,13,'Mix together the wine, cornflour and half the soy sauce in a large bowl, then tip in the chicken pieces and toss to coat. You can do this up to a day ahead and keep covered and chilled until needed. Heat half the oil in a wok over a high heat and fry the chicken for 4-5 mins, stirring occasionally, until golden on all sides. Scoop out onto a plate. Heat the rest of the oil in the same wok and fry the peppers and spring onion whites for 1 min. Add the garlic, ginger and chilli, and stir-fry for another minute. Add the sugar, vinegar and the remaining soy. Let everything sizzle for 30 seconds, then tip the chicken and any juices into the wok along with the nuts. Stir-fry for another 1-2 mins until the chicken is cooked through. Stir through the spring onion greens and serve with rice or toss through some cooked noodles, if you like.','15 mins','15 mins'),
(657,'healthy chicken stir-fry','https://www.bbcgoodfood.com/recipes/healthy-chicken-stir-fry','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Healthy-chicken-stir-fry-02a0a0f.jpg','Make this nutritious, veg-packed stir-fry when you need dinner fast – and if you like a hit of spice, add some chilli to the rich peanut dressing',4,2,1,1,13,'Cook the rice following pack instructions, then drain. Heat the oil in a non-stick wok over a high heat and fry the ginger and red onions for 2 mins. Add the broccoli stem, carrots and chilli, if using, and cook for 1 min. Tip in the chicken and cumin, stir-fry briefly, then add the broccoli florets and 3 tbsp water. Cover and leave to steam for 3-4 mins, or until the broccoli florets are just tender and the chicken is cooked through. Meanwhile, mix the peanut butter with the tamari and vinegar. Stir the sauce into the veg and chicken, then serve over the cooked rice.','10 mins','20 mins'),
(658,'chicken, sweet potato & pea curry','https://www.bbcgoodfood.com/recipes/chicken-sweet-potato-pea-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chickensweet-potato-curry-86a5ed8.jpg','Serve this family-friendly chicken curry with rice or roti. It''s tasty and healthy, containing four of your 5-a-day. Add extra korma paste for added spice ',19,4,1,1,13,'Heat the oil in a deep frying pan or wok, stir in the curry paste and fry for 30 seconds until it becomes fragrant. Stir in the chicken, then add the sweet potatoes and lentils and keep stirring to coat everything in the paste. Add 300ml water and the coconut milk. Bring to the boil, then simmer for 15-20 mins or until the chicken and sweet potato are cooked through and the consistency of the sauce is slightly thickened. Tip in the peas, bring back to the boil and simmer for a further 2 mins, then add the tomatoes and cook for 2 mins more. Serve with steamed rice, or roti.','10 mins','30 mins'),
(659,'chicken goujons','https://www.bbcgoodfood.com/recipes/crispy-chicken-strips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-katsu-dippers-faff4ff.jpg','Serve these family-friendly crunchy chicken bites with carrot salad, tomato sauce and a baked potato. Use panko breadcrumbs if you can, as they''re larger and crisper',19,4,1,1,13,'Pour the buttermilk into a wide, shallow bowl. Cut each chicken breast in half horizontally and pat dry with kitchen paper. Turn them over in the buttermilk, leave in the dish and chill for 1 hr. Meanwhile, for the salad, mix the carrot with the lemon juice and olive oil. Mix the sauce ingredients together in another bowl and set both aside. Heat a large, non-stick frying pan, tip in the breadcrumbs and toast in the dry pan for 2-3 mins until pale brown, stirring regularly so they don’t burn. Tip into a bowl and break up any large clumps. Heat oven to 230C/210C fan/gas 8. Tip the crumbs onto a large plate, then lift the chicken breasts from the buttermilk, and roll them in the crumbs, making sure they are coated evenly. Heat ½ tbsp of oil in a non-stick frying pan. Fry the chicken for 1-2 mins over a medium-high heat, then turn it over, adding the rest of the oil and fry for 1 min more. Transfer the chicken to a baking sheet and bake for 5 mins, then rest for 5 mins and cut into strips. Serve the chicken and the carrot salad with the sauce and baked potatoes.','15 mins','15 mins'),
(692,'honey & mustard chicken thighs with spring veg','https://www.bbcgoodfood.com/recipes/honey-mustard-chicken-thighs-spring-veg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/honey-mustard-chicken-thighs-with-spring-veg-6cee676.jpg','This self-saucing one-pot is like a roast dinner without the fuss. Plus it''s rich in iron, fibre and folate',16,2,1,1,13,'Heat oven to 200C/180C fan/gas 6. In a small bowl, mix together the honey, mustard, garlic and the lemon zest and juice. Pour the marinade over the chicken thighs and season. Put the chicken, skin-side up, on a large baking tray, then dot the new potatoes in between them. Drizzle the oil over the potatoes and sprinkle with sea salt. Roast in the oven for 35 mins until the chicken skin caramelises and is charred in places. Add the spinach and peas to the roasting tray. Return to the oven for 2-3 mins until the spinach has begun to wilt and the peas are hot and covered in the mustardy sauce.','10 mins','40 mins'),
(660,'classic chicken parmigiana','https://www.bbcgoodfood.com/recipes/chicken-parmigiana-2','https://images.immediate.co.uk/production/volatile/sites/30/2020/12/Chicken-parmigiana-c13fae7.jpg','Make midweek easier with our family-friendly chicken parmagiana. Ideal for freezing for busy week nights, the recipe is also easily doubled',7,4,2,1,13,'Cut each chicken breast in half lengthways so you have four fillets. Put the fillets on a board, cover with a sheet of baking parchment and bash with a rolling pin until they are the same thickness all over. Put the flour in a shallow dish and season with a little salt. Tip the egg into another dish, then the breadcrumbs and parmesan into a third dish, stirring to combine. Working with one chicken fillet at a time, coat in the flour, then dip into the beaten egg, and finally coat in the cheesy breadcrumb mixture. Transfer the breadcrumbed fillets to a plate as you go. To freeze, stack the fillets in a freezerproof container between sheets of baking parchment. Will keep frozen for three months. To make the sauce, heat the olive oil in a shallow pan and sizzle the garlic for 2 mins. Sprinkle in the sugar and add a splash of the vinegar, then tip in the tomatoes or passata. Add the oregano, season and bubble for 10-15 mins, stirring occasionally until thickened. To freeze, leave to cool completely, then transfer to a freezerproof container (portion it into more than one, if you like). Will keep frozen for up to three months. Defrost overnight in the fridge, then reheat in a pan until piping hot. Heat the sunflower oil in a large frying pan over a medium heat and cook the breadcrumbed chicken fillets for 3 mins on each side (fresh or from frozen) until golden and crisp. Arrange on a baking tray. Spoon a little of the sauce over the middle of each fillet, and top each with a slice of mozzarella. Heat the grill to high. Cook the spaghetti following pack instructions, then drain and toss with the remaining sauce. Grill the fillets for 3-4 mins, or 1-2 mins more if they’re from the freezer, until the mozzarella is melted and bubbling, then serve.','40 mins','30 mins'),
(661,'chicken burrito bowl','https://www.bbcgoodfood.com/recipes/chicken-burrito-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/buddha-bowls-2358192.jpg','Whip up a simple chicken burrito bowl for a satisfying lunch for two. Pile on avocado, sweet potato wedges and tortillas for an easy, filling meal',5,2,1,1,13,'Heat the oven to 200C/180C fan/gas 6. Rub the sweet potato wedges with 1 tbsp oil, season and bake for 20 mins, or until tender but slightly crisp at the edges. Cover to keep warm. Meanwhile, heat the remaining oil in a frying pan, then briefly fry the garlic and most of the onion. Add the chicken and fry for another 7-10 mins, or until the chicken is cooked through. Add the vinegar, honey and black beans and stir to combine, then cook for 2 mins. Add the rice from the pouch, breaking it up as you stir, and warm everything through for 5 mins. Divide the rice mixture between two shallow bowls. Combine the tomato, remaining onion, the lime juice and zest and the coriander. Arrange half of the avocado over each bowl along with half of the sweet potato wedges. Spoon on some of the salsa and top with some tortilla strips. Serve with hot sauce and lime wedges, if you like.','15 mins','30 mins'),
(662,'chicken provençal with olives & artichokes','https://www.bbcgoodfood.com/recipes/chicken-provencal-olives-artichokes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-provencal-with-olives-artichokes-64f2716.jpg','Cater for a crowd with this easy, hearty chicken casserole that evokes summer in Provence using a fragrant selection of herbs, tomatoes, olives and artichokes',52,5,1,1,13,'Tip the chicken into a large bowl or dish and add the garlic, wine, 2 tbsp oil and the thyme. Season with 2 tbsp flaky salt and mix well until completely coated. Cover and chill for at least a few hours, but preferably overnight. Heat grill to high. Arrange the plum tomatoes on a tray, cut-side up, drizzle with the remaining 1 tbsp oil and grill for 15 mins, or until charred. Set aside. Heat 1 tbsp oil in a large casserole dish or flameproof roasting tin. Scrape the marinade off the chicken pieces back into the bowl and fry the chicken in batches for about 15 mins until the skin is golden and crisp – watch out, the oil has a tendency to sputter. Remove the chicken from the dish and pour off the oil. Place the dish back on the heat, add the tomato purée and cook for 2 mins, then add the herbs, fennel seeds, remaining marinade and chicken stock. Bring up to the boil, add the grilled tomatoes, then simmer gently for 20 mins. Heat oven to 160C/140C fan/gas 4. Nestle the chicken pieces back into the dish, then stir them in to coat in the sauce. Scatter over the celery, button onions and artichokes. Bring up to a simmer, pop the lid on, then transfer to the oven and cook for 1 hr. Remove the lid and stir in the olives. Season to taste and leave everything to settle for 15 mins before serving straight from the dish.','15 mins','1 hr and 40 mins'),
(663,'spring chicken in a pot','https://www.bbcgoodfood.com/recipes/spring-chicken-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513635_11-975dc34.jpg','Casseroles aren''t just for winter – this light, vibrant one-pot is packed with spring veg and herby pesto',43,4,1,1,13,'Heat the oil in a large, heavy pan. Add the onion, gently fry for 5 mins until softened, add the chicken, then fry until lightly coloured. Add the potatoes, stock and plenty of freshly ground black pepper, then bring to the boil. Cover, then simmer for 30 mins until the potatoes are tender and the chicken is cooked. Can be frozen at this point. Add the broccoli, spring greens, petit pois and spring onions, stir well, then return to the boil. Cover, then cook for 5 mins more, stir in the pesto and heat through.','20 mins','45 mins'),
(733,'lamb chops with chilli & ligurian broad bean pesto','https://www.bbcgoodfood.com/recipes/lamb-chops-chilli-ligurian-broad-bean-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb-chops-with-chilli-ligurian-broad-bean-pesto-8a683ab.jpg','Impress dinner guests with these lightly spiced lamb chops served on a bed of broad bean pesto. Serve with fennel fronds, a drizzle of olive oil and lemon',70,6,2,1,12,'Put the chops in a large bowl with the olive oil, lemon juice, chilli and some black pepper. Turn them with your hands, cover and put in the fridge to marinate for a few hours. Shell the broad beans and cook them in boiling water for 2-3 mins until tender. Drain and rinse in cold water. Slip each bean out of its skin, put them in the bowl of a food processor and add the garlic, mint and anchovy fillets. Remove any fronds from the fennel and set them aside with the tips. Take off any coarse outer layer, split the fennel in half and remove the hard core from each piece. Discard these. Chop the flesh and the reserved fennel trimmings. Heat 2 tbsp of the oil in a small frying pan and fry the fennel flesh gently for 10 mins until soft. Tip the fennel into the food processor along with the cheese and a little seasoning. Blitz to a purée while pouring in 120ml of the olive oil. Taste and add the lemon juice. You might find you want a bit more olive oil as well, or a little water to produce a thinner consistency. Remember to season again if you add water. Scrape the purée into a bowl and keep covered in the fridge until you’re ready to serve, but let it come to room temperature before you cook the chops. Take the chops out of the fridge and bring to room temperature. Heat a griddle pan over a high heat. Lift the chops out of the marinade and, when the pan is really hot, cook until well coloured on each side, seasoning as you go. Press the meaty bit down so that they get good griddle marks. The chops should be pink in the middle when you serve them – they only take 1½ mins on each side as they’re so small. Do this in batches if you need to. Serve the chops on top of the purée with some mint scattered over, any reserved fennel fonds, a drizzle of extra virgin olive oil and some lemon wedges on the side.','40 mins','20 mins'),
(664,'chicken & chorizo ragu','https://www.bbcgoodfood.com/recipes/chicken-chorizo-ragu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-chorizo-ragu-95dc274.jpg','Serve this delicious chicken and chorizo ragu over rice or pasta as an easy midweek dinner for the family. You can freeze any leftovers for another day',41,4,1,1,13,'Fry the chorizo over a medium heat in a large saucepan or flameproof casserole dish for 5 mins or until it releases its oil and starts to char at the edges. Add the onion and fry for 5 mins more or until soft. Tip in the garlic and cook for 2 mins before stirring in the paprika and sundried tomatoes. Add the chicken thighs and fry for 2 mins each side until they are well coated in the spices and beginning to brown. Pour in the chopped tomatoes and stock, and turn the heat down. Cover and cook for 40 mins until the chicken is falling apart and the sauce is thick. Stir the lemon juice through. Serve by piling spoonfuls of the ragu into hot jacket potatoes with parsley sprinkled over and a dollop of soured cream, if you like.','15 mins','40 mins'),
(665,'slow-cooker chicken casserole','https://www.bbcgoodfood.com/recipes/slow-cooker-chicken-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-chicken-casserole-1172edc.jpg','Treat the family to this wholesome chicken casserole that delivers comfort while being low-fat. And you can let a slow cooker do most of the work',44,2,1,1,13,'Heat a knob of butter and ½ tbsp rapeseed or olive oil in a large frying pan, cook 1 finely chopped large onion for 8-10 mins until softened and starting to caramelise. Meanwhile, put 1 ½ tbsp flour and a little salt and pepper in a bowl and toss 650g boneless, skinless chicken thigh fillets in it. Add 3 crushed garlic cloves and the chicken to the pan and cook for 4-5 mins more until the chicken is starting to brown. Transfer to your slow cooker, along with 400g halved baby new potatoes, 2 diced celery sticks, 2 diced carrots, 250g quartered mushrooms, 15g dried and soaked porcini mushrooms with the 50ml soaking liquid, 500ml chicken stock, 2 tsp Dijon mustard and 2 bay leaves. Give it a good stir. Cook on Low for 7 hours or High for 4 hours. Remove the bay leaves and serve with a little Dijon mustard on the side.','10 mins','4 hrs and 15 mins - 7 hrs and 15 mins'),
(666,'easy chicken casserole','https://www.bbcgoodfood.com/recipes/chicken-white-bean-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192693_11-a5d83e6.jpg','This flavoursome, low-fat chicken casserole is easy to make and freezes really well, so why not make double and freeze for speedy midweek meals?',43,4,1,1,13,'Heat the oil in a large pan, add the chicken, then fry until lightly browned. Add the veg, then fry for a few mins more. Stir in the herbs and stock. Bring to the boil. Stir well, reduce the heat, then cover and cook for 40 mins, until the chicken is tender. Stir the beans into the pan, then simmer for 5 mins. Stir in the parsley and serve with crusty bread.','20 mins','1 hr'),
(667,'honey mustard chicken pot with parsnips','https://www.bbcgoodfood.com/recipes/honey-mustard-chicken-pot-parsnips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513710_11-4471483.jpg','For an easy, warming family casserole on a budget, this one-pot ticks all the boxes',5,4,1,1,13,'Heat half the oil in a large frying pan or shallow casserole with a lid. Brown the chicken until golden, then set aside. Heat the remaining oil, then cook the onions for 5 mins until softened. Nestle the thighs back amongst the onions and add the parnips. Mix the stock with the mustard and honey, then pour in. Scatter over the thyme, then bring to a simmer. Cover, then cook for 30 mins (or longer, see tip) until the chicken is tender, then season. Serve with steamed greens.','5 mins','40 mins'),
(668,'one-pot chicken chasseur','https://www.bbcgoodfood.com/recipes/one-pot-chicken-chasseur','https://images.immediate.co.uk/production/volatile/sites/30/2010/07/Chicken-chasseur-41ef293.jpg','This French bistro classic is easy to make at home and fabulous with creamy mash or crusty bread',50,4,1,1,13,'Heat 1 tsp olive oil and half of the 25g butter in a large lidded casserole. Season 4 chicken legs, then fry for about 5 mins on each side until golden brown. Remove and set aside. Melt the remaining butter in the pan. Add 1 chopped onion, then fry for about 5 mins until soft. Add 2 crushed garlic cloves, cook for about 1 min, add 200g small button or chestnut mushrooms, cook for 2 mins, then add 225ml red wine. Stir in 2 tbsp tomato purée, let the liquid bubble and reduce for about 5 mins, then stir in 2 thyme sprigs and pour over 500ml chicken stock. Slip the chicken legs back into the pan, then cover and simmer on a low heat for about 1 hr until the chicken is very tender. Remove the chicken legs from the pan and keep warm. Rapidly boil down the sauce for 10 mins or so until it is syrupy and the flavour has concentrated. Put the chicken legs back into the sauce and serve.','20 mins','1 hr and 30 mins'),
(1092,'chocolate owl cake','https://www.bbcgoodfood.com/recipes/chocolate-owl-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1079489_11-2772627.jpg','A fun chocolate birthday cake decorated with buttons, biscuits and marzipan - bound to be a hit with young children',94,25,1,2,11,'Preheat the oven to 160C/fan 140C/gas 4. Base line a 20cm round and a 18cm square cake tin. Put the butter and chocolate into a saucepan and stir over a very gentle heat until melted and smooth. Remove from the heat and stir in the vanilla extract. Sieve the flour, baking powder and bicarbonate of soda into a large bowl. Add the sugar, breaking up any lumps if necessary. Beat together the eggs and soured cream and pour onto the flour mixture. Add the melted chocolate and mix all well together until smooth. Divide the mixture equally between the two tins and bake in the oven for about 55 mins - 1 hour. A good way to check that the mixture is equally divided is to put a cocktail stick into each mixture and the levels should be the same. To make the icing, put the butter into a bowl and beat until creamy. Add the icing sugar and cocoa a little at a time beating well between each addition. Add the milk to mix to a soft spreading consistency. To assemble, place the round cake on a large wooden board or tray (the cake will need to served on this). Place the round cake tin on the square cake about 12cm from one edge and cut around the edge of the tin. Remove the smaller piece and place at one end of the round cake to represent the ears. Using the remains of the square cake cut almost in half to give an oblong (for the fence) and a semi circle (for the wings). Place the oblong at the bottom of the owl cake. Slice the semi circle in half horizontally through the middle. Arrange one piece each side of the round cake to make the wings. Spread the butter icing all over the cake. Cut the flake bars into small pieces and arrange on the wings to represent feathers. Keep the flake crumbs. Arrange milk chocolate buttons over the body. Place chocolate fingers on the bottom cake to make the fence. Using a fork, mark the icing to make the owl’s ears. Use the flake crumbs to make a v-shape between the ears like a ''widow’s peak''. Arrange the white chocolate buttons in two circles to make the eyes and place a milk chocolate button in the centre of each. Use a third of the marzipan to shape a beak and use the remaining marzipan to make two feet. Place the candles on the ‘fence’ part of the cake. You can also pipe a child’s name or message on the fence using writing icing.','1 hr','1 hr'),
(669,'mexican chicken stew with quinoa & beans','https://www.bbcgoodfood.com/recipes/mexican-chicken-stew-quinoa-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-889478_10-e0864f1.jpg','Spicy chicken casserole with a healthy superfood side dish of protein-rich quinoa',17,4,1,1,13,'Heat the oil in a deep frying pan and fry the onions and peppers for a few mins until softened. Stir in the chipotle paste for a minute, followed by the tomatoes. Add up to a tomato can-full of water to cover the chicken and bring to a gentle simmer. Add the chicken breasts and gently simmer, turning the chicken occasionally, for 20 mins until the chicken is cooked through. Bring a large saucepan of water to the boil with the stock cubes. Add the quinoa and cook for 15 mins until tender, adding the beans for the final min. Drain well and stir in the coriander and lime juice, then check for seasoning before covering to keep warm. Lift the chicken out onto a board and shred each breast using two forks. Stir back into the tomato sauce with the sugar and season. Serve with the quinoa, scattering the stew with some coriander leaves just before dishing up and eating with a dollop of yogurt on the side.','25 mins','30 mins'),
(670,'chicken & red wine casserole with herby dumplings','https://www.bbcgoodfood.com/recipes/chicken-red-wine-casserole-herby-dumplings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1073467_11-5c9e5a7.jpg','A rich and warming red wine chicken casserole - ideal for when it''s cold and dark outside.',38,6,2,1,13,'Preheat the oven to fan 180C/ conventional 200C/gas 6. Season the chicken with salt and freshly ground black pepper, then coat lightly in a little of the flour. Heat the oil in a large ovenproof lidded casserole dish and, in batches, brown the chicken on both sides over a high heat. Remove the chicken and set aside. Reduce the heat, add the onions and lardons and cook for about 5-8 minutes so they are golden tinged. Add the garlic, then sprinkle in the plain flour and cook for 1 minute, stirring to prevent sticking. Add the mushrooms, bay leaves, redcurrant sauce and orange zest, then pour in the red wine and stock and season with salt and pepper. Bring to the boil, then return the chicken to the casserole dish, making sure it is well covered with the liquid. Put on the lid and cook in the oven for 30 minutes. While the casserole is cooking, prepare the dumplings. Put the self raising flour, breadcrumbs, mustard and butter in a food processor and blitz to a crumb consistency. Add the thyme, parsley, eggs and salt and pepper. Briefly blitz until the mixture forms a fairly moist dough. Using floured hands, roll the dough into 6 large, even-sized balls. Remove the casserole from the oven when the 30 minutes is up and sit the dumplings on top. Pop the lid back on and return to the oven for a further 20 minutes, until the casserole is cooked and the dumplings have puffed up. Spoon the chicken and sauce onto six plates and top each with a dumpling. Serve with a rich, fruity red wine. If you want to use a slow cooker, brown the chicken in batches and transfer to the slow cooker pot. Add the flour, onions, lardons, garlic, mushrooms, bay leaves, redcurrant, orange zest, red wine and stock, and season. Cover and cook on High for 4 hours. Make the dumplings according to step 4, then sit on top of the casserole after 4 hours. Cook for another 1-2 hours, then serve.','50 mins','No Time'),
(671,'rosemary chicken with tomato sauce','https://www.bbcgoodfood.com/recipes/rosemary-chicken-tomato-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513844_11-755cbc3.jpg','This Italian-style casserole will warm you up, and it''s great value too',5,4,1,1,13,'Heat half the oil in a non-stick pan, then brown the chicken all over. Add half the chopped rosemary, stir to coat, then set aside on a plate. In the same pan, heat the rest of the oil, then gently cook the onion for about 5 mins until soft. Add the garlic, anchovies and remaining rosemary, then fry for a few mins more until fragrant. Pour in the tomatoes and capers with the wine, if using, or 75ml water if not. Bring to the boil, then return the chicken pieces to the pan. Cover, then cook for 20 mins until the chicken is cooked through. Season and serve with a crisp green salad and crusty bread.','5 mins','30 mins'),
(672,'chicken and mushrooms','https://www.bbcgoodfood.com/recipes/chicken-mushrooms','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1096468_12-76a4839.jpg','A healthy and low calorie chicken casserole with bacon, button mushrooms, peas and a parsley sauce - use chicken thighs for extra flavour and juiciness',10,4,1,1,13,'Heat 1 tbsp oil in a frying pan. Season and dust the chicken with flour, brown on all sides. Remove. Fry the pancetta and mushrooms until softened, then remove. Add the final tbsp oil and cook shallots for 5 mins. Add the stock and vinegar, bubble for 1-2 mins. Return the chicken, pancetta and mushrooms and cook for 15 mins. Add the peas and parsley and cook for 2 mins more, then serve.','15 mins','25 mins'),
(673,'mexican chicken stew','https://www.bbcgoodfood.com/recipes/mexican-chicken-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1068489_11-2da4c12.jpg','Bring a touch of Mexican flavour to your midweek meal with this low-fat, one-pan supper',5,4,1,1,13,'Heat the oil in a medium saucepan. Add the onion and cook for 5 mins or until softened and starting to turn golden, adding the garlic for the final min. Stir in the sugar, chipotle paste and tomatoes. Put the chicken into the pan, spoon over the sauce, and simmer gently for 20 mins until the chicken has cooked (add a splash of water if the sauce gets too dry). Remove the chicken from the pan and shred with 2 forks, then stir back into the sauce. Scatter with a little red onion, the coriander, and serve with remaining red onion, tortillas or rice. If you want to use a slow cooker, cook the onion and garlic as above, then put into your slow cooker with the sugar, chipotle, tomatoes and chicken. Cover and cook on High for 2 hours. Remove the chicken and shred then serve as above.','20 mins','25 mins'),
(674,'slow-cooker spanish chicken','https://www.bbcgoodfood.com/recipes/slow-cooker-spanish-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-spanish-chicken-4932469.jpg','This slow-cooked stew is packed with Mediterranean flavour from chorizo, olives, tomatoes and peppers. The meat will fall off the bone – mop up the sauce with bread',4,6,1,1,13,'Heat 2 tbsp olive oil in a large frying pan. Fry 1 sliced Spanish onion for about 5 mins until golden. Tip into the slow cooker pot (we used a 6.5-litre model), then fry 12 large bone-in skinless chicken thighs and 225g thickly sliced chorizo picante in the same pan until starting to colour – you will need to do this in two batches. Add to the slow cooker with 3 mixed colour peppers, cut into chunks and 150g pitted Spanish pimento stuffed green olives. Tip 300ml dry white wine, 300ml chicken stock and 1 tbsp tomato purée into the pan. Scrape up any bits stuck to the bottom, then tip into the slow cooker, cover and cook on Low for 6 hrs.','15 mins','6 hrs and 20 mins'),
(675,'pesto chicken stew with cheesy dumplings','https://www.bbcgoodfood.com/recipes/pesto-chicken-stew-cheesy-dumplings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1085527_11-ad12e39.jpg','A slow-cooked chicken casserole with Italian basil flavours that makes a perfect weekend one-pot',5,8,1,1,13,'Heat the oil in a large casserole dish. Brown the chicken until golden on all sides – you might have to do this in batches – remove the chicken from the pan as you go and set aside. Add the lardons to the pan and sizzle for a few mins, then add the onion, celery and leeks, and cook over a medium heat for 8-10 mins until the vegetables have softened. Stir in the flour, season and cook for a further 2 mins. Gradually stir in the wine and allow it to bubble away, then stir in the stock. Return the chicken to the pan with the bay leaves and cover with a lid. Reduce the heat and simmer gently for 1½ hrs or until the chicken is tender. The stew can now be cooled and frozen if you’re making ahead. Just defrost thoroughly, then gently warm through back in the pan before continuing. Heat oven to 200C/180C fan/gas 6. Add the peas, sundried tomatoes, pesto and basil to the stew. To make the dumplings, rub the butter into the flour until it resembles fine breadcrumbs. Mix in the grated cheese and add 150ml water, mixing with a cutlery knife to bring the crumbs together to form a light and sticky dough. Break off walnut-sized lumps and shape into small balls. Roll the tops of the dumplings in the pine nuts so a few stick to the outside, then place the dumplings on top of the stew and scatter with any remaining nuts. Put the dish in the oven and bake for 25 mins until the dumplings are golden brown and cooked through. Serve with mashed potato and extra veg if you like.','50 mins','2 hrs and 20 mins'),
(676,'moroccan chicken stew','https://www.bbcgoodfood.com/recipes/moroccan-chicken-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/moroccan-chicken-stew-2f0a4e9.jpg','This hearty stew, full of vibrant veg and warming spices, is a Hemsley sister favourite. Serve with a side of cauliflower tabbouleh as a light accompaniment',95,4,2,1,13,'In a large, dry pan, gently toast the almonds for 2 mins until golden – don’t take your eyes off them, as they burn easily – then set aside. In the same pan, heat the ghee and gently cook the onions for 8 mins until softened. Add the garlic, ginger and spices, and fry for 1 min more. Add the chicken thighs, skin-side down, and cook until the skin is golden and crisp, then turn and cook to lightly golden on the flesh side. Add the red peppers, lemon slices, olives, stock or bone broth and dates or apricots (and chilli, if using). Simmer with the lid on for about 40 mins until the chicken is cooked through. If you find the sauce is too watery, take off the lid and leave it to reduce a little. If the sauce is too thick, add a few more tbsps of water. Add the green beans for the final 4 mins of cooking time. Season to taste and top with the coriander, parsley and the toasted almonds to serve.','15 mins','40 mins'),
(677,'chicken tagine with spiced brussels sprouts & feta','https://www.bbcgoodfood.com/recipes/chicken-tagine-spiced-brussels-feta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tagine-0fb6a87.jpg','Joe Wicks’ tasty sprout recipe balances earthy, sweet and spicy flavours, using tender chicken thighs, chickpeas and cheese for a hearty festive stew',96,4,1,1,13,'Heat 1 tbsp of the coconut oil in a large flameproof casserole dish over a medium heat. When melted and hot, add the onion, pepper and garlic. Cook, stirring regularly, for 3 -4 mins or until just starting to soften. Increase the heat to maximum and add the chicken thighs. Fry everything together for about 3 mins, stirring occasionally. Sprinkle in the spices, squeeze in the tomato purée and fry, stirring almost constantly, for 1 min. Pour in the stock and bring to the boil. Reduce the heat to a simmer and partially cover with a lid. After 30 mins, add the dried apricots and chickpeas, and continue to simmer for a further 10 mins. While the tagine is bubbling away, heat the remaining 1/ 2 tbsp of coconut oil in a frying pan over a high heat. When melted, add the cumin seeds, toast for 10 secs, then add the shredded sprouts. Fry the sprouts over the high heat, stirring almost constantly, for 5 mins, by which time they should have softened and browned in places. Serve the tagine in a large bowl, scatter over the fried sprouts, crumble over the feta and finish with the coriander.','10 mins','35 mins'),
(678,'summer chicken stew','https://www.bbcgoodfood.com/recipes/summer-chicken-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/summer-chicken-stew-db5129f.jpg','One-pots aren''t just for winter, try this fresh summer version with leeks, green beans, peas, new potatoes and tender chicken thighs',9,4,1,1,13,'Heat the oil in a large casserole dish over a medium heat. Add the leeks, garlic and thyme, cover and cook gently for 10 mins, stirring occasionally. Season the chicken and tip into the dish with the potatoes. Turn up the heat, pour in the stock and bring to a simmer. Reduce the heat and allow to gently bubble with the lid on for 35 mins. Add the green beans and peas for the final 10 mins of cooking. Season to taste, then ladle into bowls or lipped plates with a squeeze of lemon.','10 mins','55 mins'),
(1173,'brie, apple & onion tart','https://www.bbcgoodfood.com/recipes/brie-apple-onion-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/brie-apple-onion-tart-9ff84b4.jpg','Apples and cheese make perfect partners in this puff pastry tart, ideal for using up the remnants of a cheeseboard or leftover cranberry sauce. Serve hot or cold for a Boxing Day buffet',6,8,1,2,14,'Heat oven to 200C/180C fan/gas 6. Unroll the pastry sheet onto a large baking tray (leave it on the paper from the pack). If the edges are not straight, trim them with a sharp knife. Score a border, about 1cm in from the edge. Score a criss-cross pattern over the central piece of pastry (this will prevent it from rising too much) and around the border, if you like. Bake for 15 mins. Meanwhile, heat the oil in a large pan. Add the onions and cook for 10-15 mins until softened and starting to caramelise. Stir in the vinegar, bubble for 1 min more, then add the Dijon mustard and thyme, season well and set aside. Slice the apple thinly through the core so a few slices have a nice star in the middle, and remove any seeds. Toss the slices in a splash of vinegar to prevent them from browning. The pastry should be puffed and starting to colour. Push down the centre, then spread over the onions. Add blobs of cranberry sauce, then top with the apple slices and brie, overlapping them to cover the tart. Add a few more thyme sprigs, drizzle with a little oil, then put back in the oven for a further 25 mins until the cheese is bubbling and the apple slices are soft. Serve warm or cold.','20 mins','40 mins'),
(679,'lighter chicken cacciatore','https://www.bbcgoodfood.com/recipes/lighter-chicken-cacciatore','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lighter-chicken-cacciatore-c94e376.jpg','The classic Italian ''hunter''s stew'' gets a healthy makeover, with low-fat chicken breasts, prosciutto and a rich herby tomato sauce',37,4,1,1,13,'Heat the oil in a large non-stick frying pan. Tip in the prosciutto and fry for about 2 mins until crisp. Remove with a slotted spoon, letting any fat drain back into the pan, and set aside. Put the onion, garlic and herbs in the pan and fry for 3-4 mins. Spread the onion out in the pan, then lay the chicken breasts on top. Season with pepper and fry for 5 mins over a medium heat, turning the chicken once, until starting to brown on both sides and the onion is caramelising on the bottom of the pan. Remove the chicken and set aside on a plate. Raise the heat, give it a quick stir and, when sizzling, pour in the wine and let it bubble for 2 mins to reduce slightly. Lower the heat to medium, return the prosciutto to the pan, then stir in the tomatoes (breaking them up with your spoon), tomato purée and mushrooms. Spoon 4 tbsp of water into the empty tomato can, swirl it around, then pour it into the pan. Cover and simmer for 15-20 mins or until the sauce has thickened and reduced slightly, then return the chicken to the pan and cook, uncovered, for about 15 mins or until the chicken is cooked through. Season and scatter over the parsley to serve.','15 mins','50 mins'),
(680,'chicken & olive casserole','https://www.bbcgoodfood.com/recipes/chicken-olive-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-stew-60372a9.jpg','A simple midweek chicken stew with rich tomato, olive and caper sauce. Serve with rice or bread',42,4,1,1,13,'Heat the oil in a large saucepan or flameproof casserole dish on a medium heat, add the onion and cook for a few mins. Add the garlic and cook for 1 min more. Remove the onion and garlic from the pan and set aside on a plate. Turn up the heat, add the chicken and cook for a few mins each side until golden. Return the onion and garlic to the pan with the remaining ingredients. Cover and cook for 20 mins until the chicken is cooked through, juicy and tender, and the sauce is rich and thickened. Serve with rice.','10 mins','30 mins'),
(681,'easy chicken gumbo','https://www.bbcgoodfood.com/recipes/chicken-gumbo-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-gumbo-549dc50.jpg','A warming Cajun-inspired casserole with smoked bacon, peppers and okra - perfect served over rice and beans',29,4,1,1,13,'Heat the oil in a large pan, add the lardons and onion, and fry for 5 mins until the bacon is starting to crisp and the onion is soft. Add the peppers, chicken and flour, and fry for a further 5 mins or until the chicken is golden brown and the flour is starting to take on a a biscuty colour. Stir in the Cajun spice mix and garlic, then slowly add the chicken stock, stirring all the time. Add the okra, passata and some seasoning and simmer for 20 mins, uncovered, over a medium heat. Stir frequently during cooking to loosen the flour from the base of the pan. Sprinkle over the spring onions and serve with rice and black beans.','15 mins','40 mins'),
(682,'chicken & chorizo rice pot','https://www.bbcgoodfood.com/recipes/chicken-chorizo-rice-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-954452_11-fc1175f.jpg','A one-pot chicken casserole for sharing, flavoured with Spanish sausage and slow-cooked with rice - an alternative paella',50,4,1,1,13,'Heat the oil in a large flameproof casserole dish and brown the chicken pieces on all sides – you may have to do this in batches. Remove from the dish and put to one side. Lower the heat, add the onion and pepper, and gently cook for 10 mins until softened. Add the garlic and chorizo, and cook for a further 2 mins until the chorizo has released some of its oils into the dish. Stir in the tomato purée and cook for 1 min more. Return the chicken pieces to the dish along with the thyme, white wine and stock. Bring the liquid to a boil, cover the dish with a tight-fitting lid and lower the heat. Cook for 30 mins. Tip in the rice and stir everything together. Cover, set over a low heat and cook for a further 15 mins, or until the rice is cooked and has absorbed most of the cooking liquid. Remove from the heat and leave the dish to sit for 10 mins to absorb any remaining liquid. Season to taste and scatter with parsley to serve.','20 mins','1 hr and 20 mins'),
(693,'ginger chicken udon noodles','https://www.bbcgoodfood.com/recipes/ginger-chicken-udon-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2023/02/Chicken-udon-809159e.jpg','Serve up these fragrant and light noodles with chicken, beansprouts and cabbage in 30 minutes. With fiery ginger and red chilli, it''s full of flavour',15,2,1,1,13,'Heat the oil in a large, deep frying pan over a medium-high heat and, once hot, stir-fry the chicken and cabbage for 5-7 mins until browned and almost cooked through. Add the ginger and chilli, and cook for a few minutes more until fragrant. Add the remaining ingredients, except the coriander and pickled ginger, and fry until the chicken is cooked and the noodles are tender, about 1-2 mins more. Season, adding more soy, vinegar or mirin, if you like. Top with the coriander and pickled ginger, if using.','10 mins','20 mins'),
(683,'thai minced chicken salad','https://www.bbcgoodfood.com/recipes/thai-minced-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273669_8-6f61db9.jpg','Known as larb gai in Thailand, the hot spiciness in this chicken salad recipe works really well with the crunchy veg, creating a perfect balance of flavour and heat',5,4,1,1,13,'Roughly chop the lemongrass, lime leaves, red chillies, garlic and ginger, then throw them all into a processor and blitz until everything is very finely chopped together. Mince the chicken breasts into tiny pieces. Heat a wok over a high heat and add the vegetable oil and the sesame oil. Throw in the lemongrass mixture and fry briefly before adding the minced chicken and the chilli powder. Stir-fry the chicken for 4 mins then splash in the fish sauce. Turn down the heat a little and allow the chicken and fish sauce to bubble together for another 4 mins, stirring, then add the chopped red onion and cook for another min. Remove from the heat, pour over the lime juice and toss in the herbs. Serve with the salad veg and a lime wedge on the side.','40 mins','10 mins'),
(684,'spicy mince & lettuce cups','https://www.bbcgoodfood.com/recipes/spicy-mince-lettuce-cups','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424667_11-3b7ba14.jpg','A Thai inspired-dish which is quick, simple, cheap and low fat – yet still feels special and is full of flavour. A great pre-dinner nibble',7,4,1,1,13,'Heat the oil in a large frying pan. Fry the ginger, garlic and chillies for 1 min. Add the mince, then cook on a high heat until golden brown, breaking it up with a wooden spoon as you go. Sprinkle over the brown sugar, fish sauce, lime juice and shredded lime leaves, then cook everything down until sticky. Tip the mince into a serving bowl, then serve with a bowl of lettuce leaves for wrapping the mince in; the herbs, shallots and peanuts for scattering over; and the lime wedges for squeezing.','10 mins','15 mins'),
(685,'mini chicken bagel burgers','https://www.bbcgoodfood.com/recipes/mini-chicken-bagel-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7372_11-76964fd.jpg','These mini burgers are great fun for parties, or make them bigger and have them for a fun Friday supper',5,12,1,1,13,'Heat grill to medium. Whizz the chickpeas in a food processor or mash until well broken up. Put into a bowl with the onion, garlic, mince and sweet potato. Mix well and season. Can be frozen for up to 1 month. Line a baking sheet with foil. Divide the mixture into 12 and shape into mini burgers. Put onto the baking sheet, brush with oil and grill for 10 mins, turning once. Can be chilled and reheated in the microwave on High for 2 mins. Split the bagels and toast under the grill, if you like. Spread the bases with mayonnaise, top with a chicken burger, some shredded lettuce, tomato, cucumber and the bagel tops.','15 mins','10 mins'),
(686,'spicy meatballs','https://www.bbcgoodfood.com/recipes/spicy-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-364491_12-23f51ee.jpg','These are great to make with the kids. Teach them about handling raw meat and using different flavours',5,6,1,1,13,'Heat oven to 180C/fan 160C/gas 4. Put the mince into the mixing bowl. Add the onions, garlic, curry powder, cumin, garam masala, paprika or cayenne pepper and coriander, then mix well. By adding these spices, you’ll get a delicious flavour without having to add any salt. Add the beaten egg and breadcrumbs, then mix again. Divide the meat mixture into 15-18 evensized pieces and shape into balls (they should be about the size of a walnut). Always wash your hands thoroughly after handling raw meat so you don’t transfer any germs that may be on the meat to other food or equipment. Heat the oil in the frying pan over a medium heat and add the meatballs using a spoon. Cook them for 5 mins, turning until golden brown. Remove from the pan and place them on to the tray. Bake in the oven for 15-20 mins. Remove from the oven. Remember to use oven gloves! Allow to cool slightly and serve with a fresh, crisp green salad, some pitta bread and tomato salsa.','15 mins','25 mins'),
(687,'indian turkey with spinach & new potatoes','https://www.bbcgoodfood.com/recipes/indian-turkey-spinach-new-potatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-539_11-c1b3fbe.jpg','Spice up turkey mince with this tasty Indian recipe',5,4,1,1,13,'Boil the new potatoes until tender. Drain. Heat the vegetable oil in a large pan and cook 1 finelythe chopped onion for 5-6 minutes. Add the garlic cloves and minced turkey and cook for 4-5 minutes. Stir in the Madras curry paste and cook for 1 minute. Stir in the chopped tomatoes and chicken stock, cover and cook for 20 minutes. Stir in the potatoes and baby spinach and heat through for 3-4 minutes.','40 mins','No Time'),
(1536,'avocado with virgin mary dressing','https://www.bbcgoodfood.com/recipes/avocado-virgin-mary-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-with-virgin-mary-dressing-12e6025.jpg','After a tasty, healthy snack or a starter for entertaining? This avocado dish contributes two of your five-a-day and takes mere minutes to prepare',4,2,1,3,15,'Put the tomatoes in a bowl with the Worcestershire and chilli sauces and the celery salt. Blitz to a purée with a hand blender then spoon into the avocado, top with the olives and eat with a spoon. Best eaten straightaway.','5 mins','No Time'),
(688,'hoisin chicken in crisp lettuce','https://www.bbcgoodfood.com/recipes/hoisin-chicken-crisp-lettuce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3549_12-80faaa6.jpg','A healthy and light chicken dish that''s perfect for a buffet',97,8,1,1,13,'In a bowl, mix the sherry and cornflour to a smooth liquid. Add the soy and hoisin sauces, sugar and stock. Set aside. Heat the oil in a wok or large frying pan, toss in the garlic and whites of the spring onions and stir fry for 2-3 minutes. Tip in the chicken and stir fry over a high heat until it colours, using the back of a spoon to break up any big lumps. Tip in the water chestnuts and stir fry for a further 1-2 minutes. Push the chicken mixture to one side of the wok. Pour the sherry mixture into the empty part and stir for 1-2 minutes until it bubbles and thickens. Combine thoroughly, then leave to simmer for 5-10 minutes. Season and stir in the green spring onions and coriander. Lay the lettuce leaves on serving plates, spoon in the warm mixture without overfilling, and scatter with the shredded spring onion. Suggest your guests roll the lettuce up around the filling to make a shape that’s easy to eat as finger food. Prepare ahead; you can cook the filling the day before, leave to cool and store in a plastic container in the fridge overnight. Reheat it well for 5-10 minutes until hot, stirring all the time and adding a splash of hot water if it’s too dry.','20 mins - 30 mins','30 mins'),
(689,'minced chicken & prawn lettuce parcels','https://www.bbcgoodfood.com/recipes/minced-chicken-prawn-lettuce-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/minced-chicken-prawn-lettuce-parcels-e299505.jpg','These bite-sized lettuce cups are packed with fresh Asian flavours such as lemongrass and chilli - perfect as a light starter or canapés',5,6,2,1,13,'Soak the glass noodles in boiling water and leave for 5 mins, then drain and run under cold water. Using scissors, cut into little pieces. Heat the oil in a wok, add the garlic, chilli and lemongrass and fry until fragrant. Add the green beans and carrot, and stir-fry for 2 mins until the vegetables lose their rawness but maintain their crunch. Add the chicken mince and break into small pieces with a wooden spoon while mixing with the vegetables. Once the chicken turns opaque and starts to cook through, add the prawns. Season with oyster and fish sauce, and pepper, and cook for 1-2 mins. Add the noodles, mix well, cook for 1 min more making sure that the chicken and prawns are cooked. Add the rice wine vinegar, mix well and turn off the heat. Set aside and cool for 10 mins. Peel the leaves off the lettuce carefully. If using iceberg lettuce, use scissors and, following the curve of the leaf, cut away the green floppy edge. You will be left with a firmer section, which acts like a cup. Spoon the chicken mixture onto the lettuce and sprinkle liberally with peanuts. Squeeze a little lime juice all over before serving.','20 mins','20 mins'),
(690,'thai turkey filo dippers','https://www.bbcgoodfood.com/recipes/thai-turkey-filo-dippers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/thai-turkey-filo-dippers-365e5b5.jpg','Fill filo pastry parcels with turkey or chicken mince, spring onions and ginger for a buffet nibble or party canapé similar to a spring roll',48,12,1,1,13,'To make the filling, mix all the ingredients in a small bowl until thoroughly combined. Cut each sheet of filo pastry into 4 evenly sized rectangles. Make a pile of 12 rectangles and cover with a damp tea towel to stop them drying out. Brush 1 of the rectangles lightly with oil. With one of the shortest sides facing you, spoon 1 level tbsp of the filling across the pastry 2cm up from the bottom of the rectangle and around 1cm in from both sides. Form into a sausage shape. Fold in the bottom over the filling, then fold in the 2 long sides all the way up the rectangle. Roll up from the short side close to you, fully enclosing the filling. Place on a baking tray lined with baking parchment. Repeat as above to make a further 11 filo rolls. Brush with more oil and sprinkle with sesame seeds, if using. Can be covered with cling film at this stage and kept in the fridge for up to 8 hrs before baking. Heat oven to 200C/180C fan/gas 6. Bake the dippers for 10-15 mins or until the pastry is light golden brown and the filling is cooked thoroughly. Serve with Thai sweet chilli dipping sauce.','30 mins','15 mins'),
(691,'steam-fried bao buns (sheng jian bao)','https://www.bbcgoodfood.com/recipes/veggie-bao-buns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-bao-buns-final-01aba6c.jpg','This street food dim sum from Beijing is an authentic type of bao bun that puffs up in the pan. You can fill with minced chicken, pork or prawns if you don''t want to go veggie',98,20,3,1,13,'In a large bowl, mix the flour, sugar, yeast and 1/ 4 tsp salt with 180ml lukewarm water. Knead the mixture for 10 mins or until elastic. Put in a lightly oiled bowl, cover with a damp tea towel and leave in a warm place to prove until it has doubled in size, about 1 1/ 2 hrs. Meanwhile, in a small bowl, mix the seasoning ingredients and 1/ 4 tsp salt. In a separate small bowl, mix the cornflour with 2 tbsp cold water and set aside. In a wok over a medium heat, fry the ginger with 1 tbsp rapeseed oil for 30 secs. Add the mushrooms and the seasoning mixture and stir-fry for 1 min. Pour the cornflour paste into the wok and stir-fry for 1 min more, or until the sauce thickens. Add the Chinese cabbage and fry briefly, then remove the wok from the heat. Add the noodles, tofu, spring onions, sweetcorn and sesame oil. Gently fold everything together so that the filling is evenly mixed, then leave to cool. Punch the air out of the dough and divide into 20 small balls about the size of a golf ball, each weighing roughly 25g. Put them in a roasting tin and re-cover with the tea towel. Leave in a warm place to prove for a further 20 mins. Flatten the dough balls with your hands. Use the tip of a rolling pin to roll around the edge of the dough ball a few times to flatten it, then gently work around the edges using your fingertips. The aim is to achieve a round sheet of dough with a thicker bump in the middle on which you can place the filling. Repeat until all of the balls have been rolled. Cover them again with the damp tea towel as you go so they don’t dry out. Taking a dough sheet in the palm of your hand, put 2 tsp of the filling in the middle. Cup your palm so the filling remains in the middle, then gently gather up the edges into the middle and twist to seal. Continue until all the buns are filled. Put them back in the roasting tin and re-cover with the tea towel again Add 1 tbsp rapeseed oil to a frying pan over a medium heat. Arrange half the buns in the pan, leaving 1/ 2 cm between them so they have enough room to rise during cooking. Fry for 1-2 mins, sprinkling with a little water, then pour 100ml water into the pan and cover with a tight-fitting lid. Cook for 2-3 mins until all the water has evaporated and the buns have doubled in size. They should become very white and the bottom should be crispy and golden brown. Repeat the process with the remaining 1 tbsp oil and the rest of the buns. Mix the sriracha and oyster sauce together in a small dish and serve with the buns.','1 hr and 30 mins','30 mins'),
(704,'moroccan-style chicken with sour cherries & olives','https://www.bbcgoodfood.com/recipes/moroccan-style-chicken-with-sour-cherries-olives','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/Moroccan-style-chicken-with-sour-cherries-and-olives-3882143.jpg','By adding dried fruit, Tom Kerridge brings a subtle sweetness to this North African chicken tagine. Plus, you can double up and freeze half for later',52,4,1,1,13,'Season the chicken well, and toss with the ras el hanout to coat. This can be prepared the night before and kept chilled. Heat the oil in a large flameproof casserole or frying pan over a medium heat and spend 15-20 mins browning the chicken on all sides. (You can do this in batches to avoid crowding the pan.) Transfer the chicken to a plate using a slotted spoon and set aside. Cook the onions and carrots in the pan for 8-10 mins until soft and golden, adding a drizzle more oil if the pan is dry. Add the garlic and ginger, and cook for 2 mins more. Stir in the harissa and preserved lemon. Cook for another minute until the vegetables are well coated and sticky. Add the cinnamon and bay leaves, then tip in the tomatoes and bring to a simmer. Cook for a few minutes until the liquid has reduced to a thick paste. Tip the chicken and its resting juices back into the pan, and pour over the stock. Stir in the olives and cherries and return to a simmer. Put the lid on and simmer gently for 45 mins–1 hr until the chicken is cooked through and tender. Scatter over the chopped coriander and serve with couscous. Once completely cool, will keep chilled for up to three days or frozen for three months. Defrost in the fridge overnight before reheating over a low heat or in the microwave until piping hot.','20 mins','45 mins'),
(694,'chicken arrabbiata stew & parmesan dumplings','https://www.bbcgoodfood.com/recipes/chicken-arrabbiata-stew-parmesan-dumplings','https://images.immediate.co.uk/production/volatile/sites/30/2020/12/Chicken-arrabbiata-stew-5634b8d.jpg','Try a modern take on stew and dumplings with Italian classic tomato alla arrabbiata and parmesan dumplings. Simple and oh so comforting, it''s perfect for cold weather cooking',14,4,1,1,13,'Heat the oil in a large shallow casserole dish. Fry the onion over a low-medium heat for 7 mins or until soft and turning translucent. Turn up the heat slightly, add the chicken and fry for 5 mins until beginning to brown around the edges. Tip the garlic and chilli into the casserole and fry for 1 min. Stir through the pesto, pour in the stock and tomatoes, then add the thyme, sugar and some seasoning. Turn the heat to low and cover with a lid. Cook gently for 40 mins. Remove the thyme and check the seasoning. Heat the oven to 200C/180C fan/gas 6. To make the dumplings, put the butter and flour in a bowl with ½ tsp fine salt, then rub the butter into the flour with your fingertips until it resembles fine breadcrumbs. Mix in 50g of the cheese and pour in 50ml of the milk, then swiftly combine with a cutlery knife until everything is well distributed and you have a soft dough (add more milk if it’s too dry). Briefly bring the dough together on a worksurface, then divide into eight balls. Arrange the dumplings over the stew, then scatter each with the remaining cheese. Put in the oven and bake, uncovered, for 25 mins or until the dumplings are doubled in size and golden brown. Scatter over the parsely and serve.','30 mins','1 hr'),
(695,'chicken & chorizo one-pan','https://www.bbcgoodfood.com/recipes/chicken-chorizo-one-pan','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Chicken-and-chorizo-one-pan-ec4788d.jpg','Save on washing up with this one-pan chicken, chorizo, rice and peppers dish. It''s packed with flavour and low in calories too for an easy midweek meal',99,4,1,1,13,'Heat a large frying pan over a medium heat and fry the chorizo for a couple of minutes until the oil is released. Add the onion and fry for 8-10 mins more until it begins to soften and caramelise. Add the garlic and cook for 1 min before adding the chicken. Fry for 5 mins until browned all over. Sprinkle in the oregano, basil, garlic granules and paprika, and cook for another 30 seconds. Tip in the rice and toast slightly in the chorizo oil. Pour in the stock and bring to a simmer, then add the pepper. Bring to a boil, then reduce to a simmer and cook for 15 mins, or until the rice is almost cooked. Top up with more stock or water, if needed. Add the petit pois and cook for a few minutes more until the rice is tender. Once the chicken is cooked and the rice is done to your liking, season to taste. Serve in bowls sprinkled with the parsley and top with a drizzle of garlic mayonnaise, if you like.','15 mins','35 mins'),
(696,'chipotle chicken tinga','https://www.bbcgoodfood.com/recipes/chipotle-chicken-tinga','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chipotle-chicken-tinga-00236ac.jpg','Serve this chilli chicken with our tomato salsa, tortilla chips, guacamole and coriander for an easy midweek meal',14,4,1,1,13,'Heat the oil in a large non-stick flameproof casserole dish or saucepan. Fry the onions with a pinch of salt for 10 mins, or until softened. Add the garlic and fry for 1 min more. Tip in the chipotle paste, tomatoes and sugar with half a can of water. Sit the chicken thighs in the sauce. Bring to a low simmer and cook, covered, for 20-25 mins, stirring often, until the liquid has reduced slightly and the chicken is cooked through. Using two forks, shred the chicken and stir it back through the sauce. Season to taste. If the sauce is too thin, return to the heat and simmer for 5 mins until the sauce has reduced more. To freeze, leave to cool completely and transfer to large freezerproof bags. To make the salsa, combine the red onion, tomatoes, lime zest and juice, coriander leaves and stalks. Season to taste. Serve the chicken with the tomato salsa, tortilla chips, guacamole and a scattering of coriander leaves.','15 mins','40 mins'),
(697,'next level sweet and sour chicken','https://www.bbcgoodfood.com/recipes/next-level-sweet-sour-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweet-n-sour-16d2d7c.jpg','Get the best version of sweet and sour chicken with our ultimate recipe. You''ll be rewarded with crisp, battered chicken in a sharp, sweet and fruity sauce',7,4,2,1,13,'Mix the chicken with the soy sauce, rice wine, ginger, garlic and a pinch of salt in a large bowl. Put in the fridge and leave to marinate for at least 1 hr or ideally overnight. Meanwhile, make the sauce. Tip the rice wine, vinegar and sugar into a saucepan with a pinch of salt and bring to the boil. Stir until the sugar has dissolved, then add the garlic and ketchup. Continue to cook for 10 mins until syrupy, then remove from the heat and set aside. To prepare the toppings, put the coriander and spring onion in a bowl of iced water to perk them up. Toast the peppercorns in a dry frying pan, if using, then crush using a pestle and mortar. Mix with 1 tbsp sea salt and set aside. Mix the beaten egg into the chicken mix, then stir in the cornflour and five-spice powder until the chicken is coated. Set aside. Pour the oil into a wok until it’s no more than a third full and heat to 170C, or heat a deep-fat fryer to the same temperature. If you don’t have a thermometer, drop a bit of the batter into the oil; when the batter sizzles and turns golden within 1 min, it’s ready. Fry the chicken in batches for 4-5 mins until golden and crisp, then drain on kitchen paper and keep warm in a low oven. Meanwhile, gently reheat the sauce and add the soy sauce, stock pot or cube, vegetables, pineapple and ginger. Simmer for 3 mins until the veg is slightly softened. Mix the chicken with the sauce and serve with the coriander and spring onions, salt and pepper mix and some rice on the side.','30 mins','30 mins'),
(698,'easy spanish chicken','https://www.bbcgoodfood.com/recipes/easy-spanish-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Spanish-Chicken-96abb56.jpg','Enjoy our Spanish chicken recipe – a classic, comforting stew made with chicken thighs nestled in a tomato and smoked paprika sauce with chorizo and butter beans',100,4,1,1,13,'Heat the oven to 200C/180C fan/gas 6. Heat the olive oil in a large ovenproof frying pan over a medium heat and fry the onion, chorizo and peppers along with a pinch of salt and pepper for 15 mins until the veg has softened and the chorizo has released its oils. Add the paprika and garlic, and cook for another few minutes until fragrant. Tip in the chopped tomatoes, olives and butter beans, stir to combine and season. Nestle in the chicken thighs and season well. Transfer to the oven and bake for 40 mins until the chicken skin is crisp and the meat cooked through and tender. Scatter with the parsley and serve.','15 mins','1 hr'),
(699,'sticky orange chicken with parsnips, maple & pecans','https://www.bbcgoodfood.com/recipes/sticky-orange-chicken-parsnips-maple-pecans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sticky-orange-chicken-with-parsnips-439cd07.jpg','Pop chicken thighs and root veg into a roasting tin with a sweet and tangy maple glaze for a delicious one-pot dinner',17,2,1,1,13,'Heat oven to 180C/160C fan/gas 4. Juice 1 of the oranges and whisk together with the maple syrup, olive oil, vinegar, mustard and cranberry jelly. Cut the parsnips into chunky lengths. Put the parnips, chicken thighs and shallots in a roasting tin – make sure everything can sit in a single layer but quite snug. Drizzle over half the orange sauce with some seasoning and toss to coat everything. Roast for 35 mins. Remove the tin from the oven and poke the orange slices in among everything. Scatter over the thyme and drizzle over the rest of the orange sauce. Roast for another 15 mins until the chicken is tender and cooked through, and everything is sticky and golden. Mix in the pecans and cook for another 5 mins. Serve straight away, remembering to scrape out all the sticky juices from the tin, and eat with a mixed leaf salad or some wilted spinach, plus a little rice to soak up the sauce, if you like.','25 mins','1 hr and 5 mins'),
(700,'one-pan piri piri chicken dinner','https://www.bbcgoodfood.com/recipes/one-pan-piri-piri-chicken-dinner','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/One-pan-piri-piri-chicken-dinner-1489950.jpg','Try this one-pan piri piri chicken for a family dinner and adjust the chilli levels to suit your family''s tolerance. You can use a whole chicken instead, if you like',3,4,1,1,13,'Pound all the marinade ingredients together with 1 tsp salt using a large pestle and mortar, or blitz in a mini chopper to create a loose paste. Slash each chicken piece a few times, then tip into a bowl and coat in the marinade. Cover and chill for at least 1 hr or up to 24 hrs. Heat the oven to 220C/200C fan/gas 7. Tip the chicken along with the marinade and potatoes into a roasting tin. Toss together, then arrange the chicken on top of the potatoes, thighs skin-side up. Roast for 35-40 mins until the chicken has browned. Remove the tin from the oven and mix the peppers in with the potatoes, baste the chicken in some of the juices in the tin and turn the drumsticks. Nestle the cherry tomatoes in among the chicken and roast for another 20-25 mins until the chicken is cooked through, the skin is crisp and the vegetables are nicely roasted. Scatter with the coriander and some flaky sea salt, if you like, and serve straight from the tin with the buttered corn on the side.','30 mins','1 hr'),
(701,'piri-piri chicken with smashed sweet potatoes & broccoli','https://www.bbcgoodfood.com/recipes/piri-piri-chicken-smashed-sweet-potatoes-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/piri-piri-chicken-with-smashed-sweet-potatoes-broccoli-84cb220.jpg','Serve up an easy one-pan traybake of piri-piri chicken with sweet potatoes and broccoli. As well as being simple to make, it delivers three of your 5-a-day',6,2,1,1,13,'Heat the oven to 180C/160C fan/gas 4. Toss the sweet potatoes with a generous drizzle of oil and some seasoning, and tip into a very large roasting tin. Push the potatoes to one end of the tin, then, in the other end, toss the chicken with the onions, spice mix, a drizzle of oil and some seasoning. Roast for 40 mins, stirring everything halfway through. Add the broccoli to the tin, drizzle with a little oil and season, then roast for 10-15 mins more. Remove the chicken, onions and broccoli from the tin. Roughly mash the potatoes using a fork, making sure you incorporate all the chicken juices and spices from the pan. Spread the mash over the base of the tin, then top with the broccoli, chicken and onions and serve from the tin in the middle of the table.','20 mins','55 mins'),
(702,'lemon & herb chicken traybake with butter beans & potato wedges','https://www.bbcgoodfood.com/recipes/lemon-herb-chicken-traybake-with-butter-beans-potato-wedges','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Lemon-and-herb-chicken-traybake-9bff51c.jpg','Make this versatile traybake for a hearty family meal. Filling and full of fuss-free, flavourful ingredients, this is budget-friendly food at its best',63,4,1,1,13,'Cut the potatoes into large wedges, then toss well in a large bowl with 2 tbsp of the olive oil and some salt. Spread the wedges out on a baking tray. Fry the onion and garlic in the remaining olive oil over a low heat until soft and translucent. Add the tomatoes, butter beans, and some seasoning. Simmer for a few minutes, then pour into a baking tin or dish. Mix a pinch of salt with the herbs. Stuff equal amounts under the skin of each chicken thigh. Put a slice of lemon on top of each thigh, then arrange on top of the tomato and bean mixture in the baking tin. Bake the wedges and chicken traybake in the oven at 180C/160C fan/gas 4 for 45 mins until the chicken is cooked through. Check the potatoes after about 30 mins, shaking them around in the tray – depending on the size of the wedges, they may already be cooked through.','15 mins','50 mins'),
(703,'slow cooker honey mustard chicken thighs','https://www.bbcgoodfood.com/recipes/slow-cooker-honey-mustard-chicken-thighs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/honey-mustard-chicken-f111be8.jpg','Use just a few ingredients to create this no-fuss slow-cooker chicken thigh dish with a creamy honey and mustard sauce. Kids will love it – and grown-ups, too',19,4,1,1,13,'Heat the slow cooker. Melt the butter in a frying pan and quickly brown the chicken thighs all over. Make sure the skin picks up plenty of colour. Season, then put them in the slow cooker. Brown the spring onions and add them to the slow cooker as well. Add the stock, mustard and honey and cook on low for 4 hrs. Stir in the cream or crème fraîche and peas, then cook for a further 15 mins with the lid off. Re-crisp up the chicken skin under the grill, if you like.','5 mins','4 hrs and 20 mins'),
(705,'honey & soy chicken with sesame broccoli','https://www.bbcgoodfood.com/recipes/honey-soy-chicken-thighs-sesame-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/honey-soy-chicken-thighs-with-sesame-broccoli-a2af21e.jpg','Enjoy these sticky honey and soy chicken thighs as part of an easy midweek meal. This budget-friendly dinner is sure to be a family favourite',16,4,1,1,13,'Heat oven to 200C/180C fan/gas 6. Mix the soy, honey, 1 tbsp of the oil and ½ tbsp garlic & ginger paste in a bowl. Pour over the chicken thighs and leave to marinate if you have time, but don’t worry if not – they will be delicious either way. Tip the chicken with all the marinade into a roasting tin, skin-side up. Cook for 45 mins until sticky and the skin is crispy. When the chicken has 15 mins left, start cooking the rice following pack instructions, if using, and heat the remaining oil in a frying pan. Add the remaining garlic & ginger paste, sizzle for 1 min, then tip in the broccoli along with a splash of water. Cover and cook for 10 mins until tender, then take the lid off, tip the sesame seeds into the pan and give everything a good toss around. Season. Serve with the chicken, and all the caramelised bits from the bottom of the tin.','10 mins','45 mins'),
(706,'slow cooker chicken chasseur','https://www.bbcgoodfood.com/recipes/slow-cooker-chicken-chasseur','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-chasseur-7a5a984.jpg','Throw a handful of ingredients in the slow cooker at the start of the day and this classic French chicken chasseur will be ready by the time you get home',12,2,1,1,13,'Heat the slow cooker to low. Put the oil in a frying pan over a medium-high heat. Season the chicken and fry, skin-side down, for 4-5 mins until crisp. Turn and fry for 3-4 mins more until golden all over. Put on a plate and set aside. Tip the shallots into the pan and fry for 10 mins over a medium heat until soft. Add the garlic and mushrooms and fry for 10 mins more until the mushrooms are golden. Pour in the wine and bubble for a few minutes until reduced by half. Stir in the tomato purée, chopped tomatoes and herbs. Season well and bring to a simmer. Tip the sauce into the slow cooker, and top with the chicken thighs (in one layer, if possible). Pour over the chicken stock until the chicken thighs are covered, adding a little more if needed. Cover and cook for 6-8 hrs until the sauce has thickened and the chicken is tender. Transfer the chicken to a plate and bubble the sauce for a few minutes more with the lid off, if you prefer a thicker sauce (you can only do this if there’s a reduce function on the slow cooker. If not, pour the sauce into a pan and simmer until reduced). Remove the skin from the chicken before serving, if you prefer. Sprinkle over the parsley and serve the chicken and sauce with buttery mash, jackets, pasta or small roast potatoes, if you like.','15 mins','6 hrs and 35 mins'),
(707,'roast chicken thighs with brown rice & salsa verde','https://www.bbcgoodfood.com/recipes/roast-chicken-thighs-brown-rice-salsa-verde','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roast-chicken-thighs-with-brown-rice-salsa-verde-6d23d73.jpg','How about a healthy, low-calorie roast chicken dinner? Enjoy this tasty meal with benefits like vitamin C from the salsa verde which helps iron absorbtion',16,2,1,1,13,'Heat oven to 200C/180C fan/gas 6. Season the chicken, rub with ½ tbsp oil, then put in a large roasting tin with the garlic and roast for 25-30 mins. Meanwhile, blitz the herbs, anchovy, capers, lemon juice and remaining oil with some seasoning in a food processor until finely chopped. Set aside. Once the chicken is cooked, remove the tin from the oven and squeeze the garlic out of their skins. Tip in the rice and use a wooden spoon to break it up, then add the spinach and lemon zest and toss. Return to the oven for 5 mins. Divide between bowls and dollop on the salsa verde.','10 mins','35 mins'),
(708,'chicken chilli con carne','https://www.bbcgoodfood.com/recipes/chicken-chilli-con-carne','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Chicken-chilli-con-carne-4f4e606.jpg','Not too keen on red meat? Try our chicken version of chilli con carne, with all the richness of the classic dish. It makes a great sofa supper on cold nights',14,4,1,1,13,'Heat the oil in a casserole dish over a medium heat, and fry the onion and peppers for 10-12 mins, or until softened. Add the garlic, coriander stalks, ground coriander, cumin and chipotle paste, and fry for 2 mins more. Tip in the tomatoes, tomato pureé and stock, then add the cinnamon stick. Nestle the chicken thighs into the mixture, then reduce the heat to low and simmer, covered, for 45 mins or until the chicken is tender. Remove the chicken from the casserole and transfer to a chopping board, then shred the meat off the bone using two forks. Return the shredded chicken to the pan, then tip in the black beans along with the liquid from the can, the drained kidney beans, vinegar and chocolate. Stir well to combine all of the ingredients and melt the chocolate, then turn the heat up to medium and simmer, uncovered, for 10-15 mins, or until the mixture has thickened slightly. Remove the cinnamon stick, season to taste and stir though most, or all of the coriander leaves (you can reserve a few leaves to scatter over at the end, if you like). Serve the chilli with rice or tortilla chips, topped with guacamole and soured cream, if using, and scatter over any remaining coriander.','15 mins','1 hr'),
(709,'old delhi-style butter chicken','https://www.bbcgoodfood.com/recipes/old-delhi-style-butter-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/old-delhi-style-butter-chicken-c274fcd.jpg','Head to your spice rack to make this butter chicken curry, a dish that symbolises Indian food for millions of people all over the world',101,6,1,1,13,'Mix all of the marinade ingredients together in a large mixing bowl with 1½ tsp salt. Add the chicken pieces and mix together until well-coated, then cover the bowl and chill for 1 hr or overnight in the fridge. Heat the oven to 240C/220C fan/gas 9. Transfer the chicken pieces to a large baking tray (discard any remaining marinade), and cook for 10-15 mins. Turn the pieces after 10 mins so they colour evenly on both sides. The chicken doesn’t need to be completely cooked through at this point as it will cook for a few more mins in the sauce. Meanwhile, for the sauce, slice the tomatoes in half and put in a large pan in a single layer with 125ml water, the grated ginger, garlic, cardamom, cloves and bay leaf. Simmer, covered, until the tomatoes have completely disintegrated, about 20-25 mins. Remove the whole spices and blend the tomato mixture with a stick blender, then pass it through a sieve to make a smooth purée. Return to a clean pan, add the chilli powder and simmer for 12-15 mins. It should slowly begin to thicken. When the sauce turns glossy, add the chicken pieces and any of the reserved roasting juices from the tray. Slowly stir in the butter, a couple of pieces at a time, and simmer for 6-8 mins until the chicken is cooked through. Add the chopped ginger, green chillies and cream, then simmer for a min or two longer, taking care that the sauce doesn’t split. Stir in 1 tsp salt, fenugreek leaves, if using, and the garam masala, then check the seasoning, adjust if necessary, then add the sugar. In a separate pan, warm all the ingredients for the spiced butter, if using, until the seeds start to pop (see below). Spoon over the curry, scatter with the coriander, onion, chilli, and a drizzle more cream, if using. Serve with naan, pilau rice and chutney or keto bread for a keto-friendly version.','50 mins','1 hr'),
(710,'beef stew','https://www.bbcgoodfood.com/recipes/beef-vegetable-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-890477_11-0101064.jpg','A traditional braised beef casserole with thick, rich gravy and chunky veg – an ideal recipe for cheap cuts, as slow-cooking guarantees a tender dish',17,5,1,1,12,'Heat oven to 160C/140C fan/gas 3 and put the kettle on. Put 2 thickly sliced celery sticks, 1 chopped onion, 2 chunkily sliced carrots, 5 bay leaves and 1 whole thyme sprig in a flameproof casserole dish with 1 tbsp vegetable oil and 1 tbsp butter. Soften for 10 mins, then stir in 2 tbsp plain flour until it doesn’t look dusty anymore, followed by 2 tbsp tomato purée, 2 tbsp Worcestershire sauce and 2 crumbled beef stock cubes. Gradually stir in 600ml hot water, then tip in 850g stewing beef and bring to a gentle simmer. Cover and put in the oven for 2hrs 30 mins, then uncover and cook for 30mins – 1hr more until the meat is really tender and the sauce is thickened. Garnish with the picked leaves of the remaining thyme sprig.','15 mins','3 hrs - 3 hrs and 50 mins'),
(711,'beef bourguignon with celeriac mash','https://www.bbcgoodfood.com/recipes/beef-bourguignon-celeriac-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1035610_10-f38e2ab.jpg','This sumptuous, step-by-step recipe for slow-cooked stew from Gordon Ramsay makes a great winter supper',84,4,1,1,12,'Heat a large casserole pan and add 1 tbsp goose fat. Season 600g large chunks of shin beef and fry until golden brown, about 3-5 mins, then turn over and fry the other side until the meat is browned all over, adding more fat if necessary. Do this in 2-3 batches, transferring the meat to a colander set over a bowl when browned. In the same pan, fry 100g sliced smoked streaky bacon, 350g peeled shallots or pearl onions, 250g chestnut mushrooms, 2 sliced garlic cloves and 1 bouquet garni until lightly browned. Mix in 1 tbsp tomato purée and cook for a few mins, stirring the mixture. This enriches the bourguignon and makes a great base for the stew. Then return the beef and any drained juices to the pan and stir through. Pour over 750ml bottle red wine and about 100ml water so the meat bobs up from the liquid, but isn’t completely covered. Bring to the boil and use a spoon to scrape the caramelised cooking juices from the bottom of the pan – this will give the stew more flavour. Heat oven to 150C/fan 130C/gas 2. Make a cartouche: tear off a square of foil slightly larger than the casserole, arrange it in the pan so it covers the top of the stew and trim away any excess foil. Cook for 3 hrs. If the sauce looks watery, remove the beef and veg with a slotted spoon, and set aside. Cook the sauce over a high heat for a few mins until the sauce has thickened a little, then return the beef and vegetables to the pan. To make the celeriac mash, peel 600g celeriac and cut into cubes. Heat 2 tbsp olive oil in a large frying pan. Tip in the celeriac and fry for 5 mins until it turns golden. Season well with salt and pepper. Stir in 1 or 2 sprigs of rosemary and thyme, 2 bay leaves and 4 cardamom pods, then pour over 200ml water, enough to nearly cover the celeriac. Turn the heat to low, partially cover the pan and leave to simmer for 25-30 mins. After 25-30 mins, the celeriac should be soft and most of the water will have evaporated. Drain away any remaining water, then remove the herb sprigs, bay and cardamom pods. Lightly crush with a potato masher, then finish with a glug of olive oil and season to taste. Spoon the beef bourguignon into serving bowls and place a large spoonful of the celeriac mash on top. Garnish with one of the bay leaves, if you like.','20 mins','3 hrs and 15 mins'),
(712,'muffin-topped winter beef stew','https://www.bbcgoodfood.com/recipes/muffin-topped-winter-beef-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1666_11-9dabc70.jpg','Muffin on stew? You’d better believe it – think of it as a lighter alternative to suet dumplings',69,2,1,1,12,'Heat oven to 150C/fan 130C/gas 2. Toss the beef in seasoned flour. Heat the oil in a large flameproof casserole. Fry the beef over a high heat until browned all over – it’s easiest to do this in batches. Remove with a slotted spoon and set aside. Add 2 tbsp water and the onion to the pan, stir well over a medium heat, scraping up the crusty bits from the bottom of the pan. Lower the heat and fry gently for 10 mins, stirring occasionally until the onions have softened. Tip in the carrots, parsnips and bay leaf, then fry for 2 mins more. Return the beef to the pan, stir in the tomato paste, wine if using, and stock, then bring to the boil. Cover and cook in the oven for 1¾-2 hrs until the meat is tender. Can be made up to 2 days ahead or frozen for up to 1 month. Defrost thoroughly in the fridge before topping and baking. Take out of the oven and increase the temperature to 190C/fan 170C/gas 5. To make the topping, sieve the flour and baking powder into a bowl and add half the cheese. Mix olive oil and milk, then stir into the flour to make a soft, slightly sticky dough. Add more milk, if necessary. Spoon the dough over the meat and vegetables, sprinkle with the remaining cheese and bake for about 15 mins until golden, risen and cooked through.','20 mins','2 hrs and 30 mins'),
(713,'beef & guinness stew with carrots','https://www.bbcgoodfood.com/recipes/beef-stout-stew-carrots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513689_11-f809510.jpg','With sweet, slow-cooked carrots and beautifully tender meat, this beef and stout stew is comfort in a pot. Serve with creamy parsnip mash for a warming meal.',7,4,1,1,12,'Heat oven to 160C/140C fan/gas 3. Heat the oil in large lidded casserole dish, brown the meat really well in batches, then set aside. Add the onion and carrots to the dish, give them a good browning, then scatter over the flour and stir. Tip the meat and any juices back into the dish and give it all a good stir. Pour over the stout and crumble in the stock cube. Season the stew with salt, pepper and a pinch of sugar. Tuck in the herbs and bring everything to a simmer. Cover with a lid and place in the oven for about 2½ hrs until the meat is really tender. The stew can now be chilled and frozen for up to 3 months – defrost completely before reheating until piping hot. Leave the stew to settle a little, then serve with creamy parsnip mash for a true celebration of winter vegetables.','30 mins','3 hrs'),
(714,'beef massaman curry','https://www.bbcgoodfood.com/recipes/beef-massaman-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1035626_12-10a1140.jpg','Slow-cooked, meltingly tender beef and a sprinkling of crunchy peanuts to finish - it''s curry heaven',17,4,1,1,12,'Heat oven to 200C/180C fan/gas 6, then roast the peanuts on a baking tray for 5 mins until golden brown. When cool enough to handle, roughly chop. Reduce oven to 180C/160C fan/gas 4. Heat 2 tbsp coconut milk (use the cream on top if there is any) in a large casserole dish with a lid. Add the curry paste and fry for 1 min, then stir in the beef and fry until well coated and sealed. Stir in the rest of the coconut with half a can of water, the potatoes, onion, lime leaves, cinnamon, tamarind, sugar, fish sauce and most of the peanuts. Bring to a simmer, then cover and cook for 2 hrs in the oven until the beef is tender. Sprinkle with sliced chilli and the remaining peanuts, then serve straight from the dish with jasmine rice.','15 mins','2 hrs and 10 mins'),
(715,'steak, ale & mushroom pie','https://www.bbcgoodfood.com/recipes/proper-beef-ale-mushroom-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-194484_12-d5cdc2d.jpg','Spend a weekend afternoon making this classic beef and ale pie with porcini mushrooms and homemade pastry. It''s even better made a couple of days in advance.',7,6,3,1,12,'If you’re using the porcini mushrooms, cover 10g of mushrooms in boiling water for 20 mins, then squeeze out but keep the soaking water. Heat the oven to 160C/140C fan/gas 3. Heat 1 tbsp vegetable oil in a large casserole dish, brown 1kg large chunks of braising steak really well in batches, then set aside. Add 2 large onions, roughly chopped, and 4 large carrots, chopped into large chunks, to the pan. Add a drizzle more vegetable oil, then cook on a low heat for 5 mins until coloured. Add the soaked porcini mushrooms, sizzle for 1 min more, then scatter over 2 tsp golden caster sugar and 4 tbsp plain flour, stirring until the flour turns brown. Tip the braising steak and any juices back into the pan and give it all a good stir. Pour over 300ml dark ale, 2 beef stock cubes mixed with 400ml boiling water and the porcini mushroom soaking liquid, discarding the last few drops. Season the stew, tuck in a small tied-together bunch of thyme, bay leaf and parsley, and bring everything to a simmer. Cover with a lid and place in the oven for about 2 hrs, until the braising steak is really tender. While the stew is cooking, heat a drop more vegetable oil in a frying pan and sizzle 200g smoked bacon lardons for 3 mins until crisp. Turn up the heat, add 200g halved chestnut mushrooms and cook for 4 mins until golden. Remove from the heat and, when the stew is cooked, stir them through. Leave everything to cool completely – better still, make this up to 2 days in advance and keep it in the fridge as the pie will be better if the filling is fridge-cold when added. Can also be frozen for up to 3 months and defrosted when needed. Make the pastry up to 2 days before you want to assemble the pie. Crumble 650g plain flour and 250g cold diced lard or butter, together with a generous pinch of sea salt until completely combined, then add up to 200ml ice-cold water to make a soft dough. This can be done in a food processor if you want. Knead the pastry, then wrap in cling film and leave to rest in the fridge for at least 1 hr. The pastry can be made up to 2 days ahead and kept in the fridge or frozen for up to a month. When you want to make the pie, heat oven to 220C/200C fan/gas 7 and place a flat baking tray in the oven. Heavily grease a 24-28cm pie dish and dust well with flour. Cut a third off the pastry and set aside. Roll out the pastry to a thickish round that will easily line the pie dish with an overhang, then line the tin. Add the braising steak to the dish using a slotted spoon so some gravy is left in the container, as you don’t want too much sauce in the pie. You want the filling to be slightly higher than the rim of the dish. If you have a bit too much, set it aside. Roll out the remaining pastry to a thick round big enough to cover the dish. Brush the edges of the pastry in the dish with 1 beaten egg yolk, then cover with the pastry lid. Trim the edges, crimp the pastry, then re-roll your trimmings to make a decoration, for example, pastry leaves. Brush the top heavily with egg. Make a few little slits in the centre of the pie, place on the hot baking tray. Bake for 40 mins until golden. Leave the pie to rest for 10 mins while you heat up the gravy left in the container. Serve the pie at the table with a jug of gravy and a big pile of greens.','1 hr','3 hrs'),
(716,'crispy topped cumberland pie','https://www.bbcgoodfood.com/recipes/crispy-topped-cumberland-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-907451_11-59d7988.jpg','This crisp, potato-topped pie is slow-cooked and inexpensive to make. It''s a great family meal but smart enough for entertaining too ',17,5,1,1,12,'Heat oven to 160C/140C fan/gas 3. Soften the celery, onion, carrots, bay and 1 thyme sprig in a casserole with 1 tbsp oil and the butter for 10 mins. Stir in the flour, followed by the purée, Worcestershire sauce and stock cubes. Gradually stir in 600ml hot water, then tip in the beef and bring to a gentle simmer. Cover and cook in the oven for 2 hrs 30 mins, then uncover and cook for 30 mins -1 hr more until the meat is really tender and sauce thickened. Meanwhile, cook potatoes in a pan of boiling water until they’re not done but about ¾ of the way there. Transfer meat to a baking dish. Slice spuds into 1cm thick rounds and gently toss with seasoning, the remaining oil and thyme leaves. Layer on the beef, scattering with the cheese as you layer. You can cover and chill the pie now for 1 day, or freeze for up to 3 months. Increase oven to 200C/180C fan/gas 6 and bake for 30-40 mins until golden and crispy, and sauce bubbling if the dish went in cold. Serve with peas.','45 mins','3 hrs - 3 hrs and 50 mins'),
(717,'beef bourguignon cottage pie','https://www.bbcgoodfood.com/recipes/beef-bourguignon-cottage-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424726_11-eebfe03.jpg','A hearty meal for all the family, by Gary Rhodes. You can prepare it up to a month in advance',80,6,1,1,12,'(Up to a month ahead) heat the oil in a large pan. Fry the bacon lardons over a high heat for several mins until well coloured. Using a slotted spoon, remove from the pan, drain on kitchen paper and set aside. Season the beef, then fry in the fat left in the pan. Once coloured, add all the remaining ingredients for the beef, except the cornfl our and bacon, and bring to a simmer. Partly cover the pan with a lid and continue to cook for a further 2-2½ hrs or until the beef is tender. Once cooked, tip everything into a colander set over another pan so you can catch the sauce. Tip the contents of the colander into a large pie dish, about 28cm x 23cm x 5cm. Re-boil the sauce and check for seasoning. Thicken with the loosened cornfl our, adding a little at a time and stirring until you have the desired coating consistency. Spoon enough sauce over the beef to barely cover and loosen it, then stir. Remaining sauce can be frozen, ready to re-heat and offer separately on the day. While the beef is cooling, make the mash. Boil the potatoes until tender, 15-20 mins, depending on size. Drain off the water and replace the lid. Shake the pan to break up the boiled potatoes. Add butter and milk a little at a time while mashing. Season. Spoon and spread the mashed potatoes on top of the cooled beef. Once covered, the mash can be marked with a spoon, palette knife or fork for a more interesting fi nish (see Gary’s tip, left). When cold, the cottage pie is ready to cover and freeze. The day before serving, transfer the pie to the fridge to defrost overnight. (Or reheat from frozen on the day, see below.) On the day, reheat the pie. Heat oven to 180C/fan 160C/gas 4. If thawed, bake for 50 mins-1 hr, or until heated through, then increase heat to 200C/fan 180C/gas 6 for the fi nal 10 mins for a golden brown topping. If cooking from frozen, cover with foil, then bake for 1¼ hrs; remove foil and bake uncovered for 1¼ hrs more, then increase heat as above for the last 10 mins.','45 mins - 55 mins','2 hrs - 2 hrs and 45 mins'),
(718,'beef & beetroot curry','https://www.bbcgoodfood.com/recipes/beef-beetroot-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1217464_11-3f4c69f.jpg','This vibrant purple root adds a welcome dash of colour and some healthy nutrients to an Indian spice pot',17,6,1,1,12,'Put the beetroot in a large pan of water, bring to the boil and simmer until tender – check with a skewer; you may need to remove smaller ones first while the larger ones carry on cooking. Drain and cool, then put on rubber gloves to peel the skin and remaining stalks. Chop into large chunks and set aside two-thirds for later. Put the remaining one-third beetroot in a food processor or blender with the ginger, garlic, chilli, chopped coriander, cardamom pods, tomato paste, treacle, cumin, ground coriander, fennel seeds, cloves and 2 tsp ground black pepper. Whizz to a paste. Heat oven to 160C/140C fan/gas 3. Heat half the oil in a big flameproof casserole. Fry the beef chunks in batches until browned all over, removing to a dish as you go. Add the remaining oil, lower the heat, then tip in the onions and fry gently until softened, scraping up the beefy bits. Tip in the spice paste and fry for 5 mins until really fragrant. Return the beef and stock cubes with enough boiling water from a kettle to cover. Bring to a simmer, then put on the lid and bake for 2 hrs. Uncover and bake for a further 30 mins until the beef is really tender. Put back on the hob and stir in the garam masala and reserved beetroot to heat through. Season well with salt, scatter with coriander leaves and serve with naan, basmati rice and raita or yogurt, if you like.','30 mins','2 hrs and 50 mins'),
(719,'beef & beer pie','https://www.bbcgoodfood.com/recipes/beef-beer-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-872583_11-7e29c5b.jpg','A steak and ale pie is a comforting classic - this version has suet pastry and a dark rich gravy',17,4,2,1,12,'Heat oven to 160C/140C fan/gas 3. Using a flameproof casserole dish with a lid, soften onion and celery in the butter. Stir in the flour until it has disappeared, then stir in the beef and Worcestershire sauce. Crumble in stock cubes and add thyme. Pour over ale, bring to a simmer, then cover and cook in the oven for 2½ hrs. Remove lid and cook for another 30 mins – this should thicken the sauce nicely. Increase oven to 200C/180C fan/gas 6. For the pastry, whizz the flour, suet and 1 tsp salt in a food processor until there are no suet lumps. Keeping the motor running, dribble in 1 tbsp water at a time until the pastry starts to come together, then tip onto a lightly floured surface and bring together with your hands. Roll out half the pastry and use to line a 20-22cm pie dish. Using a slotted spoon, spoon in all of the meat, then pour over some of the sauce until the meat is just coated. Put the rest of the sauce into a saucepan. Roll out the remaining pastry to cover the pie. Trim the edge with a small, sharp knife, then crimp or fork the edges to seal. Make a small heart from cuttings. Brush top of the pie and heart with beaten egg, then dip heart into poppy seeds and stick to top. Bake for 30-40 mins until golden brown and crisp. Reheat sauce and serve with the pie along with some mash and greens.','1 hr','4 hrs'),
(720,'spicy beef stew with beans & peppers','https://www.bbcgoodfood.com/recipes/spicy-beef-stew-beans-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513511_11-0d2b39e.jpg','Just the thing to warm you up on a cold winter night',5,6,1,1,12,'Heat 1 tbsp of oil in a large saucepan with a lid. Season meat, then cook about one-third over a high heat for 10 mins until browned. Tip onto a plate and repeat with 2 tbsp of oil and rest of the meat. Add a splash of water and scrape pan to remove bits. Add ½ tbsp oil. Turn heat down, fry onion and garlic until softened. Return meat to the pan, add flour and stir for 1 min. Add treacle, cumin, tomatoes and stock. Bring to boil, reduce heat, cover and simmer for 1 hr 45 mins. Stir occasionally and check that the meat is covered with liquid. Add peppers and beans, and cook for a further 15 mins. The stew can now be cooled and kept in the fridge for 2 days, or frozen for 1 month. Serve in bowls, with sour cream, coriander and cornbread.','15 mins','2 hrs and 45 mins'),
(721,'beef bourguignon','https://www.bbcgoodfood.com/recipes/beef-bourguignon','https://images.immediate.co.uk/production/volatile/sites/30/2010/10/beef-bourguignon-f3bd4cb.jpg','The secret to this rich beef casserole is to use all wine and no stock. Our ultimate beef bourguignon recipe is an instant comforting classic, full of satisfying flavours.',7,6,1,1,12,'Tip 1.6kg braising steak, cut into large chunks, into a large bowl with 3 bay leaves, a small bunch of thyme, 2 bottles of red wine and some pepper, then cover and leave in the fridge overnight. Heat the oven to 200C/180C fan/gas 6. Place a colander over another large bowl and strain the marinated meat, keeping the wine. Heat 1 tbsp oil in a large frying pan, then brown the meat in batches, transferring to a plate once browned. When all the meat is browned, pour a little wine into the now-empty frying pan and bubble to release any caramelised bits from the pan. Heat 1 tbsp oil in a large casserole and fry 3 large or 6 normal carrots, cut into large chunks, and 2 roughly chopped onions until they start to colour. Stir in 3 tbsp plain flour for 1 min, then add 1 tbsp tomato purée. Add the beef and any juices, the wine from the frying pan and the rest of the wine and herbs. Season and bring to a simmer. Give everything a good stir, then cover. Transfer to the oven and bake for 2 hrs until the meat is really tender. Cool. Will freeze for up to 3 months. To serve, defrost completely overnight in the fridge if frozen, then place on a low heat to warm through. Meanwhile, heat a small knob of butter in a frying pan and add 300g bacon lardons and 500g peeled pearl onions or small shallots. Sizzle for about 10 mins until the bacon starts to crisp and the onions soften and colour. Add 400g halved mushrooms and fry for another 5 mins, then stir everything into the stew and heat for 10 mins more. Serve scattered with chopped parsley.','45 mins','3 hrs and 30 mins'),
(722,'beef pie with crisp potato crust','https://www.bbcgoodfood.com/recipes/beef-pie-crisp-potato-crust','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2811_12-3d202b9.jpg','A classic recipe with a contemporary twist, beef pie with crisp potato crust',8,4,1,1,12,'Heat oil in a non-stick pan over a high heat. Add beef and cook for 10 mins until browned all over, then remove from pan. Cook onion for 7 mins until softened. Stir through mushrooms, then cook for 3 mins more until golden. Stir in tomato paste and flour and cook for 1 min more. Pour in wine and beef stock, bring to a simmer. Add beef back to pan. Cook, uncovered, over a gentle heat for around 2 hrs until meat is really tender, topping up with water if needed. Pour mixture into a 2 litre baking dish. Heat oven to 190C/fan 170C/gas 5 and bring a pan of lightly salted water to the boil. Cook the potatoes for 10 mins until beginning to soften. Cool under a running tap, then coarsely grate. Sprinkle over the beef and dot with butter. Pop in the oven and cook for 30 mins until the crust is nice and crispy.','2 hrs and 40 mins','No Time'),
(723,'thai beef curry','https://www.bbcgoodfood.com/recipes/thai-beef-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-908463_10-87fede4.jpg','A spicy, authentic Thai green curry for your slow cooker that''s ideal for taking the last-minute stress out of entertaining',28,8,1,1,12,'Heat a little of the oil in a large pan and brown the beef in batches, removing to a plate after, reserving any juices. If your slow cooker has a browning function, use this instead. Meanwhile, in a mini chopper or food processor, whizz half the coriander, the chopped lemongrass, garlic, chillies and galangal with the rest of the oil until you have a rough paste. Turn the slow cooker to High. Heat the paste for a few mins, then add the beef and all the remaining ingredients, apart from the remaining coriander and lime juice. Turn slow cooker to Low and cook for 8 hrs, or until the meat is falling off the bone. If using beef ribs, remove bones from the beef, then shred the meat with 2 forks. If the sauce is too thin, strain it off and boil it to reduce. Stir in the remaining coriander and lime juice, then season with more fish sauce or sugar.','20 mins','8 hrs'),
(724,'carbonnade flamande','https://www.bbcgoodfood.com/recipes/carbonnade-flamande','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-496_11-4bae7db.jpg','Cook a classic ale casserole with chunks of meaty beef',5,4,2,1,12,'Marinate the beef overnight in the ale with the garlic and bay leaves. The next day, drain the beef from the marinade, reserving the marinade. Pat the meat dry with kitchen paper and toss it in the seasoned flour until evenly coated. Shake off any excess flour. Heat 2 tbsp of the olive oil in a large flameproof casserole until hot. Fry the beef in 3-4 batches for about 5 minutes per batch, stirring occasionally, until it is a rich golden brown all over. You may need to add a little more oil between batches but make sure it is hot again before adding the next batch. Remove the meat with a slotted spoon to a plate and set aside. Don’t worry if the bottom of the casserole is starting to brown, this all adds to the flavour of the finished dish. Lower the heat to medium and fry the pancetta in the casserole for 6-8 minutes, stirring occasionally, until crisp and golden. Scoop the pancetta out with a slotted spoon and set aside with the beef. Preheat the oven to fan 140C/conventional 160C/gas 3. Tip the carrots, onions and leek into the casserole and fry, stirring occasionally, until they start to brown – this takes about 12 minutes. Spoon in the tomato purée and continue to cook for 2 minutes, stirring constantly. Add the beef and pour in the reserved marinade. Bring to a simmer, scraping any sticky bits off the bottom of the pan, then add all the beef stock and bouquet garni to the casserole. Season with salt and pepper and bring everything to the boil. Remove from the heat. Cover with a lid and cook in the oven for 2 hours, stirring once halfway through. (The carbonnade may now be left to cool and frozen for up to 1 month. Add 100ml/31⁄2 fl oz more stock to the sauce when reheating.) When the beef is ready, taste for seasoning and add more salt and pepper if you think it needs it. Scatter the chopped parsley over the top and serve straight from the casserole, with creamy mash or jacket potatoes and buttered greens or cabbage.','1 hr and 15 mins - 1 hr and 30 mins','2 hrs'),
(725,'stir-fried beef with oyster sauce','https://www.bbcgoodfood.com/recipes/stir-fried-beef-oyster-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/stir-fried-beef-with-oyster-sauce-6633208.jpg','Marinate slices of lean beef steak then pan-fry with chunks of red pepper and a savoury sauce in this classic Chinese dish',102,4,1,1,12,'Cut the beef into slices 5cm long and 5mm thick, cutting against the grain of the meat. Put them in a bowl. Mix in the soy sauce, sesame oil, rice wine or Sherry and cornflour. Leave to marinate for 20 mins. Heat a wok until it is very hot, then add the groundnut oil. When it is slightly smoking, add the beef slices and stir-fry for 5 mins or until lightly browned. Remove the meat from the wok and drain well in a colander set inside a bowl. Discard the drained oil. Wipe the wok clean and reheat it over a high heat. Add the peppers, and cook for 3-4 mins or until softened. Add the oyster sauce and bring it to a simmer. Return the drained beef slices to the wok and toss them thoroughly with the oyster sauce. Turn the mixture on to a serving platter, top with the spring onions and serve immediately.','10 mins','10 mins'),
(726,'beef stew in red wine sauce recipe','https://www.bbcgoodfood.com/recipes/beef-red-wine-melting-onions','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beef-in-red-wine-with-melting-onions-8a70bf1.jpg','Beef skirt and shin are great value cuts, and become particularly delicious when slow-cooked in this one-pot dish - ideal for no-fuss family dinners',5,4,1,1,12,'Heat oven to 150C/130C fan/gas 2. In a large, heavy-based flameproof casserole dish with a lid, melt the butter over a medium heat. Add the onions and garlic, cook for 10 mins until starting to brown, then transfer to a small plate. Put the flour in a large plastic food bag with plenty of black pepper. Add half the beef, shake to coat, then remove, leaving some flour in the bag. Add the rest of the beef and shake to coat in the remaining flour. Heat the oil in the same casserole dish you cooked the onions in (there’s no need to clean it first). Add the beef and bay leaves, and fry until the meat is browned all over. Pour in the wine and return the onions to the dish. Add the tomato purée and stock, stir and return to a simmer. Cover with the lid and put in the oven to stew for 1 hr. After 1 hr, add the mushrooms and return to the oven for another hour. Taste the meat – if it’s tender, remove from the oven. If it’s still a little firm, cook for 30 mins more and test again. Serve scattered with parsley, if you like.','20 mins','2 hrs and 10 mins'),
(1537,'chilli avocado','https://www.bbcgoodfood.com/recipes/chilli-avocado','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chilli-avocado-58a4b18.jpg','Avocado makes a great snack as it keeps you fuller for longer, add a touch of heat and a squeeze of lime juice for added zing',16,1,1,3,15,'Sprinkle the avocado with the chilli flakes, lime juice and a little black pepper, and eat with a spoon.','2 mins','N/A'),
(727,'slow-cooked beetroot & beef curry (chukandar gosht)','https://www.bbcgoodfood.com/recipes/slow-cooked-beetroot-beef-curry-chukandar-gosht','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beef-beetroot-bded487.jpg','This Pakistani curry has a deep purple hue and is flavoured with fragrant cinnamon, cumin and coriander',103,4,1,1,12,'Using a large, non-stick lidded wok or saucepan, heat the oil over a medium flame, add the cardamom, cinnamon, cumin and coriander seeds. Once the spices start to sizzle, add the onions and stir-fry until light golden brown. Add the garlic and ginger, and cook for 1 min until it no longer smells raw. Add a splash of water if the garlic and ginger start to stick to the pan, then add the tomatoes and cook until soft. Add 1 tsp salt, red chilli powder and the beef, turn up the heat and stir-fry until the meat is sealed on all sides. Reduce the heat to medium-low, cover loosely and cook for about 10 mins. Turn up the heat and add the grated beetroots. Stir-fry for a few mins, then cover the pan, reduce the heat and cook for 1 hr 15 mins or until the meat is tender. Stir from time to time and add a little water if the curry becomes dry. The finished dish should be a thick mushy curry with chunky, tender beef pieces and softened beetroots. Sprinkle with the coriander leaves and green chilli; add a squeeze of lime, and serve with naan or basmati rice.','15 mins','1 hr and 40 mins'),
(728,'slow cooker beef curry','https://www.bbcgoodfood.com/recipes/home-style-beef-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/home-style-beef-curry-0907fd9.jpg','This slow-cooked beef curry is easy to make and perfect for feeding the family – add chunks of potato for an extra hearty dish. Serve with rice and naan bread',19,4,1,1,12,'Heat your slow cooker if you need to. Fry the beef in batches in 2 tbsp oil until it is browned all over and tip into the slow cooker. Put the remaining oil in the frying pan and fry the onion over a low heat until it starts to soften. Add the garlic and ginger and fry for 1 minute. Add all the spices and fry for 1 minute or until the spices start to smell fragrant. Add the coconut milk and bring to a simmer, then tip everything into the slow cooker. Cook for 4 hours on high or 8 hours on low. Stir in the coriander and serve.','20 mins','4 hrs and 30 mins - 8 hrs and 30 mins'),
(729,'big beef in red wine pie','https://www.bbcgoodfood.com/recipes/big-beef-red-wine-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-913456_11-8068d9b.jpg','Slow cook a cheap cut of meat like braising steak in rich red wine gravy for the perfect base to a classic pie',7,6,2,1,12,'If you have time, place the beef in a large bowl with the herbs and star anis and season well with pepper. Pour over all the wine, cover with cling film and chill for up to 24 hrs. The next day sit a colander over another bowl and drain the beef well. Pick out the herbs and star anis and set aside. If you don’t have time just start with all the ingredients prepared and ready to cook. Heat oven to 170C/150C fan/gas 3 - 4. Heat a drizzle of oil in a large casserole until very hot then brown the meat well in batches. For each batch, heat more oil and brown for around 10 mins before transferring to a plate and repeating the process. When all the meat is done add the vegetables and cook for 5 mins until starting to colour then add the herbs and star anis and cook for a minute more. Sprinkle sugar over the vegetables, then scatter over the flour and stir together. Cook for a few minutes until starting to brown, stir in the tomato purée then pour over the wine, scraping the bottom of the pan with your spoon. Tip in the beef and any juices then crumble in the stock cubes. Pour over just enough boiling water from the kettle to cover the meat and vegetables. Season with salt and pepper and bring everything to the boil, stir well, cover and place in the oven for 2 1/2 hrs until the meat is really tender. This can be done up to 3 days in advance and chilled until ready to make the pie or frozen for up to 3 months. To make the pie heat oven to 200C/180C fan/ gas 6. Tip the filling into a 2-litre pie or baking dish. Roll the pastry out on a lightly floured surface to fit the pie dish with a slight overhang and drape over the dish. Crimp the edges if you are able to and poke a couple of little steam holes in the middle. Brush the pastry with the beaten egg and sprinkle with flaky sea salt. Bake for 30 mins until golden brown. This is goes well with mash and buttered green beans.','25 mins','3 hrs'),
(730,'beef, potato & banana curry with cashew rice','https://www.bbcgoodfood.com/recipes/beef-potato-banana-curry-cashew-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/curry_5-fcb6010.jpg','Banana and tamarind add a rich sweetness to this slow-cooked spice pot with nutty basmati and coconut sauce',17,4,1,1,12,'Heat oven to 180C/160C fan/gas 4 and heat half the oil in a large frying pan. Brown the beef in batches, and transfer to a flameproof casserole dish. Add the remaining oil to the pan with the onions, and fry gently until softened. Stir in the lemongrass, ginger, tamarind, garlic, turmeric, curry powder and sugar, and fry for a few mins until fragrant. Add the coconut milk with 1 can of water, stir into the beef and bring to a simmer. Cover and bake for 1 hr. Meanwhile, toast the cashews in a dry frying pan until golden. After 1 hr, stir in the new potatoes and cook for 1 hr more until the potatoes are tender, removing the lid halfway. The casserole can now be cooled and chilled, or frozen, if preparing ahead. Put the casserole back onto the hob. Stir in the bananas and garam masala and simmer, uncovered, for 10 mins until the sauce has thickened. Cook the rice following pack instructions, then mix with the cashews, crispy onions and some salt. Scatter the curry with coriander, season well, then taste – it may need a squeeze of lemon to finish it off.','30 mins','2 hrs and 30 mins'),
(731,'lamb chops with griddled courgette & feta salad','https://www.bbcgoodfood.com/recipes/lamb-chops-griddled-courgette-feta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb-chops-with-griddled-chops-feta-salad-8ca0abd.jpg','Griddled chops are great with this warm courgette, mangetout and cheese salad - a simple, speedy weeknight meal for two',86,2,1,1,12,'Heat a griddle over a high heat. Mix 1 tbsp oil with the thyme and seasoning, and brush over the lamb chops. Cook the chops for 3-4 mins each side, then stand them up and griddle the fat for 1-2 mins. Remove to a plate, wrap in foil and leave to rest. Brush the courgettes with the remaining oil and season. Griddle for 2-3 mins each side until they are soft and have griddle marks – you may need to do this in 2 batches. Meanwhile, cook the mangetout for 2-3 mins in a medium pan of boiling water. Drain and tip into a large bowl with the courgettes. To make a dressing, mix the vinegar, mustard, chilli flakes (if using), mint and basil. Toss the courgettes and mangetout in the dressing. Crumble over the feta and serve the veg with the lamb chops.','15 mins','25 mins'),
(793,'vegan lentil & sweet potato cottage pie','https://www.bbcgoodfood.com/recipes/lentil-sweet-potato-cottage-pie','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Lentil-and-sweet-potato-cottage-pie-75d994c.jpg','Serve your family our hearty meat-free pie for dinner. The rich umami sauce is made with soy and mustard powder, and pumpkin seeds add a good crunch',46,6,1,1,10,'Tip the onions, carrots, celery and olive oil into a large saucepan. Cook over a medium heat for 8-10 mins until softened, add the garlic and fry for another minute. Pour in the wine to deglaze the pan (scraping up any bits stuck on the bottom), and simmer for 2 mins until most of the wine has evaporated. Add the thyme, tomato purée, soy sauce, caster sugar and vinegar to the pan and stir to combine. Tip in the lentils, tomatoes and 100ml water and simmer for 10-12 mins until reduced and thick enough to coat the back of a spoon. Meanwhile, make the mash topping. Bring a large pan of water to the boil, add the sweet potatoes and simmer for 10-15 mins until cooked all the way through. Drain, then add the dairy-free margarine, mustard powder and milk, and mash until smooth along with plenty of salt and pepper. Heat the oven to 200C/180C fan/gas 6. Season the filling and pour it into a 25cm x 20cm x 7cm casserole dish. Top with the mash, starting with spoonfuls on the outside corners and working your way inwards so the filling doesn’t spill out. Sprinkle the pumpkin seeds on top and bake for 30-35 mins until golden and bubbling. Once baked and left to cool, the pie will keep frozen for up to three months. Defrost thoroughly overnight, then reheat in the oven until piping hot.','15 mins','1 hr'),
(734,'lamb chops with thyme, chilli & greek htipiti','https://www.bbcgoodfood.com/recipes/lamb-chops-with-thyme-chilli-greek-htipiti','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Lamb-chops-with-thyme-chilli-and-Greek-htipiti-a06812c.jpg','Serve lamp chops with htipiti for a lovely Greek-inspired dish with fabulous flavours. Olive oil-roasted baby potatoes make a great accompaniment',70,4,1,1,12,'Put the lamb cutlets in a bowl with the thyme, chilli flakes and oil. Toss together to coat, then leave to marinate in the fridge for 30 mins-1 hr. For the htipiti, cut the peppers into small pieces. Put in a bowl and add all the other ingredients. Leave for about 15 mins for the flavour to infuse. Heat a griddle pan or frying pan. When it’s really hot, lift the chops out of the marinade. Cook them, a few at a time, for 3 mins either side, seasoning as you go. Serve the chops with the sauce, and some baby potatoes, if you like.','10 mins','18 mins'),
(735,'spiced lamb chops & herb salad','https://www.bbcgoodfood.com/recipes/spiced-lamb-chops-herb-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-lamb-chops-herb-salad-2645be1.jpg','Marinated with garlic, lemon and North African spice blend ras el hanout, these lamb chops are served with a light herb and pomegranate salad',92,2,1,1,12,'Put the lamb chops in a large bowl. Add the ras el hanout, garlic, oil, most of the lemon juice and some seasoning. Toss everything together and set aside for at least 15 mins. Combine the herbs, onion, pomegranate seeds and lemon zest in a bowl. Season and set aside. Heat a griddle or grill until very hot and cook the lamb chops for 3-4 mins each side, until cooked but still pink and juicy on the inside, then leave to rest for a few mins. Serve the chops with the salad, houmous and pitta, and squeeze over the remaining lemon juice.','15 mins','8 mins'),
(736,'griddled lamb with wild rice salad','https://www.bbcgoodfood.com/recipes/griddled-lamb-wild-rice-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/griddled-lamb-with-wild-rice-salad-87ec7d0.jpg','Pan-fried lamb cutlets pair beautifully with this colourful rice salad, studded with olives, diced tomatoes and toasted almonds',5,4,1,1,12,'Cook the rice in boiling salted water following pack instructions. Drain and immediately run under cold water until the rice is completely cold. Meanwhile, put the oil, vinegar, garlic and cumin in a screw-top jar. Season generously and shake well to mix. Toast the almonds in a small frying pan, stirring, for 1-2 mins. Cool, then roughly chop. Mix the rice with the almonds, tomatoes, olives and parsley. Heat a griddle pan and season the lamb well. When the pan is hot, cook the lamb, turning occasionally, for 7-8 mins. Remove from the pan, cover with foil and leave to rest for 2 mins. Pour the dressing over the rice salad and mix well. Top with the lamb cutlets and serve.','10 mins','35 mins'),
(737,'griddled lamb with spiced new potatoes','https://www.bbcgoodfood.com/recipes/griddled-lamb-spiced-new-potatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb_6-f0e27fb.jpg','Cumin-spiced new potatoes give a kick to this delicious lamb dish, the perfect comforting dinner',5,4,1,1,12,'Put the potatoes in a large pan, cover with water, add a little salt and bring to the boil. Cook for 10-15 mins or until tender. Drain and set aside half for tomorrow. Return the rest of the potatoes to the drained pan with 1 tbsp of the oil, the garlic, preserved lemon and cumin. Keep warm while you cook the lamb. Rub the lamb chops with the remaining oil and season. Heat a griddle pan and cook for 3 mins each side or until cooked to your liking. Season the potatoes and stir in the mint, then serve with the griddled lamb and peas or salad.','10 mins','30 mins'),
(738,'lemon & rosemary traybake lamb','https://www.bbcgoodfood.com/recipes/lemon-rosemary-traybake-lamb','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb_1-b7752b0.jpg','Get two of your 5-a-day with this simple Mediterranean-inspired one pan supper with tasty cutlets, new potatoes, peppers, olives and capers',104,4,1,1,12,'Whisk the lemon zest and juice with 1 tbsp olive oil, the garlic, chopped rosemary leaves and seasoning. Add the lamb cutlets, toss to coat, then set aside to marinate for 30 mins if you have the time. Heat oven to 220C/200C fan/gas 7. Toss the potatoes with ½ tbsp olive oil and some seasoning. Tip into a large, shallow roasting dish and put in the oven for 15 mins. Remove the dish and shake to loosen the potatoes, then top with the peppers, lemon slices, rosemary sprigs and tomatoes. Drizzle with the remaining oil and some seasoning. Return to the oven for 10 mins. Place the lamb cutlets on top of the veg. Scatter over the olives and capers. Cook for a final 10-12 mins, turning the lamb halfway until it is just pink. Serve straight from the dish.','15 mins','35 mins'),
(820,'vegan pizza margherita','https://www.bbcgoodfood.com/recipes/vegan-pizza-margherita','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-pizza-67125d6.jpg','Vegans needn''t miss out on pizza Margherita. Our recipe combines the classic flavours of this Italian comfort food using plant-based substitutes',29,2,1,1,10,'Put the flour, yeast and sugar in a large bowl. Measure 150ml of cold water and 150ml boiling water into a jug and mix them together – this will mean your water is a good temperature for the yeast. Add the oil and 1 tsp salt to the warm water then pour it over the flour. Stir well with a spoon then start to knead the mixture together in the bowl until it forms a soft and slightly sticky dough. If it’s too dry add a splash of cold water. Dust a little flour on the work surface and knead the dough for 10 mins. Put it back in the mixing bowl and cover with cling film greased with a few drops of olive oil. Leave to rise in a warm place for 1 hr or until doubled in size. Heat oven to 220C/200C/gas 9 and put a baking sheet or pizza stone on the top shelf to heat up. Once the dough has risen, knock it back by punching it a couple of times with your fist then kneading it again on a floured surface. It should be springy and a lot less sticky. Set aside while you prepare the sauce. Put all the ingredients for the tomato sauce together in a bowl, season with salt, pepper and a pinch of sugar if you like and mix well. Set aside until needed. Divide the dough into 2 or 4 pieces (depending on whether you want to make large or small pizzas), shape into balls and flatten each piece out as thin as you can get it with a rolling pin or using your hands. Make sure the dough is well dusted with flour to stop it sticking. Dust another baking sheet with flour then put a pizza base on top – spread 4-5 tbsp of the tomato sauce on top and add some sliced tomatoes and grated vegan cheese. Drizzle with a little olive oil and bake in the oven on top of your preheated baking tray for 10-12 mins or until the base is puffed up and the vegan cheese has melted and is bubbling and golden in patches. Repeat with the rest of the dough and topping. Serve the pizzas with fresh basil leaves or chilli oil if you like and sprinkle over vegan parmesan just after baking.','15 mins','15 mins'),
(739,'lemon & herb lamb lollipops','https://www.bbcgoodfood.com/recipes/lemon-herb-lamb-lollipops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lollies_0-5e02344.jpg','Serve these crunchy cutlets at room temperature alongside a dip and encourage everyone to dig in with their hands',6,8,2,1,12,'Slice the lamb cutlets between the bones to give you 16 pieces. Remove any big pieces of fat. Cover the cutlets with cling film and lightly bash the meat with a rolling pin to flatten slightly. Put the cutlets in a bowl and sprinkle with flour and some seasoning, tossing to coat. Whisk the egg and milk in a shallow bowl. Place the breadcrumbs, parsley and lemon zest in another. Heat the oven to 200C/180C fan /gas 6. Heat enough oil in a large frying pan to come 0.5cm up the side. Dip each lamb cutlet into the egg mixture, then into the breadcrumbs, pressing gently to help crumbs stick. Cook each cutlet in the hot oil for 2 mins each side until golden brown – you will need to do this in batches. Drain on a plate lined with kitchen paper. When all cutlets have been fried, place on a baking tray and cook in the oven for 3 mins more. This will ensure that any parts of the bone that did not touch the pan have been cooked. Leave to cool, then chill if making ahead or pack into containers for your picnic.','25 mins','30 mins'),
(740,'sizzled masala lamb with chopped salad','https://www.bbcgoodfood.com/recipes/sizzled-masala-lamb-chopped-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1284456_8-1bb16c1.jpg','Marinate lamb chops with curry paste and serve up a colourful meal for one with crisp radish, tomato, cucumber and herb salad',5,1,1,1,12,'Heat grill. Rub the oil and curry paste all over the chops and season. Put the chops on a baking tray and grill for about 4-5 mins each side for medium, depending on the thickness of the chop, or to your liking. Allow to rest for a couple of mins. Meanwhile, put the remaining ingredients (except the garnish) in a bowl, give everything a stir, then season. Spoon the salad onto a plate and lay the chops on top. Scatter with the remaining herb leaves.','10 mins','8 mins - 10 mins'),
(741,'chilli ginger lamb chops','https://www.bbcgoodfood.com/recipes/chilli-ginger-lamb-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1036499_11-3b50edb.jpg','Marinate your chops with ginger, lemon, chilli and cumin then chargrill until juicy in the middle',4,4,1,1,12,'Put the garlic in a bowl with the ginger, lemon juice, oil, spices and seasoning. Blitz with a hand blender until smooth, then use to coat the lamb chops on both sides. Leave to marinate in the fridge for a couple of hours or overnight. Heat a barbecue until hot. Barbecue the chops over the coals for 3 mins on each side until cooked but still pink and juicy in the centre.','10 mins','6 mins - 8 mins'),
(742,'spiced lamb with lemon & herb quinoa','https://www.bbcgoodfood.com/recipes/spiced-lamb-lemon-herb-quinoa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-889489_11-a3042a1.jpg','Quickly griddle marinated lamb on a sizzling grill then serve with a healthy grain salad with herbs and citrus',7,4,1,1,12,'In a pestle with a mortar crush the cumin seeds then add the garlic clove, chilli and some salt and pepper. Mash to a paste. Stir in the lemon juice and olive oil and pour half the mix over the lamb chops and leave to marinate. Set the other half aside. Rinse the quinoa in cold water then place in a saucepan with twice the volume of water. Place on the heat and bring to the boil then turn down the heat slightly and boil gently for 10 mins. Turn the heat off and stir through the reserved dressing. Cover and leave for another 10 mins. Meanwhile heat a griddle pan or grill to high and cook the lamb for about 3-5 mins on each side or until cooked to your liking. When ready to serve stir the spring onions and coriander through the quinoa and serve with the lamb and steamed stem broccoli dressed in a drizzle more oil.','15 mins','20 mins'),
(743,'lamb chops with red pepper & mint salsa','https://www.bbcgoodfood.com/recipes/lamb-chops-red-pepper-mint-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-659579_11-10ba537.jpg','This summery salsa can be made a couple of days ahead and kept in the fridge. Grill the chops in the oven or on the bbq',43,6,1,1,12,'Season the chops all over with salt and pepper. Make a slit between the bone and meat in each chop, then push a sliver of garlic and small sprig of rosemary into each. For the salsa, put the peppers in a mini chopper or food processor with the garlic, chilli, mint and lemon. Blitz until finely chopped, but not puréed. Add the oil and blitz briefly to mix. Tip into a bowl and leave at room temperature until serving time. Grill or barbecue the chops for 5-7 mins each side until brown, then serve on a platter with the salsa (stir before serving).','15 mins','15 mins'),
(744,'rosemary roast lamb chops','https://www.bbcgoodfood.com/recipes/rosemary-roast-lamb-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513788_11-8c39795.jpg','This one-pan lamb chops supper only requires some quick assembling - pack in the Mediterranean flavours then let the oven do the work',7,4,1,1,12,'Turn the oven to fan 200C/ conventional 220C/gas 7. Heat half the oil in a flameproof roasting tin or ovenproof sauté pan. Brown the lamb for 2 minutes on each side, then lift out of the pan and set aside. Add the rest of the oil to the pan, add the potatoes, then fry for 4-5 minutes, until starting to brown. Add the rosemary and garlic, then return the lamb to the pan, too. Roast in the oven for 20 minutes, then scatter over the tomatoes and drizzle with the balsamic vinegar. Place back in the oven for 5 minutes, until the tomatoes just begin to split. Remove from the oven and serve straight from the dish.','10 mins','30 mins'),
(745,'one-pan lamb & couscous','https://www.bbcgoodfood.com/recipes/one-pan-lamb-couscous','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-513559_10-8d3d5d6.jpg','Whip up a deliciously flavoursome lamb dish in one pot and within half an hour',5,2,1,1,12,'Rub the lamb with half the harissa paste. Heat the oil in a frying pan and fry the lamb for 3 mins on each side for medium-rare, or a little longer for well-done. Lift the meat out of the pan and tip in the remaining harissa, the dried fruit and nuts, couscous and chickpeas, then stir to coat in the paste. Take the pan off the heat and pour in the stock. Stir briefly, return the lamb to the pan and cover with a lid or tightly-fitting foil for 10 mins until the couscous has absorbed all the liquid. Fluff up the couscous with a fork, season to taste and fold the mint leaves through to serve.','5 mins','20 mins'),
(746,'imam bayildi with bbq lamb & tzatziki','https://www.bbcgoodfood.com/recipes/imam-bayildi-bbq-lamb-tzatziki','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-356518_11-edb81df.jpg','There are hundreds of versions of this Turkish aubergine dish. It is authentically quite oily, but reduce the oil if you prefer',19,6,1,1,12,'Heat oven to 190C/170C fan/gas 5. Halve the aubergines lengthways and score the flesh side deeply, brush with a good layer of olive oil and put on a baking sheet. Roast for 20 mins or until the flesh is soft enough to scoop out. Fry the onion in a little oil until soft, add the garlic and cinnamon and fry for 1 min. Once the aubergines are cool enough to handle, scoop out the centres. Roughly chop the flesh and add it to the onions. Halve the tomatoes, scoop the seeds and juice into a sieve set over a bowl, then chop the flesh. Add the chopped tomatoes to the pan and cook everything for 10 mins until nice and soft. Add a little more oil if you need to. Stir in the parsley, leaving a little for scattering at the end. Lay the aubergine halves in a baking dish and divide the tomato mixture between them. Pour over the juice from the tomatoes, drizzle with more olive oil and bake for 30 mins until the aubergines have collapsed. Meanwhile, mix the tzatziki ingredients together and put in a small serving bowl. Season the lamb with salt, black pepper and a pinch of paprika. Griddle, grill or barbecue for 3 mins on each side or until the fat is nicely browned, then put in a serving dish and squeeze over the lemon halves. Scatter the aubergines with parsley, then serve with the lamb and tzatziki.','15 mins','1 hr and 30 mins'),
(747,'quick roast lamb','https://www.bbcgoodfood.com/recipes/quick-roast-lamb','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-339027_11-b6f31e5.jpg','When you want the roast experience but haven''t got hours to spare, this version really hits the spot',5,2,1,1,12,'Heat oven to 220C/fan 200C/gas 7. Put the potatoes and carrots onto a baking tray, toss with the oil and rosemary, then season well. Roast for 15 mins on the top shelf until the veg is golden and almost tender. Meanwhile, make the gravy. Put the wine and stock into a small pan, then boil until reduced by about two-thirds. Stir in the redcurrant jelly, season and keep warm. Rub the lamb in a little oil, then season. Tuck the lamb in amongst the veg, then return to the oven for 8-10 mins, turning the lamb halfway through. Serve with the redcurrant gravy and some green veg.','30 mins','No Time'),
(748,'greek-style lamb with lemon & thyme','https://www.bbcgoodfood.com/recipes/greek-style-lamb-lemon-thyme','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-329531_12-ff796bc.jpg','Marinate lamb chops in a zesty mixture for 15 minutes, then coat with yogurt and grill over the coals',25,6,1,1,12,'Sit lamb in a large shallow dish, then mix the lemon zest and juice, thyme leaves and garlic together. Pour over, turn to coat, and marinate at room temp for 15 mins. Mix the yogurt with some seasoning, lift the lamb out of the marinade and rub with the yogurt. Barbecue or grill for 5-15 mins, turning once, until the lamb is cooked to your liking. Scatter with a little more thyme, if you like, and serve with dollops of houmous, some pitta bread and salad leaves.','10 mins','15 mins'),
(749,'grilled lamb with hummus mash','https://www.bbcgoodfood.com/recipes/grilled-lamb-hummus-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-265613_11-21bf090.jpg','Forget potatoes, try this tasty mash made with chickpeas - you''ll be hooked in no time',5,4,1,1,12,'Heat grill to high. Put the chops and sliced courgettes on a baking tray, then toss with 1 tbsp olive oil and season. Grill for 3-5 mins, then turn everything over and cook for 5 mins more or until the lamb is cooked through. Meanwhile, cut the onion into thin halfmoon slices and tip into a bowl with another tbsp olive oil and a good squeeze of lemon juice. Leave to marinate. Tip the chickpeas into a sieve. Pour over boiling water to warm them, then whizz with the remaining oil and half the feta. Add a few tbsps of water to loosen, then season to taste. Serve with the lamb, courgettes and onions, sprinkled with the mint and remaining feta.','10 mins','10 mins'),
(750,'lamb chops with fruity couscous & mint','https://www.bbcgoodfood.com/recipes/lamb-chops-fruity-couscous-mint','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-16173_10-392bf4a.jpg','This Moroccan-style spiced lamb dish is simple enough to do on a weeknight, but also makes a great dish to serve to friends',10,4,1,1,12,'Place the lamb in a bowl. Sprinkle over the paprika, half the onion, 1 tbsp vinegar and ½ tbsp oil. Season, then set aside. To make the couscous, boil the kettle and tip the couscous into a heatproof bowl. Stir in the pine nuts, dates, lemon zest and half the juice. Pour over 140ml just-boiled water, push the coucous to the sides of the bowl, cover, then leave to stand for 10 mins. Heat grill to high. Lift the lamb out of its marinade, grill for 2-3 mins on each side, then rest, wrapped in foil, for 10 mins. Stir half the chopped herbs and some seasoning into the couscous, then add the remaining lemon juice to taste. Mix together the remaining herbs, red onion, vinegar, oil and seasoning to make a herby vinaigrette. Serve the lamb and couscous with the vinaigrette.','10 mins','6 mins'),
(751,'minty lamb with warm veg salad','https://www.bbcgoodfood.com/recipes/minty-lamb-warm-veg-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-13650_11-8376d86.jpg','Pair lamb with homemade mint sauce for a bit of zing - or make it veggie and use halloumi instead',5,4,1,1,12,'Heat oven to 220C/fan 200C/gas 7. Mix together the carrots, parsnips and onions in a roasting tin with 1 tbsp oil, then roast for 15 mins. Toss in the beetroot, drizzle with 2 tbsp balsamic vinegar, then roast for 15 mins more until just tender. Meanwhile, heat 1 tbsp oil in a non-stick frying pan. Fry the chops for 6-8 mins, turning halfway, until cooked to your liking. Whizz half the mint in a small food processor with the remaining vinegar and oil, then season. Add a little water if the mixture is a bit thick. Toss the remaining mint with the roasted veg, then season and serve alongside the chops, drizzled with mint sauce.','10 mins','30 mins'),
(752,'warm salad of spring vegetables with griddled lamb','https://www.bbcgoodfood.com/recipes/warm-salad-spring-vegetables-griddled-lamb','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-9601_12-7907a8e.jpg','This warm, dressed salad is delicious on its own, but if you need a bit more bite, grilled lamb is the perfect choice',7,6,1,1,12,'Get a bowl of iced water at the ready and bring a large pan of salted water to the boil. Cook the asparagus in the water for 3 mins, then scoop into the iced water with a slotted spoon. Cook the peas for 2 mins, then place in the iced water. Cook the broad beans for another 2 mins, pop into the iced water, then remove them from their skins. Cut a cross in the bottom of each tomato, then blanch in boiling water for about 10 secs before placing in iced water. Peel, quarter and deseed the tomatoes and cut the flesh into small chunks. Heat the oil and coriander seeds gently in a small saucepan, then take off the heat and stir through all the veg and the vinegar. Season with salt and pepper, and stir through the tarragon and mint. Heat a large griddle pan or barbecue. Season the lamb with salt and pepper, and cook for 4 mins on each side for medium, or 6 mins for well done. Serve each lamb chop with the vegetable dressing.','30 mins','15 mins'),
(753,'lamb with spicy chickpeas','https://www.bbcgoodfood.com/recipes/lamb-spicy-chickpeas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-9643_11-fe8ed02.jpg','A storecupboard favourite, harissa can turn a bit of meat into a real treat. Leftovers make a good salad too',5,2,1,1,12,'Heat grill to high. Season the lamb, then grill for 5 mins each side until browned. Meanwhile, heat the oil in a pan, add the onion, then fry for 2 mins. Stir in the cauliflower, cumin and harissa paste, then cook for 1 min more. Add 200ml water, season well, then bring to the boil. Cover, cook for 5 mins until the cauliflower is just tender, then stir in the chickpeas and cook for 2 mins more. Stir in the parsley and orange juice, then serve with the lamb.','5 mins','15 mins'),
(754,'pork chops with rhubarb & grains','https://www.bbcgoodfood.com/recipes/pork-chops-rhubarb-grains','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-chops-with-rhubarb-grains-746abd4.jpg','Rhubarb complements the pork chops in this dish perfectly, adding a touch of sharpness. Served with mixed grains, it''s an easy midweek supper',41,2,1,1,12,'Heat oven to 200C/180C fan/gas 6. In a small roasting tin, toss the rhubarb, onion wedges and garlic with the balsamic vinegar, a little olive oil and some seasoning. Roast in the oven for 20 mins. Meanwhile, melt the butter over a high heat and fry the rosemary. Add the pork and cook for 2 mins on each side until nicely browned, then remove from the heat. Take the roasting tin out of the oven and add the grains and chicken stock and mix together. Add the meat and the rosemary to the roasting tin, placing the chops on top. Roast for 10-12 mins further, or until the pork is cooked through. Serve each chop with a big spoonful of the grains, scattered with parsley.','10 mins','30 mins'),
(755,'summer pork, fennel & beans','https://www.bbcgoodfood.com/recipes/summer-pork-fennel-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/3.summer-pork-fennel-beans-349fd1d-scaled.jpg','Pan-fry thick pork chops, then roast with cherry tomatoes, shallots and cannellini beans to make an easy one-pot dinner',33,4,1,1,12,'Heat oven to 200C/180C fan/gas 6. Heat 1 tbsp oil and the butter in a large ovenproof frying pan or wide flameproof casserole dish. Season the chops generously and fry over a medium-high heat for 3 mins each side until lightly golden – brown the edges of the fat for 30 secs or so too. Remove to a plate. Add the sliced shallot and fennel to the pan and cook for 2 mins, stirring now and then. Splash in the wine and simmer for a few secs to reduce a little. Add the lemon wedges, drizzle with the remaining oil and put in the oven to roast for 10 mins. Toss the veg gently, sit the pork chops on top and roast for another 20 mins. Add the tomatoes to the pan and cook for 5 mins more or until the chops are cooked through, the fennel is tender and turning golden, and the tomatoes are soft. Meanwhile, mix the chopped shallot, the lemon juice, beans and fennel seeds. Remove the meat to a plate to rest for a few mins while you fold the dressed beans and basil leaves into the pan. Add the resting juices, season to taste, then serve with the pork.','15 mins','45 mins'),
(1568,'greek courgetti salad','https://www.bbcgoodfood.com/recipes/greek-courgetti-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/greek-courgetti-salad-961c8de.jpg','Grab a pack of courgetti or spiralize your own for a simple vegetarian supper',16,2,1,3,16,'Slice the cucumber on the diagonal and crumble the feta. Combine all the ingredients in a bowl with a little extra virgin olive oil and some seasoning, then divide between plates.','3 mins - 10 mins','No Time'),
(756,'herb-brined pork chops','https://www.bbcgoodfood.com/recipes/herb-brined-pork-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/herb-brined-pork-chops-b88bd43.jpg','Soaking tender pork in a juniper, coriander and thyme brine keeps it succulent and plump - griddle until just charred for an attractive main',105,6,1,1,12,'Put all the brining ingredients in a large saucepan with 1 litre of water and bring to the boil. Remove from the heat and leave to cool completely. Once cooled, put the chops in the brine, cover with cling film and leave in the fridge for 2 hrs. Remove the chops from the brine, rinse under the cold tap and pat dry with kitchen paper. Heat oven to 200C/180C fan/gas 6. Heat a large griddle pan over a high heat and fry the pork chops for 3-4 mins each side or until starting to char – you may need to do this in 2 batches. Transfer to a baking tray lined with foil and roast for 10-12 mins until cooked through.','10 mins','35 mins - 40 mins'),
(757,'oven-baked pork chops','https://www.bbcgoodfood.com/recipes/oven-baked-pork-chops','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Oven-Baked-Pork-Chops-2dfeaea.jpg','Enjoy oven-baked pork chops cooked in a honey and wholegrain mustard glaze with new potatoes for a deliciously easy dinner, just add your favourite veg',12,4,1,1,12,'Heat the oven to 200C/fan 180C/gas 6. Toss the potatoes, oil, garlic and thyme on a shallow roasting tray and mix will with your hands to coat in the oil. Season well. Roast for 25 mins until the potatoes are just turning tender when pierced with a knife. Whisk the honey and mustard together in a small bowl. Season the pork chops with salt and pepper, then brush with half the marinade. Nestle into the potatoes on the tray, and bake for another 10 mins before turning the chops and spooning over the remaining glaze. Continue to bake for 10 more mins until cooked through.','10 mins','45 mins'),
(758,'pork chops lyonnaise','https://www.bbcgoodfood.com/recipes/pork-chops-breadcrumbs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-b5053a5.jpg','Use up leftover bread to make a crumb coating for our tasty Lyonnaise-style pork chops. Serve with a salad and sautéed potatoes',70,4,1,1,12,'Heat oven to 210C/190C fan/gas 7. Heat the oil in a frying pan and brown the chops all over, seasoning as you go. It’s important to brown the fat as well as the meat, so prop the chops up in the pan on their fatty bits so they get coloured too. You’re not cooking the chops all the way through. Transfer them to a shallow ovenproof dish in which they fit snugly in a single layer. Heat the butter in the pan and sauté the onions over a low heat until they’re really soft and pale gold. This will take about 15 mins, so keep going. Stir the mustard and the thyme into the onions. Spread the onion mixture thickly over each chop and press the breadcrumbs on top. Put in the oven for 20 mins, or until the tops are golden brown and the pork is cooked through.','10 mins','50 mins'),
(759,'pork & parsnip traybake','https://www.bbcgoodfood.com/recipes/pork-parsnip-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-parsnip-traybake-7ec5875.jpg','Sear pork chops, then oven-roast with red onions and root vegetables for an easy weeknight dinner',5,4,1,1,12,'Heat oven to 220C/200C fan/gas 7. Put the vegetables in a roasting tin, season and toss with 2 tbsp oil and 1 tbsp mustard. Roast for 20 mins. Meanwhile, place a frying pan over a high heat. Season the pork chops and rub with the remaining oil. Fry the chops for 30 secs-1 min each side until just browned – turn on their sides to brown any fat. Stir the veg, then place the chops on top and rub them with the remaining mustard. Roast for a further 15 mins. Drizzle with honey and scatter over the sage, then return to the oven for 5 mins or until the pork is cooked through. Serve with the juices from the tin.','10 mins','40 mins'),
(760,'miso mustard pork chops with sesame green beans','https://www.bbcgoodfood.com/recipes/miso-mustard-pork-chops-sesame-green-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso_mustard_pork_chops_with_sesame_green_beans-1c07d2f.jpg','This easy Asian inspired pork recipe is ready in just 15 minutes - use grapefruit and lime as an alternative to fruity yuzu juice',10,2,1,1,12,'Mix the sauce ingredients together in a small bowl. Rub a spoonful of the sauce over each pork chop. Reserve the rest to serve alongside. Heat a griddle pan until hot and bring a pan of water to the boil. Drizzle the chops with 1 tsp of the sesame oil. Cook in the griddle pan for 5 mins each side, or until cooked through. Meanwhile, cook the beans in the boiling water for 2 mins, then drain. Heat the remaining oil in the same pan, add the beans and sesame seeds, and cook for 2 mins. Serve the chops with the beans, the remaining miso sauce and an extra sprinkling of sesame seeds.','5 mins','10 mins'),
(761,'cumin pork chops with honey carrots','https://www.bbcgoodfood.com/recipes/cumin-pork-chops-honey-carrots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pork-chop-ea045c5.jpg','Flavour your meat with earthy cumin and serve with sweet Chantenay or baby carrots and natural yogurt',5,2,1,1,12,'In a large bowl, whisk together half the oil, half the honey, the lemon zest and juice, and the cumin seeds. Add the pork chops and ensure they are well coated. Heat a large frying pan and add the chops along with the glaze. Cook for 5-7 mins on each side, basting with the glaze every so often. Remove from the pan, wrap in foil and leave to rest. Meanwhile, boil the carrots until just tender, about 10 mins. Mix together the remaining oil and honey, the cinnamon and some seasoning. Add this to the pan you’ve just cooked the pork in, along with the carrots. Toss to coat, heat through, then serve the carrots alongside the chops with a dollop of natural yogurt.','5 mins','15 mins'),
(762,'mustard chops & chips','https://www.bbcgoodfood.com/recipes/mustard-chops-chips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-993689_11-21b5ae6.jpg','Dijon mustard-smeared pork with a piquant fennel and green bean salad and crispy potato fries',5,4,1,1,12,'Heat oven to 220C/200C fan/gas 7. Toss the potatoes with 1 tbsp oil and some seasoning in a baking tray. Spread out in a single layer and bake for 20 mins. Add the fennel seeds and paprika, turn potatoes and bake for 20 mins more until golden. Meanwhile, cook the green beans in boiling water for 5-6 mins. Run under cold water and drain well. Mix 1 tbsp more oil, the vinegar, shallot, sugar, 1 tsp mustard and some seasoning. Toss the fennel and green beans in the dressing. Rub the remaining oil on the chops and season. Pan-fry for about 7 mins on each side until cooked through. Make sure to brown fat on the edge, too. Smear with remaining mustard and rest for a few mins. Serve with the chips and bean salad, drizzling over the mustardy resting juices.','15 mins','40 mins'),
(763,'fennel-crusted pork chops with winter celeriac slaw','https://www.bbcgoodfood.com/recipes/fennel-crusted-pork-chops-winter-celeriac-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-911566_11-1329f19.jpg','The aniseed flavours of the meat''s marinade perfectly match the earthy celeriac coleslaw - a robust supper, with enough leftovers for lunch',5,4,1,1,12,'Heat oven to 190C/170C fan/gas 5. Put the oil, fennel seeds, oregano, chilli and some seasoning on a shallow plate. Rub each pork chop on both sides in the mixture. Place on a baking tray and roast for 40 mins. Meanwhile, make the slaw. Put the mayonnaise, vinegar, Worcestershire sauce, mustard, sugar and a good pinch of salt and pepper in a large bowl. Mix well, then toss in the vegetables. When the pork chops are done, set aside 2 of the chops with one-third of the slaw for lunch. Divide remaining slaw between 4 plates and add a pork chop to each.','15 mins','40 mins'),
(764,'herb & lemon pork chops','https://www.bbcgoodfood.com/recipes/herb-lemon-pork-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-376506_12-8faefa3.jpg','Cook these fresh, zesty chops on the barbecue, griddle or in a frying pan',33,6,1,1,12,'Sit the chops in a dish. Mix together the garlic, parsley, fennel seeds, lemon zest and juice, oil and bay, then rub all over the chops. Leave the chops to marinate for at least an hour, or up to a day. Heat a barbecue, frying pan or griddle pan. Season the chops, then cook for 5-8 mins on each side until cooked through (if cooking in batches, wrap the cooked chops in foil while you finish the rest). Add the lemon quarters for the last min to char, then serve alongside the pork.','15 mins','15 mins'),
(765,'pork schnitzel','https://www.bbcgoodfood.com/recipes/pork-schnitzel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-494495_12-16f2358.jpg','Children love anything breadcrumbed and simple schnitzels won''t disappoint',8,8,1,1,12,'Tip the sage leaves and breadcrumbs onto a plate, then mix together with a little seasoning. Pour the eggs onto another plate. Pat the pork dry, then coat each chop in the egg mixture, letting any excess drip away. Then dip in the breadcrumbs until well covered. Cook or wrap in cling film or freezer bags and freeze. To cook, defrost completely if frozen. Place the chops on a lightly greased baking sheet and brush with a little of the butter. Cook under a medium grill for 7-10 mins until browned. Then turn over, brush with more butter and cook for a further 7 mins until crisp, browned all over and cooked through. Serve with green beans, fresh peas and some mashed potato mixed with grated cheddar.','10 mins','20 mins'),
(766,'one-pot roast pork chops with fennel & potatoes','https://www.bbcgoodfood.com/recipes/one-pot-roast-pork-chops-fennel-potatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-365468_11-fc6a740.jpg','Throw your meat and veg into a pan with Italian-style flavourings and simply roast, for a no-fuss dinner',5,4,1,1,12,'Heat oven to 200C/180C fan/gas 6. Put the potatoes, fennel, pepper, thyme and garlic in a large roasting tin. Mix together the tomato paste and stock, then pour into the pan. Tightly cover with foil and cook for 30 mins. Take out of the oven and increase the temperature to 220C/200C fan/gas 7. Remove the foil and place the pork in the roasting tin, nestling in between the veg. Season well and return to the oven for 15-20 mins more or until golden brown and cooked through. Serve with the pan juices drizzled over.','10 mins','50 mins'),
(767,'cheese & onion pork chops','https://www.bbcgoodfood.com/recipes/cheese-onion-pork-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338697_11-c798473.jpg','Enjoy British pork with melted Cheshire cheese and a kick of English mustard',5,4,1,1,12,'Heat grill to high, then place the chops on a grill pan, rub with oil and season. Grill for about 6 mins on each side, until golden. Spread a little mustard over one side of each chop, then top with 1 tbsp onions. Mix the cheese and thyme, sprinkle over the chops, then grill until golden and bubbly.','5 mins','15 mins'),
(1642,'country salad','https://www.bbcgoodfood.com/recipes/country-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-48617_10-404ede7.jpg','This hearty salad makes a healthy and delicious lunch with a few slices of ham',8,4,1,3,17,'Cook the eggs in a pan of boiling water for exactly 8 mins. Lift from the pan, cool under the cold tap, then peel and halve. Whisk together the lemon juice, olive oil, capers and spring onions. Toss together most of the dressing with the leaves. Place on a serving dish, top with the halved eggs and drizzle over the remaining dressing. Serve on its own or with slices of ham.','10 mins','8 mins'),
(768,'oven pork chops with roasted potato wedges','https://www.bbcgoodfood.com/recipes/oven-pork-chops-roasted-potato-wedges','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-146452_12-04d51fe.jpg','This healthy take on chops and chips is full of flavour - just pop everything into the oven and cook',8,4,1,1,12,'Heat oven to 200C/fan 180C/gas 6. Toss the potatoes with the oil and cook in the oven for 35 mins until lightly charred, then season with a little salt. Meanwhile, whizz together breadcrumbs, garlic and mustard in a food processor, then season lightly. Lightly oil a baking tray with the oil spray, place the chops on top and sprinkle some of the breadcrumb mixture over each chop. Spray with a little more oil, then bake for 20 mins until the meat is cooked through and the topping is golden. Serve with the sweet potato wedges and mixed leaves, lightly drizzled with vinaigrette if you like.','50 mins','No Time'),
(769,'fragrant pork with fennel slaw','https://www.bbcgoodfood.com/recipes/fragrant-pork-fennel-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-13619_11-e59a4f6.jpg','This fruity, fresh coleslaw goes beautifully with pork, or it''s great as part of a buffet',5,4,1,1,12,'Rub the pork with the lemon zest and fennel seeds, then heat the oil in a non-stick frying pan. Brown the chops for 3 mins on each side, then remove from the heat. Splash over half the lemon juice, season, then leave to rest while you make the salad. Mix together the sliced fennel, apple, onion, mayo mix and nuts. Season with black pepper and squeeze in the remaining lemon juice to taste. Serve the pork with the pan juices spooned over, and the fennel slaw on the side. Great with jacket potatoes.','10 mins','6 mins'),
(770,'pork chop melts','https://www.bbcgoodfood.com/recipes/pork-chop-melts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-998_12-9598786.jpg','Succulent, grilled pork chops with rich, creamy, melted blue cheese - ready in less than 30 minutes',5,4,1,1,12,'Turn on the grill. Season the chops on both sides with a good sprinkling of salt and pepper. Grill under a moderate heat for 12-16 minutes, turning once, until just cooked. Add the topping. Thinly slice the cheese (don’t worry if it breaks up a bit). Spoon the apple sauce over the chops and top each one with a couple of slices of cheese. Slide back under the grill for 2-3 minutes until the cheese is melted and tinged brown. Serve with mashed potatoes and a green veg such as leeks or broccoli, and with any melted cheesy bits from the bottom of the pan.','30 mins','No Time'),
(771,'pork chops with aubergine','https://www.bbcgoodfood.com/recipes/pork-chops-aubergine','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4160_11-4f17509.jpg','Perk up chops in just 30 minutes',5,4,1,1,12,'Heat the oil in a large saucepan, tip in the aubergine then fry over a medium heat for 5 mins until soft and golden. Pour in the vinegar. Once the bubbling subsides, stir in the sugar, followed by the tomatoes, basil and seasoning to taste. Stir well and cook over high heat for 5 mins, until the sauce has thickened. Meanwhile, heat the grill to medium and season the pork. Place the chops under the grill for 15 mins, turning halfway, until golden and cooked through. Serve the chops with the aubergines on the side.','5 mins','25 mins'),
(772,'balsamic pork with olives','https://www.bbcgoodfood.com/recipes/balsamic-pork-olives','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5472_12-4faaeac.jpg','The perfect way to end a busy day, just 20 minutes ',5,4,1,1,12,'Mix the oil with vinegar, mustard and garlic. Score the meat on both sides, season, then put into a dish. Pour over the balsamic mixture and leave to marinate for 5 mins. Heat a griddle pan until very hot. Lift the pork from the marinade, scraping off any garlic, then cook for 4 mins on each side. Remove the meat and keep warm. Pour the marinade into the pan with the olives, cook for 2 mins, then stir in the basil. Pour any juices from the pork into the pan, drizzle the sauce over the pork and serve with pasta.','10 mins','10 mins'),
(773,'asian pork with rice noodle salad','https://www.bbcgoodfood.com/recipes/asian-pork-rice-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-8105_12-94dcdcd.jpg','A weeknight supper that''s full of colour and spice and all things nice, and ready in just 20 minutes',5,4,1,1,12,'Mix together the lemongrass paste, ginger, chilli, oil and 1 tbsp of the fish sauce. Smear the mixture all over the pork chops, then set aside to marinate while you make the salad. Tip the noodles into a large bowl. Pour over a kettleful of boiling water, then leave to sit for 5 mins until the noodles are soft (or prepare them according to pack instructions). Drain the noodles into a sieve. Run under the cold tap until cool, then drain again and tip back into the bowl. Whisk the remaining 2 tbsp fish sauce with the lime zest and juice and sugar. Pour over the cold noodles. Slice the cucumber into thin strips, then toss through the salad along with the mint leaves. Heat a griddle pan until smoking hot. Griddle the chops for 5 mins on each side or until cooked through, then serve alongside the noodle salad.','10 mins','10 mins'),
(774,'mustard & rosemary pork chops','https://www.bbcgoodfood.com/recipes/mustard-rosemary-pork-chops','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2261_11-bc58835.jpg','Marinate these succulent pork chops in mustard, rosemary and garlic, then griddle for 10 minutes for a quick dinner. They''re also a great addition to a barbecue',5,4,1,1,12,'Mix together the mustard, oil, garlic, rosemary leaves, vinegar and a little black pepper. Tip into a large food bag then add the pork chops. Rub together, seal and marinate for 3 hrs. Drain the chops of their marinade. This can then be squeezed out of the bag and mixed with a little cold water until runny. To cook on a griddle, heat the griddle pan until a good heat rises and cook for 10 mins, turning once or twice. As they cook, brush occasionally with the marinade juices. Remove from the heat and season with salt. To help the meat relax a little, leave it loosely covered with foil for about 10 mins before serving.','5 mins','10 mins'),
(775,'cheesy chops & chips','https://www.bbcgoodfood.com/recipes/cheesy-chops-chips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2010_12-583f00b.jpg','Protein packed pork and potato one pot supper - cheap and perfect for students.',7,4,1,1,12,'Heat oven to 230C/fan 210C/gas 8. In a large bowl toss the potatoes, onion, liquid and oil together. Season if you like. Tip the onion and potatoes into a large sauté pan and bake for 20-30 mins until the potatoes start to brown. Lay the chops on the potatoes and cook for 10 mins more. While the potatoes are cooking, mix the cheese, mustard and milk together. When the chops have had 10 mins, spread the cheese mixture over them and switch the oven over to grill. Place the pan under the grill and cook for about 5 mins until the cheese is bubbling and the potatoes are golden and crispy. Serve straight from the pan.','1 hr and 15 mins','No Time'),
(776,'slow-cooker beef stew','https://www.bbcgoodfood.com/recipes/slow-cooker-beef-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-beef-stew-8ade07f.jpg','Cook braising steak in the slow cooker for really tender meat. Add button mushrooms or smoked paprika for extra flavour – it will be an instant family favourite.',19,4,1,1,12,'Fry the onion and celery in 1 tbsp oil over a low heat until they start to soften – about 5 mins. Add the carrots, bay and thyme, fry for 2 mins, stir in the purée and Worcestershire sauce, add 600ml boiling water, stir and tip everything into a slow cooker. Crumble over the stock cubes or add the stock pots and stir, then season with pepper (don’t add salt as the stock may be salty). Clean out the frying pan and fry the beef in the remaining oil in batches until it is well browned, then tip each batch into the slow cooker. Cook on low for 8-10 hrs, or on high for 4 hrs. If you want to thicken the gravy, mix the cornflour with a splash of cold water to make a paste, then stir in 2 tbsp of the liquid from the slow cooker. Tip back into the slow cooker, stir and cook for a further 30 mins on high. Stir in the parsley and season again to taste. Serve with mash, if you like. Leave to cool before freezing.','20 mins','4 hrs'),
(777,'slow cooker barbacoa beef','https://www.bbcgoodfood.com/recipes/slow-cooker-barbacoa-beef','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/barbacoa-e30fa3f.jpg','You can leave this barbacoa beef in the slow cooker during the day if you''re entertaining. Serve in lettuce cups, tacos, or over rice with vegetables',5,8,1,1,12,'Heat the slow cooker if necessary. Put the chipotle paste, red onion, garlic, cloves, lime juice and cider vinegar into the slow cooker and stir until combined. Add the bay leaves, stock and coriander, then put the beef on top. Use a pair of tongs to turn the meat over a few times in the sauce. Put the lid on the slow cooker. Cook on High for 1 hour and then Low for 8 hours or until the meat shreds easily when you pull it apart with 2 forks. Serve with the lettuce cups and jalapeño chilles and crème fraîche.','1 hr and 30 mins','8 hrs'),
(778,'slow-cooker beef goulash','https://www.bbcgoodfood.com/recipes/slow-cooker-beef-goulash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/goulash-8982fc1.jpg','With fall-apart beef, tomatoes and peppers in a creamy, rich stew, our slow-cooker goulash recipe couldn''t be easier or more comforting',12,8,1,1,12,'Heat the slow cooker to low and heat 2 tbsp oil in a deep frying pan over a medium heat. Season and sear the beef in batches until brown on all sides. Transfer to a plate. Put the remaining oil in the pan and fry the onions for 10 mins until lightly golden. Add the peppers and garlic, and fry for another 5-10 mins, then stir in the flour and all of the spices. Cook for 2 mins more, then stir in the tomato purée, tomatoes and 400ml beef stock. Season well. Bring the mixture to a simmer, then tip into the slow cooker with the seared beef. Add the remaining stock, if needed, to cover the meat completely. Cover and cook for 6-7 hrs until the beef is tender and the sauce has thickened slightly. Season to taste, then swirl the soured cream and most of the parsley through the stew. Scatter over the remaining parsley and some sweet smoked paprika, then serve with small roasted potatoes or brown rice, if you like.','25 mins','7 hrs'),
(779,'slow-cooker beef pot roast','https://www.bbcgoodfood.com/recipes/slow-cooker-pot-roast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-pot-rost-fea3e21.jpg','Prepare your beef in the slow cooker first thing in the morning, so that come evening-time your Sunday dinner is a breeze. Perfect for busy family weekends.',29,6,1,1,12,'Heat 2 tbsp sunflower oil in a large pan. Dust 1½ kg rolled beef brisket with 2 tbsp plain flour and season well with salt and pepper. Put 3 chopped carrots, 3 chopped celery sticks, 2 chopped parsnips, 1 chopped onion and 80g button mushrooms in the bottom of your slow cooker and turn it to Low. Sear the beef all over in the hot pan then place it on top of the vegetables in the slow cooker. Add the 2 bay leaves, 2 crushed garlic cloves and 2 tsp English mustard then pour over 500ml red wine and 250ml beef stock. Cover with the lid and cook for 7 hours. Heat oven to 200C/180C fan/gas 6. Carefully take the beef out of the slow cooker and place it on a baking tray then roast it in the oven for 20 mins. While the beef is in the oven, carefully ladle the cooking liquid out of the slow cooker into a shallow pan. Boil rapidly on a high heat to reduce to a rich gravy. Serve the beef sliced with roast potatoes, the softened vegetables, gravy and wilted greens, if you like.','15 mins','7 hrs and 30 mins'),
(780,'slow cooker beef brisket','https://www.bbcgoodfood.com/recipes/slow-cooker-brisket-with-golden-ale-gravy-horseradish-mash','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Slow-cooker-brisket-with-golden-ale-gravy-and-horseradish-mash-b0d9d61.jpg','Prepare this beef brisket and let it bubble away in the slow cooker, leaving you with a tender, rich stew. Serve with golden ale gravy and horseradish mash.',12,6,1,1,12,'Season the brisket all over with salt and pepper. Heat the vegetable oil in a frying pan (or use the sear function on your slow cooker) and sear the meat all over until browned. Transfer to a plate. Add another drizzle of oil, if needed, and fry the onions with a pinch of salt until lightly golden, about 12 mins. Sprinkle over the flour, stir until well-mixed, then pour in the ale and scrape the bottom of the pan to pick up any browned bits. Tip everything into the slow cooker (if using a frying pan). Add the yeast or beef extract, sugar and vinegar, then the beef and herbs. Top up with stock until the brisket is just over half submerged. Cover and cook for 7-8 hrs on low until tender. Remove the meat from the slow cooker and cover to keep warm. Use the reduce or simmer function on your slow cooker and simmer for 10-15 mins until the gravy has thickened to your liking. If yourslow cooker doesn’t have a simmer function, pour the gravy into a pan and simmer on the hob until thickened. Add a pinch more salt, sugar or a dash more vinegar if needed. If you like your gravy to be extra-thick, mix the cornflour with a splash of cold water, then add to the pan and bubble for a few minutes. Just before you remove the meat from the slow cooker, start on the mash. Cook the potatoes in a pan of boiling salted water for 20 mins until very tender when pressed. Drain and steam-dry for a few minutes before mashing with the horseradish, butter and crème fraîche. Scatter with parsley to serve. Cut the brisket into wedges and serve with the gravy and mash.','30 mins','8 hrs and 30 mins'),
(781,'slow cooker lasagne','https://www.bbcgoodfood.com/recipes/slow-cooker-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beeflasagne-101bf4f.jpg','Slow cook your next lasagne for extra tender mince – and this version is low-fat and low-calorie. A healthy family meal to give you comfort on cold nights',5,4,1,1,12,'Heat the slow cooker if necessary. Heat the oil in a large non-stick pan and fry the onions, celery, carrots and garlic for 5-10 mins, stirring frequently until softened and starting to colour. Tip in the meat and break it down with a wooden spoon, stirring until it browns. Pour in the tomatoes with a quarter of a can of water, the tomato purée, bouillon, balsamic vinegar, thyme and plenty of black pepper, return to the boil and cook for 5 mins more. Spoon half the mince in the slow cooker and top with half the lasagne, breaking it where necessary so it covers as much of the meat layer as possible. Top with the rest of the meat, and then another layer of the lasagne. Cover and cook on Low while you make the sauce. Tip the milk and flour into a pan with the bay leaf and nutmeg and cook on the hob, whisking continuously until thickened. Carry on cooking for a few mins to cook the flour. Remove the bay leaf and stir in the cheese. Pour onto the pasta and spread out with a spatula, then cover and cook for 3 hours until the meat is cooked and the pasta is tender. Allow to settle for 10 mins before serving with salad.','1 hr and 15 mins','3 hrs'),
(782,'cottage pie','https://www.bbcgoodfood.com/recipes/cottage-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1074465_10-0f090a9.jpg','Make our classic meat and potato pie for a comforting dinner. This great-value family favourite freezes beautifully and is a guaranteed crowd-pleaser',5,10,1,1,12,'Heat 1 tbsp olive oil in a large saucepan and fry 1¼kg beef mince until browned – you may need to do this in batches. Set aside as it browns. Put the other 2 tbsp olive oil into the pan, add 2 finely chopped onions, 3 chopped carrots and 3 chopped celery sticks and cook on a gentle heat until soft, about 20 mins. Add 2 finely chopped garlic cloves, 3 tbsp plain flour and 1 tbsp tomato purée, increase the heat and cook for a few mins, then return the beef to the pan. Pour over a large glass of red wine, if using, and boil to reduce it slightly before adding the 850ml beef stock, 4 tbsp Worcestershire sauce, a few thyme sprigs and 2 bay leaves. Bring to a simmer and cook, uncovered, for 45 mins. By this time the gravy should be thick and coating the meat. Check after about 30 mins – if a lot of liquid remains, increase the heat slightly to reduce the gravy a little. Season well, then discard the bay leaves and thyme stalks. Meanwhile, make the mash. In a large saucepan, cover the 1.8kg potatoes which you''ve peeled and chopped, in salted cold water, bring to the boil and simmer until tender. Drain well, then allow to steam-dry for a few mins. Mash well with the 225ml milk, 25g butter, and three-quarters of the 200g strong cheddar cheese, then season with freshly grated nutmeg and some salt and pepper. Spoon the meat into 2 ovenproof dishes. Pipe or spoon on the mash to cover. Sprinkle on the remaining cheese. If eating straight away, heat oven to 220C/200C fan/gas 7 and cook for 25-30 mins, or until the topping is golden. If you want to use a slow cooker, brown your mince in batches then tip into your slow cooker and stir in the vegetables, flour, purée, wine, stock, Worcestershire sauce and herbs with some seasoning. Cover and cook on High for 4-5 hours. Make the mash following the previous steps, and then oven cook in the same way to finish.','35 mins','1 hr and 50 mins'),
(783,'goulash in a dash','https://www.bbcgoodfood.com/recipes/goulash-dash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1074470_11-d3b9327.jpg','Whip up this Hungarian paprika-infused beef casserole in minutes, or use a slow cooker for an extra tender finish',5,4,1,1,12,'Heat half the oil in a large non-stick pan and fry the beef for 2 mins, stirring once halfway through. If your pan is small, do this in two batches. Tip the meat onto a plate. Heat the remaining oil in the pan (no need to clean) and fry the mushrooms for 2-3 mins until they start to colour. Sprinkle the paprika over the mushrooms, fry briefly, then tip in potatoes, stock and tomato sauce. Give it all a good stir, cover and simmer for 20 mins until the potatoes are tender. Return the beef to the pan along with any juices, and warm through. Stir in the parsley and a swirl of yogurt, then serve straight from the pan.','30 mins','No Time'),
(1232,'easy chocolate bark','https://www.bbcgoodfood.com/recipes/easy-chocolate-bark','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-bark-a9135be.jpg','Create a delicious homemade chocolate bark with our easy, versatile recipe. We''ve decorated it using salty pretzel pieces and crunchy honeycomb',5,14,1,2,3,'Melt the chocolate in short bursts in the microwave, stirring every 20 secs, until smooth. Spoon onto a parchment-lined baking tray and smooth over with a spatula to make a thinnish layer, around 35 x 20cm.  Sprinkle over the chocolate chips along with the pieces of pretzel and honeycomb, then chill until solid. For neat slices, remove the bark from the fridge and leave for a minute to come to room temperature. Use a sharp knife to cut it into shards (if it''s fridge cold, the chocolate will snap rather than cut). ','10 mins','No Time'),
(784,'pressure cooker short ribs with herb salad','https://www.bbcgoodfood.com/recipes/asian-short-ribs-herb-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ribs_0-f221114.jpg','A Thai-style beef recipe that''s made in a pressure cooker - the meat becomes melt-in-the-mouth in its rich sauce.',7,4,2,1,12,'In a mini chopper, make a paste with the ginger, coriander stalks, onion and garlic, then set aside. Heat the oil in the pressure cooker pan and brown the meat well on all sides, then remove. Fry the paste for 2-3 mins with 1 tsp golden caster sugar. Return the ribs to the pan and add the soy sauce, star anise, oyster sauce and enough chicken stock to just cover the ribs. Bring to a simmer, cover and bring up to high pressure. Cook for 45 mins, then release the pressure slowly. Take off the lid, add a splash of vinegar and remove the ribs from the pan. Simmer the sauce down until reduced and intensely flavoured. While the sauce is simmering, mix the salad ingredients together. Serve each rib in a puddle of sauce, topped with a pile of salad and rice on the side.','20 mins','1 hr'),
(785,'big-batch bolognese','https://www.bbcgoodfood.com/recipes/big-batch-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1074456_10-1a5351d.jpg','Whip up a huge batch of bolognese that''s fit to feed a hungry crowd, or freeze half for a speedy midweek meal',7,12,1,1,12,'Heat the oil in a very large saucepan. Gently cook the bacon, onions, carrots and celery for 20 mins until golden. Add the garlic, herbs, bay and mushrooms, then cook for 2 mins more. Heat a large frying pan until really hot. Crumble in just enough mince to cover the pan, cook until brown, then tip in with the veg. Continue to fry the mince in batches until used up. Tip the tomatoes and purée in with the mince and veg. Rinse the cans out with the red wine, if you have some, or with a little water, then add to the pan with the vinegar and sugar. Season generously and bring to a simmer. Simmer slowly for 1 hr until thick and saucy and the mince is tender. Serve with pasta and parmesan. If you want to make this in a slow cooker, visit our slow cooker bolognese recipe.','25 mins','1 hr and 30 mins'),
(786,'easy singapore noodles','https://www.bbcgoodfood.com/recipes/singapore-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/singapore-noodles-0cd04ed.jpg','Cater for the whole family with our Singapore noodles. They''re healthy and vegan, making an ideal accompaniment to a Chinese meal or just on their own',6,4,1,1,10,'Boil the kettle and put the noodles in a large pan or bowl. Pour over enough boiled water to cover, pushing the noodles under the water to help them soften evenly. Set aside for 5-10 mins, until the noodles are completely soft. Mix the curry powder, turmeric, sugar, sesame oil, soy sauce and 1 tbsp water in a bowl. Heat the wok until very hot. Add the sunflower oil, onion and pepper. Stir-fry for 3-4 mins until softened and starting to brown in places. Drain the noodles and add to the pan, along with the sauce mixture and beansprouts. Stir-fry for a further 3-4 mins, tossing everything through the sauce, until hot. Adjust the seasoning with a little more soy or sugar, if you like, and scatter over the chilli, if you like more spice.','15 mins','15 mins'),
(787,'salt and pepper tofu','https://www.bbcgoodfood.com/recipes/salt-pepper-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salt-pepper-tofu-67d409e.jpg','Use both types of peppercorns for our salt & pepper tofu if you can, for a tongue-tingling kick and a spicy warmth. The dish is great for a Chinese banquet',6,4,1,1,10,'Drain the tofu, wrap loosely in kitchen paper and put on a plate. Rest a chopping board on top. If you’re using a light chopping board, weigh it down with a couple of cans – a heavier chopping board will be sufficient on its own. Leave for 10-20 mins until the cloth feels wet from the excess liquid. Pressing the tofu like this helps to give it a firmer texture once cooked. Cut the tofu in half down the centre like a book. Cut each piece into four triangles, as you would a piece of toast, then in half again to give you 16 pieces in total. Mix the cornflour, ground pepper and 1 tsp flaky sea salt on a plate. Gently turn each piece of tofu in the cornflour mix to coat. Heat 1 tbsp oil in a wok. Stir-fry the peppers and broccoli for a few minutes, to soften a little. Add the beansprouts and soy sauce. Cook for another 1-2 mins, making sure the veg still has a nice crunch. Drizzle with a little sesame oil. Heat the remaining sunflower oil in a large non-stick frying pan and fry the coated tofu for 5 mins on each side until crisp. Serve on top of the vegetables, scattered with the coriander.','15 mins','20 mins'),
(788,'italian-style roast cabbage wedges with tomato lentils','https://www.bbcgoodfood.com/recipes/italian-style-roast-cabbage-wedges-tomato-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/italian-crispy-roast-cabbage-wedges-523a758.jpg','Roast cabbage for a veggie centrepiece, with crisp, caramelised leaves on the outside and a soft, sweet centre. Serve with our Italian-inspired lentils and golden roast new potatoes',46,6,2,1,10,'Heat the oven to 200C/180C fan/gas 6. Bring a large pan of water to the boil and blanch the potatoes for 10-12 mins until tender, then drain and leave to cool. For the cabbage, mix together the oil and garlic, rub all over the wedges, season, then spread out on a large baking tray. Roast on the top shelf for 15 mins. While the cabbage is cooking, put the new potatoes onto a large baking tray and gently crush with the back of a spoon or a masher, so they split open slightly. Drizzle over the oil and some seasoning, then stir in the lemon zest. Once the cabbage has had 15 mins, put the potatoes on the top shelf of the oven and move the cabbage to the bottom. Cook for 45 mins, turning everything halfway through. For the lentils, heat the oil in a medium saucepan and fry the onions and celery until soft and translucent, about 10-15 mins. Tip in the lentils, tomatoes, 450ml water, stock cubes, tomato purée and vinegar, bring to the boil and simmer uncovered for 30-35 mins, stirring occasionally until the lentils are soft and cooked through. If the mixture gets a little dry, add a splash of water to stop it sticking. Stir in the basil and an extra 50ml boiling water to loosen the mixture. Season well. While the lentils are cooking, make the pangrattato. Heat the oil in a large frying pan, fry the garlic and chilli for 30 seconds, then add the breadcrumbs. Toast until golden and crunchy, then tip in the lemon zest and olives and fry for a further 3 mins. Set aside to cool. To serve, spoon lentils onto each plate, put a cabbage wedge on top and sprinkle over the pangrattato. Garnish with the extra basil and serve the potatoes on the side.','30 mins','1 hr and 15 mins'),
(789,'vegan biryani','https://www.bbcgoodfood.com/recipes/vegan-biryani','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Vegan-Biryani-b5b7147.jpg','Try our vegan version of a biryani. Our plant-based recipe has raisins and cashews for texture and flavour, and provides all of your 5-a-day',4,4,1,1,10,'Rinse the rice until the water runs clear, then cook in a pan of fresh cold water following pack instructions for about 20 mins, or until almost tender. Meanwhile, heat the oil in a large, deep frying pan over a medium heat and stir in the onion, cinnamon stick, chilli, garlic and ginger so they’re coated in the oil. Scatter over the cumin seeds, cover and cook for 5 mins. Stir well, then add the pepper and aubergine, and cook, stirring for 3-5 mins, until the veg is starting to soften. Stir in the curry powder, then the tomatoes and bouillon. Tip in the cauliflower florets, coriander stems and raisins, then cover and simmer for 10 mins over a medium-low heat. Drain the rice, then tip it into the veg mixture and gently toss to combine. Cover and cook over a low heat for 8 mins until the rice and cauliflower are tender. Try not to add extra liquid, as you don’t want the end result to be wet. Remove from the heat and leave to stand for 5 mins, then gently toss through the cashews and coriander leaves. Will keep chilled for two days. Leave to cool completely first. Reheat portions in the microwave until piping hot before serving.','15 mins','45 mins'),
(790,'brussels sprouts pad thai','https://www.bbcgoodfood.com/recipes/boxing-day-sprout-pad-thai','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/noodles-99a7139.jpg','Vegans will love this pad Thai that you can make on Boxing Day to use up leftover brussels sprouts. You could also cook the dish using fresh sprouts',41,4,1,1,10,'First, put the noodles in a large heatproof bowl, cover in boiling water and leave for 10 mins. Drain and rinse with cold water, then set aside. In a bowl, mix the soy sauce or tamari, tamarind or lime juice and sugar. Heat the oil in a large frying pan or wok. Fry the garlic, spring onions, chilli and the cooked or leftover brussels sprouts for around 2 mins (to cook the sprouts from raw, boil for 8-10 mins until tender). Then, add the noodles and beansprouts and fry for 1 min more. Pour over the sauce and toss well, working quickly to coat all the vegetables and noodles. Once everything is heated through, season and divide between four bowls. Scatter with the nuts and serve with lime wedges to squeeze over.','15 mins','10 mins'),
(791,'vegan carbonara','https://www.bbcgoodfood.com/recipes/vegan-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Vegan-carbonara-ebf05ee.jpg','Mushrooms with smoky paprika replace of bacon in this plant-based take on an Italian classic, forming part of the vegan Healthy Diet Plan',4,4,1,1,10,'Boil the spaghetti for 10 mins or following pack instructions until al dente, reserving a little of the water. Put the cashews, bouillon and mustard in a bowl, then pour over 350ml boiling water. Heat the oil in a large non-stick pan. Add the mushrooms and grated garlic, and stir-fry over a high heat until the mushrooms are cooked and starting to crisp up. Take off the heat, stir in the paprika, then tip onto a plate and set aside. Add the grated courgette to the pan and cook, stirring every now and then until softened. Meanwhile, whizz the soaked cashews, whole garlic clove and nutritional yeast flakes, if using, with a hand blender until completely smooth. Tip the mixture into the pan with the courgettes and briefly stir over the heat. Add the spaghetti and toss in the cashew and courgette mixture until well coated, then toss through the smoky mushrooms. If following the Healthy Diet Plan, serve half with half the spinach on the side, and chill the rest for another day. Will keep for three days. Reheat in a covered pan with a dash of water, and cook the remaining spinach to serve on the side.','20 mins','20 mins'),
(792,'beetroot & red onion tarte tatin','https://www.bbcgoodfood.com/recipes/beetroot-red-onion-tarte-tatin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beetroot-star-anise-red-onion-tart-tatin-6617a3f.jpg','Try this vegan tart for a show-stopping centrepiece. The bold red of beetroot against the vibrant green salad also makes it ideal for a meat-free celebration.',41,4,1,1,10,'Heat oven to 200C/180C fan/gas 6. In a bowl, toss the beetroot and onion in 2 tbsp of the oil, the vinegar and sugar. Add the star anise and season well. Heat the rest of the oil in a large, ovenproof non-stick frying pan, then nestle in the veg so that they cover the surface of the pan. Cover with foil and cook in the oven for 45 mins. On a well-floured surface, roll the pastry to a thickness of 0.5cm and cut out a circle the same size as your frying pan. Carefully take the pan out of the oven, remove the foil and wiggle the beets and onion around in the pan to make a compact layer. Put the pastry on top, tucking it in all around the edges, then return the pan to the oven and bake for 35 mins or until the pastry has puffed up and is a deep golden brown. Slide a palate knife around the edge of the tart, then put a plate on top of the pastry, serving side down. Flip the pan over to turn the tart out onto the plate – be careful not to burn yourself with the handle. Top with the orange zest and a sprinkle of sea salt, then serve with a peppery salad on the side.','10 mins','1 hr and 20 mins'),
(794,'vegan meatballs','https://www.bbcgoodfood.com/recipes/vegan-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-meatballs-cc1ede5.jpg','Try a vegan version of meatballs, made with mushrooms, oats and black beans and topped with a delicious tomato sauce. Serve with spaghetti or polenta',14,4,1,1,10,'Tip the dried porcini into a bowl and cover with boiling water. Leave to soak for 20 mins. Meanwhile heat 1 tbsp of olive oil a frying pan. Add the onion and fry over a low heat for 10 mins or until softened and translucent. Add the garlic and paprika and cook for 1 min. Tip the black beans and oats into a food processor and blitz until you have a chunky, textured mixture. Tip the beans into a mixing bowl and stir through the miso, breadcrumbs and cooked onion mix. Strain and finely chop the porcini mushrooms and add those (keep the liquid for soup or risottos). Season and roll into 12 balls and chill in the fridge while you make the sauce. Heat 2 tbsp oil in a saucepan. Add the onion and fry over a low heat for 10 mins or until softened and translucent. Add the garlic and chilli and cook for 1 min. Stir through the tomatoes and sugar and season to taste. Simmer uncovered for 20 mins. Heat the oven to 180C/160C fan/gas 4. Heat the remaining 2 tbsp oil for the meatballs in a non-stick frying pan over a medium heat. Add the balls and fry for 5 mins until evenly brown. Transfer to a baking tray and put in the oven to cook through for 12 mins. Add the cooked meatballs to the pan of sauce and toss everything to coat, then scatter with the basil. Serve with spaghetti or soft polenta.','25 mins','1 hr'),
(795,'vegan katsu curry','https://www.bbcgoodfood.com/recipes/vegan-katsu-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tofyu_katsu_curry-94f3c80.jpg','Enjoy a vegan version of katsu curry, with crispy, golden tofu. Serve with rice, fresh herbs and cucumber and carrot ribbons',6,2,1,1,10,'First, make the curry sauce. Heat the oil in a pan and cook the onion and carrot until soft and starting to caramelise, about 8 mins. Add the garlic and ginger and sizzle for another minute or so, then stir in the curry powder and turmeric. Once the spices are warmed through, add the coconut milk, maple syrup and 100ml water. Season well, cover and simmer over a low heat for 20 mins, topping up with more water if it gets too thick. Meanwhile, slice the tofu in half through the centre. Carefully cut each piece into four triangles. In a wide bowl, mix the cornflour with 3 tbsp water and some seasoning until combined. Put the breadcrumbs in a second bowl. Dip a tofu piece into the flour mixture, then the breadcrumbs, turning until well coated. If the crumbs are not sticking well make the cornflour mixture a little bit thicker and try again. Press firmly. When the carrot in the curry sauce is soft, blitz using a stick or tabletop blender. If the sauce is too thick, add a little more water. Taste and add more salt, maple syrup or some lime juice, if you like. Keep warm. Heat the oil in a frying pan and cook the tofu for 4-5 mins on each side until golden and hot through. To serve, put the rice in bowls and top with the curry sauce, crispy tofu, the cucumber and carrot ribbons, herbs and lime wedges.','25 mins','40 mins'),
(796,'stuffed pumpkin','https://www.bbcgoodfood.com/recipes/stuffed-pumpkin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pumpkin-57f52fa.jpg','Throwing a vegan dinner party in the autumn or winter months? Bake a pumpkin with a gorgeous stuffing of rice, fennel, apple, pomegranate seeds and pecans',16,4,1,1,10,'Heat oven to 200C/180C fan/gas 6. Cut the top off the pumpkin or squash and use a metal spoon to scoop out the seeds. Get rid of any pithy bits but keep the seeds for another time (see our pumpkin seed recipe ideas). Put the pumpkin on a baking tray, rub with 2 tbsp of the oil inside and out, and season well. Roast in the centre of the oven for 45 mins or until tender, with the ‘lid’ on the side. Meanwhile, rinse the wild rice well and cook following pack instructions, then spread out on a baking tray to cool. Thinly slice the fennel bulb and apple, then squeeze over ½ the lemon juice to stop them discolouring. Heat the remaining 2 tbsp oil in a frying pan. Fry the fennel seeds and chilli flakes, then, once the seeds begin to pop, stir in ½ the garlic and the fennel. Cook for 5 mins until softened, then mix through the apple, pecans and lemon zest. Remove from the heat. Add the mixture to the the cooked rice, then stir in the chopped parsley and taste for seasoning. Pack the mixture into the cooked pumpkin and return to the oven for 10-15 mins until everything is piping hot. Meanwhile, whisk the remaining lemon juice with the tahini, the rest of the garlic and enough water to make a dressing. Serve the pumpkin in the middle of the table, topped with pomegranate seeds and the dressing.','15 mins','1 hr'),
(797,'vegan pie','https://www.bbcgoodfood.com/recipes/vegan-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rainbow-pie-cd160c2.jpg','Make this fantastic vegan beetroot, sweet potato, chard and celeriac rainbow-layered pie as a stunning centrepiece for a meat-free Christmas or dinner party',16,10,3,1,10,'First, make the filling. Heat oven to 220C/200C fan/gas 7. Mix together 1½ tbsp oil with the cumin, cinnamon and vinegar, and rub the mixture all over the beetroot. Put the beetroot into a small roasting tin, season well, then cover with foil and roast for 20 mins. Meanwhile, toss the celeriac with 2½ tbsp oil, the thyme, garlic and some seasoning in a second roasting tin. Separate out the slices so they cook evenly, then cover the tin in foil. In a third roasting tin, mix the sweet potato with the remaining oil, the smoked paprika and some seasoning, and cover with foil. Once the beets have cooked for 20 mins, add the celeriac and sweet potato to the oven alongside them, and roast all the veg for 40 mins further or until tender. Remove the thyme sprigs, squeeze the garlic cloves out of their skins and mash them in with the celeriac, then leave all the veg to cool. All the veg can be cooked the day before and kept in the fridge. For the pastry, boil the kettle and use some coconut oil to grease a deep 20cm springform cake tin. Pour the flour into a bowl and add 1 tsp salt. Mix the coconut oil with 200ml boiling water, stir until melted (put it in the microwave if need be), then pour into the flour and mix with a wooden spoon to form a dough. Working as quickly as you can (it’s best to roll the pastry when it’s warm), cut off a ¼ of the dough and set aside under a tea towel. Roll out the rest to 0.5cm thick, then use it to line the cake tin, pressing the dough into the corners and leaving any excess pastry overhanging the sides. Don’t worry if the pastry breaks – it’s very forgiving, so you can patch it up as you go. Heat oven to 200C/180C fan/gas 6. Now build the pie. Cover the base with chard leaves, then scatter over the semolina (which will absorb the beet juices), press in the beetroot, and season. Add another layer of chard, followed by the sweet potato, and season. Add a final layer of chard leaves, then top with the celeriac and season again. Roll out the pastry you set aside to a thickness of 0.5cm to use as the lid. Put the lid on top of the pie and, using a fork, press together the overhanging pastry to create a crimped edge. Make a steam hole, then brush the top with a little almond milk mixed with a spoonful of oil (this will help to colour the pastry). Bake in the centre of the oven for 45 mins until the pastry is a deep golden brown. Leave to cool for 15 mins, then remove from the tin and serve in the middle of the table. Will keep for up to three days in the fridge (the pie is also delicious cold).','1 hr','1 hr and 45 mins'),
(798,'beetroot & squash wellingtons with kale pesto','https://www.bbcgoodfood.com/recipes/beetroot-squash-wellingtons-kale-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/wellington-2edb6c1.jpg','Put some welly in your Christmas dinner. Make these vegan wellingtons ahead of time for a less stressed Christmas dinner. Even the meat eaters will love them!',16,6,2,1,10,'Heat oven to 190C/170C fan/ gas 5. Toss the onion, beetroot, squash and garlic in a roasting tin with 2 tbsp olive oil, the thyme leaves, sumac and some seasoning. Roast for 45 mins until the vegetables are tender but still retain their shape, then stir in the lentils and half the chestnuts. Squeeze the garlic cloves from their skins, reserve half and squash the other two into the lentil mixture. Leave to cool completely. Bring a large pan of salted water to the boil, tip in the kale, cook for 1 min until wilted, then drain and run under cold water until cool. Squeeze all the water from the kale, then put it in the small bowl of a food processor along with the reserved garlic cloves, chestnuts, the lemon juice, olive oil and some seasoning. Blitz to a thick pesto, and season to taste. On a lightly floured surface, unravel the sheets of puff pastry. Cut each sheet into three widthways so that you have six strips in total then divide the kale pesto between these, followed by the roasted veg and lentils, heaping the mixture on top of the pesto and leaving one side free of filling so that it is easier to roll. Brush all the borders with half the milk, fold over the ends, then carefully roll the pastry lengthways to completely encase the filling into a roll. Place your six individual Wellingtons on a baking tray lined with baking parchment and chill for at least 1 hr, or cover with cling film and leave overnight. If freezing, cover and freeze on a lined baking tray for up to 3 months.  To bake from chilled, heat oven to 190 C/170 C fan/gas 3 and line a baking tray with parchment. Brush the top of each Wellington with the remaining milk and sprinkle with a little sumac, then bake for 30 mins from chilled or 45 mins from frozen until crisp and golden. Scatter over extra thyme and some flaky sea salt and serve.','40 mins','1 hr and 20 mins'),
(799,'corn & split pea chowder','https://www.bbcgoodfood.com/recipes/corn-split-pea-chowder','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/corn-and-split-pea-chowder-440-400-1941cb2.jpg','Cook this healthy vegan soup and pack in four of your 5-a-day along with bags of flavour from ginger, chillies, garlic, thyme and coconut yogurt',4,4,1,1,10,'Tip the split peas, celery and thyme sprig into a medium pan with 1 litre of boiling water, bring back to the boil and simmer, covered, for 25 mins. Meanwhile, fry the onion in the oil in a large pan for 10 mins. Stir in the ginger, chilli and garlic. Tip in the pepper and potato, and pour in ½ litre boiling water with the bouillon and remaining thyme. Tip in the split pea mixture and corn, bring to the boil, then cover and simmer for 30-35 mins until the veg is tender. Remove the thyme sprig. Take out a third of the veg, then purée the rest in the pan with a hand blender (or use a potato masher). Return the veg to the pan with the yogurt, and stir well. If you''re following our Healthy Diet Plan, eat two portions now, and cool then chill the rest for another day. Will keep in the fridge for three days.','10 mins','50 mins'),
(800,'aubergine & sesame dan-dan-style noodles','https://www.bbcgoodfood.com/recipes/aubergine-sesame-dan-dan-style-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Aubergine-and-sesame-dan-dan-style-noodles-36ab6a2.jpg','Try our spin on dan dan noodles – a street-food classic from the Sichuan province in China – for a vegan dinner packed with aubergine and rich, nutty sesame',6,1,1,1,10,'Heat the sesame oil in a wok over a high heat and fry the aubergine for 5 mins until softened and starting to brown in places. Meanwhile, put the kettle on to boil. Cook the noodles in a pan of boiling water for a minute less than the pack instructions, then drain and rinse under cold running water to stop the cooking process. Add the five-spice powder, garlic and Sichuan pepper to the pan with the aubergine and stir-fry for 1 min. 3 Mix the tahini, hoisin, soy, vinegar and chilli oil together in a bowl. Stir in 50ml hot water from the kettle, then pour the mixture over the aubergine and cook for another 1-2 mins until coated in the glossy sauce. Add the noodles and toss to coat, adding a splash more hot water if the sauce is too thick. Sprinkle with the spring onions and sesame seeds just before serving.','15 mins','15 mins'),
(801,'vegan cauliflower cheese','https://www.bbcgoodfood.com/recipes/vegan-cauliflower-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/vegan-cauli-cheese-12f14ea.jpg','Enjoy our roasted cauliflower tossed with a silky, rich dairy-free cheese sauce and topped with golden breadcrumbs – you won’t even notice that it’s entirely plant-based',106,4,1,1,10,'Heat the oven to 200C/180C fan/gas 6. Toss the cauliflower florets and stalks with the oil in a large roasting tin and season well. Roast for 25-30 mins until almost tender, then add the leaves and cook for a further 5 mins. To make the sauce, melt the vegan butter block in a large saucepan. Add the flour, mustard powder, onion and garlic granules and cook for 2-3 mins. Gradually add the soya milk, whisking well to incorporate. Bring to a gentle simmer, stirring constantly, then simmer for 4-5 mins until thickened and reduced slightly. Add the nutritional yeast and season well, then whisk to combine. Tip the sourdough breadcrumbs and vegan cheese into a bowl and stir to combine. Season well. Remove the cauliflower from the oven, spoon over the sauce and stir to combine. Scatter over the cheesy sourdough crumbs and return to the oven for 10 mins until bubbling and golden. Serve as a side or main with a green salad.','15 mins','45 mins'),
(802,'thirty-minute courgettes with dukkah sprinkle','https://www.bbcgoodfood.com/recipes/egyptian-courgettes-dukkah-sprinkle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-egyptian-courgettes-with-dukkah-sprinkle-a304ddf.jpg','Get four of your 5-a-day in one vegan dinner. This budget-friendly recipe is healthy and gluten-free, plus it provides a source of calcium, folate, fibre, vitamin C and iron',4,4,1,1,10,'Heat the oil in a large non-stick pan and fry the onions for 5 mins, stirring occasionally until starting to colour. Stir in the ground coriander and paprika, then tip in the tomatoes with a can of water. Add the bouillon powder and courgettes, cover and cook for 6 mins. Meanwhile, make the dukkah. Warm the whole spices, sesame seeds and almonds in a pan until aromatic, stirring frequently, then remove the pan from the heat. Add the butter beans, tomatoes and peas to the courgettes, cover and cook for 5 mins more. Stir in the coriander, then spoon into bowls. Crush the spices and almonds using a pestle and mortar and scatter on top. If you''re cooking for two people, put half the seed mix in a jar and chill half the veg for another day.','10 mins','25 mins'),
(803,'portobello jackfruit burgers','https://www.bbcgoodfood.com/recipes/portobello-jackfruit-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Portobello-Jackfruit-Burger-9ff5f68.jpg','Use mushrooms as a bun substitute to make these healthy plant-based burgers. For the filling, choose jackfruit canned in water rather than brine to avoid added salt',4,4,1,1,10,'Heat the oven to 200C/180C fan/gas 6. Toss the chips in 2½ tbsp of the oil and spread out on a large non-stick baking sheet lined with baking parchment. Rub the outside of the mushrooms with a drop of oil, then arrange them on another baking sheet, rounded-side up, and put the chips and mushrooms in the oven for 20 mins. Turn the mushrooms and chips over after 10 mins. Meanwhile, squeeze as much juice as you can out of the jackfruit. The best way is to wrap it in kitchen paper, then squeeze with your hands. Trim off the feathery bits of jackfruit that look a bit like shredded meat, weigh out 75g and stir in the tamari sauce. Tip the remaining jackfruit into a bowl. Add the chickpeas, tomato purée and garlic and blitz together until smooth using a hand blender or use a food processor. Stir in the remaining jackfruit and shape into four burgers about the size of the mushrooms. Coat in the flour. Check the chips and mushrooms and cook for 5-10 mins longer if needed. Meanwhile, heat the remaining oil and fry two of the burgers for a few minutes each side until brown and heated through. To make the relish, put the chopped tomatoes in a bowl with the tomato purée, vinegar, onion and basil or coriander. To serve, sandwich two of the burgers between the mushrooms. If the mushrooms are very juicy, blot with a little kitchen paper first. Serve with half the chips, sprinkled with the paprika, and serve half the relish on the side. The remaining mushrooms, burgers and chips will keep for two days in the fridge. To serve again, warm the remaining mushrooms and burgers in a low oven, and heat the chips, uncovered, until piping hot.','30 mins','40 mins'),
(804,'coconut & corn curry','https://www.bbcgoodfood.com/recipes/coconut-corn-curry','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Coconut-and-corn-curry-3ef570b.jpg','Enjoy this warming coconut and corn curry for an easy meat-free meal for two. Full of warming spices, it''s a quick and comforting vegan dinner',46,2,1,1,10,'Put the lentils in a sieve and rinse until the water runs clean. Put the oil in a saucepan over a medium heat, tip in the onion and cook for 8 mins until softened. Add the ginger, turmeric and curry powder, and cook for another minute until fragrant. Stir in the lentils. Tip in 400ml hot water and bring to the boil. Turn the heat down to medium and simmer for 15 mins until most of the water has been absorbed. Add the baby corn and 100ml water, cover and continue to simmer for 5-10 mins until the lentils are soft and the corn still has a little bite. Stir in the coconut yogurt to warm up, but don’t let it boil. Serve topped with more coconut yogurt, if you like, and coriander leaves sprinkled over.','5 mins','30 mins'),
(805,'spiced aubergine bake','https://www.bbcgoodfood.com/recipes/spiced-aubergine-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-aubergine-bake-7108341.jpg','This is vegan comfort food at its best. Layer up slices of aubergine with a spicy coconut milk and tomato sauce for a hearty, warming meal',67,4,1,1,10,'Heat oven to 220C/200C fan/gas 7. Generously brush each aubergine slice with vegetable oil and place in a single layer on a baking tray, or two if they don’t fit on one. Cook on the low shelves for 10 mins, then turn over and cook for a further 5-10 mins until they are golden. Reduce the oven to 180C/160C fan/gas 4. Heat the coconut oil in a large, heavy-based frying pan and add the onions. Cover and sweat on a low heat for about 5 mins until softened. Add the garlic, mustard seeds, fenugreek seeds, garam masala, chilli powder, cinnamon stick, cumin and ground coriander. Cook for a few secs until it starts to smell beautiful and aromatic. Pour the chopped tomatoes and coconut milk into the spiced onions and stir well. Check the seasoning and add a little sugar, salt or pepper to taste. Spoon a third of the tomato sauce on the bottom of a 2-litre ovenproof dish. Layer with half the aubergine slices. Spoon over a further third of tomato sauce, then the remaining aubergine slices, and finish with the rest of the sauce. Sprinkle over the flaked almonds and coriander (if using), reserving some to serve, and bake for 25-30 mins. Serve garnished with more coriander.','15 mins','45 mins - 55 mins'),
(806,'vegan brownies','https://www.bbcgoodfood.com/recipes/vegan-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/brownies-ba587aa.jpg','Try these squidgy chocolate brownies without the dairy or eggs. They''re perfect for vegans and people following a dairy-free diet and they taste utterly divine',6,12,1,1,10,'Heat the oven to 170C/150C fan/gas 3½. Grease and line a 20cm square tin with baking parchment. Combine the flaxseed with 6 tbsp water and set aside for at least 5 mins. In a saucepan, melt 120g chocolate, the coffee and margarine with 60ml water on a low heat. Allow to cool slightly. Put the flour, almonds, cocoa, baking powder and ¼ tsp salt in a bowl and stir to remove any lumps. Using a hand whisk, mix the sugar into the melted chocolate mixture, and beat well until smooth and glossy, ensuring all the sugar is well dissolved. Stir in the flaxseed mixture, vanilla extract and remaining chocolate, then the flour mixture. Spoon into the prepared tin. Bake for 35-45 mins until a skewer inserted in the middle comes out clean with moist crumbs. Allow to cool in the tin completely, then cut into squares. Store in an airtight container and eat within three days.','15 mins','40 mins'),
(807,'vegan leek & potato soup','https://www.bbcgoodfood.com/recipes/vegan-leek-potato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Vegan-leek-and-potato-soup-42fb8de.jpg','Try this dairy-free, plant-based twist on the classic leek and potato soup. Ideal for lunch or a starter, top with chopped chives and enjoy with crusty bread',4,4,1,1,10,'Heat the oil in a large pan over a medium heat and fry the garlic and leeks, stirring, until the veg has started to soften. Add the potatoes and stock, then cover and simmer for 15 mins until the leeks and potatoes are soft. Pour in the almond milk, then remove from the heat and blitz using a hand blender until almost smooth, with a slightly chunky texture. Or, if you prefer, blitz until completely smooth. Reheat over a low heat if needed, then ladle into bowls and scatter with chives, drizzle with a little oil and serve with bread, if you like. Can be frozen for up to three months.','10 mins','25 mins'),
(808,'spinach, sweet potato & lentil dhal','https://www.bbcgoodfood.com/recipes/spinach-sweet-potato-lentil-dhal','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spinach-sweet-potato-and-lentil-dhal-be8fae5.jpg','A comforting vegan one-pot recipe that counts for 3 of your 5-a-day! You can''t go wrong with this iron-rich, low-fat, low-calorie supper',16,4,1,1,10,'Heat 1 tbsp sesame oil in a wide-based pan with a tight-fitting lid. Add 1 finely chopped red onion and cook over a low heat for 10 mins, stirring occasionally, until softened. Add 1 crushed garlic clove, a finely chopped thumb-sized piece of ginger and 1 finely chopped red chilli, cook for 1 min, then add 1½ tsp ground turmeric and 1½ tsp ground cumin and cook for 1 min more. Turn up the heat to medium, add 2 sweet potatoes, cut into even chunks, and stir everything together so the potato is coated in the spice mixture. Tip in 250g red split lentils, 600ml vegetable stock and some seasoning. Bring the liquid to the boil, then reduce the heat, cover and cook for 20 mins until the lentils are tender and the potato is just holding its shape. Taste and adjust the seasoning, then gently stir in the 80g spinach. Once wilted, top with the 4 diagonally sliced spring onions and ½ small pack torn basil leaves to serve. Alternatively, allow to cool completely, then divide between airtight containers and store in the fridge for a healthy lunchbox.','10 mins','35 mins'),
(809,'vegan pulled jackfruit','https://www.bbcgoodfood.com/recipes/vegan-pulled-jackfruit','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-pulled-jackfruit-5140be4.jpg','Learn how to cook jackfruit and use it to make a vegan ''pulled'' meat substitute. Perfect for stuffing into bread rolls and enjoying with all the barbecue trimmings',107,4,1,1,10,'Heat the oil in a frying pan and cook the onion until very soft, for around 10-12 mins. Add the cinnamon, cumin and paprika to the onions and cook for a further 2-3 mins. Next add the hot sauce, vinegar and bbq sauce and mix well before adding in the tomato, the drained jackfruit and 200ml water. Leave to simmer gently, covered, for 30 mins stirring every 5-10 mins to help break down the jackfruit, then take the lid off and cook a further 10 minutes. Once cooked, use a fork to make sure all of the jackfruit is well shredded. Check seasoning and add another tbsp of bbq sauce if necessary for extra stickiness.','5 mins','55 mins'),
(810,'vegan banana bread','https://www.bbcgoodfood.com/recipes/vegan-banana-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-banana-bread-ce14fcd.jpg','Use up your ripe bananas to make easy vegan banana bread – the perfect breakfast treat to enjoy with your morning cuppa. We love it toasted with peanut butter',108,8,1,1,10,'Heat oven to 200C/180C fan/gas 6. Mash 3 large black peeled bananas with a fork, then mix well with 75g vegetable or sunflower oil and 100g brown sugar. Add 225g plain flour, 3 heaped tsp baking powder and 3 tsp cinnamon or mixed spice, and combine well. Add 50g dried fruit or nuts, if using. Bake in an oiled, lined 2lb loaf tin for 20 minutes. Check and cover with foil if the cake is browning. Bake for another 20 minutes, or until a skewer comes out clean. Allow to cool a little before slicing. It''s delicious freshly baked, but develops a lovely gooey quality the day after.','10 mins','40 mins'),
(811,'vegan shepherd''s pie','https://www.bbcgoodfood.com/recipes/vegan-shepherds-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-shepherds-pie-d0ee305.jpg','A warming vegan supper with porcini mushrooms, leeks, carrots, and butternut squash, topped with crispy potatoes. It''s low calorie, low fat, and perfect for when the nights draw in',25,8,1,1,10,'Put the unpeeled potatoes in a large saucepan, cover with water, bring to the boil and simmer for 40 mins until the skins start to split. Drain and leave to cool a little. Meanwhile, heat the vegetable oil in a large heavy-based sauté pan or flameproof casserole dish. Add the mushrooms, leeks , onions, carrots and the stock cube and cook gently for 5 mins , stirring every so often. If it starts to stick, reduce the heat and stir more frequently, scraping the bits from the bottom. The veg should be soft but not mushy. Add the garlic, tomato purée, paprika, squash and herbs. Stir and turn the heat up a bit, cook for 3 mins, add the celery, then stir and cook for a few more mins. Tip in the chickpeas along with the water in the can and reserved mushroom stock. Add the peas and spinach and stir well. Cook for 5 mins, stirring occasionally, then season, turn off and set aside. There should still be plenty of liquid and the veg should be bright and a little firm. Peel the potatoes and discard the skin. Mash 200g with a fork and stir into the veg. Break the rest of the potatoes into chunks, mix with the olive oil and parsley and season. Divide the filling into the pie dishes and top with the potatotes. Heat oven to 190C/170C fan/gas 5 and bake the pies for 40-45 mins, until the top is golden and the filling is heated through. If making individual pies, check after 20 mins. Best served with tomato ketchup – as all great shepherd’s pies are.','30 mins','1 hr and 20 mins'),
(812,'carrot & coriander soup','https://www.bbcgoodfood.com/recipes/carrot-coriander-soup','https://images.immediate.co.uk/production/volatile/sites/30/2010/12/carrot-and-coriander-soup-recipe-e71fd8f.jpg','Everyone loves this super healthy soup, perfect for an easy supper',5,4,1,1,10,'Heat 1 tbsp vegetable oil in a large pan, add 1 chopped onion, then fry for 5 mins until softened. Stir in 1 tsp ground coriander and 1 chopped potato, then cook for 1 min. Add the 450g peeled and chopped carrots and 1.2l vegetable or chicken stock, bring to the boil, then reduce the heat. Cover and cook for 20 mins until the carrots are tender. Tip into a food processor with a handful of coriander then blitz until smooth (you may need to do this in two batches). Return to pan, taste, add salt if necessary, then reheat to serve.','15 mins','25 mins'),
(813,'mushroom & potato curry','https://www.bbcgoodfood.com/recipes/one-pot-mushroom-potato-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-339051_11-3366b35.jpg','Create a tasty, spicy vegetarian dish with mushroom and curry in less than half an hour',5,4,1,1,10,'Heat the oil in a large saucepan, add the onion and potato. Cover, then cook over a low heat for 5 mins until the potatoes start to soften. Throw in the aubergine and mushrooms, then cook for a few more mins. Stir in the curry paste, pour over the stock and coconut milk. Bring to the boil, then simmer for 10 mins or until the potato is tender. Stir through the coriander and serve with rice or naan bread.','10 mins','20 mins'),
(814,'vegan tagine','https://www.bbcgoodfood.com/recipes/vegetable-tagine-chickpeas-raisins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1202592_11-73d208f.jpg','Achieve all five of your five-a-day with this easy vegan tagine. It''s bursting with summer veg, including courgettes, tomatoes and peas, alongside storecupboard staples such as chickpeas and raisins',5,4,1,1,10,'Heat the oil in a pan, then fry the onions for 5 mins until soft. Stir in the spices. Add the courgettes, tomatoes, chickpeas, raisins and stock, then bring to the boil. Cover and simmer for 10 mins. Stir in the peas and cook for 5 mins more. Sprinkle with coriander, to serve.','10 mins','N/A'),
(815,'vegan sponge cake','https://www.bbcgoodfood.com/recipes/vegan-sponge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-sponge-cake-eefa085.jpg','Treat friends and family to a Victoria sponge cake made with vegan ingredients. This easy recipe is perfect for afternoon tea or a coffee morning',29,8,1,1,10,'Heat the oven to 180C/160C fan/gas 4. Line the bases of 2 x 20cm sandwich tins with baking parchment and grease with a little of the dairy-free spread. Put the dairy-free milk into a jug and add the vinegar, leave for a few minutes until it looks a little lumpy. Put half of the vanilla seeds and all the other cake ingredients into a large bowl, then pour over the milk mixture. Using electric beaters or a wooden spoon, beat everything together until smooth. Divide the mix between your two tins, then bake in the centre of the oven for 30-35 mins or until a skewer inserted into the middle of the cakes comes out clean. Leave them in their tins until cool enough to handle, then carefully turn out onto wire racks to cool completely. While the cakes are cooling, make the filling. To make the vegan buttercream, whisk or beat together the dairy-free spread, icing sugar and remaining vanilla seeds until pale and fluffy. Dairy-free spreads do vary, so if the spread you are using is quite soft, try to avoid using electric beaters. Stir the ingredients together instead to avoid overworking it. However, if the mixture is too firm, use electric beaters to help lighten it and add 1-2 tbsp dairy-free milk when whisking. Spread the jam onto one of the cooled sponges, top with the buttercream, then place the other sponge on top. Dust the assembled cake with a little icing sugar or caster sugar before slicing.','20 mins','30 mins - 35 mins'),
(816,'vegan burritos','https://www.bbcgoodfood.com/recipes/vegan-burritos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/burrito-bf165ae.jpg','Pack these vegan wraps with vegetables, rice and chipotle black beans to make a delicious and nutritious lunch.',19,4,1,1,10,'To make the beans, heat the oil in a pan and fry the garlic for a minute, then stir in the chipotle paste. Tip in the tomatoes, stir and bring to a simmer. Season with salt. Simmer until thick, add the beans and cook briefly (make sure any water gets cooked off), then stir in the coriander. If you are using cold cooked rice, then warm it through, stir in the lime juice, red onion and nuts and season well. Lay out the tortillas and sprinkle over some spinach, add some avocado slices and some rice, then top with the bean mix. Add a shake of hot sauce, if you like. Roll the bottom up, then fold the sides in to stop the filling falling out as you roll. Wrap tightly in foil, if you like, and cut in half.','10 mins','30 mins'),
(817,'coconut & banana pancakes','https://www.bbcgoodfood.com/recipes/coconut-banana-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/coconut-pancakes-0e32c8a.jpg','These vegan coconut milk pancakes with passion fruit and banana topping couldn''t be simpler. They''re perfect for a brunch with family and friends',19,8,1,1,10,'Sift the flour and baking powder into a bowl, and stir in 2 tbsp of the sugar and a pinch of salt. Pour the coconut milk into a bowl, whisk to mix in any fat that has separated, then measure out 300ml into a jug. Stir the milk slowly into the flour mixture to make a smooth batter, or whizz everything in a blender. Heat a shallow frying pan or flat griddle and brush it with oil. Use 2 tbsp of batter to make each pancake, frying two at a time – any more will make it difficult to flip them. Push 4-5 pieces of banana into each pancake and cook until bubbles start to pop on the surface, and the edges look dry. They will be a little more delicate than egg-based pancakes, so turn them over carefully and cook the other sides for 1 min. Repeat to make 8-10 pancakes. Meanwhile, put the remaining coconut milk and sugar in a small pan. Add a pinch of salt and simmer until the mixture thickens to the consistency of single cream. Use this as a sauce for the pancakes and spoon over some of the passion fruit seeds.','10 mins','15 mins'),
(818,'avocado hummus & crudités','https://www.bbcgoodfood.com/recipes/avocado-houmous-crudites','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-houmous-crudites-54e1ffe.jpg','Get all five of your 5-a-day with this healthy, low-calorie, vegan recipe that works as lunch or a starter. Enjoy deliciously creamy hummus made with avocado',16,2,1,1,10,'Blitz together the avocado, chickpeas, garlic, chilli flakes and lime juice, and season to taste. Top the hummus with the coriander leaves and a few more chilli flakes, and serve with the carrot, pepper and sugar snap crudités. Make the night before for a great take-to-work lunch.','10 mins','No Time'),
(819,'alu tamatar masala','https://www.bbcgoodfood.com/recipes/alu-tamatar-masala','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/Alu-tamatar-masala-7257e00.jpg','Try this vegan dish of potatoes cooked in tomato masala. It''s simple to make, but with tomatoes cooked in spices and curry leaves, it''s packed with flavour',109,4,1,1,10,'Bring a large pan of water to the boil over a medium-high heat and cook the potatoes for 10-15 mins until just tender (a cutlery knife should just be able to cut through). Drain and set aside. Heat the oil in a large frying pan over a low heat and add the mustard seeds. When they begin to pop, add the curry leaves, chilli, garlic and ginger, and cook for 1 min until the aromas start to release. Stir in the tomatoes, then cover and cook for 10 mins until soft and mushy Add the garam masala, chilli powder, turmeric and 1 tsp salt. Mix well and cook for another minute before adding the cooked potatoes. Pour in 200ml water, cover and simmer for 10 mins until the potatoes are completely tender. Mash a few of the potatoes with a potato masher, keeping the rest whole. Sprinkle over the coriander leaves to serve.','15 mins','25 mins'),
(821,'sweet potato and coconut soup','https://www.bbcgoodfood.com/recipes/speedy-sweet-potato-soup-coconut','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338728_11-1ed1b41.jpg','Blitz up this simple vegan soup in just 20 minutes for a quick lunch or starter. Sweet potatoes, coconut and Thai green curry paste make for a flavourful combination, best enjoyed with warm naan bread',5,4,1,1,10,'Heat the oil in a deep saucepan, then soften the onion for 4-5 mins. Stir in the curry paste and cook for 1 min more until fragrant. Add the grated sweet potato and stock, then bring quickly to the boil, simmering for 5 mins until the potato is tender. Remove the soup from the heat, stir in the coconut and seasoning, then cool briefly before whizzing with a stick blender until smooth. Sprinkle with coriander and serve with warm naan breads.','10 mins','10 mins'),
(822,'vegan pumpkin soup','https://www.bbcgoodfood.com/recipes/vegan-pumpkin-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-pumpkin-soup-1d458f8.jpg','Make the most of pumpkin season in early autumn with this healthy vegan pumpkin soup. Toast the pumpkin seeds and use as a garnish to add texture',12,6,1,1,10,'Heat the oven to 200C/180C fan/gas 6. Peel, deseed and chop the pumpkin into 3cm chunks (reserve the seeds for the relish – see tip). Tip the pumpkin onto a baking tray, drizzle over half the oil, scatter with the allspice and toss well. Season and roast for 30 mins until golden and tender. Meanwhile, toast the coconut flakes for the relish in a dry frying pan over a low heat for a few minutes until lightly golden. Remove from the pan and leave to cool, then stir in the pumpkin seeds, chilli, coriander and lime zest.  Heat the remaining oil in a large saucepan over a medium heat, and fry the onion for 5 mins until tender but not golden. Add the ginger and garlic and fry for another minute, then tip in 1 litre stock and the miso. Put the roasted pumpkin in the pan, then bring to a simmer. Remove from the heat and blend the soup with a stick blender until smooth. If you prefer a thinner soup, add the remaining stock. Return the soup to a gentle simmer, then season well and stir in the lime juice. Ladle into bowls, and serve topped with a swirl of the soya cream and a sprinkle of the relish. ','25 mins','40 mins'),
(823,'healthy pumpkin soup','https://www.bbcgoodfood.com/recipes/healthy-pumpkin-soup','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Healthy-Pumpkin-Soup-a553a0d.jpg','Make the most of pumpkins in the supermarkets in the autumn and make this warming and filling pumpkin soup. Serve with our savoury granola sprinkled on top',15,4,1,1,10,'Put the pumpkin seeds in a sieve and rinse to remove any pulp. Spread out on a plate and pat dry. Toast 50g of the seeds in a pan over a medium heat along with the oats and 1 tsp each of the coriander seeds and chilli flakes for a few minutes until fragrant. Stir in the tamari and maple syrup. Simmer for 30 seconds until reduced and sticky. Transfer to a plate. Heat the oil in the pan over a medium heat and cook the onion, celery, carrots, garlic and pumpkin, along with the remaining spices and some seasoning for 10 mins. Pour in the stock. Bring to a simmer and cook with the lid on for 20-25 mins. Blitz until smooth using a hand blender. Serve with the savoury granola sprinkled on top.','20 mins','50 mins'),
(824,'spicy roasted parsnip soup','https://www.bbcgoodfood.com/recipes/spicy-roasted-parsnip-soup','https://images.immediate.co.uk/production/volatile/sites/30/2010/12/Spicy-roasted-parsnip-soup-abc72fe.jpg','Aromatic flavours transform the ordinary parsnip into a delicious warming soup',5,4,1,1,10,'Heat oven to 220C/fan 200C/gas 7. In a bowl, mix together 2 tbsp olive oil and 1 tsp coriander seeds, 1 tsp cumin seeds, ½ tsp ground turmeric and ½ tsp mustard seeds. Add 1 large onion, cut into 8 chunks, 2 garlic cloves, 675g parsnips, diced, and 2 quartered plum tomatoes and mix well. Spread over a heavy baking sheet, then roast for 30 mins until tender. Spoon into a food processor or liquidiser with 600ml vegetable stock and process until smooth. Pour into a pan with the remaining 600ml vegetable stock, season, then heat until barely simmering. Remove from the heat and stir in 1 tbsp lemon juice. Garnish with cumin seeds.','10 mins','35 mins'),
(825,'leek, pea & watercress soup','https://www.bbcgoodfood.com/recipes/leek-pea-watercress-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/leek-pea-watercress-soup-7733968.jpg','Pack in the goodness with this vibrant green soup, packed with leeks, peas and watercress. It''s tasty and healthy too, both low in calories and fat',110,4,1,1,10,'Heat the oil in a large saucepan over a medium heat. Add the leeks and garlic and fry for 7-10 mins or until softened and translucent. Pour in the hot stock and simmer for 5-10 mins. Stir through the watercress, reserving a few leaves for garnish, then the peas, and cook for 5 mins until wilted. Use a hand blender or processor and whizz until smooth. Stir through the lemon juice and zest, then season to taste. Stir through half the parsley. Ladle into bowls and top with the remaining parsley, reserved watercress and a drizzle of olive oil. Swirl through some crème fraîche, then serve with crusty bread, if you like.','10 mins','22 mins'),
(826,'pasta e fagioli','https://www.bbcgoodfood.com/recipes/pasta-e-fagioli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pasta-e-fagioli-cd06b61.jpg','Make this vegan soup in the slow cooker for a super-easy, healthy supper. Packed with veg, pasta and beans, it''s a satisfying soup for winter',111,6,1,1,10,'Drain the beans and bring to the boil in a pan of salted water. Cook for 10 mins, drain, rinse and put in a slow cooker with the onions, carrots and celery. Stir in the olive oil, garlic, stock, tomatoes, half a can of water and the miso. Tie the herbs together with kitchen string and add these as well. Season. Cover and cook on low for 6-8 hrs, until the beans are cooked through and all of the veg is really tender. Remove and discard the herbs and stir in the pasta. Cover and cook on high for another 30 mins. Add the cavolo nero stalks and leaves and cook for a final 30-40 mins, or until the pasta is cooked through and the greens are tender. Serve scattered with the cheese and drizzled with a little more olive oil, if you like.','20 mins','7 hrs - 9 hrs'),
(827,'soup maker tomato soup','https://www.bbcgoodfood.com/recipes/soup-maker-tomato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/soup-maker-tomato-soup-5f6ae46.jpg','Make this tasty tomato soup in a soup maker for delicious results with minimal effort. It''s low in calories and fat, and delivers two of your 5-a-day',5,2,1,1,10,'Put all the ingredients into the soup maker and press the ‘smooth soup’ function. Make sure you don’t fill the soup maker above the max fill line. Once the cycle is complete, season well, and check the soup for sweetness. Add a little more sugar, salt or tomato puree for depth of colour, if you like.','5 mins','30 mins'),
(828,'indian winter soup','https://www.bbcgoodfood.com/recipes/indian-winter-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-518468_11-10f1c7e.jpg','This warming winter soup is high in fibre, low in fat and can be frozen for ultimate convenience',5,4,1,1,10,'Rinse the pearl barley and cook following pack instructions. When it is tender, drain and set aside. Meanwhile, heat the oil in a deep, heavy-bottomed pan. Fry the mustard seeds, cumin seeds, chillies, bay leaf, cloves, cinnamon and turmeric until fragrant and the seeds start to crackle. Tip in the onion and garlic, then cook for 5-8 mins until soft. Stir in the parsnip, butternut and sweet potato and mix thoroughly, making sure the vegetables are fully coated with the oil and spices. Sprinkle in the paprika, ground coriander and seasoning, and stir again. Add the lentils, pearl barley, tomatoes and 1.7 litres water. Bring to the boil then turn down and simmer until the vegetables are tender. When the lentils are almost cooked, stir in the chopped coriander, ginger and lemon juice.','15 mins','30 mins'),
(829,'vegan ramen','https://www.bbcgoodfood.com/recipes/vegan-ramen','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-ramen-c656140.jpg','Enjoy this vegan ramen for a tasty midweek meal in just 25 minutes. Dried mushrooms and miso paste give the broth plenty of umami flavour',6,2,1,1,10,'Crush the garlic with the back of a big knife, then put it in a saucepan with the ginger, miso, neri goma, mushrooms, stock and soy. Bring to a gentle simmer, cover and bubble for 5 mins until the ginger is soft. Strain into a clean pan and discard everything left in the strainer. Meanwhile, cook the tofu. Toss it in the cornflour and heat the oil in a frying pan. Fry for a few mins on each side, being careful as you turn it that it doesn’t fall apart. Cook the noodles for 1 min less than pack instructions, so they retain a little bite. Drain and leave in the pan with a little cooking water so they don’t stick together. Add the pak choi and whites of the spring onions to the broth and gently reheat for 1-2 mins until the greens have just wilted. Divide the noodles between two deep bowls, ladle over the broth and veg. Top with the tofu, beansprouts, carrot and ginger matchsticks, green parts of the spring onions and a drizzle of sesame oil, plus the other toppings, if you like.','10 mins','15 mins'),
(830,'chunky peanut soup','https://www.bbcgoodfood.com/recipes/chunky-peanut-soup','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Chunky-peanut-soup-0993ffc.jpg','Make this peanut soup for lunch. Nutrient-dense, peanuts are a great source of good-for-you fats, protein and fibre, as well as stress-busting B vitamins',4,4,1,1,10,'Heat the oil in a large pan (it makes a big quantity) and fry the onions and ginger, stirring frequently, for 10 mins until starting to turn golden. Add the garlic and chilli, cook for a few mins more, then stir in the curry powder and cumin seeds. Pour in the tomatoes and stock, add the peanut butter and tomato purée, then bring to the boil. Stir in the potatoes, squash, chickpeas and cabbage, then cover the pan and leave to simmer for 15 mins until the vegetables are tender. Stir in the fresh coriander, then spoon two portions into two bowls and serve. Cool and chill the remainder to eat another day. Will keep chilled for up to three days. To serve, reheat in a pan until piping hot.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','20 mins','25 mins'),
(1233,'easy easter nests','https://www.bbcgoodfood.com/recipes/shredded-wheat-nests','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/shreddedwheatnests-92749c8.jpg','These easy chocolate nests are the perfect Easter treat. They''re simple enough for kids to get involved and they need just three ingredients',5,12,1,2,3,'Melt the chocolate in a small bowl placed over a pan of barely simmering water. Pour the chocolate over the shredded wheat and stir well to combine. Spoon the chocolate wheat into 12 cupcake cases and press the back of a teaspoon in the centre to create a nest shape. Place 3 mini chocolate eggs on top of each nest. Chill the nests in the fridge for 2 hrs until set.','25 mins','8 mins'),
(831,'herby broccoli & pea soup','https://www.bbcgoodfood.com/recipes/herby-broccoli-pea-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/herby-broccoli-pea-soup-d42271a.jpg','Our vibrant broccoli and pea soup delivers three of your 5-a-day, and is full of nutrients including iron, folate and vitamin C',14,4,1,1,10,'Heat the oil in a large saucepan. Add the onion and fry for 8 mins until soft and translucent. Add the garlic and cook for 1 min more. Tip in the broccoli, peas and chard, then pour over the stock and bring the mixture to the boil. Reduce the heat to a simmer, cover and cook for 25 mins. Stir through the herbs, lemon zest and juice, then blitz the soup with a stick blender until completely smooth. Ladle into bowls and serve with the toasted pumpkin seeds scattered over the top.','10 mins','30 mins'),
(832,'vegan curried squash, lentil & coconut soup','https://www.bbcgoodfood.com/recipes/curried-squash-lentil-coconut-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-910467_11-5ab0424.jpg','Flavour up butternut squash with Indian spices for this warming and healthy vegan soup. Serve with naan for a hearty lunch',7,6,1,1,10,'Heat the oil in a large saucepan, add the squash and carrots, sizzle for 1 min, then stir in the curry powder and cook for 1 min more. Tip in the lentils, the vegetable stock and coconut milk and give everything a good stir. Bring to the boil, then turn the heat down and simmer for 15-18 mins until everything is tender. Using a hand blender or in a food processor, blitz until as smooth as you like. Season and serve scattered with roughly chopped coriander and some naan bread alongside.','10 mins','25 mins'),
(833,'miso soup','https://www.bbcgoodfood.com/recipes/miso-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso_soup-e06ed98.jpg','Make your own miso soup with soft cubes of tofu, crunchy curls of seaweed and aromatic spring onions. Enjoy it as a starter or as a snack',112,4,1,1,10,'Put the wakame in a small bowl and cover with cold water, then leave it for 5 mins until the leaves have fully expanded.  Make the dashi (see tip below) or heat until it reaches a rolling boil. Add the tofu and cook for 1 min before adding the seaweed.  Reduce the heat. Put both types of miso in a ladle or strainer and dip it into the pot. Slowly loosen up the miso with a spoon inside the ladle or strainer; the paste will slowly melt into the dashi. Once all the miso is dissolved into the soup, turn off the heat immediately. Sprinkle with chopped spring onions to add colour and fragrance. ','10 mins','5 mins'),
(834,'easy vegan pho','https://www.bbcgoodfood.com/recipes/easy-vegan-pho','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-vegan-pho-0eaa94e.jpg','Make our easy vegan noodle soup for a warming midweek meal. This low-calorie recipe can be spiced up according to taste with a side serving of sriracha',29,2,1,1,10,'Tip the noodles into a bowl and cover with boiling water. Leave to stand for 10 mins, then drain, rinse in cold water and set aside. In a jug, mix the Marmite with 500ml boiling water. Set aside while you cook the vegetables. Heat the oil in a saucepan, then add the mushrooms and leek. Cook for 10-15 mins until softened and beginning to colour, then add the soy sauce and Marmite and water mixture and stir. Bring to the boil for 5 mins. Divide the noodles between two deep bowls, then ladle over the hot broth. Top with the chilli slices, mint leaves and peanuts, and serve with some sriracha on the side.','10 mins','20 mins'),
(835,'indian chickpea & vegetable soup','https://www.bbcgoodfood.com/recipes/indian-chickpea-vegetable-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488563_11-7eac2f9.jpg','Healthy, flavour-packed and low-cost, this soup''s ideal for taking to work for lunch',10,4,1,1,10,'Heat the oil in a medium saucepan, then add the onion, ginger and garlic. Fry for 2 mins, then add the garam masala, give it 1 min more, then add the stock and carrots. Simmer for 10 mins, then add the chickpeas. Use a stick blender to whizz the soup a little. Stir in the beans and simmer for 3 mins. Pack into a flask or, if you’ve got a microwave at work, chill and heat up for lunch. Great with naan bread.','10 mins','15 mins'),
(836,'minty pea & potato soup','https://www.bbcgoodfood.com/recipes/minty-pea-potato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488594_10-bcbf7c7.jpg','This vibrant soup is fresh-tasting and substantial enough to have for dinner',5,4,1,1,10,'Heat the oil in a large saucepan, then fry the onion for 5 mins until softened. Add the potatoes and stock, then bring to the boil. Cover and simmer for 10-15 mins until tender, adding the peas 2 mins before the end of the cooking time. Use a slotted spoon to remove a quarter of the vegetables from the pan and set aside. Blend the remaining vegetables and stock in a food processor or using a hand blender until smooth, then stir through the reserved veg, chopped mint and some seasoning. Serve with bread on the side.','5 mins','25 mins'),
(1249,'white sourdough','https://www.bbcgoodfood.com/recipes/white-sourdough','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/white-sourdough-a59e971.jpg','Master the art of making sourdough bread with our step-by-step recipe. Learn how to create a starter, levain and the loaf itself with our expert tips.',7,2,3,2,18,'Day 1:To begin your starter, mix 50g flour with 50g tepid water in a jar or, better still, a plastic container. Make sure all the flour is incorporated and leave, semi-uncovered, at room temperature for 24 hrs. Day 2:Mix 50g flour with 50g tepid water and stir into yesterday’s mixture. Make sure all the flour is incorporated and leave, semi-uncovered, at room temperature for another 24 hrs. Day 3:Mix 50g flour with 50g tepid water and stir into yesterday’s mixture. Make sure all the flour is incorporated and leave, semi-uncovered, at room temperature for another 24 hrs. Day 4:You should start to see some activity in the mixture now; there should be some bubbles forming and bubbling on top. Mix 50g flour with 50g tepid water and stir into yesterday’s mixture. Make sure all the flour is incorporated and leave, semi-uncovered, at room temperature for another 24 hrs. Day 5:The mixture should be very active now and ready for making your levain. If it’s not bubbling, continue to feed it on a daily basis until it does. When it’s ready, it should smell like yogurt. You now have a starter, which is the base to the bread. You’ll need to look after it, but naming is optional! Keep it in the fridge (it will stay dormant) and 24 hrs before you want to use it, pour half of it off and feed it with 100g flour and 100g water. Leave it at room temperature and it should become active again. The longer the starter has been dormant, the more times it will need to be refreshed – the process of pouring off half the starter and replacing it with new flour and water – to reactivate. If your starter is ready to use, a teaspoonful of the mixture should float in warm water. For the levainMix 1 tbsp of the starter with 100g flour and 100g water (this is the same process as feeding the starter, but you should do this in a new, separate bowl, keeping the original starter as back-up, as this is the mixture you''ll use to bake your bread). Leave the levain for 8 hrs at room temperature until it becomes active. When ready, a teaspoonful of the mixture should float in warm water. For the breadPour 600g tepid water into the levain and stir with a rubber spatula to mix together – don’t worry if there are unmixed bits of levain. Tip in the flour and mix everything together with a rubber spatula to make a rough dough, ensuring all the flour is incorporated evenly and that there are no dry bits up the side of the bowl. Cover and leave somewhere warm for at least 30 mins, or up to 4 hrs. Sprinkle over the salt and add 50g water to the dough. Pinch and scrunch the salt and water through the dough with your hands. If the dough goes stringy (like old chewing gum), just keep working it until it’s one smooth texture. Leave for another 15 mins. Wet your hands, grab the dough from one side and stretch it over itself, then repeat with the other side. This stretching technique helps develop the gluten. Pick the dough up and curl it around onto itself, then cover and leave for another 20-30 mins. Repeat this process two more times (three in total), then leave the dough for another 2-3 hrs until it’s risen by about 30% and looks bubbly and soft. Using a rubber spatula, scrape the dough out onto a lightly floured work surface and split in two. Fold each piece onto itself to create a ball, then leave uncovered for 30 mins. The dough balls will spread during this time. Dust two bread-proving baskets generously with flour. Scrape one of the balls of dough off the work surface, then fold it onto itself to create a tight ball that’s trapped in all the air. Lift the ball into a basket, seam-side up, then transfer to the fridge to chill overnight, or for up to 18 hrs. Repeat with the other dough ball. Heat the oven to 240C/220C fan/gas 9 and put a lidded casserole dish in the oven to heat. Cut a sheet of baking parchment into a square slightly larger than the base of one of the loaves. Carefully remove the hot casserole dish from the oven and remove the lid. Invert one loaf onto the baking parchment, then, working quickly, score the top at an angle. Use the corners of the parchment to lift the loaf into the casserole dish. Cover with the lid and bake for 30 mins, then carefully uncover and continue to bake for another 10 mins (or longer for a darker finish). Carefully lift the bread out of the dish using a spatula, transfer to a wire rack and leave to cool to room temperature before slicing. Repeat with the second loaf.','1 hr','40 mins'),
(837,'danish-style yellow split pea soup','https://www.bbcgoodfood.com/recipes/danish-style-yellow-split-pea-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/danish-style-yellow-split-pea-soup-7e5ae63.jpg','Fill up on this comforting yellow split pea soup, full of vegetables and health-boosting nutrients. Serve with rye bread, mustard and pickle to complete the meal',111,6,1,1,10,'Drain the peas and bring to the boil in a pan of salted water. Cook for 10 mins, drain, rinse and put in a slow cooker with the remaining ingredients except the dill. Cover and cook on low for 6-8 hrs, or until everything has softened. Remove and discard the thyme. Stir, adjusting the soup with 250-300ml boiled water as needed. Season to taste. Sprinkle over the dill and serve with rye bread, mustard and pickle, if you like.','20 mins','6 hrs - 8 hrs'),
(838,'mushroom & potato soup','https://www.bbcgoodfood.com/recipes/mushroom-potato-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mushroom-potato-soup-440-400-53841c1.jpg','Porcini mushrooms give this healthy soup a real umami flavour boost. Pour into a flask for a warming, low-calorie lunch that you can take to work',4,4,1,1,10,'Heat the oil in a large pan. Tip in the onions and fry for 10 mins until golden. Meanwhile, pour 1.2 litres boiling water over the dried mushrooms and stir in the bouillon. Add the fresh mushrooms and garlic to the pan with the potatoes, thyme and carrots, and continue to fry until the mushrooms soften and start to brown. Pour in the dried mushrooms and stock, cover the pan and leave to simmer for 20 mins. Stir in the parsley and plenty of pepper. Ladle into bowls and serve each portion topped with 2 tbsp yogurt and a quarter of the walnuts. The rest can be chilled and reheated the next day.','15 mins','30 mins'),
(839,'moroccan spiced cauliflower & almond soup','https://www.bbcgoodfood.com/recipes/moroccan-spiced-cauliflower-almond-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-869452_10-b622544.jpg','Satisfying and comforting soup is the ultimate meal in a bowl, like this smooth and spicy North African blend',5,4,1,1,10,'Cut the cauliflower into small florets. Fry olive oil, ground cinnamon, cumin and coriander and harissa paste for 2 mins in a large pan. Add the cauliflower, stock and almonds. Cover and cook for 20 mins until the cauliflower is tender. Blend soup until smooth, then serve with an extra drizzle of harissa and a sprinkle of toasted almonds.','5 mins','25 mins'),
(840,'thai squash soup','https://www.bbcgoodfood.com/recipes/thai-squash-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-929460_11-d0ee305.jpg','Up your vegetable intake with this fragrant pumpkin soup spiced with Asian flavours ',5,4,1,1,10,'Fry the onion, lemongrass and most of the chilli in a large pan with a splash of water for 2-3 mins until softened – add more water if it starts to catch. Tip in the squash and stir. Cover with 1 litre water, bring to the boil and simmer for 15 mins until the squash is tender. Add lime juice, remove from the heat and blitz with a hand blender until smooth. Pour in the coconut milk, season, then return to the heat to gently warm through. Ladle into bowls and serve with coriander and the remaining chilli.','15 mins','20 mins'),
(841,'vegan pasta bake','https://www.bbcgoodfood.com/recipes/vegan-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Mac-n-cheese-01249e4.jpg','Dive into this comforting vegan pasta bake with sweet potato and cherry tomatoes. Creamy, nutty cashews take centre stage, along with plant-based milk, dairy-free cheese and plenty of veg',113,4,1,1,10,'For the sauce, put the cashew nuts in a saucepan of water and bring to the boil. Boil for 5 mins, then add all the vegetables and the garlic. Continue to simmer for at least 10 mins until all the veg is tender. Drain and transfer to a blender. Add all the remaining sauce ingredients and season with salt. Blend until smooth. Bring a large pan of water to the boil and add a good pinch of salt. Tip in the macaroni and cook following pack instructions. Drain and mix with the vegetable sauce. If you like, sprinkle with vegan parmesan to serve. The macaroni cheese can be eaten like this or you can bake it in the oven. To bake the mac ''n'' cheese, heat the oven to 200C/180C fan/gas 6. Transfer to an ovenproof dish and stir through the tomatoes. Mix the breadcrumbs with the basil and vegan cheese, then scatter over the top and bake for 25 mins until piping hot and bubbling.','20 mins','25 mins'),
(851,'asparagus & lemon spaghetti with peas','https://www.bbcgoodfood.com/recipes/asparagus-lemon-spaghetti-peas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/asparagus-lemon-spaghetti-with-peas-440-400-44cdab0.jpg','This healthy, vegan pasta dish is ready in under 20 minutes. It''s low calorie and provides 3 of your 5-a-day along with fibre, vitamin C and iron.',4,2,1,1,10,'Boil the spaghetti for 12 mins until al dente, adding the asparagus for the last 3 mins. Meanwhile, heat the oil in a large non-stick frying pan, add the leeks and chilli and cook for 5 mins. Stir in the garlic, peas and lemon zest and juice and cook for a few mins more. Drain and add the pasta to the pan with ¼ mug of the pasta water and toss everything together until well mixed. Spoon into shallow bowls and serve with lemon wedges for squeezing over, if you like.','7 mins','12 mins'),
(842,'jackfruit bolognese with vegan parmesan','https://www.bbcgoodfood.com/recipes/jackfruit-bolognese-vegan-parmesan','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-bolognese-b6f2686.jpg','Cutting back on meat? Try jackfruit as an alternative. This vegan bolognese not only tastes delicious, but it counts as a whopping five of your 5-a-day',4,4,1,1,10,'Heat the oil in a large non-stick pan and fry the carrots, celery and garlic for 10 mins, stirring every now and then. Meanwhile, make the vegan parmesan. Tip the cashews, nutritional yeast and garlic granules into a food processor and blitz to fine crumbs. Add a little salt, if needed, and set aside. Tip the jackfruit into the veg mix with the lentils, 700ml water, the tomato purée, bouillon powder and thyme, then cover and cook for 25 mins. Add a little more water if needed – it should be pulpy. Meanwhile cook half the spaghetti, following pack instructions, until al dente. If following the Healthy Diet Plan toss with half the sauce, divide between two bowls and sprinkle with half of the vegan parmesan and parsley, if using. The sauce will keep, covered, in the fridge for up to 3 days, or in the freezer for up to 3 months.','10 mins','40 mins'),
(843,'creamy spinach & mushroom penne','https://www.bbcgoodfood.com/recipes/creamy-spinach-mushroom-penne','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Creamy-spinach-and-mushroom-penne-ba9951e.jpg','Make a speedy, nutritious pasta supper with garlicky mushrooms and spinach. Cashew nuts and dried mushrooms create a deliciously creamy yet light sauce',4,2,1,1,10,'Cook the penne following pack instructions and put the kettle on to boil. Meanwhile, put the cashews and dried mushrooms in a medium heatproof bowl along with the bouillon powder, and pour over 200ml boiling water from the kettle. Leave to soak for 5 mins. After this time, blitz the mixture with a hand blender until smooth and creamy. Heat the oil in a large non-stick frying pan over a medium heat and fry the fresh mushrooms and garlic for a couple of minutes until just starting to soften. Add the spinach and continue to cook, stirring frequently until the spinach has wilted. Drain the pasta, reserving a little of the cooking water. Tip the pasta into the pan with the mushroom mixture, season with plenty of black pepper and toss everything together well. Remove from the heat and stir through the creamy mushroom sauce, adding a drop of the reserved cooking water if needed to loosen. Serve straightaway.','10 mins','10 mins'),
(844,'pasta alla norma','https://www.bbcgoodfood.com/recipes/pasta-alla-norma','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Pasta-Alla-Norma-f189a02.jpg','Try this easy version of an alla norma, which uses roast aubergine instead of fried. It''s then tossed with spaghetti and a rich tomato and basil sauce',14,4,1,1,10,'Toss the aubergines with the salt, then tip into a colander set over a bowl for 30 mins, to extract any moisture. Heat 2 tbsp of the oil in a casserole dish or saucepan. Tip in the onion and a pinch of salt, and cook over a low heat for 10 mins. Add the garlic and chilli, and cook for 1 min. Tip in the tomatoes and sugar, and bring to the simmer. Cook, uncovered, for 20 mins, stirring occasionally. Heat the oven to 220C/200C fan/gas 7. Pat the aubergine dry with kitchen paper, then toss with the remaining oil. Spread out on a baking sheet and roast for 25-30 mins until softened and golden around the edges. Meanwhile, cook the pasta following pack instructions, reserving 100ml of the cooking water. Stir the aubergine into the sauce with the basil and capers. Season to taste, then toss the spaghetti in the pan with the sauce and a splash of the pasta water. Divide between four bowls and top with the ricotta and extra basil, if using.','10 mins','1 hr'),
(845,'lentil lasagne','https://www.bbcgoodfood.com/recipes/lentil-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1095476_12-e11b740.jpg','Vegan cooking made easy - this Italian bake uses cauliflower and soya milk for a white sauce and canned lentils as filling',5,4,1,1,10,'Heat the oil in a pan, add the onion, carrot and celery, and gently cook for 10-15 mins until soft. Add the garlic, cook for a few mins, then stir in the lentils and cornflour. Add the tomatoes plus a canful of water, the mushroom ketchup, oregano, stock powder and some seasoning. Simmer for 15 mins, stirring occasionally. Meanwhile, cook the cauliflower in a pan of boiling water for 10 mins or until tender. Drain, then purée with the soya milk using a hand blender or food processor. Season well and add the nutmeg. Heat oven to 180C/160C fan/gas 4. Spread a third of the lentil mixture over the base of a ceramic baking dish, about 20 x 30cm. Cover with a single layer of lasagne, snapping the sheets to fit. Add another third of the lentil mixture, then spread a third of the cauliflower purée on top, followed by a layer of pasta. Top with the last third of lentils and lasagna, followed by the remaining purée. Cover loosely with foil and bake for 35-45 mins, removing the foil for the final 10 mins of cooking.','15 mins','1 hr and 15 mins'),
(852,'vegan lasagne','https://www.bbcgoodfood.com/recipes/vegan-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-lasagne-756ea33.jpg','Feed your family this delicious plant-based lasagne with salad. Plus, use the meat-free filling to make vegan spaghetti bolognese and moussaka',16,6,1,1,10,'Pour 800ml boiling water over the dried porcini and leave for 10 mins until hydrated. Meanwhile pour 1½ tbsp oil into a large saucepan. Add the onion, carrot, celery and a pinch of salt. Cook gently, stirring for 10 mins until soft. Remove the porcini from the liquid, keeping the mushroomy stock and roughly chop. Set both aside. Add the garlic and thyme to the pan. Cook for 1 min, then stir in the tomato purée and cook for a minute more. Pour in the red wine, if using, cook until nearly reduced, then add the lentils, reserved mushroom stock and cans of tomatoes. Bring to the boil, then reduce the heat and leave to simmer with the lid on. Meanwhile, heat a large frying pan. Add 1½ tbsp oil, then tip all of the mushrooms into the pan, including the rehydrated ones. Fry until all the water has evaporated and the mushrooms are deep golden brown. Pour in the soy sauce. Give everything a good mix, then scrape the mushrooms into the lentil saucepan. Stir in the Marmite, then continue to cook the ragu, stirring occasionally, over a low to medium heat for 30-45 mins until the lentils are cooked and the sauce is thick and reduced, adding extra water if necessary. Remove the thyme sprigs and season to taste. Heat the oven to 180C/160C fan/gas 4. To make the white sauce, heat 3 tbsp olive oil in a pan, whisk in the flour and cook for a couple of minutes to get rid of the raw flour taste, then slowly whisk in the soya milk. Cook until you have a creamy sauce, about 10 mins. Season to taste, adding a good grating of nutmeg. Spread a third of the ragu on the bottom of your ovenproof dish, then top with six lasagne sheets, followed by another third of the vegan white sauce, then another third of the ragu. Top with the remaining lasagne sheets, then the remaining ragu and finally, spread over the rest of the white sauce. Sprinkle over the nutritional yeast, if using, and bake for 45 mins-1 hr until the pasta is cooked.','25 mins','2 hrs'),
(846,'squash & spinach fusilli with pecans','https://www.bbcgoodfood.com/recipes/squash-spinach-fusilli-pecans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/squash-spinach-fusilli-with-pecans-0690874.jpg','You don’t have to be vegan to enjoy this colourful low-fat, low-calorie squash and spinach pasta topped with pecans. It''s tasty as well as healthy',4,2,1,1,10,'Heat oven to 200C/180C fan/gas 6. Toss the butternut squash, garlic and sage in the oil, then spread out in a roasting tin and cook in the oven for 20 mins, add the courgettes and cook for a further 15 mins. Give everything a stir, then add the pecans and cook for 5 mins more until the nuts are toasted and the vegetables are tender and starting to caramelise. Meanwhile, boil the pasta according to pack instructions – about 12 mins. Drain, then tip into a serving bowl and toss with the spinach so that it wilts in the heat from the pasta. Add the roasted veg and pecans, breaking up the nuts a little, and toss again really well before serving.','10 mins','40 mins'),
(847,'tomato & aubergine pasta','https://www.bbcgoodfood.com/recipes/tomato-aubergine-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3253_11-5d1afcc.jpg','Superhealthy, high in fibre',5,4,1,1,10,'Boil the pasta according to pack instructions. Meanwhile, heat the oil in a frying pan and fry the aubergine for 5 mins. Season to taste. Add garlic and sugar, and fry for another 2 mins until the garlic is softened. Tip the tomatoes, capers and parsley into the pan, warm through briefly then fold through the cooked, drained pasta.','10 mins','20 mins'),
(848,'veggie hummus pasta salad','https://www.bbcgoodfood.com/recipes/veggie-hummus-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Veggie-hummus-pasta-salad-3727588.jpg','Make your own hummus in this family pasta salad. Dish up for little ones first, then scatter with pul biber chilli flakes and crumbled feta for the grown-ups',5,4,1,1,10,'Boil the kettle. Tip half the chickpeas into a food processor, add roughly half the reserved liquid from the can (the liquid should come to just below the level of the chickpeas in the blender), the tahini, olive oil, garlic, lemon zest and juice and some seasoning. Blitz until you have a smooth, loose hummus. Check for seasoning. Cook the pasta following pack instructions. Drain, reserving a mugful of the cooking water, and rinse the under cold running water for a few seconds until cool. Toss the cooked pasta, spinach, tomatoes, cucumbers, olives, the rest of the chickpeas and the hummus dressing together in a large bowl until everything is well-coated. Add a splash of the reserved pasta cooking water if the dressing is too thick. Will keep covered and chilled for up to 6 hrs, or in an airtight container in a cool bag for 2 hrs. Add a splash of water to loosen the dressing again before serving.','20 mins','10 mins'),
(849,'red lentil pasta with creamy tomato & pepper sauce','https://www.bbcgoodfood.com/recipes/red-lentil-pasta-with-creamy-tomato-pepper-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Red-lentil-pasta-604d53f.jpg','Add to your five-a-day with this red lentil pasta. Gluten and dairy free, the sauce is made with peppers and cashews, which add protein and give it a creamy texture',4,2,1,1,10,'First, make the sauce. Put the cashews and garlic in a bowl, then pour over 125ml boiling water and set aside for 5 mins. Meanwhile, put a large pan of water over a high heat. When the water is boiling rapidly, tip in the pasta and a little salt, stir once, then continue to boil for 8 mins, or following pack instructions, until the pasta is softened but still retains some bite. Meanwhile, add the roasted pepper and sun-dried tomatoes to the bowl with the cashews, garlic and water, then whizz with a hand blender or in a food processor to make a smooth, creamy sauce. Stir through the basil and season with black pepper. Drain the pasta, mix with the sauce in a large bowl, then divide between two bowls and add a handful of rocket to each.','10 mins','8 mins'),
(850,'vegan bolognese','https://www.bbcgoodfood.com/recipes/vegan-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-bolognese-b4f9b85.jpg','Try our hearty vegan bolognese for a quick, healthy and satisfying dinner. Our simple pasta dish packs in four of your 5-a-day and it''s even low-fat',16,3,1,1,10,'Pour 400ml boiling water over the dried porcini and leave for 10 mins until hydrated. Meanwhile pour 1 tbsp oil into a large saucepan. Add the onion, carrot, celery and a pinch of salt. Cook gently, stirring for 10 mins until soft. Remove the porcini from the liquid, keeping the mushroomy stock and roughly chop. Set both aside. Add the garlic and thyme to the pan. Cook for 1 min then stir in the tomato purée and cook for a min more. Pour in the red wine, if using, cook until nearly reduced, then add the lentils, reserved mushroom stock and tomatoes. Bring to the boil, then reduce the heat and leave to simmer with a lid on. Meanwhile, heat a large frying pan. Add the remaining oil, then tip in the chestnut, portobello and rehydrated mushrooms. Fry until all the water has evaporated and the mushrooms are deep golden brown. Pour in the soy sauce. Give everything a good mix, then scrape the mushrooms into the lentil mixture. Stir in the Marmite and continue to cook the ragu, stirring occasionally, over a low-medium heat for 30-45 mins until the lentils are cooked and the sauce is thick and reduced, adding extra water if necessary. Remove the thyme sprigs and season to taste. Cook the spaghetti in a large pan of salted water for 1 min less than packet instructions. Drain the pasta, reserving a ladleful of pasta water, then toss the spaghetti in the sauce, using a little of the starchy liquid to loosen up the ragu slightly so that the pasta clings to the sauce. Serve topped with fresh basil and some black pepper.','20 mins','1 hr'),
(854,'simple mushroom curry','https://www.bbcgoodfood.com/recipes/simple-mushroom-curry','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Simple-mushroom-curry-26e3403.jpg','Serve these garlicky mushrooms in a rich tomato masala spiced with ground ginger, fennel seeds and cumin for an autumnal supper. Enjoy with rice or naan',114,4,1,1,19,'Melt the butter in a large wok, karahi or non-stick frying pan over a medium-high heat and cook the mushrooms for 10 mins, or until any moisture has evaporated and they’re starting to brown. Transfer to a bowl and set aside. Heat the oil in the same pan over a medium-high heat and fry the cumin and fennel seeds, stirring continuously for about 30 seconds until they release a nutty aroma. Stir in the onion and cook for 12-15 mins until golden. Reduce the heat to medium, then add the garlic and continue frying for 1 min. Add the ginger, turmeric, chilli powder and garam masala, followed by the tomatoes and sugar. Cook, uncovered, for 5-7 mins, or until the masala thickens and a layer of oil forms around the edge of the pan. Spoon the yogurt into a small bowl, add a small ladleful of the hot masala and mix well before stirring the yogurt into the pan of masala. Pour in 100ml hot water and simmer for 3-4 mins until the curry has the consistency of double cream. Season to taste, then return the mushrooms to the pan and stir to warm through. Scatter with the chopped coriander and serve with boiled rice or naan, if you like.','15 mins','30 mins - 40 mins'),
(855,'vegetarian enchiladas','https://www.bbcgoodfood.com/recipes/bean-enchiladas','https://images.immediate.co.uk/production/volatile/sites/30/2011/01/Vegetarian-enchiladas-50345cc.jpg','Pack flavour into your meal with these easy veggie enchiladas that are filled with nutritious ingredients. This comforting, cheesy dish is the perfect crowd-pleasing family dinner',5,4,1,1,19,'Heat the oil in a large frying pan. Cook the onions and carrots for 5-8 mins until soft – add a splash of water if they start to stick. Sprinkle in the chilli powder and cook for 1 min more. Pour in the tomatoes and pulses and bring to the boil. Turn down the heat and simmer for 5-10 mins, stirring occasionally, until thickened. Remove from the heat and season well. Heat grill to high. Spread a spoonful of the bean chilli over a large ovenproof dish. Lay each tortilla onto a board, fill with a few tbsp of chilli mixture, fold over the ends and roll up to seal. Place them into the ovenproof dish. Spoon the remaining chilli on top. Mix the yogurt and grated cheese together with some seasoning, and spoon over the enchiladas. Grill for a few mins until the top is golden and bubbling. Serve with a green salad.','10 mins','30 mins'),
(856,'creamy baked gnocchi with squash & spinach','https://www.bbcgoodfood.com/recipes/creamy-baked-gnocchi-with-squash-spinach','https://images.immediate.co.uk/production/volatile/sites/30/2022/12/Creamy-Baked-Gnocchi-Spinach-and-Squash-4b38d7b.jpg','Impress dinner guests with this oozy squash and spinach gnocchi dish. Guests will never know you made it in just 30 minutes with the help of a microwave',6,2,1,1,19,'Put the squash in a heatproof bowl with a splash of water and pinch of seasoning, then cover and microwave on high for 5 mins. Or, toss the squash in a drizzle of oil and some seasoning, and cook for 12 mins at 200C in an air fryer until soft and caramelised. It can also be roasted in the oven for 25 mins. Meanwhile, heat 2 tsp of the oil in a large ovenproof frying pan over a medium heat and fry the garlic for 30 seconds until sizzling but not coloured. Add the spinach, season and cook until wilted. When the squash is tender, roughly mash half of it using a fork. Stir the mashed squash and the mascarpone into the spinach, then add 150ml water and stir until you have a smooth, creamy sauce. Grate in a generous amount of nutmeg and add half the parmesan. Gently stir in the gnocchi and squash chunks to coat in the sauce. Simmer for 1-2 mins until everything is heated through, adding a splash of water if the sauce is very thick. Scatter over the breadcrumbs and remaining parmesan. Heat the grill to medium-high, then slide the gnocchi under for a few minutes until golden and bubbling at the edges.','15 mins','15 mins'),
(857,'curried sate noodles','https://www.bbcgoodfood.com/recipes/curried-sate-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Curried-sate-noodles-75bdc86.jpg','Serve up some quick and easy curried sate noodles. Packed with wholewheat, pulses, vegetables and spices, this recipe is full of healthy plant-based foods',4,2,1,1,19,'Boil the noodles following pack instructions, then rinse well to ensure the strands are separate. Meanwhile, heat the oil in a wok or sauté pan over a high heat and stir-fry the pepper, carrot, ginger, garlic and chilli, if using, for 5 mins until softened. Stir in the cumin seeds and curry powder and cook for 30 seconds or so until aromatic. Mix the peanut butter and tomato purée with the vegetable stock until smooth. Add the drained noodles to the wok along with the frozen peas, then pour in the peanut and tomato mixture and toss everything together. If it seems a bit claggy, stir in a drop more water. Squeeze in the lemon juice, toss well and serve.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','20 mins'),
(858,'vegetable bake','https://www.bbcgoodfood.com/recipes/vegetable-bake','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Vegetable-bake--c8beaaf.jpg','Prep, assemble and freeze this meat-free dish in advance for busy days when you need an easy family meal – simply defrost in the fridge overnight, then bake',15,6,1,1,19,'Heat the oven to 200C/180C fan/gas 6. Toss the celeriac, cauliflower florets and stems, the garlic and maple syrup with 1 tbsp of the olive oil in a roasting tin, and season well. Roast for 30-35 mins until the veg is caramelised. Remove from the oven, fish out the roasted garlic cloves, then mash them on a chopping board with a fork and set aside. Tip the rest of the roasted veg mixture into a baking dish. Meanwhile, heat the remaining oil in a large frying pan over a medium-low heat and cook the leeks, mushrooms and thyme sprigs for 10-15 mins until the veg has softened. Turn up the heat slightly for 5 mins to caramelise the mushrooms and leeks, then carefully pour in the white wine. Cook until the wine has almost completely reduced. Stir in the butter and, when it begins to foam, add the flour. Cook for a few minutes, stirring. Gradually pour in the milk, stirring well after each addition. Bring to a gentle simmer and cook for 5 mins, stirring often until thickened. Remove from the heat, add the mashed roasted garlic and the cheddar, and stir until the cheese has melted. Season well. Roughly chop the cauliflower leaves and stir these through the sauce. Pour the cheese sauce into the baking dish with the roasted veg, and stir to combine. Sprinkle over the sourdough crumbs, then return to the oven for 15-20 mins, or until the top is golden and the sauce is bubbling at the edges. Serve alongside roast chicken, or as a main with a green salad on the side. To freeze, leave to cool completely in the dish, wrap well and freeze for up to three months. Defrost completely in the fridge overnight, then bake as directed.','20 mins','1 hr'),
(859,'vegetarian bolognese','https://www.bbcgoodfood.com/recipes/vegetarian-bolognese','https://images.immediate.co.uk/production/volatile/sites/30/2018/12/Veggie-bolognese-8ecf29a.jpg','Make the most of vegetarian mince for this easy veggie bolognese that''s low in fat and calories. Cook a large batch ahead and freeze for easy family dinners',19,4,1,1,19,'Heat the oil in a saucepan and gently fry the onion, carrots and celery until the onion is starting to soften. Stir in the garlic and the vegetarian mince (there’s no need to defrost it) and fry for a couple of mins. Add the bay leaf, passata, vegetable stock cube and 200ml water, then bring everything to the boil. Turn down the heat and simmer for 30 mins or until all the pieces of veg are tender and disappearing into the tomato sauce. Add the milk, then cover with a lid and cook for 10 mins. Season to taste. If the sauce is a bit thin, keep bubbling until it thickens. Stir through the basil. Serve with the spaghetti and grate the cheese over the top, if you like. Can be frozen into portions and reheated.','10 mins','1 hr'),
(860,'sweet potato & peanut curry','https://www.bbcgoodfood.com/recipes/satay-sweet-potato-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/satay-sweet-potato-curry-17cc62d.jpg','Cook this tasty, budget-friendly vegan curry for an easy family dinner. With spinach and sweet potato, it boasts two of your five-a-day and it’s under 400 calories',41,4,1,1,19,'Melt 1 tbsp coconut oil in a saucepan over a medium heat and soften 1 chopped onion for 5 mins. Add 2 grated garlic cloves and a grated thumb-sized piece of ginger, and cook for 1 min until fragrant. Stir in 3 tbsp Thai red curry paste, 1 tbsp smooth peanut butter and 500g sweet potato, peeled and cut into chunks, then add 400ml coconut milk and 200ml water. Bring to the boil, turn down the heat and simmer, uncovered, for 25-30 mins or until the sweet potato is soft. Stir through 200g spinach and the juice of 1 lime, and season well. Serve with cooked rice, and if you want some crunch, sprinkle over a few dry roasted peanuts.','15 mins','45 mins'),
(861,'butternut squash & sage risotto','https://www.bbcgoodfood.com/recipes/butternut-squash-sage-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1040452_11-c7ac00d.jpg','A satisfying veggie supper that gives a basic risotto recipe an autumnal twist',7,4,1,1,19,'Heat the oven to 220C/200C fan/gas 7. Toss the squash in 1 tbsp oil together with the chopped sage. Scatter into a shallow roasting tin and roast for 30 mins until brown and soft. While the squash is roasting, prepare the risotto. Bring the stock to the boil and keep on a low simmer. In a separate pan, melt half the butter over a medium heat. Stir in the onions and cook gently for 8-10 mins until soft but not coloured, stirring occasionally. Stir the rice into the onions until completely coated in the butter, then stir continuously until the rice is shiny and the edges of the grain start to look transparent. Pour in the wine and simmer until completely evaporated. Add the stock, a ladleful at a time, and stirring the rice over a low heat for 25-30 mins, until the rice is cooked al dente (with a slightly firm, starchy bite in the middle). The risotto should be creamy and slightly soupy. At the same time, gently fry the whole sage leaves in a little olive oil until crisp, then set aside on kitchen paper. When the squash is cooked, mash half of it to a rough purée and leave half whole. When the risotto is just done, stir through the purée, then add the cheese and remaining butter and leave to rest for a few minutes. Serve the risotto scattered with the whole chunks of squash and the crisp sage leaves.','10 mins','40 mins'),
(902,'slow cooker red cabbage','https://www.bbcgoodfood.com/recipes/slow-cooker-red-cabbage','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/red_cabbage-ab5633d.jpg','Dig out your slow cooker for this delicious red cabbage. Braised cabbage makes the perfect side dish for a festive lunch or roast dinner',6,8,1,1,19,'Heat your slow cooker to Low. Peel the outer leaves from the cabbage and discard. Quarter the cabbage, removing the tough stem, then thinly slice. Arrange a layer of the cabbage on the bottom of your slow cooker, then top with some of the onions, apples, zest, mixed spice, sugar and some seasoning. Continue to create layers until you have used up these ingredients. Season.  Pour over the vinegar and cider and dot the butter on top. Cover with a lid and cook for 4-5 hrs until tender. Will keep in the fridge, covered, for up to three days or in the freezer for up to two months. Reheat in a pan or in the microwave. ','30 mins','4 hrs - 5 hrs'),
(862,'veggie shepherd''s pie with sweet potato mash','https://www.bbcgoodfood.com/recipes/veggie-shepherds-pie-sweet-potato-mash','https://images.immediate.co.uk/production/volatile/sites/30/2011/02/veggie-shepherds-pie-with-sweet-potato-mash-ced560e.jpg','The secret to this shepherd''s pie filling is to choose big carrots so they don’t lose their texture when cooked',4,4,1,1,19,'Heat 1 tbsp olive oil in a frying pan, then fry 1 halved and sliced large onion until golden. Add 2 large carrots, cut into sugar-cube size pieces and most of the 2 tbsp chopped thyme, reserving a sprinkling for later. Pour in 200ml red wine, 150ml water and a 400g chopped tomatoes, then crumble in 2 vegetable stock cubes and simmer for 10 mins. Tip in a 410g can green lentils, including the juice, then cover and simmer for another 10 mins until the carrots still have a bit of bite and the lentils are pulpy. Meanwhile, boil 950g sweet potatoes, cut into chunks, for 15 mins until tender, drain well, then mash with 25g butter and season to taste. Pile the lentil mixture into a pie dish, spoon the mash on top, then sprinkle over 85g grated vegetarian mature cheddar and the remaining thyme. The pie can now be covered and chilled for 2 days, or frozen for up to a month. Heat oven to 190C/170C fan/gas 5. Cook for 20 mins if cooking straightaway, or for 40 mins from chilled, until golden and hot all the way through. Serve with broccoli.','10 mins','50 mins - 1 hr'),
(863,'veggie-loaded flatbread','https://www.bbcgoodfood.com/recipes/veggie-loaded-flatbread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-loaded-flatbread-331b78b.jpg','Top a shop-bought flatbread with chickpeas, tomatoes, feta, olives and tahini for a pizza-inspired lunch or dinner. Make it vegan by swapping the feta for extra veggies',14,1,1,1,19,'Heat the grill to its highest setting. Spread the onion wedges out on a baking tray and drizzle with 1 tbsp of the oil. Grill for 3-5 mins turning halfway through, then add the tomatoes to the tray, season and grill for a further 5 mins or until juicy and popping. Put the chickpeas in a pan with the garlic and remaining 1 tbsp oil, then heat for 5 mins before crushing the chickpeas using a potato masher. Stir through the tahini, the lemon zest and juice along with 2 tbsp water. Season. Warm your flatbread under the grill for a couple of minutes. Top with the crushed chickpeas, then the tomato and onion mixture. Finish with the olives, feta and basil, and a drizzle of olive oil.','10 mins','15 mins'),
(864,'chilli paneer','https://www.bbcgoodfood.com/recipes/chilli-paneer-2','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Chilli-paneer-4c0d9ff.jpg','Enjoy this flavour-packed chilli paneer with rice and your favourite vegetarian sides for a delicious, lightly spiced dinner for two',15,2,1,1,19,'Heat the vegetable oil in a large non-stick pan over a medium heat. Combine the cornflour, plain flour, chilli powder, 1 tsp fine sea salt, ½ tsp freshly ground black pepper and 2 tbsp water in a bowl until you have a smooth paste, adding a splash more water if it''s too thick. Add the paneer cubes and toss to coat. Carefully tip the coated paneer into the hot oil in the pan and fry, stirring, until crisp and browned on all sides. Transfer to a plate lined with kitchen paper, and set aside. To make the sauce, tip the cornflour into a jug and gradually whisk in the soy sauce until smooth. Add the chilli sauce, ketchup, rice vinegar, honey and 90ml water, then whisk again and set aside. Drain most of the excess oil from the pan and return to a medium heat. Fry the ginger, garlic and chopped chilli for a few minutes until fragrant. Stir in the chilli powder, onion and pepper, turning up the heat to medium-high, and fry until lightly browned. Tip in the soy sauce mixture bubble until thickened slightly. Remove from the heat and stir in the paneer, ensuring it’s well-coated in the sauce. Add a splash more water if needed, and season to taste. Sprinkle over the spring onion and serve with rice, if you like.','20 mins','20 mins'),
(865,'gingery broccoli-fry with cashews','https://www.bbcgoodfood.com/recipes/gingery-broccoli-fry-with-cashews','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Gingery-broccoli-fry-with-cashews-42012f6.jpg','Try our gingery broccoli-fry for a healthy veggie dinner. It''s a bit like fried rice but with antioxidant-rich broccoli instead',4,2,1,1,19,'Blitz the broccoli stalks in a food processor until finely chopped. Add the florets and pulse again to achieve a rice-like texture. Lightly toast the cashews in a wok or frying pan, then tip onto a plate and set aside. Heat the oil in a pan over a high heat and add the ginger, onion, pepper, carrot, garlic and chilli. Stir-fry for 2-3 mins until starting to brown, then put a lid on and cook for another 2 mins. Add the broccoli and 3 tbsp water and stir-fry for 3 mins until all the veg is tender. Pour in the tamari, lime juice and zest and coriander, stir well, then pour in the eggs and stir-fry very briefly to just set. Serve with the cashews, extra coriander and extra sliced chilli scattered over, if you like.','15 mins','10 mins'),
(913,'foccaccia rolls','https://www.bbcgoodfood.com/recipes/foccaccia-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-743466_11-e87df17.jpg','Team these rustic bread rolls with a selection of antipasti or your favourite Italian fare',37,10,1,2,20,'Mix the flour, yeast and salt in a large mixing bowl. Make a dip in the centre of the flour and pour in almost 300ml hand warm (cool rather than hot) water and 1 tbsp olive oil, with a round-bladed knife. Then mix in enough of the remaining water and a bit more if needed, to gather up any dry bits in the bottom of the bowl, until the mixture comes together as a soft, not too sticky, dough. Gather it into a ball with your hands. Put the dough onto a very lightly floured surface and knead for 8-10 mins until it feels smooth and elastic, only adding the minimum of extra flour if necessary to prevent the dough sticking. Place the ball of dough on a lightly floured work surface. Cover with an upturned, clean, large glass bowl and leave for 45 mins-1 hr or until doubled in size and feels light and springy. Timing will depend on the warmth of the room. Knock back the dough by gently kneading just 3-4 times. You only want to knock out any large air bubbles, so too much handling now will lose the dough’s lightness. Shape into a ball, then cover and leave for 10 mins. Cut the dough into 10 even pieces. Shape each into a ball and sit them on 2 baking parchment-lined baking sheets. Cover with a clean dry tea towel and leave for 40-45 mins, or until about doubled in size. Finish by using your fingers press a wide indent in the middle of each roll. Scatter over the roasted peppers, cheese and olives pressing down quite firmly into the indents, finish with rocket. Drizzle with most of the remaining oil mixed with a good pinch of dried oregano. Season. Put a roasting tin in the bottom of the oven 20 mins before ready to bake and heat oven to 230C/210C fan/gas 8. Put the risen bread in the oven, carefully pour about 250ml cold water into the roasting tin (this will hiss and create a burst of steam to give you a crisp crust), then lower the heat to 220C/200C fan/gas 7. Bake for 20 mins or until golden. Remove and cool on a wire rack. If you tap the underneath of the loaf if should be firm and sound hollow. When baked, drizzle with a little extra olive oil.','20 mins','20 mins'),
(866,'slow cooker vegetable lasagne','https://www.bbcgoodfood.com/recipes/slow-cooker-vegetable-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegelasagne-43d04d5.jpg','Make this low-calorie veggie lasagne as a lighter alternative to the traditional bake. It''s layered with pasta, ratatouille and sliced aubergine, and packs in all of your five-a-day',5,4,1,1,19,'Heat 1 tbsp rapeseed oil in a large non-stick pan and fry 2 sliced onions and 2 chopped large garlic cloves for 5 mins, stirring frequently until softened. Tip in 2 diced large courgettes, 1 red and 1 yellow pepper, both roughly sliced, and 400g chopped tomatoes with 2 tbsp tomato purée, 2 tsp vegetable bouillon and 15g chopped basil. Stir well, cover and cook for 5 mins. Don''t be tempted to add more liquid as plenty of moisture will come from the vegetables once they start cooking. Slice 1 large aubergine. Lay half the slices of aubergine in the base of the slow cooker and top with 3 sheets of lasagne. Add a third of the ratatouille mixture, then the remaining aubergine slices, 3 more lasagne sheets, then the remaining ratatouille mixture. Cover and cook on High for 2½ - 3 hours until the pasta and vegetables are tender. Turn off the machine. Scatter 125g vegetarian buffalo mozzarella over the vegetables then cover and leave for 10 mins to settle and melt the cheese. Scatter with extra basil and serve with a handful of rocket.','30 mins','2 hrs and 30 mins - 3 hrs'),
(867,'tofu stir-fry','https://www.bbcgoodfood.com/recipes/tofu-stir-fry','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Tofu-stir-fry-16f5101.jpg','Get children to help you chop the vegetables and make the sauce for this colourful, simple tofu stir-fry. It''s perfect when you need a quick and easy family meal',20,4,1,1,19,'Stir together the soy sauce, honey and vinegar in a bowl, then set aside. Pat the tofu chunks dry, then season well. Heat half the oil in a wok or large frying pan over a high heat. Fry the tofu for 5 mins until golden, stirring occasionally. Add the garlic and ginger and fry for 1 min more. Tip the tofu mixture into a bowl, pour over half the soy dressing and set aside. Add the remaining oil to the wok. Add the vegetables and cook for 5 mins until beginning to turn golden, then add a good splash of water and cook for 3-5 mins more until tender. Stir the cornflour and 1 tbsp water into the remaining soy dressing, then tip into the wok, allow to bubble for 30 seconds, stirring to coat everything in the sauce. Sprinkle over the spring onion, sesame seeds and coriander. Serve with rice.','15 mins','15 mins'),
(868,'mushroom bourguignon pithivier','https://www.bbcgoodfood.com/recipes/mushroom-bourguignon-pithivier','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Mushroom-bourguinon-pithivier-77b6287.jpg','Serve our moreish mushroom bourguignon pithivier as a vegan main course. Ideal for a special occasion such as Christmas, it''s full of deep flavours',2,6,1,1,19,'Cook the mushrooms in a large saucepan, covered, for 5 mins. Remove the lid so the liquid can evaporate, about 15 mins. Add the oil and fry the mushrooms for 5-6 mins until golden. Remove to a bowl with a slotted spoon, leaving any excess oil behind, and set aside. Fry the shallots for 6-8 mins until beginning to brown, then add the carrot, thyme leaves and garlic, and fry for 1 min. Add the tomato purée and flour, mix well and cook for 1-2 mins. Pour in the wine, stir and simmer for 3-4 mins until the alcohol has evaporated. Pour in the stock and cook for 15-20 mins more until the shallots and carrots are almost tender. Return the mushrooms to the pan and cook for 10-15 mins more until everything is cooked through, the liquid has almost evaporated and the sauce is thick. It’s important that you cook the liquid down until thick, or it will be difficult to shape later. Remove from the heat and leave to cool for 1 hr until cold. You can make the filling up to three days ahead and chill. Heat the oven to 200C/180C fan/gas 6. Cut off a third of the pastry block and roll out on a lightly floured surface to a 5mm-thick square. Put on a sheet of baking parchment. Spoon the cooled veg mixture into the centre piling it up high. Roll out the larger pastry block as before, ensuring the square is enough to cover the base. Brush the exposed pastry on the base around the mushroom filling with soya milk, then lay the larger pastry square on top to enclose the filling. Press around the edges to seal, then trim the edges so you end up with an 18cm circle with the filling in the middle. Score lines or patterns into the top of the pastry, being careful not to cut all the way through. Brush all over with soya milk, then bake for 30-40 mins until the filling is hot and the pastry is puffed up and golden.','40 mins','1 hr and 30 mins'),
(869,'vegetarian wellington','https://www.bbcgoodfood.com/recipes/vegetarian-wellington','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegetarian-wellington-db87aaa.jpg','Create a vegetarian centrepiece with this meat-free wellington with beetroot, mushroom and butternut squash wrapped in pastry. It''s ideal for a celebration',12,8,2,1,19,'Heat the oven to 200C/180C fan/gas 6. Toss the squash with 1 tbsp oil, half the sage and some seasoning. Tip onto a baking tray and roast for 25 mins until tender. Meanwhile, heat 1 tbsp oil in a large frying pan and fry the shallots for 10 mins until tender. Add the mushrooms and fry for 15 mins until softened, and the liquid has evaporated. Add the garlic and remaining sage to the mushrooms, fry for a minute, then add the double cream. Stir until it coats the mushrooms and no liquid remains. Fold in the breadcrumbs, mace, chestnuts and roast squash. Season everything well, adding a grating of nutmeg. Leave to cool before assembling the wellington. Roll the pastry out on a floured work surface to a 35 x 45cm rectangle. Transfer to a large parchment-lined baking sheet with one of the long lengths of the pastry facing towards you. Spoon the mushroom and squash mixture down the length of the pastry, leaving a 7cm border along the top and 1cm on both sides. Brush all the exposed pastry with beaten egg. Put the beetroots in a line down the middle of the filling – they should be touching. Fold the filling-covered, long length of the pastry over the beets, using the paper to help you. Roll up and trim the pastry with a knife when it’s overlapping by 1cm. Place the wellington seal-side down, and pinch the ends to seal. Egg wash the whole thing and use a fork or blunt cutlery knife to score the pastry in a pretty pattern. Chill until for at least 30 mins, or up to 24 hrs. Heat the oven to 200C/180C fan/gas 6. Brush the wellington with more beaten egg, sprinkle with the sesame or poppy seeds and bake for 45-50 mins until golden brown and puffed up. Leave to rest for 10 mins before carving into chunky slices with a sharp knife to serve.','1 hr','1 hr and 20 mins'),
(870,'easy vegetarian chilli','https://www.bbcgoodfood.com/recipes/easy-vegetarian-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Vegetarian-chilli-206c469.jpg','Rustle up our easy veggie chilli. It''s a great recipe for batch-cooking – you can easily double it if you have a pan big enough, and freeze the rest',2,8,1,1,19,'Heat the oil in a large saucepan over a low-medium heat and fry the carrots, celery, onions and mixed herbs for 10-12 mins, stirring occasionally until the veg is soft but not golden. You may need to add a splash of water if the veg starts to catch. Stir in the garlic and both peppers, and cook for a further 5 mins until the peppers begin to soften. Sprinkle in the chilli powder and paprika, turn up the heat to medium, then stir and cook for 1 min. Mix in the tomato purée and cook for a further 1 min, then pour in all of the beans, the tomatoes and stock. Stir well, bring to the boil, then reduce the heat to a simmer. Cook for 25-35 mins until the beans are tender and the sauce has thickened. Serve with rice, grated cheddar and soured cream, if you like.','25 mins','55 mins'),
(871,'teriyaki tofu with charred spring onions','https://www.bbcgoodfood.com/recipes/charred-spring-onions-teriyaki-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/charred-spring-onions-teriyaki-tofu-1635424.jpg','Rustle up this teriyaki tofu served with wholegrain rice in just 30 minutes. Easy and low in fat, this vegetarian dish is perfect for busy weeknights',29,2,1,1,19,'Cook the rice according to pack instructions. Pour the soy sauce, mirin, ginger and honey into a small saucepan and add 50ml water. Bring to a simmer and cook for around 5 mins or until slightly thickened. Remove from the heat and set aside until needed. If your tofu doesn’t feel very firm, you’ll need to press it. To do this, wrap the block of tofu in a few layers of kitchen paper, then weigh it down with a heavy pan or tray for 10-15 mins – the longer you press it, the firmer it will be. Cut the tofu into thick slices. Heat a griddle pan over high heat and lightly brush the tofu and spring onions with the oil. Griddle the tofu and spring onion until deep char lines appear on both sides (around 4 mins each side) – you may have to do this in batches depending on the size of your griddle pan. Divide the cooked rice between two plates, top with the tofu and spring onion, then drizzle with the teriyaki sauce. Garnish with the sesame seeds and sliced red chilli, if using.','5 mins','25 mins'),
(872,'baked tomato, mozzarella & basil risotto','https://www.bbcgoodfood.com/recipes/baked-tomato-mozzarella-basil-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Baked-tomato-mozzarella-and-basil-risotto-368464f.jpg','Imagine all the best bits of arancini – a golden, crunchy exterior; cheesy, oozing rice – in risotto form and you have this moreish baked tomato, mozzarella and basil risotto. It''s deliciously comforting',7,4,1,1,19,'Heat the oven to 200C/180C fan/gas 6. Heat half the oil in an ovenproof pan or casserole dish, then stir in the onion and cook over a low heat for 8-10 mins until golden. Scatter over the rice and mix well until coated. Pour over the wine, if using, and cook for 2 mins until absorbed. Pour over the hot stock, then give the rice a quick stir. Put the dish in the oven and bake the rice for 10 mins. Remove from the oven and stir through the tomatoes, basil, half of the parmesan and some seasoning. Flatten everything out roughly with the back of a spoon, then nestle the chunks of mozzarella evenly into the risotto. Sprinkle over the breadcrumbs and the remaining parmesan, and drizzle with the rest of the olive oil. Put the dish back in the oven, and bake everything for another 10-15 mins until the rice is just cooked through, heating it under the grill if you want a deep, golden crust. Leave to stand for a few minutes, scatter with a few extra basil leaves and serve scooped straight from the dish.','10 mins','30 mins'),
(873,'alfredo sauce','https://www.bbcgoodfood.com/recipes/alfredo-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Alfredo-dc662e3.jpg','Enjoy this classic Italian sauce folded into cooked pasta. It''s been adapted over the years to include double cream, making it rich, unctuous and delicious',2,4,1,1,19,'Melt the butter in a pan over a medium-low heat and fry the garlic for 1 min until softened but not browned. Stir in the cream and mixed herbs, and bring the cream to a gentle simmer, lowering the temperature if necessary. Add the parmesan, stirring continuously until it has melted. Sprinkle over the parsley (you can reserve a little to garnish, if you like). Fold into the cooked pasta and serve.','5 mins','5 mins'),
(874,'vegetarian fajitas','https://www.bbcgoodfood.com/recipes/veggie-fajitas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-fajitas-d49e108.jpg','For a quick, easy veggie family meal, try these black bean, avocado and pepper fajitas. If you don''t have the spices, swap them out for fajita seasoning',19,4,1,1,19,'To make the fajita mix, take two or three strips from each colour of pepper and finely chop them. Set aside. Heat the oil in a frying pan and fry the remaining pepper strips and the onion until soft and starting to brown at the edges. Cool slightly and mix in the chopped raw peppers. Add the garlic and cook for 1 min, then add the spices and stir. Cook for a couple of mins more until the spices become aromatic, then add half the lime juice and season. Transfer to a dish, leaving any juices behind, and keep warm. Tip the black beans into the same pan, then add the remaining lime juice and plenty of seasoning. Stir the beans around the pan to warm them through and help them absorb any flavours of the fajita mix, then stir through the coriander. Warm the tortillas in a microwave or in a low oven, then wrap them so they don’t dry out. Serve the tortillas with the fajita mix, beans, avocado and soured cream for everyone to help themselves.','5 mins','10 mins'),
(875,'easy vegetarian puff pastry tart','https://www.bbcgoodfood.com/recipes/mediterranean-slices','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-202455_11-f77374d.jpg','Keep sliced roasted peppers, puff pastry and artichokes in the freezer to make this smart starter in a flash – just add vegetarian pesto',5,4,1,1,19,'Heat oven to 200C/fan 180C/gas 6. Unroll the pastry and cut into 4 rectangles. Take a sharp knife and score a 1cm edge inside each rectangle, taking care that you don’t cut all the way through the pastry. Place on a baking sheet. Spread 1 tbsp pesto onto each slice, staying inside the border, then pile up the peppers and artichokes. Cook in the oven for 15 mins until the pastry is starting to brown. Tear the mozzarella ball into small pieces, then scatter it (or use cheddar, if you prefer) over the veg. Return to the oven for 5-7 mins until the pastry is crisp and the cheese has melted. Serve with a green salad.','10 mins','20 mins'),
(876,'chickpea curry','https://www.bbcgoodfood.com/recipes/kadala-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kadala-curry-fad4df3.jpg','This easy chickpea curry, created by reader Jon Gregg, is a vegan recipe that''s three of your five-a-day. It works well as a main course or a simple side dish',5,4,1,1,19,'To make the paste, heat a little of the 2 tbsp oil in a frying pan, add 1 diced onion and 1 tsp fresh or dried chilli, and cook until softened, about 8 mins. In a food processor, combine 9 garlic cloves, a thumb-sized piece of peeled ginger and the remaining oil, then add 1 tbsp ground coriander, 2 tbsp ground cumin, 1 tbsp garam masala, 2 tbsp tomato purée, ½ tsp salt and the fried onion. Blend to a smooth paste – add a drop of water or more oil, if needed. Cook the paste in a medium saucepan for 2 mins over a medium-high heat, stirring occasionally so it doesn’t stick. Tip in two 400g cans drained chickpeas and a 400g can chopped tomatoes, and simmer for 5 mins until reduced down. Add 100g creamed coconut with a little water, cook for 5 mins more, then add ½ small pack chopped coriander and 100g spinach, and cook until wilted. Garnish with extra coriander and serve with rice or dhal (or both).','15 mins','25 mins'),
(877,'creamy pesto & kale pasta','https://www.bbcgoodfood.com/recipes/creamy-pesto-kale-pasta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-pesto-kale-pasta-d582e18.jpg','Enjoy our creamy pesto and kale pasta as a tasty and nutritious supper. Make it with just a handful of ingredients and storecupboard staples',14,4,1,1,19,'Heat the oil in a large pan over a medium heat. Fry the onions for 10 mins until softened and beginning to caramelise. Add the kale and 100ml water, then cover and cook for 5 mins more, or until the kale has wilted. Cook the pasta following pack instructions. Drain, reserving a little of the cooking water. Toss the pasta with the onion mixture, soft cheese and pesto, adding a splash of the reserved cooking water to loosen, if needed. Season.','10 mins','25 mins'),
(878,'tomato & mascarpone risotto','https://www.bbcgoodfood.com/recipes/tomato-mascarpone-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-mascarpone-risotto-b090efa.jpg','This comforting risotto is perfect for a weekend wind-down for special mid-week meal. Switch mascarpone with cream cheese for a cheaper alternative',14,2,1,1,19,'Heat the oil in a large, heavy-based saucepan. Add the onion along with a pinch of salt, and fry for 10 mins or until beginning to soften and turn translucent, then add the garlic and fry for 1 min. Stir in the rice and cook for 2 mins. Tip in the tomatoes and bring to a simmer. Add half the stock, cooking and stirring until absorbed. Add the remaining stock, a ladleful at a time, and cook until the rice is al dente, stirring constantly for around 20 mins. Stir through the parmesan, mascarpone or cream cheese, and basil, then season to taste. Spoon into bowls to serve.','10 mins','30 mins'),
(879,'gnocchi cacio e pepe','https://www.bbcgoodfood.com/recipes/gnocchi-cacio-e-pepe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gnocchi-cacio-e-pepe-a1392fd.jpg','Enjoy a comforting bowl of gnocchi with parmesan and black pepper. It''s simple but utterly delicious – and the best part is, it only takes seven minutes to make',14,2,1,1,19,'Cook the gnocchi in a large pan of lightly salted, boiling water. Drain and reserve 200ml of the cooking water. Heat the butter in a large frying pan. Add the gnocchi, cheese and pepper as well as 150ml of the cooking water, raise the heat a little and stir vigorously until melted and the gnocchi is well coated. Pour in more of the reserved water if you like it saucier. Season with a little salt. Transfer the gnocchi to bowls and serve with a mixed salad, if you like.','2 mins','5 mins'),
(880,'courgette & lemon risotto','https://www.bbcgoodfood.com/recipes/courgette-lemon-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/courgette-lemon-risotto-fe5a759.jpg','An easy vegetarian one-pot risotto - simply stir in your seasonal veg, simmer and enjoy',1,2,1,1,19,'Melt the butter in a deep frying pan. Add the onion and fry gently until softened for about 8 mins, then add the garlic and stir for 1 min. Stir in the rice to coat it in the buttery onions and garlic for 1-2 mins. Dissolve the stock cube in 1 litre of boiling water, then add a ladle of the stock to the rice, along with the lemon juice and thyme. Bubble over a medium heat, stirring constantly. When almost all the liquid has been absorbed, add another ladle of stock and keep stirring. Tip in the courgette and keep adding the stock, stirring every now and then until the rice is just tender and creamy. To serve, stir in some seasoning, the lemon zest, vegetarian Italian-style hard cheese and crème fraîche.','10 mins','40 mins'),
(881,'indian butternut squash curry','https://www.bbcgoodfood.com/recipes/indian-butternut-squash-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488679_11-5bfcf60.jpg','Up your veg count and get three of your five-a-day with this fragrant, low fat vegetarian curry',5,4,1,1,19,'Cook the rice in boiling salted water, as per pack instructions. Meanwhile, heat the oil in a large frying pan and cook the butternut squash for 2-3 mins until lightly browned. Add the onion and the curry paste and fry for 3-4 mins more. Pour over the stock, then cover and simmer for 15-20 mins, or until the squash is tender. Add the tomatoes and chickpeas, then gently cook for 3-4 mins, until the tomatoes slightly soften. Take off the heat and stir through the yogurt and coriander. Serve with the rice and some wholemeal chapattis if you like.','10 mins','40 mins'),
(882,'easy vegetarian stuffing','https://www.bbcgoodfood.com/recipes/vegetarian-stuffing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-441563_10-a1223ed.jpg','Our easy, vegetarian stuffing is full of rich flavours and is the ideal side dish for a Sunday roast or Christmas dinner.',33,12,1,1,19,'Heat oven to 200C/fan 180C/gas 6. Oil a non-stick 12-hole bun tin and line holes with two strips baking parchment, to form a cross. Put the mushrooms in a bowl and pour over hot vegetable stock. Leave for 20 mins, then strain, reserve the stock and chop the mushrooms, if large. Heat oil in a pan, add onion and fry for 5 mins until softened. Add the pine nuts and fry until lightly toasted. Add the garlic, rice and cook for 2 mins. Tip in wine, let it bubble, then add the reserved stock, mushrooms and bring to the boil. Reduce heat, cover and simmer for 10 mins, until the rice is just cooked. Remove from heat and cool, then stir in the egg, basil, pepper, and salt, if using. Fill the tins with stuffing, sprinkle over parmesan. Bake for 20-25 mins, until golden on top. Cool in the tins, or for 5 mins if serving straight away. Remove by pulling up the parchment crosses. Make up to two days ahead. Reheat on an oiled baking sheet, at 200C/fan 180C/gas 6 for 10 mins.','15 mins','45 mins'),
(883,'pea & leek open lasagne','https://www.bbcgoodfood.com/recipes/pea-leek-open-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pea-leek-open-lasagne-d5cc5b7.jpg','Make this healthy pea and leek lasagne in just 25 minutes. It delivers three of your 5-a-day and costs less than £2 a serving – perfect for midweek suppers ',16,2,1,1,19,'Put a frying pan over a medium heat. Pour in the oil and add the leeks, garlic and a pinch of salt. Cook, stirring occasionally, until collapsed and soft. Meanwhile, bring a pan of water to the boil. Tip the peas and kale into the pan with the leeks and add a splash of water. Cook until the kale has wilted and peas are defrosted. Turn down the heat to low. Stir in the mustard, crème fraîche and ¾ of the lemon zest. Add enough water to make a sauce. Give everything a good mix and season to taste. Drop the lasagne into the water and cook following pack instructions, then drain well. Put one lasagne sheet on each plate, top with half the leek and pea mix then layer up the second lasagne sheet and the remaining greens. Scatter over the remaining lemon zest and add a good grinding of black pepper.','10 mins','15 mins'),
(884,'fettuccine alfredo','https://www.bbcgoodfood.com/recipes/fettucine-alfredo','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fettucine-alfredo-96d7406.jpg','Hankering for a comforting bowl of pasta in creamy sauce? This sumptuous Italian dish is for you. It takes just 25 minutes so it''s an easy midweek meal',77,2,1,1,19,'In a medium saucepan, stir the clotted cream, butter and cornflour over a low-ish heat and bring to a low simmer. Turn off the heat and keep warm. Meanwhile, put the cheese and nutmeg in a small bowl and add a good grinding of black pepper, then stir everything together (don’t add any salt at this stage). Put the pasta in another pan with 2 tsp salt, pour over some boiling water and cook following pack instructions (usually 3-4 mins). When cooked, scoop some of the cooking water into a heatproof jug or mug and drain the pasta, but not too thoroughly. Add the pasta to the pan with the clotted cream mixture, then sprinkle over the cheese and gently fold everything together over a low heat using a rubber spatula. When combined, splash in 3 tbsp of the cooking water. At first, the pasta will look wet and sloppy: keep stirring until the water is absorbed and the sauce is glossy. Check the seasoning before transferring to heated bowls. Sprinkle over some chives or parsley, then serve immediately.','15 mins','10 mins'),
(885,'air fryer pasta chips','https://www.bbcgoodfood.com/recipes/air-fryer-pasta-chips','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Air-fryer-pasta-chips-8283523.jpg','Try these crispy air fryer pasta chips for a delicious budget snack.',16,4,1,1,19,'Cook the pasta following the packet instructions. Drain and transfer to a large bowl.  Add the olive oil, spices, herbs and season with salt and pepper, and mix together so that the pasta is well coated.  Set your air fryer to 180C and cook the pasta for 15-20 minutes, shaking every 5 minutes, until the pasta is crisp and golden. Serve the pasta chips on their own or with a dip.','5 mins','25 mins - 30 mins'),
(886,'broccoli cheese with wholemeal pasta & brown breadcrumbs','https://www.bbcgoodfood.com/recipes/broccoli-cheese-with-wholemeal-pasta-brown-breadcrumbs','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Broccoli-cheese-with-wholemeal-pasta-and-brown-breadcrumbs-59f49af.jpg','Love mac ''n'' cheese? Try this hearty veggie pasta dish with cheese and breadcrumbs. Adding broccoli and using wholemeal pasta ramps up the flavour and nutrition',54,4,1,1,19,'Bring a large pan of salted water to the boil and cook the pasta until al dente – this will take a couple of minutes less than pack instructions. Halfway through the pasta cooking time, add the broccoli to the pan. Drain, then toss the pasta and broccoli in the oil to coat. While the pasta and broccoli are cooking, bring the milk to a simmer with the bay leaf in a separate pan, then remove from the heat and set aside. Melt the butter in a saucepan over a medium heat, add the flour and whisk together. Reduce the heat to medium-low and cook for a couple of minutes, stirring often until combined and beginning to stick to the pan. Whisk in the warm infused milk gradually, making sure there are no lumps, then reduce the heat to low and cook gently for 8-10 mins until the sauce is thickened and smooth. Remove from the heat and stir in three-quarters of the cheese, the mustard (you can add more, if you like), nutmeg, plenty of black pepper and some salt. Remove the bay leaf. Heat the oven to 180C/160C fan/gas 4. Tip the cooked pasta and broccoli into a large baking dish (ours measured 30 x 22cm) and pour over the sauce. Sprinkle over the remaining cheese and the breadcrumbs. Bake for 30-35 mins until golden brown and bubbling. Remove from the oven and leave to rest for 5 mins before serving.','10 mins','1 hr'),
(887,'mushroom carbonara','https://www.bbcgoodfood.com/recipes/mushroom-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Crispy-Mushroom-Carbonara-1bbdb44.jpg','Rustle up this healthy veggie carbonara with chestnut and baby button mushrooms in 30 minutes. It uses a one-pan method of cooking to save on washing-up',15,1,1,1,19,'Cook the mushrooms in a large non-stick pan over a medium-high heat, shuffling the pan occasionally until deeply browned. Transfer to a plate. Drizzle the oil into the pan and cook the garlic and shallots for a few minutes until softened. Put the egg, parmesan and parsley in a bowl and whisk with a fork to combine. Put the pasta and 400ml boiling water into the pan with the shallots and simmer for 8-10 mins until reduced and the pasta is cooked. Remove from the heat and stir in the egg mix and mushrooms. Leave to stand for 1 min before stirring again, then season to taste. Add a little more water if the pasta looks too dry. Sprinkle over extra parmesan and grind over some black pepper, if you like.','10 mins','20 mins'),
(888,'tomato penne with avocado','https://www.bbcgoodfood.com/recipes/mexican-penne-avocado','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mexican-penne-5cd4efb.jpg','Get all five of your 5-a-day in this mildly spiced, healthy pasta dish. It''s rich in iron, fibre and vitamin C as well as being low-fat and low-calorie.',4,2,1,1,19,'Cook the pasta in salted water for 10-12 mins until al dente. Meanwhile, heat the oil in a medium pan. Add the sliced onion and pepper and fry, stirring frequently for 10 mins until golden. Stir in the garlic and spices, then tip in the tomatoes, half a can of water, the corn and bouillon. Cover and simmer for 15 mins. Meanwhile, toss the avocado with the lime juice and zest, and the finely chopped onion. Drain the penne and toss into the sauce with the coriander. Spoon the pasta into bowls, top with the avocado and scatter over the coriander leaves.','10 mins','20 mins'),
(889,'pasta arrabbiata with aubergine','https://www.bbcgoodfood.com/recipes/penne-arrabbiata-aubergine','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/penne-arrabiata-440-400-0b82cdd.jpg','Choose this healthy pasta supper after a workout. Although it''s low in fat and calories, it''s packed with flavour, and also provides calcium and fibre',4,2,1,1,19,'Heat the oil in a large non-stick pan, add the onions, cover and cook for 5 mins. Remove the lid and cook for 5 mins more, stirring frequently until softened. Add the garlic, chilli flakes and paprika, stir briefly, then tip in the tomatoes and a can of water. Stir in the bouillon and aubergine, then bring to a simmer, cover and cook for 20 mins. Cook the penne in a pan of boiling water for 12 mins until al dente. Drain, reserving 60ml of the cooking water. Add the cooked penne to the sauce, and toss well with the basil and a little of the reserved water, if needed. Spoon into two shallow bowls, and serve topped with the cheese and some extra basil, if you like.','8 mins','35 mins'),
(890,'vegetarian lasagne','https://www.bbcgoodfood.com/recipes/easy-vegetable-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/vegetarian-lasagne-5ee6162.jpg','Make our easy vegetarian lasagne using just a handful of ingredients. You can use ready-made tomato sauce and white sauce, or batch cook the sauces and freeze them',8,6,1,1,19,'To make the tomato sauce, heat the olive oil in a saucepan. Add the onions, garlic and carrot. Cook for 5-7 mins over a medium heat until softened. Turn up the heat a little and stir in the tomato purée. Cook for 1 min, pour in the white wine, then cook for 5 mins until this has reduced by two-thirds. Pour over the chopped tomatoes and add the basil leaves. Bring to the boil then simmer for 20 mins. Leave to cool then whizz in a food processor. Will keep, cooled, in the fridge for up to three days or frozen for three months. To make the white sauce, melt the butter in a saucepan, stir in the plain flour, then cook for 2 mins. Slowly whisk in the milk, then bring to the boil, stirring. Turn down the heat, then cook until the sauce starts to thicken and coats the back of a wooden spoon. Will keep, cooled, in the fridge for up to three days or frozen for three months. Heat the oven to 200C/180C fan/gas 6. Lightly oil two large baking trays and add the peppers and aubergines. Toss with the olive oil, season well, then roast for 25 mins until lightly browned. Reduce the oven to 180C/160C fan/gas 4. Lightly oil a 30 x 20cm ovenproof dish. Arrange a layer of the vegetables on the bottom, then pour over a third of the tomato sauce. Top with a layer of lasagne sheets, then drizzle over a quarter of the white sauce. Repeat until you have three layers of pasta. Spoon the remaining white sauce over the pasta, making sure the whole surface is covered, then scatter over the mozzarella and cherry tomatoes. Bake for 45 mins until bubbling and golden.','25 mins','1 hr and 10 mins'),
(891,'cheesy sprout pasta bake','https://www.bbcgoodfood.com/recipes/cheesy-sprout-pasta-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheesy-sprout-pasta-bake-e17a106.jpg','Make this cheesy sprout pasta bake for a filling family supper. Our easy veggie recipe makes for a tasty twist on a standard macaroni cheese',29,4,1,1,19,'Heat oven to 200C/180C fan/gas 6. Toss the brussels sprouts in the oil and vinegar, then tip into a roasting tin. Bake for 10-15 mins, tossing again halfway through. Meanwhile, cook the pasta until it has a slight bite (around two minutes less than pack instructions), then drain and set aside. Heat the butter in a pan. Add the garlic and flour and cook for 1-2 mins. Gradually add the milk until the mixture is smooth, then simmer for 5 mins until thick. Remove from the heat and add the mustard and most of the cheese, reserving one handful for the topping. Season well and stir. Add the pasta, then tip the mixture into a large baking dish. Top with the roasted sprouts and reserved cheese and bake for 20 mins until the cheese topping is golden and bubbling. Serve with a green salad, if you like.','5 mins','45 mins'),
(892,'lentil bolognese','https://www.bbcgoodfood.com/recipes/lentil-ragu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-563473_12-55e3d75.jpg','Struggle to get your five-a-day? This superhealthy lentil ragu will get you four steps closer and can be frozen for extra convenience',17,6,1,1,19,'Heat the oil in a large saucepan and add the onions, carrots, celery and garlic. Cook gently for 15-20 mins until everything is softened. Stir in the lentils, chopped tomatoes, tomato purée, herbs and stock. Bring to a simmer, then cook for 40-50 mins until the lentils are tender and saucy – splash in water if you need. Season. If eating straight away, keep on a low heat while you cook the spaghetti, following pack instructions. Drain well, divide between pasta bowls or plates, spoon sauce over the top and grate over some cheese. Alternatively, cool the sauce and chill for up to 3 days. Or freeze for up to 3 months. Simply defrost portions overnight at room temperature, then reheat gently to serve.','15 mins','1 hr and 15 mins'),
(893,'spinach & courgette lasagne','https://www.bbcgoodfood.com/recipes/spinach-courgette-lasagne','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spinach-courgette-lasagne-dd15c74.jpg','Enjoy a vegetarian lasagne bursting with spinach and courgette, held together with a creamy mascarpone sauce. It''s a filling midweek meal for all the family',41,6,1,1,19,'Pour boiling water over the spinach in a sieve or colander to wilt it. Leave until cool enough to handle, then squeeze out any excess liquid. Heat the oil in a non-stick frying pan over a medium heat, add the garlic and soften for 1 min. Tip in the nutmeg and cook for 1 min more, then add the mascarpone, spinach, half the cream and half the parmesan. Season generously, stir well and set aside. Heat oven to 180C/160C fan/gas 4. Spread a third of the filling over the base of a 20 x 30cm baking dish, cover with 3 lasagne sheets, then add a layer of courgettes. Repeat twice more. Pour the remaining cream over the final layer and sprinkle over the remaining parmesan. Bake for 40-45 mins or until the sauce is bubbling and the pasta has no resistance when you push a skewer through. Rest for 5 mins, then serve.','25 mins','50 mins'),
(894,'slow cooker ratatouille','https://www.bbcgoodfood.com/recipes/slow-cooker-ratatouille','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-ratatouille-4ecd407.jpg','Make up a batch of this slow-cooked ratatouille and freeze for easy midweek meals when you’re busy. Packed with nutrients, it also delivers four of your 5-a-day',46,6,1,1,19,'Heat the oil in a large frying pan and fry the onion for 8 mins until translucent. Add the garlic and fry for 1 min. Turn the heat to medium-high, add the aubergines and fry for 5 mins until golden. Stir in the courgettes and peppers and fry for 5 mins more until slightly soft. Add the tomato purée, fresh tomatoes, herbs, canned tomatoes, vinegar, sugar and 1 tsp salt and bring to the boil. Transfer to the slow cooker and cook on low for 5-6 hours or until everything is soft and the sauce has thickened. Season, scatter over some extra basil, and serve with sourdough, if you like.','10 mins','6 hrs and 20 mins'),
(895,'slow cooker vegetable stew with cheddar dumplings','https://www.bbcgoodfood.com/recipes/spring-veg-stew-cheddar-dumplings','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spring-veg-stew-with-cheddar-dumplings-2bb2085.jpg','Give classic stew a veggie twist with creamy sauce and cheesy dumplings. Make it in the slow cooker and be rewarded with a hearty meal at the end of the day',12,6,1,1,19,'Set the slow cooker to low. Heat 1 tbsp of the oil in a frying pan and fry the carrots for 5 mins until just golden, then tip into the slow cooker.  Heat the remaining oil in the pan and fry the leeks with a pinch of salt for 5 mins until soft. Add the garlic and stir in the flour. Gradually add the stock, stirring, until the flour has dissolved and there are no lumps. Bring to the boil, then tip into the slow cooker. Add the courgettes, beans and herbs, topping up with water to cover the veg, if needed. Cover and cook for 4 hrs. To make the dumplings, tip the flour into a bowl and stir in the suet or butter until evenly distributed. Add the cheese, parsley, ½ tsp cracked black pepper and a pinch of salt. Mix in 3-4 tbsp cold water with your hands to make a soft, slightly sticky dough (add a little more water if needed). Divide into six and roll into balls. Add the crème fraîche, mustard, broad beans or peas and spinach to the slow cooker and turn it to high. Arrange the dumplings over the stew, cover and cook for 1-2 hrs more until firm and doubled in size. Scatter with parsley and serve. Will keep for up to three days in the fridge or in the freezer for up to three months.','20 mins','6 hrs'),
(928,'cheese-stuffed garlic dough balls with a tomato sauce dip','https://www.bbcgoodfood.com/recipes/cheesy-stuffed-garlic-dough-balls-tomato-sauce-dip','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/dough-balls-8ec4bea.jpg','Impress family and friends with the ultimate sharing starter: cheesy, moreish garlic dough balls. With a tomato-based dipping sauce, they make an ideal addition to any Italian-inspired menu',46,20,2,2,20,'Heat 175ml water in a saucepan until steaming, then add the butter. Remove from the heat and leave to cool until the mixture is just warm (it should not be hot). Combine the flour, yeast, sugar and 1 tsp salt in a large bowl or stand mixer. Add the cooled butter mixture, and mix to a soft dough using a wooden spoon or the mixer. Knead for 10 mins by hand (or 5 mins using a mixer) until the dough feels bouncy and smooth. Transfer to an oiled bowl and cover with a clean tea towel. Leave somewhere warm to rise for 1½-2 hrs, or until doubled in size. Alternatively, leave to prove in the fridge overnight. Oil and line a baking sheet with baking parchment. Knock the air out of the dough, then knead again for several minutes. Flatten a small piece of dough (about 20g) into a disc, and put a cube of the mozzarella and a pinch of the gruyère into the middle of the disc. Enclose the cheeses with the dough, then roll into a ball. Transfer to the prepared baking sheet. Repeat with the remaining cheese and dough, placing the dough balls ½cm apart on the baking sheet – they should be just touching after proving. Cover with a clean tea towel and leave somewhere warm to rise for 30 mins. Meanwhile, make the garlic butter. Melt the butter in a small pan over a low heat, then stir in the garlic and rosemary. Remove from the heat and set aside until needed. Heat the oven to 180C/160C fan/gas 4. Brush the risen dough balls with the garlic butter, then bake for 25-30 mins until the dough balls are cooked through and the middles are oozing. While the dough balls are baking, make the tomato sauce dip. Heat the oil in a saucepan and fry the garlic for 30 seconds. Tip in the passata, vinegar, sugar and chilli flakes, and simmer for 10 mins until thickened. Season to taste and stir in the basil. Brush the warm dough balls with any remaining garlic butter, then serve with the tomato sauce dip on the side for dunking.','40 mins','35 mins'),
(896,'slow cooker aubergines','https://www.bbcgoodfood.com/recipes/slow-cooker-aubergines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/aubergine-salad-5fec46c.jpg','An easy vegetarian slow cooker recipe, packed with summer flavours - ideal if you''re busy, simply put it on in the morning and it''ll be ready come supper time',29,6,1,1,19,'Pour half of the olive oil into the slow cooker and put the onions and crushed garlic on top. Slice the aubergines into 1cm thick slices lengthways and brush with the remaining olive oil. Put them into the slow cooker on top of the onions and nestle the tomatoes, fennel slices and sundried tomatoes in and around the aubergines. Sprinkle the coriander seeds over the top then season everything well with salt and pepper. Cook on low for 6-8 hours or until the aubergines are soft. Once cooked put all the dressing ingredients into the bowl of a mini food processor and blitz until smooth. Transfer the cooked vegetables to a serving platter using a slotted spoon and drizzle over the herby dressing. Top with crumbled feta cheese, flaked almonds and serve with some crusty bread on the side.','15 mins','8 hrs'),
(897,'slow cooker mulled cider','https://www.bbcgoodfood.com/recipes/slow-cooker-mulled-cider','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Slow-cooker-mulled-cider-baba8d6.jpg','Planning a Christmas party? Let guests help themselves to this festive mulled cider – simply plug the slow cooker in and ladle into heatproof glasses',12,10,1,1,19,'Put all the ingredients except the calvados in a slow cooker and cook for 1 hr on high or up to 4 hrs on low. Just before serving, stir in the calvados. Ladle into heatproof glasses or cups to serve.','5 mins','1 hr'),
(898,'slow cooker spiced root & lentil casserole','https://www.bbcgoodfood.com/recipes/slow-cooker-spiced-root-lentil-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-3c6fa95.jpg','Warm up on chilly days with this vegetarian slow-cooker stew. It''s great for freezing and reheating for midweek meals',12,4,1,1,19,'Heat the slow cooker to low. Heat the oil in a frying pan and cook the onion for 10 mins until soft and transparent. Add the carrots and parsnips and fry for 8-10 mins more until the veg is just golden. Stir in the garlic and spices, and fry for 4-5 mins until fragrant, stirring. Add a splash of water if needed. Tip into the slow cooker and stir through the lentils, stock, bay and seasoning. Close the lid and cook on low for 5-6 hrs until the vegetables are tender and the sauce has thickened. Check the seasoning and stir in the lemon juice to taste. Serve with bread, rice, or potatoes, if you like.','20 mins','5 hrs and 30 mins'),
(899,'slow cooker vegetable curry','https://www.bbcgoodfood.com/recipes/slow-cooker-vegetable-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooker-vegetable-curry-2b72ddd.jpg','Use the slow cooker to make our easy veggie curry with coconut sauce and freeze the leftovers for another day. One delicious serving provides four of your five-a-day',4,2,1,1,19,'Put the coconut milk, curry paste, bouillon powder, chilli, ginger, garlic, butternut squash, pepper and aubergine into the slow cooker pot and stir well. Cover with the lid and chill overnight. Cook on low for 6 hrs until the vegetables are really tender, then stir in the coriander and defrosted peas. The heat of the curry should be enough to warm them through. Taste and add a good squeeze of lime juice, if you fancy extra zing. Serve with a wholemeal flatbread.','10 mins','6 hrs'),
(900,'slow cooker bio yogurt','https://www.bbcgoodfood.com/recipes/slow-cooker-bio-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/liveyogurt-7a3acd3.jpg','You need just two ingredients – milk and live yogurt – to make this bio yogurt, full of gut-friendly probiotics. Mix with fruit, or cereal for breakfast',5,2,1,1,19,'Tip the milk into the slow cooker. Cover and heat on High until the temperature of the milk reaches 82C, this will take a couple of hours. Turn off the slow cooker and allow the temperature to drop to 43C for a further 2-3 hours. Take a mugful of the warm milk and mix it with the yogurt then pour the mixture back into the slow cooker and stir really well. Cover, wrap the slow cooker in a big towel and then leave undisturbed for 9-12 hours until the mixture has set. Eat on top of cereal or porridge, topped with fresh fruit, in marinades or drink in smoothies. If you want it thicker, for dips for example, line a large sieve with muslin and place it over a bowl, tip in the yogurt and allow some of the whey to strain off until you get the consistency of yogurt that you like. The longer you leave it, the thicker it will become. Store in the fridge for up to 2 weeks.','17 hrs and 30 mins','N/A'),
(901,'slow cooker breakfast beans','https://www.bbcgoodfood.com/recipes/slow-cooker-breakfast-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smokybreakfastbeans-f4b5371.jpg','Enjoy this slow-cooked alternative to canned baked beans for breakfast or brunch, served on toast or with eggs. They''re a good source of protein',5,4,1,1,19,'Heat the slow cooker if necessary. Heat the oil in a large frying pan and fry the onion until it starts to brown, then add the garlic and cook for 1 min. Add the vinegar and sugar and bubble for a minute. Stir in the beans and passata and season with black pepper. Tip everything into the slow cooker. Cook on Low for 5 hours. If the sauce seems a little thin turn the heat to High and cook for a few more minutes. Stir through the coriander.','30 mins','5 hrs'),
(903,'slow cooker sticky toffee pudding','https://www.bbcgoodfood.com/recipes/slow-cooker-sticky-toffee-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Sticky-Toffee-Pudding-ef07c63.jpg','Leave our slow cooker sticky pudding to cook all day so it’s ready to serve in the evening with ice cream. Rich and indulgent, it''s the ultimate comforting dessert',14,8,1,1,19,'Put the dates in a heatproof bowl, cover with 150ml boiling water, and leave to soak for 30 mins. Butter a 1-litre pudding basin and line the base with baking parchment. Tip half the butter, half the treacle, the vanilla, 75g of the sugar and the cream into a pan set over a medium heat. Cook for 4-5 mins, stirring, until the sugar dissolves. Turn up the heat, bubble for 3 mins, then whisk in a pinch of salt. Pour a third of the sauce into the basin. Beat the remaining butter, treacle, sugar and the eggs together, then fold in the flour, bicarb, ¼ tsp salt, the dates and their soaking liquid. Spoon into the basin and smooth the surface, leaving a 1cm gap from the top. Cover with a double layer of baking parchment and foil, making a pleat in the middle so the pud can expand. Secure with kitchen string. Set the slow cooker to low. Sit the basin inside, then add boiling water so it comes halfway up the basin. Cover and cook for 7-8 hrs. Run a knife around the edge of the pudding and turn out onto a plate. Reheat the remaining sauce and pour over. Serve with ice cream.','30 mins','8 hrs'),
(904,'slow cooker mac ’n’ cheese','https://www.bbcgoodfood.com/recipes/slow-cooker-mac-n-cheese','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mac-n-cheese-caa1c92.jpg','There’s no need to make a white sauce with this easy recipe. Just tip the ingredients into your slow cooker for a velvety smooth veggie pasta dish',12,4,1,1,19,'Pour boiling water over the pasta and drain, then put everything in a slow cooker and stir well. Season, cover and cook on low for 1 hr. Stir again, put the lid back on and cook for another 30 mins until the pasta is cooked and the sauce has reduced enough to coat the macaroni. Leave the lid off and reduce for the last 10 mins if you need to, or add a splash more milk – this will depend on the size of your slow cooker and the brand. Serve with extra cheese.','15 mins','1 hr and 30 mins'),
(905,'slow-cooked summer blush','https://www.bbcgoodfood.com/recipes/slow-cooked-summer-blush','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3384_11-d65ffbb.jpg','Superhealthy - counts as 2 of 5-a-day',5,20,1,1,19,'Heat oven to 160C/fan 140C/gas 3. In a large, deep roasting pan, gently toss everything, except the basil, together, then season. Place the vegetables in the oven, then roast for 2 hrs, undisturbed. Remove from the oven and leave to cool. If you want, drain the vegetables before serving and serve their juice on the side, or simply stir through the basil and correct the seasoning. The vegetables can be roasted up to 2 days ahead. Cover and leave in the fridge, but bring out at least 4 hours before serving so the vegetables are served at room temperature.','30 mins','2 hrs'),
(906,'slow cooker turkish breakfast eggs','https://www.bbcgoodfood.com/recipes/slow-cooker-baked-turkish-breakfast-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/turkishbreakfasteggs-10a1988.jpg','Spice up your baked eggs and get two of your five-a-day with this Turkish-style breakfast or brunch dish. It''s low-calorie, too, and high in vitamin C',5,4,1,1,19,'Oil the inside of a small slow cooker and heat if necessary. Heat the remaining oil in a heavy-based frying pan. Stir in the onions, pepper and chilli. Cook until they begin to soften. Tip into the slow cooker and add the cherry tomatoes and bread and stir everything. Season. Whisk the eggs with the milk and parsley and pour this over the top, making sure all the other ingredients are covered. Cook for 5-6 hours. Serve with the yogurt.','15 mins','5 hrs - 6 hrs'),
(907,'spiced carrot & lentil soup','https://www.bbcgoodfood.com/recipes/spiced-carrot-lentil-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1074500_11-ee0d41a.jpg','A delicious, spicy blend packed full of iron and low in fat to boot. It''s ready in under half an hour, or can be made in a slow cooker',33,4,1,1,19,'Heat a large saucepan and dry-fry 2 tsp cumin seeds and a pinch of chilli flakes for 1 min, or until they start to jump around the pan and release their aromas. Scoop out about half with a spoon and set aside. Add 2 tbsp olive oil, 600g coarsely grated carrots, 140g split red lentils, 1l hot vegetable stock and 125ml milk to the pan and bring to the boil. Simmer for 15 mins until the lentils have swollen and softened. Whizz the soup with a stick blender or in a food processor until smooth (or leave it chunky if you prefer). Season to taste and finish with a dollop of plain yogurt and a sprinkling of the reserved toasted spices. Serve with warmed naan breads.','10 mins','15 mins'),
(935,'crusty garlic bread','https://www.bbcgoodfood.com/recipes/crusty-garlic-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crusty-garlic-bread-5ca729e.jpg','Get the kids in the kitchen to make this simple side dish - pop any extra garlic butter into the fridge or freezer for a busy weeknight',42,6,1,2,20,'Put the butter in a bowl, add the garlic and mix well. Spoon the butter out onto a sheet of cling film and roll up to make a sausage-shaped log. Chill for 10 mins. Heat oven to 200C/180C fan/gas 6. Slice the baguette into about 12 slices but not all the way through, leaving the base intact to hold it together. Remove the cling film from the butter and thinly slice on a chopping board. Press each butter slice between the slices of bread. Wrap the baguette in foil, place on a tray and bake for 5–6 mins, then peel back the foil. Cook for a further 4–5 mins to crisp up.','5 mins','15 mins'),
(908,'slow-cooked marrow with fennel & tomato','https://www.bbcgoodfood.com/recipes/slow-cooked-marrow-fennel-tomato','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/slow-cooked-marrow-with-fennel-tomato-f61297a.jpg','Try this vegetarian stew with crusty sourdough, or as an accompaniment to grilled meats. Marrow is the star here, but you''re getting four of your five-a-day',11,4,1,1,19,'Halve, deseed and chop the marrow into chunks. Toss in some salt and leave the chunks to drain in a colander for 30 mins to remove excess water. Heat the olive oil in a large pan over a medium heat. Add the rosemary and fennel seeds and cook for a few mins until the rosemary is sizzling. Add the red onion, dried chilli, fennel and a large pinch of salt, and cook for 10 mins until the onions are starting to colour and sweeten. Add the garlic and cook, stirring, for another min. Turn up the heat slightly, add the marrow and a good grind of pepper, and cook for 10 mins, stirring so it doesn’t catch. Deglaze the pan with the white wine. Cook until it’s evaporated, then add the tomatoes and red wine vinegar. Stir to combine everything, then turn down the heat and cook, stirring occasionally, on the lowest heat for at least 35 mins, until the tomatoes have concentrated and the marrow has taken on all the flavours in the pan. Stir in the capers and butter beans to warm them through. Toast the sourdough and cut each slice in half. Divide the mixture between four plates, then spread the toast with the goat’s cheese, drizzle with some olive oil, scatter over the oregano and rosemary, and serve.','30 mins','1 hr'),
(909,'5-a-day tagine','https://www.bbcgoodfood.com/recipes/5-day-tagine','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1069520_11-38d37ad.jpg','Get your day''s vegetable quota all in one go. Great for feeding a group, or freeze portions',5,4,1,1,19,'Heat oven to 200C/fan 180C/gas 6. Scatter the veg over a couple of baking trays, drizzle with half the oil, season, then rub the oil over the veg with your hands to coat. Roast for 30 mins until tender and beginning to brown. Meanwhile, fry the spices in the remaining oil for 1 min – they should sizzle and start to smell aromatic. Tip in the tomatoes, apricots, honey and a can of water. Simmer for 5 mins until the sauce is slightly reduced and the apricots plump, then stir in the veg and some seasoning. Serve with couscous or jacket potatoes.','10 mins','35 mins'),
(910,'slow cooker clotted cream fudge','https://www.bbcgoodfood.com/recipes/slow-cooker-clotted-cream-fudge','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Slow-cooker-clotted-cream-fudge-ee3e78f.jpg','Dig out your slow cooker to make some moreish clotted cream fudge. It''s easy to make, and makes a great gift for family and friends – just put in little bags and tie with ribbon',12,16,1,1,19,'Put the caster sugar, golden syrup, clotted cream, vanilla and a pinch of sea salt flakes in a slow cooker. Stir until well-mixed and smooth. Cook on high for 4-5 hrs, stirring every 30 mins, or until it’s a golden caramel colour and looks glossy. If your slow cooker works with the lid off, cook uncovered to speed up cooking. It will take about 5 hrs if using a multi-function cooker with a locked lid that requires you to keep opening and stirring it. Oil a 20cm baking tin and line with baking parchment. Remove the bowl of the slow cooker carefully and set on a heatproof mat or chopping board. Mix with a wooden spoon or spatula vigorously for 10-15 mins to cool the fudge and to break up any large sugar crystals. The fudge will thicken as it cools and start to look matte. Tip into the tin and use a spatula to smooth the surface. Sprinkle with a little more salt and chill until set, about 1 hr. Cut into chunks. Will keep for five days in an airtight container.','15 mins','5 hrs'),
(911,'easy bread rolls','https://www.bbcgoodfood.com/recipes/easy-bread-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/03/EasyBreadRolls-e4a7255.jpg','Bake these simple white bread rolls for sandwiches, burger buns or to dunk in soup. Using only a handful of ingredients, bread-making has never been so easy',7,8,1,2,20,'Tip the flour, yeast, sugar, salt and oil into a bowl. Pour over 325ml warm water, then mix (with a spatula or your hand), until it comes together as a shaggy dough. Make sure all the flour has been incorporated. Cover and leave for 10 mins. Lightly oil your work surface and tip the dough onto it. Knead the dough for at least 10 mins until it becomes tighter and springy – if you have a stand mixer you can do this with a dough hook for 5 mins. Pull the dough into a ball and put in a clean, oiled bowl. Leave for 1 hr, or until doubled in size. Tip the dough onto a lightly floured surface and roll into a long sausage shape. Halve the dough, then divide each half into four pieces, so you have eight equal-sized portions. Roll each into a tight ball and put on a dusted baking tray, leaving some room between each ball for rising. Cover with a damp tea towel and leave in a warm place to prove for 40 mins-1 hr or until almost doubled in size. Heat the oven to 230C/210C fan/gas 8. When the dough is ready, dust each ball with a bit more flour. (If you like, you can glaze the rolls with milk or beaten egg, and top with seeds.) Bake for 25-30mins, until light brown and hollow sounding when tapped on the base. Leave to cool on a wire rack.','30 mins','25 mins - 30 mins'),
(912,'pizza rolls','https://www.bbcgoodfood.com/recipes/pizza-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-46013_11-99b8eda.jpg','These easy-to-make snacks can be doubled to feed a crowd of kids',5,6,1,2,20,'Heat oven to 180C/160C fan/gas 4. Cut the tops off the rolls and scoop out the insides. Spread the rolls with tomato purée, then fill with ham, tomatoes and finally the mozzarella. Scatter with dried oregano and top each with an olive, if you like. Place the rolls on a baking tray and bake for 15 mins until the rolls are crusty brown and the cheese is bubbling. Leave to rest for a min, then serve hot with a side salad.','15 mins','15 mins'),
(914,'ploughman’s rolls','https://www.bbcgoodfood.com/recipes/ploughmans-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201816_10-7f0a38f.jpg','Bake your own bread buns and fill them with classic cheese ploughman''s ingredients for an inspired lunch',17,8,1,2,20,'Stir the celery seeds into the bread dough mix. Pour the milk into a jug, make up to 325ml with water, then warm to hand temperature (this is easy to do in the microwave). Add to the bread mix and bring together following pack instructions. Leave to rise in a warm place until about doubled in size. Meanwhile, mix together 85g of the cheddar, the Brie, apple and spring onions, and season a little. Divide the dough into 8 even pieces. Roll a piece into a roughly 10cm-wide circle (about the thickness of a naan), then spoon an eighth of the cheesy mixture into the middle. Gather up the edges of the dough around the filling and pinch together to seal firmly – you don’t want it to burst while baking. Turn the roll over so the pinched bit seals and squashes together underneath. Re-shape a little if necessary. Repeat with the remaining dough and filling, then sit on floured baking sheets, spaced a little apart. Cover with oiled cling film and set aside in a warm place for 30 mins more. Heat oven to 200C/180C fan/gas 6. Brush the rolls with a splash of milk, scatter with the poppy seeds plus a pinch more celery seeds and the remaining cheddar, then bake for 20-22 mins. Eat warm or cold, with pickle.','40 mins','22 mins'),
(915,'hedgehog rolls','https://www.bbcgoodfood.com/recipes/hedgehog-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hedgehog-bread-roll-78eef35.jpg','Introduce kids to the art of breadmaking with this fun and easy starter recipe. These mini hedgehog rolls are the cutest bake you''ll ever make',19,6,1,2,20,'Make the bread mixture with the butter following pack instructions. It’s easiest to use a stand mixer but not difficult to do by hand. Leave the dough to rest for 5 mins, then knead for 5 mins. Cut the dough into six pieces. Dust the surface with a little flour and shape each piece into a ball by rolling it between your hand. Now make it hedgehog-shaped by pulling one side out a little and squeezing it gently into a snout. Be quite firm or it will bounce back. Put the hedgehogs on a baking sheet, cover with a damp tea towel and leave to rise for 1 hr. Heat oven to 200C/180C fan/gas 6. Using kitchen scissors (supervise younger children), carefully snip into the dough to make the spikes on the backs of the hedgehogs. Press raisins in for the eyes and push a flaked almond into the end of each snout. Bake for 15 mins or until the rolls are risen and golden. Will keep for two days in an airtight container.','20 mins','15 mins'),
(916,'basic granary bread dough (for rolls or a large loaf)','https://www.bbcgoodfood.com/recipes/basic-granary-bread-dough-rolls-or-large-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2032_11-d91e764.jpg','Nothing beats homemade bread straight from the oven - ready in just 35 minutes',69,12,1,2,20,'Mix the flours and salt together in a large bowl, then stir in the sugar and yeast. Make a well in the centre of the flour and pour in the warm milk, beaten egg, olive oil and enough of the water to form a soft, wet dough. On a lightly floured surface, knead dough for 10 mins until smooth. Put the dough in a lightly oiled bowl, cover with oiled cling film, then leave to rise in a warm place until doubled in size. The dough is now ready to be shaped. To make 12 rolls, divide the dough into 12 pieces and shape into rolls. Lightly flour 3 baking sheets. Place the rolls onto the sheets and cover loosely with oiled cling film. Set aside in a warm place for 30 mins or until they have doubled in size. Heat oven to 200C/fan 180C/gas 6. Brush the bread with beaten egg and sprinkle with sesame and poppy seeds. Bake for 15-20 mins until deep golden brown and well risen. To make a flowerpot loaf, leave the dough to rise for about 1 hour, or until doubled in size. Soak one large or eight small terracotta flowerpots in cold water for about 30 mins. Dry thoroughly, then brush the inside of the pots with oil and line with parchment paper. Shape dough into a smooth round with a roughly pointed shape at one end so it half-fills the pot. Leave until doubled in size. Heat oven to 190C/fan 170C/gas 5. Brush the top of the loaf with beaten egg, sprinkle with poppy or sesame seeds and bake for 45-50 mins (15-20 mins for small loaves) until golden. Let it sit for 5-10 mins, then take out of the pot and leave to cool on a wire rack.','15 mins','15 mins - 20 mins'),
(917,'tear-and-share cheese & garlic rolls','https://www.bbcgoodfood.com/recipes/tear-and-share-cheese-garlic-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tear-and-share-cheese-garlic-rolls-b4638a7.jpg','Enjoy this cheesy garlic tear-and-share bread at a family dinner. It''s perfect for feeding a crowd at a barbecue, or as a side dish to a hearty casserole',19,20,1,2,20,'Heat 280ml water in a saucepan for a minute until warm but not too hot to stick your finger in (don’t burn yourself!). Remove from the heat, then add 50g butter. Combine the flour, yeast, sugar and 1 tsp salt in a large bowl or a tabletop mixer. Add the warm water and beat to make a soft dough. Knead for 10 mins by hand, or 5 mins in a mixer, until the dough feels stretchy and smooth. Tip into a clean, oiled bowl and cover with oiled cling film. Leave to rise for 1½-2 hrs, or until doubled in size. Brush a large baking tray with oil and scatter over the polenta. Knock the air out of the dough. Pinch off small pieces (about the size of a walnut), then roll each piece into a ball and put on the baking tray. Leave a little space between each dough ball. Heat oven to 180C/160C fan/gas 4. Cover the tray with oiled cling film, then prove for 30 mins-1 hr until the dough has doubled in size and the balls are touching. Mix the remaining butter with the garlic. When the rolls are ready to cook, brush the tops with the garlic butter and scatter with the cheeses. Bake for 25-30 mins until the dough balls are cooked through. Leave to cool for 5 mins, then serve.','40 mins','40 mins'),
(918,'cheese & pesto whirls','https://www.bbcgoodfood.com/recipes/cheese-pesto-whirls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheese-pesto-whirls-01e4b02.jpg','These herby tear-and-share bread rolls have mozzarella and sun-dried tomatoes baked into them - perfect for a picnic or for dipping into soup',6,12,2,2,20,'Combine the flour, yeast, sugar and 1½ tsp fine salt in a large mixing bowl, or the bowl of a tabletop mixer. Measure out 300ml warm water and add roughly 280ml to the flour, along with the olive oil, and start mixing until the ingredients start to clump together as a dough. If the dough seems a little dry, add the remaining water. Once combined, knead for 10 mins by hand on your work surface, or for 5 mins on a medium speed in a mixer. The dough is ready when it feels soft, springy and elastic. Clean the bowl, drizzle in a little oil, then pop the dough back in, turning it over and coating the sides of the bowl in oil. Cover with some oiled cling film and set aside in a warm place to double in size – this will take 1-3 hrs, depending on the temperature. Line a baking tray with parchment. Uncover the dough and punch it down a couple of times with your fist, knocking out all the air bubbles. Tip out onto a floured work surface and dust the top with a little flour too, if it is sticky. Roll the dough out to a rectangle, roughly 40 x 30cm. Spread the pesto over the dough, then scatter over the tomatoes, both cheeses and the basil. Roll the dough up from one of the longer sides, into a long sausage. Use a sharp knife to cut the dough into 12 even pieces. Place on the baking tray, cut-side up, in a 3-by-4 formation, making sure the open end of each roll is tucked in towards the centre on the arrangement – this will prevent them from uncoiling during cooking. Leave a little space between each roll as they will grow and touch as they prove. Loosely cover with oiled cling film and leave to prove for 30 mins–1 hr until almost doubled in size again. Heat oven to 200C/180C fan/gas 6. Uncover the bread when it is puffed up. Bake on the middle shelf in the oven for 35-40 mins until golden brown and the centre looks dry and not doughy. Remove from the oven and leave to cool for at least 10 mins.','40 mins','40 mins'),
(919,'cheese roll-ups','https://www.bbcgoodfood.com/recipes/cheese-roll-ups','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1025484_11-f56ab42.jpg','Teach kids a lifetime cookery skill with a special recipe from CBeebies'' ''i can cook''- these buns show how to make dough',5,6,1,2,20,'Heat oven to 220C/200C fan/gas 7. Put the flour and butter in a bowl and rub them together with your fingers. Rubbing in mixture with cold butter is hard and tiring on young fingers, so use slightly softened butter – but not so soft that it is oily. Now stir in the paprika and mix again. Add 100ml milk and mix with a fork until you get a soft dough. Add a splash more milk if the dough is dry. This process will teach you how to feel the dough and decide if it needs more liquid. You can always add more milk if required. On a lightly floured surface, roll out the dough like pastry to about 0.5cm thick. Try to keep a rectangular shape. Only roll in one direction, and roll and turn, roll and turn – by keeping the dough moving, you avoid finding it stuck at the end. Sprinkle the grated cheese on top, then roll up like a sausage along the long side. Cut into 12 thick rings using a table knife. Get an adult to show you how to hold the dough with one hand and cut straight through with the other. Line the baking tray with baking parchment. Place the roll-ups on the parchment, cut-side down, almost touching each other, making sure that you can see the spiral. Get an adult to put them in the oven for you and bake for 20-25 mins until golden and melty. Ask an adult to remove them from the oven, then leave to cool. The cheese roll-ups will keep for up to 3 days in an airtight container.','30 mins','25 mins'),
(920,'simit bread','https://www.bbcgoodfood.com/recipes/simit-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/simit-bread-d198642.jpg','Simit breads are sesame bread rings, which look similar to bagels. They are sold on every street corner in Turkey and are popular for breakfast or as a snack',6,4,1,2,20,'Tip the flour, salt and sugar into a bowl and mix together. Add the yeast and 150ml warm water, mix, then knead for a few mins to bring everything together into a soft dough. Tip onto your work surface and continue kneading for 10-12 mins until the dough feels stretchy. Return to a clean bowl, cover and leave to rise for an hour or two until doubled in size. Meanwhile, toast the sesame seeds in a dry frying pan until golden. Set aside in the pan. Mix the molasses syrup or pomegranate molasses with 50ml water in a wide shallow bowl. Divide the dough into four equal pieces. Roll each into a sausage about 45cm long. Flip the rope of dough back on itself then twist the two strands and loop into a ring, squeezing the ends together to secure. Once shaped, dip each piece into the molasses water, turning to coat, then into the sesame seeds, making sure each one is well covered. Transfer to a baking tray, cover and leave to prove for another 45 mins-1 hr. Heat oven to 200C/180C fan/gas 6. When the breads have puffed up, uncover them and bake for 15-18 mins until golden. Cool, then wrap in a tea towel to keep them fresh. Can be made up to a day ahead and warmed gently before serving.','30 mins','15 mins'),
(921,'roquefort & walnut rolls','https://www.bbcgoodfood.com/recipes/roquefort-walnut-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-781508_11-47e1bfb.jpg','Use your favourite cheese in these delicious rolls from Great British Bake Off judge Paul Hollywood',5,8,1,2,20,'Place the walnuts in a food processor and pulse to chop finely; leave to one side. Put the flours, 10g salt, yeast, butter and 300ml water in a bowl, stir together and begin massaging the ingredients for a minute. If the mixture seems dry, add more water a tbsp at a time to make a soft but not sticky dough, then knead in the bowl for a further couple of minutes to bring the dough together. Tip the dough onto a lightly floured work surface and knead well for 10 mins before returning to the bowl. Cover the bowl with cling film and leave to rise in a warm place for 1 hr. Briefly knead the walnuts into the dough, then leave to rise in the bowl for a further 20 mins. Tip the dough out onto a lightly floured surface and roll out into a rectangle, approx 50 x 20cm, 1cm thick. Sprinkle the Roquefort evenly over the dough and lightly press in. Place the rectangle width-ways in front of you and roll up the rectangle like a Swiss roll. Cut into 8 equal sized pieces and place them onto a lightly greased baking tray, cut-side down. Cover with cling film and leave to rise for 1 hr. Heat oven to 220C/200C fan/gas 7. Brush each round of dough with the beaten egg and bake for 20 mins. Leave to cool for a few minutes and eat while still warm. These are delicious on their own or served with a soup or a chunky salad with apple in it.','30 mins','20 mins'),
(922,'jalapeño & cheddar corn rolls','https://www.bbcgoodfood.com/recipes/jalapeno-cheddar-corn-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jalapeno-cheddar-corn-rolls-4ccc22f.jpg','These bread rolls are full of strong cheddar and pop with jalapeños. They make a tasty veggie side and you can make a batch to freeze for later',6,12,1,2,20,'Heat oven to 180C/160C fan/gas 4 and brush the holes of a 12-hole muffin tin with melted butter. Dust generously with polenta and shake out the excess. Tip 175g of the sweetcorn into a food processor with 1 tsp salt and the remaining ingredients, except the jalapeños. Blend until smooth, then remove the blade and stir through the remaining sweetcorn and jalapeños. Divide the batter between the holes and bake for 25-30 mins until risen and golden. Insert a skewer into the centre of a roll – it should come out with a few moist crumbs, but the mixture should not be wet. Return to the oven for 5 mins more if necessary, then test again. Cool for 5 mins, then transfer the rolls to a wire rack to cool. Will keep, wrapped in cling film, for up to 3 days, or freeze for up to 2 months – warm in a low oven before serving.','20 mins','30 mins'),
(957,'lime & chilli pitta crisps','https://www.bbcgoodfood.com/recipes/lime-chilli-pitta-crisps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2795_11-6ddb1ca.jpg','Enjoy a low-fat snack with Lime & chilli pitta crisps',33,2,1,2,20,'Heat oven to 180C/fan160C /gas 4. Tear 2 pitta breads into crisp-size pieces and brush with 1 tsp oil. Sprinkle with lime zest and 1⁄2 tsp chilli flakes. Bake for 10 mins until crisp. Sprinkle with salt if you like.','15 mins','No Time'),
(923,'olive bread swirls','https://www.bbcgoodfood.com/recipes/olive-bread-swirls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-27118_12-d7e3e79.jpg','These delicious savoury rolls make an excellent side dish for a lunch party',115,12,2,2,20,'In a large bowl, mix the flour, salt, yeast, 1 tbsp olive oil and 300ml warm water, to make a soft dough. Knead by hand for 10 mins or with the dough hook in a food processor or mixer for 5 mins. Put the dough back in the cleaned out mixing bowl, then cover the bowl with oiled cling film. Leave to rise for an hr in a warm place until doubled in size. Meanwhile, make the filling. Discard any tough basil stalks (leave tender ones on) and put in a mini food processor or hand blender beaker with the black olives. Add 4 tbsp olive oil, the garlic and the anchovies, if using. Whizz to a rough paste. Heat oven to 220C/200C fan/gas 7. Line a shallow baking tin about 30 x 20cm with non-stick paper. On a floured worktop, roll out the dough to a rectangle roughly 30 x 40cm. Spread the olive paste all over and arrange the whole green olives in a line down one of the short edges. Roll up the dough like a Swiss roll, starting at the short olive-encrusted end to make a sausage shape. Cut the dough into 12 slices, then carefully lift each one into the tin, to make 4 rows of 3 swirl shapes. Lightly brush all over with the remaining olive oil. Loosely cover with cling film, then leave to rise for 20 mins or so, until slightly puffed up and filling the tin. Cook for 20-25 mins until golden, then leave to cool in the tin.','20 mins - 25 mins','20 mins - 25 mins'),
(924,'tear-and-share stuffed deli rolls','https://www.bbcgoodfood.com/recipes/tear-and-share-stuffed-deli-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bread_1-8d59545.jpg','A picnic-friendly sharing loaf stuffed with mozzarella cheese, peppers and artichokes',6,12,2,2,20,'Make up the bread dough following pack instructions, adding 1 tbsp of pesto instead of the oil suggested on the pack. Leave the dough to rise until doubled in size. Brush a 23cm springform tin with some of the oil from the artic and set aside 2 tbsp pesto and 1 tbsp chopped peppers for the filling. Mix the remaining ingredients together in a bowl. Divide the dough into 12 equal pieces, using scales to be exact. Roll each piece into a ball, then roll out to form a disc with a rolling pin. Divide the pesto-veg filling between the 12 pieces of dough, then shape back into balls by pinching the edges together and rolling until smooth. Arrange the dough balls, seam-side down, in the cake tin. Cover with a piece of oiled cling film and leave to prove until doubled in size. 4 Heat oven to 180C/160C fan/gas 4. Remove the cling film. Brush the rolls with the remaining pesto and scatter over the rest of the peppers. Bake for 50 mins until golden and cooked through. Cool in the tin for 10 mins, then cool completely on a wire rack before transferring the deli rolls to your picnic basket.','25 mins','50 mins'),
(925,'brazilian cheese bread (pão de queijo)','https://www.bbcgoodfood.com/recipes/brazilian-cheese-bread-pao-de-queijo','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheeserolls-c49d7e3.jpg','Try baking a South American mainstay – these light and fluffy cheese puffs are best served warm from the oven',10,24,1,2,20,'Heat the oven to 220C/200C fan/gas 7. Oil or butter a baking sheet and set aside. Heat the milk, butter and salt in a medium saucepan and bring to a boil. Remove from the heat and add the tapioca flour. Stir vigorously, then leave to cool a little. Pour the dough into a standing mix to beat the dough until its cool, or use a wooden spoon and beat by hand. Beat the eggs in one at a time, waiting until each is completely incorporated before adding the next. When the dough is glossy and mixed, add the parmesan. Beat again until mixed. Spoon the dough onto the baking sheet in 1 tbsp balls, 5cm apart. Bake for 30 mins until crisp and golden. Remove from the oven and eat while still warm.','20 mins','30 mins'),
(926,'brioche buns','https://www.bbcgoodfood.com/recipes/brioche-buns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/brioche-c64ba15.jpg','We show you how to master this soft bread enriched with eggs and milk. Serve split and filled with barbecued meat, burgers or pulled pork',10,16,1,2,20,'Mix the warm water, yeast, warm milk and sugar in a bowl. Let it stand for 5 mins until it becomes frothy – this is how you know the yeast is working. Tip the flour and 1 tsp salt into a large mixing bowl, add the butter and rub together with your fingertips until the mixture resembles fine breadcrumbs. Make a well in the centre of the buttery flour and add the warm yeast mixture and the eggs. Use your hands to mix it into a sticky dough – don’t worry if the mixture feels a little wet at this stage, it will come together when kneading. Tip the dough out onto a floured work surface. Knead the dough for 10 mins by stretching it on the work surface – it will still be very sticky at this stage but don’t be tempted to add too much flour. The dough is ready when it feels soft and bouncy – this means that the gluten strands have developed. Place in an oiled bowl, cover with cling film and set aside to rise for 1-3 hrs or until doubled in size. Once the dough has doubled in size, knock the air out and knead again for 2 mins. The dough should be much less sticky now, but add a little flour if it needs it. Divide the dough into 12-16 even pieces. Roll into balls and arrange on lined baking trays. Loosely cover with oiled cling film and leave for about 1 hr or until doubled in size again. Heat oven to 200C/180C fan/gas 6 and place a shallow baking tray at the bottom. Uncover the trays, brush the buns with egg and sprinkle with sesame seeds. Pour a cup of water into a baking tray at the bottom of the oven to create steam (see Tips for success, left). Bake for 20 mins or until golden, then leave to cool on a wire rack.','15 mins','20 mins'),
(927,'garlic & basil ciabatta','https://www.bbcgoodfood.com/recipes/garlic-basil-ciabatta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327811_12-848628f.jpg','Perfect as a starter or as a side dish for a dinner party',5,4,1,2,20,'Heat the grill to high. Beat together the mayonnaise, butter and garlic cloves until smooth. Chop basil, stir through and season with salt and pepper. Put the halves of the ciabatta, sliced lengthways, on a baking tray and spread with the butter. Sprinkle with grated Parmesan, then grill for 2-3 mins.','5 mins','3 mins'),
(929,'cheesy garlic baguette','https://www.bbcgoodfood.com/recipes/cheesy-garlic-bread-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3401_11-3aa611b.jpg','Make this family-friendly, cheesy garlic bread to accompany soup, chilli or pasta. It''s an easy crowd-pleaser that''s full of melty mozzarella.',116,6,1,2,20,'Light the barbecue. Chop the mozzarella and taleggio into small chunks and tip into a bowl with the butter, garlic, herbs and some salt and pepper. If you want, you can mix everything together with a wooden spoon, but I prefer to use my hands. Using a bread knife, cut diagonal slices along the loaf about three quarters of the way into the bread – don’t cut all the way through. If it looks like the bread won’t fit on the barbecue, cut it in half and make two smaller ones. Push the garlic butter generously between each cut, allowing some to ooze onto the top – it will caramelise and taste superb. Sprinkle the paprika over. Wrap the bread up like a cracker in a double thickness of foil. You can prepare to this stage up to a day ahead. Put the pack straight onto the barbecue. Cook for 2 minutes then roll it over onto its side and cook for 2 minutes more. Roll it onto its other side and cook for a further 2 minutes. Have a peek – if the cheese is melting and the bread is crispy, remove it from the barbie. Pull the slices apart and eat while hot and gooey.','20 mins','No Time'),
(930,'garlic bread','https://www.bbcgoodfood.com/recipes/garlic-bread-toasts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-328149_12-1c70276.jpg','Make this classic side dish with a crispy crust and buttery, garlicky centre. Our cheesy garlic bread toasts are delicious served with lasagne, soups and plenty more.',8,12,1,2,20,'Slice the bread in half lengthwise. Toast the crust side for 2 mins until really crispy. Mix together butter, garlic and parsley in a bowl. Spread over the cut bread halves. Sprinkle over the Parmesan. Just before serving place on a baking sheet, then cook under a hot grill for 5 mins until toasty and lightly golden. Let it cool for 1 min before cutting into thick slices.','10 mins','7 mins'),
(931,'cheesy garlic bread wedges','https://www.bbcgoodfood.com/recipes/cheesy-garlic-bread-wedges','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1632_11-29e1867.jpg','A wonderfully gooey version of garlic bread with jarlsberg',37,12,1,2,20,'Preheat the oven to fan 170C/conventional 190C/ gas 5. Beat the butter and garlic, then mix in the cheese, parsley and chillies. Cut the bread in half crossways through the centre. Spread the cheese mixture on the cut sides of both bread halves. Wrap each half loosely in foil and put on a baking sheet. Bake for 20 minutes, unwrap the foil and bake a further 10-15 minutes until starting to turn pale golden. Cut into wedges and serve.','35 mins - 45 mins','N/A'),
(932,'garlic bread pizzas','https://www.bbcgoodfood.com/recipes/garlic-bread-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-24471_11-f05770e.jpg','A fresh, easy-to-prepare snack',5,4,1,2,20,'Tip all of the base ingredients into a mixing bowl. Pour over 300ml warm water – not too hot. Stir quite hard with a spoon until the mixture comes together. Turn out onto a lightly floured surface and knead 2-3 times until the mixture forms a ball. Divide dough into 8 equalsize pieces. Take 1 piece, leaving the remainder loosely covered with oiled cling film. Lightly flour the surface again and use a rolling pin to roll out dough into a round about 15cm diameter or as thin as you can. Put pizza on a baking sheet. Mix together the butter and crushed garlic, then spread a little over the pizza. Tear the mozzarella into thin strips and scatter over. Repeat for the remaining pieces of dough. You should be able to fit 2 pizzas per baking sheet; depending on how many baking sheets you have, you may have to cook in batches. Heat oven to 240C/fan 220C/gas 9. Bake pizzas for 12-15 mins until crisp and cooked through. Meanwhile, mix together the remaining ingredients, then season well. When the pizza comes out of the oven, top with the tomato mixture and serve straight away.','25 mins','15 mins'),
(933,'spicy garlic bread','https://www.bbcgoodfood.com/recipes/spicy-garlic-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-851456_11-f2829b8.jpg','Lashings of spiced garlic butter spread on ciabatta then baked makes a great homemade side dish',5,15,1,2,20,'In a small bowl, combine ½ tsp salt with all the ingredients apart from the ciabatta. Mash together with a fork so everything is combined. Heat oven to 200C/180C fan/gas 6. Using a bread knife, cut diagonal slices into the loaves, about three-quarters of the way through. Spread the butter into the slices, then wrap each loaf in a double layer of foil. Bake in the oven for 15 mins, then open up the foil and bake for a further 10 mins until crispy.','15 mins','25 mins'),
(934,'garlic bread nachos','https://www.bbcgoodfood.com/recipes/garlic-bread-nachos','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/garlic-bread-nachos-fa322e0.jpg','This easy side dish is sure to disappear quickly. Layer up garlic bread, cheddar, tortilla chips, spring onions and bake until melted. Serve with guacamole',17,8,1,2,20,'Cut through the baguettes to separate all the slices, discarding the end pieces. Pile the bread slices on top of your biggest baking sheet, all overlapping, as you would with tortillas for nachos. Scatter over most of the cheese, spring onions and crushed tortilla chips as you layer, finishing with a final layer of these toppings. If you’re preparing ahead, you can cover the tray with cling film and chill until ready to cook. Heat oven to 200C/180C fan/gas 6. Bake for 15 mins until the cheese has melted and the bread is golden and crisp. Loosen the soured cream with enough water to make a more drizzly topping, then spoon over the nachos and sprinkle with some coriander. Serve while everything is still hot and melted.','10 mins','15 mins'),
(936,'wild garlic butter on music paper bread','https://www.bbcgoodfood.com/recipes/wild-garlic-butter-music-paper-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/wild-garlic-butter-on-music-paper-bread-8c2573d.jpg','This delicate, super-thin bread is inspired by a Sardinian classic, carta di musica - serve with flavoured butter for a special starter or side',7,8,1,2,20,'In a bowl, mix together the flours and 1/2 tsp salt. Pour in 175ml water and mix to a soft dough. Tip onto a lightly floured surface and knead for about 5 mins or until smooth. Wrap in cling film and leave to rest for 30 mins. Meanwhile, place a pizza stone or one or two large baking trays in the oven, then heat it to 240C/220C fan/gas 9 or as high as it will go. Divide the dough into 8 and shape into balls. On a floured surface, roll out balls very thinly into rough circles about 20-22cm in diameter. Cut out 8 squares of baking parchment a little larger than the dough circles. Pop the dough circles on top and brush with olive oil. Cook the breads, 1 or 2 at a time, on the hot pizza stone or baking tray (still on the baking parchment) for 6-8 mins or until bubbled up in places, golden brown and crisp. Remove and leave to cool on a wire rack while you cook the rest. Pop all the breads back in the oven for 1 min to warm through. Remove and top with a slice of garlic butter (see Goes well with) and sea salt.','20 mins','35 mins'),
(937,'lamb & garlic bread salad','https://www.bbcgoodfood.com/recipes/lamb-garlic-bread-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb-garlic-bread-salad-cd258f6.jpg','Try these tasty lamb cutlets served with a vibrant salad of garlic bread, mint, charred cucumber and tomato. It''s an easy midweek meal or an al fresco lunch',41,4,1,2,20,'Heat oven to 200C/180C fan/gas 6. Mix half the mint with 1 tbsp oil, the lemon zest and a large pinch of salt and black pepper, then rub the mixture all over the lamb cutlets. Bake the garlic bread for 15 mins, then allow to cool a little. Tear into chunks and return to the oven for 5 mins to dry out. Meanwhile, put a griddle pan or large frying pan over a high heat and, once very hot, cook the lamb for 2-3 mins each side or until nicely seared outside but still pink in the middle. (You may have to do this in two batches.) Set aside to rest while you make the salad. Put the tomatoes and cucumber in the same hot pan and cook for 2 mins or until a little charred – you will need to do this in batches. Tip into a large bowl and add thetoasted bread, remaining mint and the parsley. Mix the lemon juice with the remaining olive oil and the honey, then season. Pour the dressing over the salad and toss gently so the tomatoes don’t break up too much. Serve the warm salad alongside the lamb cutlets.','20 mins','35 mins'),
(938,'christmas tree garlic bread','https://www.bbcgoodfood.com/recipes/christmas-tree-garlic-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/christmas-tree-garlic-bread-cffa5a3.jpg','Each piece of this pull-apart bread has a melted mozarella centre. Brushed with garlic butter and served with a tomato dipping sauce, this will be a hit at any Christmas party buffet table',6,8,2,2,20,'Tip the flour into a large bowl or the bowl of a freestanding mixer. Add the yeast and sugar to one side of the bowl and 1 tsp fine salt to the other. Mix together, then add the melted butter and 275ml warm water. Mix to a dough, then knead for 10 mins by hand (or 5 mins in a freestanding mixer) until the dough feels stretchy and soft. Clean out the bowl, then lightly grease with a little oil. Turn the dough over in the oiled bowl until it’s well coated. Cover with cling film or a tea towel and set aside somewhere warm until the dough has doubled in size. Alternatively, put the dough in the fridge and leave to rise slowly for 2 days (bring it to room temperature before continuing to step 2). Line your largest baking sheet with parchment, and dust with polenta or cornmeal. Drain the mozzarella and dry the balls on some kitchen paper. Tip the dough onto your work surface and punch out the air bubbles. To make a Christmas tree shape, you’ll need 23 balls. Tear off lumps of dough, ensuring each one is roughly the same size (if you want to be exact, weigh the dough, divide the weight by 23, then weigh each ball of dough as you break them off). Shape each piece of dough into a disc and place a ball of mozzarella in the middle. Pull up the sides of the dough to encase the cheese, pinching the dough together to seal. Roll into a ball and place on the baking sheet, sealed-side down, in a tree shape. Leave a little space between each ball, as they will grow when proving. You’re likely to have 2 or 3 balls without mozzarella, so use these pieces of dough for the trunk – for anyone who doesn’t like cheese. Cover the tray with a sheet of oiled cling film and set aside for 30 mins until almost doubled in size. While the dough proves, make the tomato dipping sauce. Melt the butter in a saucepan, add the garlic and sizzle for 30 secs until fragrant. Add the tomatoes, oregano, vinegar, sugar and some seasoning. Bubble for 30 mins until the sauce is thick. Heat oven to 200C/180C fan/gas 6. When the dough is ready, uncover the baking sheet and bake for 20-25 mins until golden brown. Meanwhile, make the garlic butter. Melt the butter in a saucepan, add the garlic and sizzle for 1-2 mins until the garlic is a shade darker (but not brown). When the bread is cooked, transfer it to a platter. Stir most of the parsley into the garlic butter and use a pastry brush to brush it all over the bread. Leave it to soak in, then brush on more. Sprinkle over a little extra parsley to garnish, if you like. Reheat the tomato sauce and serve it alongside the bread with any remaining garlic butter.','45 mins','55 mins'),
(939,'smoked mackerel chowder with hedgehog garlic bread','https://www.bbcgoodfood.com/recipes/smoked-mackerel-chowder-hedgehog-garlic-bread-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoked-mackerel-chowder-with-hedgehog-garlic-bread-ea7adbe.jpg','Whip up this budget-friendly, creamy fish soup and easy garlic loaf using smoked mackerel and a few store cupboard ingredients',6,4,1,2,20,'Heat oven to 220C/200C fan/gas 7. Mash the garlic with 50g butter. Slice the bread first one way, then the other (in a hedgehog pattern), but do not cut all the way through. Stuff the crevices with garlic butter, then wrap loosely in foil, leaving the top open to crisp, and bake for 20 mins while you make the chowder. Heat a knob of butter in a saucepan with the oil. Tip in the celery and onion, cook for 5 mins or until soft, then add the flour and mix to a paste. Pour in the milk, a little at a time, and cook until smooth and the consistency of double cream. Add the potatoes and half the mackerel, cover and simmer for 15 mins until the potatoes are cooked. Flake the remaining mackerel, add to the chowder with the sweetcorn and simmer for 1-2 mins until the sweetcorn is cooked. Splash in a little water if it is too thick. Ladle into bowls, scatter over the celery leaves and serve with the bread for dunking.','10 mins','25 mins'),
(940,'smoked haddock & sweetcorn chowder with herby garlic bread','https://www.bbcgoodfood.com/recipes/smoked-haddock-sweetcorn-chowder-herby-garlic-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chowder-c9c5372.jpg','This creamy fish soup with corn and potato will warm you up - serve with buttery homemade garlic baguette and plenty of parsley',5,4,1,2,20,'Heat oven to 200C/180C fan/gas 6. Mix together the butter, garlic and half the flat-leaf parsley, then spread the mix between diagonal cuts in the baguette. Wrap the bread in foil and set to one side. Heat the oil in a large pan over a medium heat. Fry the onion and bacon for about 5 mins until softened, then add the potato and cook for another 5 mins. Meanwhile, put the garlic bread in the oven and cook for 15 mins, opening the foil for the last 5 mins to brown. Stir the flour into the bacon and potato, and cook for a couple of mins, then gradually add the milk and stock. Bring to the boil, then reduce the heat and simmer gently for 10-15 mins until the potato is almost cooked. Add the haddock and sweetcorn to the pan, and simmer for a further 5 mins. Stir the remaining parsley into the chowder and serve alongside the garlic bread.','30 mins','35 mins'),
(941,'easy sausage casserole','https://www.bbcgoodfood.com/recipes/sausage-casserole-instant-garlic-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-901576_11-b01794a.jpg','A comforting and hearty one-pot sausage casserole recipe with spicy chorizo, smoked paprika and plenty of vegetables, perfect for a filling family meal',44,4,1,2,20,'Heat 2 tbsp olive or rapeseed oil in a large heavy-based pan. Add 1 finely chopped onion and cook gently for 5 minutes. Add 2 finely chopped medium celery sticks, 1 chopped yellow pepper and 1 chopped red pepper and cook for a further 5 mins. Add 6 chorizo sausages and 6 pork sausages and fry for 5 minutes. Stir in 3 chopped garlic cloves, 1 ½ tsp sweet smoked paprika, ½ tsp ground cumin and 1 tbsp dried thyme and continue cooking for 1 – 2 mins or until the aromas are released. Pour in 125ml white wine and use a wooden spoon to remove any residue stuck to the pan. Add two 400g cans of tomatoes, and 2 sprigs of fresh thyme and bring to a simmer. Crumble in the chicken stock cube and stir. Cook for 40 minutes. Stir in a 400g drained and rinsed can of aduki beans and cook for a further five minutes. Remove the thyme sprigs, season with black pepper and stir through some snipped chives, if using. Serve.','15 mins','1 hr'),
(942,'garlic & prosciutto plait','https://www.bbcgoodfood.com/recipes/garlic-prosciutto-plait','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-741479_11-85a163e.jpg','Create your own artisan bread with these attractive, yet simple-to-make savoury loaves',37,2,1,2,20,'Mix your choice of brown flour with the white, the yeast and salt in a large mixing bowl. Put in the butter and rub it into the flour. Mix in the crushed garlic. Make a dip in the centre of the flour and pour in almost 300ml hand warm (cool rather than hot) water, with a round-bladed knife. Then mix in enough of the remaining water and a bit more if needed, to gather up any dry bits in the bottom of the bowl and until the mixture comes together as a soft, not too sticky, dough. Gather it into a ball with your hands. Put the dough on to a very lightly floured surface and knead for 8-10 mins until it feels smooth and elastic, only adding the minimum of extra flour if necessary to prevent the dough sticking. Place the ball of dough on a lightly floured work surface. Cover with an upturned, clean, large glass bowl and leave for 45 mins-1 hr or until doubled in size and feels light and springy. Timing will depend on the warmth of the room. Knock back the dough by lightly kneading just 3-4 times. You only want to knock out any large air bubbles, so too much handling now will lose the dough’s lightness. Shape into a ball. Cover with the glass bowl and leave for 15 mins. Shape by cutting the ball into 6 even pieces. Roll each with your hands into a 30-35cm sausage shape, that is plump in the middle and tapers off at each end. For each plait, lay 2 dough sausages in front of you like an X, then lay the other piece lengthways down the middle of the X and scatter over a few slices of proscuitto. Start to plait from the centre down, left over right, right over left etc. When you reach the end, carefully turn the plait over and complete the plaiting, working down from the centre again. Press the tapered ends together to seal then lay each on a baking sheet lined with baking parchment. Tuck the rest of the proscuitto into the folds of each plait. Cover with a clean tea towel. Leave for 40-45 mins, or until doubled in size. Put a roasting tin in the bottom of the oven 20 mins before ready to bake and heat oven to 230C/210C fan/gas 8. Put the risen bread in the oven, carefully pour about 250ml cold water into the roasting tin (this will hiss and create a burst of steam to give you a crisp crust), then lower the heat to 220C/200C fan/gas 7. Bake for 25 mins or until golden. Remove and cool on a wire rack. If you tap the underneath of the loaf if should be firm and sound hollow. Finish by brushing with beaten egg.','20 mins','25 mins'),
(943,'garlic bacon butties','https://www.bbcgoodfood.com/recipes/garlic-bacon-butties','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-8137_11-ca8c698.jpg','You''ll need good, crusty white bread for these instant hangover cures',69,6,1,2,20,'Heat a griddle or frying pan, then cook the bacon for 3 mins each side or until golden and crisp. Cut 6 thick slices from the loaf and butter each one on one side. Spread the chutney evenly over 3 slices of the bread and top each with 2 rashers of bacon. Top with the other slices of bread, buttered side down, then press together well. Return the butties to the pan over a medium heat (you''ll have to do this in batches), then toast for 2-3 mins each side until golden. Remove from pan, then rub both sides with cut side of the garlic clove. Cut the butties in half and serve straight away.','10 mins','15 mins'),
(944,'sweet potato & rosemary soup with garlic toasts','https://www.bbcgoodfood.com/recipes/sweet-potato-rosemary-soup-garlic-toasts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-279556_11-d852fd4.jpg','This rustic soup is super-quick and cheap to make, but full of flavour',5,4,1,2,20,'Heat the oil in a large saucepan, then fry the onion until soft. Crush 2 garlic cloves, add to the onion, then fry for 1 min more. Stir in the sweet potato, then cover with the stock and bring to the boil. Strip the leaves from one of the rosemary sprigs, then add to the pan. Simmer for 10 mins until the potato is soft. Meanwhile, heat oven to 200C/ fan 180C/gas 6. Place the bread on a baking sheet, then brush with olive oil. Slice the remaining garlic clove in half, then rub the cut side over the bread along with the leaves from the second rosemary sprig. Season, then bake for 10 mins until crisp and golden. Use a hand blender to roughly purée the soup, adding a splash of hot water if it seems too thick. Season well, then pour into warmed bowls and serve with the toasts.','10 mins','20 mins'),
(945,'seeded wholemeal loaf','https://www.bbcgoodfood.com/recipes/seeded-wholemeal-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/seeded-wholemeal-loaf-8a19940.jpg','This hearty, wholesome bread is rich in flavour and packed with seeds - try pumpkin, sunflower, poppy or linseeds',1,1,2,2,20,'Combine both flours in a large bowl with the yeast and 1 tsp fine salt. Mix the treacle with 250ml warm water until well combined. Stir into the flour to make a slightly sticky dough. If you need to add more water, splash it in 1 tbsp at a time. Knead the dough on a lightly floured surface for 10 mins (or in a tabletop mixer for 5-7 mins). Your dough should be smooth and elastic when it’s ready. Place the dough in a lightly oiled bowl, flip the dough over to coat it in oil, then cover with a sheet of oiled cling film. Leave in a warm place until doubled in size – this will take about 1 hr (see note, below). Lightly oil a 900g loaf tin. Once doubled in size, knead the dough again for 3-5 mins to knock out the air bubbles – add most of the seeds and work these into the dough as you knead. Shape the dough into an oval roughly the same length as your tin. Place in the tin and leave to prove, covered with oiled cling film, for 30-45 mins until it has nearly doubled in size again. Heat oven to 200C/180C fan/gas 6. Gently press a finger into the loaf to check if it has had enough proving time (see tip). When it’s ready, glaze the top of the loaf with the egg yolk and sprinkle over the remaining seeds. Bake in the oven for 40-45 mins until golden brown – if you tip the loaf out of the tin and tap the bottom, it should sound hollow. Leave to cool on a wire rack for at least 30 mins before slicing.','35 mins','45 mins'),
(975,'honey & almond layer cake','https://www.bbcgoodfood.com/recipes/honey-almond-layer-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/honey-almond-layer-cake-2a49c60.jpg','April opts for the ''naked'' style of decorating so the cake isn''t swamped with icing, but instead delicately finished with candied almonds and Swiss meringue buttercream',117,12,3,2,21,'First, make the honey & almond sponges. Heat oven to 180C/160C fan/ gas 4. Grease two 15cm cake tins and line the bases with baking parchment. Using a stand mixer or electric hand mixer, beat the butter, muscovado sugar and 140g honey until pale and fluffy. Add the eggs and yolks, one at a time, beating after each addition until well incorporated. In a clean bowl, whisk together the flour, almonds, baking powder and 1 / 4 tsp salt. Add half the flour mixture to the butter mixture and beat until just combined. Add the vanilla and milk, continuing to beat, then add the remaining flour mixture, beating until just combined. Divide the mixture evenly between the tins and bake for 45-55 mins or until a skewer inserted into the centre of each cake comes out clean. Allow the cakes to cool for 10 mins in their tins, then turn out onto a wire rack to cool completely. Leave the oven on for the candied almonds. To make a honey syrup, put 2 tbsp honey and 2 tbsp water in a small saucepan and heat for 1-2 mins or until the honey has dissolved. Set aside. Make the candied almonds by heating 150ml water and the sugar in a medium, heavy-bottomed saucepan with a sugar thermometer attached, and swirl until the sugar has dissolved. Meanwhile, spread the almonds out on a baking sheet and bake in the oven for 10-12 mins until golden brown. Heat the sugar syrup to 119C, turn off the heat and stir in the warm almonds. Keep stirring until the syrup coats the nuts and turns white and crunchy. Tip the almonds back onto the baking sheet and roughly chop half of them. Next, make the Swiss meringue buttercream. Using a stand mixer or electric hand mixer, beat the butter until pale and fluffy. Put the egg whites and sugar in a clean heatproof bowl over a pan of simmering water. Whisk using a clean electric hand mixer and heat the mixture until the sugar has dissolved (you can check this by feeling some of the egg white between your fingers to check that it isn’t grainy). Remove the bowl from the heat and, using a stand or electric hand mixer, whisk for 10 mins or until the mixture forms stiff peaks and has cooled to room temperature. Add the butter, 1-2 tbsp at a time, and beat until the mixture is smooth and creamy. Beat in the honey and transfer to a piping bag fitted with a 1cm nozzle. To assemble the cake, level the sponges by cutting off the domed tops and split each sponge in half to create four layers. Fix the bottom layer to a cake board or stand with a splodge of buttercream, brush with the honey syrup and pipe a layer of buttercream on top. Gently press the second layer on top and repeat with the remaining layers. Cover the cake with a thin layer of buttercream, then use a palette knife to scrape the sides clean. Pipe a ring of buttercream blobs around the top, or spread a thick layer with a palette knife. Decorate with the candied almonds.','1 hr and 30 mins','1 hr and 30 mins'),
(946,'wholemeal flatbreads','https://www.bbcgoodfood.com/recipes/wholemeal-flatbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/wholemeal-flatbreads-fdbebff.jpg','These flatbreads are a doddle – simply make a large batch out of storecupboard ingredients and freeze them for later in the week',48,8,1,2,20,'Put the flour in a medium bowl and rub in the oil with your fingertips. Stir in 225ml warm water, mix thoroughly, then knead until the dough feels smooth and elastic. Put the dough onto a lightly floured surface and divide into eight balls. Sprinkle the work surface with a little more flour and roll out one of the balls very thinly, using a floured rolling pin, to around 22cm in diameter. Turn the dough regularly and sprinkle with a little more flour if it begins to stick. Set aside and make the other flatbreads in the same way. If making ahead, freeze before cooking. Put a medium non-stick frying pan over a high heat and, once hot, add one of the flatbreads. Cook for about 30 secs, then turn over and cook on the other side for 30 secs. Press the flatbread with a spatula while cooking to encourage it to puff up and cook inside – it should be lightly browned in patches and look fairly dry, without being crisp. Repeat with the remaining flatbreads, keeping them warm by wrapping in a clean tea towel until needed.','10 mins','10 mins'),
(947,'mixed seed bread','https://www.bbcgoodfood.com/recipes/mixed-seed-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-77451_11-15b4214.jpg','This recipe uses a basic bread dough method, but the mix of flours gives an interesting texture and flavour',5,1,2,2,20,'Mix the flours, salt, yeast, sunflower, caraway, and poppy seeds in a large bowl. Add the black treacle and water, then mix well. If the dough seems a little stiff, add 1 tbsp or more extra water. Mix well, then put on a lightly floured work surface and gently knead the dough for 7 mins. Put it back into a lightly oiled bowl for approx 2 hours until doubled in size. Line a tray with baking parchment. Tip the dough onto a lightly floured work surface and knock back, then gently mould the dough into a ball. Roll the dough in the sesame seeds and place on the baking tray to prove for a further hour until doubled in size. Heat oven to 220C/fan 200C/gas 7. Cut the top into criss-cross slashes with a sharp knife and bake for 30 mins until golden brown and the loaf sounds hollow when tapped underneath. Cool on a wire rack.','25 mins','30 mins'),
(948,'rye bread','https://www.bbcgoodfood.com/recipes/rye-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1274479_8-2fd8fce.jpg','This rye bread recipe is lower in gluten than your average white loaf - this recipe uses white or wholemeal flour to give a light texture but you can experiment with ratios',6,1,1,2,20,'Tip the flours, yeast and salt into a bowl. In a jug, mix the honey with 250ml warm water, pour the liquid into the bowl and mix to form a dough. Rye flour can be quite dry and absorbs lots of water, if the dough looks too dry add more warm water until you have a soft dough Tip out onto your work surface and knead for 10 mins until smooth. Rye contains less gluten than white flour so the dough will not feel as springy as a conventional white loaf. Place the dough in a well oiled bowl, cover with cling film and leave to rise in a warm place for 1-2 hrs, or until roughly doubled in size. Dust a 2lb/900g loaf tin with flour. Tip the dough back onto your work surface and knead briefly to knock out any air bubbles. If using caraway seeds work these in to the dough. Shape into a smooth oval loaf and pop into your tin. Cover the tin with oiled cling film and leave to rise somewhere warm for a further 1 – 1.5 hr, or until doubled in size. Heat oven to 220C/200C fan/gas 7. Remove the cling film and dust the surface of the loaf with rye flour. Slash a few incisions on an angle then bake for 30 mins until dark brown and hollow sounding when tapped. Transfer to a wire cooling rack and leave to cool for at least 20 mins before serving','50 mins','No Time'),
(949,'triangular bread thins','https://www.bbcgoodfood.com/recipes/triangular-bread-thins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bread-thins-8a75e8f.jpg','Bake our easy bread thins with wholemeal spelt and top with your favourite ingredients. We have ideas for using them in two healthy lunch recipes',4,6,1,2,20,'Heat oven to 200C/180C fan/gas 6 and line a baking sheet with baking parchment. Mix the flour, bicarbonate of soda and baking powder in a bowl, then stir in the diluted yogurt with the blade of a knife until you have a soft, sticky dough, adding a little water if the mix is dry. Tip the dough onto a lightly floured surface and shape and flatten with your hands to make a 20cm round. Take care not to over-handle as it can make the bread tough. Lift onto the baking sheet and cut into six triangles, slightly easing them apart with the knife. Bake for about 10-12 mins – they don’t have to be golden, but should feel firm. Leave to cool on a wire rack. Use to make our wild salmon & avocado triangles and goat''s cheese, tomato & olive triangles. The rest can be packed into a food bag to use later in the week, or frozen until needed.','8 mins','10 mins - 12 mins'),
(950,'brown loaf','https://www.bbcgoodfood.com/recipes/brown-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-743472_11-9d86607.jpg','Try this simple step-by-step brown bread recipe to make a seeded loaf or try one of three gorgeous variations',37,1,1,2,20,'Mix your choice of brown flour with the white, the yeast and salt in a large mixing bowl. Put in the butter and rub it into the flour. Stir in the seeds if using. Make a dip in the centre of the flour and pour in almost 300ml hand warm (cool rather than hot) water, with a round-bladed knife. Then mix in enough of the remaining water and a bit more if needed, to gather up any dry bits in the bottom of the bowl and until the mixture comes together as a soft, not too sticky, dough. Gather it into a ball with your hands. Put the dough on to a very lightly floured surface and knead for 8-10 mins until it feels smooth and elastic, only adding the minimum of extra flour if necessary to prevent the dough sticking. Place the ball of dough on a lightly floured work surface. Cover with an upturned, clean, large glass bowl and leave for 45 mins-1 hr or until doubled in size and feels light and springy. Timing will depend on the warmth of the room. Knock back the dough by lightly kneading just 3-4 times. You only want to knock out any large air bubbles, so too much handling now will lose the dough’s lightness. Shape into a ball. Cover with the glass bowl and leave for 15 mins. Now shape to make a tin loaf Grease a 1.2-litre capacity loaf tin (about 23 x 13 x 5.5cm) and line the base with baking parchment. Using your knuckles, flatten the dough into a rectangle about 25 x 19cm. Fold both shorter ends into the centre like an envelope, make a ¼ turn, then flatten again into the same size and roll up very tightly, starting from one of the short ends. Roll the top of the dough in extra seeds and place in the tin with the join underneath, pressing the seeds gently into the dough. Cover with a clean tea towel. Leave for 40-45 mins, or until risen about 5cm above the top of the tin. Put a roasting tin in the bottom of the oven 20 mins before ready to bake and heat oven to 230C/210C fan/gas 8. Put the risen bread in the oven, carefully pour about 250ml cold water into the roasting tin (this will hiss and create a burst of steam to give you a crisp crust), then lower the heat to 220C/200C fan/gas 7. Bake for about 30 mins or until golden, covering with foil for the last 5 mins if starting to brown too quickly. Leave in the tin for 2-3 mins, then remove and cool on a wire rack. If you tap the underneath of the baked loaf if should be firm and sound hollow.','20 mins','30 mins'),
(951,'indian bread with courgettes & coriander','https://www.bbcgoodfood.com/recipes/indian-bread-courgettes-coriander','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1376_13-ebec0b7.jpg','This bread is called thepla in India and is best served hot with lightly salted plain yogurt, or cold with mango chutney',5,12,2,2,20,'Dry fry the cumin seeds for 30-60 seconds in a non-stick pan, tossing constantly until toasted. Remove from heat. Wash the courgettes, then trim and grate them on the coarsest side of the grater (unpeeled) into a largish bowl. Tip in both the flours, the ginger, turmeric and coriander, add 1 tsp salt and stir well. Pour 11⁄2 tbsp of the oil into the flour mixture and rub it in, then gradually add 4-5 tbsp cold water until the mixture comes together to form a soft dough, a bit wetter than pastry. Tear or cut off 12 equal-sized pieces and shape into balls. Dust the work surface and rolling pin with a little extra flour and roll each piece into a thin 14cm round – don’t worry if the edges are crinkly. Heat a large cast-iron griddle or heavy-based frying pan until very hot. Put one or two breads on the griddle and cook for 2 minutes on one side, patting the edges with a clean soft cloth – this keeps the bread in contact with the heat and helps cook it fast. Turn the breads over and cook for 2 minutes more. Drizzle a little oil around the edges of the bread, turn them over again and cook for 30-60 seconds more, then drizzle a few drops of oil on this side. Remove and set aside on a plate. Repeat with the rest of the rounds. (You can make the breads up to 2 hours ahead, then wrap in foil and reheat in a low oven.) Serve hot or cold.','1 hr and 15 mins','No Time'),
(952,'spiced flat breads','https://www.bbcgoodfood.com/recipes/spiced-flat-breads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2317_12-b04cdea.jpg','Easy to make and delicious from the grill',5,8,1,2,20,'If making the paste, use a food processor, electric spice mill or pestle and mortar to blend together the garlic, chilli, sesame seeds and ground spice to a thick paste. Work in the oil, then the coriander, to a spreading paste and season. Set aside. Put the flours, 1 tsp salt and yeast into a large bowl and mix. Stir 120ml tepid water into the melted butter, then mix this into the flour, adding extra drops of tepid water until you have a smooth dough. Knead on a lightly floured surface for 5 mins, then return to the bowl, cover and leave in a warm place to rise until doubled in size. This should take 1 hr. Divide the dough into 8 balls. Roll out each ball on a lightly floured surface to a rough 18cm circle the thickness of a 10p coin. Prick each one well with a fork to stop them rising. To cook on a barbecue: put the breads on the centre of the grill. Cook for a few mins, then turn and cook the other side. If using the paste, spread 1 tsp on one side as the breads come off the heat. Otherwise, brush the breads with more melted butter. Reheat the griddle between batches and stack them on top of each other to keep warm.','35 mins','10 mins'),
(953,'stout & apple wheaten bread','https://www.bbcgoodfood.com/recipes/stout-apple-wheaten-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apple-and-beer-wheaten-bread-4ef5b91.jpg','An easy beer bread with chunks of sweet apple. This wholemeal loaf is made with buttermilk and treacle for a dense, moist slice you can enjoy with your tea',70,3,1,2,20,'Heat oven to 180C/160C fan/gas 4. Butter three 1lb loaf tins. Toss the apple with 2 tbsp of the plain flour. Mix the flours and oatmeal with 1 tsp salt and the bicarbonate of soda. Add the butter and rub it in with your fingertips. Stir in the sugar. Make a well in the middle of the mixture and gradually pour in the stout, followed by the treacle, then the buttermilk. Mix the liquids in with a butter knife as they are added, also working in the floured apple – work quickly and be careful not to overmix. Divide the mixture between the loaf tins, sprinkle over the oats or seeds. and bake for 35-40 mins. To test whether the loaves are ready, remove one from the tin and tap the bottom. If it sounds hollow, it’s ready; if not, return to the oven for a little longer. Turn the loaves out of the tins and leave to cool on a wire rack.','30 mins','35 mins - 40 mins'),
(954,'seeded flatbreads','https://www.bbcgoodfood.com/recipes/seeded-flatbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1173649_11-9b4fc64.jpg','Moroccan-style breads with nigella and sesame seeds, great for wrapping and dipping',6,12,1,2,20,'Mix the yeast with 2 tbsp warm water and sugar, and leave for a few mins. Tip the flours into a large bowl with 1 tsp salt and make a well in the centre. Pour in the yeast mixture and 500ml warm water. Mix with a wooden spoon until it comes together as a dough, then tip onto a work surface and knead for 5-10 mins until smooth and elastic – add a little extra flour if the dough is too sticky. Put the dough in an oiled bowl, cover with a tea towel and leave in a warm place to rise for 1 hr until doubled in size. Tip the dough onto your work surface and knock out all the air. Knead the seeds into the dough until well distributed. Divide the dough into 12 pieces, then roll out each as thinly as you can. Heat a large frying pan, cook the flatbreads for 2 mins or until bubbles appear on the surface, then flip over and cook for 2 mins more. Once all are cooked, wrap in foil and keep for up to a day. Pop in a warm oven to reheat.','45 mins','30 mins'),
(955,'pitta bread','https://www.bbcgoodfood.com/recipes/pitta-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pitta-bread-74a1b32.jpg','Rustle up homemade pitta bread to serve with dips or as a side dish to mop up juices. You can easily make them ahead and freeze them for a fail-safe snack',16,8,1,2,20,'Mix the yeast with 300ml warm water in a large bowl. Leave to sit for 5 mins until the yeast is super bubbly then tip in the flour, salt and olive oil. Bring the mixture together into a soft dough. Don’t worry if it looks a little rough round the edges. Tip the dough onto a lightly floured work surface. Knead for 5-10 mins until you have a soft, smooth and elastic dough. Try to knead using as little extra flour as possible, just enough so that the dough doesn’t stick – this will keep the pittas light and airy. Once kneaded, place in a lightly oiled bowl, cover with a tea towel and leave to double in size, approximately 1 hour. Heat oven as high as it will go (ideally 250C/230C fan/gas 9) and put a large baking tray on the middle shelf of the oven to get searingly hot. Divide the dough into eight balls then flatten each into a disc with the palm of your hand. On a lightly floured surface, roll each disc into an oval, around 20cm long, 15cm wide and 3-5mm thick. Carefully remove the hot tray from the oven. Dust with flour then place your pittas directly onto it – you may have to do this in batches. Return swiftly to the oven and bake for 4-5 mins, or until the pittas have puffed up and are a pale golden colour. Wrap each hot pitta in a clean tea towel once it''s baked to keep it soft while the others cook.','20 mins','40 mins'),
(956,'pitta pizzas','https://www.bbcgoodfood.com/recipes/pitta-pizzas-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-775483_11-eb3ab52.jpg','Little ones will love these fun and super-easy pizza''s for lunch or a light supper',5,4,1,2,20,'Heat the grill. Spread each pitta bread with 1 tbsp tomato purée, sprinkle over the mixed herbs, then lay on the tomato slices. Divide the salami between the pittas, sprinkle over the cheese and grill until the cheese is golden and bubbling.','20 mins','25 mins'),
(958,'spicy courgette pitta pockets','https://www.bbcgoodfood.com/recipes/spicy-courgette-pitta-pockets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1284452_8-6b75c9b.jpg','Treat yourself to a solo supper of grilled vegetables, served in bread pockets with tahini, hummus, broad beans and harissa',5,1,1,2,20,'Toss the courgette slices in the harissa and olive oil, and season. Cook on a hot griddle pan for 2 mins each side or until tender. Transfer to a plate and set aside. Cook the broad beans in boiling water for 2 mins, drain under cold running water, then slip them out of their outer skins. Discard the skins. Put the broad beans, hummus and spring onion in a small bowl and mix to combine. In another bowl, mix the tahini, garlic, lemon juice and yogurt. Toast the pitta and split it to create 2 pockets. Spoon the hummus mix inside each pocket, followed by the spicy courgette slices and a drizzle of the yogurt mixture.','10 mins','6 mins'),
(959,'fluffy pittas','https://www.bbcgoodfood.com/recipes/fluffy-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Fluffy-pittas-1cf2989.jpg','Try making these Grecian-style pittas which are fluffier than the ones you''ll find in most shops. Fill with barbecued chicken or pork, salad and tzatziki for a satisfying fakeaway kebab',7,8,1,2,20,'A few hours before you want to eat, make the dough. Mix the flour, yeast, sugar, thyme, if using, and 2 tsp salt in a bowl with your fingertips. Pour over the milk, olive oil and 100ml lukewarm water and, still using your fingers, combine to make a dough. Tip onto a work surface and knead for 8-10 mins (or blitz in a stand mixer for 5 mins). Clean, then lightly oil a bowl, put the dough in the bowl and cover with a tea towel. Leave to rise for 1 hr or until nearly doubled in size. If you want to give yourself more time, the dough will take longer to rise in the fridge (at least 2 hrs). Divide the dough into eight equal pieces. One at a time, on a lightly floured surface, roll out to circles about 20cm wide, then brush with a little more olive oil and cook on the hot side of the barbecue for 2-3 mins on each side until puffed up and blistered. Alternatively, heat a frying pan or griddle (or two if you can) over a medium-high heat and cook, one by one in each pan, until bubbles appear on the surface after 1-2 mins. When the underside is golden, flip and cook for another 2-3 mins. Continue until all the pittas are cooked, wrapping them in foil as you go to keep warm.','20 mins','40 mins'),
(960,'toaster pitta pockets','https://www.bbcgoodfood.com/recipes/toaster-pitta-pockets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-355452_10-6a40f58.jpg','If your kids can''t wait until their next meal, these little pittas will keep them going in the meantime',5,1,1,2,20,'Pop the pitta into the toaster for 30 secs- 1 min, until just puffed but not crisp. Meanwhile, mix together the cheeses. Slice an opening at one end of the pitta and use a teaspoon or a knife to spread the cheese mixture in. Push in some of your favourite fillings, gently squash the pitta closed between your hands, then put back in the toaster – cut-side up. Toast for 1-2 mins until golden and crisp.','5 mins','No Time'),
(961,'quick pitta pizzas','https://www.bbcgoodfood.com/recipes/pitta-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-285501_12-02ab1ba.jpg','Kids will love this quick supper. Experiment with different toppings such as sweetcorn and ham',5,2,1,2,20,'Heat oven to 200C/180C fan/gas 6 and put a baking sheet inside to heat up. Spread each pitta with 1 tsp purée. Top with the tomatoes, shallot, chorizo and cheddar. Place on the hot sheet and bake for 10 mins until the pittas are crisp, the cheese has melted and the chorizo has frazzled edges. Scatter with basil, if you like, and serve with a green salad.','10 mins','10 mins'),
(962,'piri-piri chicken pittas','https://www.bbcgoodfood.com/recipes/piri-piri-chicken-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/piri-piri-chicken-pittas-3baabcf.jpg','Make these easy piri-piri chicken pittas for lunch or a quick dinner. All you need is four ingredients: chicken, piri-piri sauce, coleslaw and pitta breads',14,4,1,2,20,'Chop the chicken into 2cm cubes and toss in the piri-piri sauce. Heat the grill to high, push the chicken onto four skewers and grill on all sides for 10-12 mins in total or until cooked through and a little charred. Meanwhile, warm the pittas in a toaster. Slice the pittas in half, stuff with the coleslaw, then slide the chicken from the skewers and add to the pittas.','10 mins - 12 mins','N/A'),
(963,'fast feta & aubergine pizzas','https://www.bbcgoodfood.com/recipes/fast-feta-aubergine-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1250_12-6804657.jpg','This vegetarian pizza''s not only healthy - it''s also incredibly quick to make',5,4,1,2,20,'Toast the pitta breads in batches until lightly golden and just starting to crisp. Drain the aubergines, reserving some of the oil, and whizz half in a food processor until smooth. Spread aubergine paste over each pitta. Top with remaining aubergine slices and scatter on feta. Sprinkle over mint, drizzle with reserved oil and serve.','10 mins','No Time'),
(976,'vegan simnel cake','https://www.bbcgoodfood.com/recipes/vegan-simnel-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Vegan-simnel-cake-15ea937.jpg','Make a vegan-friendly version of a classic simnel cake this Easter so everyone can enjoy a treat. Use a kitchen blowtorch to easily achieve that signature burnished look on the marzipan',12,12,2,2,21,'Mix the soya milk with the lemon juice in a jug and set aside for a few minutes to thicken. Cut 150g of the marzipan from the larger block. Roll this out on a work surface lightly dusted with icing sugar until it''s just slightly smaller than the base of a 23cm cake tin. Using the dairy-free spread, butter a 23cm springform cake tin and line with a double layer of baking parchment. Heat the oven to 180C/160C fan/gas 4. Mix the dried fruit, lemon zest, chopped cherries and orange zest and juice together in a large bowl, tossing a few times until well combined. Beat the vegan spread with the brown sugar until pale using an electric whisk, then add the flour, almonds, baking powder, spices and vanilla. Tip in the milk mixture until you have a smooth batter. Fold in the fruit along with a pinch of salt and any juice from the bowl until well combined – don’t worry if the batter looks slightly curdled. Spoon half the batter into the prepared tin, then gently smooth the surface with the back of the spoon and top with the round of marzipan. Spoon over the remaining batter and smooth the surface again. Bake for 30 mins, then reduce the oven to 150C/130C fan/gas 2 and bake for another 1 hr 45 mins-2 hrs, or until a skewer comes out clean and the cake is well-risen and firm. Carefully remove from the tin and leave to cool completely on a wire rack. Cut into slices to serve. Will keep for two weeks in an airtight container. Brush the warm apricot jam over the cooled cake. Cut a 200g piece from the remaining marzipan and roll it out again as before. Cut out a 23cm circle using the base of the cake tin as a guide. Use the rolling pin to help you lift the marzipan circle over the cake and gently press it onto the surface (the apricot jam will help it stick). Divide the remaining marzipan into 11 pieces and roll into balls. Use a little more jam to stick the balls around the edge of the cake – these represent the 12 apostles, minus Judas. If you like, use a kitchen blowtorch to slightly burnish the marzipan balls and topping until just golden (do this carefully so they don''t burn).','30 mins','2 hrs and 30 mins'),
(964,'cheese & pepper pitta pocket','https://www.bbcgoodfood.com/recipes/cheese-pepper-pitta-pocket','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3840_11-e98a3ec.jpg','A fantastic high-speed or vegetarian snack to make your mouth water',5,1,1,2,20,'Trim off one of the long edges of the pitta bread, put the pitta in the toaster on a medium setting for a second or two, then pop it up and open it like they do in kebab houses, being careful it doesn’t split. (Warming the bread first makes this easier.) If the pitta doesn’t fit your toaster, use the grill, or try mini pittas. Stuff the cheese and peppers inside the pocket and close by gently pressing it down. Pop it back into the toaster on a medium setting (or under the grill) with the open side upwards. Toss the watercress in a drizzling of your favourite dressing or some olive oil. When the pitta pops up, cut it in half for easy eating, or leave it as it is. Allow it to cool for a moment as it is quite hot at first. Serve with the watercress and have a napkin at the ready – the yummy pepper juices may leak when you take the first bite.','10 mins','No Time'),
(965,'beetroot hummus with crisp pittas','https://www.bbcgoodfood.com/recipes/beetroot-hummus-crisp-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2103_11-a75e4f9.jpg','Think hummus means just chickpeas? Think again – you can make a gorgeous beetroot version',5,4,1,2,20,'Roughly chop the beetroot. Drain and rinse the chickpeas. Put these in a food processor with the garlic, coriander, lemon juice and olive oil. Blitz until fairly smooth. Taste and add freshly ground pepper, and salt if you wish. Divide the salad leaves between 6 plates. Spoon a little hummus onto each plate and scatter with a few olives. Drizzle a little oil over the leaves and sprinkle with a little black pepper, and salt, if you wish. Toast the pittas, cut into wedges and serve with the hummus.','10 mins','No Time'),
(966,'turkey & pepper pittas','https://www.bbcgoodfood.com/recipes/turkey-pepper-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-365525_12-a246988.jpg','Kids will love this tasty snack-supper, and we bet they''ll never know it''s superhealthy too',5,2,1,2,20,'Heat the oil in a wok or large frying pan and fry the turkey and chilli flakes for 5-6 mins. Add the peppers and spring onions and stir-fry until the turkey is cooked but the peppers still have crunch. Season. Divide the avocado and coriander between the pitta halves, then spoon in the turkey and pepper mix. Add a dollop of soured cream to each and serve straight away.','15 mins','12 mins'),
(967,'chicken pitta pockets with hummus drizzle','https://www.bbcgoodfood.com/recipes/chicken-pitta-pockets-hummus-drizzle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3806_12-866a164.jpg','Brilliant for snacks - just toast, stuff and eat!',5,2,1,2,20,'Mix together the shredded chicken breast, cucumber strips, grated carrot and a handful of cress. Set to one side. Mix together the hummus with the natural or Greek yogurt. Lightly toast the wholemeal pitta breads, cut in half and split open. Stuff the chicken mixture into pitta pockets and top each with the hummus drizzle.','10 mins','N/A'),
(968,'egg & veggie pittas','https://www.bbcgoodfood.com/recipes/egg-veggie-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pittas-815c508.jpg','Cram wholemeal bread pockets with healthy aubergine, beetroot and carrot, then add a garlic and dill yogurt and eggs',5,2,1,2,20,'Heat oven to 220C/200C fan/gas 7. Put the aubergine slices on a baking sheet, season, brush with oil and bake for 15 mins. Turn, spread with the harissa, and bake for another 5 mins. Meanwhile, carefully lower the eggs into a pan of boiling water, turn down the heat and simmer for 10 mins. Run under cold water to cool, peel and put to one side. In a bowl, mix the vinegar and agave or sugar with some seasoning, then tip in the beetroot, carrot and onion. In another bowl, mix together the yogurt, dill, garlic and seasoning. Toast the pittas and split them in half. Slice the eggs and put them inside the pittas with the spicy aubergine rounds and some of the beetroot salad. Spoon in the yogurt and serve with any of the remaining aubergine, salad and yogurt on the side.','15 mins','20 mins'),
(969,'mozzarella, ham & pesto pizzas','https://www.bbcgoodfood.com/recipes/mozzarella-ham-pesto-pizzas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4264_11-7fe7336.jpg','An easy snack or supper that everyone will love, and it''s ready in only 10 minutes',5,2,1,2,20,'Turn the grill to high, put the pittas on the grill rack and heat for about a minute while you slice the mozzarella into five. Turn the pittas over and spread each one with 1 tsp pesto, then top with a mozzarella slice. Pile the ham on top, so it looks quite ruffled, then tear the final mozzarella slice into four, put it on top of the ham and grind over black pepper. Return to the grill for 3-4 minutes more until melted and starting to turn golden.','10 mins','No Time'),
(1016,'salted dark chocolate, rye & courgette cake','https://www.bbcgoodfood.com/recipes/salted-dark-chocolate-rye-courgette-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/choc-cake-0f9577f.jpg','Not only are these chocolate squares a great way to use up a glut of courgettes, they''re topped with rye crumbs and sea salt for a chocolate cake with a twist',6,12,1,2,21,'Heat oven to 180C/160C fan/gas 4. Grease a 20 x 30cm cake tin and line with baking parchment. Mix the flours, cocoa powder, baking powder, bicarbonate of soda and sugar in a large bowl with ½ tsp salt. Shake the bowl a few times to encourage any large lumps of sugar to come to the surface and crush these with your fingertips. Mix the butter, buttermilk, eggs and vanilla in a jug and pour into the dry ingredients, add the courgette and chopped chocolate too. Stir everything together, then scrape into the tin. Bake for 45-50 mins until the cake is risen, and a skewer inserted to the centre comes out with just a few moist crumbs, but no wet cake mixture. Leave to cool in the tin for 15 mins, then transfer to a wire rack to cool. To make the icing, put the chocolate in a heatproof bowl, suspended over a pan of barely simmering water. Stir every now and then until melted. (Alternatively, melt in the microwave, stirring the chocolate every 20 secs so it doesn’t burn.) Remove the bowl from the heat and leave to cool a little. Meanwhile, put the butter and icing sugar in another large bowl and mash with a spatula to combine (this will prevent you covering the work surface in icing sugar), then whisk with a hand mixer until smooth. Sift in the cocoa and pour in the melted chocolate and milk, then mix again until smooth. If using the rye bread, heat oven to 160C/140C fan/gas 3. Blitz the bread in a food processer to chunky crumbs, then tip onto a baking tray, spreading them in an even layer. Bake for 15 mins, stirring halfway, until lightly toasted and crispy. Spread the chocolate icing over the cake, then scatter with rye crumbs and sea salt. Cut into squares to serve. ','30 mins','1 hr'),
(970,'smoked salmon & bean dip with crispy pittas','https://www.bbcgoodfood.com/recipes/smoked-salmon-bean-dip-crispy-pittas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2144_12-f2a07db.jpg','The pittas become crunchy in the oven making them unbelievably moreish',4,8,1,2,20,'Tip the beans into a food processor with the yogurt and blend until smooth. Add the salmon and pulse, keeping the salmon quite chunky. Tip into a bowl and stir in the dill, lemon juice and seasoning. This can be made up to a day ahead. For the pittas, preheat the oven to fan 180C/conventional 200C/gas 6. Tear the pittas onto 2 baking trays. Drizzle with theoil and sprinkle with the sea salt and dill. Bake for 7 minutes until crisp. These can be made a day ahead or just before serving. Once the pittas have cooled, pack them and the dip into seperate rigid containers. Transport the radishes packed in ice to keep them crisp. Put the bowl of dip on a platter and surround with the pitta. Serve the iced radishes separately.','30 mins','No Time'),
(971,'diy kofta burgers','https://www.bbcgoodfood.com/recipes/diy-kofta-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424790_11-d0de898.jpg','With these quick, easy and cheap koftas, there''s no excuse for hitting the kebab van',5,8,1,2,20,'Tip the mince into a large bowl (use a clean washing-up bowl if you don’t have anything big enough) with all the other burger ingredients and a good pinch of salt. Roll up your sleeves, get your hands into the mix and squelch everything together through your fingers until completely mixed. Pat the mix into 16 small burgers. These may now be frozen for up to 1 month or chilled up to a day ahead. To cook, heat grill to its highest setting and lay the burgers in a single layer on a baking tray (you may need to do this in batches, depending on how big your tray is). Grill on the highest shelf for 5-6 mins on each side until browned and cooked through. Pile burgers onto a platter and serve with all the accompaniments, so everyone can construct their own sandwich.','30 mins','20 mins'),
(972,'pea pakora pockets','https://www.bbcgoodfood.com/recipes/pea-pakora-pockets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424694_11-51b521e.jpg','Cheap and easy to prepare, but fun for everyone to put together - the yogurt dressing really makes these',5,4,1,2,20,'Heat oven to 200C/fan 180C/gas 6. Boil the potatoes for about 8 mins until tender, throwing in the peas for the final few mins. Drain well, pick out the potato, then return to the saucepan with a third of the peas. Add the curry powder and some seasoning, then mash together over a low heat – this will help to dry out the veg. Stir in the remaining peas. Using 2 tablespoons, shape the mix into rough rugby ball shapes (you should get about 16), then place on a baking sheet lined with baking parchment. Bake for 20 mins until golden and crisp around the edges. Mix the yogurt with the chopped mint. Warm the halved pittas, and toss the remaining mint leaves with the shredded lettuce. Serve the hot pakoras with all the extras for stuffing into the warmed pittas.','10 mins','30 mins'),
(973,'sweet potato falafels with coleslaw','https://www.bbcgoodfood.com/recipes/sweet-potato-falafels-coleslaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1081547_11-0c8d59a.jpg','Pittas bursting with homemade falafels and salad make a low-fat lunch or light supper for less than a pound per serving',5,4,1,2,20,'Heat oven to 200C/180C fan/gas 6. Microwave sweet potato whole for 8-10 mins until tender. Leave to cool a little, then peel. Put the potato, cumin, garlic, ground and fresh coriander, lemon juice and flour into a large bowl. Season, then mash until smooth. Using a tablespoon, shape mix and into 20 balls. Put on an oiled baking sheet, bake for around 15 mins until the bases are golden brown, then flip over and bake for 15 mins more until brown all over. Meanwhile, stir the vinegar and sugar together in a large bowl until the sugar has dissolved, toss through the onion, carrot and cabbage, then leave to marinate for 15 mins. To serve, toast the pittas, then split. Fill with salad, a dollop of hummus and the falafels.','20 mins','50 mins'),
(974,'turkish-style lamb','https://www.bbcgoodfood.com/recipes/turkish-style-lamb','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7172_11-25d3c42.jpg','Bring the taste of Turkey to your kitchen with these fun lamb pittas, great hand held food',5,4,1,2,20,'Heat grill to high. Season the lamb, then grill for 2 mins on each side until browned, but still very rare. Meanwhile, mix the seasoning, oregano and half of the mint into one of the tubs of yogurt, smother this over the lamb, then return to the grill for another 2-3 mins or until the yogurt is blistered and the meat is cooked to your liking. Leave the meat to rest on a board for a few mins while you toast the pittas, shred the lettuce and thinly slice the red onion. Stir the rest of the mint into the second tub of yogurt. Thickly slice the meat and stuff into the pitta bread with the salad and minted yogurt. Squeeze over lemon juice before tucking in, if you like.','15 mins','N/A'),
(977,'number birthday cake','https://www.bbcgoodfood.com/recipes/number-birthday-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/08/Number-birthday-cake-cd437e9.jpg','Make a child''s birthday special with this number cake decorated with mini meringues, biscuits, macarons and raspberries - and any other treats they like!',65,12,2,2,21,'Heat the oven to 200C/180C fan/gas 6. Butter a deep 30 x 23cm rectangular tin. Beat the butter and sugar together in a stand mixer until pale, about 5 mins. Add the eggs, one at a time. Sift in the flour and baking powder, and add the lemon zest and milk. Fold in using a large metal spoon. Spoon into the tin. Bake for 35-40 mins until a skewer inserted into the centre comes out clean. Leave to cool for 10 mins in the tin, then turn out onto a wire rack to cool completely. Transfer the cake to a board and cut in half through the equator. Put one half on a baking tray. Mix the caster sugar with the lemon juice, then brush this over the cut sides of the cake halves. For the buttercream, beat the butter in a stand mixer to loosen. Slowly add the icing sugar and a splashof water and continue to beat until pale. Spread the jam over the base cake layer, then carefully spread a third of the buttercream on top. Sandwich with the other cake layer, then cut the cake into the shape of your desired number using a serrated knife, trying to waste as little of the cake as possible (offcuts can be eaten as a small treat). Spoon the remaining buttercream into a piping bag fitted with a star nozzle and pipe swirls on the cake. Decorate with your chosen toppings. Will keep chilled for up to three days.','40 mins','40 mins'),
(978,'rainbow layer cake','https://www.bbcgoodfood.com/recipes/rainbow-cake-2','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/rainbow-cake-main-e76d826.jpg','Show support for the LGBTQ+ community by making this beautiful rainbow layer cake, ideal for sharing at Pride festivities',5,12,2,2,21,'Butter three 20cm round cake tins and line their bases with baking parchment. Heat the oven to 190C/170C fan/gas 5. Beat the butter and sugar together in a large bowl until light and smooth. Gradually beat in the eggs until combined, then mix in the vanilla. Fold in the flour and baking powder to fully combine. Stir in the milk to loosen. Divide the mixture between the three tins, and bake the sponges for 20 mins until golden and a skewer inserted into the centre of each cake comes out clean. Leave to cool in the tins for 10 mins, then transfer to a wire rack to cool completely. Meanwhile, make the Swiss meringue buttercream. Put the egg whites and sugar in a heatproof bowl set over a small pan of gently simmering water, ensuring the bowl doesn’t touch the water. Whisk for 3-4 mins until the sugar dissolves. Remove from the heat and tip the mixture into the bowl of a stand mixer, or use an electric whisk, and whisk for 10 mins on a medium-high speed until you have a thick meringue. Gradually whisk in the softened butter 1 tbsp at a time until fully incorporated and the buttercream is fluffy. Mixin the vanilla extract. Put one of the sponges on a plate or cake stand, top it with 3-4 tbsp of the buttercream and spread out so that it’s thicker at the edge. Add half of either the caramel or lemon curd and spread over the icing on the middle part of the cake – don''t spread to the edges. Add the next sponge, and repeat. Top with the final sponge. Smooth a thin layer of the buttercream around the cake – this is called a crumb coat. Chill in the fridge for at least 45 mins, then smooth another layer of buttercream around the cake using a palette knife to fully cover the sponge, reserving half for decoration. Put the cake and reserved buttercream back in the fridge for another 45 mins-1 hr. Divide the remaining buttercream between 11 small bowls and mix in a different gel colouring to 10 of them, leaving the last bowl white. Use a different piping bag for each colour, either cutting a 1cm hole in the end or applying a round nozzle. Pipe one row of buttercream blobs in alternating colours from the top to the bottom of the cake, then use a teaspoon or small palette knife to very lightly smear each blob of frosting away from you. For the second row, choose the colour that is next in sequence to the colour alongside it, piping over the thinnest part of that smeared blob. As you continue to pipe around the cake following this pattern, you will create a swirl effect of colours. Continue the previous piping method in a circle on top of the cake, but this time smearing the icing in towards the middle of the cake until the cake is covered, then it''s ready to slice and serve. The cake will keep in an airtight container for up to three days.','1 hr and 45 mins','20 mins'),
(979,'rainbow zebra cake','https://www.bbcgoodfood.com/recipes/rainbow-zebra-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rainbow-zebra-cake-663a548.jpg','Celebrate our 30th birthday with us – or your own special occasion – with this impressive rainbow sponge. Iced with buttercream and topped with coloured sprinkles, it''s a cake that has the wow factor',14,12,1,2,21,'Heat the oven to 180C/160C fan/gas 4. Butter and line three 20cm loose-bottomed sandwich cake tins with baking parchment. To make the sponge, beat the butter and sugar together in a stand mixer or using an electric whisk for 5-8 mins or until light and fluffy. Whisk in the eggs, one at a time, beating well after each addition. Fold in the flour, baking powder and milk to loosen. Divide the mixture between three mixing bowls, weighing for accuracy if you like. Colour one of the batters blue and another pink, starting with a little colouring, then adding more for a deeper finish. Leave one of the bowls of batter uncoloured. Get the three cake tins and bowls of batter ready in front of you. Spoon a heaped tablespoon of blue batter into the centre of the first tin, then pink in the second, and plain in the third. Working quickly so that it doesn’t spread too much, add another dollop of a different coloured batter on top of each one. Don’t wait for the batter to settle or spread, just keep alternating the colours until you run out. The weight of each layer will cause the batter to spread as you go, so very gently tap and shake the cake tins to even out the mixture, then bake in the centre of the oven for 25-30 mins or until firm to the touch. Turn out top-side down onto wire racks straightaway, then leave to cool completely. For the icing, put the butter, icing sugar and vanilla seeds into a stand mixer fitted with the paddle attachment or put in a bowl and use an electric whisk. Beat for 5 mins or until fluffy and aerated. Add the cream cheese and beat briefly until just combined. Place one of the cakes on a cake stand or serving plate and spread 2 tbsp of the buttercream on top, then repeat with the other sponges. Spread a smooth layer of the icing over the top and sides of the cake, using a large palette knife, leaving some slightly less iced spots for a ‘naked’ finish. Top with sprinkles and candles, if you like. Will keep in an airtight tin for three days.','50 mins','30 mins'),
(980,'chocolate & vanilla celebration cake','https://www.bbcgoodfood.com/recipes/chocolate-vanilla-celebration-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Chocolate-and-vanilla-celebration-cake-a79b7f2.jpg','Round off the Easter weekend with this stunning showstopper cake. The sponges taste even better after a day or two, so it’s a great get-ahead recipe',6,15,2,2,21,'Heat the oven to 180C/160C fan/gas 4. Oil three 20cm cake tins and line with baking parchment. Weigh out the flour, cocoa powder, baking powder, bicarb and sugar into a large bowl, add a pinch of salt and whisk gently to combine. Shake the bowl to bring any lumps of sugar to the surface and squeeze these with your fingertips. Measure the oil, milk, vinegar, coffee and vanilla into a jug, then add the eggs and whisk to combine. Pour the wet ingredients into the dry and stir with a whisk until smooth, making sure there are no pockets of flour at the bottom of the bowl – the mixture will be quite runny. Divide the batter evenly between the prepared tins, then bake on the middle shelf of the oven (or middle and top if they don’t all fit on one shelf) for 25 mins. Swap the tins around and bake 5 mins more. Check the cakes are cooked by inserting a skewer into the centre of each – if any wet batter clings to the skewer, return the tins to the oven for another 5 mins, then check again. Leave to cool in the tins for 30 mins, then turn the sponges out onto wire racks to cool completely. If making ahead, wrap the cooled sponges well and store in an airtight container for up to three days, or the freezer for up to two months. For the buttercream, beat the butter using an electric whisk or in a stand mixer for a few minutes until smooth and creamy. Add half the icing sugar and beat again to combine. Add the remaining icing sugar, the vanilla and 4 tbsp milk and beat again to combine, adding a splash more milk if needed to make a smooth, fluffy buttercream. Divide between three bowls. Add a few drops of different food colouring paste to each bowl, then mix well – we used pastel pink, purple and yellow. Spoon the coloured buttercream mixtures into three separate piping bags and snip off the tips. Pipe a dot of buttercream onto a cake stand or board and place a sponge on top to help keep it in place. Pipe small blobs of purple buttercream all over the top of the sponge by holding the piping bag directly above the cake, about ½cm from the surface, and squeezing the bag while pulling upwards to create a small peak. Place the second sponge on top, then repeat the process with the pink buttercream. Add the third sponge on top, then neatly pipe blobs of yellow buttercream over the surface. Decorate with edible flowers, sugared almonds or mini chocolate eggs, if you like. Will keep in a tin at room temperature for up to five days.','35 mins','30 mins'),
(981,'unicorn cake','https://www.bbcgoodfood.com/recipes/unicorn-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Unicorn-cake-32e0971.jpg','Stop people in their tracks with a stunning unicorn cake. With a light sponge and salted caramel buttercream, it looks fabulous and tastes divine too',46,12,2,2,21,'Start by making the unicorn''s horn and ears – they''ll need to dry completely before use. Roll 225g white fondant into a long sausage shape, with one end slightly thicker than the other. Starting from the thicker end, twist the sausage into a tight coil until you have a horn shape. Pinch the small end upwards into a pointed tip. Next, divide the remaining white fondant into two pieces. Roll each one out into a rough oval on a sheet of baking parchment, then pinch the top end of each oval upwards to make a point. Set the ears aside with the horn and leave to dry while you make the cake. Heat the oven to 180C/160C fan/gas 4. Butter the base of three 20cm sandwich tins and line with baking parchment. Beat the butter, sugar and vanilla together in a large bowl with an electric whisk for 8-10 mins until pale and fluffy. Add the eggs, one at a time, beating well after each addition. Sift in the flour, baking powder and a pinch of salt, and fold in using a large metal spoon. Add just enough of the milk to create a soft dropping consistency. Divide the batter between the prepared tins and smooth the tops with a spatula. Bake on the middle shelf of the oven for 25-30 mins, or until golden and firm to the touch. Leave to cool slightly in the tins, then transfer to a wire rack to cool completely. While the cake is cooling, make the buttercream. Beat the butter, icing sugar and vanilla together until pale and fluffy, beating in 4-6 tbsp hot water to loosen the mixture if necessary. Put one of the cooled sponges on a cake board or stand and spread a little of the buttercream on top using a palette knife. Gently spread over half the salted caramel, if using, then sandwich with another sponge. Repeat the process with a little more buttercream, the remaining salted caramel (if using) and the third sponge. Spread the outside of the cake with a thin layer of buttercream – this doesn''t have to be neat. Chill for 30 mins. Spread a thick, even layer of buttercream over the chilled cake using a palette knife. Divide the remaining buttercream into three bowls and colour each one differently using the gel food colouring. Ours were light pink, purple and dark pink. Put a little gold lustre dust into a bowl, add a drop of water and stir to make a gold paste. Using a small paintbrush, paint the unicorn horn gold using the paste, as well as the inside of the ears, if you like. Carefully place the gold horn in the middle of the iced cake, with the ears on either side. Fit three piping bags with three different nozzles (we used a round nozzle and two different-sized star nozzles) and fill each with a different colour of buttercream. Pipe each colour around the ears and horn and all over the top of the cake, coming down one side and over the edge of the front – this is the unicorn''s mane. Decorate with some sprinkles, if using. Divide the black fondant in two pieces, and roll each into a thin sausage shape. Shape each into an arch, then press onto the front of the cake for eyes. If you like, curve the outer edges of the eyes upwards for eyelashes. Cut into slices and serve. Will keep in the fridge for up to three days.','1 hr and 30 mins','30 mins'),
(982,'matcha mousse cake','https://www.bbcgoodfood.com/recipes/matcha-mousse-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/matcha-mousse-cake-6f978c9.jpg','A popular ingredient in Asian desserts, matcha powder is made from finely ground green tea leaves. This pretty cake with white chocolate glaze and cherries is a bit of a challenge but worth it',6,12,3,2,21,'Heat oven to 180C/160C fan/gas 4. Grease two 20cm cake tins (one should be at least 8cm deep) with oil, and line the base and sides with baking parchment. Tip the egg yolks, caster sugar and vanilla into a large bowl, and the egg whites into a second large bowl. Combine the flour, almonds and 1 /4 tsp salt in a third bowl. Using an electric hand whisk, beat the egg whites until doubled in size and holding soft peaks. Transfer the beaters to the egg yolk bowl (no need to clean the beaters first) and whisk until the mixture is thick and pale. Lift the beaters and drizzle the mixture around the bowl – a ribbon should sit on the surface for 2-3 secs. With a large metal spoon, transfer a spoonful of egg white to the yolks, and mix to loosen the consistency. Add the remaining whites and gently fold into the yolks, trying to retain as much volume as possible. Stop before the mixture is totally combined. Sprinkle the flour and almond mixture evenly over the surface, then drizzle the cooled butter around the edge of the bowl. Fold everything together until just combined, making sure you scoop the spoon right down to the bottom of the bowl to catch the butter and any pockets of flour. Divide the mixture between the cake tins, level the surfaces, then bake on the middle shelf of the oven for 22-25 mins. Test the cakes are done by inserting a skewer into the centre – it should come out clean. Cool in their tins on a wire rack for 30 mins, then remove the tins and parchment, and leave to cool completely. Boil the kettle. Put the gelatine in a bowl of cold water and set aside to soften. Pour 150ml cream and 50ml hot water into a saucepan. Add the matcha powder and whisk over a gentle heat until well combined and steaming. Remove from the heat. Squeeze the water from the gelatine leaves and add them, one by one, to the hot matcha cream, whisking until they’re dissolved. Pour the warm cream into a bowl and put in the fridge to cool to room temperature – don’t leave it too long or the gelatine will set. Line the deepest of your 20cm cake tins with a double layer of cling film, ensuring the sides are well covered. Put one of the sponges in the tin, flattest-side down. Pour the remaining 450ml cream into a bowl and add the icing sugar. Whip until the cream is holding soft peaks, then fold through the cooled matcha cream. Scrape the cream into the cake tin and level the surface. Put the remaining sponge on top, flattest-side facing up, pushing the sponge gently into the cream. Wrap the tin in cling film and chill for at least 4 hrs. Put the cream, chocolate and matcha powder in a saucepan and heat very gently, stirring until the chocolate has melted and the matcha is well mixed in. Set aside to cool and thicken for 10 mins. Remove the cake from the fridge and unwrap the tin. Invert the cake onto a cake stand or plate and remove from the tin and the cling film. Using a palette knife held at a 90-degree angle to the side of the cake, scrape around the edge to remove any excess mousse and give the cake a smooth finish. Using baking parchment, create a tight collar, sitting about 2cm taller than the cake. Pour the cooled chocolate glaze over the top, smoothing it to the edge – the collar should prevent it from dribbling down the sides. Chill for 30 mins more, or for up to 24 hrs. Carefully pull off the parchment collar. Hold a chopping board (or another object with a straight edge) over half the cake and dust the exposed surface with icing sugar. Arrange fresh cherries and cherry blossom or another edible flower on top, if you like, and serve immediately.','55 mins','30 mins'),
(983,'mermaid cake','https://www.bbcgoodfood.com/recipes/ombre-mermaid-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mermaid_cake_sliced-ce72925.jpg','Create a cake with the wow factor with our colourful ombre mermaid. It''s a baking challenge but makes a magical party cake for kids and grown-ups alike',46,14,3,2,21,'If you''re making the mermaid tails, melt the white chocolate in a heatproof bowl in 20-second bursts in the microwave, stirring after each turn. Pour into the tail moulds and small shells, and put on a small tray flat in the freezer for 15-20 mins until set. You can repeat this for four tails if you only have one mould. Once set, remove from the mould, mix the lustre with ¼ tsp of water and paint it onto the mermaid tails (see tip, below). Put back on a plate or tray in the freezer until needed. Heat the oven to 180C/160C fan/gas 4. Butter and line the base of three 18cm sandwich cake tins with baking parchment. Using a stand mixer or electric hand whisk, beat together the butter, sugar, eggs, vanilla, baking powder and flour until you have a fluffy mix. Divide the mixture evenly between three bowls and mix a few drops of colouring into each bowl until fully combined, making each bowl a different shade of purple, getting lighter. Pour each coloured sponge mix into separate tins and smooth over the tops. Bake for 25-30 mins until a skewer inserted into the middle comes out clean. After 10 mins, remove the cakes from their tins and transfer to a cooling rack, top-side facing down, and leave to cool. Don’t worry if the outside doesn’t look purple, the inside will! While the cake is cooling, make the icing. Beat together the icing sugar and butter until pale and fluffy, mixing in 1 tbsp warm water to loosen the mixture if necessary. Set half of the icing aside in a bowl. Divide the other half of the icing between three bowls and colour each a different shade of blue, a dark, medium and light. Stack the sponges, spreading most of the white icing over each one using a palette knife, then use the palette knife to roughly coat the outside of the cakes with the remaining white icing to create a crumb coat. Chill for 30 mins. Put each of the blue icing shades in separate piping bags and cut a large 2cm hole at the bottom. Starting with the dark shade at the bottom of the cake, pipe an even ring onto the whole cake, two-three times around. Continue working your way up the cake with the icing getting lighter as you reach the top of the cake. Using a small palette knife, work your way around the join in between each row, gently pushing to join each line together. Save some icing in the piping bags to decorate the top. Smooth over the cake with a large palette knife or cake scraper to create a smooth ombre icing, keeping the excess to decorate the top. You can either mix all the leftover icing together and put in a clean piping bag with a star nozzle to get a multicoloured effect, or pipe individual shades on the top. Add a few dots around the top of the cake in the varying blue colours to look like waves (using some of the white to look like the top of a crashing wave), and dot around the pearls, sprinkles, shells and mermaid tails, pushing them into the icing on the side of the cake, and on top to finish.','1 hr and 30 mins','30 mins'),
(984,'chocolate drip cake','https://www.bbcgoodfood.com/recipes/malted-chocolate-drizzle-honeycomb-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/malted-chocolate-drizzle-honeycomb-cake_3-5760e58-scaled.jpg','This malted drip cake is smothered in cream cheese icing and drizzled with a dark chocolate ganache – a stunning multi-layered centrepiece for a big occasion',6,20,3,2,21,'First make the malt sponges. Heat oven to 180C/160C fan/gas 4. Grease and line the bases of 2 x 20cm cake tins with baking parchment. Put the butter and sugar in a large bowl and whisk until pale and fluffy. Add the eggs, one at a time, whisking well between each addition. Tip the flour, malt and baking powder into the bowl with ¼ tsp salt and fold together, then add the yogurt and vanilla, and give everything a final stir. Divide the cake mixture between the 2 tins, level the tops and bake on the middle shelf for 25-30 mins or until a skewer pushed into the centre of the cakes comes out clean. Cool for 10 mins, then remove from the tins, peel off the baking parchment and transfer to a wire rack to cool completely. Wash the tins. Now make the chocolate sponges. Grease and line the base of the tins as before and boil the kettle. In the same bowl (don’t worry about washing it), cream the butter and sugar as before, adding the eggs, one at a time, and mixing until combined. Sift in the flour, cocoa and baking powder with ¼ tsp salt. Fold the mixture together, then add the yogurt, melted chocolate and 100ml boiling water. Stir until combined, then divide between the tins. Bake for 25-30 mins, testing with a skewer as before. Cool in the tins for 10 mins, then turn out, remove the baking parchment and cool completely. The sponges can be made up to 2 days before being iced, cooled and wrapped tightly in cling film. Make the honeycomb. Grease a baking tray with a little butter or oil. Put the sugar and golden syrup in a large pan. Warm over a medium heat and leave to bubble to a liquid caramel, but don’t stir. When you’re happy with the colour (the darker it gets, the more intense the flavour will be), add the bicarbonate of soda and quickly stir into the syrup. Before the bubbles die down, pour the foaming mixture onto the baking tray, then set aside for 30 mins to cool and firm up. Make the icing. In your largest mixing bowl, beat the butter with half the icing sugar, the malt and vanilla until smooth. Add the remaining icing sugar and the cream cheese, and beat again until well combined – don’t overmix or the icing may become runny. You’re now ready to assemble. Put one of the sponges on a cake stand or cake board, sticking it down with a small blob of icing. Use ¼ of the icing to stack the cakes, alternating between the malt and chocolate sponges. Place the final sponge on top, flat-side up. When assembled, use a palette knife to cover the entire cake with a thin layer of the icing, filling any gaps between sponges, but don’t worry about completely covering the sponges at this stage. This is called a crumb coat and ensures that your final layer is crumb-free. Make space in your fridge and chill the cake for 30 mins to firm up the icing. Once the icing is chilled, use the remaining icing to completely cover the cake. This is easiest if you pile the icing on top of the cake, then use a palette knife to ease it over the edge and down the sides. You can make it as smooth or as rough as you like. Chill for another 30 mins. Make the drizzle. Meanwhile, put the dark chocolate in a bowl and heat the cream in a small pan until just steaming. Pour the cream over the chocolate and leave to melt for 5-10 mins. Stir to make a glossy ganache, then set aside to firm up a little – you want the ganache to be pourable but not too runny, to make drizzly droplets down the side of the cake. When the ganache is the correct consistency, remove the cake from the fridge and spoon the ganache over the top edge of the cake, encouraging it to drizzle down the side with your spoon – start at the back of the cake to get the hang of it. Fill in the middle of the top of the cake with chocolate too. Break the honeycomb into tall shards and stack them on top of the cake. Finish with whole and halved Maltesers, adding a few around the base, along with some honeycomb crumbs. The cake is best served within 2-3 hrs of assembling, but will still taste delicious for up to 3 days. Store leftovers in the fridge and allow to come back to room temperature before serving.','2 hrs','1 hr'),
(985,'pimm''s cake','https://www.bbcgoodfood.com/recipes/pimms-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pimms-cake-2c7a35c.jpg','Yes, you read right, Pimm''s in cake form. Smooth strawberry and lemon buttercream coupled with cucumber essence make this the ultimate taste of summer',29,18,2,2,21,'Put the sliced and halved strawberries for the filling and decoration into a small bowl and pour over the Pimm''s. Leave to soak in for at least one hour or overnight. Heat oven to 190C /170C fan/gas 5. Line 3 x 20cm sandwich tins with baking parchment and grease with butter. Cut the whole cucumber in half lengthways and using a teaspoon scrape out and discard the seeds. Roughly chop the rest of the cucumber and puree in a blender with the lemon juice. In a large bowl and using electric beaters whisk up the softened butter with the sugar until pale and light. Continue whisking as you add the eggs, one at a time, whisking in between additions. Once all the eggs have been added pour in the cucumber purée, lemon zest and vanilla extract. Don’t worry if the mixture splits - it will come back together when you add the flour. Fold in the flour and baking powder and whisk once again briefly until there are no lumps of flour remaining then divide the mixture between the three tins. Bake in the oven for 30-35mins or until a skewer pushed into the middle of the cakes comes out clean. Take the cakes out of the oven and leave to cool in their tins for five minutes then turn out onto wire racks to cool completely. While the cakes are cooking and cooling make the buttercreams. To make the strawberry buttercream, mash the strawberries in a small bowl with a fork until mushy. In a larger bowl whisk up the butter and half the icing sugar until smooth, then whisk in the mashed strawberries followed by the remaining icing sugar. Whisk until smooth and pale pink and place in the fridge until needed. Repeat the process with the lemon buttercream. Once the cakes are completely cold, put one of the cakes onto a cake turntable and add about one third of the lemon buttercream, spread over the surface evenly then top with half of the soaked strawberry slices. Next, spread one third of the strawberry buttercream onto another of the cakes and top with the remaining strawberry slices. Carefully stack the two filled layers with the final cake on top. Transfer the remaining buttercreams to disposable piping bags and snip off the ends. Pipe the strawberry buttercream in rings around the outside of the cake, starting at the base. Once you reach three quarters of the way up, switch to the lemon buttercream and continue piping onto the top of the cake. Smooth over the top using a palette knife then smooth the sides with either a large palette knife or, better still a pastry scraper. Use long sweeping movements and gently turn the cake turntable so that you get a smooth finish but a subtle gradient effect where the two colours meet. Scrape any excess icing into a bowl. To finish, top with the remaining soaked strawberry halves and the slices of cucumber.','1 hr','30 mins'),
(986,'summer party cake','https://www.bbcgoodfood.com/recipes/summer-party-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/summer-celebration-cake-423f402.jpg','This generous cake is big enough to feed a crowd and is a combination of all our favourite summer ingredients: strawberries, Victoria sponge, meringues, fresh cream, white chocolate and buttery shortbread',6,20,3,2,21,'Heat oven to 180C/160C fan/gas 4. Grease and line two x 23cm tins with baking parchment. Beat the butter, sugar and vanilla in a tabletop mixer (or with a hand mixer or wooden spoon) until the mixture is very light, pale and fluffy.  Gradually mix in the beaten eggs, scraping down the side of the bowl after each addition, until fully combined. If using a mixer, on slow, add the flour until just combined - don''t over-beat as it can make the sponge heavy. If you''re not using a mixer, fold in the flour with a large metal spoon.  Divide the mixture between the tins and bake in the oven for 30 mins or until the cakes come away from the side and the skewer inserted into the centre comes out clean. Remove and cool in the tin for 5 mins. Turn out onto a wire rack and remove the parchment to allow the steam to escape and the cakes to cool. Meanwhile, make the decorations.  Now assemble your cake. Before you split the sponges, trim the domed tops off each cake with a bread knife. Use the same knife or a cake wire, to split each sponge horizontally, so you''re left with four sponge layers. A good tip is to cut a marker line on each sponge using a bread knife, so that once you''ve cut the sponges and filled them, you can use the line to put them back together in the right place. That way, if your cutting was a little wonky, it won''t make the cake uneven.  Whip the double cream with the icing sugar and vanilla until light and fluffy, and just holding shape. Put the bottom layer on a cake stand or board. Spread with a thin layer of jam, then spread or pipe on a good layer of cream. Arrange an even layer of strawberries neatly round the edge, then dot the middle of the sponge with more fruit. Repeat with two more of the cake layers. For the top layer, add the sponge and jam, but neatly pipe the cream over. Gently press gold leaf onto the chocolate on the strawberries, a few meringues and some of the bark.  Arrange the chocolate-coated strawberries on top of the creamy sponge, then nestle in shards of bark, meringues and biscuit. Finish with a sprinkling of crushed meringue and a little more of the freeze-dried strawberries (from the chocolate bark recipe). Serve and eat within a few hours. ','40 mins','30 mins'),
(987,'funfetti cake','https://www.bbcgoodfood.com/recipes/funfetti-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fun-cake-9735009.jpg','Perfect for a party, this rainbow-speckled cake screams ''celebrate''! Pipe creamy vanilla icing and scatter over sprinkles for a showstopping cake that adults and kids will love',1,20,2,2,21,'Preheat the oven to 180C/160C fan/gas 4 and grease and line 4 x 20cm cake tins. If you don’t have 4, halve the sponge recipe and bake in 2 batches. Put the sugar, butter and vanilla paste in a large bowl and beat with an electric whisk (or in a table top mixer) until pale and fluffy. Add the eggs gradually, beating between each addition until fully incorporated. Add a tbsp or 2 of flour if the mixture looks like it’s curdling. Sift in the flour and baking powder with a tsp of salt and fold into the cake batter. Pour in the milk and beat to loosen the mixture. Scatter over the sprinkles and ripple through the cake batter before dividing between each cake tin. Bake for 25-30 mins until golden and the sponge springs back when you press it lightly. Swap the tins around in the oven after 15 mins to ensure they cook evenly. Cool on wire racks completely before icing. Meanwhile, make the icing by beating the butter with half of the icing sugar until combined and fluffy. Add the cream cheese and the rest of the icing sugar, beating again until fully combined. Drizzle over the vanilla paste and beat until incorporated. Put a large round nozzle into a piping bag and spoon in the icing. When the cakes have cooled put a blob of icing onto a cake board and sit 1 of the sponges on top. Pipe blobs of vanilla icing in a circle covering the whole base of the sponge, then top with the next sponge. Repeat with all 3 layers, scattering over some extra sprinkles on the top. Serve in slices.','30 mins','25 mins - 30 mins'),
(988,'rosewater & raspberry sponge cake','https://www.bbcgoodfood.com/recipes/rosewater-raspberry-sponge-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rosewater-raspberry-sponge-cake-d57286d.jpg','Eloise Shardlow creates a delicate, floral twist on the classic sponge cake. Dried rose petals, rose icing and raspberries make for a showstopping decoration',5,10,1,2,21,'Heat oven to 180C/160C fan/gas 4. Lightly grease two 20cm cake tins and line with baking parchment. In a large mixing bowl, beat the butter and sugar together until pale and fluffy. Gradually add the eggs a little at a time, scraping down the side of the bowl after each addition. If the mixture starts to curdle, add 1 tsp flour. Add the vanilla extract, mix in half the flour, then fold in the rest. Divide the mixture between the cake tins and bake for 20-25 mins – a skewer inserted into the middle of the cakes should come out clean. Leave to cool in the tins for 10 mins, then turn out onto a wire rack to cool completely. Meanwhile, make the filling. Whisk the cream to soft peaks, adding the rosewater as you go, then fold in the jam, being careful not to over-mix. Place one of the sponges on a serving plate and top with the cream mixture. Scatter with the raspberries, then top with the other sponge. For the icing, mix together the icing sugar, 2 tbsp water and the rosewater, and stir in the juice from a few crushed raspberries. Drizzle over the cake, arrange the rose petals and the remaining raspberries on top and dust with icing sugar.','35 mins','25 mins'),
(1264,'vegan chocolate party traybake','https://www.bbcgoodfood.com/recipes/vegan-chocolate-party-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-traybake-f03821c.jpg','Make an indulgent vegan chocolate traybake the whole family will love. This simple bake is perfect for sharing at parties or a special occasion ',107,12,1,2,18,'Heat the oven to 180C/160C fan/gas 4. Oil a 23 x 33cm baking tin (at least 4cm deep) and line the bases and sides with baking parchment. Whisk the milk and vinegar together in a jug – the milk should curdle slightly. Set aside. Whisk the sugar, oil and vanilla extract together in a bowl, then whisk in the yogurt, making sure to break down any sugar lumps. Pour in the soured milk and mix well. Sift the flour, cocoa powder, baking powder, bicarbonate of soda and ¼ tsp salt into a separate bowl and stir well to combine. Gradually whisk the wet ingredients into the dry until you have a smooth batter, but be careful not to over-mix. Pour the batter into the tin and bake for 30-35 mins, until well risen and springy, and a skewer inserted into the centre comes out almost clean. A few sticky crumbs are fine, but the mixture should not be wet. Leave the cake to cool in its tin for 20 mins, then carefully turn out onto a wire rack to cool completely. It will be delicate so be gentle. The sponge will keep, covered, at room temperature for up to two days. For the vegan buttercream, melt the chocolate in the microwave or a bowl set over a pan of simmering water. Leave to cool. Beat the spread and vanilla on high speed in a stand mixer for a few minutes until pale and fluffy. Add the icing sugar gradually, beating on slow to start with, then turning up the speed to max until the mixture is light and creamy. Pour in the cooled chocolate and combine thoroughly, add a splash of plant milk if the icing is very stiff. Chill the buttercream for at least 30 mins before using. When the cake is completely cool, spread the icing over the top and decorate with the sprinkles, if you like. Keep the cake in the fridge to stay firm, then remove 1 hr before serving. Will keep, covered, in the fridge, for up to three days.','30 mins','45 mins'),
(989,'chocolate & raspberry birthday layer cake','https://www.bbcgoodfood.com/recipes/chocolate-raspberry-birthday-layer-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-raspberry-cake-a1392fd.jpg','Who could resist our chocolate and raspberry cake? Like a Victoria sponge but better, try budget-friendly frozen raspberries for the cream',12,12,1,2,21,'Heat the oven to 180C/160C fan/gas 4. Oil and line two round 20cm springform cake tins with baking parchment. Whisk the oil, sugar, eggs and milk in a bowl until smooth. Sieve the flour, cocoa and bicarb into another large bowl, then gradually mix in the wet ingredients.  Divide the mixture between the tins and bake for 35-40 mins until the cakes are risen and spring back when pressed. Leave to cool in the tins for 10 mins, then transfer to a wire rack to cool completely. For the raspberry layer, stir the jam and the defrosted raspberries together. Once the cakes are cool, whip the cream with the sugar to soft peaks, then gently fold half the raspberry mixture through the cream to create a ripple effect.  Spoon most of the reserved raspberry mixture over one of the cakes, then dollop on half of the cream. Smooth over with a palette knife, then place the other sponge on top. Swirl over the remaining cream and swirl the last of the raspberry mixture through it. Will keep in the fridge for two days.','20 mins','40 mins'),
(990,'gravity-defying sweetie cake','https://www.bbcgoodfood.com/recipes/gravity-defying-sweetie-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/anti-gravity-cake_0-4845f38.png','Anti-gravity cakes are a great option for birthdays or parties - this stunning chocolate sweetie cake will delight kids and grown-ups alike',6,12,2,2,21,'Heat oven to 180C/160C fan/gas 4. Grease and line two 20cm cake tins with baking parchment – if your cake tins are quite shallow, line the sides to a depth of at least 5cm. Put the flour, cocoa powder, baking powder, bicarbonate of soda, sugar and 1 tsp salt in a bowl and mix well. If there are any lumps in the sugar, squeeze these through your fingers to break them up. Measure the buttermilk, coffee, oil and vanilla in a jug. Add the egg and whisk until smooth. Pour the wet ingredients into the dry and whisk until well combined. Pour the cake mixture evenly into the two tins, and bake for 25-30 mins until risen and a skewer inserted into the centre comes out clean. Cool in the tins for 10 mins, then turn out onto a wire rack, peel off the baking parchment and leave to cool. These sponges can be made up to three days ahead and will stay moist if wrapped in cling film, or you can wrap well and freeze for up to two months. To make the chocolate fudge icing, put the chocolate in a heatproof bowl, suspended over a pan of barely simmering water. Stir every now and then until melted. (Alternatively, melt in the microwave, stirring the chocolate every 20 secs so it doesn’t burn.) Remove the bowl from the heat and leave to cool a little. Meanwhile, put the butter and icing sugar in another large bowl and mash with a spatula to combine (this will prevent you covering the work surface in icing sugar), then whisk with a hand mixer until smooth. Sift in the cocoa and pour in the melted chocolate and milk, then mix again until smooth. Use roughly half the icing to sandwich the cakes together on a cake stand or board. Use a palette knife to cover the entire cake with the remaining icing – don’t worry about being too neat. Use the chocolate fingers to cover the sides of the cakes – do this straight after icing as the icing will set after a while, and the chocolate fingers won’t stick. Melt the remaining 50g chocolate in the microwave or in a small bowl suspended over a pan of gently simmering water. Leave to cool until the chocolate is a spreadable consistency. You can speed this up by putting it in the fridge – just stir it every 5 mins or so to prevent the chocolate from setting. Push a skewer into the centre of the cake and slip the straw over the top – this will give it more stability. Push 1-2cm of the straw into the cake. Use a cutlery knife to spread a blob of chocolate onto a sugar coated chocolate sweet and, starting at the base, stick the sweets to the straw. You will have to do this in stages to allow the chocolate to set a little before adding another layer of sweets. Work your way up the straw until it’s completely covered. If the chocolate in your bowl becomes too firm, simply heat again until it is at the correct consistency. Stop when you reach the bend in the straw. Stick the remaining skewer into the top of the straw so that it pokes out at an angle. Place the paper bag on top – you may have to use a little sticky tape to hold it in place. If any of the straw is exposed, cover it with more sweets. Tip the remaining sweets on top of the cake to flood the surface. Will keep well stored in an airtight tin for 2 days.','1 hr','35 mins'),
(991,'cookies & cream party cake','https://www.bbcgoodfood.com/recipes/cookies-cream-party-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/oreo-cookie-cake-dc42389.png','Stack up our best ever chocolate sponges and smother in a biscuit icing to make this towering celebration cake',6,20,2,2,21,'Heat oven to 180C/160C fan/gas 4. Grease and line two 20cm cake tins with baking parchment – if your cake tins are quite shallow, line the sides to a depth of at least 5cm. Put 200g flour, 8 tbsp cocoa powder, 2 tsp baking powder, 1 tsp bicarbonate of soda, 280g light brown soft sugar and 1 tsp salt in a bowl and mix well. If there are any lumps in the sugar, squeeze these through your fingers to break them up. Measure 200ml buttermilk, 100ml coffee, 150ml oil and 2 tsp vanilla in a jug. Add 2 eggs and whisk until smooth. Pour the wet ingredients into the dry and whisk until well combined. Pour the cake mixture evenly into the two tins, and bake for 25-30 mins until risen and a skewer inserted into the centre comes out clean. Cool in the tins for 10 mins, then turn out onto a wire rack, peel off the baking parchment and leave to cool. Repeat steps 1 and 2, so that you have 4 sponge cakes in total. These sponges can be made up to three days ahead and will stay moist if wrapped in cling film, or you can wrap well and freeze for up to two months. To make the icing, put the butter in a large bowl and sieve in half the icing sugar. Roughly mash together with a spatula, then whizz with a hand mixer until smooth. Add the cream cheese and sieve in the remaining icing sugar, mash together again, then blend once more with the hand mixer. Put the biscuits in a food processor and whizz to fine crumbs. Add the biscuit crumbs to the icing and mix again until combined. Next, make the cookie cake toppers. You’ll need one biscuit for each letter of the word you’d like to write – someone’s name or, like we have done, ‘WOW!’ Carefully insert a skewer into the creamy filling in the middle of each biscuit – some cookies may break, so save these ones for later. Melt the chocolate in the microwave, stirring every 20 secs or so, or in a heatproof bowl suspended over a pan of gently simmering water. Spread a little chocolate over the surface of one side of each cookie. Roll out the fondant icing to the thickness of a 50p coin, and cut out your letters, then stick these to the cookies and set aside to dry. Alternatively, let the chocolate dry, then use an icing pen to write the name or word directly on the surface. To assemble the cake, stick one of your sponges to a cake stand or board with a little of the cream cheese icing. Use roughly half the icing to stack the remaining cakes on top, spreading a generous amount between each layer. Pile the remaining icing on top of the assembled cake, and use a palette knife to ease it over the edges, covering the entire surface of the cake. Tidy the plate with a piece of kitchen paper. If you have any leftover biscuits, break them into pieces and push these around the base of the cake. Insert the dried cookie cake toppers into the top of the cake and serve. Store leftovers in the fridge for up to 2 days, but bring back to room temperature for an hour or so before eating.','1 hr','50 mins'),
(992,'storecupboard chocolate hazelnut cake','https://www.bbcgoodfood.com/recipes/storecupboard-chocolate-hazelnut-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Storecupboard-chocolate-hazelnut-cake-655f699.jpg','Raid your storecupboard to make this lovely chocolate cake. Using ground hazelnuts delivers an incredible texture and flavour, or try almonds if you prefer',118,8,1,2,21,'Heat the oven to 180C/160C fan/gas 4. Tip the sugar, butter and dark chocolate into a saucepan over a low heat and stir until glossy and melted. Remove the pan from the heat and leave to cool for 10 mins. Whisk the eggs into the cooled chocolate mixture, then pour into a large bowl with the ground hazelnuts, flour, cocoa and baking powder. Gently stir to combine, then pour into a 20cm tin lined with baking parchment. Bake for 40-50 mins, or until a skewer inserted into the middle comes out clean. If any wet mixture remains, return it to the oven for 5-10 mins. Leave to cool in the tin for 5 mins, then turn out onto a wire rack to cool completely. For the ganache, pour the yogurt into a heatproof bowl and set it over a pan of simmering water, making sure the bowl doesn’t touch the water. Whisk for 3-4 mins until hot, then tip in the chocolate. Turn off the heat and whisk continuously until the chocolate has melted. If the mixture looks as though it might split, pour in a splash of milk, leave it for 15 seconds to warm through, then whisk briefly until smooth. Leave the ganache to cool for 15-20 mins so it’s easier to spread. Spread the ganache all over the cake, then scatter over the roasted hazelnut halves. Leave the cake to cool and set completely. Will keep in an airtight container, chilled or at room temperature, for up to two days. Serve at room temperature.','20 mins','1 hr and 15 mins'),
(993,'orange and raspberry hey duggee cake','https://www.bbcgoodfood.com/recipes/orange-and-raspberry-hey-duggee-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/douggie-4402-dcb4f81.jpg','Dazzle your kids with their favourite TV character in cake form! This orange buttercream and jam-filled sponge is sure to be a crowd-pleaser, great for a birthday party ',75,16,3,2,21,'Start by making the cake. Heat oven to 180C/160C fan/gas 4 and lightly grease a 23cm deep round cake pan, lining the base with parchment paper. Cream the butter and sugar together in a large bowl using an electric hand whisk until light and fluffy – this should take about 10 mins. Add the vanilla and whisk again to combine. Beat the eggs a little with a fork, and with the beaters still running, add the eggs a little bit at a time. Wait until each addition has been fully combined before adding more. Once all of the eggs have been added, fold in the flour with a spatula in three additions, alternating with the yogurt. If the finished batter feels a little stiff, mix in a couple tbsp of milk to loosen it. Transfer the batter into the prepared cake pan and level out with a spatula. Bake in the preheated oven for about 1 hr 30 – 1 hr 40 mins or until a skewer inserted into the middle of the cake comes out clean. If the cake is browning too quickly, cover loosely with baking parchment for the last 15 mins of cooking. Allow the cake to cool in the tin for 20 mins. While the cake is cooling in the tin make the syrup. Place the sugar, 50ml water and vanilla extract into a small saucepan over a medium heat and bring to a simmer, cooking for a few mins until the sugar has dissolved. Transfer the cake onto a wire rack to cool completely then use a skewer to poke holes all over the top of the cake. Use a pastry brush to spread the syrup over the top of the cake, allowing it to soak into the sponge before adding more. To make the buttercream, beat the butter and icing sugar together with an electric hand whisk until light and fluffy, then add the orange curd and whisk again to combine. If the cake is domed on the top, use a large serrated knife to level it off then cut the cake through the middle, into two layers. Place the bottom layer of cake onto a 23cm round cake board and spread a thin layer of buttercream on the cake and top with the raspberry jam. Place the second layer of cake on top and spread the remaining buttercream over the top and sides of the cake. On a work surface lightly dusted with icing sugar, knead the red sugarpaste until soft and pliable. Roll it out until about 3-4mm thick and wide enough to cover the cake. Roll the sugarpaste onto the rolling pin and carefully drape over the cake. Gently smooth the sugarpaste down the sides of the cake and trim off the excess with a small sharp knife. Reserve the trimmings for the details later on. Repeat the rolling process with the yellow sugarpaste but this time cutting into thin strips. Cut 30 strips approx 1.5cm x 10cm and stick to the sides of the cake all the way around, dipping your finger in water to brush onto the back of each one, which will act as glue (the strips should cover the entire height of the cake but only reach a couple of centimetres over the top of cake). Next roll out the blue sugarpaste to the same thickness as the red and yellow but this time cut out a 20cm circle and stick it in place in the middle of the cake using a little water. Roll out the brown sugarpaste as well and use a thin sharp knife to cut out Duggee’s face and attach to the blue circle. For Duggee’s clothes knead a little yellow into the remaining brown sugarpaste until it is a light brown colour and use this to cut out his shirt and lip area, sticking with a little water as before. Use the black sugarpaste to roll his eyes and nose, using your hands rather than rolling on the work surface. Use the remaining sugarpaste to shape the decorations for his outfit and mouth and stick as before with a little water. Wrap the ribbon around the bottom edge of the cake and secure with tape or a pin but remember to remove before serving. This cake will keep in a sealed container for up to 4 days.','1 hr and 30 mins','1 hr and 40 mins'),
(994,'toasted marshmallow & ginger cake','https://www.bbcgoodfood.com/recipes/toasted-marshmallow-ginger-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cake-sparklers-7ab381e.jpg','Decorative Italian meringue masks a spicy, moist ginger cake layered with ginger buttercream- an amazing bake!',6,8,3,2,21,'Melt the butter, sugar, treacle and syrup in a small saucepan, whisking until smooth. Set aside to cool for 10 mins. Heat oven to 160C/140C fan/gas 3. Grease and line a 2lb loaf tin with baking parchment (our tin was 22 x 7 x 7cm). In a bowl, mix the flour, bicarbonate of soda, ground ginger, cinnamon and 1⁄2 tsp salt. Pour in the warm syrup mixture and the milk, followed by the egg yolks. Whisk until you have a smooth batter. Pour into the tin and bake for 1 hr or until a skewer comes out clean. Cool in the tin for 30 mins, then slice off the top to give you a smooth surface. Flip onto a wire rack and remove the baking parchment. Leave to cool. Can now be covered with cling film and kept for up to 5 days. To make the buttercream, put the ingredients in a large bowl and mash together with a fork or spatula (this prevents the icing sugar from going everywhere). When roughly combined, blend with an electric hand whisk until smooth. Now make the marshmallow icing. Put the egg whites, sugar, 1 tbsp water and a good pinch of salt into a heatproof bowl. Place over a pan of simmering water, making sure the bowl doesn’t touch the hot water, and whisk until thick and leaving a prominent trail from the beaters – this will take about 4 mins. Remove the bowl from the heat and continue whisking for another 3 mins until cooled slightly and really stiff. Transfer half to a piping bag fitted with a large round nozzle and set aside. When ready to assemble, slice the cake lengthways into 3 even layers. On a cake stand or plate, reassemble the cake, with the largest slice on the bottom, layering with the buttercream. Using a small offset palette knife or small butter knife, cover the cake with the marshmallow icing left in the bowl. Use the palette knife to create vertical lines along the edges (see tip, left). Now use the piping bag to pipe spikes of meringue over the top of the cake. Use a cook’s blowtorch to toast the meringue to a golden brown – a few charred patches will add to the effect. Serve straight away or chill for up to 2 days, removing from the fridge 30 mins before serving. We decorated ours with sparklers.','55 mins','1 hr and 15 mins'),
(995,'funfetti cupcakes','https://www.bbcgoodfood.com/recipes/birthday-custard-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Geburtstags-Cupcakes-2-5829290.jpg','Bake a batch of these funfetti cupcakes for a kids'' party – they have an airy, custard-flavoured sponge and creamy vanilla frosting, all topped with rainbow sprinkles',119,12,1,2,21,'Heat the oven to 180 C/160 Fan/gas 4 and line 12 holes of a muffin tin with paper cases. Whisk the oil with the sugar and eggs, then stir in the plain yogurt and lemon juice. Mix the flour with the baking powder, a pinch of salt and the custard powder, and add this to the wet ingredients. Mix well and add the 2 tbsp of sprinkles, if you like. Use an ice cream scoop or two teaspoons to divide the mixture between the muffin cases. Bake the muffins for about 25 mins until golden brown, then let them cool completely. For the frosting, beat the butter with the icing sugar and vanilla extract for about 5 mins until the colour is slightly lighter and the texture is creamy. Beat the soft cheese to loosen, then stir it in. Put the frosting in a piping bag with your choice of nozzle and pipe icing onto each muffin. Decorate with the sprinkles. Will keep chilled for up to three days.','20 mins','25 mins'),
(996,'chocolate birthday cake','https://www.bbcgoodfood.com/recipes/chocolate-birthday-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-373469_11-d808d9f.jpg','Bake this simple and stunning chocolate birthday cake for a kids'' or adults'' party centrepiece. Decorate with icing shapes and colourful candles.',5,12,1,2,21,'Heat oven to 180C/fan 160C/gas 4. Butter and line the base of a 18 x 28cm cake tin or tray. Put the butter and sugar into a bowl; beat together with electric hand beaters until light and fluffy. add the eggs a little at a time, beating well between each addition. Sieve the flour, cocoa and bicarbonate of soda into the bowl, then tip in any bran left in the sieve. Pour in the natural yogurt. Stir everything to a smooth mixture and spoon into the prepared tin. Bake for 20-25 mins until just firm and shrinking away from the sides of the tin. Cool in the tin for 5 mins, then turn onto a wire rack to cool completely. For the icing, sieve the icing sugar and cocoa into a bowl, then pour in the butter and 2 tbsp just-boiled water. Stir together to a smooth, spreadable consistency. if it’s too stiff, very carefully add a little more boiling water, drop by drop. Spread the icing over the top of the cake, using a palette knife dipped in hot water. Will keep in an airtight container for up to 1 week. Melt the chocolates in three different bowls in the microwave on High for 1 min or over a pan of simmering water. Spoon the melted chocolates into three plastic disposable piping bags. Snip the ends off and pipe 12 simple shapes on top of the cake. Leave to set. Just before serving, cut the cake into 12 squares and push in the candles.','20 mins','25 mins'),
(997,'super-easy birthday cake','https://www.bbcgoodfood.com/recipes/super-easy-birthday-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/super-easy-birthday-cake-7a52db2.jpg','Children love colour, so what could be better than a birthday cake with two different coloured sponges made from one mixture and a colourful icing?',19,8,1,2,21,'Heat oven to 180C/160C fan/gas 4. Butter two 18cm loose-based cake tins and line the bases with baking parchment. Beat the butter and sugar in a mixer or by hand, then add the eggs, one at a time, mixing well after each. Fold in the flour, milk and vanilla extract until the mixture is smooth. Divide the mixture between two bowls. Sift the cocoa powder into one of the bowls. Scrape the vanilla batter into one tin and the chocolate batter into the other and level the tops. Bake for 20-25 mins or until a skewer comes out clean. Cool for 5 mins, then transfer to a wire rack and cool completely. To make the icing, beat the butter and add the icing sugar a little at a time, beating each lot in until you have a smooth, creamy icing. Add a little pink colour and beat it in (add more if you want a stronger colour). Sandwich the two cakes together with icing and spread the rest on top using a palette knife. Will keep in an airtight container for three days.','20 mins','25 mins'),
(998,'hedgehog cake','https://www.bbcgoodfood.com/recipes/hedgehog-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hedgehog_0-45fced0.jpg','A celebration cake with a touch of woodland style, this chocolate cake is decorated with edible spikes, buttercream and chocolate details',94,12,1,2,21,'Heat the oven to 160C/140C fan/gas 3. Grease and base line a 1 litre heatproof glass pudding basin and a 450g loaf tin with baking parchment. Put the butter and chocolate into a saucepan and melt over a low heat, stirring. When the chocolate has all melted remove from the heat. In a large bowl mix together the flour, baking powder, bicarbonate of soda and muscovado sugar. In another bowl beat together the eggs, soured cream and vanilla extract. Pour the egg and chocolate mixtures into the flour and mix thoroughly together. Weigh 250g into the loaf tin, then spoon the rest of the mixture into the pudding basin. Cook both cakes side by side in the oven. The loaf cake will cook in about 30 mins and the basin cake in about 1 hour. Take the loaf cake out of the oven and close the oven door quickly so as not to allow the heat out. Test with a skewer inserted into the centre, it should come out clean. If not return to the oven for another five mins. Repeat with the basin cake after about another half an hour. Once each cake is cooked, allow to cool completely before turning out. To make the icing, mix together the cocoa and water to make a smooth paste. Beat the butter until soft and gradually beat in the icing sugar. Add the cocoa paste and beat until smooth. To assemble the cake, trim the top of the basin cake so it is flat. Turn cut side down onto a board and cut in half vertically. Spread a little butter icing on each of the flat bases and sandwich these two buttercreamed surfaces together to make a rugby ball shape for the hedgehog body. Place onto a 30cm cake board or wooden board. Cut the loaf cake in half vertically. Take one half and trim the short end to a v shape to make the pointy head. Use a little icing to attach to the body. Cut pieces from the remaining loaf cake to fill any gaps. Cover the whole cake in butter icing. Cut the flake bars into pieces ranging from 2.5cm to 5cm. The spikes do not have to be regular or even in shape. Starting at the front push the spikes into the body of the hedgehog at a slight angle. Start with the small pieces gradually using larger ones as you go towards the back. Press a round chocolate into the tip of the head to represent the nose and use chocolate chips for the eyes. Using a tiny bit of butter icing attach sliver or gold balls to the eyes. The cake is best made the day before cutting and icing and will keep well for 3–4 days.','1 hr and 30 mins','1 hr'),
(999,'vanilla party cake','https://www.bbcgoodfood.com/recipes/vanilla-party-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vanilla-party-cake-c191e6b.jpg','Cover the light buttery sponge of this classic celebration cake with silky-smooth meringue buttercream and colourful edible polka dots',75,16,3,2,21,'Heat oven to 180C/160C fan/gas 4 and grease 3 x 20cm sandwich tins, lining the bases with baking parchment and greasing the parchment too. Beat the butter and sugar in a large bowl with an electric whisk until light and fluffy. Add the vanilla extract and the egg whites, a little at a time, beating until fully combined before adding more. Mix together the flour, cornflour and baking powder. Add the dry ingredients in 3 additions, alternating with the buttermilk. Divide the batter between the tins and level the tops. Bake for 25-30 mins or until a skewer inserted into the middle comes out clean. Allow the cakes to cool in the tins for 10 mins, then turn out onto a wire rack, peeling off the parchment. Cool completely. To make the buttercream, put the egg whites and sugar in a big bowl (the bowl of your tabletop mixer, if you have one) with the vanilla seeds and set over a pan of gently simmering water. Lightly whisk until the sugar has fully dissolved – you can test this by dipping two fingers into the bowl and rubbing them together; if you can’t feel any grains of sugar, the mixture is ready. Remove the bowl from the heat and keep whisking until a thick meringue has formed. Continue whisking until the meringue has cooled to room temperature, then slowly add in the butter, 1 tbsp at a time. By the time all the butter has been incorporated, the mixture should have transformed into a silky-smooth buttercream. If it hasn’t, continue to whisk until it does. If it still refuses to thicken, it may be the mixture is still too warm, so chill for 10 mins, then continue whisking. Add the vanilla extract and mix to combine. To assemble the cake, place a sponge on a cake board or serving plate and top with a thin layer of buttercream. Repeat with the remaining cake layers and finish by spreading the remaining buttercream over the top and sides of the cake (see below). To get a smooth finish, use the edge of a palette knife, and drag carefully around the sides of the cake, smoothing out the buttercream. To decorate the cake, press the sides of it with the edible polka dot sprinkles creating a full border at the bottom with less and less the further up the cake you go. Best served within 2 days of baking, but the cake will keep for up to 4 days.','1 hr and 15 mins','40 mins'),
(1000,'maple & pear ruffle cake','https://www.bbcgoodfood.com/recipes/maple-pear-ruffle-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/maple-pear-ruffle-cake-f274281.jpg','A grown-up, lightly-spiced sponge that is sandwiched with pears caramelised in maple syrup and topped with meringue buttercream',75,16,3,2,21,'Heat oven to 180C/160C fan/gas 4 and grease 3 x 20cm sandwich tins, lining the bases with baking parchment and greasing the parchment too. To make the sponge, put the butter and sugars in a large bowl and beat with an electric whisk until light and fluffy, about 5 mins. Add the eggs, a little at a time, beating until fully incorporated before adding more. In another bowl, mix the flour, cornflour, spices and baking powder. Stir in the dry ingredients in 3 additions, alternating with the milk. Divide the batter among the prepared tins, smooth the surfaces and bake for 25-30 mins or until a skewer inserted into the middle of the cakes comes out clean. Allow the cakes to cool in the tins for 10 mins, then turn out onto a wire rack, peeling off the parchment, to cool completely. For the caramelised pears, peel, core and dice them into small cubes, tossing with the lemon juice as you go. Tip into a frying pan with the maple syrup and butter, and cook over a medium-high heat until the pears have softened and most of the liquid has evaporated. Set aside to cool. For the buttercream, put the egg whites and sugar in a large heatproof bowl (the bowl of your tabletop mixer, if you have one) and place over a pan of gently simmering water. Whisk until the sugar has fully dissolved - you can test this by dipping two fingers into the bowl and rubbing them together; if you can’t feel any grains of sugar, the mixture is ready. Remove the bowl from the pan and beat with an electric whisk (or use your tabletop mixer) until a thick meringue has formed. Continue whisking until the meringue has cooled to room temperature. Slowly beat in the butter, 1 tbsp at a time. The mixture should now look like a silky- smooth buttercream – if it doesn’t, continue to whisk until it does. If it still refuses to thicken, it may be the mixture is still too warm, so chill for 10 mins, then continue whisking. Whisk in the maple syrup last. To assemble the cake, place the first sponge on a cake board or serving plate and spread with a thin layer of the buttercream. Top with half of the caramelised pear chunks, discarding any leftover liquid. Repeat, finishing with the third sponge. To finish, spread a very thin layer of the buttercream over the sides and top of the cake – this is called ‘crumb-coating’ (see tip). Now spread a thick layer of buttercream on top of the cake. Put the remaining buttercream in a piping bag fitted with a small petal piping tip (see tip). Hold the piping bag with the wide end of the nozzle touching the side of the cake, at the very bottom. Gently wiggle the bag side to side, back and forth, in a zigzag motion, working up the cake in a strip. Repeat, starting next to the previous strip each time, working all the way around the cake until it’s entirely covered. To finish, pipe 1 row of buttercream around the edge of the top of the cake (which will neaten any rough edges at the top of your zigzags). If you like a little sparkle, finish the cake with a coat of edible gold spray. Best served within 2 days of baking, but the cake will keep for up to 4 days.','1 hr and 30 mins','40 mins'),
(1001,'easy lemon layer cake','https://www.bbcgoodfood.com/recipes/lemon-layer-cake-with-soft-cheese-icing','https://images.immediate.co.uk/production/volatile/sites/30/2020/12/Lemon-layer-cake-d2a2df0.jpg','Indulge in a slice of lemon cake for afternoon tea. With lovely light sponge layers, a citrus zing offsets the rich and creamy soft cheese icing',46,8,1,2,21,'Heat the oven to 180C/160C fan/gas 4 and line the base of two 20cm sandwich tins with baking parchment. Beat the butter and sugar together for 3 mins using an electric whisk until smooth and fluffy. Add the eggs, one at a time, beating well between each addition and scraping down the sides of the bowl. Fold in the flour and baking powder until well incorporated, then fold in the yogurt, vanilla and lemon zest. Divide between the tins and bake for 30-35 mins until golden and a skewer inserted into the middles comes out clean. Meanwhile, make the drizzle. Tip the sugar, lemon juice and 100ml water into a small pan set over a medium heat and stir until dissolved. Add the lemon zest, bring to the boil and simmer for 2-3 mins until the zest has softened and the liquid is syrupy. Remove the zest to a sheet of baking parchment using a slotted spoon, and remove the syrup from the heat. Leave the sponges to cool for 10 mins in the tins, then pour over the warm drizzle. Leave to cool completely. For the icing, beat the butter and icing sugar together using an electric whisk for 4-5 mins until smooth, scraping down the sides of the bowl as you go. Add the vanilla and soft cheese and beat for 4 mins more until thick and creamy. Don’t worry if it doesn’t look thick at first – it will loosen, then thicken again as you beat it. Remove the cooled sponges from the tins. Spoon the icing into a piping bag fitted with a star nozzle. Put one sponge on a cake stand or serving plate, and pipe just under half the icing around the edge using a circular motion for a wavy effect. Pipe a little more icing over the empty middle (this doesn’t need to be neat) and smooth with the back of a spoon. Chill for 45 mins-1 hr until set. Top with the second sponge, then pipe eight blobs of icing around the edge at regular intervals, leaving a gap between each. Spoon the candied lemon zest into each gap, then serve.','50 mins','35 mins'),
(1002,'mini victoria sponge cakes','https://www.bbcgoodfood.com/recipes/mini-victoria-sponges','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Mini-Victoria-Sponges-ecc33e9.jpg','Make these bite-sized victoria sponges for a special afternoon tea or birthday celebration. Choose your favourite jam for the filling, then add whipped cream',6,12,1,2,21,'Heat the oven to 180C/160C fan/gas 4 and line a 12-hole muffin tin with paper cases. Beat the butter and caster sugar together using an electric whisk until pale and fluffy, then whisk in the eggs, one at a time, scraping down the sides of the bowl after each addition. Add the milk, vanilla, flour and a pinch of salt, then beat again until smooth. Divide the batter evenly between the paper cases – this can be easily done using an ice cream scoop. Bake for 17-20 mins, or until the sponges are golden, risen and a skewer inserted into the middles comes out clean. Leave to cool in the tin for 10 mins, then transfer to a wire rack to cool completely. Once completely cool, the sponges can be frozen for up to three months. Remove the paper cases from the sponges, then split the sponges in half through the equator using a small serrated knife. Set aside. Whip the cream and icing sugar together to soft peaks using an electric whisk. Spread a little jam over the base of each sponge, then spoon or pipe over a dollop of whipped cream and sandwich with the tops of the sponges. Dust with a little icing sugar before serving. Best eaten on the same day.','20 mins','20 mins'),
(1003,'blackberry & orange cake','https://www.bbcgoodfood.com/recipes/blackberry-orange-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/Blackberry-and-orange-cake-1ef4b69.jpg','Try a twist on layered sponge cake with blackberries, zesty oranges and marmalade in place of your usual filling. Pipe the topping for a professional look',46,10,1,2,21,'Heat the oven to 180C/160C fan/ gas 4. Butter two 20cm loose-bottomed cake tins and line with baking parchment. Beat the butter and caster sugar together in a large bowl using an electric whisk until pale and fluffy. Add the eggs, one at a time, until combined. Tip in the vanilla, flour and baking powder and fold until smooth, then gently fold in the orange zest and blackberries, being careful not to overmix. Divide the batter between the two tins and bake for 35-40 mins until golden and a skewer inserted into the middles comes out clean. Cover with foil after 30 mins if they start to colour too quickly. Meanwhile, make the drizzle. Put the orange juice, zest and sugar in a small pan and simmer for 4-5 mins until a thin syrup forms. Pour into a heatproof bowl and set aside. When the sponges are ready, drizzle the syrup over both while still warm and brush over evenly using a pastry brush. Leave to cool completely in the tins. Meanwhile, make the buttercream for the topping. Beat the butter and icing sugar in a large bowl with an electric whisk until light, pale and fluffy. Whisk in the orange zest and milk until loosened slightly. Spoon into a piping bag fitted with a large round nozzle. Beat the marmalade to loosen, then transfer a little to a piping bag fitted with a small round nozzle. Remove the cooled sponges from the tins, and place one on a cake board or plate. Pipe buttercream dots on top in a circle around the edge, then three dots in the middle. Spoon some marmalade into the gaps between the buttercream, then top with the second sponge. Pipe more buttercream dots all over the surface, then fill in the gaps by piping in the remaining marmalade. To finish, scatter with the blackberries and extra orange zest.','45 mins','40 mins'),
(1004,'citrus, almond & yogurt cake','https://www.bbcgoodfood.com/recipes/citrus-almond-yogurt-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Citrus-almond-and-yogurt-cake-99026e5.jpg','Enjoy this zesty citrus, almond and yogurt cake for afternoon tea. Best served warm with a cup of tea',5,12,1,2,21,'Melt the butter in a small pan. Remove from the heat and leave to cool slightly. Meanwhile, butter a 23cm springform cake tin and line with baking parchment. Heat the oven to 180C/160C fan/gas 4. Put the 200g caster sugar, flour and ground almonds in a large bowl and mix well. Whisk the eggs and 75g yogurt into the cooled melted butter, then pour this into the bowl with the dry ingredients. Zest the lemon and orange over the bowl. Stir with a spatula until there are no streaks of flour, then scrape into the tin and bake on the middle shelf of the oven for 40 mins. Cut a few slices each from the zested lemon and orange, then squeeze the juice from what’s left of each into a saucepan (you’ll need about 6 tbsp total). Add the 2 tbsp caster sugar and the fruit slices to the pan. Bring to the boil and cook for 5-10 mins, or until the juice has reduced to a thin syrup and the fruit slices have softened. Leave to cool. Remove the fruit slices to a sheet of baking parchment and leave to dry. Insert a skewer into the middle of the cake – it should come out dry, with no wet cake mix clinging to it. If it’s not ready, bake for 5-10 mins more and check again. Leave to cool in the tin for 5 mins, then spoon over the citrus syrup. Leave to cool for 10 mins more, then remove from the tin. To freeze, first leave to cool completely, then wrap the cake well. Will keep frozen for up to three months. Mix the 2 tbsp yogurt with the icing sugar to make a thick icing. Spoon this onto the centre of the cake and use the back of a spoon to ease it to the edge (it should drip over the side). Scatter over the flaked almonds and decorate with the fruit slices. Serve warm, or leave to cool completely. Will keep in an airtight tin for up to five days.','15 mins','45 mins'),
(1005,'easy pound cake','https://www.bbcgoodfood.com/recipes/easy-vanilla-pound-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vanilla-pound-cake-d282921.jpg','Bring a smile to your friends and family with a slice of our vanilla pound cake. It''s the perfect partner to a cuppa for elevenses or afternoon tea',46,8,1,2,21,'Pre-heat oven to 180C/160C fan/gas 4 and line a 900g loaf tin with parchment paper (mine was 19cm x 9cm x 6cm). Put all of the ingredients except those for the icing into a large bowl and beat together, either using an electric hand whisk or a wooden spoon until smooth and combined. Pour the mix into the loaf tin and spread evenly with the back of spoon. Bake for 40 – 45 mins. Once out of the oven, leave to cool and make the icing. Combine the icing sugar and vanilla paste with three tbsp of water – you may need more but add it gradually to get a thick icing. Spread it over the top of the cake; it’s fine if it drips down the edges. Allow to set and then slice. ','10 mins','45 mins'),
(1006,'butterfly cupcakes','https://www.bbcgoodfood.com/recipes/butterfly-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/butterfly-cupcakes-64fc369.jpg','These pretty cupcakes are perfect for a special occasion. The flavoured syrup help the cakes stay moist, so you can make them a day ahead and decorate the next day',29,24,2,2,21,'Heat oven to 180C/160C fan/gas 4 and line two 12-hole cupcake tins with paper muffin cases. Put all the cake ingredients in a large bowl and whisk with an electric whisk for 3-4 mins or until pale and light. Divide the mixture evenly among the 24 cases and bake for 20 mins or until a skewer inserted into the centre of a cake comes out clean. Leave to cool on a wire rack. While the cupcakes bake, make the syrup. Put the sugar, lavender and 300ml water in a medium pan over a low heat. Bring gradually to the boil, cook for 2 mins, then turn off the heat and leave to infuse. When cold, strain through a sieve into a small jug. To make the icing, put the butter, icing sugar and 100ml of the syrup in a large bowl. Mix with a wooden spoon, then switch to an electric whisk and whisk until smooth and fluffy. Divide the mixture between three bowls. Add a tiny dot of food colouring gel to each bowl to make three different colours of buttercream – stir to mix, then transfer half of each colour to disposable piping bags. Use a cocktail stick to poke a few little holes in the top of the cupcakes, then drizzle each with 1 tsp of the syrup. Put 1 heaped tsp of icing onto each cake (do eight of each colour) and swirl it round with the back of the spoon to completely cover the top. Next, use the matching piping bag to pipe a straight line about 2-3 cm long across the middle of each cake. To make the wings, melt the white chocolate in a heatproof bowl over a pan of simmering water. Divide into three smaller bowls and add the same gel colours as before. Cut 8cm squares of baking parchment and, using a pen, draw a pair of butterfly wings on each square so that the design shows through on the other side or, if you prefer, trace the design below. Turn the paper over and, using the line as a guide, paint on the melted chocolate. Sprinkle crushed biscuits on the edges to create patterns, then leave to set. Once set, gently push the wings into the cupcakes at a 45-degree angle.','2 hrs','25 mins'),
(1007,'lemon drizzle sponge pudding','https://www.bbcgoodfood.com/recipes/lemon-drizzle-sponge-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lemon-drizzle-sponge-pudding-2a11a13.jpg','Transform a classic lemon drizzle cake into a self-saucing pud for a cheap, comforting dessert. Serve with cream or custard',12,10,1,2,21,'Heat the oven to 180C/160C fan/gas 4. Butter a 30 x 20cm deep baking dish. Put the butter and 250g caster sugar in a bowl and beat for 5 mins until pale and fluffy. Whisk in the eggs, then sieve over the flour and baking powder and fold in until you have a batter. Stir in the lemon zest, reserving a little for decoration. Spoon the sponge batter into the dish and smooth over the top. Mix the lemon juice with the cornflour in a heatproof bowl to make a smooth paste. Mix the remaining 130g caster sugar with 300ml boiling water in a jug, pour over the cornflour mix and whisk until smooth. Pour this over the sponge. Bake for 45-50 mins until golden and set, and the sponge springs back when touched. While the pudding is baking, make the lemon drizzle. Mix the icing sugar with enough lemon juice (about half of it) to create a loose consistency. Drizzle over the sponge while it’s still warm and decorate with the reserved lemon zest. Serve straightaway with cream or custard.','15 mins','50 mins'),
(1008,'sponge cake','https://www.bbcgoodfood.com/recipes/sponge-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cake-f7d9f31.jpg','Choose your favourite filling for this easy, perfectly fluffy sponge cake – we''ve opted for lemon curd and whipped cream, but you could have jam. Perfect for afternoon tea.',16,8,1,2,21,'Heat oven to 180C/160C fan/gas 4, butter and line the base of two 20cm spring-form cake tins with baking parchment. Using an electric whisk beat the butter and sugar together until pale and fluffy. Crack the eggs in one at a time and whisk well, scraping down the sides of the bowl after each addition. Add the lemon zest, vanilla, flour, milk and a pinch of salt. Whisk until just combined then divide the mixture between the two tins. Bake in the centre of the oven for 25-30 mins until a skewer inserted into the middle of each cake comes out clean. After 10 mins remove the cakes from their tins and leave to cool completely on a wire rack. Fill how you like. My personal favourite is a good dollop of lemon curd and some fresh cream, then dust the top with icing sugar. Will keep for 3 days.','15 mins','30 mins'),
(1009,'cherry bakewell cake','https://www.bbcgoodfood.com/recipes/cherry-bakewell-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363595_11-2bd93cb.jpg','If you know someone who likes bakewell tart, then they will just love this cake – it’s full of almond flavour and sandwiched with cherry jam',33,8,1,2,21,'Heat oven to 180C/160C fan/gas 4 and make sure there’s a shelf ready in the middle. Butter and line the bases of two 20cm round sandwich tins with baking paper. Using electric beaters, beat together all the cake ingredients with a pinch of salt until smooth, then spoon into the tins and level the tops. Bake for 30 mins or until golden and springy. Don’t open the oven before 25 mins cooking time has passed. When they’re ready, cool the sponges for a few mins, then tip out of the tins and cool completely on a wire rack. Make sure the top of one of the cakes is facing up as you’ll want a smooth surface for the icing later on. When cool, put one sponge on a serving plate, then spread with jam. Sandwich the second sponge on top. Sieve the icing sugar into a large bowl. Add the water or lemon juice, then stir until smooth and thick. Spread evenly over the top and let it dribble over the sides. Scatter with the nuts and leave to set for a few mins before cutting.','15 mins','30 mins'),
(1010,'easy chocolate cake','https://www.bbcgoodfood.com/recipes/easy-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy_chocolate_cake-b62f92c.jpg','Master the chocolate cake with an airy, light sponge and rich buttercream filling. It''s simple enough for an afternoon tea but special enough for a party too',29,12,1,2,21,'Heat oven to 190C/170C fan/gas 5. Butter the base and sides of two 20cm round sandwich tins and line the bases with baking parchment. In a large bowl, beat together 200g golden caster sugar, 200g softened unsalted butter, 4 large eggs, 200g self-raising flour, 2 tbsp cocoa powder, 1 tsp baking powder, ½ tsp vanilla extract, 2 tbsp milk and a pinch of salt until pale. Divide the mixture between the prepared tins. Bake for 20 mins or until a skewer inserted into the centre of the cake comes out clean. Leave to cool in the tin for 10 mins, then turn out onto a wire rack to cool completely. For the buttercream, put 100g chopped milk chocolate in a heatproof bowl and melt in the microwave, stirring every 30 secs. Leave the melted chocolate to cool for 5 mins. Mash 200g softened butter and 400g icing sugar together with a fork, then switch to a wooden spoon or electric beaters, if you have them. Sift in 5 tbsp cocoa powder with a pinch of salt and pour in the melted chocolate and 2 tbsp milk. Mix again until smooth. On a cake stand or large plate, sandwich the cakes together with half of the buttercream, then spread the rest on top. Decorate with chocolate shards, if you like. To make chocolate shards: melt 50g dark chocolate and pour it onto a tray lined with baking parchment or foil. Now melt 25g milk chocolate and 25g white chocolate and drizzle them over the dark chocolate before it sets. Shake the tray gently to level the mixture then leave to set somewhere cool. Chop into shards.','35 mins','20 mins'),
(1047,'lemon curd, mascarpone & passion fruit tart','https://www.bbcgoodfood.com/recipes/lemon-curd-mascarpone-passion-fruit-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lemon-curd-mascarpone-passion-fruit-tart-78d0369.jpg','Serve this vibrant lemon curd and passion fruit tart as an easy dessert or sweet treat. Its tropical flavours make it perfect for summer',14,6,1,2,6,'Heat the oven to 200C/180C fan/gas 6. Unroll the puff pastry onto a lined baking tray. Score a 2cm border around the edge of the pastry using a sharp knife, then brush the border with the egg. Bake for 15-20 mins, or until golden and crisp. Gently push down the middle with the back of a spoon, then leave to cool completely. Whisk the mascarpone with the double cream and 3 tbsp lemon curd until soft and spoonable. Fold in another 3 tbsp lemon curd and the seeds of 1 passion fruit. Spoon the cream mixture in the centre of the pastry and scatter over the seeds of 2 passion fruit and the chopped pistachios.','15 mins','25 mins'),
(1011,'marble bundt cake','https://www.bbcgoodfood.com/recipes/marble-bundt-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/CAT4Babiccinababovka1007-8b8d952.jpg','If you need an easy but impressive cake for a celebration or charity bake sale, try this chocolate and vanilla sponge cake',120,12,1,2,21,'Butter and line a 2.5l bundt tin. Heat the oven to 180°C/160°C fan/gas 4. Beat the egg yolks with the sugar until lighter in colour, then beat in the soft butter followed by the milk or cream. In another bowl, beat the egg whites until they form soft peaks, then add the vanilla sugar and beat until stiff. Beat the flour and baking powder into the yolk mixture, then fold in the egg whites. Pour half of the batter into the tin, fold the cocoa powder into the remaining batter, then pour in over the vanilla layer. Use the handle of a spoon to swirl the batters together. Bake for about 1 hr or until the cake is risen and lightly browned and a skewer inserted into the middle comes out clean. Remove the cake from the oven, let it cool for a while in the tin, then turn it out and leave to cool completely on a wire rack. Dust with icing sugar to serve.','30 mins','1 hr'),
(1012,'sachertorte','https://www.bbcgoodfood.com/recipes/classic-sachertorte','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Sachertorte-63802bc.jpg','This rich chocolate cake was created in Vienna, Austria, and has a signature layer of apricot jam. Discover how to achieve the perfect mirror glaze below',119,12,1,2,21,'Melt the chocolate in a heatproof bowl set over a pan of simmering water, ensuring the bowl doesn’t touch the water. Alternatively, melt it in short bursts in the microwave, stirring between each burst. Set the chocolate aside to cool a little while you separate the eggs. Whisk the egg whites and a pinch of salt using electric beaters until they form soft peaks, then gradually add 200g of sugar, continuing to whisk until the egg whites form stiff peaks. Line a 24cm springform tin with baking parchment. Heat the oven to 170 C/150 fan/gas 3. Using electric beaters, beat the butter and remaining sugar in a large bowl until light and creamy and the sugar has dissolved. Gradually beat in the egg yolks and cooled chocolate. Fold about ⅓ the whisked egg whites into the chocolate mixture. Sift the flour over the mixture in two batches and fold each in. Gently fold in the remaining beaten egg whites using a spatula. Pour the cake batter into the prepared tin, spread evenly and bake for about 1 hr or until the cake is risen and springy to the touch. Put on a wire rack and leave to cool completely in the tin. Heat the jam and liqueur (or juice) in a small saucepan, then pour into a bowl through a fine sieve. Loosen the bottom of the cake tin, then slide off the tin and peel away the baking parchment. Halve the base horizontally. Place the bottom layer on a cake stand and spread with half of the jam mixture, then place the other half of the cake on top. Spread a thin layer of the remaining jam all over the cake and leave to dry. For the chocolate glaze, bring 40ml water and sugar to the boil in a small saucepan until the sugar has dissolved. Leave to cool slightly, then stir in 2 tbsp of the double cream. Heat the remaining cream in a separate pan and stir in the butter until melted. Remove from the heat and gradually add the chocolate, letting it melt into the cream, and stir in the sugar syrup. Place the cake on a cooling rack over a tray to catch any excess glaze. Evenly coat the cake with the glaze by pouring it slowly into the middle, tilting the cake gently so it flows slowly over the edges. Don’t smooth it with a knife or it will look streaky. Best enjoyed fresh but will keep for up to three days. For the striped pattern, melt the milk chocolate either in a microwave or in a heatproof bowl set over a pan of simmering water, ensuring the bowl isn’t touching the water. Pour the melted chocolate into a small piping bag and pipe onto the still-damp icing in parallel strips, 2-3cm apart. Carefully pull the tip of a spoon alternately from top to bottom through the lines to make the pattern, then allow the glaze to dry.','1 hr','1 hr'),
(1013,'chocolate fridge cake','https://www.bbcgoodfood.com/recipes/chocolate-fridge-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-fridge-cake-extraordinaire_-7a8e5b9.jpg','Sophisticated and intensely chocolatey with a hit of juicy raisins and crunchy biscuit pieces, serve this decadent chocolate fridge cake as a teatime treat',77,16,1,2,21,'Put the dried fruit in a small bowl and pour in just enough brandy or juice to cover. Microwave for 2 mins until steaming, then set aside. The non-messy way to chop chocolate is to smash it (still wrapped) onto your work surface, then unwrap it down to the foil and snap it (still in foil) into small bits using your fingers. Empty the chocolate into a microwaveable bowl. Add the butter and golden syrup, cover the bowl with cling film and microwave for 2 mins until melted. Set aside. Meanwhile, line a 20cm square tin with baking parchment, using a little butter to stick it in place. (You can also use foil but it’s more fiddly to peel off later, or cling film if you don’t mind wrinkles on the underside of your cake.) Take a third, large bowl. Use your fingers to break the biscuits into it. Stir in the nuts, soaked fruit and soaking liquid, then mix everything together. Pour the melted chocolate into the bowl with the biscuits, using a spatula or spoon to scrape out every last bit of chocolate. Mix together until everything is coated in chocolate. Check the ingredients list to make sure everything is accounted for (it’s surprisingly easy to miss something in this recipe), then tip the chocolate mixture into the tin, pushing well into corners and flattening the top. Put in the fridge (no need to cover) for 4 hrs or overnight until set. To serve, remove from tin, peel away the parchment and cut into 16 squares (any crumbly bits that fall off make tasty samples for the cook). Will keep for up to 1 week in an airtight container in the fridge. Can be frozen for up to 1 month.','20 mins','No Time'),
(1014,'chocolate swiss roll','https://www.bbcgoodfood.com/recipes/chocolate-swiss-roll','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Chocolate-Swiss-Roll-e800e06.jpg','This nostalgic dessert can be made a day ahead. Fill with a vanilla cream, dust with icing sugar to finish and serve in slices',7,6,1,2,21,'Heat the oven to 220C/200C fan/gas 7. Line a 22 x 32cm swiss roll tin (if it’s 20 x 30cm that’s still fine) with baking parchment. Sift the flour and cocoa powder together into a bowl and set aside. Crack the eggs into another bowl or the bowl of a stand mixer and tip in the sugar. Use an electric whisk or the whisk attachment to beat the eggs and sugar together for 5 mins until pale and very thick. Use a spoon or spatula to gently fold in the flour mixture until completely incorporated and everything’s the same colour with no streaks. Gently scrape the mix into the tin and spread to the corners. Bake for 9-10 mins until puffed up and springy to touch. While the cake is baking, get another piece of parchment a little larger than the tin and scatter it with sugar. When the sponge is cooked, use oven gloves to carefully invert it onto the sugar-coated paper. Lift off the tin and peel away the top layer of parchment. Score a line 2cm from one of the shorter edges, then use the parchment to roll the sponge into a spiral with the parchment running through it. Leave the rolled sponge to cool completely. While the cake is cooling, make the filling by whisking all the ingredients together until the cream stiffens. Unroll the sponge and spread over the filling, leaving the scored edge clear. Starting from the scored edge, use the parchment to roll the sponge up around the filling. Lift onto a board or serving platter and dust with icing sugar. Can be served straight away or chilled for up to 24 hrs. Serve in slices.','30 mins','10 mins'),
(1015,'chocolate caramel cake','https://www.bbcgoodfood.com/recipes/chocolate-caramel-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363834_12-04080db.jpg','This rich cake makes an impressive centrepiece for any summer tea party',8,12,1,2,21,'Heat oven to 180C/160C fan/gas 4. Lightly grease 2 x 23cm loose-bottom cake tins and line with a circle of baking parchment. Beat together the butter and sugar until pale and creamy. Add the eggs, one at a time, beating well between each addition, then add the baking powder, cocoa and a third of the flour. Mix well to combine, then stir in half the soured cream. Alternating between adding the flour and soured cream, mix everything until just combined. Spoon into the prepared tins, then smooth over the surface with a spoon. Bake for 30 mins or until the cakes are firm to touch and a skewer inserted into the centres comes out clean. Leave to cool in the tins for 5 mins, then turn out onto a rack and cool completely. When you’re ready to serve, whip the double cream with 2 tbsp Dulce de leche until soft peaks form. Place 1 cake on a serving plate and spread with half the cream, then drizzle over the remaining Dulce de leche. Top with the other cake, then spread the remaining cream on top. Sprinkle with the chocolate. Chill until ready to serve.','20 mins','30 mins'),
(1017,'chocolate & sesame loaf cake','https://www.bbcgoodfood.com/recipes/chocolate-sesame-loaf-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-sesame-loaf-cake-0d21b2f.jpg','Tahini gives this cake a subtle nutty flavour that partners perfectly with rich chocolate. This recipe makes 2 cakes, so why not freeze one as a treat for another day!',29,2,1,2,21,'Heat oven to 180C/160C fan/gas 4. Grease two 900g loaf tins (8.5cm deep) and line with baking parchment. First, make the chocolate cake mixture. Put the butter and chocolate in a large heatproof bowl set over a pan of simmering water, stirring occasionally. Put all the other ingredients in a large mixing bowl and, when the chocolate and butter have melted, pour over and mix with an electric whisk until smooth and well combined. Set aside and rinse the beaters. In another large bowl, add all the sesame cake ingredients, then combine with an electric whisk until smooth. Alternating with dollops of each mixture, divide between the two tins. Level the top, then swirl a skewer through the mixture to create a marbled effect. Bake in the middle of the oven for 45-50 mins or until a skewer inserted into the centre comes out clean. Leave the cakes to cool completely on a wire rack while you make the topping (there’s enough for both cakes but the topping recipe is easily halved if you want to freeze one and ice it later) Heat the double cream and butter together gently in a small pan until just simmering, then remove from the heat and add the chocolate. Stir until the mixture is smooth, then place in the fridge for 20 mins to firm up. Beat well with a wooden spoon, then transfer to a disposable piping bag. Snip the end off to make a wide opening, then pipe thick, overlapping zigzags across the cakes. Cut the sesame snaps into shards and arrange on top, scattering over any crumbs, then serve.','20 mins','55 mins - 1 hr'),
(1018,'dark chocolate, coconut & passion fruit mousse cake','https://www.bbcgoodfood.com/recipes/dark-chocolate-coconut-passion-fruit-mousse-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/passion-fruit-cake-896b246.png','Sandwich our best ever chocolate sponge with a fruity coconut mousse and smother in a rich ganache for a stunning party centrepiece',6,12,3,2,21,'Heat oven to 180C/160C fan/gas 4. Grease and line two 20cm cake tins with baking parchment – if your cake tins are quite shallow, line the sides to a depth of at least 5cm. Put the flour, cocoa powder, baking powder, bicarbonate of soda, sugar and 1 tsp salt in a bowl and mix well. If there are any lumps in the sugar, squeeze these through your fingers to break them up. Measure the buttermilk, coffee, oil and vanilla in a jug. Add the egg and whisk until smooth. Pour the wet ingredients into the dry and whisk until well combined. Pour the cake mixture evenly into the two tins, and bake for 25-30 mins until risen and a skewer inserted into the centre comes out clean. Cool in the tins for 10 mins, then turn out onto a wire rack, peel off the baking parchment and leave to cool. These sponges can be made up to three days ahead and will stay moist if wrapped in cling film, or you can wrap well and freeze for up to two months. To make the mousse, put the gelatine leaves in a bowl of cold water, one at a time, so they don’t stick together. Set aside for 5 mins to soften. Scoop the pulp and seeds from the passion fruit into a sieve suspended over a bowl, and push through the juice – you should have about 100ml. Put 1 tbsp of the seeds in a small bowl and set aside. Save 2 small empty passion fruit halves and discard the rest. Pour the coconut cream, passion fruit juice and sugar into a saucepan and bring to a gentle simmer. Remove from the heat and stir in the coconut rum liqueur. Add the gelatine to the hot liquid, a leaf at a time, squeezing out the water before you add them. Stir until the gelatine has dissolved. Fill each reserved passion fruit half with some of the mixture, place in a small dish and chill until needed. Leave the remaining mixture to cool to room temperature. Pour the double cream into a large bowl and whip until it’s holding its shape. Add half the passion fruit mixture, fold together, then add the remaining mixture and fold through again. Line a deep 20cm cake tin with a double layer of cling film. Slice each sponge in half through the middle and drizzle each layer with 1 tbsp coconut rum liqueur. Place one of the layers into the tin, flat-side down, and spread with a third of the passion fruit mousse – don’t worry if a little trickles down the sides. Layer the remaining sponges and mousse on top, finishing with a layer of cake. Cover the top of the cake with cling film and chill for at least 4 hrs, or preferably overnight. When the cake has chilled and the mousse is set, turn out onto a cake stand, so the base now becomes the top, and remove the cling film. Using a palette knife, scrape any passion fruit mousse off the edges of the cake, to give it a smooth surface. Place the cake back in the fridge while you prepare the ganache. Put the chocolate and cream in a pan and gently heat, stirring now and then, to make a runny chocolate sauce. Leave to firm up at room temperature until it reaches a spreadable consistency. Remove the cake from the fridge. Spread the ganache over the top and sides of the cake with a palette knife until completely covered. Decorate with a ring of toasted coconut shavings, if you like. Remove the filled passion fruit shells from the fridge and place these on top too, topped with the reserved passion fruit seeds. Chill until ready to serve, but bring out of the fridge 1 hr before slicing (don’t forget to serve the passion fruit jellies to your favourite guests too). Leftovers will keep in the fridge for 2 days.','45 mins','45 mins'),
(1019,'easy vegan chocolate cake','https://www.bbcgoodfood.com/recipes/chocolate-avocado-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-avocado-cake-51c276b.jpg','This indulgent, fudgy vegan cake is topped with a rich frosting – you''d never guess that it''s gluten-free and made without dairy, eggs, wheat or nuts',33,12,1,2,21,'Heat oven to 160C/140C fan/gas 3. Grease two 20cm sandwich tins with a little dairy-free sunflower spread, then line the bases with baking parchment. Put 1 large avocado and 300g light muscovado sugar in a food processor and whizz until smooth. Add 350g gluten-free plain flour, 50g cocoa powder, 1 tsp bicarbonate of soda, 2 tsp gluten-free baking powder, 400ml unsweetened soya milk, 150ml vegetable oil and 2 tsp vanilla extract to the bowl with ½ tsp fine salt and process again to a velvety, liquid batter. Divide between the tins and bake for 25 mins or until fully risen and a skewer inserted into the middle of the cakes comes out clean. Cool in the tins for 5 mins, then turn the cakes onto a rack to cool completely. While you wait, start preparing the frosting. Beat together 85g ripe avocado flesh and 85g dairy-free sunflower spread with electric beaters until creamy and smooth. Pass through a sieve and set aside. Melt 200g dairy-free chocolate, either over a bowl of water or in the microwave, then let it cool for a few mins. Sift 25g cocoa powder into a large bowl. Bring 125ml unsweetened soya milk to a simmer, then gradually beat into the cocoa until smooth. Cool for a few mins. Tip in the avocado mix, 200g sifted icing sugar, melted chocolate and 1 tsp vanilla, and keep mixing to make a shiny, thick frosting. Use this to sandwich and top the cake. Cover with sprinkles or your own decoration, then leave to set for 10 mins before slicing. Can be made 2 days ahead.','30 mins','25 mins'),
(1020,'easy chocolate brownie cake','https://www.bbcgoodfood.com/recipes/chocolate-brownie-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-50773_12-39c611e.jpg','The perfect cake for brownie lovers, try a big slice warm with some ice cream',5,10,1,2,21,'Heat oven to 180C/fan 160C/gas 4. Butter a 20-25cm cake tin and line with greaseproof paper. Place 175g/6oz of the chocolate, plus the butter and sugar in a heavy-based pan and heat gently until melted, stirring occasionally. Leave to cool. Whisk the egg yolks into the chocolate mixture, then add the flour, nuts and the remaining chocolate. Whisk the egg whites until they form soft peaks, then gently, but thoroughly, fold into the chocolate mixture. Pour into the prepared tin and bake in the centre of the oven for about 35-40 mins until crusty on top. Leave to cool, then run a knife around the sides and remove from the tin. Dust with icing sugar and serve warm with custard or ice cream or cold with cream.','15 mins - 20 mins','45 mins'),
(1021,'chocolate & lime cake','https://www.bbcgoodfood.com/recipes/chocolate-lime-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-and-lime-cake-e1919f8.jpg','Give chocolate cake a zesty lift with lime buttercream filling, chocolate and lime icing and candied zest to decorate – an irresistible flavour pairing',70,8,1,2,21,'Heat oven to 180C/160C fan/gas 4. Butter and line the base of 2 x 20cm cake tins with baking parchment. Beat the butter and light brown sugar together until pale and fluffy. Add the eggs gradually, beating well between each addition. Fold in the flour, baking powder and cocoa powder. If the mixture is very stiff, stir in the milk. Spoon into the cake tins and bake for 20-25 mins or until a skewer inserted into the centre comes out clean. Turn the cakes out onto a wire rack and peel off the parchment. Gently heat the lime juice and caster sugar together, stirring to help the sugar dissolve. While the cakes and the syrup are still warm, pierce the cakes all over using a skewer and spoon over the syrup. Leave to cool completely. Next, make the candied lime zest. Pare the zest from the limes, cutting it into broad strips. Cut away the white pith and discard, then slice the zest into matchstick-sized strips. Put them in a pan and cover with water, then bring to the boil. Cook for 1 min, then drain and rinse. Dry and set aside. Juice the limes and add enough water to make 225ml. Heat gently with the sugar until the sugar has melted. Add the strips of zest and simmer until the liquid has almost evaporated and turned syrupy – about 30 mins. Scoop the strips of zest out with a slotted spoon, then place them on parchment to dry. To make the buttercream, beat the butter and icing sugar together using an electric mixer or by hand (start off gently or the icing sugar will fly everywhere), until pale and light, but don’t beat for so long that it gets too fluffy. Add the lime zest and beat again until smooth. Chill in the fridge until the buttercream is firm, but spreadable. To make the icing, heat the cream gently (don''t let it boil), then take it off the heat. Add the chocolate and let it melt, stirring to help it along. Put the marmalade in a small pan with the extra 1 tbsp cream and heat slowly to dissolve. Add to the chocolate and cream and stir until everything is blended. Leave to cool a little, but not so long that it becomes firm. Pour over one of the layers of sponge and leave to cool and set – it will run down the sides and drip off. Spread the buttercream over the other layer, then put the chocolate coated layer on top. Decorate with the candied lime zest.','1 hr and 10 mins','40 mins'),
(1039,'salted caramel & apple pudding','https://www.bbcgoodfood.com/recipes/salted-caramel-apple-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/11/Salted-caramel-and-apple-pudding-8b0b0ab.jpg','Whip up this warming, family-friendly dessert in under an hour using apples and golden caramel. Serve hot from the oven with a scoop of ice cream',46,6,1,2,6,'Heat the oven to 180C/160C fan/gas 4. Butter a 30 x 20 x 4cm baking dish. Put the eggs, extra yolks, sugar, milk, cream, 200g of the caramel, the vanilla and a large pinch of sea salt in a large bowl, then whisk until combined. Stir in the flour until the batter is smooth. Halve, core and cut the apples into 1cm slices – there’s no need to peel them. Arrange in the baking dish and pour over the batter. Bake for 30-35 mins until the batter is set in the centre, golden and slightly risen. Drizzle over the remaining caramel, then sprinkle with a little extra sea salt. Leave to rest for 5-10 mins (it will deflate a little). Serve warm with vanilla or peanut butter ice cream, if you like.','15 mins','35 mins'),
(1022,'gregg''s tangy lemon tart','https://www.bbcgoodfood.com/recipes/greggs-tangy-lemon-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-198460_11-a8d172b.jpg','Gregg Wallace fell in love with Michel Roux Snr''s lemon tart at first bite. This is his version of the classic recipe',121,8,2,2,6,'To make the pastry, mix the flour and icing sugar in a bowl. Rub the butter into the flour with your fingers until crumbly. Mix in the egg yolks. If the pastry is still too dry, add 1-2 tbsp water until it comes together. Roll into a ball and divide in half (freeze one half for another recipe). Flatten out the pastry with your hands, wrap the dough in cling film, then chill for at least 30 mins. While the pastry is chilling, make the filling. Beat all the ingredients, except for the zest, together. Sieve the mixture, then stir in the zest. Roll out the pastry on a lightly floured surface to about the thickness of a £1 coin, then lift into a 23cm tart tin. Press down gently on the bottom and sides, then trim off any excess pastry. Stab a few holes in the bottom with a fork and put back in the fridge for 30 mins. Heat oven to 160C/140C fan/gas 3. Line the tart with foil and fill with rice or dried beans. Bake for 10 mins, then remove the tart tin from the oven, discard the foil, and bake for another 20 mins until biscuity. When the pastry is ready, remove it from the oven, pour in the lemon mixture and bake again for 30-35 mins until just set. Leave to cool, then remove the tart from the tin and serve at room temperature or chilled.','25 mins','1 hr'),
(1023,'raspberry bakewell slice','https://www.bbcgoodfood.com/recipes/raspberry-bakewell-slice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-420451_12-8aa8cc8.jpg','Get ahead this weekend and make this indulgent, freezable treat, perfect for pud or with a cup of tea',5,10,1,2,6,'Heat oven to 200C/180C fan/gas 6. Line the base and sides of a buttered traybake tin, about 18-20cm x 30cm, with baking parchment. Roll out the pastry to line. Lift into the tin and evenly press right into the corners. Prick with a fork and chill for 20 mins. Bake the pastry for 8-10 mins until it‘s cooked but not too coloured. Cool for a few mins and turn down the oven to 180C/160C fan/gas 4. Dot the raspberry jam over the pastry (there''s no need to spread) and scatter over the raspberries. For the sponge, put all the ingredients into a large bowl and beat with an electric whisk until soft and very well mixed. Spoon this over the raspberry layer, then smooth evenly. Scatter over the flaked almonds and bake for 35-40 mins until golden and firm. Cool completely in the tin. Will freeze for up to 3 months – overwrap the tin with baking parchment and foil beforehand. To serve, thaw for 4 hrs at room temperature, then reheat in a low oven. Melt the apricot jam with 1 tbsp water and brush over the top of the sponge just before serving.','45 mins','35 mins'),
(1024,'clementine custard tarts','https://www.bbcgoodfood.com/recipes/clementine-custard-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Clementinecustardtarts-67ae426.jpg','Try a festive twist on custard tarts by adding the zest and juice of clementines. They''re delicious served with an afternoon cuppa at Christmas',2,12,2,2,6,'Heat the oven to 200C/180C fan/gas 6. Bring the clementine juice to the boil in a small saucepan over a medium heat. Cook until it has reduced to about 50ml (this may take up to 20 mins). Leave to cool. Unravel the puff pastry. Scatter two-thirds of the clementine zest over it. Re-roll the pastry into a tight spiral from one of the short ends, then cut along its length at 2cm intervals into 12 even rounds. On a lightly floured surface, roll each round into a roughly 10cm circle. Line the holes of a 12-hole muffin tin with the pastry rounds. Chill in the fridge until needed. Put the eggs, egg yolks, cornflour, sugar, reduced clementine juice and the remaining zest in a pan, and whisk together to combine. Gradually whisk in the milk until the mixture is smooth, then warm over a low-medium heat until thickened and starting to simmer, about 8-10 mins. Remove from the heat, pour into a jug and leave to cool for a few minutes, whisking occasionally to prevent a skin from forming. Fill each pastry case with custard until almost full. Bake for 18-22 mins until the pastry cases are golden and the custard has browned slightly on top. Leave to cool completely in the tin. Dust with icing sugar, before serving, if you like.','25 mins','25 mins'),
(1025,'custard tart','https://www.bbcgoodfood.com/recipes/custard-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-741456_11-f1de437.jpg','With buttery pastry and a creamy filling, it''s no wonder this dish has become a classic',7,8,2,2,6,'To make the pastry, rub the butter into the flour with the lemon zest and a pinch of salt until it resembles breadcrumbs. Add the sugar, egg and milk and bring together to form a dough. This can be made 2 days in advance. On a lightly floured surface, roll the pastry out and use it to line a 20cm tart tin, leave 2cm of pastry hanging over the edge. Chill for 30 mins. Heat oven to 180C/160C fan/gas 4. Line the case with baking beans, bake blind for 20 mins, then remove the beans and continue to cook for a further 20 mins until the base is biscuity. Remove from oven and reduce the temperature to 140C/120C fan/gas 1. Bring the cream, milk, vanilla pod, lemon zest and a small grating of nutmeg to the boil. Beat the egg yolks with the sugar until pale, then pour the hot milk and cream over, beating as you go. Strain custard into a jug, allow to settle for a few mins, then skim off any froth. Carefully pour the custard into the tart case, grate some more nutmeg over the top and bake for 40 mins or until just set with the very slightest wobble in the middle. Remove from the oven, trim the pastry edges off (away from the filling), then leave to cool completely before serving in slices with a grating more of nutmeg, if you like.','25 mins','1 hr and 20 mins'),
(1026,'manchester tart','https://www.bbcgoodfood.com/recipes/manchester-tart','https://images.immediate.co.uk/production/volatile/sites/30/2021/01/Manchester-tart-59da211.jpg','End dinner on a retro note with this Manchester tart recipe. It''s easy enough to make midweek, and with a pastry base and custard filling, it''s also super-comforting',46,12,1,2,6,'To make the pastry, put the flour and butter in a bowl and rub with your fingertips until the mixture resembles breadcrumbs. Mix in the sugar and a pinch of salt, followed by the egg yolk, and bring it together into a dough. If the pastry is too dry to come together, add 1-2 tbsp cold water. Tip onto a work surface and form into a rough ball. Knead briefly. Roll the pastry out between two sheets of baking parchment into a roughly 28cm circle. Chill for 20-30 mins until firm, then use it to line a 23cm tart tin. Push the pastry into the corners, then trim the edge so there’s 1-2cm pastry overhanging the rim (don’t worry if it breaks slightly, as it can be patched with any off-cuts). Prick the pastry base several times with a fork, then chill for 45 mins. Heat the oven to 200C/180C fan/gas 6. Line the chilled pastry case with a sheet of baking parchment, then fill to the top with baking beans, uncooked rice or lentils. Bake for 15 mins, then remove the beans and parchment and bake for 10-15 mins more until the pastry is golden brown. Leave to cool in the tin, then trim the excess pastry using a small serrated knife. Meanwhile, make the custard. Bring the milk and vanilla to the boil in a large saucepan over a medium heat. Beat the egg yolks, caster sugar and custard powder together in a medium bowl until well combined. Slowly pour the hot milk mixture over the egg mixture, whisking continuously until everything is smooth and well combined. Tip the custard mixture back into the pan and cook over a medium-low heat, stirring continuously, for 2-3 mins until very thick. Remove from the heat and leave to cool slightly. Spread the jam over the base of the cooled pastry case. Carefully pour over the custard and smooth with the back of a spoon or spatula. Sprinkle over the coconut and dot the maraschino cherries around the edge in a circle. Chill for 2-3 hrs, or until set.','25 mins','25 mins'),
(1027,'mini bakewell tarts','https://www.bbcgoodfood.com/recipes/bakewell-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bakewell-tarts-9d665e9.jpg','Use shop-bought pastry to make our easy bakewell tarts. Simply layer jam, frangipane and icing inside a shortcrust case for a nostalgic teatime treat',41,12,1,2,6,'Heat the oven to 180C/160C fan/gas 4. Lightly butter a 12 hole muffin tin. Unroll the sheet of pastry and use a 10cm circular pastry cutter to cut 12 circles out of the pastry sheet. Press the cut circles into the holes of the tin, making sure they come right up and slightly over the top – pushing out any creases. Chill the pastry in the fridge for 20mins. Scrunch up 10cm x 10cm squares of baking paper and then un-scrunch and use to line each of the pastry tarts, then fill with baking beans, rice or dried pulses. Bake for 10 mins, then remove the paper and beans and bake for 10 mins longer, until golden brown. Set aside to cool a little. Make the filling by beating together the butter and sugar until light and fluffy, then whisk in the egg, followed by the flour (the flour will prevent the mixture from splitting). Fold in the ground almonds. Spoon a level tsp of cherry jam into each of the pastry shells, followed by a tablespoon of the frangipane mixture. Bake for 20 mins, until the frangipane is golden and springy. Set aside to cool completely. Neaten the edges of the pastry with a small knife or scissors if you like. Mix the icing sugar with 2 tbsp water. Spread the icing over each of the tarts, top each with a glacé cherry, leave to set for 20 mins, then serve with tea.','30 mins','40 mins'),
(1028,'next-level egg custard tart','https://www.bbcgoodfood.com/recipes/next-level-egg-custard-tart','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Egg-custard-tart-0198a27.jpg','Bake your own custard tart with our next-level recipe. We’ve updated this classic dessert with a few extra touches, including a new way to make pastry with brown butter',7,8,2,2,6,'First, make the pastry. Melt the butter in a saucepan until foaming, then sizzle until it turns nut-brown. Pour off the fat into a bowl, leaving the sediment in the pan to be discarded. Leave the fat to cool, then chill until it hardens again. Combine the flour, icing sugar and a pinch of salt in a medium bowl. When the fat has hardened, cut it into chunks and rub it into the flour mixture until crumbly. Stir in the egg yolk and 1 tsp cold water, and bring it together into a dough. Flatten into a disc, then cover and chill for at least 30 mins. Can be made up to two days ahead. (The pastry can also be made with regular butter.) Roll the chilled pastry out on a lightly floured surface to a roughly 5mm thickness, then lift into a 20cm tart tin. Press the pastry into the base and up the side using a small piece of excess floured pastry. Leave some overhanging the rim. Chill for at least 30 mins, or freeze for 10 mins. Reserve any excess pastry. Heat the oven to 180C/160C fan/gas 4. Line the pastry case with baking parchment and fill with baking beans or uncooked rice. Put on a baking tray and bake for 15 mins. Remove the parchment and beans and bake for 15-20 mins more until biscuity. Meanwhile, make the filling. Tip the cream and vanilla into a saucepan and bring to the boil. Stir (don’t whisk) the egg yolks and sugar together in a heatproof bowl. Slowly drizzle the hot cream over the egg yolk mixture, stirring continuously (not whisking) until everything is incorporated. Sieve the custard through a fine mesh sieve into a jug, then spoon off any froth. Remove the baked tart case from the oven, and reduce the temperature to 140C/120C fan/gas 1. Trim the excess pastry from the case, and mend any cracks with the reserved raw pastry. With the tart case on the baking tray, slide it halfway back into the oven, then carefully pour in the filling to the top of the case. Finely grate over at least half a nutmeg. Gently slide the tray fully into the oven and bake for 40 mins, or until just set with a very slight wobble. Leave to cool completely in the tin, then remove from the case and cut into slices.','30 mins','50 mins'),
(1029,'millionaire''s chocolate tart','https://www.bbcgoodfood.com/recipes/millionaires-chocolate-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-209483_11-eca84b2.jpg','A dinner party version of teatime favourite, millionaire''s shortbread. It''s not too bitter, so the kids will love it too',17,10,2,2,6,'Break the pastry into chunks and drop into a food processor. Drizzle over the vanilla paste and pulse until the vanilla is speckled through the pastry (the extract should be completely absorbed). Tip out onto a floured surface, bring together into a ball, then roll out to line a 23cm tart tin (leave any overhanging pastry as you will trim this away when the tart is baked). Chill for 30 mins. Heat oven to 200C/180C fan/gas 6. Line the pastry with greaseproof paper. Fill with baking beans, bake blind for 15-20 mins, then remove the paper and beans and bake for 5-10 mins more until pale golden. Carefully spread caramel over the base and set aside while you make the filling. Lower oven to 180C/160C fan/gas 4. Melt the chocolates in a bowl over a pan of barely simmering water, then stir in the melted butter. Whisk the eggs, yolks and sugar together with an electric whisk in a large mixing bowl for 10 mins, until pale and thick enough to leave a trail when the beaters are lifted up. Fold in the melted chocolate with a large metal spoon, then scrape into the tin. Bake for 20-25 mins – the surface should be set and puffed but still with a slight wobble. Cool, then chill for at least 3 hrs or overnight, before dusting with icing sugar and serving.','30 mins','55 mins'),
(1045,'classic rice pudding','https://www.bbcgoodfood.com/recipes/baked-rice-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2021/09/Rice-pudding-e008fc9.jpg','Have this creamy rice pudding cooking in the oven while you make dinner and round off your meal with a comforting pud. Serve with jam for a feel-good treat',14,4,1,2,6,'Heat the oven to 160C/140C fan/gas 3 and butter a 1.5-litre baking dish. Put the pudding rice, sugar, vanilla, milk and cream in a bowl and stir to combine. Pour the mixture into the prepared dish and grate over a generous layer of nutmeg. Bake for 1 hr 30 mins until a brown skin has formed on top and the rice is cooked and tender. Leave to cool slightly, then scoop the warm pudding into bowls and serve with a dollop of jam on top.','5 mins','1 hr and 30 mins'),
(1030,'butternut squash tarte tatin','https://www.bbcgoodfood.com/recipes/butternut-squash-tarte-tatin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/butternut-tarte-f662d6e.jpg','Revamp the classic tarte tatin and try our vibrant veg version. Our butternut squash tarte is delicious served warm with a scoop of vanilla ice cream',19,8,1,2,6,'Heat the oven to 180C/160C fan/gas 4. Lay the squash slices on a baking tray and sprinkle over a little of the caster sugar. Bake for 20 mins or until the squash is still slightly resistant to a sharp knife, but not hard. Don’t overcook the squash, or it will break when you try to move it. Meanwhile, scatter the rest of the sugar into an ovenproof frying pan about 20cm across, and heat it gently until it melts and starts to caramelise. Tip the pan if you need to combine patches of sugar that haven’t melted into the liquid parts, but don’t be tempted to stir. When you have a medium amber-coloured caramel, sprinkle on the spices and lemon juice. Turn the heat off. Take the squash out of the oven and leave to cool for a couple of mins. Use tongs to place the squash in the frying pan, gently arranging the slices in a tight spiral on the caramel. Start with a large slice in the centre and lay the other slices on top so it looks neat when you turn it out. When the squash is all in the pan, turn the heat on again and cook until the caramel starts to bubble. Turn the heat down and cook for about 10-15 mins, making sure the caramel doesn’t burn. You want the squash to give off any water and then to start absorbing the caramel – the caramel will start to get very liquid as this happens, so turn the heat up at the end and make sure the caramel thickens up completely. Turn the heat off, drop the butter into the centre of the pan and wait for it to melt. Cut the pastry into a rough circle just bigger than the pan and drape it over the squash, folding any excess over on itself and carefully tucking it in around the edge. Bake for 20 mins, then turn the heat down to 160C/140C fan/gas 3 and bake for a further 20 mins. Turn out onto a plate while the tarte is hot so the squash and caramel release easily. Serve with crème fraîche or ice cream.','30 mins','1 hr'),
(1031,'chocolate, hazelnut & salted caramel tart','https://www.bbcgoodfood.com/recipes/chocolate-hazelnut-salted-caramel-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-931468_11-eaa5b05.jpg','A buttery pastry case with a layer of caramel and just a hint of salt, studded with nuts and finished with a fudge filling',5,12,2,2,6,'To make the pastry, whizz the hazelnuts in a food processor until finely ground. Add the flour, icing sugar and butter, and pulse until the mixture resembles breadcrumbs. Add the egg yolk and 1-2 tbsp cold water, and pulse until the dough comes together. Tip the dough out and flatten into a disc, then wrap in cling film and chill for 30 mins. Heat oven to 180C/160C fan/gas 4. On a floured surface, roll out the pastry to line a 23cm loose-bottomed, deep tart tin. Trim the edges and prick the base with a fork, then line with baking parchment and fill with baking beans. Bake for 20 mins, then carefully remove the baking beans and parchment and bake for a further 5-10 mins until light golden. Allow to cool. Meanwhile, make the salted caramel. Tip the sugar into a small pan, add 1-2 tbsp water and heat gently to dissolve the sugar. Increase the heat and cook until the sugar turns to an ambercoloured caramel. Reduce the heat and add the butter, cream and golden syrup, and stir until the sauce is smooth and thickened. Remove from the heat and add the salt. Allow to cool for a few mins, then spread onto the tart base. Scatter with chopped hazelnuts and set aside. For the chocolate filling, melt the chocolate and butter in a heatproof bowl set over a pan of simmering water. Stir until smooth, then remove from the heat and allow to cool slightly. In a separate bowl, whisk the eggs, egg yolk and caster sugar for about 6 mins until thick and pale. Fold in the melted chocolate and cocoa, then pour into the tart case. Transfer to a baking sheet and cook for 20-25 mins or until set and the top has formed a crust. Allow to cool to room temperature before serving in slices.','40 mins','45 mins - 50 mins'),
(1032,'peach & almond tart','https://www.bbcgoodfood.com/recipes/peach-almond-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201784_10-6608107.jpg','Juicy peaches, sweet almonds, melt-in-the-mouth pastry... you''ll want a large slice of this one',121,8,1,2,6,'To make the pastry, rub together the flour and butter until it looks like fine breadcrumbs, then mix in the sugar. Add 1 egg yolk and 1 tsp water, followed by the next yolk and 1 tsp water until the dough just comes together. Wrap in cling film and refrigerate for 30 mins. Heat oven to 200C/180C fan/gas 6. Roll the pastry out on a lightly floured surface until big enough to line a 23cm tart tin. If the dough rips, simply patch it up. Cut away the excess pastry. Line with greaseproof paper, fill with baking beans, then bake for 10 mins. Remove paper and beans. Prick quite a few holes in the base of your tart, then bake for another 10-15 mins until biscuity. While the pastry cooks, make the filling. Whisk the sugar and butter together until you get a pale paste. Beat in the eggs and finally add the almonds and flour. Spoon into pastry case, arrange the peach slices across the top in a pattern and bake for 30-40 mins until golden. When cool, brush your preserve or jam across the top. Serve with a little pot of crème fraîche on the side and eat it quick before anyone else gets their hands on it!','25 mins','1 hr'),
(1033,'chocolate & date tart','https://www.bbcgoodfood.com/recipes/chocolate-date-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-tart-ef27fe1.jpg','Impress your guests with this decadent chocolate and date tart in a shortcrust pastry case. Suitable for vegans, it''s sure to go down a treat with everyone',41,8,2,2,6,'Un-roll the pastry sheet onto a lightly floured surface, mix the cocoa powder and icing sugar together. Use a sieve to sprinkle all over the pastry sheet, then fold in half like a book and chill for 15 mins. On a lightly floured surface, roll the pastry sheet into a slightly larger rectangle, big enough to line a 20cm tart tin. Line the tin, leaving any overhanging pastry – you''ll trim this away once the tart is baked. Chill for 30 mins on a baking tray. Heat oven to 200C/180C fan/gas 6. Line the pastry with greaseproof paper and fill with baking beans. Bake for 15-20 mins, then remove the paper and beans and bake for 15 mins more, until cooked through. Remove from the oven and leave the case to cool in its tin, then trim the sides. While the pastry is cooling, make the date caramel. Warm the coconut milk until steaming, then pour 120ml of the coconut milk over the dates. Soak for 5 mins then tip into a food processor with the vanilla extract and a big pinch of flaky salt. Blitz until smooth. Spread the caramel onto the base of the tart and put in the fridge for 30 mins to set a little. To make the ganache topping, heat the remaining coconut milk over a low heat until steaming. Put the chocolate and coconut oil in a large bowl and pour the warm coconut milk over the top. Leave to sit for a minute, then gently stir until combined. Spoon the mixture over the caramel and spread it out – don’t worry if the caramel comes up the sides a bit. Sprinkle with flaky sea salt and leave to set in the fridge for about an hour, then bring to room temperature before slicing and serving.','30 mins','40 mins'),
(1034,'custard tart with nutmeg pastry','https://www.bbcgoodfood.com/recipes/custard-tart-nutmeg-pastry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-54115_12-9ce7dd5.jpg','There''s something so honest about a custard tart, simply topped with a grating of nutmeg',5,12,2,2,6,'Heat oven to 200C/180C fan/gas 6. Roll the pastry out to about 1cm thick, then grate over a dusting of nutmeg using a fine grater. Fold the dough in half, then roll out again, to the thickness of 2 x £1 coins and large enough to line a 20cm loose-bottomed sandwich tin with some overhang. Press the pastry into the tin (leaving the excess draped over the sides), put onto a baking sheet and chill for 10 mins. Line the pastry with baking parchment and fill with baking beans. Bake for 20 mins, then remove beans and paper. Bake for another 15 mins until golden and sandy all over. Trim the edges of the tart with a sharp serrated knife. Turn oven down to 150C/130C fan/ gas 2. Whisk together the eggs and sugar in a large bowl. Put the cream, milk, vanilla pod and seeds into a saucepan and bring to the boil. Pour onto the eggs, whisking as you go. Sieve into a jug. Put the baking sheet onto the pulled-out oven rack, then pour in the custard, right to the top. You may not need every last drop. Grate over a nice layer of nutmeg, then slide gently back into the oven and bake for 1 hr. When it’s ready, the tart should be set and pale golden on the top, and have just the merest tremor in the centre when you jiggle the tray. Cool completely, then serve in slices.','15 mins','1 hr and 35 mins'),
(1035,'blueberry & almond tart','https://www.bbcgoodfood.com/recipes/blueberry-almond-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-613700_11-e182734.jpg','Use Mary Cadogan''s shortcrust pastry case recipe as the base for this gorgeous summery tart',43,8,1,2,6,'Heat oven to 190C/170C fan/gas 5. Beat together the butter and sugar until it is light and fluffy, about 1 min. Stir in the almonds, egg and almond extract. Stir in half the blueberries and spoon into the pastry case. Smooth the top using the back of a metal spoon, then scatter over the remaining blueberries, pressing them in lightly. Bake for 45-50 mins until the pastry is crisp and golden, and the filling is golden and feels firm to the touch. Cool the tart for 10 mins in the tin, then lift onto a serving plate. Dust with a little icing sugar and serve warm or at room temperature.','15 mins','50 mins'),
(1036,'banoffee pie','https://www.bbcgoodfood.com/recipes/banoffee-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banoffee-pie-9793520.jpg','An easy family favourite with buttery pastry and sweet dulce de leche. We recommend a generous dollop of cream to serve',29,8,1,2,6,'Start by making the pastry case. Put the butter and flour in a food processor and pulse until it resembles fresh breadcrumbs. Add the yolk of the egg and the sugar and pulse again until mixed through then, add a tbsp at a time of very cold water pulsing the processor after each addition. Stop adding water as soon as the dough starts to come together as one piece. Remove the dough from the processor and continue to bring together by kneading gently with your hands. Once you have a smooth ball of dough wrap it tightly in cling film and chill in the fridge for 30mins.  Heat oven to 190C/170C/gas 4 and grease a 23cm loose bottomed fluted tart tin. Roll out the chilled pastry on a lightly floured surface and use it to line the tin. Leave about half a cm of pastry proud of the edge of the tin - snipping it to length with kitchen scissors.  Line the base with a piece of baking parchment and weigh it down with baking beans or uncooked rice. Bake for 15 mins then remove the paper and beans and brush the pastry surface with beaten egg white. Return to the oven for a further 15-20 mins or until crisp, golden and cooked through. Leave to cool completely (can be done a day ahead).  Spread half of the caramel on the case of the pastry case and layer the sliced bananas on top. Cover with the rest of the caramel and place in the fridge. Whip the cream until billowy thick and spoon it over the top of the tart. To serve finely grate over a 1-2 pieces of dark chocolate to garnish. ','30 mins','30 mins - 35 mins'),
(1037,'caramelised passion fruit & lime tart','https://www.bbcgoodfood.com/recipes/caramelised-passion-fruit-lime-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5261_11-6aa1128.jpg','This zingy tart has a wonderful flavour and is a great way to cook with passion friut',7,8,1,2,6,'Roll out the pastry and use it to line a 23cm tart ring, then chill for at least 20 mins. Heat oven to 180C/fan160C/gas 4. Line the tart with baking parchment and baking beans, then bake for 15 mins. Remove the parchment, bake for a further 5-10 mins until golden, then remove from the oven and reduce the temperature to 140C/fan 120C/gas 1. While the case is cooking, cut the passion fruits in half, scoop out the flesh into a bowl, then blitz with a hand blender. Push the pulp through sieve and mix the juice with the lime zest and juice. Beat the sugar and yolks together until pale, then beat in the cream and fruit juice. Carefully pour the custard into the tart case, then bake for 40 mins, until set with a very slight wobble. Remove the tart from the oven, then leave to cool completely. You can now either serve the tart as it is, or try a chef’s tip and dust it with icing sugar, then caramelise the top with a blowtorch.','30 mins','40 mins'),
(1038,'chocolate crunch with caramel sauce','https://www.bbcgoodfood.com/recipes/chocolate-concrete-caramel-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-concrete-6877b37.jpg','Update a classic school-dinner recipe by using caramel sauce instead of pink custard to serve alongside chocolate squares. Kids (and big kids) will love it',19,12,1,2,6,'Heat oven to 180C/160C fan/gas 4. Put the flour, almonds, cocoa, caster sugar and a large pinch of salt into a food processor and whizz briefly. Add the butter and whizz until the mixture resembles damp sand. Generously butter the base and sides of a small (15 x 25cm) baking tin. Tip the mixture into the tin and press it down. Bake for 30 mins. Remove from the oven, sprinkle over some demerara sugar and cut into squares using a sharp knife while still it’s soft. Meanwhile, heat the caramel and cream together in a pan. Serve the concrete warm with the caramel sauce. You can cool any leftovers and eat them cold – they will harden like shortbread.','15 mins','30 mins'),
(1040,'strawberry cheesecake in 4 easy steps','https://www.bbcgoodfood.com/recipes/strawberry-cheesecake-4-easy-steps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1028453_10-36f879a.jpg','Follow our step-by-step recipe for this easy no-cook cheesecake – a delicious summer dessert for all occasions',5,12,1,2,6,'To make the base, butter and line a 23cm loose-bottomed tin with baking parchment. Put the digestive biscuits in a plastic food bag and crush to crumbs using a rolling pin. Transfer the crumbs to a bowl, then pour over the melted butter. Mix thoroughly until the crumbs are completely coated. Tip them into the prepared tin and press firmly down into the base to create an even layer. Chill in the fridge for 1 hr to set firmly.For more detail on how to make a biscuit base, take a look at our how to make a biscuit base video. Slice the vanilla pod in half lengthways, leaving the tip intact, so that the two halves are still joined. Holding onto the tip of the pod, scrape out the seeds using the back of a kitchen knife. Pour the double cream into a bowl and whisk with an electric mixer until it’s just starting to thicken to soft peaks. Place the soft cheese, icing sugar and the vanilla seeds in a separate bowl, then beat for 2 mins with an electric mixer until smooth and starting to thicken, it will get thin and then start to thicken again. Tip in the double cream and fold it into the soft cheese mix. You’re looking for it to be thickened enough to hold its shape when you tip a spoon of it upside down. If it’s not thick enough, continue to whisk. Spoon onto the biscuit base, starting from the edges and working inwards, making sure that there are no air bubbles. Smooth the top of the cheesecake down with the back of a dessert spoon or spatula. Leave to set in the fridge overnight. Bring the cheesecake to room temperature about 30 mins before serving. To remove it from the tin, place the base on top of a can, then gradually pull the sides of the tin down. Slip the cake onto a serving plate, removing the lining paper and base. Purée half the strawberries in a blender or food processor with the icing sugar and 1 tsp water, then sieve. Pile the remaining strawberries onto the cake, and pour the purée over the top.','30 mins','No Time'),
(1041,'self-saucing sticky toffee chocolate pudding','https://www.bbcgoodfood.com/recipes/self-saucing-sticky-toffee-chocolate-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/12/Molten_chocolate_sticky_toffee_pudding-694940c.jpg','Combine sticky toffee pudding sponge with a generous helping of chocolate chunks and chocolate sauce for the ultimate comforting dessert',46,8,1,2,6,'Put the dates in a bowl and pour in 300ml boiling water. Leave to soak for 30 mins. Meanwhile, make the sauce by tipping all the ingredients, a big pinch of salt and 300ml boiling water into a pan, then whisk over a medium heat and simmer for 2 mins. Pour into a jug and leave to cool slightly. Once the dates are soaked, use a hand blender to blitz the dates and water until you have smooth paste. Leave to cool slightly. Butter a deep 35 x 25cm dish. Heat oven to 180C/160C fan/gas 4. Beat together the butter and sugar for 3 mins until smooth. Add the eggs, one at a time, beating between each addition. Fold in the flour, bicarb and baking powder along with a pinch of salt. Once combined, mix in the date purée, then fold in the dark chocolate. Pour the sponge batter into the prepared dish and spread out evenly using the back of a spoon. Pour the chocolate sauce evenly over the top of the batter. Bake in the oven for 30-35 mins until risen, then leave to rest for 2 mins. Serve warm with a big scoop of ice cream or warm custard.','20 mins','40 mins'),
(1042,'jam & coconut sponge puddings','https://www.bbcgoodfood.com/recipes/raspberry-coconut-steamed-sponge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raspberry-coconut-steamed-sponge-2f8a8c4.jpg','Need a no-fuss, make-ahead dessert that’s comforting and great for serving after Sunday lunch? Look no further than our raspberry and coconut steamed sponge',12,6,1,2,6,'Butter six small pudding basins and spoon 1 tbsp jam into the bottom of each one. Put the butter and sugar in a medium bowl and beat with an electric whisk until pale and fluffy. Beat in one of the eggs followed by a spoonful of the flour, then continue until all the eggs have been incorporated. Fold in the remaining flour, the coconut and a pinch of salt, then the vanilla and coconut cream. Spoon the mix into the prepared basins, leaving a 1cm gap at the top. Put the basins on a baking tray, transfer to the freezer and freeze until solid. Will keep in an airtight container, or tightly wrapped in the freezer for up to three months. Heat the oven to 180C/160C fan/ gas 4. Put the basins in a deep baking dish and half-fill with boiled water from the kettle. Tightly cover the dish with foil and bake for 1 hr, or until a skewer inserted into the middle of a sponge comes out clean. Warm the custard with the remaining 2 tbsp coconut cream, if using, and pour into shallow bowls. Run a knife around the sides of the basins and carefully turn out the sponges onto the custard.','15 mins','1 hr'),
(1043,'classic bread & butter pudding','https://www.bbcgoodfood.com/recipes/classic-bread-butter-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/classic-bread-butter-pudding-a2e2843.jpg','Cook this bread and butter pudding in a water bath for a gorgeous soft and creamy texture that contrasts wonderfully with the caramelised crust',52,6,1,2,6,'Heat the cream in a pan until boiling. Meanwhile, whisk the egg yolks and sugar together in a large bowl with an electric whisk until light and fluffy, about 3-5 mins. Pour the hot cream over the eggs, whisking continuously, and when everything is mixed, set aside. Spread the bread with the butter, and cut into triangles. Arrange in a shallow baking dish, about 20 x 26 x 5cm, buttered-side up, in three layers, sprinkling the raisins and finely grating some nutmeg over the bottom two layers and leaving the top layer clear. Pour over the warm custard, lightly pressing the bread down with your fingers to help it soak in. Leave to stand for at least 20 mins before cooking. Can be prepared up to this stage several hours ahead, chilled and cooked when needed. Heat the oven to 180C/160C fan/gas 4. Sit the dish in a large roasting tin and pour a kettleful of hot water into the roasting tin so it comes three-quarters of the way up the outside of the dish. Bake in the oven for 20-25 mins until the custard is just set. Remove the pudding from its water bath and scatter the top liberally with more sugar. Caramelise the top under a hot grill or with a blowtorch – take it really far (a few burnt edges add to the flavour), but be careful it doesn’t catch. Leave the pudding to rest for 5 mins, then serve with your favourite ice cream – I like bitter orange marmalade ice cream.','20 mins','25 mins'),
(1044,'blueberry & orange traybake pancake','https://www.bbcgoodfood.com/recipes/blueberry-orange-traybake-pancake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberry-orange-traybake-pancake-cb2cb11.jpg','Serve this blueberry and orange traybake pancake at the end of a family dinner for a crowd-pleasing dessert. Swap the blueberries for raspberries, if you like',19,6,1,2,6,'Heat oven to 180C/160C fan/gas 4. Brush a 25 x 20 x 2cm baking tray with butter. Tip the flour and baking powder into a bowl and mix in the egg, milk, butter and caster sugar. Stir in the orange zest and 50ml juice. Scrape the mixture into the tin and level the surface. Dot the blueberries over the top and bake for 20 mins or until the middle of the pancake feels cooked through when pressed lightly. Dust with icing sugar and serve with more blueberries, blueberry compote or ice cream, if you like.','5 mins','30 mins'),
(1048,'strawberry shortcake slice','https://www.bbcgoodfood.com/recipes/strawberry-shortcake-slice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/strawberry-shortcake-slice-5de45ff.jpg','Layer biscuits with strawberries and cream and they''ll soften to create an indulgent no-bake dessert. Super easy and delicious, it''s a fabulous summer treat',12,14,1,2,6,'Line a 20cm cake tin or dish (either a square or rectangle works well) with cling film, leaving an overhang. Whip 500ml of the cream, the vanilla, orange blossom water and 125g icing sugar with an electric whisk until thick and billowy. Add a layer of biscuits to the tin, and spoon over some of the cream, about 1cm thick all over. Add a layer of strawberry slices, then repeat with the cream, biscuits and strawberries until you fill the tin, finishing on a layer of biscuit. You’ll have some strawberries left over to serve. Press everything down well so every biscuit is covered in cream. Cover and chill overnight. To serve, flip the tin onto a serving plate, and remove the cling film. Whip the remaining 100ml cream with 1 tbsp icing sugar and dollop on top of the cake, swirling it around. Top with the remaining strawberry slices in lines, sift over the remaining 1 tbsp icing sugar, and sprinkle the biscuit crumbs in between the rows of strawberries. Cut into slices to serve.','25 mins','No Time'),
(1049,'rhubarb crumble','https://www.bbcgoodfood.com/recipes/rhubarb-crumble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-219487_11-9c9ae75.jpg','Make this classic rhubarb crumble for an easy, family-friendly dessert. Use seasonal rhubarb and a handful of ingredients to make a stunning pud.',121,4,1,2,6,'Tip 500g thumb-length chunks of rhubarb into a saucepan with 100g golden caster sugar and 3 tbsp port, if using. Cover and simmer on a very low heat for 15 mins, adding more sugar if you want. When soft (but still holding its shape) and sweet enough, pour the rhubarb into a medium baking dish. Heat oven to 200C/180C fan/gas 6. To make the topping, rub 140g self-raising flour and 85g chilled butter together with your fingers until you have a soft, crumbly topping. Now add 50g light brown muscovado sugar and 50g chopped walnuts, if using. Mix together with your hands. Scatter the topping over the rhubarb and bake for 30 mins or until golden brown on top. Serve piping hot with a big jug of thick vanilla custard. Refer to the tip for a recipe.','20 mins','1 hr'),
(1050,'treacle tart','https://www.bbcgoodfood.com/recipes/treacle-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/treacle-tart-6ed628f.jpg','Treat family and friends to a comforting treacle tart with crumbly pastry and rich filling. A classic British dessert, serve with ice cream or clotted cream',14,10,1,2,6,'Sieve the flour and salt into a large bowl. Add the butter and rub together with your fingers to a fine breadcrumb-like texture (you can also do this part in a food processor). Stir though the icing sugar, then quickly add the egg yolks and 2 tbsp water, mixing swiftly with a cutlery knife to combine. Form into a ball (add another tbsp water if you need to), wrap and chill for 30 mins. Roll out to the thickness of a pound coin, and line a 22cm fluted tart tin with the pastry, leaving an overhang. Return to the fridge for 30 mins. Heat the oven to 200C/180C fan/gas 6. Put a baking sheet into the oven to heat up. Line the pastry case with baking parchment and baking beans, then put in the oven on the baking sheet, and bake for 15 mins. Remove the parchment and bake for a further 10 mins or until the pastry is golden brown. Leave the pastry to cool before trimming off the overhanging edges with a small serrated knife. Lower the oven temperature to 160C/140C fan/gas 4. Combine the golden syrup, ginger, ginger syrup, lemon, eggs and breadcrumbs in a bowl, briefly whisking everything together until combined. Carefully pour the filling into the pastry case and put in the lower part of the oven to bake for 35-40 mins or until the filling is just set. Remove from the oven and leave to cool down for 20 mins before serving with ice cream or thick clotted cream.','30 mins','1 hr and 5 mins'),
(1051,'peanut butter & jam pudding','https://www.bbcgoodfood.com/recipes/peanut-butter-and-jam-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Peanut-butter-and-jam-pudding-6ae332f.jpg','The popular combo of jam and peanut butter works beautifully in this sponge pudding. The salty, nutty flavour you get from the peanuts matches so well with the jammy fruit',14,8,1,2,6,'Heat the oven to 180C/160C fan/gas 4. Tip the berries into a large saucepan with the jam, and heat gently for about 5 mins until just starting to turn saucy. Spoon some of the liquid into a small bowl and mix with the cornflour. Stir the cornflour mixture into the fruit mixture in the pan, and simmer for 2 mins. Pour into the base of a baking dish (ours was 18 x 25cm), then set aside. Beat the butter and sugar together in a stand mixer, or using an electric whisk, for 5 mins until pale and fluffy. Add the eggs, one at a time, beating between each addition, then the vanilla, flour and peanut butter, beating briefly to combine. Stir in the milk to loosen the mixture. Carefully spoon the batter into the dish over the berries, then smooth the surface gently with the back of a spoon and scatter over the nuts. Bake for 45-50 mins. Leave to cool for 10 mins before serving warm with vanilla ice cream or custard.','15 mins','1 hr'),
(1052,'lemon & rhubarb rice pudding','https://www.bbcgoodfood.com/recipes/lemon-rhubarb-rice-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lemon-rhubarb-rice-pudding-d0326f8.jpg','Infuse traditional rice pudding with lemon curd and rhubarb for a zesty yet comforting dessert. It''s a real treat on a cold day',14,6,1,2,6,'Put the pudding rice in a pan with the milk and caster sugar and bring to the boil. Reduce the heat and simmer for 40-45 mins, or until the rice is tender, stirring often to ensure it doesn’t catch on the bottom. Meanwhile, cut the rhubarb into 3cm pieces and put in a pan with the caster sugar, stem ginger and the syrup. Bring to a simmer and cook for 7-10 mins, or until the rhubarb is soft but still holds its shape. Stir the double cream and lemon curd through the rice pudding, then top with some of the poached rhubarb.','5 mins','1 hr'),
(1053,'chocolate brownie cake','https://www.bbcgoodfood.com/recipes/chocolate-brownie-cake-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-809539_11-f9b1b31.jpg','Get kids cooking at the weekend by baking this yummy brownie cake, much better than shop bought',5,6,1,2,6,'Heat oven to 180C/fan 160C/gas 4. Grease and line a 20cm cake tin. Place the butter, caster sugar, brown sugar, chocolate and golden syrup in the pan and melt gently on a low heat until it is smooth and lump-free. Remove the pan from the heat. Break the eggs into the bowl and whisk with the fork until light and frothy. 5 Add the eggs, vanilla extract or essence, flour, baking powder and cocoa powder to the chocolate mixture and mix thoroughly. Put the mixture into the greased and lined cake tin and place on the middle shelf of the oven. Bake for 25-30 mins. Remove and allow to cool for 20-30 mins before cutting into wedges and serving. Serve with cream or ice cream and plenty of fresh fruit.','15 mins','N/A'),
(1054,'courgette muffins','https://www.bbcgoodfood.com/recipes/courgette-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-392465_11-2cb6e62.jpg','Get kids busy in the kitchen with these scrummy courgette muffins, perfect for cooks aged between 8-14',5,12,1,2,11,'Brush the muffin tin with oil. Ask your grown-up helper to switch the oven to 190C/ 170C fan/gas 5. Grate the courgettes and put them in a large bowl. Grate the apple and add to the bowl. Squeeze the orange and add the juice to the bowl. Break the egg into a bowl; if any bits of shell get in, scoop them out with a spoon. Stir the butter and egg into the courgette and apple mix. Sieve the flour, baking powder and cinnamon into the bowl. Add the sugar and sultanas. Mix with a spoon until everything is combined, but don’t worry if it is lumpy. Spoon the mixture into the tin. Ask your helper to put it in the oven and cook for 20-25 mins. Cool in the tin, then spread some icing on each.','35 mins','25 mins'),
(1055,'chocolate cornflake cakes','https://www.bbcgoodfood.com/recipes/cooking-kids-chocolate-cornflake-cakes-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1049503_11-0679f31.jpg','Everyone loves crispy chocolate cornflake cakes, whatever their age. Get the whole family in the kitchen to make these simple bites',44,12,1,2,11,'Children: Weigh out the ingredients. Older children can do this by themselves with supervision and little ones can help to pour or spoon ingredients into the weighing scales. Put 50g butter, 100g milk or dark chocolate, broken into chunks and 3 tbsp golden syrup in a saucepan or microwavable bowl. Put 100g cornflakes in another large bowl. Grown ups: Melt the weighed butter, chocolate and golden syrup in the saucepan over a low heat or briefly in the microwave. Allow to cool a little before pouring over the cornflakes. Children: Stir the ingredients together gently using a wooden spoon. Spoon the mixture into 12 cupcake cases arranged on a muffin tray (or baking sheet, if you don’t have one). Grown ups will need to do this for younger children or simply arrange on a tray and let the mess happen. Put in the fridge to set.','10 mins','5 mins'),
(1056,'no-bake orange cheesecake','https://www.bbcgoodfood.com/recipes/no-bake-orange-cheesecake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-47412_11-f0749ab.jpg','This is an easy cheesecake for kids to prepare with just a little supervision – they''ll love smashing up the biscuits for the base.',5,8,1,2,11,'Crush the biscuits roughly. You can do this by putting them in a plastic food bag and crushing them with a wooden spoon. Put the biscuits into a bowl and mix in the melted butter. Put the mixture into a 23cm cheesecake tin, then use the back of a spoon to spread it evenly and press it down in the tin to form the base. Put the tin into the fridge to set. This should take about 30 mins. Put the curd or cream cheese, orange zest, mascarpone, sugar and milk into a bowl and mix it well with the wooden spoon. Put the cream in a clean bowl and whisk until it is the consistency of thick custard. Add to the orange mixture and mix thoroughly. Remove the cheesecake tin from the fridge. Pour the filling over the biscuit base and spread evenly. Decorate with segments of orange and return to the fridge until ready to serve (at least 4 hours or overnight).','25 mins','No Time'),
(1057,'raspberry ripple cake','https://www.bbcgoodfood.com/recipes/raspberry-ripple-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560500_12-904e3cc.jpg','Rainy day? Get the kids in the kitchen and have some fun making this fruity, fun cake',8,10,1,2,11,'Grease a 23cm-deep, loose-bottomed cake tin and cut out a circle of baking parchment to fit into the bottom. Cut a thick strip of parchment to fit around the side of the tin. Heat oven to 190C/fan 170C/gas 5. Put the sugar and butter into a large mixing bowl. Using an electric whisk (or wooden spoon if you’ve got more time and energy), beat them together until creamy and soft. Crack in the eggs, one by one, giving a good beat after each addition. Stir in the vanilla extract, then the flour and baking powder, then stir through the milk to loosen the mixture. Carefully stir in half the raspberries, making sure you don’t mix too vigorously or they will fall apart. Cook the cake for 25-30 mins until just about cooked through. Then, carefully take it out of the oven. Scatter the marshmallows all over the cake, trying to leave a 2cm border round the edge of the tin as the marshmallows will expand when cooking, then tuck raspberries into any gaps. Return to the oven for another 10 mins until the cake has fully cooked through and the marshmallows are lightly browned and gooey. Leave for 5 mins, then ease out of the tin, cut up and eat.','10 mins - 20 mins','40 mins'),
(1058,'father''s day cupcakes','https://www.bbcgoodfood.com/recipes/dads-chocolate-drop-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560903_11-b6f79b6.jpg','Dad''ll love these easy cupcakes, make them really chocolatey or put his favourite sweet or nut on top - the perfect treat!',33,12,1,2,11,'Line a 12-hole muffin tin with paper cases and heat oven to 190C/fan 170C/gas 5. In a jug, mix the yogurt, eggs and vanilla extract. Put the dry ingredients, plus a pinch of salt, into a large bowl and make a well in the middle. Add the yogurty mix and melted butter, and quickly fold in with a spatula or metal spoon – don’t overwork it. Spoon into the cases (they will be quite full) and bake for 18-20 mins or until golden, risen and springy to the touch. Cool for a few mins, then lift the cakes onto a wire rack to cool completely. Keep in an airtight container for up to 3 days or freeze as soon as possible. Chocolate frosting: Melt the chocolate in the microwave on High for 1½ mins, stirring halfway. Leave to cool. Beat the butter and icing sugar in a large bowl until creamy. Beat in the chocolate. Cover and chill for up to one month. Up to 48 hrs before serving (or the day before if it’s really hot), bring back to room temperature, then spread over the cakes. Put the chocolate buttons on. Keep cool, out of direct sunlight.','20 mins','N/A'),
(1059,'iced fairy cakes','https://www.bbcgoodfood.com/recipes/iced-fairy-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2013/04/Iced-fairy-cakes-7a55f02.jpg','Teach 8-14 year olds how to make fairy cakes and guarantee a fun kitchen activity',5,12,1,2,11,'Ask a grown-up helper to turn the oven on to 180C/160C fan/gas 4. Put a paper case in hole of a 12-hole bun tin. Put the sugar and butter (it must be soft or you won’t be able to mix it properly) in a bowl and mix it together. Sift in the flour. Break the eggs into a separate bowl (spoon out any bits of shell that fall in) and add them to the bowl with the vanilla. Mix everything together. Divide between the cases using a spoon, scraping it off with a knife. Ask a grown-up helper to put the tray in the oven for 20 minutes. Mix the butter and icing sugar to make a creamy icing. Add colouring, if you like. Push an icing nozzle into an icing bag, then scoop in the icing. Let the cakes cool completely in the tray. Pipe icing onto each cake and decorate with marshmallows or sprinkles, or whatever you like.','40 mins','20 mins'),
(1060,'very chocolatey cake','https://www.bbcgoodfood.com/recipes/very-chocolatey-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-443463_12-ca0551c.jpg','An indulgent chocolate cake that kids can make for a friend''s birthday or as a family treat',5,6,1,2,11,'Ask a grown-up to switch the oven on to 180C/160C fan/ gas 4. Break the eggs into a small bowl and scoop out any bits of shell, then tip them into a large bowl. Put the sugar and butter in a bowl, then sift over the flour, baking powder and cocoa. Beat everything together using a wooden spoon or electric beaters, then stir in the chocolate drops. Spoon half of the mixture into each tin and smooth the tops. Ask a grown-up to bake the cakes for 20-25 mins or until risen and a skewer comes out clean. Cool in the tin for 5 mins, then turn out onto a rack. Beat the butter and icing sugar, then fold in the chocolate. Spread over the middle, sandwich the cakes together, then dust with icing sugar.','35 mins','25 mins'),
(1061,'sticky upside-down banana cake','https://www.bbcgoodfood.com/recipes/sticky-upside-down-banana-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sticky-upside-down-banana-cake-06460f1.jpg','Edd Kimber gives us an irresistible new take on the seventies classic. This stunning upside-down cake makes a great cooking project for teen cooks',75,12,1,2,11,'Heat oven to 180C/160C fan/gas 4 and lightly grease a deep 23cm round cake tin, lining the base with baking parchment. To make the topping, put the butter and sugar in a small saucepan and cook over a medium heat until melted and combined. Add the vanilla and mix together. Pour the caramel into the cake tin and spread in an even layer. Cut the bananas in half lengthways and arrange on top of the caramel, cut-side down. To make the batter, put all the ingredients in a large bowl with 1/4 tsp salt. Use an electric mixer to beat together on a low speed until the batter is smooth and evenly mixed. Pour the batter on top of the bananas and level out with a spatula. Bake in the oven for 55 mins-1 hr or until a skewer inserted in the middle comes out clean. If the cake begins to look too dark, cover with foil after 45 mins. Allow to cool in the tin for 10 mins before turning out onto a plate. Best served warm but also delicious at room temperature. Best eaten within 2 days.','15 mins','1 hr and 5 mins'),
(1062,'ice cream cone cakes','https://www.bbcgoodfood.com/recipes/ice-cream-cone-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/icecreamcone_0-ca05c01.jpg','Wafer cones make an unlikely but perfect case for light custard and vanilla sponge- decorate with buttercream and your favourite ice cream toppings',17,10,1,2,11,'Heat oven to 180C/160C fan/gas 4. Sit the cones in a muffin tin to hold them upright. Put the butter, flour, custard powder, vanilla, sugar and eggs in a large mixing bowl. Beat together with an electric whisk until smooth. If you have nimblefingered little helpers, hand round pairs of teaspoons and set them to work filling the cones. If you’re making them yourself, spoon the cake batter into a food bag or disposable piping bag, snip off the end to give you a wide hole, then pipe into the cones, filling them ¾ full – this will enable you to get the batter right to the bottom. Bake the cone cakes, still in the muffin tin, for 30 mins until a skewer poked into the centre of the cake comes out clean. Leave to cool. To decorate the cakes, beat the butter until smooth, then add the icing sugar and vanilla, and beat again until well mixed. Put into a piping bag fitted with a big star nozzle, then pipe icing on top of each cake as you would a cupcake. Decorate with sprinkles, cherries, drizzles of sauce – whatever you like.','15 mins','30 mins'),
(1063,'giant cookie cake','https://www.bbcgoodfood.com/recipes/giant-cookie-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/giant-cookie-cake-5257dd0.jpg','Studded with white and milk chocolate chips, Lisa Lipman''s moreish cake is delicious served either warm with a dollop of ice cream or cold, with its chewy cookie texture',5,8,1,2,11,'Heat oven to 200C/180C fan/gas 6 and grease a 22cm springform cake tin. Beat the butter and sugar in a large bowl until pale and fluffy, then mix in the egg, extra yolk and vanilla extract. Combine the flour, cornflour and baking powder, then fold into the butter mixture until fully incorporated – it will be quite thick. Add the chocolate chips and mix until evenly distributed. Spoon the mixture into the prepared tin and bake for 20-25 mins or until light golden brown. Leave to cool for 5 mins. Serve warm with vanilla ice cream.','20 mins','20 mins - 25 mins'),
(1064,'blueberry lemon cake with coconut crumble topping','https://www.bbcgoodfood.com/recipes/blueberry-lemon-cake-coconut-crumble-topping','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-741507_11-68787f2.jpg','This sticky, crumbly bake is irresistible - it''s a pudding straight from the tin',5,16,1,2,11,'Heat oven to 180C/fan 160/gas 4 and grease and line a 20 x 30 cm cake tin with baking parchment. Beat together 250g of the butter with 250g sugar and the lemon zest until light and fluffy. In a separate bowl, break up 4 eggs with a fork, then gradually beat into the butter and sugar mixture, adding a spoonful of flour if it begins to curdle. When the eggs are incorporated, fold in the flour and a third of the blueberries, then spoon into the tin. Flatten the surface with a spatula, sprinkle over another third of the blueberries, and bake for 20 mins until the surface is set. To make the topping, melt the rest of the butter, then stir in the coconut and remaining sugar and eggs until combined. Warm the lemon curd gently for a few mins in a small pan until it is runny and pourable. After the initial baking, scatter the remaining blueberries over the top of the cake, drizzle over the lemon curd, and crumble over the coconut mixture. Bake for a further 20-25 mins until the coconut is golden. Leave the cake in the tin to cool. Cut into 16 squares and store in a tin, interlined with greaseproof paper.','15 mins','45 mins'),
(1065,'peach melba pop pies','https://www.bbcgoodfood.com/recipes/peach-melba-pop-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pop-tarts-1b22f2b.jpg','These easy peasy pies are stuffed with flavour and are great fun for little fingers to decorate',6,12,1,2,11,'Set 6 plump raspberries aside and tip the rest into a bowl. Add the peaches and toss together. In a small bowl mix the cornflour and honey to make a paste, pour over the fruit and combine. Unroll the pastry sheets and use a pizza wheel to cut out 6 rectangles from each one. Turn a piece of pastry so the long side is nearest to you, and fold it in half like a book, to create a fold down the middle. Open the pastry out and spoon the fruit filling onto one side, leaving a border of about 1cm around the edge. Brush the beaten egg around the edges and fold the pastry again to encase the filling. Use a fork to seal the edges all the way around, then brush all over with more egg. Poke a few air holes in the top with the fork. Repeat with the remaining pastry and filling. Arrange the pop pies on a baking sheet lined with baking parchment and chill for at least 30 mins, or for up to 24 hrs. Alternatively freeze for up to 2 months. Heat oven to 200C/180C fan/gas 6. Bake the pop pies for 20 mins, or until the pastry is golden and the filling is bubbling through the holes. Remove from the oven and cool for at least 20 mins. If cooking from frozen, bake for an extra 5 mins. Meanwhile crush the remaining raspberries until juicy and mix with the icing sugar to make a thick icing. Spread thinly over the pies and sprinkle with freeze dried raspberries or sprinkles, if you like. Eat warm, or cold, for breakfast or drizzle with cream or custard for dessert.','30 mins','20 mins'),
(1066,'mixed fruit tart','https://www.bbcgoodfood.com/recipes/mixed-fruit-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mixed-fruit-tart-c0e3e90.jpg','This tasty tart is a great introduction to pastry and gives budding young chefs a chance to flex their creative muscles as they decorate with vanilla mascarpone and a variety of berries',75,8,2,2,11,'Heat oven to 180C/160C fan/gas 4. To make the pastry, put the flour, sugar and a pinch of salt in the bowl of a food processor, then pulse to combine. Add the butter and pulse until the mixture resembles coarse breadcrumbs. Add the egg yolk and 3 tbsp very cold water, and pulse until the mixture just starts to come together. Tip the dough onto a work surface and use your hands to bring together into a uniform dough. Wrap in cling film and chill for 1 hr. Can be made up to 3 days in advance. On a lightly floured work surface, roll out the pastry and use to line a 23cm tart tin. Trim off the excess and chill for 30 mins. Prick the chilled pastry shell with a fork and line with a crumpled piece of baking parchment. Fill with baking beans or rice, and bake in the oven for 20 mins. Remove the parchment and put the pastry back in the oven for 12-14 mins or until golden. Take out of the tin and put on a wire rack to cool completely. To make the filling, put the mascarpone, cream, vanilla and icing sugar in a large bowl and whisk until it just holds soft peaks. Spread the cream mixture into the pastry case in an even layer. Decorate with the fruit, however you like. Best eaten on the day.','25 mins','35 mins'),
(1076,'cheese & spinach pasties','https://www.bbcgoodfood.com/recipes/cheese-spinach-pasties','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1071530_11-b1308ad.jpg','Teach your kids some pastry skills - and provide a lunchbox filler - with this recipe from CBeebies ''i can cook''',5,6,1,2,11,'Heat oven to 200C/180C fan/gas 6. Line the baking tray with baking parchment. Put the spinach onto pieces of kitchen paper. Pat flat to remove any water, then put in the bowl. Add the cheeses and nutmeg, stir together and set aside. Using a flour dredger, sprinkle some flour on the work surface. Roll out the pastry to a long rectangle (step 1). Using the biscuit cutter, cut out 6 circles (step 2). Brush the edges with water (step 3), then spoon the spinach mixture into the middle of each. Fold the edges together and squeeze them. Brush the tops with more water and make holes with a fork (step 4). Bake for 15-20 mins until golden, then enjoy warm or leave to cool completely and pack into lunchboxes.','15 mins','20 mins'),
(1067,'easy apple pie','https://www.bbcgoodfood.com/recipes/apple-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apple-pie-0fb9ad7.jpg','Kids will love to help make and eat this comforting, classic fruity pudding with homemade shortcrust pastry',5,4,1,2,11,'Heat oven to 200C/180C fan/gas 6. To make the pastry, sift the flour into a large mixing bowl and add the butter or margarine. Using your fingers, mix together until the mixture resembles breadcrumbs. Add about 3 tbsp cold water – 1 tbsp at a time – to bind the mixture into a ball. Then wrap it in cling film and leave to chill in the fridge while you prepare the apples, or for 30 mins if you have time. While the pastry is chilling, core the apples, then cut into even-sized chunks so they all cook in the same amount of time. Put the apples into the pie dish, drizzle over the honey and add the cinnamon, mixed spice and about 2 tbsp water. Roll out the pastry on a floured work surface until it is large enough to cover the pie dish. Using the rolling pin, carefully lift the pastry and lay it over the top of the apple mixture. Carefully trim off the excess pastry (this can be rerolled and cut into shapes to decorate the pie crust if you like) and press the pastry edges onto the dish to create a seal. Make a small cut in the pastry so that the air can escape during cooking, then brush with beaten egg to glaze. Bake the pie in the oven for 20-30 mins until the pastry is golden and sandy in appearance and the apple filling is bubbling and hot. Serve while still warm with crème fraîche, ice cream or natural yogurt.','30 mins','20 mins - 30 mins'),
(1068,'curly twirlies','https://www.bbcgoodfood.com/recipes/curly-twirlies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/twirlies-0f396b3.jpg','Use ready-to-roll croissant pastry to create these chocolate and toffee-filled treats that are gooey and sticky when eaten warm',17,12,1,2,11,'Heat oven to 200C/180C fan/gas 6. Put the toffees into a small pan with the milk and melt over a very low heat, stirring occasionally to bring together to a smooth sauce. Cool off the heat for 1-2 mins until a little less molten and thickened slightly. Cover two baking sheets with baking parchment. Unroll one tube of croissant dough. Pinch together the diagonal perforated seams, but divide the dough into three rectangles along the straight crossing seams. Cut each rectangle into 4 long, thin strips – so you end up with 12. Drizzle a little toffee along the length of each strip, then scatter with some chocolate drops. Unroll the other dough and cut into the same size strips, then sandwich together with the toffee-choc covered ones. Holding the ends of each strip, twist tightly and lay on the prepared baking sheets – they’ll expand a little. Once all are shaped, bake for 12-15 mins until golden and crisp. Dust with a little golden icing sugar to serve and enjoy best, freshly baked.','20 mins','20 mins'),
(1069,'sausage plait','https://www.bbcgoodfood.com/recipes/sausage-plait','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1136490_11-2de86c5.jpg','A step-by-step recipe for kids from CBeebies series ''i can cook''- the roll contains peppers and chilli and teaches pastry skills',5,4,1,2,11,'Heat oven to 200C/180C fan/ gas 6. Grease a baking tray with oil using a pastry brush, then cover it with baking parchment. Put to one side. Remove the meat from the sausage skins by snipping off the ends, then squeezing the sausagemeat into a bowl (see step 1). Cut the pepper into small pieces with scissors. Break the egg into the cup, beat with a fork, and save 2 tbsp for glazing. Add the red pepper and remaining egg to the sausagemeat with the chilli flakes, if using, and purée. Mix well with a fork or clean hands (step 2). Sprinkle some flour on the work surface. Using a rolling pin, roll out the pastry into a rough square shape, about 30 x 30cm. Put the pastry on the lined baking tray (step 3). Now spoon the filling down the middle of the pastry in a sausage shape – leave a little gap at the top and bottom (about 3cm) (step 4). Cut the pastry at a slight diagonal, on either side of the filling, into 1.5cm strips, the same number each side – we cut 12 strips each side. Brush the pastry all over with most of the saved egg (step 5). Tuck the top and bottom edges of the pastry over the filling. Starting at the top, lay the pastry strips over the filling, taking one from each side, to cross like a plait. Now brush the top all over with the last of the egg. Bake for 35-40 mins or until golden. Serve hot or cold with baked beans or salad (step 6).','30 mins','40 mins'),
(1070,'cheese stars','https://www.bbcgoodfood.com/recipes/cheese-stars','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheese-stars-c3c0024.jpg','Show off your cooking skills and get in the festive spirit with these fun looking easy cheesy treats - they make great party snacks for grazing guests',29,20,1,2,11,'Unroll the pastry and sprinkle most of the cheese over one half of the sheet. Fold the pastry in half to cover the cheese and seal it in. Dust your rolling pin and work surface with a little flour and put your pastry sheet on it. Roll it out until doubled in size. Heat oven to 200C/180C fan/gas 6 and line two baking sheets with baking parchment. Using cookie cutters, cut the pastry into shapes and put them on the prepared baking sheets. (Put the cutters on the dough close together so that less pastry is wasted. You can fold the trimmings back on themselves and re-roll to cut out more shapes.) Brush the shapes with milk and add a pinch of cheese and a sprinkling of your chosen topping, if using. Bake in the oven for 10-12 mins or until risen and golden, then transfer to a serving plate. Will keep in an airtight container for 3 days.','15 mins','10 mins - 12 mins'),
(1077,'tomato tart','https://www.bbcgoodfood.com/recipes/tomato-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-356477_12-0d66092.jpg','This easy-to-make tomato tart is a fun way of getting kids aged 8-14 involved in the kitchen',5,4,1,2,11,'Ask a grown-up helper to turn the oven on to 200C/180C fan/gas 6. Mix the soft cheese together with the mustard in a small bowl. Cut the tomatoes into thick slices. Roll the pastry into a rectangle on a floured surface – don’t worry if the edges are uneven. Lift the pastry onto a baking sheet Now trim off any really wiggly bits. Mark a border the thickness of two fingers around the edge. Spread the soft cheese inside the border, then arrange the tomato slices on top. Decorate with anchovies or olives (if using). Grate the Parmesan all over. Ask a grown-up to put the tart in the oven for 30 mins or until the pastry is golden and the tomatoes are cooked.','30 mins','30 mins'),
(1071,'mince & pea pies','https://www.bbcgoodfood.com/recipes/mince-pea-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mince-pea-pies-cd7ef81.jpg','Make these meat pies in individual portions and freeze them so you can pull one out when you''re short on time. The kids will love them',19,4,1,2,11,'Fry the mince in a little oil over a high heat, stirring to break up any lumps, until it’s well browned all over. Transfer the mince to a plate, then fry the onion, carrots and celery in the rest of the oil over a low heat until softened. Stir in the tomato purée and crumble in the stock cube, then return the mince to the pan and give everything a good stir. Fry for a minute, then add 300ml water. Cover with a lid and simmer for 20 mins, then remove the lid and simmer until the sauce has thickened slightly. Stir in the peas, then turn off the heat and leave to cool for 10 mins Heat oven to 200C/180C fan/gas 6. Divide the mince mixture between four individual pie dishes (or use one large dish). Brush the dish rims with egg. Unroll the pastry and cut it into four pieces for the individual pies – roll the pieces out a little more to fit the pie dishes if you need to. Top each pie with some pastry, press down against the rim and trim any excess. Seal the edges with a fork, or crimp if you like, then brush the pastry with egg. Poke a little hole in the top, decorate with any offcuts if you like (brushed with a little egg), then cook for 25-30 mins or until the pastry is golden and risen. Cool for 5-10 minutes before serving.','20 mins','55 mins'),
(1072,'crumbly chicken & mixed vegetable pie','https://www.bbcgoodfood.com/recipes/crumbly-chicken-mixed-vegetable-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-411668_11-22ae4d0.jpg','Let your children pick and choose which veg they like best, and get them to help make this simple pie',5,4,1,2,11,'Heat oven to 200C/fan 180C/gas 6. Peel and chop the onion as finely as possible. Melt the butter in the pan over a medium heat. Add the chopped onion and leave to cook for 5 mins, stirring occasionally. Finely slice the mushrooms and add them to the pan with the onions. When the onions and mushrooms are almost cooked, add the flour to the pan and stir to make a thick paste called a roux. Crumble the stock cube in and stir well. Continuously stir the roux over a low heat for 2 mins to cook out the flour. This stops the sauce from having a starchy taste. Slowly add the warm milk to the pan, stirring all the time. Once all the milk has been added, season with salt, pepper, nutmeg and mustard powder. Add the bay leaf and bring to the boil, stirring all the time to stop the sauce becoming lumpy. When the sauce has thickened, add more milk or water as necessary to get the desired consistency, bring to the boil, then turn down to a very low heat. Chop the cooked chicken into even-sized pieces and add to the sauce. Add your veg and pour into the pie dish. Put the pastry on a clean, floured surface. Make sure you also dust the rolling pin with flour. Roll out the pastry, trying not to make any tears. Measure the pastry against the pie dish and once the pastry is slightly larger than the top, lift it carefully over the dish. It might be helpful to use the rolling pin to help you move the pastry. Trim off the pastry hanging over the edge of the dish. Press the outside edge of the pastry with a fork or your fingers to make a nice pattern. Brush the top of the pie with beaten egg or milk. Make a small hole in the centre of the pastry top to allow the steam to escape. You can use any leftover pastry to decorate the top of the pie with pastry shapes if you like. Place the pie in the oven for 25 mins. It is ready when golden brown.','30 mins','45 mins'),
(1073,'chicken & sweetcorn pies','https://www.bbcgoodfood.com/recipes/chicken-sweetcorn-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-243465_11-41592be.jpg','This fun and easy-to-make dish is perfect for getting kids aged 8-14 years busy in the kitchen ',5,6,1,2,11,'Ask a grown-up helper to turn the oven on to 180C/160C fan/gas 4. Roll out the pastry on a floured surface and trim to make a rectangle about 24 x 36cm. Cut the pastry in half, lengthways, then cut each half into 3 equal squares, about 12cm along each side, using a small knife or scissors. Push each square into the oiled tin, making sure it is pushed right into the edges Use scissors or a small knife to cut the chicken into strips, then cut into chunks. Put chunks in a bowl. Add the sweetcorn, peas, cream and mustard. Mix together. Divide mixture between the pies. Fold the tops of the pies over roughly and press together. Don’t worry if they don’t cover all the filling. Brush the pastry with the beaten egg. Ask your grown-up helper to put them in the oven for 35 minutes or until they brown and the filling bubbles.','25 mins','35 mins'),
(1074,'sausage roll twists with tomato dip','https://www.bbcgoodfood.com/recipes/sausage-roll-twists-tomato-dip','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7363_11-9618dc3.jpg','Perfect for kids birthday parties - just make sure the mums and dads don''t eat them all!',5,24,1,2,11,'Heat oven to 220C/fan 200C/gas 7. Roll out the pastry to £1 coin thickness (about 20 x 30cm) and cut into strips about 1cm wide, cutting from the shorter edge. Thin each sausage a little by twisting, then snip in half. Wind one pastry strip around each half-sausage, then place on a baking sheet, pastry ends down. Bake for 20 mins until the sausages and pastry are golden. Meanwhile, mix together the ketchup, vinegar and cherry tomatoes. Serve in little bowls alongside the sausage twists.','10 mins','20 mins'),
(1075,'red pepper, ham & cheese tart','https://www.bbcgoodfood.com/recipes/red-pepper-ham-cheese-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-218505_11-e60a638.jpg','A sheet of ready-rolled puff pastry makes this tart easy as pie',5,4,1,2,11,'Heat oven to 220C/200C fan/gas 7. Unroll the pastry onto a baking sheet and score a border 1cm from the edge. Prick inside the border with a fork and spread with the sundried tomato paste. Bake for 12-15 mins. Squash down the puffed-up pastry inside the scored rim, then top with the ham, cheese, peppers and olives. Sprinkle with oregano and bake for 10-12 mins more until the base is nice and crisp. Scatter with rocket leaves and serve.','5 mins','30 mins'),
(1079,'strawberry tartlets','https://www.bbcgoodfood.com/recipes/strawberry-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-285540_11-2940222.jpg','Get your kids in the kitchen to create these teatime treats, perfect for 8-14 year-olds to show off their cooking skills',5,4,1,2,11,'Ask a grown-up to turn the oven to 180C/fan 160C/gas 4. Roll the pastry out on a floured surface until it’s as thick as £1 coin. Cut round a saucer to make 4 circles. Put each one into the tin. Put a square of greaseproof paper and some rice in each. Ask a grown-up to put them in the oven for 25 minutes, then take paper and rice out. Leave to cool. Use a whisk to whisk the cream until it starts to get thick, then stir in the lemon curd. Pull the green hulls out of the strawberries, and cut each one carefully into slices with a small knife. Spoon the cream into 4 pastry cases. Arrange the strawberry slices in a spiral or in rows on top of the lemon cream. Mix the jam in a bowl with 1 tbsp hot water, then brush it over the strawberries.','30 mins','25 mins'),
(1080,'quick cheese straws','https://www.bbcgoodfood.com/recipes/quick-cheese-straws','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-561031_11-741168c.jpg','Delicious cheesy sticks, perfect for dipping, just as good on their own - ready in under 20 minutes.',8,8,1,2,11,'heat oven to 220c/fan 200c/gas 7. Unroll a puff pastry, scatter over a couple of handfuls of grated Parmesan, then fold in half. On a lightly floured surface, roll out to the thickness of a £1 coin. Cut into 1cm strips, then twist the strips 3-4 times. Lay on a baking sheet, scatter over more cheese and bake for 12 mins, or until golden. Leave to cool, then keep in an airtight container for up to 2 days. As these cheese straws are very delicate, roll them in kitchen paper before packing into a plastic container.','5 mins','12 mins'),
(1081,'strawberry toffee tart','https://www.bbcgoodfood.com/recipes/strawberry-toffee-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2333_11-c1c4707.jpg','Easy-to-make and perfect for kids',43,6,1,2,11,'Line a 20cm flan tin with baking parchment. Put the biscuits in a strong food bag and bash with a rolling pin to finely crush them. Tip into a bowl and mix in the melted butter. Press over the base of the tin. Chill for about 30 mins until it feels firm. Slice or halve the strawberries, depending on their size. Remove the biscuit base from the flan tin and slide onto a flat serving plate. Put 2 tbsp of the cream in a small bowl with the toffees, then whip the rest until it just holds its shape in soft folds. Fold in the yogurt, then spoon over the biscuit base and cover with strawberries. Melt the toffees and cream together in the microwave on Medium for 30 secs-1 min, then stir until it forms a sauce. Drizzle over the tart filling. The tart can be chilled for a couple of hours at this stage. Dust with icing sugar before serving.','45 mins','N/A'),
(1082,'easy sausage rolls','https://www.bbcgoodfood.com/recipes/super-sausage-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-rolls-aa4a59d.jpg','Eat these family-friendly sausage rolls with a chutney or pickle. They make a great snack for a party buffet',19,12,1,2,11,'Roll out the pastry to a 35 x 30cm rectangle on a surface lightly dusted with flour. Trim the edges neatly, then cut in half lengthways to form two long strips. Spread with a thin layer of the apple sauce, pickle or chutney, leaving a border along the edges. Tip the sausagemeat into a large bowl, add 3 tbsp cold water and squash together. Divide the mixture in two and mould each half into a cylindrical shape. Put each portion of meat into the middle of a pastry strip, leaving a border at either side. Brush the pastry border and the top of the sausage mix with the beaten egg. Fold one edge of the pastry over the meat and roll to encase, then use a fork to press the pastry edges together. Cut the sausage rolls into 5cm lengths and arrange on a lined baking tray. Chill for 20 mins. Can be made a day ahead or frozen for up to one month; to bake from frozen, add an extra 10 mins to the cooking time. Heat oven to 200C/180C fan/gas 6. Brush the sausage rolls with the rest of the beaten egg and sprinkle with the sesame seeds or nigella seeds (if using). Bake for 30-35 mins until the pastry is deep golden. Transfer the sausage rolls to a wire rack and leave to cool for 10 mins.','25 mins','35 mins'),
(1083,'tuna & sweetcorn slice','https://www.bbcgoodfood.com/recipes/tuna-sweetcorn-slice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-874514_11-f1f038c.jpg','This easy budget supper uses ready-rolled pastry to make an open pie - experiment with your own toppings',5,4,1,2,11,'Heat oven to 220C/200C fan/ gas 7. Lay the pastry out on a baking sheet. Pinch up the edges to form a border, pressing firmly into the corners. Prick the centre all over with a fork and pop in the oven for 10-15 mins. Meanwhile, mix the tuna and sweetcorn in a bowl and season. Remove the pastry from the oven, pressing the centre down with the back of a fork, as it will have puffed up a bit. Spread the crème fraîche across centre, spoon the tuna mix on top, then sprinkle over the cheese. Bake for 10-15 mins more, until golden, puffed up and cooked though. Sprinkle with chives and cut into quarters.','5 mins','25 mins - 30 mins'),
(1084,'digger cake','https://www.bbcgoodfood.com/recipes/digger-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/digger-cake_0-c4711f0.jpg','You don’t need to be an engineer to make this impressive construction site birthday cake, just a few toys, some chocolate coated wafer bars and a little creative flair',33,20,1,2,11,'Heat the oven to 180C/fan 160C/gas 4. Use a little of the butter to grease the sides and bases of two 20cm sandwich tins. Melt the rest of the butter in a small saucepan. Off the heat, add the milk, yogurt and vanilla, followed by the eggs. Beat well with a fork. Put the dry ingredients plus ¼ tsp salt into a large bowl. Whisk to combine – this aerates and saves sifting. Tip in the wet ingredients and whisk to a smooth, silky batter. Don’t hang around at this point. Pour the batter evenly into the prepared tins and put onto the middle shelf in the oven. Bake for 25 mins or until risen and a skewer inserted into the middle of the cakes comes out clean. Cool for 10 mins in the tins, then carefully invert the cakes and leave to cool upside down on a cooling rack.  Make the buttercream. Stir the cocoa into 2 tbsp milk until smooth. Put the butter into a large bowl and sift the icing sugar on top. Add the cocoa mixture, vanilla, and a pinch of salt. Beat for a few mins with electric beaters until creamy and spreadable. Add the extra 1 tsp milk if you need to. Place one of the cakes towards the back of the board or plate, and fix it with a little of the buttercream to stop it slipping about. Spread with ¼ of the buttercream. Sandwich the second cake on top. With a small, serrated knife, cut away a scallop-shaped crater from the top cake. Cut about half way down into the sponge and removing about a quarter of the top of the cake. Keep the piece you remove. Position two diggers on the top of the cake to see if your crater is about the right size and shape, then set them aside again. Using a palette knife, paddle the rest of the buttercream evenly over the top of the cake and down its sides. Make sure that the edge of the excavated area is defined. Snap the ends from enough of the KitKat fingers to follow the edge of the excavated area, then press them into the buttercream. Now fix the rest of the whole fingers around the side of the cake. Crumble the cake that you removed in step 6, to make soil. Part-fill the excavated hole with the soil, adding a few Smarties, if you like. Position the diggers on top and put some soil and Smarties into their buckets. Fill a lorry or dump truck with more crumbs and Smarties, and let some of the soil and spill over the edge of the cake and the broken KitKats. Use the marker to write your message on the card and draw some diagonal black lines. Cut the straight part of the straw into two equal pieces, then fix these to the back of the card with tape to make a sign. Position the sign at the back of the cake. If the sponges are used fresh or within a day of baking (wrap well once cooled), the finished cake will keep in a cool place (not the fridge) for 2 days.','20 mins','25 mins'),
(1085,'pirate ship and treasure island cake','https://www.bbcgoodfood.com/recipes/pirate-ship-and-buried-treasure-island-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pirate-ship-534d036.jpg','A stunning children''s birthday chocolate cake with hidden treasure buried on a sandy desert island for the kids to dig into',94,20,2,2,11,'Heat the oven to 160C/140C fan/gas 3. Grease and line a 30x20cm cake tin with baking parchment. Put the butter and 200g of the chocolate into a saucepan and heat gently until melted. Remove from the heat. Sieve the flour, baking powder and bicarbonate of soda into a large bowl. Mix in the sugar. Beat together the eggs, soured cream and vanilla extract. Pour the chocolate and egg mixtures into the flour and sugar and beat well until smooth. Pour into the prepared tin and bake in the oven for about 40 minutes. Leave until completely cold before cutting and shaping. Cut the cake in half lengthways. Place one complete half on the 30cm cake board. Cut the remaining half into 3 rectangles, 13x10cm, 7x10cm and 10x10cm. Attach the 10x10cm piece to one end of the base cake using a little warm jam. Cut a ‘V’ shape to represent the prow of the ship. Place the 13x10 piece at the other end of the base cake, attaching with jam. Using jam again attach the 7x10 piece on top of the 13x10 piece to make the upper deck. Slice the back of the ship at a very slight angle. Brush the whole cake with jam. Roll out about 550g of the ready-to-roll icing and cover the cake. Trim the edges at the base and keep any trimmings. Melt the remaining 100g chocolate in the microwave or in a heatproof bowl over a saucepan of barely simmering water. Use melted chocolate to stick the mini Toblerones to the edges of the two decks and to the ‘V’ shape of the prow. Trim any to fit exactly. Use the chocolate to stick on the chocolate balls for canon balls, finger biscuits stacked up as canons and chocolate caramels for port holes. Using a little of the marzipan, roll out and cut four windows. Make window frames from brown icing trimmings, rolling out to thin sausages to form a cross and sticking with chocolate. Attach to the sides of the ship with chocolate. Poke the chocolate stick out of the front of the prow, if using. Arrange the chocolate coins in a little pile on the cake board, reserving 3 or 4 coins for the top. Roll out the remaining brown icing and cover the coins completely. Brush with melted chocolate and sprinkle with muscovado sugar. Push 3 or 4 coins into the sand along with the chocolate skull if using. Put a little of the royal icing in a bowl. Colour the remainder blue. Spread the icing on the cake board around the ship and the island. Use the white icing to make ‘surf’ around the edge of the island. Using the remaining marzipan and brown icing, make three barrels. Place one on the deck of the ship and the others in the sea. Cut out sails from the paper and make holes in them so you can push the straws through. Attach the sails to the ship and poke sandwich flags out of the top to finish.','2 hrs and 30 mins','45 mins'),
(1086,'pretty princess birthday cake','https://www.bbcgoodfood.com/recipes/pretty-princess-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/princess-cake-c811626.jpg','Pull out all the stops with this sensational celebration cake, a princess doll enrobed in pretty pink and white icing - perfect for a special birthday party',5,16,1,2,11,'Heat the oven to 160C/140C fan/gas 3. Grease and line a deep 15cm round cake tin, making sure the lining is higher than the top of the tin. Grease and base line a 1 litre ovenproof glass pudding basin. Using electric beaters or a tabletop mixer, beat the butter, sugar, vanilla and ¼ tsp salt together until pale and fluffy, then pour in the eggs, one at a time, giving the mix a really good beating before adding the next. Add 1 tbsp of the plain flour if the mix starts to look slimy rather than fluffy. Beat in the yogurt. Mix the flours; then, using a large metal spoon, fold them into the batter, and spoon into the prepared tin and bowl. The mixture will come almost to the top. Place the bowl and cake tin on a baking tray. Cook in the oven for about 1 hour 40 to 1hour 50 mins. Allow the cakes to cool completely. To make the butter icing, put the butter into a bowl and beat well until softened. Gradually beat in the icing sugar. Trim the tops of the cakes level. Slice the pudding basin cake in three and the 15cm cake in half. Using a 5cm round cutter, stamp out the centre of each piece of cake. Place one half of the 15cm cake on a cake board. Spread with a little butter icing and place the other half on top. Spread a little icing on this layer and top with the widest layer of the basin cake. Repeat with the other layers. Spread the remaining butter icing all over the assembled cake. Take 250g white fondant icing and roll out into a long strip about 7cm wide. Press the edge gently with a teaspoon to give a frilled effect or use a ball tool. Attach to the base of the cake, pushing gently onto the icing. Roll out the pink icing to a large circle about 36cm in diameter. Cut a hole from the centre using the 5cm cutter. Cut away just under an eighth section of the icing. Drape the icing over the cake, making the join at the back. At even intervals push up the pink fondant to create a swagged look. Spray the cake lightly with the edible lustre. Take the doll and wrap the legs in plastic film. Place the doll in the cake. The top of the cake should come to hip not waist level. Using the trimmings, roll out an oblong of pink icing and wrap around the body of the doll to make the bodice. Smooth the bottom of the bodice onto the top of the skirt. Roll out some white fondant and cut out a 9cm circle using a fluted or flower cutter. Cut a small circle from the centre of this and then split one side and place around the waist of the doll, cutting away any excess. Use the remaining white icing to make a large bow and place at the back of the dolls waist. Make up the royal icing according to pack instructions. Using a piping bag fitted with a small plain nozzle (or use a strong plastic bag and cut off the tip), pipe around the edge of the pink skirt. Attach a rose to the top of each swag. Pipe three lines of dots just above the roses going up the dress. Pipe across the top of the bodice and half way down the front. Using the icing attach a sugar butterfly to the top of the bodice and one large sugar rose at the waist.','2 hrs','1 hr and 40 mins - 1 hr and 50 mins'),
(1087,'surprise piñata football cake','https://www.bbcgoodfood.com/recipes/surprise-pinata-football-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/foodball-d3497ce.jpg','Slice into this sensational mint chocolate celebration cake and let the hidden sweets pour out for your party guests to enjoy',94,16,1,2,11,'Heat the oven to180C/160C fan/gas 4. Grease and base line a 20cm round cake tin with baking parchment. To make the chocolate cake, put the butter into a bowl and beat until soft. Add all the remaining ingredients and beat well until light and creamy. Spoon into the tin and spread level. Bake in the oven for about 45 mins or until a skewer inserted into the centre of the cake comes out clean. Cool. When cold, slice the top off the cake to make it level. To make the mint cake, grease and base line your 20cm cake tin with baking parchment. Put the butter into a bowl and beat until soft. Add the remaining ingredients and beat well until light and creamy. Spoon into the tin and spread level. Bake in the oven for about 45 mins or until a skewer inserted into the centre of the cake comes out clean. Cool. Do not slice the top level as it needs to be rounded. To make the butter icing, put the butter into a bowl and beat until soft. Gradually beat in the icing sugar. Slice the chocolate and mint cakes in half horizontally so you have four layers. Using an 8cm round cutter, stamp a circle from the centre of each layer. Place the bottom layer of chocolate cake on a 25cm cake board. Spread with a little butter icing. Place the bottom green layer on top. Spread with butter icing and place the other chocolate layer on top. Spread with more icing and place the rounded green sponge on top. Fill the centre hole with sweets, almost to the top. Take one of the 8cm green sponge pieces and slice in half horizontally. Place one piece on the cake to cover the hole containing the sweets. Spread the top with the remaining butter icing. Roll out about 300g of the white fondant icing to a 20cm circle and place on top of the cake. Brush the sides of the cake with apricot jam. Measure the circumference of the cake and the depth. Add 2.5cm to the depth measurement. Using these measurements roll out the green fondant to a long strip. (You may find it easier to do two shorter strips). Attach the strip around the cake. Use scissors snip around the top edge to represent grass. Make a cardboard template of a pentagon (5 sides). Roll out the black icing and cut out six pentagons. Place them on the cake as for a football. If you wish to stick them in place use a little icing sugar mixed with water. With the remaining white icing trimmings make a scarf and paint with food colouring in your team colours. Drape the scarf over the edge of the cake. Add candles as desired.','2 hrs','1 hr and 30 mins'),
(1088,'fire engine cake','https://www.bbcgoodfood.com/recipes/fire-engine-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fire-engine-8dc5f66.jpg','Stun a crowd and get creative with this themed celebration cake - details include a chocolate ladder, icing water splash and toy firemen',94,16,2,2,11,'Heat the oven to 180C/160C fan/gas 4. Grease and base line a 20cm square cake tin with baking parchment. Put the butter and sugar into a bowl and beat until light and fluffy. Add the eggs, almond extract, flour, ground almonds and milk and beat very well until light and pale in colour. Spread into the prepared tin and bake in the oven for 35–40 mins. Remove and allow to become completely cold before cutting and icing. To make the butter icing, put the butter into a bowl and beat well. Gradually beat in the icing sugar until smooth and creamy. Cut the cake in half to make two rectangles each 10x20cm. Take one half and cut into two pieces measuring 10x14cm and 10x6cm. Using a little butter icing, attach the 10x6cm piece to one end of the 10x20cm piece. This is the cab part of the engine. Take the 10x14cm piece and slice in half horizontally. Spread a little butter icing on the main part of the fire engine (behind the cab) and stick on one of the 10x14cm pieces. The remaining piece of cake is not required. Using a sharp knife, slice the front of the cab at an angle where the windows will be placed. Spread the whole cake with butter icing. Roll out the red fondant and use to cover the fire engine. Trim around the base. Roll out all but 50g of the grey icing to an irregular strip and place on a 30cm cake board. Carefully lift the fire engine onto the board. Unroll about half the liquorice from each of the four wheels. Attach the strips, using a little of the red icing, to make a double border around the base of the cake. Again using the icing, attach the four wheels. Use leftover liquorice to make strips to mark the centre of the road. Roll out the reserved grey icing and cut out 6 shutters and two wing mirrors. Attach to the cake using red icing. Use the white icing to make two front and two side windows for the cab. Attach with red royal icing and pipe around each window and pipe the cab doors. Stick on dolly mixtures to represent headlights, roof lights and ladder supports. Make a ladder from the chocolate covered biscuit or mint sticks, chopping and sticking with icing. Attach to the top of the engine with icing. Stick together the two party rings and attach to the side of the fire engine. Roll the yellow fondant icing into a long sausage and wrap around the biscuits to represent the hose, snake out onto the board and then make a small circle of yellow icing and stick on the end for the nozzle. Colour the remaining royal icing a pale blue colour. Thin with a little water if necessary and drizzle onto the cake board to represent water flowing from the hose. Using a little blue colouring thinned with water, paint the windows with a fine paint brush. Using black colouring, paint windscreen wipers on the front windows.','2 hrs','40 mins'),
(1089,'easy castle cake','https://www.bbcgoodfood.com/recipes/easy-castle-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/castle-9d794d5.jpg','A magical three-tiered sponge cake that''s simple to make, but no one would ever guess...',94,20,1,2,11,'Heat the oven to 160C/140C fan/gas 3. Grease and base line a round 13cm cake tin and a round 18cm cake tin. Place two muffin cases in a muffin tin. Put the butter and sugar into a large bowl and beat together until light and creamy. Add all the remaining ingredients except the jam and beat well. Half fill the muffin cases and then fill the two cake tins 3/4 full. Bake in the oven placing the cake tins on one shelf and the muffin tin on another. Open the oven door and remove the muffins and smaller cake quickly when they are done as you don''t want to let the heat out of the oven. The muffins will take about 35 mins, the 13cm cake about 1 hour 10 mins and the 18cm cake about 1 1/2 hours. You know each is done when a skewer inserted into the centre comes out clean. Allow the cakes to become completely cold before icing. Trim the tops of the two cakes so they are flat. Slice each one in half, spread with jam and then sandwich together again. Using a 6cm cutter, cut each muffin to make a cylinder. Trim the tops flat. Sandwich together with jam to make a tower. To make the butter icing put the butter into a bowl and beat until soft. Gradually beat in the icing sugar. Spread the butter icing over both cakes and the muffin tower. Roll out the white icing and use to cover the two cakes. Place the larger one on a cake board. Put the smaller one on top. Roll a strip of white icing and wrap around the muffin tower. Place on top of the cake. Push a wooden skewer though the cakes to hold them together. Using about 150g pink icing, mould into an onion shape for the dome. Put to one side. Roll out a little pink icing and cut out a 6cm round and place on top of the muffin tower. Using the made up royal icing, attach the dome to the top of the tower. Using the pink icing make two doors and two curtains. Attach to the cake using royal icing. Make windows by rolling out the lilac icing and cutting out hearts using medium, small and tiny cutters. Attach these upside down to the cake with royal icing. Roll a small piece of lilac icing into a thin sausage and stick around the curtains to make the window frame. Again using the royal icing, stick on the pieces of gum around the doors, and the sugar cubes around the edge of the cakes. Attach silver balls to the dome to decorate and to the doors to make handles. Score horizontal lines down the doors to give a textured effect. Attach a piece of gum under each window on the middle tier and then stick three flowers on top to make window boxes. Colour the remaining royal icing green and spoon into a piping bag fitted with a plain nozzle. Pipe creeper branches up the castle walls. Attach mini sugar flowers. Stick coloured flags or candles into the middle tier of the cake. Sprinkle a little edible glitter over the dome and sugar cubes.','2 hrs','1 hr and 30 mins'),
(1090,'easy volcano cake','https://www.bbcgoodfood.com/recipes/volcano-cake-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1079486_11-619bc81.jpg','Decorate a towering chocolate sponge with sweets and molten toffee ''lava'' to create a dazzling children''s treat',94,12,1,2,11,'Preheat the oven to 180C/160C fan/gas 4. Grease and base line an 18cm round cake tin, a 800–900ml oven proof (Pyrex) bowl and a 1.2 litre oven proof bowl. Put the butter, 300g of the sugar, eggs, flour and milk into a large bowl and beat well until light and creamy. Spread just under half the mixture in the cake tin. Take just under half of the remaining mixture and put into a bowl and colour deep red with food colouring. Put 3–4 small spoonfuls of red cake mix onto the mixture in the cake tin. Using the handle of a teaspoon pull the red mix through to give a marbled effect. Using the remaining plain and red cake mix place spoonfuls in a random fashion in the two prepared bowls. Again use the teaspoon handle to give a marbled effect. Bake in the oven for about 30 mins for the small bowl, about 35 mins for the medium and 50–60 mins for the cake tin. The mixture is cooked when firm to the touch and when a skewer inserted into the centre comes out clean. When the cakes are cold trim off any peaks that have formed. Place the round cake on a large cake board. Cut three small ‘v’ shapes on the edge of the cake, to make an uneven shape for the volcano base. Spread a little apricot glaze all over the cake and place the larger bowl cake, rounded side up, on top. Brush with apricot glaze and place the remaining cake, rounded side up, on top. Cut a small hollow in the top of this cake (to represent the ‘mouth’ of the volcano) and then brush the cake with apricot glaze. Roll out the marzipan to a large circle to cover the cake. It can be quite a rough shape. Lift it onto the cake and drape in a random fashion in the nooks and crannies to make a volcano shape. Melt the chocolate in a microwave or in a bowl over a pan of hot water. Using a pastry brush, paint the volcano with chocolate. Attach chunks of chocolate, popcorn etc with melted chocolate. Add the dinosaurs. Drizzle any remaining chocolate over the volcano in a random fashion. To make the lava put the remaining 50g sugar into a small non-stick frying pan. Heat gently without stirring until the sugar has melted but is still clear. Lightly oil a metal baking sheet. When the sugar is just turning light brown at the edge, remove from the heat and carefully stir in a little red paste food colouring. Using a teaspoon, drizzle lava shapes onto the prepared tray. Leave to cool completely. When cold, carefully lift the lava shapes and place in the top of the volcano. For extra effect use birthday candles, cake sparklers, or cake fountains.','1 hr and 15 mins','1 hr'),
(1091,'birthday bug cake','https://www.bbcgoodfood.com/recipes/birthday-bug-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-364196_11-f58bf40.jpg','Children will love the butterfly and ladybird decorations on this colourful cake – and you''ll love its simplicity',33,16,1,2,11,'Bake the Easy vanilla cake in a greased, lined deep 20cm cake tin as in the basic recipe; drench with syrup and leave to cool. Leave the oven on. Break the white chocolate into cubes into a microwaveable bowl, and heat on High for 1 min (or melt over a pan of simmering water). Stir, then leave any remaining lumps to melt in the warm liquid chocolate. Once just-warm, beat the chocolate into the buttercream. Start the butterflies. Put the whole giant buttons on a flat baking tray on non-stick baking paper, then put into the oven for 20-30 secs or until the chocolate looks shiny. Take out, scatter with hundreds and thousands, then leave to set completely before cutting in half with a large non-serrated knife. For the ladybirds, pipe dots of icing all over the already cut giant button halves, then leave aside to dry. Spread the buttercream over the cake, then start to arrange the butterflies. Cut each Matchmaker into 3 – these will make the bodies. Press onto the cake, then stick four giant button halves around each body to make ‘wings’. For the ladybirds, place two spotty button halves together, then use a small button for the head. Scatter more hundreds and thousands all over the cake, then poke in the candles.','50 mins','1 hr and 20 mins'),
(1093,'teddy christening cake','https://www.bbcgoodfood.com/recipes/teddy-christening-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1287504_7-8aaa693.jpg','Make an impressive Christening or 1st birthday cake using these simple techniques and our step-by-step photographs',5,45,1,2,11,'Make the 15cm fruit cake. You can do this up to a month in advance and wrap it in cling film. Once a week you can feed it with a little dark rum or brandy by poking holes in the top and spooning a little into the holes. Marzipan the fruitcake. Heat the apricot jam until bubbling, then take off the heat. Sieve if necessary to remove any bits. Level off the top of the fruit cake using a long serrated bread knife. Brush the top with jam and flip the cake over and position it on top of the 15cm cake board. Briefly knead the marzipan and smooth into a ball. Dust the work surface with icing sugar. Roll out into a circle. Use a bit of string or tape measure to measure your fruit cake’s top and sides so you know how far to roll out the dough. Brush the cake with apricot jam and then carefully transfer the marzipan to the cake (see picture 1). Gently smooth the top first, then ease your hands down the sides, carefully lifting and coaxing the marzipan so that it fits without overlapping (see picture 2). When you have reached the bottom edges of the cake, use a sharp knife to shave off any excess marzipan. You can do this up to a month in advance. Ice the cake drum (see picture 4). Lightly knead 500g icing and form into a ball. Dust your work surface with sifted icing sugar and roll out the icing in a circle using a tape measure to check that it is wide enough (23cm). Brush the drum with cooled boiled water and transfer the icing to the board. Gently roll over the board again, then trim the edges with a small sharp knife. Roll gently again if you like to get a really smooth finish. Watch out for fingerprints and bits of fluff as ready-to-roll icing can be quite delicate.You can do this in advance too. If you do, put it in a cake box to stop it gathering dust. Make the 23cm lemon cake. The unfilled cake will keep well if you wrap it with baking parchment and cling film for up to 4 days, or in the freezer for up to a month. Buttercream the zingy lemon cake. Make buttercream by beating together the butter, icing sugar and lemon zest. You can do this the day before icing. Level off the top of the lemon cake using a long serrated bread knife. Spread a little of the buttercream onto the 23cm cake board. Turn cake upside down onto the board. Brush all over with a thin layer of the sieved apricot jam - this helps to stop crumbs getting into the buttercream. Cut the cake in half horizontally using the bread knife. It doesn’t matter if it’s not perfect as people won’t be able to see once the cake is covered. Use a palette knife to spread a layer of buttercream onto the top of the cut side of the cake on the board. Put the top layer back on and press down a little to level it. Spread the remaining buttercream on the top and sides of the cake using the palette knife to smooth the surface (see picture 3). Pop the cake in the fridge until the buttercream is firm, this will make it easier to ice. To cover the cakes in the icing, you’ll need 500g for the smaller cake and 900g for the larger one. For each, gently work the icing into a ball. Dust the work surface with icing sugar and roll it into a circle. Again use a tape measure or piece of string to ensure the circle is big enough to cover the cake. In the case of the fruitcake, brush the marzipan with cooled boiled water, then transfer the icing onto the cake and smooth it down in the same way as the marzipan. Use a cake smoother if you have one to get a really smooth finish (see picture 5). For the lemon cake, you won’t need any additional gluing, the buttercream will do the job. Once you have smoothed the icing over the cake, trim it carefully. A ribbon will hide the bottom of the cake so it doesn’t need to be perfect. Save any leftover icing to make the decorations. Wrap it in clingfilm and put into a freezer bag to seal out the air. To stack the cakes, use three plastic dowels, these help to keep the cake stable and level. Insert three dowels in a triangle, 2cms in from where the edges of the smaller cake will be (see picture 6). With a pen, mark where the top of the icing comes to on the dowel. Carefully pull out the dowels, you can use tweezers for this, and line them up. Pick the dowel with the highest mark, this is the length you want all the dowels to be. Score the dowels with scissors at this length and cut or snap the plastic. Put the rods back into the three holes, rounded end down. Smooth a little leftover buttercream onto cake where the dowels are. Pick the best side of the smaller cake and aim to position this at the front. Carefully lift it onto the larger one, you can use a palette knife to help you lower the cake. To decorate, pinch off a small ball of icing and leave it white. Divide the remaining icing into quarters. Dye two quarters brown. Do this by dipping a toothpick into the colouring paste and gradually adding more colour to the icing, kneading it as you go (see picture 7). Keep going until you have the colour you want and it is smoothly incorporated with no streaks. It’s better to do this gradually as the colours can be quite strong and it’s easy to add too much. Dye the remaining two balls of icing in your chosen pastels. Wrap all of the balls in clingfilm and pop into a freezer bag until ready to use. This will stop them from becoming crusty. Start by decorating the bottom layer. Roll out the icing, cutting out approximately 12 shapes. We did six brown teddies and alternated them with either a pink duck (three in total) or a blue duck (three in total). Paint a small amount of edible glue onto the back of each teddy and stick it on. Then repeat with the ducks, placing them between the teddies. For the top layer, we cut out 12 small bunnies, alternating brown, blue, pink, brown, blue pink etc. Stick the shapes with even spacing between them on to the cake. The number of shapes you need will depend on the size of the cutters you’ve chosen. To make the teddy, roll out one small ball for the head and one slightly larger one for the body. Stick the head on top of the body. Roll out two sausages of icing one for the legs and one for the arms. Cut them in half. Stick the arms to the side of the body and flatten the ends to form little paws. Next stick on the legs, again flattening the ends to form paws. Roll two small balls of brown, flatten and cup them slightly to make ears. Make a little ball of white icing to stick onto the face to make a muzzle. Dip a toothpick into the brown colouring paste and do two dots for eyes. Roll tiny balls of icing for the nose (in brown) and buttons (in white) and stick them on. If you like, take some white icing and make pads for the feet and for the inner ears too (see picture 9). To make the rabbit, roll a sausage of pink or blue icing and snip the top and the bottom one third along. Form the top into ears and the bottom into legs. Pinch a neck about 1 cm under the ears and then snip each side upwards from the legs to just below the neck to form arms and mould them slightly (see picture 8). Make a face by dipping your toothpick back into the brown paste and draw on eyes, a nose and whiskers. Sit the teddy on top of the cake and arrange the bunny on his lap. If you like and are handy with writing icing, write the child’s name on the second tier or stick a candle there.','4 hrs','No Time'),
(1094,'dinosaur cake','https://www.bbcgoodfood.com/recipes/snappy-dinosaur-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/dinosaur-cake-3c7ec00.jpg','This show-stopping party cake is deceptively easy to make and looks really professional. Ideal for kid''s birthdays, try this colourful decorated vanilla sponge with bite',33,16,1,2,11,'Heat the oven to 180C/fan 160C/gas 4. Use a little of the butter to grease the sides and bases of two 20cm sandwich tins. Melt the rest of the butter in a small saucepan. Off the heat, add the milk, yogurt and vanilla, followed by the eggs. Beat well with a fork. Put the dry ingredients plus ¼ tsp salt into a large bowl. Whisk to combine – this aerates and saves sifting. Tip in the wet ingredients and whisk to a smooth, silky batter. Don’t hang around at this point. Pour the batter evenly into the prepared tins and put onto the middle shelf in the oven. Bake for 25 mins or until risen and a skewer inserted into the middle of the cakes comes out clean. Cool for 10 mins in the tins, then carefully invert the cakes and leave to cool upside down on a cooling rack. Make the buttercream. Put the butter into a large bowl and sift the icing sugar on top. Add the vanilla and milk and a pinch of salt then beat for a few mins with electric beaters until creamy, pale and spreadable. Sandwich the cakes with just under ¼ of the buttercream and then all of the jam, if using. Mark the cake into six then cut out two of the wedges. Chill all of the cake in the fridge for 30 mins to firm up the crumbs, which will make icing easier. While you wait, colour the remaining buttercream with a little green food colouring gel. Split the sugarpaste into four pieces, one 500g, one 250g, one 150g and one 100g. Colour 250g green to make the legs and arms. Colour 150g pink, to make the spines. Position the cakes on the board and, once you’re happy, use a little buttercream to anchor them. The main part of the cake will make the body of the dinosaur and the root of its tail, and the two cutout wedges will make the face. Shape the 500g piece of sugarpaste to make a tail, tapering at the end. Flatten the other end out (the piece will look a bit like a witch’s hat at this point) and mould it over the tail part of the cake. Using a palette knife, spread the coloured buttercream over each piece of cake and the tail. Shape 100g white icing into eyes and teeth and poke them into the buttercream. Shape two small sausages to make an arm, with two claws, and position on the cake. Shape the rest of the green sugarpaste into two legs, adding claws if you like. Press them into the buttercream on the dinosaur’s belly. Shape the pink icing into spikes and position them along the spine and under the tail. Use two small sausages of pink icing for the eyelids if you like. Finally press the Smarties all over the dinosaur, saving two for the eyes. Leave to set. If you want to cover the board, rub a few drops of blue colouring into the coconut until even. Mix the royal icing sugar with 2 tbsp water, then brush this all over the board and around the dinosaur. Sprinkle with the coconut and the chocolate sprinkles and leave to set. If the sponges are used fresh or within a day of baking (wrap well once cooled), the finished cake will keep in a cool place (not the fridge) for 3 days.','20 mins','25 mins'),
(1095,'chocolate sandwich cookie ice cream cupcakes','https://www.bbcgoodfood.com/recipes/chocolate-sandwich-cookie-ice-cream-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Chocolate-sandwich-cupcakes-4b30ada.jpg','Impress family and friends this summer with these cooling ice cream cupcakes. You only need a few ingredients to make them and no baking is required',122,8,1,2,11,'Line eight holes of a 12-hole cupcake tin with paper cases. Tip the cookies into a food processor and blitz to a fine crumb, or do this in a bowl using the end of a rolling pin. Measure 215g of the crumbs into a bowl and mix in the melted butter. Divide the mixture between the paper cases, pressing it into the bases slightly. Pour the cream, condensed milk and 55g of the remaining crushed cookies into a bowl, and whisk until just starting to thicken, about 5 mins. Spoon the mixture evenly over the chocolate cookie bases, then freeze for at least 2 hrs or overnight until solid. Will keep frozen for up to two days. Just before serving, make the topping. Whip the cream with the icing sugar using an electric whisk until it’s a pipeable consistency. Spoon into a piping bag fitted with a star nozzle and pipe a swirl of whipped cream over each cupcake. Decorate each with a whole cookie and remaining cookie crumbs. Serve immediately.','20 mins','No Time'),
(1096,'marshmallows dipped in chocolate','https://www.bbcgoodfood.com/recipes/marshmallows-dipped-chocolate','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1163514_11-86c1f82.jpg','Kids will love making these sweet chewy lollipops decorated with melted chocolate and cake sprinkles',44,26,1,2,11,'Heat the chocolate in separate bowls over simmering water or on a low setting in the microwave. Allow to cool a little. Put your chosen sprinkles on separate plates. Push a cake pop or lolly stick into a marshmallow about half way in. Dip into the white or milk chocolate, allow the excess to drip off then dip into the sprinkles of your choice. Put into a tall glass to set. Repeat with each marshmallow.','10 mins','5 mins'),
(1097,'ring doughnuts','https://www.bbcgoodfood.com/recipes/ring-doughnuts','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/ring-doughnuts-37fd271.jpg','Master the ultimate ring doughnuts. When it comes to the execution, the devil is in the details: grab a ruler, set your timer and have a thermometer handy.',7,6,2,2,11,'Tip the flour, sugar, yeast and 1 tsp salt into a bowl and mix to combine. Pour in the milk, egg and vanilla, and bring everything together with your hands into a rough dough, making sure all the flour is mixed in. Or, do this using a stand mixer. Cover with a clean, damp cloth and set aside for 30 mins. Tip the dough onto a clean surface and knead for 10-15 mins by hand or 5-8 mins in a stand mixer on low speed until springy and glossy. Use your hands to work the softened butter into the dough – this will take about 5 mins. At first, it will look like it’s not coming together, but keep working the dough through your fingers and it will become smooth and glossy. Form the dough into a ball and return it to the bowl. Cover and leave in a warm place to prove for 2-3 hrs, or until nearly doubled in size. Line a baking tray with baking parchment. Roll the dough out on a lightly floured surface to a 2cm thickness, then use a 9cm cutter to stamp out as many circles as you can. Use a 4cm cutter to stamp out the middles. Re-roll the trimmings (including the middles) and stamp out more (you should end up with six ring doughnuts). Re-roll any remaining trimmings and use the 4cm cutter to stamp out five or six mini bites. Put the doughnuts and bites on the prepared tray, cover with a clean tea towel and leave to prove in a warm place for another 2-3 hrs, or in the fridge overnight until doubled in size. The lightness of the doughnuts depends on thissecond proving, and a longer prove in the fridge will give the doughnuts more flavour. Fill a deep-fat fryer following manufacturer’s instructions, or a heavy-based pan, ensuring it is no more than a third full. Heat to 170C or until a cube of bread dropped in browns in 30 seconds. Carefully lift two or three doughnuts from the tray and slide them into the hot oil using a floured spatula. Fry for 2 mins each side until puffed up and golden brown, then remove to a sheet of kitchen paper using a slotted spoon or tongs. Leave to cool. Repeat with the remaining doughnuts, then fry all the mini bites for a total of 2 mins. When the doughnuts and bites have cooled, you can either coat them in sugar or a glaze. For sugar doughnuts, combine the sugar and cinnamon, then toss with the doughnuts. For glazed doughnuts, sift the icing sugar into a bowl and mix in 2 tbsp cold water until you have a runny icing. Dip in the doughnuts, then decorate with the toppings of your choice. Best eaten straightaway, but will keep in an airtight container for a day.','30 mins','15 mins'),
(1098,'vanilla milkshake bar','https://www.bbcgoodfood.com/recipes/vanilla-milkshake-bar','https://images.immediate.co.uk/production/volatile/sites/30/2020/11/Vanilla-milkshake-bar-b0586f8.jpg','What could be more exciting than a milkshake bar for kids (and big kids)? Perfect for a party or celebration, let guests choose their own toppings',5,4,1,2,11,'Tip the mini cookies, sprinkles, marshmallows and other sweets of your choice into separate serving bowls, and set on the table alongside the squirty cream. Blitz the ice cream, milk and vanilla in a blender until combined and creamy. Pour into a large jug. If you like, dip the rims of the glasses into the melted chocolate, then some sprinkles. Leave to set slightly, then divide the milkshake between glasses. Let everyone customise their shake with the toppings and squirty cream, and serve with the paper straws.','10 mins','No Time'),
(1099,'cheese & onion pasties','https://www.bbcgoodfood.com/recipes/mini-cheese-onion-pasties','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Mini-cheese-and-onion-pasties-1ff7af1.jpg','Rustle up these little cheese and onion pasties for a family picnic. If you won''t eat all 12, they freeze well and can be cooked straight from frozen',6,10,1,2,11,'First, make the pastry. Tip the flour into a bowl. Holding the block of butter in its wrapper, grate it directly into the flour (you may need to dip the end of the butter into the flour occasionally if it becomes sticky). Add the cayenne pepper, mustard powder and half the beaten egg, and season with a pinch of salt. Bring everything together with your hands until the mixture starts to clump together. Tip the pastry onto the work surface and knead very briefly into a smooth ball, adding up to 3 tbsp cold water if it’s too dry. Squash the pastry into a disc. Wrap and chill while you make the filling. Heat the oil in a frying pan and cook the onions and thyme leaves for 8-10 mins until the onions have softened and started to caramelise. Tip into a bowl and leave to cool. Once cool, mix in the grated cheese, mustard and spring onions. Heat the oven to 200C/180C fan/gas 6 and line a baking tray with baking parchment. Roll the chilled pastry out on a lightly floured surface to the thickness of a £1 coin. Cut out as many 10-12cm circles as you can (you can use a cup or mug as a template), then re-roll any off-cuts and repeat. You should get about 10-12 circles in total. Spoon a mound of the filling into the centre of each pastry circle, leaving the edges clear. Working with one circle at a time, brush a little of the remaining beaten egg around the edge, then bring two sides up to meet in the middle in a half-moon shape. Pinch the pastry together, crimping the edge to seal. Transfer to the baking tray and repeat with the remaining pastry circles and filling. Brush all the pasties with some beaten egg. Will keep frozen for two months. Bake the pasties for 20-25 mins (or 40-45 mins from frozen) until the pastry is deep golden brown. Leave to cool for at least 10 mins before eating warm, or cool completely and pack into containers for a picnic. Will keep in an airtight container for up to three days.','30 mins','35 mins'),
(1100,'egg & cress club sandwich','https://www.bbcgoodfood.com/recipes/egg-cress-club-sandwich','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egg-cress-club-sandwich-9d415c2.jpg','Enjoy these toasted egg and cress club sandwiches as part of a summer family picnic. Skewer each sandwich with a sandwich pick and serve with crisps',19,1,1,2,11,'Bring a pan of water to the boil and carefully lower in the eggs. Cook for 6 mins, then cool under running water until they can be peeled. Peel the eggs, then leave to cool completely. Mash or chop the eggs, then mix with 1½ tbsp mayonnaise and some seasoning, if you like. Toast the bread. Lay one slice of bread on a board. Butter it, then spread on three quarters of the egg and scatter over the cress. Add another slice of toast and gently spread on the remaining mayo. Add the tomato or lettuce and ham or cheese (or whichever combination you prefer). Dot the remaining egg over the top, spread gently, then top with the final piece of toast. Cut the crusts off if you like, then gently cut the sandwich into four quarters, being careful not to squash out the egg. Skewer each sandwich with a sandwich pick. Serve with crisps.','10 mins','10 mins'),
(1101,'double choc & coconut traybake','https://www.bbcgoodfood.com/recipes/double-choc-coconut-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-coconut-tray-bake-2140a67.jpg','Pair creamy milk chocolate with coconut for a match made in heaven, then decorate with more coconut and a drizzle of white chocolate',14,12,1,2,22,'Line a 22 x 28cm rectangular cake tin with baking parchment. Heat the oven to 180C/160C fan/gas 4. First, prepare the topping. Put the milk or dark chocolate pieces in a large heatproof bowl. Heat the double cream in a medium pan until just steaming, then slowly pour over the chocolate, whisking until the chocolate has melted and the mix is smooth. Leave to cool slightly, then chill for 1 hr 30 mins until soft, but still spoonable. Beat the butter, sugars and ¼ tsp salt together in a stand mixer or using an electric whisk for 5 mins, or until light and fluffy. Add the eggs one at a time, beating well after each addition. Add the desiccated coconut, flour, yogurt and coconut flavouring, if using, and briefly beat until just combined. Spoon the mix into the tin, smooth the surface using the back of a spoon, then bake for 30 mins until golden and firm to the touch. Leave in the tin to cool completely. When fully cool, spread over the chocolate topping, then drizzle with the white chocolate and scatter over the toasted coconut flakes. Chill for around 15 mins until the topping is set. Cut into 12 squares, then serve.','30 mins','40 mins'),
(1102,'raspberry & white chocolate financier traybake','https://www.bbcgoodfood.com/recipes/raspberry-white-chocolate-financier-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raspberry-and-white-chocolate-traybake-53e4073.jpg','Think of financiers like posh bakewells made with brown butter. Tom Kerridge turns them into a traybake here, using raspberries and white chocolate',52,8,2,2,22,'Melt the butter in a small pan until sizzling. Watch it carefully – the butter will foam, and, when the foam subsides, it will turn from yellow to a nutty brown. Remove from the heat, tip into a bowl to stop it cooking and leave to cool. Heat the oven to 180C/160C fan/gas 4. Butter a 20 x 25cm baking tin at least 4cm deep and line with baking parchment. Mix the sugar, flour, almonds and cinnamon together. Beat the egg whites in a separate large bowl using an electric whisk to soft peaks. Fold the beaten egg whites into the flour mix using a large metal spoon. Slowly pour in the brown butter, folding it in until everything is combined and the batter is light. Scrape into the prepared tin. Scatter over the raspberries and chocolate. Bake for 20-25 mins until firm. Leave to cool in the tin, then dust with some icing sugar and cut into squares or bars. Will keep, well wrapped, for up to three days at room temperature. Serve with clotted cream.','20 mins','30 mins'),
(1103,'banoffee traybake','https://www.bbcgoodfood.com/recipes/banoffee-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banoffee-cake-559ef2a.jpg','Fill your cake tin with this simple banoffee traybake, bursting with banana, toffee and chocolate flavours. It''s guaranteed to be a new family favourite ',6,12,1,2,22,'Heat oven to 180C/160C fan/gas 4. Butter and line a 20 x 30cm baking tin with two strips of criss-crossed baking parchment. In a jug, mash the bananas with a fork, then add the butter and sugar and mix with an electric whisk until smooth and creamy. Add the eggs one at a time, beating well after each addition, then add the vanilla and a pinch of salt. Use a spatula to fold through the flour, yogurt and chocolate chunks. Scrape the mixture into the tin and smooth over the surface. Bake for 30 mins until risen and golden and a skewer inserted into the centre of the cake comes out clean. Cool for 10 mins in the tin, then transfer to a wire rack. Cut the traybake into 12 squares, then top each one with a spoonful of caramel and a banana chip, if you like. Will keep in a tin for up to five days, or in the freezer – without the toppings – for up to a month.','20 mins','30 mins'),
(1125,'gluten-free muffins','https://www.bbcgoodfood.com/recipes/amaretti-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/amaretti-muffins-f5d5e33.jpg','These simple gluten-free muffins are full to bursting with crunchy amaretti biscuits and creamy Greek yogurt for a light-textured sponge. Perfect with a cuppa',41,12,1,2,22,'Heat oven to 180C/160C fan/gas 4. Line a 12-hole muffin tin with muffin cases or baking parchment squares. Whisk the oil, sugar and eggs together. Add the yogurt and espresso, then fold in the ground almonds, rice flour and baking powder. Carefully fold the broken amaretti biscuits into the mixture. Divide the mixture between the cases (about 2 tbsp in each). Sprinkle with the flaked almonds and bake for 20-25 mins until golden on top. Serve warm with a large dollop of Greek yogurt.','15 mins','20 mins - 25 mins'),
(1104,'peach melba squares','https://www.bbcgoodfood.com/recipes/peach-melba-squares','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363879_12-f7f4edf.jpg','This traybake can be mixedtogether in no time and is simple to slice andtransport',33,12,1,2,22,'Butter and line a traybake or small roasting tin, about 20 x 30cm. Heat oven to 180C/160C fan/gas 4. Gently melt the butter in a large saucepan, cool for 5 mins, add the sugar, vanilla and eggs, then beat until smooth with a wooden spoon. Stir in the flour, almonds and ¼ tsp salt. Tip the mix into the tin, then lay the peach slices evenly on top – that way each square of cake will have a bite of fruit. Scatter the raspberries and almonds over, then bake for 1 hr-1 hr 10 mins, covering with foil after 40 mins. Test with a skewer: the middle should have just a tiny hint of squidginess, which will firm up once the cake cools. Cool in the tin for 20 mins, then lift out onto a cooling rack. Once cold, dredge with icing sugar, then cut into squares.','10 mins','1 hr'),
(1105,'chocolate traybake','https://www.bbcgoodfood.com/recipes/chocolate-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-traybake-350881f.jpg','This American-style chocolate traybake is super-easy, can be prepared in advance and cuts into even portions. It''s perfect for birthday parties or bake sales',6,10,1,2,22,'Heat oven to 180C/160C fan/gas 4. Oil and line the base and sides of a 33cm x 23cm roasting tin or cake tin with a lip of at least 2½ cm. Combine the flour, cocoa powder, baking powder, bicarb, sugar and a good pinch of salt in a large bowl. Rub any lumps of sugar between your fingers, shaking the bowl a few times to bring them to the surface. Whisk the oil, buttermilk, coffee, vanilla and eggs in a jug, then pour the wet ingredients into the dry. Use a spatula to stir well, removing any pockets of flour. Pour the mixture into the lined tin and bake for 25-30 mins until a skewer inserted into the centre comes out clean. If any wet cake mixture clings to the skewer, return the cake to the oven for 5 mins, then check again. Once cooked, remove from the oven and leave to cool in the tin for at least 20 mins. Meanwhile, make the icing. Melt the butter in a saucepan, then remove from the heat and stir in the icing sugar, cocoa powder and milk. The icing will be very runny but will thicken a little as it cools. (If the icing has set too much before the cake has cooled, reheat it slightly to make it easier to pour.) Pour the icing over the cake and leave to set. Decorate with the sweets, sprinkles, chocolate shavings or nuts, then cut into squares and dig in.','15 mins','30 mins'),
(1106,'triple cheese & onion muffins','https://www.bbcgoodfood.com/recipes/triple-cheese-onion-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-994484_11-ce1e28b.jpg','Savoury fluffy cakes packed with cheddar, Parmesan, chives, spring onion and cream cheese - delicious fresh from the oven',17,12,1,2,22,'Heat oven to 200C/180C fan/gas 6 and oil a 12-hole muffin tin. Whisk the oil, egg and buttermilk. In a mixing bowl, mix the flour, 1 tsp salt and mustard powder, then stir in the cheddar, spring onions, chives and half the Parmesan. Use a large spoon to gently fold the wet ingredients into the dry – don’t overmix, just lightly combine. With the final few folds, mix in the soft cheese cubes. Spoon the mixture between the holes in the tin (they will be quite full), scatter with the remaining Parmesan and bake for 25 mins.','15 mins','25 mins'),
(1107,'smoked salmon & poppy seed palmiers','https://www.bbcgoodfood.com/recipes/smoked-salmon-poppy-seed-palmiers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoked-salmon-poppy-seed-palmiers-b5468f3.jpg','These savoury pastries are sure to please at a posh picnic, with cream cheese, smoked fish and puff pastry',6,10,1,2,22,'Unroll the pastry and brush one side all over with the beaten egg. Sprinkle over the poppy seeds, then flip over the pastry. Spread the cream cheese over the pastry and top with the smoked salmon, then scatter over the chives. With one of the longer sides facing you, mark a line roughly down the centre, but don’t cut all the way through. Tightly roll each end in towards the middle until you reach the centre. Slice into 1cm-thick pieces and place, cut-side up, on a baking tray lined with baking parchment. Chill until ready to bake, or freeze for up to 2 months. Heat oven to 200C/180C fan/gas 6. Bake for 20-25 mins until puffed up and golden brown (if baking from frozen, cook for an extra 5 mins). Leave to cool on a wire rack before packing for your picnic.','25 mins','25 mins'),
(1108,'brie, courgette & red pepper muffins','https://www.bbcgoodfood.com/recipes/brie-courgette-red-pepper-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/briemuffins-28ad664.jpg','These cheesy savoury bites are best eaten on the day they''re baked – ideally fresh from the oven. They''re perfect for picnics or lunchboxes',43,10,1,2,22,'Heat oven to 190C/170C fan/gas 5. Line 10 holes of a muffin tin with paper cases. Melt the butter in a pan and gently cook the courgettes for about 5 mins until softened. Mix the flour, baking powder, oregano and some seasoning in a large bowl. Make a well in the centre and add the eggs, milk and oil, stirring all the time to draw the flour into the centre. Beat for 1 min or so to make a smooth batter. Add the courgettes, peppers, two thirds of the cheddar and all the brie or camembert to the batter, stirring well. Divide between the muffin cases and sprinkle with the remaining cheddar. Bake for 25-30 mins until the muffins feel firm to the touch and are golden and crusty on top. Serve warm or at room temperature.','25 mins','35 mins'),
(1126,'raspberry & white chocolate muffins','https://www.bbcgoodfood.com/recipes/raspberry-white-chocolate-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/white-choc-and-raspberry-muffins-312c691.jpg','Indulge in these raspberry and white choc chip muffins for a sweet treat. Made with fresh or frozen raspberries, they''re perfect served with a cuppa',46,8,1,2,22,'Heat the oven to 200C/180C fan/gas 6 and line a 12-hole muffin tin with paper cases. Beat the butter and sugar together for 5 mins until pale and fluffy. Add the eggs gradually and beat in until combined. Stir in the yogurt, vanilla and milk. Fold in the flour, baking powder and a pinch of fine salt until you have a smooth batter. Finally, fold in 150g raspberries and white chocolate chips and fill the muffin cases three-quarters full. Push 2 raspberries into the top of each muffin so they''re sticking out slightly. Bake for 8 mins, then reduce the oven to 180C/160C fan/gas 4 and bake for 20-25 mins more until risen and golden, and a cocktail stick inserted into the centre comes out clean.','20 mins','30 mins'),
(1109,'cheese & marmite pasties','https://www.bbcgoodfood.com/recipes/cheese-marmite-pasties','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-980452_11-dca4bc4.jpg','Savoury Marmite works beautifully with cheese and onion when baked up in shortcrust parcels. These easy homemade pasties are ideal for picnics',17,6,2,2,22,'Put the grated potato, cheese, breadcrumbs and spring onions into a mixing bowl with 1 egg. Sprinkle over 1 tsp salt and plenty of freshly ground black pepper. Mix together. Roll out the pastry on a lightly floured surface to just slightly thicker than a £1 coin. Use a 16-17cm saucer or bowl to cut out 6 circles of pastry – you may need to re-roll trimmings to get the last one. Warm the Marmite in a small pan or in the microwave with a splash of water so it is a bit runnier, then brush over each circle, leaving a 2-3cm border around each edge. Divide the filling between the middles of each circle, then beat remaining egg and brush a little around each border. For each, bring up 2 sides of pastry to meet over the filling, and pinch and crimp to seal into a pasty shape. If eating straight away, transfer pasties to a baking sheet and chill while you heat oven to 160C/140C fan/gas 3. Or leave on the tray, wrap well in cling film and freeze for up to 3 months. Brush the pasties all over with more egg. Bake for 50 mins-1 hr until golden brown and crisp. Eat warm, or cool, in the next 24 hrs.','25 mins','1 hr'),
(1110,'leek, goat’s cheese, walnut & lemon tart','https://www.bbcgoodfood.com/recipes/leek-goats-cheese-walnut-lemon-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-871484_11-95fb5ab.jpg','A no-fuss open puff pastry pie that makes a great vegetarian main course or quick lunchtime treat',5,4,1,2,22,'Heat oven to 220C/200C fan/gas 7. Heat the olive oil in a large frying pan, then add the butter. Once sizzling, add the leeks and cook over a medium heat until softened but not coloured. Stir in the thyme and half the lemon zest, then increase the heat. Add the lemon juice and cook for about 30 secs until the lemon juice reduces, then season well. Remove from the heat and cool slightly. Unroll the pastry and lay on a baking sheet lined with baking parchment. Lightly mark a 1cm border around the edges with the tip of a sharp knife, then prick the base all over with a fork. Spread the lemony leeks on top of the pastry, within the border. Crumble over the cheese, scatter with the walnuts, then season with pepper. Drizzle with some olive oil, brushing the edges with a little oil as well. Put tart in the oven for 15-20 mins until the pastry puffs up around the edges and is golden brown. Scatter with parsley and the remaining lemon zest. Serve hot, warm or cool.','25 mins','30 mins'),
(1111,'cheese & onion bread','https://www.bbcgoodfood.com/recipes/cheese-caramelised-onion-coburg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-741483_11-32e63e1.jpg','This savoury cheese and onion bread is delicious served warm from the oven and makes a great easy sharing starter',37,2,1,2,22,'Mix your choice of brown flour with the white, the yeast and salt in a large mixing bowl. Put in the butter and rub it into the flour. Stir in the seeds if using. Make a dip in the centre of the flour and pour in almost 300ml hand warm (cool rather than hot) water, with a round-bladed knife. Then mix in enough of the remaining water and a bit more if needed, to gather up any dry bits in the bottom of the bowl and until the mixture comes together as a soft, not too sticky, dough. Gather it into a ball with your hands. Put the dough on to a very lightly floured surface and knead for 8-10 mins until it feels smooth and elastic, only adding the minimum of extra flour if necessary to prevent the dough sticking. Place the ball of dough on a lightly floured work surface. Cover with an upturned, clean, large glass bowl and leave for 45 mins-1 hr or until doubled in size and feels light and springy. Timing will depend on the warmth of the room. Knock back the dough by lightly kneading just 3-4 times. You only want to knock out any large air bubbles, so too much handling now will lose the dough’s lightness. Shape into a ball. Cover with the glass bowl and leave for 15 mins. Fry the onion in the olive oil until very lightly caramelised, then leave to cool. Cut the dough in half and shape into 2 balls. Cover with the glass bowl and leave for 15 mins. Shape by flattening each ball into an 18cm round, put each on a large baking tray lined with baking parchment. Make 6 deepish cuts with a sharp knife to mark each round into 6 wedges. Cover with a clean tea towel. Leave for 40-45 mins, or until doubled in size. Finish by brushing with beaten egg, scatter over the onion, then the grated cheese. Put a roasting tin in the bottom of the oven 20 mins before ready to bake and heat oven to 230C/210C fan/gas 8. Put the risen bread in the oven, carefully pour about 250ml cold water into the roasting tin (this will hiss and create a burst of steam to give you a crisp crust), then lower the heat to 220C/200C fan/gas 7. Bake for 30-35 mins or until golden. If the onion is getting too brown, lay a piece of parchment over the top. Remove and cool on a wire rack. If you tap the underneath of the loaf if should be firm and sound hollow.','20 mins','30 mins'),
(1112,'cheese, rosemary & potato loaf','https://www.bbcgoodfood.com/recipes/cheese-rosemary-potato-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-328585_10-0aa1880.jpg','Making a big savoury scone loaf is a quick and tasty alternative to making bread with yeast',33,8,1,2,22,'Heat the oil in a medium pan, then gently fry the garlic for 10 mins until softened but not coloured. Set aside. Heat oven to 220C/200C fan/gas 7. Put the flour, salt and baking powder into a food processor, then whizz in the butter until it disappears. Tip into a large bowl, then make a well in the middle. Warm the yogurt and milk together in a microwave for 1 min or in a pan; it should be hot and may well go a bit lumpy-looking. Tip into the bowl and quickly work into the flour mix using a cutlery knife. As soon as it''s all in, stop. Turn the dough onto a floured surface, bring it together with floured hands, then press out to a large rectangle. Scatter over the potatoes, chopped rosemary, garlic, some of its oil and most of the cheese. Knead a few times, shape into a round, then lift onto a floured baking sheet. Score the top, scatter with the rest of the cheese, poke in a few rosemary sprigs, then drizzle with the remaining oil. Bake for 25 mins until risen, golden and sizzling around the edges. Best eaten just warm.','20 mins','35 mins'),
(1113,'homemade soft pretzels','https://www.bbcgoodfood.com/recipes/learn-to-make-pretzels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/soft-pretzel-ae1eab2.jpg','Edd Kimber demonstrates how to make perfect pretzels with a chewy crust and soft, fluffy interior. Mix honey and mustard for a fantastic dip to go with them',75,8,2,2,22,'Put the flour, yeast, sugar and 1 tsp salt in a large bowl and mix together to combine. In a large jug, mix together 300ml lukewarm water and the butter. Make a well in the flour mixture and pour in the water, mixing together to form a rough dough. Tip out onto a floured work surface and knead for 10-15 mins or until smooth and elastic. Put the dough in a lightly oiled bowl, cover with oiled cling film and set aside until doubled in size, about 1hr. Once risen, knock out the air bubbles in the dough and divide into 8 equal pieces. Using your hands, roll each piece into a long rope about 60cm long. To form into pretzels, lay the rope in a U-shape with the curve pointing towards you. Take the two ends and cross them over. Take the ends, lift them backwards and press them into the curve of the U-shape. Repeat with the remaining dough. Heat oven to 200C/180C fan/gas 6. Carefully place the pretzels on a baking tray lined with parchment and lightly greased with oil. Cover lightly with oiled cling film. Set aside for about 20 mins until puffy (not fully risen like bread dough). Fill a medium-sized saucepan with water, bring to the boil, add the baked bicarbonate of soda, then reduce the heat to a low simmer. One at a time, carefully lift the pretzels into the pan and cook for 20 secs per side. The pretzels will rise to the surface; flip with a slotted spoon. Use the spoon to gently lift the pretzels from the pan and return them to the baking tray. Once they have all been cooked in the water, lightly brush with the egg and sprinkle with flaked sea salt. Bake in the oven for 20-25 mins or until a rich, dark brown. Allow to cool on the baking tray for 10 mins, then transfer to a wire rack to cool completely. Best served on the day made but can be frozen for up to 1 month.','40 mins','20 mins'),
(1114,'gruyère & onion tear & share','https://www.bbcgoodfood.com/recipes/gruyere-onion-tear-share','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gruyere-onion-tear-share-fc27fd4.jpg','These tasty white bread rolls are filled with cheese and onion – let everyone help themselves as part of a buffet',24,12,2,2,22,'In a large bowl, combine the flour, yeast, sugar and 1 tsp salt. Make a well in the centre and gradually pour in 350ml tepid water, stirring and mixing the water into the flour. If the mix is a little wet, stir in a bit of extra flour; if a little dry, add a bit more water. The dough should be soft but not too sticky. Turn the dough out onto a floured work surface and knead for about 10 mins until the dough is springy but smooth. Put in a large floured bowl, cover with cling film and leave in a warm place to rise until doubled in size (this will take 45 mins-1 hr). While the dough rises, heat the 2 tbsp oil and butter in a non-stick frying pan. Stir in the onions and cook over a medium heat for 10-15 mins until golden and softened. Leave to cool, then stir in the cheese and thyme leaves, and season. Once the dough has risen and doubled in size, knock it back and divide into 12 balls. Flour a work surface and roll out the balls to about 10cm across. Spoon 1 heaped tbsp of the onion mixture in the middle of the dough and gather up the edges over the filling, pressing together to seal. Do this with all the balls, reserving a little onion mixture. Oil a baking tray and arrange the balls, seam-side down, in rows. Cover with oiled cling film and leave in a warm place to prove for another 30 mins. Heat oven to 220C/200C fan/gas 7. Once the buns have risen a little more and are beginning to join together, remove the cling film, drizzle with the remaining oil and spoon over the leftover onion mixture. Bake for 10 mins, then turn the oven down to 200C/180C fan/gas 6 and cook for another 10 mins. The buns should be golden and crisp on the bottom. Cool before serving.','45 mins','30 mins - 35 mins'),
(1115,'jumbo sausage roll with salsa beans','https://www.bbcgoodfood.com/recipes/jumbo-sausage-roll-salsa-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jumbo-sausage-roll-with-salsa-beans_0-d4504df.jpg','This giant sausage roll and spicy homemade baked beans are easy to make and perfect for sharing - great for students on a budget',6,6,1,2,22,'Heat oven to 200C/180C fan/gas 6 and line a baking tray with baking parchment or foil. In a large bowl, mix together the sausagemeat, herbs, half the salsa and roughly half the egg until well combined. Unroll the pastry sheet on your baking tray. Pile the sausage mixture onto one side of the pastry – along the longer side – leaving a gap of 4cm around the edge. Brush a little of the egg around the edges, then fold the pastry over the filling. Press the edges together with a fork and score a few air holes through the top with a knife (to let steam escape). Brush with the remaining egg and bake for 40 mins. Meanwhile, tip the beans, tomatoes, remaining salsa and some seasoning into a saucepan and cover with a lid. Simmer for 20 mins or until the sauce is thick and clings to the beans. To serve, scatter the beans with the chilli and serve with slices of sausage roll.','10 mins','40 mins'),
(1116,'microwave brownies','https://www.bbcgoodfood.com/recipes/microwave-peanut-butter-and-jam-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cropped-brownie-a97f845.jpg','Forget microwave mug cakes - if you''re after some serious indulgence in just 15 minutes, this easy chocolate, peanut butter and jam brownie recipe is sure to hit the spot',123,6,1,2,22,'Add the butter and peanut butter to a microwave-safe bowl and cook on High for 30 secs to melt. Remove from the microwave and stir thoroughly. Allow to cool slightly then add the egg and stir to combine. Grease with butter and line a second microwaveable round dish (about 15cm wide) with cling film. Tip the peanut butter mix into the lined dish then sieve in the cocoa powder and flour and tip in the sugar. Mix gently to form a thick, sticky paste. Smooth the top of the mixture with the back of a large spoon. Using a teaspoon, make crater-holes in the top of the brownie mixture and fill each generously with jam as it will melt and bubble down when cooked. Pop in the microwave for 3-4 mins on High. Remove and leave to cool for 15 mins (it will carry on cooking as it cools) then enjoy!','10 mins','5 mins'),
(1117,'blood orange & poppy drizzle muffins','https://www.bbcgoodfood.com/recipes/blood-orange-poppy-drizzle-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blood-orange-poppy-drizzle-muffins-d136543.jpg','These citrussy muffins are lovely and light, and make a delicious pudding served with ice cream or thick Greek yogurt',17,12,1,2,22,'Heat oven to 200C/180C fan/gas 6 and lightly grease a 12-hole muffin tin. Mix together the flour, caster sugar, baking powder and poppy seeds in a large mixing bowl. In another bowl, whisk together the oil, milk, egg, and zest and juice from 1 of the oranges. Working very quickly, pour the wet ingredients into the dry and barely mix together with a fork – the mixture should be lumpy, so don’t overwork it. Spoon the mixture into the tins and bake for 10-12 mins until risen and browned. While the muffins are baking, mix the remaining zest with the granulated sugar in a small bowl. Have the remaining blood orange juice ready too. Leave the muffins to cool for 2 mins, then transfer to a wire rack. Again working quickly, pour the juices into the sugar mix and spoon some over each muffin, repeating until it is all used up. Leave to set for 5 mins before eating.','20 mins','12 mins - 15 mins'),
(1118,'microwave coffee & walnut cake','https://www.bbcgoodfood.com/recipes/microwave-coffee-walnut-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273549_8-895c4f7.jpg','Good Food reader Catherine Dunkley shares her super-quick and easy recipe for the perfect last-minute coffee and walnut cake',5,4,1,2,22,'In a medium bowl, beat the butter and sugar together until light and fluffy. Gradually add the eggs followed by the flour and instant coffee. Stir most of the walnuts (if using) into the batter, reserving a few for decoration. Transfer to a microwave-proof dish and cook in the microwave on full power for 2 mins. Reduce the power to medium and cook for 2 mins. After 4 mins, check to see if the cake is cooked – it should be risen and spring back when touched. If it needs a bit longer, cook on medium for 1 min more at a time, checking after each minute until the cake is cooked. Remove and allow to cool. Meanwhile, make the coffee butter cream. Dissolve the coffee in the milk, then add the butter and icing sugar. Beat until smooth, spread over the cake and top with the reserved walnuts.','15 mins','10 mins'),
(1119,'vegan cupcakes with banana & peanut butter','https://www.bbcgoodfood.com/recipes/vegan-banana-peanut-butter-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cupcakes_0-86b99b4.jpg','Employ some clever tricks to achieve a dairy-free bake. Egg-free mayonnaise, almond milk and margarine are the secrets to these vegan cupcakes',5,16,1,2,22,'Heat oven to 170C/150C fan/gas 3½. Line muffin tins with 16 cases. In a bowl, combine the flour, sugar, ½ tsp salt and bicarbonate of soda. In a second bowl or a jug, mix the mayonnaise, mashed bananas and vanilla extract. Pour the wet ingredients into the dry and mix with a spoon until just combined (don’t overmix or your cupcakes will be heavy). Spoon the mixture into the cases and bake for 20 mins. When the cupcakes come out of the oven, sprinkle the chocolate chips over – they will melt and then harden again, so don’t touch them. For the icing, combine the vegan margarine and icing sugar in an electric mixer, then add the vegan milk and continue to mix on a slow speed until completely combined. Turn the mixer up and combine for a further 3 mins. Finally, stir in the peanut butter. Pipe or simply spread the icing on top of the cakes. Store in an airtight container and eat within 2 days.','25 mins','20 mins'),
(1120,'raspberry bakewell cake','https://www.bbcgoodfood.com/recipes/raspberry-bakewell-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-559459_11-4dd3009.jpg','This simple almondy cake is a great way of using up pick-your-own raspberries',7,8,1,2,22,'Heat oven to 180C/160C fan/gas 4 and base-line and grease a deep 20cm loose-bottomed cake tin. Blitz the ground almonds, butter, sugar, flour, eggs and vanilla extract in a food processor until well combined. Spread half the mix over the cake tin and smooth over the top. Scatter the raspberries over, then dollop the remaining cake mixture on top and roughly spread – you might find this easier to do with your fingers. Scatter with flaked almonds and bake for 50 mins until golden. Cool, remove from the tin and dust with icing sugar to serve.','10 mins','50 mins'),
(1121,'easy chocolate cupcakes','https://www.bbcgoodfood.com/recipes/easy-chocolate-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363907_12-20473e7.jpg','These light cakes are perfect for freezing – they just need a quick blast in the microwave to bring them back to life',5,10,1,2,22,'Heat oven to 180C/fan 160C/gas 4 and line a 10-hole muffin tin with paper cases. Whizz the chocolate into small pieces in a food processor. In the largest mixing bowl you have, tip in the flour, sugar, cocoa, oil, 100ml soured cream, eggs, vanilla and 100ml water. Whisk everything together with electric beaters until smooth, then quickly stir in 100g of the whizzed-up chocolate bits. Divide between the 10 cases, then bake for 20 mins until a skewer inserted comes out clean (make sure you don’t poke it into a chocolate chip bit). Cool on a wire rack. To make the icing, put the remaining chocolate bits, soured cream and 3 tbsp sugar in a small saucepan. Heat gently, stirring, until the chocolate is melted and you have a smooth icing. Chill in the fridge until firm enough to swirl on top of the muffins, then tuck in.','15 mins - 20 mins','20 mins'),
(1122,'microwave chocolate cake','https://www.bbcgoodfood.com/recipes/microwave-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-911517_11-e4e23bf.jpg','Edwina Hanslo''s delicious and super quick chocolate cake with a creamy ganache is the perfect choice for busy mums',5,10,1,2,22,'Grease a 22cm silicone microwaveable cake pan with a little oil and place a circle of baking parchment in the bottom. Mix the sugar, flour, cocoa and baking powder in a bowl. In a jug, whisk the oil, eggs, vanilla and 100ml hot water until combined. Add the liquid ingredients to the dry ingredients and mix thoroughly until you have a lump-free batter. Pour the mixture into the cake pan and gently tap to pop any air bubbles. Cover with cling film. Microwave on Full power (800 watts) for 7 mins. Remove and check if the cake is cooked by poking a skewer into the middle: if it comes out clean, the cake is ready. Allow the cake to stand for 5 mins, then remove the cling film and turn out onto a cooling rack. For the ganache, melt the chocolate on Half power (600 watts) for approximately 2 mins, stirring every 30 secs until melted. Add the cream and mix thoroughly until smooth and glossy. Once the cake is cool, spread over the ganache and scatter with the sprinkles. The cake will keep for up to 3 days in an airtight container.','5 mins','10 mins'),
(1123,'banoffee muffins with cream & salted caramel','https://www.bbcgoodfood.com/recipes/banoffee-muffins-cream-salted-caramel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banoffee-muffins-with-cream-and-salted-caramel-4e53f46.jpg','Give much-loved banana bread a modern update it by transforming it into tasty muffins iced with fresh cream and a moreish salted caramel sauce',11,12,1,2,22,'Heat oven to 200C/180C fan/gas 6. Prepare a muffin tin by thoroughly buttering and lining with cases. In a bowl, mash three bananas, then stir in 50g yogurt. In another bowl, sift in the flours, baking powder, cardamom and a pinch of salt, and stir to combine. Add the eggs and sugar to the bowl of a stand mixer, then whisk until pale and frothy – about 3 mins. Slowly pour in the melted butter in a steady stream, then add the vanilla. Turn the speed down, then add the flour mixture, 2 tbsp at a time, alternating with the yogurt and banana mix, until everything is incorporated. Mix in the walnuts for no longer than 20 seconds. Divide the mixture between the muffin cases. Bake for 25-30 mins or until a skewer inserted comes out clean. Meanwhile, make the salted caramel by heating the butter, cream and sugar in a non-stick pan until simmering. Remove from the heat, then add the salt – you want enough to pique the sauce, but it shouldn’t taste salty. Once the muffins are cooked, remove from the oven and leave to cool on a wire rack. Whip the cream to soft peaks, fold through 2 tbsp yogurt and slice the final banana. Spoon or pipe the cream onto the muffins. Top with the sliced banana and drizzle with salted caramel.','20 mins','25 mins - 30 mins'),
(1124,'toddler recipe: mini egg & veg muffins','https://www.bbcgoodfood.com/recipes/toddler-recipe-mini-egg-veg-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gfkids_muffins-9cbad2b.jpg','These egg and vegetable muffins are packed with courgette, carrot, peas, eggs and feta cheese. Make them for your toddler as a snack, or for lunch',44,6,1,2,22,'Heat the oven to 200C/180 fan/gas 6. Grease 6 holes of a muffin tin. Put the carrot, courgette and peas (about 190g veg) in a microwaveable dish with 2 tbsp water. Cover with cling film and cook for 2 mins or until tender. Drain the veg. Beat the eggs and stir in the veg and feta. Pour into the 6 holes. Bake for 15 minutes until the eggs are set. Leave for a minute or two, then use a knife to carefully remove the muffins.','3 mins','17 mins'),
(1127,'doughnut muffins','https://www.bbcgoodfood.com/recipes/doughnut-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-856543_10-0d65b66.jpg','These individual sugar-dipped cupcakes are baked not fried but taste just as delicious and are best straight from the oven',17,12,1,2,22,'Heat oven to 190C/170C fan/gas 5. Lightly grease a 12-hole muffin tin (or use a silicone one). Put 140g sugar, flour and bicarb in a bowl and mix to combine. In a jug, whisk together the yogurt, eggs and vanilla. Tip the jug contents and melted butter into the dry ingredients and quickly fold with a metal spoon to combine. Divide two-thirds of the mixture between the muffin holes. Carefully add 1 tsp jam into the centre of each, then cover with the remaining mixture. Bake for 16-18 mins until risen, golden and springy to touch. Leave the muffins to cool for 5 mins before lifting out of the tin and rolling in the extra sugar.','20 mins','18 mins'),
(1128,'simnel muffins','https://www.bbcgoodfood.com/recipes/simnel-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-227452_11-ce8cbe3.jpg','A new twist on a traditional recipe and perfect for getting the kids involved in the festivities',43,12,1,2,22,'Tip the fruit into a bowl, add the zest and juice and microwave on medium for 2 minutes (or leave to soak for 1 hour). Line 12 deep muffin tins with paper muffin cases. Preheat the oven to fan 180C/ 160C/gas 4. Beat together the butter, sugar, eggs, flour, spices and milk until light and fluffy (about 3-5 minutes) – use a wooden spoon or hand held mixer. Stir the fruit in well. Half fill the muffin cases with the mixture. Divide the marzipan into 12 equal pieces, roll into balls, then flatten with your thumb to the size of the muffin cases. Put one into each muffin case and spoon the rest of the mixture over it. Bake for 25-30 minutes, until risen, golden and firm to the touch. Leave to cool. Beat together the icing sugar and orange juice to make icing thick enough to coat the back of a wooden spoon. Drizzle over the muffins and top with a cluster of eggs. Leave to set. Best eaten within a day of making.','45 mins - 55 mins','N/A'),
(1129,'blueberry bakewell muffins','https://www.bbcgoodfood.com/recipes/blueberry-bakewell-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberry-bakewell-muffins-c6cff59.jpg','These easy blueberry muffins have a delightfully jammy centre and crunchy crumble topping – a tasty treat for teatime or packed lunch',17,12,1,2,22,'Heat oven to 200C/180C fan/gas 6 and line a 12-hole muffin tin with paper cases. Put all the topping ingredients, apart from the jam, in a bowl and rub together. Beat the butter and caster sugar together until pale and fluffy. Add the eggs and beat in for 1 min, then mix in the yogurt, extracts and milk. Combine the flour, baking powder and bicarb in a bowl with ¼ tsp fine salt, then tip this into the wet ingredients and stir in. Finally, fold in the blueberries and divide the mixture between the muffin cases. Top each muffin with 1 tsp blueberry jam, then scatter over the crumble mixture. Bake for 5 mins, then reduce oven to 180C/160C fan/gas 4 and bake for 15-18 mins more until risen and golden, and a cocktail stick inserted in comes out with just jam on it – no wet cake mixture. Cool in the tin for 10 mins, then carefully lift out onto a wire rack to finish cooling. Will keep for up to four days in an airtight container – after a day or two, pop them in the microwave for 10-15 secs on High to freshen up.','20 mins','20 mins'),
(1130,'summer fruit crumble muffins','https://www.bbcgoodfood.com/recipes/summer-fruit-crumble-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2022/07/Muffins-a9ef761.jpg','Combine raspberries, blueberries and peaches in these fruity crumble muffins. They''re perfect for picnics, playdates or to enjoy with a cuppa',6,12,1,2,22,'Heat the oven to 180C/160C fan/gas 4 and line a 12-hole muffin tin with paper cases. Beat 100g of the butter and the caster sugar together using an electric whisk until pale and fluffy. Add the eggs, beat for 1 min, then mix in the yogurt, vanilla and milk. Combine 250g of the flour with the baking powder and bicarb in a bowl with ¼ tsp salt, then stir this into the wet ingredients. Spoon 2 tbsp of the batter into each paper case. Gently push two raspberries each into four of the cases, two or three blackberries into another four and a large spoonful of chopped peaches into the final four. Divide the rest of the batter between the cases, spooning it over the fruit, then top with more berries or peaches, to match the filling. Mix the remaining 50g flour and 25g butter together with your fingers until the mixture clumps together in small chunks, then stir in the demerara sugar. Sprinkle a little of the crumble mixture over each muffin, then bake for 22-25 mins until risen, golden and a skewer inserted into the centre comes out clean. Leave to cool in the tin for 10 mins, then carefully lift out onto a wire rack to cool completely. Will keep for three days in an airtight container.','25 mins','20 mins'),
(1155,'easy venison pie','https://www.bbcgoodfood.com/recipes/easy-venison-pie','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Venison-Pie-0fb996b.jpg','Make a venison pie packed with pancetta, mushrooms and red wine, topped with golden puff pastry. It''s cooked low and slow for beautifully tender meat',7,6,1,2,14,'Heat the oven to 160C/140C fan/gas 3. Heat half the oil in a large flameproof casserole, brown the venison well in batches for 10 mins over a high heat, then set aside. Scatter the bacon into the pan with another ½ tbsp oil and sizzle for 8-10 mins until golden. Stir through the onions and bay leaves, drizzling with the remaining oil, and cook over a low heat for 5 mins until browned. Lower the heat to medium, then add the mushrooms and cook for a few minutes more until softened. Scatter over the flour, stirring until the flour turns brown. Tip the meat and any juices back into the pan along with the ketchup and give it all a good stir. Pour over the wine or ale, if using, and bring to the boil. Bubble for a few minutes, then pour over the stock. Season and bring to a simmer. Cover with a lid and put in the oven for about 2 hrs until the meat is tender. Will keep chilled for three days or frozen for up to three months. Leave to cool completely first. Defrost in a fridge overnight before reheating thoroughly in a microwave or pan until the meat is piping hot. To make the pie, heat the oven to 220C/200C fan/gas 7. Tip the filling into a 24-26cm rimmed pie dish and brush the rim of the dish with some of the egg yolk. Unravel the pastry, drape it over the dish and use a knife to trim and press against the rim. Re-roll the trimmings to make a decoration, if you like. Will keep covered and frozen for up to three months. Defrost fully before continuing with step three. Brush the pie with the remaining egg yolk and season with sea salt flakes, if you like. Make a few little slits in the centre of the pie to allow the steam to escape, and bake for 30-35 mins until deep golden.','15 mins','3 hrs'),
(1131,'chocolate muffins with hot chocolate custard','https://www.bbcgoodfood.com/recipes/chocolate-muffins-hot-chocolate-custard','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-338761_12-9b4f0db.jpg','These classic cocoa puddings with a hot chocolate sauce contain just 215 calories each - and there''s a lemon version too',5,6,1,2,22,'Heat oven to 160C/fan 140C/gas 3. Brush 6 holes of a muffin tin with a drop of oil. Sieve the cocoa into a large bowl, then add the rest of the dry ingredients. Stir to combine, then make a well in the centre. Beat the milk, egg and oil together in a jug, pour into the well, then stir quickly to make a batter. Spoon into the muffin tin, then bake for 15 mins or until risen and firm to the touch. Heat the custard in the microwave according to instructions, or in a pan, then tip in the chopped chocolate and stir until smooth. Turn the puddings into bowls and pour the custard over.','10 mins','10 mins'),
(1132,'maple-glazed blueberry muffins','https://www.bbcgoodfood.com/recipes/maple-glazed-blueberry-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/maple-glazed-blueberry-muffins-19604a0-scaled.jpg','Give the classic blueberry muffin a makeover by adding a crisp maple-sugar glaze, which sets to a cracked crust',6,12,1,2,22,'Heat oven to 180C/160C fan/gas 4. Place the muffin cases in a muffin tray. In a large bowl, combine the flour, baking powder, bicarb, sugar and 1/2 tsp salt. Whisk the butter, buttermilk, eggs, maple syrup and vanilla extract in a jug. Pour the wet ingredients into the dry and add most of the blueberries. Stir briefly to just combine, but don’t overmix or the muffins will be heavy. Divide the mixture between the muffin cases (use an ice cream scoop if you have one) and bake for 22-25 mins until well risen and a skewer inserted into the centre comes out clean. Transfer to a wire rack. Mix the remaining maple syrup and the icing sugar together with a pinch of salt until smooth. Brush the icing over each muffin while they are still warm. Dot a few blueberries on top of each one. Best eaten within a day or two.','20 mins','25 mins'),
(1133,'gooseberry & custard pies','https://www.bbcgoodfood.com/recipes/gooseberry-custard-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gooseberry-custard-tarts-d0a4cd8.jpg','Use slightly tart gooseberries in this summer dessert with a delicate vanilla custard and filo pastry',52,4,2,2,14,'First, make the compote. Put the sugar and vanilla pod with seeds in a saucepan with 3 tbsp water and bring to the boil. Try to gently swirl rather than stir. Drop in the gooseberries and simmer for 20 mins or until half the liquid has bubbled off. Divide between four small pie dishes and put in the fridge to cool while you make the custard. Put the cream, milk and vanilla pods with seeds in a saucepan and bring just to the boil. Meanwhile, in a large bowl, whisk the egg yolks, sugar, flour and cornflour together. Pour the hot milk mixture over the egg mixture, whisking constantly. Rinse out the saucepan, then pour the custard mixture into the pan. Cook on a low heat, whisking, for 5-6 mins until thick. Pour through a sieve over a large jug and discard the vanilla pods, then pour the custard over the compote in the pie dishes. Leave to cool. Heat oven to 180C/160C fan/gas 4. To make the pie lids, lay one sheet of the filo on the work surface and brush with the melted butter, then put another sheet on top. Repeat with the rest of the sheets. On the final layer, scatter over the sugar, then cut the pastry into four rectangles. Lightly scrunch each rectangle and place loosely on top of each pie dish. Bake the pies in the oven for 20 mins or until the pastry is golden and caramelised. Remove from the oven and serve.','25 mins','50 mins'),
(1134,'prawn, pepper & chickpea filo pie','https://www.bbcgoodfood.com/recipes/prawn-pepper-chickpea-filo-pie','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Prawn-pepper-and-chickpea-filo-pie-4f37a9b.jpg','Try a lighter version of a traditional pastry-topped pie, with a prawn, pepper & chickpea filling. The filo pastry also gives it an excellent crunch',15,4,1,2,14,'Heat the oven to 200C/180C fan/gas 6. Heat the 1 tsp of oil in an ovenproof pan over medium heat and add the onion and garlic. Cook gently for a few minutes until slightly softened. Stir in the harissa and tomato purée, and cook for 3 mins more. Tip in the chickpeas, prawns, peppers and stock. Bring to a gentle simmer, then season, stir through a squeeze of lemon juice and the parsley. Ruffle the pastry sheets on top and brush over the remaining olive oil. Bake for 15 mins until golden and bubbling. Serve with lemon wedges.','10 mins','20 mins'),
(1135,'moroccan-spiced turkey pie','https://www.bbcgoodfood.com/recipes/moroccan-spiced-turkey-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/morrocan-spiced-turkey-pie-13657f5.jpg','Transform leftover turkey in this spiced filo pastry parcel, inspired by a Moroccan pastilla - a perfect main course for Boxing Day',1,6,1,2,14,'Heat a large non-stick pan over a medium heat and toast the coriander and cumin seeds for 1-2 mins until fragrant. Add the cinnamon, olive oil, aubergine and shallots. Coat the veg in the spices and cook on a medium-low heat for about 10 mins until the aubergine has softened slightly. Add the harissa and peppers. Give everything a good stir, then add the tomatoes and crumble over the stock cube. Simmer, uncovered, for 20 mins until the veg is tender and the mixture is nice and thick. Stir through the turkey meat and season well. Set aside to cool. Heat oven to 200C/180C fan/gas 6 and grease a 23cm springform tin with a little butter. Lay two sheets of filo over the base of the tin and brush with the melted butter. Cross over another two layers of filo and brush again with butter. Repeat once more, then put the cooled filling in the centre. Using the remaining four pastry sheets, cover the filling and brush with more melted butter. Scrunch the top of the pie together and bake for 30-35 mins. Remove from the oven and scatter the pie with the flaked almonds. Cook for a final 10 mins until the pie is golden and piping hot. Serve straight away.','20 mins','1 hr and 15 mins'),
(1136,'feta, herb & spring onion borek','https://www.bbcgoodfood.com/recipes/feta-herb-spring-onion-borek','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Feta-herb-and-spring-onion-borek-123a708.jpg','Try this classic borek for lunch or dinner, with crispy filo pastry and a tasty feta, herb and spring onion filling that doesn’t require cooking in advance',124,6,1,2,14,'Heat the oven to 200C/180C fan/gas 6. Prepare the filling first. Mash the feta in a bowl with the back of a fork. Add the chilli, spring onions, herbs and olive oil, then mix everything well using the fork. For the sauce, whisk the beaten egg with the olive oil and milk in a small bowl until fully combined. Lay the filo sheets on a clean work surface and cover with a clean, damp cloth to ensure they don’t dry out as you work. Brush the base of a shallow 20cm round ovenproof dish with some olive oil. Layer six sheets of filo in the bottom of the dish, overlapping them by about 45 degrees and fanning them out, also ensuring that half of each sheet covers the base while the other half overhangs the dish. Between each addition, liberally brush the sheets with the sauce. The entire base should be covered with the filo, and the filo generously basted in the sauce. Tip in all the filling, then fold the overhanging filo over the filling to enclose it, brushing the sheets with more sauce as you do. Cover the top with a final sheet of filo, but don’t brush this with the sauce. Whisk the egg yolks with the 1 tbsp olive oil and brush this on top of the borek. Sprinkle with the sesame and nigella seeds and bake for 30 mins until golden brown.','30 mins','30 mins'),
(1137,'spinach filo spiral pie','https://www.bbcgoodfood.com/recipes/spinach-filo-spiral-pie','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Spinach-filo-spiral-pie-3d77d2c.jpg','These impressive-looking spinach filo spirals are deceptively easy to make. A take on Greek spanakopita, serve with a side salad for a weekend lunch',60,4,1,2,14,'Heat a large non-stick pan over a medium heat. Tip in the spinach and cook, stirring, until wilted (you won’t need any oil or water as it will steam in its own moisture). Leave to cool a little, then tip into a clean tea towel and squeeze out as much water as possible. Chop and put in a bowl. Heat the knob of butter in a non-stick frying pan. Add the onion and cook for about 10 mins until softened. Tip into the bowl with the spinach, then stir in the feta using a fork until fully combined. Taste for seasoning. Heat the oven to 190C/170C fan/gas 5. Brush a 24 x 4cm deep metal pie dish with some of the butter. Lay a filo sheet on your work surface. Brush with some of the melted butter, then spread about 2 tbsp of the filling in a line along the longest edge of the pastry. Roll loosely into a long sausage (don’t roll it too tight as this might cause the pastry to split), then coil the pastry and put in the centre of the tin. Repeat with the rest of the pastry and filling, arranging the coils around the central one. Brush the tops with more butter, then sprinkle with the nigella seeds. Bake for 25-30 mins until golden and crisp.','30 mins','25 mins'),
(1138,'sticky tamarind tomato & spinach whirl','https://www.bbcgoodfood.com/recipes/sticky-tamarind-tomato-spinach-whirl','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Tamarind-tomato-and-spinach-whirl-fe414fd.jpg','Try this riff on a Greek spanakopita. With a sticky-sweet, spiced and tangy twist, it''s a spectacular veggie centrepiece for any special occasion',89,8,2,2,14,'Heat the oven to 160C/140C fan/gas 3. Mix the tomatoes, garlic, half the oil, tamarind and sugar together, along with a generous pinch of seasoning. Tip onto a baking tray and put in the oven for 25-30 mins or until sticky and roasted. Leave to cool a little. Heat the remaining oil in a large frying pan over a medium heat, then add the leek and spring onions, stirring occasionally until softened, about 10 mins. Leave to cool completely, then tip into a bowl and mix with the spinach, feta, roasted tomatoes, eggs, coriander, smoked paprika and seasoning. Heat the oven to 200C/180C/gas 6. Brush a sheet of filo with melted butter, then lay another sheet on top and brush with butter again, keeping the rest of the filo covered with a clean, damp tea towel to stop it drying out, Spoon a third of the filling along the long side of the rectangle and gently roll into a long tube. Repeat this two more times until you have used all the filo and filling. In a round pie dish, or round ovenproof pan, carefully coil the three filo rolls from the outside of the dish in towards the middle, so you end up with a large spiral. Don’t worry if it cracks a bit – you can layer the filo to cover any cracks. Brush the whole spiral with more butter, sprinkle over the chilli flakes and bake for 50-60 mins until deep, golden brown. Scatter over the extra coriander to serve.','40 mins','1 hr and 30 mins'),
(1139,'lighter spanakopita','https://www.bbcgoodfood.com/recipes/lighter-spanakopita','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lighter-spanakopita-7b44e5b.jpg','This traditional Greek spinach and feta cheese pie gets a healthier makeover - we''ve slashed the fat, salt and calories',37,6,1,2,14,'Pack the spinach into a large heatproof bowl. Pour boiling water over and press the spinach down with a wooden spoon. Leave for 1 min to wilt, stirring a couple of times so all the leaves are submerged. Tip into a large colander and place under running cold water to cool quickly. Drain well – squeeze out any excess water by pressing the spinach down with the wooden spoon, then squeezing with your hands. Using kitchen paper, pat the spinach dry into a compact shape, lay it on a board and roughly chop, then set aside. Heat 2 tsp of the oil in a large non-stick frying pan. Add the shallot, spring onions and garlic, and fry over a high-ish heat for 3-4 mins, stirring often, until softened and just starting to brown. Lower the heat, tip in the spinach and stir for 1 min only to finish drying. Season with pepper and a good pinch of nutmeg, and leave to cool. Heat oven to 190C/170C fan/gas 5 and put a baking sheet in to heat up. In a bowl, stir the ricotta into the beaten egg and mix in the 2 tbsp dill, the chives and mint. Season with pepper and a pinch of salt. Lightly oil a 20cm square x 4cm deep cake tin. Mix the remaining oil with the 1 rounded tsp dill. Lay the filo pastry sheets on a large board. Brush the top one with a little of the dill oil, then carefully lower it into the tin to line the base and 2 of the sides, with an overhang on both sides. The pastry doesn’t have to fit in smoothly – a few wrinkles and folds in it are fine. Brush a bit more oil over the second pastry sheet and this time lay it down in the tin the other way round, so it drapes over and lines the base and the other 2 sides. The tin should now be completely covered, with a pastry overhang all round. Repeat the criss-cross layering with 2 more oiled filo sheets. Stir the ricotta mixture into the spinach, then crumble in the feta. Lightly stir it in so you leave a few chunky pieces. Spoon this filling into the lined tin and level it. Bring the pastry sides up and over the filling, then brush a little oil over them. Brush the remainder of the oil over the last filo sheet on the board. Cut the sheet in half widthways and lay one half over at a time, in big folds, to cover the filling and give you a pastry topping. Place the tin on the heated baking sheet and bake for 25-30 mins until the pastry is crisp and golden. Leave to cool for 10-15 mins. Remove the tin and transfer to a serving plate. If your tin doesn’t have a loose bottom, carefully upturn it onto a flat board, then flip it over onto the serving plate. Serve warm or cold with minted yogurt dip and a tomato, cucumber & pepper salad (see below), if you like.','1 hr','30 mins - 35 mins'),
(1156,'chicken, kale & mushroom pot pie','https://www.bbcgoodfood.com/recipes/chicken-kale-mushroom-pot-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-kale-mushroom-pot-pie-1b0817a.jpg','A satisfying chicken and mushroom one-pot that makes a great family supper or freeze leftovers for another day.',1,4,1,2,14,'Heat ½ tbsp oil over a gentle heat in a flameproof casserole dish. Add the onion and cook for 5 mins until softening. Scatter over the thyme and garlic, and stir for 1 min. Turn up the heat and add the chicken, frying until golden but not fully cooked. Add the mushrooms and the remaining oil. Heat oven to 200C/180 fan/gas 6. Add the stock, crème fraîche, mustard and kale, and season well. Add the cornflour mixture and stir until thickened a little. Remove from the heat and cover with the puff pastry lid, pressing into the sides of the casserole dish. Slice a cross in the centre and glaze with the egg. Bake for 30 mins until the pastry is puffed up and golden.','10 mins','40 mins'),
(1140,'summer herb & feta pies','https://www.bbcgoodfood.com/recipes/summer-herb-feta-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/summer-herb-feta-pies-8067809.jpg','These little filo pastry parcels are packed with a cheese, basil, mint and watercress filling - perfect for a picnic',43,8,1,2,14,'Heat oven to 200C/180C fan/gas 6. Remove any thick stalks from the watercress, then chop the rest and put in a food processor with the herbs and spring onions. Blitz for a few secs, then add the eggs, nutmeg and some seasoning, and blitz again briefly – you don’t want too fine a chop. Tip the mixture into a bowl and stir in the feta and artichokes. Melt the butter and unwrap the filo sheets. Cut them into 12-13cm squares through the layers – you may have too much pastry but the leftovers can be re-wrapped and saved for another recipe . Stack 4 squares of pastry on top of each other, brushing lightly with butter as you go, offsetting each square to get a star shape all round the edge. Continue until you have 8 stacks. Press each stack into a deep-hole muffin tin and fill with the feta mixture. Fold over the pastry to enclose the filling, pinching it together. Brush the tops with the remaining butter and sprinkle with Parmesan. Bake for 25 mins until the pastry is golden and crisp. Leave to cool for a few mins, then remove the tins and cool completely on a wire rack. Pack into a plastic container to take to the picnic and serve with a tomato salad, if you like.','25 mins','25 mins'),
(1141,'sausage, apple & sage roll','https://www.bbcgoodfood.com/recipes/sausage-apple-sage-roll','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-apple-sage-roll-6949a31-scaled.jpg','Crispy filo pastry filled with sausagemeat, sage and chunks of juicy apple makes a simple yet tasty weeknight supper',48,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. Line a large baking tray with baking parchment. Heat 1 tsp of the oil in a small non-stick saucepan and gently fry the onion and apple for 6-8 mins, stirring regularly, until softened and lightly browned. Leave to cool for 5 mins. Place 1 sheet of pastry on your work surface, with the short end closest to you, and brush lightly with oil. Place another sheet on top, but off-set roughly 6cm to the right, and brush with a little more oil. Top with the final sheet, off-set another 6cm to the right. The pastry rectangle should be roughly 40 x 45cm. Squeeze the sausagemeat from the skins into a large bowl. Add the onion, apple and sage, and mix well. Form the pork mixture into a large sausage shape and place across the pastry, about 8cm in from one short end and 8cm from each side. Fold up the bottom of the pastry until it begins to cover the filling, then fold in the sides. Roll the pastry from the bottom, gently but firmly, to enclose the filling. Place on the baking tray and brush with the remaining oil. Bake for 30 mins or until the filling is cooked and the pastry is golden brown and crisp. Serve with mixed salad leaves.','20 mins','40 mins'),
(1142,'baklava cake','https://www.bbcgoodfood.com/recipes/baklava-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Baklava-cake-869a3f5.jpg','Try a twist on baklava by turning the classic nutty filo pastry dessert into a cake. It makes a spectacular centrepiece that''s perfect for a celebration',125,10,2,2,14,'Tip the flour, baking powder, semolina and cinnamon into a large bowl. Put the nuts in a food processor and blitz until coarsely chopped. Beat the egg whites using an electric whisk or stand mixer on a high speed until stiff and slowly add half of the sugar. Reduce the speed to medium and beat for about 5 mins until the meringue is nice and firm, then set aside. Beat the egg yolks on a high speed, gradually adding the remaining 50g of sugar. When all the sugar is incorporated, beat for about 10 mins on a high speed until pale in colour and doubled in volume. Gently fold the egg whites into the egg yolk mixture, then add the nut and flour mixtures and carefully fold in. Set aside. Heat the oven to 200C/180C fan/gas 6. For the filo layers, use some of the melted butter to brush the inside of a 23cm bundt tin. Lay the sheets of filo on a board and cut them in half down the middle to give you 16 strips. Take eight strips, keeping the rest covered, and brush one at a time with more butter. Arrange in the tin, making sure the inside of the ring is covered, with overhang. Repeat with the remaining filo. Dollop the cake batter evenly into the tin. Fold the overhanging filo strips inwards to cover the cake mixture, trimming off the excess. Brush the top of the filo with more butter, then prick all over using the point of a sharp knife. Bake for 25 mins or until golden. When a skewer is inserted it should come out clean with no sticky crumbs. Meanwhile, make the syrup. Put the sugar, honey, rosewater, cinnamon stick and 300ml water in a saucepan. Bring to the boil over a high heat, then reduce the heat to medium and simmer for 5 mins. Do not stir. Pour half of the hot syrup over the baklava cake as soon as it comes out of the oven. Leave the cake to cool completely before removing it from the tin, carefully turning it out onto a lined baking sheet. Poke the top of the cake with a skewer. Return it to the oven for approximately 15 mins or until the filo is crispy and golden. Meanwhile, reheat the remaining syrup, then pour over the cake when it comes out of the oven. Leave to cool or come to room temperature before serving. Pipe rosettes of rosewater cream on top and sprinkle with chopped walnuts, pistachios and edible rose petals, if you like. Will keep for three days in the fridge.','40 mins','40 mins'),
(1143,'veggie spiral pie with spiced tomato sauce & chopped salad','https://www.bbcgoodfood.com/recipes/veggie-spiral-pie-spiced-tomato-sauce-chopped-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-spiral-pie-with-spiced-tomato-sauce-chopped-salad-49d8d9f.jpg','Rice, feta and spinach make the filling for a savoury twist on the Moroccan dish M''hanncha - perfect for entertaining outdoors',6,8,3,2,14,'Heat a large saucepan, tip in the cumin and coriander seeds and toast for a few mins until you can smell their fragrance and they turn a shade darker, then tip into a pestle and mortar. Add the oil to the pan, then tip in the onions and cook slowly until golden and caramelised – this will take 15-20 mins. Meanwhile, bring 2 pans of water to the boil. Add the lentils to 1 pan and cook for 20 mins. Put the rice and a pinch of salt in the other pan and cook for 5 mins (it should still have a little bite). Drain both pans, and leave the lentils and rice to steam-dry. Boil the kettle. Stir the garlic into the onions and cook for 1-2 mins over a low heat. Grind the whole spices in the pestle and mortar to a fine powder and add these to the onion mixture. Grate half of the nutmeg and add to the onions with the turmeric and allspice. Put the spinach in a colander in the sink, pour over a kettle of boiling water, then rinse under cold water. Use your hands to squeeze out as much liquid as possible. Finely chop the spinach and add to the onions with the herbs, lemon zest and juice and the rice, lentils and plenty of seasoning. Leave to cool before stirring through the feta. To assemble the pie, you’ll need plenty of space on your work surface – about 1 metre. Unwrap the filo and cover with a damp tea towel. Have your bowls of melted butter and beaten egg to hand, as well as a pastry brush for each. Working quickly, lay 4 sheets of filo end to end, running along the length of your work surface, and butter each piece generously, overlapping each sheet by about 10cm. Top with another 4 sheets, butter well, then repeat with 3 final sheets of filo (save the last one to cover any cracks later on.) Spoon the rice filling down the centre of the filo, leaving 5cm free on either end. Brush the egg around the edges and tuck the ends in to cover the filling. Starting from one end, roll the filo over the filling, working your way along until you have a long filo sausage. From one end, start to coil the sausage back on itself – if the pastry cracks, patch it over the hole with your reserved piece of filo. When the coil is complete, slide onto a tray lined with baking parchment, brush the top with beaten egg and sprinkle over the sesame seeds. You can now cover it loosely with cling film and chill for up to 24 hrs. When you’re ready to bake, heat oven to 200C/180C fan/gas 6. Place the pie on the middle shelf and bake for 45 mins until golden and crisp. Meanwhile, tip the ingredients for the tomato sauce into a pan, season and bubble for 30 mins until rich and thick. For the salad, halve the cucumber through the centre, then cut in half lengthways and chop into small cubes. Put in a bowl, add the remaining ingredients and season well. Set aside until ready to serve. Leave the pie to cool for 20 mins before serving with the sauce, salad and a large bowl of yogurt.','50 mins','1 hr and 30 mins'),
(1144,'samosa pie','https://www.bbcgoodfood.com/recipes/samosa-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/samosa-pie-f865c5a.jpg','Cover spiced lamb mince, stewed potato and peas with crispy filo pastry for a low-fat, Indian-inspired dinner',24,4,1,2,14,'Heat oven to 180C/160C fan/gas 4. Heat 1 tbsp of the oil in a frying pan. Cook the onion and mince for about 5 mins until the meat is browned. Stir in the garlic, curry powder, sweet potato and 300ml water. Cook for 5-8 mins until the potato has softened. Stir in the peas, coriander and a squeeze of lemon juice, then season. Spoon the mixture into a baking dish. Brush the sheets of filo with the remaining oil and scrunch over the top of the mince. Sprinkle with cumin seeds and bake for 10-15 mins or until the top is crisp.','5 mins','30 mins'),
(1275,'planet cookies','https://www.bbcgoodfood.com/recipes/planet-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/planet-cookies-6b34384.jpg','Make a whole solar system from your kitchen with our super cute planet cookies! The baking and decorating will keep even the most energetic children busy over the half-term break',23,20,2,2,23,'Heat oven to 190C/170C fan/gas 5. Line a baking sheet with baking parchment. Using an electric whisk, beat the butter and sugar together in a large mixing bowl until pale and fluffy. Gradually beat in the egg and vanilla extract. Stir in the flour, then knead the mixture briefly to make a dough. Divide the dough in half. One half can now be frozen or chilled to make another batch of biscuits. On a floured work surface, roll out the remaining dough to the thickness of a £1 coin. Using plain round biscuit cutters, cut out the following size biscuits: 1 x 8cm, 2x7cm, 4x6cm, 2x5cm and 1x3.5cm. Carefully transfer the biscuits to the prepared baking sheet and bake for 10-12 mins until pale golden brown. Leave them on the baking sheet for 5 mins, then transfer to a wire rack to cool completely. Mix the icing sugar with 2-3 tbsp water to make a smooth, spreadable icing – it shouldn’t be too runny. Spoon 1 tsp of the icing into a small bowl and mix enough yellow food colouring to make it bright yellow, then use to cover the largest biscuit and set aside to dry. Spoon 2 tsp of the white icing into another small bowl and mix in enough blue food colouring to make it a bright, light blue. Use a little of this icing to cover a 6cm biscuit to make Uranus. Set aside to dry. Add a little more blue colour to the blue icing and use a little to cover another 6cm biscuit, to make the water on the Earth. Set aside to dry. Add a little more colour to the blue icing and cover another 6cm biscuit. Using a cocktail stick, swirl a little extra blue food colour and white icing onto this iced biscuit to make Neptune. Set aside to dry. Add a little more blue and a tiny bit of black food colouring to the blue icing and spread it over the 3.5 cm biscuit to make Pluto. Set aside to dry. For Mercury, spoon 1 tbsp of the white icing into another bowl and add enough of the caramel flavouring to make a beige colour. Spread a little of it over a 5cm biscuit. Using a cocktail stick, swirl a little extra caramel flavouring into the icing on the biscuit. Set aside to dry. Spread a little of the beige icing over a 7cm biscuit. Using a cocktail stick, ￼swirl in a little red food colouring and white icing to make the patterns of Jupiter. Set aside to dry. For Saturn, add a little yellow food colouring to the beige icing and spread it over the other 7cm biscuit, leaving a 1cm border around the edge. Use a cocktail stick to swirl a little yellow food colouring into the icing and set aside to dry. For Venus, add a little orange food colouring to the beige icing and spread some over the remaining 6cm biscuit. Using a cocktail stick, swirl in a little orange. Set aside to dry. To finish Earth, spoon 1tsp white icing into another small bowl and add enough green food colouring to make it bright green. Blob a little of the green icing over parts of the blue icing. For Mars, spoon 1tsp white icing into a small bowl and add enough red food colouring to make it bright red. Spread the red icing over a 5cm biscuit. Using a cocktail stick, swirl a little red food colouring into the icing. Set aside to dry. Fit a plain nozzle to a piping bag and add the remaining white icing. Pipe 2 stripes around the middle of the Saturn biscuit to give it its rings. If you like, you can sprinkle the Mercury and Venus biscuits with a little edible gold glitter to make them sparkle.','1 hr and 30 mins','10 mins - 12 mins'),
(1145,'jumbo turkey samosas','https://www.bbcgoodfood.com/recipes/jumbo-turkey-samosas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jumbo-turkey-samosas-40e3458.jpg','Use up leftover cooked potatoes and turkey in these spiced filo pastry parcels, then serve with tangy mango chutney',6,4,1,2,14,'In a large bowl, combine the curry paste, mango chutney and 2 tbsp yogurt, and mix well. Fold in the potatoes, turkey, peas and coriander, then season well. Heat oven to 200C/180C fan/gas 6. Cut the sheets of filo in half lengthways so you have 12 strips – keep the pastry covered with a tea towel while you work so it doesn’t dry out. Layer up 3 sheets of filo, brushing a little oil between each sheet. Pile a quarter of the filling in the top corner of the pastry in a triangle shape. Fold the pastry over to encase the filling, then keep folding until the parcel is sealed and the filo is used up. Brush the final edge with a little beaten egg to help stick the pastry together. Repeat to make all 4 jumbo samosas. Lay your samosas on a baking sheet and brush each one with more beaten egg, then sprinkle with sesame seeds. Bake for 35 mins or until the samosas are golden brown and the filling is piping hot. Serve with the remaining yogurt, some mango chutney and coriander leaves.','25 mins','35 mins'),
(1146,'mincemeat samosas','https://www.bbcgoodfood.com/recipes/mincemeat-samosas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mincemeat-samosas-de113fc.jpg','Give mince pies a makeover with this alternative recipe - all the festive flavour wrapped up in sheets of filo pastry and baked until crisp',5,18,1,2,14,'Mix together the fruit, dried fruit, nuts, spices and brandy. Leave the mixture in the fridge for 30 mins to allow the flavours to develop, then stir in the honey. Heat oven to 200C/180C fan/gas 6. Arrange the filo sheets with the short side facing you and, using scissors, cut the stack in 3 vertically, to make long strips. Brush the top 3 strips with melted butter. Working quickly, put 1 heaped spoonful of mincemeat at the top of the first strip. Fold one corner of the pastry over it to form a triangle, then continue folding alternately left and right to form a triangular package, until you’ve used up all the strip. Repeat with the other strips, brushing with butter before folding. Place on a baking sheet and brush each one with more melted butter. Bake for 15-20 mins until crisp and golden. Dust with icing sugar and serve warm (reheat later in a low oven if necessary).','1 hr and 30 mins','15 mins - 20 mins'),
(1147,'aubergine, tomato & feta baklava','https://www.bbcgoodfood.com/recipes/aubergine-tomato-feta-baklava','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/aubergine-tomato-feta-baklava-d6637b1.jpg','This impressive make-ahead vegetarian dish layers up a spiced veggie filling with crispy filo pastry - perfect with a light salad',126,4,2,2,14,'Heat the olive oil in a large heavy-based pan over a low heat and sauté the onions until softened and sticky – this can take up to 30 mins. Meanwhile, heat a griddle pan until very hot. Brush a few of the aubergine slices with a little oil, sprinkle with sea salt and cook over a high heat until well charred and cooked through, 5-7 mins, turning halfway through cooking. Lift onto kitchen paper and cook the remaining slices. Add the garlic, cinnamon and tomato purée to the onions and cook for a further 2 mins. Add the tomatoes, sugar, dill, mint, vinegar and a generous pinch of sea salt. Reduce the heat to a simmer and cook for 10-15 mins until thickened. Adjust the seasoning to taste and allow to cool. Can be made up to 2 days ahead. Heat oven to 180C/160C fan/gas 4. Unfold the pastry and cover with a damp cloth to prevent it drying out. Brush a baking tray (about 30 x 20cm) with melted butter. Line the tray with a sheet of filo (cut to fit if too big), brush with butter and repeat until you have a 6-layer thickness. Spread half the tomato mixture over the pastry, top with half each of the dates, almonds and feta, and cover with half of the aubergine slices. Sandwich another 4 layers of filo together with melted butter and place on top of the aubergines. Top with the remaining tomato mixture, dates, feta and aubergines. Sandwich the remaining 4 filo sheets together as before and place on top. Lightly score the top, cutting into diamonds about 5 x 10cm. Brush with the remaining butter, splash with a little water and cook for 35-45 mins or until golden. Leave to cool a little before serving, then drizzle each portion with a little honey. Serve with houmous and a bowl of mixed green leaves, dressed with olive oil and lemon juice, with flat-leaf parsley leaves and chopped Kalamata olives.','5 mins','2 hrs'),
(1148,'crispy filo tart with seasonal veg','https://www.bbcgoodfood.com/recipes/crispy-filo-tart-seasonal-veg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tart_5-e76d0ab.jpg','This light, zesty tart is packed with asparagus, courgette, herbs and feta. Best served al fresco',6,6,2,2,14,'Heat oven to 180C/160C fan/gas 4 and pop in a baking sheet to heat up. Use a little of the oil to lightly grease an 18 x 25cm tart tin (ours was 3cm deep). Line the tin with a few strips of baking parchment – this will help you to lift out the tart later. Unfold the pastry, but keep it covered with a damp tea towel as you work to prevent it from drying out. Taking 1 sheet at a time, brush each piece well with oil, then use to line your tart tin, leaving a little pastry hanging over the edges. Sprinkle a thin layer of breadcrumbs between each layer of pastry as you go – this will help to keep the pastry crisp. When you get to the final layer, brush with a little extra oil and scrunch the edges together to create a pretty border. Toss the asparagus and courgettes with seasoning and the remaining oil and tip onto a baking tray. Place the pastry case on the top shelf of the oven, with the veg on the shelf below and cook for 10 mins. Remove the veg from the oven. Brush the tart with a little beaten egg and bake for a further 5 mins until the pastry is golden brown and crunchy. Whisk the remaining egg with the lemon zest, herbs, crème fraîche and seasoning. Lay half the veg in the bottom of the pastry case and add half the feta. Pour over the egg mixture, then top with the remaining veg and feta. Bake for 40 mins until the filling has set. Remove from the oven and leave to cool. Serve with a grinding of black pepper and scattered with small salad leaves or herbs, if you like.','25 mins','50 mins'),
(1149,'griddled vegetable & feta tart','https://www.bbcgoodfood.com/recipes/griddled-vegetable-feta-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tart-f0b44a1.jpg','Crispy filo pastry makes a light base for a vegetarian tart filled with Mediterranean veg and feta',28,4,1,2,14,'Heat oven to 220C/200C fan/gas 7. Pop 33 x 23cm baking tray in the oven to heat up. Brush a griddle pan with about 1 tsp of the oil and griddle the aubergines until nicely charred, then remove. Repeat with the courgettes and onions, using a little more oil if you need to. Remove the tray from the oven and brush with a little oil. Brush a large sheet of filo with oil, top with another sheet, add a little more oil and repeat with the final sheet. Transfer the pastry to the hot tray, pushing it into the edges a little. Arrange the griddled veg on top, then season. Add the tomatoes, cut-side up, then drizzle on the vinegar and any remaining oil. Crumble on the feta and sprinkle with oregano. Cook for about 20 mins until crispy and golden. Serve with the dressed mixed salad leaves.','10 mins','40 mins'),
(1150,'pear, chocolate & marzipan strudel','https://www.bbcgoodfood.com/recipes/pear-chocolate-marzipan-strudel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/strudel_0-894d472.jpg','Prepare ahead and freeze this rich filo pastry pud, then cook from frozen for the perfect fuss-free seasonal dessert',5,6,1,2,14,'Melt a knob of butter in a large frying pan, add the pears and 1 tbsp sugar, and cook for 5 mins. Tip into a bowl and leave to cool. Then add the Disaronno (if using), biscuits, cinnamon, chocolate, marzipan, and 2 tbsp sugar and mix well. Line a tray with baking parchment. Lay a sheet of filo on it, brush with melted butter and sprinkle with a little sugar. Top with a second sheet of filo and keep going until you’ve used all the pastry. Pile the filling down one of the long sides of the pastry, then roll it up to form a sausage. Make sure it is seam-side down, then tuck the ends under tightly. Roll up in the parchment, wrap tightly in foil and freeze. Heat oven to 200C/180C fan/gas 6. Put a tray in the oven to heat. Unwrap the strudel and place, seam-side down and still on its parchment, onto the tray. Brush with melted butter and sprinkle over 1 tbsp sugar. Bake for 35-40 mins, or until golden and crisp. Leave to cool for 10 mins, then dust with icing sugar.','25 mins','45 mins'),
(1151,'filo-topped fish pie','https://www.bbcgoodfood.com/recipes/filo-topped-fish-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/filo_0-ca29708.jpg','Fish pie doesn''t have to mean heavy mashed potato. Light, crisp pastry tops this tomato-based recipe with halibut, sea bass and prawns',42,6,1,2,14,'Heat 25g of the butter in a large saucepan until foaming. Add the onion and fennel, and cook gently for about 10 mins until very tender. Stir in the garlic and cook for 1 min more. Add the tomato purée, cook for 1 min, then add the white wine and simmer for a few mins until reduced by half. Tip in the chopped tomatoes and simmer for 15 mins until you have a rich, thick sauce. Remove the sauce from the heat. Stir in the cream, fish, prawns and herbs, then season. Spoon the mixture into a 2-litre baking dish. When ready to cook, heat oven to 180C/160C fan/gas 4 and melt the rest of the butter. Unroll the filo pastry and brush 6 sheets all over one side with the melted butter. Gently scrunch up the pastry and lay on the top of the pie filling, covering completely. Bake for 25 mins in the middle of the oven until the pastry is golden and crisp.','25 mins','40 mins'),
(1152,'sweet potato & ginger parcels','https://www.bbcgoodfood.com/recipes/sweet-potato-ginger-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259563_8-69d833e.jpg','These moreish snacks are made with Tunisian brik pastry. Similar to filo, but crispier, it''s well worth keeping a pack in the freezer',5,15,1,2,14,'Microwave or oven-cook potatoes until tender. Melt a knob of butter in a pan. Fry chilli for 30 secs, then spring onion whites and ginger for 1 min. Scrape flesh from potatoes, discard skins, then mash into the pan. Season, then stir in onion greens. Heat oven to 200C/180C fan/gas 6. Unroll a pastry sheet and cut 3 x 6cm strips from the middle; discard leftovers. Brush strips with melted butter, place a spoonful of potato mix at one end, then fold up from side-to-side to make a triangular parcel. The filling should be completely sealed in. Arrange parcels on a baking sheet, brush with butter and repeat until you’ve used up all the filling. Bake for 20 mins until crisp and golden. Dust with a pinch of cinnamon; then serve.','30 mins','30 mins'),
(1153,'chicken & almond pastillas','https://www.bbcgoodfood.com/recipes/chicken-almond-pastillas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1238480_8-3792667.jpg','Morocco''s very own version of the pasty - spiced meat and apricots are wrapped in crisp filo pastry and finished with cinnamon sugar',6,6,1,2,14,'Heat 1 tbsp butter in a large casserole dish, add the onion, ginger and garlic, and cook for 5 mins until soft. Add the cinnamon, the cardamom seeds and the turmeric. Stir around the pan for 30 secs until aromatic, then add the chicken and flour. Stir to coat the chicken pieces, then add the chicken stock and season. Stir, cover with a lid and leave to simmer for 45 mins. Remove the lid from the pan and lift the chicken pieces onto a plate. Add the honey and almonds to the cooking liquid and turn up the heat. Bubble the liquid until thick and reduced by about half. Meanwhile, finely shred the chicken with 2 forks. Return the chicken to the sauce and stir in the orange zest, apricots, flaked almonds and parsley – the mixture should be quite thick. Leave to cool. Heat oven to 200C/180C fan/gas 6. Unwrap the pastry, keeping any you’re not working with under a damp tea towel, so it doesn’t dry out. Taking one piece at a time, brush all over with the melted butter, and around the edges with the beaten egg. Put roughly a sixth of the chicken filling in the top right-hand corner in a triangular shape. Fold the opposite side of the pastry over to cover the filling. Keep flipping the encased filing down the pastry, brushing between the layers with butter as you go, to give you a triangular parcel, like a large samosa. Repeat with the remaining pastry and filling until you have 6 pastillas. Put the pastillas on a baking tray and brush the tops with egg. Bake for 25 mins until the pastry is crisp and golden. For an authentic finish, mix together the remaining cinnamon and icing sugar, and dust the pastillas just before serving.','40 mins','1 hr and 10 mins'),
(1154,'roast chicken pie','https://www.bbcgoodfood.com/recipes/roast-chicken-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roast-chicken-pie-bcaa355.jpg','The ultimate comfort food recipe. Golden brown pastry and a creamy chicken, leek and bacon filling make this a real feast for friends and family',5,6,2,2,14,'Heat oven to 180C/160C fan/gas 5. Season the chicken and roast for 1 hr 30 mins until the juices run clear when you cut into the leg. To make the pastry, sift the flour and salt into a bowl, then grate in the butter and lard. Rub the mixture with your fingers into breadcrumbs, then add 2 tbsp cold water. Continue to add cold water, 1 tbsp at a time, until the mixture binds together. Be careful not to add too much. Wrap in cling film and leave to rest while you make the filling. (Alternatively you can use ready-rolled shortcrust pastry). In a large, non-stick frying pan, heat the oil, then fry the bacon. Once browned, reduce the heat, add the leeks and cook until they have softened. Mix the butter and flour into a paste and add to the pan, stirring to coat the bacon and leeks. Slowly add the milk, stirring constantly to ensure the mixture doesn’t get lumpy. Add the chicken stock and petit pois, cook for 2 mins, then add the mustard and remove from the heat. Once the chicken is cooked, remove from the oven, leave to rest briefly and carve the meat, discarding the skin and bones. Add this to the pan along with the parsley. Turn the oven up to 220C/200C fan/gas 6. Roll out two-thirds of the pastry and use to line a 25cm pie dish. Spoon your filling on top, then roll out the remaining pastry for the pie topping. Brush the edges of the pastry in the pie dish with water, then lay the top over it. Crimp and seal the edges and use any off-cuts for decoration. Brush with the beaten egg, then make an X-shaped cut in the middle for steam to escape. Bake for 45 mins until golden.','45 mins','2 hrs and 15 mins'),
(1157,'slow cooker shepherd''s pie','https://www.bbcgoodfood.com/recipes/slow-cooker-shepherds-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/shepherds-pie-6b14a43.jpg','Slow cook shepherd''s pie and you''ll be rewarded with savoury, succulent mince. This recipe is also low-fat and low-calorie with three of your five-a-day',5,4,1,2,14,'Heat the slow cooker if necessary. Heat the oil in a large frying pan. Tip the onions and thyme sprigs and fry for 2-3 mins. Then add the carrots and fry together, stirring occasionally until the vegetables start to brown. Stir in the mince and fry for 1-2 mins until no longer pink. Stir in the flour then cook for another 1-2 mins. Stir in the tomato purée and lentils and season with pepper and the Worcestershire sauce, adding a splash of water if you think the mixture is too dry. Scrape everything into the slow cooker. Meanwhile cook both lots of potatoes in simmering water for 12-13 minutes or until they are cooked through. Drain well and then mash with the crème fraîche. Spoon this on top of the mince mixture and cook on Low for 5 hours - the mixture should be bubbling at the sides when it is ready. Crisp up the potato topping under the grill if you like.','1 hr','5 hrs'),
(1158,'one-pot paneer curry pie','https://www.bbcgoodfood.com/recipes/one-pot-paneer-curry-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/one-pot-paneer-curry-pie-c1bcd2f.jpg','What''s more comforting than a curry in pie form? This one-pot paneer pie is filled with a makhani-style filling and topped with a crisp puff pastry lid ',14,6,2,2,14,'Heat the oil over a medium heat in a shallow flameproof casserole dish roughly 30cm wide. Add the paneer and fry for 5 mins, turning with tongs until each side is golden. Remove from the pan and set aside on a plate lined with kitchen paper.  Heat the ghee or butter in the same dish over a medium-low heat, then add the onions and a big pinch of salt. Fry for 15 mins, or until softened and caramelised. Stir in the garlic and ginger, cook for 1 min, then tip in the spices and fry for a further 2 mins. Scrape the spiced onions into a food processor or blender along with the tomatoes and blitz until smooth. Pour back into the pan with 1½ cans of water, then stir through the sugar and potatoes. Bring to the boil, lower to a simmer, then cover and cook, stirring occasionally, for 20-25 mins or until the potato is just tender. Add the spinach and peas, and cook for 5 mins. Stir in the cream and cashew butter, then return the paneer to the pan and season to taste. Remove from the heat and set aside to cool completely. Heat the oven to 220C/200C fan/gas 8. On a lightly floured surface, roll the pastry out to just bigger than your casserole dish. Cut a thin strip off each side and fix these around the edge of the casserole. Roll the pastry sheet over the top and press the edges with a fork to seal, and tuck in any overhang. Brush with the egg, sprinkle with the nigella seeds and bake for 30-35 mins or until deep golden brown. Leave to rest for 15 mins before serving with pilau rice or green veg.','25 mins','1 hr and 30 mins'),
(1159,'sweet potato-topped cottage pie','https://www.bbcgoodfood.com/recipes/sweet-potato-topped-cottage-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweet-potato-topped-cottage-pie-ee5bd75.jpg','Pre-prepare the mince base and freeze it to make this delicious cottage pie later in the week. It''s a wonderfully comforting and nutritious family meal',19,4,1,2,14,'For the base: heat half of the oil in a pan, add the onion and fry until it starts to soften, then add the garlic, celery and carrot and cook until soft. Meanwhile, heat the remaining oil in a separate frying pan and fry the mince in batches, scooping each batch out with a slotted spoon and leave any excess oil behind. Add the mince to the veg, then stir in the tomato purée and cook for 1 min. Stir in the passata and bring to a simmer. Cover and cook over a very low heat for 1½-2 hrs, then add the milk and cook for 30 mins. If you''re making the base ahead of time, you can leave it to cool at this stage then freeze for up to a month. (Defrost fully before using in the next step.) To finish the recipe, heat more oil in a pan and fry another onion until it starts to soften. Add the carrot and celery and fry until tender. Add the thyme and bay and cook for 1 min, then tip in the mince base and bring to a simmer. Cover and cook for 20 mins (take off the lid towards the end if it’s not thick enough). Heat oven to 180C/160C fan/gas 4. Cook the sweet potatoes in simmering water until tender, drain well, then add the milk and mash the potatoes. Tip the meat mixture into a casserole dish and level it out (fish out the thyme and bay and discard), then pile the potato mixture on top and spread it out. Cook for 30 mins or until the top begins to brown.','25 mins','3 hrs and 35 mins'),
(1160,'sausage & leek mash pie','https://www.bbcgoodfood.com/recipes/sausage-leek-mash-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sausage-leek-mash-pie-07a67d4.jpg','Enjoy sausages and kale with a cheesy mash topping made with leeks and mustard. You can freeze this pie for busy weeknights when you need to feed a crowd',14,4,1,2,14,'Boil the potatoes for 12 mins or until tender. Drain and leave to steam-dry. Heat 1 tbsp oil in a shallow casserole or deep skillet (20-25cm). Pinch the sausages out of their skins into large pieces, directly into the pan, and fry over a medium heat for 7-8 mins or until golden brown. Add the flour and stir everything together, cooking for 1 min. Pour in the gravy and 100-200ml water (depending on how thick you like your gravy), bring to a simmer, then add the kale and cook for 5 mins. Remove from the heat. Meanwhile, in a separate pan, add the remaining 1 tbsp oil, then the leeks and fry for 5-10 mins until tender, adding a small splash of water to help them cook. Heat your grill to its highest setting. Mash the potatoes until smooth. Stir though the leeks, mustard and milk, and season. Spoon and swirl the mash on top of the sausage mixture, then scatter over the grated cheese and grill for 3-5 mins or until golden and bubbling.','5 mins','40 mins'),
(1161,'cheese and potato pie','https://www.bbcgoodfood.com/recipes/melty-cheese-potato-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/melty-cheese-and-potato-pie-3a45759.jpg','Drawing from the apres-ski classic tartiflette, this indulgent pie transforms into a cheesy fondue with creamy sliced potatoes and crisp puff pastry ',7,8,2,2,14,'Heat oven to 220C/200C fan/gas 7. Tip the potatoes into a pan of cold water, bring up to the boil and cook for 2 mins. Drain well, tip back into the saucepan and mix with the crème fraîche. Season, sprinkle over the nutmeg and set aside. Dust your work surface with flour, roll the first block of pastry to a circle about 28cm wide and place on a baking tray. Arrange a third of the potatoes in the middle of the pastry, then sit the cheese on top. Pile the rest of the potato around the cheese, leaving a 1cm border at the edge of the pastry. Roll the remaining pastry out to a circle about 30cm wide, dusting with more flour if needed. Brush the edges of the pastry on the tray with egg, then drape over the larger circle and seal the sides well, crimping as you go round. A tight seal is important to stop the cheese escaping. Brush all over with egg and lightly score a spiral into the pastry with the back of a knife. Bake the pie for 30 mins until golden brown and puffed up. As soon as the pie comes out of the oven, glaze with a little more egg and leave to cool for 5 mins. Meanwhile, whisk the shallot, mustard, vinegar and oil together. Place the chicory in a bowl and toss with the dressing. Serve the pie cut into oozy wedges, with the salad on the side.','40 mins','35 mins'),
(1162,'spanakopita','https://www.bbcgoodfood.com/recipes/crispy-greek-style-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-491503_12-f496108.jpg','A crispy Greek-style spinach and feta pie that you can adapt for your needs. This simple spanakopita recipe uses just five ingredients.',5,4,1,2,14,'Put the spinach into a large pan. Pour over a couple tbsp water, then cook until just wilted. Tip into a sieve, leave to cool a little, then squeeze out any excess water and roughly chop. Roughly chop the tomatoes and put into a bowl along with the spinach, feta and eggs. Mix well. Carefully unroll the filo pastry. Cover with some damp sheets of kitchen paper to stop it drying out. Take a sheet of pastry and brush liberally with some of the sundried tomato oil. Drape oil-side down in a 22cm loosebottomed cake tin so that some of the pastry hangs over the side. Brush oil on another piece of pastry and place in the tin, just a little further round. Keep placing the pastry pieces in the tin until you have roughly three layers, then spoon over the filling. Pull the sides into the middle, scrunch up and make sure the filling is covered. Brush with a little more oil. Heat oven to 180C/fan 160C/gas 4. Cook the pie for 30 mins until the pastry is crisp and golden brown. Remove from the cake tin, slice into wedges and serve with salad.','10 mins','30 mins'),
(1163,'chicken & mushroom puff pie','https://www.bbcgoodfood.com/recipes/chicken-mushroom-puff-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-563494_12-da38a6b.jpg','This is just what you need on a cold night. Serve with creamy mashed potatoes',33,4,1,2,14,'Heat the oil in a large, non-stick frying pan. Season the chicken thighs and fry for 5-8 mins until golden brown, turning occasionally. You may need to do this in two batches, depending on the size of your pan. Lift the chicken onto a plate and tip the bacon pieces into the pan. Fry for 5 mins until crisp. Add the onion, mushrooms and thyme sprigs, then fry on a high heat for another 3 mins until the onions start to colour. Tip the flour into the pan and cook, stirring, for 1 min. With the pan off the heat, gradually stir or whisk in the chicken stock, followed by the milk, then add the chicken back to the pan. Bring to the boil, then simmer for 30 mins. Spoon the filling into a large pie or baking dish (approx 20 x 30cm) with a lip and leave to cool. Heat the oven to 220C/200C fan/gas 7. On a floured surface, roll the pastry to the thickness of two £1 coins. Cut a long strip as wide as the rim of the pie dish and, using a little of the beaten egg, fix to the edge of the pie dish. Brush with beaten egg, then lift the rest of the pastry over the pie, using the rolling pin to help. Gently press the edges with your fingers and trim with a sharp knife. Brush lightly with egg to glaze, then bake for 30 mins or until the pastry is risen and dark golden brown.','45 mins','30 mins'),
(1164,'salmon & leek pastry','https://www.bbcgoodfood.com/recipes/salmon-leek-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-22092_10-e65855e.jpg','Much easier than it looks, once you have mastered this salmon in filo recipe it will be a dinner party favourite',5,2,1,2,14,'Heat oven to 200C/fan 180C/gas 6. Put the leeks into a large saucepan with 3 tbsp water and a couple of knobs of butter. Cover, then cook for 10 mins until tender. Remove from the pan, allow to cool, then mix with the crème fraîche and some seasoning. To assemble the parcels, brush a sheet of pastry with melted butter, then lay 2 more sheets on top, brushing butter between each layer. Place a salmon fillet in the centre, season, then spoon over half the leek mix. Fold the ends over the top, pull up the sides and scrunch together to enclose. Repeat. Brush both the parcels with melted butter. Put onto a lightly greased baking sheet, then cook for 20-25 mins until browned and crisp. Delicious served with a green salad.','15 mins','35 mins'),
(1165,'poached chicken & pancetta pie with spring onion colcannon','https://www.bbcgoodfood.com/recipes/poached-chicken-pancetta-pie-with-spring-onion-colcannon','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/Chicken-and-Panchetta-Pie-cc2423b.jpg','For an alternative Sunday lunch, try cooking this hearty poached chicken and pancetta pie pie topped with a buttery spring onion colcannon mash',14,6,2,2,14,'Sit the chicken in a large, lidded saucepan. Pour over the milk and stock, then nestle in 1 onion half and the bay leaves. Bring to a simmer over a medium heat, then cover and cook gently for 1 hr 15 mins until the chicken is cooked through – when ready, it should be opaque through the middle and the internal temperature should read 70C on a meat thermometer. Remove from the heat and leave the chicken to cool in the poaching liquid for 30mins, or until cool enough to handle. Drain, reserving 600ml of the poaching liquid, then shred the meat off the carcass using two forks. Discard the bones (or see tip, below). Finely chop the remaining onion half. Heat a deep frying pan over a medium heat and fry the pancetta for 5 mins until crisp. Add the chopped onion and fry for 10 mins more until the onion has softened. Transfer the mixture to a plate using a slotted spoon and set aside. Melt the butter in the same pan, then stir in the flour and cook for 2 mins. Pour in the wine and bring to the boil, stirring continuously. Remove from the heat and slowly whisk in the reserved poaching liquid, a little at a time. Return the pan to a medium heat and cook for 5 mins, whisking continuously until thickened. Stir through the shredded chicken, the mustard, double cream and the fried pancetta and onion. Season to taste. Spoon the mixture into a large ovenproof dish. Heat the oven to 200C/180C fan/ gas 6. To make the topping, cook the potatoes in a pan of boiling salted water for 15 mins until easily pierced with a cutlery knife. Drain, then tip back into the pan and leave to steam-dry for 10 mins. Melt the butter in a frying pan over a medium heat and fry the spring onions for 5 mins. Mash the potatoes with a potato masher or fork, then stir through the buttery spring onions, the milk and seasoning. Spoon the mash over the chicken filling, then scatter over the cheese and bake for 30-35 mins until the topping is golden and crisp. The unbaked pie can be kept frozen for up to a month. Defrost thoroughly in the fridge overnight, then bake as above.','30 mins','2 hrs and 15 mins'),
(1166,'turkey twist pie','https://www.bbcgoodfood.com/recipes/turkey-twist-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/turkey-twist-pie-6c8fd3e.jpg','You''re just 4 ingredients away from a crispy filo pastry pie, filled with chargrilled veggies, turkey and creamy ricotta. A great low calorie mid-week meal',86,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. Fry the veg in 1 tbsp olive oil for 8-10 mins. Chop the turkey, mix with the veg and ricotta, and season well. Oil a work surface and overlap 4 filo sheets to create a 40 x 60cm rectangle. Brush with oil and layer the remaining sheets on top. Spoon the filling along the bottom third, fold in the short edges, roll up and twist into a spiral. Brush with a little more oil and bake on a non-stick tray for 45 mins.','53 mins - 55 mins','N/A'),
(1167,'chicken, morel mushroom & asparagus one-pan pie','https://www.bbcgoodfood.com/recipes/chicken-morel-mushroom-asparagus-one-pan-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-morel-and-asparagus-pie-ee6d9fa.jpg','Cook up a spring chicken and asparagus pie using seasonal morel mushrooms. This hearty one-pan dish is a fresh new take on the classic chicken pie',7,5,2,2,14,'If you are using dried mushrooms, heat the stock and soak them for 10 mins, then remove them, strain the liquid and set it aside. If you’re using fresh mushrooms, clean them thoroughly before using. Set a couple of the nicest-looking morels aside to decorate the top of the pie, and halve the rest. Heat half the butter in a skillet and fry the halved morels for 3-4 mins or until wilted. Scoop them onto a plate and set aside. Heat the remaining butter and gently cook the shallots in the pan with the thyme and bay. Once softened, stir in the flour and cook for 1 min or until you have a sandy paste. Pour in the sherry and sizzle, then carefully stir in the strained soaking liquid (or 200ml chicken stock if you’ve used fresh morels), followed by the crème fraîche. Season well and bring the sauce to a gentle simmer. Add the chicken and poach in the sauce for 10 mins or until the chicken is just cooked through. Remove the bay, stir through the asparagus, tarragon and fried morels, then remove from the heat. Heat oven to 220C/200C fan/gas 8. The pastry needs to sit on top of the ingredients, so if your pan is too deep, use a pie dish instead. Roll out the pastry on a floured surface to the thickness of a £1 coin, then cut the pastry to fit the pan or dish, and drape it over the pie mixture using a rolling pin to help you. Liberally brush with egg, season the pastry with flaky sea salt, and pop your reserved morels on the top. Bake for 20 mins or until the pastry has puffed and is a deep golden brown. Leave tor rest for 5 mins before serving straight from the pan.','30 mins','35 mins'),
(1168,'healthy fish pie','https://www.bbcgoodfood.com/recipes/fish-pie-swede-potato-topping','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52756_11-4d4ea61.jpg','Making fish pie can be a palaver - not this one! Our healthy fish pie is simple to make and ready in just over an hour',5,4,1,2,14,'Cook the potatoes and swede in boiling water until tender (about 20 minutes). Preheat the oven to 190C/gas 5/fan 170C.While the potatoes and swede cook, put the soft cheese and stock into a large saucepan and heat gently, stirring with a wooden spoon, until blended and smooth. Now add the blended cornflour and cook until thick. Stir the fish into the sauce with the prawns and parsley. Season with some pepper. Tip the mixture into a 1.5 litre/2¾ pint baking dish. Drain the potatoes and swede, mash them well and season with black pepper. Spoon the mash over the fish to cover it completely. Bake for 25-30 minutes until piping hot, then transfer to a hot grill for a few minutes to brown the top. Serve with frozen peas or sweetcorn.','1 hr and 15 mins','No Time'),
(1169,'moroccan spiced pie','https://www.bbcgoodfood.com/recipes/moroccan-spiced-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-424498_12-e6c03cf.jpg','The perfect showpiece to your Christmas Day meal - and it''s suitable for veggies',37,6,2,2,14,'Preheat the oven to fan 180C/ conventional 200C/gas 6. Dry fry the seeds briefly in a small pan over a medium heat until toasty – don’t let them burn. Grind coarsely using a pestle and mortar (or a bowl and the end of a rolling pin), then mix in the paprika, cinnamon, 1⁄2 tsp salt and 4 tbsp oil. Tip the squash into a roasting tin, pour over the spiced oil and toss. Roast for 20 minutes. Meanwhile, heat 2 tbsp of oil in a frying pan, add the shallots and cook, stirring, until they start to brown. Stir in the ginger and 100g/4oz each almonds and pistachios.When brown, toss in the cranberries, 2 tbsp honey, and the spinach so it wilts. Take off the heat and stir into the squash, when it comes out of the oven. Set aside. In a food processor, whizz the chickpeas with the garlic, cumin, remaining oil, lemon juice, 2 tbsp water and salt and pepper to make houmous. Stir in the coriander. Melt the butter in a small pan. Put a loose-bottomed 28cm quiche tin on a baking sheet and brush with some butter. Keeping the filo covered with a damp cloth so it doesn’t dry out, lay one sheet over half of the tin so that it hangs over the edge by about 10cm. Lay another sheet on the other side, so it overlaps the first in the centre and hangs over the opposite edge. Brush with butter. Lay two more filo sheets in the opposite direction in the same way and brush with more butter. Build up two more layers in this way, so you use a total of eight sheets of filo. Pile half the squash mixture in the centre of the pastry. Spread over the houmous and then the rest of the squash mixture.One at a time, bring the edge of each filo sheet up to the centre to cover the filling, creating voluptuous folds as you go. Brush carefully with more butter. (If making a day ahead, cover now with cling film and chill. To reheat, remove the pie from the fridge, heat the oven, then bake for 35-40 minutes.) Bake for 30-35 minutes, until crisp and golden. Just before the pie is ready, reheat any remaining butter in the pan, tip in the rest of the nuts and fry until golden. Spoon in the 4 remaining tbsps of honey and, when it melts, take off the heat and pour over the pie. Serve with Harissa yogurt sauce (mix the yogurt and milk together to make a thin sauce, stir in the herbs and season. Swirl in harissa to taste) and lemon wedges.','1 hr and 30 mins','35 mins'),
(1170,'puff pastry cinnamon rolls','https://www.bbcgoodfood.com/recipes/puff-pastry-cinnamon-rolls','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/puff-pastry-cinnamon-rolls-d57fcf8.jpg','Use ready-made puff pastry to quickly and easily make these cinnamon rolls – an all-butter pastry will give the best flavour',20,12,1,2,14,'Heat the oven to 200C/180C fan/gas 6 and line large baking tray with baking parchment. Stir the cinnamon and sugar together in a small bowl. Unravel the puff pastry on a lightly floured work surface, then sprinkle the cinnamon sugar mixture all over the top. Gently level the cinnamon sugar mixture with the back of a spoon so it covers the pastry almost completely, leaving a 1cm border on one of the short sides. Brush a little of the beaten egg over the exposed pastry border. Roll the pastry up in a tight log from the short side without the border. Gently press along the egg-washed border to seal, then slice into 12 equal rounds. Arrange the cinnamon rolls on the prepared baking tray, placing them up against each other so they''re just touching. Brush the remaining beaten egg all over the tops and sides of the pastry, then bake for 15-18 mins until golden and risen. Leave to cool on the tray for 10-15 mins. Combine the icing sugar with 1½ tsp water in a small bowl until you have a thick icing that easily runs off the spoon. Drizzle this over the warm rolls, then immediately serve.','10 mins','15 mins'),
(1171,'char siu style sausage puffs','https://www.bbcgoodfood.com/recipes/char-sui-puffs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/char-sui-puffs-89dbb7e.jpg','We''ve combined sausage rolls with the flavours of Chinese char siu pork - doubly delicious!',6,18,1,2,14,'You can make the pastry up to 2 days before. Weigh the flour into a bowl and add 1/2 tsp salt. Holding the butter by the foil (to prevent it from becoming too warm), grate it directly into the flour on the coarse side of the grater. Dip the block of butter into the flour every now and then, and mix the grated butter into the flour with a cutlery knife to prevent it clumping together in the bowl. When the butter is evenly distributed throughout the flour, pour in 100g cold water. Mix with the cutlery knife until it clumps together. Tip onto your work surface and knead briefly to bring the pastry together – don’t overwork or you’ll lose the pockets of butter, which will create the layers. Wrap in cling film and chill for at least 30 mins. Heat oven to 200C/180C fan/gas 6 and line two baking trays with parchment. Squeeze the sausagemeat from the skins into a bowl. Add the five-spice, oyster sauce, hoisin and ginger. Mix until fully combined. Lightly flour your work surface. Roll out the pastry to the thickness of a 50p coin, then use a 10cm biscuit cutter to stamp out as many circles as you can. Fold the scraps back on themselves, but don’t scrunch up the pastry, or you’ll lose the layers. Re-roll and stamp out a few more if you can – you should make 18-20. Scoop the sausage mixture into balls (the size of cherry tomatoes) with a spoon and put in the middle of each pastry circle. Brush a little beaten egg over the pastry, then fold three sides in to partially cover the sausage and create a triangle shape, pinching the corners together. Brush more egg over the top of the pastry and sprinkle with sesame seeds. Arrange on the trays and bake for 25 mins until golden brown. Cool on the trays, ready to pack up for a picnic – or serve warm. Will keep in the fridge for up to 2 days.','25 mins','25 mins'),
(1172,'apple & blueberry danishes','https://www.bbcgoodfood.com/recipes/apple-blueberry-danishes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-592514_12-97b2e2e.jpg','These delicious fruit pastries are a must for your next brunch',50,8,1,2,14,'First make the filling: melt the butter and 3 tbsp of the sugar in a medium saucepan. Stir in the apples and spices and cook for 5 mins, until the fruit is slightly softened but not cooked. Remove from the heat and stir in the blueberries. Roll the pastry out on a lightly floured surface to £1 coin thickness. Cut out 8 x 11cm squares. Prick the squares all over with a fork and transfer to a baking tray. Divide the filling between the pastry squares, spooning it into the centre of each square. Bring the four corners into the centre of each square and pinch together edges using a little of the egg to help stick. Brush all over the danishes with the rest of the egg. They can be covered and chilled for a few hours now. Heat oven to 220C/200C fan/gas 7. Dust with the remaining demerara and cook for 20 mins until the pastry is golden. Don’t worry if they pop open while cooking – they look even prettier.','20 mins','25 mins'),
(1174,'cheese & bacon turnovers','https://www.bbcgoodfood.com/recipes/cheese-bacon-turnovers','https://images.immediate.co.uk/production/volatile/sites/30/2021/05/Cheese_Bacon-Turnovers-8145f86.jpg','Make some moreish cheese and bacon turnovers for lunch, a picnic or buffet, and add a dollop of mustard if you like. They''ll keep for up to three days',12,6,1,2,14,'Heat the oven to 200C/180C fan/gas 6. Unravel the pastry on the sheet of baking parchment it comes with, and cut into six squares. Mix the soft cheese with the mustard, if using, and a good grinding of black pepper. Divide between the middles of the pastry squares, and smooth over in a diagonal line from one corner to the opposite side. Lay a bacon rasher over the soft cheese, then scatter over the cheese. Brush the two exposed corners of pastry with beaten egg, and fold over the top of the filling to join together. Brush the tops with more egg, then slide the baking parchment on to a baking sheet and bake for 15-20 mins until the cheese has melted and the pastry is golden. Transfer to a cooling rack for 5-10 mins before serving warm. Will keep chilled in an airtight container for two-three days.','10 mins','20 mins'),
(1175,'cinnamon custard pie','https://www.bbcgoodfood.com/recipes/cinnamon-custard-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/11/Cinnamon-custard-pie-3722240.jpg','Love custard tarts or pastel de nata? Try GF member Sue Tappin''s recipe for cinnamon-topped custard pie – it''s deliciously decadent',127,12,2,2,14,'Mix together the egg yolks, caster sugar, custard powder and milk in a bowl. Pour the cream into a saucepan set over a medium heat, bring to the boil, then gradually pour into the egg yolk mixture, whisking until smooth. Pour the mixture back into the saucepan and cook over a low-medium heat for a few minutes, whisking all the time to keep it smooth. It should be very thick. Remove from the heat, then stir in the vanilla paste. Set aside to cool. Heat the oven to 200C/180C fan/gas 4. Line a 28 x 18cm baking tray with baking parchment. Unroll the pastry sheet with the long edge closest to you. Cut it in half vertically, to make two rectangles that will fit the base of the tray. Put one sheet in the baking tray, prick all over with a fork, and bake for 15 mins. Remove from the oven and allow to cool for 5 mins. Whisk the custard again briefly, then spread evenly over the cooled pastry. Lay the other pastry sheet on top, tucking in the edges. Brush with half the butter. Mix the granulated sugar with the cinnamon, then sprinkle evenly over the pie. Drizzle over the remaining melted butter and bake for a further 35-45 mins until the custard has set and browned on top. Remove from the oven and allow to cool in the tray, before cutting into bars to serve. Can be kept chilled for up to three days.','15 mins','1 hr and 10 mins'),
(1176,'spiced salmon coulibiac','https://www.bbcgoodfood.com/recipes/spiced-salmon-coulibiac','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-salmon-coulibiac-and-raita-b310ed5.jpg','This make-ahead main of pastry-wrapped fish and rice is an extra-special way to serve seafood for a dinner party, spiced with cardamom, cloves and chilli',128,6,2,2,14,'Heat the oil in a frying pan and add the onion, garlic, cardamom, cinnamon, fennel, star anise, fenugreek, cloves, ginger and 1 tsp garam masala. Cook over a medium heat until the onion is a deep golden brown. Add the rice with 250ml water and the bay leaf, and season with salt. Bring to the boil, then turn down to a gentle simmer. Cover and cook for 15 mins or until the liquid has been absorbed and the rice is light and fluffy. Remove from the heat, tip into a bowl and leave to cool. Once cooled, remove the cardamom, cinnamon stick, star anise, cloves, ginger and bay leaf. Add the coriander, chilli, mango chutney and lemon juice. Check the seasoning, cover and chill until needed. Take the pastry out of the fridge and leave at room temperature for 10 mins. Unroll the pastry sheets, roll out to make a little wider and put each on a non-stick baking sheet. Cover with cling film and place in the fridge to re-chill for 30 mins. Season the salmon on both sides with a little salt and pepper and the rest of the garam masala, then place one piece of salmon on one piece of pastry and cover with half of the rice mixture. Top with the other piece of salmon, then brush around the pastry edges with beaten egg. Cover with the second piece of pastry, pushing down well, then roll the edges over, crimp with a fork to seal and decorate as you like. Can be made ahead and chilled for 1 day. Brush all over with beaten egg and chill for 30 mins. Heat oven to 220C/200C fan/gas 7. Bake in the oven for 10 mins, then turn the oven down to 180C/160C fan/gas 4 and continue to bake for about 30 mins until the pastry is golden brown. Set aside in a warm place to rest for 10 mins. Serve with the rest of the rice mix (heated or cold) and the Raita (see goes well with).','20 mins','50 mins'),
(1177,'ham & tarragon pot pie','https://www.bbcgoodfood.com/recipes/ham-tarragon-pot-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ham-tarragon-pot-pie-9805a90.jpg','Use ready-rolled puff pastry for a fuss-free, midweek masterpiece - fill this pie with pulled ham hock and a creamy, herby sauce',29,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. In a shallow casserole dish, melt the butter over a medium heat on the hob. Add the onion and celery, and fry gently to soften. Add the flour and stir to coat the vegetables. Slowly add the milk, stirring really well after each addition, making sure there are no lumps of flour before you add the next splash of milk. Once all the milk has been added, bring to a simmer to thicken, then fold in the ham hock and the tarragon. Season to taste, then add about half the beaten egg. Cut a circle of puff pastry 2cm wider than the top of the pan and pop it onto seal in the filling underneath, folding the edges to create a crust. Cut the off-cuts of pastry into rough strips and use them to decorate the top. Brush the pastry with the remaining egg and bake in the oven for 25 mins or until the pastry is golden and crisp. Serve with peas.','20 mins','30 mins - 35 mins'),
(1178,'peach puff pastry tart with almonds','https://www.bbcgoodfood.com/recipes/peach-puff-pastry-tart-almonds','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/peach-puff-pastry-tart-with-almonds-3796129.jpg','Top puff-pastry with sliced peaches to make this rustic, simple Spanish-style fruit tart - an easy yet attractive dinner party dessert',88,8,1,2,14,'Heat oven to 200C/180C fan/gas 6. Line a large baking tray with baking parchment. On a lightly floured surface, roll out the pastry to a 3mm thickness. Cut a circle about 33cm in diameter with a sharp knife, using a plate or anything round as a guide. Using the rolling pin, transfer to the baking tray and score a circle 1cm in from the edge. Bake in the oven for 12-15 mins until lightly puffed and golden. Remove from the oven and gently push the centre down with the back of a large spoon so that you have a flat base with a raised border. Meanwhile, halve and stone the peaches, then slice thinly. In a bowl, beat together the butter, sugar, ground almonds, egg, lemon zest, almond extract and sherry, if using. Spread the frangipane evenly over the pastry, leaving the border free. Top with the peach slices, neatly overlapping them in circles. Sprinkle over the remaining sugar and the flaked almonds. Bake for 30 mins, then turn the oven down to 180C/160C fan/gas 4, cover the tart lightly with foil and cook for a further 30-40 mins. The frangipane should puff up between the peaches and be golden brown and set in the centre. Cool on a wire rack, then slice and serve. Leftovers will keep for 1 day.','15 mins','1 hr and 20 mins'),
(1216,'oaty hazelnut cookies','https://www.bbcgoodfood.com/recipes/oaty-hazelnut-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/oaty-hazelnut-cookies-97e36d5.jpg','Soft and slightly chewy, these oaty cookies contain apple and maple syrup instead of sugar. They''re packed with hazelnuts which are a good source of vitamins and minerals',5,9,1,2,3,'Heat oven to 180C/160C fan/gas 4 and lightly grease a non-stick baking tray (or line a normal baking tray with baking parchment). Tip the butter and syrup into a small non-stick pan and melt together, then add the apple and cook, stirring, over a medium heat until it softens, about 6-7 mins. Stir in the cinnamon and raisins. Mix the oats, spelt flour, and hazelnuts in a bowl, pour in the apple mixture, then add the egg and beat everything together really well. Spoon onto the baking tray, well spaced apart to make 9 mounds, then gently press into discs. Bake for 18-20 mins until golden, then cool on a wire rack. Will keep for 3 days in an airtight container or 6 weeks in the freezer.','15 mins','30 mins'),
(1179,'salmon & spinach tart','https://www.bbcgoodfood.com/recipes/salmon-spinach-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-spinach-tart-4f6a55f-scaled.jpg','A light lunch or dinner party starter of puff pastry topped with mustard crème fraîche, flaked salmon fillets, and baby spinach',1,4,1,2,14,'Heat oven to 200C/180C fan/gas 6 and line a large baking sheet with baking parchment. Unroll the pastry sheet and score a border about 1cm from the edge. Prick the middle area with a fork all over and glaze the border with the egg yolk. Bake the pastry for 15 mins until puffed up and almost cooked. Meanwhile, put the salmon fillets in a microwaveable dish and cover with cling film. Cook in the microwave on High for 2 mins until just cooked. Set aside to cool for 1-2 mins, then flake into chunky pieces. Mix the crème fraîche with the mustard in a small bowl and season. Take the pastry out of the oven and flatten the middle area with the back of a spoon. Cover the base with the crème fraîche sauce, then scatter the spinach and salmon flakes over. Bake in the oven for a further 5 mins or until the pastry is cooked and golden. Serve with dressed mixed leaves.','15 mins','20 mins'),
(1180,'free-form asparagus & potato tart','https://www.bbcgoodfood.com/recipes/free-form-asparagus-potato-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/free-from-asparagus-potato-tart_0-8e7801e.jpg','Top cheesy rough-puff pastry with soured cream, grated gruyère and seasonal vegetables for a rustic vegetarian lunch or light dinner',17,8,2,2,14,'The morning before you make the pie, put the butter in the freezer – first wrap it in foil, which will help to keep it cold when you grate it later. To make the pastry, put the flour in a big bowl and mix in a good pinch of salt and the cheese. Using a coarse cheese grater, grate in the butter – try to hold it as little as possible so it doesn’t warm up too much – and stir it in with a knife as you go. When all the butter is in, give it a final stir to make sure all the strands of butter are coated with flour, then quickly stir in 125ml of very cold water. Splash in a little more if it looks dry, then lightly bring the pastry together with your hands. Wrap in cling film and chill for at least 30 mins. Meanwhile, bring a big pan of water to the boil, then tip in the potatoes. Boil until just tender, then drain well, slice and allow to cool. Heat oven to 200C/180C fan/gas 6 with a baking tray inside. On a large sheet of baking parchment dusted with a little flour, roll out the pastry into a rough circle about 30cm wide, until the thickness of 2 x £1 coins. Brush the mustard over the pastry. Arrange the potatoes, asparagus and most of the gruyère in layers in the centre of the base, leaving a border of pastry that’s roughly 5cm all the way around. Season and fold up the pastry edges to enclose the vegetables and cheese, pinching the edges to seal. Whisk together the soured cream and the egg with a little seasoning, then pour into the centre of the tart. Sprinkle over the remaining cheese and slide the tart onto the preheated baking tray. Bake for 30 mins until the pastry is golden and crisp. Eat just warm or at room temperature.','40 mins','30 mins'),
(1181,'almond & raspberry cruffins','https://www.bbcgoodfood.com/recipes/almond-raspberry-cruffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/almond-raspberry-cruffins-84f615d.jpg','Cross a croissant with a muffin and you''ll get these buttery, flaky, fruity pastries - perfect for brunch or an indulgent treat',33,6,2,2,14,'For the pastry, measure 75ml just-warm water, add the yeast and stir it to dissolve. Measure another 75ml ice-cold water and add the lemon juice to it. Mix the flour and sugar with 1/2 tsp fine salt in a large bowl, then toss in the cubes of butter until coated in the flour. Splash the yeast water and lemon water over the contents of the bowl. Using a round-bladed knife, work quickly to bring the mix to a rough dough with lumps of butter held inside it. Turn onto a floured work surface, shape into a squat rectangle without kneading it too much, then wrap in cling film and chill for 15 mins in the freezer. Dust the work surface and pastry with flour. Roll the pastry in one direction until it’s 3 times long as it is wide, or about 45 x 15cm. Try to keep the sides straight as you roll, and the top and bottom edges as square as possible. Fold the pastry over itself. Fold the bottom third up, then the top third down, to make a neat block. Turn the block so that its open edge is facing to the right, like a book. Press the edges together gently with the rolling pin. Roll out and fold the pastry like this 3 more times to make a smooth dough, with the odd streak of butter here and there. If it feels greasy at any point, or becomes springy, cover and chill for 10 mins before continuing. Wrap the finished dough and chill for at least 1 hr. To make the filling, beat together all the ingredients and season with a pinch of salt. Set aside until needed. To shape, put the dough on a floured work surface. Cut it in half across the middle and return one half to the fridge. Roll the other to a 30 x 20cm rectangle, then cut this into 3 strips, each 10cm wide. To create even more layers, fold each piece in half lengthways. Put a generous teaspoon of the almond mix at one end of the pastry, then roll the pastry around it in a spiral. Place, cut-edge up, into a non-stick muffin tin. Repeat to make 6 cruffins. Loosely cover with cling film. Can be chilled overnight. (You may have some filling left over, which can be frozen.) Prove the dough at a cool room temperature for 2 hrs or until the pastry has filled the wells of the tin. Heat oven to 190C/170C fan/gas 5. Bake the cruffins for 20-25 mins until risen and deep golden brown. Remove and cool a little on a rack. To fill with jam, spoon the jam into a piping bag fitted with a 5mm nozzle, or use a chef’s squeezy bottle. Push the nozzle into the middle of the cruffin and squeeze. Dust the cruffins with icing sugar and eat cool or warm on the day of baking.','40 mins','20 mins - 25 mins'),
(1182,'spiced beetroot & feta tarts with tahini-dressed leaves','https://www.bbcgoodfood.com/recipes/spiced-beetroot-feta-tarts-tahini-dressed-leaves','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-beetroot-feta-tarts-with-tahini-dressed-leaves-1f96bda.jpg','Turn root vegetables and puff pastry into a stunning vegetarian starter or main with coriander, cumin and harissa',93,4,2,2,14,'Heat oven to 200C/180C fan/gas 6. Rub the beetroots and garlic cloves with 2 tsp olive oil. Wrap in a double layer of foil, adding 5 tbsp water into the package. Place in an ovenproof dish and bake for 2 hrs or until tender. Test them by inserting a small sharp knife. When the beetroot is cool enough to handle, gently rub off the skins. Set aside, saving the garlic cloves. Meanwhile, heat a small frying pan. Toast the coriander seeds for a few mins until aromatic, then grind using a pestle and mortar. Toast the cumin seeds, leave them whole and mix in with the coriander. Heat 3 tbsp olive oil in a frying pan, add the onions, toasted spices and some seasoning. Cook very gently for 30 mins or until the onions are soft and golden. Unroll the pastry and cut out 4 large circles using a 12cm cookie cutter (or use a small sharp knife to score round a bowl or plate). Place the pastry circles on a baking- parchment-lined baking sheet. Gently score a 1cm border around each and prick the centre area with a fork. Bake in the oven for 10-15 mins. Meanwhile, mix the harissa, vinegar and remaining 1 tbsp olive oil in a small bowl. Thinly slice the beetroot. Remove the pastry bases from the oven and carefully press down the middle areas with the back of a spoon to flatten. Squeeze the garlic cloves out of their skins and stir into the onions. Spread this mixture evenly into the tarts, then layer on the beetroot slices in a circular pattern, slightly overlapping them. Brush the harissa mix onto the beetroot, then brush the egg on the border and sprinkle with the nigella seeds. Bake in the oven for a further 12-15 mins until the pastry is cooked and golden. To make the salad, whisk together the extra virgin olive oil, tahini, lemon juice, honey, seasoning and 1 tbsp water. Toss the chicory and watercress in the dressing. Remove the tart from the oven, scatter over the feta and serve alongside the leaves.','35 mins','2 hrs and 30 mins'),
(1183,'braised ox cheek wellingtons with peppercorn gravy','https://www.bbcgoodfood.com/recipes/braised-ox-cheek-wellingtons-peppercorn-gravy','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/braised-ox-cheek-wellingtons-with-peppercorn-gravy-668176c.jpg','Crisp puff pastry covers tender meat in these individual wellingtons – the ultimate main course for a romantic Valentine''s Day meal for two',6,2,2,2,14,'Heat the oven to 160C/140C fan/gas 3. Heat the oil in an ovenproof pan (one with a tight-fitting lid) or flameproof casserole dish over a high heat. Cut the ox cheek into four large chunks, then season well and toss in the flour and mustard powder. Place the pieces of meat in the hot oil – they should sizzle – and brown the meat all over. Add the onion to the pan and continue cooking until softened and starting to caramelise. Add the thyme, stock cube, porcini and some seasoning. Pour 450ml water into the pan, stirring to scrape any meaty bits off the bottom. Cover with a lid and place in the oven to cook for 3 hrs, turning the meat over in the liquid once while cooking, and topping up with a splash of water if the pan looks dry. While the meat cooks, melt the butter in a pan. Add the garlic, stir for 1 min but don’t brown, then add the mushrooms and cook for 10-12 mins until the pan is dry and the mushrooms are turning golden. Season and set aside. When their cooking time is up, scoop the ox cheeks out of the liquid and use 2 forks to shred the meat – discard any bits of fat or sinew. Strain the cooking liquid. Add 3-4 tbsp of the liquid back to the meat, along with the cooked mushrooms. Cool, then chill the meat mixture and the cooking liquid for 3 hrs, or up to 48 hrs. Roll out the pastry on a floured surface to the thickness of a £1 coin. Cut out 2 x 12cm circles and 2 x 15cm circles – use two plates to cut around if you can. Arrange the prosciutto in pairs on top of the smaller circles of pastry, in a cross shape. Split the meat mixture in half and mount each portion on top of the prosciutto. Fold the prosciutto over the meat to encase it, then flip the little parcel of meat over, so the smooth side is facing up on top of the pastry. Brush the edge of each pastry circle with a little beaten egg, then place the larger circles of pastry on top. Use your hands to create a domed centre, then crimp the edges with a fork (or your fingers) to seal in the meat. Score the pastry in a criss-cross pattern, then brush all over with beaten egg. Use any pastry scraps to cut out a heart for each pie, stick this on top and brush with egg too. Poke a steam hole on either side of each heart. Place each wellington on a square of baking parchment and chill for at least 30 mins, or up to 24 hrs. Heat oven to 200C/180C fan/gas 6 and put a baking tray in the oven to heat up. Slide the wellingtons on their parchment onto the hot tray and bake for 30 mins. Meanwhile, reheat the cooking liquid, add the cream and peppercorns, and keep warm until ready to eat. Serve each wellington with green veg and the peppercorn gravy for pouring over.','30 mins','3 hrs and 45 mins'),
(1184,'squash & chorizo pot pies','https://www.bbcgoodfood.com/recipes/squash-chorizo-pot-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/squash-chorizo-pot-pies-d9d9c6b.jpg','These individual pies are quick to prepare, packed with spicy sausage and butternut squash, then topped with a ready-rolled puff pastry lid',6,4,1,2,14,'Heat the oil in a large pan, add the onions and cook for a few mins until soft, then add the chorizo. Stir around the pan for a few more mins until the chorizo leaks some of its oils, then add the squash and 100ml water. Cover with a lid and leave to cook for 10-15 mins until the squash is just cooked through. Add the chickpeas with any liquid from the can, the spinach and some seasoning. Stir, then cover with a lid and simmer for 1-2 mins until the spinach has wilted. Stir in the crème fraîche and bubble for 3-4 mins, adding a splash of water if the sauce looks too thick. Heat oven to 200C/180C fan/gas 6. Divide the filling between 4 pie dishes. Unroll the pastry and cut out 4 lids, large enough to cover the pies. Brush the edge of each dish with a little beaten egg, then put a pastry lid on top and brush this with egg too. Poke a hole in the top of each pie. Bake on a tray for 25 mins until the lids are puffed and golden.','15 mins','50 mins'),
(1185,'carrot tarte tatin','https://www.bbcgoodfood.com/recipes/carrot-tarte-tatin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-tarte-tatin-1baa152.jpg','Top puff pastry with onion and cheese, then finish with caramelised slices of carrot for a stylish vegetarian starter or main',42,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. Peel the carrots and slice on the diagonal to the thickness of a £1 coin. Heat the oil in a 23cm ovenproof frying pan and cook the onion gently until softened and lightly golden. Transfer to a bowl, toss with the semi-dried tomatoes and set aside. Heat the butter and sugar in the frying pan, add the carrots and 100ml water and cook very gently for 15 mins until the water has reduced and the carrots are golden and tender. Arrange the carrots in a single layer on the bottom of the frying pan. Spoon the onion and tomatoes on top and cover with a layer of cheese. Roll out the pastry about 1.5cm bigger than the pan, and trim. Lay the pastry over the top of the pan, tucking in around the carrots as you go. Bake in the oven for 30 mins until the pastry is golden and cooked through. Remove from the oven and leave to stand for a few mins. Invert the tart onto a serving plate and lift off the pan using a spatula. Serve warm with a crunchy seasonal salad.','20 mins','50 mins'),
(1186,'tomato galette','https://www.bbcgoodfood.com/recipes/tomato-galette','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Tomato-galette-9d79139.jpg','Please a crowd with this gorgeous galette. Bursting with colour and sweet roasted tomatoes, it transports well and can be eaten hot or cold',2,8,1,2,14,'Tip the tomatoes into a colander set over a large bowl, sprinkle over 1 tsp salt and set aside for 1 hr to draw out the moisture. Meanwhile, make the pastry. Tip the flour, butter and a good pinch of salt into a food processor and blitz to a fine breadcrumb-like consistency. Beat the eggs in a jug, then tip into the processor and pulse to a soft dough – if it seems dry, add ½ tsp cold water at a time until it comes together. Shape into a disc, then wrap and chill for 20 mins. Heat the oven to 180C/160C fan/gas 4. Tip the tomatoes into a large bowl with the cheese, garlic, herbs and a good grinding of black pepper. Gently combine using your hands and set aside. Roll the chilled pastry out on a lightly floured surface to a roughly 30cm circle. Pile the tomato mixture into the middle, leaving a roughly 5cm border around the edge. Brush the exposed pastry border with some beaten egg, then fold the border up so it slightly overlaps the tomato filling. It doesn’t need to be even or neat. Brush the pastry with more beaten egg, then bake for 40-50 mins until the pastry is golden and the tomatoes have softened and are tender.','20 mins','50 mins'),
(1187,'cheese & piccalilli tart','https://www.bbcgoodfood.com/recipes/cheese-piccalilli-tart','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Cheese-and-piccalilli-tart-5d43de0.jpg','Imagine crisp pastry, a delicious layer of piccalilli and a soft cheesy filling – this tart has all those things. It''s perfect for lunch, dinner or picnics',61,4,2,2,14,'First, make the pastry. Put the flour in the bowl of a food processor. Add the butter, yeast extract and a good pinch of salt. Whizz until you have a crumbly mixture that starts to clump. Add 1 tbsp water at a time, whizzing the mixture as you do (we used 2 tbsp water in total). As soon as the dough comes together, stop the processor and don’t add more water. Tip the dough out onto a work surface and lightly roll out to the thickness of a £1 coin. Wrap and chill for 30 mins. Remove the dough from the fridge, then roll out on a lightly floured work surface until large enough to line a 20cm fluted (4cm high), loose-bottomed tart tin, with some overhang. Carefully use the rolling pin to lift the dough up and lay it into the base of the tin. Use your hands to encourage the pastry into the base and up the side of the tin, gently pushing it into the fluted edges. Leave any excess overhanging the rim. Put in the freezer for 15 mins and heat the oven to 180C/160C fan/gas 4 with a baking tray on the middle shelf. Remove the pastry case from the freezer and prick the base all over with a fork. Line the inside of the case with a sheet of scrunched-up baking parchment, pressing it into the base and up the side. Pour in baking beans or dried rice or lentils to weigh the parchment down. Bake on the hot tray in the oven for 25 mins, then remove the parchment and baking beans and trim off the excess pastry using a small serrated knife. Return to the tray in the oven and bake for another 10 mins. Meanwhile, mix the eggs and the milk together in a jug with some seasoning until combined. Remove the tart case from the oven and transfer to a wire rack. Spread the piccalilli over the base, then sprinkle over the cheese and carefully pour over the egg mixture. Sprinkle with the onion seeds. Return the tart to the baking tray in the oven for 30 mins until the filling is set with a slight wobble in the centre. Leave to cool completely in the tin. Chill until cold, then cut into wedges to serve. Will keep chilled for up to three days.','30 mins','1 hr and 5 mins'),
(1188,'smoked salmon, leek & dill tart','https://www.bbcgoodfood.com/recipes/smoked-salmon-leek-dill-tart','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Smoked-salmon-leek-and-dill-tart-85a6ca1.jpg','Use ready-to-roll puff pastry in place of shortcrust as the base for this quiche and top with smoked salmon trimmings and leeks',118,4,1,2,14,'Heat the olive oil or butter in a frying pan over a medium heat, then cook the leeks with a pinch of sea salt for 10 mins, stirring frequently until softened. Transfer to a plate and leave to cool for 3-4 mins. Meanwhile, whisk the crème fraîche, eggs, a good grinding of black pepper, lemon zest and a pinch of sea salt with half of the dill. Heat the oven to 200C/180C fan/gas 6. Leaving the puff pastry on its baking parchment, unroll it into a small rectangular roasting tin (we used a 24 x 20cm tin), so that the pastry comes up the sides. Trim the shorter overhanging ends of the pastry, if you like – they’re great for making cheese straws. Spread the cooled leeks all over the pastry base. Arrange the smoked salmon on top, then pour over the egg and crème fraîche mixture. Season with black pepper and bake for 25-30 mins until there’s no wobble and the pastry is golden. Leave to cool for 10 mins before serving with the remaining dill scattered over. Will keep chilled for up to two days. Reheat until piping hot or serve cold.','5 mins','45 mins'),
(1189,'courgette & caramelised red onion puff pastry tart','https://www.bbcgoodfood.com/recipes/courgette-caramelised-red-onion-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/courgette-and-caramelised-red-onion-tart-ed7043f.jpg','Prep this vibrant veggie puff pastry tart easily using courgettes and sweet caramelised red onion – it''s a great way to feed the family on a budget',16,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. On a lightly floured surface, roll the pastry out to slightly smaller than an A4 rectangle. Slide onto a baking tray, brush with the beaten egg and cook for 20 mins or until golden. Meanwhile, melt the butter in a frying pan over a medium heat. Once foaming, add the onions with a pinch of salt. Cook, stirring regularly, for 10 mins until soft. Pour in the balsamic and cook for a further 6-8 mins until sticky and caramelised. Spoon over the pastry, then top with the courgette ribbons and blobs of goat’s cheese. Return to the oven for 15 mins until the cheese is bubbling. Serve with salad.','20 mins','35 mins'),
(1190,'leek, manchego & chorizo tart','https://www.bbcgoodfood.com/recipes/leek-manchego-chorizo-tart','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Leek-manchego-and-chorizo-tart-cca9791.jpg','Make the most of leeks and combine them with punchy chorizo, sweet smoked paprika and manchego in this savoury tart, made with a homemade pastry case',14,6,2,2,14,'Heat the oven to 180C/160C fan/gas 4. Tip the flour, ¼ tsp salt and the butter into a food processor and pulse to fine breadcrumbs. Add the egg yolks and 2 tbsp ice-cold water and pulse again until the mixture clumps, adding up to 2 tbsp more water if needed. Tip onto a lightly floured surface and bring together into a disc with your hands, then wrap tightly and chill for 30 mins. Roll the chilled pastry out on a lightly floured surface into a 30cm round. Use it to line a 22cm fluted tart tin, leaving any excess overhanging the rim, then chill for 30 mins more. Line the pastry case with baking parchment and baking beans and bake for 15 mins. Remove the beans and parchment and bake for 10 mins more. Leave to cool, then trim away any excess pastry using a serrated knife. Heat a large frying pan over a medium heat and fry the chorizo for 5 mins until beginning to crisp up. Remove to a bowl with a slotted spoon and set aside. Fry the leeks in the residual chorizo oil with a pinch of salt for 10-15 mins until softened, adding a pinch of paprika in the last minute. Remove from the heat and leave to cool for 15 mins. Whisk the eggs, cream, most of the cheese and chives and some seasoning together. Spread the leek mixture into the base of the tart case, then gently pour over the egg mixture. Scatter with the remaining cheese, chives and paprika. Bake in the centre of the oven for 15-20 mins, then remove and leave to cool for 15 mins before serving with a green salad.','30 mins','55 mins'),
(1191,'roasted tomato, basil & parmesan quiche','https://www.bbcgoodfood.com/recipes/roasted-tomato-basil-parmesan-quiche','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327537_12-3817f6c.jpg','A taste of summer, this quiche is full of Italian flavours and is perfect for dinner in the garden',7,8,1,2,14,'To make the pastry, tip the flour and butter into a bowl, then rub together with your fingertips until completely mixed and crumbly. Add 8 tbsp cold water, then bring everything together with your hands until just combined. Roll into a ball and use straight away or chill for up to 2 days. The pastry can also be frozen for up to a month. Roll out the pastry on a lightly floured surface to a round about 5cm larger than a 25cm tin. Use your rolling pin to lift it up, then drape over the tart case so there is an overhang of pastry on the sides. Using a small ball of pastry scraps, push the pastry into the corners of the tin. Chill in the fridge or freezer for 20 mins. Heat oven to 200C/fan 180C/gas 6. In a small roasting tin, drizzle the tomatoes with olive oil and season with salt and pepper. Put the tomatoes in a low shelf of the oven. Lightly prick the base of the tart with a fork, line the tart case with a large circle of greaseproof paper or foil, then fill with baking beans. Blind-bake the tart for 20 mins, remove the paper and beans, then continue to cook for 5-10 mins until biscuit brown. When you remove the tart case from the oven, take out the tomatoes, too. While the tart is cooking, beat the eggs in a large bowl. Gradually add the cream, then stir in the basil and season. When the case is ready, sprinkle half the cheese over the base, scatter over the tomatoes, pour over the cream mix, then finally scatter over the rest of the cheese. Bake for 20-25 mins until set and golden brown. Leave to cool in the case, trim the edges of the pastry, then remove from the tin. Scatter over the remaining basil and serve in slices.','40 mins','40 mins'),
(1192,'caramelised mushroom tartlets','https://www.bbcgoodfood.com/recipes/caramelised-mushroom-tartlets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tarts-9f35c0f.jpg','Try these super-clever caramelised mushroom tartlets on those occasions when you''re entertaining and need an exciting canapé, but could do without the faff',77,12,1,2,14,'Heat the oil in a generous frying pan, add the onion and fry over moderate heat for about 7 mins until soft and golden. Stir in the sugar and seasoning, turn up the heat and add the mushrooms. Sizzle for 5 mins until you have driven off any moisture and the mushrooms are golden. Stir in the garlic for a few further mins, until fragrant, then turn off the heat and stir in most of the thyme (save some for sprinkling). The mushroom mix can be chilled at this point. To make the tartlet bases, cut 7-8cm circles out of the bread using a cookie cutter or glass. Butter one side and stick buttered-side down into a 12-hole tartlet tin. Freeze any leftovers to make breadcrumbs. When ready to bake, heat oven to 220C/200 fan/gas 7. Divide the mushroom mixture between the tartlets and top with a sprinkle of cheese. Don’t be too tidy about this – any cheese on the tin will form a lacy edge to the tartlets. Bake for 10-15 mins until golden and bubbling. Sprinkle over the reserved herbs and serve.','10 mins','20 mins'),
(1193,'double cheese & spring vegetable tart','https://www.bbcgoodfood.com/recipes/double-cheese-spring-vegetable-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/double-cheese-spring-vegetable-tart-fe6bec7.jpg','This savoury make-ahead tart has a delicious crumbly base – no one will realise it''s shop-bought pastry! Fill with goat''s cheese and beautiful spring veg including asparagus and fresh peas ',7,8,1,2,14,'Roll the pastry out into a rectangle on a work surface lightly dusted with flour. Scatter over the cheese, fold the pastry in half and roll out again into a circle that fits a 25cm tart tin with an overhang. Chill for 20 mins. Meanwhile, cook the asparagus in boiling water for 3 mins, then drain and refresh under cold water. Cook the fresh peas the same way for a minute, or simply defrost the frozen peas. Heat oven to 200C/180C fan/gas 6. Prick the base of the tart well with a fork, line with baking parchment and fill with baking beans. Bake the tart for 30 mins, remove the parchment and beans, prick again if it has puffed up, then bake for another 10-15 mins until biscuit brown. Meanwhile, beat the eggs in a bowl, add the crème fraîche and cream, season and add a pinch of freshly grated nutmeg. Scatter the peas and most of the watercress over the tart and crumble over half the goat’s cheese. Pour over the creamy egg mixture, then lay the asparagus spears on top. Finally, slice the remaining goat’s cheese and arrange on top, then bake for 25-30 mins until the custard is just set and the cheese is golden brown. Leave to cool in the tin, trim the edges of the pastry, then remove from the tin, scatter with the remaining watercress and serve cut into slices. Can be made up to a day ahead, leave out the fridge to keep the pastry crisp. ','30 mins','1 hr'),
(1194,'beetroot & shallot tatins','https://www.bbcgoodfood.com/recipes/beetroot-shallot-tatins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beetroot-tart-e454736.jpg','Bursting with colour and flavour, these beetroot & shallot tatins made with puff pastry will make a delicious lunch or light dinner',4,4,2,2,14,'Heat oven to 220C/200C fan/gas 7 and grease 2 x 15cm sandwich tins with solid bases. In a non-stick pan, gently cook the shallots in the oil until golden and softened. If they start to colour before softening, add a dash of water and cover the pan. Tip out the shallots and wipe out the pan. Put the butter in the pan with the nuts, garlic, rosemary, maple syrup and vinegar. Season and allow to bubble until syrupy. Tip in the shallots and cook briefly, stirring to coat them. Spoon half the shallots and syrup into each tin, then add the beetroots, alternating them with the shallots as you go. Cut the pastry in half, then roll out each piece on a lightly floured surface until just larger than the tins. Place the pastry over the vegetables and tuck it down the insides of the tin. Trim off any excess with scissors and pierce the middle of the pastry with a knife to make a hole for steam to escape. Bake for 15-20 mins until golden. Cool for a few mins in the tin, then turn out onto plates and top with the rocket.','10 mins','30 mins'),
(1195,'roasted ratatouille & goat’s cheese tart','https://www.bbcgoodfood.com/recipes/roasted-ratatouille-goats-cheese-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tart_6-fc77481.jpg','Packed with oven-roasted aubergines, courgettes, peppers and onions, this tart makes a great centrepiece for a picnic or al fresco lunch',5,6,2,2,14,'Heat oven to 200C/180C fan/gas 6. Toss the aubergine, courgette, pepper and onion together with the garlic, olive oil and some seasoning. Tip onto a large baking tray (you may need to use 2 trays) and roast for 20 mins. Toss through the tomatoes and roast for a further 15-20 mins or until the vegetables are tender and beginning to caramelise. Meanwhile, roll out the pastry on a lightly floured surface and use to line a 23cm loose-bottomed tart tin. Chill for 30 mins. While the vegetables cool, reduce oven to 190C/170C fan/gas 5. Prick the base of the tart case with a fork, line with baking parchment and fill with baking beans. Pop on a baking tray and bake for 15 mins, then remove the beans and parchment. Bake for 10-15 mins more until the pastry is lightly golden. Reduce oven to 160C/140C fan/gas 3. Squeeze the garlic from their skins into a jug and mash with a little salt to form a paste. Slowly whisk in the cream, followed by the eggs and some seasoning. Toss the basil through the cooled vegetables, then tip two-thirds into the pastry case, spreading out to an even layer. Crumble over three-quarters of the goat’s cheese, then pour over the cream mix. Top with the remaining veg and cheese. Bake for 40-50 mins until the tart is just set, with a slight wobble in the centre. Serve warm for lunch or cold on a picnic, scattered with extra basil leaves, and some toasted pine nuts, if you like.','15 mins','2 hrs'),
(1196,'smoked trout tartlets','https://www.bbcgoodfood.com/recipes/smoked-trout-tartlets','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Smoked-trout-tartlets-57d11e2.jpg','Rustle up these smoked trout tartlets with lemon and horseradish flavours for afternoon tea. Made from ready-rolled pastry, they''re quick and easy to make',2,12,1,2,14,'Heat the oven to 200C/180C fan/gas 6. Unroll the pastry and, keeping it on its paper, cut out 12 discs of pastry using a 7cm round cutter. Use the pastry discs to line a 12-hole shallow tart tin. Put in the fridge to chill while you make the filling. Combine all the remaining ingredients except the smoked trout in a large jug and season well with a little pinch of salt (the trout is already quite salty) and a good grinding of black pepper. Mix well to combine, ensuring there are no lumps of crème fraîche or egg. Remove the tin from the fridge and divide the trout between the pastry cases. Carefully pour over the crème fraîche mixture, leaving a 1-2mm rim of pastry at the top. It helps to give the mixture a stir after each pour so the chives and lemon zest don’t sink to the bottom. Bake in the oven for 18-20 mins until the pastry is golden and the filling lightly golden and puffed up – they will sink a little after a few minutes. Serve warm.','15 mins','20 mins'),
(1197,'slow-roast tomato tatin','https://www.bbcgoodfood.com/recipes/slow-roast-tomato-tatin','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-tatin-c1048c7.jpg','Make a savoury tarte tatin with sweet and sour-slow cooked tomatoes on a crisp puff pastry base',17,6,1,2,14,'Heat oven to 220C/200C fan/gas 7. Melt the butter with a splash of olive oil in your widest frying pan. Add the tomatoes, skin-side down, in a single layer (you can do this in 2 batches if they won’t all fit) and cook over a low heat until they release their juices. Lift out with a slotted spoon and rearrange in a tart tin (roughly 23cm), skin-side down – cram them in as they will shrink a little and you don’t want any gaps. Add the sugar and vinegar to the pan, and cook until the pan juices are reduced and syrupy. Drizzle over the tomatoes in the tin, then scatter with the oregano or thyme and season. Roll the pastry out on a lightly floured surface to a good 25-26cm round. Lay on top of the tomatoes, and tuck the edges down. Use a fork to prick holes all over the pastry – this will help the steam to escape. Sit the tart tin on a flat baking tray and bake for 30 mins or until the pastry is golden brown and crisp. Let the tart sit for 10 mins, then run a knife round the edge to release the pastry. Carefully flip the tart over onto a serving plate or board and scatter with more herbs to serve.','20 mins','50 mins'),
(1281,'edible name place biscuits','https://www.bbcgoodfood.com/recipes/edible-name-place-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-849607_11-aaaf1ea.jpg','Finish off a beautifully laid table with these simple, mint-iced cookies - they''re fun to make with the kids!',5,4,1,2,23,'Mix icing sugar with peppermint extract and enough water to make a thick icing. Spread the icing over cookies and create a border around the edges using sweets and sprinkles. Leave for 10 mins for the icing to set. Then, using an icing pen, write a guest’s name in the centre of each cookie. When the icing is dry, wrap in cellophane and tie up with a pretty ribbon.','30 mins','No Time'),
(1198,'herby asparagus & bacon tart','https://www.bbcgoodfood.com/recipes/herby-asparagus-bacon-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-967566_11-f7a4bc2.jpg','An open puff pastry pie of seasonal greens, mint, chives and parsley with a creamy mascarpone base',17,4,2,2,14,'Heat oven to 200C/180C fan/gas 6. Roll out the pastry to about 15 x 30cm, the thickness of a £1 coin, and trim the edges. Transfer to a lightly floured baking tray. Score a 1cm border, making sure you don’t go all the way through the base. Prick all over the base with a fork, brush the edges of the tart with beaten egg and bake for 15 mins. Meanwhile, heat the oil in a small pan and fry the lardons until golden and crisp. Mix the ricotta, mascarpone, lemon zest, herbs and half the Parmesan in a bowl with salt and lots of black pepper. Remove tray from the oven, squash down centre of the tart and spread with cheese mixture. Scatter over the bacon, reserving the oil. Place asparagus along length of the tart, alternating direction of the tips. Brush with a little bacon oil, scatter over the remaining Parmesan and bake for 20 mins more. Serve warm.','25 mins','35 mins'),
(1199,'creamy chard, squash & parmesan tart','https://www.bbcgoodfood.com/recipes/creamy-chard-squash-parmesan-tart','https://images.immediate.co.uk/production/volatile/sites/30/2021/10/Creamy-chard-squash-and-parmesan-tart-914e1e8.jpg','Make the most of British winter veg with this creamy chard, squash and parmesan tart, made with homemade pastry and topped with hazelnuts',14,6,2,2,14,'An hour before making the pastry, wrap the butter and chill in the freezer until firm. Heat the oven to 200C/180C fan/gas 6. Tip the flour into a large bowl with the salt, stir, then unwrap the end of the chilled butter and coarsely grate into the seasoned flour. Stir everything together using a cutlery knife, then swiftly add 8-10 tbsp ice-cold water, a little at a time, stirring until it starts to clump together. Form into a disc with your hands, adding a little more water if the pastry feels dry. Wrap and chill for 1 hr. At this stage, the pastry will keep frozen for two months. Toss the squash with 1 tbsp oil, most of the thyme leaves and seasoning. Arrange on a baking sheet and roast for 15 mins. Heat the rest of the oil and the butter in a frying pan over a medium heat and cook the shallots with a pinch of salt for 15 mins until caramelised. Add the sugar and vinegar, and cook for 3 mins more. Season, then leave to cool slightly. Steam the chard leaves in a steamer basket for 3 mins. Tip into a tea towel and squeeze out the excess water. Roughly chop, then mix with two-thirds of the hard cheese and the mascarpone. Season. Put a baking sheet in the oven to heat up. Roll the pastry out between two sheets of baking parchment to a 40 x 30cm rectangle. Transfer to a baking tray on its parchment, then peel off the top layer. Spoon over the shallots, leaving a 3cm border. Top with the chard mixture, the rest of the cheese and the squash. Fold the pastry border slightly over the edge of the filling, then brush with the egg wash. Chill for 1 hr. Slide the tray onto the hot baking sheet and bake for 35-40 mins. Scatter over the remaining thyme and the hazelnuts. Serve with a chicory salad.','40 mins','1 hr and 20 mins'),
(1200,'ultimate onion tart','https://www.bbcgoodfood.com/recipes/ultimate-onion-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-491544_12-fd84cc1.jpg','This simple quiche is a classic veggie favourite',5,6,2,2,14,'Heat oven to 190C/fan 170C/gas 5. Roll out the pastry on a lightly floured surface and use to line a deep 23cm fluted flan tin. Line with baking paper and fill with baking beans. Bake blind for 15 mins. Heat the butter and oil in a large frying pan, then gently fry the onions, covered, for about 30 mins until completely softened, but still pale in colour. Beat the eggs and cream together in a bowl, then add the cheese and some seasoning. Stir in the onions, then spoon the mixture into the flan case. Bake for 25-30 mins until lightly set and browned.','15 mins','1 hr'),
(1201,'courgette, ham & ricotta tarts','https://www.bbcgoodfood.com/recipes/courgette-ham-ricotta-tarts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-350462_11-2d85cbb.jpg','Make double the quantity of these simple, summery tarts and have one for lunch the next day',5,4,1,2,14,'Heat oven to 200C/180C fan/gas 6. Roll the pastry from one side to make a rough square, then quarter to make 4 smaller squares. Prick all over with a fork, transfer to a baking sheet and bake for 15 mins until puffed and golden. Meanwhile, fry the courgettes in the oil until soft and golden, stirring in the garlic for the final min. Mix together the cream, ricotta, egg and Parmesan. Remove the tarts once they’ve puffed up, flatten the middles and thickly spread the ricotta mixture over, leaving a small border. Scatter over the ham, tomatoes and courgette, season, then bake for 15 mins until the filling is slightly risen and golden. Serve with salad, if you like.','5 mins','30 mins'),
(1202,'slow-roasted tomato & gruyère tart','https://www.bbcgoodfood.com/recipes/slow-roasted-tomato-gruyere-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-260490_12-bfd0e2f.jpg','Slow-roasting tomatoes will intensify their flavour and sweetness. Serve warm for best results',43,6,2,2,14,'Heat oven to 140C/120C fan/gas 1. Arrange the tomatoes over a baking sheet, cut-sides up. Brush lightly with oil and put a little basil on top of each. Bake for 1½ hrs until tomatoes are semi-dried. Remove from the oven and increase the heat to 190C/170C fan/gas 5. To make the pastry, tip the flour into a food processor with the butter and whizz until the mixture resembles fine breadcrumbs. Add cheese and 2-3 tbsp cold water and pulse until the dough comes together. Turn out onto a lightly floured surface and briefly knead. Roll out the pastry and line a 25cm flan tin, there’s no need to trim off the excess at this stage. Line the pastry with greaseproof paper and fill with baking beans. Bake for 15 mins, then remove the paper and beans and continue cooking until crisp and light golden. Using a small, sharp knife, carefully trim off excess pastry. Spread the pesto over the base of the pastry case. Lightly whisk the eggs, then whisk in the cream and milk. Season with salt and pepper, then stir in the grated cheese. Pour into the pastry case and arrange the tomatoes over the top, cut-sides up. Scatter with olives and bake for 25-30 mins until puffed and golden, then leave to cool slightly before serving.','35 mins','50 mins'),
(1506,'omelette roll-up','https://www.bbcgoodfood.com/recipes/mexican-egg-roll','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mexican-egg-roll-7774590.jpg','Turn a one-egg omelette into a wrap and slather on some salsa for a low-carb alternative to a flatbread or sandwich. A quick and easy vegetarian snack',4,1,1,3,24,'Beat the egg with 1 tbsp water. Heat the oil in a medium non-stick pan. Add the egg and swirl round the base of the pan, as though you are making a pancake, and cook until set. There is no need to turn it. Carefully tip the pancake onto a board, spread with the salsa, sprinkle with the coriander, then roll it up. It can be eaten warm or cold – you can keep it for 2 days in the fridge.','5 mins','10 mins'),
(1203,'easter fudge','https://www.bbcgoodfood.com/recipes/easter-fudge','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/Easter-fudge-2dde018.jpg','Indulge in a classic homemade fudge, given a twist for Easter. Creamy and packed full of mini chocolate eggs, it''s a perfect sweet treat during the spring',100,20,1,2,3,'Line a 20cm square brownie tin with baking parchment. Tip all the ingredients, except the mini chocolate eggs, into a saucepan set over a low heat. Stir gently until all of the sugar has melted. Turn up the heat and simmer for 10-15 mins, scraping the bottom of the pan regularly until the mixture reaches 115C on a sugar thermometer. If you don''t have a sugar thermometer, drop a teaspoon of mixture into a small bowl of cold water – if it''s ready, it will form a soft ball that can be easily flattened. Remove the fudge mixture from the heat and leave to cool for 5 mins. Use a wooden spoon to beat the mixture for another 5 mins. Roughly chop 100g of the mini chocolate eggs and tip these into the fudge mixture, then stir until evenly combined. Tip the fudge mixture into the prepared tin, smoothing the surface and pushing it into the corners using a rubber spatula. Scatter over the remaining mini chocolate eggs, then leave at room temperature until completely firm and set. Turn out onto a chopping board and cut into 20 squares before serving.','15 mins','25 mins'),
(1204,'crumbly fudge','https://www.bbcgoodfood.com/recipes/crumbly-fudge','https://images.immediate.co.uk/production/volatile/sites/30/2021/10/Crumbly-Fudge-be47e16.jpg','Gifting your own homemade fudge at Christmas shows friends and family you care. It''s easy to make and has a lovely crumbly texture',100,36,1,2,3,'Line a 20cm square brownie tin with baking parchment. Put the butter, sugar, condensed milk and whole milk into a pan and heat gently for a few minutes until the butter is fully melted. Turn the heat up and simmer for 20-30 mins, stirring regularly to prevent the mixture sticking to the bottom of the pan, until thick and darker in colour. Remove from the heat and use an electric whisk to beat the mixture for 5 mins. Stir in the vanilla, then tip the mixture into the prepared tin. Level the top and leave to cool completely before cutting into small squares.','15 mins','40 mins'),
(1205,'funky fudge','https://www.bbcgoodfood.com/recipes/funky-fudge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fudge-a194712.jpg','Don''t stick to standard flavours for homemade fudge - try these combinations of sweet with salty and zesty with fiery black pepper',5,36,3,2,3,'Line two 19 x 9 x 6cm loaf tins with baking parchment. Tip the sugar, cream, butter and glucose syrup into a medium to large saucepan. Heat to dissolve the sugar and melt the butter, stirring now and again. Once dissolved, put a sugar thermometer in the pan, making sure the measuring tip is completely immersed in the syrup – if not, transfer the mixture to a smaller pan, but be aware the syrup will bubble up. Increase the heat and bring the syrup to a steady boil. Keep bubbling, stirring occasionally to stop the sugar from catching, until the mixture reaches 116C – this is known as the soft-ball stage. Remove the pan from the heat and leave to sit undisturbed for 5 mins until the temperature drops to 110C. Stir in a good pinch of salt. Keep the sugar thermometer in the pan and begin beating the mixture with a wooden spoon, quite vigorously, until the temperature cools to about 60C. By this time the fudge will be really thick and will have lost its glossy shine. Remove the thermometer and continue beating for a few mins more. This process is important when making fudge, as it creates small sugar crystals, which give the fudge its lovely smooth and creamy texture. Working quickly, divide the mixture into 2 heatproof bowls. In one bowl, stir in the mashed banana. In the other, stir in the clementine zest, juice and 1/4 tsp of freshly ground black pepper. Before it sets, quickly pour the fudge mixes into your prepared tins and smooth over the surface. Sprinkle the cooked bacon over the top of the banana fudge. Leave both to cool at room temperature overnight – don’t put the fudge in the fridge as it will become sticky and won’t set properly. Cut into bite-sized pieces and put in a box to give as a present. Banana fudge keeps in a sealed container for up to 1 week, clementine fudge for 2 weeks.','10 mins','40 mins'),
(1206,'chocolate fudge icing','https://www.bbcgoodfood.com/recipes/chocolate-fudge-icing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-fudge-icing-7064ff0-scaled.jpg','Use this easy chocolate buttercream recipe for rich and smooth icing every time - ideal for finishing birthday, celebration or party cakes',6,1,1,2,3,'Put the chocolate in a heatproof bowl, suspended over a pan of barely simmering water. Stir every now and then until melted. (Alternatively, melt in the microwave, stirring the chocolate every 20 secs so it doesn’t burn.) Remove the bowl from the heat and leave to cool a little. Meanwhile, put the butter and icing sugar in another large bowl and mash with a spatula to combine (this will prevent you covering the work surface in icing sugar), then whisk with a hand mixer until smooth. Sift in the cocoa and pour in the melted chocolate and milk, then mix again until smooth, and use to ice your chosen cake immediately. Will keep in the fridge for up to a week, but remove an hour before using to bring it back to room temperature, and beat well.','15 mins','10 mins'),
(1207,'chocolate & orange fudge squares','https://www.bbcgoodfood.com/recipes/chocolate-orange-fudge-squares','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-363845_12-ddc151e.jpg','Rich chocolate brownies with a light, carrot cake-style orange topping',33,15,1,2,3,'Heat oven to 180C/fan 160C/gas 4. Butter and line a traybake tin, 23 x 23cm or thereabouts. Put the chocolate, sugar and butter in a pan, then heat very gently for about 5 mins, stirring every min until the butter and chocolate have melted. Leave to cool for 10 mins. Beat in the egg yolks, flour, vanilla and half the orange zest. Put the egg whites into a large, very clean bowl, then whisk until they stand up in peaks. Stir ¼ of the whites into the chocolate mix to loosen it, then carefully fold in the rest with a metal spoon. Pour the mix into the tin, then bake for 25 mins or until evenly risen and just firm to the touch. Cool in the tin, then cut into squares. Can be frozen for up to 1 month. Beat together the cheese, vanilla, sugar and remaining zest until smooth. Spread over each chocolate square and serve. If you’re making ahead, spread the topping over just before serving.','10 mins','30 mins'),
(1208,'brownie fudge pie','https://www.bbcgoodfood.com/recipes/brownie-fudge-pie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-816584_11-0ab336a.jpg','This gooey chocolate dessert is given a hint of spice with cinnamon in the pastry. It freezes well for an easy dinner party dessert',5,8,1,2,3,'Tip the flour, butter, icing sugar and cinnamon into a food processor and pulse to make crumbs. Add 2 1⁄2-3 tbsp water and pulse until it comes together. Wrap in cling film and chill for 20 mins. Heat oven to 180C/160C fan/gas 4. Roll out the pastry on a lightly floured surface and use to line a deep 23cm loose-based tart tin. Leave the excess pastry overhanging the edges of the tin. Line with baking parchment, fill with baking beans and chill for 15 mins. Bake for 15 mins, remove the paper and beans, then bake 5 mins more until the pastry is crisp and pale golden. Beat the eggs and sugar together to create a mousse-like texture. Stir in the vanilla and melted butter, then fold in the flour and cocoa. Scatter the nuts and chocolate over the pastry case and pour the filling on top. Bake for 30 mins until firm with a slight wobble. Trim away the excess pastry and leave to cool. If freezing, wrap in cling film and foil. To defrost, unwrap and thaw. Serve at room temperature or warm, with a scoop of ice cream and dusting of cocoa.','40 mins','50 mins'),
(1209,'chocolate fudge cupcakes','https://www.bbcgoodfood.com/recipes/chocolate-fudge-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-256531_11-4bf4923.jpg','These bright and fun cupcakes are perfect for kids'' parties',17,12,1,2,3,'Heat oven to 160C/140C fan/gas 3 and line a 12-hole muffin tin with cases. Gently melt the butter, chocolate, sugar and 100ml hot water together in a large saucepan, stirring occasionally, then set aside to cool a little while you weigh the other ingredients. Stir the eggs and vanilla into the chocolate mixture. Put the flour into a large mixing bowl, then stir in the chocolate mixture until smooth. Spoon into cases until just over three-quarters full (you may have a little mixture leftover), then set aside for 5 mins before putting on a low shelf in the oven and baking for 20-22 mins. Leave to cool. For the icing, melt the chocolate in a heatproof bowl over a pan of barely simmering water. Once melted, turn off the heat, stir in the double cream and sift in the icing sugar. When spreadable, top each cake with some and decorate with your favourite sprinkles and sweets.','30 mins','25 mins - 30 mins'),
(1210,'spider web chocolate fudge muffins','https://www.bbcgoodfood.com/recipes/spider-web-chocolate-fudge-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-364110_12-e976865.jpg','Special Hallowe''en chocolatey bakes for lucky trick or treaters - you can make ahead and freeze',37,10,2,2,3,'Preheat the oven to fan 170C/ conventional 190C/gas 5 and line a muffin tin with 10 paper muffin cases. Break the chocolate into a heatproof bowl, add the butter and liquid. Melt in the microwave on Medium for 30-45 seconds (or set the bowl over a pan of gently simmering water). Stir and leave the mixture to cool. Mix the flour, bicarbonate of soda and both sugars in a bowl. Beat the egg in another bowl and stir in the soured cream, then pour this on the flour mixture and add the cooled chocolate. Stir just to combine – don’t overmix or it will get tough. Spoon the mixture into the cases to about three quarters full. Bake for 20 minutes until well risen. Loosen the edges with a round-bladed knife, let them sit in the tins for a few minutes, then lift out and cool on a wire rack. For the topping, make two piping bags out of greaseproof paper (or cut the ends off two clean plastic bags). Break the dark and white chocolate into separate bowls and melt in the microwave on Medium for 2 minutes (or over a pan as in step 1). Put 2 spoonfuls of dark chocolate in one bag and the same of white chocolate in the other. Working with one muffin at a time, spread with dark chocolate from the bowl, letting it run down a bit, then pipe four concentric circles of white chocolate on top. Using a small skewer, drag through the circles at regular intervals, from the centre to the edge, to create a cobweb effect. Repeat with four more muffins. On the remaining five, spread over the white chocolate and decorate with the dark. Best eaten the day they’re made – even better while the chocolate’s soft.','45 mins','No Time'),
(1211,'banana ice sundaes with fudge sauce','https://www.bbcgoodfood.com/recipes/banana-ice-sundaes-fudge-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1202636_10-fbc1549.jpg','Delectable banana ice sundae, all of the taste, none of the fat',5,4,1,2,3,'Slice two bananas into a blender or food processor and blitz with the Yofu and sugar for 30 seconds or until smooth. Churn in an ice cream maker until it begins to thicken - 10-15 minutes. Or freeze in a shallow container, whisking every 1-2 hours to break up ice crystals. To make the sauce, melt the spread, add the sugar and syrup and cook gently until the sugar dissolves. Stir in the Soya Dream and boil for 2-3 minutes to make a sticky sauce. Remove from the heat and allow to cool. To serve, layer the banana ice in four glasses with slices of the remaining bananas and sauce, then top with drizzles of sauce and a few pecans.','25 mins','5 mins'),
(1217,'healthy pumpkin muffins','https://www.bbcgoodfood.com/recipes/healthy-pumpkin-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/Pumpkin-Muffins--c4fb9e5.jpg','Use up leftovers from carving Halloween lanterns with these pumpkin muffins. They don’t have any added fat - the pumpkin that keeps them lovely and moist',4,10,1,2,3,'Heat the oven to 200C/180C fan/gas 6 and line a muffin tin with 10-11 cases and very lightly oil them (otherwise the fat-free mixture tends to stick to the paper). Alternatively, use silicone cases. Put the pumpkin in a bowl, partially cover so steam can escape, then microwave on full power for 5 mins. Mash the cooked pumpkin with the spices until smooth. Allow to cool. Beat the yogurt, eggs, oat milk and maple syrup into the pumpkin, then fold in the flour, raisins and baking powder until well mixed. Add a drop more milk if required. Scoop into the muffin cases and sprinkle over the pecans. Bake for 20-25 mins until risen and firm. Leave to cool and serve. Will keep chilled for up to three days in an airtight container.','15 mins','25 mins'),
(1212,'salted honey fudge & chocolate tart','https://www.bbcgoodfood.com/recipes/salted-honey-fudge-chocolate-tart','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salted-honey-fudge-chocolate-tart-e6659f5.jpg','Layer salted caramel and thick chocolate cream on a chocolate pastry base and top with crunchy honeycomb for a dessert with a difference',17,10,1,2,3,'Dice the pastry into chunks and put in a food processor. Sift over the cocoa, drizzle over the honey, then pulse to combine into a chocolate pastry (see tip box, below left). Roll out the dough on a lightly cocoa-dusted work surface, big enough to line a 23cm loose-bottomed tart tin with a little overhang. Cover and chill for 30 mins. Heat oven to 200C/180C fan/gas 6. Scrunch up a big sheet of baking parchment and use to line the tart tin. Fill with baking beans and bake for 15-20 mins until sandy and the sides are firm. Remove the beans and paper, and bake for a further 5-15 mins until really biscuity. Trim off the overhang and cool. Put the honey fudge ingredients into a small pan and warm gently until the butter has completely melted. Bring to a gentle simmer and bubble, stirring, for 4-5 mins until more golden coloured and thickened. Stir in ¼-½ tsp good salt (to taste), then pour into the tart case. Chill for 30 mins. For the topping, mix the chocolate and cream in a heatproof bowl over a pan of barely simmering water. Gently melt, stirring occasionally, until combined. Cool for 5 mins, then pour into the tart case gently, covering the honey layer. Chill until ready to serve. Remove from the fridge 10 mins before serving, to soften a little. Dust with cocoa and scatter with a little crumbled honeycomb, if you like, then serve with dollops of crème fraîche or a jug of cream.','50 mins','30 mins - 45 mins'),
(1213,'chunky fudge & coffee ripple ice cream','https://www.bbcgoodfood.com/recipes/chunky-fudge-coffee-ripple-ice-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-10685_11-f1a3e0f.jpg','Your friends and family will love this tasty ice cream with a fudge twist',7,6,2,2,3,'Place a container in the freezer. Split the vanilla pod lengthways, scrape the seeds out with the point of the knife and tip into a pan with the milk, cream and pod. Bring to the boil, then remove from the heat and leave to infuse for at least 20 mins. For the best flavour, this can be done a few hours beforehand and left to go cold (see tip, below). In a large bowl, whisk the sugar and egg yolks together for a few minutes until they turn pale and fluffy. Put the vanilla cream back on the heat until it’s just about to boil. Stir through the coffee granules then carefully sieve the liquid onto the yolks, beating with the whisk until completely mixed. At this point, get a large bowl of iced water and sit a smaller bowl in it. Pour the custard back into the pan and cook on the lowest heat, stirring slowly and continuously, making sure the spoon touches the bottom of the pan, for about 10 mins until thickened (see rules, below). Strain the custard into the bowl sitting in the iced water and leave to cool, then churn until scoopable (see methods, below). Layer the cold container with ice cream and spoonfuls of coffee sauce and fudge pieces. Freeze overnight and serve.','15 mins','20 mins'),
(1214,'healthy biscuits','https://www.bbcgoodfood.com/recipes/orange-oat-sultana-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-545451_11-34d08e1.jpg','Looking for a healthier version of your favourite treat? These cookies from Leiths School of Food and Wine use wholemeal flour, oats and walnuts to create an ultra nourishing sweet bite',5,16,1,2,3,'Heat oven to 180C/160C fan/gas 4. Cream the butter and sugar together until well blended and smooth. Gradually beat in the banana, vanilla extract and egg. Add the zest. Mix well with a wooden spoon until thoroughly blended. In a large bowl, mix the flour, salt, baking powder, oats, coconut, walnuts and sultanas or chocolate chips. Stir the dry ingredients into the wet and mix thoroughly until a thick dough is formed. Line a baking sheet with parchment. Drop heaped tbsps onto the sheet, leaving a 5cm (2in) space around each one, and press down lightly. Bake for 15-20 mins until lightly browned. Cool.','30 mins','20 mins'),
(1215,'dark chocolate walnut butter cups','https://www.bbcgoodfood.com/recipes/chocolate-walnut-butter-cups','https://images.immediate.co.uk/production/volatile/sites/30/2020/12/Dark-chocolate-walnut-butter-cups--5177959.jpg','Keep these bite-sized dairy-free nut butter cups in the freezer. Made with medjool dates, they''re perfect when you crave a sweet treat but want something lighter',5,12,1,2,3,'Line the holes of the muffin tin with paper cases and set aside. Heat the oven to 160C/140C fan/gas 3. Spread the walnuts out in a single layer on a baking tray and roast for 12-15 mins, or until fragrant and slightly darker in colour. Tip into a small food processor and blitz in 1-min bursts, scraping down the sides between each burst. When you have a buttery, mostly smooth paste, the walnut butter is ready – this may take 5-10 mins. You don’t want it to be completely smooth, as it will become too runny to fill the cups. Melt the chocolate in a heatproof bowl set over a pan of simmering water, making sure the bowl doesn’t touch the water. Alternatively, do this in the microwave in 30-second bursts. Spoon 1 tsp chocolate into each case, then freeze for 10 mins before adding a spoonful of walnut butter. Push in a few pieces of chopped date, then spoon over the remaining melted chocolate. Freeze for at least another 30 mins, or until the chocolate is firm (the walnut centre will remain gooey), then transfer to a freezerproof container. Will keep frozen for up to a month.','15 mins','15 mins'),
(1227,'avocado & strawberry ices','https://www.bbcgoodfood.com/recipes/avocado-strawberry-ices','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-strawberry-ices-copy-a3dcaf8.jpg','Replace dairy with creamy avocado, full of healthy fat, in these delicious ices and bring out the flavour of strawberries with balsamic vinegar',5,4,1,2,3,'Put the strawberries (save four pieces for the top), avocado, vinegar and vanilla in a bowl and blitz using a hand blender (or in a food processor) until as smooth as you can get it. Have a taste and only add the maple syrup if the strawberries are not sweet enough. Pour into containers, add a strawberry to each, cover with cling film and freeze. Allow the pots to soften for 5-10 mins before eating.','5 mins','No Time'),
(1218,'healthy flapjacks','https://www.bbcgoodfood.com/recipes/vegan-date-walnut-flapjacks','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Vegan-date-walnut-flapjacks-09e488f.jpg','Make these easy healthy flapjacks with dates & walnuts for when you need a pick-me-up. Plant-based and vegan, they''re ideal for those on a dairy-free diet',22,9,1,2,3,'Line a 20 x 20cm square tin with parchment. Roughly chop the dates and cover with 100ml boiling water. Leave to soak for 10 mins. Put the banana in a bowl, mash with a fork, then stir in the cinnamon, vanilla, a pinch of sea salt and the soaked dates along with their liquid. Tip the mixture into a food processor with half of the walnuts and 100g of the oats. Pulse until you have a thick paste. Tip into a bowl and combine with most of the remaining nuts, oats and hemp seeds. Heat the oven to 180C/160C fan/gas 4. Press the mixture into the lined tin, top with the remaining nuts and seeds and, bake for 20-25 mins until golden and firm. Leave to cool completely before cutting into nine squares.','15 mins','25 mins'),
(1219,'berry yogurt pots','https://www.bbcgoodfood.com/recipes/berry-yogurt-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/berry-yogurt-pots-copy-033e216.jpg','Ever feel peckish between meals during the day? These bio yogurt pots brimming with berries, seeds and nuts make a quick, filling and healthy morning or afternoon snack',5,2,1,2,3,'Spoon the yogurt into two tumblers. Mix the fruit, seeds or nuts of your choice with the mint, if using, and pile into the tumblers.','5 mins','No Time'),
(1220,'almond, raisin & popcorn trail mix','https://www.bbcgoodfood.com/recipes/almond-raisin-popcorn-trail-mix','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/almond-raisin-popcorn-trail-mix-copy-2-8ec1cfb.jpg','Store this nut, raisin and popcorn trail mix in an airtight container and grab a handful for a healthy snack when you’re peckish or out and about',5,6,1,2,3,'Heat the oil in a small pan, add the corn, cover and cook for 2-3 mins or until all the kernels pop. Tip into a bowl, leave to cool, then stir in the raisins, almonds and puffed wheat. Store in an airtight container.','5 mins','2 mins - 3 mins'),
(1221,'minty pineapple smoothie','https://www.bbcgoodfood.com/recipes/minty-pineapple-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-smoothie-5287182.jpg','There''s more to smoothies than fruit - this green blend contains spinach, oats, linseeds and cashew nuts too',45,2,1,2,3,'Put all the ingredients in a blender with 200ml water and process until smooth. If it’s too thick, add more water (up to 400ml) until you get the right mix.','10 mins','No Time'),
(1222,'acai bowl','https://www.bbcgoodfood.com/recipes/acai-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/acai-bowl-e1d7cbc.jpg','Try this fruity breakfast smoothie bowl for a quick, easy start to your day. Vary toppings according to the season – fresh berries or peaches work well in summer',5,1,1,2,3,'Put the açaí powder, frozen berries, banana and ice cubes into a powerful blender with 100ml water. Blitz until smooth, then pour into a bowl and add your choice of toppings.','5 mins','No Time'),
(1223,'energy balls with dates','https://www.bbcgoodfood.com/recipes/dried-fruit-energy-nuggets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201568_10-82b1a9a.jpg','Give your energy levels a real boost with these nutritionally-balanced healthy fruit bites',5,6,1,2,3,'Whizz apricots with dates and cherries in a food processor until very finely chopped. Tip into a bowl and use your hands to work in coconut oil. Shape the mix into walnut-sized balls, then roll in sesame seeds. Store in an airtight container until you need a quick energy fix.','10 mins','No Time'),
(1224,'healthy chocolate milk','https://www.bbcgoodfood.com/recipes/healthy-chocolate-milk','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/healthy-chocolate-milk-56a029c.jpg','Enjoy this healthier version of chocolate milk that’s high in protein. The green tinge comes from iron-rich spinach, but trust us – it tastes great',29,1,1,2,3,'Whiz all the ingredients together in a blender until smooth, pour into a tall glass and serve.','5 mins','No Time'),
(1225,'frozen fruit sticks with passion fruit & lime drizzle','https://www.bbcgoodfood.com/recipes/frozen-fruit-sticks-passion-fruit-lime-drizzle','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-693474_11-b259269.jpg','On a hot day you''ll be glad of this fruity, frozen snack that is low-fat and a great source of vitamin C – perfect for kids',5,8,1,2,3,'Mix the drizzle ingredients in a small bowl, stirring until the sugar has dissolved. If you want the sauce to be smooth, pass it through a sieve to remove the seeds, or leave them in if you prefer. Skewer the fruits onto wooden skewers and drizzle the sauce on top, reserving a little for dipping. Pop the skewers in the freezer for 45 mins, until just starting to freeze. Serve with the leftover drizzle.','20 mins','No Time'),
(1226,'fruity fondue','https://www.bbcgoodfood.com/recipes/fruity-fondue','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-613599_11-d07864c.jpg','It counts as 2 of your 5-a-day and is superhealthy, so what are you waiting for? Get dipping!',5,4,1,2,3,'Thread the fruits onto wooden skewers. Melt the milk chocolate on a low heat in the microwave and transfer to a small serving bowl. Serve the kebabs on a platter with the yogurt and melted chocolate for dipping and get everyone to dig in.','10 mins','5 mins'),
(1234,'chocolate rice crispy cakes','https://www.bbcgoodfood.com/recipes/chocolate-rice-krispie-cakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-rice-krispie-cakes-f331428.jpg','Make a kids'' classic with these chocolate rice crispy cakes and decorate with sweets, nuts or dried fruit for the ultimate hometime treat',19,9,1,2,3,'Put the chocolate in a heatproof bowl with the butter and golden syrup and gently melt in 10-second bursts in the microwave, or melt it over a pan of simmering water, making sure the bowl doesn’t touch the water. Stir until smooth, then take off the heat and stir in the puffed rice, coating them gently with the chocolate until they are all completely covered. Divide the mixture between nine cupcake or 12 fairy cake paper cases – it’s easier if you slide these into a muffin tin as it will help them hold their shape. Leave to set. If you want them to set faster, put in the fridge for 1 hr. Drizzle with a little melted chocolate and decorate with sweets, dried fruit or nuts while they are still wet enough to stick them on. Will keep in an airtight container for five days.','15 mins','5 mins'),
(1235,'easy chocolate mousse','https://www.bbcgoodfood.com/recipes/easy-chocolate-mousse','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-mousse-ef7e0cc.jpg','Make this classic chocolate dessert in minutes with just a few ingredients – light yet delicious. Perfect for fuss-free entertaining to end a rich meal',41,4,1,2,3,'Melt the chocolate in the microwave in a microwaveable bowl, stirring every 30 seconds until just melted. Whisk the egg whites to soft peaks, scatter in the sugar and whisk again until the mixture forms stiff peaks when you lift the whisk out (around 30 seconds). Add a big tablespoon of the egg white to the melted chocolate and mix vigorously, working fast so the chocolate doesn’t cool and harden. Then fold in the remaining egg whites carefully using a spatula or large metal spoon. Spoon into 4 glasses or ramekins and set in the fridge for 2-3 hours. Serve each with a dollop of crème fraiche and shavings of dark chocolate.','5 mins','2 mins'),
(1236,'sticky chocolate cake','https://www.bbcgoodfood.com/recipes/sticky-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sticky-chocolate-cake-933a0ca.jpg','Bake this super easy chocolate cake with just a handful of storecupboard ingredients. Use marmalade (or jam if you prefer) to keep it moist and fruity',77,8,1,2,3,'Heat oven to 180C/160C fan/gas 4 and line a loose-based 20cm cake tin with baking parchment. Put the butter and chocolate in a medium saucepan and heat gently, stirring all the time, until melted. Put 2-3 tbsp of the marmalade in a small bowl and set aside. Off the heat, stir the rest of the marmalade and the sugar into the buttery melted chocolate, and mix well, then break in the eggs and mix again. Finally, stir in the flour in three batches, folding lightly until combined. Scrape into the tin and smooth the top. Bake for 45-55 mins, rotating the tin halfway through so it bakes evenly. Keep an eye on it in the later stages as (depending on your marmalade) there may be a tendency for the top to scorch. After about 30 mins, cover loosely with foil. If it starts to get a little dark, tent loosely with a sheet of foil. The cake is cooked when the centre is firm when gently touched, and a skewer inserted in the centre comes out with a very few crumbs attached, like a brownie. Allow to cool for 10 mins, then turn out and leave to cool right-side-up on a wire rack. Heat the reserved marmalade in the microwave until runny, then brush all over the cake to glaze. Leave to cool before serving.','10 mins','45 mins - 55 mins'),
(1237,'easy chocolate truffles','https://www.bbcgoodfood.com/recipes/easy-chocolate-truffles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-110457_11-9947d06.jpg','Chocolate truffles, beautifully wrapped in boxes, will put a smile on anyone''s face – and they''re so easy to make',5,50,1,2,3,'Chop the chocolate and tip into a large bowl. Put the cream and butter into a saucepan and heat gently until the butter melts and the cream reaches simmering point. Remove from heat, then pour over the chocolate. Stir the chocolate and cream together until you have a smooth mixture. Add any flavourings to the truffle mix at this stage (divide the mixture between bowls and mix in liqueurs or other flavourings, a tsp at a time, to taste. Try bourbon, Grand Marnier, coconut rum or the zest and juice of an orange), or leave plain. Cool and chill for at least 4 hrs. To shape the truffles, dip a melon baller in hot water and scoop up balls of the mixture, then drop the truffles onto greaseproof paper. Or lightly coat your hands in flavourless oil (such as sunflower) and roll the truffles between your palms. You could also use a piping bag to pipe rounds onto baking parchment. Coat your truffles immediately after shaping. Tip toppings into a bowl and gently roll the truffles until evenly coated, then chill on baking parchment. Try: crushed, shelled pistachio nuts; lightly toasted desiccated coconut; or roll a truffle flavoured with orange zest and juice in cocoa powder. To coat in chocolate, line a baking tray with baking parchment. Melt 100g milk, dark or white chocolate for 10 truffles. Allow chocolate to cool slightly. With a fork, pick up one truffle at a time and hold over the bowl of melted chocolate. Spoon the chocolate over the truffle until well-coated. Place on the baking tray, then chill. To give as presents, place 8-10 truffles in individual foil or paper cases inside small, lined boxes tied with ribbon. Keep in the fridge until you’re ready to give them. Will keep in an airtight container in the fridge for three days, or frozen for up to a month. Defrost in the fridge overnight. ','30 mins','5 mins'),
(1238,'hot chocolate stirrers','https://www.bbcgoodfood.com/recipes/hot-chocolate-stirrers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hot-chocolate-stirrer-3a3eaf4.jpg','We all know a few die hard chocolate fans - make them one of these hot choc stirrers as a gift and they''ll never want to use powdered cocoa again',29,6,1,2,3,'You will need:6 cupcake cases, pencil, pastry brush, 6 empty 47g fromage frais pots (washed and dried), 6 wooden lolly sticks, cellophane and string or ribbon for wrapping, 6 gift tagsPush a small hole in the middle of the cupcake cases with a pencil and put aside for later. Dip the pastry brush in the sunflower oil and paint a very thin layer of oil over the insides of the fromage frais pots. Put the chocolate in a bowl and heat in the microwave in 30-sec bursts until runny, stirring after each blast. Or melt it in a heatproof bowl set over a pan of simmering water (get an adult to help you). If you’re using different types of chocolate, you should melt them separately. Carefully pour the melted chocolate into the pots. Put a lolly stick in the middle of each and sprinkle your chosen decorations around it. Sit a cake case on top of each pot so that it covers the chocolate and the stick pokes through the hole. Put them in the fridge to set overnight. The next day, carefully pull the chocolates out of the pots and throw away the paper cases. Wrap each in cellophane tied with string and write a tag to read: ‘Simply stir into hot milk.’','10 mins','2 mins - 3 mins'),
(1239,'fudgy brownies','https://www.bbcgoodfood.com/recipes/fudgy-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Fudgy-brownies--daa8eb5.jpg','Indulge in these glorious brownies with a crispy top and soft, fudgy centre. They contain pretzels and nuts too, for added flavour and texture',129,4,1,2,3,'Heat the oven to 200C/180C fan/gas 6. Butter a 20cm square tin and line with baking parchment. Melt the butter and chocolate in a large heatproof bowl in 30-second bursts in the microwave until the mixture is smooth and glossy. Alternatively, set the bowl over a pan of simmering water, ensuring the base doesn''t touch the water, and stir until smooth. Leave to cool slightly. Whisk the sugar and eggs together in a bowl using an electric whisk until the mixture has doubled in volume and a ribbon holds for 3 seconds when you lift out the beaters. Scrape the cooled chocolate mixture into the egg mixture, and fold gently until well-combined. Sift in the cocoa powder, flour and a pinch of salt. Mix to combine, then fold in most of the milk chocolate, pretzels and nuts, reserving some for the top. Scrape the batter into the prepared tin and scatter with the reserved chocolate, pretzels and nuts. Bake on the middle shelf of the oven for 25 mins until just set with a shiny top and gentle wobble in the middle. Leave to cool completely in the tin, then slice and serve.','20 mins','35 mins'),
(1240,'salted caramel brownies','https://www.bbcgoodfood.com/recipes/salted-caramel-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salted-caramel-brownies-e067f0c.jpg','Take indulgent, fudgy chocolate brownies to the next level with a layer of salted caramel running through the centre of each bite.',33,16,1,2,3,'Heat oven to 180C/160C fan/gas 4. Grease then line a 23cm square traybake tin with baking parchment. Melt 200g unsalted butter in a medium pan, break in 100g chocolate (70% cocoa solids) and 100g chocolate (50% cocoa solids), then remove the pan from the heat and wait for the cubes to melt. In a small bowl, mix 175g caramel from a 397g can with 1 tsp sea salt – it will loosen up. Put the rest of the caramel in a large bowl with 200g golden caster sugar and 4 medium eggs, and beat with an electric hand mixer or balloon whisk until even. Whisk in the chocolate and butter. In another bowl, combine 130g plain flour, 50g cocoa powder and a good pinch of table salt, then sift this on top of the chocolate mix. Beat briefly until smooth. Pour half the brownie batter into the tin and level it with a spatula. Using a teaspoon, spoon half of the salted caramel on top of the batter layer in 5 thick, evenly spaced stripes. Spoon the rest of the brownie batter on top and smooth it out, trying not to disturb the caramel beneath. Top with the rest of the caramel in the same stripy fashion. Drag a skewer or tip of a knife through the caramel to make a feathered pattern on the top. Scatter with a little more sea salt, then bake for 25-30 mins or until risen all the way to the middle with a firm crust on top. When ready, the brownie will jiggle just a little when you shake the tin. Let it cool completely in the tin, then cut into squares.','20 mins','30 mins'),
(1241,'hazelnut brownies','https://www.bbcgoodfood.com/recipes/hazelnut-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hazelnut-brownies-12fe739.jpg','Hazelnut wafer chocolates make an indulgent finishing touch to these fudgy chocolate brownie squares studded with chopped nuts',17,16,1,2,3,'Unwrap the chocolates, place on a tray and pop in the freezer. Heat oven to 180C/160C fan/gas 4. Lightly grease and line the base and sides of a 21-22cm square tin with baking parchment. Put the butter, sugars and cocoa into your largest saucepan and gently melt together, stirring regularly so the mixture doesn’t catch. Once the sugar granules have just about disappeared, take off the heat, tip into a bowl and leave to cool for 5 mins. Use a whisk or wooden spoon to beat the eggs, one by one, into the mixture. When they’re completely incorporated and the mixture is smooth and shiny, stir in the flour, hazelnuts and liqueur. Tip the mixture into the prepared tin and bake for 35 mins. Remove the tin from the oven and use a cutlery knife to mark the top of the brownies into 16 squares (don’t cut through, it’s just as a guide). Use a teaspoon to push a little dent in the centre of each portion and add a frozen hazelnut wafer chocolate into each dip. Return to the oven for 3 mins, then remove and leave to cool completely. Once cool, cut into 16 squares. Will keep for 3 days in an airtight container.','15 mins','45 mins'),
(1242,'gluten-free brownies','https://www.bbcgoodfood.com/recipes/gluten-free-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gluten-free-brownies-4be63ff.jpg','These rich, fudgy chocolate brownies are gluten-free. Serve with a scoop of vanilla ice cream for dessert, or enjoy as an afternoon treat with a cup of tea',14,12,1,2,3,'Heat oven to 180C/160C fan/gas 4. Butter a 30cm x 20cm non-stick tin with butter and line the base with non-stick baking parchment. Fill a small saucepan a third full with water, bring to a simmer and put a snug-fitting heatproof bowl on top of it. Add the butter and chocolate and gently melt over a low heat, stirring occasionally. Be careful not to let it catch and burn on the bottom. Remove from the heat and leave to cool a little. Beat the eggs and sugar together using an electric whisk for 8-10 mins or until thick enough to leave a trail. Gently fold through the cooled melted chocolate and vanilla, followed by the flour, cocoa and salt. Finally, fold through the chocolate chunks. Pour the brownie batter into the lined tin, place in the centre of the oven and bake for 30-35 mins. Leave to cool a little in the tin before cutting into 12 squares.','20 mins','40 mins'),
(1243,'chai caramel chocolate brownies','https://www.bbcgoodfood.com/recipes/chai-caramel-chocolate-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Chai-caramel-chocolate-brownies-8e66323.jpg','Blend chai spices with caramel to make these moreish chocolate brownies, with cardamom, cinnamon, cloves and ginger',5,16,1,2,3,'Heat the oven to 180C/160C fan/gas 4. Butter a 20cm square cake tin and line with two strips of baking parchment. Melt the butter and chocolate in a heatproof bowl over a pan of simmering water (ensuring the bowl doesn’t touch the water) or in 20-second bursts in the microwave. Leave to cool slightly. For the chai caramel, mix all the ingredients together in a bowl until the caramel has loosened. Put the eggs and sugar in the bowl of a stand mixer fitted with the whisk attachment and beat for around 5 mins until pale and thick. Alternatively, do this using an electric whisk. Fold in the slightly cooled melted chocolate mixture using a spatula and, when fully combined, add the flour and cocoa powder. Mix until everything is combined and there are no streaks of flour. Pour into the prepared tin, drizzle over the chai caramel and gently swirl into the batter using a teaspoon. Bake for 25-30 mins until the brownies are set at the edges with a slight wobble in the middle. If the brownies wobble a lot, they’re not done – bake for another 5 mins until the top has a shiny, papery crust and the sides are just beginning to come away from the tin. Leave to cool completely in the tin, then remove and slice into 16 squares to serve. Will keep in an airtight container for up to three days.','25 mins','30 mins'),
(1244,'easter egg brownies','https://www.bbcgoodfood.com/recipes/easter-egg-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easter-egg-brownies-70c3441.jpg','Loaded with chocolate eggs and chicks, this is the ultimate Easter centrepiece - an ''eggstra'' special treat for afternoon tea or dessert',5,9,1,2,3,'Put the butter and the chocolate in a medium-sized heatproof bowl and set over a pan of simmering water, stirring occasionally until melted. Alternatively, cover the bowl loosely with cling film and microwave at 30 sec intervals until melted. Leave the melted chocolate mixture to cool to room temperature. Heat oven to 180C/160C fan/gas 4. Grease a 20cm square tin with butter and line with 2 long strips of folded baking paper or foil – one running top to bottom, the other left to right then line the base with a piece of baking paper. The strips will help you lift it out later. Break the eggs into a large bowl and tip in the caster sugar. With an electric mixer on maximum speed, whisk the eggs and sugar for around 5-8 mins or until they look thick and creamy and have doubled in size. Pour the cooled chocolate mixture over the egg mixture, then gently fold together. Sieve the flour and cocoa into the wet ingredients then continue to fold the mixture gently with a spatula just until everything is fully combined, try not to over-mix. Pour the mixture into the prepared tin and carefully level the mixture with the spatula. Put in the middle of the oven and bake for 20 minutes. Meanwhile, cut the fondant filled eggs in half and set aside then place the mini chocolate eggs into a pestle and mortar. Crush a few of the mini chocolate eggs but leave some whole. After 20 mins take the brownie out of the oven and press in the fondant eggs, cut side up. Scatter over half of the mini chocolate eggs too then put it back in the oven for a further 5 mins. Once baked, top with the remaining mini eggs, leave to cool completely then place in the fridge for about 1hr to firm up. Lift it out of the tin using the strips of baking paper and cut into squares to serve. Decorate with little Easter chicks if you like.','30 mins','30 mins'),
(1245,'vegan cherry & almond brownies','https://www.bbcgoodfood.com/recipes/vegan-cherry-almond-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/brownies-e232686.jpg','Deeply rich and decadent, this dairy and egg-free chocolate bake makes an indulgent vegan treat',5,12,1,2,3,'Heat oven to 170C/150C fan/gas 31⁄2. Grease and line a 20cm square tin with baking parchment. Combine the flaxseed with 6 tbsp water and set aside for at least 5 mins. In a saucepan, melt the chocolate, coffee and margarine with 60ml water on a low heat. Allow to cool slightly. Put the flour, almonds, cocoa, baking powder and 1⁄4 tsp salt in a bowl and stir to remove any lumps. Using a hand whisk, mix the sugar into the melted chocolate mixture, and beat well until smooth and glossy, ensuring all the sugar is well dissolved. Stir in the flaxseed mixture and vanilla extract, the cherries and then the flour mixture. It will now be very thick. Stir until combined and spoon into the prepared tin. Bake for 35-45 mins until a skewer inserted in the middle comes out clean with moist crumbs. Allow to cool in the tin completely, then cut into squares. Store in an airtight container and eat within 3 days.','20 mins','45 mins'),
(1246,'homemade sourdough bread','https://www.bbcgoodfood.com/recipes/homemade-sourdough-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sourdough-e088dc0.jpg','Make our easy sourdough starter seven days ahead to make our simple homemade sourdough loaf. Serve with your favourite casseroles or soups',5,1,1,2,18,'To make the sourdough starter, whisk 50g strong white flour and 50g strong wholemeal flour with 100ml slightly warm water until smooth. Transfer to a large jar or plastic container. Leave the lid ajar for 1 hr or so in a warm place, then seal and set aside for 24 hrs. For the next six days, you will need to ‘feed’ it. Each day, tip away half the original starter, add an extra 25g of each flour and 50ml slightly warm water, and stir well. After a few days, you should start to see bubbles on the surface, and it will smell yeasty. On day seven, the starter should be bubbly and smell much sweeter. It is now ready to be used, but make sure you keep half back and carry on feeding for your next loaf.  Tip both the flours, 300ml warm water and the starter into a bowl, stir with a wooden spoon into a dough and leave somewhere for an hour.  Tip in 25ml more water and the salt and bring everything together. Cover and leave somewhere warm for 3 hrs, folding the dough onto itself several times in the first hour. The dough should increase in size by about a third. Line a medium bowl with a clean tea towel and flour it really well, or flour a proving basket. Tip the dough back onto your work surface, shape into a tight, smooth ball and dust it with flour.  Place the dough, seam-side up, in the bowl or proving basket, and leave at room temperature for 3 hrs – or better still in the fridge overnight – until risen by about a quarter. Heat oven to 230C/210C fan/gas 8 and put a casserole dish with a lid or a baking stone in the oven for at least 30 mins to heat up, and a large roasting tin filled with boiling water underneath. After 30 mins, carefully remove the casserole dish, invert the loaf into it and slash the top. Cover and bake for 20 mins, then take the lid off and bake for another 20 mins. Or invert onto the baking stone, slash and bake for 40 mins, or until the crust is as dark as you like it. ','1 hr','40 mins'),
(1247,'crusty bread','https://www.bbcgoodfood.com/recipes/crusty-bread','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/White-bread-cdd3875.jpg','Try our next level recipe for crusty white bread - it takes a lot longer to prepare but the result will be the best loaf of bread you''ve baked at home',7,8,2,2,18,'Tip the flour into a large bowl along with the yeast. Pour over 325g room-temperature water (you’ll get a more accurate amount by weighing the water, but you can also use 325ml, depending on your scales), then use your hands or a spatula to mix until a dough forms. It’s important that you ensure all of the flour is mixed in, including any bits around the side of the bowl. Cover with a damp tea towel and leave to rest for 30 mins. Sprinkle over the salt, then work it in by stretching the dough over the salt several times until fully incorporated. Tip out onto a lightly floured surface. Use the heel of one hand to stretch the dough while you hold it with your other hand. Then, fold the dough back onto itself, turn it 90 degrees to the left and repeat. Do this for about 5 mins, or until the dough looks shinier and immediately bounces back when rolled into a tight ball or gently poked. Roll the dough into a tight ball, cover with a damp tea towel and leave to prove at room temperature for 1 hr until doubled in size. Or, for a deeper flavour, transfer to a clean bowl, cover and leave to prove in the fridge overnight until doubled in size. Cold dough is easier to work with, so overnight is best. When the dough has doubled in size and is light and pillowy, tip it out onto a work surface and knead it a few more times to knock the air out. Gather the dough into a ball and leave to rest for 5 mins. Gently form the dough ball into a tight, rounded shape on the work surface, then tip domed-side down into a proving basket or bowl lined with a clean tea towel. Leave to prove in a warm place for 40 mins-1 hr, or in the fridge for 2-3 hrs until roughly doubled in size. Put a casserole along with its lid in the oven, then heat the oven to 230C/210C fan/gas 8. Once the oven is at temperature, remove the casserole. Flip the dough onto a square of baking parchment, slash it using a sharp knife and use the sides of the parchment to carefully lower the dough (still on the parchment) into the hot casserole. Cover with the lid. Bake for 25 mins. Remove the lid and bake for 5-10 mins more until it has a deeply golden, crisp crust and sounds hollow when tapped on the bottom. Cool on a wire rack for 40 mins before slicing. Will keep in an airtight container for up to two days.','30 mins','30 mins'),
(1248,'vegan soda bread','https://www.bbcgoodfood.com/recipes/vegan-soda-bread','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/vegan-soda-bread-e5fa0c2.jpg','Enjoy a vegan version of soda bread, which is just as quick and easy as the original and tastes equally delicious spread with your favourite toppings',130,1,1,2,18,'Mix the plant milk and cider vinegar together in a jug, then set aside for 30 mins. Heat the oven to 220C/200C fan/gas 7 and line a baking sheet with baking parchment. Combine both flours, 1 tsp salt and baking soda in a large bowl, then pour over the plant-milk mixture and stir to combine. Bring together into a dough using your hands, then form into a ball. If the ball is very sticky, lightly flour your hands to make it easier to handle. Place the dough ball onto the prepared baking sheet and flatten slightly into a thick disc using your hands. Cut a deep cross into the centre using a serrated knife, then sprinkle over the oats and bake for 35-40 mins until the base sounds hollow when tapped. Leave on the baking sheet for 5 mins, then transfer to a wire rack to cool completely. Slice and serve.','10 mins','40 mins'),
(1250,'focaccia','https://www.bbcgoodfood.com/recipes/focaccia','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/focaccia-f599171.jpg','Make a simple, homemade version of this classic Italian bread. Serve our rosemary focaccia alongside pasta dishes or enjoy with green salads',46,12,1,2,18,'Tip the flour into a large mixing bowl. Mix the yeast into one side of the flour, and the fine salt into the other side. Then mix everything together, this initial seperation prevents the salt from killing the yeast. Make a well in the middle of the flour and add 2 tbsp oil and 350-400ml lukewarm water, adding it gradually until you have a slightly sticky dough (you may not need all the water). Sprinkle the work surface with flour and tip the dough onto it, scraping around the sides of the bowl. Knead for 5-10 mins until your dough is soft and less sticky. Put the dough into a clean bowl, cover with a tea towel and leave to prove for 1 hr until doubled in size. Oil a rectangle, shallow tin (25 x 35cm). Tip the dough onto the work surface, then stretch it to fill the tin. Cover with a tea towel and leave to prove for another 35-45 mins. Heat the oven to 220C/200C fan/gas 7. Press your fingers into the dough to make dimples. Mix together 1½ tbsp olive oil, 1 tbsp water and the flaky salt and drizzle over the bread. Push sprigs of rosemary into the dimples in the dough. Bake for 20 mins until golden. Whilst the bread is still hot, drizzle over 1-2 tbsp olive oil. Cut into squares and serve warm or cold with extra olive oil, if you like.','25 mins','20 mins'),
(1251,'ciabatta','https://www.bbcgoodfood.com/recipes/ciabatta-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ciabatta-c81d854.jpg','Try making a loaf of this Italian white bread with our simple recipe. Get that characteristic crisp crust and soft inside that''s perfect for dipping in olive oil',41,2,2,2,18,'The night before, make the biga (see tip, below). Stir yeast with 50ml warm water, stand for 10 mins, then add another 80ml warm water. Gradually add the flour in a stand mixer on its lowest setting. Once it’s a wet dough, transfer to a well-oiled bowl, cover and leave for 12 hours or overnight at room temperature. In the morning, combine the yeast and milk and leave to stand for 10 mins. Tip into a freestanding mixer fitted with a dough hook, add 160ml water, the biga and the olive oil. Then add the flour and 1 heaped tsp salt. Use the dough hook of a stand mixer to combine the dough. Knead for 10 mins until smooth and elastic. Don’t worry if it looks very wet, it should to be a very wet dough! Pour into a well-oiled bowl and cover with cling film. Leave to prove for an hour and a half or until doubled in size. Once rested, begin to do a series of folds – lift the dough from the edge, pull up, over, then release it. Turn the bowl 90 degrees and do the same again. Repeat so you do a full turn of the bowl twice, or 8 folds. Rest for 30 mins, then repeat the whole folding process once more. Heat the oven to 220C/200C fan/gas mark 6. Tip the dough onto a really well-floured surface and cut in half. The dough will feel like a batter and spread across the surface a bit, but don’t panic, just work on a well-floured surface, using the flour and a pastry scraper to help move the dough. Shape the dough into 2 large squares (about 20cm x 20cm). Dealing with each loaf at a time, fold the dough in from each side, as if folding a booklet. Flip over, then pick up the roll and place each onto separate well-floured sheets of baking paper. The roll will be very soft, so oil or flour your hands well. Allow to rest for another 30 mins, covered with a floured tea towel. Don’t worry if it spreads a little. While the dough rests, heat a baking sheet in the oven. Once the dough has rested, slide each of the loaves, along with the baking paper beneath them, onto the hot baking sheet. Bake for 35-40 mins, until the crust is golden and the loaves sound hollow when tapped on the base. Move to a wire rack and cool for an hour before slicing and serving with olive oil and balsamic vinegar.','30 mins','40 mins'),
(1252,'potato & turmeric focaccia','https://www.bbcgoodfood.com/recipes/potato-turmeric-focaccia','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/potato-turmeric-focaccia-fff62c4.jpg','Try this twist on a traditional foccacia with a topping of potato, turmeric and rosemary. It takes a little effort, but you''ll be well rewarded for your time',131,12,2,2,18,'In a large bowl, dissolve the yeast in 350ml cold water and add the oil. Add the flour and 1 tsp salt and mix thoroughly with your hands to make a dough. Cover and leave in the fridge or a cool place overnight. The next day, turn your dough out onto an oiled tray approx 30cm x 20cm. Using your fingertips, gently stretch the dough into a rectangle, then fold it in half. Rotate the dough 90 degrees and repeat the process. Leave to rest for 30 mins. Repeat this stretching, folding and resting process twice more. Heat oven to 24OC/220C fan/gas 9. Gently stretch the dough to fill your tray. If it shrinks back, don’t force it – rest for 10 more mins, then try again. Spread the potato slices over the dough, overlapping. Mix the olive oil with the turmeric and brush over, then sprinkle with sea salt and bake on the top shelf of the oven for 25-30 mins. When cooked, drizzle with a little more olive oil and scatter over the rosemary.','20 mins','30 mins'),
(1253,'tortillas','https://www.bbcgoodfood.com/recipes/tortillas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tortillas-32d9534.jpg','Forget shop-bought and try homemade tortillas. They''ll store for two days or you can freeze them to use later. They''re perfect for wraps',5,6,1,2,18,'Combine the flour, vegetable oil and salt in a bowl. Pour over 150ml warm water and knead to bring the mixture together. Remove from the bowl and knead on a lightly floured work surface for around 5 mins until you make a soft, smooth ball of dough. Cover with a clean tea towel and rest for at least 15 mins before rolling out.  Cut the dough into 6 equal pieces (about 75g each). Roll out each ball of dough as thinly as possible on a floured work surface. They should make rounds about 20 cm wide, 2mm thick.  Heat a large frying pan over a medium-high heat. Cook the tortillas for 1-2 mins on each side until golden brown and toasted. Wrap them in foil and cover in a tea towel to keep warm, while you cook the rest. Keeps for two days well wrapped, or cool, wrap and freeze for up to three months. ','30 mins','25 mins'),
(1254,'vegan chocolate chip cookies','https://www.bbcgoodfood.com/recipes/vegan-chocolate-chip-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan_cookies-affe287.jpg','Try our best-ever vegan chocolate chip cookies. Non-vegans will be hard pressed to notice the difference between these and their favourite choc chip biscuits',7,20,1,2,18,'Tip the coconut oil and sugars into a bowl and whisk until completely combined, then whisk in the coconut milk and vanilla. Tip the flour, baking powder, bicarb and a good pinch of flaky sea salt into the mix to make a thick batter, then fold through the chocolate chips. Chill the batter for at least 1hr. Can be made two days ahead. Heat the oven to 180C/160C fan/gas 4. Line a couple of baking sheets with baking parchment, then scoop or roll plum-sized balls of the dough and place them on the baking sheets about 2cm apart. Flatten ever so slightly and sprinkle with a bit more flaky salt if you want. Cook on the middle shelf for 12-15 mins, turning the tray once, until the cookies have spread and are golden but still soft in the middle. Leave to cool slightly, then lift the cookies onto a cooling rack while you bake another batch. Will keep in a biscuit jar for up to three days.','15 mins','12 mins'),
(1655,'spicy chorizo with lentils & beetroot','https://www.bbcgoodfood.com/recipes/spicy-chorizo-lentils-beetroot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo_1-649fb18.jpg','A rustic salad of spicy Spanish sausage, storecupboard pulses and sweet beetroot',4,2,1,3,17,'Heat a large pan, add a drop of the olive oil, and cook the onion and chorizo for a few mins until starting to colour. Tip in the lentils with the beetroot wedges, vinegar, remaining oil, some seasoning and 1 tbsp water. Cover the pan and heat for 1 min to warm through. Pile into a bowl, toss through the rocket and serve.','5 mins','10 mins'),
(1255,'vegan chocolate truffles','https://www.bbcgoodfood.com/recipes/vegan-truffles','https://images.immediate.co.uk/production/volatile/sites/30/2020/09/Vegan-Chocolate-Truffles-6f2dc50.jpg','Make these gorgeous vegan truffles as an edible gift for family and friends at Christmas. They''re made with dairy-free chocolate and plant-based milk',46,20,1,2,18,'Put the chocolate in a medium heatproof bowl. Tip the milk, sugar and vanilla into a small saucepan, then bring to the boil over a medium heat, stirring occasionally. Pour the hot milk mixture over the chocolate, leave to sit for 1 min, then stir until the chocolate has melted and the mix is smooth. Cover and chill for 4 hrs until set. Roll teaspoons of the mixture into balls using your hands, then transfer to a small baking tray or plate – you may like to wear gloves. You should have about 20 balls. Put the cocoa powder on a shallow plate, and roll the chilled truffles in it until well-coated, tapping off any excess, if needed. Chill until you’re ready to gift. Will keep in the fridge for up to four days.','15 mins','10 mins'),
(1256,'vegan chocolate ice cream','https://www.bbcgoodfood.com/recipes/vegan-chocolate-ice-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-chocolate-ice-cream-fc31d9f.jpg','Indulge in homemade chocolate ice cream as the dessert course on a vegan menu. It''s dairy free, egg free and gluten free, but still tastes delicious ',12,10,1,2,18,'Pour most of the coconut milk into a saucepan, reserving a splash in a small bowl for later. Add the sugar, cocoa, chocolate, sea salt flakes and vanilla to the pan. Heat gently for 10 mins until the sugar has dissolved and the chocolate has melted. Mix the cornflour with the reserved coconut milk until smooth. Pour into the hot milk mixture and cook for another 5-10 mins, stirring constantly, until the mixture thickens to a pourable custard consistency. Strain into a bowl then cover and leave to cool to room temperature. Pour the cooled custard into an ice cream maker and churn for 20-30 mins until you have a soft-scoop ice cream. Transfer to a freezerproof sealable container and freeze for up to three months, or until ready to serve. If you don’t have an ice cream maker, pour the mixture into a wide-based plastic tub or dish. Freeze for 3 hrs, stirring every 20 mins to break up any large ice crystals, until you have a soft ice cream. Transfer to a freezerproof sealable container and freeze until ready to serve. Leave to stand for 10 mins at room temperature before scooping into bowls or cones.','25 mins','20 mins'),
(1257,'dark chocolate, ginger & pistachio squares','https://www.bbcgoodfood.com/recipes/dark-chocolate-ginger-pistachio-squares','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Dark-chocolate-ginger-and-pistachio-squares0-5ec502d.jpg','No cooking is required to make these vegan chocolate squares studded with stem ginger, gingernut biscuits and pistachios. Best enjoyed with an afternoon cuppa',16,25,1,2,18,'Oil a 20 x 20cm brownie tin and line with baking parchment. Tip the gingernuts into a food processor and blitz to crumbs. Melt the coconut oil in a small pan over a low heat or in a heatproof bowl in the microwave, then pour this over the crumbs and pulse to combine. Tip the mix into the prepared tin, press into an even layer and chill until needed. Meanwhile, put the chocolate in a heatproof bowl with 1 tbsp coconut oil and the ginger syrup. Slowly pour 150ml boiling water from the kettle into the chocolate mixture, whisking all the time until you have a smooth ganache. (If you haven’t chopped the chocolate small enough and a few lumps remain, simply zap in the microwave for 10 seconds until smooth.) Stir in a large pinch of salt. Pour the ganache over the biscuit base and scatter with the stem ginger pieces, pistachios and a sprinkle of salt. Chill for at least 4 hrs until set, then cut into 25 bite-sized squares. Can be made up to three days in advance and kept covered in the fridge. Bring out of the fridge 20 mins before serving to soften up a little.','20 mins','No Time'),
(1258,'vegan millionaire''s shortbread','https://www.bbcgoodfood.com/recipes/made-over-millionaires-bars','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/made-over-millionaires-bars-ac1da53.jpg','These vegan, gluten-free chocolatey treats with dates, cashews and maple syrup are just as moreish as the original millionaire''s shortbread',6,16,2,2,18,'Grease a 20cm square cake tin and line with baking parchment. Tip the cashew nuts and oats into a food processer and blitz to crumbs. Add the dates and coconut oil, and blend again. Transfer to the tin and use a spoon to press the nutty mixture into a compact, even layer that covers the base. Chill while you prepare the filling. For the filling, add the dates, almond milk, maple syrup and coconut oil to a saucepan with a generous pinch of salt and bring to a simmer. Boil for 2-3 mins until the dates are really soft. Cool the caramel a little, then tip into the blender, add the vanilla extract and blitz to a smooth purée. The caramel will be hot so be careful when handling. Add a little more salt if the mixture is too sweet. Pour over the nutty base and spread to the sides of the tin, getting the surface as smooth as possible. Chill while you prepare the topping. Gently heat the coconut oil in a saucepan until melted. Remove from the heat and whisk in the cocoa and maple syrup until there are no lumps. Cool for 10 mins, pour over the caramel layer and return to the fridge for at least 3 hrs or until firmly set. To serve, cut into squares. Will keep in the fridge for up to a week.','30 mins','5 mins'),
(1302,'simple jammy biscuits','https://www.bbcgoodfood.com/recipes/simple-jammy-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-45874_11-f237710.jpg','Make a treat that the whole family will enjoy with these jammy biscuits',5,12,1,2,23,'Heat oven to 190C/fan 170C/gas 5. Rub the flour, sugar and butter together until the mixture resembles breadcrumbs. Alternatively, you can do this in the food processor. Add enough egg to bring the mixture together to form a stiff dough. Flour your hands and shape the dough into a tube, about 5cm in diameter. Cut into 2cm-thick slices and place on a large baking sheet. Space them out as the mixture will spread when baking. Make a small indentation in the middle of each slice with the end of a wooden spoon, and drop a tsp of jam in the centre. Bake for 10-15 mins until slightly risen and just golden. Cool on a wire rack.','10 mins','12 mins'),
(1259,'vegan cookies & cream brownies','https://www.bbcgoodfood.com/recipes/vegan-cookies-cream-brownies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-brownies-9f40ac0.jpg','Bake these indulgent vegan brownies with a cookie topping. They''re easy to make and don''t need expensive ingredients – just simple items from your storecupboard',107,12,1,2,18,'Heat the oven to 170C/150C fan/gas 3 and line a 18cm x 30cm brownie tin with baking parchment. In a bowl mix together the flour, sugar, cocoa and ½ tsp salt. In a jug mix together 125ml water with the vanilla, oil, vinegar and yogurt. Pour the wet ingredients into the dry and mix until smooth and combined. Pour the mix into the tin and smooth the top. Push the chunks of vegan biscuits into the batter, leaving some poking out of the top. Bake for 30-35 mins until set but still squidgy in the middle. Leave to cool in the tin, then cut into squares.','15 mins','30 mins'),
(1260,'vegan chocolate banana ice cream','https://www.bbcgoodfood.com/recipes/vegan-chocolate-banana-ice-cream','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-banana-ice-cream-6e319c6.jpg','A low-fat chocolate ice cream? It really does exist! Our blitzed banana creation is gluten and dairy-free and ready in minutes',16,1,1,2,18,'In a blender, blitz the frozen banana with the cocoa powder until smooth. Eat straight away.','5 mins','N/A'),
(1261,'vegan chocolate cake','https://www.bbcgoodfood.com/recipes/vegan-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-choc-cake-1905166.jpg','Make our easy vegan chocolate sponge for a special occasion using dairy substitutes. It''s finished with luscious ''buttercream'' and seasonal fruits',29,10,1,2,18,'Heat oven to 190C/170C fan/gas 5. Grease the base and sides of 2 x 20cm sandwich tins with dairy-free spread, then line the bases with baking parchment. Put the dairy-free milk in a jug and add the vinegar – it will split but don’t worry. Put all of the other cake ingredients into a large bowl, pour over the milk mixture and beat well until smooth. Divide the mixture between the prepared tins and bake for 25-30 mins or until a skewer inserted into the middle of the cakes comes out cleanly. Leave to cool in the tins for 10mins then turn out onto wire racks to cool completely. To make the buttercream, put the chocolate into a heatproof bowl and melt in the microwave, stirring every 30 seconds. Leave the melted chocolate to cool for 5 minutes. Beat the dairy-free spread and icing sugar together with a wooden spoon then sift in the cocoa powder with a pinch of salt. Pour in the melted chocolate and dairy-free milk and keep mixing until smooth. Sandwich the two cooled sponges together with half of the buttercream then pile the rest on top and down the sides. Decorate with the fresh fruit.','35 mins','20 mins'),
(1262,'salted caramel biscuit bars','https://www.bbcgoodfood.com/recipes/salted-caramel-biscuit-bars','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salted-caramel-biscuit-bars-01199af.jpg','A healthier take on one of our favourite chocolate bars, these biscuits are packed with wholesome ingredients, and free from refined sugar and dairy',49,18,2,2,18,'Heat oven to 180C/160C fan/gas 4 and line a large baking tray with baking parchment. For the base, blitz the oats in a food processor until flour-like. Add the remaining ingredients and whizz until the mixture starts to clump together. Scrape into a bowl, then roll and cut into 18 equal-sized rectangular bars, about 9 x 2cm. Place on the prepared tray and use a small palette knife to neaten the tops and sides of each biscuit. Bake for about 10 mins until lightly golden at the edges, then leave to cool. Meanwhile, put all the caramel ingredients in the food processor (no need to rinse it first) and blitz until it forms smooth, shiny clumps. Using a spatula, push the mixture together, then roll into 18 even-sized balls using your hands. Once the biscuits are cool, squash the caramel onto them. Use your fingers to press it into shape and smooth out any bumps, especially around the edges (as they will show underneath the chocolate coating). Melt the chocolate in a heatproof bowl set over a pan of simmering water – make sure the water doesn’t touch the bowl (otherwise, it might seize and go grainy). Carefully dip one of the caramel-coated biscuits in the chocolate, turning it gently with a small palette knife (use this to lift it out as well). Use a spoon to drizzle over more chocolate to coat it fully. Let the excess chocolate drip into the bowl, then carefully put the biscuit back on the lined tray. Repeat with the remaining biscuits, then chill in the fridge for at least 30 mins or until the chocolate has set. Put the biscuits in an airtight container and store in the fridge. Will keep for five days.','45 mins','15 mins'),
(1263,'chocolate crunch & raspberry pots','https://www.bbcgoodfood.com/recipes/chocolate-crunch-raspberry-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-17963_12-11f0ec0.jpg','You''d never guess this was an allergy free dessert - no nuts, dairy, gluten or eggs. The perfect solution to those tricky menus',55,4,3,2,18,'Divide the raspberries between 4 glasses. Sprinkle ½ tbsp of Cointreau and a little orange zest and juice over each, then set aside. Melt the chocolate and stir into the soya milk, then set aside. Tip the sugar into a pan along with 3 tbsp water. Over a gentle heat, cook without stirring for about 7 mins until the sugar melts and starts to turn golden brown. Tip in the muesli, stir, then pour onto a tray lined with baking parchment. Leave to cool, then shatter into thin shards. Divide the chocolate mixture between the glasses and allow to cool, but don’t refrigerate. Scatter over the caramel crunch to serve.','5 mins','10 mins'),
(1320,'rum ‘n’ raisin ice cream sandwich','https://www.bbcgoodfood.com/recipes/rum-n-raisin-ice-cream-sandwich','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rum-n-raisin-789a3fd.jpg','Mix plump raisins and dark rum with creamy vanilla ice cream then sandwich between two oat and raisin cookies to make this decadent grown-up dessert',41,2,1,2,23,'In a small saucepan, warm the raisins in the rum for a few mins. Set aside to cool for 15 mins, then mix with the ice cream. Freeze for a few hours, or until solid. Divide the ice cream between 2 cookies, then top with the other cookies.','5 mins','5 mins'),
(1265,'vegan carrot cake','https://www.bbcgoodfood.com/recipes/vegan-carrot-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-6f4d30e.jpg','Give free-from baking a go with this easy vegan sandwich cake – an indulgent carrot cake with coconut and cashew icing that everyone will want another slice of',41,12,1,2,18,'Start by making the icing first. Mash the coconut cream with 2 tbsp hot water and the lemon juice until smooth. Add the cashew butter then whisk in the icing sugar followed by the oat milk. Continue to whisk until fully combined, set aside in the fridge until needed. Heat the oven to 180C/160C fan/gas mark 4. Grease 2 x 20cm cake tins with a little of the melted coconut oil and line the bases with baking parchment. Whisk together the oil and sugar, then add the vanilla and milk. Combine the flour, baking powder, bicarbonate of soda, spices and orange zest in a separate bowl. Add these to the wet mixture and stir well. Finally stir in the carrot and the nuts. Divide the mixture between the prepared tins and bake for 25-30 mins until a skewer inserted into the middle of the cake comes out cleanly. Cool in the tin for 5 mins before transferring to a wire rack to cool completely. Sandwich the cakes together with half the icing then cover the top with the remaining icing (add a splash of oat milk if the icing feels too firm). Scatter over the nuts and dust the cake with a little cinnamon and decorate with edible flowers.','35 mins','25 mins - 30 mins'),
(1266,'vegan cookies & cream cake','https://www.bbcgoodfood.com/recipes/vegan-cookies-cream-cake','https://images.immediate.co.uk/production/volatile/sites/30/2022/06/Vegan-cookies-and-cream-cake-da1e568.jpg','Make this delicious vegan cookies and cream cake for any children or adults with allergies or intolerances, or those following a dairy-free diet',6,16,1,2,18,'Oil a 20 x 20cm baking tin and line the base and sides with baking parchment. Heat the oven to 180C/ 160C fan/gas 4. Combine the oil, milk, vinegar, vanilla and yogurt in a jug. Mix the sugar, flour, cocoa powder, baking powder, bicarb and a pinch of salt together in a bowl. Pour the wet ingredients into the dry and mix until there are no pockets of flour remaining. Tip the mixture into prepared tin and level the surface with a spatula. Bake for 35 mins, or until a skewer inserted into the middle of the cake comes out clean. If any wet cake mixture clings to the skewer, return the cake to the oven for another 5 mins, then check again. Remove from the oven and leave to cool completely in the tin. Set half of the cookies aside for decorating later. Bash the rest with the end of a rolling pin or blitz in a food processor to chunky rubble. Beat the spread, vanilla and icing sugar together using an electric whisk until fluffy, then gently fold in the crushed cookies until combined. Put the cake on a board. Spread over the icing. Halve or crumble the rest of the cookies and use these to decorate. Cut into squares. Will keep in an airtight container for four days.','30 mins','35 mins'),
(1267,'vegan lemon cheesecake','https://www.bbcgoodfood.com/recipes/raw-lemon-cheesecake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raw-lemon-cheesecake-34c2ef7.jpg','An easy, no-cook cheesecake that''s dairy-free and gluten-free with just a little agave syrup to sweeten. This lusciously lemony vegan dessert will go down wonderfully',5,12,1,2,18,'Put the cashews in a large bowl, pour over boiling water and leave to soak for 1 hr. Meanwhile, blitz the ingredients for the base with a pinch of salt in a food processor. Grease a 23cm tart tin with coconut oil, then press the mix into the base and pop in the fridge to set (about 30 mins). Drain the cashews and tip into the cleaned out food processor. Add all the remaining topping ingredients, reserving a quarter of the lemon zest in damp kitchen paper to serve, then blitz until smooth. Spoon onto the base and put in the fridge to set completely (about 2 hrs). Just before serving, scatter over the reserved lemon zest.','15 mins','No Time'),
(1268,'vegan apple cake','https://www.bbcgoodfood.com/recipes/vegan-apple-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Vegan-apple-cake-c466e14.jpg','Make this apple and almond cake to fill your cake tin. It''s an ideal treat for anyone following a dairy-free or vegan diet, and is delicious for afternoon tea with a cuppa',14,8,1,2,18,'Use the spread to butter a 20cm deep cake tin and line with baking parchment. Heat the oven to 180C/160C fan/gas 4. Pour the oat milk into a jug, add the lemon juice and leave to sit for 5 mins at room temperature. Put the flour, both sugars and the baking powder in a large bowl, pour over the thickened oat milk mixture and almond extract, then add the butter and ½ tsp salt. Beat everything together using an electric whisk. Tip the apples into a second large bowl, sprinkle over the extra 1 tbsp flour and toss to coat. Fold the apples through the cake batter using a spatula, then spoon the mixture into the prepared tin and sprinkle over the almonds. Bake in the centre of the oven for 1 hr-1 hr 20 mins, or until golden and firm to the touch. Leave to cool for a few minutes in the tin. Serve warm, or turn out onto a wire rack and leave to cool completely. Once cool, will keep in an airtight container for up to three days.','20 mins','1 hr and 20 mins'),
(1269,'vegan ginger loaf cake','https://www.bbcgoodfood.com/recipes/vegan-ginger-loaf-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/09/Vegan-ginger-loaf-cake-72a0d19.jpg','Get set for autumn with this iced and spiced ginger loaf cake. It''s a real treat, whether you’re vegan or not',46,8,1,2,18,'Heat the oven to 200C/180C fan/gas 6. Oil a 900g loaf tin (ours was 19 x 9 x 6cm) and line with a long strip of baking parchment. Mix the flour, muscovado sugar, baking powder and ground ginger together in a large bowl. Tip in the oil, treacle, grated stem ginger and 170ml cold water, then beat together until smooth using a wooden spoon. Pour the cake mixture into the prepared tin and bake in the centre of the oven for 45-50 mins until a skewer inserted into the middle comes out clean. Leave to cool in the tin for 5 mins, then transfer to a wire rack to cool fully. Sieve the icing sugar into a bowl, add the ginger syrup and mix in just enough lemon juice to make a thick, pourable icing that can coat the back of a spoon. Put the cake on a plate or stand, remove the parchment and drizzle over the icing. Immediately scatter over the stem ginger. Cut the cake into thick slices to serve.','15 mins','45 mins - 50 mins'),
(1270,'vegan lemon cake','https://www.bbcgoodfood.com/recipes/vegan-lemon-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-lemon-cake-8726002.jpg','Try baking a vegan version of lemon cake. Light and zingy, it also works well if you replace the flour and baking powder with gluten-free alternatives',132,12,1,2,18,'Heat oven to 200C/180C fan/gas 6. Oil a 1lb loaf tin and line it with baking parchment. Mix the flour, sugar, baking powder and lemon zest in a bowl. Add the oil, lemon juice and 170ml cold water, then mix until smooth. Pour the mixture into the tin. Bake for 30 mins or until a skewer comes out clean. Cool in the tin for 10 mins, then remove and transfer the cake to a wire rack to cool fully. For the icing, sieve the icing sugar into a bowl. Mix in just enough lemon juice to make an icing thick enough to pour over the loaf (if you make the icing too thin, it will just run off the cake).','15 mins','30 mins'),
(1271,'unicorn biscuits','https://www.bbcgoodfood.com/recipes/unicorn-party-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/unicorn-biscuits-66a81b8.jpg','Swirly vanilla cookies, edged with sprinkles and stars – these unicorn-inspired biscuits are ideal for kids'' parties or as a fun home baking activity',29,20,1,2,23,'Rub the flour and butter together with your fingertips until it looks and feels like fresh breadcrumbs then add a pinch of salt. In another bowl, mix together the sugar, egg and vanilla extract then pour it over the butter and flour mixture. Gently knead it together then separate the dough into 2 equal blocks. Knead some pink food colouring into one of them and keep the other plain. Wrap both types of dough in sheets of cling film and chill in the fridge for 20-30 mins. Roll the plain dough out onto a lightly floured surface until it’s about 25cm long and 20cm wide. Do the same with the pink dough and lay one on top of the other. Lightly roll over the surface once or twice with your rolling pin just to press them together. Trim off all the edges so they''re straight then carefully roll them up from one of the short edges to make a tight spiral. Wrap tightly in cling film and chill for 1hr or overnight. Heat oven to 180C/160C fan/gas 4 and line 2 trays with baking parchment. Unwrap the dough, trim the end and cut the rest into 20 slices and lay them cut side down on your prepared baking tray. Bake for 15-17 mins or until ever so slightly golden at the very edges. Allow them to cool on the tray before transferring them to a wire rack to cool completely. Mix the icing sugar with enough lemon juice to make it the consistency of smooth peanut butter and pour the sprinkles into a shallow bowl or plate. Dip the outside edges of the biscuits into the icing (or spread it onto the edges using the back of a teaspoon) and then into the sprinkles, turning to coat. Leave to set before serving.','15 mins','30 mins'),
(1272,'birthday biscuits','https://www.bbcgoodfood.com/recipes/birthday-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-45893_12-5c70e1d.jpg','These ring biscuits are perfect for parties and work well in any shape, get the kids to help',5,24,1,2,23,'Heat oven to 180C/fan 160C/gas 4. Put the flour, sugar and butter into a food processor. Whizz until the mixture forms crumbs, then pulse a little more until it forms a ball. Spread a large sheet of baking parchment over the work surface and turn the dough onto it. Dust with a little flour, then roll out to about the thickness of two £1 coins. Stamp out 24 rounds using a 5cm round fluted cutter, then cut out the middles with the end of a piping nozzle. Carefully peel the rest of the mixture away from the stamped shapes. Lift onto baking sheets and bake for 10 mins until pale golden. Cool on a wire rack. Put the lemon curd into a bowl with 2 tbsp boiling water and mix until smooth. Sieve in 175g of the icing sugar; stir together to make a smooth icing. Put the strawberry fruit spread in another bowl and add 2 tsp boiling water, mix, then sieve in the remaining icing sugar. Spoon the lemon icing over the biscuits, then drizzle or pipe over the pinky icing. Leave to set for at least 20 mins. Will keep for up to 2 days in an airtight tin, longer if left un-iced.','30 mins','10 mins'),
(1273,'chocolate orange cookies','https://www.bbcgoodfood.com/recipes/chocolate-orange-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-orange-cookies-ca8f469.jpg','Top rich chocolate biscuits with candied orange peel to make these moreish bites. They make a great edible gift for Christmas ',19,25,1,2,23,'Beat the butter with an electric whisk until it is light and fluffy, then beat in the icing sugar a little at a time, adding the orange zest and 1 tbsp orange juice. Beat in the flour and cocoa a little at a time, then mix in the chocolate and orange peel and 1-2 tbsp milk. Divide the mixture into two, pat each into a rough log shape on a piece of non-stick wrap or baking parchment, then use the wrap to help you shape each piece into a log around 15cm long. Chill until firm, about 2 hrs (see tip, below). Heat the oven to 180C/160C fan/gas 4. Line two baking sheets. Cut ½cm-thick cookies from the dough and lay them on the baking sheets, leaving space so they can puff up and spread. Put a piece of sugared candied orange on top of each. Bake for 10-15 mins or until the cookies are puffed. Cool on the sheets for a couple of mins before sliding them onto a wire rack. Will keep for two to three days in an airtight container.','15 mins','15 mins'),
(1274,'easiest ever biscuits','https://www.bbcgoodfood.com/recipes/easiest-ever-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easiest-ever-biscuits-4192551.jpg','The simplest biscuits you and the kids will ever bake. We’ve stamped clean toys into these to decorate them – try your own favourite patterns',19,24,1,2,23,'Heat the oven to 200C/180C fan/gas 6 and line a baking sheet with baking parchment. Put the butter in a bowl and beat it with electric beaters until soft and creamy. Beat in the sugar, then the egg and vanilla, and finally the flour to make a dough. If the dough feels a bit sticky, add a little bit more flour and knead it in. Pull pieces off the dough and roll them out to about the thickness of two £1 coins on a floured surface. The easiest way to do this with small children is to roll the mixture out on a baking mat. Cut out shapes using a 9cm biscuit cutter, or a use the rim of a small glass and peel away the leftover dough around the edges. Press some clean toys gently into the biscuits, making sure you make enough of a mark without going all the way through. Re-roll off-cuts and repeat. Transfer the whole mat or the individual biscuits to the baking sheet and bake for 8-10 mins or until the edges are just brown. Leave to cool for 5 mins, then serve. Will keep for three days in a biscuit tin.','10 mins','20 mins'),
(1276,'peanut butter cookies','https://www.bbcgoodfood.com/recipes/peanut-butter-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/peanut-butter-cookies-8d07ccb.jpg','With just three ingredients, these simple peanut butter cookies will delight kids and grown-ups alike – and they''re gluten-free, too',6,16,1,2,23,'Heat oven to 180C/160C fan/gas 4 and line 2 large baking trays with baking parchment. Measure the peanut butter and sugar into a bowl. Add ¼ tsp fine table salt and mix well with a wooden spoon. Add the egg and mix again until the mixture forms a dough. Break off cherry tomato sized chunks of dough and place, well spaced apart, on the trays. Press the cookies down with the back of a fork to squash them a little. The cookies can now be frozen for 2 months, cook from frozen adding an extra min or 2 to the cooking time. Bake for 12 mins, until golden around the edges and paler in the centre. Cool on the trays for 10 mins, then transfer to a wire rack and cool completely. Store in a cookie jar for up to 3 days.','15 mins','12 mins'),
(1277,'jammy coconut mallows','https://www.bbcgoodfood.com/recipes/jammy-coconut-mallows','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-20830_12-6562c76.jpg','Kids will love to help sandwiching these biscuits with the melted marshmallow',33,36,1,2,23,'Heat oven to 190C/fan 170C/gas 5. Beat butter, sugar, egg and vanilla together with a pinch of salt until smooth. Fold in the flour and coconut to form a dough. On a floured surface, shape the dough into a round, then roll to the thickness of a £1 coin. Cut into rounds using a 6cm cutter. Lift onto baking sheets, then bake for 14 mins until light golden. Cool for a few mins, then transfer to a cooling rack. To sandwich the biscuits, lay half out on a baking sheet, under-side up. Put ½ tsp jam onto each one, top with a piece of marshmallow, then bake for 2½ mins or until just melted. Remove from oven, then quickly top with the other biscuits, pressing down so that the marshmallow sticks them together and just oozes out. Cool for 10 mins. Put the coconut and remaining jam onto plates, dip the edges in the jam, then roll in the coconut. Will keep in an airtight tin for up to 3 days.','30 mins','17 mins'),
(1278,'bunting biscuits','https://www.bbcgoodfood.com/recipes/bunting-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-967721_10-4f527ba.jpg','Add a touch of fun to your party with these edible flag-shaped cookies - ice in your favourite colours',17,24,2,2,23,'Put the flour, butter, bicarb and sugar in a food processor, whizz until you can’t see any lumps of butter, then tip into a mixing bowl. Whisk the egg, syrup and vanilla together, then stir into the bowl with a wooden spoon. Using your hands, knead together into a smooth dough. Heat oven to 200C/180C fan/gas 6. Cut a triangle template from card the size you want the bunting to be. Roll out dough on a lightly floured surface. Use template to cut out triangles – re-roll trimmings to get as many as you can. Line some baking sheets with baking parchment and lift on the biscuits. Use a pencil end to make 2 holes in the top of each one – not too close to the edge. Bake, one tray at a time, for 8-10 mins, remaking each hole when biscuits are just out and still soft. Cool. Un-iced biscuits can be frozen for up to 3 months, or will keep in an airtight container for a week. Divide the icing into as many colours as you want, and knead in food colourings to get your desired colours. Roll out thinly on a surface lightly dusted with icing sugar, and use your template to cut out icing triangles. Brush the backs of the icing with a little water and stick onto the biscuits – making holes in the icing to match the biscuits as you go. Cut out small circles from leftover icing, brush backs with water and stick onto some of the biscuits. Decorate with icing pens, if you wish, then carefully thread through ribbon or string and hang up your treats.','50 mins','8 mins - 10 mins'),
(1279,'spider biscuits','https://www.bbcgoodfood.com/recipes/spider-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spider-biscuits-49adf2a.jpg','Create these cute spider biscuits with kids as part of a Halloween party feast. Children will love adding the spooky chocolate spider legs and icing eyes',19,20,1,2,23,'Heat oven to 180C/160C fan/gas 4 and line two baking sheets with parchment. Using an electric hand whisk, cream the butter, peanut butter and sugar together until very light and fluffy, then beat in the egg and vanilla. Once combined, stir in the flour, bicarb and ¼ tsp salt. Scoop 18-20 tbsps of the mixture onto the trays, leaving enough space between each to allow for spreading. Make a thumbprint in the centre of the cookies. Bake for 10-12 mins or until firm at the edges but still soft in the middle – they’ll harden a little as they cool. Leave to cool on the tray for a few mins before topping each biscuit with a peanut butter cup, Rolo or Malteser. Transfer to a wire rack to cool completely. Heat the chocolate in the microwave in short bursts, or in a bowl set over a pan of simmering water, until just liquid. Scrape into a piping bag and leave to cool a little. Pipe the legs onto each spider, then stick two eyes on each. Leave to set. Will keep for three days in an airtight container.','25 mins','12 mins'),
(1280,'rainbow cookies','https://www.bbcgoodfood.com/recipes/rainbow-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-358511_12-b85142e.jpg','These colourful cookies are bound to be a big hit with kids and adults alike',17,22,1,2,23,'Heat oven to 200C/180C fan/gas 6. Mix the butter, sugars, egg yolks and vanilla with a wooden spoon until creamy, then mix in the flour in 2 batches. Stir in the orange zest. Roll the dough into about 22 walnut-size balls and sit on baking sheets. Bake for 15 mins until golden, then leave to cool. Meanwhile, mix the icing sugar with enough orange juice to make a thick, runny icing. Dip each biscuit half into the icing, then straight into the sprinkles. Dry on a wire rack.','25 mins - 30 mins','15 mins'),
(1282,'medal cookies','https://www.bbcgoodfood.com/recipes/medal-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1047512_10-c2c11f7.jpg','Make these round vanilla biscuits, ice in bright colours, thread on a ribbon and give out as edible prizes',6,15,1,2,23,'Heat oven to 200C/180C fan/gas 6. Line 2 baking trays with baking parchment. Melt the butter, sugar and syrup in a small saucepan. Mix in the vanilla, remove from the heat and leave to cool for 10 mins. Sieve the flour and bicarbonate of soda into a bowl. Pour in the melted butter mixture and the egg, and stir together to form a stiff-ish dough. Pop the dough in the fridge for 10 mins to chill and firm up. Roll the dough out on a floured surface to 5mm thick, then stamp out 15 circles using a 7.5cm round cookie cutter; you may need to re-roll the trimmings to get 15 cookies. Make a hole at the top of each circle with the end of a pencil. Place on the baking trays and bake for 12 mins until golden, swapping the trays around halfway through. Cool on a wire rack. Decorate the cookies (see below), making sure the hole is at the top of each one. Will keep for 3 days in an airtight container.','35 mins','15 mins'),
(1283,'easy chocolate chip cookies','https://www.bbcgoodfood.com/recipes/chocolate-chunk-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-chunk-cookies_0-6f27241.jpg','Learn how to make classic chocolate chip cookies and enjoy them still warm from the oven. Perfect for a bake sale or with your afternoon tea.',16,10,1,2,23,'Heat oven to 180C/160C fan/gas 4 and line two baking sheets with parchment. Cream the butter and sugars together until very light and fluffy, then beat in the egg and vanilla. Once combined, stir in the flour, bicarb, chocolate and ¼ tsp salt. Scoop 10 large tbsps of the mixture onto the trays, leaving enough space between each to allow for spreading. Bake for 10-12 mins or until firm at the edges but still soft in the middle – they will harden a little as they cool. Leave to cool on the tray for a few mins before eating warm, or transfer to a wire rack to cool completely. Will keep for three days in an airtight container.','20 mins','12 mins'),
(1284,'gingerbread stained glass biscuits','https://www.bbcgoodfood.com/recipes/gingerbread-stained-glass-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gingerbread-stained-glass-biscuits-220d688.jpg','Bake these pretty gingerbread biscuits with a stained glass centre as treats at Christmas time with the kids. They make great presents for friends and family',19,30,1,2,23,'Heat the sugar, golden syrup and butter in a pan until melted. Mix the ginger and flour in a large bowl and make a well in the centre. Add the bicarbonate of soda to the melted mixture and stir – it will fizz a little – then pour into the flour mixture with the egg. Stir to combine. The mix will be soft but will firm up as it cools. Scoop the mixture into a box or fridge bag and chill for at least 1 hr until firm enough to roll out. The dough can be kept in the fridge for up to a week or frozen for three months. Heat oven to 190C/170C fan/gas 5. Turn the dough out onto a lightly floured surface and cut in half. Briefly knead the first piece, then roll it on a lightly floured surface to 2mm thick. Cut into shapes with snowflake or star cutters about 12cm across, then transfer to lined baking sheets, leaving a little room for them to spread. Cut a window out of each biscuit using another cutter about about 6cm across, then add a sweet to the centre. If the sweets are large, chop them up first – you’ll have to judge by the size of the hole. (Don’t be tempted to add too much or it will spill over the edge.) If you plan to hang the biscuits, make a small hole in the top of each one using the end of a piping nozzle (the hole will close up a little so make sure it’s big enough). Repeat with remaining dough. Bake in batches for 5-6 mins or until they darken slightly and the sweets have melted. If the holes have closed up, remake them while the biscuits are warm. Leave to cool and harden up completely before moving them. Don’t forget to bake the parts you’ve cut out, too! You can decorate the biscuits further by using white piped icing, if you like.','40 mins','5 mins - 6 mins'),
(1285,'bourbon biscuits','https://www.bbcgoodfood.com/recipes/neapolitan-bourbons','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/neapolitan-bourbons-743c14b.jpg','Add some colour to classic bourbon biscuits using pink food colouring. Kids will love piping the filling – and eating them afterwards of course!',19,25,1,2,23,'Beat the butter and sugar together until creamy, then mix in the rest of the ingredients. Add a splash of milk if the mixture looks a bit dry – it should come together as a dough. Line three baking sheets, dust lightly with flour, then roll and pat one-third of the dough out to the thickness of a £1 coin on each. Cover and freeze for 15 mins. Heat oven to 180C/160C fan/gas 4. Slide the dough off the trays and put a new piece of parchment on each. Trim the edges of the dough to straighten, then cut into rectangles, roughly 6 x 3cm. Lift each one carefully onto the tray, leaving some space between them as they’ll expand. Bourbons usually have a pricked pattern, so use a cocktail stick to do this if you want (not too many or they’ll break up). Put the dough back in the freezer if it gets too soft. Bake for 8-10 mins, then leave to cool on the sheet as the biscuits will be soft when they’re hot. Sprinkle over some sugar. Will keep for a week in an airtight container. Meanwhile, make the filling. Beat the butter and icing sugar together, then divide the mixture into three. Add the cocoa to one lot, a dot of pink colour to another, and leave the last one plain (add a little more icing sugar if you need to). Spoon each into a piping bag or a sandwich bag with the corner snipped off. When the biscuits have cooled completely, pipe the icings onto half of them, then sandwich together with the other halves. Leave to set.','40 mins','10 mins'),
(1286,'chocolate & hazelnut thumbprint cookies','https://www.bbcgoodfood.com/recipes/chocolate-hazelnut-thumbprint-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-hazelnut-thumbprint-cookies_-06460f1.jpg','The food processor does most of the work in this cookie recipe, but younger children will enjoy rolling the dough into balls and thumbprinting. Older kids can make them on their own',75,25,1,2,23,'Line a baking tray with baking parchment. Tip the hazelnuts into the bowl of a food processor and pulse until finely chopped. Add the flours, sugar and a pinch of flaked sea salt, and process for 20-30 secs until fully combined. Add the butter and pulse until the mixture just starts to come together. Tip the dough out onto a work surface and knead by hand until smooth. Roll the dough into 25 small balls, then transfer to the prepared baking tray. Using your thumb or the handle of a wooden spoon, make an indent in the centre of each piece of dough. Put the tray in the fridge and chill for 30 mins before baking. Heat oven to 180C/160C fan/gas 4. Bake in the oven for 15-20 mins or until light golden brown. Put the chocolate and oil in a heatproof bowl and set over a pan of simmering water, stirring occasionally, until fully melted. Use a teaspoon to top each cookie with a little melted chocolate. Put aside until the chocolate has set.','20 mins','20 mins'),
(1287,'oaty energy cookies','https://www.bbcgoodfood.com/recipes/oaty-energy-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1020464_11-140dd56.jpg','Get your children in the kitchen to bake a batch of these simple biscuits from CBeebies’ ''i can cook''',5,6,1,2,23,'Heat oven to 150C/130C fan/gas 2. Line a baking tray with baking parchment. Using children’s scissors, cut the apricots into small pieces. Put the butter and sugar in a mixing bowl and beat well with a wooden spoon. Use very soft butter to make it easy for young wrists to beat the butter and sugar together. Add the condensed milk, beat well, then add the oats and apricots, and mix well again. Finally, add the flour and mix until it starts to disappear – you can use your hands to mix it, too. Bring the dough together into one big ball, then break into 6 equal-sized lumps (this ensures the cookies bake at the same time). Roll each into a ball, then squash onto the baking parchment with the palm of your hand. Bake for 25-30 mins until golden on the edges. Leave to cool on the tray. The cookies will keep in an airtight container for up to 3 days.','10 mins','30 mins'),
(1288,'vintage chocolate chip cookies','https://www.bbcgoodfood.com/recipes/vintage-chocolate-chip-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1079477_11-e712431.jpg','An easy chocolate chip cookie recipe for soft biscuits with a squidgy middle that will impress family and friends. Make plenty as they''re sure to be a hit',94,30,1,2,23,'Heat the oven to 190C/fan170C/gas 5 and line two baking sheets with non-stick baking paper. Put 150g softened salted butter, 80g light brown muscovado sugar and 80g granulated sugar into a bowl and beat until creamy. Beat in 2 tsp vanilla extract and 1 large egg. Sift 225g plain flour, ½ tsp bicarbonate of soda and ¼ tsp salt into the bowl and mix it in with a wooden spoon. Add 200g plain chocolate chips or chunks and stir well. Use a teaspoon to make small scoops of the mixture, spacing them well apart on the baking trays. This mixture should make about 30 cookies. Bake for 8–10 mins until they are light brown on the edges and still slightly soft in the centre if you press them. Leave on the tray for a couple of mins to set and then lift onto a cooling rack.','15 mins','10 mins'),
(1289,'vegan cranberry & orange cookies','https://www.bbcgoodfood.com/recipes/vegan-cranberry-orange-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Vegan-cranberry-and-orange-cookies-b06ed7d.jpg','Make some cranberry and orange vegan cookies for your Christmas biscuit tin. They’re made with jumbo oats, orange zest, dried cranberries and cinnamon',133,12,1,2,23,'Heat the oven to 190C/170C fan/ gas 5 and line two baking trays with baking parchment. Whisk the oil together with all the sugar, the vanilla extract and 2 tbsp water in a large bowl until smooth. Stir in the orange zest. In a separate bowl, combine the flour, oats, baking powder, bicarb, cinnamon and 1/2 tsp sea salt flakes. Tip the dry ingredients into the wet, and mix until you have a thick dough. Stir in the cranberries. Form the dough into 12 evenly sized balls. Arrange on the baking trays, well spaced apart. Flatten slightly with your palm, then bake for 13-15 mins until the edges are set. Leave to cool for 15 mins on the trays before transferring to a wire rack to cool completely. Will keep in an airtight container for three days.','10 mins','15 mins'),
(1290,'tahini & white chocolate cookies','https://www.bbcgoodfood.com/recipes/tahini-white-chocolate-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2023/03/Tahini-and-white-chocolate-cookies-b7b8be2.jpg','Fill your biscuit tin with these sweet and nutty cookies, made with tahini, white chocolate chunks and black sesame seeds. They''re perfect served with a cuppa',15,18,1,2,23,'Beat the butter and tahini until soft and combined in a stand mixer or using an electric whisk. Tip in the sugars and beat again until light and fluffy, about 5 mins. Crack in the eggs, one by one, beating well after each addition. Mix in the vanilla, flour and bicarbonate of soda until no pockets of flour remain. Stir in the chocolate, then cover the bowl and chill overnight (for at least 18 hrs). Will keep chilled for up to three days. Or, roll into a log or balls and freeze for up to three months. Heat the oven to 180C/160C fan/gas 4 and line two large baking trays with baking parchment. Roll the dough into cherry-tomato-sized balls, about 50g each. Arrange on the baking trays, spaced well apart, and press down on each one lightly with the palm of your hand to flatten them slightly. Sprinkle over the sesame seeds and a pinch of sea salt flakes. Bake for 12-14 mins until the edges are golden and set, and the middle is still gooey. Leave to cool completely on the trays before serving. Will keep in an airtight container for three days.','15 mins','30 mins'),
(1291,'double-dipped shortbread cookies','https://www.bbcgoodfood.com/recipes/double-dipped-shortbread-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/double-dipped-shortbread-cookies-dbc1b36.jpg','What could be better than shortbread biscuits? Shortbread double-dipped in milk and white chocolate! They''re sure to go down a storm with family and friends',19,15,1,2,23,'Heat the oven to 180C/160C fan/gas 4. Beat the butter with the icing sugar using an electric whisk until the mixture is light and fluffy. Beat in the vanilla and flour (the mixture will stiffen and look like crumble as the flour is added). Add the milk and keep beating until the mixture softens and sticks together (add some more milk if needed). Scoop the dough into a piping bag fitted with a large star nozzle and pipe swirled rings onto a baking sheet lined with baking parchment. If the mixture is too stiff to pipe easily, you can roll the dough into balls and put them on the sheet instead. The cookies will spread as they cook so don’t worry if there’s a small gap in the centre of the piped swirls, but ensure there is enough space between each cookie so they don’t stick together as they cook. Bake for 15 mins, or until lightly golden, then transfer to a wire rack to cool. Put the white and milk chocolate in separate bowls and microwave each in 20-second bursts until melted. Dip the cookies into each chocolate, milk at one end and white on the other, then let any excess drip off before returning them to the rack to set. Will keep in an airtight tin for up to four days.','20 mins','30 mins'),
(1292,'gingerbread cookies','https://www.bbcgoodfood.com/recipes/gingerbread-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gingerbread_cookies-ec3acde.jpg','Put your feet up and enjoy these moreish gingerbread cookies with a cuppa, made with aromatic cloves, ginger, cinnamon and rich black treacle',14,20,1,2,23,'Beat together the butter, treacle, brown sugar and salt in a large mixing bowl. Add the egg, then beat through all of the remaining dry ingredients apart from the caster sugar. Chill the mixture in the fridge for 1 hr. Heat the oven to 200C/180C fan/gas 4. Line two baking sheets with baking parchment. Roll the mixture into 20 even-sized balls (weighing for accuracy, if you like). Tip the caster sugar onto a small plate, then add each ball and roll around to coat. Space each ball out on the baking sheets. Bake for 9-10 mins until golden brown. Leave to cool completely on a wire rack.','20 mins','12 mins'),
(1293,'festive cookies for the freezer','https://www.bbcgoodfood.com/recipes/festive-cookies-for-the-freezer','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Cookies-for-the-freezer-fbd5010.jpg','Make this uncooked dough as a gift for the future. It can be kept frozen for up to three months, ready to be sliced and baked for a welcome sweet treat',86,30,1,2,23,'Beat the butter and sugar together with an electric whisk until light and fluffy. Add the condensed milk and vanilla and beat again for 1 min until just combined. Mix the flour, baking powder and cinnamon together. Tip the mixture into the wet ingredients and stir together to make a dough. Gently knead in the cranberries and white chocolate chunks. Divide the dough in half and roll each piece into a 4cm-thick log. Wrap each log in a double layer of baking parchment. Tie the ends with ribbon as you would a Christmas cracker, then freeze for at least 24 hrs until solid. Will keep frozen for three months. Once frozen, it’s important to keep it frozen until baked. To keep it frozen on a short journey to the giftee’s home, pack in an ice box or cool bag with freezer blocks. Write the following instructions on a card for the giftee: Heat the oven to 180C/160C fan/gas 4. Slice the dough into 1cm-thick rounds and arrange on a non-stick baking sheet, leaving room between each for the biscuits to spread. Bake for 12 mins until golden at the edges. Leave to cool for 5 mins, then lift onto a wire rack to cool completely.','15 mins','12 mins'),
(1294,'christmas crinkle cookies','https://www.bbcgoodfood.com/recipes/christmas-crinkle-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/09/Crinkle-cookies-d9f120f.jpg','Love crinkle cookies? Try our extra-festive version for Christmas, with orange zest, mixed spice and cinnamon – and not forgetting the classic fudgy centre',46,30,1,2,23,'Mix the cocoa, caster sugar and oil together. Add the eggs one at a time, whisking until fully combined. Combine the flour, baking powder, orange zest, mixed spice, cinnamon and a pinch of salt in a separate bowl, then add to the cocoa mixture and mix until a soft dough forms. If it feels too soft, put in the fridge to chill for 1 hr. Heat the oven to 190C/170C fan/gas 5 and tip the icing sugar into a shallow dish. Roll heaped teaspoons of the dough into balls (about 20g each), then roll in the icing sugar to coat. Put the balls on one large or two medium baking trays lined with baking parchment, ensuring they’re evenly spaced apart. Bake on the middle rack of the oven for 10 mins, then transfer to a wire rack to cool – they will firm up as they cool, but still be fudgy in the centre. Will keep for up to four days in an airtight container.','20 mins','10 mins'),
(1295,'next level chocolate chip cookies','https://www.bbcgoodfood.com/recipes/next-level-chocolate-chip-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/choc-chip-cookies-3d801c0.jpg','Forget brittle packet versions and make these ultimate choc chip cookies. Expect a crisp exterior, chewy middle and chunks of gooey dark chocolate',7,12,2,2,23,'Melt the butter in a small saucepan until sizzling. Watch it carefully – the butter will foam, and, when the foam subsides, it will turn from yellow to a hazelnut brown. At this point, remove from the heat, tip into a bowl and leave to cool. Meanwhile, mix the flours, salt, baking powder and bicarb together in a bowl and set aside. When the butter is cool, tip in the sugars and beat with a wooden spoon to combine. Add the egg, yolks and vanilla and beat together. Scrape the wet mixture into the dry ingredients and stir until it all comes together into a dough. Fold in the chocolate, then chill for at least 3 hrs, or until firm. Will keep chilled for up to 24 hrs. Heat the oven to 180C/160C fan/gas 4. Line two large baking sheets with baking parchment. Separate the dough into 12 pieces, weighing for accuracy if you like. Roll each piece into a ball using your hands, then put six balls on each sheet, leaving space between them for spreading. Bake for 10 mins, then, one sheet at a time, remove from the oven, sprinkle with some sea salt and slam the sheet hard against your work surface. Bake for another 3 mins, then repeat the slamming of the sheet. Bake for 3 mins more. This timing will give you soft cookies, but if you prefer them a bit firmer, cook for a few minutes longer. Leave to cool on the sheets for 10 mins before transferring to a wire rack to cool completely.','20 mins','20 mins'),
(1296,'double ginger cookies','https://www.bbcgoodfood.com/recipes/double-ginger-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/double-ginger-cookies-f9444b7.jpg','If you love ginger nuts, you''ll love these American-style spiced biscuits with chunks of stem ginger, dipped in dark chocolate',4,24,1,2,23,'Mix the flour, ground ginger, bicarbonate of soda, 1/2 tsp salt and sugar in a bowl, then rub in the butter to make crumbs. Stir in the chopped stem ginger. Beat together the egg and syrup, pour into the dry ingredients and stir, then knead with your hands to make a dough. Cut the dough in half and shape each piece into a thick sausage about 6cm across, making sure that the ends are straight. Wrap in cling film and chill for 20 mins. You can now freeze all or part of the dough for 2 months. Heat oven to 180C/160C fan/gas 4 and line 2 baking sheets with baking parchment. Thickly slice each sausage into 12 and put the slices on the baking sheets, spacing them well apart and reshaping any, if necessary, to make rounds. Bake for 12 mins, then leave to cool for a few mins to harden before transferring to a wire rack to cool completely. Melt the chocolate in a bowl over a pan of gently simmering water, making sure that the water isn’t touching the bottom of the bowl. Dip half of each cookie into the chocolate – you may need to spoon it over when you get to the final few. Decorate with a slice of ginger, if you like, and leave to set. Will keep for 1 week in an airtight container.','20 mins','12 mins'),
(1321,'banoffee s''mores','https://www.bbcgoodfood.com/recipes/banoffee-smores','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banoffee-smores_0-3286bd2.jpg','We love a classic American-style melty marshmallow s''more, so we created our own banana and toffee version with caramel sauce and chocolate oat biscuits',1,8,1,2,23,'Preheat the grill to high and line a baking sheet with parchment. Put 8 Hobnobs on the tray with a marshmallow on top and grill until slightly brown and melting. Put a tsp of dulce de leche on the remaining 8 Hobnobs and top with 2 banana slices. Sandwich the biscuits together.','3 mins','2 mins'),
(1297,'chilli chocolate cookies','https://www.bbcgoodfood.com/recipes/chilli-chocolate-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1286511_7-64c04e4.jpg','A big pile of spiced double chocolate chip biscuits next to a tub of ice cream is an easy way to feed a crowd',5,40,1,2,23,'Beat the butter and sugars together, then gradually mix in the vanilla and eggs. In another bowl, combine the flour, cocoa, a pinch of salt, baking powder and cayenne pepper, then mix these into the butter and fold in all the chocolate. Heat oven to 180C/160C fan/gas 4. Place walnut-sized spoonfuls of the mixture on baking sheets lined with baking parchment, leaving enough space between so they don’t melt together. Bake in batches, for 10-12 mins, depending on how gooey you like your cookies. Allow to cool on the baking sheets for 1 min before removing to a plate to cool completely. Serve with vanilla ice cream.','20 mins','12 mins'),
(1298,'double choc peanut butter cookies','https://www.bbcgoodfood.com/recipes/double-choc-peanut-butter-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/double-choc-peanut-butter-cookies-36993db.jpg','What could be better with a glass of cold milk than a warm, gooey peanut butter cookie? These choc chunk treats are topped with crunchy chopped peanuts',16,12,1,2,23,'Heat the oven to 180c/160 fan/gas mark 4 and line two baking trays with parchment. Using a food mixer or electric whisk beat the butter and sugar together until light and fluffy. Add in the egg and whisk to combine then beat in the flour, cocoa powder, salt and chocolate chunks until fully incorporated. Using a spoon swirl the peanut butter through the cookie dough. Scoop the dough into 12 large cookies onto the two trays using a dessert spoon, leaving plenty of room between each cookie as they''ll spread. Bake in the oven for 9-10 mins until still soft and melty in the middle. They will look underbaked but will harden once cool. Drizzle over the melted milk chocolate and top with a few chopped peanuts and a pinch of flaky sea salt. Serve with a glass of milk for dunking.','20 mins','10 mins'),
(1299,'basic cookies','https://www.bbcgoodfood.com/recipes/basic-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/basic-cookies-0e36dfd.jpg','Bake a batch of these easy cookies with just five ingredients – or fewer. A perfect partner to a cup of tea, you can whip them up in no time',134,25,1,2,23,'Heat the oven to 190C/170C fan/gas 5. Cream the butter in a large bowl with a wooden spoon or in a stand mixer until it is soft. Add the sugar and keep beating until the mixture is light and fluffy. Sift in the flour and add the optional ingredients, if you’re using them. Bring the mixture together with your hands in a figure-of-eight motion until it forms a dough. You can freeze the dough at this point. Roll the dough into walnut-sized balls and place them slightly apart from each other on a baking sheet (you don’t need to butter or line it). Flatten the balls a little with the palm of your hand and bake them in the oven for around 10-12 mins until they are golden brown and slightly firm on top. Leave the cookies on a cooling rack for around 15 mins before serving.','20 mins','12 mins'),
(1300,'cookie dough pizza','https://www.bbcgoodfood.com/recipes/cookie-dough-pizza','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cookie-dough-pizza-a872d07.jpg','We''ve seen pizza in all sorts of varieties, but this has to be our favourite. Top your cookie dough pizza base with any treats that take your fancy',1,15,1,2,23,'Heat the oven to 180C/160 fan/ gas 4. Beat the butter and sugars together in a large bowl using an electric whisk or table top mixer. Pour in the beaten eggs and vanilla, beating well until combined. Sift in the flour and salt and fold until evenly combined. Scatter over the chocolate chips and stir until they are rippled throughout the dough. Roll into a big round cookie and place onto a large baking sheet covered with parchment. If the dough feels very soft at this stage pop it in the fridge for 30 mins to firm up. Bake in the preheated oven for 25 mins until golden at the edges and still a little wobbly in the middle. Leave to cool for a couple of mins before topping. Spread the melted chocolate over the cookie, sprinkle with giant chocolate buttons, mini marshmallows and strawberries. Cut into about 15 slices and serve while it is still a little warm.','15 mins','25 mins'),
(1301,'shortbread biscuits','https://www.bbcgoodfood.com/recipes/shortbread-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/shortbread-7ed3e1a.jpg','Make these moreish shortbread biscuits using just three ingredients. They''re wonderfully crumbly and go perfectly with a cup of tea for a mid-morning treat',5,20,1,2,23,'Heat the oven 170C/150C fan/gas 3. Put the flour, butter and sugar into a mixing bowl. Use your hands to combine the ingredients until the mixture looks like breadcrumbs, then squeeze until it comes together as a dough. On a lightly floured surface, use a rolling pin to roll out the dough to ½ cm thick. Cut the dough into fingers and place on a lined baking tray. Use a fork to create imprints, then sprinkle with the remaining caster sugar. Chill the dough in the fridge for 20 mins, then bake for 15-20 mins until golden brown. Remove the shortbread fingers from the oven and leave to cool on the tray for 10 mins.','20 mins','15 mins'),
(1303,'ricciarelli biscuits','https://www.bbcgoodfood.com/recipes/ricciarelli-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Ricciarelli-0ddad78.jpg','Rustle up these Italian-inspired almond biscuits. Gift to family and friends at Christmas, or make a batch for your own biscuit tin as a treat',133,20,2,2,23,' Whisk the egg whites to stiff peaks in a clean bowl, then whisk in the almond extract and lemon zest. In a separate bowl, sift together the icing sugar, ground almonds and a pinch of salt. Gently fold this into the whisked egg whites until it all comes together into a dough. Cover and chill for at least 4 hrs, or ideally overnight. Heat the oven to 190C/170C fan/ gas 5. Turn the dough out onto a work surface generously dusted with icing sugar. Roll into a long, fat sausage (about 40cm long) and cut 2cm-thick slices down the length (you should get about 20). Shape each slice into an oval and pat down the tops a little – the biscuits should be about 4-5cm wide and 5-6cm long. Arrange on a baking tray lined with baking parchment and dust with more icing sugar. Bake for 20-23 mins until the biscuits are just turning brown. They will be soft, but will firm up as they cool. Leave to cool completely on the tray. Will keep in an airtight container for up to three days.','15 mins','25 mins'),
(1304,'forgotten cookies','https://www.bbcgoodfood.com/recipes/forgotten-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-45829_11-c68b28c.jpg','What''s in a name? Pop these in the oven, forget about them overnight, and you''ll have crumbly cookies in the morning',5,32,1,2,23,'Preheat the oven to 180C/gas 4/fan 160C. Line two baking sheets with foil to cover. In a large clean bowl, whisk the egg whites with a pinch of salt until stiff and dry. Gradually whisk in the sugar a little at a time to make a thick and glossy meringue. Tip in the pecan nuts and chocolate, then the vanilla extract and gently fold into the meringue with a large metal spoon. Spoon heaped teaspoonfuls of the meringue mixture, spaced apart, on to the lined baking sheets. Put the sheets in the oven, then turn it off and leave the cookies for at least 3 hours, overnight or until the oven is cold. Carefully peel the cookies from the foil. Store in an airtight tin where they will keep for 3-4 days.','20 mins - 30 mins','No Time'),
(1305,'hanukkah biscuits','https://www.bbcgoodfood.com/recipes/hanukkah-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-916451_11-48f1e17.jpg','Good Food reader Danielle Brown shares her grandmother''s recipe for savoury Kaak biscuits, traditionally eaten during the Jewish holiday of Hanukkah ',5,12,1,2,23,'Place the flour, ½ tsp salt, caraway seeds and yeast in a large bowl. Make a well in the centre and pour in the melted margarine and 200ml warm water. Mix to combine all of the ingredients, adding more water or flour if needed, then knead for 2 mins to form a soft dough. Place in an oiled bowl, cover with cling film and leave in a warm place to rise for 1 hr. Once doubled in size, knead the dough for 1 min, then divide the mixture into 12 balls. Cut each ball in half and roll each piece into a thin sausage shape. Twist the 2 pieces together, then join the ends to form a circle. Place on a lightly greased baking tray, cover with a tea towel and leave to rise again for 30 mins. Heat oven to 200C/180C fan/gas 5. Brush the biscuits with a little beaten egg and bake for 10-15 mins until golden. Remove to cool on a wire rack, then turn the oven down to 110C/90C fan/gas 1⁄4. Once cool, return the biscuits to the oven and bake for a further 10 mins, then turn off the heat completely, leaving the biscuits in the oven to dry out for 2 hrs.','30 mins','25 mins'),
(1306,'little almond cookies','https://www.bbcgoodfood.com/recipes/little-almond-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-75479_11-43336a9.jpg','These delicate snow-dusted Mexican cookies are rich, crumbly and melt in the mouth',32,40,1,2,23,'Heat oven to 170C/150C fan/gas 3 and roast the almonds for 5-10 mins until they are a pale golden colour. Cream together the butter, sugar, rum and a pinch of salt, then add the flour until the dough just comes together – it will be soft. Roughly chop the almonds and add them to the dough. Carefully roll out the dough on a well-floured surface to a 4-5mm thickness and cut out small cookies roughly 3-4cm across. Freeze the cookies on a baking sheet for 20 mins to harden. Bake the cookies in the oven for about 20 mins until just set – they will still be very pale. Lay out on a wire rack and dust icing sugar on both sides while they are still warm, being careful not to break them as you turn them. The icing sugar will melt. When they are cool, sprinkle them extremely generously with more icing sugar so that they are completely white. Store in an airtight container for up to 2 days, or freeze for up to 2 months.','35 mins','30 mins'),
(1307,'double chocolate shortbreads','https://www.bbcgoodfood.com/recipes/double-chocolate-shortbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-613654_11-699fca6.jpg','You''re only five ingredients away from a double chocolate melt-in-your-mouth delight',7,10,1,2,23,'Mix the butter and sugar together with a wooden spoon. Stir in the flour and cocoa, followed by the chocolate chips – you’ll probably need to mix it together with your hands at this stage. Halve the dough and roll each piece into a log about 5cm thick. Wrap in cling film and chill for 1 hr or for several days. Can be frozen for up to 1 month. Heat oven to 180C/160C fan/gas 4. Slice logs into 1cm-thick rounds, transfer to a baking tray lined with baking parchment and bake for 10-12 mins. Cool on the tray.','10 mins','12 mins'),
(1322,'vegan sticky toffee pear pudding','https://www.bbcgoodfood.com/recipes/sticky-toffee-pear-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sticky-toffee-pear-pudding-9f4408b.jpg','A lighter version of sticky toffee pudding, rich with dates and spices, and the juicy texture of poached pears, this vegan dessert is sure to please a crowd',6,8,2,2,25,'Peel the pears and cut the bottom off each to give a flat base – cut them to a height that will fit snugly in your tin. Use a melon baller or small knife to cut out the pips from the base. Roughly chop the pear scraps, discarding the pips, and set aside. Tip the sugar, cinnamon, star anise, cloves, zests and 600ml water into a saucepan large enough to fit all the pears. Bring to the boil, then simmer until the sugar has dissolved. Add the pears, cover with a lid or a piece of baking parchment, and poach gently for 15 mins until a knife easily slides into a pear. Leave to cool in the liquid. Now make the sponge. Put the dates and linseeds in a saucepan and add the almond milk. Bring to a gentle simmer, then cook for 2-3 mins until the dates are soft. Pour into a food processer and blitz until smooth. Add the oil and blend again, then scrape into a bowl and set aside to cool a little. Heat oven to 180C/160C fan/gas 4. Grease and line a 20 x 30cm baking tin (a loose -bottomed one if possible) with a strip of baking parchment. Put the dry ingredients in a large mixing bowl with 1/ 2 tsp salt. Mix well, breaking up any lumps of sugar with your fingers, and shaking the bowl a few times to encourage any remaining lumps to come to the surface. Add the date and oil mixture, and stir well. Fold in the chopped pear scraps. Scrape the cake mixture into the tin, then nestle in the pears, standing straight up, so that the bottom halves are covered. Bake for 35-40 mins until the cake is cooked through. Insert a skewer to the centre to check – it should come out clean. If there is any wet cake mixture on the skewer, return the cake to the oven and bake for 10 mins more, then check again. Meanwhile, bring the pear poaching liquid back to the boil and simmer until reduced to a glossy syrup. When the pudding is cooked, cool for 5-10 mins, then brush all over with the syrup, saving a little extra to serve alongside, with vegan ice cream, if you like.','25 mins','50 mins'),
(1308,'sugar-dusted vanilla shortbread','https://www.bbcgoodfood.com/recipes/sugar-dusted-vanilla-shortbread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-286_13-f362659.jpg','Great for keeping the biscuit tin topped up over Christmas, these sweet shortbread thins make a perfect treat',5,35,1,2,23,'Tip the flour into a food processor. Cut the butter into small pieces and drop them into the bowl, then whizz until the mixture looks like breadcrumbs. Add the sugar, vanilla and egg yolks and whizz to a smooth dough. With your hands, roll the dough on a lightly floured surface into a sausage shape about 25cm/9in long and 5cm/2½in in diameter. Wrap the roll and chill for at least 1 hour. (The roll can be frozen for up to 6 weeks. To use, remove from the freezer and allow to thaw for one hour at room temperature so that the dough is soft enough to be sliced into biscuits.) Preheat the oven to 180C/gas 4/fan 160C and lightly grease 2 large baking sheets. Using a sharp knife, cut the dough into slices, each a generous 5mm/¼in thick, then arrange them on the greased baking sheets, spacing the biscuits slightly apart so they have a bit of room to spread as they cook. Bake for 20 minutes until the biscuits are just turning pale golden around the edges, then transfer to a wire rack to cool. Dust generously with icing sugar. The biscuits will keep fresh for up to one week stored in an airtight tin.','20 mins','No Time'),
(1309,'vanilla shortbread dough','https://www.bbcgoodfood.com/recipes/vanilla-shortbread-dough','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-365_11-9eb6be2.jpg','Use this dough as the base for biscuits, to eat yourself, or to make as a Christmas gift',5,16,1,2,23,'Tip the flour into a food processor. Cut the butter into small pieces and drop them into the bowl, then whizz until the mixture looks like breadcrumbs. Add the sugar, vanilla and egg yolks and whizz to a small dough. Tip the dough onto a sheet of non-stick baking parchment and use the paper to roll the dough into a log shape, twisting the ends like a cracker. Chill for 3 hours or overnight. Heat oven to 180C/160C fan. Slice the log into 1cm-thick rounds and arrange on a baking sheet leaving gaps between them. Bake for 15-20 mins until golden. Leave to firm on baking sheet for 3-4 mins then transfer to a wire rack.','10 mins - 15 mins','N/A'),
(1310,'lemon & ginger shortbread','https://www.bbcgoodfood.com/recipes/lemon-ginger-shortbread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lemon-ginger-shortbread-c963750.jpg','This zesty teatime shortbread from BBC Good Food reader Jo Foster has a beautiful crumbly texture, plus zingy lemon and warming ginger flavour',5,12,1,2,23,'Heat oven to 150C/130C fan/gas 2 and grease a 22cm loose-bottomed fluted tart tin. Put the butter in a bowl and beat with a wooden spoon until soft. Beat in the sugar, then stir in the lemon zest and ginger. Stir in the flour and work with your hands to form a soft dough. Tip into the tin and press into an even, flat layer with your fingers. Prick all over with a fork and bake in the oven for 40 mins until pale gold. Cut into wedges then leave to cool completely in the tin. Remove from the tin and dust with caster sugar to serve.','10 mins','40 mins'),
(1311,'coconut & chocolate macaroons','https://www.bbcgoodfood.com/recipes/coconut-chocolate-macaroons','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5097_11-6d02b4e.jpg','A delicious macaroon with the tropical taste of fresh coconut',7,12,2,2,23,'Heat the oven to 180C/fan 160C/gas 4. In a clean bowl, whisk the egg whites until stiff then gradually add the sugar, whisking continuously until thick and glossy. Sift in the flour, then fold into the egg whites with the coconut until completely combined. Using an 8cm pastry cutter, squash spoonfuls of the mixture onto a baking tray lined with non-stick baking paper or silicone plastic sheets – you may need to do this in two batches. Bake for 15-18 mins until golden around the edges and just starting to brown on top. Leave to cool, then transfer to a rack. While the macaroons are cooling, melt the chocolate in a microwave or over a pan of hot water and leave to cool slightly. Cover the smooth side with chocolate and leave to set in the fridge. The macaroons will keep in an airtight container for 2 days.','30 mins','20 mins'),
(1312,'shortbread recipe','https://www.bbcgoodfood.com/recipes/shortbread-recipe','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-46789_12-e43670b.jpg','Bake these classic shortbread biscuits to wow a crowd. You only need four ingredients, but you can mix it up with lemon or orange zest, or try adding chopped pistachios',8,24,1,2,23,'Place the butter and 140g sugar in a food processor and whizz until smooth. Tip in both the flours and a pinch of salt, then whizz until mixture comes together. Using your hands, roughly spread the mixture out in a 20 x 30 x 4cm baking tray. Cover with cling film and smooth over until there are no wrinkles. Place in the fridge, uncooked, for at least 30 mins and up to 2 days. Heat oven to 180C/160C fan/gas 4. Remove cling film, then lightly mark the shortbread all over with a fork. Sprinkle with the remaining sugar, then bake for 20-25 mins. Leave to cool in the tin, then cut into 24 thin slices. Shortbread will keep in an airtight container for up to 1 week.','15 mins','20 mins - 25 mins'),
(1313,'anzac biscuits','https://www.bbcgoodfood.com/recipes/anzac-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259668_7-494e4d6.jpg','These iconic biscuits were originally made to send to the ANZACs (Australian and New Zealand Army Corps) serving in Gallipoli',5,20,1,2,23,'Heat oven to 180C/fan 160C/gas 4. Put the oats, coconut, flour and sugar in a bowl. Melt the butter in a small pan and stir in the golden syrup. Add the bicarbonate of soda to 2 tbsp boiling water, then stir into the golden syrup and butter mixture. Make a well in the middle of the dry ingredients and pour in the butter and golden syrup mixture. Stir gently to incorporate the dry ingredients. Put dessertspoonfuls of the mixture on to buttered baking sheets, about 2.5cm/1in apart to allow room for spreading. Bake in batches for 8-10 mins until golden. Transfer to a wire rack to cool.','35 mins','No Time'),
(1314,'fruity flapjack cookies','https://www.bbcgoodfood.com/recipes/fruity-flapjack-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2021/09/Fruity-flapjack-cookies-173ad8f.jpg','Toast oats before mixing them into the dough to make these fruity cookies, a delicious hybrid of syrupy flapjacks and coconut macaroons',7,12,1,2,23,'Heat the oven to 180C/160C fan/gas 4. Scatter the oats over a baking tray and bake for 20 mins, turning once until lightly browned. Remove from the oven and set aside to cool. Tip the butter and sugar into a bowl and beat with an electric whisk for 2 mins until fluffy. Crack in the egg and add the golden syrup and vanilla, then beat until completely combined. Scatter in the flour, all the dried fruit, the coconut, cinnamon (if using), the toasted oats and a pinch of salt. Beat again until everything is combined and you have a thick dough. Use the dough straightaway or cover and chill for up to two days. Line two baking sheets with baking parchment and arrange six large spoonfuls of dough on each, well- spaced apart. Bake for 15 mins (or 18-20 mins if the dough is fridge-cold) until the cookies have spread and are brown at the edges but soft in the middle. Leave to cool on the baking sheet for 5 mins, then transfer to a wire rack and leave to cool completely. Will keep in an airtight container or tin for up to five days.','20 mins','40 mins'),
(1315,'freezer biscuits','https://www.bbcgoodfood.com/recipes/freezer-biscuits','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-559569_11-7f6dc8b.jpg','Everyone needs a treat now and again! These oat-packed biscuits are designed to be stored in the freezer – simply bake when the mood strikes',8,30,1,2,23,'When the butter is really soft, tip it into a bowl along with the sugar. Using an electric hand whisk or exercising some arm muscle, beat together until the sugar is mixed through. Beat in the eggs, one at a time, followed by the vanilla extract and a pinch of salt, if you like. Stir in the flour and oats. The mixture will be quite stiff at this point. Now decide what else you would like to add – any or all of the flavours are delicious – and stir through. Tear off an A4-size sheet of greaseproof paper. Pile up half the mixture in the middle of the sheet, then use a spoon to thickly spread the mixture along the centre of the paper. Pull over one edge of paper and roll up until you get a tight cylinder. If you have problems getting it smooth, then roll as you would a rolling pin along a kitchen surface. You’ll need it to be about the width of a teacup. When it is tightly wrapped, twist up the ends and then place in the freezer. Can be frozen for up to 3 months. To cook, heat oven to 180C/fan 160C/gas 4 and unwrap the frozen biscuit mix. Using a sharp knife, cut off a disk about ½cm wide. If you have difficulty slicing through, dip the knife into a cup of hot water. Cut off as many biscuits as you need, then pop the mix back into the freezer for another time. Place on a baking sheet, spacing them widely apart as the mixture will spread when cooking, then cook for 15 mins until the tops are golden brown. Leave to cool for at least 5 mins before eating.','15 mins','15 mins'),
(1316,'bumper oat cookies','https://www.bbcgoodfood.com/recipes/bumper-oat-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-45809_12-2d72a47.jpg','Oaty, chewy, spicy cookies - ready in under an hour and great for tea time',5,18,1,2,23,'Heat the oven to 180C/fan160C/gas 4. Line several baking sheets with baking parchment or non-stick sheets. Warm the butter, sugar and golden syrup in a large saucepan over a medium heat until the butter has melted. Stir in the flour, bicarbonate of soda, oats, cinnamon, dried fruits and ginger, then the water and finally the egg. Leave to cool until easy to handle. With dampened hands, shape the mixture into 18 large balls, then flatten them onto the baking sheets – allowing plenty of space for spreading – and bake for 15-20 mins until golden. (This will give a soft, chewy cookie. For a crisper one, reduce the heat to 160C/fan140C/gas 3 and bake for a further 5-10 mins.) Allow the cookies to cool on the trays briefly, then lift onto to a cooling rack. Will keep in an airtight container, separated with baking parchment, for up to 1 week.','50 mins','No Time'),
(1317,'oatmeal raisin cookies','https://www.bbcgoodfood.com/recipes/oatmeal-raisin-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cookies_0-c371661.jpg','Fill the biscuit tin with these rustic cookies. Soaking the raisins gives a boost to the texture and stops them from burning during cooking',5,25,1,2,23,'Heat oven to 180C/160C fan/gas 4 and line 2 baking trays with baking parchment. Pour 50ml/2fl oz boiling water over the raisins and leave to soak for 20 mins until plump. Drain, reserving the liquid. Meanwhile, in a large bowl, mix together the oil and sugar. Gradually beat in the egg, along with the reserved water from the raisins, the cinnamon and vanilla extract. Sift the flour, bicarbonate of soda and a pinch of salt into the bowl, then add the oats. Finally, mix in the raisins. Drop heaped tbsps of the cookie dough onto the baking trays, well spaced apart as they will spread when cooking. Bake for 12-15 mins until golden. Leave to cool on the trays for 10 mins before tucking in, or transfer to a cooling rack to cool completely. Will keep in an airtight container for up to 3 days.','15 mins','15 mins'),
(1318,'fruity cookies','https://www.bbcgoodfood.com/recipes/fruity-cookies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-561001_12-910bdff.jpg','This easy yet delicious recipe was whipped up by the Year 7 pupils from the Priory School in Suffolk',5,20,1,2,23,'Heat oven to 160C/140C fan/gas 3. Cream the butter and sugar until light and fluffy. Mix the marmalade with 2 tbsp boiling water. Stir into the creamed mix, then add the spices, oats, flour and baking powder. Mix in the fruit and nuts. Dust your hands and surface with flour and roll the dough into a long sausage shape. Cut into about 20 discs. Place on a baking tray lined with baking parchment, spaced out as they will spread. Bake for about 25 mins until golden brown.','20 mins','25 mins'),
(1319,'walnut & raisin oatcakes','https://www.bbcgoodfood.com/recipes/walnut-raisin-oatcakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-73664_12-3c3739e.jpg','Quick and easy to make, these biscuits will add a flourish to the final Christmas course',5,40,1,2,23,'Heat oven to 180C/160C fan/gas 4. Dissolve the baking powder in the milk. Beat the butter and sugar until pale and creamy, then mix in the oats, flour, raisins, walnuts and milk to make a stiff dough. Tip the dough onto lightly floured surface and roll out until about 1cm thick. Cut into circles with a 5cm pastry cutter, place on an oiled baking sheet, then bake for 15 mins until lightly golden. Leave to cool, then serve with cheese. Can be baked up to 2 days ahead and kept in an airtight container until serving.','20 mins','15 mins'),
(1323,'vegan flapjacks','https://www.bbcgoodfood.com/recipes/vegan-flapjacks','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-flapjack-bebe955.jpg','Choose your favourite nuts and dried fruits in this versatile dairy-free flapjack recipe, or change it up each time you make them – be adventurous!',12,16,1,2,25,'Heat the oven to 160C/140C fan/gas 4 and line a 20cm square baking tin with baking parchment. Melt the dairy-free spread, sugar and syrup in a saucepan over a medium heat. Remove from the heat and tip in the oats, chopped nuts and dried fruit. Transfer to the tin, packing the mix in with the back of a spoon. Bake for 30 - 35 mins until lightly golden and crisp around the edges. Leave to cool in the tin before slicing into squares. Keep in an airtight container for up to three days. ','15 mins','35 mins'),
(1324,'vegan hot cross buns','https://www.bbcgoodfood.com/recipes/vegan-hot-cross-buns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-hot-cross-buns-90fd1d1.jpg','What says Easter better than hot cross buns? Try this vegan version, using dairy-free spread and almond milk. Cinnamon, mixed spice, sultanas and citrus bring all the traditional flavours',14,12,1,2,25,'Put the almond milk in a saucepan over a medium heat. Once simmering, add the spread, remove from the heat and allow to melt. Set aside to cool to hand temperature. Mix the flour, yeast, sugar, salt and spices in a large mixing bowl. Make a well in the centre and pour in the milk mixture, swiftly combining with a wooden spoon to create a sticky dough. Tip out of the bowl onto a lightly floured surface. Knead the dough by stretching it back and forth on the surface for 5 - 7 mins or until smooth, springy and elastic. Shape into a ball and put into a lightly oiled mixing bowl. Cover and leave in a warm spot to rise, for 1hr or until doubled in size. Turn the dough back onto the surface and flatten into a round. Spread the orange zest, sultanas and mixed peel onto the dough and knead again until everything is well distributed. Form into a ball, return to the bowl, cover and leave to rise for another hour. Line a large baking sheet with baking parchment. Knock the dough back by turning it out onto your surface and gently punching out the air. Divide the dough into 12 even-sized pieces, weighing for accuracy if you like. Roll each one into a ball. Arrange the buns on the baking sheet, leaving a 2cm space between each one. Cover with lightly oiled cling film and leave to rise for 45 mins. Heat the oven to 220C/ 200 fan/ gas 7. To make the cross, in a small bowl, mix together the flour with 1 tbsp of water at a time to create a thick, pipeable paste. Spoon it into a piping bag fitted with a small round nozzle and pipe crosses on the buns. Transfer to the middle shelf of the oven and bake for 15-20 mins or until deep golden brown. Meanwhile, gently heat the jam in a small saucepan over a low heat to loosen. Pass through a sieve to remove any lumps. Once cooked, brush the warm jam over the tops of the buns. Set aside to cool a little before eating.','35 mins','20 mins'),
(1325,'ginger & marmalade roulade','https://www.bbcgoodfood.com/recipes/ginger-marmalade-roulade','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roll-0caacdd.jpg','Use aquafaba (the liquid from a can of chickpeas) to create a delicious vegan ginger and marmalade roulade. It takes a little effort, but it''s well worth it',41,6,2,2,25,'Heat the oven to 190C/170C Fan/gas 5 and line a 23 x 33cm Swiss roll tin with baking parchment. Whisk the aquafaba for 6 mins, or until it reaches soft peaks, then add the caster sugar and vanilla, 1 spoonful at a time, whisking in between each addition so that the mixture turns to stiff peaks. Sieve in the self-raising flour with the ground ginger, then use a metal spoon to fold the flour into the aquafaba mix. Pour into the lined tin and smooth with a spatula so it’s evenly spread. Bake for 20 mins until just firm to the touch. While the sponge is baking, lay a piece of baking paper onto a work surface and dust with caster sugar. Once the sponge has baked, turn it out on the sugared paper and peel off the baking paper on the bottom. Allow to cool a little, then use the sugared paper to gently roll up the sponge from the shortest edge. Leave to cool a little longer in the rolled-up shape (this will make it easier to roll up again later), then gently unroll to cool completely. Whisk the coconut yogurt or cream with the icing sugar until thickened. Once the cake is cooled, spread it with the marmalade leaving a 2cm border, then scatter with the stem ginger. Spread the coconut yogurt on top, keeping the border, then use the sheet of baking paper again to help you to roll up the sponge back into the rolled shape. Remove the baking paper, trim the ends, then place on a serving dish, dust with icing sugar and serve.','20 mins','20 mins'),
(1326,'vegan scones','https://www.bbcgoodfood.com/recipes/vegan-scones','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-scone-860f010.jpg','Enjoy our vegan version of scones, which uses soy milk and vegan vegetable spread to replace the dairy. Serve with vegan cream and your favourite jam ',46,6,1,2,25,'Mix the flour, salt, baking powder and sugar in a bowl. Rub in the vegetable spread until you have fine breadcrumbs. You can also gradually pulse it in a food processor until it resembles breadcrumbs. Gradually stir the milk into the flour mixture until you have a smooth dough. Lightly dust your surface with flour and gently roll out the scone dough until 2cm thick. Transfer onto a baking tray lined with parchment and put it in the fridge for 30 mins to firm up. Remove the dough from the fridge and using a 7cm cutter, cut out the scones. Put them, top side down, onto another baking sheet lined with paper, leaving a 2cm gap between each one. This gives the scones an even top. Brush with the extra milk, making sure that it doesn’t drip onto the sides (otherwise they will rise unevenly). Put the scones in the freezer for 15 mins. Heat the oven to 220C/200C fan/gas 7. Remove from the freezer and bake for 15-20 mins until golden brown. Serve with jam and vegan cream, if you like.','20 mins','20 mins'),
(1327,'gluten-free shortcrust pastry','https://www.bbcgoodfood.com/recipes/gluten-free-shortcrust-pastry','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/GF-Shortcrust-Pastry-2-f72f807.jpg','Make a gluten-free pastry tart base to fill with your favourite savoury fillings. To make sweet pastry, simply add a tablespoon of icing sugar and fill as you like',130,1,1,2,25,'In a large bowl, rub the butter and flour together using your hands until it resembles coarse breadcrumbs. Stir in the xanthan gum and a large pinch of salt. In a small bowl, combine the egg yolk and 1 tbsp of cold water. Add the egg mixture to the flour and, using a knife, stir to combine until it starts to come together. If still a bit crumbly, add a drop more cold water a tsp at a time. Bring together into a disc, using your hands and wrap in cling film. Place into the fridge to chill for at least 30 mins. When ready to use, roll out between two sheets of greaseproof paper to the thickness of a pound coin. Remove the top sheet of paper and carefully flip into the tin, with the remaining greaseproof paper facing up. Remove the remaining paper and reserve. Press the pastry into the base and sides of the tin. It may crumble, but you can patch up any holes with the remaining pastry. Trim the sides and place to rest in the fridge for 30 mins to firm up. Heat the oven to 180°C/160°C fan/gas 4. Scrunch up one of the reserved sheets of greaseproof and carefully press into the pastry. Fill with baking beans, making sure they go up the sides as well. Place onto a baking tray and blind bake for 15-20 mins until the edges are starting to turn golden and the base is set. Remove the baking beans and greaseproof paper and bake for a further 5-10 mins until the base feels dry. Remove to a wire rack to cool, then fill as you like.','20 mins','30 mins'),
(1344,'matcha breakfast bowl','https://www.bbcgoodfood.com/recipes/matcha-breakfast-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/matcha-breakfast-bowl-0e1cd88.jpg','Mix nutritious matcha powder with natural yogurt in this maple syrup and fruit-topped breakfast that''s 2 of your 5-a-day ',1,2,1,3,26,'Mix the yogurt with the matcha and maple syrup, and divide between two bowls. Top with the kiwi, peach and blueberries. Sprinkle over the coconut flakes just before serving.','15 mins','No Time'),
(1328,'gluten-free cupcakes','https://www.bbcgoodfood.com/recipes/gluten-free-cupcakes','https://images.immediate.co.uk/production/volatile/sites/30/2022/09/GF-Cupcakes-bd4b78a.jpg','Treat family and friends to these simple vanilla-flavoured cupcakes, baked with gluten-free flour and baking powder',130,12,1,2,25,'Heat the oven to 180C/160C fan/gas 4 and line a 12-hole cupcake tin with paper or silicone cases. Put the butter and sugar in a large bowl and beat with an electric whisk until light and creamy. Pour in the egg, a little at a time, mixing well after each addition. Next, beat in the vanilla paste, then fold in the flour, baking powder and a pinch of salt until well combined. Spoon the mixture evenly between the cases. Bake for 15-20 mins until the tops are golden and firm to the touch at the edges. Leave to cool in the tin for 15 mins, then transfer to a wire rack to cool completely. For the buttercream, beat the butter with an electric whisk until pale and fluffy in a clean, large bowl. Add the icing sugar, one spoonful at a time, mixing as you do. Beat in the vanilla. To decorate, transfer the buttercream to a piping bag fitted with a round or star nozzle. Pipe generous swirls on top of each cupcake. Will keep in an airtight container for up to three days.','30 mins','20 mins'),
(1329,'gluten-free white chocolate & raspberry cheesecake','https://www.bbcgoodfood.com/recipes/gluten-free-white-chocolate-raspberry-cheesecake','https://images.immediate.co.uk/production/volatile/sites/30/2023/03/Gluten-free-white-chocolate-and-raspberry-cheesecake-db2eaf5.jpg','Make a gluten-free version of cheesecake using free-from digestive biscuits for the base. Top with a silky white chocolate and raspberry filling',2,8,1,2,25,'An hour before you begin, remove all the ingredients from the fridge and bring to room temperature. Line the base of a 20cm springform cake tin with baking parchment. Melt the white chocolate in a heatproof bowl in the microwave for 1 min, stirring halfway through. If necessary, put back in for further 10-second bursts until the chocolate is melted. Alternatively, melt the chocolate in a heatproof bowl over a pan of just simmering water, ensuring the bottom of the bowl doesn’t meet the water. Leave to cool while you make the base. Blitz the digestives in a food processor, then tip into a bowl or crush in the bowl using the end of a rolling pin. Pour in the melted butter and mix until the texture resembles damp sand. Tip into the prepared tin and press down to form an even layer. Keep chilled while you make the filling. Beat together the soft cheese, vanilla extract and double cream in a bowl using an electric whisk until thick and creamy. Fold through the melted white chocolate – if it seizes, you can pop the bowl in the microwave for 10 seconds before whisking to bring it back together. Stir through 250g of the raspberries, ensuring some break up to give you a bit of a ripple. Spoon the cheesecake mix over the biscuit base and smooth to an even layer. Put in the fridge to chill for at least 6 hrs, or overnight. Will keep chilled for two days. To serve, carefully remove from the tin and scatter over the remaining raspberries, then grate over some white chocolate, if you like.','25 mins','1 min'),
(1330,'gluten-free chilli cornbread','https://www.bbcgoodfood.com/recipes/gluten-free-chilli-cornbread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gluten-free-chilli-cornbread-adacfe0.jpg','Golden polenta and frozen sweetcorn make a deliciously different alternative to your regular loaf - best eaten fresh from the oven',135,4,2,2,25,'Lightly toast the polenta in a dry frying pan for 3-4 mins, stirring to ensure even cooking, until the polenta has heated through, is fragrant and small patches are starting to turn golden brown. Take off the heat, tip half into a large bowl and add the buttermilk. Stir well, cover and leave to soak for 2-3 hrs. Melt the butter in a 25cm ovenproof frying pan (a cast-iron one is perfect) and heat oven to 220C/200C fan/gas 7. Stir the butter and the remaining ingredients, including the rest of the toasted polenta and 1/2 tsp salt, into the buttermilk and polenta mixture. (Don’t wipe out the frying pan – the slick of butter will ensure the bread doesn’t stick.) Put the pan back on the heat and turn up the temperature. Pour the mixture into the pan – it should sizzle as it hits it, like a Yorkshire pudding. Put the whole pan in the oven and bake for 15-20 mins until golden brown and firm in the middle. Leave to cool a little, then serve cut into wedges.','20 mins','30 mins'),
(1331,'free-from butterscotch pie','https://www.bbcgoodfood.com/recipes/free-from-butterscotch-pie','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Butterscotch-pie-bac30f1.jpg','Make a butterscotch pie for dessert. This one is free from dairy, eggs, soy and gluten, so everyone in the family can enjoy it. Top with chopped nuts, if you like.',136,6,1,2,25,'Combine the sugar, golden syrup, ¼ tsp salt and 250ml of the coconut milk in a large saucepan, then bring to the boil over a medium heat. Meanwhile, combine the rest of the coconut milk, vanilla bean paste or extract and cornflour in a large heatproof bowl, whisking until smooth. When the syrupy coconut mixture comes to the boil, slowly drizzle it into the bowl with the coconut and vanilla mixture in a steady stream, whisking continuously. Pour back into the pan and bring to a simmer over a medium heat, continuing to whisk until thick, about 5-7 mins. Remove from the heat and whisk in the margarine and yogurt or soured cream. Pour into the prepared pie crust, then cover the surface with a circle of baking parchment to prevent a skin forming. Chill for 4-6 hrs until set. Will keep chilled for up to two days.','10 mins','15 mins'),
(1345,'chia & almond overnight oats','https://www.bbcgoodfood.com/recipes/chia-almond-overnight-oats','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chia-almond-overnight-oats-with-raspberries-and-blueberries-1-440-400-98ea1d8.jpg','Start the day with energising oats and omega-3-rich chia seeds with this delicious breakfast. It''s vegan, but you can use cow''s milk and yogurt, if you prefer',4,4,1,3,26,'Tip the oats and seeds into a bowl and pour over the milk and vanilla extract. Leave for 5-10 mins for the oats to absorb some of the liquid. Reserve 16 raspberries, then add the remainder to the oats and crush them into the mixture. Spoon into four tumblers or sundae dishes, then top with the yogurt and both lots of berries. Cover and chill overnight or until needed. To serve, pour 2 tbsp almond milk over each and scatter with the almonds.','10 mins','No Time'),
(1332,'pastel de choclo - corn cake','https://www.bbcgoodfood.com/recipes/pastel-de-choclo-corn-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pastel-de-choclo-corn-cake-c123e29.png','Martin Morales'' recipe for this Peruvian sweetcorn, feta and olive side dish is adapted from his book, Ceviche',5,4,2,2,25,'Heat oven to 120C/100C fan/gas 1/2. Grease a 20 x 20cm cake tin and line with baking parchment. Separate the eggs. Put the yolks in a food processor or blender with the sweetcorn and blitz to a rough purée. Tip the blended mixture into a large bowl and stir in the butter, cornflour, baking powder, sugar and 1/2 tsp salt. Mix thoroughly. Whisk the egg whites until they have reached the stiff peak stage. Gently fold the egg whites into the corn mixture using a large metal spoon until completely incorporated, keeping as much air in the mixture as possible. Spread the corn batter over the base of the tin with a spatula. Sprinkle over the cubes of cheese and push them into the mixture so they are almost completely covered. Cook for 1 hr 45 mins until firm and golden and shrinking slightly from the sides. Remove from the oven and leave to cool for at least 15 mins before cutting into chunks and serving, scattered with olives. Leftovers will keep in the fridge until the next day.','15 mins','1 hr and 45 mins'),
(1333,'gluten-free banana bread','https://www.bbcgoodfood.com/recipes/gluten-free-banana-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/04/GlutenFreeBananaBread-5e91a5a.jpg','Use up overripe bananas and make banana bread with gluten-free flour and ground almonds. This recipe is ideal for anyone following a gluten-free diet',46,8,1,2,25,'Heat the oven to 180C/160C fan/gas 4 and line a 900g loaf tin with parchment paper (our tin was 19 x 9 x 6cm). Put all the ingredients, except the sliced banana, 1 tbsp caster sugar and the icing sugar, into a large bowl and stir until smooth and combined. Pour into the tin and put the two remaining banana halves cut-side up across the top of the batter, pressing down slightly. Sprinkle over the caster sugar. Bake for 1hr-1hr 15 mins until a skewer comes out clean, covering with foil towards the end of cooking if it browns too much. Dust with icing sugar and leave to cool.','10 mins','1 hr and 15 mins'),
(1334,'easy gluten-free flatbread','https://www.bbcgoodfood.com/recipes/toasted-cumin-flatbreads','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273647_8-09d57b3.jpg','Use our easy gluten-free bread recipe to make light, fluffy breads which can be used for scooping up sauces, served alongside curries or served as a lunchtime snack with some dips',5,8,1,2,25,'Heat the grill to medium and dust a baking sheet with a little flour. Mix the flour and cumin seeds in a bowl, then season. Stir in the yogurt and 100ml water, then mix well to form a soft dough. Divide the dough into 8 equal pieces, then shape into circles or ovals about ½cm thick. Dust lightly with a little flour. Grill on the baking sheet for 3-5 mins on each side until golden and puffed. Serve warm.','10 mins','10 mins'),
(1335,'amaretto meringue cake with strawberries & cherries','https://www.bbcgoodfood.com/recipes/amaretto-meringue-cake-strawberries-cherries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/amaretto-meringue-cake-with-strawberries-cherries-cb63db6-scaled.jpg','This Pavlova cake is inspired by the fruity filling of the Austrian dessert, Spanische Windtorte, with strawberries, cream, cherries and almonds',6,12,2,2,25,'Heat oven to 140C/120C fan/gas 1 and cut 3 pieces of baking parchment to fit 3 baking sheets for the meringues (if you don’t have 3 baking sheets, you can cook it in batches – simply divide the meringue mixture into 3 and cook separately). Using a plate or the base of a cake tin, draw a 20cm circle on each piece of parchment, then flip over so that the pencil side is facing down. Put 3 oven racks in your oven – if you only have 2 but have a large grill pan, this will work too, sat in the bottom of the oven. Put the egg whites in a large bowl, and whisk with an electric hand whisk until thick and holding soft peaks. Start adding the sugar, 1 or 2 tbsp at a time, whisking continuously. When all the sugar has been incorporated, the meringue should be thick, glossy and holding stiff peaks. Add the vinegar and whisk again briefly. Use a blob of meringue to stick down the corners of each piece of parchment to the trays. Divide the meringue between the 3 circles on the parchment, swirling it towards the outline to make thin circles. Scatter the almonds over, saving a few to serve on top once assembled. Bake for 1 hr, then leave to cool in the oven. When the oven is completely cold, remove the meringues and place somewhere cool and dry for up to 24 hrs, or assemble straight away. Toss the fruit with 1 tbsp icing sugar and set aside to macerate for 10-15 mins. Pour the cream, amaretto, almond extract and remaining icing sugar into a large bowl, and whisk until softly whipped. Use a little of the cream to stick your least successful meringue to a cake stand or plate. Spread over a third of the cream and scatter with some strawberries and cherries. Top with the remaining meringues, cream and fruit, finishing the top layer with the whole strawberries and cherries. Scatter over the remaining almonds and serve. Leftovers will keep in the fridge for 1 day.','30 mins','1 hr'),
(1336,'gluten-free chocolate cake','https://www.bbcgoodfood.com/recipes/gluten-free-chocolate-cake','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Gluten-free-chocolate-cake-eae2d2f.jpg','Make a dense, rich chocolate cake for a special occasion or anytime treat. While suitable for those following a gluten-free diet, it will prove a hit with everyone',46,10,1,2,25,'Heat the oven to 180C/160C fan/gas 4. Oil the base of two 18cm sandwich tins and line with baking parchment. Mix the flour, xanthan gum, cocoa, baking powder, bicarbonate of soda, a large pinch of salt and both sugars together in a large bowl. Whisk the golden syrup, eggs, oil, yogurt, milk and coffee together in a jug. Gradually whisk the wet ingredients into the dry until you have a smooth mixture. Pour the mixture into the prepared tins and bake for 30-35 mins until risen and firm to the touch. Remove from the oven and leave to cool for 10 mins in the tins before turning out onto a cooling rack. While the cakes are cooling, make the icing. Beat the icing sugar, cocoa powder, butter and milk together for 5 mins until smooth and fluffy. Gradually pour in the melted chocolate while beating, scraping down the sides of the bowl to make sure everything’s included. Spread half the icing over the middle of one sponge using a small palette knife. Sandwich the other sponge on top, with the flat bottom-side facing up. Spread the remaining icing around the top and sides of the cake, smoothing over with a palette knife. If you have any icing left, you can pipe some rosettes on top of the cake.','45 mins','35 mins'),
(1337,'crusted polenta tart with pesto, courgette & gruyère','https://www.bbcgoodfood.com/recipes/crusted-polenta-tart-pesto-courgette-gruyere','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/18.crusted-polenta-tart-with-pesto-courgette-gruyere-41062a1-scaled.jpg','A gluten-free polenta base makes this cheesy tart much lower in fat than traditional pastry – delicious served hot or cold',67,4,1,2,25,'First, make the polenta crust. Bring the vegetable stock to a simmer in a medium saucepan and, working quickly, pour the polenta into the pan. Keep your pan over a low heat and, using a wooden spoon, stir constantly, beating any lumps that form. Continue stirring for 5-6 mins until the polenta is very thick. Remove the pan from the heat, add the Gruyère and stir until the cheese has melted. Finally, stir through the beaten egg, season generously, and allow to cool slightly. Lightly brush a baking sheet with oil. Tip the polenta into the centre and, using a spatula or oiled fingers, gently spread the polenta into a rectangle shape roughly the size of an A4 sheet of paper. Heat oven to 200C/180C fan/gas 6. Smother a thin layer of pesto over the polenta, leaving a 1-1.5cm border around the edge of the tart. Top the pesto with slices of courgette, intermittently adding the sliced garlic and most of the basil. Season with ground black pepper and a good sprinkling of gruyère. Cook on the middle shelf of the oven for 45 mins. Reduce the temperature to 180C/160C fan/gas 4 for a further 15 mins. Remove and allow to cool for 5-10 mins, scatter over the remaining basil and serve.','20 mins','1 hr and 15 mins'),
(1338,'buttermilk & sultana scones','https://www.bbcgoodfood.com/recipes/buttermilk-sultana-scones','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scones-a3a56be.jpg','A classic teatime treat goes gluten-free- serve with butter, jam, fresh fruit, clotted cream or a combination of your choice',4,8,1,2,25,'Heat oven to 220C/200C fan/gas 7 and lightly flour a large baking sheet. Tip the flour into a large bowl and stir in the baking powder, xanthan gum, sugar and ½ tsp salt. Rub the butter into the flour mixture with your fingertips until it is completely incorporated, then add the sultanas. Stir the buttermilk and milk together, then pour into the flour mixture. Stir in with the blade of a knife to make a soft dough. Tip onto a lightly floured work surface and pat out with your hands until about 4cm thick. Don’t knead the mixture as this will make a heavy scone, and if the mixture seems a little too wet, leave for a few mins, as gluten-free flour requires more liquid than wheat flour. Stamp out rounds using a 7cm floured cutter, then place the scones on the baking tray, spaced apart. You will need to lightly squash the dough trimmings together to give you 8-9 scones in total. Brush the tops of the scones with milk, sprinkle with sugar and bake for 10-12 mins until pale golden. Serve with butter and jam, or push the boat out with clotted cream and strawberries, too. Best eaten on the day they are made.','20 mins','10 mins - 12 mins'),
(1339,'gluten-free bread','https://www.bbcgoodfood.com/recipes/gluten-free-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gluten-free-bread-713ab27.jpg','A simple loaf of bread for those following a gluten-free diet. Bake this wheat-free bread and use it to make sandwiches or toast, or simply slather it in butter.',7,1,1,2,25,'Heat oven to 180C/160C fan/gas 4. Mix the flour, salt and yeast in a large bowl. In a separate bowl, whisk together the buttermilk, eggs and oil. Mix the wet ingredients into the dry to make a sticky dough. Grease a 900g loaf tin, or flour a baking sheet. With oiled hands, shape the dough into a sausage shape for a loaf or a ball for a cob. If making a loaf, place the dough in the tin. For a cob, place it on the baking sheet and score the top with a sharp knife. Cover loosely with a piece of oiled cling film and leave somewhere warm for 1 hr, or until risen by a third or so. Bake for 50-60 mins until golden and well risen. Turn out onto a wire rack and leave to cool for at least 20 mins before cutting.','5 mins','1 hr'),
(1340,'gluten-free hot cross buns','https://www.bbcgoodfood.com/recipes/gluten-free-hot-cross-buns','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/glutenfree-hot-cross-99fcb97.png','Paul Hollywood''s fruity bakes are just as delicious when you make some simple swaps to make them wheat-free',137,15,2,2,25,'Bring the milk to the boil, then remove from the heat and add the butter. Leave to cool until it reaches hand temperature. Mix the flour, salt, sugar and yeast with the warm milk and egg in a mixer with a dough attachment, or with a wooden spoon, then bring together the dough with your hands. Don''t knead. Put the dough in a lightly oiled bowl. Cover with oiled cling film and leave to rise in a warm place for 1 hr or until doubled in size and a finger pressed into it leaves a dent. Tip in the olive oil, sultanas, mixed peel, orange zest, apple and cinnamon and mix into the dough. Shape into buns by lightly oiling your hands and dividing the dough into 100g pieces before rolling into balls. Leave to rise for another hour. Heat oven to 220C/200C fan/gas 7. Mix the flour with about 3 tbsp water to make the paste for the cross – add the water 1 tbsp at a time, so you add just enough for a thick paste. Spoon into a piping bag with a small nozzle. Pipe a line along each row of buns, then repeat in the other direction to create crosses. Bake for 20 mins on the middle shelf of the oven, until golden brown. Gently heat the apricot jam to melt, then sieve to get rid of any chunks. While the jam is still warm, brush over the top of the warm buns and serve.','30 mins','20 mins'),
(1341,'gluten-free sundried tomato bread','https://www.bbcgoodfood.com/recipes/gluten-free-sundried-tomato-bread','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273643_8-3fbc534.jpg','A quick, gluten-free bread recipe - no need for yeast, ready in under an hour',5,1,1,2,25,'Heat oven to 180C/fan 160C/gas 4. Mix the flour, salt and baking powder in a large bowl. In a separate bowl, whisk together the buttermilk, eggs, tomato purée and oil. Fold the wet ingredients into the dry, then add the sundried tomatoes and half the Parmesan. Grease a 900g loaf tin and pour in the mixture. Sprinkle the remaining Parmesan on top and bake for 50-60 mins until a skewer inserted into the middle comes out clean. Turn out onto a wire rack to cool.','15 mins','1 hr'),
(1342,'chocolate, orange & hazelnut cake','https://www.bbcgoodfood.com/recipes/chocolate-orange-hazelnut-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chocolate-orange-hazelnut-cake-04bd29d.jpg','You''d never guess that this spectacular dessert is dairy-free, decorated with a zesty ganache using orange juice instead of cream',75,10,2,2,25,'Make the candied orange slices a day ahead if you can. Put the sugar and 300ml water in a medium saucepan and bring to the boil. Reduce to a gentle simmer, add the orange slices and cook for 1 hr-1 hr 10 mins or until the pith is translucent, turning occasionally. Line a baking tray with parchment. Carefully remove the slices from the syrup, place on the prepared tray and set aside to dry (at least 8 hrs) before cutting in half. Reserve the syrup. Heat oven to 180C/160C fan/gas 4. Lightly grease a loaf tin (mine was 900g) with olive oil and line with a strip of baking parchment. Grind the hazelnuts in a food processor until they resemble coarse breadcrumbs (do not blitz them too much or they will become oily), then add to a bowl along with the orange zest, a pinch of salt and the flour, baking powder and cocoa. Mix together until evenly combined. Pour the oil and the orange juice into a jug and mix together. Put the eggs and sugar in a tabletop mixer or large bowl and whisk together for 5-10 mins or until the mixture has tripled in volume and holds a ribbon on the surface when the beaters are lifted out. Slowly pour the oil mixture into the egg mixture and fold together until combined. Add the flour mixture to the egg mixture in 3 or 4 additions, folding together until combined. You can’t sieve this mixture over the eggs because of the hazelnuts, but try not to dump the flour in one place – you need to be careful and fold the batter to retain its lightness. Once fully combined, pour the batter into the prepared loaf tin and bake for 50-55 mins or until a skewer inserted into the centre of the cake comes out clean. Prick the top of the cake all over, then pour over 5 tbsp of the reserved orange syrup. Cool in the tin for 15 mins, then turn out onto a wire rack to cool completely. Trim the top of the cake (keep this for a mini trifle) and turn out, cut-side down, onto a serving platter. To decorate, pour the orange juice into a small pan and bring to a simmer. Put the chocolate in a small bowl, pour the orange juice over and stir together to form a smooth ganache. Set aside in the fridge until thickened, about 20-25 mins. Tip the hazelnuts into a small bowl and add the gold powder with a dash of water, stirring together to coat. Put the ganache in a piping bag fitted with a small round piping tip. Pipe in peaks over the top of the cake, decorating with the golden hazelnuts and the orange slices. Will keep for up to 3 days in an airtight container.','50 mins','50 mins'),
(1343,'blueberry & coconut cake','https://www.bbcgoodfood.com/recipes/blueberry-coconut-cake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-522460_11-14b8623.jpg','This dairy-free dessert is a guaranteed crowd-pleaser and is even freezer-friendly',5,12,1,2,25,'Heat oven to 180C/160C fan/gas 4 and grease a 22cm Bundt or ring tin. Whisk the oil, eggs, sugar and vanilla in a large bowl. Combine the flour and coconut. Alternately, fold the flour mix and soya milk into the wet ingredients, starting and ending with the flour. Spoon a quarter into the tin. Fold the blueberries into the remaining batter, then spoon into the tin. Bake for 1-1¼ hrs, or until a skewer comes out clean. Cover the cake with foil if it browns too quickly. Cool in tin for 10 mins, then turn out onto a wire rack and cool completely. Fill centre of the cake with extra blueberries and dust with icing sugar to serve.','20 mins','1 hr - 1 hr and 15 mins'),
(1346,'blueberry baked oats','https://www.bbcgoodfood.com/recipes/blueberry-nut-oat-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberry-nut-oat-bake-4a1dd90.jpg','Swap your regular porridge for a healthy baked version, packed with oats, juicy blueberries and crunchy almonds. It''s a warming breakfast for cold mornings',111,6,1,3,26,'Heat the oven to 200C/180C fan/gas 6. Mix all of the ingredients together in a large bowl. Tip the mixture into a 2-litre ovenproof dish, then bake for 30-35 mins until piping hot in the middle. Serve warm with a little milk or yogurt, honey and extra blueberries, if you like.','5 mins','35 mins'),
(1347,'homemade muesli with oats, dates & berries','https://www.bbcgoodfood.com/recipes/homemade-muesli-oats-dates-berries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/homemade-muesli-with-oats-dates-berries-copy-f17fb44.jpg','Packed with oats, pecans, seeds, dates, puffed wheat and berries, this delicious breakfast muesli will help you to start your day the right way',4,4,1,3,26,'Tip the oats into a frying pan and heat gently, stirring frequently until they are just starting to toast. Add the pecans and seeds to warm briefly, then tip into a large bowl and toss so they cool quickly. Add the dates and puffed wheat, mix well until thoroughly combined, then serve topped with the yogurt and fruit, and a sprinkling of cinnamon, if you like.','5 mins','2 mins'),
(1348,'baked oats','https://www.bbcgoodfood.com/recipes/classic-baked-oats','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Easy-Classic-Baked-Oats-b173e46.jpg','Enjoy baked oats for breakfast with bananas, mixed spice and your choice of chocolate chips, blueberries or raspberries. Serve warm with more fruit to make it extra-special',12,4,1,3,26,'Heat the oven to 180C/160C fan/gas 4. Put the oats in a blender and pulse a few times until they start to resemble flour. Add the baking powder, chopped banana, maple syrup or honey, eggs and mixed spice or cinnamon, and whizz until smooth. Stir in the chocolate chips or berries. Lightly oil four heatproof ramekins, then divide the batter between them. Bake for 20-25 mins, or until well-risen and the baked oats spring back when lightly pressed. Scatter with more chocolate chips or berries before serving, if you like.','5 mins','25 mins'),
(1349,'healthy baked oats','https://www.bbcgoodfood.com/recipes/cinnamon-baked-oats-with-blueberries','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Baked-Oats-5a51f8c.jpg','Enjoy these healthy mini oat pots with a topping of yogurt and your favourite berries. Although usually served for breakfast, they make a great little snack',4,2,1,3,26,'Heat the oven to 200C/180C fan/gas 6. Beat the milk, egg and oats together with 2 tbsp water, the cinnamon and vanilla essence. Divide between two 165ml ramekins, then bake in the oven for 10 mins until almost set. Top each one with half the yogurt, then dust with cinnamon and top with the berries.','5 mins','10 mins'),
(1350,'pink barley porridge with vanilla yogurt','https://www.bbcgoodfood.com/recipes/pink-barley-porridge-vanilla-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pink-barley-porridge-with-vanilla-yogurt-17fd180.jpg','Power yourself through the day with a healthy breakfast porridge that gets its pink hue from plums. Pearl barley adds great texture as well as releasing energy slowly',5,4,1,3,26,'Tip the barley and oats into a bowl, pour over 1 litre boiling water and stir well. Cover and leave to soak overnight. The next morning, tip the mixture into a pan and stir in the plums. Simmer for 15 mins, stirring frequently and adding a little water if necessary to get a consistency you like. Stir the vanilla into the yogurt and serve on top of the porridge with the seeds sprinkled over.','10 mins','15 mins'),
(1351,'chia & oat breakfast scones with yogurt and berries','https://www.bbcgoodfood.com/recipes/chia-oat-breakfast-scones-yogurt-and-berries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chia_breakfast_scones_440_400-f1fe7c4.jpg','These healthy breakfast scones have a slightly bouncier texture than traditional ones, but make a lovely start to the day and provide folate, vitamin C and fibre.',4,4,1,3,26,'Heat oven to 200C/180C fan/gas 6 and line the base of 4 x 185ml ramekins with a disc of baking parchment and oil the sides with the rapeseed oil. Measure the milk in a jug and make up to 300ml with water. Stir in the lemon juice, vanilla and the 2 tsp oil. Mix the flour, seeds and oats then blitz in a food processor to make the mix as fine as you can. Stir in the baking powder. Pour in the liquid, then stir in with the blade of a knife until you have a very wet batter like dough. Spoon evenly into the ramekins then bake on a baking sheet for 20 mins until risen – they don’t have to be golden but should feel firm. Cool for a few mins then run a knife round the inside of the ramekins to loosen the scones then carefully ease out. The scones can be eaten immediately or cooled and stored for later. If you''re following our Healthy Diet Plan, this recipe can be used for two people over two meals, use two scones straight away split in half and served topped with half the yogurt and two portions of the berries. Cool and pack the remainder to eat on another day on the plan.','8 mins','20 mins'),
(1352,'healthy granola','https://www.bbcgoodfood.com/recipes/crunchy-oat-clusters-peach-yogurt','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crunchy-oat-clusters-with-peach-yogurt-4-440-400-0cf467f.jpg','This healthy homemade granola is high in fibre and packed with nutritious ingredients such as dried apricots, nuts, and seeds. Serve with fresh peaches and yogurt.',4,6,1,3,26,'Heat oven to 180C/160C fan/gas 4 and line a large baking tray with baking parchment. Tip the apricots, oil and egg whites into a bowl (see tip box for how to use up the egg yolks), then blitz with a hand blender until very smooth. Stir in the oats, cinnamon and vanilla, then fold through the desiccated coconut, almonds and pumpkin seeds. Pinch clusters of the mixture together to create texture in the granola, then scatter over the lined baking tray in a single layer. Bake for 15 mins, then toss (turning the larger pieces) and bake for a further 10 mins until golden and crunchy. Cool the granola completely on the tray, then pack into a large airtight jar or container. If you''re following our Healthy Diet Plan, serve two portions over three days, filling the base of each bowl with yogurt (½ pot for each portion) and topping with half a peach, sliced.','5 mins','15 mins - 20 mins'),
(1353,'oat pancakes','https://www.bbcgoodfood.com/recipes/oat-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pancakes-4e180d1.jpg','Make these oat pancakes for breakfast or brunch and top with berry compote and a dollop of yogurt. They''re tasty, healthy and full of fibre and vitamin C ',14,2,1,3,26,'To make the compote, put the orange juice, berries and apple in a small saucepan and gently simmer over a medium heat for 10-12 mins until the berries begin to burst and the apple has softened. Remove from the heat and leave to cool a little. Add a splash of water to loosen, if you like. Blitz the oats in a food processor to form a coarse flour consistency, then tip into a mixing bowl. Add the baking powder, cinnamon and orange zest and stir well. Whisk together the oat milk and egg in a jug, then pour into the dry ingredients and combine to create a thick batter. Heat a little of the oil in a non-stick frying pan, then pour in 2 tbsp of the batter to make a small pancake. Cook over a medium heat for 2 mins, or until small bubbles start to appear on the surface. Flip the pancake over and cook on the other side for 1 min, then transfer to a plate. Repeat with the remaining oil and batter (you should end up with six pancakes). Stack the pancakes on two warm plates, spoon over the compote and top with a dollop of the Greek yogurt.','10 mins','25 mins'),
(1354,'porridge with blueberry compote','https://www.bbcgoodfood.com/recipes/porridge-blueberry-compote','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberryporridge_5677-675d730.jpg','Top high-fibre porridge oats with creamy Greek yogurt and healthy blueberries – buy frozen to help cut the cost of this filling breakfast',4,2,1,3,26,'Put the oats in a non-stick pan with 400ml water and cook over the heat, stirring occasionally for about 2 minutes until thickened. Remove from the heat and add a third of the yogurt. Meanwhile, tip the blueberries into a pan with 1 tbsp water and the honey if using and gently poach until the blueberries have thawed and they are tender, but still holding their shape. Spoon the porridge into bowls, top with the remaining yogurt and spoon over the blueberries.','5 mins','5 mins'),
(1355,'fruit & nut breakfast bowl','https://www.bbcgoodfood.com/recipes/fruit-nut-breakfast-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fruitnutbreakfastbowl_5833-f6053e0.jpg','Top cheap and healthy porridge oats with chopped fresh oranges, Greek yogurt and a sprinkling of dried fruit, nuts and seeds',4,2,1,3,26,'Put the oats in a non-stick pan with 400ml water and cook over the heat, stirring occasionally for about 4 mins until thickened. Meanwhile, cut the peel and pith from the oranges then slice them in half, cutting down either side, as closely as you can, to where the stalk would be as this will remove quite a tough section of the membrane. Now just chop the oranges. Pour the porridge into bowls, spoon on the yogurt then pile on the oranges and the fruit, nut and seed mixture.','5 mins','5 mins - 10 mins'),
(1356,'green eggs','https://www.bbcgoodfood.com/recipes/green-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-eggs-1ea9f7a.jpg','Supplying vitamin C and iron, this breakfast of eggs, spinach and leeks will super-charge your morning. It provides energy from protein and fat, plus two of your 5-a-day',16,2,1,3,26,'Heat the oil in a large frying pan. Add the leeks and a pinch of salt, then cook until soft. Add the garlic, coriander, fennel and chilli flakes. Once the seeds begin to crackle, tip in the spinach and turn down the heat. Stir everything together until the spinach has wilted and reduced, then scrape it over to one side of the pan. Pour a little oil into the pan, then crack in the eggs and fry until cooked to your liking. Stir the yogurt through the spinach mix and season. Pile onto two plates, top with the fried egg, squeeze over a little lemon and season with black pepper and chilli flakes to serve.','5 mins','15 mins'),
(1357,'cinnamon porridge with banana & berries','https://www.bbcgoodfood.com/recipes/cinnamon-porridge-banana-berries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1071551_11-583c548.jpg','Start the day in a superhealthy way with energy-boosting oats and fresh fruit',5,4,1,3,26,'In a medium-sized saucepan, mix the oats, cinnamon, sugar, milk and half the sliced bananas. Bring to the boil, stirring occasionally. Turn down the heat and cook for 4-5 mins, stirring all the time. Remove and divide between 4 bowls, top with the remaining banana, strawberries, a dollop of yogurt and a sprinkle of cinnamon.','15 mins','5 mins'),
(1358,'tofu scramble','https://www.bbcgoodfood.com/recipes/tofu-scramble','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tofu_scramble-eaa0c64.jpg','Try our vegan spiced tofu with cherry tomatoes for an egg-free take on scramble on toast. Perfect for breakfast, served on rye bread',5,2,1,3,26,'Heat the oil in a frying pan over a medium heat and gently fry the onion for 8 -10 mins or until golden brown and sticky. Stir in the garlic, turmeric, cumin and paprika and cook for 1 min. Roughly mash the tofu in a bowl using a fork, keeping some pieces chunky. Add to the pan and fry for 3 mins. Raise the heat, then tip in the tomatoes, cooking for 5 mins more or until they begin to soften. Fold the parsley through the mixture. Serve on its own or with toasted rye bread (not gluten-free), if you like.','10 mins','20 mins'),
(1359,'breakfast egg wraps','https://www.bbcgoodfood.com/recipes/breakfast-egg-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/05/breakfast-egg-wraps-c0880fe.jpg','Enjoy these protein-rich egg wraps filled with mushrooms and tomatoes for a quick, filling and healthy breakfast. It provides iron, folate and fibre.',4,4,1,3,26,'Thickly slice half the pack of mushrooms. Heat 2 tsp rapeseed oil in a non-stick pan. Add the mushrooms, stir briefly then fry with the lid on the pan for 6-8 mins. Stir in half the tomatoes then cook 1-2 mins more with the lid off until softened. Beat together the eggs really well with the parsley and oats. Heat a drop of oil in a large non-stick frying pan. Pour in a ¼ of the egg mix and fry for 1 min until almost set, flip over as if making a pancake. Tip from the pan, spread with a quarter of the mustard, spoon a ¼ the filling down the centre and roll up. Now make a second wrap using another ¼ of the egg mix and filling. If you''re following our Healthy Diet Plan, save the rest for the following day.','5 mins','7 mins'),
(1360,'salsa verde baked eggs','https://www.bbcgoodfood.com/recipes/salsa-verde-baked-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Salsa-verde-baked-eggs-80334f1.jpg','Dunk flatbreads into these salsa verde baked eggs to soak up the lovely juices. Healthy and low in calories, it takes just 15 minutes to make',46,4,1,3,26,'Drizzle 1 tbsp of the olive oil in a frying pan or skillet, and fry the paprika and cumin for 30 seconds over a medium heat. Add the canned tomatoes and fresh tomatoes, bring to the boil, then simmer with a lid on over a medium heat for 5-6 mins until the tomatoes have softened. Meanwhile, put the garlic, most of the parsley, the basil, mint, capers, mustard, white wine vinegar, 4 tbsp oil and 3 tbsp cold water in a mini food processor and blitz to a smooth paste. Season. Stir the spinach into the pan with the tomatoes until wilted (put the lid back on for a few minutes, then stir again to help it wilt). Make four dips in the mixture and gently crack an egg into each one. Cover with a lid and cook over a medium heat for 6-8 mins, or until the eggs are just set. Uncover the pan, then drizzle over the herby sauce. Scatter over the reserved parsley and chilli flakes, if using. Serve with flatbreads, if you like.','5 mins','15 mins'),
(1361,'crunchy peanut butter & banana pots','https://www.bbcgoodfood.com/recipes/crunchy-peanut-butter-banana-pots','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Peanut-butter-and-banana-pots-ac3a464.jpg','Try a winning combo of peanut butter and banana for breakfast with these healthy vegan breakfast pots, made with porridge oats, soya yogurt and cinnamon',4,6,1,3,26,'Put the oats and cinnamon in a large pan with 1.3 litres water, then bring to the boil. Reduce to a low heat and simmer until the oats are cooked and the mixture has thickened, stirring occasionally. Stir in the chopped bananas, cook for 1 min, then remove the pan from the heat. Leave to cool for a few minutes, then stir in 6 tbsp of the soya yogurt. Beat the peanut butter into the remaining yogurt until well combined. Divide half the oat mixture into the base of six glass tumblers and spoon in half of the the nutty yogurt. Top with the rest of the oat mixture, then the remaining yogurt mixture to create layers. Will keep covered and chilled for up to four days. To serve, dust with a little cinnamon, if you like.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','5 mins'),
(1362,'breakfast burrito','https://www.bbcgoodfood.com/recipes/breakfast-burrito','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/breakfast-burrito-b086548.jpg','Fill a nutritious wholemeal wrap with lots of healthy breakfast ingredients to make this veggie burrito. We’ve included protein-rich eggs and avocado to add good fats',16,1,1,3,26,'Whisk the chipotle paste with the egg and some seasoning in a jug. Heat the oil in a large frying pan, add the kale and tomatoes. Cook until the kale is wilted and the tomatoes have softened, then push everything to the side of the pan. Pour the beaten egg into the cleared half of the pan and scramble. Layer everything into the centre of your wrap, topping with the avocado, then wrap up and eat immediately.','5 mins','10 mins'),
(1398,'avocado panzanella','https://www.bbcgoodfood.com/recipes/avocado-panzanella','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/panzanella-d883069.jpg','With just 20 minutes preparation and no cooking time you''ll be able to whip up this colourful avocado and bread salad in no time',104,4,1,3,27,'Halve or roughly chop the tomatoes (depending on size) and put them in a bowl. Season well and add the garlic, capers, avocado and onion, and mix well. Set aside for 10 mins. Meanwhile, tear or slice the ciabatta into 3cm chunks and place in a large serving bowl or on a platter. Drizzle with half the olive oil, half the vinegar and add some seasoning. When ready to serve, pour over the tomatoes and any juices. Scatter with the basil leaves and drizzle over the remaining oil and vinegar. Give it a final stir and serve immediately.','20 mins','No Time'),
(1363,'vanilla & cinnamon breakfast rice','https://www.bbcgoodfood.com/recipes/vanilla-cinnamon-breakfast-rice','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Vanilla-and-cinnamon-breakfast-rice-a4f3268.jpg','Fancy a change from porridge? This healthy rice-based vanilla and cinnamon breakfast is topped with apricots and walnuts, and will fill you up for the morning',4,4,1,3,26,'Tip the rice into a large, deep non-stick pan with 600ml water, the milk, vanilla, bay leaves and cinnamon stick. Bring to the boil, then cover with a lid and simmer for 25-30 mins until the rice is tender. Keep an eye on it to ensure it doesn’t boil dry towards the end of its cooking time. Meanwhile, tip the apricots into a small pan with 300ml water and cook over a low-medium heat until tender, about 10-15 mins. Set aside. Remove the bay leaves and cinnamon stick from the rice, then stir in half the yogurt. Both the rice and the apricots will keep chilled, separately, for up to 24 hrs. Spoon the rice into bowls and top with the remaining yogurt, the apricots and their juices and the walnuts. If you’ve prepared the rice and apricots the day before, eat cold or reheat until warm, adding a splash of milk to the rice to loosen.','10 mins','35 mins'),
(1364,'budget porridge','https://www.bbcgoodfood.com/recipes/budget-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Budget-porridge-b68e2f2.jpg','Start your day with a bowl of warming porridge topped with grated apple, raisins, walnuts and cinnamon – a nutritious and filling breakfast',4,2,1,3,26,'Tip the oats and cinnamon into a non-stick pan with 150ml water and the 250ml soya milk. Put the pan over a gentle heat. Once simmering, leave for 5 mins, stirring frequently (as soya milk has a tendency to stick) until the porridge has thickened. Meanwhile, coarsely grate the apples, including the skin, into a bowl, until you''re just left with the cores. Serve the porridge with the apple, raisins and nuts on top, and sprinkle over some extra cinnamon, if you like. Stir through the extra soya milk to loosen.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','5 mins'),
(1365,'high protein breakfast','https://www.bbcgoodfood.com/recipes/high-protein-breakfast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fry-up-e377f9d.jpg','A protein-packed version of a classic fry-up from Joe Wicks. Pile on the steak, mushrooms and spinach for a hearty, filling start to the day',96,1,1,3,26,'Heat grill to high. Drizzle a little of the oil on your tomatoes and season well. Lay the tomatoes on a baking tray lined with foil and place under the grill. Cook for 4-5 mins, then add the bacon to the tray. Turn the bacon as necessary until it is cooked through. Turn off the grill and shut the door to keep everything warm. Meanwhile, heat the remaining oil in a large, non-stick frying pan over a high heat. Season your steak well and, when the pan is very hot, fry for 3-4 mins each side for medium-rare, then leave it to rest until you are ready to eat. While the steak is resting, throw the mushrooms into the still-hot pan, fry for 2-3 mins until browned, then move to one side of the pan. Add the spinach to the other side of the pan and cook until wilted. Put a saucepan of water on to boil. Crack the egg into the boiling water, then reduce the heat until the water is gently simmering. Poach the egg for 3-4 mins or until the white has set but the yolk is still runny. Carefully lift it out with a slotted spoon and drain on kitchen paper. Serve everything together and top with the poached egg.','5 mins','20 mins'),
(1366,'apricot & hazelnut muesli','https://www.bbcgoodfood.com/recipes/apricot-hazelnut-muesli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apricot-hazelnut-muesli-440-400-8119236.jpg','Packed with filling oats, nuts, seeds and dried fruit, this delicious cereal is ready in just 15 minutes. Make this gluten-free by using gluten-free oats or serve with non-dairy milk for a vegan breakfast',4,6,1,3,26,'Toast the porridge oats in a frying pan over a gentle heat, stirring frequently. Turn off the heat and stir in the nuts, seeds and cinnamon until fully combined. Tip into a large bowl, stirring to help it cool, then add the fruit, breaking the apple crisps into smaller pieces. Toss to combine. If you''re following our Healthy Diet Plan, tip two-thirds into an airtight container for other days on the plan. Will keep for up to two weeks. Serve the rest in two bowls with 100ml milk and 40g berries in each.','10 mins','5 mins'),
(1367,'avocado & black bean eggs','https://www.bbcgoodfood.com/recipes/avocado-black-bean-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado_blackbean_eggs-9b605bb.jpg','Set yourself up for the day with this healthy veggie breakfast with eggs, avocado and black beans. It takes just 10 minutes to throw together and makes a great lunch, too',4,2,1,3,26,'Heat the oil in a large non-stick frying pan. Add the chilli and garlic and cook until softened and starting to colour. Break in the eggs on either side of the pan. Once they start to set, spoon the beans (with their juice) and the tomatoes around the pan and sprinkle over the cumin seeds. You’re aiming to warm the beans and tomatoes rather than cook them. Remove the pan from the heat and scatter over the avocado and coriander. Squeeze over half of the lime wedges. Serve with the remaining wedges on the side for squeezing over.','5 mins','5 mins'),
(1667,'warm chorizo & chickpea salad','https://www.bbcgoodfood.com/recipes/warm-chorizo-chickpea-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/warm-chorizo-chickpea-salad-ee46dcc.jpg','Full of bold flavours like chorizo, dried tomatoes and red onion, this salad is bulked out with chickpeas to give 3 of your 5 a day',1,4,1,3,17,'In a large frying pan, dry-fry the chorizo until golden for about 10 mins, then use a slotted spoon to scoop it from the pan and set aside. Add the onion and peppers to the pan and soften in the chorizo fat for 10 mins. Stir in the chickpeas and tomatoes, warming through. Pour in the red wine vinegar and season. Serve in bowls with handfuls of rocket on top.','5 mins','20 mins'),
(1368,'seeded soda bread with hummus & tomatoes','https://www.bbcgoodfood.com/recipes/seeded-soda-bread-with-hummus-tomatoes','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Seeded-soda-bread-with-hummus-06c2556.jpg','Pair our seeded soda bread with homemade hummus and tomatoes to start the day off with a low-GI option that will sustain you through the morning',106,8,1,3,26,'First, make the hummus. Put all the ingredients in a bowl and blitz with a hand blender and 4 tbsp water from the canned chickpeas until smooth. Chill until needed. Heat the oil in a large non-stick frying pan, then add the whole cherry tomatoes in batches (on or off the vine) and cook over a medium heat for just a few minutes until they look on the point of bursting. To serve two for breakfast on the Healthy Diet Plan, toast 3 slices of the bread. Use a quarter of the hummus to spread over the toast, then top with a quarter of the tomatoes and scatter with ½ tsp of the seeds. Halve each slice of toast and serve 3 pieces per person. Keep the remaining bread, hummus and tomatoes chilled to serve on three subsequent mornings. Will keep chilled for up to four days.','5 mins','30 mins - 5 mins'),
(1369,'healthy shakshuka','https://www.bbcgoodfood.com/recipes/healthy-shakshuka','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/shakshuka-9bc1852.jpg','Start the day on a lighter note with this satisfying shakshuka, a wonderful pan full of tomatoes, pepper, spinach and eggs. It packs in four of your 5-a-day',14,2,1,3,26,'Heat the oil in a large, non-stick frying pan. Add the onion and peppers and fry over a medium heat for 8-10 mins until the veg is beginning to soften. Add the garlic, cumin, coriander and paprika and fry for 1 min more. Tip in the tomatoes, spinach and 100ml water and bubble until the spinach has wilted, then lower to a simmer and cook, uncovered, for 10 mins. Season to taste. Make four indentations in the tomato mixture and gently crack an egg into each one. Cover with a lid or foil and cook over a gentle heat for 8-10 mins, or until the eggs are just set. Uncover, scatter with the fresh herbs and serve.','10 mins','30 mins'),
(1370,'black beans & avocado on toast','https://www.bbcgoodfood.com/recipes/mexican-beans-avocado-toast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mexican-beans-and-avo-33f4279.jpg','A vibrant Mexican-style breakfast with fresh avocado and black beans. Give yourself a healthy start with our easy vegan beans on toast with a twist',19,4,1,3,26,'Mix the tomatoes, ¼ onion, lime juice and 1 tbsp oil and set aside. Fry the remaining onion in 2 tbsp oil until it starts to soften. Add the garlic, fry for 1 min, then add the cumin and chipotle and stir until fragrant. Tip in the beans and a splash of water, stir and cook gently until heated through. Stir in most of the tomato mixture and cook for 1 min, season well and add most of the coriander. Toast the bread and drizzle with the remaining 1 tbsp oil. Put a slice on each plate and pile some beans on top. Arrange some slices of avocado on top, then sprinkle with the remaining tomato mixture and coriander leaves to serve.','20 mins','10 mins'),
(1371,'herb omelette with fried tomatoes','https://www.bbcgoodfood.com/recipes/herb-omelette-fried-tomatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/omelette-4fe3f28.jpg','Make a perfect, protein-rich brunch or breakfast for two people. This healthy omelette can be on the table in just 10 minutes, served with juicy tomatoes',4,2,1,3,26,'Heat the oil in a small non-stick frying pan, then cook the tomatoes cut-side down until starting to soften and colour. Meanwhile, beat the eggs with the herbs and plenty of freshly ground black pepper in a small bowl. Scoop the tomatoes from the pan and put them on two serving plates. Pour the egg mixture into the pan and stir gently with a wooden spoon so the egg that sets on the base of the pan moves to enable uncooked egg to flow into the space. Stop stirring when it’s nearly cooked to allow it to set into an omelette. Cut into four and serve with the tomatoes.','5 mins','5 mins'),
(1372,'smoky mushroom & potato hash with oaty thins','https://www.bbcgoodfood.com/recipes/smoky-mushroom-potato-hash-with-oaty-thins','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Hash-with-eggs-26ad895.jpg','Serve our mushroom & potato hash with biscuity ‘thins’ to give texture to the hash and eggs, and also pack in cholesterol-busting oats',4,2,1,3,26,'Tip the oats and soya milk into a large bowl and blitz using a hand blender to break down the oats to a less coarse texture. Set aside for 10 mins to soak. Meanwhile, boil the potatoes for 5 mins, then drain. Heat the oil in a large non-stick frying pan over a medium heat, and cook the mushrooms, onion and paprika for a few minutes until softened. Tip in the potatoes and cook for 10 mins, turning the mixture over every now and then. Stir in the halved tomatoes and leave to cook for 5 mins. The oat mixture should now be stiff. Work in the baking powder using your hands, then halve the mixture. With wet hands, press out one half of the mixture on a plastic chopping board to make a thin disc, like a pancake. Carefully lift it off with a palette knife and cook in a dry non-stick frying pan for 2 mins on each side. Remove to a plate, and repeat with the other half. While the second one cooks, push the potato mixture to the side in the other pan, break in the eggs and cook until the whites are set and the yolks are runny, around 2-3 mins. Serve the oat thins topped with the mushroom hash and eggs.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','50 mins'),
(1373,'wholewheat flatbreads with beans & poached egg','https://www.bbcgoodfood.com/recipes/wholewheat-flatbreads-beans-poached-egg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/wholewheat-flatbreads-with-beans-poached-egg-ce00103-scaled.jpg','This wholesome, flavourful dish makes for a really substantial brunch - if you''re eating earlier for breakfast you could serve half the beans.',4,2,1,3,26,'Tip the passata into a food processor with the onions, date and paprika, and blitz until completely smooth. Heat in a medium pan, cover and simmer for 10 mins, stirring frequently, to make a thick pulpy sauce. Taste to make sure the onion is fully cooked. If not, add a splash of water and cook a little longer. Stir in the vinegar and beans, then remove from the heat. To make the flatbreads, tip the flour and baking powder into a bowl, then stir in the yogurt to make a soft dough. Tip out onto a lightly floured surface and lightly knead, fully incorporating any flour left in the bowl. Halve the mixture and flatten each piece to a rough oval, using your hands or a rolling pin, to a thickness of two £1 coins. Cut slashes through the centre of the ovals a couple of times with a sharp knife, being careful not to cut through an edge. Heat a large, non-stick pan, add a flatbread and cook for 1 min each side until firm and slightly puffed, then repeat with the other. Meanwhile, heat a large pan of water and poach the eggs to your liking. Warm the beans and serve on top of each flatbread with a poached egg and some black pepper.','10 mins','25 mins'),
(1374,'oat & chia porridge with prunes','https://www.bbcgoodfood.com/recipes/oat-chia-porridge-prunes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/oat-chia-porridge-with-prunes-82781bd.jpg','Make this porridge the night before so you don''t miss breakfast. It offers a good dose of omega-3 and can help lower cholesterol as part of a healthy diet',4,2,1,3,26,'The night before, put the prunes in a small pan with the cinnamon. Cover scantily with water and bring to the boil, then simmer for 5 mins. Tip into a bowl and set aside to soak overnight. Put a kettle full of water on to boil. Tip the oats and chia seeds into a bowl, pour over 300ml boiling water then stir well. Cover and leave to soak overnight too. The next morning, stir the vanilla and half the yogurt into the oat mixture then dilute to the consistency you like best with a little milk or water if necessary. Spoon into bowls and top with the remaining yogurt, the prunes, pears and seeds, if using, then dust with a little more cinnamon, if you like.','10 mins','5 mins'),
(1375,'feta & roasted tomato shakshuka','https://www.bbcgoodfood.com/recipes/feta-roasted-tomato-shakshuka','https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Feta-and-roasted-tomato-shakshuka-ce25544.jpg','Indulge in a moreish shakshuka for brunch or lunch, with roasted tomatoes, perfectly gooey eggs, and crisp, golden feta, halloumi and chunks of bread',124,4,1,3,26,'Heat the oven to 200C/180C fan/gas 6. Arrange the tomatoes cut-side down in a roasting tin, drizzle with some olive oil and season. Roast for 20 mins. Set aside to cool. Meanwhile, cook the onions in a medium ovenproof frying pan with the 2 tsp olive oil over a medium heat until they start to caramelise, about 8 mins. Once the onions have softened, add the peppers and season with salt, pepper and the pul biber. Peel the skins from the roasted tomatoes, then add these to the pan of onions and peppers along with all the resting juices. Crush the tomatoes roughly with a wooden spoon and cook for 5 mins over a medium-high heat. Add the wild garlic or spinach and wilt for a minute, then remove from the heat. Tuck the bread pieces in amongst the mixture, then create three gaps for the eggs using the spoon. Crack the eggs into the gaps, crumble over the feta and grate over the halloumi. Drizzle with some extra olive oil and sprinkle with a pinch of pul biber. Turn the oven down to 160C/140C fan/gas 3 and bake the shakshuka for 12-15 mins. The eggs should be cooked but runny in the middle, and the feta, halloumi and bread should be crisp and golden.','15 mins','40 mins'),
(1376,'chia pudding','https://www.bbcgoodfood.com/recipes/chia-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2021/02/Chia-pudding-1667b59.jpg','Rustle up this simple ‘pudding’ for breakfast or dessert. Chia seeds are rich in protein, antioxidants and omega 3, as well as many other nutrients',6,1,1,3,26,'Combine the chia seeds, milk and 2 tsp maple syrup in a jar or bowl. Cover and chill overnight or for at least 8 hrs. When ready to serve, top with fruit and a drizzle of maple syrup.','5 mins','No Time'),
(1377,'egg wraps with black beans & rocket','https://www.bbcgoodfood.com/recipes/egg-wraps-with-black-beans-rocket','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Egg-wraps-8d10eb1.jpg','Make these egg wraps, filled with black beans and salad, to sustain you through the day – eggs keep you fuller for longer than a carb-based meal of equivalent calories',4,2,1,3,26,'Put the pepper in a large non-stick pan with 1 tsp of the oil. Cover and cook over a medium heat for 10 mins, stirring occasionally. Add the garlic and spices, then tip in the bouillon and beans, along with the water in the can, then cook for a few minutes, stirring until slightly reduced. Mash the beans a couple of times to thicken the mixture. For the wraps, beat 2 eggs in a bowl with half the parsley or coriander and half the oats. Heat half the remaining oil in a 21cm non-stick frying pan, and fry the egg mixture for 1 min 30 seconds until almost set, then turn over. Fry for another 30-60 seconds. Tip onto a plate, spoon half the filling down the centre and scatter over half the tomato and rocket, then roll up and serve. Repeat the process with the rest of the ingredients to make the other wrap.','5 mins','20 mins'),
(1685,'beetroot smoothie','https://www.bbcgoodfood.com/recipes/heart-helper-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/heart-helper-c1578c7.jpg','Blend beetroot, apple, blueberries and ginger to create a smooth and heart-healthy drink with depth and zing',5,1,1,3,28,'Put the beetroot, apple, blueberries and ginger in a blender, top up with water then blitz until smooth.','5 mins','No Time'),
(1378,'smoky beans & baked eggs','https://www.bbcgoodfood.com/recipes/smoky-beans-baked-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoky-beans-baked-eggs-4a53fb1.jpg','Pack in the nutrients with smoky beans and baked eggs. Great for a veggie family lunch or supper, serve with flatbreads or toast for extra sustenance',6,3,1,3,26,'Heat the oil in a wide, shallow pan and cook the onion for 8-10 mins until softened. Add the pepper and cook for another 5 mins, stirring regularly, until softened. Stir in the garlic, paprika and ketchup, then tip in the tomatoes, cover and leave to simmer gently for 10 mins. Remove the lid and cook for a few more minutes to thicken, then add the beans. Can be cooled and frozen in portions at this stage. Use a spoon to make 4-6 spaces in the mixture for the eggs – you should be able to see some of the bottom of the pan. Crack the eggs one by one into a cup or bowl and gently drop one into each hole. Cover the pan and reduce the heat to low. Cook for 3-5 mins until the egg whites are cooked through but the yolks are still runny. Scatter over the coriander to serve.','10 mins','35 mins'),
(1379,'apricot & seed overnight chia','https://www.bbcgoodfood.com/recipes/apricot-seed-overnight-chia','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/apricot-seed-overnight-chia-632d4a2.jpg','Kick-start your day with a breakfast of apricots, chia seeds, mixed seeds and blueberries. Soak the chia overnight and it will be ready to go in the morning',14,4,1,3,26,'Put the chia seeds, milk and half the mixed seeds in a medium bowl and stir. Cover and transfer to the fridge to soak overnight. Heat oven to 180C/160C fan/gas 4. Spread the apricots out over a flat baking tray and toss with the vanilla bean paste. Roast for 15-20 mins until soft and just caramelised. Tip the blueberries into a pan with the lemon zest and juice and simmer for 4-5 mins until juicy and bursting. Divide the chia seed mixture between four bowls, then spoon over the apricots, blueberry compote, remaining mixed seeds and a drizzle of honey, if you like.','10 mins','25 mins'),
(1380,'smoky beans on toast','https://www.bbcgoodfood.com/recipes/smoky-beans-toast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoky-beans-on-toast-389ae9b.jpg','Forget shop-bought cans of baked beans. This homemade version is tastier and healthier, with an impressive four of your 5-a-day',92,1,1,3,26,'Heat the oil in a small pan, add the onion and pepper, and fry gently until soft, about 10-15 mins. Crush half the garlic and add this to the pan, along with the tomatoes, paprika, vinegar, beans, sugar and some seasoning. Bring to a simmer and cook for 10-15 mins or until slightly reduced and thickened. Toast the bread, rub with the remaining garlic and drizzle with a little oil. Spoon the beans over the toast, drizzle with a little more oil and scatter over the parsley.','5 mins','20 mins - 30 mins'),
(1381,'raspberry ripple chia pudding','https://www.bbcgoodfood.com/recipes/raspberry-ripple-chia-pudding','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/raspberry-ripple-chia-pudding-e7d93d3.jpg','Fancy raspberry ripple for breakfast? Well now you can with our vegan chia pudding bowl',49,2,1,3,26,'Divide the chia seeds and coconut milk between two serving bowls and stir well. Leave to soak for 5 mins, stirring occasionally, until the seeds swell and thicken when stirred. Meanwhile, combine the purée ingredients in a small food processor, or blitz with a hand blender. Swirl a spoonful into each bowl, then arrange the nectarine or peach slices on top and scatter with the goji berries. Will keep in the fridge for 1 day. Add the toppings just before serving.','15 mins','No Time'),
(1382,'breakfast peppers & chickpeas with tofu','https://www.bbcgoodfood.com/recipes/breakfast-peppers-chickpeas-with-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Tofu-With-Chickpeas-Peppers-70a6360.jpg','Enjoy this simple and healthy vegan breakfast which, thanks to the chickpeas, will keep you feeling full until lunchtime',4,4,1,3,26,'Heat 1 tbsp oil in a large, deep frying pan over a medium heat. Tip in the onions, cover and cook for 5 mins. Remove the lid and stir the onions – they should have softened and started to brown in places. Stir in the pepper, chilli, chopped tomatoes, tomato purée, bouillon powder, oregano, paprika and chickpeas, along with the liquid from the cans. Cover and simmer for 15-20 mins until slightly thickened. Meanwhile, slice half the tofu and fry in ½ tbsp oil over a medium heat until lightly golden. Combine the yogurt and garlic in a small bowl. If you''re following the Healthy Diet Plan, serve half the tomato and chickpea mixture with the tofu, half the yogurt and a scattering of parsley and extra paprika. Leave the leftovers to cool completely and chill the remaining chickpea mixture and yogurt for up to three days. Reheat the chickpea mixture in a small pan with a splash of water until piping hot, then fry the remaining tofu as above before serving.','20 mins','25 mins'),
(1507,'masala omelette muffins','https://www.bbcgoodfood.com/recipes/masala-omelette-muffins','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/masala-44831e7.jpg','This recipe makes four large muffin-shaped bakes which you can eat over two days. Serve with slaw, salad or cooked vegetables for a healthy lunch option',4,4,1,3,24,'Heat oven to 220C/200C fan/ gas 7 and lightly oil four 200ml ramekins. Grate the courgettes and squeeze really well, removing as much liquid as possible. Put all the ingredients except the feta in a large jug and mix really well. Pour into the ramekins, scatter with the feta and bake on a baking sheet for 20-25 mins until risen and set. You can serve the muffins hot or cold with salad, slaw or cooked vegetables.','10 mins','20 mins - 25 mins'),
(1383,'dhal poached eggs with herby raita','https://www.bbcgoodfood.com/recipes/dhal-poached-eggs-with-herby-raita','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/Dhal-poached-eggs-e700674.jpg','Try this new take on dhal with poached eggs for a healthy, nutrient-rich meal. Served with a herby raita, it delivers four of your 5-a-day',4,4,1,3,27,'Heat the oil in a large frying pan over a medium heat and fry the onions for 5 mins until they start to colour. Stir in the spices, garlic and chilli, and cook for a few more seconds until aromatic. Add the lentils, chickpeas and stock, then cook, uncovered, for 15 mins until the lentils are tender. Stir in the spinach until wilted. Remove half the dhal from the pan and leave to cool. Will keep chilled for up to three days. Break 4 eggs, spaced apart, into the lentils that are left in the pan, cover and cook over a medium heat for 8-10 mins. Meanwhile, put all the ingredients for the raita in a bowl and blitz together using a hand blender. Serve the eggs and dhal with half the raita and scatter over a few extra coriander leaves, if you like. The remaining raita will keep chilled for up to three days. Reheat the remaining dhal in a pan over a medium heat until bubbling. Add a drop of water if it looks dry, then cook the eggs in the hot dhal as described in step 3 and serve with the remaining raita.','10 mins','35 mins'),
(1384,'moroccan freekeh traybake','https://www.bbcgoodfood.com/recipes/moroccan-freekeh-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentils-15790dc.jpg','Combine chickpeas and freekah with cherry tomatoes, olives and apricots to make this easy vegetarian one-pan supper. It''s tasty and healthy, serving up three of your 5-a-day',5,2,1,3,27,'Heat oven to 200C/180C fan/ gas 6. Toss the oil with the chickpeas, spices and chilli flakes in a medium roasting tin. Roast for 15 mins or until the chickpeas are beginning to crisp and turn golden brown. Add the tomatoes, apricots, olives and freekeh to the pan and toss everything together. Return to the oven for a final 10-15 mins until the tomatoes start to burst and everything is piping hot. Season to taste. Combine the yogurt and most of the dill and season with salt. Serve the freekeh with any extra dill fronds scattered over and a dollop of the herby yogurt.','5 mins','30 mins'),
(1385,'fennel-roasted cauliflower with quinoa','https://www.bbcgoodfood.com/recipes/fennel-roasted-cauliflower-with-quinoa','https://images.immediate.co.uk/production/volatile/sites/30/2021/03/Fennel-roasted-cauliflower-with-quinoa-2437e83.jpg','Pile this colourful vegetable quinoa salad onto a plate, top with roasted cauliflower and drizzle over the garlic yogurt for a healthy, gluten-free dinner',64,4,1,3,27,'Heat the oven to 200C/180C fan/gas 6. Bring a large pan of salted water to the boil and cook the cauliflower for 5 mins. Drain and spread out on a surface so any excess water evaporates. Crush the fennel and coriander seeds using a pestle and mortar and mix with the paprika and a pinch of seasoning Put the cauliflower in a large bowl, drizzle with half the olive oil and sprinkle over the spice mix. Toss the florets to fully coat them. Tip the florets onto a baking tray and space them apart. Put the red onions, peppers and courgettes on a separate baking tray, drizzle with the remaining oil, and cook both for 30-35 mins, turning halfway through so they brown all over and turn slightly crisp in places. Mix the garlic with lemon juice and stir through the yogurt, adding a little extra water to loosen if needed. Stir the roasted onions, peppers and courgettes into the cooked quinoa along with the lemon zest and a pinch of salt. Pile the quinoa salad onto a plate, then top with the cauliflower florets. Drizzle over the garlic yogurt.','15 mins','40 mins'),
(1386,'veggie yaki udon','https://www.bbcgoodfood.com/recipes/veggie-yaki-udon','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/yaki-udon-19d4efb.jpg','Pack in the veg with our flavour-packed Japanese-inspired yaki udon. It''s healthy, takes just 25 minutes to make, and is low in fat and calories',14,2,1,3,27,'Heat the oil in a non-stick frying pan or wok over a high heat. Add the onion and fry for 5 mins. Stir in the mangetout, corn, pak choi and spring onions and cook for 5 mins more. Add the garlic, curry powder and soy sauce, and cook for another minute. Add the udon noodles along with the ginger (if using) and reserved brine, and stir in 2-3 tbsp hot water until the noodles are heated through. Divide between bowls and serve.','10 mins','15 mins'),
(1387,'sweet & sour tofu','https://www.bbcgoodfood.com/recipes/sweet-sour-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweet-and-sour-tofu-51da0af.jpg','Enjoy our sweet and sour tofu for one. With pineapple, red pepper, onion and Chinese flavourings, it’s vegan and healthy, delivering three of your 5-a-day',14,1,1,3,27,'Heat half the oil in a non-stick frying pan over a medium heat. Add the tofu and fry for 5 mins, turning regularly, until golden brown on all sides. Remove to a plate with a slotted spoon and set aside. Heat the remaining oil in the pan over a high heat. Fry the onion, pepper and garlic for 5-6 mins, or until the veg begins to soften. Add the pineapple, ketchup, vinegar, soy sauce and 50ml water, and simmer for 1 min, or until slightly reduced. Stir the tofu back into the pan. Cook the basmati rice following pack instructions. Serve the tofu in bowls with the rice and a sprinkling of sesame seeds.','10 mins','15 mins'),
(1388,'penne with cabbage & walnuts','https://www.bbcgoodfood.com/recipes/penne-with-cabbage-walnuts','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Penne-with-walnuts-6539545.jpg','Combine magnesium-rich walnuts with cabbage for a filling, healthy pasta dish. Cabbage is rich in inulin, which helps boost magnesium absorption',4,4,1,3,27,'Cook the penne in a pan of boiling water for 12 mins until tender. Meanwhile, heat the oil in a large pan over a medium heat and fry the cabbage for 8-10 mins until softened. Add the chilli and garlic and cook for 3-4 mins more until the garlic is starting to colour. Add the nuts and cook for 1 min more. Remove from the heat and stir in the lemon zest. Drain the penne, reserving the cooking water. Add the drained pasta to the cabbage mixture along with the lemon juice and two ladlefuls of the reserved water (if needed, to loosen), and toss everything well to combine. Serve half between two people. Cool and chill the rest to eat another day. To serve, reheat in a pan over a low heat with a splash of water until piping hot – don’t allow the penne to go soft. Will keep chilled for up to three days.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','15 mins'),
(1389,'five-bean chilli','https://www.bbcgoodfood.com/recipes/five-bean-chilli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/five-bean-chilli-89538ef.jpg','Batch-cook this vegan five-bean chilli, then freeze in portions for busy weeknights. With beans, tomatoes and peppers, it''s full of nutritious, filling veg',14,4,1,3,27,'Heat the oil in a casserole dish and fry the onion and peppers for 10 mins over a medium heat until the onion is golden brown. Add the garlic and spices, and fry for 1 min. Pour in the tomatoes, both cans of beans, 50ml water, then add the sugar and season. Simmer, stirring regularly, for 15-20 mins until thickened. Meanwhile, cook the rice following pack instructions. Serve the chilli on the rice and scatter over the coriander. Top with a spoonful of soured cream, or guacamole, if you like.','5 mins','30 mins'),
(1390,'little spicy veggie pies','https://www.bbcgoodfood.com/recipes/little-spicy-veggie-pies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/little-spicy-veggie-pies-440-400-947e1e1.jpg','Each individual potato-topped pie provides four of your 5-a-day, and is healthy, vegan, low calorie and gluten-free too. Get ahead and freeze for another day',4,4,1,3,27,'To make the topping, boil the potatoes for 15-20 mins until tender then drain, reserving the water, and mash with the ground and fresh coriander and yogurt until creamy. While the potatoes are boiling, heat the oil in a large pan, add the ginger and fry briefly, tip in the curry powder and garlic, stirring quickly as you don’t want it to burn, then tip in a can of chickpeas with the water from the can. Stir well, then mash in the pan to smash them up a bit, then tip in the second can of chickpeas, again with the water from the can, along with the carrots, corn, bouillon and tomato purée. Simmer for 5-10 mins, adding some of the potato water, if needed, to loosen. Heat the oven to 200C/180C fan/gas 6. Spoon the filling into four individual pie dishes (each about 10cm wide, 8cm deep) and top with the mash, smoothing it to seal round the edges of the dishes. If you''re following our Healthy Diet Plan, bake two for 25 mins until golden, and cook half the spinach, saving the rest of the bag for another day. Cover and chill the remaining two pies to eat another day. Will keep in the fridge for four days – if following the Healthy Diet Plan pop the extra two pies in the freezer for the end of the week. If freezing, to reheat, bake from frozen for 40-45 mins until golden and piping hot.','10 mins','55 mins'),
(1391,'chickpea & coriander burgers','https://www.bbcgoodfood.com/recipes/chickpea-coriander-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-654456_10-5dfdcd6.jpg','High in fibre, low in fat and counting as 2 of your 5-a-day, this tasty veggie burger delivers on every level',5,4,1,3,27,'In a food processor, whizz the chickpeas, lemon zest, lemon juice, cumin, half the coriander, the egg and some seasoning. Scrape into a bowl and mix with 80g of the breadcrumbs and the diced onions. Form 4 burgers, press remaining breadcrumbs onto both sides and chill for at least 10 mins. Heat the oil in a frying pan until hot. Fry the burgers for 4 mins each side, keeping the heat on medium so they don’t burn. To serve, slice each bun and fill with a slice of tomato, a burger, a few red onion slices, some cucumber slices, a dollop of chilli sauce and the remaining coriander.','15 mins','10 mins'),
(1392,'jerk-style cauliflower with coconut rice','https://www.bbcgoodfood.com/recipes/jerk-style-cauliflower-with-coconut-rice','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Jerk-Style-Cauliflower-With-Coconut-Rice-2c54f98.jpg','Cauliflower roasted in the oven takes on a delicious, nutty flavour. Here, it is coated in a spicy jerk paste and served on brown rice with vegan-friendly coconut yogurt',5,4,1,3,27,'Heat the oven to 200C/180C fan/gas 6. Put the chilli, garlic, ginger, oil, spices and lemon juice in a small bowl with the yogurt and blitz using a hand blender until smooth. Cut the cauliflower carefully into eight, ensuring that you slice through the stalk each time as this will hold it together. Brush the spice paste over both sides of the cauliflower steaks, then arrange on a baking sheet (use two if needed) rubbed with a little oil and roast for 30 mins. Turn over and roast for 20 mins more, or until the cauliflower is tender. Meanwhile, put the rice in a large pan with 450ml water, the bouillon and thyme. Bring to the boil, cover and cook for 10 mins, then add the peppers and spring onions, cover again and simmer for 10-15 mins more until the rice is tender. Check towards the end that the water hasn’t run dry as you may need to add a splash more. Add the beans and yogurt and warm through. If you''re following the Healthy Diet Plan, serve half the cauliflower and rice, then chill the rest for another day. Will keep for up to two days in the fridge.','20 mins','35 mins'),
(1508,'basil & olive eggs','https://www.bbcgoodfood.com/recipes/basil-olive-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/stuffed-eggs-hdp-snack-recipe-image-440-400-acc2fae.jpg','Get a boost of protein with eggs stuffed with basil and Kalamata olives. They''d work as a tasty lunch alongside a salad as well as being a healthy vegetarian snack',5,2,1,3,24,'Boil the eggs for 8 mins then cool in cold water. Peel and halve. Scoop out the yolks. Put the basil, olives, oil, vinegar and a grinding of pepper in a small bowl. Blitz with a hand blender then add the yolk and roughly mash together. Spoon back into the eggs and keep in the fridge.','5 mins','8 mins'),
(1393,'artichoke & aubergine rice','https://www.bbcgoodfood.com/recipes/artichoke-aubergine-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/artichoke-aubergine-rice-recipe-image-2e7d0a8.jpg','As well as being tasty, this aubergine and artichoke is low fat, low calorie and cost effective. Make a large batch and eat it cold the next day',41,6,1,3,27,'Heat 2 tbsp of the oil in a large non-stick frying pan or paella pan. Fry the aubergines until nicely coloured on all sides (add another tbsp of oil if the aubergine begins catching too much), then remove and set aside. Add another tbsp of oil to the pan and lightly fry the onion for 2-3 mins or until softened. Add the garlic and parsley stalks, cook for a few mins more, then stir in the spices and rice until everything is well coated. Heat for 2 mins, add half the stock and cook, uncovered, over a medium heat for 20 mins, stirring occasionally to prevent it from sticking. Nestle the aubergine and artichokes into the mixture, pour over the rest of the stock and cook for 20 mins more or until the rice is cooked through. Chop the parsley leaves, stir through with the lemon juice and season well. Bring the whole pan to the table and spoon into bowls, with the lemon wedges on the side.','15 mins','50 mins'),
(1394,'tomato, pepper & bean one pot','https://www.bbcgoodfood.com/recipes/tomato-pepper-bean-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-pepper-bean-one-pot-398c4b2.jpg','Make a batch of this stew at the weekend, then chill or freeze it in portions for easy midweek lunches. See our different toppings to keep it interesting',12,6,1,3,27,'Heat the oil in a large pan or casserole on a medium heat. Fry the onion, celery and carrots for 10 mins until soft and golden, then add the peppers and fry for another 5 mins. Stir in the garlic for a minute, then add the tomato purée, all the beans and chopped tomatoes, then swirl out the tomato cans with a splash of water and add to the pan with the stock cube, bay leaves, sugar and vinegar. Season and simmer, uncovered, for 25 mins until the sauce reduces to coat the beans and the peppers are soft. Leave to cool before storing in transportable containers. Will keep in the fridge for 3 - 4 days or freeze in portions and defrost in the fridge overnight.Choose your toppingsSweet & spicyAdd diced dried apricots and 1 tbsp harissa. Top with yogurt swirled with more harissa, and toasted flaked almonds.Tex-MexStir in ½ - 1 tbsp chipotle paste, shredded leftover roast chicken if you have any, and top with diced avocado, grated cheddar and coriander.Smoky BBQ beansStir in 1 tbsp smoky BBQ sauce and crumble over shop-bought crispy bacon, a dollop of soured cream or yogurt, and some chopped herbs.Added greensStir in some spinach and top with a sliced boiled egg.Beans on toastServe the beans on toast or bread, add a dash of Tabasco or chilli flakes, crumble over feta and drizzle with olive oil.Italian-inspiredTop with toasted croutons, chopped rosemary, lemon zest and parmesan.','15 mins','45 mins'),
(1395,'veggie tahini lentils','https://www.bbcgoodfood.com/recipes/veggie-tahini-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-tahini-lentils-22a5475.jpg','Quick, easy and packed with healthy veg, this is a great midweek meal for vegans and veggies',16,4,1,3,27,'In a jug, mix the tahini with the zest and juice of the lemon and 50ml of cold water to make a runny dressing. Season to taste, then set aside. Heat the oil in a wok or large frying pan over a medium-high heat. Add the red onion, along with a pinch of salt, and fry for 2 mins until starting to soften and colour. Add the garlic, pepper, green beans and courgette and fry for 5 min, stirring frequently. Tip in the kale, lentils and the tahini dressing. Keep the pan on the heat for a couple of mins, stirring everything together until the kale is wilted and it’s all coated in the creamy dressing.','10 mins','10 mins'),
(1396,'easy vegan tacos','https://www.bbcgoodfood.com/recipes/chargrilled-vegetable-tacos-smoky-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chargrilled-tacos-3060d1e.jpg','Make these tasty vegan tacos with a smoky-sweet salsa and pack in all of your 5-a-day in one meal. Kiwi brings a moreish, fruity dimension to the salsa',4,2,1,3,27,'Heat oven to 220C/200C fan/gas 7. Pile the corn, red onion and pepper into a large shallow roasting tin and toss with the cumin seeds and oil. Add the kiwi and tomato on one side of the tin and roast for 20 mins. Meanwhile, mix 60ml water into the flour with the blade of a knife to make a dough. Knead briefly until smooth, then cut equally into four and roll out each piece on a lightly floured surface into a 16cm round tortilla. Cover with a tea towel to stop them drying out. Remove the cooked tomato and kiwi from the tin and return the veg to the oven for 10 mins. Remove the skin from the kiwi and scoop the flesh into a bowl with the tomato, garlic, half the coriander, bouillon and paprika. Use a hand blender to blitz to a smooth salsa. Heat a large non-stick frying pan, without oil, and cook the tortillas one at a time for a minute on one side and about 10 seconds on the other, until you see them puff up a little. Spread a tortilla with some salsa, top with cabbage and roasted veg, then scatter with the remaining coriander. Add a spoonful more salsa and eat with your hands.','10 mins','30 mins'),
(1397,'mint & basil griddled peach salad','https://www.bbcgoodfood.com/recipes/mint-basil-griddled-peach-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mint-and-basil-griddled-peach-salad-1-440-400-777204a.jpg','This vegan salad makes a healthy and filling meal, providing 4 of your 5-a-day. Enjoy hearty quinoa, creamy avocado and juicy griddled peaches',4,2,1,3,27,'Mix the lime zest and juice, oil, mint and basil, then put half in a bowl with the peaches. Meanwhile, cook the quinoa following pack instructions. Cook the beans for 3-4 mins until just tender. Meanwhile, griddle the peaches for 1 min on each side. If you don’t have a griddle pan, use a large non-stick frying pan with a drop of oil. Drain the quinoa and divide between shallow bowls. Toss the warm beans and onion in the remaining mint mixture and pile on top of the quinoa with the lettuce and rocket. Top with the avocado and peaches and scatter over the mint leaves. Serve while still warm.','10 mins','25 mins'),
(1686,'vitamin booster smoothie','https://www.bbcgoodfood.com/recipes/vitamin-booster-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vitamin-booster-3c28ae8.jpg','Up your vitamin quota with help from this bright and fresh smoothie. Orange, carrot, celery and mango pack a nutritious punch',5,1,1,3,28,'Put all the orange, carrot, celery and mango in the blender, top up with water, then blitz until smooth.','5 mins','No Time'),
(1399,'sweet potato & cauliflower lentil bowl','https://www.bbcgoodfood.com/recipes/sweet-potato-cauliflower-lentil-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sweet-potato-bowl-6743798.jpg','Whip up this zingy vegan bowl in advance and keep in the fridge for quick, satisfying lunches on demand. Each bowlful boasts four of your 5-a-day',16,4,1,3,27,'Heat oven to 200C/180C fan/gas 6. Toss the sweet potato and cauliflower with the garam masala, half the oil and some seasoning. Spread out on a large roasting tray. Add the garlic and roast for 30-35 mins until cooked. Meanwhile, put the lentils in a saucepan with 400ml cold water. Bring to the boil, then simmer for 20-25 mins until the lentils are cooked but still have some bite. Drain. Remove the garlic cloves from the tray and squish them with the blade of your knife. Put the garlic in a large bowl with the remaining oil, ginger, mustard, a pinch of sugar and one-third of the lime juice. Whisk, then tip in the warm lentils, stir and season to taste. Coarsely grate the carrots, shred the cabbage and roughly chop the coriander. Squeeze over the remaining lime juice and season to taste. Divide the lentil mixture between four bowls (or four containers if saving and chilling). Top each serving with a quarter of the carrot slaw and a quarter of the sweet potato and cauliflower mix.','20 mins','35 mins'),
(1400,'smashed cannellini bean crostini','https://www.bbcgoodfood.com/recipes/smashed-cannellini-bean-crostini','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/Smashed-cannellini-bean-crostini-0ebca31.jpg','Add a little more oil to this smashed cannellini bean dip, if you prefer – the more you add, the silkier it becomes',7,4,1,3,27,'Heat half the oil in a shallow saucepan over a low-medium heat and scatter in the sage leaves, if using. Fry for 2 mins until crisp, lift onto a plate using a slotted spoon. Tip the onion into the pan and cook for 3 mins until soft, then add the garlic and cook for 1 min more. Sprinkle in the pepper and toast for 1 min, then stir in the beans. Turn off the heat. Stir in the lemon juice. Season the mixture with salt, then tip into a food processor or mini chopper with the remaining oil. Blitz everything together to your desired consistency – it can be a smooth or a rough paste. Will keep covered in the fridge for two days. Bring to room temperature 1 hour before serving. Serve the dip as is with crostini or breadsticks, or drizzle with a little extra olive oil and top with the crispy sage leaves, if using.','10 mins','10 mins'),
(1401,'chickpea salad','https://www.bbcgoodfood.com/recipes/no-cook-chickpea-salad','https://images.immediate.co.uk/production/volatile/sites/30/2017/05/chickpea-salad-021d806-e1655726572507.jpg','Use a touch of harissa to liven up a healthy chickpea salad. It takes just 10 minutes to whip up and makes an ideal side dish for slow-cooked Greek lamb',25,6,1,3,27,'Mix all the ingredients together, mashing a little so the chickpeas are a bit rough round the edges – this helps absorb the dressing. (Can be made a day ahead and kept in the fridge.) Try it with slow-cooked Greek lamb and tzatziki sauce.','10 mins','No Time'),
(1402,'spring tabbouleh','https://www.bbcgoodfood.com/recipes/spring-tabbouleh','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spring-tabbouleh-019158d.jpg','A simple, budget, spring salad that can add a healthy dose of vegetables to your midweek meal. This easy vegan dish also makes great leftover lunches',16,4,1,3,27,'Heat oven to 200C/180C fan/ gas 6. Mix 4 tbsp oil with the garam masala and some seasoning. Toss with the chickpeas in a large roasting tin, then cook for 15 mins until starting to crisp. Tip in the mixed grains, peas and lemon zest. Mix well, then return to the oven for about 10 mins until warmed through. Transfer to a large bowl or platter, then toss through the herbs, radishes, cucumber, remaining oil and lemon juice. Season to taste and scatter over the pomegranate seeds. Any leftovers will be good for lunch the next day.','20 mins','25 mins'),
(1403,'vegan baked oats','https://www.bbcgoodfood.com/recipes/vegan-almond-berry-baked-oats','https://images.immediate.co.uk/production/volatile/sites/30/2022/06/Almond-Berry-Baked-Oats-b0f57ba.jpg','Try our almond and mixed berry baked oats for breakfast or as a lovely, light pudding. Frozen berries are an economical choice, even in summer',4,4,1,3,27,'Heat the oven to 190C/170C fan/ gas 5. Tip the oats into a heatproof bowl, then pour over 450ml boiling water from the kettle. Stir well, then stir in the vanilla and almond milk. Mash the banana, then spread over the base of a medium baking dish (ours was a 20 x 28cm oval) and stir in the frozen fruit to fully combine. You want the banana to sweeten the berries, but you can add 1 tsp maple syrup, if you like. Pour over the oat mixture and scatter with the almonds. Bake for30-40 mins until set and the fruit juices are bubbling at the edges of the dish. Scoop into bowls and serve with a little extra milk, if you like.','5 mins','40 mins'),
(1509,'curried turkey lettuce wraps','https://www.bbcgoodfood.com/recipes/curried-turkey-lettuce-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/curried-turkey-lettuce-wraps-0dc0dce.jpg','A 5-minute filler to keep you going until dinner. This low-fat lettuce cup with a touch of spice is a super simple snack that has all the flavours of Coronation chicken',5,2,1,3,24,'Mix the natural yogurt with the curry powder, tomato purée and raisins. Stir in the cooked turkey or chicken, then spoon onto the lettuce leaves. Scatter over the sunflower seeds and the coriander leaves.','5 mins','N/A'),
(1404,'baked salmon & leek parcel','https://www.bbcgoodfood.com/recipes/salmon-leek-parcel','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-leek-parcel-6dd60f6.jpg','A simple salmon dish, parcelled up to seal in the flavours. With creamy mascarpone and fresh leeks, this keto-friendly fish supper is easy and rich in omega-3 ',5,2,1,3,27,'Heat oven to 200C/180C fan/gas 6. Place two sheets of baking parchment (large enough to wrap up each salmon fillet) on your work surface. Put the leeks in a pan with 6 tbsp water, cover and bring to the boil. Cook for 5 mins until the water has been absorbed and the leeks are almost tender. Stir in the mascarpone, 1 tbsp dill and some seasoning. Spoon half the creamy leeks into the middle of one sheet of parchment and place a salmon fillet on top, then repeat to make a second parcel. Sprinkle over the lemon zest with a squeeze of juice, then scatter over the capers and the remaining 1 tsp dill. Bring the parchment up over the fish and join the two edges together by folding them over several times down the middle. Do the same with the ends and place the parcels, spaced apart, on a baking sheet. Bake for 12-15 mins, depending on how well done you like your fish, then carefully tear open the parcel. Serve with lemon wedges for squeezing over and wilted spinach, if you like.','10 mins','20 mins'),
(1405,'mediterranean turkey-stuffed peppers','https://www.bbcgoodfood.com/recipes/mediterranean-turkey-stuffed-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/red-pepper-a97ba7d.jpg','This low-calorie dinner from Joe Wicks is 3 of your 5-a-day, rich in vit C and folate too. Filling the peppers with low-fat turkey breast mince keeps it lean',96,2,1,3,27,'Heat oven to 190C/170C fan/gas 5. Halve the peppers lengthways, then remove the seeds and core but keep the stalks on. Rub the peppers with a drizzle of olive oil and season well. Put on a baking tray and roast for 15 mins. Meanwhile, heat 1 tbsp olive oil in a large pan over a medium heat. Fry the mince for 2-3 mins, stirring to break up the chunks, then tip onto a plate. Wipe out your pan, then heat the rest of the oil over a medium-high heat. Add the onion and garlic, stir-fry for 2-3 mins, then add the cumin and mushrooms and cook for 2-3 mins more. Tip the mince back into the pan and add the chopped tomatoes and tomato purée. Crumble in the stock cube and cook for 3-4 mins, then add the oregano and season. Remove the peppers from the oven and fill them with as much of the mince as you can. (Don’t worry if some spills out it – it will go satisfyingly crisp in the oven.) Top with the cheese and return to the oven for 10-15 mins until the cheese starts to turn golden. Carefully slide the peppers onto a plate and serve alongside a pile of your favourite greens blanched, boiled or steamed.','20 mins','30 mins'),
(1406,'tarragon, mushroom & sausage frittata','https://www.bbcgoodfood.com/recipes/tarragon-mushroom-sausage-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tarragon-mushroom-sausage-frittata-2a21182.jpg','Serve up this filling frittata in just 20 minutes. With tarragon and mustard to boost the flavour and asparagus to top up your greens, it''s also rich in folate',14,2,1,3,27,'Heat the grill to high. Heat the oil in a medium-sized, non-stick frying pan, add the mushrooms and fry over a high heat for 3 mins. Squeeze the sausagemeat out of their skins into nuggets, add to the pan and fry for a further 5 mins until golden brown. Add the garlic and asparagus and cook for another 1 min. Whisk the eggs, soured cream, mustard and tarragon in a jug. Season well, then pour the egg mixture in to the pan. Cook for 3-4 mins, then grill for a further 1-2 mins or until the top has just set with a slight wobble in the middle. Serve with the salad leaves, if you like.','5 mins','15 mins'),
(1407,'one-pot coconut fish curry','https://www.bbcgoodfood.com/recipes/onepot-coconut-fish-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/coconut-fish-curry-462110e.jpg','Cook our easy one-pot curry in just half an hour, with only 5 mins prep. Serve with rice and yogurt, it''s ideal for feeding the family quickly, on a budget. Or serve with cauliflower rice for a keto-friendly dinner.',41,4,1,3,27,'Heat the oil in a large saucepan over a medium heat, add the onion and a big pinch of salt. Gently fry until the onion is translucent, so around 10 mins, then add the garlic and spices. Stir and cook for another minute, adding a splash of water to prevent them sticking. Tip in the coconut milk and stir well, then simmer for 10 mins. Tip the fish pie mix and the frozen peas into the pan and cook until the peas are bright green and the fish is starting the flake, so around 3 mins. Season and add lime juice to taste. Ladle into bowls and serve with yogurt and rice.','5 mins','25 mins'),
(1408,'creamy salmon, leek & potato traybake','https://www.bbcgoodfood.com/recipes/creamy-salmon-leek-potato-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-salmon-leek-potato-traybake-367b3ff.jpg','Nestle leeks, potato and capers around salmon fillets to make this easy traybake for two. It''s great as an midweek meal, or for a more romantic occasion',14,2,1,3,27,'Heat the oven to 200C/180C fan/gas 6. Bring a medium pan of water to the boil. Add the potatoes and cook for 8 mins. Drain and leave to steam-dry in a colander for a few minutes. Toss the potatoes with ½ of the oil and plenty of seasoning in a baking tray. Put in the oven for 20 mins, tossing halfway through the cooking time.  Meanwhile, heat the remaining oil in a frying pan over a medium heat. Add the leek and fry for 5 mins, or until beginning to soften. Stir through the garlic for 1 min, then add the cream, capers and 75ml hot water, then bring to the boil. Stir through the chives. Heat the grill to high. Pour the creamy leek mixture over the potatoes, then sit the salmon fillets on top. Grill for 7-8 mins, or until just cooked through. Serve topped with extra chives and capers and a salad on the side, if you like.','5 mins','35 mins'),
(1409,'ginger & soy salmon en papillote','https://www.bbcgoodfood.com/recipes/ginger-soy-salmon-en-papillote','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-en-papillote-795a4ca.jpg','Steaming the salmon in a parcel, in its own marinade, with the spiralized veg, ensures the fish is juicy and full of flavour. Serve with cauliflower rice for a keto-friendly meal',16,2,1,3,27,'Heat oven to 180C/160C fan/gas 4. Before you prep the veg mix together the soy, vinegar, ginger, garlic and some black pepper in a bowl. Add the salmon fillets, cover and leave to marinate for 10 mins at room temperature or up to 2 hrs in the fridge. Tear two large sheets of baking parchment, big enough to encase the fish and vegetables in and put onto a baking tray. Divide the vegetables between the centre of the paper and top each with a marinated salmon fillet and the sliced chilli. Bring the sides of the parchment up over the salmon and pour half of the remaining marinade over each fillet then scrunch the paper tightly together to seal the fish in a parcel. Roast for 20–25 mins, until the salmon is just cooked through and flakes into large pieces. Serve the fish in the parcel with some cauliflower or regular rice, if you like.','15 mins','25 mins'),
(1410,'swedish meatballs with beetroot & apple salad','https://www.bbcgoodfood.com/recipes/swedish-meatballs-beetroot-apple-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-swedish-meatballs-with-beetroot-apple-salad-1d82399.jpg','Grated courgette makes these lean pork meatballs extra juicy – serve with a creamy, herby sauce and a flavour-packed salad for a healthy, satisfying dinner',4,4,1,3,27,'To make the meatballs, tip the pork into a bowl and add the grated courgette and onion, half the dill, half the garlic and plenty of black pepper. Break in the egg and mix well with your hands. When everything is well combined, break off walnut-size pieces and roll into balls – you should end up with about 28. The mixture will be very soft. Heat the oil in a large non-stick frying pan then gently fry the meatballs in two batches until firm and pale golden, turning them every now and then so they brown evenly. Each batch should take 6-8 mins. Wipe out the pan to remove excess fat and add the remaining garlic and thyme, then pour in the bouillon and bring to the boil. Turn off the heat, stir in the crème fraîche and remaining dill, then return the meatballs to the pan and turn them in the mixture until coated. For the salad, mix the apple, onion, parsley and capers with the lemon, then stir through the beetroot. Serve the salad alongside the meatballs. If you’re cooking for two people, eat half the meatballs and salad now, and chill the rest for another day – it will keep in the fridge for up to three days. Reheat the meatballs in the microwave until piping hot.','20 mins','20 mins'),
(1411,'chicken casserole','https://www.bbcgoodfood.com/recipes/easy-slow-cooker-chicken-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-slow-cooker-chicken-casserole-821b704.jpg','Make this chicken casserole with potatoes, carrot and leek in the morning using a slow cooker and it''ll be ready for dinner. It''s low-fat and low-calorie too.',138,4,1,3,27,'Put the veg and chicken in a slow cooker. Pour the stock over and around the chicken thighs, then mix in the gravy granules to thicken it up (the sauce will be quite thick – use less gravy if you prefer a runnier casserole). Switch the slow cooker to low and leave to cook for at least 4 hrs, or up to 8 hrs – try putting it on before you go to work, so that it’s ready when you get home. Season well, then serve.','10 mins','4 hrs - 8 hrs'),
(1412,'sea bass with sizzled ginger, chilli & spring onions','https://www.bbcgoodfood.com/recipes/sea-bass-sizzled-ginger-chilli-spring-onions','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52125_11-ee6ad2a.jpg','For a sophisticated, healthy and speedy supper, fry sea bass fillets for a few minutes with ginger, garlic, chilli and spring onions',33,6,1,3,27,'Season 6 sea bass fillets with salt and pepper, then slash the skin 3 times. Heat a heavy-based frying pan and add 1 tbsp sunflower oil. Once hot, fry the sea bass fillets, skin-side down, for 5 mins or until the skin is very crisp and golden. The fish will be almost cooked through. Turn over, cook for another 30 seconds - 1 minute, then transfer to a serving plate and keep warm. You’ll need to fry the sea bass fillets in 2 batches. Heat 2 tbsp sunflower oil, then fry the large knob of peeled ginger, cut into matchsticks, 3 thinly sliced garlic cloves and 3 thinly shredded red chillies for about 2 mins until golden. Take off the heat and toss in the bunch of shredded spring onions. Splash the fish with 1 tbsp soy sauce and spoon over the contents of the pan.','15 mins','10 mins'),
(1413,'spiced lamb meatball stew','https://www.bbcgoodfood.com/recipes/spiced-lamb-meatball-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-lamb-meatball-stew-014e307.jpg','Enjoy spiced lamb meatballs with chickpeas and pomegranate seeds in a harissa sauce for an easy midweek meal. Freeze any leftovers for busy weekdays',14,4,1,3,27,'Heat the oil in a large, shallow casserole dish or deep frying pan. Add the meatballs and fry for 3-4 mins, turning regularly, until golden. Remove and set aside. Tip the onion into the pan and fry for 7 mins until softened. Stir through the garlic cumin, coriander and cinnamon, and cook for 1 min. Add the harissa, tomatoes and lamb stock, and bring to a simmer. Stir though the chickpeas and meatballs, season and simmer for 10-15 mins until slightly reduced and the meatballs are cooked through. Stir through half the herbs, then scatter the rest on top along with the pomegranate seeds.','10 mins','30 mins'),
(1414,'summer fish stew','https://www.bbcgoodfood.com/recipes/summer-fish-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/summer-fish-stew-04a4a3b.jpg','This storecupboard fish stew is bulked out with bread and is full of fresh, summer flavours',1,4,1,3,27,'Heat oven to 200C/180C fan/gas 6. Put the bread on a large baking sheet, drizzle over 1 tbsp oil and bake for 10 mins until golden. Set aside. Meanwhile, heat the rest of the oil in a large flameproof casserole dish over a medium heat. Add the onion and cook until softened for about 10 mins, then add the garlic and chilli flakes and stir for 1 min. Tip in the tomatoes and fish fillets. Cover and simmer for 10 mins until the fish is nearly cooked, then uncover. Tip in the butter beans, season well, then cook until everything is hot. Serve scattered with the croutons, parsley and lemon.','10 mins','25 mins'),
(1415,'home-style chicken curry','https://www.bbcgoodfood.com/recipes/home-style-chicken-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-908515_10-0551430.jpg','A rustic and authentic quick Indian one-pot packed with tonnes of hot Asian spices and fragrant coriander.',114,4,1,3,27,'Roughly chop 1 large onion, transfer to a small food processor, and add 3 tbsp of water - process to a slack paste. You could use a stick blender for this or coarsely grate the onion into a bowl – there’s no need to add any water if you are grating the onion. Tip into a small bowl and leave on one side. Put 6 roughly chopped garlic cloves and 50g roughly chopped ginger into the same food processor and add 4 tbsp water – process until smooth and spoon into another small bowl. Alternatively, crush the garlic to a paste with a knife or garlic press and finely grate the ginger. Heat 4 tbsp vegetable oil in a wok or sturdy pan set over a medium heat. Combine 2 tsp cumin seeds and 1 tsp fennel seeds with a 5cm cinnamon stick and 1 tsp chilli flakes and add to the pan in one go. Swirl everything around for about 30 secs until the spices release a fragrant aroma. Add the onion paste – it will splutter in the beginning. Fry until the water evaporates and the onions turn a lovely dark golden - this should take about 7-8 mins. Add the garlic and ginger paste and cook for another 2 mins – stirring all the time. Stir in 1 tsp garam masala, 1 tsp turmeric, and 1 tsp caster sugar and continue cooking for 20 secs before tipping in a 400g can chopped tomatoes. Continue cooking on a medium heat for about 10 mins without a lid until the tomatoes reduce and darken. Cut 8 skinless, boneless chicken thighs into 3cm chunks and add to the pan once the tomatoes have thickened to a paste. Cook for 5 mins to coat the chicken in the masala and seal in the juices, and then pour over 250ml hot chicken stock. Simmer for 8-10 mins without a lid until the chicken is tender and the masala lightly thickened – you might need to add an extra ladleful of stock or water if the curry needs it. Sprinkle with 2 tbsp chopped coriander and serve with Indian flatbreads or fluffy basmati rice and a pot of yogurt on the side.','15 mins','30 mins'),
(1416,'mustard-stuffed chicken','https://www.bbcgoodfood.com/recipes/mustard-stuffed-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1274488_8-6868db5.jpg','This is so good we''d be surprised if this chicken fillet recipe doesn''t become a firm favourite. Save it to your My Good Food collection and enjoy.',5,4,1,3,27,'Heat oven to 200C/fan 180C/gas 6. Mix the cheeses and mustard together. Cut a slit into the side of each chicken breast, then stuff with the mustard mixture. Wrap each stuffed chicken breast with 2 bacon rashers – not too tightly, but enough to hold the chicken together. Season, place on a baking sheet and roast for 20-25 mins.','10 mins','20 mins'),
(1417,'one-pan salmon with roast asparagus','https://www.bbcgoodfood.com/recipes/one-pan-salmon-roast-asparagus','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-52738_10-f28c3e9.jpg','For an easy side dish to complement a spring roast, just cook this recipe without the salmon',7,2,1,3,27,'Heat oven to 220C/fan 200C/gas 7. Tip the potatoes and 1 tbsp of olive oil into an ovenproof dish, then roast the potatoes for 20 mins until starting to brown. Toss the asparagus in with the potatoes, then return to the oven for 15 mins. Throw in the cherry tomatoes and vinegar and nestle the salmon amongst the vegetables. Drizzle with the remaining oil and return to the oven for a final 10-15 mins until the salmon is cooked. Scatter over the basil leaves and serve everything scooped straight from the dish.','20 mins','50 mins'),
(1418,'superhealthy salmon burgers','https://www.bbcgoodfood.com/recipes/superhealthy-salmon-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-195496_11-7d050d4.jpg','If you''re after something a bit lighter than potato-packed fishcakes, try these simple salmon burgers with ginger, soy sauce and coriander',5,4,1,3,27,'Tip the salmon into a food processor with the paste, ginger, soy and chopped coriander. Pulse until roughly minced. Tip out the mix and shape into 4 burgers. Heat the oil in a non-stick frying pan, then fry the burgers for 4-5 mins on each side, turning until crisp and cooked through. Meanwhile, use a swivel peeler to peel strips of carrot and cucumber into a bowl. Toss with the vinegar and sugar until the sugar has dissolved, then toss through the coriander leaves. Divide the salad between 4 plates. Serve with the burgers and rice.','20 mins','10 mins'),
(1419,'miso-roasted aubergine steaks with sweet potato','https://www.bbcgoodfood.com/recipes/miso-roasted-aubergine-steaks-sweet-potato','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso-roasted-aubergine-steaks-31dc83d.jpg','Roast thick slices of aubergine with miso, garlic and ginger, then serve with chunky sweet potato wedges for a filling, vegetarian dinner',21,2,1,3,29,'Heat oven to 180C/160C fan/gas 4. Peel the aubergine with a potato peeler and roughly spread the miso paste all over it – the best way to do this is with the back of a spoon. Put it in a roasting tin along with the sweet potato wedges. Pour 225ml boiling water into the base of the tin, then add the oil, ginger and garlic. Sprinkle a pinch of salt over the wedges and place in the oven. After 30 mins, pour another 125ml boiling water into the base of the tin and roast for another 20 mins. Repeat, adding 50ml boiling water and the spring onions, and roast for 10 mins more. Check the aubergine is cooked by inserting a knife in the centre – if it is ready it will easily slide in and out, and the aubergine will be soft on the inside. Sprinkle the chopped parsley over the potato wedges, slice the aubergine into 2cm thick ‘steaks’ and serve on top of the potatoes. If there is no sauce in the bottom of the tin, add 3 tbsp water to loosen up the miso, then pour the miso gravy over the aubergine steaks and sprinkle with cracked black pepper.','20 mins','1 hr and 5 mins'),
(1420,'spiced chicken, spinach & sweet potato stew','https://www.bbcgoodfood.com/recipes/spiced-chicken-spinach-sweet-potato-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-chicken-spinach-sweet-potato-stew-573e297.jpg','A healthy one-pot with warming spices that''s low fat, low calorie, 3 of your 5-a-day and also full of fibre!',29,4,1,3,29,'Put the sweet potato in a large, deep saucepan over a high heat. Cover with boiling water and boil for 10 mins. Meanwhile, put all the paste ingredients in a food processor and blend until very finely chopped. Set aside until needed. Put the spinach in a large colander in the sink and pour the sweet potatoes and their cooking water over it to drain the potatoes and wilt the spinach at the same time. Leave to steam-dry. Return the saucepan to the heat (no need to wash it first), then add the oil, followed by the spice paste. Fry the paste for about 5 mins until thickened, then add the chicken. Fry for 8-10 mins until the chicken starts to colour. Pour over the stock, bring to the boil and leave to simmer for 10 mins, stirring occasionally. Check the chicken is cooked by cutting into one of the thighs and making sure it’s white throughout with no signs of pink. Season with black pepper, then add the sweet potato. Leave to simmer for a further 5 mins. Meanwhile, roughly chop the spinach and add to the stew. At this point you can leave the stew to cool and freeze for up to 3 months, if you like. Scatter over the pumpkin seeds and preserved lemons, and serve with warm naan bread on the side.','15 mins','40 mins'),
(1421,'low-fat chicken caesar salad','https://www.bbcgoodfood.com/recipes/skinny-chicken-caesar-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/skinny-chicken-caesar-salad-b6f0a81.jpg','Give typically heavy caesar dressing a lighter makeover by swapping mayonnaise for Greek yogurt – it''s lower in fat and calories, but still deliciously creamy.',29,4,1,3,29,'Put the chicken breasts in a large bowl with the olive oil and 1 tbsp lemon juice, then season. Heat the grill to high. Put the chicken breasts on a foil-lined tray and cook under the grill for 10-12 mins until golden and cooked through, turning once during cooking. Transfer to a plate or board and slice. Arrange the lettuce, cress and eggs on a platter or serving plates and top with the cooked chicken. Mix together the Parmesan, chopped anchovies, yogurt and remaining lemon juice, season to taste and pour over the salad. Arrange the whole anchovy fillets on top of each salad.','30 mins','10 mins - 12 mins'),
(1422,'sesame salmon, purple sprouting broccoli & sweet potato mash','https://www.bbcgoodfood.com/recipes/sesame-salmon-purple-sprouting-broccoli-sweet-potato-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sesame-salmon-and-purple-broccoli-24cb460.jpg','Try this Asian-inspired salmon supper with a nutty sesame dressing, crisp veg and comforting sweet potato mash. It''s healthy, low-calorie and rich in omega-3',16,2,1,3,29,'Heat oven to 200C/180 fan/ gas 6 and line a baking tray with parchment. Mix together 1/2 tbsp sesame oil, the soy, ginger, garlic and honey. Put the sweet potato wedges, skin and all, into a glass bowl with the lime wedges. Cover with cling film and microwave on high for 12-14 mins until completely soft. Meanwhile, spread the broccoli and salmon out on the baking tray. Spoon over the marinade and season. Roast in the oven for 10-12 mins, then sprinkle over the sesame seeds. Remove the lime wedges and roughly mash the sweet potato using a fork. Mix in the remaining sesame oil, the chilli and some seasoning. Divide between plates, along with the salmon and broccoli.','10 mins','15 mins'),
(1423,'ham, mushroom & spinach frittata','https://www.bbcgoodfood.com/recipes/ham-mushroom-spinach-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ham-mushroom-spinach-frittata-e246673.jpg','Try a filling frittata that''s low in calories and guaranteed to keep you going until lunch. Save half in the fridge overnight if you''re cooking for one',16,2,1,3,29,'Heat the grill to its highest setting. Heat the oil in an ovenproof frying pan over a medium-high heat. Tip in the mushrooms and fry for 2 mins until mostly softened. Stir in the ham and spinach, and cook for 1 min more until the spinach has wilted. Season well with black pepper and a pinch of salt. Reduce the heat and pour over the eggs. Cook undisturbed for 3 mins until the eggs are mostly set. Sprinkle over the cheese and put under the grill for 2 mins. Serve hot or cold.','4 mins','9 mins'),
(1424,'herb & garlic baked cod with romesco sauce & spinach','https://www.bbcgoodfood.com/recipes/herb-garlic-baked-cod-romesco-sauce-spinach','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cod-3414e14.jpg','Cod is a great source of lean protein. Enjoy it with with fresh greens and a peppery romesco sauce for a tasty, low-calorie meal',4,2,1,3,29,'Heat oven to 220C/200C fan/ gas 7 and put the fish fillets in a shallow ovenproof dish so they fit quite snugly in a single layer. Mix 1 tbsp rapeseed oil with the thyme and garlic, spoon over the fish, then grate over the lemon zest. Bake for 10-12 mins until the fish is moist and flakes easily when tested. Meanwhile, heat the remaining oil in a non-stick pan and fry the pepper and leeks for 5 mins until softened. Add the almonds and cook for 5 mins more. Tip in the tomato purée, 5 tbsp water, the bouillion powder and vinegar, and cook briefly to warm the mixture through. Add the juice of up to half a lemon and blitz with a stick blender until it makes a thick, pesto-like sauce. Serve with the fish and the wilted spinach.','10 mins','20 mins'),
(1425,'creamy chicken & asparagus braise','https://www.bbcgoodfood.com/recipes/creamy-chicken-asparagus-braise','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-chicken-asparagus-braise-f541cb2.jpg','This delightfully summery, low-calorie, low-fat dish packs in plenty of greens and has a yogurt, tarragon and garlic sauce',5,2,1,3,29,'Heat the oil in a large non-stick frying pan and fry the chicken for 5 mins, turning to brown both sides. Add the asparagus (reserve the tips), leeks and celery, pour in the bouillon and simmer for 10 mins. Add the asparagus tips and peas, and cook for 5 mins more. Meanwhile, stir the egg yolk with the yogurt and garlic. Stir the yogurt mixture into the vegetables and add the tarragon. Divide between two warm plates, then place the chicken on top of the vegetables. Serve with new potatoes, if you like.','10 mins','20 mins - 25 mins'),
(1426,'carrot biryani','https://www.bbcgoodfood.com/recipes/carrot-biriyani','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gf_byriani-115d72c.jpg','Prepare and cook our low-calorie vegetarian biryani in just 25 minutes using carrot, cashew nuts and Indian spices. Serve with coriander and dollops of yogurt',41,4,1,3,29,'Heat the oil in a large frying pan, tip in the onion with a big pinch of salt and fry until softened, around 5 mins, then add the chilli and crush in the garlic and cook for 1 min more. Stir in the spices with a splash of water and cook for a couple of mins before adding the carrots and stirring well to coat in all of the spices and flavours. Tip in the rice, peas and cashews, then use the back of your spoon to break up any clumps of rice and combine with the rest of the ingredients, cover and cook over a high heat for 5 mins (it’s nice if a bit of rice catches on the base to give a bit of texture to the dish). Scatter over the coriander with spoonfuls of yogurt, then serve straight from the pan.','10 mins','15 mins'),
(1427,'spicy root & lentil casserole','https://www.bbcgoodfood.com/recipes/spicy-root-lentil-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488691_11-ca2ba1d.jpg','The potatoes in this recipe take on the spicy flavours beautifully - our idea of the perfect veggie supper',5,4,1,3,29,'Heat the oil in a large pan and cook the onion and garlic over a medium heat for 3-4 minutes until softened, stirring occasionally. Tip in the potatoes, carrots and parsnips, turn up the heat and cook for 6-7 minutes, stirring, until the vegetables are golden. Stir in the curry paste or powder, pour in the stock and then bring to the boil. Reduce the heat, add the lentils, cover and simmer for 15-20 minutes until the lentils and vegetables are tender and the sauce has thickened. Stir in most of the coriander, season and heat for a minute or so.Top with yogurt and the rest of the coriander. Serve with naan bread.','10 mins','35 mins'),
(1428,'healthy fish & chips with tartare sauce','https://www.bbcgoodfood.com/recipes/healthy-fish-chips-tartare-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-484500_12-744e58e.jpg','A perfectly healthy Friday night special meal for two, fish and chips',5,2,1,3,29,'Heat oven to 200C/fan 180C/gas 6. Toss chips in oil. Spread over a baking sheet in an even layer, bake for 40 mins until browned and crisp. Put the fish in a shallow dish, brush lightly with oil, salt and pepper. Sprinkle with half the lemon juice, bake for 12-15 mins. After 10 mins sprinkle over a little parsley and lemon zest to finish cooking. Meanwhile, mix the capers, yogurt, remaining parsley and lemon juice together, set aside and season if you wish. To serve, divide the chips between plates, lift the fish onto the plates and serve with a spoonful of yogurt mix.','5 mins','40 mins'),
(1429,'speedy butternut squash barley ‘risotto’','https://www.bbcgoodfood.com/recipes/speedy-butternut-squash-barley-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Speedy-Butternut-Squash-Barley-Risotto-775005e.jpg','Swap rice for barley to make this easy squash ''risotto'' with spinach and sage. It''s healthy and low in calories and fat – perfect for a quick supper',12,2,1,3,29,'Heat the oil in a deep frying pan over a medium heat, and fry the shallot with a pinch of salt for 5 mins until soft. Add the squash, garlic, sage and mace, and fry, stirring continually, for another 6-8 mins until the squash is starting to soften and the garlic is fragrant. Tip in the spinach and barley with some seasoning and the stock. Bring to a simmer and cook for 4-5 mins until the spinach has wilted. Add a splash more stock or water, if you like. When the ‘risotto’ is thick and creamy, taste to see if it needs a little more mace or nutmeg and seasoning. Stir in most of the parmesan, then top with the remaining parmesan and grind over some black pepper to serve.','10 mins','20 mins'),
(1430,'prawn, fennel & rocket risotto','https://www.bbcgoodfood.com/recipes/prawn-fennel-rocket-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/prawn-fennel-rocket-risotto-7584e28.jpg','This prawn and fennel risotto gets a little extra kick from lemon zest and and rocket - perfect for a dinner party',86,4,1,3,29,'Put the stock in a large saucepan, bring to the boil, then lower to a simmer. Meanwhile, heat the oil in a large saucepan. Add the onion, garlic and fennel, and cook on a low heat for 10 mins until the vegetables have softened but not coloured. Add the rice and stir for 2 mins until the grains are hot and making crackling sounds. Increase the heat to medium and start adding the stock, a ladleful at a time, stirring constantly and making sure the stock has absorbed into the rice before adding the next ladleful. When the rice is almost cooked, add the prawns, lemon zest and some seasoning. Continue adding stock and cooking for another 3-4 mins until the prawns are pink and the rice is cooked. Remove from the heat and stir through the rocket and lemon juice. Check the seasoning, leave the risotto to sit in the pan for 2 mins, then serve.','15 mins','35 mins'),
(1431,'lentil & cauliflower curry','https://www.bbcgoodfood.com/recipes/lentil-cauliflower-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cauliflower-curry-915ac5b.jpg','An earthy spice pot that''s low in fat and calories. Use your favourite curry paste along with mustard seeds, turmeric and coconut yogurt',28,4,1,3,29,'Heat the oil in a large saucepan and cook the onion until soft, about 5 mins. Add the curry paste, spices and lentils, then stir to coat the lentils in the onions and paste. Pour over the stock and simmer for 20 mins, then add the cauliflower, potato and a little extra water if it looks a bit dry. Simmer for about 12 mins until the cauliflower and potatoes are tender. Stir in the yogurt, coriander and lemon juice, and serve with the brown rice.','10 mins','40 mins'),
(1432,'cauliflower, paneer & pea curry','https://www.bbcgoodfood.com/recipes/cauliflower-paneer-pea-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cauliflower-paneer-pea-curry-eb0eb5d.jpg','Ready in less than an hour, this easy, vegetarian curry recipe made with pan-fried Indian cheese and vegetables is also cheap to prepare',92,4,1,3,29,'Heat 1 tbsp of oil in a large non-stick frying pan, add the paneer and fry gently until crisp. Remove with a slotted spoon and set aside. Add the remaining oil and the cauliflower to the pan, and cook for 10 mins until browned. Add the onions, and a little more oil if needed, and cook for a further 5 mins until softened. Stir in the garlic and curry paste, then pour in the passata and 250ml water, and season. Bring to a simmer, then cover and cook for 18-20 mins or until the cauliflower is just tender. Add the frozen peas and crispy paneer to the pan and cook for a further 5 mins. Stir through most of the coriander and garnish with the rest. Serve with basmati rice or naan bread, raita or your favourite chutney.','10 mins','45 mins'),
(1433,'chicken, broccoli & beetroot salad with avocado pesto','https://www.bbcgoodfood.com/recipes/chicken-broccoli-beetroot-salad-avocado-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-broccoli-beetroot-salad-with-avocado-pesto-5f17f37.jpg','This superfood supper is packed with ingredients to give your body a boost, such as red onion, nigella seeds, walnuts, avocado oil and lemon',92,4,1,3,29,'Bring a large pan of water to the boil, add the broccoli and cook for 2 mins. Drain, then refresh under cold water. Heat a griddle pan, toss the broccoli in 1⁄2 tsp of the rapeseed oil and griddle for 2-3 mins, turning, until a little charred. Set aside to cool. Brush the chicken with the remaining oil and season. Griddle for 3-4 mins each side or until cooked through. Leave to cool, then slice or shred into chunky pieces. Next, make the pesto. Pick the leaves from the basil and set aside a handful to top the salad. Put the rest in the small bowl of a food processor. Scoop the flesh from the avocado and add to the food processor with the garlic, walnuts, oil, 1 tbsp lemon juice, 2-3 tbsp cold water and some seasoning. Blitz until smooth, then transfer to a small serving dish. Pour the remaining lemon juice over the sliced onions and leave for a few mins. Pile the watercress onto a large platter. Toss through the broccoli and onion, along with the lemon juice they were soaked in. Top with the beetroot, but don’t mix it in, and the chicken. Scatter over the reserved basil leaves, the lemon zest and nigella seeds, then serve with the avocado pesto.','15 mins','15 mins'),
(1434,'superhealthy salmon salad','https://www.bbcgoodfood.com/recipes/superhealthy-salmon-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192540_11-b9a065b.jpg','Superhealthy by name, superhealthy by nature: this salad is high in omega-3, iron and calcium and counts as 2 of your 5-a-day',5,2,1,3,29,'Heat water in a tier steamer. Season the couscous, then toss with 1 tsp oil. Pour boiling water over the couscous so it covers it by 1cm, then set aside. When the water in the steamer comes to the boil, tip the broccoli into the water, then lay the salmon in the tier above. Cook for 3 mins until the salmon is cooked and the broccoli tender. Drain the broccoli and run it under cold water to cool. Mix together the remaining oil and lemon juice. Toss the broccoli, pomegranate seeds and pumpkin seeds through the couscous with the lemon dressing. At the last moment, roughly chop the watercress and toss through the couscous. Serve with the salmon, lemon wedges for squeezing over and extra olive oil for drizzling, if you like.','20 mins','5 mins'),
(1435,'pan-cooked feta with beetroot salsa & bean mash','https://www.bbcgoodfood.com/recipes/pan-cooked-feta-beetroot-salsa-bean-mash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fetawithbeetrootsalsa_5915-3ee22d1.jpg','Whip up this budget vegetarian dinner in minutes - pile slices of light feta onto cannellini bean mash and top with tangy beetroot salsa',4,2,1,3,29,'Mix the beetroot and the onion in a bowl with the lime zest, a good squeeze of juice, seasoning and dill. Heat the tsp oil in a non-stick pan and gently cook the garlic until softened then tip in the beans, and some of the juice from the can with seasoning. Mash the beans, keeping them quite chunky, then keep warm. Toss the slices of feta in a little flour. Heat a non-stick frying pan, wipe with a little oil then cook the feta for a few minutes on each side until golden and warmed through, flipping over to make sure they''re cooked evenly. Spoon the bean mash onto plates, top with the feta and half the salsa then scatter with extra dill. Serve with the rocket on the side and the rest of the salsa in a bowl.','10 mins','7 mins'),
(1436,'scrambled eggs with basil, spinach & tomatoes','https://www.bbcgoodfood.com/recipes/basil-scramble-wilted-spinach-seared-tomatoes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/basil-scramble-with-wilted-spinach-seared-tomatoes-619f05a.jpg','Forget toast and serve your scrambled eggs with fresh tomatoes and spinach for a speedy supercharged breakfast',5,2,1,3,29,'Heat 1 tsp oil in a large non-stick frying pan, add the tomatoes and cook, cut-side down, over a medium heat. While they are cooking, beat the eggs in a jug with the yogurt, 2 tbsp water, plenty of black pepper and the basil. Transfer the tomatoes to serving plates. Add the spinach to the pan and wilt, stirring a few times while you cook the eggs. Heat the rest of the oil in a non-stick pan over a medium heat, pour in the egg mixture and stir every now and then until scrambled and just set. Spoon the spinach onto the plates and top with the scrambled eggs.','5 mins','5 mins'),
(1437,'cardamom & peach quinoa porridge','https://www.bbcgoodfood.com/recipes/cardamom-peach-quinoa-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cardamom-peach-quinoa-porridge-8d2c612.jpg','A healthy breakfast of oats and quinoa with fresh ripe peach. Almond milk makes its suitable for dairy-free and vegan diets',16,2,1,3,29,'Put the quinoa, oats and cardamom pods in a small saucepan with 250ml water and 100ml of the almond milk. Bring to the boil, then simmer gently for 15 mins, stirring occasionally. Pour in the remaining almond milk and cook for 5 mins more until creamy. Remove the cardamom pods, spoon into bowls or jars, and top with the peaches and maple syrup.','3 mins','20 mins'),
(1438,'green shakshuka','https://www.bbcgoodfood.com/recipes/green-shakshuka','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-shakshuka-d702c5c.jpg','Treat yourself to a shakshuka with a difference. Mix up your favourite greens, eggs and flatbreads to serve with this healthy weekend breakfast',16,4,1,3,29,'Heat the oil in a wide, shallow frying pan over a medium heat. Add the leeks with a pinch of salt and cook for 4 mins until softened. Add handfuls of spinach to the pan, stirring until wilted. Stir in the peas, garlic, cumin, herbs and some seasoning. Cook for a few mins until it smells fragrant, then create four gaps and crack two eggs into each. Cover and cook for 10 mins or until the whites are set but the yolks are runny – they will carry on cooking slightly as you take them to the table. Season the eggs with flaky sea salt, dollop spoonfuls of the yogurt interspersed with the harissa, and scatter over a few mint leaves. Serve with a pile of flatbread for scooping.','10 mins','25 mins'),
(1439,'warming chocolate & banana porridge','https://www.bbcgoodfood.com/recipes/warming-chocolate-banana-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/choc-oats-3fa7094.jpg','Cocoa and vanilla add a luxurious flavour to this healthy breakfast, naturally sweetened with banana. Soak the oats overnight for a super creamy texture',4,4,1,3,29,'Put the oats, cocoa and vanilla in a large bowl and pour over 800ml – 1 litre cold water (depending how thick you like your porridge). Cover the bowl and leave to soak overnight. The next morning, tip the contents into a saucepan with the chopped banana. Cook over a medium heat for 15 mins, stirring frequently, until the oats are cooked. Put half of the mixture in the fridge for the next day. Spoon the rest into two bowls, swirl in 1 pot yogurt and slice over a banana (save the other pot and banana for the next morning). Warm through with a splash of milk to reheat.','10 mins','15 mins'),
(1440,'oat & chia porridge','https://www.bbcgoodfood.com/recipes/oat-chia-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/oat-and-chia-porridge-440-400-acb11f5.jpg','Make this vegan breakfast packed with healthy ingredients including flaxseeds. You''ll benefit from calcium, vitamin C, fibre and one of your 5-a-day. Make with gluten-free oats as desired!',4,4,1,3,29,'Soak the oats and seeds in 800ml water overnight. Toast your flaked almonds in a dry frying pan over a medium heat until golden brown on each side, about 2-3mins. Set aside in an airtight container. Tip into a pan with 200ml oat milk and heat, stirring, until bubbling and thick. If you''re following our Healthy Diet Plan, save half for the next day. Will keep in the fridge for two days. Divide the rest between two bowls, along with 50ml oat milk each and topping with a quarter portion each of the yogurt, almonds and grapefruit. The next day, prepare the second grapefruit and reheat the leftover porridge in a pan with a splash more oat milk before serving with the toppings as described in step 2.','10 mins','4 mins'),
(1441,'red lentil soup','https://www.bbcgoodfood.com/recipes/red-lentil-carrot-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/redlentilcarrotsoup_5695-ba392a8.jpg','This warming and budget-friendly vegetarian soup is perfect packed in a flask for lunch. It''s also easy to double the quantities and freeze half for later',4,2,1,3,29,'Put the kettle on to boil while you finely slice the onion. Heat the oil in a medium pan, add the onion and fry for 2 mins while you slice the garlic and dice the carrots. Add them to the pan, and cook briefly over the heat. Pour in 1 litre of the boiling water from the kettle, stir in the lentils and stock cube, then cover the pan and cook over a medium heat for 15 mins until the lentils are tender. Take off the heat and stir in the parsley. Ladle into bowls, and scatter with extra parsley leaves, if you like.','5 mins','20 mins'),
(1442,'chorizo, orzo & sweetcorn summer stew','https://www.bbcgoodfood.com/recipes/chorizo-orzo-sweetcorn-summer-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-orzo-sweetcorn-summer-stew-7b58cfc.jpg','Fry veg with chorizo and orzo to make this healthy, colourful summer stew. You can add any leftover veg you have in the fridge to reduce food waste',12,2,1,3,29,'Heat the oil in a deep frying pan and fry the white parts of the spring onions, the peppers and chorizo for 8 mins, until the peppers are soft and the chorizo is just golden. Stir in the garlic, orzo, paprika, sweetcorn and tomato, and fry for 2-3 mins more. Pour in the stock. Bring to a simmer and cook for 8-10 mins, stirring often, until the orzo is tender. Stir in the parsley, the green parts of the spring onions, and the lemon juice and zest.','10 mins','25 mins'),
(1443,'puy lentil salad with soy beans, sugar snap peas & broccoli','https://www.bbcgoodfood.com/recipes/puy-lentil-salad-soy-beans-sugar-snap-peas-broccoli','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-47332_10-1272916.jpg','A hearty Asian-style veggie main-course salad, bursting with flavour',10,4,1,3,29,'Boil lentils in stock until just cooked, about 15 mins. Drain, then tip into a large bowl. Bring a saucepan of salted water to the boil, throw in the broccoli for 1 min, add the beans and peas for 1 min more. Drain, then cool under cold water. Pat dry, then add to the bowl with the lentils. Mix together the dressing ingredients with some seasoning. Pour over the lentils and veg, then mix in well with the chopped chilli. Pile onto a serving platter or divide between 4 plates and serve.','10 mins','15 mins'),
(1444,'smoky aubergine tagine with lemon & apricots','https://www.bbcgoodfood.com/recipes/smoky-aubergine-tagine-lemon-apricots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/smoky-aubergine-tagine-with-lemons-apricots-c5aba81.jpg','A fragrant tagine is perfect for summer entertaining, lighter than stew but just as satisfying with meaty aubergines and aromatic spices',17,4,1,3,29,'Brown the aubergines in 2 tbsp of the oil so they’re golden on all sides, but not soft in the middle yet – this is best done in batches in a large, non-stick frying pan. Heat a heavy-based shallow lidded pan or flameproof tagine with the remaining oil, then add the onions, ginger and spices, and fry gently until softened and golden. Meanwhile, add the saffron to the stock to soak. Stir the preserved lemon rind and pulp, apricots, tomatoes, honey and lemon juice into the onions with the saffron stock. Snugly fit the aubergines in, cover with a lid and simmer for 30 mins until the aubergines are tender. Season to taste. Mix together the lemon zest, sesame seeds and chopped herbs, and sprinkle over the tagine. Serve with Greek yogurt and wholemeal bulghar wheat, if you like.','20 mins','1 hr'),
(1445,'prawn jalfrezi','https://www.bbcgoodfood.com/recipes/prawn-jalfrezi','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jalfrezi-a5abc2f.jpg','Satisfy a curry craving the healthy way with this Indian prawn dish - blitz the onions and spices into an authentic, thick, yet low-fat sauce',4,2,1,3,29,'Heat the oil in a non-stick pan and fry the onions, ginger and garlic for 8-10 mins, stirring frequently, until softened and starting to colour. Add the spices and chilli flakes, stir briefly, then pour in the tomatoes with half a can of water and the honey. Blitz everything in the pan with a hand blender until almost smooth (or use a food processor). Stir in the pepper and coriander stalks, cover the pan and leave to simmer for 10 mins. (The mixture will be very thick and splutter a little, so stir frequently.) Stir in the prawns and scatter over the coriander leaves. Heat the rice following pack instructions. Serve both with a minty yogurt or chutney, if you like.','10 mins','22 mins'),
(1446,'ginger chicken & green bean noodles','https://www.bbcgoodfood.com/recipes/ginger-chicken-green-bean-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/ginger-chicken-green-bean-noodles-7b58cfc.jpg','Try this speedy one-pan ginger chicken with noodles and green beans as a quick and easy midweek meal. It''s healthy, low in fat and calories, too',12,2,1,3,29,'Heat the oil in a wok over a high heat and stir-fry the chicken for 5 mins. Add the green beans and stir-fry for 4-5 mins more until the green beans are just tender, and the chicken is just cooked through. Stir in the fresh ginger and garlic, and stir-fry for 2 mins, then add the stem ginger and syrup, the cornflour mix, soy sauce and vinegar. Stir-fry for 1 min, then toss in the noodles. Cook until everything is hot and the sauce coats the noodles. Drizzle with more soy, if you like, and serve.','10 mins','15 mins'),
(1447,'turmeric, ginger & coconut fish curry','https://www.bbcgoodfood.com/recipes/turmeric-ginger-coconut-fish-curry','https://images.immediate.co.uk/production/volatile/sites/30/2021/04/Turmeric-fish-curry-f6604de.jpg','Serve up this healthy fish curry, with vibrant turmeric, ginger and coconut flavours, for an easy midweek meal. It takes just 30 minutes from prep to plate',46,4,1,3,29,'Heat the oil in a large saucepan over a medium heat, then fry the onion for 8 mins until translucent. Stir in the ginger and spices, and cook for another minute. Pour in the coconut milk and 100ml water, stir, then simmer for 10 mins. Add the cod, frozen peas and sugar snap peas, and simmer for 5 mins until the fish is flaky. Serve with the rice, sliced chilli, a good grinding of black pepper and some lime wedges on the side for squeezing over.','5 mins','25 mins'),
(1448,'chicken with crushed harissa chickpeas','https://www.bbcgoodfood.com/recipes/chicken-crushed-harissa-chickpeas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-with-crushed-harissa-chickpeas-f009cb0.jpg','Need something speedy for dinner? Try this chicken, coated in flavourful za''atar and served with spiced chickpeas. It''s simple, but seriously delicious',14,4,1,3,29,'Heat 1 tbsp of oil in a frying pan over a medium heat and fry the onions and peppers for 7 mins until softened and golden. Meanwhile, put the chicken between two sheets of baking parchment and lightly bash until about 2cm thick. Mix together the remaining oil and the za''atar, then rub over the chicken. Season to taste. Heat the grill to high. Put the chicken on a baking tray lined with foil, and grill for 3-4 mins each side, or until golden and cooked through.  Heat the chickpeas in a pan with the harissa paste and 2 tbsp water until warmed through, then roughly mash with a potato masher. Wilt the spinach in a pan with 1 tbsp of water or in the microwave in a heatproof bowl. Stir the pepper and onion mixture, spinach and parsley through the chickpeas. Serve with the sliced chicken and the lemon wedges for squeezing over.','5 mins','10 mins'),
(1449,'pesto chicken salad','https://www.bbcgoodfood.com/recipes/pesto-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/pesto-chicken-salad-a752309.jpg','Serve this pesto chicken and couscous salad in lettuce leaves for a healthy supper. With added sundried tomatoes and pine nuts, it has lots of texture',12,2,1,3,29,'Put the couscous in a large heatproof bowl and cover with 100ml boiling water. Stir in 1 tbsp pesto. Cover and leave for 8 mins.  Fluff the couscous with a fork, then stir in the rest of the pesto, the yogurt and some seasoning. Toss in the chicken, basil, cucumber and tomatoes. Spoon the couscous mix into the lettuce leaves and eat with your fingers, or serve over the lettuce like a salad. Scatter over the pine nuts and more basil before serving.','20 mins','No Time'),
(1450,'masala frittata with avocado salsa','https://www.bbcgoodfood.com/recipes/masala-frittata-avocado-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/masala-frittata-with-avocado-salsa-4d0a156.jpg','A spicy twist on a simple frittata recipe, with Masala paste, coriander and plump cherry tomatoes. Pair it with our avocado salsa for a light and budget-friendly supper',93,4,1,3,29,'Heat the oil in a medium non-stick, ovenproof frying pan. Tip in the sliced onions and cook over a medium heat for about 10 mins until soft and golden. Add the Madras paste and fry for 1 min more, then tip in half the tomatoes and half the chilli. Cook until the mixture is thick and the tomatoes have all burst. Heat the grill to high. Add half the coriander to the eggs and season, then pour over the spicy onion mixture. Stir gently once or twice, then cook over a low heat for 8-10 mins until almost set. Transfer to the grill for 3-5 mins until set. To make the salsa, mix the avocado, remaining chilli and tomatoes, chopped onion, remaining coriander and the lemon juice together, then season and serve with the frittata.','15 mins','25 mins'),
(1451,'lighter spaghetti & meatballs','https://www.bbcgoodfood.com/recipes/lighter-spaghetti-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lighter-spaghetti-meatballs-fb343c6.jpg','Lentils make a low-fat addition to these minced pork patties for a healthier family favourite - still full of herby, garlicky flavour',37,4,1,3,29,'Heat oven to 200C/180C fan/gas 6. Line a baking sheet with foil and brush with 1 tsp oil. Mash the lentils in a bowl with the back of a fork to break down a bit, but not completely. Stir in the pork, rosemary, mustard, garlic, some pepper to generously season, and mix well with the fork to distribute the lentils evenly. Divide the mixture into 4. Form each quarter into 5 small balls – to give you 20 in total – squeezing the mixture together well as you shape it. Lay the meatballs on the foil and roll them around in the oil to coat all over. Bake for 15 mins until cooked and lightly browned. Remove (leave the oven on) and set aside. While the meatballs cook, heat 2 tsp of the oil for the sauce in a large non-stick frying pan. Tip in the shallots and garlic, and fry on a medium heat for 3-4 mins until softened and tinged brown. Pour in the remaining 1 tsp oil, lay the tomatoes in the pan so most of them are cut-side down (to help release the juices), raise the heat and fry them for 3-4 mins or until the tomatoes are starting to soften and release their juices. Don’t stir, or they may lose their shape. Splash in 125-150ml water so it all bubbles, and gently mix in the tomato purée. Lower the heat and simmer for 2 mins to create a juicy, chunky sauce. Season with the chilli flakes, oregano, pepper and a pinch of salt, and give it a quick stir, adding a drop more water if needed – you want it thick enough to coat the meatballs. Pour the sauce into a casserole dish, add the meatballs and spoon the sauce over them to coat. Cover with foil and bake for 10 mins while you cook the spaghetti. Boil a large saucepan of water. Add the spaghetti, stir and bring back to the boil. Cook for 10-12 mins, or following pack instructions, until al dente. Drain well, season with pepper and serve with the meatballs, sauce and a light sprinkling of oregano.','30 mins','35 mins'),
(1452,'cheesy seafood bake','https://www.bbcgoodfood.com/recipes/cheesy-seafood-bake','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheesy-seafood-bake-1-440-400-b088c08.jpg','Comfort food at its healthiest! This protein-rich dinner is perfect after a workout, and provides an impressive 4 of your 5-a-day along with vitamin C and iron',4,2,1,3,24,'Boil the potato slices for 10 mins then drain, tip into a bowl and gently mix in the milk and half the cheese. Don’t worry if the potatoes break up a little. Meanwhile, heat the oil in a large frying pan and cook the onion until softened. Stir in the pepper and cook for 5 mins more. Spoon in the balsamic vinegar and bouillon powder, then stir in the tomatoes, basil and garlic. Lay the cod fillets on top, then cover and cook for 6-8 mins until the cod flakes when tested. Heat the grill to high. Take off the heat, stir in the prawns and tip into a shallow baking dish, breaking up the cod into large chunks. Cover with the potatoes and sprinkle with the remaining cheese. Grill until golden. While it’s grilling, steam or boil the broccoli to serve with the bake.','15 mins','25 mins'),
(1453,'scrambled egg & feta hash','https://www.bbcgoodfood.com/recipes/scrambled-egg-feta-hash','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/scrambled-eggs-d82c50e.jpg','This fresh, flavoursome breakfast from Joe Wicks is low in carbohydrates, high in protein and easy to make. It''s quick and will keep you full ''til lunch',96,1,1,3,24,'Heat the oil in a non-stick pan on a low-medium heat. Add the tomatoes and spring onions, and cook gently for 3-4 mins until softened. Add the feta and cook for 1 min to warm through, then pour in the eggs and chives. Season with pepper and keep stirring to scramble the eggs. Put a handful of the spinach on a plate and top with the eggs. Serve the rest of the spinach on the side.','7 mins','8 mins'),
(1454,'indian chicken protein pots','https://www.bbcgoodfood.com/recipes/indian-chicken-protein-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/indian-chicken-protein-pot-ffbc8a1.jpg','Use leftover chicken or buy it ready-cooked for these speedy protein pots. The chicken is combined with spiced lentils and tomatoes and topped with tzatziki',4,2,1,3,24,'Tear the corner from the lentil pack and microwave on High for 1 min. Leave to cool then tip into 2 large packed lunch pots. Top with the cherry tomatoes and chicken, add the fresh coriander then spoon on the tzatziki. Seal until ready to eat (see tip below).','10 mins','1 min'),
(1455,'miso roast salmon, lentil & pomegranate salad','https://www.bbcgoodfood.com/recipes/miso-roast-salmon-lentil-pomegranate-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon-salad-6e68639.jpg','Try our salmon, lentil and pomegranate salad for a nutrient-packed supper. It''s high in fibre and iron and delivers two of your five-a-day ',4,2,1,3,24,'Heat oven to 200C/180C fan/gas 6. Cook the lentils in a pan of boiling water for 20 mins until tender. Meanwhile, mix the miso paste with 1 tsp of the ginger, the garlic, half the lime juice, the oil and sesame seeds. Put the salmon fillets on a foil-lined baking tray and spread 1 tbsp of the miso mixture over them. Roast for 10-12 mins until cooked through. Tip the rest of the miso mixture into a bowl with the remaining ginger and lime juice. Add the lime zest, vinegar, carrots, pomegranate, onions and coriander. Drain the lentils and toss into the salad. Divide between two plates and add the salmon.','10 mins','20 mins'),
(1456,'egyptian egg salad','https://www.bbcgoodfood.com/recipes/egyptian-egg-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egyptian-egg-salad-8bf6d1c.jpg','Give salad a shake-up with an Egyptian-inspired recipe that combines fava beans with egg and the rich flavours of tahini, garlic, lemon and cumin',4,2,1,3,24,'Bring a pan of water to the boil, lower in the eggs and boil for 8 mins. Drain and run under the cold tap to cool them a little, then peel and halve. Meanwhile, mix 1 tbsp lemon juice and 3 tbsp water with the tahini to make a dressing. Heat the oil and fry the onion and garlic for 5 mins to soften them. Add the ground cumin and seeds, stir briefly then add the beans and lightly crush some of them as you heat them, adding some of the juice from the can to get a nice creamy consistency but keeping whole beans, too. Taste and add lemon juice and just a little seasoning if you need to. Spoon the beans on to plates with the lettuce, then add the eggs and tomatoes, with the tahini dressing, chilli and parsley, if using.','10 mins','5 mins'),
(1457,'healthy tuna lettuce wraps','https://www.bbcgoodfood.com/recipes/tuna-mayo-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tuna-mayo-wraps-a1f5206.jpg','Make these nifty wraps with avocado mayo as a great low-carb, high-protein lunch. They''re packed with omega-3-rich tuna and boast three of your five-a-day',4,2,1,3,24,'Brush the tuna with a little oil. Heat a non-stick pan, add the tuna and cook for 1 min each side, or a min or so longer for a thicker fillet. Transfer to a plate to rest. Halve and stone the avocado and scoop the flesh into a small bowl. Add the mustard powder and vinegar, then mash well so that the mixture is smooth like mayonnaise. Stir in the capers. Spoon into two small dishes and put on serving plates with the lettuce leaves, and tomatoes. Slice the tuna (it should be slightly pink inside) and arrange on the plates. Spoon some ‘mayo’ on the lettuce leaves and top with tuna and cherry tomatoes and a few extra capers. To eat, roll up into little wraps.','15 mins','2 mins'),
(1458,'egg & puy lentil salad with tamari & watercress','https://www.bbcgoodfood.com/recipes/egg-puy-lentil-salad-tamari-watercress','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egg-puy-lentil-salad-with-tamari-watercress-64a61f5.jpg','We''ve put together the ultimate healthy recipe to ease the symptoms of menopause, packed with calcium, folate and iron',4,2,1,3,24,'If you want to activate the lentils (see tip below), do this the night, or up to 8 hrs, before eating. Pour water over them and leave to soak at room temperature. Drain and rinse. When ready to eat, heat oven to 220C/ 200C fan/gas 7. Toss the cauliflower with a drizzle of the oil, then roast for 20 mins on a parchment-lined baking tray until tender and tinged with gold round the edges. Meanwhile, put the drained lentils in a pan with the carrot and celery. Pour in water to cover, put on a lid and boil for 20 mins until the lentils are tender. Check before they are ready in case they are boiling dry and, if necessary, top up with a little more water. While they are cooking, finely grate the garlic and set aside in a large bowl. Boil the eggs for 6 mins, this will give you eggs with a soft yolk. When they are ready, plunge into cold water, then shell. Mix the tamari and oil into the garlic to make a dressing. Check the lentils and drain, if necessary, then toss in the bowl with the dressing, tomatoes, spring onions and watercress. Pile onto plates and top with the eggs, adding any remaining dressing from the bowl over the top.','10 mins','35 mins'),
(1459,'mexican-style bean soup with shredded chicken & lime','https://www.bbcgoodfood.com/recipes/mexican-bean-soup-shredded-chicken-lime','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mexican-chicken-bean-soup-def6d71.jpg','Use leftover chicken breast in this substantial healthy soup. Alternatively, make the recipe vegetarian by topping with chunky, fresh guacamole',4,2,1,3,24,'Heat the oil in a medium pan, add the onion and pepper, and fry, stirring frequently, for 10 mins. Stir in the garlic and spices, then tip in the tomatoes and beans with their liquid, half a can of water and the bouillon powder. Simmer, covered, for 15 mins. Meanwhile, tip the chicken into a bowl, add the coriander and lime juice with a little chilli (if using, or see tip below for guacamole alternative) and toss well. Ladle the soup into two bowls, top with the chicken and serve.','10 mins','20 mins'),
(1460,'tuna niçoise protein pot','https://www.bbcgoodfood.com/recipes/tuna-nicoise-protein-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tuna-nicoise-protein-pot-e83c9e6.jpg','Looking for a tasty protein fix after a workout, or a lunch that will fill you up? Try a tuna and egg Niçoise pot, delivering 30g of protein per portion',4,1,1,3,24,'Boil the egg for 8-10 mins depending on if you want a soft or hard yolk, then at the same time steam the green beans for 6 mins above the pan until tender. Cool the egg and beans under running water then carefully shell and quarter the egg. Leave to cool. Tip the beans into a large packed lunch pot. Top with the tomato, tuna and quartered egg and spoon on the French dressing. Seal until ready to eat (see tip below).','10 mins','10 mins'),
(1461,'moroccan-style chickpea soup','https://www.bbcgoodfood.com/recipes/moroccan-chickpea-soup-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-192471_11-88af07f.jpg','For something hearty and warming with aromatic spice, this chickpea soup is an easy winner',5,4,1,3,24,'Heat the oil in a large saucepan, then fry the onion and celery gently for 10 mins until softened, stirring frequently. Tip in the cumin and fry for another min. Turn up the heat, then add the stock, tomatoes and chickpeas, plus a good grind of black pepper. Simmer for 8 mins. Throw in broad beans and lemon juice, cook for a further 2 mins. Season to taste, then top with a sprinkling of lemon zest and chopped herbs. Serve with flatbread.','5 mins','20 mins'),
(1462,'steak & broccoli protein pots','https://www.bbcgoodfood.com/recipes/steak-broccoli-protein-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/steak-broccoli-protein-pots-9fc8214.jpg','These protein pots feature steak with a tasty Japanese twist served with wholegrain rice and a zing of sushi ginger. Rustle them up in less than 20 minutes',4,2,1,3,24,'Tip the rice mix into a bowl and stir in the ginger, chopped onion greens and 4 tbsp water. Add the broccoli and the spring onion whites, but keep the onions together, on top, as you will need them in the next step. Cover with cling film, pierce with the tip of a knife and microwave for 5 mins. Meanwhile heat a non-stick frying pan and sear the steak for 2 mins each side, then set aside. Take the onion whites from the bowl and add to the pan so they char a little in the meat juices while the steak rests. Tip the rice mixture into 2 large packed lunch pots. Slice the steak, pile the charred onions on top and seal until you''re ready to eat (see tip below).','10 mins','9 mins'),
(1521,'mushroom brunch','https://www.bbcgoodfood.com/recipes/mushroom-brunch','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mushroom-brunch-2838d63.jpg','You only need mushrooms, eggs, kale and garlic to cook this tasty one-pan brunch. It''s comforting yet healthy, fibre rich and gluten-free too',29,4,1,3,15,'Slice the mushrooms and crush the garlic clove. Heat the olive oil in a large non-stick frying pan, then fry the garlic over a low heat for 1 min. Add the mushrooms and cook until soft. Then, add the kale. If the kale won’t all fit in the pan, add half and stir until wilted, then add the rest. Once all the kale is wilted, season. Now crack in the eggs and keep them cooking gently for 2-3 mins. Then, cover with the lid to for a further 2-3 mins or until the eggs are cooked to your liking. Serve with regular or keto bread for a keto-friendly version.','5 mins','12 mins - 15 mins'),
(1463,'spicy cajun chicken quinoa','https://www.bbcgoodfood.com/recipes/spicy-cajun-chicken-quinoa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-589462_11-7e45ab1.jpg','Protein-packed quinoa makes this midweek meal a superhealthy option',5,4,1,3,24,'Heat oven to 200C/180C fan/gas 6. Toss the chicken with the Cajun spice and arrange in a single layer in a roasting tin. Bake for 20 mins until the chicken is cooked. Set aside. Meanwhile, cook the quinoa in the chicken stock for 15 mins until tender, adding the apricots and lentils for the final 5 mins. Drain and place into a large bowl with the chicken, toss together. While the quinoa is cooking, heat the oil in a large frying pan and soften the onions for 10-15 mins. Toss the onions into the quinoa with the coriander and some seasoning, then mix well.','10 mins','25 mins'),
(1464,'spinach & pepper frittata','https://www.bbcgoodfood.com/recipes/spinach-pepper-frittata','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tortilla-29425bf.jpg','A balanced, omelette-like dish of protein-rich eggs baked with cheese, garlic, tomatoes, peppers and spinach',4,4,1,3,24,'Heat oven to 190C/170C fan/gas 5. Line a 20cm sandwich tin with a single sheet of baking parchment if your tin has a loose bottom. Beat the eggs in a large bowl with the cottage cheese, garlic, half the Parmesan, the spinach, peppers, nutmeg and some black pepper. Tip into the tin, top with the tomatoes and sprinkle with the remaining Parmesan. Bake for 40 mins until set all the way through and starting to puff up. Cut into wedges and serve hot or cold. Will keep for 3-4 days in the fridge.','10 mins','40 mins'),
(1465,'healthy pepper, tomato & ham omelette','https://www.bbcgoodfood.com/recipes/skinny-pepper-tomato-ham-omelette','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Healthy-pepper-ham-omelette-f80d4d4.jpg','If you''re in need of a healthy protein boost, try making this healthy omelette for breakfast – using fewer yolks lowers the cholesterol',5,2,1,3,24,'Mix the eggs and egg whites with some seasoning and set aside. Heat the oil in a medium non-stick frying pan and cook the pepper for 3-4 mins. Throw in the white parts of the spring onions and cook for 1 min more. Pour in the eggs and cook over a medium heat until almost completely set. Sprinkle on the ham and cheese, and continue cooking until just set in the centre, or flash it under a hot grill if you like it more well done. Serve straight from the pan with the green part of the spring onions sprinkled on top, the chopped tomato and some wholemeal toast, if you like.','15 mins','10 mins'),
(1466,'mussels in red pesto','https://www.bbcgoodfood.com/recipes/mussels-red-pesto','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488605_11-13c0ddf.jpg','Mussels are full of omega-3, iron and protein - try yours cooked in red pesto and wine',5,1,1,3,24,'Heat the oil in a large pan and cook the shallot for 4-5 mins until softened. Pour in the wine, add the chilli flakes and bubble for 2 mins. Add the mussels. Cover and cook for 5 mins until all the shells have opened. Discard any that remain closed. Stir in the red pesto and toss well. Tip into a large bowl and serve with crusty bread.','2 mins','10 mins'),
(1467,'wild salmon veggie bowl','https://www.bbcgoodfood.com/recipes/wild-salmon-veggie-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/wild-salmon-veggie-bowl-c5cc341.jpg','Succulent salmon flaked over a bed of healthy vegetables makes a delicious, protein-packed salad that''s also low-calorie, gluten-free and rich in beneficial omega-3 fats',5,2,1,3,24,'Shred the carrots and courgette into long spaghetti strips with a julienne peeler or spiralizer, and pile onto two plates. Stir the beetroot, balsamic vinegar, chopped dill and red onion together in a small bowl, then spoon on top of the veg. Flake over chunks of the salmon and scatter with the capers and extra dill, if you like.','10 mins','No Time'),
(1468,'red lentil & chorizo soup','https://www.bbcgoodfood.com/recipes/red-lentil-chorizo-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1253451_8-885b77b.jpg','Sweet smoked paprika and cumin flavour this rustic blend topped with spicy Spanish sausage',7,6,1,3,24,'Heat the oil in a large pan. Add the chorizo and cook until crisp and it has released its oils. Remove with a slotted spoon into a bowl, leaving the fat in the pan. Fry the onion, carrots and cumin seeds for 10 mins until soft and glistening, then add the garlic and fry for 1 min more. Scatter over the paprika and sugar, cook for 1 min, then splash in the vinegar. Simmer for a moment, then stir in the lentils, and pour over the tomatoes and chicken stock. Give it a good stir, then simmer for 30 mins or until the lentils are tender. Blitz with a hand blender until smooth-ish but still chunky. Can be made several days ahead or frozen for 6 months at this point. Serve in bowls, drizzled with yogurt and olive oil, scattered with the chorizo and a sprinkling of paprika.','10 mins','50 mins'),
(1469,'easy turkey burgers','https://www.bbcgoodfood.com/recipes/turkey-burgers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1186_11-3f87320.jpg','These easy, healthy turkey burgers are perfect for feeding the whole family, kids included. Our tasty burgers are great for summer entertaining',69,8,1,3,24,'Heat 1 tbsp oil in a pan and gently fry the onion for 5 mins until soft. Add the garlic and cook for 1 min. Add the oats and fry for 2 mins more. Tip into a bowl and set aside to cool. Add the rest of the ingredients to the cooled mixture and mix well with your hands. Season to taste and shape into 8 patties. Heat oven to 200C/fan 180C/gas 6. Heat the remaining olive oil in a large, non-stick frying pan and sear the burgers on each side until well coloured (3-4 mins). Transfer to a baking sheet and cook in the oven for 10-15 mins. Serve in rolls with Tangy tomato chutney (see ''goes well with'') and cucumber slices. For the chutney heat 1 tbsp of olive oil in a pan and add 1 finely chopped onion. Cook for 5 mins until softened. Stir in 1 crushed garlic clove and cook for a further min. Add 1 tbsp sundried tomato paste, a 400g can good-quality chopped tomatoes and a pinch of sugar. Gently cook for 20-25 mins until rich and thick. Season to taste, then leave to cool before serving.','15 mins','20 mins - 25 mins'),
(1470,'cod & tarragon fishcakes with minty peas','https://www.bbcgoodfood.com/recipes/cod-tarragon-fishcakes-with-minty-peas','https://images.immediate.co.uk/production/volatile/sites/30/2021/05/RS_GFO-0621-HealthyDietPlan-Recipes-Suppers-SalsaVerdeFishcakesWithMintyCrushedPeas_02897-3efb128.jpg','These mega-sized fishcakes are packed with high-protein white fish and served on top of a tasty mix of minty peas and leeks',4,4,1,3,24,'Heat the oven to 200C/180C fan/gas 6 and oil a non-stick baking sheet. Bring a pan of water to the boil, then cook the potatoes for 15-20 mins until tender. Drain well, then return to the pan and mash with the mustard, anchovies, spring onions, capers and tarragon until combined. Stir through the raw cod. Shape into 8 cakes, then coat lightly in the flour – about 1-2 tsp each. Beat the eggs in a shallow bowl. Coat the cakes in the egg, then sprinkle 1/2 tbsp of the almonds over each one and place on the baking sheet. Bake for 20 mins, turning each over halfway through, until starting to turn golden. Meanwhile, put the peas and leeks in a pan, submerge in water, then cover and boil for 10 mins. Drain and stir through the mint. Divide half of the pea and leek mixture between two plates with 2 fishcakes on top for each. Toss half the rocket with half the lemon juice and olive oil, then pile on top of the fishcakes. Chill 4 fishcakes and half the pea and leek mixture, separately, to eat another day. Will keep chilled for up to two days. Reheat the fishcakes on a baking tray at 200C/180C fan/gas 6 for 15-20 mins. Reheat the peas and leeks in the microwave. Dress the remaining rocket just before serving. ','20 mins','20 mins'),
(1471,'chargrilled turkey with quinoa tabbouleh & tahini dressing','https://www.bbcgoodfood.com/recipes/chargrilled-turkey-quinoa-tabbouleh-tahini-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-54106_11-852cbf5.jpg','This superhealthy supper is packed full of vibrant and fresh ingredients',5,4,1,3,24,'Tip the quinoa into a saucepan and pour over 600ml water. Cover with a lid and bring to the boil. Turn down and simmer until the water has evaporated (just as you’d cook rice) – about 20 mins. Take off the lid and leave to cool while you prepare the turkey and salad. Tip the cucumber, tomatoes, spring onions and herbs into a large mixing bowl. Pour over 1 tbsp olive oil and lemon juice, season well and mix everything together. Heat a griddle pan and, when smoking hot, rub the turkey steaks with 1 tsp olive oil. Cook for about 5 mins on each side, depending on thickness. Stir together all the dressing ingredients along with 3 tbsp water. Toss the quinoa together with the salad and arrange on plates. Cut the turkey into thick slices, pile up on the quinoa and drizzle over the dressing.','15 mins','20 mins'),
(1472,'warm roasted squash and puy lentil salad','https://www.bbcgoodfood.com/recipes/warm-roasted-squash-and-puy-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-814465_11-ed1cbd0.jpg','This fresh and vibrant salad makes use of tinned lentils, a storecupboard staple. The result is filling, low-fat and contains all of your five-a-day',5,4,1,3,24,'Heat the oven to 200C/180C fan/gas 4. Toss the butternut squash with 1 tbsp olive oil, garlic clove, thyme leaves and seasoning. Roast for 25-30 mins or until tender. Mix together the balsamic vinegar, ½ tbsp olive oil, the wholegrain mustard and 1-2 tbsp water. Drain the puy lentils in water and toss with the dressing, red onion, spinach and cherry tomatoes. Divide the lentils between four plates. Top with the squash, then crumble over Cheshire cheese and pumpkin seeds.','10 mins','30 mins'),
(1478,'miso roasted tofu with sweet potato','https://www.bbcgoodfood.com/recipes/miso-roasted-tofu-sweet-potato','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso-roasted-tofu-89b9d6f.jpg','A blend of miso and mirin give this extra-special tofu dish added depth of flavour. This simple vegan main course is flavourful, fresh and filling',10,2,1,3,24,'Heat oven to 200C/180C fan/gas 6. Wrap the tofu in kitchen paper, place in a shallow dish and put a heavy plate on top to help squeeze out the water. When the paper is wet, replace with another wrapping and weigh down again. Chop the tofu into medium cubes (about 2.5cm). In a small bowl, mix the dressing together with a whisk. Boil the beans for 1 min, then drain, rinse in cold water and set aside. Line a baking tray with parchment, spread out the tofu and pour over half the dressing. Sprinkle the sesame seeds on top and mix well. Bake for 20-25 mins until golden and crisp. Meanwhile, cut the sweet potatoes in half, place in a bowl, cover with cling film and microwave for 10-15 mins until very soft. Mash the sweet potato and serve in bowls with the tofu, green beans, the dressing poured over and some spring onions sprinkled on top.','15 mins','30 mins'),
(1473,'vegetarian carbonara','https://www.bbcgoodfood.com/recipes/healthier-veggie-carbonara','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/healthier-veggie-carbonara-117f40a.jpg','Enjoy this healthier veggie carbonara with mushrooms and courgettes for an easy family meal. It takes just 30 minutes, so is ideal on busy weeknights',107,4,1,3,24,'Put a large pan of salted water on to boil. Halve the courgettes lengthways and scoop out and discard the core, then slice the courgettes at an angle into small diagonal pieces. Put the spaghetti in the pan of boiling water and cook following pack instructions. To make the creamy carbonara sauce, put the egg yolks in a bowl, add half of the grated cheese, and mix with a fork. Add up to 3 tbsp water to make the sauce less thick. Season and set aside. Heat a large frying pan on a medium to high heat and pour in a little olive oil. Fry the courgette slices and thyme leaves with a good grinding of black pepper for a minute or two until the courgette starts to soften, then add the mushrooms. Fry for 2-3 mins until golden and slightly softened. For the last minute of the cooking, add the garlic. Working quickly, drain the pasta, reserving a little of the cooking water. Toss the pasta in the pan with the courgettes and mushrooms, then remove from the heat and add a ladleful of the reserved cooking water and the egg and cheese sauce. Add the fresh parsley, if using, and the lemon zest and juice, then sprinkle over most of the remaining cheese. Stir everything together quickly to coat the pasta. The egg will cook if the pasta is still hot. If you’re worried about it, put back on the heat for 1 min. Pour in a little more of the cooking water, if needed. You should have a silky and shiny sauce. Season to taste, then sprinkle with a little more cheese to serve. Eat straight away, as the sauce can become thick and stodgy if left for too long.','20 mins','10 mins'),
(1474,'quinoa salad with avocado mayo','https://www.bbcgoodfood.com/recipes/quinoa-salad-avocado-mayo-and-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/hdp-quinoa-salad-440-400-c1f2a12.jpg','Easy to put together and full of flavour, this healthy lunch option proides iron, folate, vitamin C and fibre. Make it vegetarian by leaving out the chicken',4,2,1,3,24,'Put the quinoa in a pan of boiling water and simmer for about 18 mins until the grains burst. Tip into a sieve and rinse under cold water. Meanwhile, scoop the avocado into a bowl and add the garlic, mustard and 2 tbsp lemon juice, then blitz with a hand blender or in a food processor until smooth. Add 1-2 tbsp cold water if it''s too thick. Stir the lemon zest into the quinoa, along with the corn, salad vegetables, mint and pumpkin seeds, then flavour with a little more lemon juice. Tip onto plates or into containers. Top with the chicken, if using, and spoon over the avocado mayo.','10 mins','18 mins'),
(1475,'slow cooker meatballs','https://www.bbcgoodfood.com/recipes/slow-cooker-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/meatballs-33b3bf7.jpg','Try these slow cooker meatballs for a tasty family meal. Turkey mince makes lighter meatballs which kids love. They also freeze well so you can batch cook',5,4,1,3,24,'Heat the slow cooker if necessary. Heat the oil in a non-stick frying pan and add the onion, carrots, celery and garlic and fry gently for a minute. Pour in the passata, add the parsley and stir, then transfer the lot to the slow cooker. To make the meatballs, tip the mince into a large bowl. Add the oats, paprika, garlic and plenty of black pepper, and mix everything together with your hands. Divide the mixture into 20 lumps about the size of a walnut and roll each piece into a meatball. Spray or run a non-stick pan with a little oil and gently cook the meatballs until they start to brown. Add them to the tomato base and cook on Low for 5 hours. Serve over rice or pasta if you like, or with a green salad.','1 hr','5 hrs'),
(1476,'salmon risotto','https://www.bbcgoodfood.com/recipes/salmon-risotto','https://images.immediate.co.uk/production/volatile/sites/30/2023/03/Salmon-risotto-ae398a0.jpg','Creamy rice and tender salmon make a comforting dinner. Stir through lemon juice and parmesan before serving this easy, delicious meal that''s full of omega 3 and high in protein',2,4,1,3,24,'Bring the chicken stock to a simmer in a saucepan over a medium heat, add the salmon fillets and poach for 6 mins or until just cooked. Remove from the pan, leaving the stock behind, and set aside to cool for 2-3 mins before removing the skin. Keep on a low heat or remove and leave the lid on to keep the stock as warm as possible. Heat the oil and half the butter in a large frying pan over a medium-low heat and, once the butter has melted, scatter in the onion and cook for 10-12 mins until softened but not golden. Add the garlic, stir and cook for 1 min before stirring in the rice and cooking for 2 mins until well coated in the oil. Pour in the wine, turn up the heat to medium, and cook for 4-5 mins until evaporated. Keep the pan on the heat and pour in about a quarter of the reserved chicken stock. Stirring often until the stock has been absorbed by the rice, about 4-5 mins. Repeat three more times, adding the next batch once the previous one has been absorbed. With the final batch, add the peas and flake in the cooked salmon fillets. Season with lots of freshly ground black pepper and cook for 3-4 mins until the rice is tender and the peas and salmon are heated through. If the rice needs a little more cooking or the stock absorbs too quickly, add a splash more hot water. Remove the risotto from the heat and stir through the remaining butter, lemon juice and parmesan. Scatter over the lemon zest, if you like, then serve straightaway.','15 mins','1 hr'),
(1477,'crispy chilli turkey noodles','https://www.bbcgoodfood.com/recipes/crispy-chilli-turkey-noodles','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/crispy-chilli-turkey-noodles-9b735d1.jpg','Crisp-fried turkey mince in a chilli, soy and garlic sauce atop udon noodles makes for a savoury 20-minute meal that''s low in fat but big on flavour ',14,4,1,3,24,'Heat 1 tbsp oil in a large non-stick frying pan over a high heat. Once hot, add the turkey mince to the pan and fry for 10-12 mins until golden brown and crispy, breaking up the meat with a wooden spoon as you go. Add the ginger and garlic to the pan and cook for 1 min. Stir in the honey, soy and chilli sauce and cook for 2 mins. Meanwhile, bring a large pan of water to the boil, add the noodles and cook following pack instructions. Drain and toss the noodles with the remaining 1 tbsp oil and all the lime juice, then divide between bowls. Top with the crispy turkey mince, carrot, onion and coriander. Serve with extra lime wedges for squeezing over, if you like.','5 mins','15 mins'),
(1479,'chicken skewers with tzatziki','https://www.bbcgoodfood.com/recipes/chicken-skewers-tzatziki','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-skewers-with-tzatziki-f4698fa.jpg','Kids will love these chicken skewers hot off the griddle for a quick supper, or pack them in wraps with green salad and tzatziki to take on a picnic',139,8,1,3,24,'Soak eight bamboo skewers in water. Using sharp kitchen scissors, chop the chicken into small pieces. Pop into a plastic box with a lid. Pare strips of lemon zest from the lemon using a vegetable peeler, then juice the lemon as well. Add both the peel and the juice to the chicken in the box along with the oregano and the garlic, crushed in. Season generously, mix and put in the fridge for 15 mins with the lid on. Deseed and chop the peppers into similar-sized pieces to those of the chicken. Heat a griddle pan to high while you get the chicken out. Discard the lemon zest and thread the chicken onto the skewers, alternating every few bits of chicken with a piece of red pepper followed by a piece of yellow pepper. Griddle for 10 mins, turning halfway. While the skewers are cooking, make the tzatziki. Get a box grater and a bowl. Cut the cucumber into long lengths, discarding the watery seedy core. Grate into the bowl, then grate the ¼ garlic clove. Season generously and stir in the Greek yogurt. Drizzle with a little extra virgin olive oil. Serve the skewers hot off the griddle with the dip, or take the chicken and peppers off the skewers, leave to cool and pack into wholemeal wraps spread with a little tzatziki and rolled up with baby spinach and a few picked leaves of parsley.','10 mins','15 mins'),
(1480,'parma pork with potato salad','https://www.bbcgoodfood.com/recipes/parma-pork-potato-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/parma-pork-with-potato-salad-6493118.jpg','Try this pork and potato salad for lunch or as a healthy dinner. The pork is wrapped in Parma ham which packs in flavour yet is low fat and low calorie',4,2,1,3,24,'Bring a pan of water to the boil, add the potatoes and celery and cook for 8 mins. Meanwhile, mix the yogurt, guerkins, caraway and mustard in a bowl. When the potatoes and celery are cooked, drain and set aside for a few mins to cool a little. Bash the pork pieces with a rolling pin to flatten them. Sprinkle over the sage and some pepper, then top each with a slice of Parma ham. Heat the oil in a non-stick pan, add the pork and cook for a couple of mins each side, turning carefully. Add the balsamic vinegar and let it sizzle in the pan. Stir the potatoes and celery into the dressing and serve with the pork, with some salad leaves on the side.','15 mins','15 mins'),
(1481,'chicken with rice & peas','https://www.bbcgoodfood.com/recipes/jamaican-chicken-rice-peas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jamaican-chicken-with-rice-peas-26381b5.jpg','When hunger strikes this healthy chicken with rice and peas dish will not only fill you up but provide plenty of nutrients, too, containing three of your five-a-day',5,4,1,3,24,'Heat the oil in a very large non-stick frying pan and cook the onions for 5 mins or until soft. Meanwhile, coat the chicken thoroughly with the thyme, curry powder and allspice. Add to the pan and briefly brown the chicken, then tip in the tomatoes, chilli, garlic and bouillon. Pour over 450ml water, cover and simmer for 30 mins or until the chicken is tender. Meanwhile, tip the rice into a medium-sized pan with the onion, garlic, thyme and bouillon. Pour in 600ml water, cover and cook for 25 mins or until the liquid has been absorbed and the rice is tender. Check toward the end of the cooking time to ensure it isn’t starting to catch on the pan. Stir in the beans and heat through. If you''re following our Healthy Diet Plan, serve half the rice with half the chicken (saving the leftovers for a second meal later in the week). Otherwise you can serve four portions, scattered with a few extra thyme leaves, if you like.','15 mins','30 mins'),
(1482,'cod with cucumber, avocado & mango salsa salad','https://www.bbcgoodfood.com/recipes/cod-cucumber-avocado-mango-salsa-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cod-cucumber-avocado-mango-salsa-salad-517846e.jpg','If you''re looking for a healthy lunch bursting with the colours and flavours of summer, this delicious cod, avocado and mango salad is low in fat and calories',5,2,1,3,24,'Heat oven to 200C/180C fan/gas 6. Put the fish in a shallow ovenproof dish and pour over half the lime juice, with a little of the zest, then grind over some black pepper. Bake for 8 mins or until the fish flakes easily but is still moist. Meanwhile, put the rest of the ingredients, plus the remaining lime juice and zest, in a bowl and combine well. Spoon onto plates and top with the cod, spooning over any juices in the dish.','5 mins','8 mins'),
(1483,'chicken meatballs with quinoa & curried cauliflower','https://www.bbcgoodfood.com/recipes/chicken-meatballs-quinoa-curried-cauliflower','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/curried-cauliflower-a2150c7.jpg','If you''re looking for a healthy bowl of flavourful chicken and veg to re-energise after a workout, these meatballs with quinoa and curried cauliflower will do the job',62,1,1,3,24,'For the meatballs, mix together all the ingredients in a bowl with some seasoning. Form into six balls and chill in the fridge for 20 mins. Heat oven to 200C/180C fan/ gas 6. Wash the quinoa and put it in a saucepan with 100ml water. Bring to the boil, then turn down to a gentle simmer and cook for 10-15 mins or until doubled in size and tender. Drain and set aside to cool. Put the cauliflower and sweet potato in a roasting tin and toss in the oil and curry powder. Put the meatballs in a separate tin. Cook both in the oven for 15 mins or until cooked through. Mix the quinoa with the cauliflower, sweet potato, pistachios and sultanas, squeeze over the lime juice, then serve with the meatballs.','25 mins','30 mins'),
(1484,'spanish chicken stew','https://www.bbcgoodfood.com/recipes/spanish-chicken-stew','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spanish-chicken-stew-7a63ed5.jpg','A healthy, hearty stew to feed the family, that you can prepare in just 10 minutes. This rustic meal is best served with chunks of crusty bread or rice',10,4,1,3,24,'In a large saucepan, heat 1 tbsp of the oil. Season the chicken and brown on both sides for 7-8 mins until golden, then set aside on a plate. Put the remaining 1 tbsp oil in the pan with the pepper, onion and garlic. Fry on a medium heat for 8 mins or until slightly golden. Add the vinegar and cook for 1 min. Return the chicken to the pan with the paprika and stock. Bring to the boil, then turn down to simmer for 25 mins until the chicken is cooked through. Add the olives and almonds, and serve with rice or crusty bread.','10 mins','30 mins'),
(1485,'tandoori trout','https://www.bbcgoodfood.com/recipes/tandoori-trout','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tandoori-trout-d6b35de.jpg','Oily fish such as trout is a great source of vitamin D, so why not serve up this spicy fillet with a cooling chutney? A healthy main meal the whole family can enjoy',16,4,1,3,24,'Coat the trout in the curry paste. Put the potatoes in a large pan of cold salted water, bring to the boil and cook for 15-20 mins until tender but still retaining their shape. Drain and leave to steam-dry. Heat the grill. Put the trout fillets on a baking tray lined with foil and cook until tender, about 6-8 mins. Meanwhile, heat the oil in a large frying pan and add the garlic and spices. Cook for a few mins until fragrant, then tip in the potatoes. Fry for 3 mins until crisp at the edges, then throw in the frozen peas. Cook for 2-3 mins more until warmed through. Season well and serve with the trout, coriander sprinkled over, and yogurt and chutney on the side.','5 mins','35 mins'),
(1486,'sesame tuna steaks with slaw','https://www.bbcgoodfood.com/recipes/sesame-tuna-steaks-asian-slaw','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tuna-steaks-2d597aa.jpg','Try to get the best quality tuna you can for these sesame steaks with a crunchy Asian-style slaw. A quick healthy supper, on the table in 20 minutes',16,2,1,3,24,'Mix together the sesame oil and lime juice with some seasoning, then toss with the red cabbage, chilli, yellow pepper, beansprouts and peanuts. Heat a non-stick frying pan until searing hot. Season the tuna steaks on both sides and turn them in the sesame seeds to coat. Fry for 3 mins each side until the tuna is browned and a sesame crust has formed, but the fish is still pink in the middle. Leave to rest for a few mins, then serve with the slaw and lime wedges for squeezing over.','10 mins','10 mins'),
(1487,'turkey burger with sweet potato wedges','https://www.bbcgoodfood.com/recipes/lean-turkey-burger-sweet-potato-wedges','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/turkey-burger-and-wedges-50dcf23.jpg','This healthy turkey burger recipe makes an easy meal for two. Try serving with vibrant green veg and sweet potato wedges for a filling dinner',96,2,1,3,24,'Heat oven to 180C/160C fan/gas 4. Zap the sweet potato wedges in the microwave for about 6 mins on full power until just turning soft. Leave to rest for 30 secs. While the sweet potato is spinning in the microwave, mix the mince in a bowl with the onion, garlic, egg and apple. Get your hands stuck in and work the ingredients together with a good pinch of salt and pepper, the oregano and paprika. Shape the mixture into two burgers about 3cm thick. Place on a lined baking tray in the oven for 20-25 mins, turning half way through cooking, until cooked through. Heat the coconut oil in a large frying pan over a high heat. Add the sweet potato wedges and shallow-fry for about 3 mins each side until they are nicely browned all over and cooked through to the centre. (Alternatively, toss in the oil and roast in the oven for 15-20 mins.) Drain on kitchen paper, then season with a good pinch of salt. Serve the burgers with the sweet potato wedges and a portion of green veg either steamed, blanched or boiled.','15 mins','25 mins'),
(1488,'chickpea stew with tomatoes & spinach','https://www.bbcgoodfood.com/recipes/chickpeas-tomatoes-spinach','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-871504_10-a91e5e1.jpg','For a great night in, whip up this warming meal, serve with a bit of naan and get comfy on the sofa',114,4,1,3,24,'Heat the oil in a wok and fry the onion over a low heat until softened. Stir in the garlic, ginger and chillies and cook for a further 5 mins until the onions are golden and the garlic slightly toasted. Add the turmeric, garam masala and cumin, stirring over a low heat for a few secs. Tip in the chopped tomatoes and add the tomato purée, then simmer for 5 mins. Add the chickpeas to the pan with 300ml water (fill the can three-quarters full). Simmer for 10 mins before stirring in the spinach to wilt. Season and serve with rice or naan.','10 mins','25 mins'),
(1505,'miso & sesame eggs','https://www.bbcgoodfood.com/recipes/miso-sesame-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso-sesame-eggs-a7b1d06.jpg','Perfect as a snack, miso adds a deep savoury flavour to these low-calorie eggs. Try them served on salad for a quick and tasty lunch, or as a starter',4,2,1,3,24,'Hard-boil the eggs for 10 mins then cool under running water. When cool enough to handle, carefully remove the shells. Halve the egg and scoop out their yolks into a small bowl. Add the miso and mash together, adding a dash of water to give a creamy consistency. Spoon back into the eggs and scatter with the seeds, then sprinkle over the paprika. Eat straight away or keep in the fridge for up to 2 days.','5 mins','10 mins'),
(1489,'tofu curry','https://www.bbcgoodfood.com/recipes/tofu-curry','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Tofu-curry-c24b410.jpg','Make a quick and summery veggie curry with tofu and spinach for an easy midweek meal. Serve with lime wedges and wholemeal chapatis.',18,4,1,3,24,'Heat 1/2 tbsp of the oil in a large, wide, non-stick frying pan over a medium-high heat. Fry the tofu with a pinch of salt for 5 mins, turning every couple of minutes until golden brown. Remove to a plate using a slotted spoon. Add the remaining oil to the pan, then fry half the onions for 5 mins, stirring often until golden brown. Add the ginger and garlic paste, stir-frying for a minute, then add the turmeric and cook for another 30 seconds to release the flavour. Stir in the coconut milk, then return the tofu to the pan along with the lime juice and 100ml water, and simmer for 5 mins. Stir in the spinach and the coriander, and cook for 1 min until wilted, then season. Serve the curry in bowls topped with the coriander leaves and the remaining onion, the lime wedges for squeezing over, and the chapatis on the side.','10 mins','20 mins'),
(1490,'puy lentils with smoked tofu','https://www.bbcgoodfood.com/recipes/puy-lentils-smoked-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentils_1-8e7c85c.jpg','Benefit from the slow-release energy of lentils and wholegrains combined with the rich flavour of smoked tofu, paprika and sweet peppers',4,2,1,3,24,'Spray a non-stick pan with 3 sprays of oil and add the courgettes, tofu and smoked paprika. Cook for a few mins to soften the courgettes. Stir in the balsamic vinegar and allow to sizzle and reduce. Meanwhile, tip the lentils, onion and pepper into a bowl and toss gently to break up any clumps of lentils. Add the tofu and courgettes, and toss again. Will keep for 2 days in the fridge. Scatter over the pea shoots just before serving.','10 mins','5 mins'),
(1491,'seitan & black bean stir-fry','https://www.bbcgoodfood.com/recipes/seitan-black-bean-stir-fry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/seitan-and-black-bean-stir-fry-6adb3dd.jpg','This handy vegan stir-fry makes a satisfying supper, with a sticky sweet and spicy sauce. Seitan, peanut butter and a mix of veggies is a winning combination',29,4,1,3,24,'Start by making the sauce, tip half the beans into the bowl of a food processor with the rest of the ingredients and add 50ml water. Season, then blend until smooth. Pour into a saucepan and heat gently for about 5 mins or until thick and glossy. Drain the seitan and pat dry with kitchen paper. Toss the seitan pieces in a bowl with the cornflour and set aside. Heat your wok to a high temperature, add a little oil, then the seitan – you might need to do this in batches. Stir-fry for around 5 mins until golden brown at the edges. Remove the seitan from the wok using a slotted spoon and set aside on a plate. If the wok is dry at this stage, add 1 tsp vegetable oil. Throw in the chopped peppers, the rest of the beans, pak choi and spring onion. Cook for 3-4 mins, then return the seitan to the pan, stir in the sauce and bring to the boil for 1 min. Serve with cooked rice or noodles.','20 mins','25 mins'),
(1492,'lentil ragu with courgetti','https://www.bbcgoodfood.com/recipes/lentil-ragu-courgetti','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentil-ragu-with-courgetti-1fec8e3.jpg','A healthy tomato ''pasta'' dish that makes full use of your spiralizer. This vegan-friendly supper is five of your five-a-day and will fill you to the brim',5,4,1,3,24,'Heat the 2 tbsp oil in a large sauté pan. Add the celery, carrots, garlic and onions, and fry for 4-5 mins over a high heat to soften and start to colour. Add the mushrooms and fry for 2 mins more. Stir in the lentils, passata, bouillon, oregano and balsamic vinegar. Cover the pan and leave to simmer for 30 mins until the lentils are tender and pulpy. Check occasionally and stir to make sure the mixture isn’t sticking to the bottom of the pan; if it does, add a drop of water. To serve, heat the remaining oil in a separate frying pan, add the courgette and stir-fry briefly to soften and warm through. Serve half the ragu with the courgetti and chill the rest to eat on another day. Can be frozen for up to 3 months.','15 mins','40 mins'),
(1493,'tempeh traybake','https://www.bbcgoodfood.com/recipes/tempeh-traybake','https://images.immediate.co.uk/production/volatile/sites/30/2021/12/BBQ-tempeh-traybake-e4b7e6b.jpg','Try our tasty tempeh-based traybake for dinner. Tempeh is a nutrient-rich plant protein made from fermented soya beans that''s easier to digest',4,2,1,3,24,'Heat the oven to 190C/170C fan/gas 5. Mix the marinade ingredients with plenty of black pepper and 2 tbsp water in a bowl, then stir in the tempeh to coat it. Set aside for 15 mins to marinate. Pile the vegetables onto a large baking tray and toss with the oil and oregano. Bake for 10 mins, then add the tempeh along with any remaining marinade, and bake for 30 mins more until the veg is tender. Leave to cool a little, then squeeze over the lemon juice to taste (start with a quarter) and scatter over the fresh thyme.','10 mins','40 mins'),
(1494,'refried beans','https://www.bbcgoodfood.com/recipes/refried-beans','https://images.immediate.co.uk/production/volatile/sites/30/2022/06/Refried-beans-4a30da5.png','Try this recipe for homemade refried beans. They work equally well as a party dip served with nachos, or as an accompaniment to a larger Tex-Mex-style meal.',140,4,1,3,24,'Lightly drain the beans, reserving the juice from the tin, but do not rinse (you want the beans in about half the liquid from the can). Depending on the texture you prefer, either mash them roughly in a bowl or blitz them using a blender until smooth. Set aside. Heat the oil in a large frying pan or casserole and cook the onions for 8-10 mins until soft and golden. Scatter in the garlic, chilli and cumin seeds, and sizzle everything for 2 mins more. Tip in the beans, oregano, paprika and a pinch of salt. Cook everything together for 5 mins until the beans have heated through adding more reserved bean juice if it’s too thick. Serve straightaway with a sprinkling of coriander, if you like, or leave to cool and keep chilled. Will keep chilled for up to three days or frozen for a month. Reheat with a splash of water mixed in.','15 mins','25 mins'),
(1495,'curried tofu wraps','https://www.bbcgoodfood.com/recipes/curried-tofu-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/curied-tofu-wrap-579cef2.jpg','This spicy, easy to make vegan supper is big on taste, offering calcium, iron, vitamin c and 2 of your 5 a day',24,4,1,3,24,'Mix the cabbage, yogurt and mint sauce, season and set aside. Toss the tofu with the tandoori paste and 1 tbsp of the oil. Heat a frying pan and cook the tofu, in batches, for a few mins each side until golden. Remove from the pan with a slotted spoon and set aside. Add the remaining oil to the pan, stir in the onions and garlic, and cook for 8-10 mins until softened. Return the tofu to the pan and season well. Warm the chapatis following pack instructions, then top each one with some cabbage, followed by the curried tofu and a good squeeze of lime.','20 mins','25 mins'),
(1496,'vegetable stew','https://www.bbcgoodfood.com/recipes/vegetarian-casserole','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-908509_10-4d83944.jpg','A Mediterranean one-pot stew with peppers, courgettes, lentils, sweet smoked paprika and thyme. This veggie stew is perfect for cold winter nights',44,4,1,3,24,'Heat the olive or rapeseed oil in a large, lidded, heavy-based pan over a medium heat. Lower the heat slightly then add the onion, cooking gently and stirring occasionally, for 5-10 mins until softened. Stir in the garlic cloves, smoked paprika, ground cumin, dried thyme, carrots, celery sticks, red and yellow peppers, and cook for 5 mins. Tip in the tomatoes along with the vegetable stock, courgettes and thyme sprigs. Give it a good stir, cover, and cook for 20-25 mins. Remove the thyme sprigs and discard. Stir in the cooked lentils and bring back to a simmer for 5 mins until warmed through. Serve with wild and white basmati rice, mash or quinoa.','10 mins','40 mins'),
(1497,'vegan fry-up','https://www.bbcgoodfood.com/recipes/vegan-fry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-fry-up-9c3dc26.jpg','Try this vegan take on the classic English breakfast that boasts vegan sausages with hash browns, mushrooms, tomatoes, scrambled tofu and baked beans',29,2,1,3,24,'Cook the potato whole in a large pan of water, boil for 10 mins then drain and allow to cool. Peel the skin away then coarsely grate. Mix with the peanut butter and season well. Set aside in the fridge until needed. Heat oven to 200C/180C fan/gas 6. Put the cherry tomatoes onto a baking tray, drizzle with 2 tsp sunflower oil, season and bake for 30 mins or until the skins have blistered and start to char. Cook the beans and sausages following the instructions on the pack so they''re ready to serve at the same time as the scrambled tofu. Meanwhile, mix the maple syrup, soy sauce and ¼ tsp smoked paprika together in a large bowl, add the sliced mushroom and toss to coat in the mixture. Leave to stand while you pour 2 tsp sunflower oil into a non-stick frying pan and bring it up to a medium high heat. Fry the mushroom until just starting to turn golden but not charred. Scoop onto a plate and keep warm until serving.  Put 1 tbsp oil into the frying pan and add spoonfuls of the potato mixture – you should get about 4. Fry for 3-4 mins each side then drain onto kitchen paper.  Crumble the tofu into your frying pan and sprinkle over the remaining ingredients and a good pinch of salt and pepper. If the pan looks a little dry add a splash more oil. Fry for 3-4 mins or until the tofu is broken into pieces, well coated in the seasoning and hot through. Divide everything between 2 plates and serve with a hot mug of tea made using soy milk.','15 mins','30 mins'),
(1498,'brazil nut refried beans','https://www.bbcgoodfood.com/recipes/brazil-nut-refried-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/refreied-beans-70b6c9f.jpg','Add crushed Brazil nuts to refried beans to give them a lovely buttery richness. Serve in our Brazil nut burritos or as a tasty side dish for a Mexican feast',29,4,1,3,24,'Crush the Brazil nuts, with a pestle and mortar or in a mini food processor, to a coarse paste. Tip into a saucepan, then add the kidney beans, including the liquid from the can. Fill the can halfway with water and add that too. Stir well, then add the rest of the ingredients except for the lime juice. Heat gently for 15 mins, then mash the beans with a potato masher until they have a loose porridge-like consistency. Keep cooking, stirring occasionally, until the mixture is still soft, but not runny, then add the lime juice and seasoning to taste. Try using the beans in our Brazil nut burritos.','5 mins','25 mins'),
(1499,'vegan fried chicken','https://www.bbcgoodfood.com/recipes/kentucky-fried-seitan','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kentucky-fried-seitan-e7d0879.jpg','Use a tofu substitute to make this addictive crispy fried ‘chicken’. Try this delicious vegan party food with BBQ sauce, or in a bun with salad or coleslaw',107,4,2,3,24,'First, make the seitan. Blitz the tofu, soy milk, miso, yeast extract, tarragon, sage, thyme, onion powder, garlic powder, 1 tsp salt and ½ tsp white pepper in a food processor until smooth. Tip into a bowl and add the wheat gluten and pea protein or protein powder. Mix to form a dough. Once it has come together, give it a really good knead, stretching and tearing for 10-15 mins. It will be ready when the seitan feels springy. Pour the veg stock into a pan with the onion, garlic and parsley stalks. Bring to a simmer. Flatten out the seitan to 1cm in thickness, and chop into chicken-breast-sized chunks. Simmer these in the stock for 30 mins, covered with a lid. Allow to cool in the stock. Ideally do this the day before and chill in the fridge. These can also be frozen if you wish. Mix the spice coating ingredients in one bowl. Place the gram flour in another and the plain flour in a third. Mix enough water into the gram flour until it has a texture similar to beaten egg. Dip the seitan pieces in the plain flour, shake off the excess, then coat each piece in the gram flour mixture and finally in the panko spice mix. In a large frying pan or deep fat fryer, heat the oil to 180C (or until a piece of bread browns in 20 seconds). Once it''s hot, carefully drop in the seitan pieces and cook for 6 mins or so, until they are dark golden brown and crispy. Transfer to kitchen paper to drain off the excess oil and sprinkle with a little salt. Serve in toasted buns with salad or slaw, or alternatively as mock chicken dippers with BBQ sauce.','45 mins','50 mins'),
(1500,'sesame & spring onion stir-fried udon with crispy tofu','https://www.bbcgoodfood.com/recipes/sesame-spring-onion-stir-fried-udon-with-crispy-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Sesame-spring-onion-stir-fried-udon-with-crispy-tofu-2801160.jpg','Do something different for dinner, with our vegan noodle, tofu, green bean and spring onion stir-fry. It''s quick and healthy, plus it''s budget-friendly too',12,4,1,3,24,'Drain and pat the tofu dry with kitchen paper. Cut into cubes, wrap in more kitchen paper, and place a heavy board over the top. Leave to drain for 15 mins. Mix the cornflour, chilli flakes and ground peppercorns together in a bowl with a pinch of salt, then add the drained tofu. Toss well to coat. Heat half the vegetable oil in a large non-stick frying pan over a high heat, and fry the tofu pieces for 5-6 mins until golden all over. Scoop out of the pan, and leave to drain on kitchen paper. Add the remaining oil to the pan and stir-fry the spring onions and beans for 3-4 mins until tender and lightly golden. Pour a kettle of boiling water over the noodles in a sieve to loosen. Drain well, then tip into the pan. Fry for a few minutes until piping hot. Add the sesame oil and sesame seeds, and sizzle for a few seconds. Splash in the soy sauce, rice vinegar, then add the tofu. Toss well, then serve in bowls with a pinch of sesame seeds on top and more soy sauce on the side.','5 mins','15 mins'),
(1501,'vegan ragu','https://www.bbcgoodfood.com/recipes/vegan-ragu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-ragu-7650653.jpg','Make this healthy vegan ragu base and turn it into a variety of meals, like warming lasagne, mouth-watering moussaka or a hearty bolognese',16,6,1,3,24,'Pour 800ml boiling water over the dried porcini and set aside for 10 mins. Meanwhile, pour 1½ tbsp oil into a large pan and gently cook the onion, carrot, celery and a pinch of salt, stirring for 10 mins, until soft. Remove the porcini and roughly chop. Set aside with the liquid. Add the garlic and thyme. Cook for 1 min, then stir in the purée and cook for 1 min. Pour in the wine. Cook until reduced, then add the lentils, mushroom stock and tomatoes. Bring to the boil, then reduce and simmer with a lid on. Meanwhile, heat a large frying pan. Add the 1½ tbsp oil, then tip in all of the mushrooms. Fry until the water has evaporated and the mushrooms are golden. Pour in the soy sauce and stir, then add the mushrooms to the lentil pan. Stir in the yeast extract. Cook over a medium heat for 30-45 mins, stirring occasionally, until the lentils are cooked. Remove the thyme and season.','20 mins','1 hr'),
(1502,'soba noodle & edamame salad with grilled tofu','https://www.bbcgoodfood.com/recipes/soba-noodle-edamame-salad-grilled-tofu','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488660_11-ecf4ff0.jpg','This low-fat, superhealthy supper will have you feeling virtuous all week. If you can''t get edamame beans, use broad beans instead',5,4,1,3,24,'Heat dressing ingredients in your smallest saucepan, simmer for 30 secs, then set aside. Boil noodles following the pack instructions, adding the edamame beans for the final 2 mins cooking time. Rinse under very cold water, drain thoroughly and tip into a large bowl with the spring onions, beansprouts, cucumber, sesame oil and warm dressing. Season if you like. Brush tofu with the veg oil, season and griddle or grill for 2-3 mins each side – the tofu is very delicate so turn carefully. Top the salad with the tofu, scatter with coriander and serve','15 mins','15 mins'),
(1503,'nutty chicken satay strips','https://www.bbcgoodfood.com/recipes/nutty-chicken-sate-strips','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/nutty-chicken-sate-strips-67269e0.jpg','Keep these nutty chicken satay strips in the fridge for a healthy choice when you''re peckish. The chicken is served with cucumber and sweet chilli sauce',4,2,1,3,24,'Heat oven to 200C/180C fan/gas 4 and line a baking tray with non-stick paper. Mix 2 tbsp chunky peanut butter with 1 finely grated garlic clove, 1 tsp Madras curry powder, a few shakes of soy sauce and 2 tsp lime juice in a bowl. Some nut butters are thicker than others, so if necessary, add a dash of boiling water to get a coating consistency. Add 2 skinless chicken breast fillets, cut into strips, and mix well. Arrange on the baking sheet, spaced apart, and bake in the oven for 8-10 mins until cooked, but still juicy. Eat warm with roughly 10cm cucumber, cut into fingers, and sweet chilli sauce. Alternatively, leave to cool and keep in the fridge for up to 2 days.','10 mins','8 mins - 10 mins'),
(1504,'protein balls','https://www.bbcgoodfood.com/recipes/protein-balls','https://images.immediate.co.uk/production/volatile/sites/30/2022/10/Protein-balls--ac4ce52-e1676039481255.jpg','Make tasty protein balls using oats, protein powder, flaxseed and cinnamon to enjoy post-exercise and replenish your protein stores.',15,15,1,3,24,'Combine the oats, protein powder, flaxseed and cinnamon in a large bowl. Stir in the maple syrup, vanilla extract, nut butter, milk and chocolate chips. Stir well to combine the mixture, then, using damp hands, roll it into 15 balls (they should be about 30g each). Arrange on a plate and chill for 30 mins until firm, then serve.','15 mins','No Time'),
(1510,'squash, pea & feta frittatinis','https://www.bbcgoodfood.com/recipes/squash-pea-feta-frittatinis','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/squash-pea-feta-frittatinis-3ab2adc.jpg','These mini Italian omelettes, with butternut squash and cheese, are baked in individual cases ready to be packed up for a picnic',6,8,1,3,24,'Heat oven to 200C/180C fan/gas 6. Put the butternut squash in a bowl, cover with cling film and cook in the microwave on High for 5-7 mins until tender. Meanwhile, line 8 holes of a muffin tin with squares of baking parchment – allow a little overhang at the top as the frittatinis will puff up. Divide the squash, peas and feta between the lined muffin holes – they should be quite full. Beat the eggs in a jug with some seasoning, then pour into the muffin holes. Put the tin in the centre of the oven and bake for 20 mins. Leave to cool for about 15 mins before packing into a cooler bag for transporting, or chill for up to 24 hrs.','20 mins','25 mins'),
(1511,'smoked paprika prawn skewers','https://www.bbcgoodfood.com/recipes/smoked-paprika-prawn-skewers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/prawn-skewers-56b1264.jpg','Healthy and packed with summer flavours, these paprika-spiked prawn skewers are based on pintxos - the Basque version of tapas',7,6,1,3,24,'Soak the skewers in a bowl of water for 10 mins. Meanwhile, peel the prawns, leaving the tails intact, and devein. To do this, run a sharp knife down the back, making a tiny incision just enough to remove the visible black vein. Wash the prawns and pat dry with kitchen paper. In a medium-sized bowl, mix together the paprika, garlic, cumin, oregano, lemon zest and 1 tbsp olive oil. Add the prawns and leave to marinate for 15 mins at room temperature. Then skewer a prawn onto each stick. Heat the remaining oil in a roomy frying pan and fry the prawns for 3-4 mins, turning halfway through until just cooked. You may need to do this in batches. Season, squeeze over some lemon juice and serve.','10 mins','10 mins'),
(1512,'turkey salad on rye','https://www.bbcgoodfood.com/recipes/turkey-salad-rye','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/rye-5330430.jpg','Open sandwiches mean you use less bread, but can pile on plenty of topping, in this case Coronation-spiced lean turkey with crisp salad',4,2,1,3,24,'Mix the dressing ingredients and spread a little on each slice of bread. Pile on the tomato, cucumber and turkey slices, and the lettuce. Top with any remaining dressing. Best eaten the same day.','8 mins','No Time'),
(1513,'sesame beef wraps','https://www.bbcgoodfood.com/recipes/sesame-beef-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1259660_8-6c66043.jpg','The perfect Asian-inspired pre-dinner nibble, perfect for a Christmas get-together with friends',8,6,1,3,24,'Heat oil in a large frying pan. When the pan is really hot, tip in the beef and pepper, then cook for a few mins until lightly browned and the beef is cooked through. Tip in the garlic, ginger and sesame seeds, then cook for 1-2 mins more, stirring often until they start to turn golden. Pour over the soy sauce and 2 tbsp water, let it bubble down a bit until it starts to look syrupy, then drizzle with the sesame oil. Separate the leaves from the lettuces and arrange on a plate. Spoon a little of the beef mixture into each leaf, drizzle with pan juices and serve warm.','10 mins','20 mins'),
(1514,'lighter scotch eggs','https://www.bbcgoodfood.com/recipes/lighter-scotch-eggs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1256468_8-a88f3b9.jpg','The picnic classic gets a healthy makeover - two thirds of the fat is knocked off by using lentils and lean pork and baking instead of frying',37,4,1,3,24,'Heat 1 tsp of the oil in a small non-stick frying pan. Tip in the shallot and fry for a few mins until softened. Transfer to a plate and set aside to cool. (No need to wash the pan, you can use it later.) Meanwhile, put 4 of the eggs in a medium pan, covering well with cold water. Bring to the boil – as the water starts to bubble, set the timer and boil for 5 mins. When cooked, pour off the boiling water and cool the eggs under cold water to stop them from cooking further. Mash the lentils well in a medium bowl with the back of a fork, then stir in the pork mince, sage, 2 tsp of the chives, the mustard, nutmeg, cooled shallots, a pinch of salt and a generous grating of black pepper. Peel the shells from the eggs and pat dry with kitchen paper. Divide the meat mix evenly into 4. Tip the flour onto a plate and roll in each egg to coat, tapping off any excess. Pat down a quarter of the meat mix on the work surface to a 12-13 cm circle, using the rest of the flour to keep it from sticking. Cup the circle in your hand and place one of the eggs in the centre. With both hands, pat, press and ease the meat mix around the egg until it is completely and evenly covered. Seal really well so there is no join, then pat and roll it into a good shape on the floured surface. Repeat with the rest of the meat mix and cooked eggs. Mix the panko crumbs on a large plate with the remaining chives. Beat the remaining egg on a plate, brush some all over each coated egg (you won’t use it all), then roll the eggs in the panko crumbs, patting them on to stick. Lay the eggs on a baking parchment-lined baking tray and chill for 20-25 mins (but not overnight). Heat oven to 190C/170C fan/gas 5. Heat 2 tsp of the remaining oil in the pan you used for the shallot. When quite hot (it is hot enough when a few panko crumbs dropped in sizzle immediately and start to brown), put in 2 of the scotch eggs and roll in the oil to coat them well. Set the timer for 2 mins, then fry, turning often, to brown all over. You are just browning, not fully cooking the eggs at this stage, so don’t overcook or the coating may start to split. Transfer to the lined baking tray and repeat with the remaining eggs, lowering the heat slightly if the pan gets too hot. Bake the scotch eggs for 12 mins. Remove and lay them on kitchen paper to drain and leave to cool slightly. Halve the scotch eggs using a sharp knife and serve slightly warm or cold. Best eaten the same day or, when cold, can be stored overnight in the fridge in an airtight container.','40 mins','25 mins'),
(1515,'miniature omelettes with ricotta','https://www.bbcgoodfood.com/recipes/miniature-omelettes-ricotta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-3774_12-60c88b3.jpg','These little omelettes – called frittatine in Italian – make a wonderful appetizer',141,6,1,3,24,'Beat the eggs with 2 tbsp water, then season if you want to. Mix in the parsley, chives, garlic and parmesan. Heat a 23cm heavy-based frying pan on a medium heat with a little oil. When the oil’s hot, add a ladleful (about a third) of the egg mixture and tip the mixture so it covers the base. Reduce the heat and cook the omelette until just firm, then flip it over and cook the other side. Keep warm. Repeat to make three omelettes, adding a little extra oil to the pan each time. Make the filling by combining the ricotta, basil, parmesan and some pepper. Spread mixture over each omelette, roll up loosely like a cigar and cut into thickish slices. Can be made up to 5 hrs ahead. Serve garnished with parsley.','35 mins','No Time'),
(1535,'baked olives with feta','https://www.bbcgoodfood.com/recipes/baked-olives-feta','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/baked-olives-with-feta-7ff33c7.jpg','Roast olives and cheese with garlic, fennel and chilli for an easy and delicious canapé - a tasty dinner party nibble',10,4,1,3,15,'Heat oven to 200C/180C fan/gas 6. Put the olives and feta in a small roasting tin with the olive oil, garlic, fennel seeds and chilli flakes. Bake for 12 mins and serve warm with cocktail sticks and bread, if you like.','5 mins','12 mins'),
(1516,'spiced tortilla','https://www.bbcgoodfood.com/recipes/spiced-tortilla','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-10375_11-bf81bd0.jpg','Perfect for dinners, lunches and snacks, this spiced tortilla is a great in-the-fridge standby for many occasions',5,4,1,3,24,'Heat the oil in a large frying pan. Fry the onion and half the chilli for 5 mins until softened. Tip in the spices, fry for 1 min more, then add the cherry tomatoes, potatoes and coriander stalks to the pan. Season the eggs well, pour over the top of the veg and leave to cook gently for 8-10 mins until almost set. Heat the grill and flash the tortilla underneath for 1-2 mins until the top is set. Scatter the coriander leaves and remaining chilli over the top, slice into wedges and serve with a green salad.','5 mins','20 mins'),
(1517,'saag paneer','https://www.bbcgoodfood.com/recipes/saag-paneer','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/saag-paneer-4893170.jpg','An Indian dish with plenty of flavour, saag paneer is a well-loved vegetarian side dish. It''s rich in calcium and folate from the spinach and is gluten-free, too',41,6,1,3,15,'Melt the ghee, whisk in with the turmeric and chilli powder, then add the cubed paneer and toss well. Set aside. If using frozen spinach, microwave for 3-5 mins, then place in a sieve and squeeze out most of the water. If using fresh spinach, place in a colander, pour over boiling water, drain and cool, then put in a tea towel and squeeze out most of the water. Roughly chop. Blitz the onion with the garlic, ginger and green chilli. Cook the paneer in a large non-stick frying pan over medium heat for around 8 mins, tossing the pan so they become golden all over. Remove and set aside on a plate, leaving spices behind in the pan. Tip the onion mix into the pan, add a pinch of salt and turn the heat down. Fry until caramel coloured, around 10 mins, adding a splash of water if it looks a little dry. Add the garam masala, stir to coat the onion mix, fry for 2 mins. Add the spinach and cook for a further 2-3 mins, adding 100ml water to release all the flavours from the bottom of the pan. Season to taste. Add the paneer and cook for 2-3 mins to heat through. Spoon into bowls and squeeze over a little lemon juice, to serve.','10 mins','20 mins'),
(1518,'greek bouyiourdi','https://www.bbcgoodfood.com/recipes/greek-bouyiourdi','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/greek-bouiyourdi-54f3d79.jpg','Enjoy this baked feta with tomatoes, garlic and chilli as part of a meze lunch. It''s perfect for scooping up with warm pitta breads alongside a Greek salad or use keto bread for a keno-friendly version',6,4,1,3,15,'Cut 1 tomato through the middle, then cut two slices from the centre and set aside. Scoop the seeds from the rest of the tomato, then grate the flesh, discarding the skin. Deseed and grate the rest of the tomatoes in the same way, then mix the grated flesh with the garlic. Season and spoon into a 16cm baking dish. Heat the oven to 200C/180C fan/gas 6. Nestle the feta block in the garlicky tomatoes, then top with the sliced tomatoes, the chilli, oregano, olive oil and a pinch of sea salt. Cover the dish and bake for 15 mins, then uncover and bake for a further 15 mins. Serve warm with the pitta or keto breads on the side for dunking.','10 mins','30 mins'),
(1519,'thai broccoli rice','https://www.bbcgoodfood.com/recipes/thai-broccoli-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/thai-broccoli-rice-54b9069.jpg','Replacing rice with broccoli that''s been blitzed to grains makes a colourful and healthy, quick-cook vegetarian keto meal ',67,4,1,3,15,'Heat a frying pan over a medium heat and add the peanuts. Toast evenly, regularly shaking the pan, then remove and set aside. Put the broccoli in a food processor and pulse until it looks like green couscous grains. Empty into a large bowl and set aside. Heat the oil in a large frying pan and fry the onion, garlic, ginger and chilli until soft and aromatic. Add the broccoli rice to the pan and mix through, making sure everything is well coated. Sauté for 3-4 mins until al dente. Transfer to a large bowl and add the red cabbage, red pepper, half the coriander and half the toasted peanuts. Mix to combine. To make the dressing, whisk the lime zest and juice, tamari, sugar and oil together until combined. Toss the dressing through the broccoli rice and transfer to a serving bowl or individual bowls. To serve, garnish with the remaining coriander and peanuts.','25 mins','10 mins'),
(1520,'keto bread','https://www.bbcgoodfood.com/recipes/keto-bread','https://images.immediate.co.uk/production/volatile/sites/30/2022/01/Keto-bread-5da17a0.jpg','Try this keto almond bread for a healthy bread replacement that''s made with nuts and seeds. The loaf will be slightly wet but you can dry the slices in an oven or toast them',142,1,1,3,15,'Heat the oven to 200C/180C fan/gas 6. Oil and line a 900g loaf tin with baking parchment. Set aside. Put all the dry ingredients in a large mixing bowl, stir together and set aside. Put the coconut oil, eggs, courgette and 115ml hot water in a separate large mixing bowl, then tip into the bowl with the dry ingredients and combine. Stir in the apple cider vinegar, mixing gently – try not to overmix the dough. Transfer the mix to the loaf tin. Wet your fingers to smooth the top of the bread, or use the back of a spoon. Bake for 20 mins, then reduce the oven to 150C/130C fan/gas 2 and bake for 30 mins more. Remove from the oven and leave to cool in the tin for 10 mins, then transfer to a cooling rack. Slice the bread and toast the slices in a pan or oven.','10 mins','50 mins'),
(1522,'vegetarian thai green curry','https://www.bbcgoodfood.com/recipes/vegetarian-thai-green-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegetarian-thai-curry-12b120a.jpg','Our easy veggie version of a classic Thai curry is ready in under an hour and contains 3 of your 5-a-day',14,4,1,3,15,'Heat the oil in a large flameproof casserole dish with a tight-fitting lid. Add the shallots with a generous pinch of salt and fry for 7-10 mins over a medium heat until softened and beginning to caramelise. Add the curry paste and chilli to the dish and fry for 2 mins. Tip in the squash and pepper, then stir through the coconut milk along with 200ml water. Add the lime leaves, cover and cook for 15-20 mins or until the squash is tender. Stir the mangetout and baby corn through the curry, then re-cover, cooking over a medium-low heat for a further 5 mins or until the veg is just cooked. Season and stir through half the coriander. Remove the lime leaves and discard. Spoon the curry into deep bowls, scatter with the remaining coriander and serve with rice and lime wedges for squeezing over.','15 mins','40 mins'),
(1523,'roast aubergines with yogurt & harissa','https://www.bbcgoodfood.com/recipes/roast-aubergines-yogurt-harissa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roast-aubergines-with-yogurt-harissa-39a0c4e.jpg','Looking for a simple side or starter that delivers on taste? Look no further than roast aubergines with yogurt and harissa. It''s vegetarian and gluten free',70,4,1,3,15,'Heat the oven to 200C/190C fan/gas 6. Pierce the aubergines a few times with the tip of a knife, then brush with olive oil and put them on a baking sheet. Roast for about 40-45 mins, or until the aubergines are completely tender.  Melt the butter in a pan, then stir through the harissa (see our recipe). Cut the aubergines in half, opening them up a bit like a baked potato. Put them on a warm platter and season the inside of each one, then mix the yogurt with the garlic. Spoon some of this into each aubergine, then drizzle over the spiced butter. Scatter over the coriander and sesame seeds to serve.','5 mins','40 mins'),
(1524,'stuffed & grilled vegetable bites','https://www.bbcgoodfood.com/recipes/stuffed-grilled-vegetable-bites','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/stuffed-grilled-vegetable-bites-5abb4a6.jpg','These delicious vegetarian tapas bites are filled with creamy ricotta, sundried tomatoes and basil - a perfect dinner party nibble',88,8,1,3,15,'Cut the aubergine and courgette into thin lengths about 2-3mm thick – you should have 8 slices of each. Drain and rinse the peppers, remove any seeds, pat dry, then cut into quarters. Mix the garlic with the olive oil and some seasoning. Heat a griddle pan over a medium heat. Brush the vegetable slices with the garlic oil and cook for 2-3 mins each side until completely soft and lightly charred (you’ll need to do this in batches). You could also do this on a barbecue. Set aside on a plate and leave to cool. The vegetables can be griddled the day before and kept covered in the fridge – bring to room temperature before serving. Mix together the cheeses, sundried tomatoes, lemon zest and seasoning. Lay out the slices of aubergine on a large board. Top each with a slice of courgette, a strip of pepper, and a basil leaf. Dot 1 tbsp of the cheese mix on top and roll the vegetables up. Skewer with a cocktail stick to secure. When ready to serve, arrange on a plate and scatter over the parsley leaves and paprika.','15 mins','35 mins'),
(1525,'roasted cauliflower steaks','https://www.bbcgoodfood.com/recipes/cauliflower-steaks-roasted-red-pepper-olive-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cauliflower-steaks-2bccad0.jpg','Unlock the flavours of cauliflower with a red pepper, olive and caper salsa, topped with almonds. Healthy and vegan, it makes a tasty light lunch or supper',16,2,1,3,15,'Heat oven to 220C/200C fan/gas 7 and line a baking tray with baking parchment. Slice the cauliflower into two 1-inch steaks – use the middle part as it’s larger, and save the rest for another time. Rub the paprika and ½ tbsp oil over the steaks and season. Put on the tray and roast for 15-20 mins until cooked through. Meanwhile, make the salsa. Chop the pepper, olives, parsley and capers, and put into a bowl and mix with the remaining oil and vinegar. Season to taste. When the steaks are cooked, spoon over the salsa and top with flaked almonds to serve.','15 mins','15 mins - 20 mins'),
(1526,'easy crustless quiche','https://www.bbcgoodfood.com/recipes/crust-less-quiche','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quiche-36cd072.jpg','Make a simple crustless quiche to cut out the faff of making pastry. It''s packed with cheese, smoky pancetta and green veg for a lovely lunch or picnic dish',14,6,1,3,15,'Heat the oven to 180C/160C fan/gas 4. Butter and line a 23cm round deep, springform cake tin so the paper comes 2-3cm up the sides of the tin. Melt the butter in a frying pan. Add the onion and pancetta and cook over a low heat for 10 mins or until softened and translucent. Bring a pan of lightly salted water to the boil. If using broccoli, cut into small florets, if using asparagus, keep whole. Blanch the veg for 2 mins, drain and leave to steam-dry. Whisk the eggs, cream and two thirds of the cheese together in a bowl. Season with salt and black pepper. Mix through the onion and bacon mixture. Pour into the tin and top with the veg and remaining cheese. Bake for 25-30 mins or until golden brown with a slight wobble in the centre.','15 mins','40 mins'),
(1527,'halloumi & pepper kebabs with lemony cabbage salad','https://www.bbcgoodfood.com/recipes/halloumi-pepper-kebabs-lemony-cabbage-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi_0-1acbdeb.jpg','The Cypriot cheese is a great choice for meat-free skewers as it doesn''t melt- these barbecue-friendly veggie bites are budget-friendly too',50,4,1,3,15,'Thread the halloumi and red peppers onto 8 soaked wooden skewers and put on a plate. Stir the oil and oregano together, then drizzle it over the kebabs. Season well with black pepper – you shouldn’t need salt. To make the salad, put the cabbage in a bowl. Mix together the oil, lemon juice and seasoning, pour over the cabbage, and toss until well coated, then stir through the parsley. Barbecue the skewers for 10 mins on each side until the cheese is golden and the peppers slightly charred. Serve on a large platter with pitta or keto breads and tzatziki, with salad on the side.','20 mins','20 mins'),
(1528,'greek salad','https://www.bbcgoodfood.com/recipes/greek-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201727_11-4026b8d.jpg','Make a fresh and colourful Greek salad in no time. It''s great with grilled meats at a barbecue, or on its own as a veggie main',7,4,1,3,15,'Place 4 large vine tomatoes, cut into wedges, 1 peeled, deseeded and chopped cucumber, ½ a thinly sliced red onion, 16 Kalamata olives, 1 tsp dried oregano, 85g feta cheese chunks and 4 tbsp Greek extra virgin olive oil in a large bowl. Lightly season, then serve with crusty bread to mop up all of the juices.','15 mins','No Time'),
(1529,'courgette & halloumi skewers','https://www.bbcgoodfood.com/recipes/courgette-halloumi-skewers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201644_11-5a291ba.jpg','Marinade cheese with mint, lemon and olive oil, thread onto kebab skewers with veg and chargrill ',5,4,1,3,15,'Mix the chilli, half the mint, lemon zest and juice, oil, courgettes and halloumi. Leave to marinate for 30 mins. Soak 8 wooden skewers for 20 mins. Thread the courgettes and halloumi onto the skewers. Cook on the BBQ, or under a grill, for 7-8 mins, turning halfway through and basting with the remaining marinade. Scatter over remaining mint.','10 mins','8 mins'),
(1530,'feta & semi-dried tomato omelette','https://www.bbcgoodfood.com/recipes/feta-semi-dried-tomato-omelette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201564_11-feaae3c.jpg','Cooking up eggs with cheese and tomato makes for a super-quick meal for one that''s ready in 10 minutes',5,1,1,3,15,'Heat the oil in a small frying pan, add the eggs and cook, swirling the eggs with a fork as they set. When the eggs are still slightly runny in the middle, scatter over the tomatoes and feta, then fold the omelette in half. Cook for 1 min more before sliding onto a plate. Serve alongside a mixed leaf salad.','5 mins','5 mins'),
(1531,'avocado with tamari & ginger dressing','https://www.bbcgoodfood.com/recipes/avocado-tamari-ginger-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-with-tamari-ginger-dressing-a71c3e7.jpg','This vegan snack with ginger, tamari and zesty lemon is a real zinger that''s ready in just 5 minutes',5,2,1,3,15,'Mix the garlic, ginger, tamari and lemon juice in a small bowl. Dilute with 1-2 tsp water. Cut the avocado in half and destone, then spoon in the dressing and eat with a teaspoon.','5 mins','No Time'),
(1532,'cheesy autumn mushrooms','https://www.bbcgoodfood.com/recipes/cheesy-autumn-mushrooms','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560595_11-efe2931.jpg','A low-carb treat, ready in 5 mins',8,4,1,3,15,'Heat oven to 200C/fan 180C/gas 6. Arrange the mushrooms on a baking tray. Scatter over the cheese, walnuts, thyme sprigs and butter. You can do up to this stage a day in advance. Pop in the oven and cook for 10 mins until the cheese is melted and the mushrooms are softened. Arrange some rocket leaves on plates and place the mushrooms on top.','10 mins','No Time'),
(1533,'soft-boiled eggs with pancetta avocado soldiers','https://www.bbcgoodfood.com/recipes/soft-boiled-eggs-pancetta-avocado-soldiers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/soft-boiled-eggs-with-pancetta-avocado-soldiers-89d0e13.jpg','Soft, creamy avocado wrapped in crispy pancetta, perfect for dipping in your morning egg',1,2,1,3,15,'Bring a large saucepan of salted water to the boil. Carefully drop the eggs into the water and boil for 5 mins for runny yolks. Meanwhile, heat the oil in a non-stick pan and wrap each avocado slice in pancetta. Fry for 2-3 mins over a high heat until cooked and crisp. Serve the eggs in egg cups with the pancetta avocado soldiers on the side for dipping.','10 mins','5 mins'),
(1534,'citrus & fennel marinated olives','https://www.bbcgoodfood.com/recipes/citrus-fennel-marinated-olives','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/citrus-fennel-marinated-olives-4fa15a9.jpg','This simple tapas dish can be made well in advance - the longer they are left, the more flavour the olives will absorb',88,4,1,3,15,'Heat a large dry pan, add the fennel seeds and cook for 1-2 mins until fragrant. Add the citrus zests, bay leaves, rosemary and chillies, then cook for 30 secs more. Tip in the olives and add the olive oil. Mix well and cook for 1-2 mins, then turn off the heat and leave to cool in the pan before serving. Will keep in a plastic container in the fridge for up to 1 week – bring to room temperature before eating.','10 mins','5 mins'),
(1538,'peanut butter chicken curry','https://www.bbcgoodfood.com/recipes/peanut-butter-chicken-curry','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/peanut-butter-chicken-curry-ea93cf8.jpg','Cook this chicken curry using peanut butter as the base of the sauce, along with plenty of chilli and spices. You''ll want to make it your regular Friday night fakeaway',52,4,1,3,15,'Put the chicken, chopped garlic, a third of the lemongrass and half the ginger, spices and lime juice in a large bowl. Toss, then cover and leave for 30 mins, or chill for up to 24 hrs. Blitz the whole garlic, the rest of the lemongrass and ginger, remaining spices, chopped chilli, onion and a large splash of water to in a food processor. Set aside. Heat the oil in a pan and brown the chicken all over. Set aside on a plate. Cook the paste for 8-10 mins until it splits. Stir in the peanut butter and kecap manis. When thickened, add the coconut milk and half a can of water, bring to a simmer, season, then add the chicken with its juices. Continue to simmer for 40 mins, stirring often. Turn off the heat, add the rest of the lime juice and season. Leave to rest for 10 mins. Scatter over the sliced chilli, spring onions, fresh coriander and peanuts to serve.','20 mins','1 hr and 10 mins'),
(1539,'goan mussels','https://www.bbcgoodfood.com/recipes/goan-mussels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1176460_11-a3e79c5.jpg','You can add any other seafood to this quick aromatic green curry - prawns can go in with the coconut milk and clams with the mussels',7,4,1,3,15,'Remove the beards, then wash the mussels well in cold water. Refresh the water and repeat until it is clear. Discard any mussels that are broken or stay open when tapped. Heat the oil in a flameproof casserole. Fry the onion until very lightly brown, then add the ginger, garlic, chillies, spices, a good pinch of salt and a grinding of pepper. Cook for 2-3 mins until fragrant and toasted. Pour in the coconut milk and bring to the boil, then simmer for a few mins to get everything mixed together. Tip the mussels into the dish, cover, turn up the heat to maximum and boil for 3-4 mins until the mussels have just opened. Scatter over the coriander sprigs and serve with lime wedges for squeezing over.','15 mins','15 mins'),
(1540,'soy & butter salmon parcels','https://www.bbcgoodfood.com/recipes/soy-butter-salmon-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/soy-butter-salmon-parcels-d75f456.jpg','Make these tasty soy, honey and butter salmon parcels on a barbecue for the taste of summer. Serve with a refreshing cucumber and sesame salad on the side',19,4,1,3,15,'Heat the barbecue. If you are using coals, wait until they turn white. If you are indoors, heat a griddle pan. Cut four pieces of foil that will easily wrap a piece of salmon and lay them on the work surface. Spread a little butter onto the centre of each piece of foil to stop the salmon sticking. Lay the salmon on top. Mix the soy with the honey and divide it between the parcels, pouring it over the salmon. Dot any remaining butter on top and then fold the foil around the salmon tightly to make a parcel. Put the parcels on the barbecue or griddle and cook for 5-10 mins. Check one parcel to see how it’s getting along but be careful – it will be hot. Once the salmon is cooked, open the parcels and scatter some sesame and spring onion into each. Mix the cucumber with a few drops of sesame oil and season with a little salt. Serve the salmon with the cucumber salad.','20 mins','10 mins'),
(1541,'peanut butter chicken','https://www.bbcgoodfood.com/recipes/peanut-butter-chicken','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/peanut-butter-chicken-37f339a.jpg','You''ll love this new, budget chicken dish. Any leftovers freeze well and make a handy lunch.',1,4,1,3,15,'Heat 1 tbsp of the oil in a deep frying pan over a medium heat. Brown the chicken in batches, setting aside once golden. Fry the onion for 8 minutes until softened. Then add the garlic, chilli and ginger and fry in the other 1 tbsp oil for 1 min. Add the garam masala and fry for 1 min more. Stir in the peanut butter, coconut milk and tomatoes, and bring to a simmer. Return the chicken to the pan and add the chopped coriander. Cook for 30 mins until the sauce thickens and the chicken is cooked through. Serve with the remaining coriander, roasted peanuts and rice, if you like.','10 mins','40 mins'),
(1542,'cashew, chilli & lime-crusted fish','https://www.bbcgoodfood.com/recipes/cashew-chilli-lime-crusted-fish','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-46050_10-bd4aa3a.jpg','The full-flavoured crust gives a hint of heat to this Indian-style meal. Serve with sautéed greens for a keto-friendly dinner.',143,4,1,3,15,'Rub the oil and garlic paste over the fish with 2 tbsp of the lime juice. Season, then marinate for 20-30 mins. Heat oven to 190C/170C fan/gas 5. Whizz together the crust ingredients and the remaining lime juice to make a rough paste. Pat the fish fillets dry with some kitchen paper, then press a quarter of the crust onto each fillet. Lift onto an oiled baking tray, then roast for 12-15 mins until cooked through.','10 mins','15 mins'),
(1543,'crisp-skin fish with asparagus','https://www.bbcgoodfood.com/recipes/crisp-skin-fish-asparagus','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4805_11-805d1d0.jpg','Jill Dupleix''s impressive yet easy salmon dish is perfect for a dinner party or keto-friendly midweek meal',5,4,1,3,15,'Snap the woody ends off the asparagus, and discard. Cut the tomatoes in half, spoon out and discard the seeds. Finely slice the tomato flesh, then cut into small dice. Set both asparagus and tomatoes aside. Heat the 1 tbsp olive oil in a large frying pan. Season the fish with salt and pepper, and cook skin-side down over medium heat for about 3-4 minutes, gently pressing, until skin is crisped and golden. Turn and cook the other side for 3-4 minutes, depending on the thickness, until cooked. Meanwhile, cook the asparagus in simmering salted water for 3-4 minutes. Drain well. For the dressing, gently warm the 3 tbsp olive oil in a small saucepan with the tomatoes, olives, capers and parsley leaves. Toss the asparagus in a little of the dressing. Arrange the asparagus on each of four warmed dinner plates, top with the fish, and spoon the warm tomato dressing around the fish.','30 mins - 40 mins','N/A'),
(1544,'cauliflower rice','https://www.bbcgoodfood.com/recipes/cauliflower-rice','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cauliflower-rice-eb31363.jpg','Pulse cauliflower in a food processor to make cauliflower couscous or an easy rice-like side dish that''s much lower GI and ready in just 10 minutes',4,4,1,3,15,'Cut the hard core and stalks from the cauliflower and pulse the rest in a food processor to make grains the size of rice. Tip into a heatproof bowl, cover with cling film, then pierce and microwave for 7 mins on high – there is no need to add any water. Stir in the coriander. For spicier rice, add some toasted cumin seeds.','3 mins','7 mins'),
(1545,'seared steak with chimichurri dressing','https://www.bbcgoodfood.com/recipes/seared-steak-chimichurri-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-329465_10-8971934.jpg','The perfect sunshine food for outdoor eating, and you won''t have to spend hours trapped in the kitchen',17,2,1,3,15,'To make the chimichurri dressing, toast the seeds in a small non-stick pan for 30 seconds or so, until they smell fragrant. Tip into a small bowl and stir in the chopped parsley, chilli, thyme, oil and vinegar. Season well with pepper – you can add salt just before serving. Put the steak onto a plate and rub over about a quarter of the dressing. Leave the steak marinating at room temp for about 30 mins. If you’re making ahead chill for up to 2 hrs, then bring out of the fridge about 15 mins before you want to cook it. Heat a barbecue, griddle or heavybased frying pan until really hot. Season the steak and dressing with salt, then cook the steak for 2-3 mins on each side depending on the thickness and how you like you meat cooked. Serve on a board, then slice into thin strips as you eat – spooning over the remaining dressing.','15 mins','8 mins'),
(1546,'summer courgetti & meatballs','https://www.bbcgoodfood.com/recipes/summer-courgetti-meatballs','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/summer-courgetti-meatballs-8052abe.jpg','Use a spiralizer or julienne peeler to make spaghetti-like ''noodles'', then top with pork meatballs and a garlicky, creamy sauce',6,4,1,3,15,'Heat oven to 200C/180C fan/gas 6. Put the mince in a bowl, season well and add the crushed garlic. Mix together with your hands, then shape into small meatballs – roughly the size of a cherry tomato. Heat 1 tbsp of the oil in a large frying pan, add the meatballs and fry for 10-15 mins until golden brown. Meanwhile, tip the tomatoes into a roasting tin with the whole garlic cloves and the remaining oil. Season and roll around the tin until well coated in oil, then roast for 15 mins. While the meatballs and tomatoes cook, use a spiralizer – I used the finer noodle attachment – or a julienne peeler to create courgette noodles. Once cooked, tip the meatballs into the roasting tin with the tomatoes, fish out the garlic and set aside, then cover the tin with foil to keep warm. Wash the frying pan. Heat another 1 tbsp oil in the pan, squeeze the garlic cloves from their skins into the pan and mash with a fork. When sizzling, add the crème fraîche, lemon zest and some seasoning. Add the courgetti and toss in the pan for 30 secs until warmed through – any longer and it will wilt. Remove the pan from the heat, and tip in the meatballs, tomatoes and any juices from the tin. Toss together and scatter with pine nuts, basil and Parmesan, if you like.','15 mins','25 mins'),
(1547,'steak & aubergine salad','https://www.bbcgoodfood.com/recipes/steak-aubergine-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/steak-aubergine-salad-0c695fb.jpg','Rustle up this protein-packed salad in just 25 minutes. Made with sirloin steak, fried aubergine and feta in a zesty dressing, it makes a great summer dish',41,2,1,3,15,'Dust the aubergine slices in the flour, mixed with some seasoning. Heat 2 tbsp of the olive oil in a large frying pan until shimmering. Add the aubergine to the pan and fry for a few mins on each side until collapsing and soft, then set aside. Turn up the heat, season the steak, then add to the pan and fry for 2 mins on each side for rare (longer if you want it medium). Set aside to rest for 5 mins, then slice. Blitz the remaining olive oil in a blender or small food processor with the rest of the dressing ingredients and a splash of water. When ready to serve, mix half the dressing with the lamb’s lettuce, then tip onto a plate. Top with the crispy aubergine and steak, crumble over the feta, then drizzle over the remaining dressing. Scatter over the remaining herbs and serve.','10 mins','15 mins'),
(1548,'courgette frittatas','https://www.bbcgoodfood.com/recipes/courgette-frittatas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/courgette-frittatas-ce0f70a.jpg','If you''re looking for lunchbox inspiration, try making these vegetarian courgette frittatas. Serve with salad for an easy, filling and tasty lunch',29,2,1,3,15,'Heat oven to 220C/200C fan/gas 7, then put an empty muffin tin inside. Coarsely grate 1 large or 2 small courgettes and slice 4 spring onions. Heat 2 tsp rapeseed oil in a frying pan and fry the spring onions for about 3 mins. Stir in 1 crushed garlic clove and the grated courgette and cook for another 1 min, then set aside to cool. Beat 3 large eggs, a good pinch dill fronds and 4 tbsp Greek yogurt together in a jug, then season. Stir in the courgette mixture. Take the muffin tin out of the oven and drop in eight muffin cases, divide the egg mixture between the cases and bake for 15-18 mins until set and golden. Serve hot or cold with salad.','20 mins','25 mins'),
(1549,'baked salmon','https://www.bbcgoodfood.com/recipes/baked-salmon','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/salmon_0-ad51cb9.jpg','Get all the goodness of omega-3 with easy baked salmon for lunch or supper. It''s super versatile and makes a great partner for salads, veggies and pasta ',19,4,1,3,15,'Heat the oven to 180C/160C fan/gas 4. Brush each salmon fillet with the oil or butter and season well. Put the salmon fillets in an ovenproof dish. Cover if you prefer your salmon to be tender, or leave uncovered if you want the flesh to roast slightly. Roast for 10-15 mins (or about 4 mins per 1cm thickness) until just opaque and easily flaked with a fork. Serve with a sprinkling of chopped herbs, lemon slices and steamed long-stem broccoli, if you like.','5 mins','15 mins'),
(1550,'garden salmon salad','https://www.bbcgoodfood.com/recipes/garden-salmon-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/garden-salmon-salad-77361b0.jpg','Choose a lighter lunch with this easy, no-cook salmon salad packed with courgettes, peas, pea shoots and radishes. It''s healthy and takes just 15 minutes to prepare',14,4,1,3,15,'Cut the courgettes into long thin strips using a peeler, and discard the soft, seeded core. Toss the courgette ribbons, peas and radishes together in a large bowl. Whisk the oil, lemon zest and juice, and yogurt together, then toss with the veg. Put the pea shoots, dressed veg and large flakes of salmon on a large platter. Finish with a good grinding of black pepper, and scatter over the mixed seeds and dill to serve.','15 mins','No Time'),
(1551,'seared tuna & anchovy runner beans','https://www.bbcgoodfood.com/recipes/seared-tuna-anchovy-runner-beans','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tuna_1-9606c28.jpg','The perfect lunch or light dinner, this healthy seared tuna and runner bean dish packs in nutrients and tastes great',4,2,1,3,15,'Put the chives, basil and mint in a small food processor with the lemon zest and juice, oil and mustard. Blitz to a purée. Take out 2 tbsp to use as a coating for the fish, then stir the capers and anchovies into the rest. Cook the runner beans in boiling salted water or steam for 5 mins until tender but still with a little bite. Brush the tuna with the reserved herby mixture and griddle for 2-3 mins each side until cooked but still a little pink in the centre. Toss half the caper dressing through the warm beans with the parsley and pile onto plates. Top with the tuna, spoon over the remaining dressing and scatter with the almonds and mint leaves.','5 mins','10 mins'),
(1552,'gluten-free banana pancakes','https://www.bbcgoodfood.com/recipes/banana-pancakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/banana-pancakes-6cfd286.jpg','Gluten-free banana pancakes you can whip up in just 10 minutes! Scatter with pecans and raspberries to enjoy a low-calorie (but still indulgent) breakfast',16,2,1,3,16,'In a bowl, mash 1 large banana with a fork until it resembles a thick purée. Stir in 2 beaten eggs, a pinch of baking powder (gluten-free if coeliac) and a splash of vanilla extract. Heat a large non-stick frying pan or pancake pan over a medium heat and brush with ½ tsp oil. Using half the batter, spoon two pancakes into the pan, cook for 1-2 mins each side, then tip onto a plate. Repeat the process with another ½ tsp oil and the remaining batter. Top the pancakes with 25g roughly chopped pecans and 125g raspberries.','5 mins','5 mins'),
(1553,'kale, tomato & poached egg on toast','https://www.bbcgoodfood.com/recipes/kale-tomato-poached-egg-toast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/kale-tomato-poached-egg-on-toast-a919665.jpg','There''s no tastier way to eat kale than smothered in a runny yolk and scattered with feta cheese. This veggie, low-calorie breakfast has it all!',16,2,1,3,16,'Bring a large pan of water to the boil. Heat the oil in a frying pan over a medium heat and add the kale, garlic and chilli flakes. Cook, stirring occasionally, for 4 mins until the kale begins to crisp and wilt to half its size. Set aside. Adjust the heat so the water is at a rolling boil, then poach your eggs for 2 mins. Meanwhile, toast the bread. Remove the poached eggs with a slotted spoon and top each piece of toast with half the kale, an egg, the cherry tomatoes and feta. ','2 mins','7 mins'),
(1554,'eggy spelt bread with orange cheese & raspberries','https://www.bbcgoodfood.com/recipes/eggy-spelt-bread-orange-cheese-raspberries','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/eggy-bread-d8664d4.jpg','Try French toast with a twist. Use spelt or wholemeal bread and top with light, citrus cottage cheese and fresh fruit',45,2,1,3,16,'Beat the eggs and orange juice in a bowl wide enough to fit the bread in it. Soak the bread in the eggs and juice for 2 mins or so, turning halfway through. Meanwhile, in a small bowl, mix together the cheese and orange zest. Put the rapeseed oil in a non-stick frying pan over a high heat. When hot, add the eggy bread. Leave to cook for a couple of mins undisturbed, then flip and cook on the other side for another 1-2 mins. Divide the bread between 2 plates, dollop the cheese on top, followed by the raspberries and honey, if you like.','5 mins','5 mins'),
(1555,'creamy mushrooms on toast','https://www.bbcgoodfood.com/recipes/creamy-mustard-mushrooms-toast','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mushrooms-0986a4e.png','Make these creamy mustard mushrooms on toast for a quick and healthy vegetarian breakfast, served with a light cream cheese sauce and a sprinkling of chives',4,1,1,3,16,'Toast the bread, then spread with a little of the cream cheese. Meanwhile, heat the oil in a non-stick pan and cook the mushrooms, stirring frequently, until softened. Spoon in the milk, remaining cheese and the mustard. Stir well until coated. Tip onto the toast and top with chives.','5 mins','5 mins'),
(1556,'veggie breakfast bakes','https://www.bbcgoodfood.com/recipes/veggie-breakfast-bakes','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201575_11-aa3f8c6.jpg','Hit 3 of your 5-a-day with this alternative fry-up - it''s packed with vegetables and oven-baked',5,4,1,3,16,'Heat oven to 200C/180C fan/gas 6. Put the mushrooms and tomatoes into 4 ovenproof dishes. Divide garlic between the dishes, drizzle over the oil and some seasoning, then bake for 10 mins. Meanwhile, put the spinach into a large colander, then pour over a kettle of boiling water to wilt it. Squeeze out any excess water, then add the spinach to the dishes. Make a little gap between the vegetables and crack an egg into each dish. Return to the oven and cook for a further 8-10 mins or until the egg is cooked to your liking.','15 mins','30 mins'),
(1557,'spiced fruit loaf','https://www.bbcgoodfood.com/recipes/spiced-fruit-loaf','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-219613_12-2bb8518.jpg','A treat when freshly baked and spread with butter, then try it toasted for breakfast the next day',50,2,2,3,16,'Soak the dried fruits in the orange juice for about 30 mins, then sieve, reserving the juice. Put the flour, yeast, caster sugar and 1 tsp salt into a large mixing bowl with the spices and soaked fruit and mix well. Make a well in the centre and pour in the warm milk, reserved orange juice, the beaten egg and the melted butter. Mix everything together to form a dough – start with a wooden spoon and finish with your hands. If the dough is too dry, add a little more warm water; if it’s too wet, add more flour. Knead in the bowl or on a floured surface until the dough becomes smooth and springy. Transfer to a clean, lightly greased bowl and cover loosely with a clean, damp tea towel. Leave in a warm place to rise until roughly doubled in size – this will take about 1 hr depending on how warm the room is. Knock the dough back by kneading for a few secs. Dust 2 x 2lb loaf tins with flour. Halve the dough. Use a little flour to help you shape each half into a smooth oval, then pop them into the tins. Cover both loosely with a clean, damp tea towel and leave to prove in a warm place for about 20 mins. Meanwhile, heat oven to 180C/160C fan/gas 4. Bake for 20 mins, then cool in the tins before turning out and slicing.','30 mins','20 mins'),
(1558,'asparagus soldiers with a soft-boiled egg','https://www.bbcgoodfood.com/recipes/asparagus-soldiers-soft-boiled-egg','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-25323_11-96436a4.jpg','A classic combination, seasoned with chilli and paprika',43,4,1,3,16,'Heat the oil in a pan, add the breadcrumbs, then fry until crisp and golden. Season with the spices and flaky sea salt, then leave to cool. Cook the asparagus in a large pan of boiling salted water for 3-5 mins until tender. At the same time, boil the eggs for 3-4 mins. Put each egg in an egg cup on a plate. Drain the asparagus and divide between plates. Scatter over the crumbs and serve.','10 mins','10 mins'),
(1559,'honey nut crunch pears','https://www.bbcgoodfood.com/recipes/honey-nut-crunch-pears','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-5252_11-58d05ea.jpg','This simple idea makes a quick mid-week treat for all the family',5,4,1,3,16,'Heat oven to 200C/fan 180/gas 6. Cut the pears in half lengthways, take out the core (see tip, left), then top with a small knob of butter and a sprinkling of the mixed spice. Sit the pears in a shallow baking dish, then roast for 5 mins until starting to soften. Meanwhile, heat the honey and another knob of butter in a large bowl in the microwave for 30 secs. Toss with the cornflakes and nuts. Take the pears out of the oven, then top with the cornflake mix. Cook for another 5 mins or until the cornflakes take on a rich golden colour. Allow to cool for a few mins (the cornflakes crisp up again as they cool), then serve warm with ice cream.','5 mins','10 mins'),
(1560,'hash browns with mustard & smoked salmon','https://www.bbcgoodfood.com/recipes/hash-browns-mustard-smoked-salmon','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-18398_12-1f1d90f.jpg','This brilliant brunch dish is especially sensational served with poached eggs',5,4,1,3,16,'Grate the unpeeled potato onto a clean tea towel. Bring up the edges of the towel, then squeeze over the sink to remove any excess water in the potatoes. Tip into a bowl and add the flour and mustard or horseradish. Season well and mix together. Divide the mixture into 8 balls and flatten between your hands. Heat a large frying pan with the butter and oil, then add the potatoes to the pan. Cook for 2-3 mins on each side, over a medium heat, until golden. Stack a couple of hash browns on each serving plate and top with a slice of smoked salmon, a dollop of soured cream or crème fraîche and some chives to serve.','10 mins','10 mins'),
(1561,'perfect porridge','https://www.bbcgoodfood.com/recipes/perfect-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-923_12-5ff2df7.jpg','Perfect porridge for a healthy way to start the day',144,1,1,3,16,'Put 50g porridge oats in a saucepan, pour in 350ml milk or water and sprinkle in a pinch of salt. Bring to the boil and simmer for 4-5 minutes, stirring from time to time and watching carefully that it doesn’t stick to the bottom of the pan. Or you can try this in a microwave. Mix the porridge oats, milk or water and a pinch of salt in a large microwaveproof bowl, then microwave on High for 5 minutes, stirring halfway through. Leave to stand for 2 minutes before eating. To serve, pour into bowls, spoon Greek yogurt, thinned with a little milk, on top and drizzle with honey.','5 mins','5 mins - 10 mins'),
(1562,'banana yogurt pots','https://www.bbcgoodfood.com/recipes/banana-yogurt-pots','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-2406_10-ad578af.jpg','Ideal as a pudding, or make it for breakfast',5,4,1,3,16,'Dollop about 1 tbsp yogurt into the bottom of 4 small glasses. Add a layer of banana, then some more yogurt. Repeat the layers until the glasses are full. Scatter over the sugar and nuts, then leave in fridge for 20 mins until sugar has dissolved.','5 mins','N/A'),
(1563,'baked eggs brunch','https://www.bbcgoodfood.com/recipes/baked-eggs-brunch','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/baked-eggs-ad986d4.jpg','This low-calorie vegetarian breakfast with sundried tomatoes, spinach, leeks and cheese is the perfect way to start a weekend',5,4,1,3,16,'Heat oven to 200C/180C fan/gas 6. Heat the oil in a pan and add the leeks, onions and seasoning. Cook for 15-20 mins until soft and beginning to caramelise. Meanwhile, put the spinach in a colander and pour over a kettle of boiling water. When cool enough to handle, squeeze out as much liquid as possible. Mix the breadcrumbs and cheese together. Arrange the leek and onion mixture between 4 ovenproof dishes, then scatter with the spinach and pieces of sundried tomato. Make a well in the middle of each dish and crack an egg in it. Season and sprinkle with cheese crumbs. Put the dishes on a baking tray and cook for 12-15 mins, until the whites are set and yolks are cooked to your liking.','10 mins','30 mins'),
(1564,'creamy yogurt porridge','https://www.bbcgoodfood.com/recipes/creamy-yogurt-porridge','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/creamy-porridge-5cd5565.jpg','This delicious oaty breakfast is low in fat and calories and will keep you full all morning. Serve with one of our suggested toppings',4,1,1,3,16,'Tip 200ml water into a small non-stick pan and stir in porridge oats. Cook over a low heat until bubbling and thickened. (To make in a microwave, use a deep container to prevent spillage as the mixture will rise up as it cooks, and cook for 3 mins on High.) Stir in yogurt – or swirl in half and top with the rest. Serve plain or with one of our toppings (see ''goes well with'').','1 min','3 mins'),
(1565,'blt pasta salad','https://www.bbcgoodfood.com/recipes/blt-pasta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blt-pasta-salad-814ac79.jpg','An easy pasta salad that makes a tasty addition to any lunchbox. Ideal for kids aged 9 - 12, it also makes up 2 of their 5-a-day',1,1,1,3,16,'The night before school, cook the pasta following pack instructions and run under cold water to cool quickly. Mix in the bacon, spinach, tomatoes, crème fraîche and mustard, and season with a little salt. Spoon into an airtight container and keep overnight in the fridge.','10 mins','10 mins'),
(1566,'healthy carrot soup','https://www.bbcgoodfood.com/recipes/carrot-ginger-immune-boosting-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-soup-71663f4.jpg','Give yourself a boost and treat yourself to this low-fat, healthy carrot soup with a swirl of soured cream',1,1,1,3,16,'Peel and chop the carrots and put in a blender with the ginger, turmeric, cayenne pepper, wholemeal bread, soured cream and vegetable stock. Blitz until smooth. Heat until piping hot. Swirl through some extra soured cream, or a sprinkling of cayenne, if you like.','5 mins','5 mins'),
(1567,'beetroot soup','https://www.bbcgoodfood.com/recipes/beetroot-onion-seed-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beetroot-onion-seed-soup-2431877.jpg','A deep red autumnal soup that''s low fat, vegetarian and full of flavour. Beetroot and apple give this soup a subtle sweet flavour, while lentils add protein and bulk',1,1,1,3,16,'Tip the beetroot, lentils, apple, garlic and onion seeds into a blender with the vegetable stock and some seasoning, and blitz until smooth. Heat until piping hot in the microwave or on the hob, then scatter over some extra onion seeds, if you like.','5 mins','5 mins'),
(1569,'griddled salad jar','https://www.bbcgoodfood.com/recipes/griddled-salad-jar','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/griddled-salad-jars-3965491.jpg','This jar salad trend is a keeper! The dressing stays at the bottom of the container, and the leafy greens remain fresh and crisp until you''re ready to serve',5,6,1,3,16,'To make the dressing, put the onion in a medium-sized pan and heat gently with the coriander seeds, vinegar and 50ml water for 15 mins or until the onion is bright pink and soft. Put the pan to one side to cool. Heat a griddle pan until smoking hot, then brush the aubergines and courgette slices with a little olive oil. Griddle for 3-4 mins each side until char lines appear. Transfer to a plate, season well and leave to cool. Whisk the lemon juice then the olive oil into the pan with the cooled onion. Season and tip into the jar. To build your salad, layer in the tomatoes followed by the feta, chickpeas and olives. Push everything down a little, then add the sundried tomatoes and some of their oil. Add your griddled vegetables, followed by the cucumber, pepper and herbs. Put on the lid and leave in the fridge or a cool box until you’re ready to eat. To serve, tip out onto a serving platter so the dressing is drizzled over the top of everything last.','30 mins','30 mins'),
(1570,'egg niçoise salad','https://www.bbcgoodfood.com/recipes/egg-nicoise-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/nicoise-egg-salad-8b5171c.jpg','A vegetarian Niçoise salad, that''s packed with goodness - fibre, folate, iron, vitamin c and gluten-free too',5,2,1,3,16,'Mix the dressing ingredients together in a small bowl with 1 tbsp water.  Meanwhile boil the potatoes for 7 mins, add the beans and boil 5 mins more or until both are just tender, then drain. Boil 2 eggs for 8 minutes then shell and halve. Toss the beans, potatoes and remaining salad ingredients, except the eggs, together in a large bowl with half the dressing. Arrange the eggs on top and drizzle over the remaining dressing. ','10 mins','10 mins'),
(1571,'easy prawn & quinoa salad','https://www.bbcgoodfood.com/recipes/asian-prawn-quinoa-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/asian-prawn-quinoa-salad-4609845.jpg','This fresh salad is 3 of your 5-a-day. With zingy lime and a hit of chilli heat, it''s also a great introduction to quinoa if you haven''t tried it before',4,2,1,3,16,'Boil the quinoa in a small pan for 15 mins until the grains are tender and look like they have burst. Drain well and tip into a bowl. Meanwhile, make the dressing: mix the lime zest and juice and the chilli in a bowl. Stir half the dressing into the quinoa with the spring onions, tamari and half the coriander. Stir in all the salad vegetables, then spoon onto two serving plates. Stir the oil and maple syrup into the remaining dressing and toss in the prawns. Spoon onto the quinoa salad and scatter over the coriander to serve.','15 mins','15 mins'),
(1572,'red lentil & sweet potato pâté','https://www.bbcgoodfood.com/recipes/red-lentil-sweet-potato-pate','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-871560_11-7d9ee9f.jpg','This easy vegetarian dip is a healthy alternative to hummus and great with crudités in a lunchbox or served as a vegan pate as part of a snack selection.',5,4,1,3,16,'Heat the oil in a large pan, add the onion and cook slowly until soft and golden. Tip in the paprika and cook for a further 2 mins, then add the sweet potato, lentils, thyme and stock. Bring to a simmer, then cook for 20 mins or until the potato and lentils are tender. Add the vinegar and some seasoning, and roughly mash the mixture until you get a texture you like. Chill for 1 hr, then drizzle with olive oil, dust with the extra paprika and sprinkle with thyme sprigs, if you like. Serve with pitta bread and vegetable sticks.','30 mins','No Time'),
(1573,'turkey & spring onion wraps','https://www.bbcgoodfood.com/recipes/turkey-spring-onion-wraps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-837835_11-700716f.jpg','Give yourself a low-fat treat with these super-quick sandwich tortillas - a fab way of using up leftover turkey',73,4,1,3,16,'Mix together the mayonnaise and pesto. Divide the lettuce leaves, turkey, spring onions and cucumber between the tortillas. Drizzle over the pesto dressing, roll up and eat.','5 mins','No Time'),
(1574,'carrot & hummus roll-ups','https://www.bbcgoodfood.com/recipes/carrot-hummus-roll-ups','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-658452_11-9c5af95.jpg','Roll-up, roll-up - snaffle something superhealthy in a flash with these vegetarian wraps',5,4,1,3,16,'Spread the hummus between wraps. Coarsely grate carrots and scatter on top of the hummus, finishing each wrap with a small handful of rocket leaves and some seasoning. Roll up and eat.','10 mins','N/A'),
(1575,'chicken, sweetcorn & noodle soup','https://www.bbcgoodfood.com/recipes/chicken-sweetcorn-noodle-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-656488_11-fb91fe8.jpg','This superhealthy bowl can be made ahead and frozen, ready to be defrosted and used when you need it most',5,8,1,3,16,'Put all the stock ingredients and the chicken in a very large saucepan, then cover everything with about 3 litres cold water. Bring to the boil, then lower to a simmer and cook for 1 hr-1½ hrs, until the chicken is cooked through. Skim off any froth every 20 mins or so. Remove the chicken to a plate to cool. Strain the stock through a sieve, skimming off as much fat as you can. Rinse out the pan and put the stock back in, then simmer on a high heat until reduced a little – you need about 2 litres in total. Add the carrots and leeks, then simmer for 10 mins. Meanwhile, shred the meat from the chicken, discarding the skin and bones. Add to the pan with the sweetcorn. Add the vermicelli noodles, unless you want to freeze the soup, and simmer for about 7 mins more, until the corn and pasta is cooked. Ladle into bowls, sprinkled with the parsley. To freeze, allow the soup to cool completely before freezing (see tip), and when you’re ready to eat, allow the soup to defrost before bringing it back to a gentle simmer in a pan. Add the noodles and simmer until cooked.','15 mins','1 hr and 30 mins'),
(1576,'prawn sweet chilli noodle salad','https://www.bbcgoodfood.com/recipes/prawn-sweet-chilli-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488629_11-4f1993b.jpg','This low-fat salad is is great for a picnic, lunchbox or even a buffet',5,4,1,3,16,'Boil the noodles for 4 mins, then drain. Cool under running water, then drain again. Put into a large bowl, then using scissors, cut into shorter lengths. Halve cucumber lengthways, then scoop out the seeds. Slice into halfmoons and add to the noodles with the onions, tomatoes, chilli and prawns. Mix the lime zest, juice and chilli sauce to make a dressing and fold through noodles. Put a handful of spinach onto each serving plate, top with the noodles and cashews.','10 mins','5 mins'),
(1577,'hot-smoked salmon, lentil & pomegranate salad','https://www.bbcgoodfood.com/recipes/hot-smoked-salmon-lentil-pomegranate-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-217517_11-a3e6a8b.jpg','No-cook, no-effort, but you won''t miss out on flavour with this vibrant supper for two',5,2,1,3,16,'Combine the oil, lemon juice, garlic, tarragon and honey in a large bowl and season. Toss in the red onion and lentils and set aside to marinate for 10 mins. Break the salmon into large flakes and fold into the salad with the parsley and pomegranate. Serve with toasted pitta.','15 mins','No Time'),
(1578,'poached salmon with tarragon','https://www.bbcgoodfood.com/recipes/poached-salmon-tarragon-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/poached-salmon-with-tarragon_0-514bf80.jpg','Lightly poach salmon fillets and serve with creamy green beans and baby new potatoes for a light meal',24,4,1,3,16,'Zest the lemon and cut into wedges. Mix together the crème fraîche, tarragon, garlic, lemon zest and a squeeze of juice, season to taste and set aside. Bring a large shallow pan of salted water to the bowl and cook the green beans for 3 mins until just done. Remove with a slotted spoon, drain and cool under cold running water before tossing with the crème fraîche mixture. Bring the pan back to the boil, then turn the heat down to a very gentle simmer and slide in the salmon pieces. Poach for 8-10 mins or until cooked to your liking. Remove from the pan and put on kitchen paper before plating up. Serve with the green beans and baby new potatoes.','10 mins','10 mins - 15 mins'),
(1579,'fajita chicken one-pot','https://www.bbcgoodfood.com/recipes/fajita-chicken-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/Fajita-chicken-one-pot-90f0938.jpg','Make the most of a classic combination of chicken and chorizo in this easy one-pot. Serve in large bowls with soured cream and a scattering of parsley.',2,4,1,3,16,'Heat the oil in a large, lidded, heavy-based saucepan over a medium heat and fry the chorizo for 4 mins to release the oils and brown it a little. Remove to a plate using a slotted spoon and set aside. Fry the chicken for 5-6 mins until browned but not cooked all the way through. You may need to do this in batches. Remove with the slotted spoon and set aside with the chorizo. Add the onions and peppers to the pan and cook, stirring often for 6-8 mins until softened and starting to lightly brown. Use a wooden spoon to scrape up any caramelised bits on the bottom and mix them in. Stir in the fajita seasoning and cook for 30 seconds before returning the chorizo and chicken to the pan. Season with salt and freshly ground black pepper and give everything a good stir. Tip in the pinto beans and potatoes, stir to coat well, then pour in the stock, topping up with water if it doesn’t cover the chicken and veg. Bring to a simmer, reduce the heat a little and put the lid on. Simmer gently for 10 mins. Give everything a stir, then sit the corn on the cobs on top. Return the lid to the pan and cook for a further 15 mins. Serve in large bowls, each topped with a spoonful of soured cream and a scattering of chopped parsley, if you like.','15 mins','45 mins'),
(1580,'sweet potato wedges with mole sauce','https://www.bbcgoodfood.com/recipes/sweet-potato-wedges-with-mole-sauce','https://images.immediate.co.uk/production/volatile/sites/30/2022/04/2022-03-30GFO-0622-SummerHDP20-SweetPotatoWedgesWithMoleSauce02326-ff1daf0.jpg','Courgettes and sweet potatoes are spiced with habanero chilli flakes in this vibrant, healthy dish, forming part of our vegan Healthy Diet Plan',4,4,1,3,16,'Toss the courgettes and sweet potatoes in 1 tsp olive oil, then tip onto a baking sheet lined with baking paper and bake at 200C/180C fan/gas 6 for 25 mins until the vegetables are tender and slightly charred. Boil the kettle. Tip the red onion slices into a heatproof bowl, cover with boiling water from the kettle, then drain. Return to the bowl and mix with the lime juice. Set aside until needed. Heat the remaining oil in a frying pan over a medium heat. Add the onion and thyme, cover and cook for 5-10 mins until the onion has started to soften. Tip in the tomatoes and a can of water, then add the bouillon powder, peanut butter, raisins, smoked paprika and chilli flakes, if using. Drop in the cinnamon stick, then cover and simmer for 20 mins. Meanwhile, tip the black beans into a small pan with the liquid from the can, the cumin and garlic. Simmer over a low heat for 5-10 mins, then roughly mash with a fork. Spoon half the sauce over two plates and top with half the roasted veg. Spoon over the beans and pickled red onions, then scatter with coriander before serving. Leftovers should be left to cool completely then keep chilled for up to two days. Reheat the sauce in a pan with a splash of water and microwave the beans and vegetables until piping hot.','25 mins','1 hr and 5 mins'),
(1581,'kitchari','https://www.bbcgoodfood.com/recipes/kitchari','https://images.immediate.co.uk/production/volatile/sites/30/2023/01/Kitchari-Dahl-7036c17.jpg','Make a batch of kitchari, a soothing, dhal-like dish linked to Ayurvedic cleanses (an ancient system of alternative medicine from India) for a healthy supper.',15,4,1,3,16,'Melt the ghee in a large flameproof casserole or saucepan over a medium heat. Stir in all the cauliflower and carrots, and season lightly. Fry gently for 10 mins until the vegetables have softened and taken on a bit of colour. Tip in all the spices and fry for a further 2 mins until fragrant. Pour in the moong dal and rice, and stir to coat in the spices. Season with salt and pour in 1.25 litres water. Bring to a simmer and cook for 45 mins, stirring occasionally until the beans and rice are fully tender and have broken down. The texture should be porridge-like. Season to taste and sprinkle over the coriander. Serve with the lime wedges on the side for squeezing over.','15 mins','50 mins'),
(1582,'spicy baby aubergine stew with coriander & mint','https://www.bbcgoodfood.com/recipes/spicy-baby-aubergine-stew-coriander-mint','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-606549_11-e4d52ca.jpg','Superhealthy and high in fibre too, this miraculous Middle Eastern one-pot is under 150 calories per serve and counts as 5 of your 5-a-day',5,4,1,3,16,'Heat oil in a heavy-based saucepan, add the onions and garlic and cook until they begin to colour. Add the chillies, coriander and cumin seeds. When the seeds give off a nutty aroma, toss in the whole aubergines, coating them in the onion and spices. Tip in tomatoes and sugar, cover and gently cook for 40 mins, until aubergines are tender. Season sauce and toss in half the mint and coriander. Cover and simmer for 2 mins. Sprinkle over remaining herbs and serve with couscous and yogurt.','10 mins','45 mins'),
(1583,'miso steak with noodle salad','https://www.bbcgoodfood.com/recipes/miso-steak-with-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/05/Miso-steak-with-noodle-salad-a841624.jpg','Looking for a quick, nutritious dish for busy days? Try our speedy, easy miso steak with a noodle and veg salad – it''s healthy, low in calories and tastes great',4,4,1,3,16,'For the salad, simmer the broccoli in a large pan of boiling water for 3-4 mins. Meanwhile, cook the noodles following pack instructions, then drain both and set aside. For the dressing, whisk the miso paste in a large bowl with the lemon juice, vinegar, ginger and oil. Toss with the cooked broccoli, noodles, red onion, carrots, tomatoes and sesame seeds. For the steak, heat a wok or frying pan over a medium heat and briefly stir-fry the garlic and ginger until they begin to colour. Add the steak and stir-fry for a few minutes until brown. Pile half on top of the noodle salad. Add the miso to the wok with 2 tbsp water to lift the ginger and garlic from the pan, then spoon over the meat. Serve half and chill the rest for another day. You can enjoy the leftovers chilled or reheat in the microwave or oven until warmed through. Will keep chilled for two days.','10 mins','10 mins'),
(1584,'mediterranean vegetables with lamb','https://www.bbcgoodfood.com/recipes/mediterranean-vegetables-lamb','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-589473_11-8f24647.jpg','A one-pot packed with veg and tender lamb that will keep the whole family satisfied',5,4,1,3,16,'Heat the oil in a large, heavy-based frying pan. Cook the lamb and shallots over a high heat for 2-3 mins until golden. Add the courgettes and stir-fry for 3-4 mins until beginning to soften. Add the spices and toss well, then add the peppers and garlic. Reduce the heat and cook over a moderate heat for 4-5 mins until they start to soften. Pour in the stock and stir to coat. Add the tomatoes, season, then cover with a lid and simmer for 15 mins, stirring occasionally until the veg are tender. Stir through the coriander to serve.','15 mins','30 mins'),
(1585,'steamed fish & pak choi parcels','https://www.bbcgoodfood.com/recipes/steamed-fish-pak-choi-parcels','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-546476_11-4d36dee.jpg','Just add steamed rice for a flavour-packed, low-calorie meal',73,4,1,3,16,'Heat oven to 200C/180C fan/gas 6. Place each fillet in the centre of a large square of foil. Top with the pak choi, spring onions, chilli and ginger, then pull up the edges of the foil. Mix together the soy sauce, lime juice and 1 tbsp of water then spoon a little over each fillet. Crimp the top of the foil to enclose the fish and make sure there are no gaps for the steam to escape. Place the parcels on a baking sheet and bake for 10-15 mins until the fish is cooked through (this will depend on the thickness of your fish). Open up the parcels and drizzle over a few drops of sesame oil. Serve with rice.','10 mins','15 mins'),
(1586,'pork & bulgur-stuffed peppers','https://www.bbcgoodfood.com/recipes/pork-bulgur-stuffed-peppers','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-191524_10-909fbcc.jpg','This colourful feel-good supper brings summer flavour at any time of year',5,4,1,3,16,'Put the peppers, cut-side down, on a plate and microwave on High for 4 mins until cooked through (but not so soft they collapse). If they need longer, microwave for 1 min more and repeat until done. Put the pork in a cold frying pan and turn on the heat. Fry, breaking up any lumps, until it starts to brown. Stir in the garlic and spices for 1 min, then add the bulgur and stock. Cover and simmer for 10 mins until the bulgur is soft. Heat the grill. Stir half the parsley into the bulgur, then stuff into the peppers on a baking tray. Grill to crisp, sprinkle over most of the parsley, then serve with the yogurt mixed with remaining parsley.','10 mins','30 mins'),
(1587,'mini chicken fajitas','https://www.bbcgoodfood.com/recipes/mini-chicken-fajitas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mini-chicken-fajitas-9a29b14.jpg','These Mexican inspired party nibbles are filled with spicy chipotle chicken and peppers - make ahead for fuss-free entertaining',48,36,1,3,16,'Heat the oil in a large, non-stick frying pan or wok and stir-fry the chicken and peppers for 2 mins. Sprinkle over the spices and cook for 30 secs more, stirring. Tip the tomatoes into the pan and stir in the chipotle paste. Simmer for 5 mins or until the sauce is thick and glossy, stirring regularly. Stir in the spring onions and coriander, cook for 1 min more, then remove from the heat and leave to cool. Cut the tortillas into quarters, then cut each quarter into three pieces, so you have 36 triangles. Place 1 tsp of the chicken mixture and a little cheese at the widest end of a triangle. Roll the pointed end of the triangle around the filling and secure with a cocktail stick. Place on a baking tray lined with baking parchment. Fill and roll the other triangles. Cover with cling film and chill for up to 8 hrs until ready to reheat. To make the dip, mash all the ingredients in a bowl until almost smooth. Cover the surface of the dip with cling film and chill for up to 24 hrs. When ready to serve, heat oven to 200C/180C fan/gas 6. Uncover the fajitas and bake for 6-8 mins or until hot throughout. Serve with the guacamole.','25 mins','16 mins - 18 mins'),
(1588,'watermelon salsa','https://www.bbcgoodfood.com/recipes/watermelon-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gfmelons_nachos-378a234.jpg','Serve this zingy, refreshing watermelon salsa as a dip or pile on top of cheesy nachos. Deliciously moreish, it makes a lovely, light summer snack',29,4,1,3,16,'Finely chop 200g watermelon, 2 small shallots and a small bunch coriander. Mix together with juice ½ lime and 2 tbsp olive oil. Season and serve as a dip or pile on top of cheesy nachos.','10 mins','No Time'),
(1589,'cheat''s duck, hoisin and cucumber lettuce cups','https://www.bbcgoodfood.com/recipes/cheats-duck-hoisin-and-cucumber-lettuce-cups','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cheats-hoisin-duck-wrap-2f002d7.jpg','A quick and easy, low-fat alternative to duck pancakes. Spiralized cucumber, sesame seeds and hoisin sauce make these a simple and delicious party snack',16,12,1,3,16,'On the chopping board you used to slice the duck, toss the duck strips in the five spice and a generous pinch of flaky sea salt and black pepper. Heat a wok or non-stick, large, high-sided frying pan over a high heat. Once really hot add the oil along with the seasoned strips of duck breast and stir-fry for 2 mins. Reduce the heat slightly and spoon in the hoisin sauce, toss everything together and stir-fry for another min until the duck is just cooked then remove from the heat. Divide the spring onions, spiralized cucumber and hoisin duck between the lettuce leaves then top with a sprinkling of sesame seeds to serve.','20 mins','5 mins'),
(1590,'smoky spanish tomato and garlic dip','https://www.bbcgoodfood.com/recipes/smoky-spanish-tomato-garlic-dip','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tomato-dip-54d0eff.jpg','A Spanish tomato dip with smoky paprika and a drizzle of sherry vinegar. A great vegan addition to the picnic platter',5,8,1,3,16,'Griddle 2 slices of rustic bread for about 2 mins each side until lightly charred. Tear up and put in a blender. Chop 400g very ripe tomatoes and add to the blender with 1 sliced garlic clove, 1-2 tbsp sherry vinegar, 50ml extra virgin olive oil and a pinch of smoked Spanish paprika. Blend until smooth and serve with breadsticks.','5 mins','5 mins'),
(1591,'aubergine & chickpea bites','https://www.bbcgoodfood.com/recipes/aubergine-chickpea-bites','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bites-4911a55.jpg','Combine chickpeas and aubergine with garlic and cumin for these tasty, healthy vegan canapés. Everyone will love them served with our harissa yogurt',16,20,1,3,16,'Heat oven to 200C/180C fan/gas 6. Spray the aubergine halves generously with oil, then put them cut-side up in a large roasting tin with the garlic, coriander and cumin seeds. Season, then roast for 40 mins until the aubergine is completely tender. Set aside to cool a little. Scoop the aubergine flesh into a bowl and discard the skins. Use a spatula to scrape the spices and garlic into the bowl. Add the chickpeas, gram flour, lemon zest and juice, roughly mash together and check the seasoning. Don’t worry if the mix is a bit soft – it will firm up in the fridge. Shape the mixture into 20 balls and put them on a baking tray lined with baking parchment, then leave to chill in the fridge for at least 30 mins. Swirl the harissa through the yogurt and set aside. Can make ahead to this point the day before and kept covered in the fridge. Heat oven to 180C/160C fan/gas 4. Tip the polenta onto a plate, roll the balls in it to coat, then return them to the tray and spray each one with a little oil. Roast for 20 mins until crisp, hot and golden. Serve with the harissa yogurt and lemon wedges, if you like.','20 mins','1 hr'),
(1592,'smoky chipotle pepper salsa','https://www.bbcgoodfood.com/recipes/smoky-chipotle-pepper-salsa','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-411456_11-12da729.jpg','Take a little pot of salsa in your lunchbox to enjoy with vegetable crudités, tortilla chips or pitta bread',5,6,1,3,16,'Heat grill to high. Put peppers onto a baking sheet, skin-side up, grill until skins are black, then put into a bowl and cover. Leave until cool, then peel skins away. Scoop out and discard tomato seeds. Put tomatoes into a food processor with garlic, lime juice, oil and chipotle paste. Add peppers, then pulse until chunky. Stir onion and coriander through, then serve.','10 mins','12 mins'),
(1593,'beetroot & mint dip','https://www.bbcgoodfood.com/recipes/beetroot-mint-dip','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1309527_3-2bb57cf.jpg','A healthy and vibrant dip, flavoured with nigella seeds and cumin to serve with crudites or flatbreads - it works well in lunchboxes too',92,4,1,3,16,'Put beetroot and cumin in the small bowl of a food processor, season and blend until smooth. Tip into a bowl, add the mint and lemon juice, then gently stir through crème fraîche to get a rippled effect. Sprinkle with mint leaves and nigella seeds.','10 mins','No Time'),
(1594,'fresh orange lollies','https://www.bbcgoodfood.com/recipes/fresh-orange-lollies','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fresh-orange-lollies-copy-d0d907e.jpg','Use the whole orange for these lollies, rather than just the juice, and with the addition of fresh pear you get extra nutrients. A great healthy treat for summer',5,4,1,3,16,'Finely grate ¼ tsp zest from one of the oranges, then peel them, roughly chop the flesh and put in a bowl (or food processor) with the zest and pears. Blitz until as smooth as you can get it, then pour into lolly containers, poke in sticks and freeze until solid.','5 mins','No Time'),
(1595,'spiced apple crisps','https://www.bbcgoodfood.com/recipes/spiced-apple-crisps','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201591_11-95c8dc3.jpg','Keep hunger pangs at bay by snacking on healthy dried fruit - this version bakes Granny Smiths with cinnamon',145,1,1,3,16,'Heat the oven to 160C/ 140C fan/ gas mark 3. Core the apple and slice through the equator into very thin slices 1 - 2mm thick. Dust with cinnamon and lay flat on a baking sheet lined with parchment paper. Cook for 45 mins – 1 hour, turning halfway through and removing any crisps that have turned brown. Continue cooking until the apples have dried out and are light golden. Cool, store in an airtight container and enjoy as a snack.','5 mins','1 hr'),
(1596,'spicy chickpeas','https://www.bbcgoodfood.com/recipes/spicy-chickpeas','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1201551_11-5a5b24a.jpg','Get the whole family snacking on this low-fat alternative to peanuts',5,4,1,3,16,'Tip the chickpeas into a bowl with the vegetable oil and chilli powder and mix until the chickpeas are coated with chilli. Transfer to a baking sheet, spread out the chickpeas, then cook for 25 mins. Remove from the oven and allow to cool. Sprinkle with sea salt before serving.','5 mins','25 mins'),
(1597,'easy onion bhajis','https://www.bbcgoodfood.com/recipes/onion-bhajis-raita','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/onion-ea06386.jpg','Serve your crispy onion bhajis with cooling cucumber and mint raita as a snack, or eat alongside a homemade Indian curry. You''ll be surprised by how easy they are to make',19,12,1,3,16,'Soak the onion in cold water while you make the base mix. Sift the flour and baking powder into a bowl, then add the chilli powder, turmeric, chopped chilli and a good sprinkling of salt. Mix in about 100ml of cold water to make a thick batter – add a splash more if it feels too stiff. For the raita, peel the cucumber and grate it into a sieve set over another bowl. Mix the remaining ingredients with some seasoning and the drained cucumber – squeezing out any extra moisture with your hands – then spoon into a small serving bowl. Drain the onion well and mix it into the batter. Heat about 5cm of oil in a wok or deep pan. Do not fill the pan more than a third full. Add a tiny speck of batter. If it rises to the surface surrounded by bubbles and starts to brown, then the oil is hot enough for frying. Lower heaped tbsps of the bhaji mixture into the pan, a few at a time, and cook for a few mins, turning once, until they are evenly browned and crisp, so about 3-4 mins. Drain on kitchen paper, sprinkle with a little salt and keep warm while you cook the rest. Serve with the raita.','30 mins','20 mins'),
(1598,'veggie pasta one-pot','https://www.bbcgoodfood.com/recipes/veggie-pasta-one-pot','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Veggie-one-pot-pasta-d09e83c.jpg','Cut down on washing-up by cooking everything in the same pot for this pasta dish. Adding a few tomatoes and peppers will reap a lot of nutritional benefits',4,4,1,3,30,'Heat the oil in a large pan over a medium heat and fry the onions for 10 mins, stirring often until golden. Add the peppers, aubergine, garlic, paprika, passata and stock. Cover and simmer for 10 mins. Stir in the penne, bring back to the boil, then reduce the heat and cook, covered, for 12-15 mins until the penne is tender. Stir in the basil and olives, then remove from the heat and leave to stand for 5 mins. Divide half the pasta between two plates, and grate half the cheese over the top. Leave the remaining pasta to cool completely and keep chilled for up to four days. To serve, tip into a pan with a splash of water and reheat over a low heat until piping hot. Grate over the remaining cheese.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','15 mins','35 mins'),
(1599,'miso broccoli, egg & quinoa salad','https://www.bbcgoodfood.com/recipes/miso-broccoli-egg-quinoa-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/miso-broccoli-egg-quinoa-salad-6c2132b.jpg','Pack a lunch that will power you through the afternoon with this healthy miso and lime-dressed salad with broccoli, peas, boiled egg and radishes. It''s tasty and full of goodness',14,1,1,3,30,'Bring a pan of water to the boil. Add the broccoli and cook for 3 mins, then lift out with a slotted spoon and leave to cool. Lower the egg into the water and gently simmer for 6 mins. Drain and set in a bowl of cold water to cool. Whisk the miso and lime juice in a small bowl, then toss with the quinoa, peas, radishes and cooked broccoli. Tip the salad into your lunchbox or an airtight container. Peel the egg, slice in half and arrange on top of the quinoa, then sprinkle with the sesame seeds.','5 mins','15 mins'),
(1600,'aubergine dhal with tomato & onion raita','https://www.bbcgoodfood.com/recipes/aubergine-dhal-tomato-onion-raita','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/aubergine-dhal-iwth-tomato-onion-raita-440-400-6a07277.jpg','This comforting chickpea and lentil curry provides all five of your 5-a-day. It’s spiced with turmeric and ginger and topped with a calcium-rich raita',4,2,1,3,30,'Heat the oil in a large non-stick pan and fry the onion, garlic and ginger for 8 mins until softened. Tip in the turmeric, coriander, cumin and chilli and cook for 20 seconds, stirring occasionally. Pour in 800ml boiling water, stir in the bouillon, lentils and aubergine, and cover the pan and leave to simmer for 15 mins until the lentils are pulpy. Stir in the chickpeas with their water, if you need a little more liquid, and cook for 5 mins. Meanwhile, stir the red onion, coriander, mint and tomatoes with the yogurt to make a raita. Spoon the dhal into two bowls, top with raita and scatter with mint leaves.','10 mins','25 mins'),
(1601,'corn, coconut & lentil chowder','https://www.bbcgoodfood.com/recipes/corn-coconut-lentil-chowder-2','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Coconut-and-lentil-chowder-b447b57.jpg','Serve up our healthy corn, coconut & lentil chowder for lunch. Lentils, onions and sweetcorn give this dish valuable fibre for a healthy digestive system',4,6,1,3,30,'Heat the oil in a large pan over a medium heat and fry the onions for 8 mins, stirring often until softened. Stir in the garlic, chillies, curry powder, cumin seeds, turmeric and lentils, and cook for a few minutes more, still stirring. Pour in the stock and sweetcorn. Bring to the boil, then cover, reduce the heat and simmer for 15-20 mins, or until the lentils are tender. Remove from the heat and briefly blitz the soup using a hand blender – you don’t want it to be completely smooth. Add the potatoes, then cover and return to a medium heat for 10-15 mins more, or until the potatoes are tender. Remove from the heat and stir in the yogurt and coriander. Serve two bowlfuls straightaway. Leave the rest of the soup to cool completely, then keep chilled for up to four days. Reheat in a pan over a low heat until piping hot.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','40 mins'),
(1602,'lentil bolognese soup','https://www.bbcgoodfood.com/recipes/lentil-bolognese-soup','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/veggie-bolognese-soup-440-400-a87c096.jpg','Tuck into this full-bodied, vegetarian Bolognese-based soup with red lentils, which brings you four of your 5-a-day and plenty of fibre',4,4,1,3,30,'Heat the oil in a large non-stick pan then fry the onions for a few mins until they start to colour. Add the carrots, celery and garlic then fry for 5 more mins, stirring frequently, until the vegetables start to soften. Pour in the passata, bouillon powder and the lentils with 2l boiling water. Add the smoked paprika, thyme and plenty of black pepper then bring to the boil, cover the pan and simmer for 20 mins. Tip in the penne then cook for 12-15 mins more until the pasta and lentils are tender, adding a little more water if necessary. Stir through the cheese, then ladle half the soup into bowls or a wide-necked flask if you''re taking it as a packed lunch. Cool the remaining soup (remove the thyme sprigs) and keep in the fridge until required. It will keep well for several days. Reheat in a pan, adding a little extra water if the soup has thickened.','10 mins','40 mins'),
(1603,'brown rice tabbouleh with eggs & parsley','https://www.bbcgoodfood.com/recipes/brown-rice-tabbouleh-eggs-parsley','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/tabbouleh-salad-ea90737.jpg','Pack up this tasty rice salad for a healthy vegetarian lunch. It''s full of fibre, folate and vitamin C and is topped with protein-rich boiled eggs',4,2,1,3,30,'Simmer the rice with the thyme and celery for 20 mins until tender. Meanwhile, boil the eggs for 7 mins, then cool in cold water and carefully peel off the shell. Drain the rice and tip into a bowl. Add the bouillon, lemon zest and juice, and red onion, then stir well and scatter over the parsley and pomegranate. Spoon onto plates or into lunchboxes, then halve or quarter the eggs and arrange on top.','10 mins','20 mins'),
(1604,'balsamic feta & mint puy lentils','https://www.bbcgoodfood.com/recipes/balsamic-feta-mint-puy-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2021/06/Balsamic-feta-and-mint-puy-lentils-b0b5a1c.jpg','Make this vibrant, wholesome balsamic feta and mint puy lentils in just five minutes – no hob or oven required! It''s a nutritious, speedy meal for two',46,2,1,3,30,'Whisk 1 tbsp of the olive oil with 1 tbsp of the balsamic vinegar and half the chopped mint. Stir in the feta, then set aside. Put the kettle on to boil. Tip the peas into a large heatproof bowl, pour over enough boiling water to defrost them, then drain and return to the bowl. Put the lentils in a fine mesh sieve, pour over some boiling water to warm them, then drain and tip into the bowl with the peas. Add the radishes and toss together with the remaining oil, vinegar and mint. Divide between bowls and top with the marinated feta. Grind over some black pepper, if you like.','5 mins','No Time'),
(1605,'roasted asparagus & pea salad','https://www.bbcgoodfood.com/recipes/roasted-asparagus-pea-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/egg-and-asparagus-salad-6265398.jpg','Whip up this quick and healthy asparagus, pea and egg salad and you''ll get plenty of nutrients including vitamin C and iron, plus three of your 5-a-day',16,2,1,3,30,'Heat oven to 220C/200C fan/gas 7. Mix the yogurt, mustard and honey together. Add the lemon zest, then add the juice and some seasoning to taste. Squeeze any remaining lemon juice over the watercress. Tear the bread into rough chunks and put them on a large roasting tray with the asparagus. Toss both in the rapeseed oil and seasoning, and roast for 10 mins until the asparagus is tender and croutons are golden. Meanwhile, cook the eggs in a pan of boiling water for 6 mins, then add the frozen peas and cook for 1 min more. Drain and rinse both under cold water until cool. Peel the eggs, then cut into quarters. To assemble, mix the asparagus and peas through the watercress, then toss through the creamy dressing. Nestle in the eggs and croutons, and serve.','10 mins','10 mins'),
(1606,'vegan mushroom & potato hash','https://www.bbcgoodfood.com/recipes/vegan-mushroom-potato-hash','https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Potato-hash-3da3229.jpg','Make this mushroom and potato hash for a healthier breakfast. Mushrooms are a great addition in a vegan diet, as they''re one of the few plant-based sources of vitamin D',4,2,1,3,30,'Tip the oats and soya milk into a large bowl and blitz using a hand blender to break down the oats to a less coarse texture. Set aside for 10 mins to soak. Meanwhile, boil the potatoes for 5 mins, then drain. Heat the oil in a large non-stick frying pan over a medium heat, and cook the mushrooms, onion and paprika for a few minutes until softened. Tip in the potatoes and cook for 10 mins, turning the mixture over every now and then. Stir in the halved tomatoes and leave to cook for 5 mins. The oat mixture should now be stiff. Work in the baking powder using your hands, then halve the mixture. With wet hands, press out one half of the mixture on a plastic chopping board to make a thin disc, like a pancake. Carefully lift it off with a palette knife and cook in a dry non-stick frying pan for 2 mins on each side. Remove to a plate, and repeat with the other half. Put the oat thins on two plates and top with the hash to serve.This recipe is part of our free 7-day healthy diet plan. To access the full meal plan, click here.','10 mins','38 mins'),
(1607,'sesame parsnip & wild rice tabbouleh','https://www.bbcgoodfood.com/recipes/sesame-parsnip-wild-rice-tabbouleh','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/parsnip-tabbouleg-f2abaaf.jpg','Get your fill of tasty veggies with our easy vegan parsnip & wild rice tabbouleh. Our simple winter salad is full of flavour, filling and 2 of your 5-a-day',16,3,1,3,30,'Heat oven to 200C/180C fan/gas 6. Toss the parsnips in 1½ tbsp of the oil, the turmeric, coriander and some seasoning, then sprinkle over the sesame seeds so each piece is well coated. Roast in the oven for 30 mins until tender. Meanwhile, cook the wild rice following pack instructions. Heat the remaining 1 tbsp oil in a separate pan, then add the sliced onion with 3 tbsp water. Cook for 10-15 mins, stirring occasionally until completely soft. Turn up the heat, add 1 tbsp of the vinegar and cook for a few mins until bright pink. Whisk the tahini with the remaining vinegar and enough warm water to make a creamy dressing. Season to taste. Drain the wild rice, then mix through the onions and ¾ of the chopped herbs. Distribute between three plates, then top with the sesame parsnips, pomegranate seeds and the remaining herbs. Serve with the tahini dressing drizzled over.','10 mins','45 mins'),
(1608,'spinach falafel & hummus bowl','https://www.bbcgoodfood.com/recipes/spinach-falafel-hummus-bowl','https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Spinach-falafel-and-hummus-bowl-5bb9369.jpg','Make this easy vegan salad bowl, with homemade spinach falafels and hummus served alongside pitta and salad. It''s packed with nutrients for a healthy supper',46,4,1,3,30,'Heat the oven to 190C/170C fan/gas 5. Line a baking sheet with non-stick parchment. Put all the falafel ingredients, except for the oil, in a food processor and season lightly. Pulse until you have a rough mix. Oil your hands lightly, then take tablespoons of the falafel mix, roll into around 16 balls and put on the baking sheet. Press each one down slightly with the palm of your hand. Brush using the 1 tbsp oil and bake for 20-25 mins until firm and golden, turning halfway through. While the falafel is cooking, make the hummus. Put all of the hummus ingredients into a food processor with 50ml of water and blitz until smooth and silky. Put the spinach, cucumber, red onion and falafel in different sections of each bowl, alongside some hummus, then drizzle with the extra olive oil. Grind over some black pepper. Serve with the pittas on the side and more lemon for squeezing over, if you like.','5 mins','25 mins'),
(1609,'vegan kebabs with avocado dressing','https://www.bbcgoodfood.com/recipes/vegan-kebabs-avocado-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/vegan-kebabs-with-avocado-dressing-c6dc947.jpg','Pile chunks of mushrooms, peaches, courgettes and red onions onto skewers and barbecue them for a wonderful BBQ flavour. Serve with a delicious avocado dressing',16,4,1,3,30,'Mix 3 tbsp oil with the crushed garlic, chilli flakes and rosemary. Thread alternate pieces of mushroom, peach, courgette and red onion onto each skewer – you can get two pieces of everything on each. Brush the kebabs with the flavoured olive oil and season with salt and black pepper, then set aside. The kebabs can be made the day before and kept in the fridge. Heat the barbecue or a grill to its highest setting. Meanwhile, blitz the avocado, half the lemon juice and 50ml water to a smooth dressing and season to taste. Whisk the remaining lemon juice, remaining ½ tbsp olive oil and mustard together, then toss with the mixed rocket salad and toasted seeds. Barbecue or grill the skewers for 4-5 mins on each side or until cooked through and nicely charred. Pile onto a platter and serve with the avocado dressing and salad on the side.','30 mins','10 mins'),
(1610,'vegan three-bean chilli with potato jackets','https://www.bbcgoodfood.com/recipes/vegan-three-bean-chilli-potato-jacket','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/three-bean-chilli-jackets-440-400-05253d5.jpg','This healthy baked potato filling provides four of your 5-a-day in each serving, plus iron, vitamin C and fibre, and plenty of flavour from storecupboard spices',4,2,1,3,30,'Heat the oven to 200C/180C fan/gas 6 and bake the potatoes for 50 mins-1 hr, or until tender. Meanwhile, heat the oil in a non-stick frying pan and fry the pepper and garlic for a few minutes. Stir in the cumin seeds, chilli flakes and spices, then tip in the tomatoes, bouillon powder and beans. Bring to a simmer, cover and cook for 15 mins, or until reduced to a thick sauce. Stir in the chopped coriander. Cut a cross into the tops of the baked potatoes and gently press on the sides to open them out. Spoon over the chilli, then top with the avocado and squeeze over some of the lime wedges. Scatter over some coriander leaves and serve with the remaining lime wedges.','15 mins','50 mins'),
(1611,'vegan roast spiced squash salad with tahini dressing','https://www.bbcgoodfood.com/recipes/vegan-roast-spiced-squash-salad-with-tahini-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2021/11/Squash-salad-84fcb8a.jpg','Make the most of butternut squash in this nutrient-rich and deliciously tasty salad. It''s as good chilled for lunch the next day as it is fresh from the oven',4,4,1,3,30,'Heat the oven to 200C/180C fan/gas 6. Tip the squash and onions onto a large baking sheet and toss with 1 tsp of the oil. Spread out and sprinkle with the paprika, cumin and thyme, then roast for 30 mins. Meanwhile, cook the quinoa following pack instructions, then drain well (or the base of the salad will be too wet). Add the kale to the tray of veg, sprinkle over the seeds and return to the oven for 10 mins. For the dressing, mix the tahini and remaining oil with the vinegar, garlic and 2 tbsp water. Put the quinoa in a bowl and toss with the lentils or beans. Pile half into a salad bowl and the rest into two lunchboxes or bowls, if you''re following the Healthy Diet Plan. Divide the veg on top, then drizzle with the dressing, scatter over the pomegranate seeds and top with the rocket. Chill the other two portions for the next day. Will keep chilled for up to three days.','10 mins','40 mins'),
(1612,'fennel, roast lemon & tomato salad','https://www.bbcgoodfood.com/recipes/fennel-roast-lemon-tomato-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fennel-roast-lemon-tomato-salad-f0b2c17.jpg','Combine roasted lemon with fronds of fennel, cherry tomatoes, pomegranate and herbs to make this colourful summer salad. It makes a perfect sharing dish',11,6,1,3,30,'Heat oven to 200C/180C fan/gas 6 and line a baking tray with baking parchment. Slice 1 lemon into thin rounds and spread out on the baking tray, drizzle over 1/2 tbsp oil and sprinkle over the sugar. Roast for 20-25 mins until shrivelled and caramelised in places. Keep an eye on them – you may have to take some out the oven before the others are done. These can be made in the morning and kept at room temperature. While the lemons are cooking, roughly chop the tomatoes and thinly slice the fennel, saving the fronds. Put in a bowl with the remaining olive oil, juice from the other lemon and pomegranate seeds. Season to taste, then give everything a good mix. When you’re ready to serve, roughly chop all the herbs and mix them into the salad along with the roasted lemons and fennel fronds.','15 mins','25 mins'),
(1613,'griddled vegetables with melting aubergines','https://www.bbcgoodfood.com/recipes/griddled-vegetables-melting-aubergines','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/440-400-griddled-vegetables-with-melting-aubergines-7f2d806.jpg','Pack all five of your 5-a-day into one healthy vegan dish. Flavoured with garlic, lemon and herbs, it''s delicious griddled on the hob or cooked on the barbecue',4,2,1,3,30,'Grill the aubergine, turning frequently, until soft all over and the skin is blistered, about 8-10 mins. Alternatively, if you have a gas hob, cook it directly over the flame. When it is cool enough to handle, remove the skin, finely chop the flesh and mix with the lemon juice, 1 chopped clove garlic, 1 tbsp parsley, 1 tsp extra virgin olive oil and the seeds. Mix the remaining parsley with the remaining chopped garlic and the lemon zest. Meanwhile, mix the thyme, crushed garlic and rapeseed oil and toss with the vegetables, keeping the onions as slices rather than breaking up into rings. Heat a large griddle pan and char the vegetables until tender and marked with lines – the tomatoes will need the least time. Pile onto plates with the aubergine purée and olives, drizzle over a little extra olive oil and scatter with the parsley, lemon zest and garlic.','10 mins','25 mins'),
(1614,'lentil fritters','https://www.bbcgoodfood.com/recipes/lentil-fritters','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentil-fritters-aae9e0d.jpg','Rustle up these lentil fritters in just 25 minutes with our basic lentils recipe, courgette and carrot. They''re healthy, low-fat and packed with nutrients',16,2,1,3,30,'Mix the leftover lentils with the chopped coriander, spring onion and gram flour, then set aside. Use a peeler to cut the carrots and courgettes into long ribbons, then toss the ribbons with the sesame seeds and coriander in sesame oil and the lime juice. Heat the rapeseed oil in a frying pan. Spoon in four dollops of the lentil mixture and flatten into patties. Fry each side until golden and serve with the ribbon salad.','15 mins','10 mins'),
(1615,'next-level chicken caesar salad','https://www.bbcgoodfood.com/recipes/next-level-chicken-caesar-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/caesar-salad-7a44316.jpg','Toss lettuce, croutons and cheese in a punchy anchovy and garlic dressing for a traditional caesar salad – plus griddled chicken to take the flavour up a notch',7,4,1,3,17,'Heat oven to 200C/180C fan/gas 6. Remove the skin from the chicken and set aside. Cover the chicken with cling film and bash out to flatten slightly, then put in a large bowl with half of the lemon juice, 1 garlic clove, a drizzle of oil and some salt and pepper. Set aside. If you’re making the crackling, lay the chicken skin on a baking sheet lined with baking parchment and sprinkle with sea salt. Put another piece of baking parchment on top, then weigh it down with a baking tray. Roast for 25-30 mins until crisp and golden. Meanwhile, trim the crusts off the sourdough and tear the centre into pieces. Tip onto a baking tray, drizzle with the rest of the olive oil and finely grate over 15g of the parmesan. Toss everything together so the bread is completely coated in oil and cheese. Bake in the oven for 15-25 mins with the crackling (if making), tossing a few times, until golden and crunchy. The croutons and crackling can be prepared a day ahead and kept in an airtight container. For the dressing, use a pestle and mortar to crush the remaining garlic to a paste, then mash in the anchovies. Spoon into a bowl and finely grate in 10g parmesan, then add the remaining lemon juice, mayo and mustard. Gradually stir in 4 tbsp oil. Can be made three hrs ahead. Heat a griddle pan until smoking. Griddle the chicken for 4-5 mins on each side until just cooked, then set aside. Trim and discard any limp outside lettuce leaves and trim the rest into bite-sized lengths. Slice the chicken into pieces and use a peeler to shave the remaining parmesan, grating the final stump that can’t be shaved. To serve, tip the lettuce, croutons, chicken and cheese into a large salad bowl and drizzle with most of the dressing. Toss everything together well (the easiest way is with your hands), then drizzle with more dressing, scatter with the crackling and serve.','40 mins','40 mins'),
(1616,'grilled peach, chicken & feta salad','https://www.bbcgoodfood.com/recipes/grilled-peach-chicken-feta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1031452_11-e7a5417.jpg','Contrast juicy sweet fruit with salty Greek cheese and finish with a honey, chilli and sherry vinegar dressing',5,4,1,3,17,'Heat a griddle pan. Toss the chicken in ½ tbsp of the oil, and season. Cook for 3-4 mins on each side or until cooked through. Pop on a plate to rest. Next toss the peach slices in ½ tbsp oil and some ground black pepper. Grill on their cut sides for 1-2 mins each side. Mix the remaining olive oil, vinegar, honey and chilli. Toss with the salad leaves. Arrange the chicken and nectarine slices on top and scatter with the feta. Drizzle with the resting juices from the chicken and eat straight away.','10 mins','12 mins'),
(1617,'sticky citrus chicken with griddled avocado & beet salad','https://www.bbcgoodfood.com/recipes/sticky-citrus-chicken-griddled-avocado-beet-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/sticky-citrus-chicken-with-griddled-avocado-beet-salad-1183d20.jpg','Soft, creamy avocado and zesty chicken make the perfect pairing for a family supper - a great recipe for using up avocados that refuse to ripen!',6,4,1,3,17,'To spatchcock the chicken, flip it over so the backbone is facing you. Using a sturdy pair of kitchen scissors, cut down either side of the backbone, then discard. Turn the chicken over and push down firmly on the breastbone to flatten it out, then make a few slashes in each leg joint. (Or you can ask your butcher to spatchcock the chicken for you.) Put the chicken in a large sealable plastic bag, or a dish. Next, make the marinade. Rinse the preserved lemon under running water to remove some of the brine, then separate the middle from the peel (the middle part – the flesh and pips – is very sour) and discard the middle. Finely chop the soft peel until it resembles a paste. Tip into a bowl and add the remaining marinade ingredients and a pinch of salt. Whisk until combined, then pour roughly half the marinade over the chicken and rub it into all the nooks. For the best flavour, leave the chicken to marinate overnight, covered, in the fridge (store the remaining marinade in the fridge too), but if you’re short on time, 2 hrs marinating will do. Heat oven to 200C/180C fan/gas 6. Tip the onions and beetroot into a large roasting tin (use your best tin and you can serve the dish straight from it), drizzle with oil and season well. Toss the vegetables to coat and spread them out towards the edges of the tin. Place the chicken on top, skin-side up, and pour over any juices from the bag or dish. Season the chicken well and roast for 45 mins. Meanwhile, bring a pan of water to the boil and add the spelt. Cook for 20 mins or until tender, then drain and set aside. Halve each avocado, remove the stones and peel. Chop each piece in half again, if you like. Char the avocado pieces on a hot griddle pan for a few mins – this will give it a smoky flavour. Remove the remaining marinade from the fridge and very gently toss the avocado pieces in it, then set them aside. Pour the remaining marinade into a small pan and bring to a rapid boil, then continue cooking for 2-3 mins until you have a sticky glaze. Remove the roasting tin from the oven, lift the chicken onto a plate or board and stir the spelt into the vegetables. Place the chicken back on top, dot the avocado pieces around the tin and brush the thickened glaze over the chicken. Return to the oven for a further 5-10 mins. To check the chicken is cooked, pierce the thickest part with a skewer. The juices should run clear, not pink. If you need to, return the chicken to the oven for a further 5-10 mins, then check again. Lift the chicken onto a plate, cover loosely with foil and return the roasting tin with the vegetables to the oven for a further 10 mins until the avocado starts to soften. To serve, nestle the chicken back into the tin of veg, scatter over a few small watercress leaves and serve the rest on the side with the yogurt.','15 mins','1 hr'),
(1618,'chicken & pistachio salad','https://www.bbcgoodfood.com/recipes/chicken-pistachio-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-pistachio-salad-ec530ea.jpg','Toss chicken breast, boiled eggs, Little Gem, sundried tomatoes and toasted nuts in a zesty dressing for a quick salad that''s rich in calcium, folate and iron',14,2,1,3,17,'Bring a large pan of water to a simmer. Add the eggs and cook gently for 7 mins. Remove with a slotted spoon and transfer to a bowl of cold water. Once cooled, carefully peel off the shell and slice each egg in half. Meanwhile, whisk the oil with the lemon zest, juice and yogurt, and season well. Shred the chicken and toss with the olives, sundried tomatoes, basil and lettuce. Pour in the dressing, season and toss together. Divide the salad between two bowls and top with the egg halves and pistachios.','10 mins','7 mins'),
(1619,'green goddess chicken salad','https://www.bbcgoodfood.com/recipes/green-goddess-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/green-goddess-chicken-salad-4bae43b.jpg','Kick-start summer with a chicken salad that''s bursting with colours and flavours, including basil, jalapeño and lime. It also boasts four of your five-a-day',14,2,1,3,17,'Put the avocado, oil, lime zest and juice, jalapeño, basil, half the cucumber and 1 tbsp water in a blender and blitz until smooth. Season to taste, then set aside in the fridge until ready to use. Cook the eggs in a pan of boiling water for 7 mins, then rinse under cold running water to cool. Drain, peel and halve, then set aside. Toss the lettuce quarters with the sundried tomatoes, cooked chicken, the spring onions and remaining cucumber. Spread the cooked quinoa over the base of a serving plate, then top with the chicken salad, eggs and the dressing. Scatter over the pistachios and serve.','25 mins','7 mins'),
(1620,'minty griddled chicken & peach salad','https://www.bbcgoodfood.com/recipes/minty-griddled-chicken-peach-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/minty-griddled-chicken-peach-salad-2-440-400-9da5092.jpg','This colourful salad supplies lean protein and is packed with nutrients including vitamins C and E. It''s also low-calorie and provides all 5 of your 5-a-day',4,2,1,3,17,'Mix the lime zest and juice, oil and mint, then put half in a bowl with the garlic. Thickly slice the chicken at a slight angle, add to the garlic mixture and toss together with plenty of black pepper. Cook the beans in a pan of water for 3-4 mins until just tender. Meanwhile, griddle the chicken and onion for a few mins each side until cooked and tender. Transfer to a plate, then quickly griddle the peaches. If you don’t have a griddle pan, use a non-stick frying pan with a drop of oil. Toss the warm beans and onion in the remaining mint mixture, and pile onto a platter or into individual shallow bowls with the lettuce and rocket. Top with the avocado, peaches and chicken and scatter over the mint. Serve with the potatoes while still warm.','10 mins','15 mins'),
(1621,'chicken caesar salad','https://www.bbcgoodfood.com/recipes/chicken-caesar-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327831_11-3524329.jpg','A classic chicken salad recipe, featuring crunchy croutons and a creamy, garlic dressing. Ideal for lunch with friends',5,4,1,3,17,'Heat oven to 200C/fan 180C/gas 6. Tear 1 medium ciabatta into big, ragged croutons or, if you prefer, cut with a bread knife. Spread over a large baking sheet or tray and sprinkle over 2 tbsp olive oil. Rub the oil into the bread and season with a little salt if you like (sea salt crystals are best for this). Bake for 8-10 mins, turning the croutons a few times during cooking so they brown evenly. Rub 2 skinless, boneless chicken breasts with 1 tbsp olive oil, season. Place pan over a medium heat for 1 min, until hot, but not smoking. Lay the chicken on the pan (it will sizzle if it’s hot enough) and leave for 4 mins. Turn the chicken, then cook for 4 mins more. Check if it’s cooked by poking the tip of a sharp knife into the thickest part; there should be no sign of pink and juices will run clear. Bash 1 garlic clove with the flat of a knife and peel off the skin. Crush with a garlic crusher. Mash 2 anchovies with a fork against the side of a small bowl. Grate a handful of parmesan cheese and mix with the garlic, anchovies, 5 tbsp Hellmann''s Real Mayonnaise and 1 tbsp white wine vinegar. Season to taste. It should be the consistency of yogurt – if yours is thicker, stir in a few tsps water to thin it. Shave the cheese with a peeler. Tear 1 large cos or romaine lettuce into large pieces and put in a large bowl. Pull chicken into bite-size strips and scatter half over the leaves, along with half the croutons. Add most of the dressing and toss with your fingers. Scatter the rest of the chicken and croutons, then drizzle with the remaining dressing. Sprinkle the parmesan on top and serve straight away.','10 mins','15 mins - 20 mins'),
(1622,'chilli-lime chicken salad','https://www.bbcgoodfood.com/recipes/chilli-lime-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/july-salad-c994666.jpg','Thai-inspired chicken makes a perfect barbecue dish, serve with a feast of dressed green salad and herbs',33,4,1,3,17,'Whisk together the lime zest and juice, fish sauce, sugar and chillies until the sugar dissolves. Pour 1/3 of this mix into a bowl, add the oil and set aside (this will be the dressing). Put the remainder into a large food bag with 1 tsp salt, the garlic, turmeric and coriander stems. Slash each piece of chicken down to the bone a few times, then add to the marinade and squish it around well in the bag. Chill for at least 2 hrs, or ideally up to 24 hrs. Heat oven to 180C/160C fan/gas 4 (if barbecuing, get the coals ready). Lift the chicken from the marinade and into a roasting tin. Roast for 30 mins until cooked through but not yet completely tender. If you’re barbecuing, transfer the chicken from its tin to a hot barbecue for 10 mins more, turning once crisp (otherwise continue roasting in the oven). Spread the spring onions, cucumber and tomatoes over a platter, add the chicken, then finish with the herbs, avocados, nuts and dressing.','15 mins','30 mins - 40 mins'),
(1623,'warm chicken salad','https://www.bbcgoodfood.com/recipes/warm-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1273655_8-6d34619.jpg','This chicken salad recipe makes a lovely main meal for a hot day, or take some to work and be the envy of your colleagues',5,2,1,3,17,'Heat oven to 200C/fan 180C/gas 6. Spread the chunks of chicken and baguette over a shallow roasting tray. Drizzle with 2 tbsp olive oil and toss to coat. Season, then put in the oven for 15 mins until the chicken is cooked through and the bread is golden and crisp. Whisk together the remaining olive oil and balsamic vinegar to make a dressing. Split the bag of leaves between two serving plates, add the beetroot, then scatter the cheese over. Toss with the warm chicken and bread, drizzle with the dressing, then serve straight away.','5 mins','15 mins'),
(1624,'peruvian chicken, avocado & quinoa salad','https://www.bbcgoodfood.com/recipes/peruvian-chicken-avocado-quinoa-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/peruvian-chicken-avocado-quinoa-salad_0-71c7fed.jpg','Fed up with pasta salad? This gluten-free alternative is packed with lean protein from the chicken, and healthy, heart-friendly fats from the avocado',48,2,1,3,17,'Half-fill a medium pan with water and bring to the boil. Rinse the quinoa in a fine sieve, then add to the water, stir well and simmer for 12 mins or until just tender. While the quinoa is cooking, put the sweetcorn in a dry frying pan over a medium-high heat. Cook for 5 mins, turning every now and then, until defrosted and lightly toasted. Set aside. Rinse the cooked quinoa in a sieve under cold water, then press hard with a serving spoon to remove excess water. Tip the quinoa into a bowl and toss with the olive oil, sweetcorn, tomatoes, coriander, spring onions, lime zest and chilli. Season well with black pepper. Halve and stone the avocado. Scoop out the flesh with a large metal spoon, cut into slices and combine with the lime juice. Add the chicken pieces and avocado to the salad and toss gently together before serving.','10 mins','20 mins'),
(1625,'niçoise chicken salad','https://www.bbcgoodfood.com/recipes/nicoise-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/nicoise-chicken-salad-bb290d3.jpg','We''ve given the usual tuna Niçoise a twist by using chicken, for a lean veggie-packed salad that''s also rich in fibre, folate, vitamin c, iron and three of your five-a-day',5,2,1,3,17,'Mix the dressing ingredients together in a small bowl with 1 tbsp water. Add 1 tbsp of the dressing to the chicken breasts and toss well to coat. Heat the oil in a small non-stick frying pan with a lid and cook the chicken for about 12 mins, covered, turning over halfway until cooked all the way through. Meanwhile, boil the potatoes for 7 mins, add the beans and boil for 5 mins more or until both are just tender, then drain. Put the chicken on a plate to rest while you toss the beans, potatoes and remaining salad ingredients together in a large bowl with half the dressing. Slice the chicken, arrange on the salad, then add any juices to the remaining dressing and spoon on top.','10 mins','15 mins'),
(1626,'chicken fattoush','https://www.bbcgoodfood.com/recipes/chicken-fattoush','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken-fattoush-778e018.jpg','This healthy Middle Eastern salad topped with sumac-sprinkled pitta bread is a quick and easy lunch or supper',29,4,1,3,17,'Pour the lemon juice into a large bowl and whisk while you slowly add the oil. When all the oil has been added and the mixture starts to thicken, season. Add the lettuce, tomatoes, parsley, cucumber, chicken pieces and spring onions, and stir well to coat the salad in the dressing. Put the pitta breads in the toaster until crisp and golden, then chop into chunks. Scatter the toasted pitta pieces over the salad and sprinkle over the sumac. Serve straight away.','15 mins','No Time'),
(1627,'chicken & freekeh chopped salad with salsa verde','https://www.bbcgoodfood.com/recipes/chicken-freekeh-chopped-salad-leftover-salsa-verde','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chicken_freekeh_chopped_salad_with_leftover_salsa_verde-cb1764c.jpg','An easy, throw together salad that uses up leftover roast chicken. This wholesome dish is packed with crunchy vegetables to make up 2 of your 5-a-day',11,4,1,3,17,'Soak the freekeh in cold water for 5 mins, then drain and rinse again under the tap. Heat 1 tbsp of the oil in a deep saucepan, add the bay leaf, garlic and freekeh , and toast, stirring vigorously, for 1 min or so. Fill the pan with boiling water, add a generous pinch of salt, bring to the boil and simmer for 15 mins until tender. Add the asparagus for the final 4 mins. Drain and leave to cool. (To speed the cooling, spread out the freekeh on a baking tray. ) Once cooled, put the freekeh in a large mixing bowl. Season and combine with the remaining oil and all the other ingredients, topping with the salsa verde and toasted pine nuts to serve.','15 mins','20 mins'),
(1628,'shredded chicken salad','https://www.bbcgoodfood.com/recipes/shredded-chicken-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/shredded-chicken-salad-6d71461.jpg','Try making this tasty chicken salad with bulgur wheat and courgette during the week for an easy midweek meal. It''s ideal for serving a small crowd.',41,3,1,3,17,'Heat oven to 200C/180C fan/gas 6. Toss the chicken in a large roasting tin with 1 tbsp oil and the garlic, season well and bake for 35 mins until cooked through and browned a little. Meanwhile, cook the bulgur wheat following pack instructions. Drain, tip in a bowl, pour in the rest of the oil to stop it sticking together, then set aside. Whisk the dressing ingredients with a splash of water (it should be runny enough to drizzle), and season to taste. When the chicken is cooked, squeeze the garlic cloves out of their skins into the tin. Leave the chicken to cool a little, then shred with a fork. Add the bulgur wheat, courgette ribbons and herbs to the tin. Toss together well, coating everything in the juices in the tin. Transfer to a serving dish and drizzle over the dressing to serve.','15 mins','45 mins'),
(1629,'quinoa, pea & avocado salad','https://www.bbcgoodfood.com/recipes/quinoa-pea-avocado-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/quinoa-pea-avocado-salad-0c32b6a.jpg','This super-healthy, herby side dish is a lovely light accompaniment to grilled chicken, fish or a BBQ spread',29,4,1,3,17,'Put the peas in a large heatproof bowl, pour over just-boiled water, then set aside. Pour the lemon juice into a small bowl and whisk in some seasoning. Keep whisking as you slowly add the olive oil, followed by the mint and chives. Drain the peas and tip into a large serving dish. Stir in the quinoa, breaking up any clumps. Pour over the dressing, then fold in the avocado and pea shoots. Serve immediately.','20 mins','No Time'),
(1630,'chopped green salad with herby chilli dressing','https://www.bbcgoodfood.com/recipes/chopped-green-salad-herby-chilli-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chopped-green-salad-with-herby-chilli-dressing-0bc8e57.jpg','This fresh, crunchy side dish makes a great addition to any buffet, with avocados, cucumber, green peppers and a zingy dressing',24,8,1,3,17,'Using a stick blender or the small bowl of a food processor, whizz together all the dressing ingredients. Season to taste. To serve, put all the salad ingredients in a large bowl, drizzle with the dressing and toss together.','15 mins','No Time'),
(1631,'green salad with avocado','https://www.bbcgoodfood.com/recipes/green-salad-avocado','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-27061_11-e68d600.jpg','A simple, fresh-tasting salad to serve as part of a summery buffet',115,10,1,3,17,'Squeeze 1 tbsp lemon juice into a jam jar with a pinch of salt. Pour in 4 tbsp olive oil, add a small bunch finely chopped chives, put on the lid, then shake well. To serve, toss with 200g bag mixed salad leaves and 2 sliced ripe avocados.','10 mins','N/A'),
(1632,'chilli green salad','https://www.bbcgoodfood.com/recipes/chilli-green-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-8337_11-4c346a9.jpg','This sweet salad is perfect teamed with Thai, Chinese and Japanese meals to give a bit of colour and crunch',8,2,1,3,17,'Mix together the sweet chilli sauce, soy sauce, zest and lime juice. Toss together with a bag of spinach, rocket and watercress salad and serve immediately.','5 mins','No Time'),
(1633,'garden salad','https://www.bbcgoodfood.com/recipes/garden-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-659583_11-dcca8ca.jpg','Use whatever herbs you have in the garden, and if you have your own lettuce, too, all the better for this simple side dish',43,6,1,3,17,'Wash and dry the lettuce, leaves and herbs. Tear larger leaves into bite-size pieces and tip all the greenery into a large salad bowl. Put the garlic, onion, parsley and watercress or rocket into a mini chopper or food processor. Blitz until finely chopped. Add the vinegar, mustard, salt and pepper and blitz briefly. Add the oil and blitz until thickened. Drizzle over the salad and toss until the leaves are just coated. You might not need all the dressing, but it will keep in the fridge for a few days.','20 mins','N/A'),
(1634,'green bean salad','https://www.bbcgoodfood.com/recipes/minted-green-bean-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1915_11-f6423fb.jpg','This summery vegan green bean salad is flavoured with fresh mint and uses just five ingredients. Put it together for a speedy al fresco side dish',43,6,1,3,17,'Cook the beans in boiling salted water for 5-6 mins, until just tender, then drain and refresh under cold running water. Shake well to remove excess water, then pat dry with kitchen paper. You can do this the day before and keep the cooked beans covered in the fridge. Heat 1 tbsp of the oil in a small pan, add the garlic and fry quickly until crisp and lightly golden. Tip into a bowl with the oil from the pan and leave to cool. This can be done the day before, then covered and chilled. On the day, whisk together the remaining oil, vinegar, mint, salt and pepper. Pour over the beans and mix well. Tip into a serving bowl and scatter over the garlic in its oil.','35 mins','No Time'),
(1635,'green salad with buttermilk dressing','https://www.bbcgoodfood.com/recipes/green-salad-buttermilk-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-25427_10-ec9b35a.jpg','Crisp iceberg lettuce and peppery watercress make a fresh springtime salad',8,12,1,3,17,'First make the dressing. Mix together the garlic, buttermilk and mayonnaise. When ready to serve, arrange the watercress and iceberg on a plate. Drizzle over most of the dressing, serving any leftovers separately for guests to help themselves','10 mins','N/A'),
(1636,'green goddess salad','https://www.bbcgoodfood.com/recipes/green-goddess-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-7678_12-fde8710.jpg','A fresh green salad to accompany any meal - and it counts as one of your 5 a day too',7,10,1,3,17,'Tip the dressing ingredients into a jar with a splash of water and shake well. Tip the salad into a bowl and keep in the fridge for up to a day. Before serving, dress the salad.','20 mins','No Time'),
(1637,'shredded greens salad','https://www.bbcgoodfood.com/recipes/shredded-greens-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1173521_11-ff23130.jpg','A crunchy salad of apples, fennel, cucumber, celery and spring onions - a healthy and light side dish for a crowd',17,10,1,3,17,'Zest and juice 2 of the limes into a big salad bowl. Top and tail the remaining 2 limes to sit flat on a chopping board. Use a small serrated fruit knife to cut away the peel and pith in downward strokes. Cut the segments from the pith and roughly chop. Add to the salad bowl with the apples, fennel, cucumber, celery, spring onions and olive oil. Cover and chill until ready to serve.','20 mins','No Time'),
(1638,'baby gem lettuce with marie rose dressing','https://www.bbcgoodfood.com/recipes/baby-gem-lettuce-marie-rose-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1159484_11-7788df5.jpg','A simple green salad with a dressing from the classic prawn cocktail - a versatile accompaniment to many dishes',25,12,1,3,17,'Separate the lettuce leaves, put on a large dish and sprinkle over the cress. Combine all the dressing ingredients with a few tsp water to make a runny consistency. Drizzle over the lettuce and cress and serve.','10 mins','No Time'),
(1639,'spring celebration salad','https://www.bbcgoodfood.com/recipes/spring-celebration-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spring-celebration-salad-7fece08.jpg','Celebrate spring with this salad using the season’s most exciting food, including new potatoes, asparagus and peas. It''s even better if you make your own mayo',11,2,2,3,17,'Heat oven to 200C/180C fan/gas 6. Drizzle the bread with some olive oil and season, then roast in the oven, turning halfway through, for 12-15 mins or until crisp and golden. Once baked, rub with the raw garlic and break or cut into croutons. Bring a pan of water to boil. Add the potatoes, mint and 1 tsp salt and simmer for 15 mins until the potatoes are tender. Don’t be afraid of overcooking them – they’re far better tender than chalky. Drain the potatoes and discard the mint sprig. Meanwhile, bring another pan of water to the boil and lower the eggs into the water. Bring back to the boil and cook for 6 mins. Drain, then run the eggs under cold water to cool. Tap the eggs on the work surface and peel. Bring another pan of salted water to the boil. Chop the asparagus spears into three and boil in the water for 3-4 mins until just tender. Scoop out with a slotted spoon and refresh in cold water. Bring the water back to the boil and add a pinch of sugar. Add the peas to the water and cook until tender (about 2-3 mins). Drain. Toss the potatoes with 2 tbsp mayo and the apple, spring onions and capers. Whisk the olive oil and white wine vinegar together, season, then dress the lettuce and soft herbs along with the asparagus and peas. Spread the remaining mayo across your plate, top with the potatoes and dressed salad, halve the eggs and add more mayo, if you like. Garnish with the croutons and extra herbs.Homemade mayoIn a large bowl, whisk 2 egg yolks, ½ tsp salt, 1 tsp Dijon mustard and the juice of ½ lemon. Still whisking, add 200ml vegetable oil a drop at a time, increasing to a stream once it starts emulsifying. If it splits, just add a squeeze of lemon and whisk vigorously – it will come together. Add 100ml extra virgin rapeseed oil, whisking, to finish the mayo, then add 2 tbsp water, a pinch of cayenne pepper and a pinch of white pepper. Whisk until creamy.','20 mins','30 mins'),
(1640,'shaved fennel & rocket salad','https://www.bbcgoodfood.com/recipes/shaved-fennel-rocket-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/02/Shaved-fennel-and-rocket-salad-f5e2fe8.jpg','Serve our fennel and rocket salad as a side – raw fennel can be fibrous, but finely slicing it before lightly pickling it in lemon juice breaks it down',7,4,1,3,17,'Remove the fronds from the fennel bulb and set aside. Cut away any tough stalks, then thinly slice the bulb using a sharp knife or ideally a mandoline. Tip the fennel slices into a bowl and, using your hands, scrunch through a large pinch of salt, the fennel seeds, if using, and the lemon juice. Cover and leave to stand at room temperature for at least 10 mins, or chill for up to a few hours. To serve, drizzle the pickled fennel with the olive oil, then toss through the rocket and reserved fennel fronds. Serve directly from the bowl or pile onto plates.','5 mins','No Time'),
(1641,'feta, beetroot & pomegranate salad','https://www.bbcgoodfood.com/recipes/feta-beetroot-pomegranate-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/feta-beetroot-pomegranate-salad-3ba84c9.jpg','Enjoy something lighter for lunch with this bright salad. It''s low-calorie and full of flavour thanks to mint, parsley, cinnamon and cumin',12,1,1,3,17,'Whisk the oil, pomegranate molasses, cinnamon, cumin seeds and lemon juice together in a bowl. Add 1 tbsp water to loosen, and season. Toss in the herbs, beetroot, feta, rocket and lettuce, and toss to coat in the dressing. Sprinkle over the pomegranate seeds to serve.','10 mins','No Time'),
(1643,'quick garden salad','https://www.bbcgoodfood.com/recipes/garden-salad-0','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/garden-salad-c4b60e0.jpg','Crunchy, herby and fresh- this green side salad is versatile, healthy and dressed with a light garlic and parsley vinaigrette',43,10,1,3,17,'Wash and dry the lettuce, leaves and herbs. Tear larger leaves into bite-sized pieces and tip everything into a large salad bowl. To make the dressing, put the garlic, onion, parsley and watercress or rocket into a mini chopper or food processor, and blitz until finely chopped. Add the vinegar, mustard and some seasoning, and blend briefly. Slowly add the oil and blitz until thickened. Drizzle over the salad just before eating and toss until the leaves are just coated. (You might not need all the dressing, but it will keep in the fridge for a couple of days.)','15 mins','No Time'),
(1644,'courgette salad','https://www.bbcgoodfood.com/recipes/courgette-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4303_11-9a659d5.jpg','This courgette salad is the perfect barbecue accompaniment',5,4,1,3,17,'Grate the courgettes on the coarse side of your grater. Toss them with the olive oil, lemon or lime juice, clear honey, poppy seeds and the crushed garlic clove. Add salt and pepper to taste. Serve as soon as possible (or it will get too watery), with sizzling barbecued chicken or lamb, kebabs or burgers.','10 mins','N/A'),
(1645,'lettuce & fennel salad with orange & mustard dressing','https://www.bbcgoodfood.com/recipes/lettuce-fennel-salad-orange-mustard-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-10102_11-d486904.jpg','An explosion of fresh flavours makes this salad a winner on any table, give it a go and see what you think',43,10,1,3,17,'Quarter, core and finely shred the fennel. A mandolin or the slicing balde of a food procesor works well. Whisk together the orange juice and Dijon mustard with salt and pepper, then whisk in the walnut oil. Pour the dressing over the fennel and leave to marinate for up to 24 hours. Tear the lettuce into bite-size pieces. Put in a large bowl, then tip in the fennel and dressing. Toss everything together and serve.','15 mins','N/A'),
(1646,'herb salad with pomegranate & pistachios','https://www.bbcgoodfood.com/recipes/herb-salad-pomegranate-pistachios','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-523504_11-ce8a46f.jpg','A sweet salad with plenty of crunch that goes perfectly with Middle Eastern dishes',17,6,1,3,17,'Mix the juice, vinegar and honey with seasoning. Tip rest of the ingredients into a large mixing bowl, drizzle over the dressing and gently toss to serve.','15 mins','N/A'),
(1647,'roasted asparagus & smashed new potato salad','https://www.bbcgoodfood.com/recipes/roasted-asparagus-smashed-new-potato-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/roasted-asparagus-smashed-new-potato-salad-9a16d76.jpg','Combine asparagus, goat''s cheese, new potatoes, peas and lettuce to make this easy springtime vegetarian dish. Great for a midweek meal',5,3,1,3,17,'Slice the bottom 2cm of the asparagus spears into thin rounds and set aside in a bowl with the lemon juice. Bring a pan of salted water to the boil and boil the potatoes for 10-12 mins until cooked through, adding the asparagus spears for the last 2 mins of cooking. Drain and steam-dry in the colander. Heat the grill to high.  Tip the potatoes onto a baking tray and use a spoon or fish slice to crush them a bit. Add the asparagus to the tray and toss in 1 tbsp of the olive oil, season and grill for 10-15 mins, turning halfway through, until the potatoes are crisp and the asparagus is cooked through and a little charred. Add the lettuce for the final 5 mins until charred. Meanwhile, cook the peas, drain and add these to the lemon juice and asparagus ends. Whisk in the olive oil and season. Pile the lettuce, asparagus and potatoes onto a large serving dish. Top with chunks of goat’s cheese, the asparagus stalks and peas, drizzling the sauce over the rest of the vegetables as you go. Scatter with dill and lemon zest to serve.','15 mins','20 mins - 25 mins'),
(1648,'mango chutney baked feta with lentils','https://www.bbcgoodfood.com/recipes/mango-chutney-baked-feta-lentils','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/mango-chutney-baked-feta-with-lentils-04b4e52.jpg','Pack in three of your 5-a-day with a baked feta and lentil salad, using mango chutney as a glaze for the cheese. An easy veggie supper for two',14,2,1,3,17,'Heat oven to 220C/200C fan/ gas 7. Put the feta in an ovenproof dish and spread over the mango chutney. Bake for about 20 mins until sticky. Mix the yogurt, half the mint, half the dill, the olive oil and 1 tbsp water to make a dressing. Season to taste. Cook the lentils in the microwave according to pack instructions. Toss the lentils with the cucumber and tomatoes. Divide between two plates and top with the feta, a drizzle of the yogurt dressing and any remaining herbs.','10 mins','20 mins'),
(1649,'puy lentil, spiced roast carrot & feta salad','https://www.bbcgoodfood.com/recipes/puy-lentil-spiced-roast-carrot-feta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-605563_11-47602ff.jpg','High in fibre, a good source of iron, vitamin C and it counts as 3 of your 5-a-day - what''s not to love about this superhealthy salad?',5,4,1,3,17,'Heat the oven to 200C/180C fan/gas 6. In a shallow roasting tin, toss together half the oil, the cumin, carrots and some seasoning. Roast for 25 mins, turning halfway through cooking. Drizzle over the honey, stir and roast for 5 mins more. Meanwhile, gently heat the lentils with the onion, lemon juice, remaining oil and some salt and pepper. Allow to cool slightly while the carrots finish cooking. Toss the dressed lentils with mint and lamb’s lettuce. Lay warm spiced carrots on top and scatter with feta.','10 mins','30 mins'),
(1650,'lentil & tomato salad','https://www.bbcgoodfood.com/recipes/lentil-tomato-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1308566_2-b1954cf.jpg','Lentils are a substantial and healthy base to this fresh salad with onion, mango chutney, coriander, greens and tomatoes',33,4,1,3,17,'Boil the lentils following pack instructions, drain, rinse well, then drain thoroughly. Meanwhile, mix the citrus juices, vinegar and a pinch of salt in a salad bowl, then toss in the onion rings – after a few mins they will soften and turn pink. Whisk together the oil, cumin, garlic and chutney, then toss into the onions with the cooled lentils, coriander, tomatoes, spinach and plenty of seasoning.','5 mins','20 mins'),
(1651,'quinoa, lentil & feta salad','https://www.bbcgoodfood.com/recipes/quinoa-lentil-feta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-488488_11-2934f83.jpg','Quinoa, a protein-rich seed, makes a great substitute for rice or couscous',73,4,1,3,17,'Cook the quinoa in a large pan of boiling water for 10-15 mins until tender, drain well, then set aside to cool. Meanwhile, heat the oil in a small pan, then cook the shallot or onion for a few mins until softened. Add the tarragon, stir well, then remove from the heat. Stir the softened shallot and tarragon into the cooled quinoa along with the lentils, cucumber, feta, spring onions, orange zest and juice and vinegar. Toss well together and chill until ready to serve.','15 mins','15 mins'),
(1652,'squash & lentil salad','https://www.bbcgoodfood.com/recipes/squash-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/squash_lentil_salad-980bd80.jpg','Make this butternut squash and lentil salad for a low-calorie, gluten-free lunch or dinner, or as a side dish. It''s easy to make, with just a few ingredients',29,2,1,3,17,'Heat oven to 220C/200C/gas 7. Toss the squash in 2 tbsp olive oil, season and roast for 30-35 mins or until golden. Add 2-3 tsp water to the raita, stir until smooth and set aside. Toss the lentils with half the raita, squash and dill. Tip the lentils onto a plate, top with remaining squash, drizzle over 2 tsp olive oil and the rest of the raita. Garnish with the remaining dill.','10 mins','35 mins'),
(1653,'cauliflower wings','https://www.bbcgoodfood.com/recipes/cauliflower-wings-zesty-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2022/03/Cauliflower-wings-and-zesty-lentil-salad-1a8edc0.jpg','Toss cauliflower florets in a hot sauce and serve with lentils and salad leaves to make this healthy, flavourful and nutrient-packed dinner',46,2,1,3,17,'Heat the oven to 220C/200C fan/gas 7. Oil and line a baking sheet with non-stick baking parchment. Put the flour, paprika and a big pinch of salt in a bowl and whisk in the yogurt and 50ml water until you have a smooth batter. Toss in the cauliflower florets until coated and spread out in an even layer on the baking sheet. Bake for 20-25 mins, tossing halfway through, until crisp and golden. Put the hot sauce and 1 tsp of the honey in a pan to heat up, toss the cauliflower in the mixture, put back on the tray and cook for about 5-8 mins more. Meanwhile, whisk together most of the lime zest and all the juice, the remaining 1 tsp honey and 1 tbsp olive oil in a large bowl, and toss in the salad leaves and lentils. Divide between two plates and top with the cauliflower wings and the remaining lime zest, if you like.','5 mins','30 mins'),
(1654,'steak, beetroot, horseradish & warm lentil salad','https://www.bbcgoodfood.com/recipes/steak-beetroot-horseradish-warm-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/steak-beetroot-horseradish-warm-lentil-salad-7e06432.jpg','Get four of your 5-a-day in one meal from this steak, beetroot and lentil salad. As well as being super-nutritious, it only takes 20 minutes to make',16,2,1,3,17,'Whisk together the horseradish, yogurt and honey. Season and add lemon juice to taste. Season the steak on all sides with a little salt and black pepper. Heat 1 tbsp oil in a non-stick frying pan. Add the steak and cook to your liking, 2-3 mins on each side for medium rare. Set aside to rest. Put the pan back on the heat, add the remaining oil, lightly crush in the garlic, then tip in the peas, lentils, beans and beetroot. Cook for a few mins, stirring, until the peas and beetroot are warmed through. Remove from the heat, then stir through the remaining lemon juice, dill and rocket. Thinly slice the steak. Divide the lentil salad between two plates, nestle in the steak and drizzle over the dressing.','10 mins','10 mins'),
(1656,'warm lentil salad with serrano, chicken & rocket','https://www.bbcgoodfood.com/recipes/warm-lentil-salad-serrano-chicken-rocket','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-47230_11-f670369.jpg','A filling rustic summer salad',33,4,1,3,17,'Put the onion in a bowl, drizzle over the vinegar, then season with salt and pepper. Set aside for 10 mins or so until the onion has softened slightly. Meanwhile, in another large bowl, mix the parsley and tomatoes with the capers. When ready to serve, tip the lentils into a sieve and rinse with boiling water from the kettle. Drain. Toss the onions and their juices into the lentils, then add the 2 tbsp olive oil and carefully mix everything together. Scoop this onto a large serving platter, then top with the ham, chicken and rocket. Drizzle with more olive oil and a little more vinegar, if you like, then serve.','15 mins','N/A'),
(1657,'halloumi with lemony lentils, chickpeas & beets','https://www.bbcgoodfood.com/recipes/halloumi-lemony-lentils-chickpeas-beets','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/halloumi-with-lemony-lentil-salad-a57237c.jpg','A great storecupboard salad, topped with golden brown halloumi and quick, homemade pickled red onion',9,4,1,3,17,'Cook the lentils in a pan of boiling water for 20-25 mins or until just done. Meanwhile, squeeze the juice from one lemon into a bowl. Add the onion and scrunch together with a pinch of salt to pickle slightly. Set aside. Finely zest the remaining lemon and set aside for the halloumi. Squeeze the juice into a jam jar or jug. Add the oil with a pinch of seasoning and the capers – shake well and set aside. When the lentils are ready, drain and tip into a large serving bowl with the chickpeas. Toss with the dressing straight away, then toss through the beets, parsley, pickled onions and their juice. Heat a frying pan over a medium heat and fry the halloumi for 1-2 mins each side or until golden brown. Toss with the lemon zest, then place on top of your salad to serve.','15 mins','30 mins'),
(1658,'salmon & puy lentil salad with olive dressing','https://www.bbcgoodfood.com/recipes/salmon-puy-lentil-salad-olive-dressing','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-23377_11-5f4ddba.jpg','Use up leftover poached salmon in this stylish main-meal salad with punchy dressing',4,4,1,3,17,'Chop half the olives (set the rest aside), then mix with the remaining dressing ingredients in a bowl. Boil the green beans for 5 mins, then refresh under cold water. Put the eggs in cold water, bring to the boil, then cook for 5 mins. Cool, shell and halve or quarter. Heat the lentils in the microwave for 2 mins per pack. Tip into a bowl and toss with the tomatoes, most of the dressing, the whole olives and green beans. Arrange eggs and salmon on top, scatter with the rocket and finish with remaining dressing. Serve with crusty bread.','20 mins','6 mins'),
(1659,'roasted carrot, rocket & lentil salad','https://www.bbcgoodfood.com/recipes/roasted-carrot-rocket-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/carrot-salad-e050f73.jpg','A vibrant and substantial salad for two that''s rich in calcium, fibre and 4 of your 5-a-day. With toasted cumin, honey and mustard it''s rich in flavour too!',16,2,1,3,17,'Heat oven to 200C/180C fan/ gas 6. Toss the carrots in 1 tbsp oil, the cumin seeds and some seasoning. Spread out on a baking tray and roast for 30-35 mins until soft and charred around the edges. Meanwhile, whisk together the remaining oil, the lemon zest and juice, honey and mustard. Once the carrots are cooked, stir through all the remaining ingredients, then toss through the dressing to serve.','10 mins','35 mins'),
(1660,'beetroot, lentil, celeriac & hazelnut salad','https://www.bbcgoodfood.com/recipes/beetroot-lentil-celeriac-hazelnut-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/beetroot-salad-9b9dc15.jpg','A lovely, wintry salad using fresh, seasonal ingredients that you can serve warm or cold. Bright beetroot, crunchy hazelnuts and a punchy mustard dressing',70,6,1,3,17,'Heat oven to 190C/170C fan/gas 5. Scrub the beets but don’t peel them. Put them in a roasting tin lined with plenty of foil, drizzle with olive oil and season. Pull the foil around them to make a kind of tent (don’t wrap the beets tightly) and seal the edges. Bake for 1-2 hrs until completely tender right through – the time will depend on the size of the beets. To make the dressing, combine the vinegar and mustard in a jug, then season. Add the remaining ingredients and whisk until amalgamated. Put the lentils in a pan with the stock, bay and thyme. Bring to the boil, then turn down to a very gentle simmer. Cook for 12-15 mins until the lentils are just tender. The stock will be absorbed as the lentils cook (if this happens before the lentils are ready, just add some boiling water). Drain the lentils (if there is any liquid left) and set aside. Fill a bowl with water and add the lemon juice. Peel the celeriac and cut it into matchsticks, dropping them into the acidulated water to stop the flesh discolouring. Steam or boil the celeriac until just tender. Peel the cooked beets and cut the flesh into small wedges or matchsticks. Drain the celeriac and pat dry. Discard the bay and toss the lentils with the celeriac, hazelnuts, parsley and most of the dressing. Season to taste, then transfer to a serving dish. Season the beets and add them to the salad, spooning the rest of the dressing over the top.','20 mins','2 hrs'),
(1661,'puy lentils, squash & kale','https://www.bbcgoodfood.com/recipes/puy-lentils-squash-kale','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/puy-lentils-squash-kale-987e443.jpg','This hearty salad is iron-rich and full of autumn flavours - serve on its own or as a Sunday roast side dish',11,4,1,3,17,'Bring a pan of salted water to the boil. Add the bay leaf and lentils, and cook for 12-15 mins until the lentils are tender but still have some bite. Drain, put in a bowl with 1 tbsp olive oil and the lemon juice, then combine. Melt the butter in a heavy bottomed frying pan or skillet that has a lid, along with the sage and remaining oil. Add the squash and some seasoning, then stir to coat them in the butter. Put the lid on and cook over a medium heat for 8-10 mins or until just tender, adding a splash of water if you need to stop it catching. Add another splash of water to the pan along with the kale. Put the lid on to steam for 3-4 mins until the kale is tender. Put the lentils on a serving platter and arrange the squash and kale on top. Garnish with the parsley and hazelnuts and squeeze over 1 tbsp lemon juice from the remaining lemon wedges, to serve.','15 mins','35 mins'),
(1662,'mustardy beetroot & lentil salad','https://www.bbcgoodfood.com/recipes/mustardy-beetroot-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lentils-ba8c632.jpg','A rustic, healthy accompaniment to serve alongside main courses like sausages, roast chicken or lamb',5,5,1,3,17,'If not using pre-cooked lentils, cook the lentils following pack instructions, drain and leave to cool. Meanwhile, combine the mustard, oil and some seasoning to make a dressing. Tip the lentils into a bowl, pour over the dressing and mix well. Stir through the beetroot, tarragon and some seasoning, then serve.','5 mins','20 mins'),
(1663,'radish, lentil & mint salad','https://www.bbcgoodfood.com/recipes/radish-lentil-mint-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/radish-lentil-mint-salad-fd9babb.jpg','A combination of sweet, sour and nutty ingredients enhance the different varieties of beautiful radish in this recipe',29,4,1,3,17,'Toast the walnut pieces in a large frying pan over a medium heat until fragrant and just starting to char at the edges. Tip into a bowl and set aside. In the same pan, turn the heat down to low and add 1 tbsp olive oil. Add the onion, fry gently for around 10 mins until soft, then take the pan off the heat. Add the black treacle, Sherry vinegar and the rest of the olive oil, then mix and leave to cool. Add the chopped mint to the pan, and season well. In a large bowl, mix together the lentils, cucumber and half the radishes, then pour over the cooled onion and mint dressing. Toss everything together and pile onto a serving dish. Scatter over the walnut pieces and the rest of the mint and radishes, then serve.','30 mins','15 mins'),
(1664,'lentil & red pepper salad','https://www.bbcgoodfood.com/recipes/lentil-red-pepper-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-160476_10-0c16035.jpg','A filling salad that can be tailored to your taste, swap the lentils for beans and throw in some extra veg',5,2,1,3,17,'Tip the lentils and peppers into a bowl with the radishes, olives, vinegar and oil, and mix well. Season to taste. Separate the lettuce leaves and put onto a large plate. Spoon over the lentil salad and scatter with the feta.','15 mins','No Time'),
(1665,'smoked salmon with lentil salad','https://www.bbcgoodfood.com/recipes/smoked-salmon-lentil-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-486460_12-860926f.jpg','A simply stylish salad that will make an impressive dinner party starter this Christmas',43,6,1,3,17,'Rinse the lentils, then put them in a pan and cover generously with water. Bring to the boil, then turn down the heat and simmer for 25-30 mins until tender. Drain well and leave to cool. Put the shallot in a bowl with the mustard, vinegar and seasoning. Mix well and whisk in the oil. Put the eggs into a small pan and cover with water. Bring to the boil, then simmer for 3 mins. Cool quickly under running cold water and peel off the shells. Put in a bowl of cold water until ready to serve. Stir the dill into the dressing and pour it over the lentils. Spoon onto 6 plates, then halve the eggs and put one half on each plate with two slices of smoked salmon. Sprinkle with black pepper, scatter over a few dill fronds and serve.','15 mins','30 mins'),
(1666,'lamb cutlets with lentil & feta salad','https://www.bbcgoodfood.com/recipes/lamb-cutlets-lentil-feta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-411500_12-9a5b868.jpg','Impressive looking, but simple to prepare, this lamb cutlet dish is perfect for a speedy mid-week supper party',5,4,1,3,17,'Cook peas in boiling water for 3-4 mins until just tender, then drain. Mix with the lentils, vinegar, sugar and mint, then crumble in the feta and season well. Heat a griddle pan, brush the cutlets with a little oil and season. Cook in the hot pan for 4 mins on each side until browned and the middle is pink. Divide the salad between four bowls, then top with a couple of cutlets per person.','5 mins','15 mins'),
(1668,'spiced whole cauliflower & warm chickpea salad','https://www.bbcgoodfood.com/recipes/spiced-whole-cauliflower-warm-chickpea-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spiced-whole-cauliflower-warm-chickpea-salad-fff78ec-scaled.jpg','Roast a whole cauliflower to lock in flavour and make an impressive centrepiece - cut at the table or serve in wedges',33,4,1,3,17,'Heat oven to 200C/180C fan/gas 6. Mix together all the marinade ingredients plus 1/2 tsp sea salt and 1/4 tsp freshly ground pepper. Drizzle the oil over the base of a medium roasting tin. Use your hands to coat the cauliflower entirely in the marinade, then sit it in the oiled tin. Spoon any leftover marinade on top. Roast for 30 mins until the topping is set. Splash 100ml water into the tin, then cover loosely with foil. Return to the oven and let the cauliflower steam for 30 mins. Meanwhile, toss the chickpeas, onion, spices and some seasoning together. Uncover the cauliflower, scatter the chickpea mixture around it (the water will have mainly evaporated), then roast for 20 mins more until the onion is tender and the chickpeas are crisping. The cauliflower should be golden and tender by now – when you insert a skewer inserted through the middle, it should meet little resistance. Stir the yogurt and coconut cream together to make a loose dressing, then season. Fold the tomatoes, coriander and lemon juice through the chickpeas, then serve with the dressing and a drizzle of extra virgin olive oil, if you like.','20 mins','1 hr and 20 mins'),
(1669,'lamb with warm potato & olive salad','https://www.bbcgoodfood.com/recipes/lamb-warm-potato-olive-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/lamb-with-warm-potato-salad-970c0f0.jpg','This restaurant-standard dish is simple to make but worthy of any dinner party. Cooking the lamb this way stops it drying out and gives it a subtle flavour',146,2,1,3,17,'Cook the potatoes in a pan of boiling, lightly salted water for 10-12 mins, then drain and transfer to a baking sheet to cool. Now prepare the lamb. Mix the yogurt with the olive oil and some sea salt to taste. Rub the lamb loin with the yogurt mixture – make sure to cover all sides. Chill in the fridge for 1 hr. Heat oven to 200C/180C fan/gas 6. Heat an ovenproof sauté pan and add the rapeseed oil. Sear the lamb on all sides until dark golden brown, then cook in the oven for 7-10 mins or until medium-rare. Remove and leave to rest for 5 mins. To finish the potato salad, cut the cooled potatoes into thick slices. Heat the olive oil in a frying pan over a medium heat, add the potato slices and fry for 10 mins or until golden brown. Transfer the potatoes to a bowl, mix with the olives, yogurt and vinegar, and add salt to taste. Serve warm with the sliced lamb loin and the leaves.','20 mins','35 mins'),
(1670,'warm beet, chorizo & pear salad','https://www.bbcgoodfood.com/recipes/warm-beet-chorizo-and-pear-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/warm-beet-chorizo-and-pear-salad.p-b7c861d.jpg','Combine succulent pear, colourful beetroot, spicy chorizo and buttery manchego cheese in this vibrant winter salad that''s ideal for sharing with friends',17,4,1,3,17,'Put the beets in a large pan of water (if you have a mix of colours, cook in separate pans as the colours will bleed). Bring the water to a boil, cover and leave to simmer – about 20-40 mins, depending on the size of the beets. Use a skewer or small sharp knife to check that they’re tender in the centre (try not to poke too often or they’ll bleed all their juices into the water). Drain the beets and leave to cool in a colander. When cool enough to handle, peel away the skins, root and stalks, then roughly chop or slice the beets. Put the chorizo in a cold frying pan and fry over a medium heat until crisp – you should collect lots of oil in the pan. Lift out the chorizo with a slotted spoon and keep warm. Tip in the almonds and fry quickly until just turning brown on the edges. Scoop out with a slotted spoon and place on kitchen paper to dry. Make the dressing by melting the membrillo in the pan with the chorizo oil, sherry vinegar and 1 tbsp lemon juice. Mix in the olive oil and season. Core and thinly slice the pear, toss with a little lemon juice to stop it browning, then arrange on a platter with the beets, chorizo and almonds. Toss together with the dressing, if you like, or serve the dressing on the side. Sprinkle over the parsley and manchego and serve immediately.','15 mins','55 mins'),
(1671,'warm salad of asparagus, bacon, duck egg & hazelnuts','https://www.bbcgoodfood.com/recipes/warm-salad-asparagus-bacon-duck-egg-hazelnuts','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-626462_11-0c1e71e.jpg','A smart and stylish springtime starter that is super healthy to boot',77,6,1,3,17,'Heat grill to High and cook the bacon for 5 mins until crisp, then snip with scissors into pieces. Set aside. Cook the eggs in boiling water for 8 mins (5 mins for hen’s eggs), drain and plunge into ice water, to cool as quickly as possible. Make the dressing: whisk all ingredients together with seasoning. Prepare the asparagus by snapping off the base of each spear – it’ll break at the tender point. Just before serving, put the nuts and bacon into a warm oven. Halve the eggs and season. Bring a pan of salted water to the boil; cook the asparagus for about 5 mins, until just tender. Drain, then divide between plates. Add egg halves, sprinkle with nuts and bacon, then drizzle with dressing in a zigzag pattern.','25 mins','25 mins'),
(1672,'warm lemony courgette salad','https://www.bbcgoodfood.com/recipes/warm-lemony-courgette-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/courgette_0-cfbbfbe.jpg','Try making this healthy, vibrant salad of shaved courgette, citrus and basil. Serve as a gluten-free starter or side dish',5,2,1,3,17,'Use a vegetable peeler to slice the courgettes into wide strips, discarding the central, seedy part. Heat the oil in a large frying pan, add the lemon zest and garlic, and fry over a medium heat for 1 min. Add the courgette strips and cook, stirring regularly, for a further 1-2 mins until the courgettes are slightly softened. Add a squeeze of lemon juice and toss the basil through.','10 mins','5 mins'),
(1673,'warm pearl barley & roasted carrot salad with dill vinaigrette','https://www.bbcgoodfood.com/recipes/warm-pearl-barley-roasted-carrot-salad-dill-vinaigrette','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/warm-pearl-barley-roasted-carrot-salad-with-dill-vinaigrette-699a142.jpg','This salad combines sweet carrots with the liquorice bite of a slightly acidic dressing and creamy blue cheese - a simple lunch or side dish',67,4,1,3,17,'Heat oven to 180C/160C fan/gas 4. Put the sliced carrots in a large roasting tin with the shallots, garlic and fennel seeds. Drizzle over the oil, toss everything together and season. Roast for 35-40 mins until everything has caramelised and the carrots are tender. Meanwhile, boil the pearl barley in a large pan of salted water until al dente, about 30 mins. Make the dressing by whisking all the ingredients together with some seasoning. Remove the carrots from the oven. Strain the pearl barley and toss through the dressing, carrots, dill, parsley and half the cheese. Finish with a few of the fennel tops and the remaining cheese. Serve warm or cold with a slice or two of warm buttered soda bread, if you like.','15 mins','40 mins'),
(1674,'warm chicken salad with garlic mushrooms','https://www.bbcgoodfood.com/recipes/warm-chicken-salad-garlic-mushrooms','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-560708_11-4320e01.jpg','A mouthwatering, warm winter salad made from just a handful of simple ingredients',147,2,1,3,17,'Preheat the oven to 220C/Gas 7/fan oven 200C. Rub the chicken pieces with the olive oil, salt and pepper, then put them skin side down in a small casserole or roasting tin. Roast in the oven for 30 minutes. Remove from the casserole and set aside. Leave the oven on. Spread the mushrooms, garlic and thyme in the casserole or tin and place the chicken pieces on top, this time skin side up. Pour 200ml/7fl oz hot water around the chicken and return the casserole or tin to the oven for another 30 minutes, stirring and checking after 15 minutes that there is enough liquid to form a stock, as this will be the Everyday food dressing. If there isn''t, add a little more hot water. Remove the casserole from the oven and allow to cool slightly. Using a fork and your fingers, finely shred the chicken into a bowl. Add the mushrooms, garlic and all the juices from the casserole. Mix well and season. To serve, sprinkle the salad leaves into the mushroom and chicken mixture and gently toss them through so they just wilt a little in the warmth. Serve the salad in a large bowl or on a platter, with some garlic bread on the side.','10 mins - 20 mins','1 hr'),
(1675,'warm chicken & ciabatta salad','https://www.bbcgoodfood.com/recipes/warm-chicken-ciabatta-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/warm-chicken-ciabatta-salad-e564443.jpg','Pan-fry chicken and ciabatta chunks, then toss with red cabbage, red onion and pomegranate seeds in this easy and attractive dish',24,4,1,3,17,'Heat oven to 200C/180C fan/gas 6. Toss the chicken pieces with 1 tbsp of the oil, season and arrange on a baking tray. Roast for 10 mins. Toss the torn ciabatta with the chopped garlic, a little seasoning and the remaining oil. Add this to the chicken and cook for another 10 mins. Toss together the red cabbage and red onion. Once the chicken is cooked, cool for a couple of mins, then add to the cabbage mix. Stir together, then toss with the pickling juices from the cabbage, and the salad leaves. Sprinkle with the pomegranate seeds.','15 mins','20 mins'),
(1676,'warm thai chicken & noodle salad','https://www.bbcgoodfood.com/recipes/warm-thai-chicken-noodle-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-4479_10-0dde777.jpg','A low fat, oriental-inspired salad - ideal for when you''re in a real rush',5,4,1,3,17,'Preheat the grill to high. Put the chicken on a baking tray and grill for 10-12 minutes without turning, until cooked through. Meanwhile, cook the noodles according to instructions. Drain and run cold water through to stop them sticking together. Mix the vegetables in a bowl. Thinly slice the chicken and add to the bowl, along with the noodles and coriander leaves. Mix the dressing ingredients together with 2 tablespoons water, pour over the salad and toss well. Serve straight away.','25 mins - 35 mins','20 mins'),
(1677,'warm halloumi, chickpea & lime salad','https://www.bbcgoodfood.com/recipes/warm-halloumi-chickpea-lime-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1066551_11-ce76c03.jpg','Rustle up a quick salad for two with green beans, chickpeas and tomatoes, topped with decadent fried cheese',5,2,1,3,17,'Cook the beans in a pan of boiling water for 3 mins until just tender, adding the chickpeas for the final min of cooking. Meanwhile, make the dressing: in a large bowl, whisk the lime juice and zest with 3 tbsp of the oil and the chilli. Season. Drain the chickpeas and beans and, while warm, toss with the dressing. Pour boiling water over the red onion, leave for 5 mins to soften, then drain and rinse. Add the peppers, tomatoes, rocket, parsley and softened onion to the bean bowl. Toss to coat everything in the dressing, then divide between 2 plates. Heat the remaining oil in a frying pan, add the halloumi slices and fry for about 1 min on each side until golden. Place a few warm halloumi slices on top of each salad and serve.','10 mins','12 mins'),
(1678,'avocado, roasted broccoli & sesame rice salad','https://www.bbcgoodfood.com/recipes/avocado-roasted-broccoli-sesame-rice-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/avocado-roasted-broccoli-sesame-rice-salad-b6a2e4a.jpg','Share this veggie avocado, broccoli and sesame rice salad with a friend or partner for an easy supper. Full of flavour, you''ll also get 3 of your 5-a-day',14,2,1,3,17,'Heat the oven to 220C/200C fan/gas 7. Tip the broccoli onto a baking sheet along with the red onion and 1 tbsp of the sesame oil. Roast for 20 mins or until the broccoli is tender. Leave to cool a little. Whisk the remaining sesame oil, soy, rice wine vinegar and honey in a small bowl. Toss the broccoli, rice, onion and dressing together in a large bowl, then gently mix in the avocado and coriander. Divide between bowls and top with the sesame seeds and chilli.','15 mins','20 mins'),
(1679,'warm kale salad with almonds & serrano ham','https://www.bbcgoodfood.com/recipes/warm-kale-salad-almonds-serrano-ham','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/warm-kale-salad-with-almonds-serrano-ham-d7d7a06.jpg','This modern take on coleslaw and cold cuts makes a delicious starter or light main, with shallots, celery, and a sweet and tangy dressing',33,2,1,3,17,'Heat oven to 200C/180C fan/gas 6. Toss the shallots with 1 tsp oil on a large non-stick baking tray. Separate the rings a little as you turn them in the oil. Roast for 10 mins, stirring halfway, until starting to soften and turn golden here and there. In a large bowl, whisk the vinegar, oil, mustard, sugar and some seasoning together to make the dressing, then tip in the raisins and leave to soak. In a smaller roasting tin, toss the almonds, oil, paprika and a pinch of sea salt. Rub the kale all over with 1 tsp vegetable oil and some seasoning. Remove the shallot tray from the oven, mix in the kale (it’s fine if it mounds a bit), then return the tray to the oven and put the almonds on the shelf below. Roast for 7 mins, giving the kale a turn halfway through, until crisp in some places, wilted in others. Ruffle the ham onto serving plates. Tip the kale, shallots and celery into the dressing bowl, make sure it coats the kale well, then mound on top of the ham and scatter with the almonds and the cheese, if using.','15 mins','20 mins'),
(1680,'warm chickpea, chorizo & pepper salad','https://www.bbcgoodfood.com/recipes/warm-chickpea-chorizo-pepper-salad','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-263_11-9d07daa.jpg','A rough-and-ready salad bursting with Mediterranean flavours - great as a starter or main',25,2,1,3,17,'Cut the chorizo into 3cm thick slices. Heat the oil in a large frying pan over a high heat. Cook the chorizo for 4-5 minutes, turning occasionally until the edges start to crisp and it has released its red oil. Tip the chickpeas into the pan and cook for a couple of minutes until hot. Add the peppers and cook for a few minutes until everything''s juicy. Remove from the heat, stir in the coriander, ladle into two bowls and top each with a spoonful of yogurt.','10 mins - 5 mins','10 mins - 15 mins'),
(1681,'green smoothie','https://www.bbcgoodfood.com/recipes/clean-green-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cropped-clean-green-smoothie-40e7904.jpg','This sweet smoothie packs in plenty of nutritious ingredients, such as spinach, flaxseeds and banana, with an optional spoonful of maca powder for a natural energy boost',49,1,1,3,28,'Pour the milk into a high-speed blender then add the ground flaxseed, maca powder (if using) and the cinnamon. Add the remaining ingredients then blitz until smooth. Pour into glasses and serve.','5 mins','No Time'),
(1682,'avocado smoothie','https://www.bbcgoodfood.com/recipes/bone-builder-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bone-builder-47d17f7.jpg','This easy smoothie gets its vibrant green colour from avocado, cucumber, spinach and kale. Blitz with pineapple and coconut water.',5,1,1,3,28,'Put the avocado, spinach, kale, pineapple and cucumber in the blender. Top up with coconut water, then blitz until smooth.','5 mins','No Time'),
(1683,'spinach smoothie','https://www.bbcgoodfood.com/recipes/spinach-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/spinach-smoothie-0f2f213.jpg','Whizz up our spinach smoothie with avocado, sweet mango and milk in minutes. If you have leftovers, try freezing them in lolly moulds for an easy dessert',6,4,1,3,28,'Whizz the frozen spinach with the avocado, chopped mango, banana and milk (we used unsweetened oat milk) in a powerful blender until smooth. Freeze the leftovers in lolly moulds for desserts, if you like.','5 mins','No Time'),
(1684,'peach melba smoothie','https://www.bbcgoodfood.com/recipes/peach-melba-smoothie','https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1081496_11-dde2a76.jpg','Blend peaches, raspberries, orange juice and fresh custard into a fruity, creamy shake that''s low in fat',5,2,1,3,28,'Drain and rinse peaches and place in a blender with raspberries. Add orange juice and fresh custard and whizz together. Pour over ice, garnish with another spoonful of custard and a few raspberries. Best served chilled.','5 mins','No Time');



















































































CREATE TABLE recipes_ingredients (
  id SERIAL,
  recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
  qty TEXT DEFAULT '1',
  unit_id INTEGER REFERENCES units (id) ON DELETE CASCADE,
  ingredient_id INTEGER REFERENCES ingredients (id) ON DELETE CASCADE
);


































CREATE TABLE ratings (
  recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
  rating INTEGER NOT NULL,
  vote_count INTEGER NOT NULL,
  PRIMARY KEY (recipe_id)
);
























CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR (30) UNIQUE NOT NULL,
    first_name VARCHAR (30) NOT NULL,
    last_name VARCHAR (30) NOT NULL,
    email TEXT CHECK (email ILIKE '%@%'),
    is_admin BOOLEAN DEFAULT FALSE,
    phone TEXT,
    header_img TEXT,
    profile_img TEXT,
    password TEXT NOT NULL
);


CREATE TABLE disliked_recipes (
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    PRIMARY KEY (user_id, recipe_id)
);

CREATE TABLE liked_recipes (
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    PRIMARY KEY (user_id, recipe_id)
);


CREATE TABLE favorite_recipes (
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    PRIMARY KEY (user_id, recipe_id)
);



CREATE TABLE saved_recipes (
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    PRIMARY KEY (user_id, recipe_id)
);


CREATE TABLE occasions (
    id SERIAL PRIMARY KEY,
    occasion VARCHAR (121) UNIQUE NOT NULL
);


CREATE TABLE recipelists (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    occasion_id INTEGER REFERENCES occasions (id),
    list_name VARCHAR (30) NOT NULL,
    UNIQUE (user_id, list_name, occasion_id)
);


CREATE TABLE recipelists_recipes (
    -- user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    id SERIAL,
    list_id INTEGER REFERENCES recipelists (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id),
    UNIQUE (list_id, recipe_id)
);


CREATE TABLE tips (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    tip TEXT NOT NULL
);

CREATE TABLE reviews (
    id SERIAL,
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id) ON DELETE CASCADE,
    stars INTEGER NOT NULL CHECK(stars < 6),
    review TEXT NOT NULL,
    PRIMARY KEY (user_id, recipe_id)
);


CREATE TABLE shoppinglists (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_id INTEGER REFERENCES recipes (id),
    list_name VARCHAR (121) NOT NULL,
    UNIQUE (user_id, recipe_id)
);


CREATE TABLE shoppinglists_items (
    id SERIAL PRIMARY KEY,
    list_id INTEGER REFERENCES shoppinglists (id) ON DELETE CASCADE,
    qty TEXT,
    unit_id INTEGER REFERENCES units (id),
    ingredient_id INTEGER REFERENCES ingredients (id)
);

CREATE TABLE user_recipes (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users (id) ON DELETE CASCADE,
    recipe_name VARCHAR (121) NOT NULL,
    UNIQUE (user_id, recipe_name)
);

CREATE TABLE user_recipes_ingredients (
    id SERIAL PRIMARY KEY,
    user_recipe_id INTEGER REFERENCES user_recipes (id) ON DELETE CASCADE,
    qty INTEGER DEFAULT 1,
    unit_id INTEGER REFERENCES units (id),
    ingredient_id INTEGER REFERENCES ingredients (id)
);

CREATE TABLE user_recipes_steps (
    id SERIAL PRIMARY KEY,
    user_recipe_id INTEGER REFERENCES user_recipes (id) ON DELETE CASCADE,
    step TEXT NOT NULL
);